INSERT INTO `words` VALUES (1, 'a', 'ə', 'art.', '(用于可数名词或单数名词前，表示未曾提到的)一(人、事、物);用于前有形容词或后有短语的不可数名词前;任何一;', 'A waiter entered with a tray...', '侍者端着托盘进来了。');
INSERT INTO `words` VALUES (2, 'abandon', 'əˈbændən', 'v.', '(不顾责任、义务等)离弃，遗弃，抛弃;(不得已而)舍弃，丢弃，离开;停止(支持或帮助);', 'He claimed that his parents had abandoned him...', '他声称父母遗弃了他。');
INSERT INTO `words` VALUES (3, 'abatement', 'əˈbeɪtmənt', 'n.', '减弱;减轻;减少;', '...the abatement of carbon dioxide emissions.', '二氧化碳排放的减少');
INSERT INTO `words` VALUES (4, 'abbreviate', 'əˈbriːvieɪt', 'v.', '缩略;把(词语、名称)缩写(成…);', 'He abbreviated his first name to Alec.', '他将自己的名字缩写为亚历克。');
INSERT INTO `words` VALUES (5, 'abbreviation', 'əˌbriːviˈeɪʃn', 'n.', '略语;缩写词;缩写形式;', 'The postal abbreviation for Kansas is KS.', '堪萨斯州的邮政缩写是KS。');
INSERT INTO `words` VALUES (6, 'abdomen', 'ˈæbdəmən', 'n.', '腹(部);(昆虫的)腹部;', 'He was suffering from pains in his abdomen.', '他感到腹部剧痛。');
INSERT INTO `words` VALUES (7, 'abdominal', 'æbˈdɒmɪnl', 'adj.', '腹部的;', '...vomiting, diarrhoea and abdominal pain.', '呕吐、腹泻和腹痛');
INSERT INTO `words` VALUES (8, 'abide', 'əˈbaɪd', 'v.', '(abide by)接受，遵照（规则，决定，劝告）;逗留，停留;', 'I can\'t abide people who can\'t make up their minds...', '我受不了优柔寡断的人。');
INSERT INTO `words` VALUES (9, 'ability', 'əˈbɪləti', 'n.', '能力;才能;本领;', 'The public never had faith in his ability to handle the job...', '公众从来不相信他有能力胜任这一职位。');
INSERT INTO `words` VALUES (10, 'able', 'ˈeɪbl', 'adj.', '能;能够;有才智的;', 'The older child should be able to prepare a simple meal...', '那个年纪稍大的孩子应当会做一顿简单的饭菜。');
INSERT INTO `words` VALUES (11, 'abnormal', 'æbˈnɔːml', 'adj.', '不正常的;反常的;变态的;', '...abnormal heart rhythms and high anxiety levels.', '心律不齐和高度焦虑');
INSERT INTO `words` VALUES (12, 'aboard', 'əˈbɔːd', 'adv./prep.', '在(船、飞机、公共汽车、火车等)上;上(船、飞机、公共汽车、火车等);', 'She invited 750 people aboard the luxury yacht, the Savarona...', '她邀请了750人乘坐豪华游艇“萨瓦罗纳”号。');
INSERT INTO `words` VALUES (13, 'abolish', 'əˈbɒlɪʃ', 'v.', '废除，废止(法律、制度、习俗等);', 'The following year Parliament voted to abolish the death penalty for murder...', '议会于翌年表决对谋杀罪废除死刑。');
INSERT INTO `words` VALUES (14, 'aboriginal', 'ˌæbəˈrɪdʒənl', 'adj.', '澳大利亚土著的;(欧洲人到来之前某地区的人、动物等)土著的，土生土长的;', 'He remained fascinated by the Aboriginals\' tales.', '他一直对澳大利亚土著居民的传说极感兴趣。');
INSERT INTO `words` VALUES (15, 'abort', 'əˈbɔːt', 'v.', '使流产;流产;小产;', 'Her lover walked out on her after she had aborted their child.', '她的情人在她打掉胎儿后甩了她。');
INSERT INTO `words` VALUES (16, 'abortion', 'əˈbɔːʃn', 'n.', '人工流产;堕胎;打胎;', 'His girlfriend had an abortion.', '他的女朋友做了流产。');
INSERT INTO `words` VALUES (17, 'abound', 'əˈbaʊnd', 'v.', '大量存在;有许多;', 'Stories abound about when he was in charge...', '关于他在任时的传闻多不胜数。');
INSERT INTO `words` VALUES (18, 'about', 'əˈbaʊt', 'adv.', '大约;左右;将近;', 'She came in for a coffee, and told me about her friend Shona...', '她进来喝了杯咖啡，并向我谈起了她的朋友肖纳。');
INSERT INTO `words` VALUES (19, 'above', 'əˈbʌv', 'prep.', '在(或向)…上面;(数目、数量、水平、年龄)超过，多于，大于;(重要性、质量)超过，胜过;', 'He lifted his hands above his head...', '他将双手举过头顶。');
INSERT INTO `words` VALUES (20, 'abrasion', 'əˈbreɪʒn', 'n.', '(皮肤、表皮)擦伤处;(表层)磨损处;磨损;', 'He had severe abrasions to his right cheek.', '他的右脸颊有严重的擦伤。');
INSERT INTO `words` VALUES (21, 'abrasive', 'əˈbreɪsɪv', 'adj.', '有研磨作用的;研磨的;生硬粗暴的;', 'His abrasive manner has won him an unenviable notoriety...', '他生硬粗暴的态度让他声名狼藉。');
INSERT INTO `words` VALUES (22, 'abroad', 'əˈbrɔːd', 'adv.', '在国外;到国外;广为流传;', 'I would love to go abroad this year, perhaps to the South of France.', '我今年很想出国，可能是去法国南部。');
INSERT INTO `words` VALUES (23, 'abrupt', 'əˈbrʌpt', 'adj.', '突然的;意外的;(言语、行为)粗鲁的，莽撞的，唐突的;', 'Rosie\'s idyllic world came to an abrupt end when her parents\' marriage broke up...', '父母婚姻的破裂使得罗茜田园诗般美好的世界陡然画上了句号。');
INSERT INTO `words` VALUES (24, 'absence', 'ˈæbsəns', 'n.', '缺席;不在;不存在;', '...a bundle of letters which had arrived for me in my absence...', '我不在期间寄给我的一大堆信件');
INSERT INTO `words` VALUES (25, 'absolute', 'ˈæbsəluːt', 'adj.', '完全的;全部的;绝对的;', 'It\'s not really suited to absolute beginners...', '它并不真正适合从零开始的初学者。');
INSERT INTO `words` VALUES (26, 'absolutely', 'ˈæbsəluːtli', 'adv.', '(强调真实无误)绝对地，完全地;绝对不;完全没有;', 'Jill is absolutely right...', '吉尔完全正确。');
INSERT INTO `words` VALUES (27, 'absorb', 'əbˈzɔːb', 'v.', '吸收(液体、气体等);使并入;吞并;', 'Plants absorb carbon dioxide from the air and moisture from the soil...', '植物从空气中吸收二氧化碳并从土壤中汲取水分。');
INSERT INTO `words` VALUES (28, 'absorption', 'əbˈzɔːpʃn', 'n.', '(液体、气体等的)吸收;并入;同化;', 'This controls the absorption of liquids...', '这控制着液体的吸收。');
INSERT INTO `words` VALUES (29, 'absurd', 'əbˈsɜːd', 'adj.', '荒谬的;荒唐的;怪诞不经的;', 'It is absurd to be discussing compulsory redundancy policies for teachers...', '讨论针对教师的强制裁员政策真是滑稽可笑。');
INSERT INTO `words` VALUES (30, 'abundance', 'əˈbʌndəns', 'n.', '大量;丰盛;充裕;', 'The area has an abundance of wildlife...', '这片地区有丰富的野生动植物。');
INSERT INTO `words` VALUES (31, 'abundant', 'əˈbʌndənt', 'adj.', '大量的;丰盛的;充裕的;', 'There is an abundant supply of cheap labour...', '廉价劳动力供应充足。');
INSERT INTO `words` VALUES (32, 'academic', 'ˌækəˈdemɪk', 'adj.', '学业的，教学的，学术的(尤指与学校教育有关);学术的(与实践性、技术性相对);学习良好的;', 'Their academic standards are high...', '他们的教学水平很高。');
INSERT INTO `words` VALUES (33, 'academy', 'əˈkædəmi', 'n.', '专科院校;(艺术、文学、科学等的)研究院，学会;(苏格兰)中等学校;', '...the Royal Academy of Music.', '皇家音乐学院');
INSERT INTO `words` VALUES (34, 'accelerate', 'əkˈseləreɪt', 'v.', '(使)加速;加速;加快;', 'Growth will accelerate to 2.9 per cent next year...', '明年的增长会加快到2.9%。');
INSERT INTO `words` VALUES (35, 'acceleration', 'əkˌseləˈreɪʃn', 'n.', '加速;加快;(车辆)加速能力，加速的幅度;', 'He has also called for an acceleration of political reforms.', '他同时呼吁加快政治改革的步伐。');
INSERT INTO `words` VALUES (36, 'accelerator', 'əkˈseləreɪtə(r)', 'n.', '(汽车等的)加速装置，油门;(基本粒子)加速器;', 'He eased his foot off the accelerator.', '他松开了油门。');
INSERT INTO `words` VALUES (37, 'accent', 'ˈæksent , ækˈsent', 'n.', '口音;腔调;土音;', 'He had developed a slight American accent.', '他说话带上了一点轻微的美国口音。');
INSERT INTO `words` VALUES (38, 'accentuate', 'əkˈsentʃueɪt', 'v.', '着重;强调;使突出;', 'His shaven head accentuates his large round face...', '光头使他的大圆脸更加突出。');
INSERT INTO `words` VALUES (39, 'accept', 'əkˈsept', 'v.', '收受;接受(建议、邀请等);(认为合适或足够好而)接受;', 'Eventually Sam persuaded her to accept an offer of marriage...', '最终萨姆说服她接受了求婚。');
INSERT INTO `words` VALUES (40, 'acceptable', 'əkˈseptəbl', 'adj.', '(社会上)认同的，认可的;可接受的;令人满意的;', 'It is becoming more acceptable for women to drink alcohol...', '女性喝酒渐渐被人们接受。');
INSERT INTO `words` VALUES (41, 'acceptance', 'əkˈseptəns', 'n.', '接受(礼物、邀请、建议等);同意;认可;', 'The Party is being degraded by its acceptance of secret donations...', '该党因私下接受捐赠而蒙羞。');
INSERT INTO `words` VALUES (42, 'access', 'ˈækses', 'n.', '通道;通路;入径;', 'The facilities have been adapted to give access to wheelchair users.', '这些设施已经改造过以方便轮椅使用者进入。');
INSERT INTO `words` VALUES (43, 'accessible', 'əkˈsesəbl', 'adj.', '可到达的;可接近的;可进入的;', 'The Centre is easily accessible to the general public...', '该中心普通大众可随意进入。');
INSERT INTO `words` VALUES (44, 'accessory', 'əkˈsesəri', 'n.', '附件;配件;附属物;', '...an exclusive range of hand-made bedroom and bathroom accessories.', '独家经营的一系列手工卧室和浴室配件');
INSERT INTO `words` VALUES (45, 'accident', 'ˈæksɪdənt', 'n.', '(交通)事故;意外遭遇;不测事件;', 'She was involved in a serious car accident last week...', '上星期她卷入了一场严重的车祸。');
INSERT INTO `words` VALUES (46, 'accidental', 'ˌæksɪˈdentl', 'adj.', '意外的;偶然的;', 'The jury returned a verdict of accidental death...', '陪审团作出意外死亡的正式裁决。');
INSERT INTO `words` VALUES (47, 'accidentally', 'ˌæksɪˈdɛntəli', 'adv.', '偶然，偶然地；出其不意地；不知不觉地；不知道为什么恰巧;', 'The jury returned a verdict of accidental death...', '陪审团作出意外死亡的正式裁决。');
INSERT INTO `words` VALUES (48, 'acclaim', 'əˈkleɪm', 'v.', '称誉某人 / 事物(为…);给予高度评价;', 'She has been acclaimed for the TV drama \'Prime Suspect\'...', '她凭借电视剧《头号嫌疑犯》而受到好评。');
INSERT INTO `words` VALUES (49, 'accommodate', 'əˈkɒmədeɪt', 'v.', '为(某人)提供住宿(或膳宿、座位等);容纳;为…提供空间;', 'The school in Poldown was not big enough to accommodate all the children...', '波尔登的学校不够大，容不下所有的孩子。');
INSERT INTO `words` VALUES (50, 'accommodation', 'əˌkɒməˈdeɪʃn', 'n.', '住处;办公处;停留处;', 'The government will provide temporary accommodation for up to three thousand people...', '政府将给多达3,000人提供临时住处。');
INSERT INTO `words` VALUES (51, 'accompany', 'əˈkʌmpəni', 'v.', '陪同;陪伴;伴随;', 'Ken agreed to accompany me on a trip to Africa...', '肯答应陪我一起去非洲。');
INSERT INTO `words` VALUES (52, 'accomplish', 'əˈkʌmplɪʃ', 'v.', '完成;', 'If we\'d all work together, I think we could accomplish our goal...', '只要大家齐心协力，我想我们就能实现目标。');
INSERT INTO `words` VALUES (53, 'accomplishment', 'əˈkʌmplɪʃmənt', 'n.', '成就;成绩;才艺;', 'For a novelist, that\'s quite an accomplishment...', '对于一位小说家来说，那是相当了不起的成就。');
INSERT INTO `words` VALUES (54, 'accord', 'əˈkɔːd', 'n.', '协议;条约;', '...a fitting way to celebrate the peace accord.', '庆祝和平协定的恰当的方式');
INSERT INTO `words` VALUES (55, 'accordance', 'əˈkɔːdns', 'n.', '一致，和谐，符合;给予，授予，赋予;', 'Entries which are not in accordance with the rules will be disqualified.', '不符合规定的参赛作品将被取消资格。');
INSERT INTO `words` VALUES (56, 'according', 'əˈkɔːdɪŋ', 'v.', '给予，赠予，授予(权力、地位、某种待遇);(与…)一致，符合，配合;', '...a fitting way to celebrate the peace accord.', '庆祝和平协定的恰当的方式');
INSERT INTO `words` VALUES (57, 'accordingly', 'əˈkɔːdɪŋli', 'adv.', '照着;相应地;因此;', 'We have a different background, a different history. Accordingly, we have the right to different futures...', '我们有不同的背景和不同的历史。因此，我们有权拥有不同的未来。');
INSERT INTO `words` VALUES (58, 'account', 'əˈkaʊnt', 'n.', '账户;账目;赊销账;', 'Some banks make it difficult to open an account...', '一些银行把开户弄得很麻烦。');
INSERT INTO `words` VALUES (59, 'accounting', 'əˈkaʊntɪŋ', 'n.', '会计;', '...allegations of theft, forgery and false accounting.', '盗窃、伪造和做假账的指控');
INSERT INTO `words` VALUES (60, 'accumulate', 'əˈkjuːmjəleɪt', 'v.', '积累;积聚;(数量)逐渐增加;', 'Households accumulate wealth across a broad spectrum of assets...', '家庭在以各种各样的资产形式积累财富。');
INSERT INTO `words` VALUES (61, 'accumulation', 'əˌkjuːmjəˈleɪʃn', 'n.', '堆积物;聚积物;堆积;积累;积聚;', '...an accumulation of experience and knowledge.', '经验和知识的积累');
INSERT INTO `words` VALUES (62, 'accurate', 'ˈækjərət', 'adj.', '正确无误的;精确的;准确的;', 'Police have stressed that this is the most accurate description of the killer to date...', '警方强调这是迄今对凶手最精确的描述。');
INSERT INTO `words` VALUES (63, 'accurately', 'ˈækjʊrɪtli', 'adv.', '准确地，精确；正确，正确地;', 'Police have stressed that this is the most accurate description of the killer to date...', '警方强调这是迄今对凶手最精确的描述。');
INSERT INTO `words` VALUES (64, 'accusation', 'ˌækjuˈzeɪʃn', 'n.', '控告;起诉;告发;', 'Kim rejects accusations that Country music is over-sentimental...', '对于乡村音乐过于感伤的指责，金表示反对。');
INSERT INTO `words` VALUES (65, 'accuse', 'əˈkjuːz', 'v.', '控告;控诉;谴责;', 'He was accusing my mum of having an affair with another man...', '他指责我妈妈与另一个男人有染。');
INSERT INTO `words` VALUES (66, 'accuser', 'əˈkjuːzə(r)', 'n.', '指责者;(尤指)控告人，控诉人;', '...a criminal proceeding where defendants have the right to confront their accusers.', '被告有权与原告对质的一种刑事诉讼');
INSERT INTO `words` VALUES (67, 'accustom', 'əˈkʌstəm', 'v.', '使习惯(于);使适应;', 'The team has accustomed itself to the pace of first division rugby...', '球队适应了英式橄榄球甲级联赛的节奏。');
INSERT INTO `words` VALUES (68, 'accustomed', 'əˈkʌstəmd', 'adj.', '习惯于;通常的;惯常的;', 'I was accustomed to being the only child at a table full of adults...', '我已经习惯了作为唯一一个小不点坐在一桌大人中间。');
INSERT INTO `words` VALUES (69, 'ache', 'eɪk', 'v.', '疼痛;隐痛;渴望;', 'Her head was throbbing and she ached all over...', '她的脑袋嗡嗡作响并且浑身疼痛。');
INSERT INTO `words` VALUES (70, 'achieve', 'əˈtʃiːv', 'v.', '(凭长期努力)达到(某目标、地位、标准);完成;成功;', 'There are many who will work hard to achieve these goals...', '有志之士将会共同为实现这些目标而努力。');
INSERT INTO `words` VALUES (71, 'achievement', 'əˈtʃiːvmənt', 'n.', '成就;成绩;功绩;', 'Reaching this agreement so quickly was a great achievement...', '如此快速达成这项协定是一大伟绩。');
INSERT INTO `words` VALUES (72, 'acid', 'ˈæsɪd', 'n.', '酸;', '...citric acid...', '柠檬酸');
INSERT INTO `words` VALUES (73, 'acidity', 'əˈsɪdəti', 'n.', '酸味;酸性;', '...citric acid...', '柠檬酸');
INSERT INTO `words` VALUES (74, 'acknowledge', 'əkˈnɒlɪdʒ', 'v.', '承认(属实);承认(权威、地位);告知收悉;', 'Naylor acknowledged, in a letter to the judge, that he was a drug addict...', '内勒在给法官的信中承认自己吸毒。');
INSERT INTO `words` VALUES (75, 'acquaint', 'əˈkweɪnt', 'v.', '使熟悉;使了解;', '...efforts to acquaint the public with their rights under the new law...', '为使公众熟悉新法律所赋予的权利而作出的努力');
INSERT INTO `words` VALUES (76, 'acquaintance', 'əˈkweɪntəns', 'n.', '认识的人;泛泛之交;熟人;', 'The proprietor was an old acquaintance of his...', '业主是他的一位旧相识。');
INSERT INTO `words` VALUES (77, 'acquire', 'əˈkwaɪə(r)', 'v.', '(通过努力、能力、行为表现)获得;购得;获得;', 'General Motors acquired a 50% stake in Saab for about $400m...', '通用汽车公司以大约4亿美元的价格购得了萨博汽车公司50%的股份。');
INSERT INTO `words` VALUES (78, 'acquisition', 'ˌækwɪˈzɪʃn', 'n.', '(知识、技能等的)获得，得到;(多指贵重的)购得物;购置物;', '...the acquisition of a profitable paper recycling company.', '对一家效益好的纸张回收公司的收购');
INSERT INTO `words` VALUES (79, 'acre', 'ˈeɪkə(r)', 'n.', '英亩(4 840平方码，约为4 050平方米);', 'The property is set in two acres of land.', '这座房产占地两英亩。');
INSERT INTO `words` VALUES (80, 'across', 'əˈkrɒs', 'adv.', '从一边到另一边;横过;宽;', 'She walked across the floor and lay down on the bed...', '她走过地板躺在了床上。');
INSERT INTO `words` VALUES (81, 'act', 'ækt', 'n.', '行为;行动;所为;', 'The deaths occurred when police acted to stop widespread looting and vandalism...', '警方采取行动制止猖狂的打砸抢行为时有多人丧生。');
INSERT INTO `words` VALUES (82, 'action', 'ˈækʃn', 'n.', '行动;行为过程;所做之事;', 'The government is taking emergency action to deal with a housing crisis...', '政府正采取紧急措施解决住房危机。');
INSERT INTO `words` VALUES (83, 'activate', 'ˈæktɪveɪt', 'v.', '使活动;激活;使活化;', 'Video cameras with night vision can be activated by movement.', '带夜视镜的摄像机一有动静就会启动。');
INSERT INTO `words` VALUES (84, 'activated', 'ˈæktɪveɪtɪd', 'v.', '使活动;激活;使活化;', 'Video cameras with night vision can be activated by movement.', '带夜视镜的摄像机一有动静就会启动。');
INSERT INTO `words` VALUES (85, 'activation', 'ˌæktɪˈveɪʃ(ə)n', 'n.', '开（驱，起，启）动；触发;活化（作用）；活性（化）；激活；使…产生放射性；敏化;', 'Video cameras with night vision can be activated by movement.', '带夜视镜的摄像机一有动静就会启动。');
INSERT INTO `words` VALUES (86, 'active', 'ˈæktɪv', 'adj.', '(尤指体力上)忙碌的，活跃的;积极的;定期进行的;', 'Having an active youngster about the house can be quite wearing.', '家里有个好动的小孩会是相当累人的。');
INSERT INTO `words` VALUES (87, 'activist', 'ˈæktɪvɪst', 'n.', '积极分子;活跃分子;', 'The police say they suspect the attack was carried out by animal rights activists.', '警方说他们怀疑袭击是由动物权益保护的积极分子们发动的。');
INSERT INTO `words` VALUES (88, 'activity', 'ækˈtɪvəti', 'n.', '活动;热闹状况;活跃;', '...an extraordinary level of activity in the government bonds market.', '政府公债市场异常活跃');
INSERT INTO `words` VALUES (89, 'actor', 'ˈæktə(r)', 'n.', '演员;', 'His father was an actor in the Cantonese Opera Company...', '他的父亲是粤剧团的演员。');
INSERT INTO `words` VALUES (90, 'actress', 'ˈæktrəs', 'n.', '女演员;', 'She\'s a very great dramatic actress.', '她是一个非常了不起的戏剧演员。');
INSERT INTO `words` VALUES (91, 'actual', 'ˈæktʃuəl', 'adj.', '真实的;实际的;(强调事情最重要的部分)真正的，…本身;', 'The segments are filmed using either local actors or the actual people involved...', '这些片断是由当地的演员或真实的当事人参与拍摄的。');
INSERT INTO `words` VALUES (92, 'actually', 'ˈæktʃuəli', 'adv.', '(在口语中用于强调事实)的确，真实地，事实上;(表示想法与事实不一致因而惊奇)居然，竟然;(礼貌地纠正他人)实际上;', 'One afternoon, I grew bored and actually fell asleep for a few minutes...', '一天下午，我觉得有些无聊，还真的睡着了一小会儿。');
INSERT INTO `words` VALUES (93, 'acupressure', 'ˈækjupreʃə(r)', 'n.', '指压(源于日本的一种治疗方式，以手指按压身体特定部位);', 'Acupressure is used to release tension spots in the shoulders and neck.', '指压按摩用于缓解肩部和颈部的紧张。');
INSERT INTO `words` VALUES (94, 'acupuncture', 'ˈækjupʌŋktʃə(r)', 'n.', '针刺疗法;', 'All of those who received the true acupuncture experienced pain relief.', '所有接受了针刺治疗的人都感到疼痛减轻了。');
INSERT INTO `words` VALUES (95, 'acute', 'əˈkjuːt', 'adj.', '十分严重的;(疾病)急性的;灵敏的;', 'The war has aggravated an acute economic crisis...', '战争加剧了原本已很严重的经济危机。');
INSERT INTO `words` VALUES (96, 'ad', 'æd', 'n.', '广告;', 'She replied to a lonely hearts ad.', '她回应了一则征友广告。');
INSERT INTO `words` VALUES (97, 'adapt', 'əˈdæpt', 'v.', '使适应，使适合(新用途、新情况);适应(新情况);改编;', 'The world will be different, and we will have to be prepared to adapt to the change...', '世界会变得不同，我们必须做好准备以适应其变化。');
INSERT INTO `words` VALUES (98, 'adaptability', 'əˌdæptəˈbɪlɪti', 'n.', '适应（合，用）性；可用性；灵活性；适应能力;', '...a more adaptable and skilled workforce...', '一支适应能力及技能更强的劳动大军');
INSERT INTO `words` VALUES (99, 'adaptation', 'ˌædæpˈteɪʃn', 'n.', '改编本;改写本;适应;', '...Branagh\'s screen adaptation of Shakespeare\'s Henry the Fifth.', '布拉纳改编自莎士比亚的《亨利五世》的影片');
INSERT INTO `words` VALUES (100, 'adaptive', 'əˈdæptɪv', 'adj.', '适应的;有适应能力的;', 'Societies need to develop highly adaptive behavioural rules for survival.', '社会需要发展具有高度适应性的生存行为规则。');
INSERT INTO `words` VALUES (101, 'add', 'æd', 'v.', '增加;加添;加;', 'Add the grated cheese to the sauce...', '把磨碎的干酪加到调味汁里。');
INSERT INTO `words` VALUES (102, 'addict', 'ˈædɪkt', 'n.', '吸毒成瘾的人;瘾君子;对…入迷的人;', 'He\'s only 24 years old and a drug addict.', '他只有24岁，却是个瘾君子。');
INSERT INTO `words` VALUES (103, 'addiction', 'əˈdɪkʃn', 'n.', '瘾;入迷;嗜好;', 'She helped him fight his drug addiction.', '她帮助他戒毒。');
INSERT INTO `words` VALUES (104, 'addition', 'əˈdɪʃn', 'n.', '加;加法;增加物;', 'Part-time English classes are offered. In addition, students can take classes in word-processing and computing...', '开设了非全日制的英语班。另外，学生可以修读文字处理和计算机课程。');
INSERT INTO `words` VALUES (105, 'additional', 'əˈdɪʃənl', 'adj.', '附加的;额外的;外加的;', 'The US is sending additional troops to the region...', '美国正向该地区增兵。');
INSERT INTO `words` VALUES (106, 'additionally', 'əˈdɪʃənəli', 'adv.', '此外;另外;额外地;', 'You can pay bills over the Internet. Additionally, you can check your balance or order statements...', '可以在网上支付账单。此外，还能查对余额或订货结算单。');
INSERT INTO `words` VALUES (107, 'additive', 'ˈædətɪv', 'n.', '(尤指食品的)添加剂，添加物;', 'Strict safety tests are carried out on food additives.', '对食品添加剂进行了严格的安全检测。');
INSERT INTO `words` VALUES (108, 'address', 'əˈdres', 'n.', '住址;地址;通信处;', 'The address is 2025 M Street, Northwest, Washington, DC, 20036...', '地址为华盛顿特区，西北区，M大街2025号，邮政编码20036。');
INSERT INTO `words` VALUES (109, 'addressing', 'əˈdresɪŋ', 'v.', '写(收信人)姓名地址;致函;演说;', 'The address is 2025 M Street, Northwest, Washington, DC, 20036...', '地址为华盛顿特区，西北区，M大街2025号，邮政编码20036。');
INSERT INTO `words` VALUES (110, 'adequate', 'ˈædɪkwət', 'adj.', '足够的;合格的;合乎需要的;', 'One in four people worldwide are without adequate homes...', '全世界有1/4的人没有个像样的家。');
INSERT INTO `words` VALUES (111, 'adhere', 'ədˈhɪə(r)', 'v.', '黏附;附着;', 'All members of the association adhere to a strict code of practice...', '协会的所有成员都遵守一套严格的行为规范。');
INSERT INTO `words` VALUES (112, 'adjacent', 'əˈdʒeɪsnt', 'adj.', '与…毗连的;邻近的;', 'He sat in an adjacent room and waited...', '他坐在隔壁房间里等候。');
INSERT INTO `words` VALUES (113, 'adjoin', 'əˈdʒɔɪn', 'v.', '紧挨;邻接;毗连;', 'Fields adjoined the garden and there were no neighbours...', '田园相连，四下无邻。');
INSERT INTO `words` VALUES (114, 'adjust', 'əˈdʒʌst', 'v.', '调整;调节;适应;', 'We have been preparing our fighters to adjust themselves to civil society...', '我们一直在培训我们的战士，以使他们适应普通的社会生活。');
INSERT INTO `words` VALUES (115, 'adjustment', 'əˈdʒʌstmənt', 'n.', '调整;调节;(行为、思想的)调整，适应;', 'Compensation could be made by adjustments to taxation...', '通过调整税收可以作出补偿。');
INSERT INTO `words` VALUES (116, 'administer', 'ədˈmɪnɪstə(r)', 'v.', '管理(公司、组织、机构等);治理(国家);施行;', 'The plan calls for the UN to administer the country until elections can be held...', '该方案呼吁联合国来管理该国，直到能进行选举为止。');
INSERT INTO `words` VALUES (117, 'administration', 'ədˌmɪnɪˈstreɪʃn', 'n.', '(企业、学校等的)管理，行政;施行;执行;', 'Too much time is spent on administration.', '在管理上花费了太多时间。');
INSERT INTO `words` VALUES (118, 'administrative', 'ədˈmɪnɪstrətɪv', 'adj.', '管理的;行政的;', 'Other industries have had to sack managers to reduce administrative costs...', '其他行业只得精简管理人员以减少行政开支。');
INSERT INTO `words` VALUES (119, 'admiration', 'ˌædməˈreɪʃn', 'n.', '钦佩;赞赏;羡慕;', 'I have always had the greatest admiration for him...', '我始终对他最为钦佩。');
INSERT INTO `words` VALUES (120, 'admire', 'ədˈmaɪə(r)', 'v.', '钦佩;赞赏;仰慕;', 'I admired her when I first met her and I still think she\'s marvellous...', '初次见面时，我就非常佩服她，时至今日我仍然认为她很出色。');
INSERT INTO `words` VALUES (121, 'admission', 'ədˈmɪʃn', 'n.', '(机构、组织等的)准许加入，加入权，进入权;(尤指对过错、罪行的)承认，招认，招供;入场费;', 'Students apply for admission to a particular college...', '学生们申请上某所大学。');
INSERT INTO `words` VALUES (122, 'admit', 'ədˈmɪt', 'v.', '(常指勉强)承认;承认(过错、罪行);招认;', 'I am willing to admit that I do make mistakes...', '我愿意承认我确实会犯错。');
INSERT INTO `words` VALUES (123, 'adobe', 'əˈdəʊbi', 'n.', '(建筑用)黏土;黏土坯;', '...a few blocks of adobe houses.', '几个街区的土坯房');
INSERT INTO `words` VALUES (124, 'adolescence', 'ˌædəˈlesns', 'n.', '青春期;青春;', 'Some people become very self-conscious in adolescence.', '有些人在青春期会变得异常害羞。');
INSERT INTO `words` VALUES (125, 'adolescent', 'ˌædəˈlesnt', 'n.', '青少年;', 'It is important that an adolescent boy should have an adult in whom he can confide...', '关键是，青春期的男孩身边应该有一个可以交心的成年人。');
INSERT INTO `words` VALUES (126, 'adopt', 'əˈdɒpt', 'v.', '收养;领养;采用(某方法);', 'Parliament adopted a resolution calling for the complete withdrawal of troops...', '议会采纳了要求全部撤军的决议。');
INSERT INTO `words` VALUES (127, 'adoption', 'əˈdɒpʃn', 'n.', '收养;领养;(想法、计划、名字等的)采用;', 'Parliament adopted a resolution calling for the complete withdrawal of troops...', '议会采纳了要求全部撤军的决议。');
INSERT INTO `words` VALUES (128, 'adore', 'əˈdɔː(r)', 'v.', '热爱，爱慕(某人);喜爱，热爱(某事物);', 'She adored her parents and would do anything to please them.', '她很爱自己的父母，为让他们高兴愿意做任何事。');
INSERT INTO `words` VALUES (129, 'adrenalin', 'əˈdrɛnəlɪn', 'n.', '肾上腺素(能使心跳加速、精力充沛、行动迅速);', 'Seeing the crowd really got my adrenalin pumping.', '看见这么多人确实让我热血沸腾。');
INSERT INTO `words` VALUES (130, 'adult', 'ˈædʌlt', 'n.', '(法律上指能为自己的行为负责的)成年人;成年动物;', 'Becoming a father signified that he was now an adult...', '当了父亲意味着他已是个成年人了。');
INSERT INTO `words` VALUES (131, 'adulteration', '', 'n.', '掺杂；搀假货;', 'The food had been adulterated to increase its weight...', '食物被掺了杂质以增加分量。');
INSERT INTO `words` VALUES (132, 'advance', 'ədˈvɑːns', 'n.', '(尤指武装部队的)前进，行进;进步;进展;', 'Reports from Chad suggest that rebel forces are advancing on the capital...', '来自乍得的报告显示叛军正向首都推进。');
INSERT INTO `words` VALUES (133, 'advanced', 'ədˈvɑːnst', 'adj.', '先进的;高级的;高等的;', 'Without more training or advanced technical skills, they\'ll lose their jobs.', '没有进一步的培训或高级技能的话，他们就会丢掉工作。');
INSERT INTO `words` VALUES (134, 'advantage', 'ədˈvɑːntɪdʒ', 'n.', '有利条件;有利因素;优势;', 'They are deliberately flouting the law in order to obtain an advantage over their competitors...', '他们为了和竞争对手抢夺先机而公然无视法律。');
INSERT INTO `words` VALUES (135, 'advantageous', 'ˌædvənˈteɪdʒəs', 'adj.', '有利的;有好处的;', 'Free exchange of goods was advantageous to all.', '商品自由交换惠泽八方。');
INSERT INTO `words` VALUES (136, 'advent', 'ˈædvent', 'n.', '(重要事件、人物、发明等的)出现，到来;将临节，基督降临节(圣诞节前的四个星期);', '...the leap forward in communication made possible by the advent of the mobile phone.', '手机的问世带来了通讯业的迅猛发展');
INSERT INTO `words` VALUES (137, 'adventure', 'ədˈventʃə(r)', 'n.', '冒险;冒险经历;奇遇;', 'I set off for a new adventure in the United States on the first day of the new year.', '新年第一天，我在美国开始了一次新的冒险。');
INSERT INTO `words` VALUES (138, 'adverse', 'ˈædvɜːs', 'adj.', '不利的;有害的;反面的;', 'The police said Mr Hadfield\'s decision would have no adverse effect on the progress of the investigation...', '警方称哈德菲尔德先生的决定将不会对调查的进展造成不利影响。');
INSERT INTO `words` VALUES (139, 'advertise', 'ˈædvətaɪz', 'v.', '(为…)做广告;登广告;(在报纸、公共场所公告牌、互联网等上)公布，征聘;', 'The players can advertise baked beans, but not rugby boots...', '球员可以给烤菜豆做广告，但不能代言橄榄球靴。');
INSERT INTO `words` VALUES (140, 'advertisement', 'ədˈvɜːtɪsmənt', 'n.', '广告;启事;广告(样)品;', 'Miss Parrish recently placed an advertisement in the local newspaper.', '帕里什小姐最近在本埠报纸上登了一则广告。');
INSERT INTO `words` VALUES (141, 'advice', 'ədˈvaɪs', 'n.', '劝告;忠告;建议;', 'Don\'t be afraid to ask for advice about ordering the meal...', '点餐时可以大胆征求意见。');
INSERT INTO `words` VALUES (142, 'advisable', 'ədˈvaɪzəbl', 'adj.', '明智;可取;', 'Because of the popularity of the region, it is advisable to book hotels or camp sites in advance...', '鉴于该地区很受人们青睐，最好提前预订旅馆或宿营地。');
INSERT INTO `words` VALUES (143, 'advise', 'ədˈvaɪz', 'v.', '劝告;忠告;建议;', 'The minister advised him to leave as soon as possible...', '部长建议他尽快离开。');
INSERT INTO `words` VALUES (144, 'adviser', 'ədˈvaɪzə(r)', 'n.', '顾问;提供意见者;', 'In Washington, the President and his advisers spent the day in meetings.', '在华盛顿，总统和他的顾问们一整天都在开会。');
INSERT INTO `words` VALUES (145, 'advisory', 'ədˈvaɪzəri', 'adj.', '顾问的;咨询的;', '...members of the advisory committee on the safety of nuclear installations...', '核设施安全顾问委员会的成员');
INSERT INTO `words` VALUES (146, 'aerial', 'ˈeəriəl', 'n.', '天线;', 'Weeks of aerial bombardment had destroyed factories and highways...', '数周的空中轰炸已经摧毁了许多工厂和公路。');
INSERT INTO `words` VALUES (147, 'aerospace', 'ˈeərəʊspeɪs', 'n.', '航空航天(工业);航空航天技术;', '...the US aerospace industry.', '美国的航天工业');
INSERT INTO `words` VALUES (148, 'affair', 'əˈfeə(r)', 'n.', '公共事务;政治事务;事件;', 'The government has mishandled the whole affair...', '政府对整个事件处理不当。');
INSERT INTO `words` VALUES (149, 'affect', 'əˈfekt', 'v.', '影响;侵袭;使感染;', 'Nicotine adversely affects the functioning of the heart and arteries...', '尼古丁会对心脏及动脉功能造成损害。');
INSERT INTO `words` VALUES (150, 'affected', 'əˈfektɪd', 'adj.', '假装的;做作的;', 'She had an affected air and a disdainful look.', '她举止做作，神情倨傲。');
INSERT INTO `words` VALUES (151, 'affection', 'əˈfekʃn', 'n.', '喜爱;钟爱;爱情;', 'She thought of him with affection...', '她很想念他。');
INSERT INTO `words` VALUES (152, 'affirm', 'əˈfɜːm', 'v.', '肯定属实;申明;断言;', 'The House of Lords affirmed that the terms of a contract cannot be rewritten retrospectively.', '上议院申明合同条款改写后不具备追溯效力。');
INSERT INTO `words` VALUES (153, 'affirmative', 'əˈfɜːmətɪv', 'adj.', '肯定的;同意的;', 'Haig was desperately eager for an affirmative answer...', '黑格非常渴望得到一个肯定的回答。');
INSERT INTO `words` VALUES (154, 'afflict', 'əˈflɪkt', 'v.', '折磨;使痛苦;', 'Italy has been afflicted by political corruption for decades...', '几十年来意大利一直饱受政治腐败之苦。');
INSERT INTO `words` VALUES (155, 'affluent', 'ˈæfluənt', 'adj.', '富裕的;', 'Cigarette smoking used to be commoner among affluent people.', '吸烟曾经在富人中间更为普遍。');
INSERT INTO `words` VALUES (156, 'afford', 'əˈfɔːd', 'v.', '买得起;(有时间)做，能做;承担得起(后果);', 'My parents can\'t even afford a new refrigerator...', '我父母甚至买不起一台新冰箱。');
INSERT INTO `words` VALUES (157, 'afraid', 'əˈfreɪd', 'adj.', '害怕，畏惧(可能受伤害、受苦);担心(会发生某事);担心，生怕(将发生不快、不幸或危险的事);', 'She did not seem at all afraid...', '她毫无惧色。');
INSERT INTO `words` VALUES (158, 'african', 'ˈæfrɪkən', 'adj.', '非洲的;', '...the African continent.', '非洲大陆');
INSERT INTO `words` VALUES (159, 'after', 'ˈɑːftə(r)', 'prep.', '(时间)在…后;表示反复不断或一个接着一个;跟随;', 'After 19 May, strikes were occurring on a daily basis...', '5月19日之后，每天都会发生罢工。');
INSERT INTO `words` VALUES (160, 'afternoon', 'ˌɑːftəˈnuːn', 'n.', '下午(中午12点至下午6点左右);', 'He\'s arriving in the afternoon...', '他下午会到。');
INSERT INTO `words` VALUES (161, 'again', 'əˈɡen', 'adv.', '再一次;又一次;返回原处;', 'He kissed her again...', '他再一次吻了她。');
INSERT INTO `words` VALUES (162, 'against', 'əˈɡenst', 'prep.', '反对;与…相反;逆;', 'She leaned against him...', '她紧靠着他。');
INSERT INTO `words` VALUES (163, 'agency', 'ˈeɪdʒənsi', 'n.', '服务机构;(尤指)代理机构，经销机构;(政府的)专门机构;', 'We had to hire maids through an agency.', '我们得通过中介雇用女佣。');
INSERT INTO `words` VALUES (164, 'agenda', 'əˈdʒendə', 'n.', '(会议的)议程表，议事日程;', 'Does television set the agenda on foreign policy?...', '电视舆论对外交政策有影响吗？');
INSERT INTO `words` VALUES (165, 'agent', 'ˈeɪdʒənt', 'n.', '(企业、政治等的)代理人，经纪人;(演员、音乐家、运动员、作家等的)代理人;原动力，动因(指对事态起重要作用的人、事物);', 'You are buying direct, rather than through an agent.', '你这是直接购买，而不是通过代理人。');
INSERT INTO `words` VALUES (166, 'aggravate', 'ˈæɡrəveɪt', 'v.', '使严重;使恶化;(尤指故意地)激怒，惹恼;', 'Stress and lack of sleep can aggravate the situation...', '压力和睡眠不足会使情况恶化。');
INSERT INTO `words` VALUES (167, 'aggression', 'əˈɡreʃn', 'n.', '好斗情绪;攻击性;侵犯;', 'Aggression is by no means a male-only trait.', '好斗性绝非男性才具有的特征。');
INSERT INTO `words` VALUES (168, 'aggressive', 'əˈɡresɪv', 'adj.', '好斗的;挑衅的;侵略的;', 'Some children are much more aggressive than others...', '一些孩子比其他孩子更好斗。');
INSERT INTO `words` VALUES (169, 'agitate', 'ˈædʒɪteɪt', 'v.', '(尤指为法律、社会状况的改变而)激烈争论，鼓动，煽动;激怒;使不安;', 'The women who worked in these mills had begun to agitate for better conditions.', '在这些工厂里做工的妇女们开始抗议要求改善工作条件。');
INSERT INTO `words` VALUES (170, 'agitation', 'ˌædʒɪˈteɪʃn', 'n.', '焦虑不安;忧虑;烦乱;', 'Danny returned to Father\'s house in a state of intense agitation...', '丹尼回到神父的房子时显得极为焦虑不安。');
INSERT INTO `words` VALUES (171, 'ago', 'əˈɡəʊ', 'adv.', '(与动词简单过去时连用)以前;', 'He was killed a few days ago in a skiing accident...', '几天前他在一场滑雪事故中丧生。');
INSERT INTO `words` VALUES (172, 'agony', 'ˈæɡəni', 'n.', '(精神或肉体的)极度痛苦;', 'A new machine may save thousands of animals from the agony of drug tests...', '一种新型机器也许可以将成千上万的动物从药物试验的痛苦中解脱出来。');
INSERT INTO `words` VALUES (173, 'agree', 'əˈɡriː', 'v.', '同意;赞成;(对…)取得一致意见，一致同意;', 'If we agreed all the time it would be a bit boring, wouldn\'t it?...', '如果我们总是意见一致，就会有点无趣，对吗？');
INSERT INTO `words` VALUES (174, 'agreeable', 'əˈɡriːəbl', 'adj.', '愉悦的;讨人喜欢的;宜人的;', '...workers in more agreeable and better paid occupations.', '在条件更惬意、报酬更丰厚的行业工作的人');
INSERT INTO `words` VALUES (175, 'agreement', 'əˈɡriːmənt', 'n.', '协定;协议;契约;', 'It looks as though a compromise agreement has now been reached...', '看起来好像现已达成一个妥协协定。');
INSERT INTO `words` VALUES (176, 'agricultural', 'ˌæɡrɪˈkʌltʃərəl', 'adj.', '农业的;农用的;(地区或社会)以农业为主的，农业高度发达的;', '...agricultural land.', '农田');
INSERT INTO `words` VALUES (177, 'agriculture', 'ˈæɡrɪkʌltʃə(r)', 'n.', '农业;农学;农艺;', 'The Ukraine is strong both in industry and agriculture.', '乌克兰的工业和农业都很发达。');
INSERT INTO `words` VALUES (178, 'aid', 'eɪd', 'n.', '援助;救援物资;援助款项;', '...regular flights carrying humanitarian aid to Cambodia...', '向柬埔寨运送人道援助物资的定期航班');
INSERT INTO `words` VALUES (179, 'ailment', 'ˈeɪlmənt', 'n.', '轻病;小恙;', 'The pharmacist can assist you with the treatment of common ailments.', '药剂师能帮你治疗一般的病痛。');
INSERT INTO `words` VALUES (180, 'aim', 'eɪm', 'n.', '目的;目标;瞄准;', 'He is aiming for the 100 metres world record...', '他志在打破100米世界纪录。');
INSERT INTO `words` VALUES (181, 'air', 'eə(r)', 'n.', '空气;空中;天空;', 'Draughts help to circulate air...', '穿堂风有助于空气流通。');
INSERT INTO `words` VALUES (182, 'aircraft', 'ˈeəkrɑːft', 'n.', '飞机;航空器;', 'The return flight of the aircraft was delayed...', '回程航班被延误了。');
INSERT INTO `words` VALUES (183, 'airing', 'ˈeərɪŋ', 'n.', '(意见等的)公开发表，公开讨论;晾;晾晒;', 'Draughts help to circulate air...', '穿堂风有助于空气流通。');
INSERT INTO `words` VALUES (184, 'airline', 'ˈeəlaɪn', 'n.', '航空公司;', '...the Dutch national airline KLM.', '荷兰国家航空公司KLM');
INSERT INTO `words` VALUES (185, 'airmail', 'ˈeəmeɪl', 'n.', '航空邮递;', '...an airmail letter...', '航空信');
INSERT INTO `words` VALUES (186, 'airport', 'ˈeəpɔːt', 'n.', '航空站;航空港;机场;', '...Heathrow Airport, the busiest international airport in the world.', '希思罗机场，世界上最繁忙的国际机场');
INSERT INTO `words` VALUES (187, 'aisle', 'aɪl', 'n.', '(教堂、戏院、火车等座位间或超级市场货架间的)走道，过道;', 'He started down the centre aisle.', '他从中间过道开始往前走。');
INSERT INTO `words` VALUES (188, 'alarm', 'əˈlɑːm', 'n.', '惊恐;惊慌;恐慌;', 'The news was greeted with alarm by MPs...', '得知这个消息，议员们忧心忡忡。');
INSERT INTO `words` VALUES (189, 'album', 'ˈælbəm', 'n.', '相册;影集;集邮簿;', 'Chris likes music and has a large collection of albums and cassettes.', '克里斯喜欢音乐，收集了大量的唱片和磁带。');
INSERT INTO `words` VALUES (190, 'alcohol', 'ˈælkəhɒl', 'n.', '含酒精饮料;酒;酒精;', 'Do either of you smoke cigarettes or drink alcohol?...', '你们俩有人吸烟或喝酒吗？');
INSERT INTO `words` VALUES (191, 'alcoholic', 'ˌælkəˈhɒlɪk', 'adj.', '酒精的;含酒精的;饮酒引起的;', 'He showed great courage by admitting that he is an alcoholic.', '他以极大的勇气承认自己酗酒。');
INSERT INTO `words` VALUES (192, 'alert', 'əˈlɜːt', 'adj.', '警觉的;警惕的;戒备的;', 'We all have to stay alert...', '我们大家都必须保持警惕。');
INSERT INTO `words` VALUES (193, 'alias', 'ˈeɪliəs', 'adv.', '(罪犯或演员等)又名，亦名，别名，化名;', 'Using an alias, he had rented a house in Fleet, Hampshire.', '他用化名在汉普郡的舰队街租了间房子。');
INSERT INTO `words` VALUES (194, 'alien', 'ˈeɪliən', 'adj.', '陌生的;不熟悉;外国的;', 'He said they were opposed to the presence of alien forces in the region.', '他说他们反对外国军队驻扎在该地区。');
INSERT INTO `words` VALUES (195, 'alienate', 'ˈeɪliəneɪt', 'v.', '使疏远;使不友好;离间;', 'The government cannot afford to alienate either group.', '疏远两个团体中的任何一方都是政府承受不起的。');
INSERT INTO `words` VALUES (196, 'alienation', 'ˌeɪliəˈneɪʃn', 'n.', '异化；疏远；转让；（内科）精神错乱；间离效果;', 'The government cannot afford to alienate either group.', '疏远两个团体中的任何一方都是政府承受不起的。');
INSERT INTO `words` VALUES (197, 'align', 'əˈlaɪn', 'v.', '排整齐;校准;(尤指)使成一条直线;', 'There are signs that the prime minister is aligning himself with the liberals...', '有迹象表明首相正在与自由党人结盟。');
INSERT INTO `words` VALUES (198, 'aligned', 'əˈlaɪnd', 'v.', '排整齐;校准;(尤指)使成一条直线;', 'There are signs that the prime minister is aligning himself with the liberals...', '有迹象表明首相正在与自由党人结盟。');
INSERT INTO `words` VALUES (199, 'alignment', 'əˈlaɪnmənt', 'n.', '排成直线;(国家、团体间的)结盟;', 'The church should have no political alignment...', '教会不应与政治结盟。');
INSERT INTO `words` VALUES (200, 'alike', 'əˈlaɪk', 'adj.', '相像;十分相似;', 'We looked very alike.', '我们长得很像。');
INSERT INTO `words` VALUES (201, 'alive', 'əˈlaɪv', 'adj.', '活着;在世;情绪饱满;', 'She does not know if he is alive or dead...', '她不知道他是生是死。');
INSERT INTO `words` VALUES (202, 'alkalinity', 'ˌælkəˈlɪnəti', 'n.', '碱度;碱性;', 'Some soils are actually too alkaline for certain plant life.', '有些土壤其实碱性过强，不利于某些植物生长。');
INSERT INTO `words` VALUES (203, 'all', 'ɔːl', 'det.', '所有;全部;全体;', '...the restaurant that Hugh and all his friends go to...', '休和他所有的朋友都光顾的餐馆');
INSERT INTO `words` VALUES (204, 'allegation', 'ˌæləˈɡeɪʃn', 'n.', '(无证据的)说法，指控;', 'The company has denied the allegations...', '公司否认了这些指控。');
INSERT INTO `words` VALUES (205, 'allege', 'əˈledʒ', 'v.', '(未提出证据)断言，指称，声称;', 'She alleged that there was rampant drug use among the male members of the group...', '她声称该团体中有大量的男性成员吸毒。');
INSERT INTO `words` VALUES (206, 'alleged', 'əˈledʒd', 'adj.', '宣称的;据传闻的;涉嫌的;', 'They have begun a hunger strike in protest at the alleged beating.', '他们开始绝食以抗议有人所宣称的殴打行为。');
INSERT INTO `words` VALUES (207, 'allegiance', 'əˈliːdʒəns', 'n.', '(对政党、宗教、统治者的)忠诚，效忠，拥戴;', 'My allegiance to Kendall and his company ran deep.', '我对肯德尔及其公司的忠诚加深了。');
INSERT INTO `words` VALUES (208, 'allergic', 'əˈlɜːdʒɪk', 'adj.', '(对…)变态反应的，变应的，过敏的;变态反应性的;变应性的;', 'I\'m allergic to cats.', '我对猫过敏。');
INSERT INTO `words` VALUES (209, 'allergy', 'ˈælədʒi', 'n.', '变态反应;过敏反应;', 'Food allergies can result in an enormous variety of different symptoms...', '食物过敏会引发很多不同的症状。');
INSERT INTO `words` VALUES (210, 'alleviate', 'əˈliːvieɪt', 'v.', '减轻;缓和;缓解;', 'Nowadays, a great deal can be done to alleviate back pain.', '如今，减轻背部疼痛可以有许多方法。');
INSERT INTO `words` VALUES (211, 'alliance', 'əˈlaɪəns', 'n.', '(国家、政党等的)结盟，同盟;结盟团体;联盟;', 'The two parties were still too much apart to form an alliance.', '两党分歧甚大，还远未能结成同盟。');
INSERT INTO `words` VALUES (212, 'allied', 'ˈælaɪd , əˈlaɪd', 'adj.', '(国与国协同作战)结盟的，联盟的;(第一次世界大战期间)协约国的;(第二次世界大战期间)同盟国的;', '...the approaching Allied forces...', '正在到来的盟军');
INSERT INTO `words` VALUES (213, 'allocate', 'ˈæləkeɪt', 'v.', '拨…(给);划…(归);分配…(给);', 'Tickets are limited and will be allocated to those who apply first...', '票数有限，先申请者先得。');
INSERT INTO `words` VALUES (214, 'allocation', 'ˌæləˈkeɪʃn', 'n.', '划拨的款项;拨给的场地;分配的东西;', 'The aid allocation for Pakistan was still under review...', '拨给巴基斯坦的援助物资仍在审核当中。');
INSERT INTO `words` VALUES (215, 'allot', 'əˈlɒt', 'v.', '分配，配给(时间、钱财等);分派(任务等);', 'The seats are allotted to the candidates who have won the most votes...', '席位被分配给了赢得选票最多的候选人。');
INSERT INTO `words` VALUES (216, 'allow', 'əˈlaʊ', 'v.', '允许;准许;给予;', 'The children are not allowed to watch violent TV programmes...', '儿童不准收看含暴力内容的电视节目。');
INSERT INTO `words` VALUES (217, 'allowable', 'əˈlaʊəbl', 'adj.', '(法律、规章等)允许的，承认的，容许的;可免稅的(錢款部分);', 'Capital punishment is allowable only under exceptional circumstances.', '只有特殊情况才允许执行死刑。');
INSERT INTO `words` VALUES (218, 'allowance', 'əˈlaʊəns', 'n.', '津贴;补贴;补助;', 'He lives on a single parent\'s allowance of £70 a week...', '他靠每周70英镑的单亲家庭生活补助过日子。');
INSERT INTO `words` VALUES (219, 'allowed', 'əˈlaʊd', 'v.', '允许;准许;给予;', 'The children are not allowed to watch violent TV programmes...', '儿童不准收看含暴力内容的电视节目。');
INSERT INTO `words` VALUES (220, 'alloy', 'ˈælɔɪ , əˈlɔɪ', 'n.', '合金;', 'Bronze is an alloy of copper and tin...', '青铜是铜和锡的合金。');
INSERT INTO `words` VALUES (221, 'almost', 'ˈɔːlməʊst', 'adv.', '几乎;差不多;', 'The couple had been dating for almost three years...', '这对情侣相恋已近3年。');
INSERT INTO `words` VALUES (222, 'alms', 'ɑːmz', 'n.', '施舍物(或金);救济金(或物);', 'Alms were distributed to those in need.', '救济品被分发给困难人群。');
INSERT INTO `words` VALUES (223, 'alone', 'əˈləʊn', 'adj./adv.', '独自;独力;单独;', 'There is nothing so fearful as to be alone in a combat situation...', '没有比孤身奋战更可怕的了。');
INSERT INTO `words` VALUES (224, 'along', 'əˈlɒŋ', 'prep.', '沿着;顺着;靠着…边;', 'Newman walked along the street alone...', '纽曼独自走在街上。');
INSERT INTO `words` VALUES (225, 'alongside', 'əˌlɒŋˈsaɪd', 'prep.', '在…旁边;沿着…的边;与…一起;', 'He crossed the street and walked alongside Central Park...', '他穿过街道，沿着中央公园走。');
INSERT INTO `words` VALUES (226, 'aloud', 'əˈlaʊd', 'adv.', '出声地;大声地;', 'When we were children, our father read aloud to us...', '我们小的时候，父亲会给我们朗读。');
INSERT INTO `words` VALUES (227, 'alphabet', 'ˈælfəbet', 'n.', '(一种语言的)字母表，全部字母;', 'The modern Russian alphabet has 31 letters...', '现代俄语字母表有31个字母。');
INSERT INTO `words` VALUES (228, 'alphabetical', 'ˌælfəˈbetɪkl', 'adj.', '按字母(表)顺序的;', 'Their herbs and spices are arranged in alphabetical order.', '他们的香草和调味料按字母顺序摆放。');
INSERT INTO `words` VALUES (229, 'alphabetically', '', 'adv.', '按字母（表）顺序地;', 'Their herbs and spices are arranged in alphabetical order.', '他们的香草和调味料按字母顺序摆放。');
INSERT INTO `words` VALUES (230, 'already', 'ɔːlˈredi', 'adv.', '已经;早已;(表示惊奇)已经，都;', 'They had already voted for him at the first ballot...', '在第一次投票选举时，他们已经投票给他了。');
INSERT INTO `words` VALUES (231, 'also', 'ˈɔːlsəʊ', 'adv.', '而且;此外;也;', 'It is the work of Ivor Roberts-Jones, who also produced the statue of Churchill in Parliament Square...', '这是艾弗·罗伯茨-琼斯的作品，他还创作了议会广场上的丘吉尔塑像。');
INSERT INTO `words` VALUES (232, 'alter', 'ˈɔːltə(r)', 'v.', '(使)改变，更改，改动;修改(衣服使更合身);', 'Little had altered in the village...', '村子里几乎没有什么变化。');
INSERT INTO `words` VALUES (233, 'alteration', 'ˌɔːltəˈreɪʃn', 'n.', '改变;变化;更改;', 'Making some simple alterations to your diet will make you feel fitter...', '对日常饮食做些简单的调整就会让你身体更健康。');
INSERT INTO `words` VALUES (234, 'alternatively', 'ɔːlˈtɜːnətɪvli', 'adv.', '(引出第二种选择或可能的建议)要不，或者;', 'Allow about eight hours for the drive from Calais. Alternatively, you can fly to Brive.', '从加来开车去，要留出大约8小时的时间。或者，你可以坐飞机前往布里夫。');
INSERT INTO `words` VALUES (235, 'although', 'ɔːlˈðəʊ', 'conj.', '虽然;尽管;即使;', 'Although he is known to only a few, his reputation among them is very great...', '虽然知道他的人不多，但他在这些人中名声却很响。');
INSERT INTO `words` VALUES (236, 'altitude', 'ˈæltɪtjuːd', 'n.', '海拔;海拔高度;高程;', 'The aircraft had reached its cruising altitude of about 39,000 feet...', '飞机已到达约3.9万英尺的巡航高度。');
INSERT INTO `words` VALUES (237, 'altogether', 'ˌɔːltəˈɡeðə(r)', 'adv.', '(用以强调)完全，全部;(表示总数或总额)总共，一共;总之;', 'When Artie stopped calling altogether, Julie found a new man...', '当阿蒂彻底不再上门后，朱莉又找了个男人。');
INSERT INTO `words` VALUES (238, 'aluminium', 'ˌæljəˈmɪniəm', 'n.', '铝;', '...aluminium cans.', '铝罐');
INSERT INTO `words` VALUES (239, 'always', 'ˈɔːlweɪz', 'adv.', '总是;每次都是;一直;', 'Whenever I get into a relationship, I always fall madly in love...', '我每次谈恋爱都深陷其中，无法自拔。');
INSERT INTO `words` VALUES (240, 'amass', 'əˈmæs', 'v.', '(尤指大量)积累，积聚;', 'How had he amassed his fortune?', '他是如何积累财富的呢？');
INSERT INTO `words` VALUES (241, 'amateur', 'ˈæmətə(r)', 'n.', '业余爱好者;业余运动员;生手;', 'Jerry is an amateur who dances because he feels like it...', '杰里是个业余跳舞爱好者，他跳舞是因为自己喜欢。');
INSERT INTO `words` VALUES (242, 'amaze', 'əˈmeɪz', 'v.', '使惊奇;使惊愕;使惊诧;', 'He amazed us by his knowledge of Welsh history...', '他对威尔士的历史知之甚多，让我们惊讶。');
INSERT INTO `words` VALUES (243, 'amazing', 'əˈmeɪzɪŋ', 'adj.', '令人大为惊奇的;(尤指)令人惊喜(或惊羡、惊叹)的;', 'It\'s amazing what we can remember with a little prompting...', '稍加提醒我们就能想起很多东西，真是让人惊奇。');
INSERT INTO `words` VALUES (244, 'ambassador', 'æmˈbæsədə(r)', 'n.', '大使;使节;', '...the German ambassador to Poland.', '德国驻波兰大使');
INSERT INTO `words` VALUES (245, 'ambiguity', 'ˌæmbɪˈɡjuːəti', 'n.', '歧义;一语多义;模棱两可的词;', 'There is considerable ambiguity about what this part of the agreement actually means.', '协议这一部分的真正含义很不明确。');
INSERT INTO `words` VALUES (246, 'ambiguous', 'æmˈbɪɡjuəs', 'adj.', '模棱两可的;含混不清的;不明确的;', 'This agreement is very ambiguous and open to various interpretations...', '这份协议非常含糊，可以有多种解释。');
INSERT INTO `words` VALUES (247, 'ambition', 'æmˈbɪʃn', 'n.', '追求的目标;夙愿;野心;', 'His ambition is to sail round the world...', '他的梦想是环球航行。');
INSERT INTO `words` VALUES (248, 'ambitious', 'æmˈbɪʃəs', 'adj.', '有野心的;有雄心的;费力的;', 'Chris is so ambitious, so determined to do it all...', '克里斯踌躇满志，决心要将它都做完。');
INSERT INTO `words` VALUES (249, 'amenable', 'əˈmiːnəbl', 'adj.', '顺从的;顺服的;可用某种方式处理的;', 'The Jordanian leader seemed amenable to attending a conference...', '约旦领导人似乎愿意参加会议。');
INSERT INTO `words` VALUES (250, 'amend', 'əˈmend', 'v.', '修正，修订(法律文件、声明等);', 'The president agreed to amend the constitution and allow multi-party elections...', '总统同意修订宪法，允许多党选举。');
INSERT INTO `words` VALUES (251, 'amendment', 'əˈmendmənt', 'n.', '(法律、文件的)改动，修正案，修改，修订;(美国宪法的)修正案;', '...an amendment to the defense bill.', '防御法案的修正案');
INSERT INTO `words` VALUES (252, 'amends', 'əˈmendz', 'v.', '修正，修订(法律文件、声明等);', 'The president agreed to amend the constitution and allow multi-party elections...', '总统同意修订宪法，允许多党选举。');
INSERT INTO `words` VALUES (253, 'american', 'əˈmerɪkən', 'n.', '美洲人;(尤指)美国人;美国英语;', '...the American Ambassador at the United Nations.', '美国驻联合国大使');
INSERT INTO `words` VALUES (254, 'amiable', 'ˈeɪmiəbl', 'adj.', '和蔼可亲的;亲切友好的;', 'She had been surprised at how amiable and polite he had seemed.', '看到他如此和蔼可亲、彬彬有礼，她非常惊讶。');
INSERT INTO `words` VALUES (255, 'ammunition', 'ˌæmjuˈnɪʃn', 'n.', '弹药;(辩论中可攻击对方的)信息，事实，炮弹;', 'He had only seven rounds of ammunition for the revolver.', '他这把左轮手枪只有7梭子子弹。');
INSERT INTO `words` VALUES (256, 'amount', 'əˈmaʊnt', 'n.', '金额;数量;数额;', 'He needs that amount of money to survive...', '他需要那样一笔钱以生存下去。');
INSERT INTO `words` VALUES (257, 'ample', 'ˈæmpl', 'adj.', '足够的;丰裕的;丰满的;', 'There\'ll be ample opportunity to relax, swim and soak up some sun...', '将会有充足的机会去放松、游泳和晒太阳。');
INSERT INTO `words` VALUES (258, 'amplification', 'ˌæmplɪfɪˈkeɪʃn', 'n.', '扩大（充）；膨胀;', 'This landscape seemed to trap and amplify sounds...', '这种地貌好像可以笼住并放大声音。');
INSERT INTO `words` VALUES (259, 'amplify', 'ˈæmplɪfaɪ', 'v.', '放大，增强(声音等);阐发，充实(故事、事情、陈述等);', 'This landscape seemed to trap and amplify sounds...', '这种地貌好像可以笼住并放大声音。');
INSERT INTO `words` VALUES (260, 'amplitude', 'ˈæmplɪtjuːd', 'n.', '(声音、无线电波等的)振幅;', 'As we fall asleep the amplitude of brain waves slowly becomes greater.', '进入睡眠状态时，我们的脑电波振幅慢慢变大。');
INSERT INTO `words` VALUES (261, 'amputate', 'ˈæmpjuteɪt', 'v.', '(用外科手术)切断;截(肢);', 'To save his life, doctors amputated his legs...', '为了挽救他的生命，医生们截掉了他的双腿。');
INSERT INTO `words` VALUES (262, 'amputation', 'ˌæmpjuˈteɪʃn', 'n.', '截肢（术），切断（术）；删除，砍除；削减;', 'To save his life, doctors amputated his legs...', '为了挽救他的生命，医生们截掉了他的双腿。');
INSERT INTO `words` VALUES (263, 'amuse', 'əˈmjuːz', 'v.', '逗笑;逗乐;(提供)消遣;', 'The thought seemed to amuse him...', '这种想法好像把他逗乐了。');
INSERT INTO `words` VALUES (264, 'amused', 'əˈmjuːzd', 'adj.', '逗乐的;觉得好笑的;', 'Sara was not amused by Franklin\'s teasing...', '被富兰克林揶揄之后，萨拉的脸上全无笑意。');
INSERT INTO `words` VALUES (265, 'amusement', 'əˈmjuːzmənt', 'n.', '可笑;愉悦;娱乐;', 'He stopped and watched with amusement to see the child so absorbed...', '他停下来，饶有兴趣地看着孩子如此投入。');
INSERT INTO `words` VALUES (266, 'analgesic', 'ˌænəlˈdʒiːzɪk', 'n.', '止痛药;镇痛剂;', 'Aloe may have an analgesic effect on inflammation and minor skin irritations.', '芦荟可能对发炎以及轻微的皮肤炎症具有镇痛作用。');
INSERT INTO `words` VALUES (267, 'analogy', 'əˈnælədʒi', 'n.', '类比;比拟;比喻;', 'It is probably easier to make an analogy between the courses of the planets, and two trains travelling in the same direction.', '将行星的运行比作两列火车朝同一方向行驶可能更容易些。');
INSERT INTO `words` VALUES (268, 'analysis', 'əˈnæləsɪs', 'n.', '(对事物的)分析，分析结果;(对物质的)分析;', 'Her criteria defy analysis...', '她的标准让人搞不明白。');
INSERT INTO `words` VALUES (269, 'analyst', 'ˈænəlɪst', 'n.', '分析者;化验员;', '...a political analyst.', '政治分析家');
INSERT INTO `words` VALUES (270, 'analytical', 'ˌænəˈlɪtɪkl', 'adj.', '分析的;解析的;分析性的;', 'I have an analytical approach to every survey.', '对每项调查我都采用分析方法。');
INSERT INTO `words` VALUES (271, 'anatomically', '', 'adv.', '解剖学上;', '...minute anatomical differences between insects.', '昆虫身体结构之间的细微差别');
INSERT INTO `words` VALUES (272, 'ancestor', 'ˈænsestə(r)', 'n.', '祖宗;祖先;(动物的)原种;', '...our daily lives, so different from those of our ancestors...', '与我们祖先的生活有着天壤之别的我们的日常生活');
INSERT INTO `words` VALUES (273, 'ancient', 'ˈeɪnʃənt', 'adj.', '古代的;古老的;很老的;', 'They believed ancient Greece and Rome were vital sources of learning.', '他们认为古代希腊罗马是知识的重要发源地。');
INSERT INTO `words` VALUES (274, 'and', 'ənd , ænd', 'conj.', '和;与;同;', 'When he returned, she and Simon had already gone...', '他回来时，她和西蒙已经走了。');
INSERT INTO `words` VALUES (275, 'anecdote', 'ˈænɪkdəʊt', 'n.', '逸事;趣闻;传闻;', 'Pete was telling them an anecdote about their mother...', '皮特正在给他们讲一件他们母亲的趣闻。');
INSERT INTO `words` VALUES (276, 'anger', 'ˈæŋɡə(r)', 'n.', '怒;怒火;怒气;', 'He cried with anger and frustration...', '他愤怒而又沮丧得哭了起来。');
INSERT INTO `words` VALUES (277, 'angle', 'ˈæŋɡl', 'n.', '角;斜角;角度;', 'The boat is now leaning at a 30 degree angle.', '船正以30度角倾斜。');
INSERT INTO `words` VALUES (278, 'angry', 'ˈæŋɡri', 'adj.', '发怒的;愤怒的;生气的;', 'She had been very angry at the person who stole her new bike...', '她很生气有人偷走了她的新自行车。');
INSERT INTO `words` VALUES (279, 'anguish', 'ˈæŋɡwɪʃ', 'n.', '剧痛;极度痛苦;苦恼;', 'A cry of anguish burst from her lips...', '她突然痛苦地大叫了一声。');
INSERT INTO `words` VALUES (280, 'animal', 'ˈænɪml', 'n.', '兽;牲畜;动物(不包括鸟、鱼、爬行动物、昆虫或人);', 'He was attacked by wild animals...', '他遭到野兽的袭击。');
INSERT INTO `words` VALUES (281, 'animation', 'ˌænɪˈmeɪʃn', 'n.', '生气;活力;富有生命力;', 'The films are a mix of animation and full-length features.', '这些电影将动画制作和长篇故事片融为一体。');
INSERT INTO `words` VALUES (282, 'ankle', 'ˈæŋkl', 'n.', '踝;踝关节;', 'John twisted his ankle badly.', '约翰的脚脖子崴得很严重。');
INSERT INTO `words` VALUES (283, 'annihilate', 'əˈnaɪəleɪt', 'v.', '消灭;歼灭;毁灭;', 'There are lots of ways of annihilating the planet...', '毁灭地球有很多方法。');
INSERT INTO `words` VALUES (284, 'anniversary', 'ˌænɪˈvɜːsəri', 'n.', '周年纪念日;', '...the one hundredth anniversary of the birth of Ho Chi Minh.', '胡志明诞辰一百周年纪念');
INSERT INTO `words` VALUES (285, 'annotate', 'ˈænəteɪt', 'v.', '给…作注解(或评注);', 'Historians annotate, check and interpret the diary selections.', '历史学家对日记选篇进行加注、核查以及阐释。');
INSERT INTO `words` VALUES (286, 'annotation', 'ˌænəˈteɪʃn', 'n.', '注解;评注;注文;', 'She retained a number of copies for further annotation.', '她保留了许多副本以便作进一步的注解。');
INSERT INTO `words` VALUES (287, 'announce', 'əˈnaʊns', 'v.', '宣布，宣告(决定、计划等);(尤指通过广播)通知;声称;', 'He will announce tonight that he is resigning from office...', '他将于今晚宣布辞职。');
INSERT INTO `words` VALUES (288, 'announcement', 'əˈnaʊnsmənt', 'n.', '(一项)公告，布告，通告;(指行动)宣布，宣告;', 'Sir Robert made his announcement after talks with the President...', '在与总统会谈之后，罗伯特爵士发表了声明。');
INSERT INTO `words` VALUES (289, 'announcer', 'əˈnaʊnsə(r)', 'n.', '(广播、电视的)广播员，播音员，节目主持人;(车站、机场等的)广播员;', 'The radio announcer said it was nine o\'clock...', '电台播音员报时9点整。');
INSERT INTO `words` VALUES (290, 'annoy', 'əˈnɔɪ', 'v.', '使恼怒;使生气;打扰;', 'Try making a note of the things which annoy you...', '试着把烦心事写下来。');
INSERT INTO `words` VALUES (291, 'annoyed', 'əˈnɔɪd', 'adj.', '恼怒;生气;烦恼;', 'She tapped her forehead and looked annoyed with herself...', '她拍打着脑门，看上去在生自己的气。');
INSERT INTO `words` VALUES (292, 'annual', 'ˈænjuəl', 'adj.', '每年的;一年一次的;年度的;', '...the annual conference of Britain\'s trade union movement...', '英国工会运动的年会');
INSERT INTO `words` VALUES (293, 'anonymous', 'əˈnɒnɪməs', 'adj.', '不知姓名的;名字不公开的;匿名的;', 'You can remain anonymous if you wish...', '你愿意的话可以不透露姓名。');
INSERT INTO `words` VALUES (294, 'another', 'əˈnʌðə(r)', 'det./pron.', '又一;再一;另一(事物或人);', 'Mrs. Madrigal buttered another piece of toast...', '马德里加尔夫人给另一片烤面包涂上了黄油。');
INSERT INTO `words` VALUES (295, 'answer', 'ˈɑːnsə(r)', 'n.', '答复;回答;(试题、练习等的)答案，正确答案;', 'I knew Ben was lying when he answered me...', '我知道本回答我时在撒谎。');
INSERT INTO `words` VALUES (296, 'ant', 'ænt', 'n.', '蚂蚁;', 'Ants swarmed up out of the ground and covered her shoes and legs.', '蚂蚁从地下成群地钻出来，爬满了她的鞋子和双腿。');
INSERT INTO `words` VALUES (297, 'antagonism', 'ænˈtæɡənɪzəm', 'n.', '对立情绪;对抗情绪;敌对;', 'There is still much antagonism between trades unions and the oil companies...', '工会和石油公司之间仍然存在着相当大的敌意。');
INSERT INTO `words` VALUES (298, 'antagonist', 'ænˈtæɡənɪst', 'n.', '对立者;对抗者;对手;', 'Spassky had never previously lost to his antagonist.', '斯帕斯基以往从未输给过他的对手。');
INSERT INTO `words` VALUES (299, 'antagonistic', 'ænˌtæɡəˈnɪstɪk', 'adj.', '对立情绪的;对抗的;敌对的;', 'Nearly all the women I interviewed were aggressively antagonistic to the idea.', '几乎我采访过的所有女性都对这个观点表示了强烈的反感。');
INSERT INTO `words` VALUES (300, 'antecedent', 'ˌæntɪˈsiːdnt', 'n.', '前事;前情;祖先;', 'We shall first look briefly at the historical antecedents of this theory.', '我们首先应大致看一下该理论在历史上的前身。');
INSERT INTO `words` VALUES (301, 'anthem', 'ˈænθəm', 'n.', '国歌;(组织或群体的)社歌，团歌;(宗教)赞美歌(常由管风琴伴奏);', 'The band played the Czech anthem.', '乐队演奏了捷克国歌。');
INSERT INTO `words` VALUES (302, 'anthology', 'ænˈθɒlədʒi', 'n.', '(不同作家作品的)选集;', '...an anthology of poetry.', '诗歌选集');
INSERT INTO `words` VALUES (303, 'antibiotic', 'ˌæntibaɪˈɒtɪk', 'n.', '抗菌素;抗生素(如青霉素);', 'Your doctor may prescribe a course of antibiotics.', '你的医生可能会开一个疗程的抗生素。');
INSERT INTO `words` VALUES (304, 'anticipate', 'ænˈtɪsɪpeɪt', 'v.', '预料;预期;预见，预计(并做准备);', 'At the time we couldn\'t have anticipated the result of our campaigning...', '那时我们不可能预料到我们这项运动的结果。');
INSERT INTO `words` VALUES (305, 'anticipation', 'ænˌtɪsɪˈpeɪʃn', 'n.', '预料;预期;预见;', 'There\'s been an atmosphere of anticipation around here for a few days now...', '几天来这里一直有一种满怀期待的气氛。');
INSERT INTO `words` VALUES (306, 'antidote', 'ˈæntidəʊt', 'n.', '解毒药;解毒剂;消除不愉快的事物;', 'When he returned, he noticed their sickness and prepared an antidote.', '他回来后注意到了他们的病状，于是配制了解毒剂。');
INSERT INTO `words` VALUES (307, 'antique', 'ænˈtiːk', 'adj.', '古老的;古董的;', '...a genuine antique.', '一件古董真品');
INSERT INTO `words` VALUES (308, 'antiseptic', 'ˌæntiˈseptɪk', 'n.', '防腐剂;抗菌剂;', 'She bathed the cut with antiseptic.', '她用消毒剂浸洗伤口。');
INSERT INTO `words` VALUES (309, 'ants', 'ænts', 'n.', '蚂蚁;', 'Ants swarmed up out of the ground and covered her shoes and legs.', '蚂蚁从地下成群地钻出来，爬满了她的鞋子和双腿。');
INSERT INTO `words` VALUES (310, 'anxiety', 'æŋˈzaɪəti', 'n.', '焦虑;忧虑;担心;', 'Her voice was full of anxiety...', '她的声音满是焦虑不安。');
INSERT INTO `words` VALUES (311, 'anxious', 'ˈæŋkʃəs', 'adj.', '焦虑;忧虑;担心;', 'Both the Americans and the Russians are anxious to avoid conflict in South Asia...', '美国人和俄罗斯人都迫切希望避免在南亚发生冲突。');
INSERT INTO `words` VALUES (312, 'any', 'ˈeni', 'det.', '(与不可数或复数名词连用，用于否定句和疑问句，也用于if或whether之后，或紧接某些动词如prevent、ban、forbid等)任何的，任一的;(与单数可数名词连用)任一;非一般的;', 'I never make any big decisions...', '我从未作过任何重大决定。');
INSERT INTO `words` VALUES (313, 'anyone', 'ˈeniwʌn', 'pron.', '(用于否定句、疑问句，也用于if或whether之后，或紧接prevent、forbid、avoid等动词，代替someone)任何人;随便哪个人;重要人物;', 'I won\'t tell anyone I saw you here...', '我不会告诉任何人我在这儿见过你。');
INSERT INTO `words` VALUES (314, 'anything', 'ˈeniθɪŋ', 'pron.', '(用于否定句、疑问句也用于if或whether之后，或紧接prevent、ban、avoid等动词，代替something)任何东西，任何事物;随便哪个东西;随便什么事物;', 'We can\'t do anything...', '我们什么都做不了。');
INSERT INTO `words` VALUES (315, 'anyway', 'ˈeniweɪ', 'adv.', '而且;加之;反正;', 'I\'m certain David\'s told you his business troubles. Anyway, it\'s no secret that he owes money...', '我敢肯定戴维已经告诉过你他生意上有麻烦了。反正他欠钱也不是什么秘密了。');
INSERT INTO `words` VALUES (316, 'anywhere', 'ˈeniweə(r)', 'adv.', '(用于否定句和疑问句，代替somewhere)在任何地方;在(或去)任何地方;随便哪个地方;', 'I haven\'t got anywhere to live...', '我还没找到住的地方。');
INSERT INTO `words` VALUES (317, 'apartment', 'əˈpɑːtmənt', 'n.', '(通常指在同一楼层的)公寓套房;(度假用的)公寓套房;(尤指巨屋、名宅的)房间;', 'Christina has her own apartment, with her own car.', '克里斯蒂娜有自己的公寓和汽车。');
INSERT INTO `words` VALUES (318, 'ape', 'eɪp', 'n.', '类人猿;', '...the theory that man is descended from the apes.', '认为人类是从类人猿演变而来的理论');
INSERT INTO `words` VALUES (319, 'aperture', 'ˈæpətʃə(r)', 'n.', '小孔;缝隙;(尤指摄影机等的光圈)孔径;', 'Through the aperture he could see daylight.', '透过缝隙，他能看见日光。');
INSERT INTO `words` VALUES (320, 'apologetic', 'əˌpɒləˈdʒetɪk', 'adj.', '道歉的;谢罪的;愧疚的;', 'The hospital staff were very apologetic but that couldn\'t really compensate...', '医院的工作人员深表歉意，但这真的于事无补。');
INSERT INTO `words` VALUES (321, 'apology', 'əˈpɒlədʒi', 'n.', '道歉;谢罪;(因不能赴会或提前离会的)致歉;', 'I didn\'t get an apology...', '还没有向我道歉。');
INSERT INTO `words` VALUES (322, 'appalling', 'əˈpɔːlɪŋ', 'adj.', '令人震惊的;使人惊骇的;极为恶劣的;', 'They have been living under the most appalling conditions for two months.', '他们已经在恶劣不堪的条件下生活两个月了。');
INSERT INTO `words` VALUES (323, 'apparatus', 'ˌæpəˈreɪtəs', 'n.', '仪器;器械;装置;', 'For many years, the country had been buried under the apparatus of the regime.', '多年来，这个国家一直处于该政权机构的高压统治下。');
INSERT INTO `words` VALUES (324, 'apparent', 'əˈpærənt', 'adj.', '显而易见;明白易懂;显然;', 'I was a bit depressed by our apparent lack of progress...', '我对我们看似缓慢的进展感到有些沮丧。');
INSERT INTO `words` VALUES (325, 'apparently', 'əˈpærəntli', 'adv.', '据…所知;看来;显然;', 'Apparently the girls are not at all amused by the whole business...', '据说女孩们觉得整件事情一点也不好笑。');
INSERT INTO `words` VALUES (326, 'appeal', 'əˈpiːl', 'n.', '上诉;申诉;吸引力;', 'The Prime Minister appealed to young people to use their vote...', '首相呼吁年轻人行使他们的投票权。');
INSERT INTO `words` VALUES (327, 'appear', 'əˈpɪə(r)', 'v.', '显得;看来;似乎;', 'There appears to be increasing support for the leadership to take a more aggressive stance...', '好像有越来越多的人支持领导层采取更为强势的立场。');
INSERT INTO `words` VALUES (328, 'appearance', 'əˈpɪərəns', 'n.', '外貌;外观;外表;', 'It was the president\'s second public appearance to date...', '这是总统到那时为止的第二次公开露面。');
INSERT INTO `words` VALUES (329, 'appearances', 'əˈpɪərənsɪz', 'n.', '外貌;外观;外表;', 'It was the president\'s second public appearance to date...', '这是总统到那时为止的第二次公开露面。');
INSERT INTO `words` VALUES (330, 'append', 'əˈpend', 'v.', '(在文章后面)附加，增补;', 'Violet appended a note at the end of the letter...', '维奥莱特在信的末尾附加了一条备注。');
INSERT INTO `words` VALUES (331, 'appendicitis', 'əˌpendəˈsaɪtɪs', 'n.', '阑尾炎;', 'He is recovering in hospital after an operation for acute appendicitis.', '做完急性阑尾炎手术后，他正在医院康复。');
INSERT INTO `words` VALUES (332, 'appetite', 'ˈæpɪtaɪt', 'n.', '食欲;胃口;强烈欲望;', 'He has a healthy appetite...', '他胃口很好。');
INSERT INTO `words` VALUES (333, 'applaud', 'əˈplɔːd', 'v.', '鼓掌;称赞;赞许;', 'The audience laughed and applauded...', '观众边笑边鼓掌。');
INSERT INTO `words` VALUES (334, 'applause', 'əˈplɔːz', 'n.', '鼓掌;喝彩;', 'They greeted him with thunderous applause.', '他们以雷鸣般的掌声欢迎他。');
INSERT INTO `words` VALUES (335, 'apple', 'ˈæpl', 'n.', '苹果;', 'I want an apple.', '我想要一个苹果。');
INSERT INTO `words` VALUES (336, 'appliance', 'əˈplaɪəns', 'n.', '(家用)电器，器具;', 'He could also learn to use the vacuum cleaner, the washing machine and other household appliances.', '他可以再学学使用吸尘器、洗衣机和其他家用电器。');
INSERT INTO `words` VALUES (337, 'applicable', 'əˈplɪkəbl', 'adj.', '适用;合适;', 'What is a reasonable standard for one family is not applicable for another...', '对一个家庭合理的标准并不适用于另一个家庭。');
INSERT INTO `words` VALUES (338, 'application', 'ˌæplɪˈkeɪʃn', 'n.', '申请;请求;申请书;', 'His application for membership of the organisation was rejected...', '他想要加入该组织的申请遭到了拒绝。');
INSERT INTO `words` VALUES (339, 'applied', 'əˈplaɪd', 'adj.', '应用的;实用的;', '...Applied Physics.', '应用物理学');
INSERT INTO `words` VALUES (340, 'apply', 'əˈplaɪ', 'v.', '(通常以书面形式)申请，请求;使用;应用;', 'I am continuing to apply for jobs...', '我正在继续找工作。');
INSERT INTO `words` VALUES (341, 'appoint', 'əˈpɔɪnt', 'v.', '任命;委任;安排，确定(时间、地点);', 'It made sense to appoint a banker to this job...', '指派一位银行家做这份工作是明智之举。');
INSERT INTO `words` VALUES (342, 'appointment', 'əˈpɔɪntmənt', 'n.', '约会;预约;约定;', 'His appointment to the Cabinet would please the right-wing.', '他被任命为内阁成员会让右翼党派很高兴。');
INSERT INTO `words` VALUES (343, 'apportion', 'əˈpɔːʃn', 'v.', '分配;分摊;分派;', 'The experts are even-handed in apportioning blame among EU governments...', '专家们在明确对欧盟各国政府所应追究的责任时十分公正。');
INSERT INTO `words` VALUES (344, 'appraisal', 'əˈpreɪzl', 'n.', '评价;估价;估计;', 'What is needed in such cases is a calm appraisal of the situation...', '在此类情况下需要对形势有一个冷静的评估。');
INSERT INTO `words` VALUES (345, 'appreciable', 'əˈpriːʃəbl', 'adj.', '(大得)可以觉察到的，足以认为重要的;可观的;', 'It contains less than 1 per cent fat, an appreciable amount of protein, and a high content of minerals...', '它所含脂肪不到1%，富含蛋白质，矿物质含量也很高。');
INSERT INTO `words` VALUES (346, 'appreciate', 'əˈpriːʃieɪt', 'v.', '欣赏;赏识;重视;', 'Anyone can appreciate our music...', '任何人都能欣赏我们的音乐。');
INSERT INTO `words` VALUES (347, 'appreciation', 'əˌpriːʃiˈeɪʃn', 'n.', '欣赏;理解;体谅;', '...an investigation into children\'s understanding and appreciation of art...', '就孩子对艺术的理解和欣赏所作的调查');
INSERT INTO `words` VALUES (348, 'apprehension', 'ˌæprɪˈhenʃn', 'n.', '忧虑;担心;疑惧;', 'It reflects real anger and apprehension about the future...', '它反映出对未来真实的愤怒和恐惧。');
INSERT INTO `words` VALUES (349, 'apprentice', 'əˈprentɪs', 'n.', '学徒;徒弟;', 'I started off as an apprentice and worked my way up...', '我从学徒开始干起，逐步上升。');
INSERT INTO `words` VALUES (350, 'approach', 'əˈprəʊtʃ', 'v.', '(在距离或时间上)靠近，接近;接洽;建议;', 'He didn\'t approach the front door at once...', '他没有马上走向前门。');
INSERT INTO `words` VALUES (351, 'approval', 'əˈpruːvl', 'n.', '赞成;同意;批准，通过，认可(计划、要求等);', '...efforts to win congressional approval for an aid package for Moscow...', '为获得国会准许向莫斯科提供一揽子援助而付出的努力');
INSERT INTO `words` VALUES (352, 'approve', 'əˈpruːv', 'v.', '赞成;同意;批准，通过(计划、要求等);', 'Not everyone approves of the festival...', '不是所有人都赞成庆祝该节日。');
INSERT INTO `words` VALUES (353, 'approximation', 'əˌprɒksɪˈmeɪʃn', 'n.', '近似值;粗略估算;类似事物;', 'That is a fair approximation of the way in which the next boss is being chosen.', '那和下一任老板的选任方式相当类似。');
INSERT INTO `words` VALUES (354, 'april', 'ˈeɪprəl', 'n.', '四月;', 'The changes will be introduced in April...', '这些变更将于4月开始实行。');
INSERT INTO `words` VALUES (355, 'apt', 'æpt', 'adj.', '恰当的;适当的;易于…;', 'The words of this report are as apt today as in 1929.', '这份报告的措词用在今天和用在1929年一样贴切。');
INSERT INTO `words` VALUES (356, 'aptitude', 'ˈæptɪtjuːd', 'n.', '天资;天生的才能;天赋;', 'An aptitude for computing is beneficial for students taking this degree.', '计算才能对于攻读这个学位的学生有益处。');
INSERT INTO `words` VALUES (357, 'aquatic', 'əˈkwætɪk', 'adj.', '水生的;水栖的;水的;', 'The pond is quite small but can support many aquatic plants and fish.', '池塘虽小，但能为许多水生植物和鱼类提供足够的养分。');
INSERT INTO `words` VALUES (358, 'arabian', 'əˈreɪbiən', 'adj.', '阿拉伯的;', '...the Arabian Peninsula.', '阿拉伯半岛');
INSERT INTO `words` VALUES (359, 'arbitrary', 'ˈɑːbɪtrəri', 'adj.', '任意的;武断的;随心所欲的;', 'Arbitrary arrests and detention without trial were common.', '随意逮捕和拘留屡见不鲜。');
INSERT INTO `words` VALUES (360, 'arc', 'ɑːk', 'n.', '弧;弧形;电弧;', 'The Aleutian chain is a long arc of islands in the North Pacific.', '阿留申群岛是北太平洋上一长串呈弧形排列的小岛。');
INSERT INTO `words` VALUES (361, 'archaic', 'ɑːˈkeɪɪk', 'adj.', '古体的;已不通用的;早已过时的;', '...archaic laws that are very seldom used...', '已很少采用的过时法律');
INSERT INTO `words` VALUES (362, 'architecture', 'ˈɑːkɪtektʃə(r)', 'n.', '建筑学;建筑设计;建筑风格;', 'He studied classical architecture and design in Rome.', '他在罗马学习了古典建筑学和设计。');
INSERT INTO `words` VALUES (363, 'archive', 'ˈɑːkaɪv', 'n.', '档案;档案馆;档案室;', '...the archives of the Imperial War Museum...', '帝国战争博物馆里的档案');
INSERT INTO `words` VALUES (364, 'archives', 'ˈɑːkaɪvz', 'n.', '档案;档案馆;档案室;', '...the archives of the Imperial War Museum...', '帝国战争博物馆里的档案');
INSERT INTO `words` VALUES (365, 'arctic', 'ˈɑːktɪk', 'n.', '北极（区）；北极圈;（常复数）御寒防水套鞋;', '...winter in the Arctic.', '北极的冬天');
INSERT INTO `words` VALUES (366, 'ardent', 'ˈɑːdnt', 'adj.', '热烈的;激情的;', 'He\'s been one of the most ardent supporters of the administration\'s policy.', '他一向是政府政策最热情的支持者之一。');
INSERT INTO `words` VALUES (367, 'arduous', 'ˈɑːdjuəs', 'adj.', '艰苦的;艰难的;', '...a long, hot and arduous journey...', '一段漫长、炎热而又艰苦的旅程');
INSERT INTO `words` VALUES (368, 'area', 'ˈeəriə', 'n.', '(地方、城市、国家、世界的)地区，地域;(房间、建筑物、处所划为某用途的)地方，场地，区;(物体上的)区，部位;', '...the large number of community groups in the area...', '该地区大量的社团');
INSERT INTO `words` VALUES (369, 'arena', 'əˈriːnə', 'n.', '圆形运动场;圆形剧场;斗争场所;', '...the largest indoor sports arena in the world.', '世界上最大的室内体育场');
INSERT INTO `words` VALUES (370, 'argue', 'ˈɑːɡjuː', 'v.', '争论;争吵;争辩;', 'The committee is concerned about players\' behaviour, especially arguing with referees...', '委员会很关注运动员的行为，特别是与裁判争吵的行为。');
INSERT INTO `words` VALUES (371, 'argument', 'ˈɑːɡjumənt', 'n.', '争论;争吵;争辩;', 'There\'s a strong argument for lowering the price...', '有充分理由要求降低价格。');
INSERT INTO `words` VALUES (372, 'arise', 'əˈraɪz', 'v.', '发生;产生;出现;', '...if a problem arises later in the pregnancy...', '如果在怀孕后期出现问题');
INSERT INTO `words` VALUES (373, 'armchair', 'ˈɑːmtʃeə(r)', 'n.', '扶手椅;', 'She was sitting in an armchair with blankets wrapped round her.', '她坐在扶手椅中，身上裹着毯子。');
INSERT INTO `words` VALUES (374, 'army', 'ˈɑːmi', 'n.', '陆上作战军队;陆军部队;(一国的)陆军;', 'After returning from France, he joined the army...', '从法国回来之后，他参了军。');
INSERT INTO `words` VALUES (375, 'arouse', 'əˈraʊz', 'v.', '激起，引起(感情、态度);激起性欲;使行动起来;', 'We left in the daytime so as not to arouse suspicion.', '我们在白天离开以避免引起怀疑。');
INSERT INTO `words` VALUES (376, 'arrange', 'əˈreɪndʒ', 'v.', '安排;筹备;整理;', 'She arranged an appointment for Friday afternoon at four-fifteen...', '她在星期五下午4点15分安排了一次会面。');
INSERT INTO `words` VALUES (377, 'arrangement', 'əˈreɪndʒmənt', 'n.', '安排;筹备;安排方式;', 'The staff is working frantically on final arrangements for the summit...', '工作人员正紧张忙乱地为峰会做最后准备。');
INSERT INTO `words` VALUES (378, 'array', 'əˈreɪ', 'n.', '大堆;大群;大量;', 'As the deadline approached she experienced a bewildering array of emotions...', '随着最后期限的临近，她开始心绪纷乱，不知所措。');
INSERT INTO `words` VALUES (379, 'arrears', 'əˈrɪəz', 'n.', '逾期欠款;', 'They have promised to pay the arrears over the next five years...', '他们保证在随后的5年内偿付逾期债款。');
INSERT INTO `words` VALUES (380, 'arrest', 'əˈrest', 'v.', '逮捕;拘留;阻止;', 'Police arrested five young men in connection with one of the attacks...', '警方逮捕了与其中一次袭击有关的5名青年男子。');
INSERT INTO `words` VALUES (381, 'arrival', 'əˈraɪvl', 'n.', '到达;抵达;到达者;', '...the day after his arrival in England...', '他到英格兰的第二天');
INSERT INTO `words` VALUES (382, 'arrive', 'əˈraɪv', 'v.', '到达;抵达;送达;', 'Fresh groups of guests arrived...', '新的客人接踵而来。');
INSERT INTO `words` VALUES (383, 'arrogant', 'ˈærəɡənt', 'adj.', '傲慢的;自大的;', 'He was so arrogant...', '他是如此傲慢。');
INSERT INTO `words` VALUES (384, 'arrow', 'ˈærəʊ', 'n.', '箭;箭号;箭头;', 'Warriors armed with bows and arrows and spears have invaded their villages.', '佩带弓箭和长矛的武士闯入了他们的村庄。');
INSERT INTO `words` VALUES (385, 'arsenal', 'ˈɑːsənl', 'n.', '(统称)武器;兵工厂;武器库;', 'Russia and the other republics are committed to destroying most of their nuclear arsenals...', '俄罗斯和其他加盟共和国承诺销毁他们储存的大部分核武器。');
INSERT INTO `words` VALUES (386, 'art', 'ɑːt', 'n.', '艺术，美术(尤指绘画、雕刻、雕塑);艺术作品;美术作品;', '...the first exhibition of such art in the West.', '此类艺术品在西方的首次展览');
INSERT INTO `words` VALUES (387, 'artery', 'ˈɑːtəri', 'n.', '动脉;干线(指主要公路、河流、铁路线等);', '...patients suffering from blocked arteries.', '患动脉栓塞的病人');
INSERT INTO `words` VALUES (388, 'arthritis', 'ɑːˈθraɪtɪs', 'n.', '关节炎;', 'I have a touch of arthritis in the wrist.', '我的手腕患有轻微关节炎。');
INSERT INTO `words` VALUES (389, 'article', 'ˈɑːtɪkl', 'n.', '(报刊上的)文章，论文，报道;(协议、契约的)条款，项;物件，物品(尤指整套中的一件);', '...a newspaper article...', '一篇报载文章');
INSERT INTO `words` VALUES (390, 'articulation', 'ɑːˌtɪkjuˈleɪʃn', 'n.', '(思想感情的)表达;说话;吐字;', '...a singer able to sustain a full tone and clear articulation over extremely long periods.', '可以很长时间保持声音洪亮并且吐字清楚的歌手');
INSERT INTO `words` VALUES (391, 'artificial', 'ˌɑːtɪˈfɪʃl', 'adj.', '人工的;人造的;假的;', '...a wholefood diet free from artificial additives, colours and flavours...', '不含人工添加剂、色素和香料的天然食品');
INSERT INTO `words` VALUES (392, 'artificially', 'ˌɑːtɪˈfɪʃ(ə)li', 'adv.', '人工（造，为）地；不自然地;', '...a wholefood diet free from artificial additives, colours and flavours...', '不含人工添加剂、色素和香料的天然食品');
INSERT INTO `words` VALUES (393, 'artillery', 'ɑːˈtɪləri', 'n.', '(统称)火炮;炮兵部队;', 'Using tanks and heavy artillery, they seized the town.', '他们倚仗坦克和重型火炮占领了这个镇。');
INSERT INTO `words` VALUES (394, 'artist', 'ˈɑːtɪst', 'n.', '艺术家;(尤指)画家;', '...the studio of a great artist...', '一个伟大的艺术家的工作室');
INSERT INTO `words` VALUES (395, 'artistic', 'ɑːˈtɪstɪk', 'adj.', '艺术的;艺术家的;有艺术天赋的;', 'They encourage boys to be sensitive and artistic...', '他们鼓励培养男孩子的敏感和艺术气质。');
INSERT INTO `words` VALUES (396, 'artwork', 'ˈɑːtwɜːk', 'n.', '(书刊等上的)插图，图片;(尤指博物馆里的)艺术作品;', 'The artwork for the LP was done by Bill Hofstadter.', '林肯公园的广告插图出自比尔·霍夫斯塔特之手。');
INSERT INTO `words` VALUES (397, 'ascend', 'əˈsend', 'v.', '上升;升高;登高;', 'Mrs Clayton had to hold Lizzie\'s hand as they ascended the steps...', '拾级而上的时候克莱顿夫人不得不抓着莉齐的手。');
INSERT INTO `words` VALUES (398, 'ascending', 'əˈsendɪŋ', 'adj.', '(次序)上升的，渐进的;', 'Now draw or trace ten dinosaurs in ascending order of size.', '现在按照体型由小到大的顺序画出或是临摹出10只恐龙。');
INSERT INTO `words` VALUES (399, 'ascent', 'əˈsent', 'n.', '上升;升高;登高;', 'In 1955 he led the first ascent of Kangchenjunga, the world\'s third highest mountain.', '他于1955年带队首攀世界第三高峰——干城章嘉峰。');
INSERT INTO `words` VALUES (400, 'ascertain', 'ˌæsəˈteɪn', 'v.', '查明;弄清;', 'Through doing this, the teacher will be able to ascertain the extent to which the child understands what he is reading...', '这样一来，老师就能确定孩子们对他所读的内容理解多少了。');
INSERT INTO `words` VALUES (401, 'ascribe', 'əˈskraɪb', 'v.', '把…归因于;把…归咎于;认为是…的特点;认为…具有;', 'An autopsy eventually ascribed the baby\'s death to sudden infant death syndrome.', '尸检最终认定孩子死于婴儿猝死综合征。');
INSERT INTO `words` VALUES (402, 'ash', 'æʃ', 'n.', '灰;灰烬;废墟;', 'A cloud of volcanic ash is spreading across wide areas of the Philippines...', '火山灰云层正在菲律宾大范围扩散。');
INSERT INTO `words` VALUES (403, 'ashamed', 'əˈʃeɪmd', 'adj.', '惭愧;羞愧;尴尬;', 'I felt incredibly ashamed of myself for getting so angry...', '我对自己发那么大的火而深感惭愧。');
INSERT INTO `words` VALUES (404, 'ashore', 'əˈʃɔː(r)', 'adv.', '向(或在)岸上;向(或在)陆地;', 'Oil has come ashore on a ten mile stretch to the east of Plymouth...', '绵延10英里的浮油已经到达了普利茅斯东岸。');
INSERT INTO `words` VALUES (405, 'asian', 'ˈeɪʃn', 'n.', '亚洲人;', '...Asian music.', '亚洲音乐');
INSERT INTO `words` VALUES (406, 'ask', 'ɑːsk', 'v.', '问;询问;要求;', '\'How is Frank?\' he asked...', '“弗兰克怎么样了？”他问道。');
INSERT INTO `words` VALUES (407, 'asleep', 'əˈsliːp', 'adj.', '睡着;', 'My four year-old daughter was asleep on the sofa.', '我4岁的女儿在沙发上睡着了。');
INSERT INTO `words` VALUES (408, 'aspect', 'ˈæspekt', 'n.', '方面;层面;样子;', 'Climate and weather affect every aspect of our lives...', '气候和天气影响着我们生活的方方面面。');
INSERT INTO `words` VALUES (409, 'aspiration', 'ˌæspəˈreɪʃn', 'n.', '渴望;抱负;志向;', '...the needs and aspirations of our pupils...', '我们的学生的需要和志向');
INSERT INTO `words` VALUES (410, 'aspire', 'əˈspaɪə(r)', 'v.', '渴望(成就);有志(成为);', '...people who aspire to public office...', '渴望担任公职的人');
INSERT INTO `words` VALUES (411, 'assassinate', 'əˈsæsɪneɪt', 'v.', '(尤为政治目的)暗杀，行刺;', 'Would the USA be radically different today if Kennedy had not been assassinated?...', '如果肯尼迪没有被暗杀，今天的美国会有根本性的不同吗？');
INSERT INTO `words` VALUES (412, 'assassination', 'əˌsæsɪ\'neɪʃ(ə)n', 'n.', '暗杀；刺杀，行刺;', 'Would the USA be radically different today if Kennedy had not been assassinated?...', '如果肯尼迪没有被暗杀，今天的美国会有根本性的不同吗？');
INSERT INTO `words` VALUES (413, 'assault', 'əˈsɔːlt', 'n.', '侵犯他人身体(罪);侵犯人身罪;攻击;', 'The rebels are poised for a new assault on the government garrisons...', '叛军准备好要对政府驻军发起新一轮的猛攻。');
INSERT INTO `words` VALUES (414, 'assemble', 'əˈsembl', 'v.', '聚集;集合;收集;', 'There wasn\'t even a convenient place for students to assemble between classes...', '在课间，学生们连一个方便凑在一起的地方都没有。');
INSERT INTO `words` VALUES (415, 'assembled', 'əˈsembld', 'v.', '聚集;集合;收集;', 'There wasn\'t even a convenient place for students to assemble between classes...', '在课间，学生们连一个方便凑在一起的地方都没有。');
INSERT INTO `words` VALUES (416, 'assembly', 'əˈsembli', 'n.', '立法机构;会议;议会;', '...the campaign for the first free election to the National Assembly.', '呼吁国民议会召开首次自由选举的运动');
INSERT INTO `words` VALUES (417, 'assert', 'əˈsɜːt', 'v.', '明确肯定;断言;坚持自己的主张;', 'Mr. Helm plans to assert that the bill violates the First Amendment...', '赫尔姆先生打算声明该法案违反了宪法第一修正案。');
INSERT INTO `words` VALUES (418, 'assertion', 'əˈsɜːʃn', 'n.', '明确肯定;断言;声称;', 'Mr. Helm plans to assert that the bill violates the First Amendment...', '赫尔姆先生打算声明该法案违反了宪法第一修正案。');
INSERT INTO `words` VALUES (419, 'assess', 'əˈses', 'v.', '评估，评定(性质、质量);估算，估定，核定(数量、价值);', 'Our correspondent has been assessing the impact of the sanctions...', '我们的记者一直在评估制裁带来的影响。');
INSERT INTO `words` VALUES (420, 'assessment', 'əˈsesmənt', 'n.', '看法;评估;评定;', 'There is little assessment of the damage to the natural environment...', '几乎未对自然环境破坏程度作出评估。');
INSERT INTO `words` VALUES (421, 'asset', 'ˈæset', 'n.', '有价值的人(或事物);有用的人(或事物);资产;', 'Her leadership qualities were the greatest asset of the Conservative Party...', '她的领导才能是保守党最宝贵的财富。');
INSERT INTO `words` VALUES (422, 'assets', 'ˈæsets', 'n.', '有价值的人(或事物);有用的人(或事物);资产;', 'Her leadership qualities were the greatest asset of the Conservative Party...', '她的领导才能是保守党最宝贵的财富。');
INSERT INTO `words` VALUES (423, 'assign', 'əˈsaɪn', 'v.', '分配(某物);分派，布置(工作、任务等);指定;', 'When I taught, I would assign a topic to children which they would write about...', '我教课时会给孩子们布置一个题目，让他们就这个题目写点东西。');
INSERT INTO `words` VALUES (424, 'assigned', 'əˈsaɪnd', 'v.', '分配(某物);分派，布置(工作、任务等);指定;', 'When I taught, I would assign a topic to children which they would write about...', '我教课时会给孩子们布置一个题目，让他们就这个题目写点东西。');
INSERT INTO `words` VALUES (425, 'assignment', 'əˈsaɪnmənt', 'n.', '(分派的)工作，任务;(工作等的)分派，布置;', 'The assessment for the course involves written assignments and practical tests.', '这门功课的考核包括书面作业和实际操作考试。');
INSERT INTO `words` VALUES (426, 'assimilate', 'əˈsɪməleɪt', 'v.', '透彻理解;消化;吸收;', 'There is every sign that new Asian-Americans are just as willing to assimilate...', '种种迹象表明新一代亚裔美国人也愿意入乡随俗。');
INSERT INTO `words` VALUES (427, 'assimilation', 'əˌsɪməˈleɪʃn', 'n.', '吸收;接受;同化(使相邻的两个音发音接近，如football中t的发音为p);', 'There is every sign that new Asian-Americans are just as willing to assimilate...', '种种迹象表明新一代亚裔美国人也愿意入乡随俗。');
INSERT INTO `words` VALUES (428, 'assist', 'əˈsɪst', 'v.', '帮助;协助;援助;', 'Julia was assisting him to prepare his speech...', '朱莉娅正在帮他准备讲演稿。');
INSERT INTO `words` VALUES (429, 'assistance', 'əˈsɪstəns', 'n.', '帮助;援助;支持;', 'Since 1976 he has been operating the shop with the assistance of volunteers...', '从1976年开始，他就在志愿者的帮助下经营这个店。');
INSERT INTO `words` VALUES (430, 'assistant', 'əˈsɪstənt', 'n.', '助理;助手;助教(在国外留学的大学生，教授本国语);', '...the Assistant Secretary of Defense.', '助理国防部长');
INSERT INTO `words` VALUES (431, 'association', 'əˌsəʊsiˈeɪʃn', 'n.', '协会;社团;联盟;', '...the British Olympic Association...', '英国奥林匹克协会');
INSERT INTO `words` VALUES (432, 'associative', 'əˈsəʊʃiətɪv', 'adj.', '联想的;结合的;', 'The associative guilt was ingrained in his soul...', '由此产生的内疚感在他灵魂深处挥之不去。');
INSERT INTO `words` VALUES (433, 'assorted', 'əˈsɔːtɪd', 'adj.', '各种各样的;混杂的;什锦的;', 'It should be a great week, with overnight stops in assorted hotels in the West Highlands.', '那一周会过得很开心，晚上可以在西部高地上的各式旅馆里过夜。');
INSERT INTO `words` VALUES (434, 'assortment', 'əˈsɔːtmənt', 'n.', '各种各样;', '...an assortment of cheese.', '干酪什锦');
INSERT INTO `words` VALUES (435, 'assume', 'əˈsjuːm', 'v.', '假定;假设;认为;', 'It is a misconception to assume that the two continents are similar...', '关于这两块大陆相似的假设是一种误解。');
INSERT INTO `words` VALUES (436, 'assumed', 'əˈsjuːmd', 'adj.', '假定的;假设的;', 'It is a misconception to assume that the two continents are similar...', '关于这两块大陆相似的假设是一种误解。');
INSERT INTO `words` VALUES (437, 'assumption', 'əˈsʌmpʃn', 'n.', '假定;假设;(责任的)承担;', 'They have taken a wrong turning in their assumption that all men and women think alike...', '他们错误地设想所有男女均见解一致。');
INSERT INTO `words` VALUES (438, 'assurance', 'əˈʃʊərəns', 'n.', '保证;担保;自信;', 'He would like an assurance that other forces will not move into the territory that his forces vacate...', '他想确保其他势力不会在他的军队撤离后进入这个地区。');
INSERT INTO `words` VALUES (439, 'assure', 'əˈʃʊə(r)', 'v.', '使确信;向…保证;弄清;', 'He hastened to assure me that there was nothing traumatic to report...', '他忙不迭地向我保证，并没有什么悲惨的事要诉说。');
INSERT INTO `words` VALUES (440, 'assured', 'əˈʃʊəd', 'adj.', '自信的;有把握的;必将发生的;', 'He was infinitely more assured than in his more recent parliamentary appearances.', '比起最近几次在国会中的露面，他表现得自信多了。');
INSERT INTO `words` VALUES (441, 'astonish', 'əˈstɒnɪʃ', 'v.', '使十分惊讶;使大为惊奇;使吃惊;', 'My news will astonish you...', '我的消息会让你感到十分惊讶的。');
INSERT INTO `words` VALUES (442, 'astonishing', 'əˈstɒnɪʃɪŋ', 'adj.', '令人十分惊讶的;使人大为惊奇的;难以置信的;', 'It\'s astonishing, he\'s learned Latin in three hours!', '他花了3个小时就学会了拉丁文，真让人吃惊！');
INSERT INTO `words` VALUES (443, 'astound', 'əˈstaʊnd', 'v.', '使震惊;使大惊;', 'He used to astound his friends with feats of physical endurance.', '过去，他表现出来的惊人耐力常让朋友们大吃一惊。');
INSERT INTO `words` VALUES (444, 'astute', 'əˈstjuːt', 'adj.', '精明的;狡猾的;', 'She was politically astute...', '她很有政治头脑。');
INSERT INTO `words` VALUES (445, 'asylum', 'əˈsaɪləm', 'n.', '(政治)庇护，避难;精神病院;', 'He applied for asylum in 1987 after fleeing the police back home.', '1987年他在本国逃脱警察追捕后，就申请了政治庇护。');
INSERT INTO `words` VALUES (446, 'at', 'æt , ət', 'prep.', '在(某处);在(学习或工作地点);在(某时间或时刻);', 'We had dinner at a restaurant in Attleborough...', '我们在阿特尔伯勒的一家餐厅里吃的饭。');
INSERT INTO `words` VALUES (447, 'athlete', 'ˈæθliːt', 'n.', '运动员;田径运动员;擅长运动的人;', 'Daley Thompson was a great athlete.', '戴利·汤普森是位著名的运动员。');
INSERT INTO `words` VALUES (448, 'athletic', 'æθˈletɪk', 'adj.', '健壮的;体育运动的;田径运动的;', 'They have been given college scholarships purely on athletic ability...', '他们完全是凭体育特长才获得了大学奖学金。');
INSERT INTO `words` VALUES (449, 'atmosphere', 'ˈætməsfɪə(r)', 'n.', '(围绕地球的)大气，大气层，大气圈;(围绕其他天体的)气体;(房间、封闭空间或某处的)空气;', '...dangerous levels of pollution in the Earth\'s atmosphere...', '地球大气层污染程度严重');
INSERT INTO `words` VALUES (450, 'atmospheric', 'ˌætməsˈferɪk', 'adj.', '大气的;大气层的;令人激动的;', '...atmospheric gases.', '大气中的气体');
INSERT INTO `words` VALUES (451, 'atom', 'ˈætəm', 'n.', '原子;', 'A methane molecule is composed of one carbon atom attached to four hydrogens.', '甲烷分子由1个碳原子与4个氢原子构成。');
INSERT INTO `words` VALUES (452, 'atomic', 'əˈtɒmɪk', 'adj.', '原子的;与原子有关的;原子能的;', '...atomic energy.', '原子能');
INSERT INTO `words` VALUES (453, 'attach', 'əˈtætʃ', 'v.', '把…固定，把…附(在…上);认为有重要性(或意义、价值、分量等);重视;', 'We attach labels to things before we file them away...', '存档前，我们先贴上标签。');
INSERT INTO `words` VALUES (454, 'attached', 'əˈtætʃt', 'adj.', '依恋;爱慕;附属于;', 'She is very attached to her family and friends.', '她非常热爱家人和朋友。');
INSERT INTO `words` VALUES (455, 'attachment', 'əˈtætʃmənt', 'n.', '依恋;爱慕;信念;', 'As a teenager she formed a strong attachment to one of her teachers...', '少女时代的她曾迷上了自己的一个老师。');
INSERT INTO `words` VALUES (456, 'attack', 'əˈtæk', 'n.', '袭击;攻击;(在战争中使用武器的)进攻;', 'Fifty civilians in Masawa were killed when government planes attacked the town...', '马萨瓦市遭到政府军飞机轰炸，有50名平民丧生。');
INSERT INTO `words` VALUES (457, 'attain', 'əˈteɪn', 'v.', '(通常经过努力)获得，得到;达到(某年龄、水平、状况);', 'Jim is halfway to attaining his pilot\'s licence.', '吉姆就快要拿到飞行员执照了。');
INSERT INTO `words` VALUES (458, 'attempt', 'əˈtempt', 'n.', '企图;试图;尝试;', 'The only time that we attempted to do something like that was in the city of Philadelphia...', '只有在费城那次我们曾试着做那样的事。');
INSERT INTO `words` VALUES (459, 'attend', 'əˈtend', 'v.', '出席;参加;经常去，定期去(某处);', 'Thousands of people attended the funeral...', '数千人参加了葬礼。');
INSERT INTO `words` VALUES (460, 'attendance', 'əˈtendəns', 'n.', '出席;参加;上学;', 'Her attendance at school was sporadic.', '她三天两头旷课。');
INSERT INTO `words` VALUES (461, 'attendant', 'əˈtendənt', 'n.', '服务员;侍者;(要人的)侍从，随从;', 'Tony Williams was working as a car-park attendant in Los Angeles.', '托尼·威廉斯在洛杉矶的一个停车场当服务生。');
INSERT INTO `words` VALUES (462, 'attention', 'əˈtenʃn', 'n.', '注意;专心;留心;', 'You have my undivided attention...', '我专心听你所讲。');
INSERT INTO `words` VALUES (463, 'attentive', 'əˈtentɪv', 'adj.', '注意的;专心的;留心的;', 'The vast majority of the attentive audience applauded these sentiments...', '大多数聚精会神听讲的观众都对这些观点表示赞赏。');
INSERT INTO `words` VALUES (464, 'attitude', 'ˈætɪtjuːd', 'n.', '态度;看法;我行我素的作派;', '...the general change in attitude towards handicapped people...', '对残疾人态度的整体转变');
INSERT INTO `words` VALUES (465, 'attorney', 'əˈtɜːni', 'n.', '律师(尤指代表当事人出庭者);(业务或法律事务上的)代理人;', '...a prosecuting attorney.', '检察官');
INSERT INTO `words` VALUES (466, 'attract', 'əˈtrækt', 'v.', '吸引;使喜爱;引起…的好感(或爱慕);', 'The Cardiff Bay project is attracting many visitors...', '加的夫海湾项目吸引了众多游客。');
INSERT INTO `words` VALUES (467, 'attraction', 'əˈtrækʃn', 'n.', '(尤指两性间的)爱慕，吸引;向往的地方;有吸引力的事;', 'Our level of attraction to the opposite sex has more to do with our inner confidence than how we look...', '我们对异性的吸引程度更多的来自内在的自信，而不是长相。');
INSERT INTO `words` VALUES (468, 'attractive', 'əˈtræktɪv', 'adj.', '性感的;妩媚的;英俊的;', 'She\'s a very attractive woman...', '她是个妩媚动人的女子。');
INSERT INTO `words` VALUES (469, 'attributable', 'əˈtrɪbjətəbl', 'adj.', '可归因于;可能由于;', '10,000 deaths a year from chronic lung disease are attributable to smoking...', '吸烟导致每年有1万人死于慢性肺病。');
INSERT INTO `words` VALUES (470, 'auction', 'ˈɔːkʃn', 'n.', '拍卖;', 'Lord Salisbury bought the picture at auction in London some years ago...', '几年前，索尔兹伯里勋爵在伦敦拍得了这幅画。');
INSERT INTO `words` VALUES (471, 'audible', 'ˈɔːdəbl', 'adj.', '听得见的;', 'The Co-lonel\'s voice was barely audible...', '上校微弱的声音几不可闻。');
INSERT INTO `words` VALUES (472, 'audience', 'ˈɔːdiəns', 'n.', '(戏剧、音乐会或演讲等的)观众，听众;(同一事物的)观众，读者;(与要人的)会见;', 'The entire audience broke into loud applause...', '全场观众爆发出热烈的掌声。');
INSERT INTO `words` VALUES (473, 'audio', 'ˈɔːdiəʊ', 'adj.', '声音的;录音的;', 'She uses her vocal training to record audio tapes of books for blind people.', '她利用接受的声乐训练给盲人录制磁带图书。');
INSERT INTO `words` VALUES (474, 'audit', 'ˈɔːdɪt', 'n.', '审计;稽核;(质量或标准的)审查，检查;', 'Each year they audit our accounts and certify them as being true and fair.', '他们每年对我们进行账务审核，以确保其真实无误。');
INSERT INTO `words` VALUES (475, 'auditing', 'ˈɔːdɪtɪŋ', 'v.', '审计;稽核;旁听(大学课程);', 'Each year they audit our accounts and certify them as being true and fair.', '他们每年对我们进行账务审核，以确保其真实无误。');
INSERT INTO `words` VALUES (476, 'augment', 'ɔːɡˈment', 'v.', '增加;提高;扩大;', 'While searching for a way to augment the family income, she began making dolls.', '为了想法子增加家庭收入，她开始制作洋娃娃。');
INSERT INTO `words` VALUES (477, 'august', 'ɔːˈɡʌst', 'adj.', '威严的;庄严的;', 'Being so near the august person of the Empress, he was overcome with awe.', '如此靠近威严的女皇，他心中充满了敬畏。');
INSERT INTO `words` VALUES (478, 'aunt', 'ɑːnt', 'n.', '姑母;姨母;伯母;', 'She wrote to her aunt in America...', '她给远在美国的姨妈写了封信。');
INSERT INTO `words` VALUES (479, 'aural', 'ˈɔːrəl', 'adj.', '听觉的;听的;', 'He became famous as an inventor of astonishing visual and aural effects.', '他因发明了神奇的视听效果而出名。');
INSERT INTO `words` VALUES (480, 'australian', 'ɒˈstreɪliən', 'adj.', '澳大利亚的;', 'She went solo backpacking for eight months in the Australian outback.', '她独自在澳大利亚内陆地区进行了8个月的徒步旅行。');
INSERT INTO `words` VALUES (481, 'authentic', 'ɔːˈθentɪk', 'adj.', '真正的;真品的;真迹的;', '...authentic Italian food...', '真正的意大利食品');
INSERT INTO `words` VALUES (482, 'authentication', 'ɔːˌθentɪˈkeɪʃn', 'n.', '身份验证;认证；鉴定;', 'He says he\'ll have no problem authenticating the stamp...', '他说自己可以对这枚邮票进行鉴定。');
INSERT INTO `words` VALUES (483, 'author', 'ˈɔːθə(r)', 'n.', '著者;作者;作家;', '...Jill Phillips, author of the book \'Give Your Child Music\'.', '吉尔·菲利普斯，《给孩子音乐》一书的作者');
INSERT INTO `words` VALUES (484, 'authoring', 'ˈɔːθərɪŋ', 'n.', '著作;', '...software authoring tools.', '软件创建工具');
INSERT INTO `words` VALUES (485, 'authoritarian', 'ɔːˌθɒrɪˈteəriən', 'adj.', '威权主义的;专制的;', 'Senior officers could be considering a coup to restore authoritarian rule.', '高官们可能会策划一场政变来复辟独裁统治。');
INSERT INTO `words` VALUES (486, 'authoritative', 'ɔːˈθɒrətətɪv', 'adj.', '命令式的;专断的;权威式的;', 'He has a commanding presence and deep, authoritative voice...', '他仪态威严，声音低沉，说话有威信。');
INSERT INTO `words` VALUES (487, 'authority', 'ɔːˈθɒrəti', 'n.', '权力;威权;当权(地位);', 'This provided a pretext for the authorities to cancel the elections...', '这给当局取消此次选举提供了借口。');
INSERT INTO `words` VALUES (488, 'auto', 'ˈɔːtəʊ', 'n.', '汽车;', '...the auto industry.', '汽车工业');
INSERT INTO `words` VALUES (489, 'automatic', 'ˌɔːtəˈmætɪk', 'adj.', '自动的;无意识的;不假思索的;', 'Modern trains have automatic doors.', '现代火车装有自动门。');
INSERT INTO `words` VALUES (490, 'automatically', 'ˌɔːtəˈmætɪkli', 'adv.', '自动地；机械地；无意识地;', 'Modern trains have automatic doors.', '现代火车装有自动门。');
INSERT INTO `words` VALUES (491, 'automation', 'ˌɔːtəˈmeɪʃn', 'n.', '自动化;', 'He wanted to use computers to automate the process.', '他想通过计算机实现流程的自动化。');
INSERT INTO `words` VALUES (492, 'automaton', 'ɔːˈtɒmətən', 'n.', '不动脑筋机械行事的人;自动操作装置;小机器人;', 'I get sick of being thought of as a political automaton.', '我讨厌被看作政治机器。');
INSERT INTO `words` VALUES (493, 'autonomy', 'ɔːˈtɒnəmi', 'n.', '自治;自治权;自主;', 'Activists stepped up their demands for local autonomy last month.', '上个月激进分子对地方自治的呼声更高了。');
INSERT INTO `words` VALUES (494, 'autumn', 'ˈɔːtəm', 'n.', '秋天;秋季;', 'We are always plagued by wasps in autumn.', '每年秋天，我们这里总是黄蜂成灾。');
INSERT INTO `words` VALUES (495, 'auxiliary', 'ɔːɡˈzɪliəri', 'adj.', '辅助的;备用的;', 'Nursing auxiliaries provide basic care, but are not qualified nurses.', '助理护士只提供基本护理，不具备护士资格。');
INSERT INTO `words` VALUES (496, 'avail', 'əˈveɪl', 'n.', '效用;利益;帮助;', 'His efforts were to no avail...', '他徒劳无功。');
INSERT INTO `words` VALUES (497, 'availability', 'əˌveɪlə\'bɪləti', 'n.', '可利用；可用性；有用（效）性；使用价值；（有效）利用率；工作效率;', 'Since 1978, the amount of money available to buy books has fallen by 17%...', '自1978年以来，可用于采购图书的经费已经减少了17％。');
INSERT INTO `words` VALUES (498, 'available', 'əˈveɪləbl', 'adj.', '可获得的;可购得的;可找到的;', 'Since 1978, the amount of money available to buy books has fallen by 17%...', '自1978年以来，可用于采购图书的经费已经减少了17％。');
INSERT INTO `words` VALUES (499, 'avenue', 'ˈævənjuː', 'n.', '(城镇的)大街;林荫道(尤指通往大住宅者);选择;', '...the most expensive stores on Park Avenue.', '帕克大街上最贵的商店');
INSERT INTO `words` VALUES (500, 'average', 'ˈævərɪdʒ', 'adj.', '平均的;典型的;正常的;', 'Take the average of those ratios and multiply by a hundred.', '算出那些比率的平均值再乘以100。');
INSERT INTO `words` VALUES (501, 'avert', 'əˈvɜːt', 'v.', '防止，避免(危险、坏事);转移目光;背过脸;', 'Talks with the teachers\' union over the weekend have averted a strike...', '周末与教师工会举行的谈判避免了一场罢工。');
INSERT INTO `words` VALUES (502, 'avoid', 'əˈvɔɪd', 'v.', '避免;防止;回避;', 'The pilots had to take emergency action to avoid a disaster...', '飞行员不得不采取紧急措施避免灾难的发生。');
INSERT INTO `words` VALUES (503, 'avoidable', 'əˈvɔɪdəbl', 'adj.', '可以避免的;', 'More than a quarter of the avoidable deaths in the developing world are caused by TB...', '发展中国家的可避免死亡中超过1/4是由肺结核引起的。');
INSERT INTO `words` VALUES (504, 'await', 'əˈweɪt', 'v.', '等候;等待;期待;', 'Very little was said as we awaited the arrival of the chairman...', '我们等候主席到来的时候都没怎么说话。');
INSERT INTO `words` VALUES (505, 'awake', 'əˈweɪk', 'adj.', '醒着(尤指入睡前或刚醒时);', 'I don\'t stay awake at night worrying about that...', '我并没有因为那件事而担心得彻夜不眠。');
INSERT INTO `words` VALUES (506, 'award', 'əˈwɔːd', 'n.', '奖;奖品;奖金;', '...the Booker prize, Britain\'s top award for fiction...', '布克奖，英国小说界的最高荣誉');
INSERT INTO `words` VALUES (507, 'aware', 'əˈweə(r)', 'adj.', '知道;意识到;明白;', 'Smokers are well aware of the dangers to their own health...', '吸烟的人都知道吸烟对自身健康的危害。');
INSERT INTO `words` VALUES (508, 'away', 'əˈweɪ', 'adv.', '(时间或空间上)离开(某距离)，在(某距离)处;去别处;朝另一个方向;', 'An injured policeman was led away by colleagues...', '受伤的警察被同伴带走了。');
INSERT INTO `words` VALUES (509, 'awe', 'ɔː', 'n.', '敬畏;惊叹;', 'She gazed in awe at the great stones...', '她惊叹地盯着那些巨石。');
INSERT INTO `words` VALUES (510, 'awful', 'ˈɔːfl', 'adj.', '很坏的;极讨厌的;非常的;', 'We met and I thought he was awful...', '我们见了面，我觉得他很讨人厌。');
INSERT INTO `words` VALUES (511, 'awfully', 'ˈɔːfli', 'adv.', '非常;极其;', 'We met and I thought he was awful...', '我们见了面，我觉得他很讨人厌。');
INSERT INTO `words` VALUES (512, 'awkward', 'ˈɔːkwəd', 'adj.', '令人尴尬的;使人难堪的;难对付的;', 'I was the first to ask him awkward questions but there\'ll be harder ones to come...', '我是第一个向他提出尴尬问题的人，不过还会有人问更难回答的问题。');
INSERT INTO `words` VALUES (513, 'babble', 'ˈbæbl', 'n.', '嘈杂的人声;含混不清的话;胡言乱语;', 'Momma babbled on and on about how he was ruining me...', '妈妈喋喋不休地念叨着他正如何毁掉我。');
INSERT INTO `words` VALUES (514, 'baby', 'ˈbeɪbi', 'n.', '婴儿;动物幼崽;(家庭或团体中)最年幼的成员;', 'She used to take care of me when I was a baby...', '我还在襁褓中时，她曾经照顾过我。');
INSERT INTO `words` VALUES (515, 'background', 'ˈbækɡraʊnd', 'n.', '出身背景;学历;经历;', 'Moulded by his background, he could not escape the traditional role of strict father...', '受成长环境的影响，他摆脱不了严父的传统角色。');
INSERT INTO `words` VALUES (516, 'backlog', 'ˈbæklɒɡ', 'n.', '积压的工作;', 'There is a backlog of repairs and maintenance in schools.', '对各学校的修缮和维护工作积压了一堆。');
INSERT INTO `words` VALUES (517, 'backtrack', 'ˈbæktræk', 'v.', '原路返回;折回;折返;', 'The committee backtracked by scrapping the controversial bonus system...', '委员会改变主意，放弃了有争议的奖金制度。');
INSERT INTO `words` VALUES (518, 'backup', 'ˈbækʌp', 'n.', '增援;后援;(文件等的)备份;', 'There is no emergency back-up immediately available...', '手头无法获得应急支持。');
INSERT INTO `words` VALUES (519, 'backward', 'ˈbækwəd', 'adj.', '向后的;朝后的;倒退的;', 'He unlocked the door of apartment two and disappeared inside after a backward glance at Larry...', '他打开2号公寓的门，回头瞥了拉里一眼，然后就进了房间。');
INSERT INTO `words` VALUES (520, 'bacon', 'ˈbeɪkən', 'n.', '咸猪肉;熏猪肉;', '...bacon and eggs.', '熏肉煎蛋');
INSERT INTO `words` VALUES (521, 'bacteria', 'bækˈtɪəriə', 'n.', '细菌;', 'Chlorine is added to kill bacteria.', '加氯是为了杀死细菌。');
INSERT INTO `words` VALUES (522, 'bad', 'bæd', 'adj.', '令人不快的;问题成堆的;坏的;', 'The bad weather conditions prevented the plane from landing...', '糟糕的天气使飞机无法降落。');
INSERT INTO `words` VALUES (523, 'badly', 'ˈbædli', 'adv.', '拙劣地;差;不认真仔细;', 'I was angry because I played so badly...', '我很生气，因为自己的表现实在是太差了。');
INSERT INTO `words` VALUES (524, 'baffle', 'ˈbæfl', 'v.', '使困惑;难住;', 'An apple tree producing square fruit is baffling experts.', '一棵结方形苹果的树令专家大惑不解。');
INSERT INTO `words` VALUES (525, 'baggage', 'ˈbæɡɪdʒ', 'n.', '行李；(因阅历而形成的)信仰，看法;', 'The passengers went through  immigration control and collected their baggage.', '旅客通过入境检查后领取了自己的行李。');
INSERT INTO `words` VALUES (526, 'bake', 'beɪk', 'v.', '(在烤炉里)烘烤;焙;(将某物)烤硬;', 'How did you learn to bake cakes?...', '你怎么学会烘蛋糕的？');
INSERT INTO `words` VALUES (527, 'bakery', 'ˈbeɪkəri', 'n.', '面包(糕饼)店;面包(糕饼)烘房;面包厂;', 'A smell of bread drifted from some distant bakery.', '一阵面包的香味从远处的面包房飘来。');
INSERT INTO `words` VALUES (528, 'balance', 'ˈbæləns', 'n.', '均衡;平衡;均势;', 'I balanced on the ledge...', '我在窗台上站稳。');
INSERT INTO `words` VALUES (529, 'bald', 'bɔːld', 'adj.', '秃顶的;秃头的;无毛的;', 'The man\'s bald head was beaded with sweat...', '这个男人的光头上满是汗珠。');
INSERT INTO `words` VALUES (530, 'ball', 'bɔːl', 'n.', '球;球状物;踢出(或击出、投出等)的一球;', '...a golf ball.', '高尔夫球');
INSERT INTO `words` VALUES (531, 'ballet', 'ˈbæleɪ', 'n.', '芭蕾舞;芭蕾舞剧;芭蕾舞团;', 'I trained as a ballet dancer...', '我受过芭蕾舞训练。');
INSERT INTO `words` VALUES (532, 'balloon', 'bəˈluːn', 'n.', '气球;热气球;', 'She popped a balloon with her fork.', '她用叉子戳破了一个气球。');
INSERT INTO `words` VALUES (533, 'ballot', 'ˈbælət', 'n.', '(无记名)投票选举;投票表决;选票;', 'The result of the ballot will not be known for two weeks...', '无记名投票的结果将于两周后公布。');
INSERT INTO `words` VALUES (534, 'bamboo', 'ˌbæmˈbuː', 'n.', '竹;竹子;', '...huts with walls of bamboo.', '竹墙小屋');
INSERT INTO `words` VALUES (535, 'ban', 'bæn', 'v.', '明令禁止;取缔;禁止(某人)做某事(或去某处等);', 'Canada will ban smoking in all offices later this year.', '加拿大将从今年晚些时候起在办公场所全面禁烟。');
INSERT INTO `words` VALUES (536, 'banana', 'bəˈnɑːnə', 'n.', '香蕉;', '...a bunch of bananas.', '一串香蕉');
INSERT INTO `words` VALUES (537, 'band', 'bænd', 'n.', '流行音乐乐队;管乐队;鼓号乐队;', 'He was a drummer in a rock band...', '他曾是一个摇滚乐团的鼓手。');
INSERT INTO `words` VALUES (538, 'bandage', 'ˈbændɪdʒ', 'n.', '绷带;', 'We put some ointment and a bandage on his knee...', '我们在他的膝盖上涂了些药膏并打上了绷带。');
INSERT INTO `words` VALUES (539, 'bandit', 'ˈbændɪt', 'n.', '土匪;', 'This is real bandit country.', '这是一个不折不扣的强盗横行的国家。');
INSERT INTO `words` VALUES (540, 'bang', 'bæŋ', 'v.', '猛敲;砸;(把…)砰的关上;', 'I heard four or five loud bangs...', '我听到四五声巨响。');
INSERT INTO `words` VALUES (541, 'banish', 'ˈbænɪʃ', 'v.', '放逐;流放;把(某人)驱逐出境;', 'John was banished from England...', '约翰被逐出了英格兰。');
INSERT INTO `words` VALUES (542, 'banker', 'ˈbæŋkə(r)', 'n.', '银行老板(或要员);银行家;(某些赌博游戏中的)庄家;', '...an investment banker.', '投资银行家');
INSERT INTO `words` VALUES (543, 'bankrupt', 'ˈbæŋkrʌpt', 'adj.', '破产;倒闭;完全缺乏(有价值的东西);', 'If the firm cannot sell its products, it will go bankrupt...', '如果公司的产品卖不出去，它就会倒闭。');
INSERT INTO `words` VALUES (544, 'bankruptcy', 'ˈbæŋkrʌptsi', 'n.', '破产;', 'It is the second airline in two months to file for bankruptcy...', '这是两个月内第二家申请破产的航空公司。');
INSERT INTO `words` VALUES (545, 'banner', 'ˈbænə(r)', 'n.', '横幅;', '...a large crowd of students carrying banners denouncing the government...', '一大群高举谴责政府的条幅的学生');
INSERT INTO `words` VALUES (546, 'banquet', 'ˈbæŋkwɪt', 'n.', '宴会;盛宴;筵席;', 'Last night he attended a state banquet at Buckingham Palace.', '昨晚他出席了在白金汉宫举行的国宴。');
INSERT INTO `words` VALUES (547, 'bar', 'bɑː(r)', 'n.', '酒吧;(出售饮料等的)柜台;(专售某类饮食的)小吃店，小馆子;', '...Devil\'s Herd, the city\'s most popular country-western bar.', '“魔鬼之群”——这个城市最火的西部乡村酒吧');
INSERT INTO `words` VALUES (548, 'barber', 'ˈbɑːbə(r)', 'n.', '(为男子理发、修面的)理发师;(男子)理发店;', '...a barber\'s shop in south London.', '伦敦南部的理发店');
INSERT INTO `words` VALUES (549, 'bare', 'beə(r)', 'adj.', '裸体的;裸露的;(树木)光秃秃的;', 'She was wearing only a thin robe over a flimsy nightdress, and her feet were bare...', '她只在薄如蝉翼的睡衣外套了一件薄睡袍，而且光着脚。');
INSERT INTO `words` VALUES (550, 'barely', 'ˈbeəli', 'adv.', '仅仅;刚刚;勉强可能;', 'Anastasia could barely remember the ride to the hospital...', '阿纳斯塔西娅几乎记不起来怎么去的医院。');
INSERT INTO `words` VALUES (551, 'bargain', 'ˈbɑːɡən', 'n.', '减价品;便宜货;协议;', 'At this price the wine is a bargain...', '这种葡萄酒卖这个价真是很便宜。');
INSERT INTO `words` VALUES (552, 'bark', 'bɑːk', 'n.', '树皮;(狗等的)吠声，嗥叫声;枪声;', 'Don\'t let the dogs bark...', '让那些狗别叫。');
INSERT INTO `words` VALUES (553, 'barren', 'ˈbærən', 'adj.', '贫瘠的;不毛的;不结果实的;', '...the country\'s landscape of high barren mountains.', '该国草木稀少的高山地貌');
INSERT INTO `words` VALUES (554, 'barricade', 'ˌbærɪˈkeɪd', 'n.', '路障;街垒;', 'Large areas of the city have been closed off by barricades set up by the demonstrators.', '城市中大片地区被示威者设立的路障封锁了。');
INSERT INTO `words` VALUES (555, 'barrier', 'ˈbæriə(r)', 'n.', '屏障;障碍物;障碍;', 'Duties and taxes are the most obvious barrier to free trade.', '关税和税收是自由贸易的最大壁垒。');
INSERT INTO `words` VALUES (556, 'barter', 'ˈbɑːtə(r)', 'v.', '(同某人)以物易物;以(财产或劳务等)作交换;', 'They have been bartering wheat for cotton and timber...', '他们一直用小麦交换棉花和木材。');
INSERT INTO `words` VALUES (557, 'basal', 'ˈbeɪsl', 'adj.', '底层的;基部的;基底的;', 'Side shoots should be cut back to one leaf above the basal cluster.', '应将旁枝剪至主串上一叶高处');
INSERT INTO `words` VALUES (558, 'base', 'beɪs', 'n.', '根基;基底;底座;', 'There was a cycle path running along this side of the wall, right at its base...', '在墙的这边，沿着墙根有一条自行车道。');
INSERT INTO `words` VALUES (559, 'baseline', 'ˈbeɪslaɪn', 'n.', '(网球场的)底线;(棒球场的)垒线;基础;', 'Martinez, when she served, usually stayed on the baseline.', '马丁内兹发球的时候通常站在底线处。');
INSERT INTO `words` VALUES (560, 'basement', 'ˈbeɪsmənt', 'n.', '地下室;', 'They bought an old schoolhouse to live in and built a workshop in the basement...', '他们买了一处老校舍当住房，然后在地下室里建了一个作坊。');
INSERT INTO `words` VALUES (561, 'basic', 'ˈbeɪsɪk', 'adj.', '基本的;基础的;最简单的;', 'One of the most basic requirements for any form of angling is a sharp hook.', '不管以什么方式钓鱼，有一个锋利的鱼钩都是最基本的要求之一。');
INSERT INTO `words` VALUES (562, 'basically', 'ˈbeɪsɪkli', 'adv.', '大体上;基本上;总的说来;', 'This gun is designed for one purpose—it\'s basically to kill people...', '这种枪只有一个用途——就是用来杀人。');
INSERT INTO `words` VALUES (563, 'basin', 'ˈbeɪsn', 'n.', '盆;(英国英语)调菜盆;一盆(的量);', 'Place the eggs and sugar in a large basin.', '把鸡蛋和糖放在一个大碗里。');
INSERT INTO `words` VALUES (564, 'basis', 'ˈbeɪsɪs', 'n.', '原因;缘由;基准;', 'We\'re going to be meeting there on a regular basis...', '我们将定期在那里会面。');
INSERT INTO `words` VALUES (565, 'basket', 'ˈbɑːskɪt', 'n.', '篮;篓;筐;', '...big wicker picnic baskets filled with sandwiches.', '装满三明治的柳编野餐篮');
INSERT INTO `words` VALUES (566, 'bat', 'bæt', 'n.', '球棒;球拍;球板;', '...a baseball bat.', '棒球球棒');
INSERT INTO `words` VALUES (567, 'batch', 'bætʃ', 'n.', '一批;(食物、药物等)一批生产的量;批;', '...the current batch of trainee priests...', '目前的一批牧师学员');
INSERT INTO `words` VALUES (568, 'bathe', 'beɪð', 'v.', '用水清洗(尤指身体部位);(到海、河等中)游泳消遣;(以光线)撒满，覆盖;', 'The police have warned the city\'s inhabitants not to bathe in the polluted river.', '警方已经告诫该市居民不要在这条被污染的河里游泳。');
INSERT INTO `words` VALUES (569, 'batter', 'ˈbætə(r)', 'v.', '连续猛击;殴打;', '...evidence that the child was being battered.', '这个孩子被家人毒打的证据');
INSERT INTO `words` VALUES (570, 'battery', 'ˈbætri', 'n.', '电池;一系列;一批;', 'The shavers come complete with batteries.', '这些剃须刀都已经配好了电池。');
INSERT INTO `words` VALUES (571, 'battle', 'ˈbætl', 'n.', '战役;战斗;搏斗;', '...the victory of King William III at the Battle of the Boyne.', '国王威廉三世在博因河战役中的胜利');
INSERT INTO `words` VALUES (572, 'bay', 'beɪ', 'n.', '(海或湖的)湾;分隔间(户外或室内的，用以停放车辆、存放货物等);(建筑物的)突出结构;', '...a short ferry ride across the bay.', '乘渡船横渡海湾的短途旅行');
INSERT INTO `words` VALUES (573, 'beach', 'biːtʃ', 'n.', '海滩;沙滩;海滨;', '...a beautiful sandy beach...', '美丽的沙滩');
INSERT INTO `words` VALUES (574, 'bead', 'biːd', 'n.', '(有孔的)珠子;(玫瑰)念珠;(液体的)小滴;', '...a string of beads.', '一串珠子');
INSERT INTO `words` VALUES (575, 'beam', 'biːm', 'n.', '光线;(电波的)波束;(粒子的)束;', 'Frances beamed at her friend with undisguised admiration...', '弗朗西丝开心地对自己的朋友笑着，毫不掩饰她的羡慕之情。');
INSERT INTO `words` VALUES (576, 'beard', 'bɪəd', 'n.', '(人的)胡须，络腮胡子，髯;(动物的)颔毛，须;', 'He\'s decided to grow a beard.', '他已经决定蓄胡子。');
INSERT INTO `words` VALUES (577, 'bearer', 'ˈbeərə(r)', 'n.', '(尤指在礼仪中)持…者;传达消息者;送信人;', 'I hate to be the bearer of bad news.', '我讨厌告诉别人坏消息。');
INSERT INTO `words` VALUES (578, 'bearing', 'ˈbeərɪŋ', 'n.', '关系;影响;姿态;', 'Experts generally agree that diet has an important bearing on your general health...', '专家们普遍认为饮食和健康总体状况密切相关。');
INSERT INTO `words` VALUES (579, 'bearish', 'ˈbeərɪʃ', 'adj.', '熊市的;(证券市场)看跌的;', 'Dealers said investors remain bearish...', '交易员说投资者依然看跌。');
INSERT INTO `words` VALUES (580, 'beast', 'biːst', 'n.', '(尤指大型或凶猛、独特的)动物，兽;性情凶残的人;行为粗暴的人;', '...the threats our ancestors faced from wild beasts.', '我们祖先面对的来自野兽的威胁');
INSERT INTO `words` VALUES (581, 'beautiful', 'ˈbjuːtɪfl', 'adj.', '美丽的;美好的;很好的;', 'She was a very beautiful woman...', '她是个大美女。');
INSERT INTO `words` VALUES (582, 'beauty', 'ˈbjuːti', 'n.', '美;美丽;美人;', '...an area of outstanding natural beauty...', '自然美景美不胜收的景区');
INSERT INTO `words` VALUES (583, 'because', 'bɪˈkɒz', 'conj.', '因为;', 'He is called Mitch, because his name is Mitchell...', '人们叫他米奇，因为他大名叫米切尔。');
INSERT INTO `words` VALUES (584, 'bed', 'bed', 'n.', '床;(河)床;(海等的)底;', 'She went into her bedroom and lay down on the bed...', '她走进卧室，躺在床上。');
INSERT INTO `words` VALUES (585, 'bedroom', 'ˈbedruːm', 'n.', '卧室;有…个卧室的;', '...the spare bedroom.', '空置的卧室');
INSERT INTO `words` VALUES (586, 'beef', 'biːf', 'n.', '牛肉;抱怨;牢骚;', '...roast beef.', '烤牛肉');
INSERT INTO `words` VALUES (587, 'beeper', 'ˈbiːpə(r)', 'n.', '寻呼机;传呼机;', 'His beeper sounded and he picked up the telephone.', '他的寻呼机响了，于是他拿起了电话。');
INSERT INTO `words` VALUES (588, 'beer', 'bɪə(r)', 'n.', '啤酒;一杯(或一瓶、一罐)啤酒;', 'He sat in the kitchen drinking beer...', '他坐在厨房里喝啤酒。');
INSERT INTO `words` VALUES (589, 'befit', 'bɪˈfɪt', 'v.', '适合;对…相称;', 'They offered him a post befitting his seniority and experience...', '他们给他提供了一个同他资历相当的职位。');
INSERT INTO `words` VALUES (590, 'before', 'bɪˈfɔː(r)', 'prep.', '在…以前;在…面前(或前面);(次序或排列)在前面;', 'Annie was born a few weeks before Christmas...', '安妮出生在圣诞节前几周。');
INSERT INTO `words` VALUES (591, 'beforehand', 'bɪˈfɔːhænd', 'adv.', '预先;事先;', 'How could she tell beforehand that I was going to go out?...', '她怎么会事先知道我要出去？');
INSERT INTO `words` VALUES (592, 'beg', 'beɡ', 'v.', '恳求;祈求;哀求;', 'I begged him to come back to England with me...', '我恳求他和我一起回英格兰。');
INSERT INTO `words` VALUES (593, 'begin', 'bɪˈɡɪn', 'v.', '开始;启动;起始;', 'He stood up and began to move around the room...', '他起身开始在屋里来回走动。');
INSERT INTO `words` VALUES (594, 'beginner', 'bɪˈɡɪnə(r)', 'n.', '新手;初学者;', 'The course is suitable for beginners and advanced students...', '这门课程适合初学者和高级程度的学生。');
INSERT INTO `words` VALUES (595, 'beginning', 'bɪˈɡɪnɪŋ', 'n.', '开头;开端;开始部分;', 'This was also the beginning of her recording career...', '这也是她唱片生涯的开始。');
INSERT INTO `words` VALUES (596, 'behalf', 'bɪˈhɑːf', 'n.', '代表，利益;', 'She made an emotional public appeal on her son\'s behalf...', '她代表儿子动情地发出了公开呼吁。');
INSERT INTO `words` VALUES (597, 'behave', 'bɪˈheɪv', 'v.', '表现;表现得体;有礼貌;', 'I couldn\'t believe these people were behaving in this way...', '我难以相信这些人竟会有这种举动。');
INSERT INTO `words` VALUES (598, 'beholder', 'bɪˈhəʊldə(r)', 'n.', '观看者;旁观者;', 'Beauty is in the eye of the beholder.', '情人眼里出西施。');
INSERT INTO `words` VALUES (599, 'belief', 'bɪˈliːf', 'n.', '相信;信心;看法;', 'One billion people throughout the world are Muslims, united by belief in one god.', '全世界有10亿穆斯林，共同的一神信仰将他们团结在一起。');
INSERT INTO `words` VALUES (600, 'believe', 'bɪˈliːv', 'v.', '相信;认为真实;把(某事)当真;', 'Experts believe that the coming drought will be extensive...', '专家们认为即将发生的旱灾将侵袭大片地区。');
INSERT INTO `words` VALUES (601, 'bell', 'bel', 'n.', '钟(声);铃(声);钟状物;', 'I had just enough time to finish eating before the bell rang and I was off to my first class...', '我刚吃完饭铃声就响了，于是我赶去上第一堂课。');
INSERT INTO `words` VALUES (602, 'belly', 'ˈbeli', 'n.', '腹部;肚子;(物体的)圆形或凸起部分;', 'She laid her hands on her swollen belly...', '她把双手放在自己隆起的肚子上。');
INSERT INTO `words` VALUES (603, 'belong', 'bɪˈlɒŋ', 'v.', '应在(某处);适应;合得来;', 'The house had belonged to her family for three or four generations.', '这套房子归她们家有三四代人了。');
INSERT INTO `words` VALUES (604, 'below', 'bɪˈləʊ', 'prep.', '在(或到)…下面;(数量)少于;(标准)低于;', 'He appeared from the apartment directly below Leonard\'s...', '他从伦纳德家正下方的公寓里走了出来。');
INSERT INTO `words` VALUES (605, 'belt', 'belt', 'n.', '腰带;皮带;传送带;', 'He wore a belt with a large brass buckle.', '他系着一条带大铜扣的腰带。');
INSERT INTO `words` VALUES (606, 'bench', 'bentʃ', 'n.', '(通常木制的)长凳，长椅;法官;法官席位;', 'He sat down on a park bench.', '他在公园的长椅上坐了下来。');
INSERT INTO `words` VALUES (607, 'benchmark', 'ˈbentʃmɑːk', 'n.', '基准;', 'The truck industry is a benchmark for the economy.', '卡车业是衡量经济的一个基准。');
INSERT INTO `words` VALUES (608, 'bend', 'bend', 'v.', '(使)倾斜，偏向;(使四肢等)弯曲;把…弄弯(或折起);', 'I bent over and kissed her cheek...', '我俯身亲吻了她的脸颊。');
INSERT INTO `words` VALUES (609, 'beneath', 'bɪˈniːθ', 'prep.', '在(或往)…下面;在(或往)…下方;(对某人来说)不够好;', 'She could see the muscles of his shoulders beneath his T-shirt...', '她可以看到他T恤衫下的肩部肌肉。');
INSERT INTO `words` VALUES (610, 'beneficial', 'ˌbenɪˈfɪʃl', 'adj.', '有利的;有裨益的;有用的;', '...vitamins which are beneficial to our health...', '对我们健康有益的维生素');
INSERT INTO `words` VALUES (611, 'beneficiary', 'ˌbenɪˈfɪʃəri', 'n.', '受益者;受惠人;遗产继承人;', 'The main beneficiaries of pension equality so far have been men.', '迄今为止，退休金均等政策的主要受益者一直是男性。');
INSERT INTO `words` VALUES (612, 'benefit', 'ˈbenɪfɪt', 'n.', '优势;益处;成效;', 'Each family farms individually and reaps the benefit of its labor...', '各家经营各自的农场，收获各自的劳动成果。');
INSERT INTO `words` VALUES (613, 'benign', 'bɪˈnaɪn', 'adj.', '善良的;和善的;慈祥的;', 'They are normally a more benign audience...', '他们通常观看时比较温和。');
INSERT INTO `words` VALUES (614, 'berth', 'bɜːθ', 'n.', '(船或火车等的)卧铺，舱位，铺位;(船的)泊位，锚地;', 'More experienced hands in the Mission had given Miss Pickerstaff a wide berth that morning...', '那天早上，代表团里的老手对皮克斯塔芙小姐都敬而远之。');
INSERT INTO `words` VALUES (615, 'beside', 'bɪˈsaɪd', 'prep.', '在旁边(或附近);与…相比;', 'On the table beside an empty plate was a pile of books...', '桌上空盘子的旁边是一堆书。');
INSERT INTO `words` VALUES (616, 'besides', 'bɪˈsaɪdz', 'prep.', '除…之外(还);', 'I think she has many good qualities besides being very beautiful...', '我觉得她不但长得非常漂亮，而且还有很多优秀品质。');
INSERT INTO `words` VALUES (617, 'best', 'best', 'adj.', '最好的;最出色的;最优秀的;', 'If you want further information the best thing to do is have a word with the driver as you get on the bus...', '如果你想了解更多信息，最好的办法是在上公交车时问一问司机。');
INSERT INTO `words` VALUES (618, 'betray', 'bɪˈtreɪ', 'v.', '出卖;泄露(机密);辜负;', 'When I tell someone I will not betray his confidence I keep my word...', '如果我告诉某人我不会辜负他的信任，我就会恪守诺言。');
INSERT INTO `words` VALUES (619, 'between', 'bɪˈtwiːn', 'prep.', '(空间上)在…中间，介于…之间;(时间上)在…之间，在…中间;(数量、重量、距离等)介于…之间;', 'She left the table to stand between the two men...', '她离开桌子，站在了那两个男人之间。');
INSERT INTO `words` VALUES (620, 'beverage', 'ˈbevərɪdʒ', 'n.', '(除水以外的)饮料;', 'Alcoholic beverages are served in the hotel lounge.', '酒店的公共休息室出售酒精饮料。');
INSERT INTO `words` VALUES (621, 'beware', 'bɪˈweə(r)', 'v.', '当心;小心;提防;', 'Beware of being too impatient with others...', '注意不要对他人太没有耐心。');
INSERT INTO `words` VALUES (622, 'bewilder', 'bɪˈwɪldə(r)', 'v.', '使迷惑;使糊涂;', 'The silence from Alex had hurt and bewildered her.', '亚历克斯的沉默伤害了她，让她不知所措。');
INSERT INTO `words` VALUES (623, 'bewildering', 'bɪˈwɪldərɪŋ', 'adj.', '令人困惑的;使人糊涂的;', 'A glance along his bookshelves reveals a bewildering array of interests...', '看一眼他的书架就知道他兴趣繁杂。');
INSERT INTO `words` VALUES (624, 'beyond', 'bɪˈjɒnd', 'prep.', '在(或向)…较远的一边;晚于;迟于;', 'They heard footsteps in the main room, beyond a door...', '他们听见门外的大厅里有脚步声。');
INSERT INTO `words` VALUES (625, 'bias', 'ˈbaɪəs', 'n.', '偏见;偏心;偏向;', 'Bias against women permeates every level of the judicial system...', '各级司法机构普遍存在对女性的偏见。');
INSERT INTO `words` VALUES (626, 'bibliography', 'ˌbɪbliˈɒɡrəfi', 'n.', '(某一专题或作家的)书目，索引;参考书目;目录学;', 'At the end of this chapter there is a select bibliography of useful books.', '本章末尾列有一份有用的精选书目。');
INSERT INTO `words` VALUES (627, 'bidder', 'ˈbɪdə(r)', 'n.', '出价者;投标者;', 'The sale will be made to the highest bidder subject to a reserve price being attained.', '只要达到了拍卖底价，标的物就会出售给出价最高的竞买人。');
INSERT INTO `words` VALUES (628, 'big', 'bɪɡ', 'adj.', '(体积、程度、数量等)大的，巨大的;年龄较大的;重大的;', 'Australia\'s a big country...', '澳大利亚是个幅员辽阔的国家。');
INSERT INTO `words` VALUES (629, 'bilateral', 'ˌbaɪˈlætərəl', 'adj.', '双方的;双边的;(身体部位)两侧的，对称的;', '...bilateral talks between Britain and America.', '英国和美国之间的双边会谈');
INSERT INTO `words` VALUES (630, 'bilingual', 'ˌbaɪˈlɪŋɡwəl', 'adj.', '会说两种语言的;用两种语言(写)的;', '...bilingual education.', '双语教育');
INSERT INTO `words` VALUES (631, 'bill', 'bɪl', 'n.', '账单;(餐馆的)账单;(提交议会讨论的)议案，法案;', 'They couldn\'t afford to pay the bills...', '他们无力支付这些账单。');
INSERT INTO `words` VALUES (632, 'billing', 'ˈbɪlɪŋ', 'n.', '(演员表上的)排名，演员名次;开具账单;营业额;', 'They couldn\'t afford to pay the bills...', '他们无力支付这些账单。');
INSERT INTO `words` VALUES (633, 'bin', 'bɪn', 'n.', '垃圾箱;(有盖)大容器，箱，柜;', 'He screwed the paper small and chucked it in the bin.', '他把纸揉成小团，扔进了垃圾桶。');
INSERT INTO `words` VALUES (634, 'bind', 'baɪnd', 'v.', '捆绑;系;(用长布条)缠绕;', 'It is the memory and threat of persecution that binds them together.', '遭受迫害的记忆和威胁把他们紧紧联系在一起。');
INSERT INTO `words` VALUES (635, 'binding', 'ˈbaɪndɪŋ', 'adj.', '必须遵守的;有法律约束力的;', '...proposals for a legally binding commitment on nations to stabilise emissions of carbon dioxide...', '要求各国作出具有法律约束力的承诺以稳定二氧化碳排放量的提案');
INSERT INTO `words` VALUES (636, 'biological', 'ˌbaɪəˈlɒdʒɪkl', 'adj.', '生物学的;生物的;与生命过程有关的;', 'The living organisms somehow concentrated the minerals by biological processes...', '生命体通过某种生物过程聚集矿物质。');
INSERT INTO `words` VALUES (637, 'bipolar', 'ˌbaɪˈpəʊlə(r)', 'adj.', '双相型障碍的;躁狂抑郁性精神病的;', '...the bipolar world of the Cold War years.', '冷战期间的两极世界');
INSERT INTO `words` VALUES (638, 'birth', 'bɜːθ', 'n.', '出生;诞生;分娩;', 'It was the birth of his grandchildren which gave him greatest pleasure...', '孙子孙女的出生给他带来了极大的快乐。');
INSERT INTO `words` VALUES (639, 'bit', 'bɪt', 'n.', '有点儿;稍微;稍顷;', 'All it required was a bit of work...', '这事儿只需稍费工夫就可以了。');
INSERT INTO `words` VALUES (640, 'bite', 'baɪt', 'v.', '咬;叮;蜇;', 'Both sisters bit their nails as children...', '两姐妹都像小孩儿一样咬指甲。');
INSERT INTO `words` VALUES (641, 'bitmap', 'ˈbɪtmæp', 'n.', '位图;', '...bitmap graphics for representing complex images such as photographs.', '表示照片这类复杂图像的位图图形');
INSERT INTO `words` VALUES (642, 'bitter', 'ˈbɪtə(r)', 'adj.', '激烈而不愉快的;充满愤怒与仇恨的;愤愤不平的;', '...the scene of bitter fighting during the Second World War.', '第二次世界大战期间激烈的战斗场面');
INSERT INTO `words` VALUES (643, 'bitterly', 'ˈbɪtəli', 'adv.', '伤心地;愤怒地;极其;', 'We are bitterly upset at what has happened.', '发生的事情让我们极为心烦。');
INSERT INTO `words` VALUES (644, 'bizarre', 'bɪˈzɑː(r)', 'adj.', '极其怪诞的;异乎寻常的;', 'The game was also notable for the bizarre behaviour of the team\'s manager...', '这场比赛也因球队主教练怪异的举动而尽人皆知。');
INSERT INTO `words` VALUES (645, 'black', 'blæk', 'adj.', '黑的;黑色的;黑暗的;', 'She was wearing a black coat with a white collar...', '她穿着一件白领黑外套。');
INSERT INTO `words` VALUES (646, 'blackmail', 'ˈblækmeɪl', 'n.', '勒索;敲诈;胁迫;', 'It looks like the pictures were being used for blackmail...', '这些照片好像被用于敲诈勒索。');
INSERT INTO `words` VALUES (647, 'blade', 'bleɪd', 'n.', '刀身;刀片;刀刃;', 'Many of these tools have sharp blades, so be careful.', '这些工具中很多都有锋利的刀片，务必小心。');
INSERT INTO `words` VALUES (648, 'blame', 'bleɪm', 'v.', '把…归咎于;责怪;指责;', 'The commission is expected  to blame the army for many of the atrocities...', '委员会理应谴责军队的诸多暴行。');
INSERT INTO `words` VALUES (649, 'blank', 'blæŋk', 'adj.', '空白的;空的;无图画(或标记、装饰)的;', 'We could put some of the pictures over on that blank wall over there...', '我们可以把一些图画贴在那边的空白墙壁上。');
INSERT INTO `words` VALUES (650, 'blanking', 'ˈblæŋkɪŋ', 'v.', '毫不理睬(某人);突然忘掉;突然思路模糊;', 'We could put some of the pictures over on that blank wall over there...', '我们可以把一些图画贴在那边的空白墙壁上。');
INSERT INTO `words` VALUES (651, 'blast', 'blɑːst', 'n.', '爆炸;(爆炸引起的)气浪，冲击波;突如其来的强劲气流;', '250 people were killed in the blast.', '250人在这次爆炸中丧生。');
INSERT INTO `words` VALUES (652, 'blaze', 'bleɪz', 'v.', '熊熊燃烧;闪耀;发亮光;', 'Three people died as wreckage blazed, and rescuers fought to release trapped drivers...', '汽车残骸熊熊燃烧，三人葬身火海，救援人员奋力解救被困的司机们。');
INSERT INTO `words` VALUES (653, 'bleach', 'bliːtʃ', 'v.', '(使)变白，漂白，晒白，退色;', 'These products don\'t bleach the hair.', '这些产品不会使头发变白。');
INSERT INTO `words` VALUES (654, 'bleak', 'bliːk', 'adj.', '不乐观的;无望的;暗淡的;', 'The immediate outlook remains bleak...', '近期前景依然暗淡。');
INSERT INTO `words` VALUES (655, 'bleed', 'bliːd', 'v.', '流血;失血;给(某人)放血;', 'His head had struck the sink and was bleeding...', '他的头撞到了水槽上，正在流血。');
INSERT INTO `words` VALUES (656, 'blend', 'blend', 'v.', '使混合;掺和;(和某物)混合;', 'Blend  the butter with the sugar and beat until light and creamy...', '把糖掺入黄油然后搅拌至滑软细腻。');
INSERT INTO `words` VALUES (657, 'bless', 'bles', 'v.', '求上帝降福于;祝福;祝圣;', '...asking for all present to bless this couple and their loving commitment to one another.', '就让在场的所有人祝福这对夫妇以及他们对彼此爱的承诺');
INSERT INTO `words` VALUES (658, 'blessing', 'ˈblesɪŋ', 'n.', '上帝的恩宠;祝福;祝颂;', 'Rivers are a blessing for an agricultural country.', '河流对于一个农业国家来说是一种恩赐。');
INSERT INTO `words` VALUES (659, 'blind', 'blaɪnd', 'adj.', '瞎的;失明的;盲人;', 'I started helping him run the business when he went blind...', '他失明以后，我就开始帮他打理生意。');
INSERT INTO `words` VALUES (660, 'blindly', 'ˈblaɪndli', 'adv.', '摸黑地;在黑暗中;不加思考地;', 'Don\'t just blindly follow what the banker says...', '不要盲目听信银行家的话。');
INSERT INTO `words` VALUES (661, 'blindness', 'ˈblaɪndnəs', 'n.', '失明，盲，瞎；盲目;精神性盲，（感觉的）缺失;理解力（或识别力、判断力）的丧失;', 'I started helping him run the business when he went blind...', '他失明以后，我就开始帮他打理生意。');
INSERT INTO `words` VALUES (662, 'blink', 'blɪŋk', 'v.', '眨眼睛;闪烁;', 'Kathryn blinked and forced  a smile...', '凯瑟琳眨了眨眼睛，挤出一丝微笑。');
INSERT INTO `words` VALUES (663, 'blinking', 'ˈblɪŋkɪŋ', 'adj./adv.', '(委婉语，与bloody同义)讨厌，可恶;', 'Kathryn blinked and forced  a smile...', '凯瑟琳眨了眨眼睛，挤出一丝微笑。');
INSERT INTO `words` VALUES (664, 'bloc', 'blɒk', 'n.', '(政治利益一致的)国家集团;', '...the former Soviet bloc.', '前苏联集团');
INSERT INTO `words` VALUES (665, 'block', 'blɒk', 'n.', '(方形平面)大块;立方体;(公寓、办公、教学、医院等)大楼;', '...blocks of council flats.', '一栋栋市政公寓大楼');
INSERT INTO `words` VALUES (666, 'blocking', 'ˈblɒkɪŋ', 'v.', '堵塞;阻塞;堵住(某人的路等);', '...blocks of council flats.', '一栋栋市政公寓大楼');
INSERT INTO `words` VALUES (667, 'bloodstream', 'ˈblʌdstriːm', 'n.', '体内循环的血液;血流;', 'The disease releases toxins into the bloodstream.', '这种病会释放毒素到血液中。');
INSERT INTO `words` VALUES (668, 'bloom', 'bluːm', 'n.', '(常指供观赏的)花;健康有精神的面貌;', '...the sweet fragrance of the white blooms...', '白色花朵的芳香');
INSERT INTO `words` VALUES (669, 'blossom', 'ˈblɒsəm', 'n.', '(尤指果树或灌木的)花朵，花簇;', 'The cherry blossom came out early in Washington this year.', '今年华盛顿的樱花开得很早。');
INSERT INTO `words` VALUES (670, 'blot', 'blɒt', 'v.', '(用软纸或布)吸干液体;把墨水溅到(纸上);', '...a blot on the reputation of the architectural profession...', '败坏建筑业名声的污点');
INSERT INTO `words` VALUES (671, 'blower', 'ˈbləʊə(r)', 'n.', '吹风机;送风机;电话;', 'Anyway, I soon got on the blower to him.', '不管怎样，我立马给他去了电话。');
INSERT INTO `words` VALUES (672, 'blue', 'bluː', 'adj.', '蓝色的;天蓝色的;蔚蓝色的;', 'There were swallows in the cloudless blue sky...', '燕子在万里无云的蓝天飞翔。');
INSERT INTO `words` VALUES (673, 'blueprint', 'ˈbluːprɪnt', 'n.', '(建筑、机器等的)蓝图;行动方案;计划蓝图;', 'The country\'s  president will offer delegates his blueprint for the country\'s future.', '该国总统会向代表们展示他构想的国家未来发展的蓝图。');
INSERT INTO `words` VALUES (674, 'blunder', 'ˈblʌndə(r)', 'n.', '愚蠢(或粗心)的错误;', 'I think he made a tactical blunder by announcing it so far ahead of time.', '我认为他在时机远未成熟时就把这事说了出去是一种失策。');
INSERT INTO `words` VALUES (675, 'blunt', 'blʌnt', 'adj.', '不锋利的;钝的;嘴直的;', 'She is blunt about her personal life...', '她对自己的私生活直言不讳。');
INSERT INTO `words` VALUES (676, 'blur', 'blɜː(r)', 'n.', '(移动的)模糊形状;模糊的记忆;', 'Out of the corner of my eye I saw a blur of movement on the other side of the glass...', '从眼角余光中我看到玻璃的另一边隐约有东西在移动。');
INSERT INTO `words` VALUES (677, 'blurry', 'ˈblɜːri', 'adj.', '模糊不清的;', '...a blurry picture of a man.', '一位男子模糊不清的照片');
INSERT INTO `words` VALUES (678, 'blush', 'blʌʃ', 'v.', '(因尴尬或害羞)脸红，涨红了脸;(因某事)羞愧，尴尬;', '\'Hello, Maria,\' he said, and she blushed again...', '“你好，玛丽亚，”他说，这让她脸上又泛起了红晕。');
INSERT INTO `words` VALUES (679, 'board', 'bɔːd', 'n.', '板;(尤指)木板;…用木板(或板材);', '...a chopping board.', '砧板');
INSERT INTO `words` VALUES (680, 'boards', 'bɔːdz', 'n.', '板;(尤指)木板;…用木板(或板材);', '...a chopping board.', '砧板');
INSERT INTO `words` VALUES (681, 'boast', 'bəʊst', 'v.', '自夸;自吹自擂;有(值得自豪的东西);', 'Witnesses said Furci boasted that he took part in killing them...', '几位证人声称富尔奇曾吹嘘自己参与了对他们的屠杀。');
INSERT INTO `words` VALUES (682, 'boat', 'bəʊt', 'n.', '小船;汽艇;舟;', 'One of the best ways to see the area is in a small boat...', '游览该地区的一个最好方式是乘坐小船。');
INSERT INTO `words` VALUES (683, 'body', 'ˈbɒdi', 'n.', '身体;躯体;躯干;', 'The largest organ in the body is the liver.', '人体最大的器官是肝脏。');
INSERT INTO `words` VALUES (684, 'boil', 'bɔɪl', 'v.', '(使)沸腾;煮沸;烧开;', 'I stood in the kitchen, waiting for the water to boil...', '我站在厨房，等着水烧开。');
INSERT INTO `words` VALUES (685, 'bold', 'bəʊld', 'adj.', '大胆自信的;敢于表白情感的;敢于冒险的;', 'Amrita becomes a bold, daring rebel...', '阿姆里塔成了一个勇敢无畏的叛逆者。');
INSERT INTO `words` VALUES (686, 'bomb', 'bɒm', 'n.', '炸弹;核武器;核弹;', 'Bombs went off at two London train stations...', '伦敦的两个地铁站发生了炸弹爆炸事件。');
INSERT INTO `words` VALUES (687, 'bombard', 'bɒmˈbɑːd', 'v.', '轰炸;轰击;大量提问;', 'He bombarded Catherine with questions to which he should have known the answers...', '他明知故问地不断质问凯瑟琳。');
INSERT INTO `words` VALUES (688, 'bomber', 'ˈbɒmə(r)', 'n.', '轰炸机;非法放置炸弹者;', '...a high speed bomber with twin engines.', '双引擎高速轰炸机');
INSERT INTO `words` VALUES (689, 'bombshell', 'ˈbɒmʃel', 'n.', '出乎意料的事情，意外消息(常指不幸);金发美女;', 'His resignation after thirteen years is a political bombshell...', '他任职13年后提出辞职，这在政治上轰动一时。');
INSERT INTO `words` VALUES (690, 'bond', 'bɒnd', 'n.', '纽带;联系;关系;', 'The experience created a very special bond between us.', '这种经历使我们之间产生了一种很特殊的关系。');
INSERT INTO `words` VALUES (691, 'bonded', 'ˈbɒndɪd', 'adj.', '(公司)有担保的;', 'The company is a fully bonded member of the Association of British Travel Agents...', '该公司是英国旅行社协会的完全担保会员。');
INSERT INTO `words` VALUES (692, 'bone', 'bəʊn', 'n.', '骨头;骨;骨质;', 'Many passengers suffered broken bones...', '许多乘客骨折。');
INSERT INTO `words` VALUES (693, 'bonus', 'ˈbəʊnəs', 'n.', '奖金;红利;意外收获;', 'Workers in big firms receive a substantial part of their pay in the form of bonuses and overtime.', '大公司的员工有相当一部分薪酬来自奖金和加班费。');
INSERT INTO `words` VALUES (694, 'book', 'bʊk', 'n.', '书;书籍;印刷(或电子)出版物;', 'His eighth book came out earlier this year and was an instant best-seller...', '他的第8本书在今年早些时候面世，迅速成为了畅销书。');
INSERT INTO `words` VALUES (695, 'booked', 'bʊkt', 'v.', '(向旅馆、饭店、戏院等)预约，预订;给(某人)预订飞机等座位;和(歌手等)预约演出日期;', 'His eighth book came out earlier this year and was an instant best-seller...', '他的第8本书在今年早些时候面世，迅速成为了畅销书。');
INSERT INTO `words` VALUES (696, 'booking', 'ˈbʊkɪŋ', 'n.', '预订;(演出等的)预约，约定;(对犯规者的)记名警告;', 'I suggest you tell him there was a mistake over his late booking.', '我建议你告诉他，他应该及早预订。');
INSERT INTO `words` VALUES (697, 'books', 'bʊks', 'n.', '书;书籍;印刷(或电子)出版物;', 'His eighth book came out earlier this year and was an instant best-seller...', '他的第8本书在今年早些时候面世，迅速成为了畅销书。');
INSERT INTO `words` VALUES (698, 'boom', 'buːm', 'n.', '(贸易和经济活动的)激增，繁荣;(某种体育运动、音乐等)突然风靡的时期;帆桁;', 'An economic boom followed, especially in housing and construction...', '接着出现了经济繁荣，尤其是在房地产业和建筑业。');
INSERT INTO `words` VALUES (699, 'boost', 'buːst', 'v.', '使增长;使兴旺;偷窃;', 'It wants the government to take action to boost the economy...', '它希望政府采取行动促进经济发展。');
INSERT INTO `words` VALUES (700, 'boot', 'buːt', 'n.', '靴子;(汽车后部的)行李箱;猛踢;', 'He sat in a kitchen chair, reached down and pulled off his boots...', '他坐到厨房的椅子上，俯身脱掉了靴子。');
INSERT INTO `words` VALUES (701, 'booth', 'buːð', 'n.', '不受干扰的划定空间(如电话亭、投票间等);临时货摊(或放映棚等);(餐馆中的)火车座，卡座;', 'I called her from a public phone booth near the entrance to the bar...', '我在酒吧入口处附近的公用电话亭给她打了电话。');
INSERT INTO `words` VALUES (702, 'booting', 'ˈbuːtɪŋ', 'v.', '猛踢;装入操作系统;启动(计算机);', 'He sat in a kitchen chair, reached down and pulled off his boots...', '他坐到厨房的椅子上，俯身脱掉了靴子。');
INSERT INTO `words` VALUES (703, 'border', 'ˈbɔːdə(r)', 'n.', '国界;边界;边疆;', 'They fled across the border.', '他们穿越边界逃走了。');
INSERT INTO `words` VALUES (704, 'bore', 'bɔː(r)', 'v.', '(尤指因啰唆)使厌烦;钻，凿，挖(长而深的洞);盯着看;', 'Dickie bored him all through the meal with stories of the Navy...', '迪基吃饭时一直在讲海军的故事，让他不胜其烦。');
INSERT INTO `words` VALUES (705, 'bored', 'bɔːd', 'adj.', '(对某人 / 事物)厌倦的;烦闷的;', 'I am getting very bored with this entire business.', '我开始对这整件事情感到非常厌倦。');
INSERT INTO `words` VALUES (706, 'boring', 'ˈbɔːrɪŋ', 'adj.', '没趣的;令人厌倦(或厌烦)的;', 'Not only are mothers not paid but also most of their boring or difficult work is unnoticed.', '母亲们不但得不到报酬，而且她们做的那些乏味艰苦的工作常常都不为人所注意。');
INSERT INTO `words` VALUES (707, 'born', 'bɔːn', 'v.', '出生;出世;出现;', 'My mother was 40 when I was born...', '母亲生我时40岁。');
INSERT INTO `words` VALUES (708, 'borrow', 'ˈbɒrəʊ', 'v.', '借;借用;借入(款项);', 'Can I borrow a pen please? ...', '我可以借支笔吗？');
INSERT INTO `words` VALUES (709, 'borrowing', 'ˈbɒrəʊɪŋ', 'n.', '借款;贷款;借贷;', 'We have allowed spending and borrowing to rise in this recession.', '在此经济萧条时期，我们容许增加消费和借贷。');
INSERT INTO `words` VALUES (710, 'bosom', 'ˈbʊzəm', 'n.', '女人的胸部(或乳房);女衣胸部(或胸襟);和爱护自己的人在一起的情形;', '...a large young mother with a baby resting against her ample bosom.', '让怀中婴儿靠在她丰满胸部上的身材高大的年轻母亲');
INSERT INTO `words` VALUES (711, 'boss', 'bɒs', 'n.', '老板;工头;领班;', 'He cannot stand his boss...', '他无法忍受他的老板。');
INSERT INTO `words` VALUES (712, 'both', 'bəʊθ', 'det./pron.', '(与复数名词连用)两个，两个都;不仅…而且…;…和…都;', 'She cried out in fear and flung both arms up to protect her face...', '她吓得大叫，并且抡起双臂护住了自己的脸。');
INSERT INTO `words` VALUES (713, 'bother', 'ˈbɒðə(r)', 'v.', '花费时间精力(做某事);使(某人)烦恼(或担忧、不安);给(某人)造成麻烦(或痛苦);', 'Lots of people don\'t bother to go through a marriage ceremony these days...', '如今很多人不再费心思举行婚礼。');
INSERT INTO `words` VALUES (714, 'bothersome', 'ˈbɒðəsəm', 'adj.', '引起麻烦的;困扰人的;', 'It\'s all been very noisy and bothersome in Parliament this week.', '这个星期国会里一直乱哄哄的，让人心烦。');
INSERT INTO `words` VALUES (715, 'bottle', 'ˈbɒtl', 'n.', '(细颈)瓶子;一瓶(的量);酒;', 'There were two empty beer bottles on the table...', '桌子上有两个空啤酒瓶。');
INSERT INTO `words` VALUES (716, 'bottom', 'ˈbɒtəm', 'n.', '底部;最下部;(朝下的)底，底面;', 'He sat at the bottom of the stairs...', '他坐在最下面的一级楼梯上。');
