INSERT INTO `words` VALUES (721, 'bouquet', 'buˈkeɪ', 'n.', '花束;(尤指酒的)香味，芬芳;', 'The woman carried a bouquet of dried violets.', '这个女人拿着一束干枯的紫罗兰。');
