INSERT INTO `words` VALUES (731, 'brain', 'breɪn', 'n.', '脑;(供食用的)动物脑髓;智力;', 'Her father died of a brain tumour.', '她父亲死于脑瘤。');
INSERT INTO `words` VALUES (732, 'brainstorm', 'ˈbreɪnstɔːm', 'n.', '突然神志不清;脑猝病;', 'I can have a brainstorm and be very extravagant.', '我有时会头脑发热，挥霍无度。');
