INSERT INTO `words` VALUES (739, 'breach', 'briːtʃ', 'n.', '(对法规等的)违背，违犯;破坏;辜负;', 'The newspaper breached the code of conduct on privacy...', '该报违反了保护隐私的行为准则。');
