INSERT INTO `words` VALUES (742, 'break', 'breɪk', 'v.', '(使)破，裂，碎;弄坏;损坏;', 'He fell through the window, breaking the glass...', '他撞破玻璃从窗口摔了下去。');
INSERT INTO `words` VALUES (743, 'breakdown', 'ˈbreɪkdaʊn', 'n.', '(车辆或机器的)故障，损坏;(关系的)破裂;(讨论、系统的)失败;', '...the breakdown of talks between the US and EU officials.', '美国和欧盟官员间谈判的破裂');
