INSERT INTO `words` VALUES (745, 'breakthrough', 'ˈbreɪkθruː', 'n.', '重大进展;突破;', 'The company looks poised to make a significant breakthrough in China...', '这家公司看来很有把握在中国取得重大突破。');
INSERT INTO `words` VALUES (746, 'breast', 'brest', 'n.', '(女子的)乳房;(男子的)退化乳房;胸部;', 'She wears a low-cut dress which reveals her breasts...', '她穿了一件露乳沟的低胸连衣裙。');
INSERT INTO `words` VALUES (747, 'breath', 'breθ', 'n.', '呼吸的空气;一次吸入的空气;微量;', 'I could smell the whisky on his breath...', '我能闻到他呼出的威士忌的味道。');
INSERT INTO `words` VALUES (748, 'breathe', 'briːð', 'v.', '呼吸;呼出;低声说;', 'He stood there breathing deeply and evenly...', '他站在那儿均匀地深呼吸。');
INSERT INTO `words` VALUES (749, 'breed', 'briːd', 'v.', '交配繁殖;饲养，培育(动植物);孕育;', '...rare breeds of cattle...', '稀有品种的牛');
