INSERT INTO `words` VALUES (766, 'brink', 'brɪŋk', 'n.', '(新的、危险的，或令人兴奋的处境的)边缘，初始状态;(峭壁、河岸等的)边沿，边缘;', 'Their economy is teetering on the brink of collapse...', '他们的经济徘徊在崩溃的边缘。');
