INSERT INTO `words` VALUES (778, 'brown', 'braʊn', 'adj.', '棕色的;褐色的;(皮肤)棕色的，被晒黑的;', '...her deep brown eyes...', '她深褐色的眼睛');
INSERT INTO `words` VALUES (779, 'browse', 'braʊz', 'v.', '(在商店里)随便看看;浏览;翻阅;', 'I stopped in several bookstores to browse...', '我曾在几家书店停留，翻看里面的书。');
