INSERT INTO `words` VALUES (781, 'brush', 'brʌʃ', 'n.', '刷子;毛刷;画笔;', 'We gave him paint and brushes...', '我们给了他油漆和几把刷子。');
INSERT INTO `words` VALUES (782, 'brutal', 'ˈbruːtl', 'adj.', '残暴的;兽性的;直截了当的;', 'He was the victim of a very brutal murder.', '他是一桩恶性谋杀案的受害者。');
INSERT INTO `words` VALUES (783, 'bubble', 'ˈbʌbl', 'n.', '泡;气泡;肥皂泡;', 'Ink particles attach themselves to air bubbles and rise to the surface.', '墨汁颗粒附着在气泡上，浮到表面上。');
INSERT INTO `words` VALUES (784, 'buck', 'bʌk', 'n.', '(一)美元;(一)澳元;(一)新西兰元;', 'That would probably cost you about fifty bucks...', '那大概要花掉你50美元左右。');
INSERT INTO `words` VALUES (785, 'bucket', 'ˈbʌkɪt', 'n.', '(有提梁的)桶;大桶状物;(起重机的)吊斗;', 'We drew water in a bucket from the well outside the door...', '我们用一个吊桶从门外的井中打水。');
