INSERT INTO `words` VALUES (787, 'bud', 'bʌd', 'n.', '芽;苞;花蕾;', 'Rosanna\'s favourite time is early summer, just before the buds open.', '罗莎娜最喜爱的季节是初夏，恰在花蕾绽放前。');
