INSERT INTO `words` VALUES (789, 'buffer', 'ˈbʌfə(r)', 'n.', '缓冲物;起缓冲作用的人;(火车头尾或轨道末端的)减震器，缓冲器;', 'Keep savings as a buffer against unexpected cash needs...', '存些积蓄以备不时之需。');
INSERT INTO `words` VALUES (790, 'buffering', 'ˈbʌfərɪŋ', 'v.', '减少，减缓(伤害);保护;使不受…的侵害;', 'Keep savings as a buffer against unexpected cash needs...', '存些积蓄以备不时之需。');
INSERT INTO `words` VALUES (791, 'bug', 'bʌɡ', 'n.', '小昆虫;虫子;轻微的传染病;', 'We noticed tiny bugs that were all over the walls.', '我们注意到墙上爬满了小虫子。');
INSERT INTO `words` VALUES (792, 'build', 'bɪld', 'v.', '建筑;建造;创建;', 'Developers are now proposing to build a hotel on the site...', '开发商现在建议在这个地方盖一座宾馆。');
INSERT INTO `words` VALUES (793, 'builder', 'ˈbɪldə(r)', 'n.', '建筑工人;建筑公司;营造商;', 'The builders have finished the roof.', '建筑工人已经盖好了房顶。');
INSERT INTO `words` VALUES (794, 'building', 'ˈbɪldɪŋ', 'n.', '建筑物;房子;楼房;', 'They were on the upper floor of the building...', '他们在这座建筑物的楼上。');
INSERT INTO `words` VALUES (795, 'bulb', 'bʌlb', 'n.', '电灯泡;(植物)鳞茎;鳞茎状物(如温度计的球部);', 'The stairwell was lit by a single bulb.', '楼梯间只有一盏灯照明。');
INSERT INTO `words` VALUES (796, 'bulge', 'bʌldʒ', 'v.', '充满;塞满;凸出;', 'Jiro waddled closer, his belly bulging and distended...', '伊罗步履蹒跚地走近，肚子鼓胀凸出。');
INSERT INTO `words` VALUES (797, 'bulk', 'bʌlk', 'n.', '主体;大部分;(大)体积;', 'The truck pulled out of the lot, its bulk unnerving against the dawn.', '卡车从空地上开出来，庞大的车身小心翼翼地避让着草坪。');
