INSERT INTO `words` VALUES (799, 'bullish', 'ˈbʊlɪʃ', 'adj.', '对未来有信心的;积极乐观的;(对股票价格)看涨的;', 'The market opened in a bullish mood.', '股市开盘时行情看涨。');
INSERT INTO `words` VALUES (800, 'bully', 'ˈbʊli', 'n.', '仗势欺人者;横行霸道者;', 'I fell victim to the office bully...', '我成了这个办公室霸王欺侮的对象。');
INSERT INTO `words` VALUES (801, 'bump', 'bʌmp', 'v.', '(无意地)碰，撞;(尤指身体部位)碰上，撞上;颠簸行进;', 'They stopped walking and he almost bumped into them...', '他们停下了脚步，结果他差点儿撞到他们身上。');
INSERT INTO `words` VALUES (802, 'bumper', 'ˈbʌmpə(r)', 'n.', '(汽车头尾的)保险杠;', '...bumper to bumper traffic jams.', '车挨着车的交通堵塞');
INSERT INTO `words` VALUES (803, 'bunch', 'bʌntʃ', 'n.', '串;束;扎;', 'My neighbours are a bunch of busybodies...', '我的邻居们是一群爱管闲事的人。');
INSERT INTO `words` VALUES (804, 'bundle', 'ˈbʌndl', 'n.', '(一)捆，包，扎;一批(同类事物或出售的货品);风趣的人;', 'She produced a bundle of notes and proceeded to count out one hundred and ninety-five pounds...', '她拿出一捆钞票，接着数出了195英镑。');
INSERT INTO `words` VALUES (805, 'bunk', 'bʌŋk', 'n.', '(尤指船或火车的)卧铺;(尤指儿童的)双层床;架子床;', 'He left his bunk and went up on deck again.', '他离开自己的铺位再次走到甲板上。');
INSERT INTO `words` VALUES (806, 'buoyant', 'ˈbɔɪənt', 'adj.', '看涨的;保持高价的;繁荣的;', 'She was in a buoyant mood and they were looking forward to their new life...', '她心情愉快，他们正在憧憬未来的新生活。');
INSERT INTO `words` VALUES (807, 'burden', 'ˈbɜːdn', 'n.', '(义务、责任等的)重担，负担;重担;重负;', 'The developing  countries bear the burden of an enormous external debt...', '发展中国家背负着巨额外债。');
INSERT INTO `words` VALUES (808, 'bureaucracy', 'bjʊəˈrɒkrəsi', 'n.', '官僚主义;官僚作风;官僚体制;', 'State bureaucracies can tend to stifle enterprise and initiative.', '国家的官僚体制往往会扼杀人们的事业心和积极性。');
INSERT INTO `words` VALUES (809, 'burgeon', 'ˈbɜːdʒən', 'v.', '激增;迅速发展;', 'Plants burgeon from every available space...', '只要有一点空隙，植物就会生根发芽。');
INSERT INTO `words` VALUES (810, 'burglar', 'ˈbɜːɡlə(r)', 'n.', '破门盗贼;入室窃贼;', 'Burglars broke into their home.', '窃贼闯进了他们家。');
INSERT INTO `words` VALUES (811, 'burial', 'ˈberiəl', 'n.', '埋葬;葬礼;', 'The priest prepared the body for burial...', '牧师给死者作安葬准备。');
INSERT INTO `words` VALUES (812, 'bury', 'ˈberi', 'v.', '埋葬;安葬;丧失(某人);', 'They make the charcoal by burying wood in the ground and then slowly burning it.', '他们通过把木材埋到地下缓慢燃烧来获取木炭。');
INSERT INTO `words` VALUES (813, 'bush', 'bʊʃ', 'n.', '灌木;灌木状的东西(尤指浓密的毛发或皮毛);(尤指非洲和澳大利亚的)荒野;', 'Trees and bushes grew down to the water\'s edge.', '树和灌木丛一直长到了水边。');
INSERT INTO `words` VALUES (814, 'business', 'ˈbɪznəs', 'n.', '商业;买卖;生意;', '...young people seeking a career in business...', '涉足商界的年轻人');
INSERT INTO `words` VALUES (815, 'bustle', 'ˈbʌsl', 'v.', '四下忙碌;催促(某人向某方向);', 'My mother bustled around the kitchen...', '我母亲在厨房忙得团团转。');
INSERT INTO `words` VALUES (816, 'busy', 'ˈbɪzi', 'adj.', '忙碌的;无暇的;忙于(做某事);', 'What is it? I\'m busy...', '什么事？我忙着呢。');
INSERT INTO `words` VALUES (817, 'but', 'bət , bʌt', 'conj.', '而;相反;然而;', '\'You said you\'d stay till tomorrow.\' —  \'I know, Bel, but I think I would rather go back.\'...', '“你说过你要呆到明天的。”——“我知道，贝尔，但我还是想回去。”');
INSERT INTO `words` VALUES (818, 'butter', 'ˈbʌtə(r)', 'n.', '黄油;奶油;', '...bread and butter...', '面包和黄油');
INSERT INTO `words` VALUES (819, 'button', 'ˈbʌtn', 'n.', '纽扣;扣子;(机器的)按钮;', '...a coat with brass buttons.', '带铜纽扣的外套');
INSERT INTO `words` VALUES (820, 'buy', 'baɪ', 'v.', '买;购买;够支付;', 'He could not afford to buy a house...', '他买不起房子。');
INSERT INTO `words` VALUES (821, 'buyer', 'ˈbaɪə(r)', 'n.', '(尤指昂贵商品的)买主;买方;采购员;', 'Car buyers are more interested in safety and reliability than speed.', '购车者们更感兴趣的是汽车的安全性和可靠性，而不是它的速度。');
INSERT INTO `words` VALUES (822, 'buzz', 'bʌz', 'v.', '发出嗡嗡声;发出蜂鸣声;充满兴奋(或活动等);', 'The intercom buzzed and he pressed down the appropriate switch...', '对讲机发出嗡嗡声，他按下了相应的开关。');
INSERT INTO `words` VALUES (823, 'buzzer', 'ˈbʌzə(r)', 'n.', '蜂鸣器;', 'She rang a buzzer at the information desk.', '她按了一下服务台的蜂鸣器。');
INSERT INTO `words` VALUES (824, 'bypass', 'ˈbaɪpɑːs', 'n.', '(绕过城市的)旁路，旁道;(给心脏接旁通管的)分流术，搭桥术;旁通管;', 'A growing number of employers are trying to bypass the unions altogether...', '越来越多的雇主试图完全绕过工会行事。');
INSERT INTO `words` VALUES (825, 'byproduct', 'ˈbaɪˌprɒdʌkt', 'n.', '副产品;意外收获;附带的结果;', 'The raw material for the tyre is a by-product of petrol refining.', '制造轮胎的原材料是提炼汽油时产生的一种副产品。');
INSERT INTO `words` VALUES (826, 'byte', 'baɪt', 'n.', '字节;位组; 位元组;', '...two million bytes of data.', '200万字节的数据');
INSERT INTO `words` VALUES (827, 'cabin', 'ˈkæbɪn', 'n.', '(轮船上工作或生活的)隔间;(飞机的)座舱;(通常为木制的)小屋，小棚屋;', 'He showed her to a small cabin.', '他把她领到了一个小船舱。');
INSERT INTO `words` VALUES (828, 'cabinet', 'ˈkæbɪnət', 'n.', '内阁;储藏柜;陈列柜;', 'The star of my medicine cabinet is the humble aspirin...', '我家药柜里的压箱宝要算是那不起眼的阿司匹林。');
INSERT INTO `words` VALUES (829, 'cable', 'ˈkeɪbl', 'n.', '(系船用的)缆绳;(支撑桥梁等用的)钢索;电缆;', '...overhead power cables.', '高架电缆');
INSERT INTO `words` VALUES (830, 'cabling', 'ˈkeɪblɪŋ', 'n.', '(统称)缆索;', '...modern offices equipped with computer cabling.', '敷设了网线的现代化办公室');
INSERT INTO `words` VALUES (831, 'cache', 'kæʃ', 'n.', '隐藏物(如武器);(秘密)贮存物;高速缓冲存储器;', 'A huge arms cache was discovered by police.', '警方发现了大批私藏的军火。');
INSERT INTO `words` VALUES (832, 'cad', 'kæd', 'n.', '卑鄙的人;粗鄙的人;无赖;', 'He\'s a scoundrel! A cad!', '他是个流氓!无赖!');
INSERT INTO `words` VALUES (833, 'cadre', 'ˈkɑːdə(r)', 'n.', '骨干(队伍);干部;', '...an elite cadre of international managers.', '国际经理人组成的精英队伍');
INSERT INTO `words` VALUES (834, 'cage', 'keɪdʒ', 'n.', '笼子;', 'I hate to see birds in cages.', '我不愿意看到鸟被关在笼子里。');
INSERT INTO `words` VALUES (835, 'cake', 'keɪk', 'n.', '糕饼;蛋糕;饼状食物;', '...a piece of cake...', '一块蛋糕');
INSERT INTO `words` VALUES (836, 'cal', 'kæl', 'abbr.', '计算机辅助学习;电脑辅助学习;', '...325 cals per serving.', '每份食物含325卡热量');
INSERT INTO `words` VALUES (837, 'calamity', 'kəˈlæməti', 'n.', '灾难;灾祸;', 'He described drugs as the greatest calamity of the age...', '他称毒品是这个时代最大的祸害。');
INSERT INTO `words` VALUES (838, 'calculate', 'ˈkælkjuleɪt', 'v.', '计算;核算;预测;', 'From this you can calculate the total mass in the Galaxy...', '由此你可以算出银河系的总质量。');
INSERT INTO `words` VALUES (839, 'calculation', 'ˌkælkjuˈleɪʃn', 'n.', '计算;估计;预测;', 'Leonard made a rapid calculation: he\'d never make it in time.', '伦纳德快速盘算了一下:他肯定赶不上了。');
INSERT INTO `words` VALUES (840, 'calculator', 'ˈkælkjuleɪtə(r)', 'n.', '计算器;', '...a pocket calculator.', '便携计算器');
INSERT INTO `words` VALUES (841, 'calendar', 'ˈkælɪndə(r)', 'n.', '日历;挂历;日程表;', 'There was a calendar on the wall above, with large squares around the dates.', '上面的墙上挂着日历,日期上画着大大的方框。');
INSERT INTO `words` VALUES (842, 'calibrate', 'ˈkælɪbreɪt', 'v.', '标定，校准(刻度，以使测量准确);', '...instructions on how to calibrate a thermometer.', '如何标定温度计的说明');
INSERT INTO `words` VALUES (843, 'call', 'kɔːl', 'v.', '给…命名;称呼;把…叫做;', 'I always wanted to call the dog Mufty for some reason...', '不知怎么的,我老想把这条狗唤作穆夫提。');
INSERT INTO `words` VALUES (844, 'caller', 'ˈkɔːlə(r)', 'n.', '打电话者;访问者;来访者;', 'An anonymous caller told police what had happened.', '有人打匿名电话向警方报告了所发生的事。');
INSERT INTO `words` VALUES (845, 'calling', 'ˈkɔːlɪŋ', 'n.', '使命感;(尤指想帮助他人的)强烈愿望，责任感;职业;', 'He was a consultant physician, a serious man dedicated to his calling.', '他是个会诊医生,严肃认真,对自己的职业全身心投入。');
INSERT INTO `words` VALUES (846, 'callous', 'ˈkæləs', 'adj.', '冷酷无情的;无同情心的;冷漠的;', '...his callous disregard for human life.', '他对人命的漠视');
INSERT INTO `words` VALUES (847, 'calm', 'kɑːm', 'adj.', '镇静的;沉着的;风平浪静的;', 'She is usually a calm and diplomatic woman...', '她通常沉着而老练。');
INSERT INTO `words` VALUES (848, 'calorie', 'ˈkæləri', 'n.', '大卡，千卡(测量食物含热量的单位);卡，卡路里(热量单位，或一克水升高1摄氏度时所需要的热量);', 'A glass of wine does have quite a lot of calories.', '一杯葡萄酒的热量确实不低。');
INSERT INTO `words` VALUES (849, 'camera', 'ˈkæmərə', 'n.', '照相机;(电影)摄影机;(电视)摄像机;', 'Her gran lent her a camera for a school trip to Venice and Egypt.', '她奶奶借给她一架相机,让她带上参加学校组织的威尼斯和埃及之旅。');
INSERT INTO `words` VALUES (850, 'camp', 'kæmp', 'n.', '营地;度假营;(尤指政府让人长时间住宿的)营房，营帐;', '...a refugee camp...', '难民营');
INSERT INTO `words` VALUES (851, 'campaign', 'kæmˈpeɪn', 'n.', '运动(为社会、商业或政治目的而进行的一系列有计划的活动);战役;', 'During his election campaign he promised to put the economy back on its feet...', '他在竞选时许诺将重振经济。');
INSERT INTO `words` VALUES (852, 'campus', 'ˈkæmpəs', 'n.', '(大学、学院的)校园，校区;', '...during a rally at the campus...', '在大学校园的一次集会中');
INSERT INTO `words` VALUES (853, 'canadian', 'kəˈneɪdiən', 'adj.', '加拿大的;', '...the Canadian government.', '加拿大政府');
INSERT INTO `words` VALUES (854, 'canal', 'kəˈnæl', 'n.', '运河;灌溉渠;导管;', '...the Grand Union Canal.', '大联盟运河');
INSERT INTO `words` VALUES (855, 'cancer', 'ˈkænsə(r)', 'n.', '癌;癌症;(迅速蔓延的)邪恶;', 'Her mother died of breast cancer...', '她母亲死于乳腺癌。');
INSERT INTO `words` VALUES (856, 'candidate', 'ˈkændɪdət', 'n.', '(竞选或求职的)候选人，申请人;投考者;应试者;', 'The Democratic candidate is still leading in the polls...', '民主党候选人的得票数依然领先。');
INSERT INTO `words` VALUES (857, 'candle', 'ˈkændl', 'n.', '蜡烛;', 'The bedroom was lit by a single candle.', '卧室里只点了一支蜡烛。');
INSERT INTO `words` VALUES (858, 'candy', 'ˈkændi', 'n.', '糖果;巧克力;一块糖(或巧克力);', '...a piece of candy...', '一块糖');
INSERT INTO `words` VALUES (859, 'cane', 'keɪn', 'n.', '(某些植物，如竹或甘蔗的)茎;(用于制作家具等的)竹竿，藤条;竹杖;', '...cane furniture.', '竹藤家具');
INSERT INTO `words` VALUES (860, 'canteen', 'kænˈtiːn', 'n.', '食堂;餐厅;(士兵、旅游者等用的)水壶;', 'Rennie had eaten his tea in the canteen.', '伦尼已经在餐厅吃过茶点了。');
INSERT INTO `words` VALUES (861, 'canvas', 'ˈkænvəs', 'n.', '帆布;(帆布)画布;油画;', '...a canvas bag.', '帆布包');
INSERT INTO `words` VALUES (862, 'canvasser', '', 'n.', '检票员，推销员，游说（拉票）者;', 'I\'m canvassing for the Conservative Party...', '我在为保守党拉选票。');
INSERT INTO `words` VALUES (863, 'cap', 'kæp', 'n.', '(尤指男用有帽舌的)便帽，制服帽;软帽;(大学师生在特别场合戴的)方帽;', '...a dark blue baseball cap.', '深蓝色棒球帽');
INSERT INTO `words` VALUES (864, 'capability', 'ˌkeɪpəˈbɪləti', 'n.', '能力;才能;(国家的)军事力量，军事武器;', 'People experience differences in physical and mental capability depending on the time of day...', '在一天当中的不同时间,人的体力和脑力也会有所差异。');
INSERT INTO `words` VALUES (865, 'capable', 'ˈkeɪpəbl', 'adj.', '有能力;有才能;能力强的;', 'He appeared hardly capable of conducting a coherent conversation...', '他好像连话都说不清楚。');
INSERT INTO `words` VALUES (866, 'capacity', 'kəˈpæsəti', 'n.', '容量;容积;容纳能力;', 'Our capacity for giving care, love and attention is limited...', '我们能够给予的关怀、关爱和关注是有限的。');
INSERT INTO `words` VALUES (867, 'cape', 'keɪp', 'n.', '披肩;披风;短斗篷;', 'In 1978, Naomi James became the first woman to sail solo around the world via Cape Horn.', '1978年,娜奥米·詹姆斯成为第一个独自驾船绕过合恩角环游世界的女子。');
INSERT INTO `words` VALUES (868, 'capital', 'ˈkæpɪtl', 'n.', '首都;国都;资本;', 'Companies are having difficulty in raising capital...', '各公司融资困难。');
INSERT INTO `words` VALUES (869, 'capitalism', 'ˈkæpɪtəlɪzəm', 'n.', '资本主义;', '...the return of capitalism to Hungary.', '匈牙利资本主义制度的回归');
INSERT INTO `words` VALUES (870, 'capsule', 'ˈkæpsjuːl', 'n.', '(装药物的)胶囊;(装物或装液体的)小塑料容器;太空舱;', '...cod liver oil capsules...', '鳕鱼肝油胶囊');
INSERT INTO `words` VALUES (871, 'captain', 'ˈkæptɪn', 'n.', '船长;机长;(海军)上校;', '...Captain Mark Phillips.', '马克·菲利普斯上尉');
INSERT INTO `words` VALUES (872, 'caption', 'ˈkæpʃn', 'n.', '(图片、漫画等的)说明文字;', 'On the back of the photo is written the simple caption, \'Mrs. Monroe\'.', '照片背面只有简单的几个字——“梦露女士”。');
INSERT INTO `words` VALUES (873, 'captive', 'ˈkæptɪv', 'adj.', '被监禁的;被关起来的;被困住的;', 'Her heart had begun to pound inside her chest like a captive animal.', '她心情紧张起来，一颗心在胸膛里扑通扑通跳个不停。');
INSERT INTO `words` VALUES (874, 'capture', 'ˈkæptʃə(r)', 'v.', '俘虏;俘获;捕获;', 'The guerrillas shot down one aeroplane and captured the pilot...', '游击队击落了一架飞机,并俘获了飞行员。');
INSERT INTO `words` VALUES (875, 'car', 'kɑː(r)', 'n.', '小汽车;轿车;火车车厢;', 'He had left his tickets in his car...', '他把票忘在车里了。');
INSERT INTO `words` VALUES (876, 'carbohydrate', 'ˌkɑːbəʊˈhaɪdreɪt', 'n.', '碳水化合物;糖类;含碳水化合物的食物;', 'Food is made up of carbohydrates, proteins and fats.', '食物由碳水化合物、蛋白质和脂肪构成。');
INSERT INTO `words` VALUES (877, 'card', 'kɑːd', 'n.', '卡片纸;厚纸片;薄纸板;', 'Check the numbers below against the numbers on your card.', '把下列数字和你卡片上的数字比照一下。');
INSERT INTO `words` VALUES (878, 'cardiac', 'ˈkɑːdiæk', 'adj.', '心脏的;心脏病的;', 'The king was suffering from cardiac weakness.', '国王患有心脏病。');
INSERT INTO `words` VALUES (879, 'cardinal', 'ˈkɑːdɪnl', 'n.', '枢机;枢机主教;基数;', 'In 1448, Nicholas was appointed a cardinal...', '1448年,尼古拉斯被任命为红衣主教。');
INSERT INTO `words` VALUES (880, 'cardiovascular', 'ˌkɑːdiəʊˈvæskjələ(r)', 'adj.', '心血管的;', 'Smoking places you at serious risk of cardiovascular and respiratory disease.', '吸烟会大大增加罹患心血管和呼吸道疾病的风险。');
INSERT INTO `words` VALUES (881, 'care', 'keə(r)', 'n.', '照料;照顾;照看;', '...a company that cares about the environment.', '注重环保的公司');
INSERT INTO `words` VALUES (882, 'career', 'kəˈrɪə(r)', 'n.', '生涯;职业;经历;', 'She is now concentrating on a career as a fashion designer...', '她目前正专注于服装设计师的工作。');
INSERT INTO `words` VALUES (883, 'careful', 'ˈkeəfl', 'adj.', '小心;注意;谨慎;', 'Be very careful with this stuff, it can be dangerous if it isn\'t handled properly...', '这东西一定要小心,稍不注意的话就会有危险。');
INSERT INTO `words` VALUES (884, 'carefully', '\'keəfəlɪ', 'adv.', '仔细地;小心翼翼的;诚惶诚恐地;', 'Be very careful with this stuff, it can be dangerous if it isn\'t handled properly...', '这东西一定要小心,稍不注意的话就会有危险。');
INSERT INTO `words` VALUES (885, 'careless', 'ˈkeələs', 'adj.', '不小心的;不仔细的;粗心的;', 'I\'m sorry. How careless of me...', '对不起,我太不小心了。');
INSERT INTO `words` VALUES (886, 'carelessly', 'ˈkeələsli', 'adv.', '粗心地;不用心地;', 'He carelessly left the garage door unlocked...', '他太粗心大意,竟然没锁车库的门。');
INSERT INTO `words` VALUES (887, 'caress', 'kəˈres', 'v.', '抚摩;爱抚;', 'He was gently caressing her golden hair.', '他深情抚摸着她的金发。');
INSERT INTO `words` VALUES (888, 'cargo', 'ˈkɑːɡəʊ', 'n.', '(船或飞机装载的)货物;', 'The boat calls at the main port to load its regular cargo of bananas.', '船停靠这个主要港口以装载其常规货物——香蕉。');
INSERT INTO `words` VALUES (889, 'carpet', 'ˈkɑːpɪt', 'n.', '地毯;(尤指铺满房间的一块)地毯;覆盖地面的一层厚东西;', 'They put down wooden boards, and laid new carpets on top.', '他们铺好木地板,在上面铺上新地毯。');
INSERT INTO `words` VALUES (890, 'carriage', 'ˈkærɪdʒ', 'n.', '(火车的)客车厢;(旧时载客的)四轮马车;运输;', 'The President-elect followed in an open carriage drawn by six beautiful gray horses.', '新当选的总统紧随其后，乘坐一辆由6匹漂亮的灰马拉着的敞篷马车。');
INSERT INTO `words` VALUES (891, 'carrier', 'ˈkæriə(r)', 'n.', '(尤指经营空运的)运输公司;军用运输车;运输舰;', 'There were armoured personnel carriers and tanks on the streets...', '街上有装甲运兵车和坦克。');
INSERT INTO `words` VALUES (892, 'carry', 'ˈkæri', 'v.', '拿;提;搬;', 'He was carrying a briefcase...', '他提着公文包。');
INSERT INTO `words` VALUES (893, 'cart', 'kɑːt', 'n.', '(两轮或四轮)运货马车;手推车;手拉车;', '...a country where horse-drawn carts far outnumber cars.', '马车的数量远远超过汽车的国家');
INSERT INTO `words` VALUES (894, 'carton', 'ˈkɑːtn', 'n.', '(尤指装食品或液体的)硬纸盒，塑料盒，塑料罐;硬纸盒(或塑料盒)所装物品;(内装小盒的)大包装盒;', '...a two-pint carton of milk.', '一盒两品脱的牛奶');
INSERT INTO `words` VALUES (895, 'cartoon', 'kɑːˈtuːn', 'n.', '(报刊中与政治或时事有关的)漫画，讽刺画;动画片;卡通片;', '...one of Britain\'s best-loved cartoon characters, Rupert the Bear.', '英国最受欢迎的漫画形象之一——鲁珀特熊');
INSERT INTO `words` VALUES (896, 'carve', 'kɑːv', 'v.', '雕刻;刻;把(熟肉)切成块;', 'One of the prisoners has carved a beautiful wooden chess set...', '有个犯人刻了一副漂亮的木头象棋。');
INSERT INTO `words` VALUES (897, 'cascade', 'kæˈskeɪd', 'n.', '小瀑布(尤指一连串瀑布中的一支);倾泻;流注;', 'The women have lustrous cascades of black hair...', '那些女子拥有瀑布般乌黑亮泽的秀发。');
INSERT INTO `words` VALUES (898, 'cash', 'kæʃ', 'n.', '现金;(任何形式的)金钱，资金;', '...two thousand pounds in cash.', '2,000英镑现金');
INSERT INTO `words` VALUES (899, 'cassette', 'kəˈset', 'n.', '磁带盒;盒式磁带; 卡式磁带;', 'His two albums released on cassette have sold more than 10 million copies.', '他的两张专辑的磁带销量突破了1,000万。');
INSERT INTO `words` VALUES (900, 'casual', 'ˈkæʒuəl', 'adj.', '不经意的;无忧无虑的;漫不经心的;', 'It\'s difficult for me to be casual about anything...', '要我轻松处事很难。');
INSERT INTO `words` VALUES (901, 'casualty', 'ˈkæʒuəlti', 'n.', '(战争或事故的)伤员，亡者，遇难者;受害者;毁坏物;', 'Troops fired on demonstrators near the Royal Palace causing many casualties.', '军队在皇宫附近向示威人群开枪，造成大量伤亡。');
INSERT INTO `words` VALUES (902, 'catalyst', 'ˈkætəlɪst', 'n.', '催化剂;促使变化的人;引发变化的因素;', 'I very much hope that this case will prove to be a catalyst for change...', '我非常希望这起事件能催生变革。');
INSERT INTO `words` VALUES (903, 'cataract', 'ˈkætərækt', 'n.', '白内障;大瀑布;', 'In one study, light smokers were found to be more than twice as likely to get cataracts as non-smokers...', '研究表明，即使抽烟不多的人也比不抽烟的人患白内障的概率高出一倍多。');
INSERT INTO `words` VALUES (904, 'catastrophe', 'kəˈtæstrəfi', 'n.', '灾难;灾祸;横祸;', 'From all points of view, war would be a catastrophe...', '不管从哪方面说，战争都将是场灾难。');
INSERT INTO `words` VALUES (905, 'catch', 'kætʃ', 'v.', '接住;截住;拦住;', 'Police say they are confident of catching the gunman...', '警方说他们有信心抓住那个持枪歹徒。');
INSERT INTO `words` VALUES (906, 'catching', 'ˈkætʃɪŋ', 'adj.', '有传染性;有感染力;', 'There are those who think eczema is catching.', '有人就是认为湿疹会传染。');
INSERT INTO `words` VALUES (907, 'category', 'ˈkætəɡəri', 'n.', '(人或事物的)类别，种类;', 'This book clearly falls into the category of fictionalised autobiography...', '这本书很显然属于自传体小说。');
INSERT INTO `words` VALUES (908, 'cater', 'ˈkeɪtə(r)', 'v.', '(为社交活动)提供饮食，承办酒席;', 'Minorca is the sort of place that caters for families...', '梅诺卡岛是那种适合家庭旅游的地方。');
INSERT INTO `words` VALUES (909, 'cathedral', 'kəˈθiːdrəl', 'n.', '主教座堂;教区总教堂;', '...St. Paul\'s Cathedral.', '圣保罗大教堂');
INSERT INTO `words` VALUES (910, 'catholic', 'ˈkæθlɪk', 'adj.', '全体基督教徒的;基督教会的;包罗万象的;', '...the Catholic Church.', '天主教教堂');
INSERT INTO `words` VALUES (911, 'cattle', 'ˈkætl', 'n.', '牛;', '...the finest herd of beef cattle for two hundred miles.', '方圆两百英里内最好的肉用牛群');
INSERT INTO `words` VALUES (912, 'causative', 'ˈkɔːzətɪv', 'adj.', '成为原因的;起因的;使役的(动词，如blacken);', 'Both nicotine and carbon monoxide inhaled with cigarette smoking have been incriminated as causative factors.', '抽烟时吸入的尼古丁和一氧化碳都已经被标定为致病因素。');
INSERT INTO `words` VALUES (913, 'cause', 'kɔːz', 'n.', '原因;起因;理由;', 'Smoking is the biggest preventable cause of death and disease...', '在可以预防的引发死亡和疾病的因素当中，吸烟排在第一位。');
INSERT INTO `words` VALUES (914, 'caustic', 'ˈkɔːstɪk', 'adj.', '腐蚀性的;苛性的;尖酸刻薄的;', '...caustic cleaning agents...', '强腐蚀性的洗涤剂');
INSERT INTO `words` VALUES (915, 'caution', 'ˈkɔːʃn', 'n.', '谨慎;小心;慎重;', 'Extreme caution should be exercised when buying part-worn tyres...', '购买部分磨损轮胎时要格外当心。');
INSERT INTO `words` VALUES (916, 'cautious', 'ˈkɔːʃəs', 'adj.', '小心的;谨慎的;', 'The scientists are cautious about using enzyme therapy on humans...', '科学家们对在人身上使用酶疗法非常慎重。');
INSERT INTO `words` VALUES (917, 'cave', 'keɪv', 'n.', '山洞;洞穴;', '...a cave more than 1,000 feet deep.', '1,000多英尺深的山洞');
INSERT INTO `words` VALUES (918, 'cease', 'siːs', 'v.', '(使)停止，终止，结束;', 'At one o\'clock the rain had ceased.', '一点时，雨已停了。');
INSERT INTO `words` VALUES (919, 'ceiling', 'ˈsiːlɪŋ', 'n.', '天花板;顶棚;最高限度;', 'The rooms were spacious, with tall windows and high ceilings...', '房间很宽敞，窗户和天花板都很高。');
INSERT INTO `words` VALUES (920, 'celebrate', 'ˈselɪbreɪt', 'v.', '庆祝;庆贺;主持宗教仪式(尤指圣餐);', 'I was in a mood to celebrate...', '我很想庆祝一下。');
INSERT INTO `words` VALUES (921, 'celebration', 'ˌselɪˈbreɪʃn', 'n.', '庆典;庆祝活动;庆祝;', 'I can tell you, there was a celebration in our house that night.', '告诉你，那天晚上我家开了个庆祝会。');
INSERT INTO `words` VALUES (922, 'celebrity', 'səˈlebrəti', 'n.', '名人;名流;名望;', 'In 1944, at the age of 30, Hersey suddenly became a celebrity.', '1944年，30岁的赫西一夜成名。');
INSERT INTO `words` VALUES (923, 'cell', 'sel', 'n.', '单间牢房;牢房;(修道士或修女住的)小房间;', 'Those cells divide and give many other different types of cells.', '那些细胞分裂，产生出许多不同种类的细胞。');
INSERT INTO `words` VALUES (924, 'cellar', 'ˈselə(r)', 'n.', '地窖;地下室;', 'The box of papers had been stored in a cellar at the family home.', '那箱文件一直藏在家中的地窖里。');
INSERT INTO `words` VALUES (925, 'cellular', 'ˈseljələ(r)', 'adj.', '细胞的;由细胞组成的;(无线电话)蜂窝状的;', 'Many toxic effects can be studied at the cellular level.', '许多毒效可以从细胞层次上去研究。');
INSERT INTO `words` VALUES (926, 'celsius', 'ˈselsiəs', 'adj.', '摄氏的;', 'Highest temperatures 11º Celsius, that\'s 52º Fahrenheit.', '最高温度11摄氏度，即52华氏度。');
INSERT INTO `words` VALUES (927, 'cement', 'sɪˈment', 'n.', '水泥;(干燥后硬化的)水泥;胶合剂;', '...a mixture of wet sand and cement.', '湿沙子和水泥的混合料');
INSERT INTO `words` VALUES (928, 'censor', 'ˈsensə(r)', 'n.', '(书籍、电影等的)审查员，审查官;', 'The military-backed government has heavily censored the news.', '有军方撑腰的政府对新闻进行了严格审查。');
INSERT INTO `words` VALUES (929, 'cent', 'sent', 'n.', '分(辅币单位，相当于许多国家主币面值的1%，如美元或欧元的1%);分币;', 'A cup of rice which cost thirty cents a few weeks ago is now being sold for up to one dollar...', '几个星期前卖30美分一杯的米饭，如今卖到1美元。');
INSERT INTO `words` VALUES (930, 'centenary', 'senˈtiːnəri', 'n.', '第100周年纪念;', '...the centenary of the death of the Dutch painter, Vincent Van Gogh.', '荷兰画家凡·高逝世一百周年纪念');
INSERT INTO `words` VALUES (931, 'centigrade', 'ˈsentɪɡreɪd', 'adj.', '摄氏度的;百分度的;', '...daytime temperatures of up to forty degrees centigrade.', '高达40摄氏度的日间温度');
INSERT INTO `words` VALUES (932, 'central', 'ˈsentrəl', 'adj.', '最重要的;首要的;主要的;', '...Central America\'s Caribbean coast...', '中美洲加勒比海沿岸');
INSERT INTO `words` VALUES (933, 'century', 'ˈsentʃəri', 'n.', '100年;百年;世纪;', '...celebrated figures of the late eighteenth century.', '18世纪晚期的名人');
INSERT INTO `words` VALUES (934, 'ceramic', 'səˈræmɪk', 'n.', '陶瓷制品;陶瓷器;制陶艺术;', '...ceramic tiles.', '瓷砖');
INSERT INTO `words` VALUES (935, 'cereal', 'ˈsɪəriəl', 'n.', '谷类植物;谷物;(常加牛奶作早餐用的)谷类食物;', 'I have a bowl of cereal every morning.', '我每天早上喝一碗麦片。');
INSERT INTO `words` VALUES (936, 'cerebral', 'səˈriːbrəl', 'adj.', '大脑的;脑的;理智的;', 'Washington struck me as a precarious place from which to publish such a cerebral newspaper.', '我的感觉是，在华盛顿出版这样一份理性的报纸不是太靠谱。');
INSERT INTO `words` VALUES (937, 'ceremony', 'ˈserəməni', 'n.', '典礼;仪式;礼节;', '...his grandmother\'s funeral, a private ceremony attended only by the family...', '他祖母的葬礼，只有家人参加的私人仪式');
INSERT INTO `words` VALUES (938, 'certainly', 'ˈsɜːtnli', 'adv.', '无疑;确定;肯定;', 'The public is certainly getting tired of hearing about it...', '关于此事，公众当然已经听腻了。');
INSERT INTO `words` VALUES (939, 'certainty', 'ˈsɜːtnti', 'n.', '确实的事;必然的事;确信;', 'I have told them with absolute certainty there\'ll be no change of policy...', '我已经十分肯定地告诉他们，政策不会变。');
INSERT INTO `words` VALUES (940, 'certificate', 'səˈtɪfɪkət , səˈtɪfɪkeɪt', 'n.', '证明;证明书;文凭;', '...birth certificates.', '出生证明');
INSERT INTO `words` VALUES (941, 'certification', 'ˌsɜːtɪfɪˈkeɪʃn', 'n.', '证明;鉴定;出具课程结业证书;', '...if the president certified that the project would receive at least $650m from overseas sources...', '如果总统证明这个工程将获得至少6.5亿美元的海外资金');
INSERT INTO `words` VALUES (942, 'certify', 'ˈsɜːtɪfaɪ', 'v.', '(尤指书面)证明，证实;颁发(或授予)专业合格证书;证明(某人)患有精神病;', '...if the president certified that the project would receive at least $650m from overseas sources...', '如果总统证明这个工程将获得至少6.5亿美元的海外资金');
INSERT INTO `words` VALUES (943, 'chain', 'tʃeɪn', 'n.', '链子;链条;锁链;', 'His open shirt revealed a fat gold chain...', '他的衬衫敞着，露出一条很粗的金链。');
INSERT INTO `words` VALUES (944, 'chained', 'tʃeɪnd', 'adj.', '被困住的;被缠住的;受到约束的;', 'Surely modern women want to be informed of world current affairs; we are no longer chained to the kitchen sink!...', '当代女性当然想了解时事，我们不再只是受缚于家务小事。');
INSERT INTO `words` VALUES (945, 'chair', 'tʃeə(r)', 'n.', '椅子;(主持会议或委员会的)主席席位;委员长职位;', 'He rose from his chair and walked to the window.', '他从椅子上站起来，朝窗口走去。');
INSERT INTO `words` VALUES (946, 'chairman', 'ˈtʃeəmən', 'n.', '(会议的)主席，主持人;(委员会的)委员长，主席;(公司等的)董事长;', 'Glyn Ford is chairman of the Committee which produced the report...', '格林·福特是提交这份报告的委员会的主席。');
INSERT INTO `words` VALUES (947, 'chalk', 'tʃɔːk', 'n.', '白垩;(白色或彩色的)粉笔;', '...the highest chalk cliffs in Britain...', '英国最高的白垩悬崖');
INSERT INTO `words` VALUES (948, 'challenge', 'ˈtʃælɪndʒ', 'n.', '挑战;艰巨任务;挑战书;', 'I like a big challenge and they don\'t come much bigger than this...', '我喜欢大的挑战，而所有挑战中再没有比这更大的了。');
INSERT INTO `words` VALUES (949, 'chamber', 'ˈtʃeɪmbə(r)', 'n.', '会议厅;(议会的)议院;(作特定用途的)房间，室;', 'We are going to make sure we are in the council chamber every time he speaks.', '每次他发表讲话我们都会确保赶到市议会会议厅去。');
INSERT INTO `words` VALUES (950, 'champion', 'ˈtʃæmpiən', 'n.', '冠军;第一名;优胜者;', '...a former Olympic champion...', '前奥运会冠军');
INSERT INTO `words` VALUES (951, 'championship', 'ˈtʃæmpiənʃɪp', 'n.', '锦标赛;冠军地位;', '...the world chess championship.', '国际象棋世界冠军赛');
INSERT INTO `words` VALUES (952, 'chance', 'tʃɑːns', 'n.', '(尤指希望发生的事的)可能性;机会;机遇;', 'Do you think they have a chance of beating Australia?...', '你认为他们有可能击败澳大利亚吗？');
INSERT INTO `words` VALUES (953, 'chancellor', 'ˈtʃɑːnsələ(r)', 'n.', '(德国或奥地利的)总理;(英国大学的)名誉校长;(某些美国大学的)校长;', '...Chancellor Gerhard Schröder of Germany.', '德国总理施罗德');
INSERT INTO `words` VALUES (954, 'change', 'tʃeɪndʒ', 'v.', '改变;变化;使不同;', 'The ambassador appealed for a change in US policy...', '大使呼吁美国在政策上作出改变。');
INSERT INTO `words` VALUES (955, 'chant', 'tʃɑːnt', 'n.', '反复呼喊的话语;重复唱的歌词;圣歌;', 'He was greeted by the chant of \'Judas! Judas!\'.', '人们看见他就反复不停地喊：“犹大！犹大！”');
INSERT INTO `words` VALUES (956, 'chaos', 'ˈkeɪɒs', 'n.', '混乱;杂乱;紊乱;', 'The world\'s first transatlantic balloon race ended in chaos last night...', '昨晚世界第一届跨大西洋热气球比赛在一片混乱中收场。');
INSERT INTO `words` VALUES (957, 'chap', 'tʃæp', 'n.', '(对男子的友好称呼)家伙，伙计;', 'She thought he was a very nice chap.', '她觉得他是个大好人。');
INSERT INTO `words` VALUES (958, 'chapel', 'ˈtʃæpl', 'n.', '(学校、监狱、私人宅院等基督教徒礼拜用的)小教堂;(教堂内的)分堂，小教堂;(基督教某些教派如英国的不从国教派教徒做礼拜的)教堂;', '...the chapel of the Virgin Mary.', '圣母堂的私人祈祷室');
INSERT INTO `words` VALUES (959, 'chapter', 'ˈtʃæptə(r)', 'n.', '(书的)章，篇，回;(人生或历史的)时期，时代，篇章;主教座堂全体教士;', 'Chromium supplements were used successfully in the treatment of diabetes (see Chapter 4)...', '铬补充剂被成功运用于治疗糖尿病(见第4章)。');
INSERT INTO `words` VALUES (960, 'char', 'tʃɑː(r)', 'v.', '(使)烧黑，烧焦;当家庭清洁工;', 'Toast hazelnuts on a baking sheet until the skins char...', '在烘板上将榛子烤至表皮微煳。');
INSERT INTO `words` VALUES (961, 'character', 'ˈkærəktə(r)', 'n.', '(人、集体的)品质，性格;(地方的)特点，特性;(事物、事件或地方的)特点，特征，特色;', 'Perhaps there is a negative side to his character that you haven\'t seen yet...', '或许，他性格中不好的一面你还没有看见。');
INSERT INTO `words` VALUES (962, 'chicken', 'ˈtʃɪkɪn', 'n.', '鸡;鸡肉;', 'Lionel built a coop so that they could raise chickens and have a supply of fresh eggs.', '莱昂内尔做了个鸡笼，打算养些鸡，吃上新鲜鸡蛋。');
INSERT INTO `words` VALUES (963, 'chief', 'tʃiːf', 'adj.', '最重要的;首要的;主要的;', '...a commission appointed by the police chief.', '警察局局长任命的委员会');
INSERT INTO `words` VALUES (964, 'chiefly', 'ˈtʃiːfli', 'adv.', '主要地;首要地;', 'He joined the consular service, chiefly because this was one of the few job vacancies...', '他到领事馆工作，主要因为那是仅有的几个空缺职位之一。');
INSERT INTO `words` VALUES (965, 'child', 'tʃaɪld', 'n.', '儿童;小孩;子女;', 'When I was a child I lived in a country village...', '我小时候生活在一个小乡村里。');
INSERT INTO `words` VALUES (966, 'childbirth', 'ˈtʃaɪldbɜːθ', 'n.', '分娩;生孩子;', 'She died in childbirth.', '她生孩子的时候死了。');
INSERT INTO `words` VALUES (967, 'childhood', 'ˈtʃaɪldhʊd', 'n.', '童年;幼年;孩童时期;', 'She had a happy childhood...', '她曾有过一个幸福的童年。');
INSERT INTO `words` VALUES (968, 'childish', 'ˈtʃaɪldɪʃ', 'adj.', '孩子的;孩子气的;稚嫩的;', '...childish enthusiasm.', '孩子般的热情');
INSERT INTO `words` VALUES (969, 'chill', 'tʃɪl', 'n.', '寒冷;寒意;凉意;', 'Chill the fruit salad until serving time...', '把水果色拉冰镇一下，吃的时候再拿出来。');
INSERT INTO `words` VALUES (970, 'chilly', 'ˈtʃɪli', 'adj.', '寒冷的;阴冷的;不友好的;', 'It was a chilly afternoon...', '那是个阴冷的下午。');
INSERT INTO `words` VALUES (971, 'chimney', 'ˈtʃɪmni', 'n.', '烟囱;烟道;(屋顶上的)烟囱管;', 'This gas fire doesn\'t need a chimney.', '这种煤气取暖器不需要烟囱。');
INSERT INTO `words` VALUES (972, 'chin', 'tʃɪn', 'n.', '颏;下巴;', '...a double chin...', '双下巴');
INSERT INTO `words` VALUES (973, 'china', 'ˈtʃaɪnə', 'n.', '瓷;瓷料;瓷制品;', '...a small boat made of china.', '瓷制小船');
INSERT INTO `words` VALUES (974, 'chip', 'tʃɪp', 'n.', '(木头、玻璃等的)缺口，缺损处;(木头、玻璃等上掉下来的)碎屑，碎片，碎渣;油炸土豆条;', 'I had fish and chips in a cafe...', '我在一家小餐馆吃了炸鱼薯条。');
INSERT INTO `words` VALUES (975, 'chocolate', 'ˈtʃɒklət', 'n.', '巧克力;朱古力;巧克力糖;', '...a bar of chocolate...', '一块巧克力');
INSERT INTO `words` VALUES (976, 'choice', 'tʃɔɪs', 'n.', '选择;挑选;抉择;', 'It\'s available in a choice of colours...', '有多种颜色可供选择。');
INSERT INTO `words` VALUES (977, 'choir', 'ˈkwaɪə(r)', 'n.', '(教堂的)唱诗班;(公开演出的)合唱团，歌唱队;(教堂)唱经楼，唱诗席;', 'He has been singing in his church choir since he was six.', '他打6岁起一直在教堂唱诗班里唱歌。');
INSERT INTO `words` VALUES (978, 'choke', 'tʃəʊk', 'v.', '(使)窒息，哽噎;(掐住喉咙)使停止呼吸，使窒息;(尤指感情激动而)说不出话来，使哽咽;', 'The coffee was almost too hot to swallow and made him choke for a moment...', '咖啡烫得几乎咽不下去，呛得他好一会儿喘不过气来。');
INSERT INTO `words` VALUES (979, 'choose', 'tʃuːz', 'v.', '选择;挑选;选取;', 'They will be able to choose their own leaders in democratic elections...', '他们将能够通过民主选举选择自己的领导人。');
INSERT INTO `words` VALUES (980, 'chop', 'tʃɒp', 'v.', '切碎;剁碎;砍;', 'Chop the butter into small pieces...', '把黄油切成小片。');
INSERT INTO `words` VALUES (981, 'chord', 'kɔːd', 'n.', '和弦;和音;弦;', '...the opening chords of \'Stairway to Heaven\'.', '《通往天堂的阶梯》的开篇和弦');
INSERT INTO `words` VALUES (982, 'chore', 'tʃɔː(r)', 'n.', '日常事务;例行工作;令人厌烦的任务;', 'She sees exercise primarily as an unavoidable chore...', '她基本上把锻炼看做是不得不做的琐事。');
INSERT INTO `words` VALUES (983, 'chorus', 'ˈkɔːrəs', 'n.', '副歌;合唱曲;合唱团;', 'Caroline sang two verses and the chorus of her song...', '卡罗琳唱了她歌曲中的两段和副歌部分。');
INSERT INTO `words` VALUES (984, 'christ', 'kraɪst', 'n.', '基督;耶稣基督;', '...the teachings of Christ.', '基督教义');
INSERT INTO `words` VALUES (985, 'christian', 'ˈkrɪstʃən', 'adj.', '基督教的;信奉基督教的;基督教徒的;', 'He was a devout Christian...', '他是个虔诚的基督徒。');
INSERT INTO `words` VALUES (986, 'christmas', 'ˈkrɪsməs', 'n.', '圣诞节(12月25日);圣诞节期间;', 'The day after Christmas is generally a busy one for retailers...', '圣诞节后的那一天零售商一般都比较忙。');
INSERT INTO `words` VALUES (987, 'chromatic', 'krəˈmætɪk', 'adj.', '半音(阶)的;', '...the notes of the chromatic scale.', '半音阶音符');
INSERT INTO `words` VALUES (988, 'chronic', 'ˈkrɒnɪk', 'adj.', '长期的;慢性的;难以治愈(或根除)的;', '...chronic back pain.', '慢性背痛');
INSERT INTO `words` VALUES (989, 'chronological', 'ˌkrɒnəˈlɒdʒɪkl', 'adj.', '按发生时间顺序排列的;按时间计算的(年龄)(相对于身体、智力或情感等方面的发展而言);', 'I have arranged these stories in chronological order.', '我把这些报道按时间顺序整理好了。');
INSERT INTO `words` VALUES (990, 'chunk', 'tʃʌŋk', 'n.', '厚块;厚片;大块;', 'They had to be careful of floating chunks of ice.', '他们必须当心大块浮冰。');
INSERT INTO `words` VALUES (991, 'church', 'tʃɜːtʃ', 'n.', '(基督教的)教堂，礼拜堂;礼拜;礼拜仪式;', '...one of Britain\'s most historic churches.', '英国最古老的教堂之一');
INSERT INTO `words` VALUES (992, 'cigar', 'sɪˈɡɑː(r)', 'n.', '雪茄烟;', 'He was sitting alone smoking a big cigar.', '他一个人坐着，抽着一支大雪茄。');
INSERT INTO `words` VALUES (993, 'cigarette', 'ˌsɪɡəˈret', 'n.', '香烟;纸烟;卷烟;', 'He went out to buy a packet of cigarettes.', '他出去买包烟。');
INSERT INTO `words` VALUES (994, 'cinema', 'ˈsɪnəmə', 'n.', '电影院;(去电影院)看电影;电影艺术;', 'The country has relatively few cinemas.', '相对来讲，这个国家几乎没有几家电影院。');
INSERT INTO `words` VALUES (995, 'cipher', 'ˈsaɪfə(r)', 'n.', '密码;暗号;无足轻重的人;', '...converting their messages into ciphers.', '把他们的信息转换成密码');
INSERT INTO `words` VALUES (996, 'circle', 'ˈsɜːkl', 'n.', '圆;圆形;圆周;', 'The flag was red, with a large white circle in the center...', '旗子是红色的，中央有个白色大圆圈。');
INSERT INTO `words` VALUES (997, 'circuit', 'ˈsɜːkɪt', 'n.', '环行;环行路线;电路;', 'Any attempts to cut through the cabling will break the electrical circuit.', '只要一割断电缆，电路就中断。');
INSERT INTO `words` VALUES (998, 'circular', 'ˈsɜːkjələ(r)', 'adj.', '圆形的;环形的;圆的;', '...a circular hole twelve feet wide and two feet deep...', '12英尺宽、2英尺深的圆坑');
INSERT INTO `words` VALUES (999, 'circulate', 'ˈsɜːkjəleɪt', 'v.', '(液体或气体)环流，循环;传播;流传;', 'The document was previously circulated in New York at the United Nations...', '这份文件先前在纽约的联合国总部传阅过。');
INSERT INTO `words` VALUES (1000, 'circulation', 'ˌsɜːkjəˈleɪʃn', 'n.', '血液循环;传递;流传;', 'The Daily News once had the highest circulation of any daily in the country...', '《每日新闻报》的发行量曾位居全国所有日报之首。');
INSERT INTO `words` VALUES (1001, 'circulatory', 'ˌsɜːkjəˈleɪtəri', 'adj.', '血液循环的;', '...the human circulatory system.', '人体血液循环系统');
INSERT INTO `words` VALUES (1002, 'circumference', 'səˈkʌmfərəns', 'n.', '圆周;圆周长;', '...a scientist calculating the earth\'s circumference...', '计算地球周长的科学家');
INSERT INTO `words` VALUES (1003, 'circumstance', 'ˈsɜːkəmstəns', 'n.', '条件;环境;状况;', 'Recent opinion polls show that 60 percent favor abortion under certain circumstances...', '最近的民意调查显示，60%的人赞成在某些情况下可以堕胎。');
INSERT INTO `words` VALUES (1004, 'circumstances', 'ˈsɜːkəmstənsɪz', 'n.', '条件;环境;状况;', 'Recent opinion polls show that 60 percent favor abortion under certain circumstances...', '最近的民意调查显示，60%的人赞成在某些情况下可以堕胎。');
INSERT INTO `words` VALUES (1005, 'circus', 'ˈsɜːkəs', 'n.', '马戏团;马戏表演(常在大帐篷里进行);引人注意的人(或事);', 'My real ambition was to work in a circus.', '我的真正理想是在马戏团工作。');
INSERT INTO `words` VALUES (1006, 'citation', 'saɪˈteɪʃn', 'n.', '引语;引文;引述;', 'His citation says he showed outstanding and exemplary courage.', '颁给他的嘉奖令称他表现出非凡的勇气，堪为表率。');
INSERT INTO `words` VALUES (1007, 'cite', 'saɪt', 'v.', '提及(原因);举出(示例);列举;', 'She cites a favourite poem by George Herbert...', '她引用了自己最喜爱的一首乔治·赫伯特的诗。');
INSERT INTO `words` VALUES (1008, 'citizen', 'ˈsɪtɪzn', 'n.', '公民;居民;市民;', '...American citizens...', '美国公民');
INSERT INTO `words` VALUES (1009, 'city', 'ˈsɪti', 'n.', '都市;城市;(由国王或女王授予特权，通常有大教堂的)特许市;', '...the city of Bologna.', '博洛尼亚市');
INSERT INTO `words` VALUES (1010, 'civic', 'ˈsɪvɪk', 'adj.', '市政的;城市的;城镇的;', '...the businessmen and civic leaders of Manchester.', '曼彻斯特的商人和市政官员');
INSERT INTO `words` VALUES (1011, 'civil', 'ˈsɪvl', 'adj.', '国民的;平民的;国家的，政府的(非宗教或军事的);', '...civil unrest.', '国内骚乱');
INSERT INTO `words` VALUES (1012, 'civilian', 'səˈvɪliən', 'n.', '平民;老百姓;庶民;', 'The safety of civilians caught up in the fighting must be guaranteed.', '必须保证陷入战斗中的平民的人身安全。');
INSERT INTO `words` VALUES (1013, 'claim', 'kleɪm', 'v.', '宣称;声称;断言;', 'He claimed that it was all a conspiracy against him...', '他声称这一切都是一场针对他的阴谋。');
INSERT INTO `words` VALUES (1014, 'clan', 'klæn', 'n.', '(尤指苏格兰的)宗族，氏族，家族;庞大的家族;宗派;', '...rival clans.', '敌对的家族');
INSERT INTO `words` VALUES (1015, 'clap', 'klæp', 'v.', '鼓掌，拍手(表示赞许或欣赏);拍(手);击(掌);', 'The men danced and the women clapped...', '男人们跳舞，女人们拍手。');
INSERT INTO `words` VALUES (1016, 'clarify', 'ˈklærəfaɪ', 'v.', '使更清晰易懂;阐明;澄清;', 'Thank you for writing and allowing me to clarify the present position...', '谢谢你的来信，并允许我阐明目前的立场。');
INSERT INTO `words` VALUES (1017, 'clarity', 'ˈklærəti', 'n.', '清晰;清楚;明确;', '...the ease and clarity with which the author explains difficult technical and scientific subjects.', '作者解释复杂的科技主题时的游刃有余、清晰透彻');
INSERT INTO `words` VALUES (1018, 'clash', 'klæʃ', 'n.', '(两群人之间的)打斗，打架，冲突;争论;辩论;', 'A group of 400 demonstrators clashed with police...', '400名示威者与警察发生了冲突。');
INSERT INTO `words` VALUES (1019, 'clasp', 'klɑːsp', 'v.', '握紧;攥紧;抓紧;', 'She clasped the children to her...', '她把孩子们紧紧地搂进怀里。');
INSERT INTO `words` VALUES (1020, 'class', 'klɑːs', 'n.', '班;班级;课;', 'He had to spend about six months in a class with younger students...', '他不得不和一群比他小的学生在一个班里上了约6个月的课。');
INSERT INTO `words` VALUES (1021, 'classic', 'ˈklæsɪk', 'adj.', '最优秀的;第一流的;有代表性的;', 'The debate in the mainstream press has been a classic example of British hypocrisy...', '主流媒体上的辩论是英国式虚伪的典型例子。');
INSERT INTO `words` VALUES (1022, 'classical', 'ˈklæsɪkl', 'adj.', '古典的;经典的;传统的;', 'Fokine did not change the steps of classical ballet; instead he found new ways of using them.', '福金没有改变传统芭蕾舞的舞步，而是另辟蹊径对这些舞步加以运用。');
INSERT INTO `words` VALUES (1023, 'classification', 'ˌklæsɪfɪˈkeɪʃn', 'n.', '分类;归类;分级;', 'Its tariffs cater for four basic classifications of customer.', '它的价目表适合4个基本类别的顾客。');
INSERT INTO `words` VALUES (1024, 'classify', 'ˈklæsɪfaɪ', 'v.', '将…分类;将…归类;划分;', 'It is necessary initially to classify the headaches into certain types...', '首先，必须将头痛分为几个类型。');
INSERT INTO `words` VALUES (1025, 'clause', 'klɔːz', 'n.', '从句;分句;子句;', 'He has a clause in his contract which entitles him to a percentage of the profits.', '他的合同中有一项条款，使他有权分得一定比例的利润。');
INSERT INTO `words` VALUES (1026, 'claw', 'klɔː', 'n.', '(动物或禽类的)爪，脚爪;(水生有壳动物的)螯，钳;爪形夹具;', 'The cat tried to cling to the edge by its claws.', '猫试图用爪子抓住边缘。');
INSERT INTO `words` VALUES (1027, 'clay', 'kleɪ', 'n.', '黏土;陶土;', '...the heavy clay soils of Cambridgeshire...', '剑桥郡的重黏土质土壤');
INSERT INTO `words` VALUES (1028, 'clean', 'kliːn', 'adj.', '洁净的;干净的;爱干净的;', 'He wore his cleanest slacks, a clean shirt and a navy blazer...', '他穿着他最干净的便裤，洁净的衬衫和藏青色上装。');
INSERT INTO `words` VALUES (1029, 'clear', 'klɪə(r)', 'adj.', '清晰易懂的;明白清楚的;不含混的;', 'The book is clear, readable and adequately illustrated...', '这本书明白易懂，可读性强，插图丰富。');
INSERT INTO `words` VALUES (1030, 'clearance', 'ˈklɪərəns', 'n.', '清除;排除;清理;', '...a slum clearance operation in Nairobi...', '内罗毕拆除贫民窟的行动');
INSERT INTO `words` VALUES (1031, 'clergy', 'ˈklɜːdʒi', 'n.', '(统称)圣职人员，神职人员;', 'These proposals met opposition from the clergy.', '这些提案遭到神职人员的反对。');
INSERT INTO `words` VALUES (1032, 'clerk', 'klɑːk', 'n.', '职员;簿记员;文书;', 'She was offered a job as an accounts clerk with a travel firm.', '她得到一份在旅行社当记账员的工作。');
INSERT INTO `words` VALUES (1033, 'clever', 'ˈklevə(r)', 'adj.', '聪明的;聪颖的;熟练的;', 'He\'s a very clever man...', '他非常聪明。');
INSERT INTO `words` VALUES (1034, 'click', 'klɪk', 'v.', '使发出咔嗒声;使咔嗒(或咔嚓)响;(在计算机屏幕上用鼠标)点击，单击;', 'The applause rose to a crescendo and cameras clicked...', '掌声越来越响，照相机的咔嗒声不绝于耳。');
INSERT INTO `words` VALUES (1035, 'client', 'ˈklaɪənt', 'n.', '委托人;当事人;(连接在服务器上的)客户机;', '...a solicitor and his client...', '律师和他的委托人');
INSERT INTO `words` VALUES (1036, 'cliff', 'klɪf', 'n.', '(常指海洋边的)悬崖，峭壁;', 'The car rolled over the edge of a cliff.', '汽车从悬崖边上滚下去了。');
INSERT INTO `words` VALUES (1037, 'climate', 'ˈklaɪmət', 'n.', '气候;气候区;倾向;', '...the hot and humid climate of Cyprus.', '塞浦路斯炎热潮湿的气候');
INSERT INTO `words` VALUES (1038, 'climax', 'ˈklaɪmæks', 'n.', '(重要事情或时刻的)高潮，极点，顶点;(戏剧、音乐等通常接近结束时出现的)高潮;性高潮;', 'For Pritchard, reaching an Olympics was the climax of her career...', '对普里查德来说，参加奥运会是她事业上的巅峰。');
INSERT INTO `words` VALUES (1039, 'climb', 'klaɪm', 'v.', '攀登;爬;(尤指吃力地向某处)爬;', 'Climbing the first hill took half an hour...', '爬第一座小山花了半个钟头。');
INSERT INTO `words` VALUES (1040, 'cling', 'klɪŋ', 'v.', '抓紧;紧握;紧抱;', 'Another man was rescued as he clung to the riverbank...', '另外一个人因为紧紧攀住河堤而获救。');
INSERT INTO `words` VALUES (1041, 'clinic', 'ˈklɪnɪk', 'n.', '诊所;(医院的)门诊部;门诊时间;', '...a family planning clinic.', '计划生育诊所');
INSERT INTO `words` VALUES (1042, 'clinical', 'ˈklɪnɪkl', 'adj.', '临床的;临床诊断的;冷淡的;', 'The first clinical trials were expected to begin next year.', '第一批临床实验有望明年开始。');
INSERT INTO `words` VALUES (1043, 'clip', 'klɪp', 'n.', '(金属或塑料的)夹子，回形针;首饰别针;剪短;', 'She took the clip out of her hair.', '她把发夹取了下来。');
INSERT INTO `words` VALUES (1044, 'clipping', 'ˈklɪpɪŋ', 'n.', '剪下物;', '...bulletin boards crowded with newspaper clippings.', '贴满报纸剪报的布告栏');
INSERT INTO `words` VALUES (1045, 'clock', 'klɒk', 'n.', '时钟;钟;', 'He was conscious of a clock ticking...', '他听到了钟的嘀嗒声。');
INSERT INTO `words` VALUES (1046, 'clockwise', 'ˈklɒkwaɪz', 'adv./adj.', '顺时针方向(的);', 'He told the children to start moving clockwise around the room.', '他让孩子们开始按顺时针方向绕着房间走。');
INSERT INTO `words` VALUES (1047, 'clone', 'kləʊn', 'n.', '克隆动物(或植物);无性繁殖动物(或植物);复制动物(或植物);', 'Tom was in some ways a younger clone of his handsome father...', '汤姆某些地方长得和他那帅气的父亲一模一样，只是年轻一些。');
INSERT INTO `words` VALUES (1048, 'closed', 'kləʊzd', 'adj.', '关闭;封闭;(尤指一段时间)停止营业;', 'It was to be a closed circle of no more than twelve women...', '它本来应该是一个不超过12名女性的封闭的小圈子。');
INSERT INTO `words` VALUES (1049, 'clot', 'klɒt', 'n.', '蠢人;笨蛋;傻瓜;', 'He needed emergency surgery to remove a blood clot from his brain.', '他需要进行紧急手术以清除脑部的一处血块。');
INSERT INTO `words` VALUES (1050, 'cloth', 'klɒθ', 'n.', '织物;布料;(一块)布;', 'She began cleaning the wound with a piece of cloth.', '她开始用一块布清洗伤口。');
INSERT INTO `words` VALUES (1051, 'clothe', 'kləʊð', 'v.', '给…穿衣;为(某人)提供衣服;', 'She was on her own with two kids to feed and clothe.', '她独自一个人要供两个孩子吃穿。');
INSERT INTO `words` VALUES (1052, 'clothes', 'kləʊðz', 'n.', '衣服;服装;', 'Moira walked upstairs to change her clothes...', '莫伊拉上楼去换衣服。');
INSERT INTO `words` VALUES (1053, 'clothing', 'ˈkləʊðɪŋ', 'n.', '衣服;(尤指某种)服装;', 'Some locals offered food and clothing to the refugees...', '一些当地人为难民们提供食品和衣服。');
INSERT INTO `words` VALUES (1054, 'cloud', 'klaʊd', 'n.', '云;云朵;云状物(如尘雾、烟雾、一群飞行的昆虫);', '...the varied shapes of the clouds...', '云朵各种各样的形状');
INSERT INTO `words` VALUES (1055, 'cloudy', 'ˈklaʊdi', 'adj.', '被云遮住的;阴云密布的;阴天的;', '...a windy, cloudy day.', '多云的大风天');
INSERT INTO `words` VALUES (1056, 'club', 'klʌb', 'n.', '俱乐部;社团;会;', '...the Chorlton Conservative Club.', '升尔顿保守党俱乐部');
INSERT INTO `words` VALUES (1057, 'clue', 'kluː', 'n.', '(帮助警方破案的)线索，迹象;(问题答案的)线索，提示;(纵横填字谜、游戏或问题的)提示词语，解答提示;', 'Geneticists in Canada have discovered a clue to the puzzle of why our cells get old and die...', '加拿大的遗传学家已经发现了解开细胞衰老、死亡之谜的线索。');
INSERT INTO `words` VALUES (1058, 'clump', 'klʌmp', 'n.', '(尤指树或植物的)丛，簇，束，串;(人的)群，组;(草的)堆;', '...a clump of trees bordering a side road.', '旁路边的树丛');
INSERT INTO `words` VALUES (1059, 'clumsy', 'ˈklʌmzi', 'adj.', '笨拙的;不灵巧的;无技巧的;', 'I\'d never seen a clumsier, less coordinated boxer...', '我从没见过更笨拙、动作更不协调的拳击手。');
INSERT INTO `words` VALUES (1060, 'cluster', 'ˈklʌstə(r)', 'n.', '(同类物丛生或聚集的)簇，团，束，串;(人或动物的)群，组;辅音丛;', '...clusters of men in formal clothes...', '一群群身着正装的男人');
INSERT INTO `words` VALUES (1061, 'clutch', 'klʌtʃ', 'v.', '紧握;抱紧;抓紧;', 'I staggered and had to clutch at a chair for support...', '我踉踉跄跄地，不得不抓住一把椅子稳住自己。');
INSERT INTO `words` VALUES (1062, 'coach', 'kəʊtʃ', 'n.', '(体育运动的)教练;私人教师;(多指)考前辅导教师;', 'Tony Woodcock has joined German amateur team SC Brueck as coach.', '托尼·伍德科克加盟德国业余队SC布吕克队担任教练。');
INSERT INTO `words` VALUES (1063, 'coal', 'kəʊl', 'n.', '煤;(尤指燃烧着的)煤块;', 'Gas-fired electricity is cheaper than coal...', '天然气发电比煤便宜。');
INSERT INTO `words` VALUES (1064, 'coalesce', 'ˌkəʊəˈles', 'v.', '合并;联合;结合;', 'Cities, if unrestricted, tend to coalesce into bigger and bigger conurbations...', '如果不加限制，城市往往会联合发展成越来越大的集合城市。');
INSERT INTO `words` VALUES (1065, 'coalition', 'ˌkəʊəˈlɪʃn', 'n.', '(两党或多党)联合政府;(尤指多个政治团体的)联合体;联合;', 'Since June the country has had a coalition government...', '从6月份起，该国一直由一个联合政府执掌政权。');
INSERT INTO `words` VALUES (1066, 'coarse', 'kɔːs', 'adj.', '粗糙的;粗织的;粗的;', '...a jacket made of very coarse cloth.', '粗布制成的夹克');
INSERT INTO `words` VALUES (1067, 'coast', 'kəʊst', 'n.', '海岸;海滨;', 'Camp sites are usually situated along the coast, close to beaches.', '野营地一般都位于海滨，靠近沙滩。');
INSERT INTO `words` VALUES (1068, 'coat', 'kəʊt', 'n.', '外套;外衣;大衣;', 'He turned off the television, put on his coat and walked out.', '他关掉电视，穿上外套，出门了。');
INSERT INTO `words` VALUES (1069, 'coating', 'ˈkəʊtɪŋ', 'n.', '(薄的)覆盖层，涂层;', 'Under the coating of dust and cobwebs, he discovered a fine French Louis XVI clock.', '在一层灰尘和蜘蛛网底下，他发现了一座精美的法国路易十六时期的时钟。');
INSERT INTO `words` VALUES (1070, 'coax', 'kəʊks', 'v.', '哄劝;劝诱;', 'After lunch, she watched, listened and coaxed Bobby into talking about himself...', '吃过午饭，她照看着博比，听他说话，哄他说说自己的事。');
INSERT INTO `words` VALUES (1071, 'cock', 'kɒk', 'n.', '公鸡;雄鸡;雄禽;', 'The cock was announcing the start of a new day.', '雄鸡正在报晓。');
INSERT INTO `words` VALUES (1072, 'cocktail', 'ˈkɒkteɪl', 'n.', '鸡尾酒;凉菜;冷盘;', 'On arrival, guests are offered wine or a champagne cocktail...', '每位宾客一到场都会有人送上葡萄酒或香槟鸡尾酒。');
INSERT INTO `words` VALUES (1073, 'cocoa', 'ˈkəʊkəʊ', 'n.', '可可粉;热可可(饮料);一杯热可可饮料;', 'The Ivory Coast became the world\'s leading cocoa producer.', '象牙海岸成为世界上可可粉的主要产地。');
INSERT INTO `words` VALUES (1074, 'code', 'kəʊd', 'n.', '密码;暗码;电码;', '...Article 159 of the Turkish Penal Code.', '《土耳其刑法法典》第159条');
INSERT INTO `words` VALUES (1075, 'coding', 'ˈkəʊdɪŋ', 'n.', '(使物体易于辨认等的)编码方法，编码系统;', '...a colour coding that will ensure easy reference for potential users.', '确保潜在用户查阅方便的颜色编码');
INSERT INTO `words` VALUES (1076, 'coerce', 'kəʊˈɜːs', 'v.', '强迫;胁迫;迫使;', 'Potter had argued that the government coerced him into pleading guilty...', '波特辩称是政府强迫他认罪的。');
INSERT INTO `words` VALUES (1077, 'coexist', 'ˌkəʊɪɡˈzɪst', 'v.', '共存;(尤指)和平共处;', 'Pockets of affluence coexist with poverty...', '少数富裕地区与贫困地区并存。');
INSERT INTO `words` VALUES (1078, 'coexistence', 'ˌkəʊɪɡˈzɪstəns', 'n.', '共处;共存;', 'He also believed in coexistence with the West.', '他还相信他们能与西方国家共存。');
INSERT INTO `words` VALUES (1079, 'coffee', 'ˈkɒfi', 'n.', '(烘烤过的)咖啡豆;咖啡粉;咖啡(热饮料);', 'Would you like some coffee?...', '要来点咖啡吗？');
INSERT INTO `words` VALUES (1080, 'cognitive', 'ˈkɒɡnətɪv', 'adj.', '认知的;感知的;认识的;', 'As children grow older, their cognitive processes become sharper.', '孩子们越长越大，他们的认知过程变得更为敏锐。');
INSERT INTO `words` VALUES (1081, 'coherent', 'kəʊˈhɪərənt', 'adj.', '合乎逻辑的;有条理的;清楚易懂的;', 'He has failed to work out a coherent strategy for modernising the service...', '他未能制定出一条连贯的策略来实现服务的现代化。');
INSERT INTO `words` VALUES (1082, 'cohesive', 'kəʊˈhiːsɪv', 'adj.', '结成一个整体的;使结合的;使凝结的;', '\'Daring Adventures\' from \'86 is a far more cohesive and successful album...', '1986年发行的《勇于冒险》是一张风格更为统一也更为成功的专辑。');
INSERT INTO `words` VALUES (1083, 'coil', 'kɔɪl', 'v.', '(使)缠绕，盘绕;', 'Tod shook his head angrily and slung the coil of rope over his shoulder...', '托德气愤地摇了摇头，然后把那卷绳子往肩上一搭。');
INSERT INTO `words` VALUES (1084, 'coin', 'kɔɪn', 'n.', '(一枚)硬币;金属货币;(统称)硬币;', '...50 pence coins.', '50便士的硬币');
INSERT INTO `words` VALUES (1085, 'coincide', 'ˌkəʊɪnˈsaɪd', 'v.', '同时发生;相同;相符;', 'The exhibition coincides with the 50th anniversary of his death...', '展览恰好在他逝世50周年之际举行。');
INSERT INTO `words` VALUES (1086, 'coincidence', 'kəʊˈɪnsɪdəns', 'n.', '(令人吃惊的)巧合，巧事;同时存在;并存;', 'Mr. Berry said the timing was a coincidence and that his decision was unrelated to Mr. Roman\'s departure...', '贝里先生说这是时间上的巧合，他的决定与罗曼先生的离开没有关系。');
INSERT INTO `words` VALUES (1087, 'coke', 'kəʊk', 'n.', '焦炭;', '...a coke-burning stove.', '烧焦炭的炉子');
INSERT INTO `words` VALUES (1088, 'cold', 'kəʊld', 'adj.', '寒冷的;冷的;未热过的;', 'Rinse the vegetables under cold running water...', '用凉的自来水清洗这些蔬菜。');
INSERT INTO `words` VALUES (1089, 'coldly', 'ˈkəʊldli', 'adv.', '冷淡地;冷漠地;不友好地;', 'Rinse the vegetables under cold running water...', '用凉的自来水清洗这些蔬菜。');
INSERT INTO `words` VALUES (1090, 'collaborate', 'kəˈlæbəreɪt', 'v.', '合作;协作;通敌;', 'He collaborated with his son Michael on the English translation of a text on food production...', '他和儿子迈克尔合作，把一篇介绍食品生产的文章译成了英文。');
INSERT INTO `words` VALUES (1091, 'collaboration', 'kəˌlæbəˈreɪʃn', 'n.', '合作;协作;合作成果(或作品);', 'There is substantial collaboration with neighbouring departments...', '与相邻的一些部门有大量的合作。');
INSERT INTO `words` VALUES (1092, 'collaborative', 'kəˈlæbərətɪv', 'adj.', '合作的;协作的;协力的;', '...a collaborative research project...', '协作研究项目');
INSERT INTO `words` VALUES (1093, 'collagen', 'ˈkɒlədʒən', 'n.', '胶原蛋白，胶原(存在于皮肤和骨骼的蛋白质，可通过注射进行美容等);', 'The collagen that is included in face creams comes from animal skin.', '面霜中的胶原蛋白取自动物皮层。');
INSERT INTO `words` VALUES (1094, 'collapse', 'kəˈlæps', 'v.', '(突然)倒塌，坍塌;(尤指因病重而)倒下，昏倒，晕倒;(尤指工作劳累后)坐下，躺下放松;', 'A section of the Bay Bridge had collapsed...', '海湾大桥有一段垮塌了。');
INSERT INTO `words` VALUES (1095, 'collar', 'ˈkɒlə(r)', 'n.', '衣领;领子;(动物，尤指狗的)颈圈;', 'His tie was pulled loose and his collar hung open.', '他的领带扯松了，领口也敞着。');
INSERT INTO `words` VALUES (1096, 'collateral', 'kəˈlætərəl', 'n.', '抵押物;担保品;', 'Many people use personal assets as collateral for small business loans...', '很多人把个人财产用作小额商业贷款的抵押品。');
INSERT INTO `words` VALUES (1097, 'colleague', 'ˈkɒliːɡ', 'n.', '同事;同僚;同人;', 'Without consulting his colleagues he flew from Lisbon to Split...', '没跟同事们商量，他就从里斯本飞到了斯普利特。');
INSERT INTO `words` VALUES (1098, 'collect', 'kəˈlekt', 'v.', '收集;采集;收藏;', 'Two young girls were collecting firewood...', '两个小女孩正在拾柴火。');
INSERT INTO `words` VALUES (1099, 'collection', 'kəˈlekʃn', 'n.', '(常指同类的)收集物，收藏品;一批物品;一群人;', 'Robert\'s collection of prints and paintings has been bought over the years...', '这些年来，罗伯特逐渐收藏了一批版画和绘画作品。');
INSERT INTO `words` VALUES (1100, 'collective', 'kəˈlektɪv', 'adj.', '集体的;共有的;共同的;', 'It was a collective decision...', '这是集体的决定。');
INSERT INTO `words` VALUES (1101, 'collectively', 'kɒˈlɛktɪvli', 'adv.', '集体地；共同地；总起来说;', 'It was a collective decision...', '这是集体的决定。');
INSERT INTO `words` VALUES (1102, 'college', 'ˈkɒlɪdʒ', 'n.', '(英国)学院，职业学校，技术学校;(美国)大学;高等专科学校;', 'Their daughter Joanna is doing business studies at a local college...', '他们的女儿乔安娜在当地的一所学院念商科。');
INSERT INTO `words` VALUES (1103, 'collide', 'kəˈlaɪd', 'v.', '碰撞;相撞;严重不一致;', 'Two trains collided head-on in north-eastern Germany early this morning...', '今天早上德国东北部两列火车迎面相撞。');
INSERT INTO `words` VALUES (1104, 'collision', 'kəˈlɪʒn', 'n.', '碰撞(或相撞)事故;(两人之间或对立意见、看法等之间的)冲突，抵触;', 'They were on their way to the Shropshire Union Canal when their van was involved in a collision with a car...', '在去往什罗普郡联盟运河的路上，他们的小货车与一辆小车相撞。');
INSERT INTO `words` VALUES (1105, 'colonel', 'ˈkɜːnl', 'n.', '(陆军、海军陆战队或美国空军)上校;', 'This particular place was run by an ex-Army colonel.', '这个地方以前由一位前陆军上校经管。');
INSERT INTO `words` VALUES (1106, 'colonial', 'kəˈləʊniəl', 'adj.', '殖民的;殖民国家的;(美国)具有英属殖民地时期特色的，英属殖民地时期的;', '...the 31st anniversary of Jamaica\'s independence from British colonial rule.', '牙买加脱离英国殖民统治独立31周年纪念');
INSERT INTO `words` VALUES (1107, 'colonist', 'ˈkɒlənɪst', 'n.', '殖民地定居者;', 'The apple was brought over here by the colonists when they came.', '苹果是早期殖民地开拓者来的时候带过来的。');
INSERT INTO `words` VALUES (1108, 'colony', 'ˈkɒləni', 'n.', '殖民地;殖民地定居者群体;(来自同一地方，职业或兴趣相同的)聚居人群;', 'In France\'s former North African colonies, anti-French feeling is growing...', '在北非的一些前法属殖民地国家，反法情绪日益增长。');
INSERT INTO `words` VALUES (1109, 'colossal', 'kəˈlɒsl', 'adj.', '巨大的;庞大的;', 'There has been a colossal waste of public money...', '一直存在巨大的公款浪费。');
INSERT INTO `words` VALUES (1110, 'column', 'ˈkɒləm', 'n.', '柱;(通常为)圆形石柱;纪念柱;', '...a London landmark, Nelson\'s Column in Trafalgar Square.', '伦敦的标志性建筑，特拉法尔加广场上的纳尔逊将军纪念柱');
INSERT INTO `words` VALUES (1111, 'combination', 'ˌkɒmbɪˈneɪʃn', 'n.', '结合体;联合体;混合体;', '...a fantastic combination of colours.', '多种颜色的绝妙混合');
INSERT INTO `words` VALUES (1112, 'combustible', 'kəmˈbʌstəbl', 'adj.', '易燃的;可燃的;', 'The ability of coal to release a combustible gas has long been known.', '人们早已知道煤能够释放一种易燃气体。');
INSERT INTO `words` VALUES (1113, 'comedy', 'ˈkɒmədi', 'n.', '喜剧;喜剧片;滑稽;', 'Actor Dom Deluise talks about his career in comedy.', '演员多姆·德卢西谈论自己的喜剧生涯。');
INSERT INTO `words` VALUES (1114, 'comet', 'ˈkɒmɪt', 'n.', '彗星;扫帚星;', 'Halley\'s Comet is going to come back in 2061.', '哈雷彗星将于2061年回归。');
INSERT INTO `words` VALUES (1115, 'comfort', 'ˈkʌmfət', 'n.', '舒服;安逸;舒适;', 'This will enable the audience to sit in comfort while watching the shows...', '这样能够让观众舒舒服服地坐着看表演。');
INSERT INTO `words` VALUES (1116, 'comfortable', 'ˈkʌmftəbl', 'adj.', '使人舒服的;舒适的;愉快放松的;', '...a comfortable fireside chair...', '舒适的炉边座椅');
INSERT INTO `words` VALUES (1117, 'comic', 'ˈkɒmɪk', 'adj.', '滑稽的;使人发笑的;喜剧的;', 'The novel is comic and tragic...', '这部小说令人笑中带泪。');
INSERT INTO `words` VALUES (1118, 'command', 'kəˈmɑːnd', 'n.', '(给人或动物的)命令;指令;命令;', 'He commanded his troops to attack...', '他命令部队发起进攻。');
INSERT INTO `words` VALUES (1119, 'commander', 'kəˈmɑːndə(r)', 'n.', '负责人;(尤指)司令官，指挥官;(英国或美国)海军中校;', 'The commander and some of the men had been released.', '指挥官和一些士兵已经获释。');
INSERT INTO `words` VALUES (1120, 'commemorate', 'kəˈmeməreɪt', 'v.', '(用…)纪念;作为…的纪念;', 'One room contained a gallery of paintings commemorating great moments in baseball history...', '有一个房间里陈列着很多纪念棒球史上伟大时刻的画作。');
INSERT INTO `words` VALUES (1121, 'commence', 'kəˈmens', 'v.', '开始发生;开始;着手;', 'The academic year commences at the beginning of October...', '学年于10月初开始。');
INSERT INTO `words` VALUES (1122, 'commend', 'kəˈmend', 'v.', '(尤指公开地)赞扬，称赞，表扬;推荐;举荐;', 'I commended her for that action...', '我赞扬了她的那一举动。');
INSERT INTO `words` VALUES (1123, 'comment', 'ˈkɒment', 'n.', '议论;评论;解释;', 'So far, Mr Cook has not commented on these reports...', '到目前为止，库克先生仍未就这些报道发表评论。');
INSERT INTO `words` VALUES (1124, 'commentary', 'ˈkɒməntri', 'n.', '(尤指电台或电视台所作的)实况报道，现场解说;注释;解释;', 'He gave the listening crowd a running commentary...', '他给这一大群听众进行现场报道。');
INSERT INTO `words` VALUES (1125, 'commentator', 'ˈkɒmənteɪtə(r)', 'n.', '(电台、电视台或报刊的)评论员;(尤指电视台或电台的)现场解说员，实况播音员;', '...a sports commentator.', '体育解说员');
INSERT INTO `words` VALUES (1126, 'commerce', 'ˈkɒmɜːs', 'n.', '(尤指国际间的)贸易;商业;商务;', 'They have made their fortunes from industry and commerce.', '他们靠工商业发了财。');
INSERT INTO `words` VALUES (1127, 'commercial', 'kəˈmɜːʃl', 'adj.', '贸易的;商业的;赢利的;', 'Docklands in its heyday was a major centre of industrial and commercial activity...', '港区在其最繁荣的时期曾是主要的工商业活动中心。');
INSERT INTO `words` VALUES (1128, 'commission', 'kəˈmɪʃn', 'n.', '(通常为政府管控或调查某事的)委员会;佣金;回扣;', 'The Ministry of Agriculture commissioned a study into low-input farming...', '农业部委托对低投入耕作进行研究。');
INSERT INTO `words` VALUES (1129, 'commissioner', 'kəˈmɪʃənə(r)', 'n.', '(委员会的)委员，专员，特派员;警察局长;警长;', '...the European Commissioner for External Affairs.', '欧洲委员会外事专员');
INSERT INTO `words` VALUES (1130, 'commit', 'kəˈmɪt', 'v.', '做出(错或非法的事);犯(罪或错等);自杀;', 'I have never committed any crime...', '我从来没犯过罪。');
INSERT INTO `words` VALUES (1131, 'commitment', 'kəˈmɪtmənt', 'n.', '承诺;许诺;允诺承担;', '...commitment to the ideals of Bolshevism.', '对布尔什维克主义理想的信奉');
INSERT INTO `words` VALUES (1132, 'committee', 'kəˈmɪti', 'n.', '委员会;', '...a committee of ministers.', '部长委员会');
INSERT INTO `words` VALUES (1133, 'commodity', 'kəˈmɒdəti', 'n.', '商品;有用的东西;有使用价值的事物;', 'The government increased prices on several basic commodities like bread and meat.', '政府提高了面包、肉类等几种基本商品的价格。');
INSERT INTO `words` VALUES (1134, 'common', 'ˈkɒmən', 'adj.', '常见的;通常的;普遍的;', 'His name was Hansen, a common name in Norway...', '他的名字叫汉森，在挪威是一个常见的名字。');
INSERT INTO `words` VALUES (1135, 'commonly', 'ˈkɒmənli', 'adv.', '通常;常常;大多数人地;', 'His name was Hansen, a common name in Norway...', '他的名字叫汉森，在挪威是一个常见的名字。');
INSERT INTO `words` VALUES (1136, 'commonplace', 'ˈkɒmənpleɪs', 'adj.', '平凡的;普通的;普遍的;', 'Foreign vacations have become commonplace...', '去国外度假已经变得很平常了。');
INSERT INTO `words` VALUES (1137, 'commotion', 'kəˈməʊʃn', 'n.', '(突然发生的)喧闹，骚乱，骚动;', 'He heard a commotion outside...', '他听见外面一阵骚动。');
INSERT INTO `words` VALUES (1138, 'communicate', 'kəˈmjuːnɪkeɪt', 'v.', '(与某人)交流(信息或消息、意见等);沟通;传达，传递(想法、感情、思想等);', 'My natural mother has never communicated with me...', '我的亲生母亲从未和我联系过。');
INSERT INTO `words` VALUES (1139, 'communication', 'kəˌmjuːnɪˈkeɪʃn', 'n.', '表达;交流;交际;', '...a communications satellite.', '通讯卫星');
INSERT INTO `words` VALUES (1140, 'communism', 'ˈkɒmjunɪzəm', 'n.', '共产主义;共产主义制度;', '...the ultimate triumph of communism in the world.', '共产主义在世界上最终的胜利');
INSERT INTO `words` VALUES (1141, 'community', 'kəˈmjuːnəti', 'n.', '社区;社会;团体;', 'He\'s well liked by people in the community...', '社区的人都非常喜欢他。');
INSERT INTO `words` VALUES (1142, 'commute', 'kəˈmjuːt', 'v.', '(乘公共汽车、火车、汽车等)上下班往返，经常往返(于两地);减刑;代偿;', 'Mike commutes to London every day...', '迈克每天都去伦敦上班。');
INSERT INTO `words` VALUES (1143, 'commuter', 'kəˈmjuːtə(r)', 'n.', '(远距离)上下班往返的人;', 'Mike commutes to London every day...', '迈克每天都去伦敦上班。');
INSERT INTO `words` VALUES (1144, 'companion', 'kəmˈpæniən', 'n.', '旅伴;伴侣;陪伴;', 'Fred had been her constant companion for the last six years of her life...', '在她生命的最后6年，弗莱德一直是她忠实的伴侣。');
INSERT INTO `words` VALUES (1145, 'company', 'ˈkʌmpəni', 'n.', '公司;商号;商行;', 'Sheila found some work as a secretary in an insurance company.', '希拉在一家保险公司找到一份当秘书的工作。');
INSERT INTO `words` VALUES (1146, 'comparable', 'ˈkɒmpərəbl', 'adj.', '类似的;可比较的;', '...paying the same wages to men and women for work of comparable value...', '男女同工同酬');
INSERT INTO `words` VALUES (1147, 'comparative', 'kəmˈpærətɪv', 'adj.', '比较的;相比的;比较而言的;', '...those who manage to reach the comparative safety of Fendel...', '达到“芬司匹利”药品相对安全标准的那些药');
INSERT INTO `words` VALUES (1148, 'comparatively', 'kəmˈpærətɪvli', 'adv.', '比较上;相对地;', '...those who manage to reach the comparative safety of Fendel...', '达到“芬司匹利”药品相对安全标准的那些药');
INSERT INTO `words` VALUES (1149, 'compare', 'kəmˈpeə(r)', 'v.', '比较;对比;与…类似(或相似);', 'Compare the two illustrations in Fig 60...', '比较图60中的两幅插图。');
INSERT INTO `words` VALUES (1150, 'comparison', 'kəmˈpærɪsn', 'n.', '比较;对比;相比;', '...a comparison of the British and German economies...', '英国经济与德国经济的比较');
INSERT INTO `words` VALUES (1151, 'compartment', 'kəmˈpɑːtmənt', 'n.', '(铁路客车车厢分隔成的)隔间;(家具或设备等的)分隔间，隔层;', 'On the way home we shared our first class compartment with a group of businessmen.', '回家的路上，我们和一群生意人一起坐在一等车厢。');
INSERT INTO `words` VALUES (1152, 'compass', 'ˈkʌmpəs', 'n.', '罗盘;罗经;指南针;', 'We had to rely on a compass and a lot of luck to get here.', '我们不得不依靠指南针和不错的运气找到这儿来。');
INSERT INTO `words` VALUES (1153, 'compassion', 'kəmˈpæʃn', 'n.', '同情;怜悯;', 'Elderly people need time and compassion from their physicians.', '老年人需要医生多花时间，还需要他们的关爱。');
INSERT INTO `words` VALUES (1154, 'compatibility', 'kəmˌpætəˈbɪləti', 'n.', '和睦相处;并存;相容;', 'Free enterprise, he argued, was compatible with Russian values and traditions...', '他认为自由企业制并不违背俄罗斯的价值观和传统。');
INSERT INTO `words` VALUES (1155, 'compatible', 'kəmˈpætəbl', 'adj.', '可共用的;兼容的;可共存的;', 'Free enterprise, he argued, was compatible with Russian values and traditions...', '他认为自由企业制并不违背俄罗斯的价值观和传统。');
INSERT INTO `words` VALUES (1156, 'compel', 'kəmˈpel', 'v.', '强迫;迫使;使必须;', '...the introduction of legislation to compel cyclists to wear a helmet...', '强制骑自行车者戴头盔的法律的出台');
INSERT INTO `words` VALUES (1157, 'compensate', 'ˈkɒmpenseɪt', 'v.', '补偿;弥补;给(某人)赔偿(或赔款);', 'The official promise to compensate people for the price rise clearly hadn\'t been worked out properly...', '显然，官方对物价上涨进行补偿的承诺没有真正兑现。');
INSERT INTO `words` VALUES (1158, 'compensation', 'ˌkɒmpenˈseɪʃn', 'n.', '补偿(或赔偿)物;(尤指)赔偿金，补偿金;赔偿;', 'He received one year\'s salary as compensation for loss of office...', '他得到一年的工资作为失业补偿金。');
INSERT INTO `words` VALUES (1159, 'compete', 'kəmˈpiːt', 'v.', '竞争;对抗;参加比赛(或竞赛);', 'The banks have long competed with American Express\'s charge cards and various store cards...', '长久以来，这些银行一直在与美国运通公司的信用卡以及各式各样的商场赊购卡竞争。');
INSERT INTO `words` VALUES (1160, 'competence', 'ˈkɒmpɪtəns', 'n.', '能力;胜任;(法庭、机构或人的)权限，管辖权;', 'His competence as an economist had been reinforced by his successful fight against inflation...', '成功地扼制制通货膨胀的经历加强了他作为经济学家的才能。');
INSERT INTO `words` VALUES (1161, 'competent', 'ˈkɒmpɪtənt', 'adj.', '足以胜任的;有能力的;称职的;', 'He was a loyal, distinguished and very competent civil servant.', '他是一位敬业、优秀而且非常称职的公务员。');
INSERT INTO `words` VALUES (1162, 'competition', 'ˌkɒmpəˈtɪʃn', 'n.', '竞争;角逐;比赛;', 'There\'s been some fierce competition for the title...', '夺冠之争一直都相当激烈。');
INSERT INTO `words` VALUES (1163, 'competitive', 'kəmˈpetətɪv', 'adj.', '竞争的;(与…)一样好的;(比…)更好的;', 'Only by keeping down costs will America maintain its competitive advantage over other countries...', '只有通过控制成本，美国才能保持对其他国家的竞争优势。');
INSERT INTO `words` VALUES (1164, 'competitor', 'kəmˈpetɪtə(r)', 'n.', '(尤指商业方面的)竞争者，对手;参赛者;竞赛者;', 'The bank isn\'t performing as well as some of its competitors.', '这家银行表现得不如几家竞争对手出色。');
INSERT INTO `words` VALUES (1165, 'compilation', 'ˌkɒmpɪˈleɪʃn', 'n.', '收集;选编;选辑;', 'His latest album release is a compilation of his jazz works over the past decade.', '最近发行的专辑是他过去10年来的爵士作品合集。');
INSERT INTO `words` VALUES (1166, 'compile', 'kəmˈpaɪl', 'v.', '编写(书、列表、报告等);编纂;编译;', 'Councils were required to compile a register of all adults living in their areas...', '各地政务委员会被要求编制一份该地区成年居民的名册。');
INSERT INTO `words` VALUES (1167, 'compiler', 'kəmˈpaɪlə(r)', 'n.', '编纂者;汇编者;编著者;', '...the compilers of dictionaries and grammars.', '词典和语法书的编纂者');
INSERT INTO `words` VALUES (1168, 'complain', 'kəmˈpleɪn', 'v.', '抱怨;埋怨;发牢骚;', 'Miners have complained bitterly that the government did not fulfill their promises...', '矿工们愤愤不平地抱怨说政府没有履行诺言。');
INSERT INTO `words` VALUES (1169, 'complaint', 'kəmˈpleɪnt', 'n.', '不满的原因;抱怨;埋怨;', 'There\'s been a record number of complaints about the standard of service on Britain\'s railways...', '有关英国铁路服务水平的投诉空前的多。');
INSERT INTO `words` VALUES (1170, 'complete', 'kəmˈpliːt', 'adj.', '(用以强调)完全的，彻底的;全部的;完整的;', 'The rebels had taken complete control...', '叛乱分子完全控制了局面。');
INSERT INTO `words` VALUES (1171, 'completely', 'kəmˈpliːtli', 'adv.', '彻底地;完全地;完整地;', 'The rebels had taken complete control...', '叛乱分子完全控制了局面。');
INSERT INTO `words` VALUES (1172, 'completion', 'kəmˈpliːʃn', 'n.', '完成;结束;(房地产等的)完成交易，完成交割;', 'The rebels had taken complete control...', '叛乱分子完全控制了局面。');
INSERT INTO `words` VALUES (1173, 'complexion', 'kəmˈplekʃn', 'n.', '面色;肤色;气色;', 'She had short brown hair and a pale complexion.', '她留着褐色短发，皮肤白皙。');
INSERT INTO `words` VALUES (1174, 'complexity', 'kəmˈpleksəti', 'n.', '复杂性;难懂;难题;', '...a diplomatic tangle of great complexity.', '非常复杂的外事纠纷');
INSERT INTO `words` VALUES (1175, 'compliance', 'kəmˈplaɪəns', 'n.', '服从;顺从;遵从;', 'Inspectors were sent to visit nuclear sites and verify compliance with the treaty...', '监督员被派去视察核武器基地并核实条约的遵守情况。');
INSERT INTO `words` VALUES (1176, 'complicate', 'ˈkɒmplɪkeɪt', 'v.', '使复杂化;', 'What complicates the issue is the burden of history...', '历史的重负使问题复杂化了。');
INSERT INTO `words` VALUES (1177, 'complicated', 'ˈkɒmplɪkeɪtɪd', 'adj.', '复杂的;难懂的;', 'The situation in Lebanon is very complicated.', '黎巴嫩的情况十分复杂。');
INSERT INTO `words` VALUES (1178, 'complication', 'ˌkɒmplɪˈkeɪʃn', 'n.', '使更复杂化(或更困难)的事物;并发症;', 'The age difference was a complication to the relationship...', '年龄差异使得双方交往更加困难。');
INSERT INTO `words` VALUES (1179, 'comply', 'kəmˈplaɪ', 'v.', '遵从;服从;顺从;', 'The commander said that the army would comply with the ceasefire...', '指挥官说军队会遵守停火协议。');
INSERT INTO `words` VALUES (1180, 'component', 'kəmˈpəʊnənt', 'n.', '组成部分;成分;部件;', 'Enriched uranium is a key component of a nuclear weapon...', '浓缩铀是核武器的关键组成部分。');
INSERT INTO `words` VALUES (1181, 'compose', 'kəmˈpəʊz', 'v.', '组成，构成(一个整体);作曲;创作(音乐);', 'The force would be composed of troops from NATO countries...', '这支军队将由来自北约国家的部队组成。');
INSERT INTO `words` VALUES (1182, 'composing', 'kəmˈpəʊzɪŋ', 'v.', '组成，构成(一个整体);作曲;创作(音乐);', 'The force would be composed of troops from NATO countries...', '这支军队将由来自北约国家的部队组成。');
INSERT INTO `words` VALUES (1183, 'composite', 'ˈkɒmpəzɪt', 'adj.', '合成的;混成的;复合的;', 'Galton devised a method of creating composite pictures in which the features of different faces were superimposed over one another.', '高尔顿发明了一种把不同人的五官相互叠加在一起的制作合成照片的方法。');
INSERT INTO `words` VALUES (1184, 'composition', 'ˌkɒmpəˈzɪʃn', 'n.', '成分;构成;组合方式;', 'Television has transformed the size and social composition of the audience at great sporting occasions...', '电视改变了重大体育赛事观众的规模和其社会阶层构成结构。');
INSERT INTO `words` VALUES (1185, 'comprehend', 'ˌkɒmprɪˈhend', 'v.', '理解;领悟;懂;', 'I just cannot comprehend your attitude...', '我就是无法理解你的态度。');
INSERT INTO `words` VALUES (1186, 'comprehension', 'ˌkɒmprɪˈhenʃn', 'n.', '理解力;领悟能力;(语言学习中的)理解练习(或训练);', 'This was utterly beyond her comprehension.', '这让她完全无法理解。');
INSERT INTO `words` VALUES (1187, 'comprehensive', 'ˌkɒmprɪˈhensɪv', 'adj.', '全部的;所有的;(几乎)无所不包的;', 'The Rough Guide to Nepal is a comprehensive guide to the region.', '《尼泊尔概况》是一本全面介绍该地区的旅游指南。');
INSERT INTO `words` VALUES (1188, 'comprise', 'kəmˈpraɪz', 'v.', '包括;包含;由…组成;', 'The special cabinet committee comprises Mr Brown, Mr Mandelson, and Mr Straw...', '特别内阁委员会包括布朗先生、曼德尔森先生和斯特劳先生。');
INSERT INTO `words` VALUES (1189, 'compromise', 'ˈkɒmprəmaɪz', 'n.', '妥协;折中;互让;', 'Encourage your child to reach a compromise between what he wants and what you want...', '鼓励孩子在他的愿望和你的愿望之间找到一个折中点。');
INSERT INTO `words` VALUES (1190, 'compulsory', 'kəmˈpʌlsəri', 'adj.', '(因法律或规则而)必须做的，强制的，强迫的;', 'In East Germany learning Russian was compulsory...', '在东德，俄语是必修课。');
INSERT INTO `words` VALUES (1191, 'computation', 'ˌkɒmpjuˈteɪʃn', 'n.', '计算;计算过程;', 'The discrepancies resulted from different methods of computation...', '这些差异是由不同的计算方法造成的。');
INSERT INTO `words` VALUES (1192, 'compute', 'kəmˈpjuːt', 'v.', '计算;估算;', 'I tried to compute the cash value of the ponies and horse boxes.', '我试着算了算马驹和运马拖车的现金价值。');
INSERT INTO `words` VALUES (1193, 'computer', 'kəmˈpjuːtə(r)', 'n.', '计算机;电脑;', 'The data are then fed into a computer...', '数据随后被输入电脑。');
INSERT INTO `words` VALUES (1194, 'comrade', 'ˈkɒmreɪd', 'n.', '(共产党或社会主义政党的)同志;朋友;同事;', 'Unlike so many of his comrades he survived the war.', '和许多战友不同，他在战争中幸存下来。');
INSERT INTO `words` VALUES (1195, 'conceal', 'kənˈsiːl', 'v.', '隐藏;隐瞒;掩盖;', 'Frances decided to conceal the machine behind a hinged panel...', '弗朗西斯决定把机器藏在带铰链的嵌板的后面。');
INSERT INTO `words` VALUES (1196, 'concede', 'kənˈsiːd', 'v.', '承认(某事属实、合乎逻辑等);(尤指勉强地)让与，让步;允许;', 'Bess finally conceded that Nancy was right...', '贝丝最终承认了南希是对的。');
INSERT INTO `words` VALUES (1197, 'conceive', 'kənˈsiːv', 'v.', '想出(主意、计划等);想象;构想;', 'I just can\'t even conceive of that quantity of money...', '那么多钱，我根本都无法想象。');
INSERT INTO `words` VALUES (1198, 'concentrate', 'ˈkɒnsntreɪt', 'v.', '集中(注意力);聚精会神;使…集中(或集合、聚集);', 'It was up to him to concentrate on his studies and make something of himself...', '是否能专心学习并取得一定成就要靠他自己。');
INSERT INTO `words` VALUES (1199, 'concentration', 'ˌkɒnsnˈtreɪʃn', 'n.', '专心;专注;关注;', 'Neal kept interrupting, breaking my concentration...', '尼尔不停地打断我，使得我无法集中注意力。');
INSERT INTO `words` VALUES (1200, 'concept', 'ˈkɒnsept', 'n.', '概念;观念;', 'She added that the concept of arranged marriages is misunderstood in the west.', '她补充说，西方人对包办婚姻的概念有些误解。');
INSERT INTO `words` VALUES (1201, 'conception', 'kənˈsepʃn', 'n.', '构思;构想;设想;', 'My conception of a garden was based on gardens I had visited in England...', '我对花园的概念是基于我在英格兰观赏过的花园。');
INSERT INTO `words` VALUES (1202, 'concern', 'kənˈsɜːn', 'v.', '影响，牵涉(某人);与…有关;涉及;', 'The group has expressed concern about reports of political violence in Africa...', '该团体对有关非洲政治暴力的报道表示关切。');
INSERT INTO `words` VALUES (1203, 'concerning', 'kənˈsɜːnɪŋ', 'prep.', '关于;涉及;', 'For more information concerning the club contact I. Coldwell.', '咨询俱乐部详情，请与I.科德维尔联系。');
INSERT INTO `words` VALUES (1204, 'concert', 'ˈkɒnsət', 'n.', '音乐会;演奏会;', '...a short concert of piano music...', '一场短暂的钢琴音乐会');
INSERT INTO `words` VALUES (1205, 'concerted', 'kənˈsɜːtɪd', 'adj.', '努力的;共同筹划决定的;同心协力的;', 'Martin Parry, author of the report, says it\'s time for concerted action by world leaders.', '该报道的作者马丁·帕里宣称是世界各国的首脑采取一致行动的时候了。');
INSERT INTO `words` VALUES (1206, 'concession', 'kənˈseʃn', 'n.', '让步;妥协;承认;', 'The King made major concessions to end the confrontation with his people.', '为了结束与其臣民的冲突，国王作出了很大的让步。');
INSERT INTO `words` VALUES (1207, 'concise', 'kənˈsaɪs', 'adj.', '简明的;简练的;简洁的;', 'Burton\'s text is concise and informative...', '伯顿的文章语言简洁，信息丰富。');
INSERT INTO `words` VALUES (1208, 'conclude', 'kənˈkluːd', 'v.', '断定;推断出;得出结论;', 'Larry had concluded that he had no choice but to accept Paul\'s words as the truth...', '拉里断定自己别无选择，只能当保罗的话是真的。');
INSERT INTO `words` VALUES (1209, 'conclusion', 'kənˈkluːʒn', 'n.', '结论;推论;结束;', 'Over the years I\'ve come to the conclusion that she\'s a very great musician...', '这些年来，我得出的结论是她是一位非常伟大的音乐家。');
INSERT INTO `words` VALUES (1210, 'conclusive', 'kənˈkluːsɪv', 'adj.', '结论性的;不容置疑的;确凿的;', 'Her attorneys claim there is no conclusive evidence that any murders took place...', '她的律师声称并无确凿证据表明发生了谋杀。');
INSERT INTO `words` VALUES (1211, 'concoct', 'kənˈkɒkt', 'v.', '调制，调合，配制(尤指食物或饮料);虚构，杜撰，编造(故事、借口等);', 'Mr Ferguson said the prisoner concocted the story to get a lighter sentence.', '弗格森先生说犯人捏造了这件事儿以求轻判。');
INSERT INTO `words` VALUES (1212, 'concordance', 'kənˈkɔːdəns', 'n.', '(书籍等中按字母顺序排列的)词语索引;(计算机显示的)语汇索引;相似;', '...a partial concordance between theoretical expectations and empirical evidence.', '理论预期与实验证据之间的部分一致');
INSERT INTO `words` VALUES (1213, 'concrete', 'ˈkɒŋkriːt', 'adj.', '混凝土制的;确实的，具体的(而非想象或猜测的);有形的;', 'The posts have to be set in concrete...', '这些柱子必须用混凝土固定。');
INSERT INTO `words` VALUES (1214, 'concurrent', 'kənˈkʌrənt', 'adj.', '并存的;同时发生的;', 'Galerie St. Etienne is holding three concurrent exhibitions...', '圣艾蒂安美术馆同时举办3场展览。');
INSERT INTO `words` VALUES (1215, 'condemn', 'kənˈdem', 'v.', '(通常因道义上的原因而)谴责，指责;宣判;判处(某人某种刑罚);', 'Political leaders united yesterday to condemn the latest wave of violence...', '政界领袖昨日联合起来对最近激增的暴力事件予以谴责。');
INSERT INTO `words` VALUES (1216, 'condense', 'kənˈdens', 'v.', '(由气体)冷凝;(使气体)凝结;(使)浓缩，变浓，变稠;', 'We have learnt how to condense serious messages into short, self-contained sentences...', '我们已经学会了如何把重要的信息压缩成简短独立的句子。');
INSERT INTO `words` VALUES (1217, 'condition', 'kənˈdɪʃn', 'n.', '状态;状况;健康状况;', 'He remains in a critical condition in a California hospital...', '他在加州的一家医院里，病情仍然十分危急。');
INSERT INTO `words` VALUES (1218, 'conditional', 'kənˈdɪʃənl', 'adj.', '附带条件的;依…而定的;条件的;', 'Their support is conditional on his proposals meeting their approval.', '他们的支持是以他的提案得到他们的批准为条件的。');
INSERT INTO `words` VALUES (1219, 'conditioning', 'kənˈdɪʃnɪŋ , kənˈdɪʃənɪŋ', 'n.', '训练;熏陶;条件作用;', 'He remains in a critical condition in a California hospital...', '他在加州的一家医院里，病情仍然十分危急。');
INSERT INTO `words` VALUES (1220, 'condolence', 'kənˈdəʊləns', 'n.', '吊唁;慰唁;', 'Neil sent him a letter of condolence.', '尼尔给他发了一封唁函。');
INSERT INTO `words` VALUES (1221, 'conducive', 'kənˈdjuːsɪv', 'adj.', '使容易(或有可能)发生的;', 'Make your bedroom as conducive to sleep as possible...', '尽量把卧室布置得适宜睡眠。');
INSERT INTO `words` VALUES (1222, 'conduction', 'kənˈdʌkʃn', 'n.', '(热或电等能量的)传导;', 'Temperature becomes uniform by heat conduction until finally a permanent state is reached.', '温度通过热传导变得一致，直到最终达到恒定状态。');
INSERT INTO `words` VALUES (1223, 'conductivity', 'ˌkɒndʌkˈtɪvəti', 'n.', '传导率（性，系数）；导电率（性，系数）;', 'Salt water is much more conductive than fresh water is.', '盐水比淡水的传导性强得多。');
INSERT INTO `words` VALUES (1224, 'confer', 'kənˈfɜː(r)', 'v.', '商讨;协商;交换意见;', 'He conferred with Hill and the others in his office...', '他与希尔及其他人在办公室里商议了一番。');
INSERT INTO `words` VALUES (1225, 'conference', 'ˈkɒnfərəns', 'n.', '(通常持续几天的大型正式)会议，研讨会;(正式)讨论会，商讨会;体育协会(或联合会);', 'The President summoned all the state governors to a conference on education.', '总统召集各州州长召开了一次教育研讨会。');
INSERT INTO `words` VALUES (1226, 'confess', 'kənˈfes', 'v.', '供认，坦白，承认(错误或罪行);承认(自己感到羞愧或尴尬的事);忏悔(罪过);', 'He had confessed to seventeen murders...', '他供认了17起谋杀案。');
INSERT INTO `words` VALUES (1227, 'confidence', 'ˈkɒnfɪdəns', 'n.', '信心;信任;信赖;', 'I have every confidence in you...', '我完全相信你。');
INSERT INTO `words` VALUES (1228, 'confident', 'ˈkɒnfɪdənt', 'adj.', '自信的;有自信心的;肯定的;', 'I am confident that everything will come out right in time...', '我坚信一切终究都会好起来。');
INSERT INTO `words` VALUES (1229, 'confidential', 'ˌkɒnfɪˈdenʃl', 'adj.', '机密的;保密的;秘密的;', 'She accused them of leaking confidential information about her private life...', '她指责他们泄露其私生活的秘密。');
INSERT INTO `words` VALUES (1230, 'configuration', 'kənˌfɪɡəˈreɪʃn', 'n.', '布局;结构;构造;', '...Stonehenge, in south-western England, an ancient configuration of giant stones.', '英格兰西南部的巨石阵——一座古老的巨石阵列');
INSERT INTO `words` VALUES (1231, 'configure', 'kənˈfɪɡə(r)', 'v.', '(按特定方式)安置;(尤指对计算机设备进行)配置;对(设备或软件进行)设定;', 'How easy was it to configure the software?', '配置这个软件容易吗？');
INSERT INTO `words` VALUES (1232, 'confirm', 'kənˈfɜːm', 'v.', '(尤指提供证据来)证实，证明;使感觉更强烈;使确信;', 'X-rays have confirmed that he has not broken any bones...', 'X光片证实他没有骨折。');
INSERT INTO `words` VALUES (1233, 'confirmation', 'ˌkɒnfəˈmeɪʃn', 'n.', '证实;确认书;证明书;', 'X-rays have confirmed that he has not broken any bones...', 'X光片证实他没有骨折。');
INSERT INTO `words` VALUES (1234, 'conform', 'kənˈfɔːm', 'v.', '顺从，顺应(大多数人或社会);随潮流;遵守，遵从，服从(规则、法律等);', 'The Night Rider lamp has been designed to conform to new British Standard safety requirements...', '“夜行者”灯的设计符合新的英国安全标准的要求。');
INSERT INTO `words` VALUES (1235, 'confront', 'kənˈfrʌnt', 'v.', '使…无法回避;降临于;处理，解决(问题或困境);', 'She was confronted with severe money problems...', '她面临严峻的资金问题。');
INSERT INTO `words` VALUES (1236, 'confuse', 'kənˈfjuːz', 'v.', '使糊涂;使迷惑;(将…)混淆，混同;', 'Great care is taken to avoid confusing the two types of projects...', '大家非常谨慎，以免把这两类项目弄混。');
INSERT INTO `words` VALUES (1237, 'confusion', 'kənˈfjuːʒn', 'n.', '不确定;困惑;混淆;', 'There\'s still confusion about the number of casualties...', '伤亡人数依然不明。');
INSERT INTO `words` VALUES (1238, 'congestion', 'kənˈdʒestʃən', 'n.', '(交通)拥塞;塞车;充血;', 'The problems of traffic congestion will not disappear in a hurry...', '交通拥堵问题不会一下子就消失。');
INSERT INTO `words` VALUES (1239, 'conglomerate', 'kənˈɡlɒmərət', 'n.', '联合大公司;企业集团;合成物;', 'Fiat is Italy\'s largest industrial conglomerate.', '菲亚特是意大利最大的产业集团。');
INSERT INTO `words` VALUES (1240, 'congratulate', 'kənˈɡrætʃuleɪt', 'v.', '向(某人)道贺;祝贺;(为成就或成功)感到高兴，感到自豪;', 'She congratulated him on the birth of his son...', '她祝贺他喜得贵子。');
INSERT INTO `words` VALUES (1241, 'congratulation', 'kənˌɡrætʃuˈleɪʃn', 'n.', '祝贺;恭贺;贺词;', 'She congratulated him on the birth of his son...', '她祝贺他喜得贵子。');
INSERT INTO `words` VALUES (1242, 'congress', 'ˈkɒŋɡres', 'n.', '代表大会;(美国及其他一些国家的)国会，议会;(用于某些国家的政党名称)国民大会;', 'A lot has changed after the party congress.', '党代会后发生了很多变化。');
INSERT INTO `words` VALUES (1243, 'congruent', 'ˈkɒŋɡruənt', 'adj.', '全等的;叠合的;适合的;', 'They want to work in an organisation whose values are congruent with their own.', '他们想在一个符合自己价值观的机构里工作。');
INSERT INTO `words` VALUES (1244, 'conjunction', 'kənˈdʒʌŋkʃn', 'n.', '连词， 连接词 (如and、but、or);(引起某种结果的事物等的)结合，同时发生;(恒星、行星等的)合;', '...the conjunction of two events.', '两个事件的同时发生');
INSERT INTO `words` VALUES (1245, 'connect', 'kəˈnekt', 'v.', '(使)连接;联结;使…连接;', 'You can connect the machine to your hi-fi...', '你可以把机器连在高保真音响上。');
INSERT INTO `words` VALUES (1246, 'connected', 'kəˈnektɪd', 'adj.', '有关的;有联系的;', 'Have you ever had any skin problems connected with exposure to the sun?...', '你曾因日晒出现过皮肤问题吗？');
INSERT INTO `words` VALUES (1247, 'conquer', 'ˈkɒŋkə(r)', 'v.', '占领;攻克;征服;', 'During 1936, Mussolini conquered Abyssinia...', '1936年，墨索里尼攻占了阿比西尼亚。');
INSERT INTO `words` VALUES (1248, 'conquest', 'ˈkɒŋkwest', 'n.', '征服;占领;占领(或征服)的地区;', 'He had led the conquest of southern Poland in 1939...', '1939年，他指挥了攻占波兰南部的行动。');
INSERT INTO `words` VALUES (1249, 'conscience', 'ˈkɒnʃəns', 'n.', '良心;良知;内疚;', 'I have battled with my conscience over whether I should actually send this letter...', '我备受良心的煎熬，不知是否真的应该把这封信寄出去。');
INSERT INTO `words` VALUES (1250, 'conscientious', 'ˌkɒnʃiˈenʃəs', 'adj.', '勤勉认真的;一丝不苟的;', 'We are generally very conscientious about our work...', '我们普遍对工作都非常认真。');
INSERT INTO `words` VALUES (1251, 'conscious', 'ˈkɒnʃəs', 'adj.', '意识到;注意到;神志清醒的;', 'He was conscious of the faint, musky aroma of after-shave...', '他注意到了须后水淡淡的麝香味。');
INSERT INTO `words` VALUES (1252, 'consciousness', 'ˈkɒnʃəsnəs', 'n.', '清醒状态;知觉;觉察;', 'That idea has been creeping into our consciousness for some time.', '不知不觉间，那种想法在我们脑海里渐渐形成已经有一段时间了。');
INSERT INTO `words` VALUES (1253, 'consecutive', 'kənˈsekjətɪv', 'adj.', '连续不断的;', 'The Cup was won for the third consecutive year by the Toronto Maple Leafs...', '多伦多枫叶队连续第三年捧杯。');
INSERT INTO `words` VALUES (1254, 'consensus', 'kənˈsensəs', 'n.', '一致的意见;共识;', 'The consensus amongst the world\'s scientists is that the world is likely to warm up over the next few decades...', '世界各国科学家一致认为在未来几十年里地球有可能变暖。');
INSERT INTO `words` VALUES (1255, 'consent', 'kənˈsent', 'n.', '同意;准许;允许;', 'At approximately 11:30 p.m., Pollard finally gave his consent to the search...', '大概晚上11点半的时候，波拉德最终同意进行搜查。');
INSERT INTO `words` VALUES (1256, 'consequence', 'ˈkɒnsɪkwəns', 'n.', '结果;后果;重要性;', 'Her lawyer said she understood the consequences of her actions and was prepared to go to jail...', '她的律师说她明白自己行为的后果，已有了入狱的心理准备。');
INSERT INTO `words` VALUES (1257, 'consequent', 'ˈkɒnsɪkwənt', 'adj.', '随之发生的;作为结果的;', 'The warming of the Earth and the consequent climatic changes affect us all...', '地球变暖以及随之而来的气候变化影响着我们所有人。');
INSERT INTO `words` VALUES (1258, 'consequently', 'ˈkɒnsɪkwəntli', 'adv.', '因此;所以;', 'Grandfather Dingsdale had sustained a broken back while working in the mines. Consequently, he spent the rest of his life in a wheelchair...', '丁斯戴尔爷爷在矿井里工作时被压断了脊骨，结果，下半辈子都与轮椅为伴了。');
INSERT INTO `words` VALUES (1259, 'conservation', 'ˌkɒnsəˈveɪʃn', 'n.', '(对自然环境的)保护;(官方对历史或艺术建筑的)保护;文物保护;', '...a four-nation regional meeting on elephant conservation.', '四国保护大象的地区性会议');
INSERT INTO `words` VALUES (1260, 'consider', 'kənˈsɪdə(r)', 'v.', '(尤指为作出决定而)仔细考虑，细想;认为;以为;', 'We don\'t consider our customers to be mere consumers; we consider them to be our friends...', '我们没有把客户仅仅看作是消费者;我们还把他们当作朋友。');
INSERT INTO `words` VALUES (1261, 'considerable', 'kənˈsɪdərəbl', 'adj.', '相当多(或大、重要等)的;', 'To be without Pearce would be a considerable blow...', '失去皮尔斯将是一个巨大的打击。');
INSERT INTO `words` VALUES (1262, 'considerate', 'kənˈsɪdərət', 'adj.', '考虑周到的;为(他人)着想的;体谅的;', 'I think he\'s the most charming, most considerate man I\'ve ever known...', '我觉得他是我所认识的最有魅力并且最为体贴的男士。');
INSERT INTO `words` VALUES (1263, 'consideration', 'kənˌsɪdəˈreɪʃn', 'n.', '仔细考虑;深思;斟酌;', 'He said there should be careful consideration of the future role of the BBC.', '他说应该认真思考英国广播公司将来要扮演的角色。');
INSERT INTO `words` VALUES (1264, 'considered', 'kənˈsɪdəd', 'adj.', '经过慎重思考的;经过深思熟虑的;', 'Obviously it was Anne\'s considered opinion that Mavis was a bold-faced liar...', '安妮认为梅维斯是个厚颜无耻的骗子，这显然是经过一番深思熟虑后才得出的看法。');
INSERT INTO `words` VALUES (1265, 'consign', 'kənˈsaɪn', 'v.', '(为摆脱而)把…置于，把…交付给;把…置于(令人不快的境地);打发;', 'For decades, many of Malevich\'s works were consigned to the basements of Soviet museums...', '马列维奇的很多作品在苏联博物馆的地下室里堆了几十年。');
INSERT INTO `words` VALUES (1266, 'consignment', 'kənˈsaɪnmənt', 'n.', '装运的货物;运送物;发送;', 'The first consignment of food has already left Bologna.', '运送的第一批食品已经离开了博洛尼亚。');
INSERT INTO `words` VALUES (1267, 'consist', 'kənˈsɪst', 'v.', '由…组成;由…构成;在于;', 'Breakfast consisted of porridge served with butter.', '早餐是麦片粥配佐餐黄油。');
INSERT INTO `words` VALUES (1268, 'consistency', 'kənˈsɪstənsi', 'n.', '一致性;连贯性;黏稠度;', 'He scores goals with remarkable consistency...', '他的进球一气呵成。');
INSERT INTO `words` VALUES (1269, 'consistent', 'kənˈsɪstənt', 'adj.', '一致的;始终如一的;连续的;', 'Becker has never been the most consistent of players anyway.', '不管怎么说，贝克尔从来就不是表现最为稳定的球员。');
INSERT INTO `words` VALUES (1270, 'consolidate', 'kənˈsɒlɪdeɪt', 'v.', '使加强;使巩固;(使)结成一体，合并;', 'The question is: will the junta consolidate its power by force?...', '问题是：军政府会通过武力巩固其政权吗？');
INSERT INTO `words` VALUES (1271, 'consolidation', 'kənˌsɒlɪˈdeɪʃn', 'n.', '（企业、诉讼和诉讼人的）合并；巩固，加强，联合;变坚固，变结实；充实;（股票等）价格平稳期;', 'The question is: will the junta consolidate its power by force?...', '问题是：军政府会通过武力巩固其政权吗？');
INSERT INTO `words` VALUES (1272, 'conspicuous', 'kənˈspɪkjuəs', 'adj.', '易见的;明显的;惹人注意的;', 'The most conspicuous way in which the old politics is changing is in the growing use of referendums...', '旧式政治最明显的变化在于其越来越多地进行全民公决。');
INSERT INTO `words` VALUES (1273, 'conspiracy', 'kənˈspɪrəsi', 'n.', '密谋策划;阴谋;', 'Seven men, all from Bristol, admitted conspiracy to commit arson...', '7个人都来自布里斯托尔，他们承认了合谋纵火。');
INSERT INTO `words` VALUES (1274, 'constant', 'ˈkɒnstənt', 'adj.', '连续发生的;不断的;重复的;', 'She suggests that women are under constant pressure to be abnormally thin...', '她暗示说女性总是处在保持身材异常瘦削的压力之下。');
INSERT INTO `words` VALUES (1275, 'constantly', 'ˈkɒnstəntli', 'adv.', '始终;一直;重复不断地;', 'She suggests that women are under constant pressure to be abnormally thin...', '她暗示说女性总是处在保持身材异常瘦削的压力之下。');
INSERT INTO `words` VALUES (1276, 'constitute', 'ˈkɒnstɪtjuːt', 'v.', '(被认为或看做)是;被算作;组成;', 'Testing patients without their consent would constitute a professional and legal offence...', '未经患者同意而对其进行检查被视为违反职业操守并触犯法律。');
INSERT INTO `words` VALUES (1277, 'constitution', 'ˌkɒnstɪˈtjuːʃn', 'n.', '宪法;章程;身体素质;', 'The king was forced to adopt a new constitution which reduced his powers.', '国王被迫采用了削弱其权力的新宪法。');
INSERT INTO `words` VALUES (1278, 'constrain', 'kənˈstreɪn', 'v.', '强迫;强制;迫使;', 'Women are too often constrained by family commitments and by low expectations...', '女性往往受到家庭责任和低期望值的束缚。');
INSERT INTO `words` VALUES (1279, 'constraint', 'kənˈstreɪnt', 'n.', '限制;限定;约束;', 'Their decision to abandon the trip was made because of financial constraints...', '他们决定放弃这次出游是因为财力有限。');
INSERT INTO `words` VALUES (1280, 'constrict', 'kənˈstrɪkt', 'v.', '(使)紧缩，缩窄;限制;限定;', 'Severe migraine can be treated with a drug which constricts the blood vessels...', '严重的偏头疼可以用一种收缩血管的药物进行治疗。');
INSERT INTO `words` VALUES (1281, 'construction', 'kənˈstrʌkʃn', 'n.', '建筑;建造;施工;', 'He\'d already started construction on a hunting lodge.', '他已经开始建造一间狩猎用的小屋。');
INSERT INTO `words` VALUES (1282, 'constructive', 'kənˈstrʌktɪv', 'adj.', '建设性的;有助益的;积极的;', 'She welcomes constructive criticism...', '她欢迎建设性的批评。');
INSERT INTO `words` VALUES (1283, 'consult', 'kənˈsʌlt', 'v.', '咨询;请教;(与某人)商议，商量(以得到许可或帮助决策);', 'Consult your doctor about how much exercise you should attempt...', '咨询一下医生你需要多大的运动量。');
INSERT INTO `words` VALUES (1284, 'consultant', 'kənˈsʌltənt', 'n.', '顾问;高级顾问医师;会诊医师;', 'Shirley\'s brother is now a consultant heart surgeon in Sweden.', '雪利的兄弟现在在瑞典做心脏外科高级顾问医师。');
INSERT INTO `words` VALUES (1285, 'consume', 'kənˈsjuːm', 'v.', '消耗，耗费(燃料、能量、时间等);吃;喝;', 'Many people experienced a drop in their cholesterol levels when they consumed oat bran.', '很多人食用燕麦麸后胆固醇水平下降了。');
INSERT INTO `words` VALUES (1286, 'consumed', 'kənˈsjuːmd', 'adj.', '沉迷…的;充满…的;', 'They are consumed with jealousy at her success.', '他们对她的成功充满了嫉妒。');
INSERT INTO `words` VALUES (1287, 'consumer', 'kənˈsjuːmə(r)', 'n.', '消费者;顾客;用户;', '...claims that tobacco companies failed to warn consumers about the dangers of smoking.', '声称烟草公司未能警示消费者吸烟的危害');
INSERT INTO `words` VALUES (1288, 'consumerism', 'kənˈsjuːmərɪzəm', 'n.', '消费;消费主义(认为高消费对社会和个人有利);', 'They have clearly embraced Western consumerism.', '他们显然完全接受了西方的消费主义观念。');
INSERT INTO `words` VALUES (1289, 'consumption', 'kənˈsʌmpʃn', 'n.', '(能量、食物或材料的)消耗，消耗量;消费;肺病;', 'The laws have led to a reduction in fuel consumption in the US.', '这些法律已经使美国燃料消费量有所减少。');
INSERT INTO `words` VALUES (1290, 'contact', 'ˈkɒntækt', 'n.', '(尤指经常的)联系，联络;触摸;接触;', 'Opposition leaders are denying any contact with the government in Kabul...', '反对派领袖否认与喀布尔政府有任何联系。');
INSERT INTO `words` VALUES (1291, 'contagious', 'kənˈteɪdʒəs', 'adj.', '(疾病)接触传染的;患接触性传染病;', '...a highly contagious disease of the lungs.', '极具传染性的肺病');
INSERT INTO `words` VALUES (1292, 'contain', 'kənˈteɪn', 'v.', '包含;含有;容纳;', 'The bag contained a Christmas card...', '这个包里装着一张圣诞卡。');
INSERT INTO `words` VALUES (1293, 'container', 'kənˈteɪnə(r)', 'n.', '容器;集装箱;货柜;', '...the plastic containers in which fish are stored and sold.', '储藏、出售鱼的塑料容器');
INSERT INTO `words` VALUES (1294, 'contaminate', 'kənˈtæmɪneɪt', 'v.', '污染;弄脏;玷污，毒害，腐蚀(人的思想或品德);', 'Have any fish been contaminated in the Arctic Ocean?', '北冰洋的鱼受到过污染吗？');
INSERT INTO `words` VALUES (1295, 'contemplate', 'ˈkɒntəmpleɪt', 'v.', '考虑;思量;思忖;', 'For a time he contemplated a career as an army medical doctor...', '有段时间，他考虑去当军医。');
INSERT INTO `words` VALUES (1296, 'contemporary', 'kənˈtemprəri', 'adj.', '属同时期的;同一时代的;当代的;', 'She writes a lot of contemporary music for people like Whitney Houston...', '她给像惠特尼·休斯敦这样的人写了很多当代音乐作品。');
INSERT INTO `words` VALUES (1297, 'contempt', 'kənˈtempt', 'n.', '蔑视;轻蔑;鄙视;', 'He has contempt for those beyond his immediate family circle...', '他鄙视直系亲属圈子以外的人。');
INSERT INTO `words` VALUES (1298, 'contend', 'kənˈtend', 'v.', '(尤指在争论中)声称，主张，认为;竞争;争夺;', 'It is time, once again, to contend with racism...', '又一次到了与种族主义战斗的时刻了。');
INSERT INTO `words` VALUES (1299, 'contention', 'kənˈtenʃn', 'n.', '争吵;争执;争论;', 'This evidence supports their contention that the outbreak of violence was prearranged.', '这一证据证实了他们的观点，即此次突发暴力事件是有预谋的。');
INSERT INTO `words` VALUES (1300, 'context', 'ˈkɒntekst', 'n.', '(事情发生的)背景，环境，来龙去脉;上下文;语境;', 'We are doing this work in the context of reforms in the economic, social and cultural spheres.', '我们是在对经济、社会、文化诸领域进行改革的背景下从事这项工作的。');
INSERT INTO `words` VALUES (1301, 'contextual', 'kənˈtekstʃuəl', 'adj.', '上下文的;与上下文有关的;与语境相关的;', 'The writer builds up a clever contextual picture of upper class life.', '作者巧妙地构筑了一幅上流社会生活的背景图画。');
INSERT INTO `words` VALUES (1302, 'contiguous', 'kənˈtɪɡjuəs', 'adj.', '相接的;相邻的;', 'Its vineyards are virtually contiguous with those of Ausone.', '其葡萄园差不多紧挨着欧颂堡的葡萄园。');
INSERT INTO `words` VALUES (1303, 'continent', 'ˈkɒntɪnənt', 'n.', '大陆;陆地;洲;', 'She loved the African continent...', '她热爱非洲大陆。');
INSERT INTO `words` VALUES (1304, 'contingency', 'kənˈtɪndʒənsi', 'n.', '可能发生的事;偶发(或不测、意外)事件;', 'I need to examine all possible contingencies.', '我需要审视一切可能出现的情况。');
INSERT INTO `words` VALUES (1305, 'continual', 'kənˈtɪnjuəl', 'adj.', '(令人厌烦地)多次重复的，频繁的;接连不断的;连续的;', 'The school has been in continual use since 1883...', '自1883年以来，该校从未停办过。');
INSERT INTO `words` VALUES (1306, 'continue', 'kənˈtɪnjuː', 'v.', '持续;继续存在;不断发生;', 'I hope they continue to fight for equal justice after I\'m gone...', '我希望我走了以后他们能继续为司法公正而战。');
INSERT INTO `words` VALUES (1307, 'continued', 'kənˈtɪnjuːd', 'adj.', '继续不变的;连续不断的;', 'I hope they continue to fight for equal justice after I\'m gone...', '我希望我走了以后他们能继续为司法公正而战。');
INSERT INTO `words` VALUES (1308, 'continuity', 'ˌkɒntɪˈnjuːəti', 'n.', '连续性;持续性;(逻辑上的)连接，联结;', 'A historical awareness also imparts a sense of continuity.', '历史意识也显示了一种连贯性。');
INSERT INTO `words` VALUES (1309, 'continuous', 'kənˈtɪnjuəs', 'adj.', '不断的;持续的;连续的;', 'Residents report that they heard continuous gunfire.', '居民们报告说他们听到了连续不断的枪声。');
INSERT INTO `words` VALUES (1310, 'contour', 'ˈkɒntʊə(r)', 'n.', '外形;轮廓;(地图上表示相同海拔各点的)等高线;', '...the texture and colour of the skin, the contours of the body...', '皮肤的肌理和颜色，身体的轮廓');
INSERT INTO `words` VALUES (1311, 'contractor', 'kənˈtræktə(r)', 'n.', '承包人;承包商;承包公司;', 'We told the building contractor that we wanted a garage big enough for two cars.', '我们告诉建筑承包商，我们需要一个能容得下两辆车的车库。');
INSERT INTO `words` VALUES (1312, 'contradict', 'ˌkɒntrəˈdɪkt', 'v.', '反驳;驳斥;批驳;', 'She dared not contradict him...', '她不敢反驳他。');
INSERT INTO `words` VALUES (1313, 'contradiction', 'ˌkɒntrəˈdɪkʃn', 'n.', '(事实、看法、行动等的)不一致，矛盾，对立;反驳;驳斥;', '...the contradictions between her private life and the public persona...', '她的私生活与公众形象之间的巨大反差');
INSERT INTO `words` VALUES (1314, 'contrary', 'ˈkɒntrəri , kənˈtreəri', 'adj.', '与之相异的;相对立的;相反的;', 'This view is contrary to the aims of critical social research for a number of reasons...', '之所以说此观点违背了批判性社会研究的宗旨，有几点理由。');
INSERT INTO `words` VALUES (1315, 'contribute', 'kənˈtrɪbjuːt', 'v.', '捐献，捐赠(尤指款或物);捐助;是…的原因之一;', 'The three sons also contribute to the family business...', '这3个儿子也为家族事业做出了贡献。');
INSERT INTO `words` VALUES (1316, 'contribution', 'ˌkɒntrɪˈbjuːʃn', 'n.', '捐款;捐资;(给雇主或政府用作医疗保险、养老金等津贴的)定期缴款;', 'American economists have made important contributions to the field of financial and corporate economics...', '美国的经济学家们在金融和企业经济学领域做出了重要的贡献。');
INSERT INTO `words` VALUES (1317, 'contributor', 'kənˈtrɪbjətə(r)', 'n.', '(杂志或书的)撰稿人，投稿人;(电台、电视节目中的)嘉宾;(会议的)发言人;', 'Old buses are major contributors to pollution in British cities...', '破旧的公交车是造成英国城市污染的主要因素。');
INSERT INTO `words` VALUES (1318, 'contrive', 'kənˈtraɪv', 'v.', '(不顾困难而)设法做到;(克服困难)促成(某事);巧妙地策划;', 'The oil companies were accused of  contriving a shortage of gasoline to justify price increases.', '石油公司被控故意制造石油短缺以名正言顺地提高油价。');
INSERT INTO `words` VALUES (1319, 'control', 'kənˈtrəʊl', 'n.', '(对国家、地区、机构等的)管理权，控制权，支配权;控制(或操纵)能力;限制;', 'The restructuring involves Mr Ronson giving up control of the company...', '这次改组涉及龙森先生放弃对公司的控制权。');
INSERT INTO `words` VALUES (1320, 'controlled', 'kənˈtrəʊld', 'adj.', '十分小心完成的;精心安排的;(受法律或规则)限制的，控制的，管制的;', 'The restructuring involves Mr Ronson giving up control of the company...', '这次改组涉及龙森先生放弃对公司的控制权。');
INSERT INTO `words` VALUES (1321, 'controller', 'kənˈtrəʊlə(r)', 'n.', '(尤指大型机构或部门的)管理者，控制者，指挥者;(机器的)控制器，调节器;(公司的)财务总管;', '...the job of controller of BBC 1.', '英国广播公司第一频道负责人的工作');
INSERT INTO `words` VALUES (1322, 'controversial', 'ˌkɒntrəˈvɜːʃl', 'adj.', '引起争论的;有争议的;', 'Immigration is a controversial issue in many countries...', '在很多国家，移民都是一个颇有争议的问题。');
INSERT INTO `words` VALUES (1323, 'controversy', 'ˈkɒntrəvɜːsi', 'n.', '(公开的)争论，辩论，论战;', 'The proposed cuts have caused considerable controversy.', '削减开支的提议引起了诸多争议。');
INSERT INTO `words` VALUES (1324, 'convalescent', 'ˌkɒnvəˈlesnt', 'adj.', '康复期的;正在康复的;渐愈的;', '...an officers\' convalescent home.', '军官康复中心');
INSERT INTO `words` VALUES (1325, 'convene', 'kənˈviːn', 'v.', '召集，召开(正式会议);(为正式会议而)聚集，集合;', 'Last August he convened a meeting of his closest advisers at Camp David...', '去年8月，他召集自己最亲近的顾问在戴维营开会。');
INSERT INTO `words` VALUES (1326, 'convenience', 'kənˈviːniəns', 'n.', '方便;适宜;便利;', 'He was happy to make a detour for her convenience.', '为了她方便，他很乐意绕道。');
INSERT INTO `words` VALUES (1327, 'convenient', 'kənˈviːniənt', 'adj.', '实用的;便利的;方便的;', '...a flexible and convenient way of paying for business expenses...', '灵活而方便的业务费用支付方式');
INSERT INTO `words` VALUES (1328, 'convention', 'kənˈvenʃn', 'n.', '习俗;常规;惯例;', 'It\'s just a social convention that men don\'t wear skirts...', '男人不穿裙子只是一种社会习俗。');
INSERT INTO `words` VALUES (1329, 'conventional', 'kənˈvenʃənl', 'adj.', '依照惯例的;遵循习俗的;墨守成规的;', '...a respectable married woman with conventional opinions.', '一位观念传统、受人尊敬的已婚女性');
INSERT INTO `words` VALUES (1330, 'converge', 'kənˈvɜːdʒ', 'v.', '汇集;聚集;集中;', 'Competitors from more than a hundred countries have converged on Sheffield for the Games...', '来自100多个国家的选手齐聚设菲尔德参加此次运动会。');
INSERT INTO `words` VALUES (1331, 'converged', 'kənˈvɜːdʒd', 'v.', '汇集;聚集;集中;', 'Competitors from more than a hundred countries have converged on Sheffield for the Games...', '来自100多个国家的选手齐聚设菲尔德参加此次运动会。');
INSERT INTO `words` VALUES (1332, 'convergence', 'kənˈvɜːdʒəns', 'n.', '(不同思想、群体或社会的)趋同，融合;', '...the need to move towards greater economic convergence...', '朝着经济进一步融合发展的需要');
INSERT INTO `words` VALUES (1333, 'conversation', 'ˌkɒnvəˈseɪʃn', 'n.', '(非正式)交谈，谈话;', 'He\'s a talkative guy, and I struck up a conversation with him...', '他是一个健谈的人，于是我开始和他攀谈起来。');
INSERT INTO `words` VALUES (1334, 'conversational', 'ˌkɒnvəˈseɪʃənl', 'adj.', '非正式的;用于交谈的;口语的;', 'What is refreshing is the author\'s easy, conversational style...', '让人耳目一新的是作者轻松自如的会话式写作风格。');
INSERT INTO `words` VALUES (1335, 'conversely', 'ˈkɒnvɜːsli', 'adv.', '相反地;反过来;', 'In real life, nobody was all bad, nor, conversely, all good.', '在现实生活中，没有彻头彻尾的坏人，相反地，也没有完完全全的好人。');
INSERT INTO `words` VALUES (1336, 'conversion', 'kənˈvɜːʃn', 'n.', '转变;转换;转化;', '...the conversion of disused rail lines into cycle routes...', '将废弃的铁路线改成自行车道');
INSERT INTO `words` VALUES (1337, 'convertible', 'kənˈvɜːtəbl', 'adj.', '可改变的;可转换的;可兑换的;', 'Her own car is a convertible Golf.', '她自己有一辆敞篷高尔夫。');
INSERT INTO `words` VALUES (1338, 'convey', 'kənˈveɪ', 'v.', '表达，传递(思想、感情等);传送;运送;', 'When I returned home, I tried to convey the wonder of this machine to my husband...', '回到家后，我竭力地想把这台机器的精妙之处描述给丈夫听。');
INSERT INTO `words` VALUES (1339, 'conveyance', 'kənˈveɪəns', 'n.', '传送;运送;输送;', 'Mahoney had never seen such a conveyance before.', '马奥尼以前从来没有见过这样的运输工具。');
INSERT INTO `words` VALUES (1340, 'conviction', 'kənˈvɪkʃn', 'n.', '判罪;定罪;证明有罪;', 'It is our firm conviction that a step forward has been taken...', '我们坚信已经向前迈进了一步。');
INSERT INTO `words` VALUES (1341, 'convince', 'kənˈvɪns', 'v.', '使确信;使相信;使信服;', 'Although I soon convinced him of my innocence, I think he still has serious doubts about my sanity...', '虽然我很快便让他相信我是清白的，但是我想他仍然非常怀疑我是否神志正常。');
INSERT INTO `words` VALUES (1342, 'cook', 'kʊk', 'v.', '烹饪;烹调;煮(或烘烤、煎炸等);', 'I have to go and cook the dinner...', '我得去做饭了。');
INSERT INTO `words` VALUES (1343, 'cooker', 'ˈkʊkə(r)', 'n.', '(带烤箱、燃气炉或电炉的)厨灶，炉具;', '...a gas cooker.', '煤气灶');
INSERT INTO `words` VALUES (1344, 'cool', 'kuːl', 'adj.', '凉的;凉爽的;凉快的;', 'I felt a current of cool air...', '我感觉到一阵凉风。');
INSERT INTO `words` VALUES (1345, 'cooperate', 'kəʊˈɒpəreɪt', 'v.', '合作;协作;协助;', 'The UN had been co-operating with the State Department on a plan to find countries willing to take the refugees...', '联合国一直都在与美国国务院合作推进一项计划，寻找愿意接纳难民的国家。');
INSERT INTO `words` VALUES (1346, 'cooperation', 'kəʊˌɒpəˈreɪʃn', 'n.', '合作;协作;协助;', 'The UN had been co-operating with the State Department on a plan to find countries willing to take the refugees...', '联合国一直都在与美国国务院合作推进一项计划，寻找愿意接纳难民的国家。');
INSERT INTO `words` VALUES (1347, 'cooperative', 'kəʊˈɒpərətɪv', 'adj.', '合作的;协作的;同心协力的;', 'They decided a housing co-operative was the way to regenerate Ormiston Crescent...', '他们最终决定通过住房合作社来重建奥米斯顿月牙街。');
INSERT INTO `words` VALUES (1348, 'cop', 'kɒp', 'n.', '警察;', 'Frank didn\'t like having the cops know where to find him.', '弗兰克不想让警察知道去哪里可以找到他。');
INSERT INTO `words` VALUES (1349, 'cope', 'kəʊp', 'v.', '(成功地)对付，处理;', 'It was amazing how my mother coped with bringing up three children on less than three pounds a week...', '我妈妈用每周不到3英镑的钱抚养大了3个孩子，简直太不可思议了。');
INSERT INTO `words` VALUES (1350, 'copious', 'ˈkəʊpiəs', 'adj.', '大量的;充裕的;丰富的;', 'I went out for a meal last night and drank copious amounts of red wine...', '我昨晚出去吃饭，喝了很多红酒。');
INSERT INTO `words` VALUES (1351, 'copper', 'ˈkɒpə(r)', 'n.', '铜;铜币;警察;', 'Chile is the world\'s largest producer of copper.', '智利是世界上最大的产铜国。');
INSERT INTO `words` VALUES (1352, 'copy', 'ˈkɒpi', 'n.', '(尤指文件或艺术品的)复印件，副本，复制品;(书、报纸等的)一本，一册，一份;(报刊等的)稿件;', 'The reporter apparently obtained a copy of Steve\'s resignation letter...', '那记者显然弄到了史蒂夫辞职信的复印件。');
INSERT INTO `words` VALUES (1353, 'copyright', 'ˈkɒpiraɪt', 'n.', '版权;著作权;', 'To order a book one first had to get permission from the monastery that held the copyright...', '要想订购书，必须先得到持有该书版权的修道院的许可。');
INSERT INTO `words` VALUES (1354, 'cord', 'kɔːd', 'n.', '(结实的)粗线，细绳;一根粗线(或细绳);灯芯绒裤子;', 'The door had been tied shut with a length of nylon cord.', '门用一段尼龙绳拴住了。');
INSERT INTO `words` VALUES (1355, 'cordial', 'ˈkɔːdiəl', 'adj.', '热情友好的;和蔼可亲的;', 'He had never known him to be so chatty and cordial...', '他从没见过他如此健谈，如此友善。');
INSERT INTO `words` VALUES (1356, 'core', 'kɔː(r)', 'n.', '(苹果等的)果心，核儿;(物体的)中心部分;最重要的部分;', 'Someone threw an apple core...', '有人扔了一个苹果核。');
INSERT INTO `words` VALUES (1357, 'cork', 'kɔːk', 'n.', '木栓，软木(一种地中海橡树皮);(尤指酒瓶的)软木塞，塑料塞;', '...cork floors.', '软木地板');
INSERT INTO `words` VALUES (1358, 'corn', 'kɔːn', 'n.', '(小麦等)谷物;谷粒;(尤指脚趾上的)钉胼，鸡眼;', '...fields of corn...', '麦田');
INSERT INTO `words` VALUES (1359, 'corner', 'ˈkɔːnə(r)', 'n.', '角;有…角的;涉及…群体的;', 'He saw the corner of a magazine sticking out from under the blanket...', '他看见杂志的一角从毯子下露了出来。');
INSERT INTO `words` VALUES (1360, 'coronary', 'ˈkɒrənri', 'adj.', '冠状动脉的;', 'If all the coronary arteries are free of significant obstructions, all parts of the heart will receive equal amounts of oxygen.', '如果所有的冠状动脉均没有较大的阻塞物，心脏各部分将得到等量的氧气。');
INSERT INTO `words` VALUES (1361, 'corporate', 'ˈkɔːpərət', 'adj.', '公司的;组成公司(或团体)的;法人的;', '...top US corporate executives.', '美国顶级公司经理人');
INSERT INTO `words` VALUES (1362, 'corporation', 'ˌkɔːpəˈreɪʃn', 'n.', '(大)公司;法人;法人团体;', '...multi-national corporations.', '跨国公司');
INSERT INTO `words` VALUES (1363, 'corps', 'kɔː(r)', 'n.', '(由两个或两个以上师组成的)军，兵团;(陆军)特种部队;(从事某工作或活动的)一群人，一组人;', '...the Army Medical Corps.', '陆军医疗队');
INSERT INTO `words` VALUES (1364, 'correct', 'kəˈrekt', 'adj.', '准确无误的;精确的;正确的;', 'The correct answers can be found at the bottom of page 8...', '正确答案在第8页末尾。');
INSERT INTO `words` VALUES (1365, 'correctly', 'kə\'rektlɪ', 'adv.', '正确地；得体地;', 'The correct answers can be found at the bottom of page 8...', '正确答案在第8页末尾。');
INSERT INTO `words` VALUES (1366, 'correlate', 'ˈkɒrələt , ˈkɒrəleɪt', 'v.', '相互关联影响;相互依赖;显示(两个或多个事实或数字等)的紧密联系;', 'Obesity correlates with increased risk for hypertension and stroke...', '肥胖会增加高血压和中风发作的几率。');
INSERT INTO `words` VALUES (1367, 'correspond', 'ˌkɒrəˈspɒnd', 'v.', '相一致;符合;类似于;', 'Racegoers will be given a number which will correspond to a horse running in a race...', '观看赛马比赛的观众将获得一个与某一匹赛马相对应的号码。');
INSERT INTO `words` VALUES (1368, 'correspondence', 'ˌkɒrəˈspɒndəns', 'n.', '来往信件;往来书信;通信;', 'The judges\' decision is final and no correspondence will be entered into...', '法官的裁决是最终裁决，再写信也不会予以审议。');
INSERT INTO `words` VALUES (1369, 'correspondent', 'ˌkɒrəˈspɒndənt', 'n.', '记者;通讯员;通信者;', '...our Diplomatic Correspondent Mark Brayne.', '我们的外交通讯员马克·布雷恩');
INSERT INTO `words` VALUES (1370, 'corresponding', 'ˌkɒrəˈspɒndɪŋ', 'adj.', '符合的;相应的;相关的;', 'Racegoers will be given a number which will correspond to a horse running in a race...', '观看赛马比赛的观众将获得一个与某一匹赛马相对应的号码。');
INSERT INTO `words` VALUES (1371, 'corrode', 'kəˈrəʊd', 'v.', '腐蚀;侵蚀;', 'He has devised a process for making gold wires which neither corrode nor oxidise...', '他设计了一种能够避免腐蚀或氧化的金丝制作流程。');
INSERT INTO `words` VALUES (1372, 'corrupt', 'kəˈrʌpt', 'adj.', '贪污的;受贿的;腐败的;', '...to save the nation from corrupt politicians of both parties...', '从两个政党的腐败政客手中挽救这个国家');
INSERT INTO `words` VALUES (1373, 'corrupted', 'kəˈrʌptɪd', 'v.', '使腐化;使堕落;破坏;', '...to save the nation from corrupt politicians of both parties...', '从两个政党的腐败政客手中挽救这个国家');
INSERT INTO `words` VALUES (1374, 'cortex', 'ˈkɔːteks', 'n.', '皮层;皮质;(尤指)大脑皮层;', '...the cerebral cortex.', '大脑皮层');
INSERT INTO `words` VALUES (1375, 'cosmic', 'ˈkɒzmɪk', 'adj.', '宇宙的;巨大且重要的;', '...cosmic radiation.', '外太空的辐射');
INSERT INTO `words` VALUES (1376, 'cosmopolitan', 'ˌkɒzməˈpɒlɪtən', 'adj.', '世界性的;全球各国的;有各国人的;', 'London has always been a cosmopolitan city.', '伦敦一直是一个国际化都市。');
INSERT INTO `words` VALUES (1377, 'costing', 'ˈkɒstɪŋ', 'n.', '成本计算;', 'We\'ll put together a proposal, including detailed costings, free of charge.', '我们将免费拟一份提案，其中包括详细的成本估算。');
INSERT INTO `words` VALUES (1378, 'costly', 'ˈkɒstli', 'adj.', '花钱多的;昂贵的;价钱高的;', 'Having professionally made curtains can be costly, so why not make your own?', '既然找专门人员做窗帘非常贵，为什么不自己做呢?');
INSERT INTO `words` VALUES (1379, 'costume', 'ˈkɒstjuːm', 'n.', '(某地或某历史时期的)服装，装束;(戏剧或电影的)戏装，服装;', 'Even from a distance the effect of his fox costume was stunning...', '即使从远处看,他的狐狸戏服也很抢眼。');
INSERT INTO `words` VALUES (1380, 'cottage', 'ˈkɒtɪdʒ', 'n.', '小屋;(尤指)村舍，小别墅;', 'They used to have a cottage in N.W. Scotland...', '他们原来在苏格兰西北部有一间小屋。');
INSERT INTO `words` VALUES (1381, 'cotton', 'ˈkɒtn', 'n.', '棉，棉花(植株);棉织物;棉布;', '...a cotton shirt.', '棉布衬衫');
INSERT INTO `words` VALUES (1382, 'cough', 'kɒf', 'v.', '咳嗽;(从喉咙或肺中)咳出;突然发出刺耳的声音;', 'Graham began to cough violently...', '格雷厄姆开始剧烈咳嗽。');
INSERT INTO `words` VALUES (1383, 'council', 'ˈkaʊnsl', 'n.', '(市、郡等的)政务委员会，地方议会;市政(或地方管理)服务机构;(顾问、立法、研究、基金等)委员会;', '...Cheshire County Council...', '柴郡政务委员会');
INSERT INTO `words` VALUES (1384, 'count', 'kaʊnt', 'v.', '(按顺序)数数;计算(或清点)总数;把…算入;', 'He was counting slowly under his breath...', '他轻声地慢慢数着数。');
INSERT INTO `words` VALUES (1385, 'counter', 'ˈkaʊntə(r)', 'n.', '(商店、银行等的)柜台;(某些棋盘游戏的)筹码;(电子)计数器，计算器;', '...those fellows we see working behind the counter at our local video rental store.', '我们见到在我们当地录像带出租店里做店员的那些人');
INSERT INTO `words` VALUES (1386, 'counteract', 'ˌkaʊntərˈækt', 'v.', '抵制;抵消;抵抗;', 'My husband has to take several pills to counteract high blood pressure...', '我丈夫不得不吃几片药来降压。');
INSERT INTO `words` VALUES (1387, 'counterbalance', 'ˌkaʊntəˈbæləns , ˈkaʊntəbæləns', 'v.', '抗衡;抵消;对…起平衡作用;', 'Add honey to counterbalance the acidity...', '加点蜂蜜来调和酸味。');
INSERT INTO `words` VALUES (1388, 'counterclockwise', 'ˌkaʊntəˈklɒkwaɪz', 'adj.', '反时针方向的；逆时针方向的;', 'Rotate the head clockwise and counterclockwise.', '按顺时针方向转动头部，然后再逆时针旋转。');
INSERT INTO `words` VALUES (1389, 'counterfeit', 'ˈkaʊntəfɪt', 'adj.', '伪造的;仿造的;假冒的;', 'He admitted possessing and delivering counterfeit currency.', '他供认持有并散播假币。');
INSERT INTO `words` VALUES (1390, 'counterpart', 'ˈkaʊntəpɑːt', 'n.', '职位(或作用)相当的人;对应的事物;', 'The Foreign Secretary telephoned his Italian counterpart to protest.', '外交部长致电意大利外长表示抗议。');
INSERT INTO `words` VALUES (1391, 'countess', 'ˈkaʊntəs', 'n.', '女伯爵;伯爵夫人;', '...the Countess of Lichfield.', '利奇菲尔德女伯爵');
INSERT INTO `words` VALUES (1392, 'countless', 'ˈkaʊntləs', 'adj.', '无数的;数不胜数的;数不尽的;', 'She brought joy to countless people through her music...', '她用音乐给无数的人带来欢乐。');
INSERT INTO `words` VALUES (1393, 'country', 'ˈkʌntri', 'n.', '国;国家;(尤指具有某种自然特征、适于某目的或与某种人有关的)地区，区域;', 'Indonesia is the fourth most populous country in the world.', '印度尼西亚是世界上第四大人口大国。');
INSERT INTO `words` VALUES (1394, 'countryside', 'ˈkʌntrisaɪd', 'n.', '乡村;农村;', 'I\'ve always loved the English countryside...', '一直以来我都很喜欢英国的乡间。');
INSERT INTO `words` VALUES (1395, 'county', 'ˈkaʊnti', 'n.', '(英国、爱尔兰的)郡;(美国的)县;', 'He is living now in his mother\'s home county of Oxfordshire...', '他现在居住在他母亲的故乡牛津郡。');
INSERT INTO `words` VALUES (1396, 'couple', 'ˈkʌpl', 'n.', '两人;两件事物;几个人;', 'Across the street from me there are a couple of police officers standing guard...', '我对面的街上有几名警官在站岗。');
INSERT INTO `words` VALUES (1397, 'coupon', 'ˈkuːpɒn', 'n.', '配给券;(购物)票证;(购物)优惠券;', 'Bring the coupon below to any Tecno store and pay just £10.99.', '持下面的优惠券到任意一家Tecno专卖店，只需付10.99英镑。');
INSERT INTO `words` VALUES (1398, 'courage', 'ˈkʌrɪdʒ', 'n.', '勇气;勇敢;无畏;', 'General Lewis Mackenzie has impressed everyone with his authority and personal courage...', '刘易斯·麦肯奇将军的威信和勇气给所有人留下了深刻的印象。');
INSERT INTO `words` VALUES (1399, 'courier', 'ˈkʊriə(r)', 'n.', '(递送包裹或重要文件的)信使，通讯员，专递公司;(旅游公司的)导游;', 'He worked as a motorcycle courier...', '他的工作是骑摩托车送快递。');
INSERT INTO `words` VALUES (1400, 'courtesy', 'ˈkɜːtəsi', 'n.', '礼貌;谦恭;彬彬有礼;', '...a gentleman who behaves with the utmost courtesy towards ladies...', '对女士极有风度的绅士');
INSERT INTO `words` VALUES (1401, 'courtyard', 'ˈkɔːtjɑːd', 'n.', '(通常为城堡、大宅第等的)庭院，院子，天井;', 'They walked through the arch and into the cobbled courtyard.', '他们穿过拱门走进铺着鹅卵石的院子。');
INSERT INTO `words` VALUES (1402, 'cousin', 'ˈkʌzn', 'n.', '同辈表亲(或堂亲);堂兄(或弟、姊、妹);表兄(或弟、姊、妹);', 'My cousin Mark helped me...', '表兄马克帮了我。');
INSERT INTO `words` VALUES (1403, 'cover', 'ˈkʌvə(r)', 'v.', '掩蔽;遮盖;盖;', 'Cover the casserole with a tight-fitting lid...', '用大小合适的盖子盖住砂锅。');
INSERT INTO `words` VALUES (1404, 'coverage', 'ˈkʌvərɪdʒ', 'n.', '新闻报道;(书、课程学习、电视等的)信息范围，信息质量;提供的数量;', 'Now a special TV network gives live coverage of most races...', '现在有一个专门的电视网络对大部分比赛进行现场直播。');
INSERT INTO `words` VALUES (1405, 'covering', 'ˈkʌvərɪŋ', 'n.', '(一层)覆盖层，遮盖物;装饰(或保护)性覆盖物;覆盖某物的一块(或一片)材料;', 'Leave a thin covering of fat...', '留下薄薄一层脂肪。');
INSERT INTO `words` VALUES (1406, 'cow', 'kaʊ', 'n.', '母牛;奶牛;菜牛;', 'He kept a few dairy cows...', '他养了几头奶牛。');
INSERT INTO `words` VALUES (1407, 'coward', 'ˈkaʊəd', 'n.', '胆小鬼;懦夫;胆怯者;', 'She accused her husband of being a coward.', '她指责丈夫胆小懦弱。');
INSERT INTO `words` VALUES (1408, 'cowboy', 'ˈkaʊbɔɪ', 'n.', '(美国西部的)牛仔;(美国西部影片中的)牛仔;(尤指产品质量差或索价太高的)奸商;', 'Boys used to play at cowboys and Indians.', '男孩们过去常玩西部牛仔与印第安人的游戏。');
INSERT INTO `words` VALUES (1409, 'crane', 'kreɪn', 'n.', '起重机;吊车;鹤;', 'The little prefabricated hut was lifted away by a huge crane.', '那间用预制板搭建的小棚屋被一辆大型起重机吊走了。');
INSERT INTO `words` VALUES (1410, 'crank', 'kræŋk', 'n.', '(想法)古怪的人;脾气坏的人;容易恼怒的人;', 'The Prime Minister called Councillor Marshall \'a crank\'...', '首相称马歇尔议员为“怪人”。');
INSERT INTO `words` VALUES (1411, 'crash', 'kræʃ', 'n.', '撞车;碰撞;相撞;', 'His elder son was killed in a car crash a few years ago.', '他的长子几年前死于车祸。');
INSERT INTO `words` VALUES (1412, 'crawl', 'krɔːl', 'v.', '爬;爬行;匍匐行进;', 'Don\'t worry if your baby seems a little reluctant to crawl or walk...', '如果你的宝宝不爱爬也不爱走，不要担心。');
INSERT INTO `words` VALUES (1413, 'craze', 'kreɪz', 'n.', '(通常为一时的)狂热，疯狂;风行一时的东西;', '...the craze for Mutant Ninja Turtles...', '《忍者神龟》的风靡一时');
INSERT INTO `words` VALUES (1414, 'crazy', 'ˈkreɪzi', 'adj.', '不理智的;疯狂的;愚蠢的;', 'People thought they were all crazy to try to make money from manufacturing...', '人们认为他们简直是疯了，竟然想从制造业中赚钱。');
INSERT INTO `words` VALUES (1415, 'cream', 'kriːm', 'n.', '奶油;乳脂;奶油夹心糖;', '...strawberries and cream.', '奶油草莓');
INSERT INTO `words` VALUES (1416, 'crease', 'kriːs', 'n.', '褶痕;皱痕;褶缝;', 'She stood up, frowning at the creases in her silk dress...', '她站起身来，看到丝裙上的褶痕皱起了眉头。');
INSERT INTO `words` VALUES (1417, 'create', 'kriˈeɪt', 'v.', '创造;创作;创建;', 'We set business free to create more jobs in Britain...', '我们放开对企业的限制以便在英国创造更多的就业机会。');
INSERT INTO `words` VALUES (1418, 'creation', 'kriˈeɪʃn', 'n.', '创造;创建;作品;', '...the Creation of the universe as told in Genesis Chapter One...', '《创世记》第一章所述的上帝创造世界万物');
INSERT INTO `words` VALUES (1419, 'creative', 'kriˈeɪtɪv', 'adj.', '创造(性)的;创作的;有创造力的;', 'Like so many creative people he was never satisfied.', '像许多富于创造力的人一样，他从不满足。');
INSERT INTO `words` VALUES (1420, 'creator', 'kriˈeɪtə(r)', 'n.', '创造者;创作者;发明者;', '...Ian Fleming, the creator of James Bond...', '詹姆斯·邦德这一形象的创作者伊恩·弗莱明');
INSERT INTO `words` VALUES (1421, 'creature', 'ˈkriːtʃə(r)', 'n.', '生物;动物;(具有某种特征的)人;', 'Alaskan Eskimos believe that every living creature possesses a spirit...', '阿拉斯加的爱斯基摩人认为每一个活着的生物都有灵魂。');
INSERT INTO `words` VALUES (1422, 'credible', 'ˈkredəbl', 'adj.', '可信的;可靠的;(因看似可能成功而)可接受的;', 'Baroness Thatcher\'s claims seem credible to many...', '撒切尔女男爵的主张在很多人看来是可信的。');
INSERT INTO `words` VALUES (1423, 'credit', 'ˈkredɪt', 'n.', '赊购;赊欠;(从银行借的)借款;', 'The group can\'t get credit to buy farming machinery...', '这些人无法赊购到农用机械。');
INSERT INTO `words` VALUES (1424, 'creditor', 'ˈkredɪtə(r)', 'n.', '债权人;债主;贷方;', 'The company said it would pay in full all its creditors.', '该公司称将偿清所有债权人的债务。');
INSERT INTO `words` VALUES (1425, 'creep', 'kriːp', 'v.', '悄悄地缓慢行进;蹑手蹑脚地移动;匍匐;', 'Back I go to the hotel and creep up to my room...', '我回到旅馆，蹑手蹑脚地上楼回到房间。');
INSERT INTO `words` VALUES (1426, 'crevice', 'ˈkrevɪs', 'n.', '(岩石或墙壁的)裂缝，裂隙，裂口;', '...a huge boulder with rare ferns growing in every crevice.', '裂缝中全长着罕见蕨类植物的巨石');
INSERT INTO `words` VALUES (1427, 'crew', 'kruː', 'n.', '(轮船、飞机等上面的)全体工作人员;(轮船、飞机等上面不包括高级职员的)全体船员，全体乘务员;技术人员团队;', 'The mission for the crew of the space shuttle Endeavour is essentially over...', '“奋进号”航天飞机上全体机组人员的任务基本上已经完成了。');
INSERT INTO `words` VALUES (1428, 'cricket', 'ˈkrɪkɪt', 'n.', '板球(运动);蟋蟀;蛐蛐;', 'During the summer term we would play cricket at the village ground.', '在夏季学期，我们会在村里的空地上打板球。');
INSERT INTO `words` VALUES (1429, 'crime', 'kraɪm', 'n.', '犯罪活动;不法行为;罪;', 'He and Lieutenant Cassidy were checking the scene of the crime...', '他和卡西迪中尉正在勘查犯罪现场。');
INSERT INTO `words` VALUES (1430, 'criminal', 'ˈkrɪmɪnl', 'adj.', '犯罪的;犯法的;涉及犯罪的;', 'A group of gunmen attacked a prison and set free nine criminals in Moroto.', '一伙持枪歹徒袭击了莫罗托的一所监狱，放走了9名罪犯。');
INSERT INTO `words` VALUES (1431, 'cripple', 'ˈkrɪpl', 'v.', '使残废;使跛;使成瘸子;', 'She has gone from being a healthy, fit, and sporty young woman to being a cripple.', '她从一个健康、强壮、爱好运动的年轻女性变成了一个跛子。');
INSERT INTO `words` VALUES (1432, 'crisis', 'ˈkraɪsɪs', 'n.', '危机;危急关头;危难时刻;', 'Natural disasters have obviously contributed to the continent\'s economic crisis...', '很显然，自然灾害也是造成该大陆经济危机的原因之一。');
INSERT INTO `words` VALUES (1433, 'crisp', 'krɪsp', 'adj.', '脆的;酥脆的;鲜脆的;', 'Bake the potatoes for 15 minutes, till they\'re nice and crisp.', '将土豆片烤15分钟，直至酥脆。');
INSERT INTO `words` VALUES (1434, 'criteria', 'kraɪ\'tɪəriə', 'n.', '(评判或作决定的)标准，准则，原则;', 'The most important criterion for entry is that applicants must design and make their own work...', '最重要的参赛标准是申请者必须设计并制作自己的作品。');
INSERT INTO `words` VALUES (1435, 'criterion', 'kraɪˈtɪəriən', 'n.', '(评判或作决定的)标准，准则，原则;', 'The most important criterion for entry is that applicants must design and make their own work...', '最重要的参赛标准是申请者必须设计并制作自己的作品。');
INSERT INTO `words` VALUES (1436, 'critic', 'ˈkrɪtɪk', 'n.', '批评家;评论家;评论员;', 'Mather was film critic on the Daily Telegraph for many years...', '马瑟多年来一直担任《每日电讯报》的影评人。');
INSERT INTO `words` VALUES (1437, 'critical', 'ˈkrɪtɪkl', 'adj.', '批评的;批判性的;挑剔的;', 'The incident happened at a critical point in the campaign...', '该事件发生在竞选活动的关键时期。');
INSERT INTO `words` VALUES (1438, 'criticism', 'ˈkrɪtɪsɪzəm', 'n.', '批评;批判;责备;', 'This policy had repeatedly come under strong criticism on Capitol Hill...', '该政策在美国国会屡遭强烈批评。');
INSERT INTO `words` VALUES (1439, 'critique', 'krɪˈtiːk', 'n.', '评论;评论文章;', 'She had brought a book, a feminist critique of Victorian lady novelists.', '她带来了一本书，这本书从女性主义视角对维多利亚时代女性小说家进行了评论。');
INSERT INTO `words` VALUES (1440, 'crooked', 'krʊkt , ˈkrʊkɪd', 'adj.', '不直的;弯曲的;扭曲的;', '...the crooked line of his broken nose.', '他骨折鼻子的歪曲鼻骨线条');
INSERT INTO `words` VALUES (1441, 'crop', 'krɒp', 'n.', '庄稼;作物;(谷物、水果等一季的)收成，产量;', 'Rice farmers here still plant and harvest their crops by hand...', '这里种植水稻的农场主仍是人工种植和收割庄稼。');
INSERT INTO `words` VALUES (1442, 'crossing', 'ˈkrɒsɪŋ', 'n.', '(通过道路、河流等的)安全通行处;人行横道;渡口;', 'He made the crossing from Cape Town to Sydney in just over twenty-six days...', '他在短短26天内从开普敦横渡到悉尼。');
INSERT INTO `words` VALUES (1443, 'crouch', 'kraʊtʃ', 'v.', '蹲;蹲下;蹲伏;', 'We were crouching in the bushes...', '我们蹲在树丛里。');
INSERT INTO `words` VALUES (1444, 'crowd', 'kraʊd', 'n.', '人群;观众;一伙人;', 'A huge crowd gathered in a square outside the Kremlin walls...', '一大群人聚集在克里姆林宫墙外的广场上。');
INSERT INTO `words` VALUES (1445, 'crowded', 'ˈkraʊdɪd', 'adj.', '人(太)多的;拥挤的;充满的;', 'He peered slowly around the small crowded room...', '他慢慢地环视这个拥挤的小屋。');
INSERT INTO `words` VALUES (1446, 'crowding', 'ˈkraʊdɪŋ', 'v.', '挤满;塞满;使…拥挤;', 'A huge crowd gathered in a square outside the Kremlin walls...', '一大群人聚集在克里姆林宫墙外的广场上。');
INSERT INTO `words` VALUES (1447, 'crown', 'kraʊn', 'n.', '王冠;皇冠;冕;', '...a crown of flowers.', '花冠');
INSERT INTO `words` VALUES (1448, 'crucial', 'ˈkruːʃl', 'adj.', '至关重要的;关键性的;', 'He had administrators under him but took the crucial decisions himself...', '他手下有管理人员，但重要的决策仍由他自己来做。');
INSERT INTO `words` VALUES (1449, 'crude', 'kruːd', 'adj.', '粗略的;简略的;大概的;', 'Standard measurements of blood pressure are an important but crude way of assessing the risk of heart disease or strokes...', '标准的血压测量是评估患心脏病或中风几率的一个重要但不精确的方法。');
INSERT INTO `words` VALUES (1450, 'cruelty', 'ˈkruːəlti', 'n.', '(尤指蓄意的)残酷，残忍，残暴;残暴的行为;不公;', 'Britain had laws against cruelty to animals but none to protect children...', '英国有禁止虐待动物的法律，但却没有保护儿童的法律。');
INSERT INTO `words` VALUES (1451, 'cruise', 'kruːz', 'n.', '乘船游览;航行;', 'He and his wife were planning to go on a world cruise...', '他和太太计划乘船环游世界。');
INSERT INTO `words` VALUES (1452, 'crumb', 'krʌm', 'n.', '食物碎屑;(尤指)面包屑，糕饼屑;一点;', 'I stood up, brushing crumbs from my  trousers.', '我站起来，掸掉裤子上的碎屑。');
INSERT INTO `words` VALUES (1453, 'crumble', 'ˈkrʌmbl', 'v.', '(使)破碎，成碎屑;坍塌;损坏;', 'Under the pressure, the flint crumbled into fragments...', '燧石在压力的作用下碎成了小块。');
INSERT INTO `words` VALUES (1454, 'crumple', 'ˈkrʌmpl', 'v.', '压皱;(使)变皱，起皱;(指脸)沮丧地皱起，哭丧着;', 'She crumpled the paper in her hand...', '她把那张纸在手里揉成一团。');
INSERT INTO `words` VALUES (1455, 'crush', 'krʌʃ', 'v.', '压坏;压伤;挤压变形;', 'Andrew crushed his empty can...', '安德鲁把他的空罐子捏扁了。');
INSERT INTO `words` VALUES (1456, 'cry', 'kraɪ', 'v.', '哭;哭泣;喊叫;', 'I hung up the phone and started to cry...', '我挂断电话，开始哭了起来。');
INSERT INTO `words` VALUES (1457, 'crying', 'ˈkraɪɪŋ', 'adj.', '哭的;好哭的;显著的;', 'There is a crying need for more magistrates from the ethnic minority communities.', '亟须更多来自少数民族群体的治安官。');
INSERT INTO `words` VALUES (1458, 'crystal', 'ˈkrɪstl', 'n.', '结晶;晶体;水晶(如石英，用于制作珠宝饰物);', '...salt crystals.', '盐晶体');
INSERT INTO `words` VALUES (1459, 'cube', 'kjuːb', 'n.', '立方体;立方形;立方形的东西(尤指食物);', '...cold water with ice cubes in it...', '放了冰块的冷水');
INSERT INTO `words` VALUES (1460, 'cubic', 'ˈkjuːbɪk', 'adj.', '立方的;用立方单位度量(或表示)的;立方形的;', '...3 billion cubic metres of soil.', '30亿土方');
INSERT INTO `words` VALUES (1461, 'cue', 'kjuː', 'n.', '暗示;提示;信号;', 'The actors not performing sit at the side of the stage in full view, waiting for their cues...', '还未轮到演出的演员坐在舞台旁边能看得一清二楚的地方，等待着他们的出场提示。');
INSERT INTO `words` VALUES (1462, 'cuff', 'kʌf', 'n.', '袖口;用掌轻拍;', '...a pale blue shirt with white collar and cuffs.', '有白色衣领和袖口的浅蓝色衬衫');
INSERT INTO `words` VALUES (1463, 'culling', 'ˈkʌlɪŋ', 'v.', '部分捕杀，宰杀(为防止动物种群量过多而杀掉其中一定数量);', 'All this, needless to say, had been culled second-hand from radio reports...', '不用说，所有这些都是从电台报道中采集来的二手材料。');
INSERT INTO `words` VALUES (1464, 'culminate', 'ˈkʌlmɪneɪt', 'v.', '(以某种结果)告终;(在某一点)结束;', 'They had an argument, which culminated in Tom getting drunk...', '他们争论了一番，最后汤姆喝醉了。');
INSERT INTO `words` VALUES (1465, 'culprit', 'ˈkʌlprɪt', 'n.', '犯错的人;罪犯;肇事者;', 'All the men were being deported even though the real culprits in the fight have not been identified.', '尽管真凶尚未查出，所有参与斗殴的男子均被驱逐出境。');
INSERT INTO `words` VALUES (1466, 'cultivate', 'ˈkʌltɪveɪt', 'v.', '耕;耕作;种植;', 'She also cultivated a small garden of her own.', '她还耕种了一片属于自己的小园子。');
INSERT INTO `words` VALUES (1467, 'culture', 'ˈkʌltʃə(r)', 'n.', '文化，文明(指国家或群体的风俗、信仰、艺术、生活方式及社会组织);文化(指拥有特定信仰等的国家、群体等);文化(艺术、音乐、文学等的统称);', 'There is just not enough fun and frivolity in culture today.', '当今的文化恰恰是不够轻松有趣。');
INSERT INTO `words` VALUES (1468, 'cumbersome', 'ˈkʌmbəsəm', 'adj.', '大而笨重的;难以携带的;缓慢复杂的;', 'Although the machine looks cumbersome, it is actually easy to use.', '尽管这台机器看上去很笨重，操作起来却很容易。');
INSERT INTO `words` VALUES (1469, 'cumulative', 'ˈkjuːmjələtɪv', 'adj.', '(在力量或重要性方面)聚积的，积累的，渐增的;累计的;累积的;', 'It is simple pleasures, such as a walk on a sunny day, which have a cumulative effect on our mood...', '一些简单的娱乐，譬如在一个阳光明媚的日子散步，就能使我们的心情更愉快。');
INSERT INTO `words` VALUES (1470, 'cunning', 'ˈkʌnɪŋ', 'adj.', '狡猾的;奸诈的;诡诈的;', 'These disturbed kids can be cunning.', '这些心理不正常的孩子可能会很狡猾。');
INSERT INTO `words` VALUES (1471, 'cup', 'kʌp', 'n.', '杯子;一杯(的量);杯(美国用作烹饪的计量单位);', '...cups and saucers.', '杯碟');
INSERT INTO `words` VALUES (1472, 'cupboard', 'ˈkʌbəd', 'n.', '橱柜;食物柜;衣柜;', 'The kitchen cupboard was stocked with tins of soup and food.', '厨房的橱柜里备有汤罐头和食品罐头。');
INSERT INTO `words` VALUES (1473, 'curb', 'kɜːb', 'v.', '控制，抑制，限定，约束(不好的事物);', '...advertisements aimed at curbing the spread of Aids...', '旨在控制艾滋病传播的广告');
INSERT INTO `words` VALUES (1474, 'cure', 'kjʊə(r)', 'v.', '治愈，治好(病人或动物);治好(疾病);解决，了结(问题);', 'An operation finally cured his shin injury...', '手术最终治好了他胫部的伤。');
INSERT INTO `words` VALUES (1475, 'curiosity', 'ˌkjʊəriˈɒsəti', 'n.', '好奇心;求知欲;罕见而有趣之物;', 'Ryle accepted more out of curiosity than anything else...', '赖尔更多的是出于好奇才同意的。');
INSERT INTO `words` VALUES (1476, 'curious', 'ˈkjʊəriəs', 'adj.', '求知欲强的;好奇的;稀奇古怪;', 'Steve was intensely curious about the world I came from...', '史蒂夫非常想了解我以前生活的那个世界。');
INSERT INTO `words` VALUES (1477, 'curl', 'kɜːl', 'v.', '卷;(使)拳曲，鬈曲;(使)成拳曲状;', '...the little girl with blonde curls...', '长着金色卷发的小女孩');
INSERT INTO `words` VALUES (1478, 'currency', 'ˈkʌrənsi', 'n.', '通货;货币;通用;', 'Tourism is the country\'s top earner of foreign currency...', '旅游业是该国外汇创收最多的行业。');
INSERT INTO `words` VALUES (1479, 'current', 'ˈkʌrənt', 'adj.', '现时发生的;当前的;现在的;', 'Under normal conditions, the ocean currents of the tropical Pacific travel from east to west...', '在正常情况下，太平洋的热带洋流自东向西流。');
INSERT INTO `words` VALUES (1480, 'curriculum', 'kəˈrɪkjələm', 'n.', '(学校等的)全部课程;', 'There should be a broader curriculum in schools for post-16-year-old pupils...', '学校对16岁以上的学生开设的课程，范围应该更广一些。');
INSERT INTO `words` VALUES (1481, 'curse', 'kɜːs', 'n.', '咒骂语;骂人话;咒;', 'I cursed and hobbled to my feet.', '我咒骂着一瘸一拐地站起来。');
INSERT INTO `words` VALUES (1482, 'curtail', 'kɜːˈteɪl', 'v.', '限制;缩短;减缩;', 'NATO plans to curtail the number of troops being sent to the region...', '北约计划缩减派往该地区的士兵数量。');
INSERT INTO `words` VALUES (1483, 'curtain', 'ˈkɜːtn', 'n.', '窗帘;帘;幔;', 'Her bedroom curtains were drawn.', '她卧室的窗帘拉上了。');
INSERT INTO `words` VALUES (1484, 'curve', 'kɜːv', 'n.', '曲线;弧线;曲面;', '...the curve of his lips.', '他嘴唇的曲线');
INSERT INTO `words` VALUES (1485, 'cushion', 'ˈkʊʃn', 'n.', '软垫;坐垫;靠垫;', '...a velvet cushion.', '天鹅绒垫子');
INSERT INTO `words` VALUES (1486, 'custodian', 'kʌˈstəʊdiən', 'n.', '监护人;看守人;保管人;', '...the custodian of the holy shrines in Mecca and Medina.', '麦加和麦地那圣地的神殿看守人');
INSERT INTO `words` VALUES (1487, 'custom', 'ˈkʌstəm', 'n.', '风俗;习俗;(个人的)习惯，习性，惯常行为;', 'The custom of lighting the Olympic flame goes back centuries...', '点燃奥林匹克圣火的习俗可以追溯到几个世纪前。');
INSERT INTO `words` VALUES (1488, 'customary', 'ˈkʌstəməri', 'adj.', '习俗的;习惯的;(某人)特有的，独特的，典型的;', 'It is customary to offer a drink or a snack to guests...', '向客人提供一杯饮料或一份点心是一种习俗。');
INSERT INTO `words` VALUES (1489, 'customer', 'ˈkʌstəmə(r)', 'n.', '顾客;主顾;客户;', 'Our customers have very tight budgets.', '我们的顾客很会精打细算。');
INSERT INTO `words` VALUES (1490, 'customs', 'ˈkʌstəmz', 'n.', '(政府部门)海关;(港口或机场的)海关;关税;', '...components similar to those seized by British customs.', '与英国海关截获的那些物品相似的部件');
INSERT INTO `words` VALUES (1491, 'cutback', 'ˈkʌtbæk', 'n.', '削减;缩减;减少;', 'London Underground said it may have to axe 500 signalling jobs because of government cutbacks in its investment.', '伦敦地铁称由于政府削减了投资，可能将不得不削减500个信号员的岗位。');
INSERT INTO `words` VALUES (1492, 'cute', 'kjuːt', 'adj.', '可爱的;漂亮迷人的;有性吸引力的;', 'Oh, look at that dog! He\'s so cute.', '哦，看看那条狗!它太可爱了。');
INSERT INTO `words` VALUES (1493, 'cutoff', 'ˈkʌtɒf', 'n.', '截止点;界限;(供应或服务的)终止;', 'The cut-off point depends on age and length of employment...', '录用标准取决于年龄和工作年限。');
INSERT INTO `words` VALUES (1494, 'cutout', 'ˈkʌtaʊt', 'n.', '布或纸上剪下的图案花样;断流器；保险开关;', 'You\'d swear he was a cardboard cut-out except that he\'d moved his rifle...', '要不是他动了动步枪，你肯定会认为他是个硬纸板模型。');
INSERT INTO `words` VALUES (1495, 'cutting', 'ˈkʌtɪŋ', 'n.', '剪报;杂志剪辑资料;插枝，插条(从植物上截取的一段供扦插的枝条);', '...a stack of old photographs and newspaper cuttings...', '一堆旧照片和剪报');
INSERT INTO `words` VALUES (1496, 'cycle', 'ˈsaɪkl', 'n.', '自行车;摩托车;循环;', 'He cycled to Ingwold...', '他骑车去英格伍德。');
INSERT INTO `words` VALUES (1497, 'cylinder', 'ˈsɪlɪndə(r)', 'n.', '圆柱;圆柱体;圆筒;', '...a cylinder of foam...', '泡沫橡胶圆柱体');
INSERT INTO `words` VALUES (1498, 'cynical', 'ˈsɪnɪkl', 'adj.', '认为人皆自私的;愤世嫉俗的;悲观的;', '...his cynical view of the world.', '他那愤世嫉俗的世界观');
INSERT INTO `words` VALUES (1499, 'dad', 'dæd', 'n.', '爸爸;爹爹;', 'How do you feel, Dad?...', '爸爸，你感觉怎么样？');
INSERT INTO `words` VALUES (1500, 'daily', 'ˈdeɪli', 'adj.', '每日的;日常的;每个工作日的;', 'Cathay Pacific flies daily non-stop to Hong Kong from Heathrow...', '国泰航空公司每天都有从希思罗机场直飞香港的航班。');
INSERT INTO `words` VALUES (1501, 'dam', 'dæm', 'n.', '水坝;拦河坝;母兽;', '...plans to build a dam on the Danube River.', '在多瑙河上建坝的计划');
INSERT INTO `words` VALUES (1502, 'damage', 'ˈdæmɪdʒ', 'n.', '(有形的)损坏，破坏，损失;损害;伤害;', 'He maliciously damaged a car with a baseball bat...', '他用棒球棒恶意损毁了一辆汽车。');
INSERT INTO `words` VALUES (1503, 'damn', 'dæm', 'int.', '(表示厌烦、失望等)该死，他妈的，讨厌;', 'Don\'t be flippant, damn it! This is serious.', '别不当回事，该死！这可不是闹着玩儿的事。');
INSERT INTO `words` VALUES (1504, 'damp', 'dæmp', 'adj.', '潮湿的;微湿的;湿气重的;', 'Her hair was still damp...', '她的头发还有点儿湿。');
INSERT INTO `words` VALUES (1505, 'damping', 'ˈdæmpɪŋ', 'v.', '弄潮;使潮湿;抑制，减弱(感情等);', 'Her hair was still damp...', '她的头发还有点儿湿。');
INSERT INTO `words` VALUES (1506, 'dance', 'dɑːns', 'n.', '舞蹈;舞步;舞蹈(艺术);', 'Polly had never learned to dance...', '波莉从未学过跳舞。');
INSERT INTO `words` VALUES (1507, 'danger', 'ˈdeɪndʒə(r)', 'n.', '危险;风险;(坏事或不快之事发生的)可能性;', 'My friends endured tremendous danger in order to help me...', '为了帮助我，朋友们冒了极大的危险。');
INSERT INTO `words` VALUES (1508, 'dangerous', 'ˈdeɪndʒərəs', 'adj.', '有危险的;引起危险的;不安全的;', 'It\'s a dangerous stretch of road.', '那段路很不安全。');
INSERT INTO `words` VALUES (1509, 'dare', 'deə(r)', 'v.', '敢于;胆敢;激(某人做某事);', 'Since he was stuck in a lift a year ago he hasn\'t dared to get back into one...', '自从一年前被困在电梯里之后，他就再也没敢乘过电梯。');
INSERT INTO `words` VALUES (1510, 'daring', 'ˈdeərɪŋ', 'adj.', '勇敢的;敢于冒险的;', 'Bergit was probably more daring than I was...', '伯吉特可能比我更敢于冒险。');
INSERT INTO `words` VALUES (1511, 'dark', 'dɑːk', 'adj.', '黑暗的;昏暗的;阴暗的;', 'When she awoke it was evening and already dark...', '她醒来时已是晚上，天已经黑了。');
INSERT INTO `words` VALUES (1512, 'darkness', 'ˈdɑːknəs', 'n.', '黑暗;阴暗;漆黑;', 'When she awoke it was evening and already dark...', '她醒来时已是晚上，天已经黑了。');
INSERT INTO `words` VALUES (1513, 'darling', 'ˈdɑːlɪŋ', 'n.', '亲爱的;宝贝;亲切友好的人;', 'Thank you, darling...', '谢谢你，亲爱的。');
INSERT INTO `words` VALUES (1514, 'dart', 'dɑːt', 'n.', '镖;飞镖;掷镖游戏(常见于英国酒吧里);', 'Ingrid darted across the deserted street...', '英格丽德飞奔过空无一人的街道。');
INSERT INTO `words` VALUES (1515, 'dash', 'dæʃ', 'n.', '猛冲;突进;急奔;', 'Suddenly she dashed down to the cellar...', '她突然冲下地窖。');
INSERT INTO `words` VALUES (1516, 'dashed', 'dæʃt', 'adj.', '(表示强调或不耐烦)该死的，讨厌的;', 'Suddenly she dashed down to the cellar...', '她突然冲下地窖。');
INSERT INTO `words` VALUES (1517, 'database', 'ˈdeɪtəbeɪs', 'n.', '(储存在计算机中的)数据库;', 'They maintain a database of hotels that cater for businesswomen.', '他们维护着一个服务于商界女性的酒店资料库。');
INSERT INTO `words` VALUES (1518, 'date', 'deɪt', 'n.', '日期;日子;年代;', 'What\'s the date today?...', '今天是几号？');
INSERT INTO `words` VALUES (1519, 'daughter', 'ˈdɔːtə(r)', 'n.', '女儿;(某地、某国等的)妇女;', '...Flora and her daughter Catherine.', '弗洛拉和她的女儿凯瑟琳');
INSERT INTO `words` VALUES (1520, 'dawn', 'dɔːn', 'n.', '黎明;拂晓;破晓;', 'Nancy woke at dawn.', '拂晓时分南希醒了。');
INSERT INTO `words` VALUES (1521, 'daylight', 'ˈdeɪlaɪt', 'n.', '日光;', 'It was still daylight but all the cars had their headlights on...', '还是大白天，但所有汽车的前灯都亮起来了。');
INSERT INTO `words` VALUES (1522, 'daytime', 'ˈdeɪtaɪm', 'n.', '白天;白昼;日间;', 'In the daytime he stayed up in his room, sleeping, or listening to music...', '他白天呆在自己的屋子里睡觉或听音乐。');
INSERT INTO `words` VALUES (1523, 'dazzle', 'ˈdæzl', 'v.', '(强光等)使目眩，使眼花;(美貌、技能等)使倾倒，使赞叹不已，使眼花缭乱;', 'George dazzled her with his knowledge of the world...', '乔治的洞察世情令她叹服。');
INSERT INTO `words` VALUES (1524, 'deactivate', 'ˌdiːˈæktɪveɪt', 'v.', '使(仪器等)停止工作;使(化学过程)灭活化(或减活化、钝化);', 'Russia is deactivating some of its deadliest missiles.', '俄罗斯正在拆除其一些最危险的导弹。');
INSERT INTO `words` VALUES (1525, 'dead', 'ded', 'adj.', '死的;失去生命的;枯萎的;', 'Her husband\'s been dead a year now...', '她的丈夫已经去世一年了。');
INSERT INTO `words` VALUES (1526, 'deadline', 'ˈdedlaɪn', 'n.', '最后期限;截止日期;', 'We were not able to meet the deadline because of manufacturing delays...', '因为制造方面的延误，我们没能赶上最后期限。');
INSERT INTO `words` VALUES (1527, 'deadlock', 'ˈdedlɒk', 'n.', '僵持;僵局;相持不下;', 'They called for a compromise on all sides to break the deadlock in the world trade talks...', '他们呼吁各方作出让步以打破世界贸易谈判中的僵局。');
INSERT INTO `words` VALUES (1528, 'deadly', 'ˈdedli', 'adj.', '(可能)致命的，致死的;极度的;十足的;', 'He was acquitted on charges of assault with a deadly weapon.', '他被判用致命武器袭击他人的罪名不成立。');
INSERT INTO `words` VALUES (1529, 'deaf', 'def', 'adj.', '聋的;耳聋的人;聋子;', 'She is now profoundly deaf.', '她现在已经严重失聪了。');
INSERT INTO `words` VALUES (1530, 'deafen', 'ˈdefn', 'v.', '使震得耳朵发聋;使聋;使听不见;', 'The noise of the typewriters deafened her.', '打字机的噪声吵得她什么都听不见。');
INSERT INTO `words` VALUES (1531, 'dealer', 'ˈdiːlə(r)', 'n.', '交易商;贸易商;贩毒者;', '...an antique dealer.', '古董商');
INSERT INTO `words` VALUES (1532, 'dean', 'diːn', 'n.', '座堂主任牧师;(乡间主管若干教堂牧师的)主任牧师;(大学的)学院院长，系主任;', 'She was Dean of the Science faculty at Sophia University.', '她是上智大学科学院的院长。');
INSERT INTO `words` VALUES (1533, 'dear', 'dɪə(r)', 'adj.', '亲爱的;宝贵的;珍视的;', 'Mrs Cavendish is a dear friend of mine...', '卡文迪什夫人是我的一位亲密朋友。');
INSERT INTO `words` VALUES (1534, 'dearly', 'ˈdɪəli', 'adv.', '非常;很;代价极大地;', 'She loved her father dearly.', '她深爱她的父亲。');
INSERT INTO `words` VALUES (1535, 'dearth', 'dɜːθ', 'n.', '缺乏;不足;', '...the dearth of good fiction by English authors.', '英国作家写的优秀小说少之又少');
INSERT INTO `words` VALUES (1536, 'death', 'deθ', 'n.', '死;死亡;生命的终止;', '1.5 million people are in immediate danger of death from starvation.', '150 万人正面临着被饿死的急迫危险。');
INSERT INTO `words` VALUES (1537, 'debate', 'dɪˈbeɪt', 'n.', '(在公共集会上或议会里就某问题进行的、常以表决结束的)辩论;(各自发表不同意见的)争论，辩论，讨论;', 'An intense debate is going on within the Israeli government...', '以色列政府内部正在进行激烈的辩论。');
INSERT INTO `words` VALUES (1538, 'debit', 'ˈdebɪt', 'n.', '借记;借方;收方;', 'We will always confirm the revised amount to you in writing before debiting your account.', '在记入您账户的借方之前，我们总是会就发生变动的数额书面通知您。');
INSERT INTO `words` VALUES (1539, 'debris', 'ˈdebriː', 'n.', '残骸;碎片;破片;', 'I stood at the foot of the collapsed tower and watched the rescue workers sifting through the debris...', '我站在倒塌的高塔脚下，看着救援人员在瓦砾堆中搜寻。');
INSERT INTO `words` VALUES (1540, 'debt', 'det', 'n.', '借款;欠款;债务;', 'Three years later, he is still paying off his debts...', '都3年了，他仍在偿还债务。');
INSERT INTO `words` VALUES (1541, 'debtor', 'ˈdetə(r)', 'n.', '债务人;借方;', '...the situation of debtor countries.', '负债国的情况');
INSERT INTO `words` VALUES (1542, 'debug', 'ˌdiːˈbʌɡ , ˈdiːbʌɡ', 'v.', '排错;调试;除错;', 'The production lines ground to a halt for hours while technicians tried to debug software.', '生产线停工达数小时之久，在此期间技术人员试图纠除软件错误。');
INSERT INTO `words` VALUES (1543, 'decade', 'ˈdekeɪd', 'n.', '十年，十年期(尤指一个年代);', '...the last decade of the nineteenth century.', '19 世纪的最后 10 年');
INSERT INTO `words` VALUES (1544, 'decay', 'dɪˈkeɪ', 'n.', '腐烂;腐朽;(社会、机构、制度等的)衰败，衰退，衰落;', 'The bodies buried in the fine ash slowly decayed...', '埋在灰烬里的尸体慢慢地腐烂了。');
INSERT INTO `words` VALUES (1545, 'deceit', 'dɪˈsiːt', 'n.', '欺骗，欺诈(行为);诡计;', 'They have been involved in a campaign of deceit.', '他们参与了一系列的欺骗活动。');
INSERT INTO `words` VALUES (1546, 'deceive', 'dɪˈsiːv', 'v.', '欺骗;蒙骗;诓骗;', 'He has deceived and disillusioned us all...', '他欺骗了大家，让我们失望至极。');
INSERT INTO `words` VALUES (1547, 'decelerate', 'ˌdiːˈseləreɪt', 'v.', '(使)减速行驶;降低运行速度;(使)减缓，变慢;', '...the sensation of the train decelerating.', '列车正在减速的感觉');
INSERT INTO `words` VALUES (1548, 'december', 'dɪˈsembə(r)', 'n.', '十二月;', '...a bright morning in mid-December...', '12 月中旬的一个明媚的早晨');
INSERT INTO `words` VALUES (1549, 'decent', 'ˈdiːsnt', 'adj.', '像样的;相当不错的;尚好的;', 'Nearby is a village with a decent pub...', '附近的村子里有一家还不错的酒馆。');
INSERT INTO `words` VALUES (1550, 'deceptive', 'dɪˈseptɪv', 'adj.', '欺骗性的;误导的;骗人的;', 'Appearances can be deceptive.', '外表可能是靠不住的。');
INSERT INTO `words` VALUES (1551, 'decibel', 'ˈdesɪbel', 'n.', '分贝(声音强度的单位);', 'Continuous exposure to sound above 80 decibels could be harmful.', '持续暴露在强度高于80分贝的噪声中可能有害。');
INSERT INTO `words` VALUES (1552, 'decide', 'dɪˈsaɪd', 'v.', '对…作出抉择;决定;选定;', 'She decided to do a secretarial course...', '她决定修一门文秘课程。');
INSERT INTO `words` VALUES (1553, 'decided', 'dɪˈsaɪdɪd', 'adj.', '明显的;明白无误的;确实无疑的;', 'Her ignorance of the area put her at a decided disadvantage...', '她对该地区一无所知，这显然对她不利。');
INSERT INTO `words` VALUES (1554, 'decimal', 'ˈdesɪml', 'adj.', '十进位的;小数的;', '...the decimal system of metric weights and measures...', '公制度量衡的十进制');
INSERT INTO `words` VALUES (1555, 'decipher', 'dɪˈsaɪfə(r)', 'v.', '破译，辨认(难认、难解的东西);', 'I\'m still no closer to deciphering the code.', '我还是无法破译该密码。');
INSERT INTO `words` VALUES (1556, 'decision', 'dɪˈsɪʒn', 'n.', '(作出的)决定，抉择;决断(力);果断;', 'A decision was taken to discipline Marshall...', '已作出对马歇尔进行处罚的决定。');
INSERT INTO `words` VALUES (1557, 'decisive', 'dɪˈsaɪsɪv', 'adj.', '决定性的;关键的;坚决的;', '...his decisive victory in the presidential elections...', '他在总统大选中取得的压倒性胜利');
INSERT INTO `words` VALUES (1558, 'deck', 'dek', 'n.', '甲板;舱面;(船或公共汽车的)一层，层面;', '...sitting on the top deck of the number 13 bus.', '坐在 13 路公共汽车的顶层');
INSERT INTO `words` VALUES (1559, 'declaration', 'ˌdekləˈreɪʃn', 'n.', '公告;宣告;宣言;', 'They will sign the declaration tomorrow...', '他们明天将签署这份公告。');
INSERT INTO `words` VALUES (1560, 'declare', 'dɪˈkleə(r)', 'v.', '公布;宣布;宣告;', 'Speaking outside Ten Downing Street, she declared that she would fight on...', '她在英国首相官邸外发表讲话时宣称她将继续斗争下去。');
INSERT INTO `words` VALUES (1561, 'declared', 'dɪˈkleəd', 'adj.', '公开宣布(或声明、表态)的;', 'Speaking outside Ten Downing Street, she declared that she would fight on...', '她在英国首相官邸外发表讲话时宣称她将继续斗争下去。');
INSERT INTO `words` VALUES (1562, 'decline', 'dɪˈklaɪn', 'n.', '(数量、价值、质量等的)减少，下降，衰落，衰退;', 'The number of staff has declined from 217,000 to 114,000...', '员工人数从 21.7万减少到了11.4万。');
INSERT INTO `words` VALUES (1563, 'decode', 'ˌdiːˈkəʊd', 'v.', '解(码);破译(尤指密码);译解(电子信号);', 'All he had to do was decode it and pass it over...', '他需要做的就是将它破译然后转给他人。');
INSERT INTO `words` VALUES (1564, 'decompose', 'ˌdiːkəmˈpəʊz', 'v.', '腐烂;(使)分解;', '...a dead body found decomposing in a wood...', '在树林里发现的一具正在腐烂的尸体');
INSERT INTO `words` VALUES (1565, 'decompression', 'ˌdiːkəmˈpreʃn', 'n.', '减压;卸压;解压缩(将压缩的东西恢复到原位);', 'Decompression blew out a window in the plane.', '减压导致飞机的一个窗户爆裂。');
INSERT INTO `words` VALUES (1566, 'decorate', 'ˈdekəreɪt', 'v.', '装饰;装潢;粉刷;', 'He decorated his room with pictures of all his favorite sports figures...', '他用自己最喜欢的几位体育明星的照片装饰房间。');
INSERT INTO `words` VALUES (1567, 'decorative', 'ˈdekərətɪv', 'adj.', '装饰性的;作装饰用的;', 'The curtains are for purely decorative purposes and do not open or close.', '那些窗帘纯粹是作装饰用的，不能拉开或合上。');
INSERT INTO `words` VALUES (1568, 'decree', 'dɪˈkriː', 'n.', '法令;政令;(法院的)裁定，判决;', 'In July he issued a decree ordering all unofficial armed groups in the country to disband...', '他于 7 月份下令解散国内所有的非正式武装组织。');
INSERT INTO `words` VALUES (1569, 'decrepit', 'dɪˈkrepɪt', 'adj.', '衰老的;老朽的;破旧的;', 'The film had been shot in a decrepit old police station.', '该影片是在一所破旧不堪的警察局里拍摄的。');
INSERT INTO `words` VALUES (1570, 'dedicate', 'ˈdedɪkeɪt', 'v.', '把…奉献给;(在书、音乐或作品的前部)题献词;为…举行奉献典礼;', 'Back on the island, he dedicated himself to politics...', '回到岛上后，他便投身于政治。');
INSERT INTO `words` VALUES (1571, 'dedicated', 'ˈdedɪkeɪtɪd', 'adj.', '献身的：专心致志的;一心一意的;专用的;', 'Her great-grandfather had clearly been a dedicated and stoical traveller.', '她的曾祖父显然是一位非常投入而且坚忍不拔的旅行爱好者。');
INSERT INTO `words` VALUES (1572, 'deduce', 'dɪˈdjuːs', 'v.', '推论;推断;演绎;', 'Alison had cleverly deduced that I was the author of the letter.', '艾莉森已经聪明地推断出那封信就是我写的。');
INSERT INTO `words` VALUES (1573, 'deduct', 'dɪˈdʌkt', 'v.', '(从总量中)扣除，减去;', 'The company deducted this payment from his compensation...', '公司从他获得的赔偿金中扣去了这笔付款。');
INSERT INTO `words` VALUES (1574, 'deduction', 'dɪˈdʌkʃn', 'n.', '演绎;推论;推理;', 'It was a pretty astute deduction...', '那是一个颇为敏锐的推论。');
INSERT INTO `words` VALUES (1575, 'deed', 'diːd', 'n.', '行为;行动;(尤指房产)契约，证书;', 'His heroic deeds were celebrated in every corner of India.', '他的英勇事迹在印度各地广为传颂。');
INSERT INTO `words` VALUES (1576, 'deem', 'diːm', 'v.', '认为;视为;相信;', 'French and German were deemed essential...', '法语和德语被认为是必不可少的。');
INSERT INTO `words` VALUES (1577, 'deep', 'diːp', 'adj.', '深的;厚的;纵深的;', 'The water is very deep and mysterious-looking...', '水看上去幽深叵测。');
INSERT INTO `words` VALUES (1578, 'deepen', 'ˈdiːpən', 'v.', '(使情感、感觉等)加强，变强烈;(使)变糟，恶化，严重;(使)变深;', 'If this is not stopped, the financial crisis will deepen...', '如果不对其加以阻止，财政危机将会加剧。');
INSERT INTO `words` VALUES (1579, 'deeply', 'ˈdiːpli', 'adv.', '很;非常;极其;', 'The water is very deep and mysterious-looking...', '水看上去幽深叵测。');
INSERT INTO `words` VALUES (1580, 'defeat', 'dɪˈfiːt', 'v.', '击败;战胜;使困惑;', 'His guerrillas defeated the colonial army in 1954...', '1954 年他领导的游击队打败了殖民军队。');
INSERT INTO `words` VALUES (1581, 'defecation', 'ˌdefəˈkeɪʃn', 'n.', '澄清，净化，通便;', 'Animals defecate after every meal.', '动物每次进食后都会排粪。');
INSERT INTO `words` VALUES (1582, 'defective', 'dɪˈfektɪv', 'adj.', '有缺点的;有缺陷的;有毛病的;', 'Her sight was becoming defective...', '她的视力开始出现问题。');
INSERT INTO `words` VALUES (1583, 'defend', 'dɪˈfend', 'v.', '防御;保护;保卫;', 'Every man who could fight was now committed to defend the ridge...', '每个能够参加战斗的男子现在都决心要保卫这条山脊。');
INSERT INTO `words` VALUES (1584, 'defer', 'dɪˈfɜː(r)', 'v.', '推迟;延缓;展期;', 'Customers often defer payment for as long as possible...', '顾客常常会尽可能地拖延付款。');
INSERT INTO `words` VALUES (1585, 'defiance', 'dɪˈfaɪəns', 'n.', '违抗;反抗;拒绝服从;', '...his courageous defiance of the government.', '他对政府的勇敢挑战');
INSERT INTO `words` VALUES (1586, 'deficiency', 'dɪˈfɪʃnsi', 'n.', '缺乏;缺少;不足;', 'They did blood tests on him for signs of vitamin deficiency...', '他们给他验了血，以检查是否有维生素缺乏的迹象。');
INSERT INTO `words` VALUES (1587, 'deficit', 'ˈdefɪsɪt', 'n.', '赤字;逆差;亏损;', 'They\'re ready to cut the federal budget deficit for the next fiscal year.', '他们已准备好削减下一财年的联邦预算赤字。');
INSERT INTO `words` VALUES (1588, 'defile', 'dɪˈfaɪl', 'v.', '弄脏;玷污;糟蹋;', 'He had defiled the sacred name of the Holy Prophet.', '他亵渎了这位神圣先知的圣名。');
INSERT INTO `words` VALUES (1589, 'definable', 'dɪˈfaɪnəbl', 'adj.', '可定义的;可界定的;可辨别的;', 'Many suffered from a definable alcohol, drug, or mental disorder.', '许多人都患有能够辨识的酗酒、吸毒或心理障碍。');
INSERT INTO `words` VALUES (1590, 'define', 'dɪˈfaɪn', 'v.', '解释(词语)的含义;给(词语)下定义;阐明;', 'We were unable to define what exactly was wrong with him...', '我们说不清楚他到底哪里不对劲。');
INSERT INTO `words` VALUES (1591, 'definite', 'ˈdefɪnət', 'adj.', '肯定的;确定的;不会改变的;', 'It\'s too soon to give a definite answer...', '要给出一个明确的答复，现在还为时过早。');
INSERT INTO `words` VALUES (1592, 'definitely', 'ˈdefɪnətli', 'adv.', '肯定;没问题;当然;', 'I\'m definitely going to get in touch with these people...', '我肯定会跟这些人取得联系。');
INSERT INTO `words` VALUES (1593, 'definition', 'ˌdefɪˈnɪʃn', 'n.', '(尤指词典里的词或短语的)释义，解释;定义;清晰度;', 'There is no general agreement on a standard definition of intelligence...', '“智力”一词目前还没有一个普遍认可的标准释义。');
INSERT INTO `words` VALUES (1594, 'definitive', 'dɪˈfɪnətɪv', 'adj.', '最后的;决定性的;不可更改的;', 'No one has come up with a definitive answer as to why this should be so...', '对于为什么应该是这样，还没有人想出最终确定的答案。');
INSERT INTO `words` VALUES (1595, 'deflate', 'dɪˈfleɪt', 'v.', '放掉(轮胎、气球等的)气;(使)瘪下来;使泄气;', 'Like any actor he can be self-centred but I think I\'ve worked out how to deflate him...', '跟其他演员一样，他有时也会以自我为中心，但我想自己已经找到了如何挫他锐气的办法。');
INSERT INTO `words` VALUES (1596, 'deflect', 'dɪˈflekt', 'v.', '(尤指击中某物后)偏斜，转向，使偏斜，使转向;转移;引开;', 'Cage changed his name to deflect accusations of nepotism...', '凯奇改了名字以避免别人指责他搞裙带关系。');
INSERT INTO `words` VALUES (1597, 'deflection', 'dɪˈflekʃn', 'n.', '(尤指击中某物后)突然转向，偏斜，偏离;', '...the deflection of light as it passes through the slits in the grating.', '光线通过光栅狭缝时的偏转');
INSERT INTO `words` VALUES (1598, 'deform', 'dɪˈfɔːm', 'v.', '改变…的外形;损毁…的形状;使成畸形;', 'Bad rheumatoid arthritis deforms limbs...', '严重的类风湿性关节炎会造成四肢变形。');
INSERT INTO `words` VALUES (1599, 'deformation', 'ˌdiːfɔːˈmeɪʃn', 'n.', '损形;变形;畸形;', 'Bad rheumatoid arthritis deforms limbs...', '严重的类风湿性关节炎会造成四肢变形。');
INSERT INTO `words` VALUES (1600, 'defraud', 'dɪˈfrɔːd', 'v.', '骗取，诈取(…的钱财);', 'He pleaded guilty to charges of conspiracy to defraud the government...', '他承认了阴谋诈骗政府的罪名。');
INSERT INTO `words` VALUES (1601, 'deft', 'deft', 'adj.', '熟练的;灵巧的;机敏的;', 'With a deft flick of his foot, Mr Worth tripped one of the raiders up.', '沃思先生机敏地把脚一伸，把其中一个袭击者给绊倒了。');
INSERT INTO `words` VALUES (1602, 'defy', 'dɪˈfaɪ', 'v.', '违抗;反抗;蔑视;', 'This was the first (and last) time that I dared to defy my mother...', '这是我第一次(也是最后一次)胆敢反抗我的母亲。');
INSERT INTO `words` VALUES (1603, 'degradation', 'ˌdeɡrəˈdeɪʃn', 'n.', '堕落;落泊，潦倒(的境况);毁坏，恶化(过程);', 'They were sickened by the scenes of misery and degradation they found...', '目睹困苦、潦倒的景象，他们感到心里极不舒服。');
INSERT INTO `words` VALUES (1604, 'degrade', 'dɪˈɡreɪd', 'v.', '降低…身份;侮辱…的人格;使受屈辱;', '...the notion that pornography degrades women...', '认为色情作品有辱女性尊严的观念');
INSERT INTO `words` VALUES (1605, 'degree', 'dɪˈɡriː', 'n.', '度，度数(角的量度单位);度数(温度单位);程度;', 'These man-made barriers will ensure a very high degree of protection...', '这些人造屏障将会确保提供高度的保护。');
INSERT INTO `words` VALUES (1606, 'delay', 'dɪˈleɪ', 'n.', '延迟(或耽搁、拖延)的时间;延期;耽搁;', 'For sentimental reasons I wanted to delay my departure until June...', '因为感情方面的原因，我想把离开的日期延迟至6月。');
INSERT INTO `words` VALUES (1607, 'delectable', 'dɪˈlektəbl', 'adj.', '美味可口的;香甜的;宜人的;', '...delectable wine.', '美酒');
INSERT INTO `words` VALUES (1608, 'delegation', 'ˌdelɪˈɡeɪʃn', 'n.', '代表团;委托;委派;', '...a delegation from Somaliland...', '来自索马里兰的代表团');
INSERT INTO `words` VALUES (1609, 'delete', 'dɪˈliːt', 'v.', '删去;删除;', 'He also deleted files from the computer system...', '他还从计算机系统里删除了文件。');
INSERT INTO `words` VALUES (1610, 'deletion', 'dɪˈliːʃən', 'n.', '删除;', 'He also deleted files from the computer system...', '他还从计算机系统里删除了文件。');
INSERT INTO `words` VALUES (1611, 'delicate', 'ˈdelɪkət', 'adj.', '易损的;易碎的;脆弱的;', 'He had delicate hands.', '他有一双纤细的手。');
INSERT INTO `words` VALUES (1612, 'delicious', 'dɪˈlɪʃəs', 'adj.', '美味的;可口的;芬芳的;', 'There\'s always a wide selection of delicious meals to choose from...', '总是有很多美味可口的饭菜可供选择。');
INSERT INTO `words` VALUES (1613, 'delight', 'dɪˈlaɪt', 'n.', '高兴;愉快;快乐;', 'Throughout the house, the views are a constant source of surprise and delight...', '从房子各处看到的景色不断给人以惊奇和欣喜。');
INSERT INTO `words` VALUES (1614, 'delighted', 'dɪˈlaɪtɪd', 'adj.', '高兴的;愉快的;快乐的;', 'I know Frank will be delighted to see you...', '我知道弗兰克见到你会很高兴的。');
INSERT INTO `words` VALUES (1615, 'delightful', 'dɪˈlaɪtfl', 'adj.', '使人快乐的;令人愉快的;宜人的;', 'It was the most delightful garden I had ever seen...', '这是我所见过的最惹人喜爱的花园。');
INSERT INTO `words` VALUES (1616, 'delirium', 'dɪˈlɪriəm', 'n.', '谵妄，神志失常，说胡话(常由疾病引起);', 'In her delirium, she had fallen to the floor several times.', '她在神志不清的状态下几次摔倒在地上。');
INSERT INTO `words` VALUES (1617, 'deliver', 'dɪˈlɪvə(r)', 'v.', '递送;传送;交付;', 'The Canadians plan to deliver more food to southern Somalia...', '加拿大计划向索马里南部地区运送更多的食品。');
INSERT INTO `words` VALUES (1618, 'delivery', 'dɪˈlɪvəri', 'n.', '传送;递送;交付;', 'Please allow 28 days for delivery...', '递送将需要 28 天的时间。');
INSERT INTO `words` VALUES (1619, 'delta', 'ˈdeltə', 'n.', '希腊字母表的第4个字母;三角洲;', '...the Mississippi delta.', '密西西比河三角洲');
INSERT INTO `words` VALUES (1620, 'delude', 'dɪˈluːd', 'v.', '欺骗;哄骗;', 'The President was deluding himself if he thought he was safe from such action...', '总统如果认为这样的行为不会威胁到他，那他就是在自欺欺人。');
INSERT INTO `words` VALUES (1621, 'deluge', 'ˈdeljuːdʒ', 'n.', '暴雨;大雨;洪水;', 'A deluge of manuscripts began to arrive in the post...', '大量的手稿开始通过邮递涌来。');
INSERT INTO `words` VALUES (1622, 'demand', 'dɪˈmɑːnd', 'n.', '(坚决的)要求;所需之物;(尤指困难、使人劳累、令人烦恼等的)要求;', 'Mr Byers last night demanded an immediate explanation from the Education Secretary...', '昨晚拜尔斯先生要求教育大臣立刻作出解释。');
INSERT INTO `words` VALUES (1623, 'demanding', 'dɪˈmɑːndɪŋ', 'adj.', '要求高的;需要高技能(或耐性等)的;费力的;', 'He found he could no longer cope with his demanding job...', '他发现自己已无力应对那份劳神费力的工作了。');
INSERT INTO `words` VALUES (1624, 'demo', 'ˈdeməʊ', 'n.', '试样唱片;录音样带;', '...an anti-racist demo.', '反种族主义示威');
INSERT INTO `words` VALUES (1625, 'democracy', 'dɪˈmɒkrəsi', 'n.', '民主政体;民主制度;民主国家;', '...the spread of democracy in Eastern Europe.', '民主制度在东欧的传播');
INSERT INTO `words` VALUES (1626, 'democrat', 'ˈdeməkræt', 'n.', '民主主义者;(美国)民主党党员，民主党人，民主党支持者;', '...a senior Christian Democrat...', '资深的基督教民主党党员');
INSERT INTO `words` VALUES (1627, 'democratic', 'ˌdeməˈkrætɪk', 'adj.', '民主的;民主政体的;民主制度的;', 'Bolivia returned to democratic rule in 1982, after a series of military governments.', '在经历了数届军人政府后，玻利维亚于 1982 年恢复了民主政权。');
INSERT INTO `words` VALUES (1628, 'demolish', 'dɪˈmɒlɪʃ', 'v.', '拆毁，拆除(建筑物);(意外)毁坏，破坏;推翻，驳倒(观点或理论);', 'A storm moved directly over the island, demolishing buildings and flooding streets...', '暴风雨径直席卷该岛，摧毁了建筑，淹没了街道。');
INSERT INTO `words` VALUES (1629, 'demonstrate', 'ˈdemənstreɪt', 'v.', '证明;证实;论证;', 'The study also demonstrated a direct link between obesity and mortality...', '该研究还表明了肥胖症和死亡率之间存在直接的联系。');
INSERT INTO `words` VALUES (1630, 'demonstration', 'ˌdemənˈstreɪʃn', 'n.', '集会示威;游行示威;示范;', 'Riot police broke up a demonstration by students.', '防暴警察驱散了示威的学生。');
INSERT INTO `words` VALUES (1631, 'denial', 'dɪˈnaɪəl', 'n.', '否认;否定;拒绝给予，剥夺(应有的权利);', 'Despite official denials, the rumours still persist...', '尽管官方予以否认，但谣言仍未平息。');
INSERT INTO `words` VALUES (1632, 'denomination', 'dɪˌnɒmɪˈneɪʃn', 'n.', '(基督教)教派，宗派;(尤指钱的)面额，面值;', 'Acceptance of women preachers varies greatly from denomination to denomination.', '在接受女性传教士的问题上，不同教派的态度大相径庭。');
INSERT INTO `words` VALUES (1633, 'denote', 'dɪˈnəʊt', 'v.', '标志;预示;象征;', 'Red eyes denote strain and fatigue...', '眼睛充血表明压力过大、身心疲惫。');
INSERT INTO `words` VALUES (1634, 'denounce', 'dɪˈnaʊns', 'v.', '谴责;指责;斥责;', 'The letter called for trade union freedom and civil rights, but did not openly denounce the regime...', '这封信呼吁保障工会自由和公民权利，但没有公开谴责该政权。');
INSERT INTO `words` VALUES (1635, 'dense', 'dens', 'adj.', '密集的;稠密的;浓密的;', 'Where Bucharest now stands, there once was a large, dense forest...', '布加勒斯特的所在地过去曾是一大片茂密的森林。');
INSERT INTO `words` VALUES (1636, 'density', 'ˈdensəti', 'n.', '密集;稠密;密度;', '...a law which restricts the density of housing...', '限制房屋密集程度的法规');
INSERT INTO `words` VALUES (1637, 'dental', 'ˈdentl', 'adj.', '牙齿的;牙科的;齿音的;', 'You can get free dental treatment.', '你可以得到免费的牙齿治疗。');
INSERT INTO `words` VALUES (1638, 'dentist', 'ˈdentɪst', 'n.', '牙科医生;牙科诊所;', 'Visit your dentist twice a year for a check-up.', '每年去牙医处做两次牙齿检查。');
INSERT INTO `words` VALUES (1639, 'deny', 'dɪˈnaɪ', 'v.', '否认;否定;拒绝承认;', 'She denied both accusations...', '她对两项指控均予以否认。');
INSERT INTO `words` VALUES (1640, 'depart', 'dɪˈpɑːt', 'v.', '离开;离去;起程;', 'Our tour departs from Heathrow Airport on 31 March and returns 16 April...', '我们这次旅行于 3月 31 号从希思罗机场出发，4 月 16 号返回。');
INSERT INTO `words` VALUES (1641, 'department', 'dɪˈpɑːtmənt', 'n.', '部;司;局;', '...the U.S. Department of Health, Education and Welfare...', '美国卫生、教育和福利部');
INSERT INTO `words` VALUES (1642, 'departure', 'dɪˈpɑːtʃə(r)', 'n.', '离开;起程;出发;', '...the President\'s departure for Helsinki...', '总统动身前往赫尔辛基');
INSERT INTO `words` VALUES (1643, 'depend', 'dɪˈpend', 'v.', '取决(于);有赖(于);依靠;依赖;指望;信赖;', 'The cooking time needed depends on the size of the potato...', '所需的烹饪时间取决于土豆的大小。');
INSERT INTO `words` VALUES (1644, 'dependence', 'dɪˈpendəns', 'n.', '(生存或成功必需的)依靠，依赖，依存;瘾;相关(性);', '...the city\'s traditional dependence on tourism...', '该市传统上对旅游业的依赖');
INSERT INTO `words` VALUES (1645, 'dependent', 'dɪˈpendənt', 'adj.', '依靠的;依赖的;有瘾的;', 'The local economy is overwhelmingly dependent on oil and gas extraction...', '当地经济过于依赖石油和天然气的开采。');
INSERT INTO `words` VALUES (1646, 'depict', 'dɪˈpɪkt', 'v.', '描绘;描画;描写;', '...a gallery of pictures depicting Nelson\'s most famous battles.', '描绘纳尔逊指挥的那些最著名战役的一组画作');
INSERT INTO `words` VALUES (1647, 'deplete', 'dɪˈpliːt', 'v.', '大量减少;耗尽;使枯竭;', '...substances that deplete the ozone layer...', '大量消耗臭氧层的物质');
INSERT INTO `words` VALUES (1648, 'depletion', 'dɪˈpliːʃən', 'n.', '弄空，竭尽，耗尽，（资源）耗减;缺乏；贫化；亏损;空虚；干枯；涸竭;', '...substances that deplete the ozone layer...', '大量消耗臭氧层的物质');
INSERT INTO `words` VALUES (1649, 'deplore', 'dɪˈplɔː(r)', 'v.', '公开谴责;强烈反对;', 'He\'s a judo black belt but he says he deplores violence...', '他是一名柔道黑带选手，但他说自己强烈反对暴力。');
INSERT INTO `words` VALUES (1650, 'deploy', 'dɪˈplɔɪ', 'v.', '部署，调度(军队或武器);有效地利用;调动;', 'The president said he had no intention of deploying ground troops.', '总统称并不打算部署地面部队。');
INSERT INTO `words` VALUES (1651, 'deposit', 'dɪˈpɒzɪt', 'n.', '订金;订钱;押金;', 'A £50 deposit is required when ordering, and the balance is due upon delivery.', '订货时需要交50英镑的订金，货到后余款结清。');
INSERT INTO `words` VALUES (1652, 'deposition', 'ˌdepəˈzɪʃn', 'n.', '沉积(物);沉淀(物);淤积(物);', 'The jury heard 200 pages of depositions.', '陪审团听取了长达 200 页的书面证词。');
INSERT INTO `words` VALUES (1653, 'depreciation', 'dɪˌpriːʃɪˈeɪʃən', 'n.', '贬值，跌价；折旧;轻视，蔑视，藐视；贬低；低估;磨损；损耗;', 'Countries may also find their currency is depreciating in foreign markets.', '一些国家也可能会发现自己的货币正在国外市场上贬值。');
INSERT INTO `words` VALUES (1654, 'depress', 'dɪˈpres', 'v.', '使抑郁;使沮丧;使消沉;', 'I must admit the state of the country depresses me...', '我必须承认国家的现状让我倍感沮丧。');
INSERT INTO `words` VALUES (1655, 'depression', 'dɪˈpreʃn', 'n.', '抑郁症;精神忧郁;抑郁;', 'Mr Thomas was suffering from depression...', '托马斯先生患有抑郁症。');
INSERT INTO `words` VALUES (1656, 'deprive', 'dɪˈpraɪv', 'v.', '剥夺;使丧失;使不能享有;', 'The disintegration of the Soviet Union deprived western intelligence agencies of their main enemies...', '苏联解体使西方情报机构丧失了主敌。');
INSERT INTO `words` VALUES (1657, 'depth', 'depθ', 'n.', '向下的距离;深(度);纵深;', 'The smaller lake ranges from five to fourteen feet in depth...', '小一些的湖深度从 5 英尺到 14 英尺不等。');
INSERT INTO `words` VALUES (1658, 'deputy', 'ˈdepjuti', 'n.', '副手;副职;代理;', '...Jack Lang, France\'s minister for culture, and his deputy, Catherine Tasca.', '法国文化部长杰克·朗和他的副部长凯瑟琳·塔斯卡');
INSERT INTO `words` VALUES (1659, 'derail', 'dɪˈreɪl', 'v.', '(使)脱轨，出轨;', 'The present wave of political killings is the work of people trying to derail peace talks.', '当前的这一系列政治谋杀乃是那些想阻挠和平谈判的人所为。');
INSERT INTO `words` VALUES (1660, 'derelict', 'ˈderəlɪkt', 'adj.', '荒废的;被弃置的;破旧的;', 'Her body was found dumped in a derelict warehouse less than a mile from her home.', '在距离她家不到一英里的一座废弃仓库里发现了她遭弃的尸体。');
INSERT INTO `words` VALUES (1661, 'derivation', 'ˌderɪˈveɪʃn', 'n.', '(尤指词语的)起源，由来，派生;', 'The derivation of its name is obscure...', '其名字的由来鲜为人知。');
INSERT INTO `words` VALUES (1662, 'derive', 'dɪˈraɪv', 'v.', '获得;取得;得到;(使)起源;(使)产生;', 'Mr Ying is one of those happy people who derive pleasure from helping others.', '英先生属于那种助人为乐的快活人。');
INSERT INTO `words` VALUES (1663, 'descend', 'dɪˈsend', 'v.', '下来;下去;下降;', 'Things are cooler and more damp as we descend to the cellar...', '当我们往下走去地窖时，四周愈见阴冷潮湿。');
INSERT INTO `words` VALUES (1664, 'descendant', 'dɪˈsendənt', 'n.', '后裔;后代;子孙;', 'They are descendants of the original English and Scottish settlers.', '他们是最初的英格兰和苏格兰定居者的后代。');
INSERT INTO `words` VALUES (1665, 'descended', 'dɪˈsendɪd', 'adj.', '(人)是…的后代的，为…的后裔的;(动物)由…演变而来的，为…的变种的;', 'She told us she was descended from some Scottish Lord.', '她告诉我们她是某苏格兰勋爵的后裔。');
INSERT INTO `words` VALUES (1666, 'descent', 'dɪˈsent', 'n.', '下降;下倾;斜坡;', 'Sixteen of the youngsters set off for help, but during the descent three collapsed in the cold and rain.', '16 名年轻人出发去寻求帮助，但在下山过程中有 3 人因寒冷和大雨晕倒了。');
INSERT INTO `words` VALUES (1667, 'describe', 'dɪˈskraɪb', 'v.', '描述;形容;把…称为;', 'We asked her to describe what kind of things she did in her spare time...', '我们请她描述一下她业余时间都在做什么。');
INSERT INTO `words` VALUES (1668, 'description', 'dɪˈskrɪpʃn', 'n.', '描写(文字);形容;说明;', 'Police have issued a description of the man who was aged between fifty and sixty...', '警方已经发布了那名年龄在 50 到 60 岁之间的男子的相貌特征通告。');
INSERT INTO `words` VALUES (1669, 'deserve', 'dɪˈzɜːv', 'v.', '值得;应得;应受;', 'Government officials clearly deserve some of the blame as well...', '政府官员显然也应当承担部分责任。');
INSERT INTO `words` VALUES (1670, 'deserving', 'dɪˈzɜːvɪŋ', 'adj.', '值得的;应得的;', 'The money saved could be used for more deserving causes.', '省下来的钱可以用于更有意义的事业。');
INSERT INTO `words` VALUES (1671, 'design', 'dɪˈzaɪn', 'n.', '设计;布局;安排;', 'They wanted to design a machine that was both attractive and practical.', '他们想设计一种既美观又实用的机械。');
INSERT INTO `words` VALUES (1672, 'designation', 'ˌdezɪɡˈneɪʃn', 'n.', '选定;指定;委任;', '...a level four alert, a designation reserved for very serious incidents.', '用于标示非常严重事件的第四级警戒');
INSERT INTO `words` VALUES (1673, 'desirable', 'dɪˈzaɪərəbl', 'adj.', '向往的;可取的;值得拥有的;', 'Prolonged negotiation was not desirable...', '拖长了的谈判并不是大家所想要的。');
INSERT INTO `words` VALUES (1674, 'desire', 'dɪˈzaɪə(r)', 'n.', '愿望;欲望;渴望;', 'I had a strong desire to help and care for people...', '我非常渴望能够帮助和照顾他人。');
INSERT INTO `words` VALUES (1675, 'desired', 'dɪˈzaɪəd', 'v.', '渴望;期望;想望;', 'I had a strong desire to help and care for people...', '我非常渴望能够帮助和照顾他人。');
INSERT INTO `words` VALUES (1676, 'desktop', 'ˈdesktɒp', 'n.', '桌面;桌面(显示使用程序图标的计算机屏幕);', 'When launched, the Macintosh was the smallest desktop computer ever produced.', '当初上市的时候，麦金塔电脑是所有台式电脑里最小的。');
INSERT INTO `words` VALUES (1677, 'despair', 'dɪˈspeə(r)', 'n.', '绝望;', 'I looked at my wife in despair...', '我绝望地看着妻子。');
INSERT INTO `words` VALUES (1678, 'desperate', 'ˈdespərət', 'adj.', '(因绝望而)不惜冒险的，不顾一切的，拼命的;绝望的;孤注一掷的;', 'Troops are needed to help get food into Kosovo where people are in desperate need...', '需要军队帮助将科索沃人民亟需的食品送抵那里。');
INSERT INTO `words` VALUES (1679, 'despise', 'dɪˈspaɪz', 'v.', '鄙视;蔑视;看不起;', 'I can never, ever forgive him. I despise him...', '我永远不会原谅他。我鄙视他。');
INSERT INTO `words` VALUES (1680, 'despite', 'dɪˈspaɪt', 'prep.', '即使;尽管;尽管(自己)不愿意;', 'Despite a thorough investigation, no trace of Dr Southwell has been found...', '尽管进行了彻底的调查，还是没有发现索思韦尔博士的任何踪迹。');
INSERT INTO `words` VALUES (1681, 'dessert', 'dɪˈzɜːt', 'n.', '(饭后)甜点，甜食;', 'She had homemade ice cream for dessert...', '她用自制的冰激凌作饭后甜点。');
INSERT INTO `words` VALUES (1682, 'destination', 'ˌdestɪˈneɪʃn', 'n.', '目的地;终点;', 'Spain is still our most popular holiday destination...', '西班牙仍是我们最喜爱的度假去处。');
INSERT INTO `words` VALUES (1683, 'destiny', 'ˈdestəni', 'n.', '命运;天命;天数;', 'We are masters of our own destiny...', '我们是自己命运的主宰者。');
INSERT INTO `words` VALUES (1684, 'destroy', 'dɪˈstrɔɪ', 'v.', '摧毁;毁灭;破坏;', 'That\'s a sure recipe for destroying the economy and creating chaos...', '那样做肯定会破坏经济、制造混乱。');
INSERT INTO `words` VALUES (1685, 'destruction', 'dɪˈstrʌkʃn', 'n.', '摧毁;毁灭;破坏;', '...an international agreement aimed at halting the destruction of the ozone layer.', '一项旨在阻止臭氧层破坏的国际协议');
INSERT INTO `words` VALUES (1686, 'destructive', 'dɪˈstrʌktɪv', 'adj.', '引起破坏(或毁灭)的;破坏(或毁灭)性的;', '...the awesome destructive power of nuclear weapons...', '核武器可怕的毁灭性力量');
INSERT INTO `words` VALUES (1687, 'detach', 'dɪˈtætʃ', 'v.', '拆卸;(使)分开，脱离;挣脱;', 'Detach the white part of the application form and keep it...', '把申请表的白色部分撕下存底。');
INSERT INTO `words` VALUES (1688, 'detached', 'dɪˈtætʃt', 'adj.', '单独的;独立的;不连接的;', 'He tries to remain emotionally detached from the prisoners, but fails...', '他试图不带感情地对待那些犯人，但是做不到。');
INSERT INTO `words` VALUES (1689, 'detachment', 'dɪˈtætʃmənt', 'n.', '超然;超脱;冷漠;', '...a doctor\'s professional detachment...', '医生职业性的冷静');
INSERT INTO `words` VALUES (1690, 'detailed', 'ˈdiːteɪld', 'adj.', '详细的;细致的;精细的;', 'Yesterday\'s letter contains a detailed account of the decisions...', '昨天的那封信里有对这些决议的详细说明。');
INSERT INTO `words` VALUES (1691, 'detain', 'dɪˈteɪn', 'v.', '拘留;扣押;耽搁;', 'The act allows police to detain a suspect for up to 48 hours...', '该法令允许警方将嫌疑犯扣押最多48小时。');
INSERT INTO `words` VALUES (1692, 'detect', 'dɪˈtekt', 'v.', '发现;查明;侦察出;', '...a sensitive piece of equipment used to detect radiation...', '一件用于探测辐射的灵敏设备');
INSERT INTO `words` VALUES (1693, 'detectable', 'dɪˈtektəbl', 'adj.', '可觉察的;可发现的;可查出的;', 'Doctors say the disease is probably inherited but not detectable at birth.', '医生说该疾病可能是遗传而来，但在出生时无法查出。');
INSERT INTO `words` VALUES (1694, 'detection', 'dɪˈtekʃn', 'n.', '侦查;探测;察觉;', '...the early detection of breast cancer.', '乳腺癌的早期发觉');
INSERT INTO `words` VALUES (1695, 'detective', 'dɪˈtektɪv', 'n.', '侦探;警探;私人侦探;', 'Detectives are appealing for witnesses who may have seen anything suspicious...', '探员呼吁看到任何可疑情况的人进行举报。');
INSERT INTO `words` VALUES (1696, 'detector', 'dɪˈtektə(r)', 'n.', '探测器;侦察器;检测器;', '...a metal detector.', '金属探测器');
INSERT INTO `words` VALUES (1697, 'deter', 'dɪˈtɜː(r)', 'v.', '制止;阻止;威慑;', 'Supporters of the death penalty argue that it would deter criminals from carrying guns...', '死刑的支持者认为它能阻吓罪犯携带枪支。');
INSERT INTO `words` VALUES (1698, 'deteriorate', 'dɪˈtɪəriəreɪt', 'v.', '变坏;恶化;退化;', 'There are fears that the situation might deteriorate into full-scale war...', '人们担心形势可能恶化而演变成一场全面战争。');
INSERT INTO `words` VALUES (1699, 'determinant', 'dɪˈtɜːmɪnənt', 'n.', '决定因素;决定条件;', 'The windows and the views beyond them are major determinants of a room\'s character.', '窗户和窗外的景色是决定一个房间特色的主要因素。');
INSERT INTO `words` VALUES (1700, 'determination', 'dɪˌtɜːmɪˈneɪʃn', 'n.', '决心;果断;坚定;', 'Everyone concerned acted with great courage and determination...', '所有相关人员都表现出了极大的勇气和决心。');
INSERT INTO `words` VALUES (1701, 'determine', 'dɪˈtɜːmɪn', 'v.', '查明;测定;准确算出;', 'The size of the chicken pieces will determine the cooking time...', '鸡块的大小将决定烹饪时间的长短。');
INSERT INTO `words` VALUES (1702, 'determined', 'dɪˈtɜːmɪnd', 'adj.', '决心;决定;决意;', 'His enemies are determined to ruin him...', '他的敌人决意要毁了他。');
INSERT INTO `words` VALUES (1703, 'detest', 'dɪˈtest', 'v.', '厌恶;憎恨;讨厌;', 'My mother detested him...', '我母亲对他很是厌恶。');
INSERT INTO `words` VALUES (1704, 'detoxify', 'ˌdiːˈtɒksɪfaɪ', 'v.', '解毒;去毒;除去…的毒素;', '...drugs which block the effects of heroin use and rapidly detoxify addicts...', '能阻止海洛因在体内起作用并让吸毒成瘾者迅速脱瘾的药物');
INSERT INTO `words` VALUES (1705, 'detriment', 'ˈdetrɪmənt', 'n.', '伤害;损害;造成伤害(或损害)的事物;', 'Children spend too much time on schoolwork, to the detriment of other activities.', '孩子把太多的时间用于做作业，影响了他们参加其他活动。');
INSERT INTO `words` VALUES (1706, 'detrimental', 'ˌdetrɪˈmentl', 'adj.', '有害的;不利的;', '...foods suspected of being detrimental to health...', '被怀疑有害健康的食品');
INSERT INTO `words` VALUES (1707, 'devalue', 'ˌdiːˈvæljuː', 'v.', '使(货币)贬值;降低…的价值;贬低;', 'They spread tales about her in an attempt to devalue her work...', '他们散布一些有关她的流言，企图贬低她的工作。');
INSERT INTO `words` VALUES (1708, 'devastate', 'ˈdevəsteɪt', 'v.', '彻底破坏;摧毁;毁灭;', 'A few days before, a fire had devastated large parts of Windsor Castle.', '几天前，温莎城堡的大部分被一场大火烧毁。');
INSERT INTO `words` VALUES (1709, 'devastating', 'ˈdevəsteɪtɪŋ', 'adj.', '破坏性极大的;毁灭性的;令人震惊的;', 'The city of Ormac took the full force of the winds and devastating floods...', '奥马克城受到了狂风和灾难性洪水的猛烈袭击。');
INSERT INTO `words` VALUES (1710, 'develop', 'dɪˈveləp', 'v.', '(使)成长，发展，壮大;开发;研制;', 'As children develop, some of the most important things they learn have to do with their sense of self...', '在成长过程中，孩子学到的一些最重要的东西与他们的自我意识有关。');
INSERT INTO `words` VALUES (1711, 'developer', 'dɪˈveləpə(r)', 'n.', '(房地产)开发商，开发公司;(新产品的)开发者，研制者;显影剂;', '...common land which would have a high commercial value if sold to developers.', '如果卖给开发商会有很高商业价值的公共用地');
INSERT INTO `words` VALUES (1712, 'developing', 'dɪˈveləpɪŋ', 'adj.', '发展中的;', 'In the developing world cigarette consumption is increasing.', '在发展中国家，香烟的消费量正在上升。');
INSERT INTO `words` VALUES (1713, 'development', 'dɪˈveləpmənt', 'n.', '发展;发育;成长;', '...an ideal system for studying the development of the embryo...', '研究胎儿发育过程的理想方法');
INSERT INTO `words` VALUES (1714, 'deviate', 'ˈdiːvieɪt', 'v.', '背离;偏离;违背;', 'They stopped you as soon as you deviated from the script...', '一旦你偏离了剧本的内容，他们就会喊停。');
INSERT INTO `words` VALUES (1715, 'deviation', 'ˌdiːviˈeɪʃn', 'n.', '背离;偏离;违背;', 'Deviation from the norm is not tolerated.', '不能容忍离经叛道行为。');
INSERT INTO `words` VALUES (1716, 'device', 'dɪˈvaɪs', 'n.', '装置;仪器;器具;', '...an electronic device that protects your vehicle 24 hours a day.', '24小时保护车辆的电子装置');
INSERT INTO `words` VALUES (1717, 'devise', 'dɪˈvaɪz', 'v.', '发明;设计;想出;', 'We devised a scheme to help him...', '我们想出了一个帮他的计策。');
INSERT INTO `words` VALUES (1718, 'devote', 'dɪˈvəʊt', 'v.', '献(身);奉献，投入(时间、精力等);将(文章或讲话的某一部分)专用(于);', 'He decided to devote the rest of his life to scientific investigation...', '他决定将自己的余生献给科学研究事业。');
INSERT INTO `words` VALUES (1719, 'devotion', 'dɪˈvəʊʃn', 'n.', '挚爱;关爱;关照;', 'At first she was flattered by his devotion.', '刚开始他的爱慕让她感到荣幸。');
INSERT INTO `words` VALUES (1720, 'devour', 'dɪˈvaʊə(r)', 'v.', '(尤指因饥饿而)狼吞虎咽地吃光;津津有味地看;如饥似渴地读;', 'A medium-sized dog will devour at least one can of food per day...', '中型犬每天至少要吃掉一罐狗粮。');
INSERT INTO `words` VALUES (1721, 'dew', 'djuː', 'n.', '露;露水;', 'The dew gathered on the leaves.', '露水在叶子上凝结。');
INSERT INTO `words` VALUES (1722, 'diagnose', 'ˈdaɪəɡnəʊz', 'v.', '诊断(疾病);判断(问题的原因);', 'The soldiers were diagnosed as having flu...', '这些士兵经诊断患了流感。');
INSERT INTO `words` VALUES (1723, 'diagnosis', 'ˌdaɪəɡˈnəʊsɪs', 'n.', '诊断;(问题原因的)判断;', 'I need to have a second test to confirm the diagnosis...', '我需要再进行一次检查以确诊。');
INSERT INTO `words` VALUES (1724, 'diagnostic', 'ˌdaɪəɡˈnɒstɪk', 'adj.', '诊断的;判断的;', '...X-rays and other diagnostic tools.', 'X射线和其他诊断工具');
INSERT INTO `words` VALUES (1725, 'diagonal', 'daɪˈæɡənl', 'adj.', '斜线的;对角线的;', '...a pattern of diagonal lines.', '斜纹图案');
INSERT INTO `words` VALUES (1726, 'diagonally', 'daɪˈægənli', 'adv.', '斜对地；斜地;', '...a pattern of diagonal lines.', '斜纹图案');
INSERT INTO `words` VALUES (1727, 'dialect', 'ˈdaɪəlekt', 'n.', '地方话;土话;方言;', 'In the fifties, many Italians spoke only local dialect...', '在 20 世纪 50 年代，许多意大利人只会说当地方言。');
INSERT INTO `words` VALUES (1728, 'dialysis', 'ˌdaɪˈæləsɪs', 'n.', '渗析;透析(尤指将废物从肾病病人的血液中分离出来);', 'I was on dialysis for seven years before my first transplant.', '在进行首次肾移植之前，我做了 7 年的透析。');
INSERT INTO `words` VALUES (1729, 'diameter', 'daɪˈæmɪtə(r)', 'n.', '直径;对径;放大率;', '...a tube less than a fifth of the diameter of a human hair.', '直径不足人的头发直径1/5的电子管');
INSERT INTO `words` VALUES (1730, 'diamond', 'ˈdaɪəmənd', 'n.', '金刚石;钻石;菱形;', '...a pair of diamond earrings.', '一对钻石耳环');
INSERT INTO `words` VALUES (1731, 'dictation', 'dɪkˈteɪʃn', 'n.', '口述;听写;', '...taking dictation from the dean of the Faculty...', '记录系主任口述的内容');
INSERT INTO `words` VALUES (1732, 'dictionary', 'ˈdɪkʃənri', 'n.', '词典;字典;辞书;', '...a Welsh-English dictionary.', '一本威尔士语-英语词典');
INSERT INTO `words` VALUES (1733, 'die', 'daɪ', 'v.', '死;死亡;凋谢;', 'A year later my dog died...', '一年后我的狗死了。');
INSERT INTO `words` VALUES (1734, 'diet', 'ˈdaɪət', 'n.', '日常饮食;日常食物;规定饮食(为健康或减肥等目的);', 'It\'s never too late to improve your diet.', '什么时候改善饮食都为时不晚。');
INSERT INTO `words` VALUES (1735, 'differ', 'ˈdɪfə(r)', 'v.', '相异;有区别;不同于;', 'The story he told police differed from the one he told his mother...', '他在警方和母亲那里说辞不一。');
INSERT INTO `words` VALUES (1736, 'difference', 'ˈdɪfrəns', 'n.', '差别;差异;不同(之处);', 'That is the fundamental difference between the two societies...', '那是这两个社会之间的根本性差异。');
INSERT INTO `words` VALUES (1737, 'different', 'ˈdɪfrənt', 'adj.', '不同的;有区别的;有差异的;', 'London was different from most European capitals...', '伦敦同大多数欧洲国家的首都都有所不同。');
INSERT INTO `words` VALUES (1738, 'differentiate', 'ˌdɪfəˈrenʃieɪt', 'v.', '区分;区别;辨别;', 'A child may not differentiate between his imagination and the real world...', '儿童可能无法将自己的幻想与真实世界区分开来。');
INSERT INTO `words` VALUES (1739, 'difficult', 'ˈdɪfɪkəlt', 'adj.', '困难的;费力的;难做的;', 'Hobart found it difficult to get her first book published...', '霍巴特发现要出版自己的第一本书很不容易。');
INSERT INTO `words` VALUES (1740, 'difficulty', 'ˈdɪfɪkəlti', 'n.', '困难;难题;难事;', '...the difficulty of getting accurate information...', '获取准确信息的难题');
INSERT INTO `words` VALUES (1741, 'dig', 'dɪɡ', 'v.', '掘(地);凿(洞);挖(土);', 'They tried digging in a patch just below the cave...', '他们试着在洞穴正下方的一块地上挖掘。');
INSERT INTO `words` VALUES (1742, 'digestion', 'daɪˈdʒestʃən', 'n.', '消化;消化能力;', 'No liquids are served with meals because they interfere with digestion.', '上饭菜时不要同时上汤水或饮品，因为这会影响消化。');
INSERT INTO `words` VALUES (1743, 'digestive', 'daɪˈdʒestɪv', 'adj.', '消化的;和消化有关的;', '...digestive juices that normally work on breaking down our food...', '通常能够帮助分解食物的有助消化的果汁');
INSERT INTO `words` VALUES (1744, 'digit', 'ˈdɪdʒɪt', 'n.', '(从 0 到 9 的任何一个)数字，数位;手指;拇指;', 'Her telephone number differs from mine by one digit.', '她的电话号码和我的只差一个数字。');
INSERT INTO `words` VALUES (1745, 'digital', 'ˈdɪdʒɪtl', 'adj.', '数字信息系统的;数码的;数字式的;', 'The new digital technology would allow a rapid expansion in the number of TV channels.', '新的数字技术将会使电视频道的数量迅速增加。');
INSERT INTO `words` VALUES (1746, 'dignity', 'ˈdɪɡnəti', 'n.', '庄重;庄严;尊严;', '...her extraordinary dignity and composure.', '她那种端庄沉着的非凡气质');
INSERT INTO `words` VALUES (1747, 'dilate', 'daɪˈleɪt', 'v.', '扩大;(使)膨胀，扩张;', 'At night, the pupils dilate to allow in more light...', '到了晚上，瞳孔就会扩大以接收更多光线。');
INSERT INTO `words` VALUES (1748, 'dilemma', 'dɪˈlemə', 'n.', '(进退两难的)窘境，困境;', 'He was faced with the dilemma of whether or not to return to his country...', '要不要回国，他进退两难。');
INSERT INTO `words` VALUES (1749, 'diligent', 'ˈdɪlɪdʒənt', 'adj.', '孜孜不倦的;勤勉的;刻苦的;', 'Meyers is a diligent and prolific worker...', '迈耶斯是个勤奋多产的工人。');
INSERT INTO `words` VALUES (1750, 'dilute', 'daɪˈluːt', 'v.', '稀释;冲淡;削弱;', 'If you give your baby juice, dilute it well with cooled, boiled water...', '如果给婴儿喝果汁，要用凉开水充分稀释。');
INSERT INTO `words` VALUES (1751, 'dim', 'dɪm', 'adj.', '暗淡的;昏暗的;微弱的;', 'She stood waiting, in the dim light...', '她站在昏暗的光线下等待。');
INSERT INTO `words` VALUES (1752, 'dimension', 'dɪˈmenʃn', 'n.', '维(构成空间的因素);尺寸;规模;', 'There is a political dimension to the accusations...', '这些指控含有政治方面的因素。');
INSERT INTO `words` VALUES (1753, 'diminish', 'dɪˈmɪnɪʃ', 'v.', '减少;(使)减弱，缩减;降低;', 'The threat of nuclear war has diminished...', '核战争的威胁已经减小了。');
INSERT INTO `words` VALUES (1754, 'dimmed', 'dɪmd', 'v.', '(使)变暗淡，变微弱，变昏暗;(使)减弱，变淡漠，失去光泽;', 'She stood waiting, in the dim light...', '她站在昏暗的光线下等待。');
INSERT INTO `words` VALUES (1755, 'din', 'dɪn', 'n.', '喧嚣声;嘈杂声;吵闹声;', 'They tried to make themselves heard over the din of the crowd.', '他们力图让自己的声音盖过人群的喧闹声。');
INSERT INTO `words` VALUES (1756, 'dine', 'daɪn', 'v.', '进餐;用饭;', 'He dines alone most nights...', '大多数晚上他都是一个人用餐。');
INSERT INTO `words` VALUES (1757, 'dingy', 'ˈdɪndʒi', 'adj.', '又黑又脏的;昏暗的;肮脏的;', 'Shaw took me to his rather dingy office.', '肖带我去了他那个陈旧昏暗的办公室。');
INSERT INTO `words` VALUES (1758, 'dinner', 'ˈdɪnə(r)', 'n.', '(中午或晚上吃的)正餐，主餐;宴会;', 'She invited us to her house for dinner...', '她请我们去她家吃晚餐。');
INSERT INTO `words` VALUES (1759, 'dint', 'dɪnt', 'n.', '凹痕；凹坑;', 'He succeeds by dint of sheer hard work...', '他完全是凭借着努力工作取得了成功。');
INSERT INTO `words` VALUES (1760, 'dip', 'dɪp', 'v.', '蘸;浸;(使)下降，下沉;', 'They dip the food into the sauce...', '他们把食物放到调味汁里蘸了蘸。');
INSERT INTO `words` VALUES (1761, 'diploma', 'dɪˈpləʊmə', 'n.', '文凭课程;毕业文凭;', '...a new two-year course leading to a diploma in social work.', '一门为期两年、毕业后可以获得社会福利工作方面文凭的新课程');
INSERT INTO `words` VALUES (1762, 'diplomacy', 'dɪˈpləʊməsi', 'n.', '外交;外交技巧;外交手腕;', 'Today\'s Security Council resolution will be a significant success for American diplomacy...', '今天的安理会决议将是美国外交取得的一个重大成功。');
INSERT INTO `words` VALUES (1763, 'diplomatic', 'ˌdɪpləˈmætɪk', 'adj.', '外交的;从事外交的;有手腕的;', '...before the two countries resume full diplomatic relations...', '在两国恢复全面的外交关系之前');
INSERT INTO `words` VALUES (1764, 'dipping', 'ˈdɪpɪŋ', 'v.', '蘸;浸;(使)下降，下沉;', 'They dip the food into the sauce...', '他们把食物放到调味汁里蘸了蘸。');
INSERT INTO `words` VALUES (1765, 'direct', 'dəˈrekt', 'adj.', '直接的;亲自的;亲身的;', 'They\'d come on a direct flight from Athens.', '他们是搭乘从雅典直飞这里的航班过来的。');
INSERT INTO `words` VALUES (1766, 'direction', 'dəˈrekʃn', 'n.', '方向;方位;趋势;', 'St Andrews was ten miles in the opposite direction...', '圣安德鲁斯在相反方向上 10 英里处。');
INSERT INTO `words` VALUES (1767, 'directive', 'dəˈrektɪv', 'n.', '指示;命令;', 'Thanks to a new EU directive, insecticide labelling will be more specific.', '根据欧盟的一项新指令，杀虫剂的标签标注将更加明确。');
INSERT INTO `words` VALUES (1768, 'directly', 'dəˈrektli', 'adv.', '直接地;径直地;坦率地;', 'The second rainbow will be bigger than the first, and directly above it...', '第二道彩虹将比第一道还要大，而且正好位于其上。');
INSERT INTO `words` VALUES (1769, 'directory', 'dəˈrektəri', 'n.', '名录;电话号码簿;公司名录;', '...a telephone directory.', '电话号码簿');
INSERT INTO `words` VALUES (1770, 'dirt', 'dɜːt', 'n.', '污物;尘土;烂泥;', 'I started to scrub off the dirt.', '我开始擦洗上面的灰尘。');
INSERT INTO `words` VALUES (1771, 'dirty', 'ˈdɜːti', 'adj.', '肮脏的;龌龊的;污秽的;', 'She still did not like the woman who had dirty fingernails...', '她还是不喜欢那个指甲脏脏的女子。');
INSERT INTO `words` VALUES (1772, 'disability', 'ˌdɪsəˈbɪləti', 'n.', '(某种)缺陷，障碍;(指状态、身心、学习等方面的)缺陷;', 'Facilities for people with disabilities are still insufficient.', '供伤残人士使用的设施仍然匮乏。');
INSERT INTO `words` VALUES (1773, 'disable', 'dɪsˈeɪbl', 'v.', '使丧失能力;使伤残;使无效;', 'She did all this tendon damage and it really disabled her...', '她遭受这么严重的肌腱损伤，这实际上让她残废了。');
INSERT INTO `words` VALUES (1774, 'disabled', 'dɪsˈeɪbld', 'adj.', '丧失能力的;有残疾的;无能力的;', '...practical problems encountered by disabled people in the workplace.', '在工作场所中残疾人所遇到的实际问题');
INSERT INTO `words` VALUES (1775, 'disadvantage', 'ˌdɪsədˈvɑːntɪdʒ', 'n.', '不利因素;障碍;不便之处;', 'His two main rivals suffer the disadvantage of having been long-term political exiles...', '他的两个主要竞争对手因长期政治流亡而处于不利的地位。');
INSERT INTO `words` VALUES (1776, 'disadvantageous', 'ˌdɪsædvənˈteɪdʒəs', 'adj.', '不利的;不便的;', 'The Second World War started in the most disadvantageous possible way for the western powers.', '二战爆发时西方列强处境极为不利。');
INSERT INTO `words` VALUES (1777, 'disagree', 'ˌdɪsəˈɡriː', 'v.', '不同意;持不同意见;有分歧;', 'You must continue to see them no matter how much you may disagree with them...', '无论你和他们之间有多大的分歧，你都得继续去看他们。');
INSERT INTO `words` VALUES (1778, 'disagreement', 'ˌdɪsəˈɡriːmənt', 'n.', '意见不一;分歧;争论;', 'Britain and France have expressed some disagreement with the proposal.', '英国和法国对这项提案已经发表了一些反对意见。');
INSERT INTO `words` VALUES (1779, 'disallow', 'ˌdɪsəˈlaʊ', 'v.', '不准许;不接受;驳回;', 'England scored again, but the whistle had gone and the goal was disallowed...', '英格兰又进一球，但哨声已响，进球无效。');
INSERT INTO `words` VALUES (1780, 'disappear', 'ˌdɪsəˈpɪə(r)', 'v.', '消失;不见;不复存在;', 'The black car drove away from them and disappeared...', '那辆黑色轿车驶离了他们的视线。');
INSERT INTO `words` VALUES (1781, 'disappearance', 'ˌdɪsəˈpɪərəns', 'n.', '消失;失踪;丢失;被盗;(指人、事物或动物)渐渐消失，灭绝;', 'Her disappearance has baffled police...', '她的失踪让警方很困惑。');
INSERT INTO `words` VALUES (1782, 'disappoint', 'ˌdɪsəˈpɔɪnt', 'v.', '使失望;使破灭;使落空;', 'She knew that she would disappoint him.', '她知道她会令他失望的。');
INSERT INTO `words` VALUES (1783, 'disappointed', 'ˌdɪsəˈpɔɪntɪd', 'adj.', '失望的;沮丧的;失意的;', 'Castle-hunters won\'t be disappointed with the Isle of Man...', '马恩岛不会让城堡探寻者失望的。');
INSERT INTO `words` VALUES (1784, 'disappointment', 'ˌdɪsəˈpɔɪntmənt', 'n.', '失望;沮丧;扫兴;', 'Despite winning the title, their last campaign ended in great disappointment...', '尽管赢得了冠军，但他们最后一役却令人大失所望。');
INSERT INTO `words` VALUES (1785, 'disapproval', 'ˌdɪsəˈpruːvl', 'n.', '不赞成;反对;', 'His action had been greeted with almost universal disapproval.', '他的行动几乎遭到了一致的反对。');
INSERT INTO `words` VALUES (1786, 'disapprove', 'ˌdɪsəˈpruːv', 'v.', '不赞成;不同意;反对;', 'Most people disapprove of such violent tactics...', '多数人反对这种暴力的手段。');
INSERT INTO `words` VALUES (1787, 'disarm', 'dɪsˈɑːm', 'v.', '缴(某人)的械;解除(某人)的武装;裁军;', 'We will agree to disarming troops and leaving their weapons at military positions.', '我们将同意解除军队的武装并把武器留在军事阵地。');
INSERT INTO `words` VALUES (1788, 'disarray', 'ˌdɪsəˈreɪ', 'n.', '混乱;紊乱;', 'The nation is in disarray following rioting led by the military...', '军队发动暴乱之后国家陷入混乱中。');
INSERT INTO `words` VALUES (1789, 'disassemble', 'ˌdɪsəˈsembl', 'v.', '拆卸;拆开;反汇编(将计算机编码译成普通语言);', 'You\'ll have to disassemble the drill.', '你得拆开这个钻头。');
INSERT INTO `words` VALUES (1790, 'disaster', 'dɪˈzɑːstə(r)', 'n.', '灾难;灾祸;灾害;', 'It was the second air disaster in the region in less than two months...', '这已经是该地区不到两个月内的第二次空难了。');
INSERT INTO `words` VALUES (1791, 'disastrous', 'dɪˈzɑːstrəs', 'adj.', '极糟糕的;灾难性的;完全失败的;', '...the recent, disastrous earthquake...', '最近的灾难性地震');
INSERT INTO `words` VALUES (1792, 'disburse', 'dɪsˈbɜːs', 'v.', '(从资金中)支付，支出;', 'The aid will not be disbursed until next year...', '援助款到明年才会下拨。');
INSERT INTO `words` VALUES (1793, 'discard', 'dɪˈskɑːd , ˈdɪskɑːd', 'v.', '丢弃;抛弃;垫(牌);', 'Read the manufacturer\'s guidelines before discarding the box.', '在丢掉盒子前先看一下制造商的使用说明。');
INSERT INTO `words` VALUES (1794, 'discarded', 'dɪˈskɑːdɪd', 'v.', '丢弃;抛弃;垫(牌);', 'Read the manufacturer\'s guidelines before discarding the box.', '在丢掉盒子前先看一下制造商的使用说明。');
INSERT INTO `words` VALUES (1795, 'discern', 'dɪˈsɜːn', 'v.', '觉察出;识别;了解;', 'You need a long series of data to be able to discern such a trend...', '你需要一长串的数据才能看出这一趋势。');
INSERT INTO `words` VALUES (1796, 'disciplinary', 'ˈdɪsəplɪnəri', 'adj.', '有关纪律的;执行纪律的;惩戒性的;', 'He will now face a disciplinary hearing for having an affair...', '他因有外遇而要面临纪律听证会的裁决。');
INSERT INTO `words` VALUES (1797, 'discipline', 'ˈdɪsəplɪn', 'n.', '训练;训导;纪律;', 'Order and discipline have been placed in the hands of headmasters and governing bodies.', '维持秩序和纪律的工作已交接给了校长和管理机构了。');
INSERT INTO `words` VALUES (1798, 'disclaim', 'dɪsˈkleɪm', 'v.', '公开否认;拒绝承认;放弃(财产、头衔等的权利);', 'She disclaims any knowledge of her husband\'s business.', '她否认对她丈夫的事知情。');
INSERT INTO `words` VALUES (1799, 'disclose', 'dɪsˈkləʊz', 'v.', '揭露;透露;泄露;', 'Neither side would disclose details of the transaction...', '双方均不肯披露交易细节。');
INSERT INTO `words` VALUES (1800, 'disco', 'ˈdɪskəʊ', 'n.', '迪斯科舞厅(或舞会等);迪斯科舞会的灯光及音响设备;', 'Fridays and Saturdays are regular disco nights.', '周五和周六是固定的迪斯科之夜。');
INSERT INTO `words` VALUES (1801, 'discomfort', 'dɪsˈkʌmfət', 'n.', '轻微的病痛;不舒服;不适;', 'She carried her left arm at an awkward angle, as if it were causing her discomfort...', '她的左胳膊很不自然地抬着，好像它让她不舒服似的。');
INSERT INTO `words` VALUES (1802, 'disconnect', 'ˌdɪskəˈnekt', 'v.', '切断(煤气、水或电的供应);切断(电话服务);停止供应(水、电、燃气);', 'The device automatically disconnects the ignition when the engine is switched off...', '当引擎关掉时，这个装置会自动切断点火开关。');
INSERT INTO `words` VALUES (1803, 'disconnection', 'ˌdɪskəˈnekʃn', 'n.', '(煤气、水、电或电话的)切断;', 'The rate of disconnections following non-payment of water charges is rising.', '不缴水费引起的断水的比率正在上升。');
INSERT INTO `words` VALUES (1804, 'discontinue', 'ˌdɪskənˈtɪnjuː', 'v.', '停止;终止;中断;', 'Do not discontinue the treatment without consulting your doctor.', '未经医嘱不得中断治疗。');
INSERT INTO `words` VALUES (1805, 'discourage', 'dɪsˈkʌrɪdʒ', 'v.', '阻拦;阻止;劝阻;', 'It may be difficult to do at first. Don\'t let this discourage you.', '万事开头难，别因此而灰心。');
INSERT INTO `words` VALUES (1806, 'discover', 'dɪˈskʌvə(r)', 'v.', '(第一个)发现;(出乎意料地)发现，找到，发觉;了解到;', 'She discovered that they\'d escaped...', '她发现他们已经逃跑了。');
INSERT INTO `words` VALUES (1807, 'discovery', 'dɪˈskʌvəri', 'n.', '发现;发觉;被发现的事物(或真相、人);', 'I felt I\'d made an incredible discovery.', '我觉得我有一个天大的发现。');
INSERT INTO `words` VALUES (1808, 'discreet', 'dɪˈskriːt', 'adj.', '(言行)谨慎的，慎重的，考虑周到的;', 'They were gossipy and not always discreet...', '他们很爱传闲话，而且常常口无遮拦。');
INSERT INTO `words` VALUES (1809, 'discreetly', '', 'adv.', '谨慎地；小心地;', 'They were gossipy and not always discreet...', '他们很爱传闲话，而且常常口无遮拦。');
INSERT INTO `words` VALUES (1810, 'discrepancy', 'dɪsˈkrepənsi', 'n.', '差异;不符合;不一致;', '...the discrepancy between press and radio reports.', '新闻和广播报道之间的出入');
INSERT INTO `words` VALUES (1811, 'discrete', 'dɪˈskriːt', 'adj.', '分离的;互不相连的;各别的;', '...instruction manuals that break down jobs into scores of discrete steps.', '把工作分解成许多独立步骤的指导手册');
INSERT INTO `words` VALUES (1812, 'discretion', 'dɪˈskreʃn', 'n.', '自行决定的自由;自行决定权;谨慎;', 'Larsson sometimes joined in the fun, but with more discretion...', '拉森有时会和大家一起玩乐，但是显得更加矜持。');
INSERT INTO `words` VALUES (1813, 'discriminate', 'dɪˈskrɪmɪneɪt', 'v.', '区别;辨别;区分;', 'He is incapable of discriminating between a good idea and a terrible one...', '他分不清主意的好坏。');
INSERT INTO `words` VALUES (1814, 'discrimination', 'dɪˌskrɪmɪˈneɪʃn', 'n.', '区别对待;歧视;偏袒;', 'She is exempt from sex discrimination laws.', '她免受性别歧视法的追究。');
INSERT INTO `words` VALUES (1815, 'discuss', 'dɪˈskʌs', 'v.', '讨论;谈论;商量;', 'I will be discussing the situation with colleagues tomorrow...', '明天我会和同事们讨论一下情况。');
INSERT INTO `words` VALUES (1816, 'discussion', 'dɪˈskʌʃn', 'n.', '讨论;商讨;详述;', 'There was a lot of discussion about the wording of the report...', '关于此则报道的措辞议论纷纷。');
INSERT INTO `words` VALUES (1817, 'disdain', 'dɪsˈdeɪn', 'n.', '鄙视;蔑视;鄙弃;', 'Janet looked at him with disdain...', '珍妮特轻蔑地看着他。');
INSERT INTO `words` VALUES (1818, 'disease', 'dɪˈziːz', 'n.', '病;疾病;弊端;', '...the rapid spread of disease in the area.', '疾病在这一区域的迅速蔓延');
INSERT INTO `words` VALUES (1819, 'diseased', 'dɪˈziːzd', 'adj.', '有病的;患病的;病态的;', 'The arteries are diseased and a transplant is the only hope...', '动脉已经发生病变，移植是唯一的希望。');
INSERT INTO `words` VALUES (1820, 'disgrace', 'dɪsˈɡreɪs', 'n.', '丢脸;耻辱;不光彩;', 'His vice president also had to resign in disgrace...', '他的副总统也被迫不光彩地辞去职务。');
INSERT INTO `words` VALUES (1821, 'disgraceful', 'dɪsˈɡreɪsfl', 'adj.', '不光彩的;可耻的;丢脸的;', 'It\'s disgraceful that they have detained him for so long...', '他们把他扣押了这么长时间，真不光彩。');
INSERT INTO `words` VALUES (1822, 'disguise', 'dɪsˈɡaɪz', 'v.', '假扮;装扮;伪装;', 'You\'ll have to travel in disguise...', '你只能乔装出行。');
INSERT INTO `words` VALUES (1823, 'disgust', 'dɪsˈɡʌst', 'n.', '厌恶;憎恶;反感;', 'He spoke of his disgust at the incident...', '他谈到了他对这件事的厌恶。');
INSERT INTO `words` VALUES (1824, 'dish', 'dɪʃ', 'n.', '碟;盘;(待清洗的)餐具;', '...plastic bowls and dishes...', '塑料碗碟');
INSERT INTO `words` VALUES (1825, 'dishonest', 'dɪsˈɒnɪst', 'adj.', '不诚实的;骗人的;欺骗性的;', 'You have been dishonest with me...', '你对我不诚实。');
INSERT INTO `words` VALUES (1826, 'disillusion', 'ˌdɪsɪˈluːʒn', 'v.', '使醒悟;使不再抱幻想;使理想破灭;', 'I\'d hate to be the one to disillusion him...', '我不愿意成为那个让他幻想破灭的人。');
INSERT INTO `words` VALUES (1827, 'disinclined', 'ˌdɪsɪnˈklaɪnd', 'adj.', '不情愿;不乐意;无意于;', 'He was disinclined to talk about himself, especially to his students...', '他不喜欢谈论他自己，尤其是当着他学生的面。');
INSERT INTO `words` VALUES (1828, 'disinfect', 'ˌdɪsɪnˈfekt', 'v.', '给…消毒;消除(计算机)病毒; 给(电脑)杀毒;', 'Chlorine is used to disinfect water...', '氯用来给水消毒。');
INSERT INTO `words` VALUES (1829, 'disinfectant', 'ˌdɪsɪnˈfektənt', 'n.', '消毒剂;杀菌剂;', 'Effluent from the sedimentation tank is dosed with disinfectant to kill any harmful organisms...', '沉降池中的废水中加入了消毒剂，以消灭所有有害微生物。');
INSERT INTO `words` VALUES (1830, 'disintegration', 'dɪsˌɪntɪˈɡreɪʃn', 'n.', '瓦解，崩溃；分解;', 'During October 1918 the Austro-Hungarian Empire began to disintegrate.', '在 1918 年 10 月间，奥匈帝国开始瓦解。');
INSERT INTO `words` VALUES (1831, 'disk', 'dɪsk', 'n.', '磁盘;磁碟;', 'The program takes up 2.5 megabytes of disk space.', '这个程序占用 2.5 兆的磁盘空间。');
INSERT INTO `words` VALUES (1832, 'dislike', 'dɪsˈlaɪk', 'v.', '不喜爱;厌恶;', 'We don\'t serve liver often because so many people dislike it...', '我们不常做肝这道菜，因为很多人吃不惯。');
INSERT INTO `words` VALUES (1833, 'dislikes', 'dɪsˈlaɪks', 'v.', '不喜爱;厌恶;', 'We don\'t serve liver often because so many people dislike it...', '我们不常做肝这道菜，因为很多人吃不惯。');
INSERT INTO `words` VALUES (1834, 'dislocate', 'ˈdɪsləkeɪt', 'v.', '使(骨头)脱位;使脱臼;扰乱(制度、计划等);', 'Harrison dislocated a finger...', '哈里森的一根手指脱臼了。');
INSERT INTO `words` VALUES (1835, 'disloyal', 'dɪsˈlɔɪəl', 'adj.', '(对国家、家庭等)不忠实的，不忠诚的;(对朋友等)不守信义的;', 'She was so disloyal to her deputy she made his position untenable...', '她对她的副手很不守信义，使得他的职位岌岌可危。');
INSERT INTO `words` VALUES (1836, 'disloyalty', 'dɪsˈlɔɪəlti', 'n.', '不忠;', 'Charges had already been made against certain officials suspected of disloyalty.', '对某些有不忠嫌疑的官员已经提起了诉讼。');
INSERT INTO `words` VALUES (1837, 'dismal', 'ˈdɪzməl', 'adj.', '忧郁的;凄凉的;惨淡的;', '...Israel\'s dismal record in the Olympics...', '以色列在奥运会上让人沮丧的记录');
INSERT INTO `words` VALUES (1838, 'dismay', 'dɪsˈmeɪ', 'n.', '诧异;惊愕;灰心;', 'Local councillors have reacted with dismay and indignation...', '地方政务委员的反应是惊恐和愤怒。');
INSERT INTO `words` VALUES (1839, 'dismiss', 'dɪsˈmɪs', 'v.', '不予考虑;摒弃;对…不屑一提;', 'Mr Wakeham dismissed the reports as speculation...', '韦克厄姆先生把这些报道当作臆测而不予考虑。');
INSERT INTO `words` VALUES (1840, 'dismissal', 'dɪsˈmɪsl', 'n.', '解雇;开除;撤职;', '...Mr Low\'s dismissal from his post at the head of the commission.', '免去洛先生的委员会主任一职');
INSERT INTO `words` VALUES (1841, 'dismount', 'dɪsˈmaʊnt', 'v.', '下(马、自行车、摩托车);', 'Emma dismounted and took her horse\'s bridle.', '埃玛下了马，拉着马笼头。');
INSERT INTO `words` VALUES (1842, 'disobey', 'ˌdɪsəˈbeɪ', 'v.', '不服从;不顺从;违抗;', '...a naughty boy who often disobeyed his mother and father...', '一个经常和父母对着干的淘气男孩');
INSERT INTO `words` VALUES (1843, 'disorder', 'dɪsˈɔːdə(r)', 'n.', '杂乱;混乱;凌乱;', '...a rare nerve disorder that can cause paralysis of the arms.', '一种可致手臂瘫痪的罕见的神经疾病');
INSERT INTO `words` VALUES (1844, 'disparity', 'dɪˈspærəti', 'n.', '(尤指因不公正对待引起的)不同，不等，差异，悬殊;', '...the economic disparities between East and West Berlin.', '东西柏林之间经济上的悬殊');
INSERT INTO `words` VALUES (1845, 'dispel', 'dɪˈspel', 'v.', '驱散，消除(尤指感觉或信仰);', 'The President is attempting to dispel the notion that he has neglected the economy.', '有人认为总统忽视了经济，总统正试图消除这种看法。');
INSERT INTO `words` VALUES (1846, 'dispense', 'dɪˈspens', 'v.', '分配;分发;施与，提供(尤指服务);', 'The Union had already dispensed £40,000 in grants...', '工会已经拨款4万英镑。');
INSERT INTO `words` VALUES (1847, 'disperse', 'dɪˈspɜːs', 'v.', '(使)分散，散开;疏散;驱散;', 'The oil appeared to be dispersing...', '油好像在扩散。');
INSERT INTO `words` VALUES (1848, 'displace', 'dɪsˈpleɪs', 'v.', '取代;替代;置换;', 'These factories have displaced tourism as the country\'s largest source of foreign exchange...', '这些工厂已取代旅游业成为该国最大的外汇来源。');
INSERT INTO `words` VALUES (1849, 'displacement', 'dɪsˈpleɪsmənt', 'n.', '取代;替代;移位;', '...the displacement of all my energy into caring for the baby.', '我所有精力都放在了照顾宝宝上');
INSERT INTO `words` VALUES (1850, 'display', 'dɪˈspleɪ', 'v.', '陈列;展出;展示;', 'Among the protesters and war veterans proudly displaying their medals was Aubrey Rose...', '在抗议者和骄傲地展示勋章的退伍老兵中，就有奥布里·罗斯。');
INSERT INTO `words` VALUES (1851, 'displease', 'dɪsˈpliːz', 'v.', '使恼怒;使生气;使不悦;', 'Not wishing to displease her, he avoided answering the question.', '为了不惹她生气，他对这个问题避而不答。');
INSERT INTO `words` VALUES (1852, 'displeasure', 'dɪsˈpleʒə(r)', 'n.', '烦恼;生气;不悦;', 'The population has already begun to show its displeasure at the slow pace of change.', '人们已经开始表露出对改革步伐缓慢的不满。');
INSERT INTO `words` VALUES (1853, 'disposal', 'dɪˈspəʊzl', 'n.', '去掉;清除;处理;', 'Do you have this information at your disposal?...', '你对这个情况有所了解吗？');
INSERT INTO `words` VALUES (1854, 'disposed', 'dɪˈspəʊzd', 'adj.', '倾向于;有意于;乐意;', 'We passed one or two dwellings, but were not disposed to stop...', '我们路过了一两个歇脚处，但却无意驻足。');
INSERT INTO `words` VALUES (1855, 'disposition', 'ˌdɪspəˈzɪʃn', 'n.', '性格;性情;倾向;', 'The rides are unsuitable for people of a nervous disposition...', '骑马这一活动不适合天性易紧张的人。');
INSERT INTO `words` VALUES (1856, 'dispute', 'dɪˈspjuːt', 'n.', '争论;辩论;争端;', 'They have won previous pay disputes with the government...', '在先前和政府的工资纠纷中他们赢了。');
INSERT INTO `words` VALUES (1857, 'disregard', 'ˌdɪsrɪˈɡɑːd', 'v.', '不理会;不顾;漠视;', 'He disregarded the advice of his executives...', '他对主管层的建议置若罔闻。');
INSERT INTO `words` VALUES (1858, 'disrupt', 'dɪsˈrʌpt', 'v.', '扰乱;使中断;打乱;', 'Anti-war protesters disrupted the debate.', '反战示威者们打断了辩论。');
INSERT INTO `words` VALUES (1859, 'disruptive', 'dɪsˈrʌptɪv', 'adj.', '引起混乱的;扰乱性的;破坏性的;', 'Alcohol can produce violent, disruptive behavior...', '酒精会引发暴力和破坏性行为。');
INSERT INTO `words` VALUES (1860, 'dissatisfaction', 'ˌdɪsˌsætɪsˈfækʃn', 'n.', '不快;不悦;不满意;', 'She has already expressed her dissatisfaction with this aspect of the policy...', '她对政策的这一方面已经表达了不满。');
INSERT INTO `words` VALUES (1861, 'disseminate', 'dɪˈsemɪneɪt', 'v.', '散布，传播(信息、知识等);', 'It took years to disseminate information about Aids in Africa...', '在非洲传播关于艾滋病方面的知识耗时数年。');
INSERT INTO `words` VALUES (1862, 'dissemination', 'dɪˌsemɪˈneɪʃn', 'n.', '宣传；散播；传染（病毒）;', 'It took years to disseminate information about Aids in Africa...', '在非洲传播关于艾滋病方面的知识耗时数年。');
INSERT INTO `words` VALUES (1863, 'dissertation', 'ˌdɪsəˈteɪʃn', 'n.', '专题论文;学位论文;', 'He is currently writing a dissertation on the Somali civil war.', '他目前正在写一篇关于索马里内战的论文。');
INSERT INTO `words` VALUES (1864, 'dissimilar', 'dɪˈsɪmɪlə(r)', 'adj.', '不一样的;不同的;不相似的;', 'His methods were not dissimilar to those used by Freud...', '他的方法和弗洛伊德的并无二致。');
INSERT INTO `words` VALUES (1865, 'dissipate', 'ˈdɪsɪpeɪt', 'v.', '(使)消散，消失;驱散;挥霍，浪费，消磨(时间、金钱等);', 'The tension in the room had dissipated...', '房间里的紧张气氛消失了。');
INSERT INTO `words` VALUES (1866, 'dissipation', 'ˌdɪsɪˈpeɪʃn', 'n.', '消散;驱散;挥霍;', 'Her face was a revelation of age and dissipation.', '她的脸暴露了她的年龄和放荡的生活。');
INSERT INTO `words` VALUES (1867, 'dissolution', 'ˌdɪsəˈluːʃn', 'n.', '(婚姻关系的)解除;(商业协议的)终止;(议会的)解散;', 'He stayed on until the dissolution of the firm in 1948...', '他一直呆到 1948 年公司解散。');
INSERT INTO `words` VALUES (1868, 'dissolve', 'dɪˈzɒlv', 'v.', '溶;使(固体)溶解;解除(婚姻关系);', 'Heat gently until the sugar dissolves...', '慢慢加热直到糖溶解为止。');
INSERT INTO `words` VALUES (1869, 'distance', 'ˈdɪstəns', 'n.', '距离;间距;(空间的)遥远;', '...the distance between the island and the nearby shore...', '该岛和附近陆地之间的距离');
INSERT INTO `words` VALUES (1870, 'distant', 'ˈdɪstənt', 'adj.', '遥远的;远处的;久远的;', 'The mountains rolled away to a distant horizon.', '群山向远处的地平线绵延而去。');
INSERT INTO `words` VALUES (1871, 'distend', 'dɪˈstend', 'v.', '(使)膨胀，肿胀;', 'Through this incision, the abdominal cavity is distended with carbon dioxide gas...', '二氧化碳气体通过这个切口进入，造成腹腔肿胀。');
INSERT INTO `words` VALUES (1872, 'distinct', 'dɪˈstɪŋkt', 'adj.', '清晰的;清楚的;明白的;', 'Engineering and technology are disciplines distinct from one another and from science...', '工程学和工艺学互不相同，也有别于自然学科。');
INSERT INTO `words` VALUES (1873, 'distinction', 'dɪˈstɪŋkʃn', 'n.', '差别;区别;对比;', 'There are obvious distinctions between the two wine-making areas...', '两个酿酒地区之间有着显著的差别。');
INSERT INTO `words` VALUES (1874, 'distinctive', 'dɪˈstɪŋktɪv', 'adj.', '独特的;特别的;有特色的;', '...the distinctive odour of chlorine.', '氯气特有的气味');
INSERT INTO `words` VALUES (1875, 'distinctly', 'dɪsˈtɪŋktli', 'adv.', '明显地；无疑地，确实地;', 'Engineering and technology are disciplines distinct from one another and from science...', '工程学和工艺学互不相同，也有别于自然学科。');
INSERT INTO `words` VALUES (1876, 'distinguish', 'dɪˈstɪŋɡwɪʃ', 'v.', '区分;辨别;分清;', 'Could he distinguish right from wrong?...', '他能分辨是非吗？');
INSERT INTO `words` VALUES (1877, 'distinguished', 'dɪˈstɪŋɡwɪʃt', 'adj.', '卓越的;杰出的;著名的;', '...a distinguished academic family.', '杰出的学者之家');
INSERT INTO `words` VALUES (1878, 'distort', 'dɪˈstɔːt', 'v.', '使变形;扭曲;使失真;', 'The media distorts reality; categorises people as all good or all bad...', '媒体会歪曲事实，将人说得不是完美无缺就是一无是处。');
INSERT INTO `words` VALUES (1879, 'distortion', 'dɪˈstɔːʃn', 'n.', '歪曲;曲解;失真;变形;', 'I think it would be a gross distortion of reality to say that they were motivated by self-interest...', '我认为说他们是受自身利益的驱使是对事实的严重歪曲。');
INSERT INTO `words` VALUES (1880, 'distract', 'dɪˈstrækt', 'v.', '转移(注意力);分散(思想);使分心;', 'Tom admits that playing video games sometimes distracts him from his homework...', '汤姆承认玩电子游戏有时会使他无心做作业。');
INSERT INTO `words` VALUES (1881, 'distraction', 'dɪˈstrækʃn', 'n.', '分散注意力的事;使人分心的事;娱乐;', 'I feel this is getting to be a distraction from what I really want to do...', '我觉得这开始让我无法集中精神做我真正想做的事情了。');
INSERT INTO `words` VALUES (1882, 'distress', 'dɪˈstres', 'n.', '忧虑;悲伤;痛苦;', 'Jealousy causes distress and painful emotions.', '嫉妒会带来忧虑和痛苦。');
INSERT INTO `words` VALUES (1883, 'distribute', 'dɪˈstrɪbjuːt', 'v.', '分发;分配;分销;', 'Students shouted slogans and distributed leaflets...', '学生们喊着口号，分发着传单。');
INSERT INTO `words` VALUES (1884, 'distributed', 'dɪˈstrɪbjuːtɪd', 'adj.', '分布的;分散的;', 'These cells are widely distributed throughout the body...', '这些细胞在人体内分布广泛。');
INSERT INTO `words` VALUES (1885, 'distribution', 'ˌdɪstrɪˈbjuːʃn', 'n.', '分配;分布;分发;', '...the council which controls the distribution of foreign aid...', '掌管外援物资派发的委员会');
INSERT INTO `words` VALUES (1886, 'distributor', 'dɪˈstrɪbjətə(r)', 'n.', '经销商;分销商;(发动机的)配电器，配电盘;', '...Spain\'s largest distributor of petroleum products...', '西班牙最大的石油产品分销商');
INSERT INTO `words` VALUES (1887, 'district', 'ˈdɪstrɪkt', 'n.', '地区;区域;区;', 'I drove around the business district.', '我开车在商业区转了转。');
INSERT INTO `words` VALUES (1888, 'disturb', 'dɪˈstɜːb', 'v.', '打扰;干扰;妨碍;', 'I hope I\'m not disturbing you.', '我希望没有打扰到你。');
INSERT INTO `words` VALUES (1889, 'disturbance', 'dɪˈstɜːbəns', 'n.', '(受)打扰，干扰，妨碍;骚乱;骚动;', 'During the disturbance which followed, three Englishmen were hurt.', '在接下来的骚乱中，3名英国人受伤。');
INSERT INTO `words` VALUES (1890, 'disturbing', 'dɪˈstɜːbɪŋ', 'adj.', '引起烦恼的;令人不安的;引起恐慌的;', 'There was something about him she found disturbing...', '他身上有某种东西使她不安。');
INSERT INTO `words` VALUES (1891, 'disuse', 'dɪsˈjuːs', 'n.', '不用;废弃;', '...a church which has fallen into disuse...', '废弃的教堂');
INSERT INTO `words` VALUES (1892, 'dither', 'ˈdɪðə(r)', 'v.', '犹豫不决;踌躇;', 'We\'re still dithering over whether to marry...', '我们仍在犹豫要不要结婚。');
INSERT INTO `words` VALUES (1893, 'dithering', 'ˈdɪðərɪŋ', 'v.', '犹豫不决;踌躇;', 'We\'re still dithering over whether to marry...', '我们仍在犹豫要不要结婚。');
INSERT INTO `words` VALUES (1894, 'ditto', 'ˈdɪtəʊ', 'n.', '(尤用于表格中)同上，同前;', 'Lister\'s dead. Ditto three Miami drug dealers and a lady.', '利斯特死了，还有三个迈阿密毒贩和一位女士也死了。');
INSERT INTO `words` VALUES (1895, 'diuretic', 'ˌdaɪjuˈretɪk', 'n.', '利尿药;', 'Alcohol acts as a diuretic, making you even more dehydrated.', '酒起着利尿剂的作用，会使你更加脱水。');
INSERT INTO `words` VALUES (1896, 'diver', 'ˈdaɪvə(r)', 'n.', '(通常有专用装备的)潜水员;跳水者;跳水运动员;', 'Police divers have recovered the body of a sixteen year old boy.', '警方潜水员已经找到了一名16岁男孩的尸体。');
INSERT INTO `words` VALUES (1897, 'diverge', 'daɪˈvɜːdʒ', 'v.', '分叉;岔开;分歧;', 'His interests increasingly diverged from those of his colleagues...', '他的兴趣和同事们越来越不一样了。');
INSERT INTO `words` VALUES (1898, 'divergence', 'daɪˈvɜːdʒəns', 'n.', '(意见、态度等的)分歧，差异;', 'There\'s a substantial divergence of opinion within the party...', '党内存在着重大的意见分歧。');
INSERT INTO `words` VALUES (1899, 'divergent', 'daɪˈvɜːdʒənt', 'adj.', '有分歧的;不同的;相异的;', '...two people who have divergent views on this question...', '在这个问题上意见相左的两个人');
INSERT INTO `words` VALUES (1900, 'diverse', 'daɪˈvɜːs', 'adj.', '不同的;相异的;多种多样的;', '...shops selling a diverse range of gifts...', '出售各种礼品的商店');
INSERT INTO `words` VALUES (1901, 'diversion', 'daɪˈvɜːʃn', 'n.', '转向;转移;偏离;', 'The robbers threw smoke bombs to create a diversion...', '劫匪扔烟幕弹来转移人们的视线。');
INSERT INTO `words` VALUES (1902, 'diversity', 'daɪˈvɜːsəti', 'n.', '差异(性);不同(点);多样性;', '...the cultural diversity of British society.', '英国社会文化的多元性');
INSERT INTO `words` VALUES (1903, 'divert', 'daɪˈvɜːt', 'v.', '使转向;使绕道;转移;', '...Rainham Marshes, east London, where a new bypass will divert traffic from the A13...', '伦敦东部的雷纳姆泽，那儿有一条新的旁道将会分流 A13 公路的交通');
INSERT INTO `words` VALUES (1904, 'divide', 'dɪˈvaɪd', 'v.', '(使)分开，分散，分割，分成…;分配;分享;', 'The physical benefits of exercise can be divided into three factors...', '运动对身体的好处可以分为3个方面。');
INSERT INTO `words` VALUES (1905, 'dividend', 'ˈdɪvɪdend', 'n.', '红利;股息;股利;', 'The first quarter dividend has been increased by nearly 4 per cent.', '第一季度的股息增长了近 4%。');
INSERT INTO `words` VALUES (1906, 'divider', 'dɪˈvaɪdə(r)', 'n.', '分隔物;分开物;分线规;', 'A curtain acted as a divider between this class and another.', '帘子作为分隔物将这间教室和另一间分开。');
INSERT INTO `words` VALUES (1907, 'divine', 'dɪˈvaɪn', 'adj.', '天赐的;上帝的;神的;', 'He suggested that the civil war had been a divine punishment.', '他暗示说内战是上天的惩罚。');
INSERT INTO `words` VALUES (1908, 'division', 'dɪˈvɪʒn', 'n.', '分开;分隔;分配;', '...the unification of Germany, after its division into two states at the end of World War Two.', '二战结束时曾被一分为二的德国实现了统一');
INSERT INTO `words` VALUES (1909, 'divorce', 'dɪˈvɔːs', 'n.', '离婚;分离;脱离;', 'Numerous marriages now end in divorce...', '现在许多婚姻都以离婚收场。');
INSERT INTO `words` VALUES (1910, 'dizziness', 'ˈdɪzinəs', 'n.', '头晕，头昏眼花;', 'Her head still hurt, and she felt slightly dizzy and disoriented...', '她的头还痛，并且觉得有些晕头转向。');
INSERT INTO `words` VALUES (1911, 'dizzy', 'ˈdɪzi', 'adj.', '头晕目眩的;眩晕的;使人眩晕的;', 'Her head still hurt, and she felt slightly dizzy and disoriented...', '她的头还痛，并且觉得有些晕头转向。');
INSERT INTO `words` VALUES (1912, 'dock', 'dɒk', 'n.', '船坞;船埠;码头;', '...the loading dock...', '装货码头');
INSERT INTO `words` VALUES (1913, 'doctor', 'ˈdɒktə(r)', 'n.', '医生;大夫;诊所;', 'Do not discontinue the treatment without consulting your doctor...', '未经医嘱不要擅自中断治疗。');
INSERT INTO `words` VALUES (1914, 'doctrine', 'ˈdɒktrɪn', 'n.', '教义;主义;学说;', '...the doctrine of reincarnation.', '轮回转世说');
INSERT INTO `words` VALUES (1915, 'documentary', 'ˌdɒkjuˈmentri', 'n.', '纪录影片;纪实广播(或电视)节目;', '...a TV documentary on homelessness.', '探讨无家可归问题的电视纪录片');
INSERT INTO `words` VALUES (1916, 'documentation', 'ˌdɒkjumenˈteɪʃn', 'n.', '必备资料;证明文件;文件记载;', 'Passengers must carry proper documentation.', '旅客必须携带有效证件。');
INSERT INTO `words` VALUES (1917, 'dodge', 'dɒdʒ', 'v.', '闪开;躲开;避开;', 'He dodged amongst the seething crowds of men...', '他在川流不息的人群中东躲西闪。');
INSERT INTO `words` VALUES (1918, 'dog', 'dɒɡ', 'n.', '狗;犬;公狗;', 'Outside, a dog was barking...', '外面，一只狗在叫。');
INSERT INTO `words` VALUES (1919, 'doggedly', '', 'adv.', '固执地；顽强地;', 'They have gained respect through sheer dogged determination.', '他们完全是凭借顽强的毅力赢得了尊敬。');
INSERT INTO `words` VALUES (1920, 'dollar', 'ˈdɒlə(r)', 'n.', '元(美国、加拿大、澳大利亚等国的货币单位);一元(纸币或硬币);美元(币值);', 'She gets paid seven dollars an hour...', '她的报酬是每小时 7元。');
INSERT INTO `words` VALUES (1921, 'domain', 'dəˈmeɪn', 'n.', '(知识、活动的)领域，范围，范畴;(尤指旧时个人、国家等所拥有或统治的)领土，领地，势力范围;域;', '...the great experimenters in the domain of art...', '艺术领域伟大的实验者们');
INSERT INTO `words` VALUES (1922, 'dome', 'dəʊm', 'n.', '穹顶;圆屋顶;圆顶状物;', '...the dome of St Paul\'s cathedral.', '圣保罗大教堂的圆顶');
INSERT INTO `words` VALUES (1923, 'domestic', 'dəˈmestɪk', 'adj.', '本国的;国内的;家用的;', '...over 100 domestic flights a day to 15 UK destinations.', '每天超过100架次国内航班飞往英国境内15个目的地');
INSERT INTO `words` VALUES (1924, 'domesticate', 'dəˈmestɪkeɪt', 'v.', '驯养，驯化(动物);驯化，培育(植物或农作物);使精于家务;', 'We domesticated the dog to help us with hunting.', '我们驯狗来帮我们打猎。');
INSERT INTO `words` VALUES (1925, 'dominance', 'ˈdɒmɪnəns', 'n.', '支配;控制;', '...an attempt by each group to establish dominance over the other...', '各个小组都想要控制其他小组的企图');
INSERT INTO `words` VALUES (1926, 'dominant', 'ˈdɒmɪnənt', 'adj.', '首要的;占支配地位的;占优势的;', '...a change which would maintain his party\'s dominant position in Scotland...', '将使他的政党在苏格兰继续占据支配地位的变化');
INSERT INTO `words` VALUES (1927, 'dominate', 'ˈdɒmɪneɪt', 'v.', '支配;控制;左右;', 'The book is expected to dominate the best-seller lists...', '这本书预计会占据畅销书排行榜的榜首。');
INSERT INTO `words` VALUES (1928, 'donate', 'dəʊˈneɪt', 'v.', '(尤指向慈善机构)捐赠，赠送;献(血);捐献(器官);', 'He frequently donates large sums to charity...', '他常常向慈善机构捐赠大笔钱款。');
INSERT INTO `words` VALUES (1929, 'donation', 'dəʊˈneɪʃn', 'n.', '捐赠物;捐赠;赠送;', 'Employees make regular donations to charity...', '员工定期向慈善机构捐赠。');
INSERT INTO `words` VALUES (1930, 'donor', 'ˈdəʊnə(r)', 'n.', '捐赠者;捐赠机构;献血者;', 'Doctors removed the healthy kidney from the donor.', '医生从捐献者体内取出健康的肾脏。');
INSERT INTO `words` VALUES (1931, 'doom', 'duːm', 'n.', '死亡;毁灭;厄运;', '...his warnings of impending doom.', '他关于大难临头的警告');
INSERT INTO `words` VALUES (1932, 'door', 'dɔː(r)', 'n.', '门;出入口;门口;', 'I knocked at the front door, but there was no answer...', '我敲了敲前门，没有人应。');
INSERT INTO `words` VALUES (1933, 'doors', 'dɔːz', 'n.', '门;出入口;门口;', 'I knocked at the front door, but there was no answer...', '我敲了敲前门，没有人应。');
INSERT INTO `words` VALUES (1934, 'doorway', 'ˈdɔːweɪ', 'n.', '门口;门道;出入口;', 'Hannah looked up to see David and another man standing in the doorway...', '汉娜抬头看见戴维和另一个男的站在门口。');
INSERT INTO `words` VALUES (1935, 'dormant', 'ˈdɔːmənt', 'adj.', '休眠的;蛰伏的;暂停活动的;', '...when the long dormant volcano of Mount St Helens erupted in 1980...', '当长期休眠的圣海伦斯火山在 1980 年爆发时');
INSERT INTO `words` VALUES (1936, 'dormitory', 'ˈdɔːmətri', 'n.', '集体宿舍;学生宿舍;', '...the boys\' dormitory...', '男生寝室');
INSERT INTO `words` VALUES (1937, 'dos', 'dɒs', 'n.', '磁盘操作系统;', 'Where do I find the instructions to load DOS programs from Windows 98?', '我从哪儿可以找到从Windows98载入DOS程序的指令呢?');
INSERT INTO `words` VALUES (1938, 'dose', 'dəʊs', 'n.', '(药的)一剂，一服;一份;一次;', 'One dose of penicillin can wipe out the infection.', '一剂青霉素就能消除感染。');
INSERT INTO `words` VALUES (1939, 'double', 'ˈdʌbl', 'adj.', '两倍的;加倍的;双的;', '...a pair of double doors into the room from the new entrance hall.', '从新的门厅通向该房间的一对双开门');
INSERT INTO `words` VALUES (1940, 'doubling', 'ˈdʌblɪŋ', 'v.', '(使)加倍;是…的两倍;把…对折;', '...a pair of double doors into the room from the new entrance hall.', '从新的门厅通向该房间的一对双开门');
INSERT INTO `words` VALUES (1941, 'doubt', 'daʊt', 'n.', '疑惑;疑问;不确定;', 'This raises doubts about the point of advertising...', '这对做广告的意义提出了疑问。');
INSERT INTO `words` VALUES (1942, 'doubtful', 'ˈdaʊtfl', 'adj.', '拿不定主意;不确定;怀疑;', 'For a time it seemed doubtful that he would move at all...', '有一段时间他似乎根本不会搬家。');
INSERT INTO `words` VALUES (1943, 'doubtless', 'ˈdaʊtləs', 'adv.', '大概;几乎肯定地;', 'He will doubtless try and persuade his colleagues to change their minds...', '他很可能会试图说服同事们改变主意。');
INSERT INTO `words` VALUES (1944, 'download', 'ˌdaʊnˈləʊd , ˈdaʊnləʊd', 'v.', '下载;', 'Users can download their material to a desktop PC.', '用户可以把资料下载到台式机上。');
INSERT INTO `words` VALUES (1945, 'downpour', 'ˈdaʊnpɔː(r)', 'n.', '倾盆大雨;暴雨;骤雨;', '...a sudden downpour of rain.', '突降的暴雨');
INSERT INTO `words` VALUES (1946, 'downstairs', 'ˌdaʊnˈsteəz', 'adv.', '顺楼梯而下;在楼下;往楼下;', 'Denise went downstairs and made some tea.', '丹尼丝下楼泡了茶。');
INSERT INTO `words` VALUES (1947, 'downstream', 'ˌdaʊnˈstriːm', 'adv.', '顺流而下;在下游方向;', 'We had drifted downstream.', '我们漂向了下游。');
INSERT INTO `words` VALUES (1948, 'downtown', 'ˈdaʊntaʊn , ˌdaʊnˈtaʊn', 'adv.', '在市中心，往市中心(尤指商业中心区);', '...an office in downtown Chicago.', '位于芝加哥市中心的办事处');
INSERT INTO `words` VALUES (1949, 'downward', 'ˈdaʊnwəd', 'adj.', '下降的;向下的;', '...a firm downward movement of the hands.', '双手有力的向下摆动');
INSERT INTO `words` VALUES (1950, 'downwards', 'ˈdaʊnwədz', 'adv.', '向下;', 'Benedict pointed downwards again with his stick...', '贝内迪克特用他的拐棍又往下指了指。');
INSERT INTO `words` VALUES (1951, 'doze', 'dəʊz', 'v.', '打瞌睡;打盹儿;小睡;', 'For a while she dozed fitfully.', '有一阵子，她时睡时醒。');
INSERT INTO `words` VALUES (1952, 'draft', 'drɑːft', 'n.', '草稿;草案;草图;', 'I rewrote his rough draft, which was published under my name...', '我把他的草稿重新改写，并以我的名字发表了。');
INSERT INTO `words` VALUES (1953, 'drag', 'dræɡ', 'v.', '(使劲而吃力地)拖，拉，拽，扯;缓慢而费力地移动(或行进);生拉硬拽;', 'He got up and dragged his chair towards the table.', '他站起来把他的椅子拖向桌子。');
INSERT INTO `words` VALUES (1954, 'drain', 'dreɪn', 'v.', '排空;(使)流光;放干;', 'Miners built the tunnel to drain water out of the mines...', '矿工们开凿了一条地道，以排出矿井里的水。');
INSERT INTO `words` VALUES (1955, 'drainage', 'ˈdreɪnɪdʒ', 'n.', '排水;放水;排水系统;', 'Line the pots with pebbles to ensure good drainage...', '在罐子里铺一层鹅卵石，以确保排水良好。');
INSERT INTO `words` VALUES (1956, 'dram', 'dræm', 'n.', '少量的酒(尤指威士忌);', '...a dram of whisky...', '一点威士忌');
INSERT INTO `words` VALUES (1957, 'drama', 'ˈdrɑːmə', 'n.', '戏;剧;戏剧文学;', 'He acted in radio dramas.', '他在广播剧中扮演角色。');
INSERT INTO `words` VALUES (1958, 'dramatic', 'drəˈmætɪk', 'adj.', '突然的;巨大的;令人吃惊的;', 'A fifth year of drought is expected to have dramatic effects on the California economy...', '第五年遭受旱灾预计会对加利福尼亚的经济产生巨大的影响。');
INSERT INTO `words` VALUES (1959, 'dramatically', 'drəˈmætɪk(ə)li', 'adv.', '戏剧地;显著地;', 'A fifth year of drought is expected to have dramatic effects on the California economy...', '第五年遭受旱灾预计会对加利福尼亚的经济产生巨大的影响。');
INSERT INTO `words` VALUES (1960, 'drastic', 'ˈdræstɪk', 'adj.', '极端的;急剧的;严厉的;', 'Drastic measures are needed to clean up the profession...', '行业清理整顿需要严厉的措施。');
INSERT INTO `words` VALUES (1961, 'draw', 'drɔː', 'v.', '(用铅笔、钢笔或粉笔)画，描绘，描画;拖(动);拉(动);', 'She would sit there drawing with the pencil stub...', '她会坐在那儿用铅笔头画画。');
INSERT INTO `words` VALUES (1962, 'drawback', 'ˈdrɔːbæk', 'n.', '缺点;不利条件;', 'He felt the apartment\'s only drawback was that it was too small.', '他觉得这个公寓唯一的缺点就是太小了。');
INSERT INTO `words` VALUES (1963, 'drawer', 'drɔː(r)', 'n.', '抽屉;开票人;出票人;', 'He opened a drawer in his writing-table.', '他打开了写字台的一个抽屉。');
INSERT INTO `words` VALUES (1964, 'drawing', 'ˈdrɔːɪŋ', 'n.', '图画;素描画;绘画(艺术);', 'She did a drawing of me.', '她给我画了张像。');
INSERT INTO `words` VALUES (1965, 'dread', 'dred', 'v.', '非常害怕;极为担心;', 'I\'m dreading Christmas this year...', '今年我非常害怕过圣诞节。');
INSERT INTO `words` VALUES (1966, 'dreaded', 'ˈdredɪd', 'adj.', '令人害怕的;可怕的;', 'No one knew how to treat this dreaded disease.', '没人知道如何治疗这种可怕的疾病。');
INSERT INTO `words` VALUES (1967, 'dreadful', 'ˈdredfl', 'adj.', '糟糕透顶的;讨厌的;令人不快的;', 'They told us the dreadful news...', '他们告诉了我们那糟糕透顶的消息。');
INSERT INTO `words` VALUES (1968, 'dreary', 'ˈdrɪəri', 'adj.', '令人沮丧的;沉闷的;枯燥无味的;', '...a dreary little town in the Midwest...', '位于中西部的沉闷的小镇');
INSERT INTO `words` VALUES (1969, 'drench', 'drentʃ', 'v.', '使湿透;', 'They turned fire hoses on the people and drenched them...', '他们将消防水管对着人们，把他们浇了个透。');
INSERT INTO `words` VALUES (1970, 'dress', 'dres', 'n.', '连衣裙;衣服;', 'She was wearing a black dress.', '她穿一条黑色连衣裙。');
INSERT INTO `words` VALUES (1971, 'dressing', 'ˈdresɪŋ', 'n.', '(拌制色拉用的)调料;(保护伤口的)敷料;穿衣;', 'Mix the ingredients for the dressing in a bowl.', '在碗里将各种原料混成调料。');
INSERT INTO `words` VALUES (1972, 'drift', 'drɪft', 'n.', '流动;趋势;逐渐变化(尤指向坏的方面);', 'We proceeded to drift on up the river...', '我们继续向河流的上游漂流。');
INSERT INTO `words` VALUES (1973, 'drill', 'drɪl', 'n.', '钻;钻头;钻床;', '...pneumatic drills.', '风钻');
INSERT INTO `words` VALUES (1974, 'drilling', 'ˈdrɪlɪŋ', 'v.', '钻(孔);打(眼);培训;', '...pneumatic drills.', '风钻');
INSERT INTO `words` VALUES (1975, 'drink', 'drɪŋk', 'n.', '饮料;一杯，一份，一口(饮料);酒;', 'He drank his cup of tea...', '他喝他的那杯茶。');
INSERT INTO `words` VALUES (1976, 'drip', 'drɪp', 'v.', '滴下;滴出;滴水;', 'Sit your child forward and let the blood drip into a tissue or on to the floor...', '让你的孩子前倾坐着，让血滴在纸巾上或地板上。');
INSERT INTO `words` VALUES (1977, 'drive', 'draɪv', 'v.', '驾驶;开车;驾车送(人);', 'I drove into town and went to a restaurant for dinner...', '我驱车到市里一家餐馆就餐。');
INSERT INTO `words` VALUES (1978, 'driver', 'ˈdraɪvə(r)', 'n.', '驾驶员;司机;驾车者;', 'The driver got out of his van.', '司机从货车上下来。');
INSERT INTO `words` VALUES (1979, 'driving', 'ˈdraɪvɪŋ', 'n.', '行车的方式;驾驶;行车;', 'Consumer spending was the driving force behind the economic growth in the summer...', '顾客消费是夏季经济增长的强大推动力。');
INSERT INTO `words` VALUES (1980, 'droop', 'druːp', 'v.', '(尤指因衰弱或疲劳)低垂，垂落，垂下;沮丧;消沉;', 'Crook\'s eyelids drooped and he yawned...', '克鲁克的眼皮耷拉下来,打了个哈欠。');
INSERT INTO `words` VALUES (1981, 'drop', 'drɒp', 'v.', '(意外地)落下，掉下，使落下;(故意)降下，使降落;累倒;', 'Temperatures can drop to freezing at night...', '夜间温度可能会降到零度以下。');
INSERT INTO `words` VALUES (1982, 'droplet', 'ˈdrɒplət', 'n.', '小滴;', 'Droplets of sweat were welling up on his forehead.', '他额头上冒出了滴滴汗珠。');
INSERT INTO `words` VALUES (1983, 'drought', 'draʊt', 'n.', '久旱;旱灾;', 'Drought and famines have killed up to two million people here.', '旱灾和饥荒夺去了这里200万人的生命。');
INSERT INTO `words` VALUES (1984, 'drown', 'draʊn', 'v.', '(使)淹死，溺死;浸透;淹没;', 'Forty-eight people have drowned after their boat capsized during a storm...', '他们的船在暴风雨中倾覆后有 48 人溺亡。');
INSERT INTO `words` VALUES (1985, 'drowse', 'draʊz', 'v.', '打瞌睡;打盹;假寐;', 'Nina drowsed for a while.', '尼娜打了会盹。');
INSERT INTO `words` VALUES (1986, 'drudgery', 'ˈdrʌdʒəri', 'n.', '单调乏味的苦差事;繁重无聊的工作;', 'People want to get away from the drudgery of their everyday lives.', '人们想摆脱日常生活中单调乏味的工作。');
INSERT INTO `words` VALUES (1987, 'drug', 'drʌɡ', 'n.', '毒品;药;药物;', 'The drug will be useful to hundreds of thousands of infected people.', '这种药将惠及成千上万的感染者。');
INSERT INTO `words` VALUES (1988, 'drunk', 'drʌŋk', 'adj.', '(酒)醉;陶醉;沉醉;', 'Stewart could not remember exactly why he had done it because he was so drunk...', '斯图尔特记不清他为什么要那样做了,因为他烂醉如泥。');
INSERT INTO `words` VALUES (1989, 'dry', 'draɪ', 'adj.', '干的;干燥的;雨少的;', 'Clean the metal with a soft dry cloth...', '用柔软的干布擦拭金属。');
INSERT INTO `words` VALUES (1990, 'dryer', 'ˈdraɪə(r)', 'n.', '烘干机;脱水机;干燥机;', 'If you buy a drier, look for one with a sensor which switches off when clothes are dry.', '你若要买烘干机,找一台带感应器的,可以在衣服干的时候自动断电。');
INSERT INTO `words` VALUES (1991, 'dual', 'ˈdjuːəl', 'adj.', '两部分的;双重的;双的;', '...his dual role as head of the party and head of state...', '他作为政党领袖和国家元首的双重身份');
INSERT INTO `words` VALUES (1992, 'dub', 'dʌb', 'v.', '把…戏称为;给…起绰号;为(影片或电视节目)配音;', '...the man whom the Labour opposition dubbed as the \'no change Prime Minister\'...', '被工党反对派戏称为“不变首相”的男人');
INSERT INTO `words` VALUES (1993, 'dubious', 'ˈdjuːbiəs', 'adj.', '怀疑;无把握;拿不准;', 'This claim seems to us to be rather dubious...', '在我们看来这种说法非常可疑。');
INSERT INTO `words` VALUES (1994, 'duck', 'dʌk', 'n.', '鸭;母鸭;鸭肉;', 'Chickens and ducks scratch around the outbuildings.', '鸡鸭在棚子周围到处乱刨。');
INSERT INTO `words` VALUES (1995, 'due', 'djuː', 'adj.', '由于;因为;预定;', 'The country\'s economic problems are largely due to the weakness of the recovery...', '该国的经济问题很大程度上是因为复苏乏力。');
INSERT INTO `words` VALUES (1996, 'duke', 'djuːk', 'n.', '公爵;(尤指旧时欧洲部分地区小公国的)君主;', '...the Queen and the Duke of Edinburgh.', '女王和爱丁堡公爵');
INSERT INTO `words` VALUES (1997, 'dull', 'dʌl', 'adj.', '枯燥无味的;无聊的;令人生厌的;', 'They are both nice people but can be rather dull...', '他俩都是好人但非常沉闷。');
INSERT INTO `words` VALUES (1998, 'duly', 'ˈdjuːli', 'adv.', '适当地;恰当地;按时地;', 'It was the beginning of the end and Watson duly went on to win his fourth Open Golf Championship...', '这是一场胜负已定的比赛，沃森不负众望赢得了他第四次高尔夫公开赛的冠军。');
INSERT INTO `words` VALUES (1999, 'dumb', 'dʌm', 'adj.', '哑的;不能说话的;一时说不出话的;', '...a young deaf and dumb man.', '年轻的聋哑男子');
INSERT INTO `words` VALUES (2000, 'dummy', 'ˈdʌmi', 'n.', '(尤指缝制或陈列服装用的)人体模型;仿制品;仿造物;', '...the bottom half of a shop-window dummy.', '商店橱窗里假人模特的下半身');
INSERT INTO `words` VALUES (2001, 'dump', 'dʌmp', 'v.', '(尤指在不合适的地方)丢弃，扔掉，倾倒;丢下;抛弃;', 'We dumped our bags at the nearby Grand Hotel and hurried towards the market...', '我们把包扔在附近的格兰德酒店后就匆匆赶往集市。');
INSERT INTO `words` VALUES (2002, 'dumping', 'ˈdʌmpɪŋ', 'n.', '(尤指危险物质的)倾倒，倾卸;', 'We dumped our bags at the nearby Grand Hotel and hurried towards the market...', '我们把包扔在附近的格兰德酒店后就匆匆赶往集市。');
INSERT INTO `words` VALUES (2003, 'dupe', 'djuːp', 'v.', '诈骗;哄骗;欺骗;', '...a plot to dupe stamp collectors into buying fake rarities...', '诱骗集邮者购买珍稀邮票赝品的阴谋');
INSERT INTO `words` VALUES (2004, 'duplication', 'ˌdjuːplɪˈkeɪʃn', 'n.', '(不必要的)重复;', '...unnecessary duplication of resources.', '不必要的资源重复');
INSERT INTO `words` VALUES (2005, 'durability', 'ˌdjʊərəˈbɪlɪti', 'n.', '耐久（用）性；经（持）久性；耐（使）用年限；寿命；强度；续（耐）航力;', 'Bone china is strong and durable.', '骨灰瓷坚固耐用。');
INSERT INTO `words` VALUES (2006, 'durable', 'ˈdjʊərəbl', 'adj.', '耐用的;持久的;', 'Bone china is strong and durable.', '骨灰瓷坚固耐用。');
INSERT INTO `words` VALUES (2007, 'duration', 'djuˈreɪʃn', 'n.', '持续时间;期间;', 'He was given the task of protecting her for the duration of the trial...', '他的任务是在审讯期间保护她。');
INSERT INTO `words` VALUES (2008, 'during', 'ˈdjʊərɪŋ', 'prep.', '在…期间;在…期间的某个时候;', 'Sandstorms are common during the Saudi Arabian winter...', '在沙特阿拉伯,冬季里沙尘暴很常见。');
INSERT INTO `words` VALUES (2009, 'dusk', 'dʌsk', 'n.', '黄昏;傍晚;', 'We arrived home at dusk.', '我们傍晚到家。');
INSERT INTO `words` VALUES (2010, 'dust', 'dʌst', 'n.', '沙土;尘土;(建筑物内、家具或地板等上的)灰尘，尘埃;', 'Tanks raise huge trails of dust when they move...', '坦克开动时扬起大量尘土。');
INSERT INTO `words` VALUES (2011, 'dusty', 'ˈdʌsti', 'adj.', '布满灰尘的;灰尘覆盖的;土灰色的;', 'They started strolling down the dusty road in the moonlight.', '在月光下他们开始沿着满是尘土的路漫步。');
INSERT INTO `words` VALUES (2012, 'dutch', 'dʌtʃ', 'adj.', '荷兰的;荷兰人的;荷兰语的;', '...the Dutch prime minister.', '荷兰首相');
INSERT INTO `words` VALUES (2013, 'duties', 'ˈdjuːtiz', 'n.', '责任;义务;本分;', 'Staff must report for duty at their normal place of work...', '职员必须在平时上班的地方签到。');
INSERT INTO `words` VALUES (2014, 'duty', 'ˈdjuːti', 'n.', '责任;义务;本分;', 'Staff must report for duty at their normal place of work...', '职员必须在平时上班的地方签到。');
INSERT INTO `words` VALUES (2015, 'dwarf', 'dwɔːf', 'n.', '(神话中有魔法的)小矮人;矮子;侏儒;', 'His figure is dwarfed by the huge red McDonald\'s sign...', '在巨大的红色麦当劳标志牌下，他显得很矮小。');
INSERT INTO `words` VALUES (2016, 'dwell', 'dwel', 'v.', '居住;栖身;', 'I\'d rather not dwell on the past.', '我不想再沉湎于过去了。');
INSERT INTO `words` VALUES (2017, 'dweller', 'ˈdwelə(r)', 'n.', '居民;居住者;栖身者;', 'The number of city dwellers is growing...', '城市居民的数量正在增长。');
INSERT INTO `words` VALUES (2018, 'dwelling', 'ˈdwelɪŋ', 'n.', '住宅;住所;公寓;', 'Some 3,500 new dwellings are planned for the area...', '该地区计划筹建约 3,500 所新居。');
INSERT INTO `words` VALUES (2019, 'dwindle', 'ˈdwɪndl', 'v.', '(逐渐)减少，变小，缩小;', 'The factory\'s workforce has dwindled from over 4,000 to a few hundred...', '工厂雇员总数已经从 4,000 多人减少到几百人。');
INSERT INTO `words` VALUES (2020, 'dye', 'daɪ', 'v.', '给…染色;染;', 'The women prepared, spun and dyed the wool...', '女人们处理羊毛,纺羊毛并给羊毛染色。');
INSERT INTO `words` VALUES (2021, 'dynamic', 'daɪˈnæmɪk', 'n.', '(人或事物)相互作用的方式，动态;力学;动力学;', 'He seemed a dynamic and energetic leader...', '他似乎是一个富有干劲、精力充沛的领导。');
