INSERT INTO `words` VALUES (2053, 'economically', 'ˌiːkəˈnɒmɪkli', 'adv.', '在经济上;在经济学上;经济地;', '...plans to trade in their car for something smaller and more economical...', '把他们的车折价贴换成更小、更经济型车的计划');
