INSERT INTO `words` VALUES (2062, 'edition', 'ɪˈdɪʃn', 'n.', '版本(出版形式);(报纸、杂志的)一份;(广播、电视节目的)一期，一辑;', 'A paperback edition is now available at bookshops.', '平装本现在在书店可以买到。');
