INSERT INTO `words` VALUES (2068, 'effect', 'ɪˈfekt', 'n.', '效应;影响;结果;', 'Parents worry about the effect of music on their adolescent\'s behavior...', '父母担心音乐对青春期孩子的行为所产生的影响。');
INSERT INTO `words` VALUES (2069, 'effective', 'ɪˈfektɪv', 'adj.', '产生预期结果的;有效的;实际的;', 'The project looks at how we could be more effective in encouraging students to enter teacher training...', '该项目着眼于我们如何能够更加有效地鼓励学生参与教师培训。');
INSERT INTO `words` VALUES (2070, 'effectively', 'ɪˈfektɪvli', 'adv.', '有效地;实际上;事实上;', 'This effectively means that the government does not agree with the proposals...', '这实际上就意味着政府不赞同这些提议。');
INSERT INTO `words` VALUES (2071, 'effectiveness', 'ɪˈfektɪvnəs', 'n.', '效率（果，力，用，能，应）；有效性;', 'The project looks at how we could be more effective in encouraging students to enter teacher training...', '该项目着眼于我们如何能够更加有效地鼓励学生参与教师培训。');
INSERT INTO `words` VALUES (2072, 'efficacious', 'ˌefɪˈkeɪʃəs', 'adj.', '有效的;奏效的;灵验的;', 'The nasal spray was new on the market and highly efficacious.', '这种鼻腔喷雾剂是新上市的，非常有效。');
