INSERT INTO `words` VALUES (2074, 'efficiency', 'ɪˈfɪʃnsi', 'n.', '效率;效能;功效;', 'There are many ways to increase agricultural efficiency in the poorer areas of the world.', '有很多方法能够提高世界贫困地区的农业效率。');
