INSERT INTO `words` VALUES (2111, 'elevate', 'ˈelɪveɪt', 'v.', '提拔，晋升，提升(到不应有的位置);举起;抬起;', 'He was elevated to the post of prime minister.', '他升任总理。');
INSERT INTO `words` VALUES (2112, 'elevation', 'ˌelɪˈveɪʃn', 'n.', '提拔;晋级;提升;', '...the addition of two-storey wings on the north and south elevations.', '在南北向的立视图上各增加两层配楼');
INSERT INTO `words` VALUES (2113, 'eleven', 'ɪˈlevn', 'n.', '十一;十一个;第十一十一;', '...the Princess and her eleven friends.', '公主和她的11位朋友');
INSERT INTO `words` VALUES (2114, 'eleventh', 'ɪˈlevnθ', 'ord.', '第十一;', 'We were working on the eleventh floor.', '我们在11层办公。');
INSERT INTO `words` VALUES (2115, 'elicit', 'iˈlɪsɪt', 'v.', '引出;探出;诱出;', 'Mr Norris said he was hopeful that his request would elicit a positive response...', '诺里斯先生说希望他的请求能够引起积极反响。');
INSERT INTO `words` VALUES (2116, 'eligible', 'ˈelɪdʒəbl', 'adj.', '有资格的;合格的;具备条件的;', 'Almost half the population are eligible to vote in today\'s election...', '大约有一半人有资格参加今天的选举投票。');
INSERT INTO `words` VALUES (2117, 'eliminate', 'ɪˈlɪmɪneɪt', 'v.', '排除;清除;消除;', 'The Sex Discrimination Act has not eliminated discrimination in employment...', '《反性别歧视法》并未根除工作中的歧视。');
INSERT INTO `words` VALUES (2118, 'elimination', 'ɪˌlɪmɪˈneɪʃn', 'n.', '淘汰；排除；消除；消灭；淘汰赛；排除；略去；丢弃;（英格兰古法）驱逐，放逐;排泄废物，排粪;', 'The Sex Discrimination Act has not eliminated discrimination in employment...', '《反性别歧视法》并未根除工作中的歧视。');
INSERT INTO `words` VALUES (2119, 'elite', 'eɪˈliːt', 'n.', '上层集团;(统称)掌权人物，社会精英;', '...a government comprised mainly of the elite...', '主要由精英组成的政府');
INSERT INTO `words` VALUES (2120, 'ellipse', 'ɪˈlɪps', 'n.', '椭圆;', 'The Earth orbits in an ellipse.', '地球轨道呈椭圆形。');
INSERT INTO `words` VALUES (2121, 'elliptical', 'ɪˈlɪptɪkl', 'adj.', '省略的;隐晦的;椭圆的;', '...the moon\'s elliptical orbit...', '月球的椭圆形轨道');
INSERT INTO `words` VALUES (2122, 'eloquence', 'ˈɛləʊkwəns', 'n.', '雄辩，善辩，口才，雄辩术;', 'I heard him make a very eloquent speech at that dinner.', '在那次晚宴上，我听到他作了一番非常有说服力的陈词。');
INSERT INTO `words` VALUES (2123, 'eloquent', 'ˈeləkwənt', 'adj.', '雄辩的;有口才的;流利的;', 'I heard him make a very eloquent speech at that dinner.', '在那次晚宴上，我听到他作了一番非常有说服力的陈词。');
INSERT INTO `words` VALUES (2124, 'else', 'els', 'adv.', '其他的;别的;另外的;', 'If I can\'t make a living at painting, at least I can teach someone else to paint...', '如果我不能靠画画谋生，至少我可以教别人画画。');
INSERT INTO `words` VALUES (2125, 'elsewhere', 'ˌelsˈweə(r)', 'adv.', '在(或去、到)别处;', 'Almost 80 percent of the state\'s residents were born elsewhere...', '该州居民中几乎有80%出生在异地。');
INSERT INTO `words` VALUES (2126, 'elucidate', 'iˈluːsɪdeɪt', 'v.', '阐明;解释;说明;', 'Haig went on to elucidate his personal principle of war...', '海格继续解释他个人的战争原则。');
INSERT INTO `words` VALUES (2127, 'elucidation', '', 'n.', '阐明，解释;', 'Haig went on to elucidate his personal principle of war...', '海格继续解释他个人的战争原则。');
INSERT INTO `words` VALUES (2128, 'elusive', 'iˈluːsɪv', 'adj.', '难找的;难以解释的;难以达到的;', 'In London late-night taxis are elusive and far from cheap.', '在伦敦，深夜时难以打到出租车，而且车费非常贵。');
INSERT INTO `words` VALUES (2129, 'emancipate', 'ɪˈmænsɪpeɪt', 'v.', '解放;使不受(法律、政治或社会的)束缚;', 'Catholics were emancipated in 1792...', '天主教徒在1792年获得了解放。');
INSERT INTO `words` VALUES (2130, 'emancipation', 'ɪˌmænsɪˈpeɪʃən', 'n.', '自立；解放；释放；摆脱管束；脱离;', 'Catholics were emancipated in 1792...', '天主教徒在1792年获得了解放。');
INSERT INTO `words` VALUES (2131, 'embargo', 'ɪmˈbɑːɡəʊ', 'n.', '禁止贸易令;禁运;', 'The United Nations imposed an arms embargo against the country...', '联合国对那个国家实施了武器禁运。');
INSERT INTO `words` VALUES (2132, 'embark', 'ɪmˈbɑːk', 'v.', '上船;装船;', 'He\'s embarking on a new career as a writer...', '他即将开始新的职业生涯——当一名作家。');
INSERT INTO `words` VALUES (2133, 'embarrass', 'ɪmˈbærəs', 'v.', '(尤指在社交场合)使窘迫，使尴尬;使困惑;使为难;', 'His clumsiness embarrassed him...', '他因自己的笨拙而感到尴尬。');
INSERT INTO `words` VALUES (2134, 'embarrassing', 'ɪmˈbærəsɪŋ', 'adj.', '使人害羞的(或难堪的、惭愧的);使显得愚蠢的(或不诚实的等);', 'That was an embarrassing situation for me...', '那种情形让我难堪。');
INSERT INTO `words` VALUES (2135, 'embarrassment', 'ɪmˈbærəsmənt', 'n.', '害羞;窘迫;愧疚;', 'It is a source of embarrassment to Londoners that the standard of pubs is so low...', '酒馆水准之低让伦敦人觉得颜面尽失。');
INSERT INTO `words` VALUES (2136, 'embassy', 'ˈembəsi', 'n.', '大使馆;(统称)使馆官员;大使馆(指馆舍);', 'The American Embassy has already complained...', '美国大使馆已经提出了抗议。');
INSERT INTO `words` VALUES (2137, 'embed', 'ɪmˈbed', 'v.', '把…牢牢地嵌入(或插入、埋入);派遣(战地记者、摄影记者等);嵌入(在I\'m aware that she knows句中，she knows为内嵌句);', 'One of the bullets passed through Andrea\'s chest before embedding itself in a wall.', '其中一颗子弹射穿了安德烈亚的胸部，打入一面墙中。');
INSERT INTO `words` VALUES (2138, 'embedded', 'ɪmˈbedɪd', 'v.', '把…牢牢地嵌入(或插入、埋入);派遣(战地记者、摄影记者等);嵌入(在I\'m aware that she knows句中，she knows为内嵌句);', 'One of the bullets passed through Andrea\'s chest before embedding itself in a wall.', '其中一颗子弹射穿了安德烈亚的胸部，打入一面墙中。');
INSERT INTO `words` VALUES (2139, 'embody', 'ɪmˈbɒdi', 'v.', '具体表现，体现，代表(思想或品质);包括;包含;', 'Jack Kennedy embodied all the hopes of the 1960s...', '杰克·肯尼迪代表了20世纪60年代的全部希望。');
INSERT INTO `words` VALUES (2140, 'embrace', 'ɪmˈbreɪs', 'v.', '抱;拥抱;欣然接受，乐意采纳(思想、建议等);', 'Penelope came forward and embraced her sister...', '佩内洛普走上前拥抱了她妹妹。');
INSERT INTO `words` VALUES (2141, 'embroider', 'ɪmˈbrɔɪdə(r)', 'v.', '刺绣;加以渲染(或润色);添枝加叶;', 'The collar was embroidered with very small red strawberries...', '衣领上绣着非常小的红色草莓。');
INSERT INTO `words` VALUES (2142, 'embroidery', 'ɪmˈbrɔɪdəri', 'n.', '绣花;刺绣图案;刺绣品;', 'The shorts had blue embroidery over the pockets...', '这条短裤的口袋上有蓝色刺绣。');
INSERT INTO `words` VALUES (2143, 'emerge', 'iˈmɜːdʒ', 'v.', '(从隐蔽处或暗处)出现，浮现，露出;暴露;露出真相;', 'Richard was waiting outside the door as she emerged...', '她出现的时候，理查德正在门外等着。');
INSERT INTO `words` VALUES (2144, 'emergency', 'iˈmɜːdʒənsi', 'n.', '突发事件;紧急情况;', 'He deals with emergencies promptly...', '他迅速处理突发事件。');
INSERT INTO `words` VALUES (2145, 'emigrate', 'ˈemɪɡreɪt', 'v.', '移居国外;移民;', 'He emigrated to Belgium...', '他移民到比利时。');
INSERT INTO `words` VALUES (2146, 'emigration', 'ˌemɪˈɡreɪʃn', 'n.', '移民出境，移居国外，侨居，（总称）移民;血细胞渗出;', 'He emigrated to Belgium...', '他移民到比利时。');
INSERT INTO `words` VALUES (2147, 'eminent', 'ˈemɪnənt', 'adj.', '(尤指在某专业中)卓越的，著名的，显赫的;非凡的;杰出的;', '...an eminent scientist.', '著名科学家');
INSERT INTO `words` VALUES (2148, 'emission', 'iˈmɪʃn', 'n.', '(光、热、气等的)发出，射出，排放;排放物;散发物;', 'The emission of gases such as carbon dioxide should be stabilised at their present level...', '二氧化碳等气体的排放应该稳定在当前水平。');
INSERT INTO `words` VALUES (2149, 'emit', 'iˈmɪt', 'v.', '发出，射出，散发(光、热、声音、气等);', 'The new device emits a powerful circular column of light.', '新设备发出明亮的圆形光柱。');
INSERT INTO `words` VALUES (2150, 'emotion', 'ɪˈməʊʃn', 'n.', '强烈的感情;激情;情感;', 'Happiness was an emotion that Reynolds was having to relearn...', '雷诺兹已经不知道什么是快乐。');
INSERT INTO `words` VALUES (2151, 'emotional', 'ɪˈməʊʃənl', 'adj.', '感情的;情感的;情绪的;', 'I needed this man\'s love, and the emotional support he was giving me...', '我需要这个男人的爱，以及他在情感上给我的支持。');
INSERT INTO `words` VALUES (2152, 'emphasis', 'ˈemfəsɪs', 'n.', '强调;重视;重要性;', 'Too much emphasis is placed on research...', '过分强调研究的重要性。');
INSERT INTO `words` VALUES (2153, 'emphatic', 'ɪmˈfætɪk', 'adj.', '强调的;有力的;明确表示的;', 'His response was immediate and emphatic...', '他的反应迅速而强烈。');
INSERT INTO `words` VALUES (2154, 'empire', 'ˈempaɪə(r)', 'n.', '帝国;大企业;企业集团;', '...the Roman Empire.', '罗马帝国');
INSERT INTO `words` VALUES (2155, 'empirical', 'ɪmˈpɪrɪkl', 'adj.', '以实验(或经验)为依据的;经验主义的;', 'There is no empirical evidence to support his thesis.', '他的论文缺乏实验证据的支持。');
INSERT INTO `words` VALUES (2156, 'empirically', '', 'adv.', '以经验为主地;', 'There is no empirical evidence to support his thesis.', '他的论文缺乏实验证据的支持。');
INSERT INTO `words` VALUES (2157, 'employ', 'ɪmˈplɔɪ', 'v.', '雇用;应用;运用;', 'The company employs 18 staff...', '公司雇用了18名员工。');
INSERT INTO `words` VALUES (2158, 'employee', 'ɪmˈplɔɪiː', 'n.', '受雇者;雇工;雇员;', 'He is an employee of Fuji Bank...', '他是富士银行的雇员。');
INSERT INTO `words` VALUES (2159, 'employer', 'ɪmˈplɔɪə(r)', 'n.', '雇用者;雇主;老板;', 'He had been sent to Rome by his employer...', '他已被老板派往罗马。');
INSERT INTO `words` VALUES (2160, 'employment', 'ɪmˈplɔɪmənt', 'n.', '工作;职业;受雇;', 'She was unable to find employment...', '她没能找到工作。');
INSERT INTO `words` VALUES (2161, 'emptiness', 'ˈemptinəs', 'n.', '空虚;空无;空旷;', 'The result later in life may be feelings of emptiness and depression.', '之后对生活产生的影响可能会是空虚感和沮丧感。');
INSERT INTO `words` VALUES (2162, 'empty', 'ˈempti', 'adj.', '空的;空洞的;说话不算数的;', 'The room was bare and empty.', '房间空荡荡的。');
INSERT INTO `words` VALUES (2163, 'emulate', 'ˈemjuleɪt', 'v.', '努力赶上;同…竞争;仿真;', 'Sons are traditionally expected to emulate their fathers.', '历来认为儿子会仿效父亲。');
INSERT INTO `words` VALUES (2164, 'emulation', 'ˌɛmjʊˈleɪʃən', 'n.', '仿真（效）（技术）；模仿（拟）；竞争；效法;', 'Sons are traditionally expected to emulate their fathers.', '历来认为儿子会仿效父亲。');
INSERT INTO `words` VALUES (2165, 'emulsion', 'ɪˈmʌlʃn', 'n.', '乳状液;乳浊液;乳剂;', '...an undercoat of white emulsion paint.', '用白色乳胶漆作底漆');
INSERT INTO `words` VALUES (2166, 'enable', 'ɪˈneɪbl', 'v.', '使能够;使有机会;使成为可能;', 'The new test should enable doctors to detect the disease early.', '新的检测手段应该能够使医生们尽早查出这种疾病。');
INSERT INTO `words` VALUES (2167, 'enabled', 'ɪˈneɪbld', 'v.', '使能够;使有机会;使成为可能;', 'The new test should enable doctors to detect the disease early.', '新的检测手段应该能够使医生们尽早查出这种疾病。');
INSERT INTO `words` VALUES (2168, 'encapsulate', 'ɪnˈkæpsjuleɪt', 'v.', '简述;概括;压缩;', 'A Wall Street Journal editorial encapsulated the views of many conservatives...', '《华尔街日报》的一篇社论概述了很多保守派人士的观点。');
INSERT INTO `words` VALUES (2169, 'encapsulation', '', 'n.', '封装，包封，密封，包围，包囊，胶囊包，灌封，封铸，密封，封结，封装，胶囊化，吸器包囊，人工卵;', 'A Wall Street Journal editorial encapsulated the views of many conservatives...', '《华尔街日报》的一篇社论概述了很多保守派人士的观点。');
INSERT INTO `words` VALUES (2170, 'encase', 'ɪnˈkeɪs', 'v.', '把…装箱(或围住、包起);', 'When nuclear fuel is manufactured it is encased in metal cans...', '核燃料生产出来后被装在金属罐中。');
INSERT INTO `words` VALUES (2171, 'enchant', 'ɪnˈtʃɑːnt', 'v.', '使着迷;使陶醉;使中魔法;', 'Dena was enchanted by the house...', '德娜非常喜欢那座房子。');
INSERT INTO `words` VALUES (2172, 'encircle', 'ɪnˈsɜːkl', 'v.', '环绕;围绕;包围;', 'A forty-foot-high concrete wall encircles the jail...', '监狱周围有一圈40英尺高的混凝土墙。');
INSERT INTO `words` VALUES (2173, 'enclose', 'ɪnˈkləʊz', 'v.', '(用墙、篱笆等)把…围起来;围住;附入;', 'The rules state that samples must be enclosed in two watertight containers...', '规则要求样本必须装在两个水密容器中。');
INSERT INTO `words` VALUES (2174, 'enclosure', 'ɪnˈkləʊʒə(r)', 'n.', '圈占地;圈用地;围场;', 'This enclosure was so vast that the outermost wall could hardly be seen.', '这块围场如此之大，几乎看不见最远端的围墙。');
INSERT INTO `words` VALUES (2175, 'encode', 'ɪnˈkəʊd', 'v.', '把…译成电码(或密码);把…编码;把…译成外语;', 'The two parties encode confidential data in a form that is not directly readable by the other party...', '双方将机密资料加密，这样对方就无法直接读取资料。');
INSERT INTO `words` VALUES (2176, 'encounter', 'ɪnˈkaʊntə(r)', 'v.', '遭遇，遇到(尤指令人不快或困难的事);偶然碰到;意外地遇见;', 'Every day of our lives we encounter stresses of one kind or another...', '我们在每天的生活中会面临这样或那样的压力。');
INSERT INTO `words` VALUES (2177, 'encourage', 'ɪnˈkʌrɪdʒ', 'v.', '支持;鼓励;激励;', 'When things aren\'t going well, he encourages me, telling me not to give up.', '事情进展不顺的时候，他鼓励我说不要放弃。');
INSERT INTO `words` VALUES (2178, 'encouragement', 'ɪnˈkʌrɪdʒmənt', 'n.', '鼓舞;鼓励;起激励作用的事物;', 'I also had friends who gave me a great deal of encouragement...', '我还有些朋友给了我很多鼓励。');
INSERT INTO `words` VALUES (2179, 'encrypt', 'ɪnˈkrɪpt', 'v.', '把…加密(或编码);', 'Account details are encrypted to protect privacy.', '账户资料被加密，以保护隐私。');
INSERT INTO `words` VALUES (2180, 'encryption', 'ɪnˈkrɪpʃ(ə)n', 'n.', '加密；加密（技）术;', 'Account details are encrypted to protect privacy.', '账户资料被加密，以保护隐私。');
INSERT INTO `words` VALUES (2181, 'end', 'end', 'n.', '(时间、事件、活动或故事等的)终止，终结，结局，结尾;末端;尽头;', 'The £5 banknote was first issued at the end of the 18th century...', '五英镑面值的钞票最早在18世纪末发行。');
INSERT INTO `words` VALUES (2182, 'endanger', 'ɪnˈdeɪndʒə(r)', 'v.', '使遭危险;危及;危害;', 'The debate could endanger the proposed peace talks.', '这一争论可能会危及提议进行的和平谈判。');
INSERT INTO `words` VALUES (2183, 'ending', 'ˈendɪŋ', 'n.', '(故事、电影等的)结尾，结局;结束;终结;', 'The film has a Hollywood happy ending.', '那部电影有一个好莱坞式的美满结局。');
INSERT INTO `words` VALUES (2184, 'endless', 'ˈendləs', 'adj.', '无止境的;无垠的;无穷无尽的;', 'They turned into an endless street...', '他们转入一条看不到尽头的街道。');
INSERT INTO `words` VALUES (2185, 'endorse', 'ɪnˈdɔːs', 'v.', '(公开)赞同，支持，认可;(在广告中)宣传，代言(某一产品);(在支票背面)签名，背书;', 'I can endorse their opinion wholeheartedly.', '我可以全力支持他们的意见。');
INSERT INTO `words` VALUES (2186, 'endorsement', 'ɪnˈdɔːsmənt', 'n.', '(公开的)赞同，支持，认可;(通常为名人在广告中对某一产品的)宣传，代言;(驾驶执照上的)违章记录;', 'That adds up to an endorsement of the status quo...', '那算是对现状的赞同。');
INSERT INTO `words` VALUES (2187, 'endow', 'ɪnˈdaʊ', 'v.', '(向学校等机构)捐钱，捐赠，资助;', 'You are endowed with wealth, good health and a lively intellect.', '你生来就拥有财富、健康和活跃的思维。');
INSERT INTO `words` VALUES (2188, 'ends', 'endz', 'n.', '(时间、事件、活动或故事等的)终止，终结，结局，结尾;末端;尽头;', 'The £5 banknote was first issued at the end of the 18th century...', '五英镑面值的钞票最早在18世纪末发行。');
INSERT INTO `words` VALUES (2189, 'endurance', 'ɪnˈdjʊərəns', 'n.', '忍耐力;耐久力;', 'The exercise obviously will improve strength and endurance.', '这种锻炼会明显改善体力，增加耐力。');
INSERT INTO `words` VALUES (2190, 'endure', 'ɪnˈdjʊə(r)', 'v.', '忍耐;忍受;持续;', 'The company endured heavy financial losses.', '那家公司遭受了严重亏损。');
INSERT INTO `words` VALUES (2191, 'energetic', 'ˌenəˈdʒetɪk', 'adj.', '精力充沛的;充满活力的;需要能量的;', 'Blackwell is 59, strong looking and enormously energetic...', '布莱克韦尔59岁，身体强壮且精力极其充沛。');
INSERT INTO `words` VALUES (2192, 'energy', 'ˈenədʒi', 'n.', '精力;活力;干劲;', 'He was saving his energy for next week\'s race in Belgium...', '他正为下周在比利时的比赛养精蓄锐。');
INSERT INTO `words` VALUES (2193, 'enforce', 'ɪnˈfɔːs', 'v.', '强制执行，强行实施(法律或规定);强迫;迫使;', 'Until now, the government has only enforced the ban with regard to American ships...', '到目前为止，政府仅仅对美国船只实施了禁令。');
INSERT INTO `words` VALUES (2194, 'engage', 'ɪnˈɡeɪdʒ', 'v.', '吸引住(注意力、兴趣);雇用;聘用;', 'I have never engaged in the drug trade...', '我从未参与过毒品交易。');
INSERT INTO `words` VALUES (2195, 'engaged', 'ɪnˈɡeɪdʒd', 'adj.', '忙于;从事于;已订婚;', 'They found the three engaged in target practice.', '他们发现那3个人在练习打靶。');
INSERT INTO `words` VALUES (2196, 'engagement', 'ɪnˈɡeɪdʒmənt', 'n.', '订婚;订婚期间;(尤指正式的或与工作有关的)约定，约会，预约;', 'He had an engagement at a restaurant in Greek Street at eight.', '他8点在希腊街的一个餐厅有个约会。');
INSERT INTO `words` VALUES (2197, 'engine', 'ˈendʒɪn', 'n.', '发动机;引擎;火车头;', 'He got into the driving seat and started the engine.', '他坐进驾驶座，发动引擎。');
INSERT INTO `words` VALUES (2198, 'engineering', 'ˌendʒɪˈnɪərɪŋ', 'n.', '工程;工程学;', '...the design and engineering of aircraft and space vehicles.', '航天器和太空交通工具的设计和工程制造');
INSERT INTO `words` VALUES (2199, 'english', 'ˈɪŋɡlɪʃ', 'n.', '英语;英文;(作为一门学科的)英语语言文学;', '...the English way of life...', '英国人的生活方式');
INSERT INTO `words` VALUES (2200, 'engrave', 'ɪnˈɡreɪv', 'v.', '在…上雕刻(字或图案);', 'Your wedding ring can be engraved with a personal inscription at no extra cost...', '可以免费在你的婚戒上刻上你的题字。');
INSERT INTO `words` VALUES (2201, 'engulf', 'ɪnˈɡʌlf', 'v.', '包围;吞没;淹没;', 'A seven-year-old boy was found dead after a landslide engulfed a block of flats...', '山体滑坡掩埋了一栋公寓楼，造成一名7岁男孩丧生。');
INSERT INTO `words` VALUES (2202, 'enhance', 'ɪnˈhɑːns', 'v.', '提高;增强;增进;', 'They\'ll be keen to enhance their reputation abroad...', '他们会非常渴望提高他们在国外的声誉。');
INSERT INTO `words` VALUES (2203, 'enhancement', 'ɪnˈhɑːnsmənt', 'n.', '提高;增加;增强;', 'Music is merely an enhancement to the power of her words...', '音乐只是增强了她话语的力度。');
INSERT INTO `words` VALUES (2204, 'enjoy', 'ɪnˈdʒɔɪ', 'v.', '享受…的乐趣;欣赏;喜爱;', 'Ross had always enjoyed the company of women...', '罗斯向来喜欢有女人伴其左右。');
INSERT INTO `words` VALUES (2205, 'enjoyable', 'ɪnˈdʒɔɪəbl', 'adj.', '有乐趣的;使人快乐的;令人愉快的;', 'It was much more enjoyable than I had expected.', '这比我预期的有趣得多。');
INSERT INTO `words` VALUES (2206, 'enjoyment', 'ɪnˈdʒɔɪmənt', 'n.', '愉快;快乐;乐趣;', 'I apologise if your enjoyment of the movie was spoiled.', '如果破坏了你看电影的兴致，我道歉。');
INSERT INTO `words` VALUES (2207, 'enlarge', 'ɪnˈlɑːdʒ', 'v.', '扩大;扩充;扩展;', '...the plan to enlarge Ewood Park into a 30,000 all-seater stadium...', '将伊伍德公园球场扩建成能容纳3万座席的体育场的计划');
INSERT INTO `words` VALUES (2208, 'enlargement', 'ɪnˈlɑːdʒmənt', 'n.', '扩大;扩充;扩展;', 'There is insufficient space for enlargement of the buildings.', '没有足够的空间扩建这些建筑。');
INSERT INTO `words` VALUES (2209, 'enlighten', 'ɪnˈlaɪtn', 'v.', '启发;开导;阐明;', 'A few dedicated doctors have fought for years to enlighten the profession...', '一些有献身精神的医生多年来一直努力指导业内同仁。');
INSERT INTO `words` VALUES (2210, 'enlightening', 'ɪnˈlaɪtnɪŋ', 'v.', '启发;开导;阐明;', 'A few dedicated doctors have fought for years to enlighten the profession...', '一些有献身精神的医生多年来一直努力指导业内同仁。');
INSERT INTO `words` VALUES (2211, 'enormous', 'ɪˈnɔːməs', 'adj.', '巨大的;庞大的;极大的;', 'The main bedroom is enormous...', '主卧室非常大。');
INSERT INTO `words` VALUES (2212, 'enough', 'ɪˈnʌf', 'det.', '(用于复数或不可数名词前)足够的，充足的，充分的;', 'They had enough cash for a one-way ticket...', '他们有足够的钱买单程票。');
INSERT INTO `words` VALUES (2213, 'enrich', 'ɪnˈrɪtʃ', 'v.', '充实;使丰富;使饱含(某物);', 'An extended family enriches life in many ways...', '大家庭在很多方面会使生活更加丰富多彩。');
INSERT INTO `words` VALUES (2214, 'ensemble', 'ɒnˈsɒmbl', 'n.', '乐团，剧团，舞剧团(全体成员);全体;整体;', '...an ensemble of young musicians...', '青年音乐家乐团');
INSERT INTO `words` VALUES (2215, 'ensue', 'ɪnˈsjuː', 'v.', '接着发生;因而产生;', 'If the Europeans did not reduce subsidies, a trade war would ensue...', '如果欧洲人不减少补贴，便会发生贸易战。');
INSERT INTO `words` VALUES (2216, 'ensure', 'ɪnˈʃʊə(r)', 'v.', '保证;担保;确保;', 'Britain\'s negotiators had ensured that the treaty which resulted was a significant change in direction...', '英国谈判代表已经担保由此而签订的条约将是方向上的重大改变。');
INSERT INTO `words` VALUES (2217, 'entail', 'ɪnˈteɪl', 'v.', '牵涉;需要;使必要;', 'Such a decision would entail a huge political risk...', '这样的决定势必带来巨大的政治风险。');
INSERT INTO `words` VALUES (2218, 'entangle', 'ɪnˈtæŋɡl', 'v.', '使纠缠;缠住;套住;', 'The blade of the oar had entangled itself with something in the water.', '船桨的桨叶和水里的什么东西缠住了。');
INSERT INTO `words` VALUES (2219, 'enter', 'ˈentə(r)', 'v.', '进来;进去;进入;', 'He entered the room briskly and stood near the door...', '他轻快地走进屋里，站在门边。');
INSERT INTO `words` VALUES (2220, 'enterprise', 'ˈentəpraɪz', 'n.', '公司;企业单位;事业单位;', 'There are plenty of small industrial enterprises.', '有许多小型工业企业。');
INSERT INTO `words` VALUES (2221, 'entertain', 'ˌentəˈteɪn', 'v.', '(尤指在自己家中)招待，款待;使有兴趣;使快乐;', '...games and ideas to entertain children...', '带给孩子们快乐的游戏和想法');
INSERT INTO `words` VALUES (2222, 'entertainment', 'ˌentəˈteɪnmənt', 'n.', '娱乐片;文娱节目;表演会;', '...the world of entertainment and international stardom...', '娱乐界和国际明星圈');
INSERT INTO `words` VALUES (2223, 'enthusiasm', 'ɪnˈθjuːziæzəm', 'n.', '热情;热心;热忱;', 'The lack of enthusiasm for unification among most West Germans fills him with disappointment...', '大多数西德人都没有渴望统一的热情，这令他无比失望。');
INSERT INTO `words` VALUES (2224, 'enthusiast', 'ɪnˈθjuːziæst', 'n.', '热衷于…的人;热心者;爱好者;', 'He is a great sports enthusiast.', '他是个超级体育迷。');
INSERT INTO `words` VALUES (2225, 'enthusiastic', 'ɪnˌθjuːziˈæstɪk', 'adj.', '热情的;热心的;热烈的;', 'Tom was very enthusiastic about the place...', '汤姆对那个地方非常感兴趣。');
INSERT INTO `words` VALUES (2226, 'entire', 'ɪnˈtaɪə(r)', 'adj.', '全部的;整个的;完全的;', 'He had spent his entire life in China as a doctor...', '他一生都在中国当医生。');
INSERT INTO `words` VALUES (2227, 'entirely', 'ɪnˈtaɪəli', 'adv.', '全部地;完整地;完全地;', '...an entirely new approach...', '全新的方法');
INSERT INTO `words` VALUES (2228, 'entirety', 'ɪnˈtaɪərəti', 'n.', '全部;全体;整体;', 'The peace plan has not been accepted in its entirety by all parties.', '和平计划并没有被各方全盘接受。');
INSERT INTO `words` VALUES (2229, 'entitle', 'ɪnˈtaɪtl', 'v.', '使享有权利;使符合资格;给…命名(或题名);', 'If the warranty is limited, the terms may entitle you to a replacement or refund...', '如果保修有限制，根据条款你也许可以要求退换或者退款。');
INSERT INTO `words` VALUES (2230, 'entity', 'ˈentəti', 'n.', '独立存在物;实体;', '...the earth as a living entity...', '作为一个有生命的实体的地球');
INSERT INTO `words` VALUES (2231, 'entreat', 'ɪnˈtriːt', 'v.', '恳求;乞求;', 'Trevor Steven entreated them to delay their departure...', '特雷弗·史蒂文恳求他们晚点走。');
INSERT INTO `words` VALUES (2232, 'entrust', 'ɪnˈtrʌst', 'v.', '委托;交托;托付;', 'If parents wanted to entrust their child to the best surgeons, they traveled to Bologna\'s medical school...', '如果父母们想给孩子看最好的外科医生，他们会前往博洛尼亚的医学院。');
INSERT INTO `words` VALUES (2233, 'entry', 'ˈentri', 'n.', '进入(指行动);进入(指权利等);参与，加入(指权利、机会);', 'Bill was among the first to gain entry to Buckingham Palace when it opened to the public recently...', '白金汉宫最近向公众开放，比尔是最先获得进入许可的人之一。');
INSERT INTO `words` VALUES (2234, 'enumerate', 'ɪˈnjuːməreɪt', 'v.', '列举;枚举;', 'I enumerate the work that will have to be done.', '我列举了必须得做的一些工作。');
INSERT INTO `words` VALUES (2235, 'envelop', 'ɪnˈveləp', 'v.', '包住;裹住;盖住;', 'That lovely, rich fragrant smell of the forest enveloped us...', '森林里那种馥郁芬芳的迷人气味围绕着我们。');
INSERT INTO `words` VALUES (2236, 'envious', 'ˈenviəs', 'adj.', '羡慕的;忌妒的;', 'I don\'t think I\'m envious of your success...', '我想我并不嫉妒你的成功。');
INSERT INTO `words` VALUES (2237, 'environment', 'ɪnˈvaɪrənmənt', 'n.', '(影响个体或事物行为或发展的)环境;客观环境;自然环境;', 'Pupils in our schools are taught in a safe, secure environment...', '我们学校的学生在安全无虑的环境中接受教育。');
INSERT INTO `words` VALUES (2238, 'environmental', 'ɪnˌvaɪrənˈmentl', 'adj.', '自然环境的;生态环境的;有关环境的;', '...economic and environmental legislation.', '经济和生态环境保护法规');
INSERT INTO `words` VALUES (2239, 'envisage', 'ɪnˈvɪzɪdʒ', 'v.', '想象;设想;展望;', 'He envisages the possibility of establishing direct diplomatic relations in the future...', '他设想在未来可以建立直接的外交关系。');
INSERT INTO `words` VALUES (2240, 'envy', 'ˈenvi', 'n.', '羡慕;忌妒;', 'Gradually he began to acknowledge his feelings of envy towards his mother...', '渐渐地，他开始承认自己羡慕母亲。');
INSERT INTO `words` VALUES (2241, 'ephemeral', 'ɪˈfemərəl', 'adj.', '短暂的;瞬息的;', 'He talked about the country\'s ephemeral unity being shattered by the defeat...', '他谈到战败使得该国短暂的统一彻底破灭。');
INSERT INTO `words` VALUES (2242, 'epidemic', 'ˌepɪˈdemɪk', 'n.', '流行病;(迅速的)泛滥，蔓延;', 'A flu epidemic is sweeping through Moscow.', '一场流感正席卷莫斯科。');
INSERT INTO `words` VALUES (2243, 'episode', 'ˈepɪsəʊd', 'n.', '(人生的)一段经历;(小说的)片段，插曲;(电视连续剧或无线电广播剧的)一集;', 'This episode is bound to be a deep embarrassment for Washington...', '这一事件肯定会让华盛顿非常难堪。');
INSERT INTO `words` VALUES (2244, 'epoch', 'ˈiːpɒk', 'n.', '时代;纪元;时期;', 'The birth of Christ was the beginning of a major epoch of world history.', '基督诞生是世界历史上一个重要纪元的开端。');
INSERT INTO `words` VALUES (2245, 'equality', 'iˈkwɒləti', 'n.', '平等;均等;相等;', '...equality of the sexes.', '男女平等');
INSERT INTO `words` VALUES (2246, 'equally', 'ˈiːkwəli', 'adv.', '平等地;同样地;平均地;', 'A bank\'s local market share tends to be divided equally between the local branch and branches located elsewhere...', '银行的地方市场份额通常会在当地分行和其他各地分行之间平均分配。');
INSERT INTO `words` VALUES (2247, 'equilibrium', 'ˌiːkwɪˈlɪbriəm', 'n.', '平衡;均衡;均势;', 'Stocks seesawed ever lower until prices found some new level of equilibrium...', '股票在震荡中进一步下跌，直到股价达到某个新的平衡点。');
INSERT INTO `words` VALUES (2248, 'equip', 'ɪˈkwɪp', 'v.', '配备;装备;使有所准备;', 'They become obsessed with trying to equip their vehicles with gadgets to deal with every possible contingency...', '他们变得痴迷于给自己的车辆装备小配件，以应付可能出现的各种紧急情况。');
INSERT INTO `words` VALUES (2249, 'equipment', 'ɪˈkwɪpmənt', 'n.', '设备;器材;配备;', '...computers, electronic equipment and machine tools.', '计算机、电子设备和机床');
INSERT INTO `words` VALUES (2250, 'equitable', 'ˈekwɪtəbl', 'adj.', '公平合理的;公正的;', 'He has urged them to come to an equitable compromise that gives Hughes his proper due...', '他已经催促他们达成合理的妥协，给予休斯他所应得的报酬。');
INSERT INTO `words` VALUES (2251, 'equity', 'ˈekwəti', 'n.', '(公司的)股本;资产净值;(公司的)普通股;', 'To capture his equity, Murphy must either sell or refinance.', '要获得资产净值，墨菲必须出售或者重新融资。');
INSERT INTO `words` VALUES (2252, 'equivalence', 'ɪˈkwɪvələns', 'n.', '(用途、功能、尺寸、价值等)相等，对等，相同;', '...the equivalence of science and rationality.', '科学和理性的对等');
INSERT INTO `words` VALUES (2253, 'equivalent', 'ɪˈkwɪvələnt', 'adj.', '(价值、数量、意义、重要性等)相等的，相同的;', 'The equivalent of two tablespoons of polyunsaturated oils is ample each day...', '每天相当于两大汤匙多重不饱和油的量就足够了。');
INSERT INTO `words` VALUES (2254, 'equivocal', 'ɪˈkwɪvəkl', 'adj.', '模棱两可的;意义双关的;含糊其辞的;', 'Many were equivocal about the idea...', '很多人对这一观点都含糊其词。');
INSERT INTO `words` VALUES (2255, 'equivocation', 'ɪˌkwɪvəˈkeɪʃn', 'n.', '含糊其辞;支吾;搪塞;', 'He is equivocating a lot about what is going to happen if and when there are elections...', '他支支吾吾地说着很多万一进行选举会发生的状况。');
INSERT INTO `words` VALUES (2256, 'era', 'ˈɪərə', 'n.', '时代;年代;纪元;', '...the nuclear era...', '核时代');
INSERT INTO `words` VALUES (2257, 'eradicate', 'ɪˈrædɪkeɪt', 'v.', '根除;消灭;杜绝;', 'They are already battling to eradicate illnesses such as malaria and tetanus...', '他们已经在努力消灭疟疾、破伤风等疾病。');
INSERT INTO `words` VALUES (2258, 'eradication', 'ɪˌrædɪˈkeɪʃn', 'n.', '根除，消（扑）灭;', 'They are already battling to eradicate illnesses such as malaria and tetanus...', '他们已经在努力消灭疟疾、破伤风等疾病。');
INSERT INTO `words` VALUES (2259, 'erase', 'ɪˈreɪz', 'v.', '清除;消除;消灭;', 'They are desperate to erase the memory of that last defeat in Cardiff...', '他们急于抹去上次在卡迪夫遭遇失败的记忆。');
INSERT INTO `words` VALUES (2260, 'eraser', 'ɪˈreɪzə(r)', 'n.', '橡皮擦;黑板擦;', '...a large, flat, pink India-rubber eraser.', '一大块粉红色的扁平橡皮擦');
INSERT INTO `words` VALUES (2261, 'erasure', 'ɪˈreɪʒə(r)', 'n.', '擦掉;抹掉;消除;', '...a further erasure of the UK\'s thin manufacturing base.', '对英国制造业薄弱基础的进一步破坏');
INSERT INTO `words` VALUES (2262, 'erect', 'ɪˈrekt', 'adj.', '垂直的;竖直的;直立的;', 'Opposition demonstrators have erected barricades in roads leading to the parliament building...', '反对派示威者已经在通往国会大厦的道路上设置了路障。');
INSERT INTO `words` VALUES (2263, 'erosion', 'ɪˈrəʊʒn', 'n.', '(河流、气候等的)侵蚀，腐蚀;(权威、权利、信心等的)逐渐丧失，削弱;(支持的)减少;', 'As their roots are strong and penetrating, they prevent erosion.', '它们的根十分粗壮而且扎得很深，从而避免了受侵蚀。');
INSERT INTO `words` VALUES (2264, 'err', 'ɜː(r)', 'v.', '犯错误;做错事;出差错;', 'It criticises the main contractor for seriously erring in its original estimates...', '它批评主要承包商在其最初估价中犯了严重的错误。');
INSERT INTO `words` VALUES (2265, 'errand', 'ˈerənd', 'n.', '差使;差事;', 'She went off on some errand...', '她办事去了。');
INSERT INTO `words` VALUES (2266, 'erratic', 'ɪˈrætɪk', 'adj.', '不规则的;不确定的;不稳定的;', 'Argentina\'s erratic inflation rate threatens to upset the plans.', '阿根廷动荡不定的通货膨胀率可能会破坏那些计划。');
INSERT INTO `words` VALUES (2267, 'erroneous', 'ɪˈrəʊniəs', 'adj.', '错误的;', 'Some people have the erroneous notion that one can contract AIDS by giving blood...', '一些人错误地认为献血会让人感染艾滋病。');
INSERT INTO `words` VALUES (2268, 'error', 'ˈerə(r)', 'n.', '错误;差错;谬误;', 'NASA discovered a mathematical error in its calculations...', '美国国家航空航天局在它的计算结果中发现了一个数学错误。');
INSERT INTO `words` VALUES (2269, 'erupt', 'ɪˈrʌpt', 'v.', '(火山)爆发;(岩浆、烟等)喷出;突然发生;', 'The volcano erupted in 1980, devastating a large area of Washington state...', '那座火山在1980年喷发，摧毁了华盛顿州大片地区。');
INSERT INTO `words` VALUES (2270, 'eruption', 'ɪ\'rʌpʃ(ə)n', 'n.', '长（萌）出，爆（喷）发，喷溢，发疹;', 'The volcano erupted in 1980, devastating a large area of Washington state...', '那座火山在1980年喷发，摧毁了华盛顿州大片地区。');
INSERT INTO `words` VALUES (2271, 'escalate', 'ˈeskəleɪt', 'v.', '(使)逐步扩大，不断恶化，加剧;', 'Both unions and management fear the dispute could escalate...', '工会和管理层都担心争端会恶化。');
INSERT INTO `words` VALUES (2272, 'escalation', 'ˌeskəˈleɪʃn', 'n.', '升级，逐步升级，增加；扩大；逐步上升;调值；价格调整;', 'Both unions and management fear the dispute could escalate...', '工会和管理层都担心争端会恶化。');
INSERT INTO `words` VALUES (2273, 'escape', 'ɪˈskeɪp', 'v.', '(从监禁或管制中)逃跑，逃走，逃出;(从不愉快或危险处境中)逃脱，摆脱，逃避;避开，避免(不愉快或危险的事物);', 'A prisoner has escaped from a jail in northern England...', '一名囚犯从英格兰北部的一所监狱中越狱。');
INSERT INTO `words` VALUES (2274, 'esoteric', 'ˌesəˈterɪk', 'adj.', '只有内行才懂的;难领略的;', '...esoteric knowledge.', '深奥的知识');
INSERT INTO `words` VALUES (2275, 'especial', 'ɪˈspeʃl', 'adj.', '格外的;特别的;特殊的;', 'The authorities took especial interest in him because of his trade union work.', '因为他在工会工作，官方对他特别感兴趣。');
INSERT INTO `words` VALUES (2276, 'especially', 'ɪˈspeʃəli', 'adv.', '尤其;特别;格外;', 'Millions of wild flowers colour the valleys, especially in April and May...', '尤其是在四五月份，不计其数的野花盛开，山谷里一片绚烂色彩。');
INSERT INTO `words` VALUES (2277, 'essence', 'ˈesns', 'n.', '本质;实质;精髓;', 'The essence of consultation is to listen to, and take account of, the views of those consulted.', '当顾问的精髓就在于倾听并考虑咨询者的观点。');
INSERT INTO `words` VALUES (2278, 'essential', 'ɪˈsenʃl', 'adj.', '完全必要的;必不可少的;极其重要的;', 'It was absolutely essential to separate crops from the areas that animals used as pasture...', '将庄稼和放牧区分开绝对必要。');
INSERT INTO `words` VALUES (2279, 'essentially', 'ɪˈsenʃəli', 'adv.', '本质上;根本上;基本上;', 'It\'s been believed for centuries that great writers, composers and scientists are essentially quite different from ordinary people...', '几个世纪以来人们一直相信伟大的作家、作曲家和科学家从根本上就与普通人大不相同。');
INSERT INTO `words` VALUES (2280, 'establish', 'ɪˈstæblɪʃ', 'v.', '建立;创立;设立;', 'The UN has established detailed criteria for who should be allowed to vote...', '联合国已经制定了有关投票资格的标准细则。');
INSERT INTO `words` VALUES (2281, 'established', 'ɪˈstæblɪʃt', 'adj.', '已确立的;已获确认的;确定的;', 'Their religious adherence is not to the established church.', '他们信仰的不是国教。');
INSERT INTO `words` VALUES (2282, 'establishment', 'ɪˈstæblɪʃmənt', 'n.', '机构;大型组织;企业;', 'His ideas influenced the establishment of National Portrait Galleries in London and Edinburgh.', '他的观念影响了伦敦和爱丁堡两所国家肖像艺术馆的建立。');
INSERT INTO `words` VALUES (2283, 'estate', 'ɪˈsteɪt', 'n.', '(通常指农村的)大片私有土地，庄园;住宅区;工业区;', '...a shooting party on Lord Wyville\'s estate in Yorkshire.', '在怀维尔勋爵位于约克郡的庄园里举行的狩猎会');
INSERT INTO `words` VALUES (2284, 'esteem', 'ɪˈstiːm', 'n.', '尊重;敬重;好评;', 'He is held in high esteem by colleagues in the construction industry...', '建筑业的同行们都非常敬重他。');
INSERT INTO `words` VALUES (2285, 'estimation', 'ˌestɪˈmeɪʃn', 'n.', '判断;评价;看法;', 'He has gone down considerably in my estimation.', '我对他的评价已经一落千丈。');
INSERT INTO `words` VALUES (2286, 'eternal', 'ɪˈtɜːnl', 'adj.', '不朽的;永久的;永恒的;', 'Whoever believes in Him shall have eternal life.', '所有相信上帝的人都会获得永生。');
INSERT INTO `words` VALUES (2287, 'ether', 'ˈiːθə(r)', 'n.', '醚;乙醚;苍穹;', '...a sweetish smell of ether and iodine.', '乙醚和碘酒的香甜气味');
INSERT INTO `words` VALUES (2288, 'ethnic', 'ˈeθnɪk', 'adj.', '民族的;种族的;具有民族特色的;', '...a survey of Britain\'s ethnic minorities.', '对英国少数民族的调查');
INSERT INTO `words` VALUES (2289, 'ethos', 'ˈiːθɒs', 'n.', '(某团体或社会的)道德思想，道德观;', 'The whole ethos of the hotel is effortless service.', '该酒店的全部理念就是提供轻松的服务。');
INSERT INTO `words` VALUES (2290, 'etiquette', 'ˈetɪkət', 'n.', '(社会或行业中的)礼节，礼仪，规矩;', 'This was such a great breach of etiquette, he hardly knew what to do.', '这是严重失礼的行为，他几乎手足无措。');
INSERT INTO `words` VALUES (2291, 'european', 'ˌjʊərəˈpiːən', 'adj.', '欧洲的;全欧的;欧盟的;', '...in some other European countries.', '在其他一些欧洲国家');
INSERT INTO `words` VALUES (2292, 'evacuate', 'ɪˈvækjueɪt', 'v.', '(把人从危险的地方)疏散，转移，撤离;(从危险的地方)撤出，搬出，撤空;排空(胃肠);', 'They were planning to evacuate the seventy American officials still in the country...', '他们正计划转移仍滞留在该国的70名美国官员。');
INSERT INTO `words` VALUES (2293, 'evade', 'ɪˈveɪd', 'v.', '逃脱;躲开;躲避;', 'By his own admission, he evaded taxes as a Florida real-estate speculator...', '他亲口承认自己在佛罗里达从事房地产投机生意时逃过税。');
INSERT INTO `words` VALUES (2294, 'evaluate', 'ɪˈvæljueɪt', 'v.', '估计;评价;评估;', 'They will first send in trained nurses to evaluate the needs of the individual situation...', '他们首先会派一些训练有素的护士来评估每个病人的需求。');
INSERT INTO `words` VALUES (2295, 'evaluation', 'ɪˌvæljuˈeɪʃn', 'n.', '定值，估计；评价；评审；计值;', 'They will first send in trained nurses to evaluate the needs of the individual situation...', '他们首先会派一些训练有素的护士来评估每个病人的需求。');
INSERT INTO `words` VALUES (2296, 'evaporate', 'ɪˈvæpəreɪt', 'v.', '(使)蒸发，挥发;(逐渐)消失，消散，衰减;', 'Moisture is drawn to the surface of the fabric so that it evaporates...', '湿气被吸到织物表面从而蒸发。');
INSERT INTO `words` VALUES (2297, 'evaporation', 'ɪˌvæpəˈreɪʃn', 'n.', '蒸发，挥发，汽化;', 'Moisture is drawn to the surface of the fabric so that it evaporates...', '湿气被吸到织物表面从而蒸发。');
INSERT INTO `words` VALUES (2298, 'evasion', 'ɪˈveɪʒn', 'n.', '躲避;规避;逃避;', 'Many Koreans were angered at what they saw as an evasion of responsibility...', '很多韩国人认为这是在逃避责任，对此非常愤慨。');
INSERT INTO `words` VALUES (2299, 'eve', 'iːv', 'n.', '(尤指宗教节假日的)前夜，前夕;傍晚;黄昏;', '...on the eve of his 27th birthday.', '在他27岁生日前一天');
INSERT INTO `words` VALUES (2300, 'evening', 'ˈiːvnɪŋ', 'n.', '晚上;傍晚;晚会;', 'All he did that evening was sit around the flat...', '那天晚上他一直在公寓里无所事事地坐着。');
INSERT INTO `words` VALUES (2301, 'event', 'ɪˈvent', 'n.', '发生的事情;(尤指)重要事情，大事;公开活动;', '...the events of Black Wednesday...', '“黑色星期三”事件');
INSERT INTO `words` VALUES (2302, 'eventful', 'ɪˈventfl', 'adj.', '充满大事的;多事故的;多变故的;', 'Her eventful life included holding senior positions in the Colonial Service...', '她一生经历丰富，曾在殖民服务局担任高级职位。');
INSERT INTO `words` VALUES (2303, 'events', 'ɪˈvɛnts', 'n.', '发生的事情;(尤指)重要事情，大事;公开活动;', '...the events of Black Wednesday...', '“黑色星期三”事件');
INSERT INTO `words` VALUES (2304, 'eventually', 'ɪˈventʃuəli', 'adv.', '最后;终于;', 'Eventually, the army caught up with him in Latvia...', '最终，大部队在拉脱维亚赶上了他。');
INSERT INTO `words` VALUES (2305, 'evergreen', 'ˈevəɡriːn', 'n.', '常青树;常绿树;', 'Holly, like ivy and mistletoe, is an evergreen.', '冬青和常春藤、槲寄生一样，都是常绿植物。');
INSERT INTO `words` VALUES (2306, 'everlasting', 'ˌevəˈlɑːstɪŋ', 'adj.', '永久的;永恒的;经久不变的;', '...a message of peace and everlasting life.', '和平和永生的预言');
INSERT INTO `words` VALUES (2307, 'every', 'ˈevri', 'det.', '(与单数名词连用，指整体中的)每一个，每个;所有可能的;完全可能的;', 'Every village has a green, a church, a pub and a manor house...', '每个村庄都有一片绿地、一座教堂、一间酒馆和一座庄园宅第。');
INSERT INTO `words` VALUES (2308, 'everyday', 'ˈevrideɪ', 'adj.', '每天的;每日发生的;日常的;', 'In the course of my everyday life, I had very little contact with teenagers.', '在我的日常生活中，我很少和青少年打交道。');
INSERT INTO `words` VALUES (2309, 'everyone', 'ˈevriwʌn', 'pron.', '每人;人人;所有人;', 'Everyone in the street was shocked when they heard the news...', '听到那条消息，街上所有的人都震惊了。');
INSERT INTO `words` VALUES (2310, 'everything', 'ˈevriθɪŋ', 'pron.', '每件事;所有事物;一切;', 'He\'d gone to Seattle long after everything else in his life had changed...', '他生活中的其他一切都改变了，很久之后他去了西雅图。');
INSERT INTO `words` VALUES (2311, 'everywhere', 'ˈevriweə(r)', 'adv./pron.', '处处;到处;各个地方;', 'Working people everywhere object to paying taxes...', '各地的工薪族都反对缴税。');
INSERT INTO `words` VALUES (2312, 'evict', 'ɪˈvɪkt', 'v.', '(尤指依法从房屋或土地上)驱逐，赶出，逐出;', 'They were evicted from their apartment after their mother became addicted to drugs...', '他们的母亲吸毒上瘾后，他们被从公寓里赶了出来。');
INSERT INTO `words` VALUES (2313, 'evidence', 'ˈevɪdəns', 'n.', '根据;证明;证据;', 'Ganley said he\'d seen no evidence of widespread fraud...', '甘利说他没有看见诈骗泛滥的证据。');
INSERT INTO `words` VALUES (2314, 'evident', 'ˈevɪdənt', 'adj.', '清楚的;显而易见的;显然的;', 'His footprints were clearly evident in the heavy dust...', '厚厚的尘土中他的脚印清晰易见。');
INSERT INTO `words` VALUES (2315, 'evidently', 'ˈevɪdəntli', 'adv.', '明显地;显然地;据说;', 'The man wore a bathrobe and had evidently just come from the bathroom...', '那个男子穿着浴袍，显然是刚刚从浴室出来。');
INSERT INTO `words` VALUES (2316, 'evil', 'ˈiːvl', 'adj.', '恶毒的;邪恶的;有害的;', 'We are still being attacked by the forces of evil...', '我们仍在遭受邪恶力量的攻击。');
INSERT INTO `words` VALUES (2317, 'evoke', 'ɪˈvəʊk', 'v.', '引起，唤起(感情、记忆或形象);', '...the scene evoking memories of those old movies...', '唤起对那些老电影的记忆的场景');
INSERT INTO `words` VALUES (2318, 'evolution', 'ˌiːvəˈluːʃn', 'n.', '进化;演变;发展;', '...the evolution of plants and animals.', '动植物的进化');
INSERT INTO `words` VALUES (2319, 'evolutionary', 'ˌiːvəˈluːʃənri', 'adj.', '进化的;演变的;逐渐发展的;', '...an evolutionary process.', '进化过程');
INSERT INTO `words` VALUES (2320, 'evolve', 'iˈvɒlv', 'v.', '(使)逐渐形成，逐步发展，逐渐演变;进化;进化形成;', 'The bright plumage of many male birds has evolved to attract females...', '很多雄鸟进化出鲜艳的羽毛是为了吸引雌鸟。');
INSERT INTO `words` VALUES (2321, 'ex', 'eks', 'n.', '前妻;前夫;以前的性伴侣;', 'He\'s different from my ex.', '他和我的前夫不一样。');
INSERT INTO `words` VALUES (2322, 'exacerbate', 'ɪɡˈzæsəbeɪt', 'v.', '使恶化;使加剧;使加重;', 'Mr Powell-Taylor says that depopulation exacerbates the problem...', '鲍威尔-泰勒先生说人口的急剧减少使得该问题更加严重。');
INSERT INTO `words` VALUES (2323, 'exact', 'ɪɡˈzækt', 'adj.', '精确的;准确的;严谨的;', 'I don\'t remember the exact words...', '我不记得确切的话语。');
INSERT INTO `words` VALUES (2324, 'exactly', 'ɪɡˈzæktli', 'adv.', '精确地;准确地;确切地;', 'Each corner had a guard tower, each of which was exactly ten meters in height...', '每个角落都有一座警戒塔，每座警戒塔正好10米高。');
INSERT INTO `words` VALUES (2325, 'exaggerate', 'ɪɡˈzædʒəreɪt', 'v.', '夸张;夸大;言过其实;', 'He thinks I\'m exaggerating...', '他觉得我在夸大其词。');
INSERT INTO `words` VALUES (2326, 'exaggerated', 'ɪɡˈzædʒəreɪtɪd', 'adj.', '夸张的;夸大的;言过其实的;', 'They should be sceptical of exaggerated claims for what such courses can achieve...', '对于那些课程能够取得何等成果的夸大宣传，他们应该持怀疑态度。');
INSERT INTO `words` VALUES (2327, 'exaggeration', 'ɪɡˌzædʒəˈreɪʃn', 'n.', '夸张;夸大;言过其实;', 'He thinks I\'m exaggerating...', '他觉得我在夸大其词。');
INSERT INTO `words` VALUES (2328, 'exalt', 'ɪɡˈzɔːlt', 'v.', '提拔，提升(有时指提拔到不该得到的职位上);表扬;褒扬;', 'However difficult she might have been, this book exalts her as both mother and muse...', '尽管她可能很难相处，这本书还是对她给予了高度赞扬，称她既是母亲又是诗人。');
INSERT INTO `words` VALUES (2329, 'exalted', 'ɪɡˈzɔːltɪd', 'adj.', '地位高的;高贵的;显赫的;', '...at the exalted level of Olympic competition...', '属于奥林匹克竞赛的高水平');
INSERT INTO `words` VALUES (2330, 'exam', 'ɪɡˈzæm', 'n.', '(笔头、口头或操作)考试;(对身体特定部位进行的)检查;体检;', 'I don\'t want to take any more exams...', '我再也不想参加考试了。');
INSERT INTO `words` VALUES (2331, 'examine', 'ɪɡˈzæmɪn', 'v.', '审查;调查;考查;', 'He examined her passport and stamped it...', '他仔细检查了她的护照，然后在上面盖了章。');
INSERT INTO `words` VALUES (2332, 'example', 'ɪɡˈzɑːmpl', 'n.', '实例;例证;例子;', 'The doctors gave numerous examples of patients being expelled from hospital...', '医生们列举了大量病人被逐出医院的实例。');
INSERT INTO `words` VALUES (2333, 'exasperate', 'ɪɡˈzæspəreɪt', 'v.', '使烦恼;使恼怒;激怒;', 'The sheer futility of it all exasperates her.', '它毫无用处，这让她很生气。');
INSERT INTO `words` VALUES (2334, 'exasperation', '', 'n.', '恼怒；恶化；惹人恼怒的事;', 'The sheer futility of it all exasperates her.', '它毫无用处，这让她很生气。');
INSERT INTO `words` VALUES (2335, 'excavate', 'ˈekskəveɪt', 'v.', '发掘，挖出(古建筑或古物);挖掘，开凿，挖空(洞、隧道等);', 'A new Danish expedition is again excavating the site in annual summer digs.', '一支新的丹麦探险队又在那个遗址上进行一年一度的夏季挖掘。');
INSERT INTO `words` VALUES (2336, 'exceed', 'ɪkˈsiːd', 'v.', '超过(数量);超越(法律、命令等)的限制;', 'Its research budget exceeds $700 million a year...', '其研究预算每年超过7亿美元。');
INSERT INTO `words` VALUES (2337, 'exceedingly', 'ɪkˈsiːdɪŋli', 'adv.', '极其;非常;特别;', 'We had an exceedingly good lunch...', '我们吃了一顿极为丰盛的午餐。');
INSERT INTO `words` VALUES (2338, 'excel', 'ɪkˈsel', 'v.', '擅长;善于;突出;', 'Caine has always been an actor who excels in irony...', '凯恩向来是个擅长讽刺的演员。');
INSERT INTO `words` VALUES (2339, 'excellence', 'ˈeksələns', 'n.', '优秀;杰出;卓越;', '...the top US award for excellence in journalism and the arts.', '美国新闻和艺术的最高杰出成就奖');
INSERT INTO `words` VALUES (2340, 'excellent', 'ˈeksələnt', 'adj.', '优秀的;杰出的;极好的;', 'The recording quality is excellent...', '录音质量非常好。');
INSERT INTO `words` VALUES (2341, 'except', 'ɪkˈsept', 'prep.', '(用于所言不包括的人或事物前)除…之外;', 'I wouldn\'t have accepted anything except a job in Europe...', '除了到欧洲工作外，我本来什么都不会接受。');
INSERT INTO `words` VALUES (2342, 'exception', 'ɪkˈsepʃn', 'n.', '一般情况以外的人(或事物);例外;规则的例外;', 'Few guitarists can sing as well as they can play; Eddie, however, is an exception...', '很少有吉他手唱歌像弹吉他那么好，然而埃迪是个例外。');
INSERT INTO `words` VALUES (2343, 'exceptional', 'ɪkˈsepʃənl', 'adj.', '杰出的;优秀的;卓越的;', '...children with exceptional ability...', '具有非凡才能的孩子们');
INSERT INTO `words` VALUES (2344, 'excessive', 'ɪkˈsesɪv', 'adj.', '过分的;过度的;', '...the alleged use of excessive force by police...', '警察涉嫌滥用武力');
INSERT INTO `words` VALUES (2345, 'exchange', 'ɪksˈtʃeɪndʒ', 'n.', '交换;互换;交流;', 'We exchanged addresses and Christmas cards...', '我们交换了地址和圣诞贺卡。');
INSERT INTO `words` VALUES (2346, 'excite', 'ɪkˈsaɪt', 'v.', '使激动;使兴奋;刺激;', 'I only take on work that excites me, even if it means turning down lots of money...', '我只做那些令我感兴趣的工作，即使这意味着要少赚很多钱。');
INSERT INTO `words` VALUES (2347, 'excited', 'ɪkˈsaɪtɪd', 'adj.', '激动的;兴奋的;受刺激的;', 'I\'m very excited about the possibility of playing for England\'s first team...', '想到可能为英格兰首屈一指的球队效力，我非常兴奋。');
INSERT INTO `words` VALUES (2348, 'excitement', 'ɪkˈsaɪtmənt', 'n.', '激动;兴奋;刺激;', 'Everyone is in a state of great excitement.', '每个人都大为兴奋。');
INSERT INTO `words` VALUES (2349, 'exciting', 'ɪkˈsaɪtɪŋ', 'adj.', '令人激动的;使人兴奋的;', 'The race itself is very exciting...', '比赛本身非常刺激。');
INSERT INTO `words` VALUES (2350, 'exclaim', 'ɪkˈskleɪm', 'v.', '(由于强烈的情感或痛苦而)惊叫，呼喊;', '\'He went back to the lab\', Iris exclaimed impatiently...', '“他回实验室去了，”艾里斯不耐烦地大声说。');
INSERT INTO `words` VALUES (2351, 'exclamation', 'ˌekskləˈmeɪʃn', 'n.', '感叹;感叹语;感叹词;', 'Sue gave an exclamation as we got a clear sight of the house.', '我们清楚看到那所房子时，休惊叹不已。');
INSERT INTO `words` VALUES (2352, 'exclude', 'ɪkˈskluːd', 'v.', '不包括;不放在考虑之列;防止…进入;', 'The Academy excluded women from its classes...', '该学院不招收女学生。');
INSERT INTO `words` VALUES (2353, 'exclusion', 'ɪkˈskluːʒn', 'n.', '排斥;排除在外;不包括在内的人(或事物);', 'It calls for the exclusion of all commercial lending institutions from the college loan program...', '它提倡将所有商贷机构排除在高校贷款计划之外。');
INSERT INTO `words` VALUES (2354, 'exclusive', 'ɪkˈskluːsɪv', 'adj.', '(个人或集体)专用的，专有的，独有的，独占的;排外的;不愿接收新成员(尤指较低社会阶层)的;', 'He is already a member of Britain\'s most exclusive club...', '他已经是英国最高级俱乐部的成员了。');
INSERT INTO `words` VALUES (2355, 'exclusively', 'ɪkˈskluːsɪvli', 'adv.', '排他地;独占地;专有地;', '...an exclusively male domain...', '男性专属的领域');
INSERT INTO `words` VALUES (2356, 'exclusivity', 'ˌekskluːˈsɪvəti', 'n.', '排他性;专有权;独特性;', 'He is already a member of Britain\'s most exclusive club...', '他已经是英国最高级俱乐部的成员了。');
INSERT INTO `words` VALUES (2357, 'excrement', 'ˈekskrɪmənt', 'n.', '粪便;排泄物;', 'The cage smelled of excrement.', '笼子里粪臭熏人。');
INSERT INTO `words` VALUES (2358, 'excrete', 'ɪkˈskriːt', 'v.', '排泄;', 'Your open pores excrete sweat and dirt...', '通过张开的毛孔排出汗液和污垢。');
INSERT INTO `words` VALUES (2359, 'excursion', 'ɪkˈskɜːʃn', 'n.', '(尤指集体)远足，短途旅行;(短期的)涉足，涉猎;', 'In Bermuda, Sam\'s father took him on an excursion to a coral barrier.', '在百慕大，萨姆的父亲带他到附近的珊瑚礁去游玩。');
INSERT INTO `words` VALUES (2360, 'execute', 'ˈeksɪkjuːt', 'v.', '(尤指依法)处决，处死;实行;执行;', 'He was executed by lethal injection earlier today...', '他于今天早些时候被注射处死。');
INSERT INTO `words` VALUES (2361, 'execution', 'ˌeksɪˈkjuːʃn', 'n.', '处决;实行;执行;', 'He was executed by lethal injection earlier today...', '他于今天早些时候被注射处死。');
INSERT INTO `words` VALUES (2362, 'executive', 'ɪɡˈzekjətɪv', 'n.', '(公司或机构的)经理，主管领导，管理人员;(统称公司或机构的)行政领导，领导层;(政府的)行政部门;', '...an advertising executive.', '广告主管');
INSERT INTO `words` VALUES (2363, 'exemplify', 'ɪɡˈzemplɪfaɪ', 'v.', '是…的典型(或典范、榜样);举例说明;例证;', 'The room\'s style exemplifies Conran\'s ideal of \'beauty and practicality\'.', '那个房间的风格是体现康兰“美观和实用”观念的典范。');
INSERT INTO `words` VALUES (2364, 'exempt', 'ɪɡˈzempt', 'adj.', '免除(责任、付款等);获豁免;', 'Men in college were exempt from military service...', '大学在校男生免服兵役。');
INSERT INTO `words` VALUES (2365, 'exemption', 'ɪɡˈzempʃn', 'n.', '免除;豁免;(指部分收入)免税;', 'Men in college were exempt from military service...', '大学在校男生免服兵役。');
INSERT INTO `words` VALUES (2366, 'exercise', 'ˈeksəsaɪz', 'n.', '(身体或脑力的)活动，锻炼，运动;(保持健康或培养技能的)一套动作，训练活动;习题;', 'They are merely exercising their right to free speech...', '他们只是在行使他们的言论自由权。');
INSERT INTO `words` VALUES (2367, 'exert', 'ɪɡˈzɜːt', 'v.', '运用;行使;施加;', 'He exerted considerable influence on the thinking of the scientific community on these issues...', '他极大地影响了科学界对这些问题的看法。');
INSERT INTO `words` VALUES (2368, 'exertion', 'ɪɡˈzɜːʃn', 'n.', '努力;尽力;费力;', 'He exerted considerable influence on the thinking of the scientific community on these issues...', '他极大地影响了科学界对这些问题的看法。');
INSERT INTO `words` VALUES (2369, 'exhale', 'eksˈheɪl', 'v.', '呼出，吐出(肺中的空气、烟等);呼气;', 'Hold your breath for a moment and exhale...', '屏住呼吸片刻，然后呼气。');
INSERT INTO `words` VALUES (2370, 'exhaust', 'ɪɡˈzɔːst', 'n.', '(车辆、发动机或机器排出的)废气;排气管;', 'Don\'t exhaust him...', '不要把他累坏了。');
INSERT INTO `words` VALUES (2371, 'exhausted', 'ɪɡˈzɔːstɪd', 'adj.', '筋疲力尽的;疲惫不堪的;用完的;', 'Don\'t exhaust him...', '不要把他累坏了。');
INSERT INTO `words` VALUES (2372, 'exhaustion', 'ɪɡˈzɔːstʃən', 'n.', '筋疲力尽;疲惫不堪;耗尽;', 'Staff say he is suffering from exhaustion.', '员工说他极度疲劳。');
INSERT INTO `words` VALUES (2373, 'exhaustive', 'ɪɡˈzɔːstɪv', 'adj.', '详尽的;彻底的;全面的;', 'This is by no means an exhaustive list but it gives an indication of the many projects taking place...', '这绝称不上是详尽无遗的清单，但是列出了很多正在进行的项目。');
INSERT INTO `words` VALUES (2374, 'exhibit', 'ɪɡˈzɪbɪt', 'v.', '展览;展出;表现，显示，显出(感情、品质或能力);', 'He has exhibited symptoms of anxiety and overwhelming worry...', '他表现出焦躁不安和过度忧虑的症状。');
INSERT INTO `words` VALUES (2375, 'exhibition', 'ˌeksɪˈbɪʃn', 'n.', '(一批)展览品;展览;展出;', '...an exhibition of expressionist art.', '表现派艺术展览会');
INSERT INTO `words` VALUES (2376, 'exhort', 'ɪɡˈzɔːt', 'v.', '规劝;敦促;告诫;', 'Kennedy exhorted his listeners to turn away from violence...', '肯尼迪劝诫听众远离暴力。');
INSERT INTO `words` VALUES (2377, 'exile', 'ˈeksaɪl', 'n.', '流放;流亡;放逐;', 'He is now living in exile in Egypt...', '他目前流亡埃及。');
INSERT INTO `words` VALUES (2378, 'exist', 'ɪɡˈzɪst', 'v.', '存在;实际上有;(尤指在困境或贫困中)生活，生存;', 'He thought that if he couldn\'t see something, it didn\'t exist...', '他认为自己看不见的东西就不存在。');
INSERT INTO `words` VALUES (2379, 'existence', 'ɪɡˈzɪstəns', 'n.', '存在;实有;(尤指艰难或无聊的)生活，生活方式;', '...the existence of other galaxies...', '其他星系的存在');
INSERT INTO `words` VALUES (2380, 'existing', 'ɪɡˈzɪstɪŋ', 'adj.', '现存的;现行的;', '...the need to improve existing products and develop new lines...', '改进现有产品、开发新生产线的需要');
INSERT INTO `words` VALUES (2381, 'exit', 'ˈeksɪt', 'n.', '出口;通道;太平门;', 'He picked up the case and walked towards the exit...', '他提起箱子，向出口走去。');
INSERT INTO `words` VALUES (2382, 'exonerate', 'ɪɡˈzɒnəreɪt', 'v.', '宣布(某人)无罪;免除责任;', 'The official report basically exonerated everyone...', '官方报道基本上宣布所有人无罪。');
INSERT INTO `words` VALUES (2383, 'exoneration', '', 'n.', '免罪；免除（责任）；解除；证明无罪；释放；解除债务或责任；追偿权；免责权；卸责权;', 'The official report basically exonerated everyone...', '官方报道基本上宣布所有人无罪。');
INSERT INTO `words` VALUES (2384, 'exorbitant', 'ɪɡˈzɔːbɪtənt', 'adj.', '过高的;高得离谱的;', 'Exorbitant housing prices have created an acute shortage of affordable housing for the poor.', '过高的房价已经造成了穷人能够买得起的住房严重短缺。');
INSERT INTO `words` VALUES (2385, 'exotic', 'ɪɡˈzɒtɪk', 'adj.', '来自异国(尤指热带国家)的;奇异的;异国情调的;', '...brilliantly coloured, exotic flowers...', '色彩斑斓的奇异花卉');
INSERT INTO `words` VALUES (2386, 'expand', 'ɪkˈspænd', 'v.', '扩大，增加，增强(尺码、数量或重要性);扩展，发展(业务);细谈;', 'Engineers noticed that the pipes were not expanding as expected...', '工程师注意到管道并没有如预期那样膨胀。');
INSERT INTO `words` VALUES (2387, 'expanding', 'ɪkˈspændɪŋ', 'v.', '扩大，增加，增强(尺码、数量或重要性);扩展，发展(业务);细谈;', 'Engineers noticed that the pipes were not expanding as expected...', '工程师注意到管道并没有如预期那样膨胀。');
INSERT INTO `words` VALUES (2388, 'expansion', 'ɪkˈspænʃn', 'n.', '扩张;扩展;扩大;', '...the rapid expansion of private health insurance.', '个人健康保险的迅速发展');
INSERT INTO `words` VALUES (2389, 'expect', 'ɪkˈspekt', 'v.', '预料;预期;预计;', '...a council workman who expects to lose his job in the next few weeks...', '预计自己在接下来的几周里会丢掉工作的市政工人');
INSERT INTO `words` VALUES (2390, 'expectancy', 'ɪkˈspektənsi', 'n.', '预料;预期;期待;', 'The supporters had a tremendous air of expectancy.', '支持者们充满了期待。');
INSERT INTO `words` VALUES (2391, 'expectation', 'ˌekspekˈteɪʃn', 'n.', '预料;预期;期待;', 'Students\' expectations were as varied as their expertise...', '学生们的期望根据他们的专业各有不同。');
INSERT INTO `words` VALUES (2392, 'expedient', 'ɪkˈspiːdiənt', 'n.', '权宜之计;应急办法;', 'Surgical waiting lists were reduced by the simple expedient of striking off all patients awaiting varicose vein operations...', '采取了将所有等待进行静脉曲张手术的病人从名单上删除的简单做法，这一权宜之计使得外科手术等待名单得以缩减。');
INSERT INTO `words` VALUES (2393, 'expedite', 'ˈekspədaɪt', 'v.', '加快;加速;', 'The government has been extremely reluctant to expedite investigations that might result in his trial...', '当局极不情愿推进调查，因为其结果可能会让他接受审判。');
INSERT INTO `words` VALUES (2394, 'expedition', 'ˌekspəˈdɪʃn', 'n.', '远征;探险;考察;', '...Byrd\'s 1928 expedition to Antarctica.', '伯德1928年前往南极洲的探险');
INSERT INTO `words` VALUES (2395, 'expel', 'ɪkˈspel', 'v.', '把…开除(或除名);驱逐出境;排出;', 'More than five-thousand secondary school students have been expelled for cheating.', '5,000多名中学生因为作弊被开除。');
INSERT INTO `words` VALUES (2396, 'expend', 'ɪkˈspend', 'v.', '花费;消费;耗费;', 'Children expend a lot of energy and may need more high-energy food than adults.', '孩子们消耗很多能量，可能比成年人需要更多高能量的食物。');
INSERT INTO `words` VALUES (2397, 'expenditure', 'ɪkˈspendɪtʃə(r)', 'n.', '花费;消费;费用;', 'Policies of tax reduction must lead to reduced public expenditure...', '减税政策必然导致公共支出的削减。');
INSERT INTO `words` VALUES (2398, 'expense', 'ɪkˈspens', 'n.', '费用;价钱;花钱的东西;', 'He\'s bought a specially big TV at vast expense so that everyone can see properly...', '他花大价钱买了一台特别大的电视机，这样每个人都能看清楚。');
INSERT INTO `words` VALUES (2399, 'expenses', 'ɪksˈpɛnsɪz', 'n.', '费用;价钱;花钱的东西;', 'He\'s bought a specially big TV at vast expense so that everyone can see properly...', '他花大价钱买了一台特别大的电视机，这样每个人都能看清楚。');
INSERT INTO `words` VALUES (2400, 'expensive', 'ɪkˈspensɪv', 'adj.', '昂贵的;花钱多的;价格高的;', 'Wine\'s so expensive in this country...', '葡萄酒在这个国家非常昂贵。');
INSERT INTO `words` VALUES (2401, 'experience', 'ɪkˈspɪəriəns', 'n.', '(由实践得来的)经验;实践;经历;', 'He has also had managerial experience on every level...', '他还有各个层级的管理经验。');
INSERT INTO `words` VALUES (2402, 'experienced', 'ɪkˈspɪəriənst', 'adj.', '有经验的;熟练的;有阅历的;', '...lawyers who are experienced in these matters...', '在这些问题上颇有经验的律师们');
INSERT INTO `words` VALUES (2403, 'experimental', 'ɪkˌsperɪˈmentl', 'adj.', '以实验(或试验)为基础的;实验性的;试验性的;', '...an experimental air conditioning system...', '试验性的空调系统');
INSERT INTO `words` VALUES (2404, 'expert', 'ˈekspɜːt', 'n.', '专家;行家;能手;', 'Our team of experts will be on hand to offer help and advice between 12 noon and 7pm daily.', '每天中午12点到下午7点，我们的专家小组将随时提供帮助和建议。');
INSERT INTO `words` VALUES (2405, 'expertise', 'ˌekspɜːˈtiːz', 'n.', '专门知识;专门技能;专长;', 'The problem is that most local authorities lack the expertise to deal sensibly in this market.', '问题是大多数地方政府都缺乏合理应对这个市场的专门知识。');
INSERT INTO `words` VALUES (2406, 'expiration', 'ˌekspəˈreɪʃn', 'n.', '告终;期满;截止;', '...a few hours before the expiration of the midnight deadline.', '午夜最后期限截止前的几个小时');
INSERT INTO `words` VALUES (2407, 'expire', 'ɪkˈspaɪə(r)', 'v.', '(因到期而)失效，终止;到期;届满;', 'He had lived illegally in the United States for five years after his visitor\'s visa expired.', '在访问签证到期后，他又在美国非法居住了5年。');
INSERT INTO `words` VALUES (2408, 'expiry', 'ɪkˈspaɪəri', 'n.', '(文件、协议等的)满期，届期，到期;', '...the expiry of a fixed term contract...', '定期合同的到期');
INSERT INTO `words` VALUES (2409, 'explain', 'ɪkˈspleɪn', 'v.', '解释;说明;阐明;', 'Not every judge, however, has the ability to explain the law in simple terms...', '然而，不是每个法官都能用简单的语言来解释法律。');
INSERT INTO `words` VALUES (2410, 'explanation', 'ˌekspləˈneɪʃn', 'n.', '解释;说明;阐述;', 'She told the court she would give a full explanation of the prosecution\'s decision on Monday...', '她告诉法庭她会在星期一对原告方的决定作出全面解释。');
INSERT INTO `words` VALUES (2411, 'explanatory', 'ɪkˈsplænətri', 'adj.', '解释的;说明的;阐述的;', 'These statements are accompanied by a series of explanatory notes...', '这些陈述后面有一系列解释性注释。');
INSERT INTO `words` VALUES (2412, 'explicit', 'ɪkˈsplɪsɪt', 'adj.', '清楚明白的;易于理解的;(说话)清晰的，明确的;', '...sexually explicit scenes in films and books.', '电影和书籍中露骨的性爱片段');
INSERT INTO `words` VALUES (2413, 'explicitly', 'ɪksˈplɪsɪtli', 'adv.', '明确地；明白地;', '...sexually explicit scenes in films and books.', '电影和书籍中露骨的性爱片段');
INSERT INTO `words` VALUES (2414, 'explode', 'ɪkˈspləʊd', 'v.', '爆炸;爆破;爆裂;', 'They were clearing up when the second bomb exploded...', '他们正在清理时，第二颗炸弹爆炸了。');
INSERT INTO `words` VALUES (2415, 'exploration', 'ˌekspləˈreɪʃn', 'n.', '勘探;勘查;探索;', 'I just wanted to explore Paris, read Sartre, listen to Sidney Bechet...', '我就想逛逛巴黎，读读萨特的作品，听听悉尼·贝谢的音乐。');
INSERT INTO `words` VALUES (2416, 'explore', 'ɪkˈsplɔː(r)', 'v.', '勘探;勘查;探索;', 'I just wanted to explore Paris, read Sartre, listen to Sidney Bechet...', '我就想逛逛巴黎，读读萨特的作品，听听悉尼·贝谢的音乐。');
INSERT INTO `words` VALUES (2417, 'explosion', 'ɪkˈspləʊʒn', 'n.', '爆炸，爆破，爆裂(声);突增;猛增;', 'After the second explosion, all of London\'s main train and subway stations were shut down...', '第二次爆炸后，伦敦所有的主要火车站和地铁站都关闭了。');
INSERT INTO `words` VALUES (2418, 'explosive', 'ɪkˈspləʊsɪv', 'adj.', '易爆炸的;可能引起爆炸的;易爆发的;', '...one-hundred-and-fifty pounds of Semtex explosive...', '150磅塞姆汀塑胶炸药');
INSERT INTO `words` VALUES (2419, 'exponent', 'ɪkˈspəʊnənt', 'n.', '(观点、理论的)拥护者，鼓吹者，倡导者;(某种活动的)能手，大师;指数;', '...a leading exponent of test-tube baby techniques.', '试管婴儿技术的主要倡导者');
INSERT INTO `words` VALUES (2420, 'exponential', 'ˌekspəˈnenʃl', 'adj.', '指数的;幂的;由指数表示的;', 'The policy tried to check the exponential growth of public expenditure.', '该政策试图控制公共开支的迅猛增长。');
INSERT INTO `words` VALUES (2421, 'exporter', 'ekˈspɔːtə(r)', 'n.', '出口商;出口公司;出口国;', 'France is the world\'s second-biggest exporter of agricultural products.', '法国是世界第二大农产品出口国。');
INSERT INTO `words` VALUES (2422, 'expose', 'ɪkˈspəʊz', 'n.', '(尤指对令人震惊或故意保密的事实的)陈述，阐述，揭露;', 'Lowered sea levels exposed the shallow continental shelf beneath the Bering Sea...', '海平面下降使白令海底部的浅层大陆架暴露出来。');
INSERT INTO `words` VALUES (2423, 'exposition', 'ˌekspəˈzɪʃn', 'n.', '(理论、计划等的)解释，说明，阐述;(产品的)展销;商品交易会;', 'The fullest exposition of Coleridge\'s thought can be found in the States-man\'s Manual.', '柯尔律治的思想可以在《政治家手册》中找到最全面的阐述。');
INSERT INTO `words` VALUES (2424, 'exposure', 'ɪkˈspəʊʒə(r)', 'n.', '面临，遭受(危险或不快);揭露;(在电视、报纸等上的)亮相，被报道;', 'Exposure to lead is known to damage the brains of young children.', '众所周知，接触铅对幼儿的大脑有害。');
INSERT INTO `words` VALUES (2425, 'express', 'ɪkˈspres', 'v.', '表示;表达;表露;', 'He expressed grave concern at American attitudes...', '他对美国的态度表示严重关切。');
INSERT INTO `words` VALUES (2426, 'expression', 'ɪkˈspreʃn', 'n.', '表示;表达;表露;', 'Laughter is one of the most infectious expressions of emotion...', '笑是最具感染力的情感表达方式之一。');
INSERT INTO `words` VALUES (2427, 'expressive', 'ɪkˈspresɪv', 'adj.', '富于表情的;有表现力的;意味深长的;', 'You can train people to be more expressive.', '可以训练人们使其更具表现力。');
INSERT INTO `words` VALUES (2428, 'expressly', 'ɪkˈspresli', 'adv.', '清楚地;明确地;确切地;', 'He expressed grave concern at American attitudes...', '他对美国的态度表示严重关切。');
INSERT INTO `words` VALUES (2429, 'expulsion', 'ɪkˈspʌlʃn', 'n.', '驱逐;逐出;开除;', 'Her hatred of authority led to her expulsion from high school.', '她对权威的仇恨导致她被中学开除。');
INSERT INTO `words` VALUES (2430, 'expunge', 'ɪkˈspʌndʒ', 'v.', '抹去;除去;删去;', 'The revolutionaries expunged domestic opposition...', '革命者肃清了国内敌对势力。');
INSERT INTO `words` VALUES (2431, 'exquisite', 'ɪkˈskwɪzɪt', 'adj.', '精美的;精致的;剧烈的;', 'The Indians brought in exquisite beadwork to sell...', '印第安人带了精美的珠饰来卖。');
INSERT INTO `words` VALUES (2432, 'extend', 'ɪkˈstend', 'v.', '使伸长;扩大;扩展;', 'The caves extend for some 18 kilometres...', '那些洞穴深约18公里。');
INSERT INTO `words` VALUES (2433, 'extensive', 'ɪkˈstensɪv', 'adj.', '广阔的;广大的;大量的;', '...an extensive tour of Latin America...', '在拉丁美洲广泛游历');
INSERT INTO `words` VALUES (2434, 'extensively', 'ɪksˈtɛnsɪvli', 'adv.', '广大地；广泛地;', '...an extensive tour of Latin America...', '在拉丁美洲广泛游历');
INSERT INTO `words` VALUES (2435, 'extent', 'ɪkˈstent', 'n.', '程度;限度;大小;', 'The government itself has little information on the extent of industrial pollution...', '政府本身几乎不了解工业污染的程度。');
INSERT INTO `words` VALUES (2436, 'exterior', 'ɪkˈstɪəriə(r)', 'n.', '(尤指建筑物的)外部，外观，表面，外貌;(人的)外貌，外表;', 'In one ad the viewer scarcely sees the car\'s exterior...', '在一则广告中，观众几乎看不到汽车的外观。');
INSERT INTO `words` VALUES (2437, 'exterminate', 'ɪkˈstɜːmɪneɪt', 'v.', '灭绝;根除;消灭;', 'A huge effort was made to exterminate the rats...', '灭鼠花了大力气。');
INSERT INTO `words` VALUES (2438, 'external', 'ɪkˈstɜːnl', 'adj.', '外部的;外面的;外界的;', '...a much reduced heat loss through external walls.', '大幅降低的外墙热损失');
INSERT INTO `words` VALUES (2439, 'extinct', 'ɪkˈstɪŋkt', 'adj.', '不再存在的;已灭绝的;绝种的;', 'It is 250 years since the wolf became extinct in Britain.', '狼在英国已经灭绝250年了。');
INSERT INTO `words` VALUES (2440, 'extinction', 'ɪkˈstɪŋkʃn', 'n.', '(植物、动物、生活方式等的)灭绝，绝种，消亡;', 'An operation is beginning to try to save a species of crocodile from extinction...', '一项努力拯救一个鳄鱼物种、使其免于灭绝的行动已经开始。');
INSERT INTO `words` VALUES (2441, 'extinguish', 'ɪkˈstɪŋɡwɪʃ', 'v.', '熄灭;扑灭;毁灭;', 'It took about 50 minutes to extinguish the fire...', '大约花了50分钟才把火扑灭。');
INSERT INTO `words` VALUES (2442, 'extort', 'ɪkˈstɔːt', 'v.', '敲诈;勒索;强夺;', 'Corrupt government officials were extorting money from him...', '腐败的政府官员向他敲诈钱财。');
INSERT INTO `words` VALUES (2443, 'extra', 'ˈekstrə', 'adj.', '额外的;分外的;外加的;', 'Police warned motorists to allow extra time to get to work...', '警方告诫驾车者出门上班要留出富余时间。');
INSERT INTO `words` VALUES (2444, 'extraction', 'ɪkˈstrækʃn', 'n.', '提取;提炼;拔出;', 'Her real father was of Italian extraction...', '她的亲生父亲是意大利裔。');
INSERT INTO `words` VALUES (2445, 'extraordinary', 'ɪkˈstrɔːdnri', 'adj.', '意想不到的;令人惊奇的;奇怪的;', 'We\'ve made extraordinary progress as a society in that regard...', '在那个方面，我们的社会已经取得了巨大的进步。');
INSERT INTO `words` VALUES (2446, 'extravagance', 'ɪkˈstrævəɡəns', 'n.', '奢侈;挥霍;铺张浪费;', '...gross mismanagement and financial extravagance...', '严重的管理不善和资金浪费');
INSERT INTO `words` VALUES (2447, 'extravagant', 'ɪkˈstrævəɡənt', 'adj.', '奢侈的;挥霍的;铺张浪费的;', 'We are not extravagant; restaurant meals are a luxury and designer clothes are out...', '我们并不铺张浪费：下馆子是一种奢侈，也不会考虑买名牌衣服。');
INSERT INTO `words` VALUES (2448, 'extreme', 'ɪkˈstriːm', 'adj.', '极度的;极大的;异乎寻常的;', 'The girls were afraid of snakes and picked their way along with extreme caution.', '女孩子们害怕蛇，走路时格外小心。');
INSERT INTO `words` VALUES (2449, 'extremely', 'ɪkˈstriːmli', 'adv.', '极其;极端;非常;', 'My mobile phone is extremely useful...', '我的手机非常有用。');
INSERT INTO `words` VALUES (2450, 'extremes', 'ɪksˈtriːmz', 'n.', '极端不同的感情(或境况、行为方式等);完全相反的事物;极端;', 'The girls were afraid of snakes and picked their way along with extreme caution.', '女孩子们害怕蛇，走路时格外小心。');
INSERT INTO `words` VALUES (2451, 'extremity', 'ɪkˈstreməti', 'n.', '末端;端点;尽头;', '...a small port on the north-western extremity of the Iberian peninsula.', '伊比利亚半岛西北端的小港口');
INSERT INTO `words` VALUES (2452, 'exuberant', 'ɪɡˈzjuːbərənt', 'adj.', '精力充沛的;热情洋溢的;兴高采烈的;', '...an exuberant young girl who decided to become a screen actress.', '决定成为一名电影演员的热情洋溢的年轻女孩');
INSERT INTO `words` VALUES (2453, 'eye', 'aɪ', 'n.', '眼睛;有…眼睛的;有…只眼的;', 'I opened my eyes and looked...', '我睁开眼睛看了看。');
INSERT INTO `words` VALUES (2454, 'eyes', 'aɪz', 'n.', '眼睛;有…眼睛的;有…只眼的;', 'I opened my eyes and looked...', '我睁开眼睛看了看。');
INSERT INTO `words` VALUES (2455, 'eyesight', 'ˈaɪsaɪt', 'n.', '视力;目力;', 'He suffered from poor eyesight and could no longer read properly.', '他视力不好，再也不能正常阅读了。');
INSERT INTO `words` VALUES (2456, 'fable', 'ˈfeɪbl', 'n.', '寓言;寓言故事;谎言;', '...the fable of the tortoise and the hare...', '龟兔赛跑的寓言');
INSERT INTO `words` VALUES (2457, 'fabric', 'ˈfæbrɪk', 'n.', '织物;布料;(社会、机构等的)结构;', '...small squares of red cotton fabric...', '小块方形红棉布');
INSERT INTO `words` VALUES (2458, 'fabricate', 'ˈfæbrɪkeɪt', 'v.', '编造;捏造;制造;', 'All four claim that officers fabricated evidence against them...', '4 人全都声称警察捏造证据陷害他们。');
INSERT INTO `words` VALUES (2459, 'fabrication', 'ˌfæbrɪˈkeɪʃn', 'n.', '制造，建造，装配，制作;虚构的事情，尤指无稽之谈；谎言；捏造的托词;构造物；构成部分;', 'All four claim that officers fabricated evidence against them...', '4 人全都声称警察捏造证据陷害他们。');
INSERT INTO `words` VALUES (2460, 'fabulous', 'ˈfæbjələs', 'adj.', '极好的;绝妙的;很大的;', 'This is a fabulous album. It\'s fresh, varied, fun...', '这张专辑真棒，新颖、有趣且富于变化。');
INSERT INTO `words` VALUES (2461, 'facet', 'ˈfæsɪt', 'n.', '(事物的)部分，方面;(宝石的)小平面，琢面;', 'The caste system shapes nearly every facet of Indian life.', '种姓制度决定了印度生活的几乎每一个方面。');
INSERT INTO `words` VALUES (2462, 'facial', 'ˈfeɪʃl', 'adj.', '面部的;', 'Cross didn\'t answer; his facial expression didn\'t change...', '克罗斯没有答话，脸上的表情也毫无变化。');
INSERT INTO `words` VALUES (2463, 'facilitate', 'fəˈsɪlɪteɪt', 'v.', '促进;促使;使便利;', 'The new airport will facilitate the development of tourism...', '新机场将促进旅游业的发展。');
INSERT INTO `words` VALUES (2464, 'facility', 'fəˈsɪləti', 'n.', '设施;设备;(机器等的)特别装置;', 'What recreational facilities are now available?...', '现在有些什么娱乐设施？');
INSERT INTO `words` VALUES (2465, 'facsimile', 'fækˈsɪməli', 'n.', '摹本;传真本;复制本;', '...a facsimile of his writing desk.', '他的写字台的仿制品');
INSERT INTO `words` VALUES (2466, 'fact', 'fækt', 'n.', '现实;实际情况;(可证实的)事实，真相;', 'His chances do not seem good in view of the fact that the Chief Prosecutor has already voiced his public disapproval...', '鉴于检察长已公开表示反对，他的机会看来不大了。');
INSERT INTO `words` VALUES (2467, 'faction', 'ˈfækʃn', 'n.', '(大团体中的)派系，派别，小集团;派系斗争;内讧;', 'A peace agreement will be signed by the leaders of the country\'s warring factions.', '该国各敌对派系领导人将签署和平协议。');
INSERT INTO `words` VALUES (2468, 'factor', 'ˈfæktə(r)', 'n.', '因素;要素;因子;', 'Physical activity is an important factor in maintaining fitness.', '进行体育活动是保持身体健康的一个重要因素。');
INSERT INTO `words` VALUES (2469, 'factoring', 'ˈfæktərɪŋ', 'v.', '把…因素包括进去;（数学）分解…的因子，将…分解成因子;以代理商（或管家等）的身份行事;', 'Physical activity is an important factor in maintaining fitness.', '进行体育活动是保持身体健康的一个重要因素。');
INSERT INTO `words` VALUES (2470, 'factory', 'ˈfæktri', 'n.', '工厂;制造厂;', 'He owned furniture factories in New York State.', '他在纽约州有几家家具厂。');
INSERT INTO `words` VALUES (2471, 'faculty', 'ˈfæklti', 'n.', '官能;天赋;才能;', 'He was drunk and not in control of his faculties...', '他喝醉了，浑身都不听使唤了。');
INSERT INTO `words` VALUES (2472, 'fade', 'feɪd', 'v.', '(使)变淡，变暗;逐渐消逝;逐渐消失;', 'All colour fades — especially under the impact of direct sunlight...', '所有颜色都会褪色——尤其是在阳光直射下。');
INSERT INTO `words` VALUES (2473, 'fahrenheit', 'ˈfærənhaɪt', 'adj.', '华氏温度计的，华氏的(冰点为32度，沸点为212度);', 'By mid-morning, the temperature was already above 100 degrees Fahrenheit.', '到早上10点钟，温度已经超过100华氏度了。');
INSERT INTO `words` VALUES (2474, 'fail', 'feɪl', 'v.', '失败;未能(做到);未做;', 'The Workers\' Party failed to win a single governorship...', '劳工党连一个州长职位也未能赢得。');
INSERT INTO `words` VALUES (2475, 'failing', 'ˈfeɪlɪŋ', 'n.', '弱点;缺点;', 'Like many in Russia, she blamed the country\'s failings on futile attempts to catch up with the West...', '像俄罗斯很多人一样，她认为国家的错误就在于徒然地竭力追赶西方。');
INSERT INTO `words` VALUES (2476, 'failure', 'ˈfeɪljə(r)', 'n.', '失败;失败的人(或事物);未做，未履行(应做之事);', 'This policy is doomed to failure...', '这项政策注定会失败。');
INSERT INTO `words` VALUES (2477, 'faint', 'feɪnt', 'adj.', '(光、声、味)微弱的，不清楚的;微小的;可能性不大的;', 'He became aware of the soft, faint sounds of water dripping...', '他开始注意到轻微的水滴声。');
INSERT INTO `words` VALUES (2478, 'fair', 'feə(r)', 'adj.', '合理的;恰当的;适当的;', 'It didn\'t seem fair to leave out her father...', '将她父亲排除在外似乎不公平。');
INSERT INTO `words` VALUES (2479, 'fairly', 'ˈfeəli', 'adv.', '一定地;相当地;公平合理地;', 'Both ships are fairly new...', '两艘船都还比较新。');
INSERT INTO `words` VALUES (2480, 'faith', 'feɪθ', 'n.', '信任;相信;信心;', 'She had placed a great deal of faith in Mr Penleigh...', '她对彭利先生极其信任。');
INSERT INTO `words` VALUES (2481, 'faithful', 'ˈfeɪθfl', 'adj.', '忠实的;忠诚的;(宗教的)忠实信徒;', 'She had been faithful to her promise to guard this secret...', '她一直信守承诺，保守着这个秘密。');
INSERT INTO `words` VALUES (2482, 'fake', 'feɪk', 'adj.', '假的;冒充的;伪造的;', 'The bank manager is said to have issued fake certificates.', '据说那个银行经理曾出具伪造的凭证。');
INSERT INTO `words` VALUES (2483, 'fall', 'fɔːl', 'v.', '落下;下落;掉落;', 'Her father fell into the sea after a massive heart attack...', '她父亲在一次严重的心脏病发作后坠入海中。');
INSERT INTO `words` VALUES (2484, 'fallacy', 'ˈfæləsi', 'n.', '谬见;谬论;谬误;', 'It\'s a fallacy that the affluent give relatively more to charity than the less prosperous...', '富人比穷人对慈善事业的捐助更多些;这种观点是一种谬见。');
INSERT INTO `words` VALUES (2485, 'fallback', 'ˈfɔːlbæk', 'n.', '应变计划;退路;', 'Yesterday\'s vote itself was a retreat from an earlier fallback position.', '昨天的投票本身就是放弃先前所留退路的做法。');
INSERT INTO `words` VALUES (2486, 'fallible', 'ˈfæləbl', 'adj.', '会犯错误的;', 'They are only human and all too fallible...', '他们只不过是普通人，太容易犯错误了。');
INSERT INTO `words` VALUES (2487, 'false', 'fɔːls', 'adj.', '错误的;不正确的;不真实的;', 'It was quite clear the President was being given false information by those around him...', '很明显，总统身边的人在不断向他传递错误信息。');
INSERT INTO `words` VALUES (2488, 'falter', 'ˈfɔːltə(r)', 'v.', '衰弱;衰退;衰落;', 'Normal life is at a standstill, and the economy is faltering...', '正常生活陷于瘫痪，经济止步不前。');
INSERT INTO `words` VALUES (2489, 'fame', 'feɪm', 'n.', '名声;声誉;名气;', 'At the height of his fame, his every word was valued...', '在他声名鼎盛之时，他的每句话都受到重视。');
INSERT INTO `words` VALUES (2490, 'familiar', 'fəˈmɪliə(r)', 'adj.', '熟悉的;常见到的;常听说的;', 'He talked of other cultures as if they were more familiar to him than his own...', '他谈起其他文化时似乎比对他自己的文化还要熟悉。');
INSERT INTO `words` VALUES (2491, 'familiarity', 'fəˌmɪliˈærəti', 'n.', '熟悉;通晓;认识;', 'Familiarity with evil breeds not contempt but acceptance.', '对罪恶熟悉之后，生出的不是蔑视，而是习以为常。');
INSERT INTO `words` VALUES (2492, 'family', 'ˈfæməli', 'n.', '家，家庭(包括父母子女);(大)家庭(包括父母子女及近亲);亲属;', 'There\'s room in there for a family of five...', '那儿能住下五口之家。');
INSERT INTO `words` VALUES (2493, 'famine', 'ˈfæmɪn', 'n.', '饥荒;', 'Thousands of refugees are trapped by war, drought and famine...', '成千上万的难民陷于战争、旱灾和饥荒之中。');
INSERT INTO `words` VALUES (2494, 'famous', 'ˈfeɪməs', 'adj.', '著名的;出名的;', 'New Orleans is famous for its cuisine.', '新奥尔良以其美食而著称。');
INSERT INTO `words` VALUES (2495, 'fan', 'fæn', 'n.', '迷;狂热爱好者;狂热仰慕者;', 'If you\'re a Billy Crystal fan, you\'ll love this movie...', '如果你是比利·克里斯托的影迷，你会喜爱这部影片。');
INSERT INTO `words` VALUES (2496, 'fanatic', 'fəˈnætɪk', 'n.', '入迷者;极端分子;狂热信徒;', 'I am not a religious fanatic but I am a Christian.', '我不是宗教狂热分子，但我是基督徒。');
INSERT INTO `words` VALUES (2497, 'fantasy', 'ˈfæntəsi', 'n.', '幻想;想象;想象产物;', '...fantasies of romance and true love.', '对浪漫和真爱的憧憬');
INSERT INTO `words` VALUES (2498, 'far', 'fɑː(r)', 'adv.', '远;(问到或谈及距离时说)有多远，远(至);久;', 'I know a nice little Italian restaurant not far from here...', '我知道有家不错的意大利小餐馆离这儿不远。');
INSERT INTO `words` VALUES (2499, 'fare', 'feə(r)', 'n.', '车费;船费;飞机票价;', 'He could barely afford the railway fare.', '他几乎买不起火车票。');
INSERT INTO `words` VALUES (2500, 'farm', 'fɑːm', 'n.', '农场;农舍;养殖场;', 'Farms in France are much smaller than those in the United States or even Britain.', '法国的农场比美国甚至是英国的农场都要小很多。');
INSERT INTO `words` VALUES (2501, 'fascinate', 'ˈfæsɪneɪt', 'v.', '深深吸引;迷住;', 'Politics fascinated Franklin\'s father...', '政治让富兰克林的父亲着迷。');
INSERT INTO `words` VALUES (2502, 'fascinating', 'ˈfæsɪneɪtɪŋ', 'adj.', '极有吸引力的;迷人的;', 'Madagascar is the most fascinating place I have ever been to...', '马达加斯加是我去过的最迷人的地方。');
INSERT INTO `words` VALUES (2503, 'fascination', 'ˌfæsɪˈneɪʃn', 'n.', '魅力;极大的吸引力;入迷;', 'I\'ve had a lifelong fascination with the sea and with small boats.', '我一生都对大海和小船着迷。');
INSERT INTO `words` VALUES (2504, 'fascist', 'ˈfæʃɪst', 'n.', '法西斯主义者;极右分子;', '...an upsurge of support for extreme rightist, nationalist and fascist organisations.', '一股支持极右团体、民族主义者以及法西斯组织的狂潮');
INSERT INTO `words` VALUES (2505, 'fashion', 'ˈfæʃn', 'n.', '(衣服、发式等的)流行款式，时兴式样;(行为、活动等的)时尚，时兴;时装业;', 'There are 20 full-colour pages of fashion for men...', '书中有 20 张有关男性时尚的彩色插页。');
INSERT INTO `words` VALUES (2506, 'fashionable', 'ˈfæʃnəbl', 'adj.', '流行的;时兴的;时髦的;', 'It became fashionable to eat certain kinds of fish...', '吃某些种类的鱼开始流行起来。');
INSERT INTO `words` VALUES (2507, 'fashioned', 'ˈfæʃnd', 'v.', '(尤指用手工)制作，使成形，塑造;', 'There are 20 full-colour pages of fashion for men...', '书中有 20 张有关男性时尚的彩色插页。');
INSERT INTO `words` VALUES (2508, 'fast', 'fɑːst', 'adj.', '快的;迅速的;敏捷的;', '...fast cars with flashing lights and sirens...', '闪着灯、鸣着笛、飞速行驶的车辆');
INSERT INTO `words` VALUES (2509, 'fasten', 'ˈfɑːsn', 'v.', '(使两部分)系牢，扎牢，结牢，扣紧;(使)关紧，盖好;使牢固;', 'She got quickly into her Mini and fastened the seat-belt...', '她迅速钻进她的 Mini 车里并系紧安全带。');
INSERT INTO `words` VALUES (2510, 'fastidious', 'fæˈstɪdiəs', 'adj.', '一丝不苟的;严谨的;讲究整洁的;', '...her fastidious attention to historical detail...', '她对历史细节的过分关注');
INSERT INTO `words` VALUES (2511, 'fat', 'fæt', 'adj.', '肥的;肥胖的;厚的;', 'I could eat what I liked without getting fat...', '我怎么吃都不胖。');
INSERT INTO `words` VALUES (2512, 'fatal', 'ˈfeɪtl', 'adj.', '致命的;灾难性的;毁灭性的;', 'It would clearly be fatal for Europe to quarrel seriously with America...', '欧洲若与美国反目显然会有致命的后果。');
INSERT INTO `words` VALUES (2513, 'fate', 'feɪt', 'n.', '命中注定的事(尤指坏事);命运的安排;命运;', 'I see no use quarrelling with fate.', '我看不出和命运抗争有什么用。');
INSERT INTO `words` VALUES (2514, 'father', 'ˈfɑːðə(r)', 'n.', '父亲;爸爸;祖先;', 'His father was a painter...', '他父亲是个画家。');
INSERT INTO `words` VALUES (2515, 'fathom', 'ˈfæðəm', 'v.', '理解;彻底了解;弄清真相;', 'We sailed into the bay and dropped anchor in five fathoms of water.', '我们驶进海湾，将锚抛进 5 英寻深的水里。');
INSERT INTO `words` VALUES (2516, 'fatigue', 'fəˈtiːɡ', 'n.', '疲劳;劳累;厌倦;', 'She continued to have severe stomach cramps, aches, fatigue, and depression...', '她仍然患有严重的胃痉挛，感觉疼痛、疲乏、抑郁。');
INSERT INTO `words` VALUES (2517, 'fatuous', 'ˈfætʃuəs', 'adj.', '愚蠢的;愚昧的;', 'The Chief was left speechless by this fatuous remark.', '这句傻话让主任一时说不出话来。');
INSERT INTO `words` VALUES (2518, 'fault', 'fɔːlt', 'n.', '责任;过错;过失;', 'There was no escaping the fact: it was all his fault...', '这全是他的错，这是无法回避的事实。');
INSERT INTO `words` VALUES (2519, 'faultless', 'ˈfɔːltləs', 'adj.', '没有错误的;无缺点的;完美无缺的;', '...Mary Thomson\'s faultless and impressive performance on the show...', '玛丽·汤姆森在演出中令人印象深刻的完美表现');
INSERT INTO `words` VALUES (2520, 'faulty', 'ˈfɔːlti', 'adj.', '不完美的;有错误的;有缺陷的;', 'The money will be used to repair faulty equipment.', '这笔钱将用来修理出故障的设备。');
INSERT INTO `words` VALUES (2521, 'fax', 'fæks', 'n.', '传真机;传真(系统);传真信件;', '...a modern reception desk with telephone and fax...', '有电话和传真机的现代化接待处');
INSERT INTO `words` VALUES (2522, 'fear', 'fɪə(r)', 'n.', '害怕;惧怕;担忧;', 'I was sitting on the floor shivering with fear.', '我坐在地板上，吓得发抖。');
INSERT INTO `words` VALUES (2523, 'fearful', 'ˈfɪəfl', 'adj.', '担心;担忧;忧虑;', 'Bankers were fearful of a world banking crisis...', '银行家们害怕会发生一场全球银行业危机。');
INSERT INTO `words` VALUES (2524, 'fearless', 'ˈfɪələs', 'adj.', '不怕的;无畏的;大胆的;', '...his fearless campaigning for racial justice.', '他为争取种族平等而进行的无畏斗争');
INSERT INTO `words` VALUES (2525, 'feasibility', 'ˌfiːzəˈbɪlɪti', 'n.', '可行性；可能性;', 'She questioned whether it was feasible to stimulate investment in these regions...', '她对在这些地区刺激投资是否可行表示怀疑。');
INSERT INTO `words` VALUES (2526, 'feasible', 'ˈfiːzəbl', 'adj.', '可行的;行得通的;', 'She questioned whether it was feasible to stimulate investment in these regions...', '她对在这些地区刺激投资是否可行表示怀疑。');
INSERT INTO `words` VALUES (2527, 'feast', 'fiːst', 'n.', '盛宴;宴会;(宗教的)节日，节期;', 'Lunch was a feast of meat and vegetables, cheese, yoghurt and fruit, with unlimited wine...', '午餐很丰盛，有肉、蔬菜、奶酪、酸奶、水果, 酒无限量供应。');
INSERT INTO `words` VALUES (2528, 'feat', 'fiːt', 'n.', '技艺;武艺;功绩;', 'A racing car is an extraordinary feat of engineering.', '赛车是工程学上一项相当了不起的成就。');
INSERT INTO `words` VALUES (2529, 'feather', 'ˈfeðə(r)', 'n.', '羽毛;翎毛;', '...a hat that she had made herself from black ostrich feathers.', '她用黑色鸵鸟羽毛给自己做的帽子');
INSERT INTO `words` VALUES (2530, 'feature', 'ˈfiːtʃə(r)', 'n.', '特色;特征;特点;', 'Patriotic songs have long been a feature of Kuwaiti life...', '爱国歌曲长期以来一直是科威特生活的一个特色。');
INSERT INTO `words` VALUES (2531, 'february', 'ˈfebruəri', 'n.', '二月;', 'He joined the Army in February 1943...', '他 1943 年 2 月参军。');
INSERT INTO `words` VALUES (2532, 'federal', 'ˈfedərəl', 'adj.', '联邦制的;(在美国、加拿大等联邦制下)联邦政府的;', 'Five of the six provinces are to become autonomous regions in a new federal system of government.', '在新的联邦政府体制中，6 省中的 5 个将成为自治区。');
INSERT INTO `words` VALUES (2533, 'federation', 'ˌfedəˈreɪʃn', 'n.', '联邦;(俱乐部、工会等的)联合会;同盟;', '...the Russian Federation.', '俄罗斯联邦');
INSERT INTO `words` VALUES (2534, 'fee', 'fiː', 'n.', '专业服务费;咨询费;报酬;', 'He hadn\'t paid his television licence fee.', '他尚未缴纳电视机使用许可费。');
INSERT INTO `words` VALUES (2535, 'feeble', 'ˈfiːbl', 'adj.', '虚弱的;衰弱的;无效的;', 'He told them he was old and feeble and was not able to walk so far...', '他告诉他们，他年老体弱，不能走那么远。');
INSERT INTO `words` VALUES (2536, 'feed', 'fiːd', 'v.', '给(人或动物)食物;喂养;饲养;', 'We brought along pieces of old bread and fed the birds...', '我们拿了几片陈面包来喂鸟。');
INSERT INTO `words` VALUES (2537, 'feedback', 'ˈfiːdbæk', 'n.', '反馈的意见(或信息);(电器的)反馈噪音;', 'Continue to ask for feedback on your work...', '继续征求对你的工作的反馈意见。');
INSERT INTO `words` VALUES (2538, 'feeder', 'ˈfiːdə(r)', 'n.', '(动植物)进某种食物者，以…方式进食者;(机器的)进料器，供给装置;鸟食罐;', '...the feeder road leading to the airport.', '通往机场的岔路');
INSERT INTO `words` VALUES (2539, 'feel', 'fiːl', 'v.', '觉得;感到;体会到;', 'I am feeling very depressed...', '我觉得非常沮丧。');
INSERT INTO `words` VALUES (2540, 'feeling', 'ˈfiːlɪŋ', 'n.', '(内心和感官的)感觉，感触;想法;看法;', 'It gave me a feeling of satisfaction...', '这给了我一种满足感。');
INSERT INTO `words` VALUES (2541, 'fellow', 'ˈfeləʊ', 'n.', '男人;男孩;小伙子;', 'She discovered to her pleasure, a talent for making her fellow guests laugh...', '她高兴地发现自己具备一种能把其他客人逗笑的才能。');
INSERT INTO `words` VALUES (2542, 'fellowship', 'ˈfeləʊʃɪp', 'n.', '伙伴关系;友谊;交情;', '...the National Schizophrenia Fellowship...', '国家精神分裂研究协会');
INSERT INTO `words` VALUES (2543, 'female', 'ˈfiːmeɪl', 'adj.', '女的;女性的;雌的;', '...a sixteen-piece dance band with a female singer...', '16 人组成的伴舞乐队和一位女歌手');
INSERT INTO `words` VALUES (2544, 'feminine', 'ˈfemənɪn', 'adj.', '(指气质或外貌)女性特有的，女性的，妇女的;阴性的;指名词、代词或形容词阴性的;', '...male leaders worrying about their women abandoning traditional feminine roles.', '男性领导人担心自己的妻子放弃传统的女性角色');
