INSERT INTO `words` VALUES (2546, 'fend', 'fend', 'v.', '照料，照顾(自己);', 'The woman and her young baby had been thrown out and left to fend for themselves.', '这个女人和她年幼的婴儿被赶了出来，无人照管。');
