INSERT INTO `words` VALUES (2552, 'fervent', 'ˈfɜːvənt', 'adj.', '热情的;热诚的;热烈的;', '...a fervent admirer of Morisot\'s work.', '摩里索作品热忱的欣赏者');
INSERT INTO `words` VALUES (2553, 'fester', 'ˈfestə(r)', 'v.', '化脓;溃烂;更加苦恼;', 'Resentments are starting to fester.', '仇恨开始日益加深。');
INSERT INTO `words` VALUES (2554, 'festival', 'ˈfestɪvl', 'n.', '(音乐、戏剧、电影等的)会演，节;节日;节期;', 'Numerous Umbrian towns hold their own summer festivals of music, theatre, and dance...', '很多翁布里亚小镇举办自己的夏季音乐、戏剧和舞蹈节。');
INSERT INTO `words` VALUES (2555, 'fetch', 'fetʃ', 'v.', '(去)拿来;(去)请来;售得，卖得(某价);', 'Sylvia fetched a towel from the bathroom...', '西尔维娅去卫生间拿了一条毛巾。');
INSERT INTO `words` VALUES (2556, 'fetter', 'ˈfetə(r)', 'v.', '束缚;限制，抑制(某人的自由);给(囚犯)上脚镣;', '...a private trust which would not be fettered by bureaucracy...', '不受官僚繁文缛节限制的私人信托');
INSERT INTO `words` VALUES (2557, 'feud', 'fjuːd', 'n.', '长期不和;争吵不休;世仇;', '...a long and bitter feud between the state government and the villagers.', '州政府和村民间的久积深怨');
INSERT INTO `words` VALUES (2558, 'feudal', 'ˈfjuːdl', 'adj.', '封建(制度)的;', '...the emperor and his feudal barons.', '皇帝和封建贵族');
INSERT INTO `words` VALUES (2559, 'fever', 'ˈfiːvə(r)', 'n.', '发烧;发热;热(病);', 'My Uncle Jim had a high fever...', '我叔叔吉姆发了高烧。');
INSERT INTO `words` VALUES (2560, 'few', 'fjuː', 'det./adj.', '(与复数名词和复数动词连用)不多，很少;(与复数名词和复数动词连用)有些，几个;', 'I gave a dinner party for a few close friends...', '我为几个密友办了晚宴。');
INSERT INTO `words` VALUES (2561, 'fickle', 'ˈfɪkl', 'adj.', '易变的;无常的;反复无常的;', 'The group has been notoriously fickle in the past.', '这伙人过去一直是出了名的善变。');
INSERT INTO `words` VALUES (2562, 'fiction', 'ˈfɪkʃn', 'n.', '小说;虚构的事;假想之物;', 'Immigrant tales have always been popular themes in fiction...', '移民故事一直是小说中常见的主题。');
INSERT INTO `words` VALUES (2563, 'fictional', 'ˈfɪkʃənl', 'adj.', '虚构的;小说(中)的;', 'It is drama featuring fictional characters...', '这部戏剧中的人物是虚构的。');
INSERT INTO `words` VALUES (2564, 'fiddle', 'ˈfɪdl', 'v.', '(尤指厌烦或紧张地)不断摸弄，不停摆弄;篡改;伪造;', 'Harriet fiddled with a pen on the desk.', '哈丽雅特摆弄着桌上的钢笔。');
INSERT INTO `words` VALUES (2565, 'field', 'fiːld', 'n.', '田;地;牧场;', '...a field of wheat...', '麦田');
INSERT INTO `words` VALUES (2566, 'fierce', 'fɪəs', 'adj.', '凶猛的;凶狠的;凶残的;', 'They look like the teeth of some fierce animal.', '它们看上去像某种猛兽的牙齿。');
INSERT INTO `words` VALUES (2567, 'fiery', 'ˈfaɪəri', 'adj.', '火一般的;火的;暴躁的;', 'A helicopter crashed in a fiery explosion in Vallejo.', '一架直升机在瓦列霍爆炸起火并坠毁。');
INSERT INTO `words` VALUES (2568, 'fifteen', 'ˌfɪfˈtiːn', 'n.', '十五;十五个人组成的橄榄球队;', 'In India, there are fifteen official languages.', '印度有 15 种官方语言。');
INSERT INTO `words` VALUES (2569, 'fifteenth', 'ˌfɪfˈtiːnθ', 'ord.', '第十五;', '...the invention of the printing press in the fifteenth century.', '15 世纪印刷机的发明');
INSERT INTO `words` VALUES (2570, 'fifth', 'fɪfθ', 'ord.', '第五;', 'Joe has recently returned from his fifth trip to Australia.', '乔最近结束了第五次澳大利亚之行归来。');
INSERT INTO `words` VALUES (2571, 'fight', 'faɪt', 'v.', '打仗;战斗;作战;', 'Mother Teresa is an elderly nun who has devoted her life to fighting poverty...', '德肋撒嬷嬷是一位毕生致力于消除贫困事业的年迈修女。');
INSERT INTO `words` VALUES (2572, 'fighting', 'ˈfaɪtɪŋ', 'v.', '打仗;战斗;作战;', 'Mother Teresa is an elderly nun who has devoted her life to fighting poverty...', '德肋撒嬷嬷是一位毕生致力于消除贫困事业的年迈修女。');
INSERT INTO `words` VALUES (2573, 'figurative', 'ˈfɪɡərətɪv', 'adj.', '比喻的;形象的;', '...an event that will change your route — in both the literal and figurative sense.', '一个无论从字面还是比喻意义上来说都将改变你的路线的事件');
INSERT INTO `words` VALUES (2574, 'figure', 'ˈfɪɡə(r)', 'n.', '(代表数量，尤指官方资料中的)数字;数字符号;字码;', 'It would be very nice if we had a true figure of how many people in this country haven\'t got a job...', '如果我们能得到该国失业人口的真实数字就好了。');
INSERT INTO `words` VALUES (2575, 'file', 'faɪl', 'n.', '文件夹;卷宗;(计算机的)文件;', 'He sat behind a table on which were half a dozen files.', '他坐在一张放有6个文件夹的桌子后面。');
INSERT INTO `words` VALUES (2576, 'filing', 'ˈfaɪlɪŋ', 'n.', '存档;归档;存档档案;', 'He sat behind a table on which were half a dozen files.', '他坐在一张放有6个文件夹的桌子后面。');
INSERT INTO `words` VALUES (2577, 'fill', 'fɪl', 'v.', '(使)充满，装满，注满，填满;堵塞，填补(洞、孔);使充满(感情);', 'Fill a saucepan with water and bring to a slow boil...', '往平底锅里加满水,小火煮沸。');
INSERT INTO `words` VALUES (2578, 'filling', 'ˈfɪlɪŋ', 'n.', '(补牙的)填料;(糕点等的)馅;(枕头、靠垫等的)填充物，填料;', 'The longer your child can go without needing a filling, the better.', '孩子越晚需要补牙就越好。');
INSERT INTO `words` VALUES (2579, 'film', 'fɪlm', 'n.', '电影;影片;电影制作艺术;', 'Everything about the film was good. Good acting, good story, good fun.', '这部片子各个方面都很不错：表演出色，故事精彩，情节有趣。');
INSERT INTO `words` VALUES (2580, 'filter', 'ˈfɪltə(r)', 'n.', '滤器;过滤器;滤光器;', 'The best prevention for cholera is to boil or filter water, and eat only well-cooked food.', '预防霍乱的最佳方法是将水煮沸或过滤，并且只食用煮熟后的食物。');
INSERT INTO `words` VALUES (2581, 'filth', 'fɪlθ', 'n.', '污物;污秽;下流言辞;', 'The living-room floor was littered with filth and tin cans...', '客厅地上丢满了脏东西和罐头听。');
INSERT INTO `words` VALUES (2582, 'filthy', 'ˈfɪlθi', 'adj.', '肮脏的;污秽的;下流的;', 'He never washed, and always wore a filthy old jacket.', '他总是穿着一件从来不洗的脏兮兮的旧夹克。');
INSERT INTO `words` VALUES (2583, 'final', 'ˈfaɪnl', 'adj.', '最终的;最后的;(指结果)最终的;', 'Astronauts will make a final attempt today to rescue a communications satellite from its useless orbit...', '今天宇航员将会做最后一次尝试,力图把一颗通信卫星从无效的轨道上抢救下来。');
INSERT INTO `words` VALUES (2584, 'finally', 'ˈfaɪnəli', 'adv.', '终于;最终;(用于列举)最后;', 'The word was finally given for us to get on board...', '终于通知我们登乘了。');
INSERT INTO `words` VALUES (2585, 'finance', 'ˈfaɪnæns', 'n.', '资金;财政;金融;', 'The fund has been used largely to finance the construction of federal prisons...', '该基金大部分用于了资助联邦监狱的建造。');
