INSERT INTO `words` VALUES (2589, 'find', 'faɪnd', 'v.', '(意外或偶然地)发现，碰到;找到;找回;', 'The police also found a pistol...', '警方还发现了一把手枪。');
INSERT INTO `words` VALUES (2590, 'finder', 'ˈfaɪndə(r)', 'n.', '发现者;寻得者;', 'The finder of a wallet who takes it home may be guilty of theft...', '将钱包据为己有的人可能会被控盗窃。');
INSERT INTO `words` VALUES (2591, 'finding', 'ˈfaɪndɪŋ', 'n.', '调查发现;调研结果;判决;', 'One of the main findings of the survey was the confusion about the facilities already in place...', '这项调查的一个主要发现就是对于哪些设备已经到位搞不清楚。');
INSERT INTO `words` VALUES (2592, 'finger', 'ˈfɪŋɡə(r)', 'n.', '手指;有…手指的;有(或用)…手指的;', 'She suddenly held up a small, bony finger and pointed across the room...', '她突然伸出一根瘦小的手指，指向屋子的另一头。');
