INSERT INTO `words` VALUES (2594, 'finish', 'ˈfɪnɪʃ', 'v.', '完成;做好;(使)结束;', 'As soon as he\'d finished eating, he excused himself...', '他一吃完就告辞离开了。');
INSERT INTO `words` VALUES (2595, 'finished', 'ˈfɪnɪʃt', 'adj.', '完成;不再与…打交道;垮台;', 'One suspects he will be finished with boxing.', '人们猜想他不会再打拳了。');
