INSERT INTO `words` VALUES (2601, 'first', 'fɜːst', 'det./ord.', '第一;最重要的;首要的;', 'She lost 16 pounds in the first month of her diet.', '她节食的头一个月就减掉了16磅。');
INSERT INTO `words` VALUES (2602, 'fiscal', 'ˈfɪskl', 'adj.', '财政的;国库的;国家岁入的;', '...in 1987, when the government tightened fiscal policy.', '在1987 年，当时政府紧缩财政政策');
