INSERT INTO `words` VALUES (2604, 'fishing', 'ˈfɪʃɪŋ', 'n.', '钓鱼;捕鱼业(或活动);', 'Despite the poor weather the fishing has been pretty good.', '尽管天气恶劣，却渔获颇丰。');
INSERT INTO `words` VALUES (2605, 'fist', 'fɪst', 'n.', '拳;拳头;', 'Angry protestors with clenched fists shouted their defiance...', '愤怒的抗议者们紧握拳头大声抗议。');
INSERT INTO `words` VALUES (2606, 'fitting', 'ˈfɪtɪŋ', 'adj.', '适合(某场合)的;恰当的;合身的;', '...brass light fittings.', '黄铜灯具配件');
INSERT INTO `words` VALUES (2607, 'five', 'faɪv', 'num.', '五;', 'Eric Edward Bullus was born in Peterborough, the second of five children.', '埃里克·爱德华·布勒斯出生于彼得伯勒，是 5 个孩子中的老二。');
INSERT INTO `words` VALUES (2608, 'fix', 'fɪks', 'v.', '使固定;安装;决定，确定(日期、时间、数量等);', 'It is fixed on the wall...', '它固定在墙上。');
INSERT INTO `words` VALUES (2609, 'fixed', 'fɪkst', 'adj.', '固定的;不变的;不能变的;', 'They issue a fixed number of shares that trade publicly...', '他们发行一定数量的可公开交易的股票。');
INSERT INTO `words` VALUES (2610, 'fixing', 'ˈfɪksɪŋ', 'v.', '使固定;安装;决定，确定(日期、时间、数量等);', 'It is fixed on the wall...', '它固定在墙上。');
INSERT INTO `words` VALUES (2611, 'fixture', 'ˈfɪkstʃə(r)', 'n.', '(定期定点举行的)体育活动，体育节;固定设施(如房屋内安装的浴缸或抽水马桶);', '...a detailed list of what fixtures and fittings are included in the purchase price.', '房屋购买价中包括的固定装置及设备的详细清单');
INSERT INTO `words` VALUES (2612, 'flabby', 'ˈflæbi', 'adj.', '(肌肉)松弛的;肥胖的;软弱的;', 'This exercise is brilliant for getting rid of flabby tums.', '这个练习对减除小肚子上的赘肉极为有效。');
INSERT INTO `words` VALUES (2613, 'flag', 'flæɡ', 'n.', '旗;旗帜(指某国家或组织及其信仰和价值观);(体育运动的)信号旗，标志旗;', 'The Marines climbed to the roof of the embassy building to raise the American flag...', '海军陆战队队员爬到使馆大楼顶上，升起了美国国旗。');
INSERT INTO `words` VALUES (2614, 'flagging', 'ˈflæɡɪŋ', 'v.', '标示(重要处);疲乏;变弱;', 'The Marines climbed to the roof of the embassy building to raise the American flag...', '海军陆战队队员爬到使馆大楼顶上，升起了美国国旗。');
INSERT INTO `words` VALUES (2615, 'flake', 'fleɪk', 'n.', '小薄片;(尤指)碎片;古怪的人;', '...flakes of paint...', '剥落的油漆碎片');
INSERT INTO `words` VALUES (2616, 'flame', 'fleɪm', 'n.', '火焰;火舌;鲜红色;', 'The heat from the flames was so intense that roads melted.', '烈焰的温度很高，连路面都被熔化了。');
INSERT INTO `words` VALUES (2617, 'flames', 'fleɪmz', 'n.', '火焰;火舌;鲜红色;', 'The heat from the flames was so intense that roads melted.', '烈焰的温度很高，连路面都被熔化了。');
INSERT INTO `words` VALUES (2618, 'flank', 'flæŋk', 'n.', '(建筑物、山等的)侧面;(军队或运动队的)翼侧，侧面，侧翼;(动物的)胁腹;', 'He put his hand on the dog\'s flank.', '他把手放在狗的胁腹上。');
INSERT INTO `words` VALUES (2619, 'flannel', 'ˈflænl', 'n.', '法兰绒;(洗擦身体用的)毛巾;法兰绒裤;', 'He wore a faded red flannel shirt.', '他身穿一件褪了色的红色法兰绒衬衫。');
INSERT INTO `words` VALUES (2620, 'flap', 'flæp', 'n.', '(附于某物的)片状下垂物，封盖，口盖，袋盖;(上下或左右)拍打，振(翅)，拍击;忧虑;', 'Grey sheets flapped on the clothes line...', '灰色的床单在晾衣绳上飘来摆去的。');
INSERT INTO `words` VALUES (2621, 'flare', 'fleə(r)', 'v.', '(短暂)烧旺;(摇曳着)燃烧;(火光)闪耀;', '...a ship which had fired a distress flare.', '发出遇险信号弹的一艘船');
INSERT INTO `words` VALUES (2622, 'flash', 'flæʃ', 'v.', '(使)闪耀，闪光;(向…)用光发出(信号);(快速地)出示，显示;', 'A sudden flash of lightning lit everything up for a second...', '突然一个闪电瞬间照亮了一切。');
INSERT INTO `words` VALUES (2623, 'flashing', 'ˈflæʃɪŋ', 'n.', '(房顶与墙交接处的)防雨板，盖片;', 'A sudden flash of lightning lit everything up for a second...', '突然一个闪电瞬间照亮了一切。');
INSERT INTO `words` VALUES (2624, 'flask', 'flɑːsk', 'n.', '烧瓶;', 'He took out a metal flask from a canvas bag.', '他从帆布包里拿出一个金属瓶子。');
INSERT INTO `words` VALUES (2625, 'flat', 'flæt', 'adj.', '水平的;平坦的;平滑的;', 'Sara lives with her husband and children in a flat in central London...', '萨拉和丈夫还有孩子们住在伦敦中心城区的一间公寓里。');
INSERT INTO `words` VALUES (2626, 'flatten', 'ˈflætn', 'v.', '(使)变平;把…弄平;摧毁，推倒，弄倒(建筑物、树木等);', 'He carefully flattened the wrappers and put them between the leaves of his book...', '他小心翼翼地把包装纸弄平，夹在书页里。');
INSERT INTO `words` VALUES (2627, 'flatter', 'ˈflætə(r)', 'v.', '奉承;讨好;向…谄媚;', 'I knew she was just flattering me.', '我知道她只是在恭维我。');
INSERT INTO `words` VALUES (2628, 'flaunt', 'flɔːnt', 'v.', '炫耀;夸示;夸耀;', 'They drove around in Rolls-Royces, openly flaunting their wealth...', '他们开着劳斯莱斯到处转悠，公开炫耀他们的财富。');
INSERT INTO `words` VALUES (2629, 'flaw', 'flɔː', 'n.', '错误;缺点;裂痕;', 'There were, however, a number of crucial flaws in his monetary theory...', '但是，他的货币理论中存在若干关键性错误。');
INSERT INTO `words` VALUES (2630, 'flee', 'fliː', 'v.', '迅速离开;(尤指害怕有危险而)逃避，逃跑;', 'He slammed the bedroom door behind him and fled...', '他把卧室房门重重地关上，然后逃跑了。');
INSERT INTO `words` VALUES (2631, 'fleet', 'fliːt', 'n.', '舰队;捕鱼船队;(一国的)全部军舰，海军;', 'The damage inflicted upon the British fleet was devastating.', '英国舰队遭到了毁灭性的重创。');
INSERT INTO `words` VALUES (2632, 'fleeting', 'ˈfliːtɪŋ', 'adj.', '短暂的;闪现的;', 'The girls caught only a fleeting glimpse of the driver...', '女孩们只匆匆瞥了一眼司机。');
INSERT INTO `words` VALUES (2633, 'flesh', 'fleʃ', 'n.', '(动物或人的)肉;(人体的)皮肤;蔬菜的可食部分;', '...maggots which eat away dead flesh.', '食腐肉的蛆虫');
INSERT INTO `words` VALUES (2634, 'fleshy', 'ˈfleʃi', 'adj.', '多肉的;肥胖的;肉质的;', 'He was well-built, but too fleshy to be impressive.', '他肌肉发达，但是太胖了，没给人留下好印象。');
INSERT INTO `words` VALUES (2635, 'flexibility', 'ˌfleksə\'bɪləti', 'n.', '柔韧性；灵活性；弹性；适应性;', '...brushes with long, flexible bristles.', '鬃毛长而柔韧的毛笔');
INSERT INTO `words` VALUES (2636, 'flexible', 'ˈfleksəbl', 'adj.', '能适应新情况的;灵活的;可变动的;', '...brushes with long, flexible bristles.', '鬃毛长而柔韧的毛笔');
INSERT INTO `words` VALUES (2637, 'flicker', 'ˈflɪkə(r)', 'v.', '闪烁;闪现;忽隐忽现;', 'Fluorescent lights flickered, and then the room was brilliantly, blindingly bright...', '荧光灯闪了几下，接着屋子里豁然大亮，刺得人睁不开眼。');
INSERT INTO `words` VALUES (2638, 'flight', 'flaɪt', 'n.', '(尤指乘飞机的)空中航行，航程;航班飞机;班机;', 'The flight will take four hours.', '这次航行要飞 4 个小时。');
INSERT INTO `words` VALUES (2639, 'fling', 'flɪŋ', 'v.', '(尤指生气地)扔，掷，抛，丢;猛动(身体或身体部位);粗暴地(向某人)说;', 'The woman flung the cup at him...', '那个女人使劲把杯子朝他扔去。');
INSERT INTO `words` VALUES (2640, 'flip', 'flɪp', 'v.', '(使)快速翻转，迅速翻动;按(开关);按(按钮);', 'He didn\'t flip on the headlights until he was two blocks away...', '他开出两个街区后才打开车头灯。');
INSERT INTO `words` VALUES (2641, 'flirt', 'flɜːt', 'v.', '调情;', 'Dad\'s flirting with all the ladies, or they\'re all flirting with him, as usual...', '和平常一样，爸爸在和所有的女士调情，或者说所有的女士都在和他调情。');
INSERT INTO `words` VALUES (2642, 'float', 'fləʊt', 'v.', '浮动;漂流;飘动;', 'They noticed fifty and twenty dollar bills floating in the water.', '他们发现水中漂浮着50和20美元的纸币。');
INSERT INTO `words` VALUES (2643, 'floating', 'ˈfləʊtɪŋ', 'adj.', '不固定的;流动的;浮动的;', 'They noticed fifty and twenty dollar bills floating in the water.', '他们发现水中漂浮着50和20美元的纸币。');
INSERT INTO `words` VALUES (2644, 'flock', 'flɒk', 'n.', '(羊或鸟)群;(尤指同类人的)一大群;(常跟随某圣职人员等学习的)信众;', 'They kept a small flock of sheep...', '他们养了一小群羊。');
INSERT INTO `words` VALUES (2645, 'flood', 'flʌd', 'n.', '洪水;水灾;大批，大量(的人或事物);', 'More than 70 people were killed in the floods, caused when a dam burst...', '大坝决口造成洪灾，70 多人因此丧生。');
INSERT INTO `words` VALUES (2646, 'flooding', 'ˈflʌdɪŋ', 'n.', '洪水泛滥;水灾;洪涝;', 'The flooding, caused by three days of torrential rain, is the worst in sixty-five years.', '连续 3 天的倾盆大雨引发的洪水是 65 年来最严重的一次。');
INSERT INTO `words` VALUES (2647, 'floor', 'flɔː(r)', 'n.', '地板;地面;(车厢内的)底板;', 'Jack\'s sitting on the floor watching TV...', '杰克正坐在地板上看电视。');
INSERT INTO `words` VALUES (2648, 'floppy', 'ˈflɒpi', 'adj.', '松散下垂的;耷拉的;松软的;', '...the girl with the floppy hat and glasses.', '戴着耷拉的帽子和眼镜的女孩');
INSERT INTO `words` VALUES (2649, 'flourish', 'ˈflʌrɪʃ', 'v.', '繁荣;昌盛;兴旺;', 'Business flourished and within six months they were earning 18,000 roubles a day...', '生意红火起来，不出6 个月他们就可以每天赚 18,000 卢布了。');
INSERT INTO `words` VALUES (2650, 'flow', 'fləʊ', 'n.', '流;流动;持续生产;', 'A stream flowed gently down into the valley...', '一条小溪潺潺流进山谷。');
INSERT INTO `words` VALUES (2651, 'flower', 'ˈflaʊə(r)', 'n.', '花;花朵;开花植物;', 'Each individual flower is tiny.', '每一朵花都很小。');
INSERT INTO `words` VALUES (2652, 'flu', 'fluː', 'n.', '流行性感冒;流感;', 'I got flu...', '我得了流感。');
INSERT INTO `words` VALUES (2653, 'fluctuate', 'ˈflʌktʃueɪt', 'v.', '(大小、数量、质量等)波动;(在…之间)起伏不定;', 'Body temperature can fluctuate if you are ill.', '人患病后体温可能会上下波动。');
INSERT INTO `words` VALUES (2654, 'fluctuation', 'ˌflʌktʃuˈeɪʃn', 'n.', '波动，涨落，起伏;', 'Body temperature can fluctuate if you are ill.', '人患病后体温可能会上下波动。');
INSERT INTO `words` VALUES (2655, 'fluency', 'ˈfluːənsi', 'n.', '(尤指外语)流利;熟练自如;流畅;', 'She studied eight foreign languages but is fluent in only six of them...', '她学习了8种外语，但是只能流利地说其中 6 种。');
INSERT INTO `words` VALUES (2656, 'fluent', 'ˈfluːənt', 'adj.', '(尤指外语)流利，文字流畅;流利的;通畅的;', 'She studied eight foreign languages but is fluent in only six of them...', '她学习了8种外语，但是只能流利地说其中 6 种。');
INSERT INTO `words` VALUES (2657, 'fluff', 'flʌf', 'n.', '(衣服等上的)绒毛，蓬松毛团，尘团;(禽兽，尤指幼者的)绒毛;没多大意义的娱乐;', '...the nestbox which contained two chicks: just small grey balls of fluff...', '窝里有两只雏鸟：看上去还只是两个灰色的毛毛球');
INSERT INTO `words` VALUES (2658, 'fluffy', 'ˈflʌfi', 'adj.', '绒毛般的;覆有绒毛的;松软的;', '...fluffy white towels...', '柔软的白毛巾');
INSERT INTO `words` VALUES (2659, 'fluid', 'ˈfluːɪd', 'n.', '液体;流体;液;', 'The blood vessels may leak fluid, which distorts vision...', '血管可能会渗漏，造成视力模糊。');
INSERT INTO `words` VALUES (2660, 'fluorescent', 'ˌflɔːˈresnt', 'adj.', '发荧光的;强烈反光的;发亮的;', '...a piece of fluorescent tape.', '一段荧光带');
INSERT INTO `words` VALUES (2661, 'flush', 'flʌʃ', 'v.', '发红;脸红;冲(抽水马桶);', 'Do you sweat a lot or flush a lot?...', '你常出汗或是脸上常发烫吗？');
INSERT INTO `words` VALUES (2662, 'flutter', 'ˈflʌtə(r)', 'v.', '(使)飘动，挥动，颤动;(鸟或昆虫)拍(翅)，振(翅)，鼓(翼);飞来飞去;', 'Her chiffon skirt was fluttering in the night breeze.', '她的雪纺裙在晚风中飘动着。');
INSERT INTO `words` VALUES (2663, 'flux', 'flʌks', 'n.', '不断的变动;不停的变化;通量;', 'Education remains in a state of flux which will take some time to settle down.', '教育还处于不断变化中，需要一段时间才能稳定下来。');
INSERT INTO `words` VALUES (2664, 'foam', 'fəʊm', 'n.', '泡沫橡胶;海绵橡胶;泡沫;', 'The water curved round the rocks in great bursts of foam.', '水流撞击岩石，产生大量泡沫。');
INSERT INTO `words` VALUES (2665, 'fodder', 'ˈfɒdə(r)', 'n.', '(马等家畜的)饲料，秣;(人或东西)只能是…的料;', '...fodder for horses...', '马饲料');
INSERT INTO `words` VALUES (2666, 'fog', 'fɒɡ', 'n.', '雾;迷惘;困惑;', 'The crash happened in thick fog...', '浓雾中发生了撞车。');
INSERT INTO `words` VALUES (2667, 'foggy', 'ˈfɒɡi', 'adj.', '有雾的;雾茫茫的;', 'It\'s quite foggy now...', '现在雾很大。');
INSERT INTO `words` VALUES (2668, 'foil', 'fɔɪl', 'n.', '(尤指包装食物等用的)箔;箔纸(覆有箔的纸);陪衬者;', 'Pour cider around the meat and cover with foil.', '在肉周围倒上苹果酒，然后以箔纸覆盖。');
INSERT INTO `words` VALUES (2669, 'fold', 'fəʊld', 'v.', '折叠，对折(纸、织物等);折小，叠平，可折小，可叠平(以便贮存或携带);包;', 'He folded the paper carefully...', '他把纸小心地折起来。');
INSERT INTO `words` VALUES (2670, 'folding', 'ˈfəʊldɪŋ', 'adj.', '折叠式的;可折叠的;', 'He folded the paper carefully...', '他把纸小心地折起来。');
INSERT INTO `words` VALUES (2671, 'foliage', 'ˈfəʊliɪdʒ', 'n.', '(植物的)叶;枝叶;', '...shrubs with grey or silver foliage.', '长着灰色或银色叶子的灌木丛');
INSERT INTO `words` VALUES (2672, 'folio', 'ˈfəʊliəʊ', 'n.', '(尤指早期印刷的)对开本;(书籍的)一页;', 'Richard told me of three 16th-century folio volumes on alchemy.', '理查德告诉过我 16 世纪的三卷关于炼金术的对开本书籍的事。');
INSERT INTO `words` VALUES (2673, 'follow', 'ˈfɒləʊ', 'v.', '跟随;跟着;(指时间或顺序)在…后发生，因…而发生;', 'We followed him up the steps into a large hall...', '我们跟着他上楼来到一个很大的大厅。');
INSERT INTO `words` VALUES (2674, 'follower', 'ˈfɒləʊə(r)', 'n.', '拥护者;追随者;信徒;', '...followers of the Zulu Inkatha movement.', '祖鲁因卡塔运动的支持者');
INSERT INTO `words` VALUES (2675, 'following', 'ˈfɒləʊɪŋ', 'adj.', '(时间上)接着的;下述的;下列的;', 'In the centuries following Christ\'s death, Christians genuinely believed the world was about to end...', '在耶稣死后的几个世纪里，基督徒们真的相信世界将要灭亡。');
INSERT INTO `words` VALUES (2676, 'follows', 'ˈfɒləʊz', 'v.', '跟随;跟着;(指时间或顺序)在…后发生，因…而发生;', 'We followed him up the steps into a large hall...', '我们跟着他上楼来到一个很大的大厅。');
INSERT INTO `words` VALUES (2677, 'fond', 'fɒnd', 'adj.', '喜爱(尤指认识已久的人);喜爱(尤指长期喜爱的事物);喜欢(做令人不快的事);', 'I am very fond of Michael...', '我很喜欢迈克尔。');
INSERT INTO `words` VALUES (2678, 'food', 'fuːd', 'n.', '食物;(某种)食物;', 'Enjoy your food.', '用餐愉快。');
INSERT INTO `words` VALUES (2679, 'foodstuff', 'ˈfuːdstʌf', 'n.', '食物;食品;', '...basic foodstuffs such as sugar, cooking oil and cheese.', '糖、食用油、干酪等基本食品');
INSERT INTO `words` VALUES (2680, 'fool', 'fuːl', 'n.', '蠢人;傻瓜;(旧时国王或王后豢养供人娱乐的)小丑，弄臣;', '\'You fool!\' she shouted...', '“你这个白痴!”她大叫道。');
INSERT INTO `words` VALUES (2681, 'foolish', 'ˈfuːlɪʃ', 'adj.', '愚蠢的;傻的;感到荒谬;', 'It would be foolish to raise hopes unnecessarily...', '无谓地燃起人们的希望是愚蠢的。');
INSERT INTO `words` VALUES (2682, 'foot', 'fʊt', 'n.', '(人或动物的)脚，足;有…脚(或足的);有…只脚(的);', 'She stamped her foot again.', '她又跺了跺脚。');
INSERT INTO `words` VALUES (2683, 'football', 'ˈfʊtbɔːl', 'n.', '足球运动;足球;橄榄球;', 'Several boys were still playing football on the waste ground.', '几个男孩还在那片废弃的空地上踢足球。');
INSERT INTO `words` VALUES (2684, 'footer', 'ˈfʊtə(r)', 'n.', '(计算机打印的每页的)页脚，页尾;', 'Page Mode shows headers, footers, footnotes and page numbers.', '页面模式可以显示页眉、页脚、脚注和页码。');
INSERT INTO `words` VALUES (2685, 'footing', 'ˈfʊtɪŋ', 'n.', '站稳;立足点;基础;', 'The new law will put official corruption on the same legal footing as treason.', '新法律将规定对公职腐败适用与叛国罪相同的量刑标准。');
INSERT INTO `words` VALUES (2686, 'footstep', 'ˈfʊtstep', 'n.', '脚步声;足迹;', 'I heard footsteps outside.', '我听见了外面的脚步声。');
INSERT INTO `words` VALUES (2687, 'footsteps', 'ˈfʊtsteps', 'n.', '脚步声;足迹;', 'I heard footsteps outside.', '我听见了外面的脚步声。');
INSERT INTO `words` VALUES (2688, 'for', 'fɔː(r) , fə(r)', 'prep.', '(表示对象、用途等)给，对，供;以帮助;为了;', 'Isn\'t that enough for you?...', '那对你来说不够吗？');
INSERT INTO `words` VALUES (2689, 'forbid', 'fəˈbɪd', 'v.', '禁止;不准;妨碍;', 'They\'ll forbid you to marry...', '他们不会准许你结婚。');
INSERT INTO `words` VALUES (2690, 'forbidden', 'fəˈbɪdn', 'adj.', '禁止的;不准的;', 'Smoking was forbidden everywhere...', '到处都禁止吸烟。');
INSERT INTO `words` VALUES (2691, 'force', 'fɔːs', 'n.', '武力;暴力;力;', 'He was charged with abducting a taxi driver and forcing him to drive a bomb to Downing Street...', '他被控劫持一名出租车司机并强迫他开车把一颗炸弹送到唐宁街。');
INSERT INTO `words` VALUES (2692, 'forced', 'fɔːst', 'adj.', '被迫的;不得已的;勉强的;', 'A system of forced labour was used on the cocoa plantations.', '可可种植园中曾实行强迫劳役制。');
INSERT INTO `words` VALUES (2693, 'fore', 'fɔː(r)', 'n.', '前面;前部;', 'A number of low-budget independent films brought new directors and actors to the fore.', '许多低成本独立制作的电影使新导演和新演员崭露头角。');
INSERT INTO `words` VALUES (2694, 'forefather', 'ˈfɔːfɑːðə(r)', 'n.', '祖先，祖宗(尤指男性);', 'They were determined to go back to the land of their forefathers.', '他们决意回到祖先的土地上。');
INSERT INTO `words` VALUES (2695, 'forefinger', 'ˈfɔːfɪŋɡə(r)', 'n.', '食指;', 'He took the pen between his thumb and forefinger.', '他用拇指和食指捏着笔。');
INSERT INTO `words` VALUES (2696, 'foregoing', 'ˈfɔːɡəʊɪŋ', 'adj.', '上述的;前述的;前面所提到的事物;', 'You might think from the foregoing that the French want to phase accents out. Not at all.', '根据前文所述，你们可能以为法国人打算废除各种口音。事实并非如此。');
INSERT INTO `words` VALUES (2697, 'foreground', 'ˈfɔːɡraʊnd', 'n.', '(景物、图画等的)前景;瞩目地位;重要位置;', 'He is the bowler-hatted figure in the foreground of Orpen\'s famous painting.', '他是奥彭那幅名画中位于前景的戴圆顶硬礼帽的那个人物。');
INSERT INTO `words` VALUES (2698, 'foreign', 'ˈfɒrən', 'adj.', '外国的;涉外的;外交的;', '...in Frankfurt, where a quarter of the population is foreign...', '在有四分之一的人口是外来移民的法兰克福');
INSERT INTO `words` VALUES (2699, 'foreigner', 'ˈfɒrənə(r)', 'n.', '外国人;外来人;外地人;', 'They are discouraged from becoming close friends with foreigners.', '他们被劝说不要和外国人密切交往。');
INSERT INTO `words` VALUES (2700, 'foreman', 'ˈfɔːmən', 'n.', '领班;工头;陪审团团长;', 'He still visited the dairy daily, but left most of the business details to his manager and foreman.', '他仍然每天到乳品场看看，但把生意上大部分琐碎事务交给经理和工头打理。');
INSERT INTO `words` VALUES (2701, 'foremost', 'ˈfɔːməʊst', 'adj.', '最重要的;最著名的;最前的;', 'He was one of the world\'s foremost scholars of ancient Indian culture...', '他是世界上最杰出的古印第安文化研究者之一。');
INSERT INTO `words` VALUES (2702, 'forerunner', 'ˈfɔːrʌnə(r)', 'n.', '先驱;先行者;预兆;', '...a machine which, in some respects, was the forerunner of the modern helicopter...', '在某些方面成为现代直升机前身的机器');
INSERT INTO `words` VALUES (2703, 'foresee', 'fɔːˈsiː', 'v.', '预料;预见;预知;', 'He did not foresee any problems...', '他没有预见到任何问题。');
INSERT INTO `words` VALUES (2704, 'forest', 'ˈfɒrɪst', 'n.', '森林;林区;(森林似的)一丛，一片;', 'Parts of the forest are still dense and inaccessible.', '森林的一些部分仍然很茂密，人迹罕至。');
INSERT INTO `words` VALUES (2705, 'foretell', 'fɔːˈtel', 'v.', '(尤指用魔力)预知，预言;', '...prophets who have foretold the end of the world.', '预言了世界末日的先知');
INSERT INTO `words` VALUES (2706, 'forfeit', 'ˈfɔːfɪt', 'v.', '(因犯错)丧失，被没收;', 'He was ordered to forfeit more than £1.5m in profits...', '下令没收了他 150 多万英镑的盈利。');
INSERT INTO `words` VALUES (2707, 'forge', 'fɔːdʒ', 'v.', '艰苦干成;努力加强;伪造;', 'The Prime Minister is determined to forge a good relationship with America\'s new leader...', '首相决意要和美国的新任领导人建立良好的关系。');
INSERT INTO `words` VALUES (2708, 'forgery', 'ˈfɔːdʒəri', 'n.', '伪造;伪造罪;伪造品;', 'He was found guilty of forgery.', '他被判伪造罪名成立。');
INSERT INTO `words` VALUES (2709, 'forget', 'fəˈɡet', 'v.', '忘记;遗忘;忘记做(或带、买等);', 'Sometimes I improvise and change the words because I forget them...', '有时，我临场发挥改了词，因为我把原来的词给忘了。');
INSERT INTO `words` VALUES (2710, 'forgetfulness', 'fəˈgɛtfʊlnəs', 'n.', '疏忽；忽略；健忘;', 'My mother has become very forgetful and confused recently.', '我妈妈近来变得非常健忘，糊里糊涂。');
INSERT INTO `words` VALUES (2711, 'forgive', 'fəˈɡɪv', 'v.', '原谅;宽恕;对不起;', 'Hopefully she\'ll understand and forgive you, if she really loves you...', '如果她真的爱你的话，希望她会理解并原谅你。');
INSERT INTO `words` VALUES (2712, 'fork', 'fɔːk', 'n.', '餐叉;叉(挖掘用的园艺工具);(道路、河流等的)分岔处，分流处，岔口，岔路;', '...knives and forks.', '刀叉');
INSERT INTO `words` VALUES (2713, 'form', 'fɔːm', 'n.', '类型;种类;形式;', 'He contracted a rare form of cancer...', '他得了一种罕见的癌症。');
INSERT INTO `words` VALUES (2714, 'formal', 'ˈfɔːml', 'adj.', '适合正式场合的;正规的;庄重的;', 'He wrote a very formal letter of apology to Douglas...', '他给道格拉斯写了一封非常正式的道歉信。');
INSERT INTO `words` VALUES (2715, 'formality', 'fɔːˈmæləti', 'n.', '正式手续;例行公事;遵守礼节;', 'With the Cold War almost over, the talks were a mere formality.', '随着冷战即将结束，对话就只是一种形式了。');
INSERT INTO `words` VALUES (2716, 'format', 'ˈfɔːmæt', 'n.', '总体安排;计划;设计;', 'I had met with him to explain the format of the programme and what we had in mind.', '我已经和他当面解释了我们节目的形式以及我们的想法。');
INSERT INTO `words` VALUES (2717, 'formation', 'fɔːˈmeɪʃn', 'n.', '组成;形成;组成物;', '...the formation of a new government.', '新政府的组建');
INSERT INTO `words` VALUES (2718, 'formatted', 'ˈfɔːmætɪd', 'v.', '格式化;安排…的版式;', 'I had met with him to explain the format of the programme and what we had in mind.', '我已经和他当面解释了我们节目的形式以及我们的想法。');
INSERT INTO `words` VALUES (2719, 'formatting', 'ˈfɔːmætɪŋ', 'v.', '格式化;安排…的版式;', 'I had met with him to explain the format of the programme and what we had in mind.', '我已经和他当面解释了我们节目的形式以及我们的想法。');
INSERT INTO `words` VALUES (2720, 'formed', 'fɔːmd', 'v.', '(使)出现，产生;(使)形成;(使)成形，组成;', 'He contracted a rare form of cancer...', '他得了一种罕见的癌症。');
INSERT INTO `words` VALUES (2721, 'former', 'ˈfɔːmə(r)', 'adj.', '以前的;昔日的;前;', 'The unemployed executives include former sales managers, directors and accountants.', '被解雇的管理人员包括前销售经理、主管和会计。');
INSERT INTO `words` VALUES (2722, 'formerly', 'ˈfɔːməli', 'adv.', '以前;从前;', 'He had formerly been in the Navy.', '他从前在海军服役。');
INSERT INTO `words` VALUES (2723, 'formidable', 'fəˈmɪdəbl', 'adj.', '可怕的;令人敬畏的;难对付的;', 'We have a formidable task ahead of us...', '我们面临着一项艰巨的任务。');
INSERT INTO `words` VALUES (2724, 'formula', 'ˈfɔːmjələ', 'n.', '公式;方程式;计算式;', 'It is difficult to imagine how the North and South could ever agree on a formula to unify the divided peninsula.', '很难想象南北双方在统一半岛的方案上究竟怎样才能达成一致。');
INSERT INTO `words` VALUES (2725, 'formulate', 'ˈfɔːmjuleɪt', 'v.', '制订;规划;构想;', 'Little by little, he formulated his plan for escape.', '他一点一点地设计构思出了逃跑的方案。');
INSERT INTO `words` VALUES (2726, 'formulation', 'ˌfɔːmjuˈleɪʃn', 'n.', '(药品、化妆品等的)配方，剂型;配方产品;(政策、计划等的)制定，构想;', 'There have been problems with the formulation of the vaccine...', '疫苗的配方有些问题。');
INSERT INTO `words` VALUES (2727, 'forsake', 'fəˈseɪk', 'v.', '抛弃，遗弃，离开(尤指不履行责任);摒弃，离开(尤指喜爱的事物);', 'I still love him and I would never forsake him.', '我仍然爱着他,我永远不会离他而去。');
INSERT INTO `words` VALUES (2728, 'forth', 'fɔːθ', 'adv.', '离去;外出;向前;', 'Go forth into the desert...', '向前走进沙漠。');
INSERT INTO `words` VALUES (2729, 'forthcoming', 'ˌfɔːθˈkʌmɪŋ', 'adj.', '即将发生(或出版等)的;现成;随要随有;', '...his opponents in the forthcoming elections.', '他在即将来临的选举中的对手们');
INSERT INTO `words` VALUES (2730, 'fortify', 'ˈfɔːtɪfaɪ', 'v.', '筑防御工事以防卫;(尤指)筑城防御;(在物质或精神上)加强，增强;', '...British soldiers working to fortify an airbase in Bahrain.', '在巴林一空军基地筑防的英国士兵');
INSERT INTO `words` VALUES (2731, 'fortitude', 'ˈfɔːtɪtjuːd', 'n.', '(在巨大痛苦或困难面前表现出的)勇气，胆量，刚毅;', 'He suffered a long series of illnesses with tremendous dignity and fortitude.', '他凭借超乎常人的保持生命尊严的信念和顽强意志力忍受着各种病痛的折磨。');
INSERT INTO `words` VALUES (2732, 'fortnight', 'ˈfɔːtnaɪt', 'n.', '两星期;', 'I hope to be back in a fortnight.', '我希望在两周内赶回来。');
INSERT INTO `words` VALUES (2733, 'fortress', 'ˈfɔːtrəs', 'n.', '城堡;堡垒;要塞;', '...a 13th-century fortress.', '13 世纪的堡垒');
INSERT INTO `words` VALUES (2734, 'fortuitous', 'fɔːˈtjuːɪtəs', 'adj.', '偶然发生的;(尤指)巧合的;', 'Their success is the result of a fortuitous combination of circumstances.', '他们的成功是各种环境因素偶然联系在一起的幸运产物。');
INSERT INTO `words` VALUES (2735, 'fortunate', 'ˈfɔːtʃənət', 'adj.', '幸运的;交好运的;吉利的;', 'He was extremely fortunate to survive...', '他能活下来真是万幸。');
INSERT INTO `words` VALUES (2736, 'fortunately', 'ˈfɔːtʃənətli', 'adv.', '幸运地;交好运地;吉利地;', 'Fortunately, the weather that winter was reasonably mild...', '幸好那个冬天还算暖和。');
INSERT INTO `words` VALUES (2737, 'fortune', 'ˈfɔːtʃuːn', 'n.', '(尤指影响人生的)机会，运气;大笔的钱;巨款;', 'We had to eat out all the time. It ended up costing a fortune...', '我们不得不总在外面吃饭，结果花了很多钱。');
INSERT INTO `words` VALUES (2738, 'forum', 'ˈfɔːrəm', 'n.', '公共讨论场所;论坛;讨论会;', 'Members of the council agreed that it still had an important role as a forum for discussion...', '理事会成员一致认为它仍然发挥着论坛的重要作用。');
INSERT INTO `words` VALUES (2739, 'forward', 'ˈfɔːwəd', 'adv.', '向前;进展;前进;', 'He came forward with his hand out. \'Mr and Mrs Selby?\' he enquired...', '他走上前去伸出手来。“是塞尔比先生和夫人吗？”他问道。');
INSERT INTO `words` VALUES (2740, 'foster', 'ˈfɒstə(r)', 'v.', '促进;助长;培养;', 'Little Jack was placed with foster parents...', '小杰克和养父母住在一起。');
INSERT INTO `words` VALUES (2741, 'foul', 'faʊl', 'adj.', '肮脏恶臭的;难闻的;很令人不快的;', '...foul polluted water...', '恶臭的脏水');
INSERT INTO `words` VALUES (2742, 'foundation', 'faʊnˈdeɪʃn', 'n.', '地基;房基;基础;', 'Best friends are the foundation of my life...', '好朋友是我生命的支柱。');
INSERT INTO `words` VALUES (2743, 'founder', 'ˈfaʊndə(r)', 'n.', '(组织、机构等的)创建者，创办者，发起人;', 'He was one of the founders of the university\'s medical faculty.', '他是该大学医学院的创建人之一。');
INSERT INTO `words` VALUES (2744, 'four', 'fɔː(r)', 'n.', '四;四个;第四四的;', 'Judith is married with four children.', '朱迪丝已经结婚了，有四个孩子。');
INSERT INTO `words` VALUES (2745, 'fourteen', 'ˌfɔːˈtiːn', 'num.', '十四;', 'I\'m fourteen years old.', '我14岁。');
INSERT INTO `words` VALUES (2746, 'fourth', 'fɔːθ', 'ord.', '第四;', 'Last year\'s winner Greg Lemond of the United States is in fourth place.', '去年的冠军、来自美国的格雷格·莱蒙德现在排名第 4。');
INSERT INTO `words` VALUES (2747, 'fowl', 'faʊl', 'n.', '家禽;鸟;', 'Carve the fowl into 8 pieces.', '把这只家禽切成 8 块。');
INSERT INTO `words` VALUES (2748, 'fraction', 'ˈfrækʃn', 'n.', '小部分;少量;一点儿;', 'She hesitated for a fraction of a second before responding...', '她在回答之前犹豫了一下。');
INSERT INTO `words` VALUES (2749, 'fractional', 'ˈfrækʃənl', 'adj.', '很小的;很少的;微不足道的;', '...a fractional hesitation.', '瞬间的犹豫');
INSERT INTO `words` VALUES (2750, 'fracture', 'ˈfræktʃə(r)', 'n.', '(指状态)骨折，断裂，折断，破裂;(指事实)骨折;', 'At least one-third of all women over ninety have sustained a hip fracture.', '超过90岁的所有女性中至少有1/3已经出现髋骨骨折。');
INSERT INTO `words` VALUES (2751, 'fragile', 'ˈfrædʒaɪl', 'adj.', '易碎的;易损的;不牢固的;', 'The fragile economies of several southern African nations could be irreparably damaged...', '一些南非国家的脆弱经济可能会被彻底破坏，无法挽回。');
INSERT INTO `words` VALUES (2752, 'fragility', 'frəˈdʒɪlɪti', 'n.', '脆弱；易碎性；虚弱；脆性;', 'The fragile economies of several southern African nations could be irreparably damaged...', '一些南非国家的脆弱经济可能会被彻底破坏，无法挽回。');
INSERT INTO `words` VALUES (2753, 'fragrance', 'ˈfreɪɡrəns', 'n.', '香气;香味;芳香;', '...a shrubby plant with a strong characteristic fragrance.', '散发出一种浓郁的独特香味的灌木');
INSERT INTO `words` VALUES (2754, 'fragrant', 'ˈfreɪɡrənt', 'adj.', '香的;芳香的;', '...fragrant oils and perfumes...', '芳香精油和香水');
INSERT INTO `words` VALUES (2755, 'frail', 'freɪl', 'adj.', '瘦弱的;弱的;易损的;', 'She lay in bed looking particularly frail.', '她躺在床上，看上去特别虚弱。');
INSERT INTO `words` VALUES (2756, 'frame', 'freɪm', 'n.', '(图画、门、玻璃等的)框架;(家具、建筑物、车辆等的)构架，支架，骨架;眼镜框;', 'Estelle kept a photograph of her mother in a silver frame on the kitchen mantelpiece.', '埃丝特尔把母亲的一张镶银框的照片挂在厨房壁炉台上。');
INSERT INTO `words` VALUES (2757, 'framework', 'ˈfreɪmwɜːk', 'n.', '(建筑物或物体的)构架，框架，结构;(作为判断、决定等基础的)信仰，观点，准则;(体系的)结构，机制;', '... within the framework of federal regulations.', '在联邦法规的体制内');
INSERT INTO `words` VALUES (2758, 'framing', 'ˈfreɪmɪŋ', 'v.', '给…做框;给…镶边;作伪证陷害;', 'Estelle kept a photograph of her mother in a silver frame on the kitchen mantelpiece.', '埃丝特尔把母亲的一张镶银框的照片挂在厨房壁炉台上。');
INSERT INTO `words` VALUES (2759, 'franchise', 'ˈfræntʃaɪz', 'n.', '(公司授予的)特许经销权;(国家授予的)特别经营权，特许;获特许权的商业(或服务)机构;', '...fast-food franchises...', '快餐特许经营权');
INSERT INTO `words` VALUES (2760, 'frank', 'fræŋk', 'adj.', '坦率的;直率的;', '\'It is clear that my client has been less than frank with me,\' said his lawyer...', '“很明显我的委托人对我不够坦白，”他的律师说。');
INSERT INTO `words` VALUES (2761, 'franking', 'ˈfræŋkɪŋ', 'v.', '(在信件上)盖邮资已付印记，盖免付邮资印记;', '\'It is clear that my client has been less than frank with me,\' said his lawyer...', '“很明显我的委托人对我不够坦白，”他的律师说。');
INSERT INTO `words` VALUES (2762, 'frankly', 'ˈfræŋkli', 'adv.', '坦率地;直率地;(表示直言)老实说;', '\'You don\'t give a damn about my feelings, do you.\' —', '');
INSERT INTO `words` VALUES (2763, 'frantic', 'ˈfræntɪk', 'adj.', '紧张忙乱的;手忙脚乱的;(由于恐惧或担心)无法控制感情的，发狂似的;', 'A bird had been locked in and was by now quite frantic.', '一只鸟被关进了笼子，到现在它都十分狂躁。');
INSERT INTO `words` VALUES (2764, 'fraud', 'frɔːd', 'n.', '欺诈罪;欺骗罪;骗子;', 'He was jailed for two years for fraud and deception...', '他因为诈骗和欺诈入狱服刑两年。');
INSERT INTO `words` VALUES (2765, 'fraught', 'frɔːt', 'adj.', '充满(不愉快事物)的;焦虑的;忧虑的;', 'The earliest operations employing this technique were fraught with dangers.', '最早使用这种技术的手术充满了危险。');
INSERT INTO `words` VALUES (2766, 'freak', 'friːk', 'n.', '狂热爱好者;(行为、外表、想法)怪异的人;怪人;', 'Weir broke his leg in a freak accident playing golf...', '韦尔因为一次打高尔夫时发生的离奇事故折断了腿。');
INSERT INTO `words` VALUES (2767, 'free', 'friː', 'adj.', '能随自己意愿的;随心所欲的;不受限制的;', 'The seminars are free, with lunch provided.', '研讨会不收会务费，而且还提供午餐。');
INSERT INTO `words` VALUES (2768, 'freedom', 'ˈfriːdəm', 'n.', '(指权利)自由;(指状态)自由;自由民地位(不是囚犯或奴隶);', '...freedom of speech...', '言论自由');
INSERT INTO `words` VALUES (2769, 'freely', 'ˈfriːli', 'adv.', '不受限制地;无拘无束地;自由地;', 'We have referred freely to his ideas...', '我们频繁地提到了他的观点。');
INSERT INTO `words` VALUES (2770, 'freeze', 'friːz', 'v.', '(使)冻结，结冰;(使)冻住，冻堵;冰冻;', 'If the temperature drops below 0°C, water freezes...', '温度降到零摄氏度以下水就会结冰。');
INSERT INTO `words` VALUES (2771, 'freight', 'freɪt', 'n.', '(海运、空运或陆运的)货物;货运;', 'France derives 16% of revenue from air freight.', '法国 16%的岁入来自航空货运。');
INSERT INTO `words` VALUES (2772, 'french', 'frentʃ', 'adj.', '法国的;法国人的;法语的;', '...the French parliament...', '法国议会');
INSERT INTO `words` VALUES (2773, 'frequency', 'ˈfriːkwənsi', 'n.', '发生率;出现率;重复率;', 'The frequency of Kara\'s phone calls increased rapidly...', '卡拉的电话飞速增多。');
INSERT INTO `words` VALUES (2774, 'fresh', 'freʃ', 'adj.', '新鲜的;新产的;刚摘的;', 'He asked Strathclyde police, which carried out the original investigation, to make fresh inquiries...', '他要求原来负责案子的斯特拉斯克莱德警方展开新一轮的调查。');
INSERT INTO `words` VALUES (2775, 'freshen', 'ˈfreʃn', 'v.', '使洁净(或凉爽、新鲜、宜人);添加液体于(饮料，尤指酒)之中;增强变冷;', 'The wind had freshened.', '风增强变冷了。');
INSERT INTO `words` VALUES (2776, 'fret', 'fret', 'v.', '苦恼;烦躁;焦虑不安;', 'I was working all hours and constantly fretting about everyone else\'s problems...', '我一刻不闲地工作，总是为别人的事操心。');
INSERT INTO `words` VALUES (2777, 'friction', 'ˈfrɪkʃn', 'n.', '摩擦;摩擦力;争执;', 'Sara sensed that there had been friction between her children...', '萨拉感觉到她的孩子之间有过争执。');
INSERT INTO `words` VALUES (2778, 'friday', 'ˈfraɪdeɪ', 'n.', '星期五;', 'Mr Cook is intending to go to the Middle East on Friday.', '库克先生打算周五去中东。');
INSERT INTO `words` VALUES (2779, 'friend', 'frend', 'n.', '朋友;友人;赞助者;', 'I had a long talk about this with my best friend...', '我和我最好的朋友就此事进行了长谈。');
INSERT INTO `words` VALUES (2780, 'friendly', 'ˈfrendli', 'adj.', '友爱的;友好的;善意的;', 'Godfrey had been friendly to me.', '戈弗雷一度对我很友善。');
INSERT INTO `words` VALUES (2781, 'friends', 'frɛndz', 'n.', '朋友;友人;赞助者;', 'I had a long talk about this with my best friend...', '我和我最好的朋友就此事进行了长谈。');
INSERT INTO `words` VALUES (2782, 'friendship', 'ˈfrendʃɪp', 'n.', '友谊;朋友关系;友情;', 'Giving advice when it\'s not called for is the quickest way to end a good friendship...', '在别人不需要的时候提供建议是葬送好友关系的最快方式。');
INSERT INTO `words` VALUES (2783, 'fright', 'fraɪt', 'n.', '惊吓;恐怖;使人惊吓的经历;', 'The steam pipes rattled suddenly, and Franklin uttered a shriek and jumped with fright...', '蒸汽管突然发出咯咯的响声，富兰克林尖叫一声，吓得跳了起来。');
INSERT INTO `words` VALUES (2784, 'frighten', 'ˈfraɪtn', 'v.', '使惊吓;使惊恐;', 'He knew that Soli was trying to frighten him, so he smiled to hide his fear...', '他知道索利是想吓唬他，所以他挤出笑容掩饰自己的恐惧。');
INSERT INTO `words` VALUES (2785, 'frightening', 'ˈfraɪtnɪŋ', 'adj.', '引起恐惧的;使惊恐的;骇人的;', 'It was a very frightening experience and they were very courageous...', '那段经历太恐怖了，他们表现得非常勇敢。');
INSERT INTO `words` VALUES (2786, 'frightful', 'ˈfraɪtfl', 'adj.', '极坏的;很糟的;十分严重的;', 'My father was unable to talk about the war, it was so frightful.', '我父亲没法谈论那场战争，那太可怕了。');
INSERT INTO `words` VALUES (2787, 'frivolous', 'ˈfrɪvələs', 'adj.', '愚蠢的;可笑的;无聊的;', 'I just decided I was a bit too frivolous to be a doctor...', '我刚刚决定说自己有点过于轻率，不适合做医生。');
INSERT INTO `words` VALUES (2788, 'from', 'frəm', 'prep.', '(表示起始点)从…起，始于;(表示开始的时间)从…开始;(表示由某人发出或给出)寄自，得自;', 'He appealed for information from anyone who saw the attackers.', '他呼吁任何看见袭击者的人提供信息。');
INSERT INTO `words` VALUES (2789, 'front', 'frʌnt', 'n.', '正面;前面;正前方;', 'One man sat in an armchair, and the other sat on the front of the desk...', '一个男人坐在扶手椅里，另一个坐在桌子前面。');
INSERT INTO `words` VALUES (2790, 'frontier', 'ˈfrʌntɪə(r)', 'n.', '国界;边界;边境;', 'It wasn\'t difficult then to cross the frontier.', '那时穿越边境并不困难。');
INSERT INTO `words` VALUES (2791, 'frost', 'frɒst', 'n.', '严寒天气;霜冻;冰点以下的温度;', 'There is frost on the ground and snow is forecast...', '地上结霜了，而且预报说会有雪。');
INSERT INTO `words` VALUES (2792, 'frosty', 'ˈfrɒsti', 'adj.', '严寒的;霜冻的;结霜的;', '...sharp, frosty nights.', '寒冷刺骨的夜晚');
INSERT INTO `words` VALUES (2793, 'frown', 'fraʊn', 'v.', '皱眉;蹙额;', 'Nancy shook her head, frowning...', '南希摇了摇头，皱着眉头。');
INSERT INTO `words` VALUES (2794, 'frugal', 'ˈfruːɡl', 'adj.', '(对金钱、食物等)节约的，节俭的;简单廉价的;', 'She lives a frugal life.', '她生活俭朴。');
INSERT INTO `words` VALUES (2795, 'fruit', 'fruːt', 'n.', '水果;果实;(大地的)产物;', 'Fresh fruit and vegetables provide fibre and vitamins.', '新鲜水果和蔬菜提供植物纤维素和维生素。');
INSERT INTO `words` VALUES (2796, 'fruitful', 'ˈfruːtfl', 'adj.', '成果丰硕的;富有成效的;富饶的;', 'We had a long, happy, fruitful relationship...', '我们有过一段长久、愉快、双方都获益良多的关系。');
INSERT INTO `words` VALUES (2797, 'fruition', 'fruˈɪʃn', 'n.', '(计划、过程或活动的)完成，实现，取得成果;', 'These plans take time to come to fruition...', '这些方案需要时间才能取得成果。');
INSERT INTO `words` VALUES (2798, 'frustrate', 'frʌˈstreɪt', 'v.', '使懊丧;使懊恼;使沮丧;', 'These questions frustrated me...', '这些问题让我沮丧。');
INSERT INTO `words` VALUES (2799, 'frustration', 'frʌˈstreɪʃn', 'n.', '懊丧;懊恼;沮丧;', 'These questions frustrated me...', '这些问题让我沮丧。');
INSERT INTO `words` VALUES (2800, 'fry', 'fraɪ', 'v.', '油炸;油煎;油炒;', 'Fry the breadcrumbs until golden brown.', '把面包屑炸成金褐色为止。');
INSERT INTO `words` VALUES (2801, 'fulfil', 'fʊlˈfɪl', 'v.', '实现;履行;执行;', 'President Kaunda fulfilled his promise of announcing a date for the referendum.', '卡翁达总统兑现了他的承诺，宣布了全民公决的日期。');
INSERT INTO `words` VALUES (2802, 'full', 'fʊl', 'adj.', '满的;充满的;满是…的;', 'Once the container is full, it stays shut until you turn it clockwise.', '容器一旦装满它会保持关闭，想要打开须按顺时针方向转动。');
INSERT INTO `words` VALUES (2803, 'fully', 'ˈfʊli', 'adv.', '完全地;全部地;充分地;', 'She was fully aware of my thoughts...', '她完全清楚我的想法。');
INSERT INTO `words` VALUES (2804, 'fumble', 'ˈfʌmbl', 'v.', '笨手笨脚地做(某事);胡乱摸找(某物);笨嘴拙舌地说话;', 'She crept from the bed and fumbled for her dressing gown...', '她从床上蹑手蹑脚地爬起来，摸索着自己的晨衣。');
INSERT INTO `words` VALUES (2805, 'fume', 'fjuːm', 'v.', '(对…)大为生气，十分恼火;冒烟;冒气;', '...car exhaust fumes...', '汽车尾气');
INSERT INTO `words` VALUES (2806, 'fun', 'fʌn', 'n.', '享乐;乐趣;快乐;', 'This year promises to be terrifically good fun...', '今年一定会非常有意思。');
INSERT INTO `words` VALUES (2807, 'function', 'ˈfʌŋkʃn', 'n.', '作用;功能;职能;', 'The main function of the merchant banks is to raise capital for industry.', '商业银行的主要职能是为产业融资。');
INSERT INTO `words` VALUES (2808, 'functional', 'ˈfʌŋkʃənl', 'adj.', '实用的;作用的;功能的;', '...modern, functional furniture...', '实用的现代家具');
INSERT INTO `words` VALUES (2809, 'functionally', 'ˈfʌŋkʃənli', 'adv.', '功能地；职务上地；职务上地；用（写成）函数式;', '...modern, functional furniture...', '实用的现代家具');
INSERT INTO `words` VALUES (2810, 'fund', 'fʌnd', 'n.', '基金;专款;资金;', 'The concert will raise funds for research into Aids.', '这场音乐会将为艾滋病研究筹款。');
INSERT INTO `words` VALUES (2811, 'fundamental', 'ˌfʌndəˈmentl', 'adj.', '十分重大的;根本的;基础的;', 'Our constitution embodies all the fundamental principles of democracy...', '我们的宪法体现了民主的所有基本原则。');
INSERT INTO `words` VALUES (2812, 'funds', 'fʌndz', 'n.', '基金;专款;资金;', 'The concert will raise funds for research into Aids.', '这场音乐会将为艾滋病研究筹款。');
INSERT INTO `words` VALUES (2813, 'funeral', 'ˈfjuːnərəl', 'n.', '葬礼;丧礼;出殡;', 'His funeral will be on Thursday at Blackburn Cathedral...', '他的葬礼将于星期四在布莱克本大教堂举行。');
INSERT INTO `words` VALUES (2814, 'funny', 'ˈfʌni', 'adj.', '滑稽的;好笑的;奇怪的;', 'Wade was smart and not bad-looking, and he could be funny when he wanted to...', '韦德脑子聪明，相貌也不难看，而且如果他愿意的话还很风趣。');
INSERT INTO `words` VALUES (2815, 'fur', 'fɜː(r)', 'n.', '(动物浓厚的)软毛;(动物的)毛皮;人造毛皮;', 'This creature\'s fur is short, dense and silky.', '这种动物的毛又短又密、丝般柔滑。');
INSERT INTO `words` VALUES (2816, 'furious', 'ˈfjʊəriəs', 'adj.', '狂怒的;暴怒的;激烈的;', 'He is furious at the way his wife has been treated...', '妻子受到的待遇让他感到异常愤怒。');
INSERT INTO `words` VALUES (2817, 'furnish', 'ˈfɜːnɪʃ', 'v.', '布置家具;向(某人 / 某事物)供应，提供;', 'Many proprietors try to furnish their hotels with antiques.', '许多经营者都想用古董装饰他们的酒店。');
INSERT INTO `words` VALUES (2818, 'furniture', 'ˈfɜːnɪtʃə(r)', 'n.', '(可移动的)家具;', 'Each piece of furniture in their home suited the style of the house.', '他们家里的每件家具都和房子的风格相得益彰。');
INSERT INTO `words` VALUES (2819, 'further', 'ˈfɜːðə(r)', 'adv.', '(空间距离)较远，更远;(过去或未来)较远，更久远;进一步;', 'Inflation is below 5% and set to fall further...', '通货膨胀率在 5%以下，而且还将进一步下降。');
INSERT INTO `words` VALUES (2820, 'furthermore', 'ˌfɜːðəˈmɔː(r)', 'adv.', '此外;而且;再者;', 'Furthermore, they claim that any such interference is completely ineffective.', '此外，他们还声称任何这样的干预都是完全无效的。');
INSERT INTO `words` VALUES (2821, 'furthest', 'ˈfɜːðɪst', 'adv.', '在最大程度上;在最大范围内;最大限度地;', 'The south of England, where prices have fallen furthest, will remain the weakest market...', '价格下跌得最厉害的英格兰南部仍将是最疲软的市场。');
INSERT INTO `words` VALUES (2822, 'fury', 'ˈfjʊəri', 'n.', '狂怒;暴怒;狂暴;', 'She screamed, her face distorted with fury and pain.', '她尖叫着，脸部因狂怒和痛苦而扭曲。');
INSERT INTO `words` VALUES (2823, 'fuse', 'fjuːz', 'n.', '保险丝;熔断器;导火线;', 'The fuse blew as he pressed the button to start the motor...', '他按下按钮启动马达的时候保险丝烧断了。');
INSERT INTO `words` VALUES (2824, 'fusing', 'ˈfjuːzɪŋ', 'v.', '(使)融合，熔接，结合;(使)熔化;(使保险丝熔断而)停止工作;', 'The fuse blew as he pressed the button to start the motor...', '他按下按钮启动马达的时候保险丝烧断了。');
INSERT INTO `words` VALUES (2825, 'fusion', 'ˈfjuːʒn', 'n.', '融合;熔接;结合;', 'His previous fusions of jazz, pop and African melodies have proved highly successful.', '事实证明他先前融合了爵士乐、流行乐和非洲旋律的作品非常成功。');
INSERT INTO `words` VALUES (2826, 'fuss', 'fʌs', 'n.', '无谓的激动(或忧虑、活动);大惊小怪;(为小事)大吵大闹，大发牢骚;', 'I don\'t know what all the fuss is about...', '我不知道为什么要这样大惊小怪的。');
INSERT INTO `words` VALUES (2827, 'fussy', 'ˈfʌsi', 'adj.', '无谓忧虑(或担心)的;大惊小怪的;挑剔的;', 'She is not fussy about her food...', '她不挑食。');
INSERT INTO `words` VALUES (2828, 'futile', 'ˈfjuːtaɪl', 'adj.', '徒然的;徒劳的;无效的;', 'He brought his arm up in a futile attempt to ward off the blow...', '他扬起胳膊徒劳地想要挡住这一拳。');
INSERT INTO `words` VALUES (2829, 'future', 'ˈfjuːtʃə(r)', 'n.', '将来;未来;未来的事;', 'The spokesman said no decision on the proposal was likely in the immediate future...', '发言人说近期不会就这项提案作出决议。');
INSERT INTO `words` VALUES (2830, 'futures', 'ˈfjuːtʃəz', 'n.', '将来;未来;未来的事;', 'The spokesman said no decision on the proposal was likely in the immediate future...', '发言人说近期不会就这项提案作出决议。');
INSERT INTO `words` VALUES (2831, 'gaily', 'ˈɡeɪli', 'adv.', '花哨地;艳丽地;华丽地;', 'Magda laughed gaily.', '玛格达开心地笑了起来。');
INSERT INTO `words` VALUES (2832, 'gain', 'ɡeɪn', 'v.', '获得;赢得;博得;', 'Students can gain valuable experience by working on the campus radio or magazine...', '学生们在校广播台或校刊工作能够获得宝贵的经验。');
INSERT INTO `words` VALUES (2833, 'gainful', 'ˈɡeɪnfl', 'adj.', '有收益的;有报酬的;有利可图的;', '...opportunities for gainful employment.', '有酬工作的机会');
INSERT INTO `words` VALUES (2834, 'gainsay', 'ˌɡeɪnˈseɪ', 'v.', '反驳;反对;否认;', 'However much people have criticised her style and some of her policies no one will gainsay her courage...', '无论人们如何强烈地批评她的处事风格和某些政策，她的勇气却无人能够否认。');
INSERT INTO `words` VALUES (2835, 'gait', 'ɡeɪt', 'n.', '步态;步法;', '...a tubby little man in his fifties, with sparse hair and a rolling gait...', '一位50多岁的矮胖男子，头发稀疏，走路晃晃悠悠');
INSERT INTO `words` VALUES (2836, 'gal', 'ɡæl', 'n.', '女孩;姑娘;女子;', 'Diesel cost 60p/gal in some places.', '在有些地方，柴油卖到每加仑60便士。');
INSERT INTO `words` VALUES (2837, 'gala', 'ˈɡɑːlə', 'n.', '庆典;盛会;演出;', '...a gala evening at the Royal Opera House.', '在皇家歌剧院举行的盛大晚会');
INSERT INTO `words` VALUES (2838, 'galaxy', 'ˈɡæləksi', 'n.', '星系;银河;银河系;', 'Astronomers have discovered a distant galaxy.', '天文学家们发现了一个遥远的星系。');
INSERT INTO `words` VALUES (2839, 'gale', 'ɡeɪl', 'n.', '大风;飓风;一阵(阵)大笑声;', '...forecasts of fierce gales over the next few days.', '预报未来几天有超强大风');
INSERT INTO `words` VALUES (2840, 'gall', 'ɡɔːl', 'n.', '鲁莽;厚颜无耻;怨恨;', 'I daresay he thought he was above the law. I can\'t get over the gall of the fellow...', '我想他以为他能凌驾于法律之上，这家伙的蛮横无理真让人受不了。');
INSERT INTO `words` VALUES (2841, 'gallantry', 'ˈɡæləntri', 'n.', '(尤指在战场上)勇敢，英勇顽强;(男子对女子的)殷勤;', 'For his gallantry he was awarded a Victoria Cross.', '他因为表现英勇而荣获维多利亚十字勋章。');
INSERT INTO `words` VALUES (2842, 'galleries', 'ˈgæləriz', 'n.', '(艺术作品的)陈列室，展览馆;画廊;私家画店;', '...an art gallery.', '美术馆');
INSERT INTO `words` VALUES (2843, 'gallery', 'ˈɡæləri', 'n.', '(艺术作品的)陈列室，展览馆;画廊;私家画店;', '...an art gallery.', '美术馆');
INSERT INTO `words` VALUES (2844, 'gallivant', 'ˈɡælɪvænt', 'v.', '游玩;游览;闲游;', 'A girl\'s place is in the home, not gallivanting around and filling her head with nonsense.', '女孩子就该在家呆着，不能四处闲逛，成天胡思乱想。');
INSERT INTO `words` VALUES (2845, 'gallon', 'ˈɡælən', 'n.', '加仑(液量单位，在英国、加拿大及其他一些国家约等于4.5升，在美国约等于3.8升，一加仑为四夸脱);', '...80 million gallons of water a day.', '每天8千万加仑水');
INSERT INTO `words` VALUES (2846, 'gallop', 'ˈɡæləp', 'v.', '(马等)飞奔，奔驰，疾驰;骑马奔驰;使(马)飞奔;', 'The horses galloped away...', '群马飞奔而去。');
INSERT INTO `words` VALUES (2847, 'galore', 'ɡəˈlɔː(r)', 'adj.', '大量;很多;', 'You\'ll be able to win prizes galore.', '你将会赢得很多奖品。');
INSERT INTO `words` VALUES (2848, 'gambit', 'ˈɡæmbɪt', 'n.', '开头一招;开局;开场白;', 'He sees the proposal as more of a diplomatic gambit than a serious defense proposal...', '在他看来，与其说这是一项精心筹划的国防动议，倒不如说是外交上的一着妙棋。');
INSERT INTO `words` VALUES (2849, 'gamble', 'ˈɡæmbl', 'v.', '(牌戏、赛马等中)赌博，打赌;冒风险;碰运气;', 'Yesterday, he named his cabinet and took a big gamble in the process.', '昨天他冒了很大的风险任命了内阁成员。');
INSERT INTO `words` VALUES (2850, 'gambling', 'ˈɡæmblɪŋ', 'n.', '赌博;打赌;赌钱;', 'Gambling is a form of entertainment.', '赌博是一种娱乐。');
INSERT INTO `words` VALUES (2851, 'game', 'ɡeɪm', 'n.', '(有规则的)游戏，运动，比赛项目;(一项)游戏，比赛;比赛(或游戏)时用的手法;', '...the wonderful game of football.', '精彩的足球比赛');
INSERT INTO `words` VALUES (2852, 'games', 'ɡeɪmz', 'n.', '(有规则的)游戏，运动，比赛项目;(一项)游戏，比赛;比赛(或游戏)时用的手法;', '...the wonderful game of football.', '精彩的足球比赛');
INSERT INTO `words` VALUES (2853, 'gamut', '\'ɡæmət', 'n.', '全部;全范围;', 'Varied though the anthology may claim to be, it does not cover the whole gamut of Scottish poetry...', '该诗集虽然可能号称收录广泛，但并未涵盖所有苏格兰诗歌。');
INSERT INTO `words` VALUES (2854, 'gang', 'ɡæŋ', 'n.', '一帮，一伙(罪犯);一群，一伙(闹事、斗殴的年轻人);一伙(经常聚在一起的朋友);', 'During the fight with a rival gang he lashed out with his flick knife...', '在与敌对帮派的打斗中，他挥舞着弹簧折刀一通猛刺。');
INSERT INTO `words` VALUES (2855, 'gangling', 'ˈɡæŋɡlɪŋ', 'adj.', '高瘦而笨拙的;', 'His gangling, awkward gait has earned him the name Spiderman.', '他又瘦又高，动作笨拙难看，因此有了“蜘蛛人”的外号。');
INSERT INTO `words` VALUES (2856, 'gap', 'ɡæp', 'n.', '开口;豁口;缺口;', 'He pulled the thick curtains together, leaving just a narrow gap.', '他拉上厚厚的窗帘，仅留下一条狭窄的缝隙。');
INSERT INTO `words` VALUES (2857, 'gape', 'ɡeɪp', 'v.', '张口结舌地看;目瞪口呆地凝视;张开;', 'His secretary stopped taking notes to gape at me...', '他的秘书停止了记录，目瞪口呆地望着我。');
INSERT INTO `words` VALUES (2858, 'garbage', 'ˈɡɑːbɪdʒ', 'n.', '(生活)垃圾;废物;垃圾场;', '...a garbage bag.', '垃圾袋');
INSERT INTO `words` VALUES (2859, 'garden', 'ˈɡɑːdn', 'n.', '(住宅旁或周围的)园圃，花园，果园，菜园;庭园;园子;', '...the most beautiful garden on Earth.', '世界上最漂亮的花园');
INSERT INTO `words` VALUES (2860, 'gardening', 'ˈɡɑːdnɪŋ', 'v.', '做园艺工作;种植花木;', '...the most beautiful garden on Earth.', '世界上最漂亮的花园');
INSERT INTO `words` VALUES (2861, 'garlic', 'ˈɡɑːlɪk', 'n.', '蒜;大蒜;', '...a clove of garlic.', '一瓣蒜');
INSERT INTO `words` VALUES (2862, 'garment', 'ˈɡɑːmənt', 'n.', '(一件)衣服;', 'Many of the garments have the customers\' name tags sewn into the linings.', '这些衣服有很多内衬上缝有顾客的姓名签。');
INSERT INTO `words` VALUES (2863, 'garrison', 'ˈɡærɪsn', 'n.', '卫戍部队;守备部队;卫戍区;', '...a five-hundred man French army garrison.', '一支500人的法国陆军卫戍部队');
INSERT INTO `words` VALUES (2864, 'gaseous', 'ˈɡæsiəs', 'adj.', '似气体的;含气体的;', 'Freon exists both in liquid and gaseous states.', '氟利昂有液态和气态两种形态。');
INSERT INTO `words` VALUES (2865, 'gasp', 'ɡɑːsp', 'v.', '(尤指由于惊讶或疼痛而)喘气，喘息，倒抽气;透不过气;气喘吁吁地说;', 'An audible gasp went round the court as the jury announced the verdict...', '陪审团宣布判决的时候，听到法庭上有人倒抽了一口气。');
INSERT INTO `words` VALUES (2866, 'gate', 'ɡeɪt', 'n.', '大门;栅栏门;围墙门;', 'He opened the gate and started walking up to the house.', '他打开大门，迈步朝房子走去。');
INSERT INTO `words` VALUES (2867, 'gateway', 'ˈɡeɪtweɪ', 'n.', '大门口;门道;出入口;', 'He walked across the park and through a gateway.', '他穿过公园，走过一道大门。');
INSERT INTO `words` VALUES (2868, 'gather', 'ˈɡæðə(r)', 'v.', '聚集;集合;召集;', 'In the evenings, we gathered around the fireplace and talked...', '晚上，我们聚在火炉边聊天。');
INSERT INTO `words` VALUES (2869, 'gathering', 'ˈɡæðərɪŋ', 'n.', '聚集;聚会;集会;', '...the twenty-second annual gathering of the South Pacific Forum.', '第22届南太平洋论坛年会');
INSERT INTO `words` VALUES (2870, 'gaudy', 'ˈɡɔːdi', 'adj.', '俗艳的;花哨的;', '...her gaudy orange-and-purple floral hat.', '她那顶饰有花卉的橙紫相间的花哨帽子');
INSERT INTO `words` VALUES (2871, 'gauge', 'ɡeɪdʒ', 'n.', '测量仪器(或仪表);计量器;宽度;', 'He gauged the wind at over thirty knots...', '他测出风速在30节以上。');
INSERT INTO `words` VALUES (2872, 'gaunt', 'ɡɔːnt', 'adj.', '(常因疾病、饥饿或忧虑而)瘦削憔悴的;寒碜的;破败的;', 'Looking gaunt and tired, he denied there was anything to worry about.', '他看起来消瘦而疲惫，却说没什么可担心的。');
INSERT INTO `words` VALUES (2873, 'gay', 'ɡeɪ', 'adj.', '同性恋的;与同性恋者有关的;愉快的;', 'The quality of life for gay men has improved over the last two decades.', '同性恋者的生活质量在过去20年里有了改善。');
INSERT INTO `words` VALUES (2874, 'gaze', 'ɡeɪz', 'v.', '凝视;注视;盯着;', 'She stood gazing at herself in the mirror...', '她站在那儿，凝视着镜中的自己。');
INSERT INTO `words` VALUES (2875, 'gear', 'ɡɪə(r)', 'n.', '排挡;齿轮;传动装置;', 'On hills, he must use low gears...', '在山上,他必须挂低速挡。');
INSERT INTO `words` VALUES (2876, 'gem', 'dʒem', 'n.', '(经切割打磨的)宝石;难能可贵的人;风景优美的地方;', '...a gold mask inset with emeralds and other gems.', '一张镶有翡翠和其他宝石的金面具');
INSERT INTO `words` VALUES (2877, 'gender', 'ˈdʒendə(r)', 'n.', '性别;性(阳性、阴性和中性，不同的性有不同的词尾等);', 'Women are sometimes denied opportunities solely because of their gender.', '妇女有时仅仅因为性别而无法获得种种机会。');
INSERT INTO `words` VALUES (2878, 'general', 'ˈdʒenrəl', 'adj.', '全体的;普遍的;总的;', 'The General\'s visit to Sarajevo is part of preparations for the deployment of extra troops.', '将军造访萨拉热窝是在为增加兵力部署做准备。');
INSERT INTO `words` VALUES (2879, 'generally', 'ˈdʒenrəli', 'adv.', '普遍地;广泛地;一般地;', 'University teachers generally have admitted a lack of enthusiasm about their subjects...', '高校教师大体上承认对其所教专业缺乏热情。');
INSERT INTO `words` VALUES (2880, 'generate', 'ˈdʒenəreɪt', 'v.', '产生;引起;', 'The Employment Minister said the reforms would generate new jobs.', '劳工大臣表示，改革将带来新的就业机会。');
INSERT INTO `words` VALUES (2881, 'generation', 'ˌdʒenəˈreɪʃn', 'n.', '(统称)一代人，同代人，同辈人;代，一代，一辈(通常认为大约30年);(家史中的)一代，一辈;', '...the younger generation of Party members...', '年轻一代的党员');
INSERT INTO `words` VALUES (2882, 'generic', 'dʒəˈnerɪk', 'adj.', '一般的;普通的;通用的;', 'Parmesan is a generic term used to describe a family of hard Italian cheeses.', '帕尔玛干酪是意大利硬奶酪的通称。');
INSERT INTO `words` VALUES (2883, 'generosity', 'ˌdʒenəˈrɒsəti', 'n.', '慷慨;大方;宽宏大量;', 'There are stories about his generosity, the massive amounts of money he gave to charities.', '有传闻说他向慈善机构慷慨解囊，捐献巨款。');
INSERT INTO `words` VALUES (2884, 'generous', 'ˈdʒenərəs', 'adj.', '慷慨的;大方的;慷慨给予的;', 'German banks are more generous in their lending...', '德国银行的借贷条件更为宽松。');
INSERT INTO `words` VALUES (2885, 'genetic', 'dʒəˈnetɪk', 'adj.', '基因的;遗传学的;', 'Cystic fibrosis is the most common fatal genetic disease in the United States.', '囊性纤维化是美国最常见的致命遗传疾病。');
INSERT INTO `words` VALUES (2886, 'genial', 'ˈdʒiːniəl', 'adj.', '友好的;亲切的;欢快的;', 'Bob was always genial and welcoming...', '鲍勃总是那么友善热情。');
INSERT INTO `words` VALUES (2887, 'genius', 'ˈdʒiːniəs', 'n.', '天才;天资;天赋;', 'This is the mark of her real genius as a designer...', '这展现了她作为设计师的真正天赋。');
INSERT INTO `words` VALUES (2888, 'gentle', 'ˈdʒentl', 'adj.', '文静的;慈祥的;温柔的;', 'My son was a quiet and gentle man who liked sports and enjoyed life...', '我儿子是个安静温和的人，喜欢运动和享受生活。');
INSERT INTO `words` VALUES (2889, 'gentleman', 'ˈdʒentlmən', 'n.', '彬彬有礼的人;有教养的人;君子;', '...this wonderful portrait of English gentleman Joseph Greenway.', '英国绅士约瑟夫·格林韦的精美画像');
INSERT INTO `words` VALUES (2890, 'gently', 'ˈdʒentli', 'adv.', '温柔地;温和地;文静地;', 'My son was a quiet and gentle man who liked sports and enjoyed life...', '我儿子是个安静温和的人，喜欢运动和享受生活。');
INSERT INTO `words` VALUES (2891, 'genuine', 'ˈdʒenjuɪn', 'adj.', '真的;名副其实的;真诚的;', 'There was a risk of genuine refugees being returned to Vietnam.', '存在将真正的难民遣返回越南的风险。');
INSERT INTO `words` VALUES (2892, 'geological', 'ˌdʒiːəˈlɒdʒɪkl', 'adj.', '地质(学)的;', 'With geological maps, books and atlases you can find out all the proven sites of precious minerals.', '有了地质图、书籍和地图册，你便可以找到所有已探明的珍贵矿藏的位置。');
INSERT INTO `words` VALUES (2893, 'geology', 'dʒiˈɒlədʒi', 'n.', '地质学;(某地区的)地质;', 'He was visiting professor of geology at the University of Jordan.', '他是约旦大学的地质学客座教授。');
INSERT INTO `words` VALUES (2894, 'geometry', 'dʒiˈɒmətri', 'n.', '几何(学);几何形状;几何图形;', '...the very ordered way in which mathematics and geometry describe nature.', '数学和几何学极有条理的描述自然的方式');
INSERT INTO `words` VALUES (2895, 'germ', 'dʒɜːm', 'n.', '微生物;细菌;病菌;', 'Chlorine is widely used to kill germs.', '氯气被广泛用于杀菌。');
INSERT INTO `words` VALUES (2896, 'german', 'ˈdʒɜːmən', 'adj.', '同父母的；同祖父母的，同外祖父母的，嫡亲的;', '...a cut in German interest rates.', '德国利率的下调');
INSERT INTO `words` VALUES (2897, 'germane', 'dʒɜːˈmeɪn', 'adj.', '与…有密切关系;贴切;恰当;', '...the suppression of a number of documents which were very germane to the case...', '对几份与本案密切相关的文件的隐瞒');
INSERT INTO `words` VALUES (2898, 'gesture', 'ˈdʒestʃə(r)', 'n.', '手势;姿势;示意动作;', 'Sarah made a menacing gesture with her fist...', '萨拉用拳头做了一个威胁性的动作。');
INSERT INTO `words` VALUES (2899, 'ghastly', 'ˈɡɑːstli', 'adj.', '(因有关疼痛、死亡等而)恐怖的，可怕的，令人毛骨悚然的;糟透的;令人不快的;', '...a mother accompanied by her ghastly unruly child...', '身边带着一位极难管教的孩子的母亲');
INSERT INTO `words` VALUES (2900, 'ghost', 'ɡəʊst', 'n.', '鬼;鬼魂;幽灵;', '...the ghost of Marie Antoinette...', '玛丽·安托瓦妮特的幽灵');
INSERT INTO `words` VALUES (2901, 'giant', 'ˈdʒaɪənt', 'n.', '(故事中常为残酷而愚蠢的)巨人;巨人;巨兽;', '...Italy\'s giant car maker, Fiat.', '意大利汽车生产巨头菲亚特公司');
INSERT INTO `words` VALUES (2902, 'gift', 'ɡɪft', 'n.', '礼物;赠品;天赋;', '...a gift of $50.00...', '价值50美元的礼物');
INSERT INTO `words` VALUES (2903, 'gifted', 'ˈɡɪftɪd', 'adj.', '有天才的;有天赋的;天资聪慧的;', '...one of the most gifted players in the world...', '世界上最有天赋的运动员之一');
INSERT INTO `words` VALUES (2904, 'gigantic', 'dʒaɪˈɡæntɪk', 'adj.', '巨大的;庞大的;', '...gigantic rocks...', '巨石');
INSERT INTO `words` VALUES (2905, 'giggle', 'ˈɡɪɡl', 'v.', '(因感到有趣、窘迫或紧张而)咯咯地笑;傻笑;', 'Both girls began to giggle...', '两个女孩都咯咯地笑了起来。');
INSERT INTO `words` VALUES (2906, 'girl', 'ɡɜːl', 'n.', '女孩;姑娘;女儿;', '...an eleven year old girl...', '11岁的女孩');
INSERT INTO `words` VALUES (2907, 'gist', 'dʒɪst', 'n.', '要点;主旨;大意;', 'He related the gist of his conversation to Naseby.', '他将他谈话的要点告诉了纳斯比。');
INSERT INTO `words` VALUES (2908, 'giver', 'ˈɡɪvə(r)', 'n.', '给予者;赠与者;捐助机构;', 'Germany is the largest giver of aid among the wealthy countries of the West.', '德国是西方富裕国家中给予援助最多的国家。');
INSERT INTO `words` VALUES (2909, 'glad', 'ɡlæd', 'adj.', '高兴;愉快;感激;', 'I\'m glad I relented in the end...', '我很高兴自己的态度最终缓和了下来。');
INSERT INTO `words` VALUES (2910, 'glance', 'ɡlɑːns', 'v.', '瞥一眼;匆匆一看;扫视;', 'He glanced at his watch...', '他匆匆看了一下手表。');
INSERT INTO `words` VALUES (2911, 'gland', 'ɡlænd', 'n.', '腺;', '...the hormones secreted by our endocrine glands.', '人体内分泌腺分泌的荷尔蒙');
INSERT INTO `words` VALUES (2912, 'glare', 'ɡleə(r)', 'v.', '怒目而视;发出刺眼的光;', 'The old woman glared at him...', '那个老妇人狠狠地瞪着他。');
INSERT INTO `words` VALUES (2913, 'glass', 'ɡlɑːs', 'n.', '玻璃;玻璃杯;酒杯;', '...a pane of glass.', '一块玻璃');
INSERT INTO `words` VALUES (2914, 'gleam', 'ɡliːm', 'v.', '发微光;隐约闪光;闪烁;', 'His black hair gleamed in the sun.', '他的黑发在阳光下闪着光泽。');
INSERT INTO `words` VALUES (2915, 'glide', 'ɡlaɪd', 'v.', '滑行;滑动;掠过;', 'Waiters glide between tightly packed tables bearing trays of pasta.', '侍者们端着一盘盘意大利面在拥挤的餐桌之间自如穿行。');
INSERT INTO `words` VALUES (2916, 'glimmer', 'ˈɡlɪmə(r)', 'n.', '微弱的闪光;闪烁的微光;微弱的迹象;', 'The moon glimmered faintly through the mists.', '月亮透过薄雾洒下微光。');
INSERT INTO `words` VALUES (2917, 'glimpse', 'ɡlɪmps', 'n.', '一瞥;一看;短暂的感受(或体验、领会);', 'Some of the fans had waited 24 hours outside the Hyde Park Hotel to catch a glimpse of their heroine.', '为了一睹女主角的风采，一些追星族已经在海德公园饭店外守候了24个小时。');
INSERT INTO `words` VALUES (2918, 'glisten', 'ˈɡlɪsn', 'v.', '闪光;闪亮;', 'The calm sea glistened in the sunlight...', '平静的海面在阳光下波光粼粼。');
INSERT INTO `words` VALUES (2919, 'glitch', 'ɡlɪtʃ', 'n.', '小故障;小毛病;小差错;', 'Manufacturing glitches have limited the factory\'s output.', '生产中的故障影响了该厂的产量。');
INSERT INTO `words` VALUES (2920, 'glitter', 'ˈɡlɪtə(r)', 'v.', '闪亮;闪耀;光彩夺目;', 'The bay glittered in the sunshine...', '海湾在阳光下闪闪发光。');
INSERT INTO `words` VALUES (2921, 'global', 'ˈɡləʊbl', 'adj.', '全球的;全世界的;整体的;', '...a global ban on nuclear testing...', '全球核试验禁令');
INSERT INTO `words` VALUES (2922, 'globe', 'ɡləʊb', 'n.', '地球仪;地球，世界(尤用以强调其大);球体;', '...bottles of beer from every corner of the globe...', '来自世界各个角落的一瓶瓶啤酒');
INSERT INTO `words` VALUES (2923, 'gloom', 'ɡluːm', 'n.', '忧郁;愁闷;无望;', '...the gloom of a foggy November morning...', '11月份一个雾蒙蒙的早晨，天空昏暗阴沉');
INSERT INTO `words` VALUES (2924, 'gloomy', 'ˈɡluːmi', 'adj.', '黑暗的;阴暗的;幽暗的;', 'Inside it\'s gloomy after all that sunshine...', '尽管阳光明媚，里面依然暗淡无光。');
INSERT INTO `words` VALUES (2925, 'glorify', 'ˈɡlɔːrɪfaɪ', 'v.', '吹捧;吹嘘;美化;', 'This magazine in no way glorifies gangs.', '这本杂志绝对没有美化混混们。');
INSERT INTO `words` VALUES (2926, 'glorious', 'ˈɡlɔːriəs', 'adj.', '值得称道的;光荣的;荣耀的;', '...a glorious rainbow in the air...', '空中壮观的彩虹');
INSERT INTO `words` VALUES (2927, 'glory', 'ˈɡlɔːri', 'n.', '荣誉;光荣;桂冠;', 'Walsham had his moment of glory when he won a 20km race.', '沃尔沙姆夺得20公里比赛冠军的那一刻无比荣耀。');
INSERT INTO `words` VALUES (2928, 'gloss', 'ɡlɒs', 'n.', '(平滑表面上的)光泽，光亮;用以产生光泽的物质;光泽涂料;', 'Rain produced a black gloss on the asphalt.', '雨后的柏油路面显得黑亮黑亮的。');
INSERT INTO `words` VALUES (2929, 'glove', 'ɡlʌv', 'n.', '(分手指的)手套;', 'He stuck his gloves in his pocket.', '他把手套塞进口袋里。');
INSERT INTO `words` VALUES (2930, 'gloves', 'glʌvz', 'n.', '(分手指的)手套;', 'He stuck his gloves in his pocket.', '他把手套塞进口袋里。');
INSERT INTO `words` VALUES (2931, 'glow', 'ɡləʊ', 'v.', '发出微弱而稳定的光;发出暗淡的光;(尤指运动后或因情绪激动、尴尬等而)发红，发热，显得红，感觉热;', '...the cigarette\'s red glow...', '香烟发出的红光');
INSERT INTO `words` VALUES (2932, 'glue', 'ɡluː', 'n.', '胶;胶水;', '...a tube of glue.', '一管胶水');
INSERT INTO `words` VALUES (2933, 'glut', 'ɡlʌt', 'n.', '供应过剩;供过于求;', 'There\'s a glut of agricultural products in Western Europe.', '西欧的农产品供过于求。');
INSERT INTO `words` VALUES (2934, 'gnaw', 'nɔː', 'v.', '咬;啃;啮;', 'Woodlice attack living plants and gnaw at the stems...', '木虱会侵害活体植物并啃咬它们的茎。');
INSERT INTO `words` VALUES (2935, 'goad', 'ɡəʊd', 'v.', '(不断地)招惹，激怒，刺激;', 'He wondered if the psychiatrist was trying to goad him into some unguarded response...', '他怀疑那位精神科医生试图刺激他作出一些本能的反应。');
INSERT INTO `words` VALUES (2936, 'goal', 'ɡəʊl', 'n.', '球门;射门;进球得分;', 'David Seaman was back in the Arsenal goal after breaking a knuckle.', '戴维·西曼在一个指关节骨折后又回到阿森纳队的球门前。');
INSERT INTO `words` VALUES (2937, 'god', 'ɡɒd', 'n.', '上帝;天主;真主;', 'He believes in God...', '他信奉上帝。');
INSERT INTO `words` VALUES (2938, 'goddess', 'ˈɡɒdes', 'n.', '女神;极受崇敬(或敬慕)的女人;', '...Athena, the goddess of war.', '战争女神雅典娜');
INSERT INTO `words` VALUES (2939, 'gold', 'ɡəʊld', 'n.', '金;金币;金饰品;', '...a sapphire set in gold...', '镶嵌在黄金上的蓝宝石');
INSERT INTO `words` VALUES (2940, 'golden', 'ˈɡəʊldən', 'adj.', '金质的;金的;金色的;', 'She combed and arranged her golden hair.', '她梳理好自己的金发。');
INSERT INTO `words` VALUES (2941, 'good', 'ɡʊd', 'adj.', '好的;优质的;符合标准的;', 'We had a really good time together...', '我们一起玩得真痛快。');
INSERT INTO `words` VALUES (2942, 'goodness', 'ˈɡʊdnəs', 'n.', '善良;优良;美德;', 'Goodness, I wonder if he knows...', '天哪，不知道他是否晓得。');
INSERT INTO `words` VALUES (2943, 'goods', 'ɡʊdz', 'n.', '商品;货品;动产;', 'Money can be exchanged for goods or services.', '钱可以用来换取商品或服务。');
INSERT INTO `words` VALUES (2944, 'goodwill', 'ˌɡʊdˈwɪl', 'n.', '友善;友好;善意;', 'I invited them to dinner, a gesture of goodwill...', '我请他们吃饭以示友善。');
INSERT INTO `words` VALUES (2945, 'gorgeous', 'ˈɡɔːdʒəs', 'adj.', '非常漂亮的;美丽动人的;令人愉快的;', '...gorgeous mountain scenery...', '秀丽的山间景色');
INSERT INTO `words` VALUES (2946, 'gossip', 'ˈɡɒsɪp', 'n.', '流言蜚语;闲言碎语;说长道短;', 'He spent the first hour talking gossip...', '他头一个小时都在闲聊。');
INSERT INTO `words` VALUES (2947, 'govern', 'ˈɡʌvn', 'v.', '统治;控制;管理;', 'They go to the polls on Friday to choose the people they want to govern their country...', '星期五，他们去投票站投票选择他们所期望的国家管理者。');
INSERT INTO `words` VALUES (2948, 'government', 'ˈɡʌvənmənt', 'n.', '政府;内阁;政体;', 'The Government has insisted that confidence is needed before the economy can improve.', '政府坚持认为只有坚定信心经济才会有起色。');
INSERT INTO `words` VALUES (2949, 'governor', 'ˈɡʌvənə(r)', 'n.', '统治者;管辖者;总督;', 'He was governor of the province in the late 1970s...', '他在20世纪70年代末担任该省的省长。');
INSERT INTO `words` VALUES (2950, 'gown', 'ɡaʊn', 'n.', '(尤指特别场合穿的)女裙;女长服;女礼服;', 'The new ball gown was a great success.', '这种新款舞会礼服很畅销。');
INSERT INTO `words` VALUES (2951, 'grab', 'ɡræb', 'v.', '抓住;攫取;(试图)抓住，夺得;', 'I managed to grab her hand...', '我抓到了她的手。');
INSERT INTO `words` VALUES (2952, 'grace', 'ɡreɪs', 'n.', '优美;优雅;文雅;', 'He moved with the grace of a trained boxer...', '他的动作带着一种训练有素的拳击手的风采。');
INSERT INTO `words` VALUES (2953, 'graceful', 'ˈɡreɪsfl', 'adj.', '优美的;优雅的;雅致的;', 'His movements were so graceful they seemed effortless.', '他的动作优美自如，看起来毫不费劲。');
INSERT INTO `words` VALUES (2954, 'gracious', 'ˈɡreɪʃəs', 'adj.', '(尤指对社会地位较低者)和蔼的，慈祥的，有礼貌的，宽厚的;富贵安逸的;(对王族及其行为的敬语)仁慈的;', 'She is a lovely and gracious woman.', '她是位可爱又亲切的女人。');
INSERT INTO `words` VALUES (2955, 'gradation', 'ɡrəˈdeɪʃn', 'n.', '逐渐的变化;层次;阶段;', 'But TV images require subtle gradations of light and shade.', '但是，电视影像要求有十分细微的光影变化。');
INSERT INTO `words` VALUES (2956, 'grade', 'ɡreɪd', 'n.', '(产品、材料的)等级，品级;(官衔的)级别;职别;', 'Dust masks are graded according to the protection they offer...', '防尘口罩根据它们的防护效果分等级。');
INSERT INTO `words` VALUES (2957, 'gradual', 'ˈɡrædʒuəl', 'adj.', '逐渐的;逐步的;渐进的;', 'Losing weight is a slow, gradual process...', '减肥是一个缓慢而渐进的过程。');
INSERT INTO `words` VALUES (2958, 'gradually', 'ˈɡrædʒuəli', 'adv.', '逐渐地;逐步地;渐进地;', 'Electricity lines to 30,000 homes were gradually being restored yesterday...', '昨天，3万户家庭逐渐恢复了供电。');
INSERT INTO `words` VALUES (2959, 'graduation', 'ˌɡrædʒuˈeɪʃn', 'n.', '(大学或美国高中的)毕业;毕业典礼;', 'They asked what his plans were after graduation...', '他们问他毕业之后有何打算。');
INSERT INTO `words` VALUES (2960, 'graft', 'ɡrɑːft', 'n.', '接穗;嫁接;移植的皮肤(或骨骼等);', 'I am having a skin graft on my arm soon.', '我马上就要接受手臂的皮肤移植手术。');
INSERT INTO `words` VALUES (2961, 'grain', 'ɡreɪn', 'n.', '谷物;谷粒;颗粒;', '...a grain of wheat.', '一粒小麦');
INSERT INTO `words` VALUES (2962, 'grammar', 'ˈɡræmə(r)', 'n.', '语法;文法;(人的)语言知识及运用能力;', 'He  doesn\'t have mastery of the basic rules of grammar.', '他还没有掌握基本的语法规则。');
INSERT INTO `words` VALUES (2963, 'grammatical', 'ɡrəˈmætɪkl', 'adj.', '语法的;文法的;符合语法规则的;', 'Should the teacher present grammatical rules to students?', '老师是否应该给学生讲授语法规则？');
INSERT INTO `words` VALUES (2964, 'gramophone', 'ˈɡræməfəʊn', 'n.', '留声机;', '...a wind-up gramophone with a big horn.', '带有大喇叭的上发条的留声机');
INSERT INTO `words` VALUES (2965, 'granary', 'ˈɡrænəri', 'n.', '谷仓;粮仓;', 'The granaries containing last year\'s harvest are nearly empty.', '贮存去年收割的粮食的谷仓几乎空了。');
INSERT INTO `words` VALUES (2966, 'grandchild', 'ˈɡræntʃaɪld', 'n.', '(外)孙子;(外)孙女;', 'Mary loves her grandchildren.', '玛丽很疼爱她的孙子们。');
INSERT INTO `words` VALUES (2967, 'granddaughter', 'ˈɡrændɔːtə(r)', 'n.', '(外)孙女;', '...a drawing of my granddaughter Amelia.', '我孙女阿梅莉亚的一张素描');
INSERT INTO `words` VALUES (2968, 'grandfather', 'ˈɡrænfɑːðə(r)', 'n.', '(外)祖父;爷爷;外公;', 'His grandfather was a professor.', '他的爷爷曾是一名教授。');
INSERT INTO `words` VALUES (2969, 'grandmother', 'ˈɡrænmʌðə(r)', 'n.', '(外)祖母;奶奶;外婆;', 'My grandmothers are both widows.', '我奶奶和外婆都是孤寡一人。');
INSERT INTO `words` VALUES (2970, 'grandparent', 'ˈɡrænpeərənt', 'n.', '祖父;祖母;外祖父;', 'Tammy was raised by her grandparents.', '塔米是爷爷奶奶抚养大的。');
INSERT INTO `words` VALUES (2971, 'grandson', 'ˈɡrænsʌn', 'n.', '孙子;外孙;', 'My grandson\'s birthday was on Tuesday.', '星期二是我孙子的生日。');
INSERT INTO `words` VALUES (2972, 'grant', 'ɡrɑːnt', 'v.', '(尤指正式地或法律上)同意，准予，允许;(勉强)承认，同意;', 'They\'d got a special grant to encourage research...', '他们得到一笔用以鼓励科研的特别经费。');
INSERT INTO `words` VALUES (2973, 'granular', 'ˈɡrænjələ(r)', 'adj.', '由颗粒构成的;含颗粒的;似颗粒状的;', '...a granular fertiliser.', '颗粒状化肥');
INSERT INTO `words` VALUES (2974, 'graphic', 'ˈɡræfɪk', 'adj.', '绘画的;书画的;图样的;', 'The descriptions of sexual abuse are graphic.', '这些性虐待的描写绘声绘色。');
INSERT INTO `words` VALUES (2975, 'graphically', 'ˈɡræfɪkli', 'adv.', '以书画(或图表)形式;形象地;生动地;', 'The descriptions of sexual abuse are graphic.', '这些性虐待的描写绘声绘色。');
INSERT INTO `words` VALUES (2976, 'graphics', 'ˈɡræfɪks', 'n.', '图样;图案;绘图;', 'The descriptions of sexual abuse are graphic.', '这些性虐待的描写绘声绘色。');
INSERT INTO `words` VALUES (2977, 'grasp', 'ɡrɑːsp', 'v.', '抓紧;抓牢;理解;', 'He grasped both my hands...', '他紧紧抓住我的双手。');
INSERT INTO `words` VALUES (2978, 'grass', 'ɡrɑːs', 'n.', '草;青草;牧草;', 'Small things stirred in the grass around the tent...', '一些小东西在帐篷四周的草丛里窸窸窣窣地动着。');
INSERT INTO `words` VALUES (2979, 'grate', 'ɡreɪt', 'n.', '炉条;炉箅;', 'A wood fire burned in the grate.', '木柴在炉栅中燃烧着。');
INSERT INTO `words` VALUES (2980, 'grateful', 'ˈɡreɪtfl', 'adj.', '感激的;表示感谢的;(尤用于书信或正式场合提出请求)感激不尽，请;', 'She was grateful to him for being so good to her...', '他对她这么好，令她很感激。');
INSERT INTO `words` VALUES (2981, 'gratify', 'ˈɡrætɪfaɪ', 'v.', '使高兴;使满意;满足(愿望、需要等);', 'Mr. Dambar was gratified by his response.', '丹巴尔先生对他的回答感到满意。');
INSERT INTO `words` VALUES (2982, 'gratifying', 'ˈɡrætɪfaɪɪŋ', 'adj.', '令人高兴的;使人满意的;', 'Mr. Dambar was gratified by his response.', '丹巴尔先生对他的回答感到满意。');
INSERT INTO `words` VALUES (2983, 'gratis', 'ˈɡrætɪs', 'adv.', '免费的;无偿的;', 'David gives the first consultation gratis.', '戴维免费提供初次咨询。');
INSERT INTO `words` VALUES (2984, 'gratitude', 'ˈɡrætɪtjuːd', 'n.', '感激之情;感谢;', '...a sense of gratitude...', '感恩图报之意');
INSERT INTO `words` VALUES (2985, 'gratuity', 'ɡrəˈtjuːəti', 'n.', '小费;赏钱;报酬;', 'The porter expects a gratuity.', '行李员想要小费。');
INSERT INTO `words` VALUES (2986, 'gravel', 'ˈɡrævl', 'n.', '沙砾;砾石;石子;', '...a gravel path leading to the front door.', '通向前门的石子路');
INSERT INTO `words` VALUES (2987, 'gravity', 'ˈɡrævəti', 'n.', '重力;地球引力;严重性;', 'Arrows would continue to fly forward forever in a straight line were it not for gravity, which brings them down to earth.', '要不是重力使箭落向地面，它们将永远以直线往前飞行。');
INSERT INTO `words` VALUES (2988, 'graze', 'ɡreɪz', 'v.', '(在草地上)吃青草;放牧;放牛;', 'Five cows graze serenely around a massive oak...', '5头奶牛在一棵大橡树旁悠闲自在地啃着青草。');
INSERT INTO `words` VALUES (2989, 'grease', 'ɡriːs', 'n.', '油脂;润滑油;(炼过的)动物油脂;', '...grease-stained hands.', '沾满油渍的手');
INSERT INTO `words` VALUES (2990, 'great', 'ɡreɪt', 'adj.', '大的;巨大的;数量大的;', 'The room had a great bay window.', '这间房有一扇巨大的凸窗。');
INSERT INTO `words` VALUES (2991, 'greatly', 'ˈɡreɪtli', 'adv.', '非常;很;大大地;', 'People would benefit greatly from a pollution-free vehicle...', '人们将享受到无污染汽车的诸多好处。');
INSERT INTO `words` VALUES (2992, 'greatness', 'ˈɡreɪtnəs', 'n.', '巨大；伟大；重大；广大；重要；著名；崇高;', 'The room had a great bay window.', '这间房有一扇巨大的凸窗。');
INSERT INTO `words` VALUES (2993, 'greed', 'ɡriːd', 'n.', '贪婪;贪心;贪欲;', '...an insatiable greed for personal power...', '对个人权力的贪得无厌');
INSERT INTO `words` VALUES (2994, 'greedy', 'ˈɡriːdi', 'adj.', '贪婪的;贪心的;贪吃的;', 'He attacked greedy bosses for awarding themselves big rises...', '他抨击那些贪心的老板为他们自己大幅加薪。');
INSERT INTO `words` VALUES (2995, 'greek', 'ɡriːk', 'n.', '希腊人;古希腊人;希腊语;', 'In the Greek general election, the New Democracy party has claimed victory.', '新民主党在希腊大选中获胜。');
INSERT INTO `words` VALUES (2996, 'green', 'ɡriːn', 'adj.', '绿色的;草绿色的;长满青草的;', '...shiny red and green apples...', '红红绿绿、富有光泽的苹果');
INSERT INTO `words` VALUES (2997, 'greet', 'ɡriːt', 'v.', '和(某人)打招呼(或问好);欢迎;迎接;', 'She liked to be home to greet Steve when he came in from school.', '她喜欢呆在家里迎接放学回家的史蒂夫。');
INSERT INTO `words` VALUES (2998, 'greeting', 'ˈɡriːtɪŋ', 'n.', '问候;招呼;迎接;', 'His greeting was familiar and friendly...', '他的问候让人觉得随意又亲切。');
INSERT INTO `words` VALUES (2999, 'grid', 'ɡrɪd', 'n.', '网格;方格;(金属或木制的)格子，格栅，栅栏;', '...a grid of ironwork.', '铁格栅');
INSERT INTO `words` VALUES (3000, 'grief', 'ɡriːf', 'n.', '(尤指因某人去世引起的)悲伤，悲痛，伤心;伤心事;悲痛事;', '...a huge outpouring of national grief for the victims of the shootings...', '举国上下对枪击事件中的遇害者所表现出的万分悲痛之情');
INSERT INTO `words` VALUES (3001, 'grieve', 'ɡriːv', 'v.', '(尤指因某人的去世而)悲伤，悲痛，伤心;使悲伤;使悲痛;', 'He\'s grieving over his dead wife and son...', '他因丧妻失子而悲痛不已。');
INSERT INTO `words` VALUES (3002, 'grill', 'ɡrɪl', 'n.', '(炊具、烤炉内的)烤架;(置于火上的)烤架;一盘烧烤食物(尤指烤肉);', 'Place the omelette under a gentle grill until the top is set.', '将煎蛋饼放在烤架下用文火烘烤，直到表面凝固。');
INSERT INTO `words` VALUES (3003, 'grim', 'ɡrɪm', 'adj.', '严肃的;坚定的;阴冷的;', 'They painted a grim picture of growing crime...', '他们描绘了犯罪率上升的严峻情形。');
INSERT INTO `words` VALUES (3004, 'grin', 'ɡrɪn', 'v.', '露齿而笑;咧着嘴笑;龇着牙笑;', 'He grins, delighted at the memory...', '想到往事，他开心地咧嘴笑了起来。');
INSERT INTO `words` VALUES (3005, 'grind', 'ɡraɪnd', 'v.', '磨碎;碾碎;把…磨成粉;', 'Store the peppercorns in an airtight container and grind the pepper as you need it.', '将胡椒粒储存在密封容器中，在需要的时候磨成粉。');
INSERT INTO `words` VALUES (3006, 'grip', 'ɡrɪp', 'n.', '紧握;紧抓;(对…的)控制，影响力;', 'She gripped the rope.', '她紧紧抓住绳索。');
INSERT INTO `words` VALUES (3007, 'grit', 'ɡrɪt', 'n.', '沙粒;沙砾;细沙;', 'He felt tiny bits of grit and sand peppering his knees.', '他感到细小的沙砾不断打在膝盖上。');
INSERT INTO `words` VALUES (3008, 'groan', 'ɡrəʊn', 'v.', '呻吟;叹息;哼哼;', 'Slowly, he opened his eyes. As he did so, he began to groan with pain...', '他慢慢地睁开眼睛，并发出痛苦的呻吟。');
INSERT INTO `words` VALUES (3009, 'groove', 'ɡruːv', 'n.', '沟;槽;辙;', 'Their wheels left grooves in the ground.', '他们的车轮在地面上留下了车辙印。');
INSERT INTO `words` VALUES (3010, 'grope', 'ɡrəʊp', 'v.', '摸索;搜索;搜寻;', 'With his left hand he groped for the knob, turned it, and pulled the door open...', '他左手摸索着门把手，转动一下，然后推开门。');
INSERT INTO `words` VALUES (3011, 'ground', 'ɡraʊnd', 'n.', '地;地面;土;', 'Forty or fifty women were sitting cross-legged on the ground...', '四五十个女人盘腿坐在地上。');
INSERT INTO `words` VALUES (3012, 'grounding', 'ˈɡraʊndɪŋ', 'n.', '基础训练;基础教学;(尤指因机件故障等)(飞机的)留地停飞;', 'The degree provides a thorough grounding in both mathematics and statistics.', '该学位课程将为数学和统计学打下扎实的基础。');
INSERT INTO `words` VALUES (3013, 'groundless', 'ˈɡraʊndləs', 'adj.', '无理由的;无根据的;', 'Fears that the world was about to run out of fuel proved groundless...', '事实证明对于世界燃料即将耗尽的担忧毫无根据。');
INSERT INTO `words` VALUES (3014, 'grounds', 'ɡraʊndz', 'n.', '地;地面;土;', 'Forty or fifty women were sitting cross-legged on the ground...', '四五十个女人盘腿坐在地上。');
INSERT INTO `words` VALUES (3015, 'group', 'ɡruːp', 'n.', '组;群;批;', 'The trouble involved a small group of football supporters...', '这次骚乱涉及一小撮足球迷。');
INSERT INTO `words` VALUES (3016, 'grove', 'ɡrəʊv', 'n.', '树丛;小树林;果树林;', '...open fields and groves of trees.', '开阔的田野和片片树林');
INSERT INTO `words` VALUES (3017, 'grow', 'ɡrəʊ', 'v.', '扩大;增加;增强;', 'We stop growing at maturity.', '我们成年之后不再长个儿。');
INSERT INTO `words` VALUES (3018, 'growl', 'ɡraʊl', 'v.', '低声吼叫;发出低沉的怒吼;咆哮;', 'The dog was biting, growling and wagging its tail.', '那条狗在一边撕咬一边低声吼叫，尾巴也跟着摇摆。');
INSERT INTO `words` VALUES (3019, 'grown', 'ɡrəʊn', 'adj.', '成熟的;成年的;长大的;', 'Few women can understand a grown man\'s love of sport...', '很少有女人能理解一个成年男人对体育的那份热爱。');
INSERT INTO `words` VALUES (3020, 'growth', 'ɡrəʊθ', 'n.', '发育;成长;生长;', '...the growth of nationalism.', '民族主义的高涨');
INSERT INTO `words` VALUES (3021, 'grudge', 'ɡrʌdʒ', 'n.', '积怨;怨恨;嫌隙;', 'He appears to have a grudge against certain players...', '他看来对某些选手怀恨在心。');
INSERT INTO `words` VALUES (3022, 'grumble', 'ˈɡrʌmbl', 'v.', '咕哝;嘟囔;发牢骚;', 'I shouldn\'t grumble about Mum — she\'s lovely really...', '我不该抱怨妈妈——她真的很好。');
INSERT INTO `words` VALUES (3023, 'grunt', 'ɡrʌnt', 'v.', '发出呼噜声;发出哼声;咕哝;', 'The driver grunted, convinced that Michael was crazy...', '司机嘟哝了一声，认定迈克尔是发疯了。');
INSERT INTO `words` VALUES (3024, 'guarantee', 'ˌɡærənˈtiː', 'n.', '保证;担保;保修单;', 'Surplus resources alone do not guarantee growth.', '仅有富余的资源并不能确保发展。');
INSERT INTO `words` VALUES (3025, 'guard', 'ɡɑːd', 'n.', '卫兵;警卫员;看守;', 'Gunmen guarded homes near the cemetery with shotguns...', '持枪者手持猎枪守卫着墓地附近的房子。');
INSERT INTO `words` VALUES (3026, 'guess', 'ɡes', 'v.', '猜测;估计;猜对;', 'The suit was faultless: Wood guessed that he was a very successful publisher or a banker...', '西装无可挑剔：伍德推测他可能是个相当成功的出版商或银行家。');
INSERT INTO `words` VALUES (3027, 'guest', 'ɡest', 'n.', '客人;宾客;旅客;', 'She was a guest at the wedding...', '她是参加婚礼的客人。');
INSERT INTO `words` VALUES (3028, 'guidance', 'ˈɡaɪdns', 'n.', '指导;引导;咨询;', '...an opportunity for young people to improve their performance under the guidance of professional coaches...', '年轻人在职业教练的指导下提高成绩的机会');
INSERT INTO `words` VALUES (3029, 'guide', 'ɡaɪd', 'n.', '指南;手册;旅游指南(或手册);', 'Our 10-page guide will help you to change your life for the better.', '我们这本10页的指南有助于您提高生活品质。');
INSERT INTO `words` VALUES (3030, 'guideline', 'ˈɡaɪdlaɪn', 'n.', '指导方针;指导原则;行动纲领;', 'The government should issue clear guidelines on the content of religious education...', '政府应当就宗教教育的内容颁布明确的指导方针。');
INSERT INTO `words` VALUES (3031, 'guild', 'ɡɪld', 'n.', '(行业)协会;(中世纪的)行会，同业公会;', '...the Writers\' Guild of America.', '美国作家协会');
INSERT INTO `words` VALUES (3032, 'guilt', 'ɡɪlt', 'n.', '内疚;悔恨;犯罪;', 'Her emotions had ranged from anger to guilt in the space of a few seconds...', '几秒钟的时间里，她经历了从愤怒到内疚的情感变化。');
INSERT INTO `words` VALUES (3033, 'guilty', 'ˈɡɪlti', 'adj.', '感到内疚的;感到惭愧的;犯了罪;', 'I feel so guilty, leaving all this to you...', '把这些事都留给你去做，我真感到过意不去。');
INSERT INTO `words` VALUES (3034, 'gulf', 'ɡʌlf', 'n.', '海湾;波斯湾;分歧;', 'Within society, there is a growing gulf between rich and poor.', '社会上的贫富差距正在逐渐加大。');
INSERT INTO `words` VALUES (3035, 'gulp', 'ɡʌlp', 'v.', '狼吞虎咽;大口吞咽;匆匆吞下;', 'She quickly gulped her tea.', '她将茶一饮而尽。');
INSERT INTO `words` VALUES (3036, 'gun', 'ɡʌn', 'n.', '枪;炮;喷射器;', 'He produced a gun and he came into the house...', '他掏出枪来，走进房子。');
INSERT INTO `words` VALUES (3037, 'gush', 'ɡʌʃ', 'v.', '(从…中)喷出，涌出，冒出;大量涌出，大量泄出(液体);过分称赞;', 'Piping-hot water gushed out...', '滚烫的水喷了出来。');
INSERT INTO `words` VALUES (3038, 'gust', 'ɡʌst', 'n.', '一阵强风;一阵狂风;(感情的)迸发，爆发，发作;', 'A gust of wind drove down the valley...', '一阵狂风刮过山谷。');
INSERT INTO `words` VALUES (3039, 'gut', 'ɡʌt', 'n.', '消化道;肠道;(尤指动物的)内脏;', 'By the time they finish, the crewmen are standing ankle-deep in fish guts.', '到他们完工时，船员站的地方鱼内脏已堆至脚踝。');
INSERT INTO `words` VALUES (3040, 'gutter', 'ˈɡʌtə(r)', 'n.', '檐沟;天沟;路旁排水沟;', 'It is supposed to be washed down the gutter and into the city\'s vast sewerage system.', '它应该是被沿排水沟冲入城市庞大的排水系统。');
INSERT INTO `words` VALUES (3041, 'guy', 'ɡaɪ', 'n.', '男人;小伙子;家伙;', 'I was working with a guy from Manchester.', '我与一个来自曼彻斯特的小伙子一起工作。');
INSERT INTO `words` VALUES (3042, 'gym', 'dʒɪm', 'n.', '健身房;体育馆;(尤指学校的)体育活动;', 'While the lads are golfing, I work out in the gym.', '小伙子们打高尔夫球的时候，我在健身房锻炼。');
INSERT INTO `words` VALUES (3043, 'habit', 'ˈhæbɪt', 'n.', '习惯;惯常行为;习性;', 'He has an endearing habit of licking his lips when he\'s nervous...', '他有个一紧张就舔嘴唇的可爱习惯。');
INSERT INTO `words` VALUES (3044, 'habitat', 'ˈhæbɪtæt', 'n.', '(动植物的)生活环境，栖息地;', 'In its natural habitat, the hibiscus will grow up to 25ft...', '在原生境中，木槿能长到25英尺高。');
INSERT INTO `words` VALUES (3045, 'habitual', 'həˈbɪtʃuəl', 'adj.', '惯常的;典型的;讨厌的;', 'He soon recovered his habitual geniality...', '他不久就恢复了自己一贯的和蔼可亲的态度。');
INSERT INTO `words` VALUES (3046, 'hack', 'hæk', 'v.', '砍;劈;猛踢;', 'An armed gang barged onto the train and began hacking and shooting anyone in sight...', '一伙持械歹徒闯上火车，开始见人就砍、举枪便射。');
INSERT INTO `words` VALUES (3047, 'haggard', 'ˈhæɡəd', 'adj.', '(由于生病、焦虑或睡眠不足)憔悴的，疲惫的;', 'He was pale and a bit haggard...', '他面黄肌瘦。');
INSERT INTO `words` VALUES (3048, 'haggle', 'ˈhæɡl', 'v.', '争论;(尤指)讲价;', 'Ella taught her how to haggle with used furniture dealers...', '埃拉教她如何和旧家具经销商砍价。');
INSERT INTO `words` VALUES (3049, 'hail', 'heɪl', 'v.', '赞扬(或称颂)…为…(尤用于报章等);招手(请出租车或公共汽车停下);跟…打招呼;', 'Faulkner has been hailed as the greatest American novelist of his generation...', '福克纳被誉为他那一代人中最伟大的美国小说家。');
INSERT INTO `words` VALUES (3050, 'hair', 'heə(r)', 'n.', '毛发;(尤指)头发;有…毛发(或头发)的;', 'I wash my hair every night...', '我每晚都洗头。');
INSERT INTO `words` VALUES (3051, 'haircut', 'ˈheəkʌt', 'n.', '理发;发型;发式;', 'Your hair is all right; it\'s just that you need a haircut.', '你的头发还好;不过你该理发了。');
INSERT INTO `words` VALUES (3052, 'hairy', 'ˈheəri', 'adj.', '多毛的;惊险的;可怕(但刺激)的;', 'He was wearing shorts which showed his long, muscular, hairy legs...', '他穿着短裤，露出自己强壮多毛的长腿。');
INSERT INTO `words` VALUES (3053, 'half', 'hɑːf', 'n.', '一半;半;(比赛、音乐会等的)半场，半局;', 'They need an extra two and a half thousand pounds to complete the project...', '他们还需要2,500英镑才能完成该项目。');
INSERT INTO `words` VALUES (3054, 'halfway', 'ˌhɑːfˈweɪ', 'adv.', '在…的中间;在中途;到一半;', 'Half-way across the car-park, he noticed she was walking with her eyes closed...', '走到停车场中间时，他看到她在闭着眼走路。');
INSERT INTO `words` VALUES (3055, 'hall', 'hɔːl', 'n.', '门厅;正门过道;(大楼内的)走廊;', 'The lights were on in the hall and in the bedroom.', '门厅和卧室的灯都亮着。');
INSERT INTO `words` VALUES (3056, 'hallmark', 'ˈhɔːlmɑːk', 'n.', '特征;特点;金银纯度印记(打在金、银、铂制品上，表示纯度、生产日期及产地);', 'It\'s a technique that has become the hallmark of Amber Films...', '这已经成为安伯电影公司的标志性技术。');
INSERT INTO `words` VALUES (3057, 'halt', 'hɔːlt', 'v.', '(使)停止，停下;', 'They halted at a short distance from the house...', '他们在离房子不远处停了下来。');
INSERT INTO `words` VALUES (3058, 'halve', 'hɑːv', 'v.', '(使)减半;把…对半分;', 'Dr Lee believes that men who exercise can halve their risk of cancer of the colon...', '李医生相信，锻炼身体能使患结肠癌的风险降低一半。');
INSERT INTO `words` VALUES (3059, 'halves', 'hɑːvz', 'n.', '一半;半;(比赛、音乐会等的)半场，半局;', 'Dr Lee believes that men who exercise can halve their risk of cancer of the colon...', '李医生相信，锻炼身体能使患结肠癌的风险降低一半。');
INSERT INTO `words` VALUES (3060, 'ham', 'hæm', 'n.', '火腿;火腿肉;无线电通讯爱好者;', '...a huge baked ham.', '巨大的烤火腿');
INSERT INTO `words` VALUES (3061, 'hammer', 'ˈhæmə(r)', 'n.', '锤子;榔头;(拍卖用的)木槌;', 'He used a hammer and chisel to chip away at the wall.', '他用榔头和凿子铲墙。');
INSERT INTO `words` VALUES (3062, 'hamper', 'ˈhæmpə(r)', 'v.', '妨碍;阻止;阻碍;', 'The bad weather hampered rescue operations...', '恶劣的天气阻碍了救援工作的进行。');
INSERT INTO `words` VALUES (3063, 'handbook', 'ˈhændbʊk', 'n.', '手册;指南;', 'If you have not kept a pet parrot before, it would be wise to purchase a handbook on the subject.', '如果以前没有养过鹦鹉，最好买一本相关的饲养手册。');
INSERT INTO `words` VALUES (3064, 'handful', 'ˈhændfʊl', 'n.', '一把(的量);用手抓起的数量;少数人(或物);', 'He surveyed the handful of customers at the bar...', '他打量着吧台那边零星的几个顾客。');
INSERT INTO `words` VALUES (3065, 'handicap', 'ˈhændikæp', 'n.', '生理缺陷;弱智;残疾;', 'He lost his leg when he was ten, but learnt to overcome his handicap.', '他10岁时失去了一条腿，但他学会了克服自己的残疾。');
INSERT INTO `words` VALUES (3066, 'handicapped', 'ˈhændikæpt', 'adj.', '有生理缺陷的;残疾的;弱智的;', 'I\'m going to work two days a week teaching handicapped kids to fish...', '我将每周工作两天，教残疾儿童钓鱼。');
INSERT INTO `words` VALUES (3067, 'handle', 'ˈhændl', 'v.', '处理，应付(局势、人、工作或感情);(用手)触，拿，搬动;控制，操纵(车辆、动物、工具等);', 'I turned the handle and found the door was open.', '我转了一下门把手，发现门开着。');
INSERT INTO `words` VALUES (3068, 'handler', 'ˈhændlə(r)', 'n.', '驯兽员;(尤指)驯犬员;搬运工;', 'Fifty officers, including frogmen and dog handlers, are searching for her.', '包括蛙人和警犬训练员在内的50名警官正展开对她的搜寻。');
INSERT INTO `words` VALUES (3069, 'handling', 'ˈhændlɪŋ', 'n.', '(形势、人、动物等的)处理，对付，对待;组织;控制;', 'I turned the handle and found the door was open.', '我转了一下门把手，发现门开着。');
INSERT INTO `words` VALUES (3070, 'handout', 'ˈhændaʊt', 'n.', '捐赠品;救济品;政府拨款(为促进商业活动);', 'Soldiers oversee the food handouts...', '士兵们看管着救济食品。');
INSERT INTO `words` VALUES (3071, 'handsome', 'ˈhænsəm', 'adj.', '英俊的;漂亮的;有魅力的;', '...a tall, dark, handsome sheep farmer.', '高个子、黑皮肤的英俊养羊人');
INSERT INTO `words` VALUES (3072, 'handwriting', 'ˈhændraɪtɪŋ', 'n.', '手写;书写;笔迹;', 'The address was in Anna\'s handwriting...', '地址是安娜的笔迹。');
INSERT INTO `words` VALUES (3073, 'handy', 'ˈhændi', 'adj.', '易使用的;容易做的;便利的;', 'The book gives handy hints on looking after indoor plants...', '这本书提供了一些关于如何照料室内花草的有用信息。');
INSERT INTO `words` VALUES (3074, 'haphazard', 'hæpˈhæzəd', 'adj.', '无秩序的;无计划的;组织混乱的;', 'The investigation does seem haphazard...', '调查工作确实看起来缺乏计划。');
INSERT INTO `words` VALUES (3075, 'happen', 'ˈhæpən', 'v.', '(尤指偶然)发生，出现;(作为结果)出现，发生;碰巧;', 'We cannot say for sure what will happen...', '我们无法确定会发生什么事。');
INSERT INTO `words` VALUES (3076, 'happening', 'ˈhæpənɪŋ', 'n.', '事件;发生的事情(常指不寻常的);即兴艺术表演;', 'The Budapest office plans to hire freelance reporters to cover the latest happenings.', '布达佩斯办事处计划聘用自由撰稿记者来报道最近发生的一些事件。');
INSERT INTO `words` VALUES (3077, 'happily', 'ˈhæpɪli', 'adv.', '快乐地;高兴地;满足地;', 'Happily, his neck injuries were not serious...', '很幸运，他脖子上的伤并不严重。');
INSERT INTO `words` VALUES (3078, 'happiness', 'ˈhæpinəs', 'n.', '幸福;愉快;(用语等的)适当;', 'Marina was a confident, happy child...', '玛丽娜是个自信、快乐的孩子。');
INSERT INTO `words` VALUES (3079, 'happy', 'ˈhæpi', 'adj.', '感到(或显得)快乐的;高兴的;给予(或带来)快乐的;', 'Marina was a confident, happy child...', '玛丽娜是个自信、快乐的孩子。');
INSERT INTO `words` VALUES (3080, 'harass', 'ˈhærəs', 'v.', '侵扰;骚扰;不断攻击(敌人);', 'A woman reporter complained one of them sexually harassed her in the locker room...', '一名女记者投诉说，他们中有一人在更衣室里对她进行性骚扰。');
INSERT INTO `words` VALUES (3081, 'harassment', 'ˈhærəsmənt', 'n.', '骚扰;烦扰;', 'Another survey found that 51 per cent of women had experienced some form of sexual harassment in their working lives.', '另一个调查发现，51%的女性在工作中曾遭遇过某种形式的性骚扰。');
INSERT INTO `words` VALUES (3082, 'harbinger', 'ˈhɑːbɪndʒə(r)', 'n.', '(常指坏的)预兆，兆头;', 'The November air stung my cheeks, a harbinger of winter.', '11月的空气刺痛了我的脸颊，预示着冬天就要来临。');
INSERT INTO `words` VALUES (3083, 'hard', 'hɑːd', 'adj.', '坚固的;坚硬的;结实的;', 'He shuffled his feet on the hard wooden floor...', '他拖着脚在硬木地板上走动。');
INSERT INTO `words` VALUES (3084, 'harden', 'ˈhɑːdn', 'v.', '(使)变硬，硬化;(使声音、面孔等)更严肃，更严厉;(使)更坚定，更强硬;', 'Mould the mixture into shape while hot, before it hardens...', '在混合物还没有变硬之前，趁热将它塑成形。');
INSERT INTO `words` VALUES (3085, 'hardiness', 'ˈhɑːdinəs', 'n.', '耐久力；顽强；抗（耐）性；耐寒性；适应性强;', 'The silver-leaved varieties of cyclamen are not quite as hardy.', '银叶仙客来品种不太耐寒。');
INSERT INTO `words` VALUES (3086, 'hardly', 'ˈhɑːdli', 'adv.', '几乎不;几乎没有;尤用于can或could之后，主要动词之前，强调做某事很难;', 'I hardly know you...', '我不太认识你。');
INSERT INTO `words` VALUES (3087, 'hardness', 'ˈhɑːdnɪs', 'n.', '硬（刚，强）度；（坚）硬性；坚固（牢）；硬度数；刚度指数；核防护能力;困难；难解；苛刻；无情;', 'He shuffled his feet on the hard wooden floor...', '他拖着脚在硬木地板上走动。');
INSERT INTO `words` VALUES (3088, 'hardship', 'ˈhɑːdʃɪp', 'n.', '艰难;困苦;拮据;', 'Many people are suffering economic hardship...', '很多人正处于经济困难之中。');
INSERT INTO `words` VALUES (3089, 'hardy', 'ˈhɑːdi', 'adj.', '能吃苦耐劳的;适应力强的;耐寒的;', 'The silver-leaved varieties of cyclamen are not quite as hardy.', '银叶仙客来品种不太耐寒。');
INSERT INTO `words` VALUES (3090, 'harm', 'hɑːm', 'n.', '伤害;损害;', 'The hijackers seemed anxious not to harm anyone.', '劫持犯似乎焦虑不安，不想伤害任何人。');
INSERT INTO `words` VALUES (3091, 'harmful', 'ˈhɑːmfl', 'adj.', '(尤指对健康或环境)有害的，导致损害的;', '...the harmful effects of smoking...', '吸烟的危害');
INSERT INTO `words` VALUES (3092, 'harmless', 'ˈhɑːmləs', 'adj.', '无害的;不会导致损伤的;不会引起不快的;', 'Industry has been working at developing harmless substitutes for these gases...', '该行业一直在努力开发一些可以替代这些气体的无害物质。');
INSERT INTO `words` VALUES (3093, 'harmonic', 'hɑːˈmɒnɪk', 'adj.', '和声的;', 'I had been looking for ways to combine harmonic and rhythmic structures.', '我过去一直在寻找将和声和节奏结合起来的方法。');
INSERT INTO `words` VALUES (3094, 'harmonious', 'hɑːˈməʊniəs', 'adj.', '友好和睦的;和谐的;协调的;', 'Their harmonious relationship resulted in part from their similar goals...', '他们关系融洽的部分原因是他们有着相似的目标。');
INSERT INTO `words` VALUES (3095, 'harmony', 'ˈhɑːməni', 'n.', '融洽;和睦;和声;', 'We must try to live in peace and harmony with ourselves and those around us...', '我们必须努力和我们自己及周围的人和睦相处。');
INSERT INTO `words` VALUES (3096, 'harness', 'ˈhɑːnɪs', 'n.', '马具;挽具;(用于人，起固定或保护作用的)背带，保护带;', '...the movement\'s ability to harness the anger of all Ukrainians...', '该运动之疏解所有乌克兰人怒气的能力');
INSERT INTO `words` VALUES (3097, 'harsh', 'hɑːʃ', 'adj.', '残酷的;严酷的;严厉的;', 'The weather grew harsh, chilly and unpredictable.', '天气变得恶劣、寒冷且变化莫测。');
INSERT INTO `words` VALUES (3098, 'harvest', 'ˈhɑːvɪst', 'n.', '收获季节;收割;收获;', 'There were about 300 million tons of grain in the fields at the start of the harvest.', '收获伊始地里大概有3亿吨粮食。');
INSERT INTO `words` VALUES (3099, 'haste', 'heɪst', 'n.', '急速;匆忙;仓促;', 'In their haste to escape the rising water, they dropped some expensive equipment...', '他们在河水上涨仓促逃离时，丢掉了一些值钱的装备。');
INSERT INTO `words` VALUES (3100, 'hasten', 'ˈheɪsn', 'v.', '急忙进行;赶紧说(或做);促进;', 'But if he does this, he may hasten the collapse of his own country.', '但是如果他这样做，就有可能加速自己国家的崩溃。');
INSERT INTO `words` VALUES (3101, 'hasty', 'ˈheɪsti', 'adj.', '匆忙的;仓促而就的;草率的;', 'One company is giving its employees airplane tickets in the event they need to make a hasty escape.', '一家公司正在给自己的员工发机票，万一需要就紧急撤离。');
INSERT INTO `words` VALUES (3102, 'hatch', 'hætʃ', 'v.', '孵出;出壳;孵化;', 'As soon as the two chicks hatch, they leave the nest burrow...', '两只小鸟一出壳就离开了巢穴。');
INSERT INTO `words` VALUES (3103, 'hate', 'heɪt', 'v.', '厌恶，讨厌，憎恶(某事物);憎恨，憎恶，仇恨(某人);(表示不愿说某事，或客气地请求)不愿，不想;', 'Most people hate him, but they don\'t dare to say so, because he still rules the country...', '人们大多憎恨他，但不敢说，因为他仍在统治这个国家。');
INSERT INTO `words` VALUES (3104, 'hateful', 'ˈheɪtfl', 'adj.', '可恶的;十分讨厌的;', 'I\'m sorry. That was a hateful thing to say.', '对不起，我不该说那么难听的话。');
INSERT INTO `words` VALUES (3105, 'hatred', 'ˈheɪtrɪd', 'n.', '仇恨;憎恨;厌恶;', 'Her hatred of them would never lead her to murder...', '她虽说仇恨他们，但绝不至于去杀人。');
INSERT INTO `words` VALUES (3106, 'haughty', 'ˈhɔːti', 'adj.', '傲慢的;高傲自大的;', 'He spoke in a haughty tone.', '他说起话来口气傲慢。');
INSERT INTO `words` VALUES (3107, 'haul', 'hɔːl', 'v.', '(用力)拖，拉，拽;用力缓慢挪动到(某处);强迫(某人)去某处;', 'A crane had to be used to haul the car out of the stream...', '只好用了起重机，才将轿车从河里拖出来。');
INSERT INTO `words` VALUES (3108, 'haunt', 'hɔːnt', 'v.', '(鬼魂)出没;(不快的事情)萦绕于脑际，难以忘却;长期不断地缠扰(某人);', 'The decision to leave her children now haunts her...', '抛下孩子们的决定现在总让她不安。');
INSERT INTO `words` VALUES (3109, 'haven', 'ˈheɪvn', 'n.', '安全的地方;保护区;避难所;', 'It\'s a real haven at the end of a busy working day.', '忙碌了一整天后，这真是一个安乐窝。');
INSERT INTO `words` VALUES (3110, 'havoc', 'ˈhævək', 'n.', '灾害;祸患;浩劫;', 'Rioters caused havoc in the centre of the town.', '暴乱分子在市中心制造了极大混乱。');
INSERT INTO `words` VALUES (3111, 'hay', 'heɪ', 'n.', '(用作饲料的)干草，草料;少量的钱;', '...bales of hay.', '一捆捆干草');
INSERT INTO `words` VALUES (3112, 'hazard', 'ˈhæzəd', 'n.', '危险;危害;', 'A new report suggests that chewing-gum may be a health hazard...', '一份新报告指出，口香糖可能对健康有害。');
INSERT INTO `words` VALUES (3113, 'hazardous', 'ˈhæzədəs', 'adj.', '危险的;有害的;', 'They have no way to dispose of the hazardous waste they produce...', '他们没有办法处理掉自己产生的有害废料。');
INSERT INTO `words` VALUES (3114, 'head', 'hed', 'n.', '头;头部;头脑;', 'She turned her head away from him...', '她把头别过去，不再看他。');
INSERT INTO `words` VALUES (3115, 'headache', 'ˈhedeɪk', 'n.', '头痛;令人头痛的人(或事物);麻烦;', 'I have had a terrible headache for the last two days.', '两天来我一直头痛得厉害。');
INSERT INTO `words` VALUES (3116, 'heading', 'ˈhedɪŋ', 'n.', '(页首或章节开头的)标题;(讲话或作品各章节的)主题;', '...helpful chapter headings.', '有用的章节标题');
INSERT INTO `words` VALUES (3117, 'headline', 'ˈhedlaɪn', 'n.', '(报纸的)大字标题;(电视或无线电广播的)新闻摘要;', 'The Daily Mail has the headline \'The Voice of Conscience\'...', '《每日邮报》的头版标题为“良知的声音”。');
INSERT INTO `words` VALUES (3118, 'headlong', 'ˈhedlɒŋ', 'adv.', '头朝前;轻率地;莽撞地;', 'He ran headlong for the open door.', '他飞快地朝敞开着的门跑去。');
INSERT INTO `words` VALUES (3119, 'headquarters', 'ˌhedˈkwɔːtəz', 'n.', '总部;总公司;大本营;', '...fraud squad officers from London\'s police headquarters...', '伦敦警察总部诈骗案调查组的警官们');
INSERT INTO `words` VALUES (3120, 'headstrong', 'ˈhedstrɒŋ', 'adj.', '固执的;倔强任性的;', 'He\'s young, very headstrong, but he\'s a good man underneath.', '他年青、任性，但骨子里是个好人。');
INSERT INTO `words` VALUES (3121, 'heal', 'hiːl', 'v.', '(使)康复，复原;治愈(病人);使又愉快起来;', 'Within six weeks the bruising had gone, but it was six months before it all healed...', '青瘀在6周内就消退了，但伤却是6个月后才全好了。');
INSERT INTO `words` VALUES (3122, 'health', 'helθ', 'n.', '人的身体(或精神)状况;健康;医疗;', 'Caffeine is bad for your health.', '咖啡因对人体健康有害。');
INSERT INTO `words` VALUES (3123, 'healthy', 'ˈhelθi', 'adj.', '健康的;健壮的;有益于健康的;', 'Most of us need to lead more balanced lives to be healthy and happy...', '我们中大多数人需要过更为平衡的生活，以保持健康和快乐。');
INSERT INTO `words` VALUES (3124, 'heap', 'hiːp', 'n.', '(凌乱的)一堆;许多;大量;', '...a heap of bricks...', '一堆砖');
INSERT INTO `words` VALUES (3125, 'heaps', 'hiːps', 'n.', '(凌乱的)一堆;许多;大量;', '...a heap of bricks...', '一堆砖');
INSERT INTO `words` VALUES (3126, 'hear', 'hɪə(r)', 'v.', '听见;听到;听;', 'She heard no further sounds...', '她再没有听到别的声音了。');
INSERT INTO `words` VALUES (3127, 'hearing', 'ˈhɪərɪŋ', 'n.', '听力;听觉;审讯;', 'His mind still seemed clear and his hearing was excellent.', '他的头脑似乎还清楚，听觉也很好。');
INSERT INTO `words` VALUES (3128, 'heart', 'hɑːt', 'n.', '心;心脏;胸部心脏的部位;', 'The bullet had passed less than an inch from Andrea\'s heart...', '子弹在离安德烈亚的心脏不到一英寸的地方穿过。');
INSERT INTO `words` VALUES (3129, 'heartbeat', 'ˈhɑːtbiːt', 'n.', '心跳;心搏;心跳声;', 'Your baby\'s heartbeat will be monitored continuously.', '宝宝的心搏将会受到持续监控。');
INSERT INTO `words` VALUES (3130, 'heartfelt', 'ˈhɑːtfelt', 'adj.', '衷心的;真诚的;', 'My heartfelt sympathy goes out to all the relatives.', '我对所有的亲人表示由衷的慰问。');
INSERT INTO `words` VALUES (3131, 'hearth', 'hɑːθ', 'n.', '壁炉炉床;壁炉前的地面;家和家庭生活;', 'It was winter and there was a huge fire roaring in the hearth.', '时值冬天，壁炉里炉火熊熊。');
INSERT INTO `words` VALUES (3132, 'heartily', 'ˈhɑːtɪli', 'adv.', '尽情地;关怀地;劲头十足地;', 'Wade was a hearty, bluff, athletic sort of guy...', '韦德是个精力充沛、率直、运动型的人。');
INSERT INTO `words` VALUES (3133, 'hearty', 'ˈhɑːti', 'adj.', '亲切的;友好的;喧闹而活泼的;', 'Wade was a hearty, bluff, athletic sort of guy...', '韦德是个精力充沛、率直、运动型的人。');
INSERT INTO `words` VALUES (3134, 'heat', 'hiːt', 'n.', '热;温度;炎热天气;', 'Meanwhile, heat the tomatoes and oil in a pan.', '同时，在平底锅里将西红柿和油加热。');
INSERT INTO `words` VALUES (3135, 'heated', 'ˈhiːtɪd', 'adj.', '愤怒的;激烈的;十分激动的;', 'It was a very heated argument and they were shouting at each other...', '争论非常激烈，他们相互之间大喊大叫。');
INSERT INTO `words` VALUES (3136, 'heater', 'ˈhiːtə(r)', 'n.', '加热器;炉子;热水器;', 'There\'s an electric heater in the bedroom.', '卧室里有个电暖器。');
INSERT INTO `words` VALUES (3137, 'heating', 'ˈhiːtɪŋ', 'n.', '供暖;供暖系统;暖气设备;', 'You can still find cottages for £150 a week, including heating.', '你仍可以找到包括供暖在内每周租金150英镑的度假别墅。');
INSERT INTO `words` VALUES (3138, 'heaven', 'ˈhevn', 'n.', '天堂;天国;极乐之地;', 'I believed that when I died I would go to heaven and see God.', '我相信自己死后会升入天堂，见到上帝。');
INSERT INTO `words` VALUES (3139, 'heavenly', 'ˈhevnli', 'adj.', '天国的;天堂的;天空的;', '...heavenly beings whose function it is to serve God.', '侍奉上帝的天使');
INSERT INTO `words` VALUES (3140, 'heavily', 'ˈhevɪli', 'adv.', '在很大程度上;大量地;以猛力;', '\'I didn\'t even think about her,\' he said heavily.', '“我甚至没有想过她，”他沉重地说道。');
INSERT INTO `words` VALUES (3141, 'heaviness', 'ˈhevinəs', 'n.', '沉重；沉闷；笨拙；迟钝；忧伤;', 'These scissors are awfully heavy...', '这把剪刀太沉了。');
INSERT INTO `words` VALUES (3142, 'heavy', 'ˈhevi', 'adj.', '重的;沉的;(在数量、程度等方面)超出一般的，比一般严重的;', 'These scissors are awfully heavy...', '这把剪刀太沉了。');
INSERT INTO `words` VALUES (3143, 'hectic', 'ˈhektɪk', 'adj.', '忙碌的;繁忙的;', 'Despite his hectic work schedule, Benny has rarely suffered poor health...', '尽管工作繁忙，本尼却少有身体不好的时候。');
INSERT INTO `words` VALUES (3144, 'heed', 'hiːd', 'v.', '留心，注意，听从(劝告或警告);', 'But few at the conference in London last week heeded his warning...', '但在上周于伦敦召开的大会上，几乎没有人留意他的警告。');
INSERT INTO `words` VALUES (3145, 'height', 'haɪt', 'n.', '(人或物的)身高;高;高度;', 'Her weight is about normal for her height...', '按她的身高，她的体重基本正常。');
INSERT INTO `words` VALUES (3146, 'heighten', 'ˈhaɪtn', 'v.', '(使)加强，提高，增加;', 'The move has heightened tension in the state...', '该举动加剧了州里的紧张气氛。');
INSERT INTO `words` VALUES (3147, 'heir', 'eə(r)', 'n.', '继承人;后嗣;(工作或传统的)继承者，承袭者，传人;', 'His heir, Lord Doune, cuts a bit of a dash in the city.', '他的继承人杜恩勋爵在城里出了一番风头。');
INSERT INTO `words` VALUES (3148, 'heiress', 'eəˈres', 'n.', '女继承人;嗣女;', '...the heiress to a jewellery empire.', '珠宝帝国的女继承人');
INSERT INTO `words` VALUES (3149, 'hello', 'həˈləʊ', 'int./n.', '(用于问候、接电话或引起注意)哈罗，喂，你好;(表示惊讶)嘿;(认为别人说了蠢话或没有注意听)喂，嘿;', 'Hello, Trish...', '你好，特里茜。');
INSERT INTO `words` VALUES (3150, 'help', 'help', 'v.', '帮助;协助;援助;', 'He has helped to raise a lot of money...', '他帮着筹集了很多钱。');
INSERT INTO `words` VALUES (3151, 'helpful', 'ˈhelpfl', 'adj.', '有用的;有益的;有帮助的;', 'The staff in the London office are helpful but only have limited information...', '伦敦办事处的工作人员给予了帮助，但他们掌握的信息有限。');
INSERT INTO `words` VALUES (3152, 'helpless', 'ˈhelpləs', 'adj.', '无自理能力的;不能自立的;无助的;', 'Parents often feel helpless, knowing that all the cuddles in the world won\'t stop the tears...', '父母经常感到无能为力，因为他们知道无论多少次拥抱也无法止住眼泪。');
INSERT INTO `words` VALUES (3153, 'hemisphere', 'ˈhemɪsfɪə(r)', 'n.', '(地球的)半球;(尤指)北半球，南半球;(大脑的)半球;', '...the northern hemisphere.', '北半球');
INSERT INTO `words` VALUES (3154, 'hence', 'hens', 'adv.', '因此;由此;', 'The trade imbalance is likely to rise again in 1990. Hence a new set of policy actions will be required soon...', '贸易失衡可能会在1990年再次加剧。因此需要很快采取一系列新的政策行动。');
INSERT INTO `words` VALUES (3155, 'henceforth', 'ˌhensˈfɔːθ', 'adv.', '此后;从此以后;', 'Henceforth, parties which fail to get 5% of the vote will not be represented in parliament...', '从今以后，选票数量未达到总数5%的党派将不能在议会里占有席位。');
INSERT INTO `words` VALUES (3156, 'her', 'hə(r)', 'pron.', '(用作动词或介词的宾语，或作表语)她;', 'I went in the room and told her I had something to say to her...', '我走进房间，告诉她我有话要和她说。');
INSERT INTO `words` VALUES (3157, 'herald', 'ˈherəld', 'v.', '是(某事)的前兆;预示;宣布(好或重要);', '...the sultry evening that heralded the end of the baking hot summer...', '预示着炙热的夏天即将结束的闷热夜晚');
INSERT INTO `words` VALUES (3158, 'herbal', 'ˈhɜːbl', 'adj.', '药草的;香草的;', '...herbal teas.', '药草茶');
INSERT INTO `words` VALUES (3159, 'herd', 'hɜːd', 'n.', '兽群;牧群;人群;', '...large herds of elephant and buffalo.', '大群的大象和水牛');
INSERT INTO `words` VALUES (3160, 'here', 'hɪə(r)', 'adv.', '(用于动词或介词之后)在这里，向这里;现在;在这一点上;', 'I\'m here all by myself and I know I\'m going to get lost...', '这里只有我一个人，我知道我会迷路的。');
INSERT INTO `words` VALUES (3161, 'hereafter', 'ˌhɪərˈɑːftə(r)', 'adv.', '在本文件其余部分;以下;此后;', 'I realised how hard life was going to be for me hereafter.', '我意识到从今以后我的生活将会多么艰难。');
INSERT INTO `words` VALUES (3162, 'hereby', 'ˌhɪəˈbaɪ', 'adv.', '特此;以此;', 'I hereby sentence you for life after all the charges against you have been proven true...', '鉴于对你的所有指控都证明属实，我特此宣判你无期徒刑。');
INSERT INTO `words` VALUES (3163, 'hereditary', 'həˈredɪtri', 'adj.', '遗传的;遗传性的;世袭的;', 'Cystic fibrosis is the commonest fatal hereditary disease...', '囊性纤维化是最常见的致命遗传疾病。');
INSERT INTO `words` VALUES (3164, 'herein', 'ˌhɪərˈɪn', 'adv.', '在此处;于此文件(或声明、事实)中;', 'The statements and views expressed herein are those of the author and are not necessarily those of the Wilson Centre...', '本书中的陈述和观点为作者个人意见，不一定代表威尔逊中心的看法。');
INSERT INTO `words` VALUES (3165, 'herewith', 'ˌhɪəˈwɪð', 'adv.', '随同此信(或书、文件);', '...the 236 revolutionary prisoners whose names are listed herewith...', '236名革命囚犯，名单附在本材料中');
INSERT INTO `words` VALUES (3166, 'heritage', 'ˈherɪtɪdʒ', 'n.', '遗产(指国家或社会长期形成的历史、传统和特色);', 'The historic building is as much part of our heritage as the paintings.', '这座历史建筑和这些画一样，都是留给我们的文化遗产的一部分。');
INSERT INTO `words` VALUES (3167, 'hero', 'ˈhɪərəʊ', 'n.', '英雄，豪杰(尤指男性);(故事、小说、电影等的)男主人公，男主角;崇拜的对象，偶像(尤指男性);', 'The hero of Doctor Zhivago dies in 1929.', '《日瓦戈医生》的男主角于1929年去世。');
INSERT INTO `words` VALUES (3168, 'heroic', 'həˈrəʊɪk', 'adj.', '英勇的;英雄的;有必胜决心的;', 'The heroic sergeant risked his life to rescue 29 fishermen from their blazing trawler...', '英勇的中士冒着生命危险从熊熊燃烧着的拖网渔船上救出了29名渔民。');
INSERT INTO `words` VALUES (3169, 'heroine', 'ˈherəʊɪn', 'n.', '女英雄;女豪杰;(故事、小说、电影等中的)女主人公，女主角;', 'The heroine is a senior TV executive.', '女主角是一名电视台的高级主管。');
INSERT INTO `words` VALUES (3170, 'hesitant', 'ˈhezɪtənt', 'adj.', '犹豫的;踌躇的;不情愿的;', 'She was hesitant about coming forward with her story...', '她不大愿意站出来讲述自己的经历。');
INSERT INTO `words` VALUES (3171, 'hesitantly', '', 'adv.', '迟疑地；踌躇地;', 'She was hesitant about coming forward with her story...', '她不大愿意站出来讲述自己的经历。');
INSERT INTO `words` VALUES (3172, 'hesitate', 'ˈhezɪteɪt', 'v.', '(对某事)犹豫，迟疑不决;顾虑;疑虑;', 'The telephone rang. Catherine hesitated, debating whether to answer it...', '电话响了。凯瑟琳犹豫了一下要不要去接。');
INSERT INTO `words` VALUES (3173, 'hesitation', 'ˌhezɪˈteɪʃn', 'n.', '犹豫;疑虑;不情愿;', 'He promised there would be no more hesitations in pursuing reforms.', '他答应在推进改革这件事上不再迟疑不决。');
INSERT INTO `words` VALUES (3174, 'heterogeneous', 'ˌhetərəˈdʒiːniəs', 'adj.', '由很多种类组成的;各种各样的;', '...a rather heterogeneous collection of studies from diverse origins.', '一组来源各异、内容丰富多样的研究');
INSERT INTO `words` VALUES (3175, 'hey', 'heɪ', 'int.', '(用以引起注意或表示兴趣、惊讶或生气)嘿，喂;(表示不真正在意或认为不重要)嘿;(用于陈述句末尾，表示话已说完，或构成问题或请求回答)就这样，怎么样，你说呢;', '\'Hey! Look out!\' shouted Patty...', '“嘿！小心点！”帕蒂喊道。');
INSERT INTO `words` VALUES (3176, 'hi', 'haɪ', 'int.', '(用于打招呼)喂，嗨;', '\'Hi, Liz,\' she said shyly.', '“你好，利兹，”她腼腆地说。');
INSERT INTO `words` VALUES (3177, 'hide', 'haɪd', 'v.', '藏;隐蔽;躲避;', 'He hid the bicycle in the hawthorn hedge...', '他把自行车藏在山楂树篱中。');
INSERT INTO `words` VALUES (3178, 'hideous', 'ˈhɪdiəs', 'adj.', '十分丑陋的;令人厌恶的;', 'She saw a hideous face at the window and screamed.', '她在窗口看到了一张极丑陋的脸，吓得尖叫了起来。');
INSERT INTO `words` VALUES (3179, 'hierarchical', 'ˌhaɪəˈrɑːkɪkl', 'adj.', '按等级划分的;等级制度的;', '...the traditional hierarchical system of military organization.', '军事组织内传统的等级制度');
INSERT INTO `words` VALUES (3180, 'hierarchy', 'ˈhaɪərɑːki', 'n.', '等级制度(尤指社会或组织);统治集团;层次体系;', 'Like most other American companies with a rigid hierarchy, workers and managers had strictly defined duties...', '和其他多数拥有严格等级制度的美国公司一样，这里的工人和管理人员都有界定严格的职责。');
INSERT INTO `words` VALUES (3181, 'high', 'haɪ', 'adj.', '高的;有某高度的;(离地面)很高的;', '...a house, with a high wall all around it...', '四周围着高墙的房子');
INSERT INTO `words` VALUES (3182, 'highlight', 'ˈhaɪlaɪt', 'v.', '突出;强调;将(文本的某部分)用彩笔做标记;', 'Last year Collins wrote a moving ballad which highlighted the plight of the homeless...', '去年，柯林斯写了一首动人的抒情歌曲，突出描绘了无家可归者的苦境。');
INSERT INTO `words` VALUES (3183, 'highlighting', 'ˈhaɪlaɪtɪŋ', 'v.', '突出;强调;将(文本的某部分)用彩笔做标记;', 'Last year Collins wrote a moving ballad which highlighted the plight of the homeless...', '去年，柯林斯写了一首动人的抒情歌曲，突出描绘了无家可归者的苦境。');
INSERT INTO `words` VALUES (3184, 'highly', 'ˈhaɪli', 'adv.', '很;非常;高标准地;', 'Mr Singh was a highly successful salesman...', '辛格先生是个非常成功的推销员。');
INSERT INTO `words` VALUES (3185, 'highway', 'ˈhaɪweɪ', 'n.', '(尤指城镇间的)公路，干道，交通要道;公用通道;', 'I crossed the highway, dodging the traffic.', '我避开车流穿过了公路。');
INSERT INTO `words` VALUES (3186, 'hijack', 'ˈhaɪdʒæk', 'v.', '劫持(交通工具，尤指飞机);操纵(会议等，以推销自己的意图);', 'Two men tried to hijack a plane on a flight from Riga to Murmansk...', '两名男子试图劫持一架从里加飞往摩尔曼斯克的飞机。');
INSERT INTO `words` VALUES (3187, 'hilarious', 'hɪˈleəriəs', 'adj.', '极其滑稽的;', 'We thought it was hilarious when we first heard about it...', '我们第一次听说此事时，觉得很好笑。');
INSERT INTO `words` VALUES (3188, 'hill', 'hɪl', 'n.', '山丘;小山;(道路的)斜坡;', 'We trudged up the hill to the stadium.', '我们费力地爬到山上的体育场。');
INSERT INTO `words` VALUES (3189, 'hind', 'haɪnd', 'adj.', '(四足动物的腿、蹄)后面的;', 'Suddenly the cow kicked up its hind legs.', '奶牛突然尥起了蹶子。');
INSERT INTO `words` VALUES (3190, 'hinder', 'ˈhɪndə(r)', 'v.', '阻碍;妨碍;阻挡;', 'Does the fact that your players are part-timers help or hinder you?...', '队员们都是兼职的，这对你有利还是不利？');
INSERT INTO `words` VALUES (3191, 'hindrance', 'ˈhɪndrəns', 'n.', '造成妨碍的人(或事物);妨碍;阻挠;', 'The higher rates have been a hindrance to economic recovery...', '高利率已经成为经济复苏的障碍。');
INSERT INTO `words` VALUES (3192, 'hinge', 'hɪndʒ', 'n.', '铰链;合叶;', 'The top swung open on well-oiled hinges.', '用润滑良好的铰链固定的盖子一旋转就打开了。');
INSERT INTO `words` VALUES (3193, 'hint', 'hɪnt', 'n.', '暗示;提示;示意;', 'The Minister gave a strong hint that the government were thinking of introducing tax concessions for mothers...', '部长明显地暗示政府正在考虑对母亲们实行税收减免。');
INSERT INTO `words` VALUES (3194, 'hinterland', 'ˈhɪntəlænd', 'n.', '内陆;腹地;内地;', '...the French Mediterranean coast and its hinterland.', '法国地中海沿岸及其腹地');
INSERT INTO `words` VALUES (3195, 'hip', 'hɪp', 'n.', '臀部;髋;臀部…的;', 'Tracey put her hands on her hips and sighed.', '特蕾西双手叉腰叹了口气。');
INSERT INTO `words` VALUES (3196, 'hire', 'ˈhaɪə(r)', 'v.', '租用;租借;聘用;', 'Sixteen of the contestants have hired lawyers and are suing the organisers...', '有16名参赛者已经请了律师，正在起诉主办方。');
INSERT INTO `words` VALUES (3197, 'hiss', 'hɪs', 'v.', '发嘶嘶声;发嘘声(表示不满，尤指对演员或演讲人);带怒气地低声说出(某事);', 'The tires of Lenny\'s bike hissed over the wet pavement as he slowed down...', '伦尼放缓车速，听到自行车轮胎在湿漉漉的路面上发出嘶嘶的响声。');
INSERT INTO `words` VALUES (3198, 'historic', 'hɪˈstɒrɪk', 'adj.', '历史上著名(或重要)的;可名垂青史的;有史时期的;', '...the historic changes in Eastern Europe.', '东欧的历史性变革');
INSERT INTO `words` VALUES (3199, 'historical', 'hɪˈstɒrɪkl', 'adj.', '(有关)历史的;有关历史研究的;历史学的;', '...an important historical figure.', '重要的历史人物');
INSERT INTO `words` VALUES (3200, 'history', 'ˈhɪstri', 'n.', '历史(指过去发生的所有事情);(有关某个地方、主题等的)发展史，历史;历史课;', 'The Catholic Church has played a prominent role throughout Polish history.', '天主教会在整个波兰历史上曾发挥过举足轻重的作用。');
INSERT INTO `words` VALUES (3201, 'hitchhike', 'ˈhɪtʃhaɪk', 'v.', '免费搭便车;搭顺风车;', 'Neff hitchhiked to New York during his Christmas vacation...', '内夫在圣诞节假期里搭顺风车去了纽约。');
INSERT INTO `words` VALUES (3202, 'hitherto', 'ˌhɪðəˈtuː', 'adv.', '迄今;直到某时;', 'The polytechnics have hitherto been at an unfair disadvantage in competing for pupils and money...', '到目前为止，理工专科学校在争取生源和资金方面一直处于劣势。');
INSERT INTO `words` VALUES (3203, 'hoarse', 'hɔːs', 'adj.', '嘶哑的;沙哑的;', '\'So what do you think?\' she said in a hoarse whisper...', '“那么你认为如何？”她声音嘶哑地悄声问道。');
INSERT INTO `words` VALUES (3204, 'hobby', 'ˈhɒbi', 'n.', '业余爱好;', 'My hobbies are letter writing, football, music, photography, and tennis.', '我的业余爱好是写信、踢足球、听音乐、玩摄影和打网球。');
INSERT INTO `words` VALUES (3205, 'hoist', 'hɔɪst', 'v.', '吊起;提升;拉高;', 'Hoisting my suitcase on to my shoulder, I turned and headed toward my hotel...', '我把手提箱扛到肩上，转身向入住的旅馆走去。');
INSERT INTO `words` VALUES (3206, 'holder', 'ˈhəʊldə(r)', 'n.', '持有者;拥有者;支托(或握持)…之物;', 'This season the club has had 73,500 season-ticket holders.', '本赛季该俱乐部已经有73,500名持有季票的观众。');
INSERT INTO `words` VALUES (3207, 'holding', 'ˈhəʊldɪŋ', 'n.', '股份;私有财产;(博物馆、图书馆等的)馆藏;', 'That would increase Olympia & York\'s holding to 35%...', '那样就会使奥林匹亚约克公司的股份上升到35%。');
INSERT INTO `words` VALUES (3208, 'hole', 'həʊl', 'n.', '洞;孔;坑;', 'He took a shovel, dug a hole, and buried his once-prized possessions...', '他拿起一把铁锨，挖了个坑，将自己曾经珍爱的物品埋了起来。');
INSERT INTO `words` VALUES (3209, 'holiday', 'ˈhɒlədeɪ', 'n.', '假期;度假期;假日，节日(尤指宗教节日或国家庆典日);', 'I\'ve just come back from a holiday in the United States...', '我刚从美国度假回来。');
INSERT INTO `words` VALUES (3210, 'hollow', 'ˈhɒləʊ', 'adj.', '中空的;空心的;凹陷的;', '...a hollow tree.', '空心的树');
INSERT INTO `words` VALUES (3211, 'holocaust', 'ˈhɒləkɔːst', 'n.', '(尤指战争或火灾引起的)大灾难，大毁灭;(20世纪30年代和40年代纳粹对数百万犹太人的)大屠杀;', 'A nuclear holocaust seemed a very real possibility in the \'50s.', '核灾难在20世纪50年代似乎真的非常可能发生。');
INSERT INTO `words` VALUES (3212, 'holy', 'ˈhəʊli', 'adj.', '与神(或宗教)有关的;神圣的;圣洁的;', 'To them, as to all Christians, this is a holy place.', '对他们以及所有基督徒来说，这里是一处圣地。');
INSERT INTO `words` VALUES (3213, 'homeland', 'ˈhəʊmlænd', 'n.', '祖国;家乡;(南非共和国过去在种族隔离制度下设立的、有一定自治权的)黑人定居地;', 'Many are planning to return to their homeland.', '很多人正计划回到自己的祖国去。');
INSERT INTO `words` VALUES (3214, 'homeless', 'ˈhəʊmləs', 'adj.', '无家的;无家可归的人们;', '...the growing number of homeless families...', '越来越多的居无定所家庭');
INSERT INTO `words` VALUES (3215, 'homely', 'ˈhəʊmli', 'adj.', '在家一样舒适的;犹如在自家一样;简单且好的;', 'I wanted a homely room but I wanted it to look smart, too...', '我想要一个像家一样舒适的房间，但也希望它看起来很漂亮。');
INSERT INTO `words` VALUES (3216, 'homemade', 'ˈhəʊmˈmeɪd', 'adj.', '国产的；自制的;', 'The bread, pastry and mayonnaise are home-made...', '面包、油酥点心和蛋黄酱都是家里做的。');
INSERT INTO `words` VALUES (3217, 'homesick', 'ˈhəʊmsɪk', 'adj.', '思乡的;想家的;患怀乡病的;', 'She\'s feeling a little homesick.', '她有点想家。');
INSERT INTO `words` VALUES (3218, 'hometown', 'ˈhəʊmtaʊn', 'n.', '家乡;故乡;', 'I went to work as a painter in my hometown, Natrona Heights, Pennsylvania.', '我在家乡宾夕法尼亚州的纳特罗纳海茨当了个油漆匠。');
INSERT INTO `words` VALUES (3219, 'homework', 'ˈhəʊmwɜːk', 'n.', '(学生的)家庭作业;准备工作;', 'Have you done your homework, Gemma?', '杰玛，你做完家庭作业了吗？');
INSERT INTO `words` VALUES (3220, 'homogeneous', 'ˌhɒməˈdʒiːniəs', 'adj.', '由相同(或同类型)事物(或人)组成的;同种类的;', 'The unemployed are not a homogeneous group...', '失业者不能一概而论。');
INSERT INTO `words` VALUES (3221, 'honest', 'ˈɒnɪst', 'adj.', '诚实的;老实的;正直的;', 'My dad was the most honest man I ever met...', '爸爸是我所见过的最正直的人。');
INSERT INTO `words` VALUES (3222, 'honesty', 'ˈɒnəsti', 'n.', '诚实;老实;正直;', 'They said the greatest virtues in a politician were integrity, correctness and honesty.', '他们说政治家身上最重要的美德就是正直、得体和诚实。');
INSERT INTO `words` VALUES (3223, 'hook', 'hʊk', 'n.', '钩;钓钩;挂钩;', 'One of his jackets hung from a hook.', '他的一件夹克衫挂在衣钩上。');
INSERT INTO `words` VALUES (3224, 'hop', 'hɒp', 'v.', '单脚跳行;齐足(或双足)跳行;突然快速去某处;', 'I hopped down three steps...', '我单脚跳下了3级台阶。');
INSERT INTO `words` VALUES (3225, 'hope', 'həʊp', 'v.', '希望，期望(某事发生);', 'She had decided she must go on as usual, follow her normal routine, and hope and pray...', '她打定主意必须像平时一样生活，遵循自己通常的作息规律，同时怀抱希望、虔诚祈祷。');
INSERT INTO `words` VALUES (3226, 'hopeful', 'ˈhəʊpfl', 'adj.', '抱有希望;满怀希望;表现出希望的;', 'I am hopeful this misunderstanding will be rectified very quickly...', '我相信这个误会将很快得到纠正。');
INSERT INTO `words` VALUES (3227, 'hopefully', 'ˈhəʊpfəli', 'adv.', '有希望地;可以指望;抱有希望地;', 'Both of them have been through an awful lot and hopefully now I can help them rebuild their lives...', '他们两人都曾历经磨难，但愿现在我能帮助他们重建正常的生活。');
INSERT INTO `words` VALUES (3228, 'hopeless', 'ˈhəʊpləs', 'adj.', '没有好转(或成功)希望的;无望的;极差的;', 'He had not heard her cry before in this uncontrolled, hopeless way...', '他从未听到过她如此绝望地失声痛哭。');
INSERT INTO `words` VALUES (3229, 'hopes', 'həʊps', 'v.', '希望，期望(某事发生);', 'She had decided she must go on as usual, follow her normal routine, and hope and pray...', '她打定主意必须像平时一样生活，遵循自己通常的作息规律，同时怀抱希望、虔诚祈祷。');
INSERT INTO `words` VALUES (3230, 'horizon', 'həˈraɪzn', 'n.', '地平线;(欲望、知识或兴趣的)范围，眼界;', 'A grey smudge appeared on the horizon. That must be Calais, thought Fay...', '一个模糊的灰影出现在地平线上。那里一定是加来，法伊心想。');
INSERT INTO `words` VALUES (3231, 'horizontal', 'ˌhɒrɪˈzɒntl', 'adj.', '水平的;与地面平行的;横的;', 'The board consists of vertical and horizontal lines...', '板上画着横纵的线条。');
INSERT INTO `words` VALUES (3232, 'horizontally', 'ˌhɒrɪˈzɒntəli', 'adv.', '水平地；横地;', 'The board consists of vertical and horizontal lines...', '板上画着横纵的线条。');
INSERT INTO `words` VALUES (3233, 'horn', 'hɔːn', 'n.', '(羊、牛等动物的)角;角质;(乐器)号;', 'He sounded the car horn.', '他按响了汽车喇叭。');
INSERT INTO `words` VALUES (3234, 'horrible', 'ˈhɒrəbl', 'adj.', '极坏的;十分讨厌的;可恶的;', 'The record sounds horrible.', '唱片难听极了。');
INSERT INTO `words` VALUES (3235, 'horrify', 'ˈhɒrɪfaɪ', 'v.', '惊吓;使厌恶;恐吓;', 'His family were horrified by the change.', '他的家人对这一变化感到震惊。');
INSERT INTO `words` VALUES (3236, 'horror', 'ˈhɒrə(r)', 'n.', '震惊;恐惧;厌恶;', 'I felt numb with horror...', '我惊呆了。');
INSERT INTO `words` VALUES (3237, 'horse', 'hɔːs', 'n.', '马;赛马;', 'A small man on a grey horse had appeared.', '一个骑着灰马的小个子男人出现了。');
INSERT INTO `words` VALUES (3238, 'horsepower', 'ˈhɔːspaʊə(r)', 'n.', '马力(功率单位);', 'The engine has more than 4,000 horsepower.', '这台发动机马力超过4,000。');
INSERT INTO `words` VALUES (3239, 'hose', 'həʊz', 'n.', '(灭火、浇花等用的)橡皮管，塑料管，水龙带;(旧时的)男式紧身裤;', 'You\'ve left the garden hose on...', '你忘了关花园里的水管了。');
INSERT INTO `words` VALUES (3240, 'hospitable', 'hɒˈspɪtəbl', 'adj.', '好客的;热情友好的;殷勤的;', 'The locals are hospitable and welcoming...', '当地人热情好客。');
INSERT INTO `words` VALUES (3241, 'hospital', 'ˈhɒspɪtl', 'n.', '医院;', '...a children\'s hospital with 120 beds...', '拥有120张病床的儿童医院');
INSERT INTO `words` VALUES (3242, 'hospitality', 'ˌhɒspɪˈtæləti', 'n.', '好客;殷勤;(款待客人、顾客等的)食物，饮料，服务;', 'Every visitor to Georgia is overwhelmed by the kindness, charm and hospitality of the people.', '来到佐治亚州的每位游客都被当地人的善良、可爱和热情好客所深深打动。');
INSERT INTO `words` VALUES (3243, 'host', 'həʊst', 'n.', '主人;东道主;主办国(或城市、机构);', 'Apart from my host, I didn\'t know a single person there...', '除了主人外，那里的人我一个也不认识。');
INSERT INTO `words` VALUES (3244, 'hostage', 'ˈhɒstɪdʒ', 'n.', '人质;', 'It is hopeful that two hostages will be freed in the next few days.', '有两名人质可望在几天后获释。');
INSERT INTO `words` VALUES (3245, 'hostess', 'ˈhəʊstəs', 'n.', '女主人;女房东;(夜总会的)女招待;', 'The hostess introduced them...', '女主人介绍了他们。');
INSERT INTO `words` VALUES (3246, 'hostile', 'ˈhɒstaɪl', 'adj.', '敌意的;敌对的;坚决否定;', 'Many people felt he would be hostile to the idea of foreign intervention...', '很多人觉得他会反对外国干预的主意。');
INSERT INTO `words` VALUES (3247, 'hot', 'hɒt', 'adj.', '温度高的;热的;觉得闷(或燥、湿)热;', 'When the oil is hot, add the sliced onion...', '油热了后，放入切好的洋葱。');
INSERT INTO `words` VALUES (3248, 'hound', 'haʊnd', 'n.', '猎犬;猎狗;', 'Rainey\'s chief interest in life is hunting with hounds.', '雷尼生活的最大爱好就是带着猎狗去打猎。');
INSERT INTO `words` VALUES (3249, 'hour', 'ˈaʊə(r)', 'n.', '小时;约一小时的时间;(工作、办公等的)固定时间;', 'They waited for about two hours...', '他们等了约两小时。');
INSERT INTO `words` VALUES (3250, 'hourly', 'ˈaʊəli', 'adj.', '每小时(一次)的;(工资、酬金、费用等)按钟点计算的，论小时的;', 'He flipped on the radio to get the hourly news broadcast.', '他打开收音机收听整点新闻。');
INSERT INTO `words` VALUES (3251, 'household', 'ˈhaʊshəʊld', 'n.', '一家人;家庭;同住一所房子的人;', '...growing up in a male-only household...', '在一个只有男性的家庭里长大');
INSERT INTO `words` VALUES (3252, 'housekeeping', 'ˈhaʊskiːpɪŋ', 'n.', '家务(尤指采购和管理开支);(旅馆、医院、写字楼等的)总务处，后勤部;家务开支;', 'I thought that cooking and housekeeping were unimportant, easy tasks.', '我认为做饭和料理家务微不足道，一下子就能搞定。');
INSERT INTO `words` VALUES (3253, 'housewife', 'ˈhaʊswaɪf', 'n.', '主妇;家庭妇女;', '...a 42-year-old housewife from Brisbane.', '来自布里斯班的42岁家庭主妇');
INSERT INTO `words` VALUES (3254, 'housing', 'ˈhaʊzɪŋ', 'n.', '(统称，尤指住房类型、价格、条件)住房，住宅;住房供给;(机器的)外壳，套;', '...a shortage of affordable housing...', '保障房的短缺');
INSERT INTO `words` VALUES (3255, 'hover', 'ˈhɒvə(r)', 'v.', '翱翔;盘旋;踌躇，彷徨(尤指在某人身边);', 'Beautiful butterflies hovered above the wild flowers...', '美丽的蝴蝶在野花上方飞舞。');
INSERT INTO `words` VALUES (3256, 'however', 'haʊˈevə(r)', 'adv.', '(与形容词或副词连用)无论到什么程度，不管多么;不管怎样;无论如何;', 'This was not an easy decision. It is, however, a decision that we feel is dictated by our duty...', '这很难抉择。不过，我们觉得自己有义务作出决定。');
INSERT INTO `words` VALUES (3257, 'howl', 'haʊl', 'v.', '长嚎;嗥叫;(因疼痛、愤怒、开心等)大声叫喊;', 'Somewhere in the streets beyond a dog suddenly howled, baying at the moon.', '远处的街上，有只狗突然对月长嚎起来。');
INSERT INTO `words` VALUES (3258, 'hub', 'hʌb', 'n.', '(某地或活动的)中心，核心;轮毂;', 'The island\'s social hub is the Cafe Sport...', '该岛的社交中心是“体育咖啡馆”。');
INSERT INTO `words` VALUES (3259, 'hubbub', 'ˈhʌbʌb', 'n.', '喧闹声;嘈杂声;喧闹;', 'There was a hubbub of excited conversation from over a thousand people.', '有一千多人在兴高采烈地大声谈话,场面无比喧闹。');
INSERT INTO `words` VALUES (3260, 'huckster', 'ˈhʌkstə(r)', 'n.', '强行推销的人;沿街叫卖的小贩;上门推销员;', 'A huckster offered to sell Carnegie the formula for guaranteed success for $20,000.', '一名骗子推销员提出以两万美元的价格卖给卡内基一张保证成功的秘方。');
INSERT INTO `words` VALUES (3261, 'huddle', 'ˈhʌdl', 'v.', '(通常因寒冷或害怕)挤在一起;(通常因寒冷或害怕)蜷缩，缩成一团;', 'She huddled inside the porch as she rang the bell...', '她按门铃时身子在门廊里缩成一团。');
INSERT INTO `words` VALUES (3262, 'hue', 'hjuː', 'n.', '颜色;色度;色调;', 'The same hue will look different in different light.', '同一颜色在不同的光线下看起来会有所不同。');
INSERT INTO `words` VALUES (3263, 'hug', 'hʌɡ', 'v.', '拥抱;搂抱;抱紧;', 'She had hugged him exuberantly and invited him to dinner the next day...', '她兴高采烈地拥抱了他，并邀请他第二天一起吃晚饭。');
INSERT INTO `words` VALUES (3264, 'huge', 'hjuːdʒ', 'adj.', '巨大的;极多的;程度高的;', '...a tiny little woman with huge black glasses...', '戴着一副硕大黑框眼镜的娇小女子');
INSERT INTO `words` VALUES (3265, 'hull', 'hʌl', 'n.', '船身;船体;', 'The hull had suffered extensive damage to the starboard side.', '船体右舷遭到重创。');
INSERT INTO `words` VALUES (3266, 'hum', 'hʌm', 'v.', '哼(曲子);发嗡嗡声;活跃;', 'The birds sang, the bees hummed...', '鸟儿叫，蜂儿鸣。');
INSERT INTO `words` VALUES (3267, 'human', 'ˈhjuːmən', 'adj.', '人的;显示人类特有弱点的;人本性的;', '...the human body.', '人体');
INSERT INTO `words` VALUES (3268, 'humane', 'hjuːˈmeɪn', 'adj.', '善良的;仁慈的;人道的;', 'In the mid-nineteenth century, Dorothea Dix began to campaign for humane treatment of the mentally ill.', '在19世纪中叶，多罗西娅·迪克斯开始发起为精神病患者争取人道待遇的运动。');
INSERT INTO `words` VALUES (3269, 'humanitarian', 'hjuːˌmænɪˈteəriən', 'adj.', '人道主义的(主张减轻人类苦难、改善人类生活);慈善的;', 'Air bombardment raised criticism on the humanitarian grounds that innocent civilians might suffer.', '空袭遭到了批评，因为从人道主义的立场来看，无辜的平民可能会受到伤害。');
INSERT INTO `words` VALUES (3270, 'humanity', 'hjuːˈmænəti', 'n.', '(统称)人;人类;人性;', 'They face charges of committing crimes against humanity.', '他们被控犯有反人类罪。');
INSERT INTO `words` VALUES (3271, 'humble', 'ˈhʌmbl', 'adj.', '谦逊的;虚心的;表示谦逊，但不够诚挚或认真;', 'He gave a great performance, but he was very humble...', '他的表演非常出色，但他却很谦虚。');
INSERT INTO `words` VALUES (3272, 'humdrum', 'ˈhʌmdrʌm', 'adj.', '乏味的;单调的;', '...her lawyer husband, trapped in a humdrum but well-paid job...', '她整天做着单调乏味的工作但收入颇丰的律师丈夫');
INSERT INTO `words` VALUES (3273, 'humid', 'ˈhjuːmɪd', 'adj.', '温暖潮湿的;湿热的;', 'Visitors can expect hot and humid conditions...', '游客会遇到炎热潮湿的天气状况。');
INSERT INTO `words` VALUES (3274, 'humidity', 'hjuːˈmɪdəti', 'n.', '(空气中的)湿度;湿热;高温潮湿;', 'The heat and humidity were insufferable.', '高温和潮湿让人难以忍受。');
INSERT INTO `words` VALUES (3275, 'humiliate', 'hjuːˈmɪlieɪt', 'v.', '羞辱;使丧失尊严;', 'She had been beaten and humiliated by her husband...', '她遭到丈夫的殴打和羞辱。');
INSERT INTO `words` VALUES (3276, 'humiliation', 'hjuːˌmɪliˈeɪʃn', 'n.', '耻辱;屈辱;丢脸;', 'She faced the humiliation of discussing her husband\'s affair.', '她面临要谈及丈夫外遇的羞辱。');
INSERT INTO `words` VALUES (3277, 'humorous', 'ˈhjuːmərəs', 'adj.', '滑稽有趣的;有幽默感的;', 'He was quite humorous, and I liked that about him.', '他很幽默，我喜欢他这一点。');
INSERT INTO `words` VALUES (3278, 'hundredth', 'ˈhʌndrədθ', 'ord.', '第一百;', 'The bank celebrates its hundredth anniversary in December.', '这家银行将于12月庆祝其成立100周年。');
INSERT INTO `words` VALUES (3279, 'hunger', 'ˈhʌŋɡə(r)', 'n.', '饥饿;饥荒;饥饿感;', 'Hunger is the body\'s signal that levels of blood sugar are too low...', '饥饿感是身体发出的信号，表示血糖水平太低。');
INSERT INTO `words` VALUES (3280, 'hungry', 'ˈhʌŋɡri', 'adj.', '感到饿的;饥饿的;挨饿的;', 'My friend was hungry, so we drove to a shopping mall to get some food...', '我的朋友饿了，于是我们开车去一家购物中心吃饭。');
INSERT INTO `words` VALUES (3281, 'hunt', 'hʌnt', 'v.', '打猎;猎取;猎杀;', 'A forensic team was hunting for clues...', '法医小组正在寻找线索。');
INSERT INTO `words` VALUES (3282, 'hunter', 'ˈhʌntə(r)', 'n.', '猎人;狩猎者;(猎食其他动物的)猎兽;', 'The hunters stalked their prey.', '猎人潜行追踪猎物。');
INSERT INTO `words` VALUES (3283, 'hunting', 'ˈhʌntɪŋ', 'n.', '狩猎运动;打猎;找;', 'Hunting is one of Italy\'s most popular sports...', '狩猎是意大利最受欢迎的体育运动之一。');
INSERT INTO `words` VALUES (3284, 'hurl', 'hɜːl', 'v.', '猛扔;猛投;猛摔;', 'Groups of angry youths hurled stones at police...', '成群结伙愤怒的年轻人朝警察投掷石块。');
INSERT INTO `words` VALUES (3285, 'hurry', 'ˈhʌri', 'v.', '赶快，匆忙，急忙(做某事);(朝某方向)迅速移动;催促(某人);', 'Claire hurried along the road...', '克莱尔匆匆地沿路而行。');
INSERT INTO `words` VALUES (3286, 'hurt', 'hɜːt', 'v.', '(使)疼痛，受伤;感到疼痛;使不快;', 'Yasin had seriously hurt himself while trying to escape from the police...', '亚辛试图从警察手中逃脱时受了重伤。');
INSERT INTO `words` VALUES (3287, 'husband', 'ˈhʌzbənd', 'n.', '丈夫;', 'Eva married her husband Jack in 1957...', '伊娃和丈夫杰克于1957年结的婚。');
INSERT INTO `words` VALUES (3288, 'hush', 'hʌʃ', 'v.', '安静;别说话;别叫喊;', 'Hush, my love, it\'s all right.', '嘘，宝贝，没事了。');
INSERT INTO `words` VALUES (3289, 'hustle', 'ˈhʌsl', 'v.', '推搡;猛推;催促(某人);', 'The guards hustled Harry out of the car...', '卫兵将哈里从车里拉了出来。');
INSERT INTO `words` VALUES (3290, 'hybrid', 'ˈhaɪbrɪd', 'n.', '杂种动物;杂交植物;杂种;', 'All these brightly coloured hybrids are so lovely in the garden.', '花园里所有这些色彩鲜艳的杂交花真美丽。');
INSERT INTO `words` VALUES (3291, 'hydraulic', 'haɪˈdrɒlɪk', 'adj.', '(通过水管等)液压的，水力的;液压驱动的;与水利(或液压)系统有关的;', 'The boat has no fewer than five hydraulic pumps.', '这艘船配有不少于5个液压泵。');
INSERT INTO `words` VALUES (3292, 'hygiene', 'ˈhaɪdʒiːn', 'n.', '卫生;', 'Be extra careful about personal hygiene.', '要非常注意个人卫生。');
INSERT INTO `words` VALUES (3293, 'hymn', 'hɪm', 'n.', '赞美诗，圣歌(尤指基督徒唱的颂扬上帝的歌);歌颂某事物的电影(或书等);', 'I like singing hymns.', '我喜欢唱圣歌。');
INSERT INTO `words` VALUES (3294, 'hypocrisy', 'hɪˈpɒkrəsi', 'n.', '伪善;虚伪;', 'He accused newspapers of hypocrisy in their treatment of the story...', '他指责了报纸在报道该新闻时的虚伪。');
INSERT INTO `words` VALUES (3295, 'hypothesis', 'haɪˈpɒθəsɪs', 'n.', '(有少量事实依据但未被证实的)假说，假设;(凭空的)猜想，猜测;', 'Work will now begin to test the hypothesis in rats...', '现在将开始在老鼠身上做实验以验证该假设。');
INSERT INTO `words` VALUES (3296, 'hypothetical', 'ˌhaɪpəˈθetɪkl', 'adj.', '假设的;假定的;', 'Let\'s look at a hypothetical situation in which Carol, a recovering cocaine addict, gets invited to a party.', '让我们假设这样一种情景，卡萝尔，一位正在康复的可卡因成瘾者，应邀去参加一个聚会。');
INSERT INTO `words` VALUES (3297, 'hysteria', 'hɪˈstɪəriə', 'n.', '歇斯底里;情绪狂暴不可抑止;大肆鼓吹;', 'No one could help getting carried away by the hysteria...', '所有人都不由自主地受到狂热情绪的感染。');
INSERT INTO `words` VALUES (3298, 'hysterical', 'hɪˈsterɪkl', 'adj.', '歇斯底里的;情绪狂暴不可抑止的;极其可笑的;', 'Police and bodyguards had to protect him as the almost hysterical crowds struggled to approach him...', '当情绪几近失控的人群拼命想接近他时，警察和保镖不得不保护他。');
INSERT INTO `words` VALUES (3299, 'i', 'aɪ', 'n.', '英文字母中的第九个字母;虚数的单位;', 'Jim and I are getting married...', '我和吉姆就要结婚了。');
INSERT INTO `words` VALUES (3300, 'ice', 'aɪs', 'n.', '冰;冰场;溜冰场;', 'Glaciers are moving rivers of ice...', '冰河是犹如河水般流动的巨大冰体。');
INSERT INTO `words` VALUES (3301, 'icon', 'ˈaɪkɒn', 'n.', '图标;图符;崇拜对象;', 'Only Marilyn has proved as enduring a fashion icon.', '只有玛丽莲是常青的时尚偶像。');
INSERT INTO `words` VALUES (3302, 'icy', 'ˈaɪsi', 'adj.', '冰冷的;冰冻的;覆盖着冰的;', 'An icy wind blew hard across the open spaces...', '刺骨的寒风于开阔处呼啸着。');
INSERT INTO `words` VALUES (3303, 'id', 'ˌaɪ ˈdiː', 'n.', '本我，伊德(指人的潜意识中其基本需求、感情和欲望假定存在的部分);', 'I had no ID on me so I couldn\'t prove I was the owner of the car...', '我身上没带身份证件，也就无法证实我就是车主。');
INSERT INTO `words` VALUES (3304, 'idea', 'aɪˈdɪə', 'n.', '想法;构思;主意;', 'It\'s a good idea to plan ahead...', '提前做计划是个好主意。');
INSERT INTO `words` VALUES (3305, 'ideal', 'aɪˈdiːəl', 'adj.', '完美的;理想的;最合适的;', '...the bohemian ideals of truth, beauty, freedom and love.', '崇信真、美、自由与爱的波西米亚式理想');
INSERT INTO `words` VALUES (3306, 'idealism', 'aɪˈdiːəlɪzəm', 'n.', '理想主义;唯心主义;唯心论;', 'She never lost her respect for the idealism of the 1960s...', '她对 20 世纪 60 年代的理想主义一直心怀崇敬。');
INSERT INTO `words` VALUES (3307, 'identical', 'aɪˈdentɪkl', 'adj.', '完全同样的;相同的;同一的;', 'Nearly all the houses were identical...', '几乎所有的房子都一模一样。');
INSERT INTO `words` VALUES (3308, 'identically', '', 'adv.', '相（恒）等；同一（样）;', 'Nearly all the houses were identical...', '几乎所有的房子都一模一样。');
INSERT INTO `words` VALUES (3309, 'identification', 'aɪˌdentɪfɪˈkeɪʃn', 'n.', '鉴定;辨认;确认;', 'Early identification of a disease can prevent death and illness...', '病症的及早诊断可避免死亡与病痛。');
INSERT INTO `words` VALUES (3310, 'identify', 'aɪˈdentɪfaɪ', 'v.', '确认;认出;鉴定;', 'There are a number of distinguishing characteristics by which you can identify a Hollywood epic...', '好莱坞的史诗大片有着一些与众不同的特点。');
INSERT INTO `words` VALUES (3311, 'identity', 'aɪˈdentəti', 'n.', '身份;本身;本体;', 'Abu is not his real name, but it\'s one he uses to disguise his identity...', '阿布不是他的真名，是他用于伪装身份的名字。');
INSERT INTO `words` VALUES (3312, 'ideology', 'ˌaɪdiˈɒlədʒi', 'n.', '思想(体系);思想意识;意识形态;', '...capitalist ideology.', '资本主义意识形态');
INSERT INTO `words` VALUES (3313, 'idiom', 'ˈɪdiəm', 'n.', '习语;成语;惯用语;', 'McCartney was also keen to write in a classical idiom, rather than a pop one...', '麦卡特尼也热衷于古典风格而不是通俗风格的创作。');
INSERT INTO `words` VALUES (3314, 'idiomatic', 'ˌɪdiəˈmætɪk', 'adj.', '表达方式地道的;符合(某一)语言习惯的;包含习语的;', '...her remarkable command of idiomatic English.', '她对地道英语的娴熟使用');
INSERT INTO `words` VALUES (3315, 'idiot', 'ˈɪdiət', 'n.', '蠢人;笨蛋;白痴;', 'I knew I\'d been an idiot to stay there...', '我知道我呆在这里很蠢。');
INSERT INTO `words` VALUES (3316, 'idle', 'ˈaɪdl', 'adj.', '懈怠的;懒惰的;闲置的;', 'Employees have been idle almost a month because of shortages.', '由于原料短缺，员工们已将近一个月无事可做。');
INSERT INTO `words` VALUES (3317, 'idleness', 'ˈaɪdlnəs', 'n.', '懒惰；闲散；安逸;失业(状态)；赋闲;无益,白费;', 'Employees have been idle almost a month because of shortages.', '由于原料短缺，员工们已将近一个月无事可做。');
INSERT INTO `words` VALUES (3318, 'idol', 'ˈaɪdl', 'n.', '受到热爱和崇拜的人(或物);偶像;神像;', 'A great cheer went up from the crowd as they caught sight of their idol...', '一看到他们的偶像,人群爆发出热烈的欢呼声。');
INSERT INTO `words` VALUES (3319, 'ignite', 'ɪɡˈnaɪt', 'v.', '(使)燃烧，着火;点燃;', 'The bombs ignited a fire which destroyed some 60 houses...', '炸弹引发的大火烧毁了大约 60 栋房屋。');
INSERT INTO `words` VALUES (3320, 'ignorance', 'ˈɪɡnərəns', 'n.', '无知;', 'I am embarrassed by my complete ignorance of history...', '我对历史一无所知,这让我很难堪。');
INSERT INTO `words` VALUES (3321, 'ignorant', 'ˈɪɡnərənt', 'adj.', '(对某事物)不了解的;无知的;愚昧的;', 'People don\'t like to ask questions for fear of appearing ignorant...', '人们不喜欢问问题,害怕自己会显得很无知。');
INSERT INTO `words` VALUES (3322, 'ignore', 'ɪɡˈnɔː(r)', 'v.', '忽视;对…不予理会;佯装未见;', 'She said her husband ignored her...', '她说丈夫不关心她。');
INSERT INTO `words` VALUES (3323, 'ill', 'ɪl', 'adj.', '有病;不舒服;坏的;', 'In November 1941 Payne was seriously ill with pneumonia...', '1941 年 11 月,佩恩得了严重的肺炎。');
INSERT INTO `words` VALUES (3324, 'illegal', 'ɪˈliːɡl', 'adj.', '不合法的;非法的;违法的;', 'It is illegal to intercept radio messages...', '拦截无线电报是非法的。');
INSERT INTO `words` VALUES (3325, 'illicit', 'ɪˈlɪsɪt', 'adj.', '非法的;违法的;违背社会常规的;', 'Dante clearly condemns illicit love.', '丹蒂旗帜鲜明地谴责不正当恋情。');
INSERT INTO `words` VALUES (3326, 'illiterate', 'ɪˈlɪtərət', 'adj.', '不会读写的;不识字的;文盲的;', 'A large percentage of the population is illiterate.', '文盲人口占有相当高的比例。');
INSERT INTO `words` VALUES (3327, 'illness', 'ˈɪlnəs', 'n.', '(身体或精神上的)疾病，病;(某种)病;患病期;', 'If your child shows any signs of illness, take her to the doctor...', '如果您的孩子出现任何患病症状,带她去看医生。');
INSERT INTO `words` VALUES (3328, 'illuminate', 'ɪˈluːmɪneɪt', 'v.', '照明;照亮;照射;', 'No streetlights illuminated the street...', '这条街上没有照明的路灯。');
INSERT INTO `words` VALUES (3329, 'illumination', 'ɪˌluːmɪˈneɪʃn', 'n.', '照明;光源;彩灯;', 'The only illumination came from a small window high in the opposite wall.', '唯一的亮光来自对面墙上高处的一扇小窗子。');
INSERT INTO `words` VALUES (3330, 'illusion', 'ɪˈluːʒn', 'n.', '错误的观念;幻想;幻想的事物;', 'Do not have any illusions that an industrial tribunal will right all employment wrongs...', '不要幻想一个劳资仲裁法庭能纠正所有的雇佣不公现象。');
INSERT INTO `words` VALUES (3331, 'illustrate', 'ˈɪləstreɪt', 'v.', '加插图于;给(书等)做图表;(用示例、图画等)说明，解释;', 'The example of the United States illustrates this point...', '美国的例子证明了这一点。');
INSERT INTO `words` VALUES (3332, 'illustration', 'ˌɪləˈstreɪʃn', 'n.', '(书、杂志等中的)图表，插图;图解;图示;', '...a perfect illustration of the way Britain absorbs and adapts external influences.', '英国吸收借鉴外来影响的绝好例证');
INSERT INTO `words` VALUES (3333, 'image', 'ˈɪmɪdʒ', 'n.', '形象;印象;声誉;', 'The image of art theft as a gentleman\'s crime is outdated...', '把盗窃艺术品看作是一种绅士犯罪的观念已经过时了。');
INSERT INTO `words` VALUES (3334, 'imaginable', 'ɪˈmædʒɪnəbl', 'adj.', '(与形容词最高级或与all、every连用，表示强调或概括)想象得到的;可想象的;', '...their imprisonment under some of the most horrible circumstances imaginable...', '他们被关押在可以想象出的最恐怖的环境中');
INSERT INTO `words` VALUES (3335, 'imaginary', 'ɪˈmædʒɪnəri', 'adj.', '想象中的;幻想的;虚构的;', 'Lots of children have imaginary friends.', '许多孩子都会凭空想象一些朋友。');
INSERT INTO `words` VALUES (3336, 'imagination', 'ɪˌmædʒɪˈneɪʃn', 'n.', '想象力;想象;想象的事物;', 'Antonia is a woman with a vivid imagination...', '安东尼娅是个想象力丰富的女人。');
INSERT INTO `words` VALUES (3337, 'imaginative', 'ɪˈmædʒɪnətɪv', 'adj.', '富于想象力的;创新的;', '...an imaginative writer.', '想象力丰富的作家');
INSERT INTO `words` VALUES (3338, 'imagine', 'ɪˈmædʒɪn', 'v.', '想象;设想;误以为;', 'He could not imagine a more peaceful scene...', '他想象不出比这更祥和的景象。');
INSERT INTO `words` VALUES (3339, 'imaging', 'ˈɪmɪdʒɪŋ', 'n.', '成像;', '...thermal imaging cameras.', '热成像照相机');
INSERT INTO `words` VALUES (3340, 'imitate', 'ˈɪmɪteɪt', 'v.', '模仿;仿效;模仿(某人的讲话、举止);', '...a genuine German musical which does not try to imitate the American model.', '不刻意模仿美国模式的真正的德国音乐剧');
INSERT INTO `words` VALUES (3341, 'imitation', 'ˌɪmɪˈteɪʃn', 'n.', '仿制品;赝品;模仿;', '...the most accurate imitation of Chinese architecture in Europe.', '全欧洲最逼真的仿中国样式的建筑');
INSERT INTO `words` VALUES (3342, 'immaterial', 'ˌɪməˈtɪəriəl', 'adj.', '不重要;无关紧要;无形体的;', 'Whether we like him or not is immaterial.', '我们是否喜欢他无关紧要。');
INSERT INTO `words` VALUES (3343, 'immature', 'ˌɪməˈtjʊə(r)', 'adj.', '(行为)不成熟的，不够老练的，幼稚的;未长成的;发育未全的;', 'The birds were in immature plumage...', '这些鸟儿羽翅未全。');
INSERT INTO `words` VALUES (3344, 'immediate', 'ɪˈmiːdiət', 'adj.', '立即的;立刻的;目前的;', 'These tragic incidents have had an immediate effect...', '这些悲剧性事件造成的后果即刻显现。');
INSERT INTO `words` VALUES (3345, 'immediately', 'ɪˈmiːdiətli', 'adv.', '立即;马上;即刻;', 'He immediately flung himself to the floor...', '他立即飞身扑到地上。');
INSERT INTO `words` VALUES (3346, 'immense', 'ɪˈmens', 'adj.', '极大的;巨大的;', '...an immense cloud of smoke...', '大片的烟云');
INSERT INTO `words` VALUES (3347, 'immerse', 'ɪˈmɜːs', 'v.', '使浸没于;(使)深陷于，沉浸在;', 'Since then I\'ve lived alone and immersed myself in my career.', '自那以后,我一直一个人生活,全身心投入我的事业当中。');
INSERT INTO `words` VALUES (3348, 'immigrant', 'ˈɪmɪɡrənt', 'n.', '(外来)移民;外侨;', '...illegal immigrants.', '非法移民');
INSERT INTO `words` VALUES (3349, 'immigrate', 'ˈɪmɪɡreɪt', 'v.', '(从外地)移居;移民;', '...a Russian-born professor who had immigrated to the United States...', '一位生于俄罗斯、后移民到美国的教授');
INSERT INTO `words` VALUES (3350, 'immigration', 'ˌɪmɪˈɡreɪʃn', 'n.', '移居(入境);移民人数;移民局检查站;', 'The government has decided to tighten its immigration policy.', '政府决定实施更加严格的移民政策。');
INSERT INTO `words` VALUES (3351, 'imminent', 'ˈɪmɪnənt', 'adj.', '即将发生的;临近的;', 'There appeared no imminent danger...', '眼前似乎没有危险。');
INSERT INTO `words` VALUES (3352, 'immoral', 'ɪˈmɒrəl', 'adj.', '不道德的;邪恶的;放荡的;', '...those who think that birth control and abortion are immoral.', '那些认为节育与堕胎是不道德行为的人');
INSERT INTO `words` VALUES (3353, 'immortal', 'ɪˈmɔːtl', 'adj.', '长生的;永世的;不朽的;', '...the immortal Reverend Dr Spooner.', '不朽的斯普纳牧师');
INSERT INTO `words` VALUES (3354, 'immune', 'ɪˈmjuːn', 'adj.', '有免疫力;不受影响;受保护;', 'This blood test will show whether or not you\'re immune to the disease...', '这个血检会显示你是否对这种疾病具有免疫力。');
INSERT INTO `words` VALUES (3355, 'immunity', 'ɪˈmjuːnəti', 'n.', '免疫力;受保护;豁免;', 'This blood test will show whether or not you\'re immune to the disease...', '这个血检会显示你是否对这种疾病具有免疫力。');
INSERT INTO `words` VALUES (3356, 'impair', 'ɪmˈpeə(r)', 'v.', '损害;削弱;', 'Consumption of alcohol impairs your ability to drive a car or operate machinery...', '饮酒会削弱你驾驶汽车或操控机器的能力。');
INSERT INTO `words` VALUES (3357, 'impart', 'ɪmˈpɑːt', 'v.', '通知;透露;传授;', 'The ability to impart knowledge and command respect is the essential qualification for teachers...', '具备传授知识并赢得尊敬的能力是对教师的基本要求。');
INSERT INTO `words` VALUES (3358, 'impartial', 'ɪmˈpɑːʃl', 'adj.', '公正的;不偏不倚的;中立的;', 'As an impartial observer my analysis is supposed to be objective...', '作为中立的观察员,我的分析应该客观。');
INSERT INTO `words` VALUES (3359, 'impatience', 'ɪmˈpeɪʃəns', 'n.', '急躁；无耐心；不耐烦;', 'He is impatient as the first hour passes and then another...', '一个小时、两个小时过去了,他不耐烦了。');
INSERT INTO `words` VALUES (3360, 'impatient', 'ɪmˈpeɪʃnt', 'adj.', '不耐烦的;没有耐心的;急于;', 'He is impatient as the first hour passes and then another...', '一个小时、两个小时过去了,他不耐烦了。');
INSERT INTO `words` VALUES (3361, 'impede', 'ɪmˈpiːd', 'v.', '阻碍;阻止;', 'Fallen rock is impeding the progress of rescue workers.', '坠落的石头阻滞了救援人员的救援进程。');
INSERT INTO `words` VALUES (3362, 'imperative', 'ɪmˈperətɪv', 'adj.', '重要紧急的;迫切的;急需处理的;', 'It was imperative that he act as naturally as possible...', '最重要的是他要做到尽可能自然。');
INSERT INTO `words` VALUES (3363, 'imperfect', 'ɪmˈpɜːfɪkt', 'adj.', '有缺点的;有缺陷的;不完全的;', 'We live in an imperfect world...', '我们生活在一个并不完美的世界上。');
INSERT INTO `words` VALUES (3364, 'imperial', 'ɪmˈpɪəriəl', 'adj.', '帝国的;皇帝的;(度量衡)英制的;', '...the Imperial Palace in Tokyo...', '东京的皇宫');
INSERT INTO `words` VALUES (3365, 'imperialist', 'ɪmˈpɪəriəlɪst', 'n.', '帝国主义者;帝国统治拥护者;', 'The developed nations have all benefited from their imperialist exploitation.', '发达国家全都从他们的帝国主义剥削中获益。');
INSERT INTO `words` VALUES (3366, 'impetus', 'ˈɪmpɪtəs', 'n.', '动力;推动;促进;', 'This decision will give renewed impetus to the economic regeneration of east London...', '这一决定会给东伦敦的经济复苏带来新的推动力。');
INSERT INTO `words` VALUES (3367, 'implicate', 'ˈɪmplɪkeɪt', 'v.', '牵涉，涉及(某人);表明(或意指)…是起因;', 'Allegations had appeared in the press implicating the army and police in some of the killings...', '有关军方和警方与几起命案有牵连的指控出现在了新闻报道中。');
INSERT INTO `words` VALUES (3368, 'implication', 'ˌɪmplɪˈkeɪʃn', 'n.', '可能的影响(或作用、结果);含意;暗指;', 'The Attorney General was aware of the political implications of his decision to prosecute...', '司法部长很清楚他决定起诉可能引发的政治后果。');
INSERT INTO `words` VALUES (3369, 'implicit', 'ɪmˈplɪsɪt', 'adj.', '含蓄的;不直接言明的;成为一部分的;', 'This is seen as an implicit warning not to continue with military action...', '这被视为一个停止军事行动的含蓄警告。');
INSERT INTO `words` VALUES (3370, 'implied', 'ɪmˈplaɪd', 'v.', '含有…的意思;暗示;暗指;', '\'Are you implying that I have something to do with those attacks?\' she asked coldly...', '“你在暗示我和那些袭击有关吗？”她冷冷地问。');
INSERT INTO `words` VALUES (3371, 'implore', 'ɪmˈplɔː(r)', 'v.', '恳求;哀求;', 'Opposition leaders this week implored the president to break the deadlock...', '反对党领袖本周恳求总统打破僵局。');
INSERT INTO `words` VALUES (3372, 'imply', 'ɪmˈplaɪ', 'v.', '含有…的意思;暗示;暗指;', '\'Are you implying that I have something to do with those attacks?\' she asked coldly...', '“你在暗示我和那些袭击有关吗？”她冷冷地问。');
INSERT INTO `words` VALUES (3373, 'importance', 'ɪmˈpɔːtns', 'n.', '重要性;重要;重大;', 'We have always stressed the importance of economic reform...', '我们一向强调经济改革的重要性。');
INSERT INTO `words` VALUES (3374, 'important', 'ɪmˈpɔːtnt', 'adj.', '重要的;有重大影响的;有巨大价值的;', 'Her sons are the most important thing in her life...', '她生命中最重要的是她的儿子们。');
INSERT INTO `words` VALUES (3375, 'importer', 'ɪmˈpɔːtə(r)', 'n.', '从事进口的人(或公司等);进口商;', '...an importer of exotic food.', '外国食品进口商');
INSERT INTO `words` VALUES (3376, 'impose', 'ɪmˈpəʊz', 'v.', '推行，采用(规章制度);强制实行;迫使;', 'Britain imposed fines on airlines which bring in passengers without proper papers...', '英国会对运载证件不齐全的乘客进入国内的航空公司课以罚款。');
INSERT INTO `words` VALUES (3377, 'imposing', 'ɪmˈpəʊzɪŋ', 'adj.', '壮观的;使人印象深刻的;', 'He was an imposing man.', '他仪表堂堂。');
INSERT INTO `words` VALUES (3378, 'imposition', 'ˌɪmpəˈzɪʃn', 'n.', '(新法律或规则等的)颁布，实施;(新税的)征收;不公平(或不合理)的要求;', 'Britain imposed fines on airlines which bring in passengers without proper papers...', '英国会对运载证件不齐全的乘客进入国内的航空公司课以罚款。');
INSERT INTO `words` VALUES (3379, 'impossibility', 'ɪmˌpɒsəˈbɪlɪti', 'n.', '办不到的事；不可能（性）;', 'It was impossible for anyone to get in because no one knew the password...', '因为没人知道密码,所以任何人都不可能进得去。');
INSERT INTO `words` VALUES (3380, 'impossible', 'ɪmˈpɒsəbl', 'adj.', '不可能存在(或做到)的;不可能的;难处理的;', 'It was impossible for anyone to get in because no one knew the password...', '因为没人知道密码,所以任何人都不可能进得去。');
INSERT INTO `words` VALUES (3381, 'impractical', 'ɪmˈpræktɪkl', 'adj.', '不明智的;不现实的;手不灵巧的;', 'When stalking subjects, a tripod is impractical...', '潜步跟踪目标时,用三脚架是不切实际的。');
INSERT INTO `words` VALUES (3382, 'imprecise', 'ˌɪmprɪˈsaɪs', 'adj.', '不确切的;不精确的;不明确的;', 'The charges were vague and imprecise...', '指控的内容模糊不清。');
INSERT INTO `words` VALUES (3383, 'impress', 'ɪmˈpres', 'v.', '使钦佩;使敬仰;给…留下深刻的好印象;', 'What impressed him most was their speed...', '最令他佩服的是他们的速度。');
INSERT INTO `words` VALUES (3384, 'impression', 'ɪmˈpreʃn', 'n.', '印象;感想;影响;', 'What were your first impressions of college?...', '你对大学的第一印象是什么？');
INSERT INTO `words` VALUES (3385, 'impressive', 'ɪmˈpresɪv', 'adj.', '令人赞叹的;令人敬佩的;', 'It is an impressive achievement...', '那是一项了不起的成就。');
INSERT INTO `words` VALUES (3386, 'imprison', 'ɪmˈprɪzn', 'v.', '监禁;关押;', 'The local priest was imprisoned for 18 months on charges of anti-state agitation...', '当地牧师由于受到煽动反对政府的指控而被监禁 18 个月。');
INSERT INTO `words` VALUES (3387, 'imprisonment', 'ɪmˈprɪznmənt', 'n.', '关押;监禁;坐牢;', 'She was sentenced to seven years\' imprisonment.', '她被判处 7 年监禁。');
INSERT INTO `words` VALUES (3388, 'impromptu', 'ɪmˈprɒmptjuː', 'adj.', '无准备的;即兴的;即席的;', 'This afternoon the Palestinians held an impromptu press conference...', '今天下午巴勒斯坦方面临时召开了记者招待会。');
INSERT INTO `words` VALUES (3389, 'improper', 'ɪmˈprɒpə(r)', 'adj.', '不诚实的;不正当的;不道德的;', '25 officers were investigated following allegations of improper conduct during the murder inquiry...', '25 名警察在被指控于调查谋杀案期间有非法行为之后受到调查。');
INSERT INTO `words` VALUES (3390, 'improve', 'ɪmˈpruːv', 'v.', '改进;改善;', 'Both the texture and condition of your hair should improve...', '你头发的发质与健康状况都应改善。');
INSERT INTO `words` VALUES (3391, 'improvement', 'ɪmˈpruːvmənt', 'n.', '改善;改进;改进处;', '...the dramatic improvements in organ transplantation in recent years...', '近几年器官移植方面取得的巨大进步');
INSERT INTO `words` VALUES (3392, 'improvise', 'ˈɪmprəvaɪz', 'v.', '临时拼凑;临时做;即兴创作(音乐、台词、演讲词等);', 'You need a wok with a steaming rack for this; if you don\'t have one, improvise...', '要做这个，你需要一口带蒸笼篦子的锅,如果没有,就用别的临时凑合一下。');
INSERT INTO `words` VALUES (3393, 'impulse', 'ˈɪmpʌls', 'n.', '冲动;心血来潮;一时的念头;', 'Unable to resist the impulse, he glanced at the sea again...', '他抑制不住冲动,又向海面瞥了一眼。');
INSERT INTO `words` VALUES (3394, 'impurity', 'ɪmˈpjʊərəti', 'n.', '杂质;肮脏;不纯;', 'The air in the factory is filtered to remove impurities.', '工厂里的空气要经过过滤，以清除其中的杂质。');
INSERT INTO `words` VALUES (3395, 'inability', 'ˌɪnəˈbɪləti', 'n.', '无能;无力;不能;', 'Her inability to concentrate could cause an accident.', '她无法集中精力可能会导致事故。');
INSERT INTO `words` VALUES (3396, 'inaccessible', 'ˌɪnækˈsesəbl', 'adj.', '难以达到的;不可得到的;', '...the remote, inaccessible areas of the Andes rainforests.', '偏僻遥远、难以到达的安第斯雨林地区');
INSERT INTO `words` VALUES (3397, 'inaccuracy', 'ɪnˈækjərəsi', 'n.', '不精确;不准确;', 'He was disturbed by the inaccuracy of the answers...', '很多答案都不准确,为此他感到很烦恼。');
INSERT INTO `words` VALUES (3398, 'inaccurate', 'ɪnˈækjərət', 'adj.', '不精确的;不准确的;有错误的;', 'The book is both inaccurate and exaggerated...', '这本书不但不准确,而且夸大其词。');
INSERT INTO `words` VALUES (3399, 'inactive', 'ɪnˈæktɪv', 'adj.', '无行动的;不活动的;不活跃的;', 'He certainly was not politically inactive...', '他政治上当然不是无所作为。');
INSERT INTO `words` VALUES (3400, 'inadequate', 'ɪnˈædɪkwət', 'adj.', '不充分的;不足的;不够的;', 'Supplies of food and medicines are inadequate...', '食物和药品供应不足。');
INSERT INTO `words` VALUES (3401, 'inaudible', 'ɪnˈɔːdəbl', 'adj.', '听不见的;', 'His voice was almost inaudible...', '他的话音低得几乎听不见。');
INSERT INTO `words` VALUES (3402, 'inaugural', 'ɪˈnɔːɡjərəl', 'adj.', '就职的;开幕的;成立的;', 'In his inaugural address, the President appealed for unity.', '总统在他的就职演说中呼吁国家统一。');
INSERT INTO `words` VALUES (3403, 'inaugurate', 'ɪˈnɔːɡjəreɪt', 'v.', '为(某人)举行就职典礼;为…举行落成仪式(或创建仪式);引进;', 'The new President will be inaugurated on January 20.', '新总统将于 1 月 20 号举行就职典礼。');
INSERT INTO `words` VALUES (3404, 'inauguration', 'ɪˌnɔːɡjəˈreɪʃn', 'n.', '就职典礼；开始，开创；开幕式;', 'The new President will be inaugurated on January 20.', '新总统将于 1 月 20 号举行就职典礼。');
INSERT INTO `words` VALUES (3405, 'inbound', 'ˈɪnbaʊnd', 'adj.', '到达的;归航的;', '...a special inbound flight from Honduras.', '从洪都拉斯归航的专机');
INSERT INTO `words` VALUES (3406, 'incalculable', 'ɪnˈkælkjələbl', 'adj.', '极大的;不可计算的;不可估量的;', 'He warned that the effects of any war would be incalculable...', '他警告说任何战争造成的后果都是不可估量的。');
INSERT INTO `words` VALUES (3407, 'incapable', 'ɪnˈkeɪpəbl', 'adj.', '没有能力(做某事);不能克制自己的;不能自理的;', 'She seemed incapable of taking decisions...', '她似乎无法作出决定。');
INSERT INTO `words` VALUES (3408, 'incentive', 'ɪnˈsentɪv', 'n.', '激励;刺激;鼓励;', 'There is little or no incentive to adopt such measures...', '几乎没有什么激励政策来促使人们采取这些措施。');
INSERT INTO `words` VALUES (3409, 'inch', 'ɪntʃ', 'n.', '英寸(长度单位，等于2.54厘米，1英尺等于12英寸);少量;短距离;', '...a candy tin 6 inches high and 8 inches in diameter.', '高 6 英寸、直径 8 英寸的糖果罐子');
INSERT INTO `words` VALUES (3410, 'incidence', 'ˈɪnsɪdəns', 'n.', '发生范围;影响程度;发生率;', 'The incidence of breast cancer increases with age.', '乳腺癌的发病率随年龄增长而递增。');
INSERT INTO `words` VALUES (3411, 'incident', 'ˈɪnsɪdənt', 'n.', '发生的事情(尤指不寻常的或讨厌的);严重事件，暴力事件(如犯罪、事故、袭击等);(两国间的)摩擦，冲突;', 'These incidents were the latest in a series of disputes between the two nations...', '这些事件是两国一系列争端中最近发生的几起。');
INSERT INTO `words` VALUES (3412, 'incidentally', 'ˌɪnsɪˈdentli', 'adv.', '(引出新话题、附加信息、或临时想到的问题)顺便提一句;偶然;附带地;', '\'I didn\'t ask you to come. Incidentally, why have you come?\'', '“我没有叫你来。顺便问一下,你为什么过来?”');
INSERT INTO `words` VALUES (3413, 'incipient', 'ɪnˈsɪpiənt', 'adj.', '刚开始的;初始的;早期的;', '...an incipient economic recovery...', '初期的经济复苏');
INSERT INTO `words` VALUES (3414, 'incite', 'ɪnˈsaɪt', 'v.', '煽动;鼓动;', 'He incited his fellow citizens to take their revenge...', '他鼓动他的同胞们报仇雪恨。');
INSERT INTO `words` VALUES (3415, 'inclination', 'ˌɪnklɪˈneɪʃn', 'n.', '倾向;意愿;趋向;', 'He had neither the time nor the inclination to think of other things...', '他没有时间也不愿意去考虑其他事情。');
INSERT INTO `words` VALUES (3416, 'inclined', 'ɪnˈklaɪnd', 'adj.', '想(做某事);有…倾向;很可能;', 'Nobody felt inclined to argue with Smith...', '没人愿意与史密斯争辩。');
INSERT INTO `words` VALUES (3417, 'include', 'ɪnˈkluːd', 'v.', '包括;包含;使成为…的一部分;', 'A good British breakfast always includes sausages...', '一顿丰盛的英式早餐总会包括香肠。');
INSERT INTO `words` VALUES (3418, 'inclusion', 'ɪnˈkluːʒn', 'n.', '(被)包括，包含;被包括的人(或事物);', '...a confident performance which justified his inclusion in the team.', '证明他入选该队当之无愧的自信表现');
INSERT INTO `words` VALUES (3419, 'inclusive', 'ɪnˈkluːsɪv', 'adj.', '包含全部费用;包括所提到的费用在内;包括提到的所有的天数(或月、数目等)在内;', '...all prices are inclusive of delivery.', '所有的价格都包括运费在内。');
INSERT INTO `words` VALUES (3420, 'incoherence', 'ˌɪnkəʊˈhɪərəns', 'n.', '联想散漫;无内聚性；不粘结性；支离破碎；松散；不（无）相干性;无条理；不连贯（性）;', 'As the evening progressed, he became increasingly incoherent...', '随着夜色加深,他说话越来越前言不搭后语了。');
INSERT INTO `words` VALUES (3421, 'income', 'ˈɪnkʌm', 'n.', '收入;收益;所得;', 'Many families on low incomes will be unable to afford to buy their own home...', '许多低收入家庭将会买不起自己的房子。');
INSERT INTO `words` VALUES (3422, 'incoming', 'ˈɪnkʌmɪŋ', 'adj.', '新当选的;新任的;正到达某地的;', 'We keep a tape of incoming calls.', '我们对来电进行录音。');
INSERT INTO `words` VALUES (3423, 'incompatibility', 'ˌɪnkəmˌpætəˈbɪləti', 'n.', '感情不和，不能和谐共处（尤指夫妇关系）；矛盾;不相容；不协调；不一致;', 'They feel strongly that their religion is incompatible with the political system...', '他们强烈感受到他们的宗教信仰与政治体制不相称。');
INSERT INTO `words` VALUES (3424, 'incompatible', 'ˌɪnkəmˈpætəbl', 'adj.', '(与某事物)不一致，不相配;(与某人)合不来，不能和睦相处;(与某物)不匹配;', 'They feel strongly that their religion is incompatible with the political system...', '他们强烈感受到他们的宗教信仰与政治体制不相称。');
INSERT INTO `words` VALUES (3425, 'incomplete', 'ˌɪnkəmˈpliːt', 'adj.', '不完整的;不完全的;不完善的;', 'The clearing of rubbish and drains is still incomplete...', '垃圾与污水的清理工作还未完成。');
INSERT INTO `words` VALUES (3426, 'incongruous', 'ɪnˈkɒŋɡruəs', 'adj.', '不合适的;不相称的;不协调的;', 'She was small and fragile and looked incongruous in an army uniform...', '她又小又弱,穿一身军装看起来很不协调。');
INSERT INTO `words` VALUES (3427, 'inconsistency', 'ˌɪnkənˈsɪstənsi', 'n.', '(行为的)反复无常，易变;前后矛盾;不一致;', 'His worst fault was his inconsistency...', '他最大的缺点就是反复无常。');
INSERT INTO `words` VALUES (3428, 'inconsistent', 'ˌɪnkənˈsɪstənt', 'adj.', '不一致;相矛盾;不符合(某套标准、思想等);', 'You are inconsistent and unpredictable.', '你这人反复无常,真叫人难以捉摸。');
INSERT INTO `words` VALUES (3429, 'inconvenience', 'ˌɪnkənˈviːniəns', 'n.', '不便;麻烦;困难;', 'We apologize for any inconvenience caused during the repairs...', '我们为维修期间造成的任何不便道歉。');
INSERT INTO `words` VALUES (3430, 'inconvenient', 'ˌɪnkənˈviːniənt', 'adj.', '不方便的;引起麻烦的;造成困难的;', 'Can you come at 10.30? I know it\'s inconvenient for you, but I must see you...', '你能 10 点半来吗？我知道你不方便,但是我必须见你。');
INSERT INTO `words` VALUES (3431, 'incorporate', 'ɪnˈkɔːpəreɪt', 'v.', '将…包括在内;包含;吸收;', 'The new cars will incorporate a number of major improvements.', '新款汽车会有若干重大改进。');
INSERT INTO `words` VALUES (3432, 'incorporated', 'ɪnˈkɔːpəreɪtɪd', 'adj.', '组成有法人地位的营业公司的;组成公司的;', '...MCA Incorporated.', 'MCA 股份有限公司');
INSERT INTO `words` VALUES (3433, 'incorporation', 'ɪnˌkɔːpəˈreɪʃən', 'n.', '法人；社团；公司；结合，合并；（国际法）并入说；〈美〉并入理论;', 'The new cars will incorporate a number of major improvements.', '新款汽车会有若干重大改进。');
INSERT INTO `words` VALUES (3434, 'incorrect', 'ˌɪnkəˈrekt', 'adj.', '不准确的;不正确的;不真实的;', 'He denied that his evidence about the telephone call was incorrect...', '他否认自己关于电话的证词是虚假的。');
INSERT INTO `words` VALUES (3435, 'increasingly', 'ɪnˈkriːsɪŋli', 'adv.', '越来越多地;不断增加地;', 'He was finding it increasingly difficult to make decisions...', '他发现越来越难以作出决定。');
INSERT INTO `words` VALUES (3436, 'incredible', 'ɪnˈkredəbl', 'adj.', '不能相信的;难以置信的;极好的;', 'The wildflowers will be incredible after this rain...', '这场雨过后,野花会变得美不胜收。');
INSERT INTO `words` VALUES (3437, 'incredulous', 'ɪnˈkredjələs', 'adj.', '不肯相信的;不能相信的;表示怀疑的;', '\'He made you do it?\' Her voice was incredulous...', '“他强迫你做的？”她的语气里带着怀疑。');
INSERT INTO `words` VALUES (3438, 'increment', 'ˈɪŋkrəmənt', 'n.', '定期的加薪;增量;增加;', 'The average yearly increment in labour productivity in industry was 4.5 per cent...', '工业劳动生产率年平均增长 4.5%。');
INSERT INTO `words` VALUES (3439, 'incremental', 'ˌɪnkrɪˈmɛnt(ə)l', 'adj.', '增加的;递增的;', '...our ability to add production capacity at relatively low incremental cost...', '我们以相对较低的边际成本增加产能的能力');
INSERT INTO `words` VALUES (3440, 'incubation', 'ˌɪŋkjuˈbeɪʃn', 'n.', '孵(卵);孵化;(传染病的)潜伏期;', 'The birds returned to their nests and continued to incubate the eggs.', '鸟儿回到巢里继续孵卵。');
INSERT INTO `words` VALUES (3441, 'incur', 'ɪnˈkɜː(r)', 'v.', '招致;遭受;引起;', 'The government had also incurred huge debts...', '政府也已负债累累。');
INSERT INTO `words` VALUES (3442, 'incurable', 'ɪnˈkjʊərəbl', 'adj.', '不能治愈的;不能改变的;无法矫正的;', 'He is suffering from an incurable skin disease.', '他患有无法根治的皮肤病。');
INSERT INTO `words` VALUES (3443, 'indebted', 'ɪnˈdetɪd', 'adj.', '感激的;蒙恩的;负债的;', 'I am deeply indebted to him for his help.', '我非常感激他对我的帮助。');
INSERT INTO `words` VALUES (3444, 'indebtedness', 'ɪnˈdetɪdnəs', 'n.', '受恩惠；所欠之款；债务;', 'I am deeply indebted to him for his help.', '我非常感激他对我的帮助。');
INSERT INTO `words` VALUES (3445, 'indeed', 'ɪnˈdiːd', 'adv.', '强调肯定的陈述或答复;;(用于very和形容词或副词之后，强调叙述、描写等)真正地;', 'Later, he admitted that the payments had indeed been made...', '后来,他承认确实付过款了。');
INSERT INTO `words` VALUES (3446, 'indefinite', 'ɪnˈdefɪnət', 'adj.', '无限期的;期限不定的;模糊不清的;', 'The trial was adjourned for an indefinite period.', '审判被无限期推迟。');
INSERT INTO `words` VALUES (3447, 'indefinitely', 'ɪnˈdefɪnətli', 'adv.', '无限期地;', 'The visit has now been postponed indefinitely...', '访问如今被无限期推迟。');
INSERT INTO `words` VALUES (3448, 'indelible', 'ɪnˈdeləbl', 'adj.', '无法忘记的;不可磨灭的;(笔迹)无法消除的，擦不掉的;', 'My visit to India in 1986 left an indelible impression on me...', '1986年的印度之行给我留下了难忘的印象。');
INSERT INTO `words` VALUES (3449, 'indemnify', 'ɪnˈdemnɪfaɪ', 'v.', '保证赔偿;赔偿;补偿;', 'They agreed to indemnify the taxpayers against any loss...', '他们同意补偿纳税人的任何损失。');
INSERT INTO `words` VALUES (3450, 'indemnity', 'ɪnˈdemnəti', 'n.', '保障;赔偿;补偿;', 'They were charged with failing to have professional indemnity cover...', '他们被指控未能提供职业保险保障。');
INSERT INTO `words` VALUES (3451, 'indent', 'ɪnˈdent , ˈɪndent', 'v.', '将(印刷或书写的行)缩进，缩格，缩排;', 'Indent the second line.', '第二行行首留空格。');
INSERT INTO `words` VALUES (3452, 'independence', 'ˌɪndɪˈpendəns', 'n.', '独立;独立(之日);自主;', 'In 1816, Argentina declared its independence from Spain.', '1816年,阿根廷宣布脱离西班牙正式独立。');
INSERT INTO `words` VALUES (3453, 'independent', 'ˌɪndɪˈpendənt', 'adj.', '独立的;自主的;自治的;', 'Your questions should be independent of each other...', '你的问题应该彼此无关。');
INSERT INTO `words` VALUES (3454, 'independently', 'ˌɪndɪ\'pendəntlɪ', 'adv.', '独立地；自立地;无关地;', 'Your questions should be independent of each other...', '你的问题应该彼此无关。');
INSERT INTO `words` VALUES (3455, 'indian', 'ˈɪndiən', 'n.', '印度人;(非因纽特人或米提人的)加拿大土著;加拿大印第安人;', '...the Indian government.', '印度政府');
INSERT INTO `words` VALUES (3456, 'indicate', 'ˈɪndɪkeɪt', 'v.', '表明;显示;象征;', 'A survey of retired people has indicated that most are independent and enjoying life...', '对退休人员的调查表明，大部分人都自食其力,享受生活。');
INSERT INTO `words` VALUES (3457, 'indication', 'ˌɪndɪˈkeɪʃn', 'n.', '表明;标示;显示;', 'All the indications are that we are going to receive reasonable support from abroad...', '所有迹象都表明，我们将从国外获得有力支持。');
INSERT INTO `words` VALUES (3458, 'indicative', 'ɪnˈdɪkətɪv', 'adj.', '表明;标示;显示;', 'The result was indicative of a strong retail market...', '结果表明零售业市场繁荣。');
INSERT INTO `words` VALUES (3459, 'indicator', 'ˈɪndɪkeɪtə(r)', 'n.', '指示信号;标志;迹象;', '...vital economic indicators, such as inflation, growth and the trade gap...', '重要的经济指标,例如通货膨胀、增长与贸易逆差');
INSERT INTO `words` VALUES (3460, 'indifference', 'ɪnˈdɪfrəns', 'n.', '漠不关心;冷淡;不感兴趣;', '...his callous indifference to the plight of his son.', '他对儿子所处困境的无情漠视');
INSERT INTO `words` VALUES (3461, 'indifferent', 'ɪnˈdɪfrənt', 'adj.', '漠不关心;不感兴趣;不很好的;', 'People have become indifferent to the suffering of others.', '人们对别人的痛苦已经变得无动于衷。');
INSERT INTO `words` VALUES (3462, 'indigenous', 'ɪnˈdɪdʒənəs', 'adj.', '本地的;当地的;土生土长的;', '...the country\'s indigenous population.', '该国的本土人口');
INSERT INTO `words` VALUES (3463, 'indignant', 'ɪnˈdɪɡnənt', 'adj.', '愤慨的;愤怒的;义愤的;', 'He is indignant at suggestions that they were secret agents...', '有人暗示他们可能是特务,这让他非常气愤。');
INSERT INTO `words` VALUES (3464, 'indignation', 'ˌɪndɪɡˈneɪʃn', 'n.', '愤慨;愤怒;义愤;', 'She was filled with indignation at the conditions under which miners were forced to work...', '矿工们被迫在如此恶劣的条件下工作,她感到异常愤怒。');
INSERT INTO `words` VALUES (3465, 'indirect', 'ˌɪndəˈrekt', 'adj.', '间接的;附带的;闪烁其词的;', 'Businesses are feeling the indirect effects from the recession that\'s going on elsewhere...', '企业感受到了别处经济衰退带来的间接影响。');
INSERT INTO `words` VALUES (3466, 'indirectly', 'ˌɪndɪˈrɛktli', 'adv.', '间接地；不诚实；迂回地;', 'Businesses are feeling the indirect effects from the recession that\'s going on elsewhere...', '企业感受到了别处经济衰退带来的间接影响。');
INSERT INTO `words` VALUES (3467, 'indiscriminate', 'ˌɪndɪˈskrɪmɪnət', 'adj.', '随意的;恣意的;不加选择的;', 'The indiscriminate use of fertilisers is damaging to the environment.', '乱用化肥会破坏环境。');
INSERT INTO `words` VALUES (3468, 'indispensable', 'ˌɪndɪˈspensəbl', 'adj.', '不可或缺的;必不可少的;', 'She was becoming indispensable to him.', '他已变得越来越离不开她了。');
INSERT INTO `words` VALUES (3469, 'indistinct', 'ˌɪndɪˈstɪŋkt', 'adj.', '模糊不清的;不清楚的;', 'The lettering is fuzzy and indistinct.', '书写的文字模糊难认。');
INSERT INTO `words` VALUES (3470, 'individual', 'ˌɪndɪˈvɪdʒuəl', 'adj.', '单独的;个别的;一个人的;', 'They wait for the group to decide rather than making individual decisions...', '他们等待团体作决定而不是各自作出决定。');
INSERT INTO `words` VALUES (3471, 'individually', 'ˌɪndɪˈvɪdʒuəli', 'adv.', '分别地;单独地;各别地;', 'They wait for the group to decide rather than making individual decisions...', '他们等待团体作决定而不是各自作出决定。');
INSERT INTO `words` VALUES (3472, 'indoor', 'ˈɪndɔː(r)', 'adj.', '(在)室内的;在户内进行的;在室内用的;', 'No smoking in any indoor facilities.', '所有室内场所都不许吸烟。');
INSERT INTO `words` VALUES (3473, 'indoors', 'ˌɪnˈdɔːz', 'adv.', '在室内;进入户内;', 'Since she was indoors, she had not been wearing a coat...', '由于她在室内,就没有穿外套。');
INSERT INTO `words` VALUES (3474, 'induce', 'ɪnˈdjuːs', 'v.', '劝说;诱使;引起;', 'Doctors said surgery could induce a heart attack.', '医生们说手术有可能引起心脏病发作。');
INSERT INTO `words` VALUES (3475, 'inducement', 'ɪnˈdjuːsmənt', 'n.', '引诱;刺激;诱因;', 'They offer every inducement to foreign businesses to invest in their states...', '他们为外国企业提供种种优厚条件，以吸引这些企业到他们所在的州投资。');
INSERT INTO `words` VALUES (3476, 'induction', 'ɪnˈdʌkʃn', 'n.', '就职;入门;接纳会员;', '...the induction of the girls into the sport.', '指引女孩子们参加该项运动');
INSERT INTO `words` VALUES (3477, 'indulge', 'ɪnˈdʌldʒ', 'v.', '沉湎，沉迷，沉溺(于…);满足(欲望、兴趣等);放纵;', 'Only rarely will she indulge in a glass of wine...', '她只是偶尔才喝杯红酒,让自己享受一下。');
INSERT INTO `words` VALUES (3478, 'indulgence', 'ɪnˈdʌldʒəns', 'n.', '沉溺;放纵;纵容;', 'The king\'s indulgence towards his sons angered the business community...', '国王对儿子们的纵容令商界恼火。');
INSERT INTO `words` VALUES (3479, 'industrial', 'ɪnˈdʌstriəl', 'adj.', '工业的;产业的;用于工业的;', '...industrial machinery and equipment.', '工业机械与设备');
INSERT INTO `words` VALUES (3480, 'industrialist', 'ɪnˈdʌstriəlɪst', 'n.', '工业家;实业家;工厂主;', '...prominent Japanese industrialists.', '杰出的日本实业家');
INSERT INTO `words` VALUES (3481, 'industrious', 'ɪnˈdʌstriəs', 'adj.', '勤奋的;勤劳的;忙碌的;', 'She was an industrious and willing worker.', '她是个勤劳肯干的员工。');
INSERT INTO `words` VALUES (3482, 'industry', 'ˈɪndəstri', 'n.', '工业;生产制造;行业;', 'British industry suffers through insufficient investment in research.', '研究投入不足让英国工业深受其害。');
INSERT INTO `words` VALUES (3483, 'ineffective', 'ˌɪnɪˈfektɪv', 'adj.', '无效果的;不起作用的;不奏效的;', 'Economic reform will continue to be painful and ineffective...', '经济改革仍将举步维艰,毫无成效。');
INSERT INTO `words` VALUES (3484, 'inefficiency', 'ˌɪnɪˈfɪʃənsi', 'n.', '效率低；无效率；无能;', 'Their communication systems are inefficient in the extreme.', '他们的通讯系统效率非常差。');
INSERT INTO `words` VALUES (3485, 'inefficient', 'ˌɪnɪˈfɪʃnt', 'adj.', '效率低的;能力差的;浪费的;', 'Their communication systems are inefficient in the extreme.', '他们的通讯系统效率非常差。');
INSERT INTO `words` VALUES (3486, 'inept', 'ɪˈnept', 'adj.', '缺乏技巧的;无能的;笨拙的;', 'He was inept and lacked the intelligence to govern...', '他没有什么能力,缺乏管理才干。');
INSERT INTO `words` VALUES (3487, 'inert', 'ɪˈnɜːt', 'adj.', '无活动能力的;无行动力的;惰性的;', 'He covered the inert body with a blanket.', '他用毯子把那具一动不动的尸体盖上。');
INSERT INTO `words` VALUES (3488, 'inertia', 'ɪˈnɜːʃə', 'n.', '缺乏活力;惰性;保守;', '...her inertia, her lack of energy...', '她的怠惰和缺乏活力');
INSERT INTO `words` VALUES (3489, 'inevitable', 'ɪnˈevɪtəbl', 'adj.', '不可避免的;不能防止的;总会发生的;', 'If the case succeeds, it is inevitable that other trials will follow...', '如果该案胜诉,其他审判必然随之而来。');
INSERT INTO `words` VALUES (3490, 'inevitably', 'ɪnˈevɪtəbli', 'adv.', '不可避免地;必然地;意料之中;', 'Technological changes will inevitably lead to unemployment...', '技术变革必然会导致失业。');
INSERT INTO `words` VALUES (3491, 'inexact', 'ˌɪnɪɡˈzækt', 'adj.', '不准确的;不精确的;', 'Both explanations were inexact...', '两种解释都不准确。');
INSERT INTO `words` VALUES (3492, 'inexpensive', 'ˌɪnɪkˈspensɪv', 'adj.', '不昂贵的;', '...a variety of good inexpensive restaurants.', '许多价钱实惠的餐馆');
INSERT INTO `words` VALUES (3493, 'inexperienced', 'ˌɪnɪkˈspɪəriənst', 'adj.', '缺乏认识(或经验)的;', 'They are inexperienced when it comes to decorating...', '说到装修，他们就没有什么经验了。');
INSERT INTO `words` VALUES (3494, 'infancy', 'ˈɪnfənsi', 'n.', '婴儿期;幼儿期;初期;', '...minute details of Deborah\'s infancy.', '德博拉幼年生活的详尽细节');
INSERT INTO `words` VALUES (3495, 'infant', 'ˈɪnfənt', 'n.', '婴儿;幼儿;0-1岁的幼儿;', '...young mums with infants in prams...', '用婴儿车推着宝宝的年轻妈妈');
INSERT INTO `words` VALUES (3496, 'infantile', 'ˈɪnfəntaɪl', 'adj.', '婴幼儿特有的;孩子气的;婴儿的;', '...infantile aggression.', '婴儿期的攻击行为');
INSERT INTO `words` VALUES (3497, 'infantry', 'ˈɪnfəntri', 'n.', '(统称)步兵;', 'The infantry were advancing to attack the ridge...', '步兵部队正在向前挺进攻打山脊。');
INSERT INTO `words` VALUES (3498, 'infect', 'ɪnˈfekt', 'v.', '传染;使感染;使携带病菌;', 'A single mosquito can infect a large number of people.', '一只蚊子就可以感染很多人。');
INSERT INTO `words` VALUES (3499, 'infected', 'ɪnˈfektɪd', 'adj.', '带菌的;感染病菌的;', 'In heavily infected areas, half the population become blind.', '在受污染严重的地区,有一半人口失明。');
INSERT INTO `words` VALUES (3500, 'infection', 'ɪnˈfekʃn', 'n.', '传染;感染;(身体某部位的)感染;', 'Ear infections are common in pre-school children...', '耳部感染在学前儿童中很常见。');
INSERT INTO `words` VALUES (3501, 'infectious', 'ɪnˈfekʃəs', 'adj.', '传染性的，感染的(尤指通过呼吸);患有传染病;有传染力;', '...infectious diseases such as measles...', '类似麻疹这样的传染病');
INSERT INTO `words` VALUES (3502, 'infective', 'ɪnˈfektɪv', 'adj.', '会传染的;传染性的;', '...a mild and very common infective disease of children.', '一种不太严重的常见儿童传染疾病');
INSERT INTO `words` VALUES (3503, 'infer', 'ɪnˈfɜː(r)', 'v.', '推断;推论;推理;', 'I inferred from what she said that you have not been well...', '我从她的话语里推断你身体一直不太好。');
INSERT INTO `words` VALUES (3504, 'inference', 'ˈɪnfərəns', 'n.', '推断的结果;结论;推断;', 'There were two inferences to be drawn from her letter.', '由她的信可以得出两个结论。');
INSERT INTO `words` VALUES (3505, 'inferior', 'ɪnˈfɪəriə(r)', 'adj.', '较差的;次的;比不上…的;', 'The cassettes were of inferior quality...', '那些磁带质量较差。');
INSERT INTO `words` VALUES (3506, 'inferiority', 'ɪnˌfɪəriˈɒrəti', 'n.', '低等;劣等;劣势;', 'The cassettes were of inferior quality...', '那些磁带质量较差。');
INSERT INTO `words` VALUES (3507, 'inferiors', 'ɪnˈfɪərɪəz', 'n.', '不如别人的人;级别(或地位)低的人;', 'The cassettes were of inferior quality...', '那些磁带质量较差。');
INSERT INTO `words` VALUES (3508, 'infest', 'ɪnˈfest', 'v.', '大量滋生;大批出没于;', '...pests like aphids which infest cereal crops.', '像蚜虫这样大肆侵害谷物的害虫');
INSERT INTO `words` VALUES (3509, 'infiltration', 'ˌɪnfɪlˈtreɪʃən', 'n.', '渗入；渗透；浸润；渗透物;', 'Activists had infiltrated the student movement...', '激进分子已经渗入到了学生运动中。');
INSERT INTO `words` VALUES (3510, 'infinite', 'ˈɪnfɪnət', 'adj.', '极大的;无法衡量的;无限的;', '...an infinite variety of landscapes...', '变化万千的风景');
INSERT INTO `words` VALUES (3511, 'infinitely', 'ˈɪnfɪnətli', 'adv.', '非常;极其;无限地;', '...an infinite variety of landscapes...', '变化万千的风景');
INSERT INTO `words` VALUES (3512, 'infinity', 'ɪnˈfɪnəti', 'n.', '无限;无穷;无限远的点;', 'These permutations multiply towards infinity...', '这些排列可以增加到无穷大。');
INSERT INTO `words` VALUES (3513, 'infirmary', 'ɪnˈfɜːməri', 'n.', '(常用于名称)医院;(学校、监狱等的)医务室;', 'Mrs Hardie had been taken to the infirmary in an ambulance.', '哈迪夫人被救护车送到了医院。');
INSERT INTO `words` VALUES (3514, 'inflame', 'ɪnˈfleɪm', 'v.', '激起…的强烈感情;(尤指)使愤怒，使激动;使(局势)恶化;', 'The General holds the rebels responsible for inflaming the situation...', '将军认为叛乱者是造成形势激化的原因。');
INSERT INTO `words` VALUES (3515, 'inflammable', 'ɪnˈflæməbl', 'adj.', '易激动的;易激怒的;', '...a highly inflammable liquid.', '高度易燃的液体');
INSERT INTO `words` VALUES (3516, 'inflammation', 'ˌɪnfləˈmeɪʃn', 'n.', '发炎;炎症;', 'The drug can cause inflammation of the liver.', '该药会引起肝脏炎症。');
INSERT INTO `words` VALUES (3517, 'inflate', 'ɪnˈfleɪt', 'v.', '使充气;膨胀;鼓吹;', 'Stuart jumped into the sea and inflated the liferaft...', '斯图尔特跳到海里给救生艇充气。');
INSERT INTO `words` VALUES (3518, 'inflation', 'ɪnˈfleɪʃn', 'n.', '通货膨胀;通胀率;充气;', '...rising unemployment and high inflation.', '不断增长的失业人数与高通货膨胀');
INSERT INTO `words` VALUES (3519, 'inflexible', 'ɪnˈfleksəbl', 'adj.', '缺乏弹性的;僵化的;固守己见的;', 'Workers insisted the new system was too inflexible...', '工人们坚持认为新制度太不灵活。');
INSERT INTO `words` VALUES (3520, 'inflict', 'ɪnˈflɪkt', 'v.', '使遭受打击;使吃苦头;', 'Rebels say they have inflicted heavy casualties on government forces...', '反叛武装称他们使政府军遭受重创。');
INSERT INTO `words` VALUES (3521, 'influence', 'ˈɪnfluəns', 'n.', '影响;作用;支配力;', 'As Hugh grew older, she had less influence and couldn\'t control him...', '休慢慢长大,她的影响力变小了,无法再控制他了。');
INSERT INTO `words` VALUES (3522, 'influential', 'ˌɪnfluˈenʃl', 'adj.', '有很大影响的;有支配力的;', 'It helps to have influential friends.', '有几个位高权重的朋友很有好处。');
INSERT INTO `words` VALUES (3523, 'influx', 'ˈɪnflʌks', 'n.', '(人、资金或事物的)涌入，流入;', '...problems caused by the influx of refugees.', '大批难民涌入产生的问题');
INSERT INTO `words` VALUES (3524, 'info', 'ˈɪnfəʊ', 'n.', '信息;消息;资讯;', 'For more info phone 414-3935.', '若需要更多信息,请拨打电话414-3935。');
INSERT INTO `words` VALUES (3525, 'inform', 'ɪnˈfɔːm', 'v.', '知会;通知;通告;', 'They would inform him of any progress they had made...', '他们会把他们取得的任何进步都告诉他。');
INSERT INTO `words` VALUES (3526, 'informal', 'ɪnˈfɔːml', 'adj.', '不拘礼节的;友好随便的;非正规的;', 'She is refreshingly informal...', '她不拘礼节,让人耳目一新。');
INSERT INTO `words` VALUES (3527, 'information', 'ˌɪnfəˈmeɪʃn', 'n.', '信息;消息;情报;', 'Pat refused to give her any information about Sarah...', '帕特拒绝向她提供任何关于萨拉的消息。');
INSERT INTO `words` VALUES (3528, 'informative', 'ɪnˈfɔːmətɪv', 'adj.', '提供有用信息的;给予知识的;', 'The adverts are not very informative...', '这些广告并没有包含太多有用信息。');
INSERT INTO `words` VALUES (3529, 'infrastructure', 'ˈɪnfrəstrʌktʃə(r)', 'n.', '(国家或机构的)基础设施，基础建设;', '...investment in infrastructure...', '对基础设施的投资');
INSERT INTO `words` VALUES (3530, 'infrequent', 'ɪnˈfriːkwənt', 'adj.', '不常发生的;罕见的;', '...John\'s infrequent visits to London.', '约翰仅有的几次伦敦之行');
INSERT INTO `words` VALUES (3531, 'infringe', 'ɪnˈfrɪndʒ', 'v.', '违背，触犯(法规);侵犯，侵害(合法权益);', 'The film exploited his image and infringed his copyright...', '这部电影使用了他的形象,侵犯了他的肖像权。');
INSERT INTO `words` VALUES (3532, 'infringement', 'ɪnˈfrɪndʒmənt', 'n.', '(对他人权利等的)侵犯，侵害;(对法律、规则等的)违反，违背;', '...infringement of privacy...', '对隐私的侵犯');
INSERT INTO `words` VALUES (3533, 'ingenious', 'ɪnˈdʒiːniəs', 'adj.', '精巧的;新颖独特的;巧妙的;', '...a truly ingenious invention...', '真正有创意的发明');
INSERT INTO `words` VALUES (3534, 'ingenuity', 'ˌɪndʒəˈnjuːəti', 'n.', '独创力;聪明才智;心灵手巧;', 'Inspecting the nest may require some ingenuity.', '探查鸟巢是需要些技巧的。');
INSERT INTO `words` VALUES (3535, 'ingest', 'ɪnˈdʒest', 'v.', '摄入;食入;咽下;', '...side effects occurring in fish that ingest this substance...', '对吸收了该物质的鱼产生的副作用');
INSERT INTO `words` VALUES (3536, 'ingestion', 'ɪndʒɛstʃən', 'n.', '摄食；吞下；吸收;', '...side effects occurring in fish that ingest this substance...', '对吸收了该物质的鱼产生的副作用');
INSERT INTO `words` VALUES (3537, 'ingredient', 'ɪnˈɡriːdiənt', 'n.', '成分;(尤指烹饪)原料;(成功的)因素，要素;', 'Mix in the remaining ingredients.', '混入其余的原料。');
INSERT INTO `words` VALUES (3538, 'inhabit', 'ɪnˈhæbɪt', 'v.', '居住在;栖居于;', 'The valley is inhabited by the Dani tribe.', '山谷里居住着达尼部落。');
INSERT INTO `words` VALUES (3539, 'inhabitant', 'ɪnˈhæbɪtənt', 'n.', '(某地的)居民，栖息动物;', '...the inhabitants of Glasgow.', '格拉斯哥市居民');
INSERT INTO `words` VALUES (3540, 'inhalation', 'ˌɪnhəˈleɪʃn', 'n.', '(空气等的)吸入;吸入药;吸入剂;', '...a complete cycle of inhalation and exhalation...', '吸入与呼出的完整循环');
INSERT INTO `words` VALUES (3541, 'inhale', 'ɪnˈheɪl', 'v.', '吸入;吸气;', 'He took a long slow breath, inhaling deeply...', '他缓缓地深吸了一口气。');
INSERT INTO `words` VALUES (3542, 'inherent', 'ɪnˈherənt', 'adj.', '固有的;内在的;', 'Stress is an inherent part of dieting.', '节食必定会带来压力。');
INSERT INTO `words` VALUES (3543, 'inherit', 'ɪnˈherɪt', 'v.', '继承(金钱、财产等);经遗传获得(品质、身体特征等);接替(责任等);', 'He has no son to inherit his land.', '他没有儿子来继承田产。');
INSERT INTO `words` VALUES (3544, 'inheritance', 'ɪnˈherɪtəns', 'n.', '继承物(如金钱、财产等);遗产继承;遗传特征;', 'She feared losing her inheritance to her stepmother.', '她担心她继承的遗产会被继母夺走。');
INSERT INTO `words` VALUES (3545, 'inhibit', 'ɪnˈhɪbɪt', 'v.', '阻止;阻碍;抑制;', 'Wine or sugary drinks inhibit digestion...', '葡萄酒或含糖饮料抑制消化。');
INSERT INTO `words` VALUES (3546, 'inhibited', 'ɪnˈhɪbɪtɪd', 'adj.', '拘束的;拘谨的;', 'We are rather inhibited about touching each other.', '我们对身体接触感觉很不自在。');
INSERT INTO `words` VALUES (3547, 'inhibition', 'ˌɪnhɪˈbɪʃn', 'n.', '拘谨;拘束感;阻止;', 'The whole point about dancing is to stop thinking and lose all your inhibitions...', '跳舞讲求的就是停止思考,完全放开。');
INSERT INTO `words` VALUES (3548, 'initially', 'ɪˈnɪʃəli', 'adv.', '开始;最初;起初;', 'Forecasters say the gales may not be as bad as they initially predicted...', '预报员说大风也许不像他们起初预报的那么猛烈。');
INSERT INTO `words` VALUES (3549, 'initiative', 'ɪˈnɪʃətɪv', 'n.', '倡议;新方案;主动性;', 'Government initiatives to help young people have been inadequate...', '政府在积极帮助年轻人方面做得还不够。');
INSERT INTO `words` VALUES (3550, 'initiator', 'ɪˈnɪʃieɪtə(r)', 'n.', '发起人;创始人;', '...one of the major initiators of the tumultuous changes in Eastern Europe.', '东欧剧变主要的发起者之一');
INSERT INTO `words` VALUES (3551, 'inject', 'ɪnˈdʒekt', 'v.', '(给…)注射(药物等);(给…)注射(液体);(给…)添加，增加(某品质);', 'His son was injected with strong drugs...', '他儿子被注射了烈性麻醉药。');
INSERT INTO `words` VALUES (3552, 'injection', 'ɪnˈdʒekʃn', 'n.', '注射;大量资金的投入;(液体)注入，喷入;', 'They gave me an injection to help me sleep...', '他们给我打了一针帮助我入睡。');
INSERT INTO `words` VALUES (3553, 'injure', 'ˈɪndʒə(r)', 'v.', '(尤指在事故中)伤害，使受伤;损害，伤害(名誉、自尊等);', 'A number of bombs have exploded, seriously injuring at least five people...', '数个炸弹爆炸,造成至少5人重伤。');
INSERT INTO `words` VALUES (3554, 'injury', 'ˈɪndʒəri', 'n.', '(对躯体的)伤害，损伤;(对感情的)伤害，挫伤;', 'Four police officers sustained serious injuries in the explosion...', '4名警官在爆炸中受重伤。');
INSERT INTO `words` VALUES (3555, 'injustice', 'ɪnˈdʒʌstɪs', 'n.', '不公正，不公平(的对待或行为);', 'They\'ll continue to fight injustice...', '他们将继续与不公平现象作斗争。');
INSERT INTO `words` VALUES (3556, 'ink', 'ɪŋk', 'n.', '墨水;墨汁;油墨;', 'The letter was handwritten in black ink.', '信是用黑色墨水写的。');
INSERT INTO `words` VALUES (3557, 'inlet', 'ˈɪnlet', 'n.', '(海、湖伸向陆地或岛屿间的)小湾，水湾;(液体、空气或气体进入机器的)入口，进口;', '...a sheltered inlet.', '避风的小港湾');
INSERT INTO `words` VALUES (3558, 'inn', 'ɪn', 'n.', '(通常指乡村的，常可夜宿的)小酒店;(通常指乡村的)小旅馆，客栈;用于客栈、旅馆和饭店的名称中;', '...the Waterside Inn.', '水畔客栈');
INSERT INTO `words` VALUES (3559, 'inner', 'ˈɪnə(r)', 'adj.', '里面的;向内的;内部的;', 'She got up and went into an inner office...', '她起身进了里间办公室。');
INSERT INTO `words` VALUES (3560, 'innocence', 'ˈɪnəsns', 'n.', '清白;无辜;无罪;', '...the sweet innocence of youth...', '青春时代的可爱与单纯');
INSERT INTO `words` VALUES (3561, 'innocent', 'ˈɪnəsnt', 'adj.', '无辜的;清白的;无罪的;', 'He was sure that the man was innocent of any crime...', '他确信那个男人没有犯任何罪。');
INSERT INTO `words` VALUES (3562, 'innovate', 'ˈɪnəveɪt', 'v.', '引入(新事物、思想或方法);创新;改革;', '...his constant desire to innovate and experiment.', '他不断进行创新和实验的渴望');
INSERT INTO `words` VALUES (3563, 'innovation', 'ˌɪnəˈveɪʃn', 'n.', '(新事物、思想或方法的)创造;创新;改革;', 'The vegetarian burger was an innovation which was rapidly exported to Britain.', '素食汉堡是一种迅速传到了英国的新开发食品。');
INSERT INTO `words` VALUES (3564, 'innumerable', 'ɪˈnjuːmərəbl', 'adj.', '多得数不清的;很多的;', 'He has invented innumerable excuses, told endless lies.', '他编造了数不清的借口和谎话。');
INSERT INTO `words` VALUES (3565, 'inoculation', 'ɪˌnɒkjʊˈleɪʃən', 'n.', '接种；接木；接插芽;（冶金）孕育（作用，处理）；加孕育剂法；变质处理；培养（植）；加制;', '...a program to inoculate every child in the state...', '为该州所有儿童预防接种的计划');
INSERT INTO `words` VALUES (3566, 'inorganic', 'ˌɪnɔːˈɡænɪk', 'adj.', '无生物的;无机的;', '...roofing made from organic and inorganic fibres.', '有机和无机纤维的屋面材料');
INSERT INTO `words` VALUES (3567, 'inquire', 'ɪn\'kwaɪə(r)', 'v.', '调查；查究；审查;询问；查问;', '\'Is something wrong?\' he enquired...', '“有什么不对劲吗?”他问道。');
INSERT INTO `words` VALUES (3568, 'insane', 'ɪnˈseɪn', 'adj.', '精神失常的;精神错乱的;精神失常的人;', 'Some people simply can\'t take it and they just go insane...', '一些人完全承受不了，结果就精神失常了。');
INSERT INTO `words` VALUES (3569, 'inscribing', 'ɪnˈskraɪbɪŋ', 'v.', '在…上写(词语、名字等);题;刻;', 'Some galleries commemorate donors by inscribing their names on the walls...', '一些美术馆把捐赠者的姓名镌刻在墙上以示纪念。');
INSERT INTO `words` VALUES (3570, 'inscription', 'ɪnˈskrɪpʃn', 'n.', '(书首页的)题词;(石头或金属上)刻写的文字，铭刻，碑文;', 'Above its doors was a Latin inscription: Non omnia possumus omnes...', '门的上方刻有拉丁文铭“人非万能”。');
INSERT INTO `words` VALUES (3571, 'insider', 'ɪnˈsaɪdə(r)', 'n.', '知内情者;内部的人;', 'An insider said, \'Katharine has told friends it is time to end her career.\'', '一位知情人说：“凯瑟琳已告诉朋友准备结束她的事业”。');
INSERT INTO `words` VALUES (3572, 'insight', 'ˈɪnsaɪt', 'n.', '洞察力;领悟;洞悉;', 'The project would give scientists new insights into what is happening to the earth\'s atmosphere...', '该项目将使科学家对地球大气层的状况有更新的深入了解。');
INSERT INTO `words` VALUES (3573, 'insignificant', 'ˌɪnsɪɡˈnɪfɪkənt', 'adj.', '微不足道的;无足轻重的;', 'In 1949 Bonn was a small, insignificant city.', '1949 年时波恩还是个无足轻重的小城市。');
INSERT INTO `words` VALUES (3574, 'insipid', 'ɪnˈsɪpɪd', 'adj.', '无味道的;淡而无味的;没有趣味的;', 'It tasted indescribably bland and insipid, like warmed cardboard.', '这东西尝起来淡而无味至极,像加热过的纸板一样。');
INSERT INTO `words` VALUES (3575, 'insist', 'ɪnˈsɪst', 'v.', '坚决要求;坚持;坚持说;', 'My family insisted that I should not give in, but stay and fight...', '家里人坚持认为我不应让步,而应该留下来继续抗争。');
INSERT INTO `words` VALUES (3576, 'insistent', 'ɪnˈsɪstənt', 'adj.', '坚决要求的;坚持的;固执的;', 'The men are very insistent that you leave right away.', '他们坚决要求你立即离开。');
INSERT INTO `words` VALUES (3577, 'insolent', 'ˈɪnsələnt', 'adj.', '粗野的;无礼的;侮慢的;', '...her insolent stare...', '她无礼的瞪视');
INSERT INTO `words` VALUES (3578, 'insolvent', 'ɪnˈsɒlvənt', 'adj.', '无力偿付债务的;破产的;', 'The bank was declared insolvent.', '银行被宣布破产。');
INSERT INTO `words` VALUES (3579, 'inspect', 'ɪnˈspekt', 'v.', '检查;查看;审视;', 'Elaine went outside to inspect the playing field...', '伊莱恩走到外面查看操场。');
INSERT INTO `words` VALUES (3580, 'inspection', 'ɪnˈspekʃn', 'n.', '视察;检查;查看;', 'Elaine went outside to inspect the playing field...', '伊莱恩走到外面查看操场。');
INSERT INTO `words` VALUES (3581, 'inspector', 'ɪnˈspektə(r)', 'n.', '检查员;视察员;巡视员;', 'The mill was finally shut down by state safety inspectors.', '工厂终于遭国家安全检查员关闭了。');
INSERT INTO `words` VALUES (3582, 'inspiration', 'ˌɪnspəˈreɪʃn', 'n.', '灵感;启发灵感的人(或事物);使人产生动机的人(或事物);', 'My inspiration comes from poets like Baudelaire and Jacques Prévert...', '我的灵感源于像波德莱尔和雅克·普雷韦这样的诗人。');
INSERT INTO `words` VALUES (3583, 'inspire', 'ɪnˈspaɪə(r)', 'v.', '激励;鼓舞;赋予灵感;', 'These herbs will inspire you to try out all sorts of exotic-flavoured dishes!...', '这些香草会激发你去品尝各种异国风味的菜肴！');
INSERT INTO `words` VALUES (3584, 'inspired', 'ɪnˈspaɪəd', 'adj.', '品质优秀的;能力卓越的;借助于灵感创作的;', 'These herbs will inspire you to try out all sorts of exotic-flavoured dishes!...', '这些香草会激发你去品尝各种异国风味的菜肴！');
INSERT INTO `words` VALUES (3585, 'inspiring', 'ɪnˈspaɪərɪŋ', 'adj.', '鼓舞人心的;激励的;启发灵感的;', 'She was one of the most inspiring people I\'ve ever met...', '她是我曾见过的最能鼓舞人心的人之一。');
INSERT INTO `words` VALUES (3586, 'instability', 'ˌɪnstəˈbɪləti', 'n.', '不稳定;不稳固;(精神的)不稳定状态，变化无常;', '...unpopular policies, which resulted in social discontent and political instability.', '导致社会不满与政治不稳定的不得人心的政策');
INSERT INTO `words` VALUES (3587, 'install', 'ɪnˈstɔːl', 'v.', '安装;设置;建立(程序);', 'They had installed a new phone line in the apartment.', '他们已经在公寓里装上了新的电话线。');
INSERT INTO `words` VALUES (3588, 'installation', 'ˌɪnstəˈleɪʃn', 'n.', '安装;设置;安装的设备(或机器);', 'The building was turned into a secret military installation.', '该建筑被改造成了一处秘密军事设施。');
INSERT INTO `words` VALUES (3589, 'instance', 'ˈɪnstəns', 'n.', '例子;事例;实例;', 'There are a number of improvements; for instance, both mouse buttons can now be used...', '在许多地方有了改进,例如,鼠标的左右键都可以使用了。');
INSERT INTO `words` VALUES (3590, 'instant', 'ˈɪnstənt', 'adj.', '立即的;立刻的;速食的;', 'For an instant, Catherine was tempted to flee...', '有那么一会儿,凯瑟琳很想逃跑。');
INSERT INTO `words` VALUES (3591, 'instantaneous', 'ˌɪnstənˈteɪniəs', 'adj.', '立即的;立刻的;瞬间的;', 'Death was instantaneous because both bullets hit the heart.', '因为两颗子弹都击中了心脏,所以死亡是瞬间发生的。');
INSERT INTO `words` VALUES (3592, 'instantly', 'ˈɪnstəntli', 'adv.', '立刻;立即;马上;', 'For an instant, Catherine was tempted to flee...', '有那么一会儿,凯瑟琳很想逃跑。');
INSERT INTO `words` VALUES (3593, 'instead', 'ɪnˈsted', 'adv.', '代替;顶替;反而;', 'She had to spend nearly four months away from him that summer, instead of the usual two...', '那个夏天她被迫离开他将近四个月而不是通常的两个月。');
INSERT INTO `words` VALUES (3594, 'instinct', 'ˈɪnstɪŋkt', 'n.', '本能;天性;直觉;', 'I didn\'t have as strong a maternal instinct as some other mothers...', '我不像别的母亲那样有那么强烈的母性本能。');
INSERT INTO `words` VALUES (3595, 'instinctive', 'ɪnˈstɪŋktɪv', 'adj.', '本能的;直觉的;天生的;', 'It\'s an absolutely instinctive reaction—if a child falls you pick it up...', '这完全是本能的反应——如果一个孩子摔倒了你会把他扶起来。');
INSERT INTO `words` VALUES (3596, 'institute', 'ˈɪnstɪtjuːt', 'n.', '(教育、专业等)机构，机构建筑;', '...the National Cancer Institute.', '国家癌症研究所');
INSERT INTO `words` VALUES (3597, 'institution', 'ˌɪnstɪˈtjuːʃn', 'n.', '(大学、银行等规模大的)机构;慈善机构;社会福利机构;', '...the Institution of Civil Engineers...', '土木工程学院');
INSERT INTO `words` VALUES (3598, 'instruct', 'ɪnˈstrʌkt', 'v.', '指示;命令;吩咐;', 'The family has instructed solicitors to sue Thomson for compensation...', '那家人已经指示律师起诉汤姆森,要求赔偿。');
INSERT INTO `words` VALUES (3599, 'instruction', 'ɪnˈstrʌkʃn', 'n.', '用法说明;操作指南;指示;', 'Two lawyers were told not to leave the building but no reason for this instruction was given.', '两名律师被告知不得离开大楼,但对这一命令并没有提供任何理由。');
INSERT INTO `words` VALUES (3600, 'instructive', 'ɪnˈstrʌktɪv', 'adj.', '富有教益的;增长知识的;', 'It\'s instructive to compare his technique with Alan Bennett\'s.', '把他的手法与艾伦·贝内特进行比较很富启发意义。');
INSERT INTO `words` VALUES (3601, 'instructor', 'ɪnˈstrʌktə(r)', 'n.', '教练;导师;(大学)讲师;', '...tuition from an approved driving instructor.', '有正式资格的驾驶教练的指导');
INSERT INTO `words` VALUES (3602, 'instrument', 'ˈɪnstrəmənt', 'n.', '器械;仪器;器具;', '...a thin tube-like optical instrument.', '试管状的细长光学仪器');
INSERT INTO `words` VALUES (3603, 'instrumental', 'ˌɪnstrəˈmentl', 'adj.', '起重要作用;用乐器演奏的;为乐器谱写的;', 'In his first years as chairman he was instrumental in raising the company\'s wider profile...', '他任董事长的最初几年为提升公司的知名度起到了积极的推动作用。');
INSERT INTO `words` VALUES (3604, 'instrumentation', 'ˌɪnstrəmenˈteɪʃn', 'n.', '(一套)仪器，仪表;器乐谱写;', 'Basic flight instrumentation was similar on both planes...', '两架飞机的基本飞行仪表相类似。');
INSERT INTO `words` VALUES (3605, 'insufficient', 'ˌɪnsəˈfɪʃnt', 'adj.', '不充分的;不足的;不够重要的;', 'He decided there was insufficient evidence to justify criminal proceedings...', '他认定证据不充分无法提起刑事诉讼。');
INSERT INTO `words` VALUES (3606, 'insulate', 'ˈɪnsjuleɪt', 'v.', '使隔热;使隔音;使绝缘;', 'They wonder if their community is no longer insulated from big city problems...', '他们在想是否大城市的问题离自己的社区也已经不再遥远了。');
INSERT INTO `words` VALUES (3607, 'insulation', 'ˌɪnsjuˈleɪʃn', 'n.', '隔热;隔音;绝缘;', 'High electricity bills point to a poor heating system or bad insulation...', '高额电费开支说明供暖系统不足或建筑隔热材料不良。');
INSERT INTO `words` VALUES (3608, 'insulator', 'ˈɪnsjuleɪtə(r)', 'n.', '隔热(或绝缘、隔音等的)材料(或装置);', 'Fat is an excellent insulator against the cold.', '脂肪是极好的抵御寒冷的绝热体。');
INSERT INTO `words` VALUES (3609, 'insulin', 'ˈɪnsjəlɪn', 'n.', '胰岛素;', 'In diabetes the body produces insufficient insulin.', '糖尿病患者体内不能分泌足够的胰岛素。');
INSERT INTO `words` VALUES (3610, 'insurance', 'ɪnˈʃʊərəns', 'n.', '保险;保险业;保险费;', 'The insurance company paid out for the stolen jewellery and silver...', '保险公司赔付了遭窃的珠宝和银器。');
INSERT INTO `words` VALUES (3611, 'insure', 'ɪnˈʃʊə(r)', 'v.', '投保;给…保险;接受投保;', 'For protection against unforeseen emergencies, you insure your house, your furnishings and your car...', '为了防范不可预见的紧急情况,人们给房子、家具和汽车上保险。');
INSERT INTO `words` VALUES (3612, 'intact', 'ɪnˈtækt', 'adj.', '完好无损;完整;', 'Most of the cargo was left intact after the explosion...', '大部分货物在爆炸发生后都完好无损。');
INSERT INTO `words` VALUES (3613, 'intake', 'ˈɪnteɪk', 'n.', '(食物、饮料等的)摄取量，吸入量;(一定时期内)纳入的人数;(机器上的液体、空气等的)进口;', 'Your intake of alcohol should not exceed two units per day...', '每天的饮酒量不应超过两个计量单位。');
INSERT INTO `words` VALUES (3614, 'intangible', 'ɪnˈtændʒəbl', 'adj.', '难以形容(或理解)的;不易度量的;无形的(指没有实体存在的资本性资产);', 'There are intangible benefits beyond a rise in the share price.', '除股价上升之外还有无形利益。');
INSERT INTO `words` VALUES (3615, 'integral', 'ˈɪntɪɡrəl', 'adj.', '必需的;不可或缺的;作为组成部分的;', 'Rituals and festivals form an integral part of every human society...', '仪式与节日构成了任何人类社会不可缺少的一部分。');
INSERT INTO `words` VALUES (3616, 'integrate', 'ˈɪntɪɡreɪt', 'v.', '(使)合并，成为一体;(使)加入，融入群体;', 'He didn\'t integrate successfully into the Italian way of life...', '他没有顺利地融入到意大利的生活方式中。');
INSERT INTO `words` VALUES (3617, 'integrated', 'ˈɪntɪɡreɪtɪd', 'adj.', '各部分密切协调的;综合的;完整统一的;', 'We believe that pupils of integrated schools will have more tolerant attitudes.', '我们相信在取消种族隔离的学校就读的学生会有更宽容的态度。');
INSERT INTO `words` VALUES (3618, 'integration', 'ˌɪntɪˈɡreɪʃn', 'n.', '结合;整合;一体化;', 'He didn\'t integrate successfully into the Italian way of life...', '他没有顺利地融入到意大利的生活方式中。');
INSERT INTO `words` VALUES (3619, 'integrity', 'ɪnˈteɡrəti', 'n.', '诚实正直;完整;完好;', 'I have always regarded him as a man of integrity...', '我一直认为他正直诚实。');
INSERT INTO `words` VALUES (3620, 'intellect', 'ˈɪntəlekt', 'n.', '(尤指高等的)智力，思维逻辑领悟力;智力高的人;才智超群的人;', 'Do the emotions develop in parallel with the intellect?...', '情感与智力是并行发展的吗？');
INSERT INTO `words` VALUES (3621, 'intellectual', 'ˌɪntəˈlektʃuəl', 'adj.', '智力的;脑力的;理智的;', 'High levels of lead could damage the intellectual development of children...', '铅含量过高会损害儿童的智力发展。');
INSERT INTO `words` VALUES (3622, 'intelligence', 'ɪnˈtelɪdʒəns', 'n.', '智力;才智;智慧;', 'She\'s a woman of exceptional intelligence.', '她是个有着非凡智慧的女子。');
INSERT INTO `words` VALUES (3623, 'intelligent', 'ɪnˈtelɪdʒənt', 'adj.', '有才智的;悟性强的;聪明的;', 'Susan\'s a very bright and intelligent woman who knows her own mind.', '苏珊是个非常聪明、有主见的女子。');
INSERT INTO `words` VALUES (3624, 'intelligible', 'ɪnˈtelɪdʒəbl', 'adj.', '易懂的;容易理解的;', 'The language of Darwin was intelligible to experts and non-experts alike...', '达尔文的语言无论对专家还是非专业人士而言都明白易懂。');
INSERT INTO `words` VALUES (3625, 'intend', 'ɪnˈtend', 'v.', '打算;计划;想要;', 'She intends to do A levels and go to university...', '她计划先参加高级考试，然后去读大学。');
INSERT INTO `words` VALUES (3626, 'intense', 'ɪnˈtens', 'adj.', '很大的;十分强烈的;严肃紧张的;', 'He was sweating from the intense heat...', '高温酷热让他大汗淋漓。');
INSERT INTO `words` VALUES (3627, 'intensify', 'ɪnˈtensɪfaɪ', 'v.', '(使)加强，增强，加剧;', 'Britain is intensifying its efforts to secure the release of the hostages...', '英国正加紧努力确保人质的获释。');
INSERT INTO `words` VALUES (3628, 'intensity', 'ɪnˈtensəti', 'n.', '强烈;紧张;剧烈;', 'He was sweating from the intense heat...', '高温酷热让他大汗淋漓。');
INSERT INTO `words` VALUES (3629, 'intensive', 'ɪnˈtensɪv', 'adj.', '短时间内集中紧张进行的;密集的;彻底的;', '...several days and nights of intensive negotiations...', '几天几夜紧锣密鼓的谈判');
INSERT INTO `words` VALUES (3630, 'intent', 'ɪnˈtent', 'adj.', '热切的;专注的;决心做(尤指伤害他人的事);', 'The rebels are obviously intent on keeping up the pressure...', '叛乱分子显然决心继续施压。');
INSERT INTO `words` VALUES (3631, 'intention', 'ɪnˈtenʃn', 'n.', '打算;计划;意图;', 'Beveridge announced his intention of standing for parliament...', '贝弗里奇宣布他打算竞选议员。');
INSERT INTO `words` VALUES (3632, 'intentional', 'ɪnˈtenʃənl', 'adj.', '故意的;有意的;存心的;', 'Women who are the victims of intentional discrimination will be able to get compensation...', '受到故意歧视的女性将会得到赔偿。');
INSERT INTO `words` VALUES (3633, 'interact', 'ˌɪntərˈækt', 'v.', '交流;沟通;合作;', 'While the other children interacted and played together, Ted ignored them.', '当别的孩子相互交流、一起玩耍的时候,泰德对他们不予理睬。');
INSERT INTO `words` VALUES (3634, 'interaction', 'ˌɪntərˈækʃən', 'n.', '相互影响（作用，制约，配合）；交互作用（影响）；交相感应；干扰（涉）;', 'While the other children interacted and played together, Ted ignored them.', '当别的孩子相互交流、一起玩耍的时候,泰德对他们不予理睬。');
INSERT INTO `words` VALUES (3635, 'interactive', 'ˌɪntərˈæktɪv', 'adj.', '合作的;相互影响的;互相配合的;', 'This will make videogames more interactive than ever.', '这将使电子游戏的互动性更胜以往。');
INSERT INTO `words` VALUES (3636, 'intercept', 'ˌɪntəˈsept', 'v.', '拦截;拦阻;截住;', 'Gunmen intercepted him on his way to the airport...', '持枪歹徒在他去机场的路上截击了他。');
INSERT INTO `words` VALUES (3637, 'intercepting', 'ˌɪntəˈseptɪŋ', 'v.', '拦截;拦阻;截住;', 'Gunmen intercepted him on his way to the airport...', '持枪歹徒在他去机场的路上截击了他。');
INSERT INTO `words` VALUES (3638, 'interconnect', 'ˌɪntəkəˈnekt', 'v.', '(使类似的事物)相联系，相互联系，相互连接;', 'The causes are many and may interconnect...', '原因有很多而且可能互相关联。');
INSERT INTO `words` VALUES (3639, 'interconnections', 'ˌɪntə(ː)kəˈnɛkʃənz', 'n.', '相互关联;相互联系;', '...the thematic interconnection between the stories.', '这些故事主题之间的相互关联');
INSERT INTO `words` VALUES (3640, 'intercourse', 'ˈɪntəkɔːs', 'n.', '(人、国家等之间的)往来，交往，交际;', '...sexual intercourse...', '性交');
INSERT INTO `words` VALUES (3641, 'interest', 'ˈɪntrəst', 'n.', '兴趣;关注;引人关注的性质;', 'There has been a lively interest in the elections in the last two weeks...', '近两周来选举受到了热烈关注。');
INSERT INTO `words` VALUES (3642, 'interested', 'ˈɪntrəstɪd', 'adj.', '感兴趣的;关心的;表现出兴趣的;', 'I thought she might be interested in Paula\'s proposal...', '我觉得她对葆拉的提议可能会感兴趣。');
INSERT INTO `words` VALUES (3643, 'interesting', 'ˈɪntrəstɪŋ', 'adj.', '有趣的;有吸引力的;', 'It was interesting to be in a different environment...', '到一个全新的环境中是很有意思的。');
INSERT INTO `words` VALUES (3644, 'interface', 'ˈɪntəfeɪs', 'n.', '(人机)界面(尤指屏幕布局和菜单);接口;接口程序;', '...a witty exploration of that interface between bureaucracy and the working world.', '对官僚阶层与劳动大众之间的临界区域的风趣探究');
INSERT INTO `words` VALUES (3645, 'interfere', 'ˌɪntəˈfɪə(r)', 'v.', '干涉;干预;介入;', 'I wish everyone would stop interfering and just leave me alone...', '我希望大家都别再来干涉我,让我一个人静一静。');
INSERT INTO `words` VALUES (3646, 'interference', 'ˌɪntəˈfɪərəns', 'n.', '干涉;干预;介入;', 'The parliament described the decree as interference in the republic\'s internal affairs...', '议会形容此法令为对共和国内政的干涉。');
INSERT INTO `words` VALUES (3647, 'interim', 'ˈɪntərɪm', 'adj.', '暂时的;过渡的;期中的;', 'She was sworn in as head of an interim government in March.', '她3月份宣誓就任过渡政府首脑。');
INSERT INTO `words` VALUES (3648, 'interior', 'ɪnˈtɪəriə(r)', 'n.', '内部;里面;内陆;', 'The interior of the house was furnished with heavy, old-fashioned pieces...', '房子里摆放着笨重的旧式家具。');
INSERT INTO `words` VALUES (3649, 'interlink', 'ˌɪntəˈlɪŋk', 'v.', '(使)连接;', 'Those two processes are very closely interlinked...', '那两个过程紧密相连。');
INSERT INTO `words` VALUES (3650, 'interlock', 'ˌɪntəˈlɒk', 'v.', '(使)连锁，紧密连接，扣紧;', 'The parts interlock...', '各部分相互扣住。');
INSERT INTO `words` VALUES (3651, 'intermediary', 'ˌɪntəˈmiːdiəri', 'n.', '中间人;调解人;', 'She wanted him to act as an intermediary in the dispute with Moscow.', '她想让他充当中间人来调解与莫斯科的争端。');
INSERT INTO `words` VALUES (3652, 'intermediate', 'ˌɪntəˈmiːdiət', 'adj.', '(两地、两物、两种状态等)之间的，中间的;中级的;中等的;', 'You should consider breaking the journey with intermediate stopovers at airport hotels.', '你应该考虑将旅程分为几段,中途在机场旅馆稍作停留。');
INSERT INTO `words` VALUES (3653, 'intermittent', 'ˌɪntəˈmɪtənt', 'adj.', '断断续续的;间歇的;', 'After three hours of intermittent rain, the game was abandoned.', '3 个小时断断续续的降雨之后,比赛取消了。');
INSERT INTO `words` VALUES (3654, 'internal', 'ɪnˈtɜːnl', 'adj.', '内部的;里面的;体内的;', 'The country stepped up internal security...', '该国加强了国内安保。');
INSERT INTO `words` VALUES (3655, 'internally', 'ɪnˈtɜːnəli', 'adv.', '内部地；内在地；国内地;', 'The country stepped up internal security...', '该国加强了国内安保。');
INSERT INTO `words` VALUES (3656, 'international', 'ˌɪntəˈnæʃnəl', 'adj.', '国际的;', '...an international agreement against exporting arms to that country.', '禁止向该国出口武器的国际协定');
INSERT INTO `words` VALUES (3657, 'interpose', 'ˌɪntəˈpəʊz', 'v.', '插进(问题或话语);将…置于(二者)之间;插入;', 'Police had to interpose themselves between the two rival groups...', '警方不得不对这两个对立团体进行干预。');
INSERT INTO `words` VALUES (3658, 'interpret', 'ɪnˈtɜːprət', 'v.', '诠释;说明;把…理解为;', 'The whole speech might well be interpreted as a coded message to the Americans...', '整个演讲完全可以看成是在向美国人传递编码信息。');
INSERT INTO `words` VALUES (3659, 'interpretation', 'ɪnˌtɜːprəˈteɪʃn', 'n.', '理解;解释;说明;', 'The opposition Conservative Party put a different interpretation on the figures...', '反对党保守党对这些数字提出一番不同的解释。');
INSERT INTO `words` VALUES (3660, 'interpreter', 'ɪnˈtɜːprətə(r)', 'n.', '口译工作者;口译译员;演绎(音乐、戏剧中人物等)的人;', 'Aristide spoke to the press through an interpreter.', '阿里斯蒂德通过口译员向媒体发表讲话。');
INSERT INTO `words` VALUES (3661, 'interrelate', 'ˌɪntərɪˈleɪt', 'v.', '相互关联(或影响);', 'The body and the mind interrelate...', '身体与头脑相互影响。');
INSERT INTO `words` VALUES (3662, 'interrelated', 'ˌɪntərɪˈleɪtɪd', 'v.', '相互关联(或影响);', 'The body and the mind interrelate...', '身体与头脑相互影响。');
INSERT INTO `words` VALUES (3663, 'interrogate', 'ɪnˈterəɡeɪt', 'v.', '讯问;审问;盘问;', 'I interrogated everyone even slightly involved.', '我讯问了每个人,甚至连稍有关联的人都没放过。');
INSERT INTO `words` VALUES (3664, 'interrupt', 'ˌɪntəˈrʌpt', 'v.', '插嘴;打扰;打岔;', 'Turkin tapped him on the shoulder. \'Sorry to interrupt, Colonel.\'...', '图尔金拍拍他的肩膀。“不好意思打断您一下,上校。”');
INSERT INTO `words` VALUES (3665, 'interruption', 'ˌɪntəˈrʌpʃn', 'n.', '阻断物;中断时间;打扰;', 'Turkin tapped him on the shoulder. \'Sorry to interrupt, Colonel.\'...', '图尔金拍拍他的肩膀。“不好意思打断您一下,上校。”');
INSERT INTO `words` VALUES (3666, 'intersection', 'ˌɪntəˈsekʃn', 'n.', '十字路口;交叉路口;交点;', '...at the intersection of two main canals.', '在两条大运河的交汇处');
INSERT INTO `words` VALUES (3667, 'interval', 'ˈɪntəvl', 'n.', '(时间上的)间隔，间隙，间歇;(戏剧、电影或音乐会的)幕间休息，休息时间;(其他事情)穿插出现的间隙;', 'The ferry service has restarted after an interval of 12 years...', '时隔12年之后，轮渡服务又重新开通了。');
INSERT INTO `words` VALUES (3668, 'intervals', 'ˈɪntəvəlz', 'n.', '(时间上的)间隔，间隙，间歇;(戏剧、电影或音乐会的)幕间休息，休息时间;(其他事情)穿插出现的间隙;', 'The ferry service has restarted after an interval of 12 years...', '时隔12年之后，轮渡服务又重新开通了。');
INSERT INTO `words` VALUES (3669, 'intervene', 'ˌɪntəˈviːn', 'v.', '出面;介入;插嘴;', 'The situation calmed down when police intervened...', '警方干预后，局势平息下来。');
INSERT INTO `words` VALUES (3670, 'intervention', 'ˌɪntəˈvenʃn', 'n.', '干涉;干预;', '...the role of the United States and its intervention in the internal affairs of many countries.', '美国扮演的角色以及其对多国内政的干涉');
INSERT INTO `words` VALUES (3671, 'interview', 'ˈɪntəvjuː', 'n.', '面试;面谈;(常指公开的)记者采访，访谈;', 'When I went for my first interview for this job I arrived extremely early...', '在参加这份工作的初次面试时,我到得非常早。');
INSERT INTO `words` VALUES (3672, 'intestine', 'ɪnˈtestɪn', 'n.', '肠;', 'This area is always tender to the touch if the intestines are not functioning properly...', '如果肠功能不正常,这个部位碰一下就会感到疼痛。');
INSERT INTO `words` VALUES (3673, 'intimation', 'ˌɪntɪˈmeɪʃn', 'n.', '透露;间接表示;暗示;', '...intimations of mortality...', '濒临死亡的征兆');
INSERT INTO `words` VALUES (3674, 'intimidate', 'ɪnˈtɪmɪdeɪt', 'v.', '恐吓;威胁;', 'Jones had set out to intimidate and dominate Paul...', '琼斯开始恐吓并控制保罗。');
INSERT INTO `words` VALUES (3675, 'intonation', 'ˌɪntəˈneɪʃn', 'n.', '语调;(演奏或唱歌中的)音准;', 'His voice had a very slight German intonation.', '他说话稍微带一点儿德国腔。');
INSERT INTO `words` VALUES (3676, 'intricate', 'ˈɪntrɪkət', 'adj.', '错综复杂的;', '...intricate patterns and motifs.', '复杂的式样与图案');
INSERT INTO `words` VALUES (3677, 'intrinsic', 'ɪnˈtrɪnzɪk', 'adj.', '固有的;内在的;本身的;', 'The paintings have no intrinsic value except as curiosities...', '这些油画除了作为古董，本身没有什么内在价值。');
INSERT INTO `words` VALUES (3678, 'intro', 'ˈɪntrəʊ', 'n.', '介绍;(尤指)前奏，前言，导言;', '...the keyboard intro to The Who\'s \'Won\'t Get Fooled Again\'.', '谁人乐队的《不会再受骗》的钢琴前奏');
INSERT INTO `words` VALUES (3679, 'introduce', 'ˌɪntrəˈdjuːs', 'v.', '把…介绍(给);引见;(自我)介绍;', 'The Government has introduced a number of other money-saving moves...', '政府已经采取了其他一些省钱的举措。');
INSERT INTO `words` VALUES (3680, 'introduction', 'ˌɪntrəˈdʌkʃn', 'n.', '初次投入使用;采用;引进;', 'Ellen Malos, in her introduction to \'The Politics of Housework\', provides a summary of the debates.', '埃伦·马洛斯在她为《家务政治学》一书所作的序言中对这些争论进行了总结。');
INSERT INTO `words` VALUES (3681, 'introductory', 'ˌɪntrəˈdʌktəri', 'adj.', '序言的;引导的;介绍的;', '...an introductory course in religion and theology.', '宗教与神学入门课程');
INSERT INTO `words` VALUES (3682, 'intrude', 'ɪnˈtruːd', 'v.', '闯入;侵入;打扰;', 'The press has been blamed for intruding into people\'s personal lives in an unacceptable way...', '新闻媒体因为以令人难以容忍的方式侵扰人们的私生活而一直受到指责。');
INSERT INTO `words` VALUES (3683, 'intuition', 'ˌɪntjuˈɪʃn', 'n.', '直觉力;(一种)直觉;', 'Her intuition was telling her that something was wrong...', '她的直觉告诉她一定出了什么问题。');
INSERT INTO `words` VALUES (3684, 'inundate', 'ˈɪnʌndeɪt', 'v.', '使不胜负荷;使应接不暇;淹没;', 'Her office was inundated with requests for tickets...', '她所在的售票处收到铺天盖地的购票请求。');
INSERT INTO `words` VALUES (3685, 'invade', 'ɪnˈveɪd', 'v.', '武装入侵;侵略;侵犯;', 'In autumn 1944 the allies invaded the Italian mainland at Anzio and Salerno...', '1944 年秋，盟军从安齐奥与萨勒诺攻入意大利本土。');
INSERT INTO `words` VALUES (3686, 'invader', 'ɪnˈveɪdə(r)', 'n.', '武装入侵的军队(或国家);侵略者;', 'The invaders were only finally crushed when troops overcame them at Glenshiel in June 1719.', '1719 年 6 月，部队在格兰舍一役击败了入侵者，直至此时他们才最终覆灭。');
INSERT INTO `words` VALUES (3687, 'invalidate', 'ɪnˈvælɪdeɪt', 'v.', '证明…错误;使站不住脚;使无效;', 'Any form of physical activity will invalidate the results...', '任何形式的物理活动都会证明这些结果是错误的。');
INSERT INTO `words` VALUES (3688, 'invaluable', 'ɪnˈvæljuəbl', 'adj.', '极有用的;极宝贵的;', 'I was able to gain invaluable experience over that year...', '在那一年里我有幸获得了非常宝贵的经验。');
INSERT INTO `words` VALUES (3689, 'invariable', 'ɪnˈveəriəbl', 'adj.', '始终如一的;永无变化的;', 'It was his invariable custom to have one whisky before his supper.', '晚饭前来杯威士忌,这是他一直以来的习惯。');
INSERT INTO `words` VALUES (3690, 'invariably', 'ɪnˈveəriəbli', 'adv.', '始终如一地;一贯地;', 'They almost invariably get it wrong...', '他们几乎总是理解错。');
INSERT INTO `words` VALUES (3691, 'invasion', 'ɪnˈveɪʒn', 'n.', '武装入侵;侵略;侵犯;', '...seven years after the Roman invasion of Britain...', '罗马人入侵英国7年以后');
INSERT INTO `words` VALUES (3692, 'invent', 'ɪnˈvent', 'v.', '发明;创造;编造;', 'He invented the first electric clock...', '他发明了第一个电动机械钟。');
INSERT INTO `words` VALUES (3693, 'invention', 'ɪnˈvenʃn', 'n.', '发明;创意;创造;', 'It\'s been a tricky business marketing his new invention...', '推广他的新发明一直是件棘手的事情。');
INSERT INTO `words` VALUES (3694, 'inventive', 'ɪnˈventɪv', 'adj.', '善于创新的;有创意的;有新意的;', '...Stroman\'s ceaselessly inventive choreography...', '斯特罗曼永远求新的舞蹈设计');
INSERT INTO `words` VALUES (3695, 'inventor', 'ɪnˈventə(r)', 'n.', '发明者;发明家;创造者;', '...Alexander Graham Bell, the inventor of the telephone.', '亚历山大·格雷厄姆·贝尔,电话的发明者');
INSERT INTO `words` VALUES (3696, 'inventory', 'ˈɪnvəntri', 'n.', '(建筑物里的物品、家具等的)清单;财产清单;(商店的)存货，库存;', 'Before starting, he made an inventory of everything that was to stay.', '开始之前,他把所有要留下的东西列了详细的清单。');
INSERT INTO `words` VALUES (3697, 'inverse', 'ˌɪnˈvɜːs', 'adj.', '(数量、位置)相反的，反向的;反面;相反的事物;', 'The tension grew in inverse proportion to the distance from their final destination.', '拉力的大小与他们离终点的距离成反比。');
INSERT INTO `words` VALUES (3698, 'inversely', '', 'adv.', '相反地；逆向地；反之;成反比地;', 'The tension grew in inverse proportion to the distance from their final destination.', '拉力的大小与他们离终点的距离成反比。');
INSERT INTO `words` VALUES (3699, 'inversion', 'ɪnˈvɜːʃn', 'n.', '倒置;颠倒;倒转;', '...a scandalous inversion of the truth.', '令人不齿的是非颠倒');
INSERT INTO `words` VALUES (3700, 'invert', 'ɪnˈvɜːt', 'v.', '(使)倒转，颠倒，倒置;', 'Invert the cake onto a cooling rack.', '把蛋糕倒扣在冷却架上。');
INSERT INTO `words` VALUES (3701, 'invest', 'ɪnˈvest', 'v.', '投资;(把资金)投入;投入(时间、精力等);', 'They intend to invest directly in shares...', '他们打算直接投资股票。');
INSERT INTO `words` VALUES (3702, 'investigate', 'ɪnˈvestɪɡeɪt', 'v.', '侦查(某事);调查(某人);研究;', 'Gas officials are investigating the cause of an explosion which badly damaged a house in Hampshire...', '煤气系统的官员正在调查一起瓦斯爆炸的起因,这起事故严重毁坏了汉普郡的一所房屋。');
INSERT INTO `words` VALUES (3703, 'investigation', 'ɪnˌvestɪˈɡeɪʃn', 'n.', '(正式的)调查，侦查;科学研究;学术研究;', 'Gas officials are investigating the cause of an explosion which badly damaged a house in Hampshire...', '煤气系统的官员正在调查一起瓦斯爆炸的起因,这起事故严重毁坏了汉普郡的一所房屋。');
INSERT INTO `words` VALUES (3704, 'investment', 'ɪnˈvestmənt', 'n.', '投资;投资额;投资物;', 'He said the government must introduce tax incentives to encourage investment...', '他说政府必须推出税收激励政策来鼓励投资。');
INSERT INTO `words` VALUES (3705, 'investor', 'ɪnˈvestə(r)', 'n.', '投资者;投资机构;', 'The main investor in the project is the French bank Credit National.', '该项目的主要投资商是法国的国民信贷银行。');
INSERT INTO `words` VALUES (3706, 'invigilate', 'ɪnˈvɪdʒɪleɪt', 'v.', '监(考);', 'I\'ve taught sixth formers and invigilated exams.', '我教过六年级学生并监考。');
INSERT INTO `words` VALUES (3707, 'invincible', 'ɪnˈvɪnsəbl', 'adj.', '不可战胜的;不能改变的;', 'When Sotomayor is on form he is virtually invincible.', '索托马约尔状态好时几乎无人可敌。');
INSERT INTO `words` VALUES (3708, 'invisible', 'ɪnˈvɪzəbl', 'adj.', '看不见的;隐形的;无形的(与服务而非商品有关);', 'The lines were so finely etched as to be invisible from a distance...', '线条被蚀刻得如此精细，在远处看都看不见。');
INSERT INTO `words` VALUES (3709, 'invitation', 'ˌɪnvɪˈteɪʃn', 'n.', '(口头或书面的)邀请;邀请;获得邀请;', '...an invitation to lunch...', '共进午餐的邀请');
INSERT INTO `words` VALUES (3710, 'inviting', 'ɪnˈvaɪtɪŋ', 'adj.', '诱人的;吸引人的;', 'The February air was soft, cool, and inviting...', '二月的空气柔和、凉爽而怡人。');
INSERT INTO `words` VALUES (3711, 'invoice', 'ˈɪnvɔɪs', 'n.', '发票;(发货或服务)费用清单;', 'We will then send you an invoice for the total course fees...', '然后我们会把全部课程费用的发票寄给你。');
INSERT INTO `words` VALUES (3712, 'invoke', 'ɪnˈvəʊk', 'v.', '援引，援用(法律、规则等作为行动理由);提及，援引(某人、某理论、实例等作为支持);提出(某人的名字，以激发某种感觉或行动);', 'The judge invoked an international law that protects refugees.', '法官援引了一项保护难民的国际法。');
INSERT INTO `words` VALUES (3713, 'involuntary', 'ɪnˈvɒləntri', 'adj.', '无意识的;不自觉的;非自愿的;', 'Another surge of pain in my ankle caused me to give an involuntary shudder.', '脚踝突然又一阵疼痛，痛得我直打哆嗦。');
INSERT INTO `words` VALUES (3714, 'involve', 'ɪnˈvɒlv', 'v.', '包含;需要;使成为必然部分(或结果);', 'Running a kitchen involves a great deal of discipline and speed...', '料理好厨房需要把一切都安排得规规矩矩，且要讲求速度。');
INSERT INTO `words` VALUES (3715, 'involved', 'ɪnˈvɒlvd', 'adj.', '参与;作为一部分;有关联;', 'If she were involved in business, she would make a strong chief executive...', '如果她投身商业的话,她会是一个很有能力的总裁。');
INSERT INTO `words` VALUES (3716, 'involvement', 'ɪnˈvɒlvmənt', 'n.', '参与;加入;插手;', 'You have no proof of my involvement in anything...', '你没有证据证明我参与了任何活动。');
INSERT INTO `words` VALUES (3717, 'inward', 'ˈɪnwəd', 'adj.', '内心的;精神的;向内的;', 'I sighed with inward relief.', '我心里舒了一口气。');
INSERT INTO `words` VALUES (3718, 'inwards', 'ˈɪnwədz', 'adv.', '向内地；向中心地;', 'She pressed back against the door until it swung inwards...', '她用背顶门，直到门向内打开。');
INSERT INTO `words` VALUES (3719, 'irish', 'ˈaɪrɪʃ', 'n.', '爱尔兰语;爱尔兰盖尔语;爱尔兰人;', '...the Prime Minister of the Irish Republic.', '爱尔兰共和国首相');
INSERT INTO `words` VALUES (3720, 'iron', 'ˈaɪən', 'n.', '铁;熨斗;铁器;', 'The huge, iron gate was locked.', '巨大的铁门紧锁着。');
INSERT INTO `words` VALUES (3721, 'ironic', 'aɪˈrɒnɪk', 'adj.', '反语的;讽刺的;(因出乎意料而)奇怪的，好笑的;', 'At the most solemn moments he will flash a mocking smile or make an ironic remark...', '在最庄严的时刻他脸上会闪现嘲弄的微笑或说一句讽刺挖苦的话。');
INSERT INTO `words` VALUES (3722, 'irony', 'ˈaɪrəni', 'n.', '(出乎意料的)奇异可笑之处;有讽刺意味的情况;反语;', 'They find only irony in the narrator\'s concern...', '他们发现叙述者只是一味地嘲讽。');
INSERT INTO `words` VALUES (3723, 'irregular', 'ɪˈreɡjələ(r)', 'adj.', '不整齐的;不平整的;参差不齐的;', 'Cars passed at irregular intervals...', '断断续续地有汽车驶过。');
INSERT INTO `words` VALUES (3724, 'irregularity', 'ɪˌreɡjəˈlærəti', 'n.', '不合乎常规的行为;不正常的做法;不规则(或无规律)的事物;', 'Cars passed at irregular intervals...', '断断续续地有汽车驶过。');
INSERT INTO `words` VALUES (3725, 'irresistible', 'ˌɪrɪˈzɪstəbl', 'adj.', '不可遏止的;无法抵制的;极诱人的;', 'It proved an irresistible temptation to Hall to go back.', '事实证明回去对霍尔是个不可抗拒的诱惑。');
INSERT INTO `words` VALUES (3726, 'irrespective', 'ɪrɪˈspektɪv', 'adj.', '不考虑；不问的；不顾的；无关的;', '...their commitment to a society based on equality for all citizens irrespective of ethnic origin...', '他们承诺建立一个不分种族出身、全民平等的社会');
INSERT INTO `words` VALUES (3727, 'irrevocable', 'ɪˈrevəkəbl', 'adj.', '无法改变的;不可更改的;', 'It may well be worth waiting for better times before making any irrevocable commitment...', '最好等到更合适的时候再作出不能反悔的承诺。');
INSERT INTO `words` VALUES (3728, 'irrigate', 'ˈɪrɪɡeɪt', 'v.', '灌溉;冲洗(伤口或身体部位);', 'None of the water from Lake Powell is used to irrigate the area.', '鲍威尔湖的水一滴都没有用于灌溉该地区。');
INSERT INTO `words` VALUES (3729, 'irrigation', 'ˌɪrɪˈgeɪʃən', 'n.', '灌溉;水利;[医]冲洗;', 'None of the water from Lake Powell is used to irrigate the area.', '鲍威尔湖的水一滴都没有用于灌溉该地区。');
INSERT INTO `words` VALUES (3730, 'irritability', 'ˌɪrɪtəˈbɪlɪti', 'n.', '刺激物；烦恼事;易怒；不能忍耐;应激性；感应性；刺激感受性；兴奋性;', 'He had been waiting for over an hour and was beginning to feel irritable.', '他已经等了一个多小时,开始变得急躁了。');
INSERT INTO `words` VALUES (3731, 'irritate', 'ˈɪrɪteɪt', 'v.', '使烦恼(尤指不断重复的事情);刺激(皮肤或身体部位);', 'Their attitude irritates me...', '他们的态度激怒了我。');
INSERT INTO `words` VALUES (3732, 'irritation', 'ˌɪrɪˈteɪʃn', 'n.', '生气，气恼，恼怒(尤指事情无法轻松阻止或控制时);恼人事;烦心事;', 'For the first time Leonard felt irritation at her methods...', '伦纳德这还是头一次对她的方法感到恼怒。');
INSERT INTO `words` VALUES (3733, 'island', 'ˈaɪlənd', 'n.', '岛;', '...a wonderful day trip to the picturesque island of Gozo...', '到风景如画的戈佐岛享受美妙的一日游');
INSERT INTO `words` VALUES (3734, 'isle', 'aɪl', 'n.', '(常用于诗歌和名称中)岛;', '...the paradise isle of Bali.', '天堂一般的巴厘岛');
INSERT INTO `words` VALUES (3735, 'isolate', 'ˈaɪsəleɪt', 'v.', '(使)隔离，孤立，脱离;将…剔出(以便看清和单独处理);使(某物质、细胞等)分离;', 'This policy could isolate the country from the other permanent members of the United Nations Security Council...', '这一政策可能会使该国孤立于联合国安全理事会其他常任理事国之外。');
INSERT INTO `words` VALUES (3736, 'isolation', 'ˌaɪsəˈleɪʃn', 'n.', '隔离;隔离状态;孤独;', 'Many deaf people have feelings of isolation and loneliness...', '很多失聪者都有孤独无助的感觉。');
INSERT INTO `words` VALUES (3737, 'isotope', 'ˈaɪsətəʊp', 'n.', '同位素;', '...tritium, a radioactive isotope of hydrogen.', '氢的放射性同位素氚');
INSERT INTO `words` VALUES (3738, 'issue', 'ˈɪʃuː', 'n.', '重要议题;争论的问题;(有关某事的)问题，担忧;', 'Agents will raise the issue of prize-money for next year\'s world championships...', '经纪人将提出下年度世锦赛奖金的问题。');
INSERT INTO `words` VALUES (3739, 'italian', 'ɪˈtæliən', 'n.', '意大利人;意大利语;', 'I watch Italian football a lot.', '我常看意大利足球。');
INSERT INTO `words` VALUES (3740, 'itch', 'ɪtʃ', 'v.', '(使)发痒;渴望;热望;', 'When someone has hayfever, the eyes and nose will stream and itch...', '花粉热临床表现为流泪、流涕，眼睛、鼻子发痒。');
INSERT INTO `words` VALUES (3741, 'item', 'ˈaɪtəm', 'n.', '项目;一件商品(或物品);一则，一条(新闻);', 'The most valuable item on show will be a Picasso drawing...', '展览会上最贵重的展品是毕加索的一幅素描。');
INSERT INTO `words` VALUES (3742, 'items', 'ˈaɪtəmz', 'n.', '项目;一件商品(或物品);一则，一条(新闻);', 'The most valuable item on show will be a Picasso drawing...', '展览会上最贵重的展品是毕加索的一幅素描。');
INSERT INTO `words` VALUES (3743, 'itinerary', 'aɪˈtɪnərəri', 'n.', '行程;旅行日程;', 'The next place on our itinerary was Silistra.', '我们行程的下一站是锡利斯特拉。');
INSERT INTO `words` VALUES (3744, 'itself', 'ɪtˈself', 'pron.', '指施动并受其影响的动物或事物;', 'Scientists have discovered remarkable new evidence showing how the body rebuilds itself while we sleep...', '科学家们已经发现了令人瞩目的新证据,说明了人体在睡眠状态下是如何自我修复的。');
INSERT INTO `words` VALUES (3745, 'ivory', 'ˈaɪvəri', 'n.', '象牙;(某些其他动物的)长牙;象牙制品;', '...the international ban on the sale of ivory.', '禁止销售象牙的国际禁令');
INSERT INTO `words` VALUES (3746, 'jacket', 'ˈdʒækɪt', 'n.', '夹克衫;短上衣;(书籍通常带有图案或画面的)护封，书套;', '...a black leather jacket.', '黑色皮夹克');
INSERT INTO `words` VALUES (3747, 'jagged', 'ˈdʒæɡɪd', 'adj.', '凹凸不平的;有尖突的;锯齿状的;', '...jagged black cliffs...', '嶙峋的黑山崖');
INSERT INTO `words` VALUES (3748, 'jam', 'dʒæm', 'n.', '果酱;拥挤;堵塞;', '...home-made jam.', '自制果酱');
INSERT INTO `words` VALUES (3749, 'january', 'ˈdʒænjuəri', 'n.', '一月;', 'We always have snow in January...', '我们这儿1月份总会下雪。');
INSERT INTO `words` VALUES (3750, 'japanese', 'ˌdʒæpəˈniːz', 'n.', '日本人;日语;', 'Japanese firms are looking for a new breed of manager.', '日本企业在寻觅新型管理人才。');
INSERT INTO `words` VALUES (3751, 'jar', 'dʒɑː(r)', 'n.', '(玻璃)罐子;广口瓶;一罐，一瓶(的量);', '...yellow cucumbers in great glass jars.', '存放在大玻璃罐里的黄瓜');
INSERT INTO `words` VALUES (3752, 'jargon', 'ˈdʒɑːɡən', 'n.', '行话;黑话;行业术语;', 'The manual is full of the jargon and slang of self-improvement courses.', '手册里满是有关自修课程的行话术语。');
INSERT INTO `words` VALUES (3753, 'jaw', 'dʒɔː', 'n.', '颌;下巴;下颌;', 'He thought for a moment, stroking his well-defined jaw...', '他摸着自己轮廓分明的下巴想了一会儿。');
INSERT INTO `words` VALUES (3754, 'jaws', 'dʒɔːz', 'n.', '颌;下巴;下颌;', 'He thought for a moment, stroking his well-defined jaw...', '他摸着自己轮廓分明的下巴想了一会儿。');
INSERT INTO `words` VALUES (3755, 'jazz', 'dʒæz', 'n.', '爵士乐;', 'The pub has live jazz on Sundays.', '那家酒馆每周日都有现场爵士乐演奏。');
INSERT INTO `words` VALUES (3756, 'jealous', 'ˈdʒeləs', 'adj.', '吃醋的;妒忌的;妒羡的;', 'She got insanely jealous and there was a terrible fight.', '她妒忌得发疯，结果是大吵一架。');
INSERT INTO `words` VALUES (3757, 'jealousy', 'ˈdʒeləsi', 'n.', '忌妒;妒羡;羡慕;', 'At first his jealousy only showed in small ways — he didn\'t mind me talking to other guys.', '起初他的小心眼表现得不是十分明显——他并不介意我和其他男人说话。');
INSERT INTO `words` VALUES (3758, 'jeep', 'dʒiːp', 'n.', '吉普车;越野车;', '...a U.S. Army Jeep.', '美国军用越野车');
INSERT INTO `words` VALUES (3759, 'jeer', 'dʒɪə(r)', 'v.', '嘲笑;嘲弄;讥讽;', 'Marchers jeered at white passers-by, but there was no violence, nor any arrests...', '游行者奚落路过的白人，但没有发生暴力事件，也没有人被逮捕。');
INSERT INTO `words` VALUES (3760, 'jelly', 'ˈdʒeli', 'n.', '果冻;肉冻;(不含水果块的)果酱;', '...a large bowl of jelly.', '一大碗果冻');
INSERT INTO `words` VALUES (3761, 'jerk', 'dʒɜːk', 'v.', '急拉;猛推;猝然一动;', 'Mr Griffin jerked forward in his chair...', '格里芬先生坐在椅子上猛地向前一凑。');
INSERT INTO `words` VALUES (3762, 'jet', 'dʒet', 'n.', '喷气式飞机;喷射流;喷射口;', 'Her private jet landed in the republic on the way to Japan...', '她的私人喷气式飞机在前往日本的途中降落在该共和国境内。');
INSERT INTO `words` VALUES (3763, 'jettison', 'ˈdʒetɪsn', 'v.', '(为减轻重量而从行驶的飞机或船上)扔弃，丢弃，投弃;摆脱;除掉;', 'The Government seems to have jettisoned the plan.', '政府似乎已经放弃了这个计划。');
INSERT INTO `words` VALUES (3764, 'jewel', 'ˈdʒuːəl', 'n.', '宝石;珠宝首饰;(手表的)宝石轴承;', '...a golden box containing precious jewels.', '盛放贵重宝石的金盒子');
INSERT INTO `words` VALUES (3765, 'jewish', 'ˈdʒuːɪʃ', 'adj.', '犹太人的;有关犹太人的;犹太教的;', '...the Jewish festival of the Passover.', '犹太教节日逾越节');
INSERT INTO `words` VALUES (3766, 'jingle', 'ˈdʒɪŋɡl', 'n.', '(金属撞击发出的)叮当声;(收音机或电视广告中易记的)短歌，短曲;', 'Brian put his hands in his pockets and jingled some change...', '布赖恩把手插进口袋里，把几枚硬币拨弄得叮当作响。');
INSERT INTO `words` VALUES (3767, 'job', 'dʒɒb', 'n.', '工作;职业;职位;', 'Once I\'m in America I can get a job...', '我一到美国就能找到工作。');
INSERT INTO `words` VALUES (3768, 'jobless', 'ˈdʒɒbləs', 'adj.', '无工作的;失业的;失业者;', 'One in four people are now jobless in inner areas like Tottenham and Peckham.', '像托特纳姆和佩卡姆这样的内陆地区失业人口达1/4。');
INSERT INTO `words` VALUES (3769, 'jog', 'dʒɒɡ', 'v.', '慢跑，慢步长跑(尤指锻炼);(偶然地)轻击，轻撞，轻碰;', 'I got up early the next morning to jog...', '第二天早晨我一大早起来去慢跑。');
INSERT INTO `words` VALUES (3770, 'join', 'dʒɔɪn', 'v.', '连接;接合;联结;', 'His wife and children moved to join him in their new home...', '他的妻子和孩子们搬进了他们的新家，和他一起生活。');
INSERT INTO `words` VALUES (3771, 'joint', 'dʒɔɪnt', 'adj.', '联合的;共同的;', 'She and Frank had never gotten around to opening a joint account...', '她和弗兰克还没有抽出时间去开联名账户。');
INSERT INTO `words` VALUES (3772, 'joke', 'dʒəʊk', 'n.', '笑话;玩笑;荒唐可笑的人(或事物、局面);', 'He debated whether to make a joke about shooting rabbits, but decided against it...', '他心里琢磨是否该开个有关打兔子的玩笑，但决定还是算了。');
INSERT INTO `words` VALUES (3773, 'joking', 'ˈdʒəʊkɪŋ', 'v.', '说笑话;开玩笑;闹着玩;', 'He debated whether to make a joke about shooting rabbits, but decided against it...', '他心里琢磨是否该开个有关打兔子的玩笑，但决定还是算了。');
INSERT INTO `words` VALUES (3774, 'jolly', 'ˈdʒɒli', 'adj.', '愉快的;快乐的;高兴的;', 'She was a jolly, kindhearted woman.', '她是个心地善良、整天乐呵呵的女人。');
INSERT INTO `words` VALUES (3775, 'jolt', 'dʒəʊlt', 'v.', '(使)震动，摇动，颠簸;使受到震惊(而采取行动);唤醒;', 'The wagon jolted again...', '货车又开始颠簸起来。');
INSERT INTO `words` VALUES (3776, 'jot', 'dʒɒt', 'v.', '匆忙记下; 略记; 摘要记载下来;', 'Could you just jot his name on there?', '你能把他的名字先记在那上面吗？');
INSERT INTO `words` VALUES (3777, 'journal', 'ˈdʒɜːnl', 'n.', '(某学科或专业的)报纸，刊物，杂志;(用于报纸名)…报;日志;', 'All our results are published in scientific journals.', '我们的研究结果全都发表在科学刊物上了。');
INSERT INTO `words` VALUES (3778, 'journalism', 'ˈdʒɜːnəlɪzəm', 'n.', '新闻业;新闻工作;', 'He began a career in journalism, working for the North London Press Group...', '他开始从事新闻工作，为北伦敦报业集团工作。');
INSERT INTO `words` VALUES (3779, 'journey', 'ˈdʒɜːni', 'n.', '(尤指长途)旅行，行程;', 'There is an express service from Paris which completes the journey to Bordeaux in under 4 hours.', '从巴黎有快车前往波尔多，全程不到4个小时。');
INSERT INTO `words` VALUES (3780, 'joy', 'dʒɔɪ', 'n.', '高兴;愉快;喜悦;', 'Salter shouted with joy.', '索尔特欣喜地叫喊着。');
INSERT INTO `words` VALUES (3781, 'joyful', 'ˈdʒɔɪfl', 'adj.', '高兴的;快乐的;令人愉快的;', 'Giving birth to a child is both painful and joyful...', '生小孩是件既痛苦又令人高兴的事。');
INSERT INTO `words` VALUES (3782, 'judge', 'dʒʌdʒ', 'n.', '法官;审判员;裁判员;', 'The judge adjourned the hearing until next Tuesday...', '法官将听证会延至下周二。');
INSERT INTO `words` VALUES (3783, 'judicial', 'dʒuˈdɪʃl', 'adj.', '法庭的;法官的;审判的;', '...an independent judicial inquiry...', '独立的司法调查');
INSERT INTO `words` VALUES (3784, 'juice', 'dʒuːs', 'n.', '果汁;菜汁;果汁(或菜汁)饮料;', '...fresh orange juice...', '鲜榨橙汁');
INSERT INTO `words` VALUES (3785, 'juicy', 'ˈdʒuːsi', 'adj.', '多汁的;汁液丰富的;生动有趣的;', '...a thick, juicy steak.', '厚实而多汁的牛排');
INSERT INTO `words` VALUES (3786, 'jukebox', 'ˈdʒuːkbɒks', 'n.', '(酒吧等的投币式)自动点唱机;', 'My favorite song is on the jukebox.', '自动点唱机在放我最喜欢的歌。');
INSERT INTO `words` VALUES (3787, 'july', 'dʒuˈlaɪ', 'n.', '七月;', 'In late July 1914, he and Violet spent a few days with friends near Berwick-upon-Tweed...', '1914年7月下旬，他和维奥莱特与几个朋友在特威德河畔贝里克附近度过了几天。');
INSERT INTO `words` VALUES (3788, 'jumble', 'ˈdʒʌmbl', 'v.', '使乱堆;使混乱;使杂乱;', 'The shoreline was made up of a jumble of huge boulders.', '海岸线上堆着一大堆杂乱的巨石。');
INSERT INTO `words` VALUES (3789, 'jump', 'dʒʌmp', 'v.', '跳;跃;跳跃;', 'I jumped over the fence...', '我跳过了栅栏。');
INSERT INTO `words` VALUES (3790, 'jumper', 'ˈdʒʌmpə(r)', 'n.', '(毛或棉的)针织套衫;跳跃者;跳跃动物;', 'Isabel had on a simple jumper and skirt.', '伊莎贝尔穿着素净的针织套衫和裙子。');
INSERT INTO `words` VALUES (3791, 'jumpy', 'ˈdʒʌmpi', 'adj.', '胆战心惊的;提心吊胆的;紧张不安的;', 'I told myself not to be so jumpy...', '我告诉自己别这么提心吊胆的。');
INSERT INTO `words` VALUES (3792, 'junction', 'ˈdʒʌŋkʃn', 'n.', '(公路或铁路的)交叉路口，汇合处，枢纽站;(电缆的)主接点;(河流的)汇合处;', 'Follow the road to a junction and turn left...', '沿着这条路到交叉路口，然后左转。');
INSERT INTO `words` VALUES (3793, 'june', 'dʒuːn', 'n.', '六月;', 'He spent two and a half weeks with us in June 1986...', '1986年6月他和我们一起呆了两周半。');
INSERT INTO `words` VALUES (3794, 'jungle', 'ˈdʒʌŋɡl', 'n.', '(热带)丛林，密林;尔虞我诈的环境;危险地带;', '...the mountains and jungles of Papua New Guinea...', '巴布亚新几内亚的山脉和丛林');
INSERT INTO `words` VALUES (3795, 'junior', 'ˈdʒuːniə(r)', 'adj.', '地位(或职位、级别)低下的;青少年的;(尤用于美国，置于同名父子中儿子的姓名之后)小;', 'Junior and middle-ranking civil servants have pledged to join the indefinite strike.', '基层和中层公务员已承诺要加入到无限期罢工中来。');
INSERT INTO `words` VALUES (3796, 'junk', 'dʒʌŋk', 'n.', '无用的东西;无价值的东西;中国式帆船;', 'Rose finds her furniture in junk shops...', '罗丝从旧货店买来了家具。');
INSERT INTO `words` VALUES (3797, 'junket', 'ˈdʒʌŋkɪt', 'n.', '(政府官员的)公费旅游;', 'He took frequent junkets with friends to exotic locales.', '他经常和朋友们到一些具有异国情调的地方公款游玩。');
INSERT INTO `words` VALUES (3798, 'jurisdiction', 'ˌdʒʊərɪsˈdɪkʃn', 'n.', '司法权;审判权;管辖权;', 'The British police have no jurisdiction over foreign bank accounts.', '外国银行账户不在英国警方的管辖权限之内。');
INSERT INTO `words` VALUES (3799, 'jury', 'ˈdʒʊəri', 'n.', '陪审团;(比赛的)评判委员会，裁判委员会;', 'The jury convicted Mr Hampson of all offences.', '陪审团裁定汉普森先生全部罪名成立。');
INSERT INTO `words` VALUES (3800, 'justice', 'ˈdʒʌstɪs', 'n.', '公平;公正;公道;', 'He has a good overall sense of justice and fairness...', '他极富正义感和公平意识。');
INSERT INTO `words` VALUES (3801, 'justifiable', 'ˌdʒʌstɪˈfaɪəbl', 'adj.', '有理由的;可证明是正当的;情有可原的;', 'The violence of the revolutionary years was justifiable on the grounds of political necessity.', '出于政治需要，革命年代的暴力行为是情有可原的。');
INSERT INTO `words` VALUES (3802, 'justification', 'ˌdʒʌstɪfɪˈkeɪʃn', 'n.', '正当理由;', 'To me the only justification for a zoo is educational...', '对我来说，动物园存在的唯一正当理由是其教育作用。');
INSERT INTO `words` VALUES (3803, 'justify', 'ˈdʒʌstɪfaɪ', 'v.', '证明…正确(或正当、有理);对…作出解释;为…辩解(或辩护);', 'No argument can justify a war...', '任何理由都不能为战争开脱。');
INSERT INTO `words` VALUES (3804, 'juvenile', 'ˈdʒuːvənaɪl', 'adj.', '少年的;未成年的;幼稚的;', 'The number of juveniles in the general population has fallen by a fifth in the past 10 years.', '总人口中少年所占比例在过去10年中下降了1/5。');
INSERT INTO `words` VALUES (3805, 'keen', 'kiːn', 'adj.', '渴望;热切;热衷于;', 'You\'re not keen on going, are you?...', '你不太想去，是吗？');
INSERT INTO `words` VALUES (3806, 'keep', 'kiːp', 'v.', '(使)保持，处于;继续，重复(做某事);使耽搁;', 'The noise kept him awake...', '噪音吵得他一直没有睡着。');
INSERT INTO `words` VALUES (3807, 'keeping', 'ˈkiːpɪŋ', 'v.', '(使)保持，处于;继续，重复(做某事);使耽搁;', 'The noise kept him awake...', '噪音吵得他一直没有睡着。');
INSERT INTO `words` VALUES (3808, 'kettle', 'ˈketl', 'n.', '(烧水用的)壶，水壶;', 'I\'ll put the kettle on and make us some tea.', '我去烧壶水给大家沏茶。');
INSERT INTO `words` VALUES (3809, 'key', 'kiː', 'n.', '钥匙;关键;要诀;', 'They put the key in the door and entered...', '他们用钥匙打开门进去了。');
INSERT INTO `words` VALUES (3810, 'keyed', 'kiːd', 'v.', '用键盘输入;键入;用钥匙划坏(汽车);', 'They put the key in the door and entered...', '他们用钥匙打开门进去了。');
INSERT INTO `words` VALUES (3811, 'keyhole', 'ˈkiːhəʊl', 'n.', '锁眼;钥匙孔;', 'I looked through the keyhole.', '我通过锁眼向里看。');
INSERT INTO `words` VALUES (3812, 'keynote', 'ˈkiːnəʊt', 'n.', '(书、演说等的)要旨，主题，基调;主音;', 'He would be setting out his plans for the party in a keynote speech...', '他将在其主旨发言中阐述他对该党的计划。');
INSERT INTO `words` VALUES (3813, 'kick', 'kɪk', 'v.', '踢;踹;踢蹬;', 'He kicked the door hard...', '他使劲儿踢门。');
INSERT INTO `words` VALUES (3814, 'kickback', 'ˈkɪkbæk', 'n.', '(不合法的)回扣，酬金，佣金;', '...alleged kickbacks and illegal party financing.', '涉嫌收受佣金和非法政党筹资');
INSERT INTO `words` VALUES (3815, 'kid', 'kɪd', 'n.', '小孩;年轻人;小山羊;', 'They\'ve got three kids...', '他们有3个孩子。');
INSERT INTO `words` VALUES (3816, 'kill', 'kɪl', 'v.', '杀死;弄死;导致死亡;', 'More than 1,000 people have been killed by the armed forces...', '1,000多人被武装部队杀死。');
INSERT INTO `words` VALUES (3817, 'killer', 'ˈkɪlə(r)', 'n.', '杀人者;杀手;导致死亡的人(或动物、事物);', 'The police are searching for his killers...', '警方正在搜捕杀害他的凶手。');
INSERT INTO `words` VALUES (3818, 'killing', 'ˈkɪlɪŋ', 'n.', '故意杀人;谋杀;', 'This is a brutal killing.', '这是残忍的谋杀。');
INSERT INTO `words` VALUES (3819, 'kilo', 'ˈkiːləʊ', 'n.', '同 kilogram;千克;公斤;', 'He\'d lost ten kilos in weight.', '他的体重已经减掉了10公斤。');
INSERT INTO `words` VALUES (3820, 'kilogram', 'ˈkɪləɡræm', 'n.', '千克;公斤;', '...a parcel weighing around 4.5 kilograms.', '重约4.5千克的包裹');
INSERT INTO `words` VALUES (3821, 'kilowatt', 'ˈkɪləwɒt', 'n.', '千瓦(电的功率计量单位，等于1 000瓦特);', '...a prototype system which produces 25 kilowatts of power.', '功率为25千瓦的原型系统');
INSERT INTO `words` VALUES (3822, 'kin', 'kɪn', 'n.', '(统称)家属，亲属，亲戚;', 'She has gone to live with her husband\'s kin.', '她住到丈夫的亲戚家里去了。');
INSERT INTO `words` VALUES (3823, 'kindergarten', 'ˈkɪndəɡɑːtn', 'n.', '学前班;幼儿园;', 'She\'s in kindergarten now.', '她目前在上幼儿园。');
INSERT INTO `words` VALUES (3824, 'kindle', 'ˈkɪndl', 'v.', '开始燃烧;点燃;激起(兴趣、感情等);', 'The second world war kindled his enthusiasm for politics...', '第二次世界大战激起了他的政治热情。');
INSERT INTO `words` VALUES (3825, 'kindly', 'ˈkaɪndli', 'adv.', '体贴地;慈祥地;友好地;', 'He was a stern critic but an extremely kindly man.', '他是个苛刻的批评家，但却是个非常善良的人。');
INSERT INTO `words` VALUES (3826, 'kindness', 'ˈkaɪndnəs', 'n.', '仁慈;善良;体贴;', 'We have been treated with such kindness by everybody.', '每个人对我们都很好。');
INSERT INTO `words` VALUES (3827, 'king', 'kɪŋ', 'n.', '君主;国王;(人、动物、事物中的)首屈一指者，最重要者，大王;', '...the king and queen of Spain...', '西班牙国王和王后');
INSERT INTO `words` VALUES (3828, 'kingdom', 'ˈkɪŋdəm', 'n.', '王国;管辖范围;领域;', 'The kingdom\'s power declined.', '王国的势力衰弱了。');
INSERT INTO `words` VALUES (3829, 'kiosk', 'ˈkiːɒsk', 'n.', '(出售报纸、饮料等的)小亭;售货亭;报刊亭;', 'I was getting cigarettes at the kiosk.', '我正在售货亭买烟。');
INSERT INTO `words` VALUES (3830, 'kiss', 'kɪs', 'v.', '亲吻;接吻;轻拂;', 'She leaned up and kissed him on the cheek...', '她倾身向前亲吻了他的面颊。');
INSERT INTO `words` VALUES (3831, 'kit', 'kɪt', 'n.', '配套元件;成套工具;成套设备;', '...a well-stocked first aid kit...', '一套完备的急救用品');
INSERT INTO `words` VALUES (3832, 'knee', 'niː', 'n.', '膝;膝盖;膝关节;', 'He will receive physiotherapy on his damaged left knee.', '因左膝受伤，他将接受物理治疗。');
INSERT INTO `words` VALUES (3833, 'knob', 'nɒb', 'n.', '(用以开关电视机等的)旋钮;(门或抽屉的)球形把手;疙瘩;', 'He turned the knob and pushed against the door.', '他扭动把手推了推门。');
INSERT INTO `words` VALUES (3834, 'knock', 'nɒk', 'v.', '敲;击;(常为无意地)碰，撞;', 'She went directly to Simon\'s apartment and knocked on the door...', '她直奔西蒙的房间，敲了敲门。');
INSERT INTO `words` VALUES (3835, 'knot', 'nɒt', 'n.', '(用绳索等打的)结;发髻;节子;', 'One lace had broken and been tied in a knot.', '一条带子已经断了，打了个结。');
INSERT INTO `words` VALUES (3836, 'know', 'nəʊ', 'v.', '知道;知悉;了解;', 'I don\'t know the name of the place...', '我不知道那个地方的名字。');
INSERT INTO `words` VALUES (3837, 'knowledge', 'ˈnɒlɪdʒ', 'n.', '知识;学问;学识;', 'She told Parliament she had no knowledge of the affair.', '她告诉议会她对此事并不知情。');
INSERT INTO `words` VALUES (3838, 'knowledgeable', 'ˈnɒlɪdʒəbl', 'adj.', '博学的;有见识的;知识渊博的;', 'We employ friendly and knowledgeable staff.', '我们雇佣友善的、在行的员工。');
INSERT INTO `words` VALUES (3839, 'laborious', 'ləˈbɔːriəs', 'adj.', '耗时费力的;辛苦的;', 'Keeping the garden tidy all year round can be a laborious task.', '一年到头把花园拾掇得干净整洁也不是件轻松活儿。');
INSERT INTO `words` VALUES (3840, 'lace', 'leɪs', 'n.', '网眼织物;花边;蕾丝;', 'She finally found the perfect gown, a beautiful creation trimmed with lace.', '她终于找到了理想的礼服——一袭饰有蕾丝的美丽长裙。');
INSERT INTO `words` VALUES (3841, 'lack', 'læk', 'n.', '缺乏;匮乏;短缺;', 'Despite his lack of experience, he got the job...', '他虽然经验不足，但还是获得了这份工作。');
INSERT INTO `words` VALUES (3842, 'lackadaisical', 'ˌlækəˈdeɪzɪkl', 'adj.', '无精打采的;委靡不振的;懒洋洋的;', 'Dr. Jonsen seemed a little lackadaisical at times.', '琼森医生有时候似乎有点懈怠。');
INSERT INTO `words` VALUES (3843, 'lackey', 'ˈlæki', 'n.', '仆人;用人;听差;', 'I\'m not staying as a paid lackey to act as your yes-man.', '我不要再做拿钱任你使唤的应声虫。');
INSERT INTO `words` VALUES (3844, 'lacking', 'ˈlækɪŋ', 'adj.', '没有;匮乏;缺乏;', '...if your hair is lacking in lustre and feeling dry...', '如果头发缺少光泽且干枯');
INSERT INTO `words` VALUES (3845, 'laconic', 'ləˈkɒnɪk', 'adj.', '简洁的;简明扼要的;凝练的;', 'Usually so laconic in the office, Dr. Lahey seemed less guarded, more relaxed...', '平常在办公室里寡言少语的莱希医生似乎少了些拘谨，多了几分轻松。');
INSERT INTO `words` VALUES (3846, 'lacquer', 'ˈlækə(r)', 'n.', '漆;头发定型剂;喷发胶;', 'We put on the second coating of lacquer...', '我们上了第二道漆。');
INSERT INTO `words` VALUES (3847, 'lacuna', 'ləˈkjuːnə', 'n.', '(文章、思想、理论等中的)缺漏，脱漏，空白，阙如;', 'There are still major lacunae in the material available.', '手边的材料依然存在严重缺漏。');
INSERT INTO `words` VALUES (3848, 'lad', 'læd', 'n.', '男孩儿;少年;男青年;', 'When I was a lad his age I would laugh at the strangest things...', '我是他那样的小毛孩时，莫明其妙地就会发笑。');
INSERT INTO `words` VALUES (3849, 'laden', 'ˈleɪdn', 'adj.', '载满的;装满的;充满的(尤指充满令人不快的东西);', 'I came home laden with cardboard boxes...', '我抱着一大堆纸箱回了家。');
INSERT INTO `words` VALUES (3850, 'la-di-da', 'ˌlɑː di ˈdɑː', 'adj.', '(描述像上层人的说话或举止)装腔作势的，装模作样的，做作的;', 'I wouldn\'t trust them in spite of all their la-di-da manners.', '尽管他们处处显得很高雅的样子，我还是不相信他们。');
INSERT INTO `words` VALUES (3851, 'ladies', 'ˈleɪdɪz', 'n.', '(指成年女子，有些人尤其是长者认为这样说比较礼貌)女士，女子;举止文雅且有教养的女子;淑女;', 'She\'s a very sweet old lady...', '她是一位很和蔼的老太太。');
INSERT INTO `words` VALUES (3852, 'lady', 'ˈleɪdi', 'n.', '(指成年女子，有些人尤其是长者认为这样说比较礼貌)女士，女子;举止文雅且有教养的女子;淑女;', 'She\'s a very sweet old lady...', '她是一位很和蔼的老太太。');
INSERT INTO `words` VALUES (3853, 'lag', 'læɡ', 'v.', '缓慢移动;发展缓慢;滞后;', 'Britain still lags behind most of Europe in its provisions for women who want time off to have babies...', '英国在妇女生育福利方面依然落后于欧洲大多数国家。');
INSERT INTO `words` VALUES (3854, 'laggard', 'ˈlæɡəd', 'n.', '迟钝懒散者;迟缓者;涣散的机构;', 'The company has developed a reputation as a technological laggard in the personal-computer arena.', '该公司在个人电脑领域因技术落后遭人诟病。');
INSERT INTO `words` VALUES (3855, 'lair', 'leə(r)', 'n.', '兽穴;兽窝;(人的)藏身处，躲藏处，独居处;', '...a fox\'s lair.', '狐狸窝');
INSERT INTO `words` VALUES (3856, 'laity', 'ˈleɪəti', 'n.', '(统称)平信徒;', 'The Church and the laity were increasingly active in charity work...', '教会与俗众越来越积极参与慈善工作。');
INSERT INTO `words` VALUES (3857, 'lake', 'leɪk', 'n.', '湖;湖泊;', 'They can go fishing in the lake...', '他们可以在湖里钓鱼。');
INSERT INTO `words` VALUES (3858, 'lakeside', 'ˈleɪksaɪd', 'n.', '湖边;湖岸;湖畔;', 'They were out by the lakeside a lot.', '他们经常到湖边去。');
INSERT INTO `words` VALUES (3859, 'lame', 'leɪm', 'n.', '金银锦缎;', 'He was aware that she was lame in one leg...', '他注意到她一条腿有残疾。');
INSERT INTO `words` VALUES (3860, 'lament', 'ləˈment', 'v.', '对…感到悲痛;痛惜;对…表示失望;', 'Ken began to lament the death of his only son...', '肯开始对独子的死悲痛不已。');
INSERT INTO `words` VALUES (3861, 'lamentable', 'ləˈmentəbl', 'adj.', '十分令人失望的;令人遗憾的;使人惋惜的;', 'This lamentable state of affairs lasted until 1947...', '这一令人遗憾的事态一直持续至1947年。');
INSERT INTO `words` VALUES (3862, 'lamentation', 'ˌlæmənˈteɪʃn', 'n.', '悲伤;悲痛;悲哀;', 'It was a time for mourning and lamentation.', '这是一个沉痛悼念的时刻。');
INSERT INTO `words` VALUES (3863, 'laminate', 'ˈlæmɪnət', 'n.', '薄片制成的材料;层压(或粘合)材料;', 'A new insulating laminate has just been designed by Attwater and Sons...', '一种新的绝缘层压材料刚由阿特沃特父子公司设计推出。');
INSERT INTO `words` VALUES (3864, 'laminated', 'ˈlæmɪneɪtɪd', 'adj.', '由薄层粘制成的;层压的;粘合的;', 'Modern windscreens are made from laminated glass.', '现在的挡风玻璃都由夹层玻璃制成。');
INSERT INTO `words` VALUES (3865, 'lamp', 'læmp', 'n.', '灯;(理疗用的)发热灯;(科学上用的)射线照射器;', 'She switched on the bedside lamp...', '她打开床头灯。');
INSERT INTO `words` VALUES (3866, 'lampoon', 'læmˈpuːn', 'v.', '嘲讽;讥讽;', 'He entertained his readers by lampooning the pretensions of the rich...', '他以冷嘲热讽自以为是的有钱人来愉悦读者。');
INSERT INTO `words` VALUES (3867, 'lan', 'læn', 'n.', '局部区域网，局域网，本地网(全写为local area network);', 'You can take part in multiplayer games either on a LAN network or via the internet.', '可以通过局域网或因特网参加多人游戏。');
INSERT INTO `words` VALUES (3868, 'lance', 'lɑːns', 'n.', '(旧时骑兵的)长矛;', 'It is a painful experience having the boil lanced.', '把疖子切开是很痛的。');
INSERT INTO `words` VALUES (3869, 'land', 'lænd', 'n.', '陆地;大地;(尤指某类型或作某种用途的)地带，土地;', 'Good agricultural land is in short supply.', '良田短缺。');
INSERT INTO `words` VALUES (3870, 'landed', 'ˈlændɪd', 'adj.', '拥有大量土地的;包括大量土地的;', 'Most of them were the nobility and the landed gentry.', '他们大多是贵族和地主乡绅。');
INSERT INTO `words` VALUES (3871, 'landfill', 'ˈlændfɪl', 'n.', '废物填埋地(或场);废物填埋;填埋的废物;', '...the environmental costs of landfill.', '垃圾填埋的环境代价');
INSERT INTO `words` VALUES (3872, 'landform', 'ˈlændfɔːm', 'n.', '地形;地貌;', 'This small country has an amazing variety of landforms.', '这个小国家拥有令人惊叹的丰富的地貌类型。');
INSERT INTO `words` VALUES (3873, 'landing', 'ˈlændɪŋ', 'n.', '楼梯平台;(两段楼梯间的)过渡平台;降落;', 'I ran out onto the landing.', '我冲出去到了楼梯口。');
INSERT INTO `words` VALUES (3874, 'landlady', 'ˈlændleɪdi', 'n.', '女房东;女地主;(酒吧或招待所的)女店主，女老板;', 'We had been made homeless by our landlady.', '我们被女房东弄得无家可归。');
INSERT INTO `words` VALUES (3875, 'landless', 'ˈlændləs', 'adj.', '无土地的;不准拥有土地的;', '...landless peasants.', '无土地的农民');
INSERT INTO `words` VALUES (3876, 'landlord', 'ˈlændlɔːd', 'n.', '业主;地主;房东;', 'His landlord doubled the rent.', '他的房东把租金提高了一倍。');
INSERT INTO `words` VALUES (3877, 'landscape', 'ˈlændskeɪp', 'n.', '(陆上，尤指乡村的)风景，景色;乡村风景画;乡村风景画的风格;', '...Arizona\'s desert landscape...', '亚利桑那的沙漠风光');
INSERT INTO `words` VALUES (3878, 'lane', 'leɪn', 'n.', '(乡间)小路;小巷;胡同;', '...a quiet country lane...', '宁静的乡间小径');
INSERT INTO `words` VALUES (3879, 'language', 'ˈlæŋɡwɪdʒ', 'n.', '语言;语言文字;言语;', '...the English language...', '英语');
INSERT INTO `words` VALUES (3880, 'languid', 'ˈlæŋɡwɪd', 'adj.', '慢悠悠的;慵懒的;', 'To his delight a familiar, tall, languid figure lowered itself down the steps of a club.', '看到一个熟悉的高大身影沿着俱乐部的台阶无精打采地走下来，他高兴极了。');
INSERT INTO `words` VALUES (3881, 'lap', 'læp', 'n.', '(坐着时的)大腿部;(跑道等的)一圈;(行程或工作中的)一段，环节;', 'She waited quietly with her hands in her lap...', '她双手放在腿上静静等候。');
INSERT INTO `words` VALUES (3882, 'lapse', 'læps', 'n.', '小错;(尤指)记错，过失，疏忽;(两件事发生的)间隔时间;', 'On Friday he showed neither decency nor dignity. It was an uncommon lapse.', '星期五那天他的表现既不得体，又有失尊严。如此失态非常少有。');
INSERT INTO `words` VALUES (3883, 'laptop', 'ˈlæptɒp', 'n.', '膝上型计算机;便携式电脑;笔记本电脑;', 'She used to work at her laptop until four in the morning.', '她从前经常用手提电脑一直工作到凌晨4点。');
INSERT INTO `words` VALUES (3884, 'large', 'lɑːdʒ', 'adj.', '大的;大规模的;大量的;', 'The Pike lives mainly in large rivers and lakes...', '狗鱼主要生活在大江大湖中。');
INSERT INTO `words` VALUES (3885, 'largely', 'ˈlɑːdʒli', 'adv.', '在很大程度上;多半;主要地;', 'The fund is largely financed through government borrowing...', '该基金大致上由政府借款支持。');
INSERT INTO `words` VALUES (3886, 'laser', 'ˈleɪzə(r)', 'n.', '激光器;', '...new laser technology...', '新的激光技术');
INSERT INTO `words` VALUES (3887, 'lash', 'læʃ', 'v.', '猛击;狠打;鞭打;', '...sombre grey eyes, with unusually long lashes...', '一双忧郁的灰眼睛，眼睫毛长得出奇');
INSERT INTO `words` VALUES (3888, 'last', 'lɑːst', 'det.', '最后的;最末的;末尾的;', 'I got married last July...', '我在刚过去的7月里结了婚。');
INSERT INTO `words` VALUES (3889, 'lasting', 'ˈlɑːstɪŋ', 'adj.', '继续存在的;持久的;耐久的;', 'We are well on our way to a lasting peace...', '我们已在实现持久和平上取得很大进展。');
INSERT INTO `words` VALUES (3890, 'lastly', 'ˈlɑːstli', 'adv.', '最后一点;最后;最后提及;', 'Lastly, I would like to ask about your future plans...', '最后，我想问一下你未来的计划。');
INSERT INTO `words` VALUES (3891, 'latch', 'lætʃ', 'n.', '门闩;插销;碰锁;', 'You left the latch off the gate and the dog escaped.', '你没把门闩插上，所以狗跑了。');
INSERT INTO `words` VALUES (3892, 'late', 'leɪt', 'adj.', '接近末期;在晚年;迟到;', 'It was late in the afternoon...', '那是下午近黄昏的时候。');
INSERT INTO `words` VALUES (3893, 'lately', 'ˈleɪtli', 'adv.', '最近;新近;近来;', 'Dad\'s health hasn\'t been too good lately...', '爸爸的健康状况近来不太好。');
INSERT INTO `words` VALUES (3894, 'latent', 'ˈleɪtnt', 'adj.', '潜在的;潜伏的;隐藏的;', 'Advertisements attempt to project a latent meaning behind an overt message.', '广告的意图是以公开的信息传达隐含之意。');
INSERT INTO `words` VALUES (3895, 'lateral', 'ˈlætərəl', 'adj.', '侧面的;横向的;向侧面移动的;', 'McKinnon estimated the lateral movement of the bridge to be between four and six inches.', '麦金农估计大桥的侧向移动介于4至6英寸之间。');
INSERT INTO `words` VALUES (3896, 'latitude', 'ˈlætɪtjuːd', 'n.', '纬度;纬度地区;选择(做什么事或做事方式)的自由;', 'In the middle to high latitudes rainfall has risen steadily over the last 20-30 years.', '在过去二三十年里，中高纬度地区的降雨量持续增加。');
INSERT INTO `words` VALUES (3897, 'latter', 'ˈlætə(r)', 'adj.', '(刚提及的两者中)后者的;(系列中)最后的，末尾的;后半期的;', 'He tracked down his cousin and uncle. The latter was sick.', '他追查到了堂弟和叔叔的下落，发现叔叔这时正在病中。');
INSERT INTO `words` VALUES (3898, 'lattice', 'ˈlætɪs', 'n.', '格子木架，格子金属架，格栅(用作篱笆等);斜条结构;斜格图案;', 'We were crawling along the narrow steel lattice of the bridge.', '我们沿着桥上狭窄的钢制格架往前爬。');
INSERT INTO `words` VALUES (3899, 'laudable', 'ˈlɔːdəbl', 'adj.', '应受赞扬的;值得赞美的;', 'One of Emma\'s less laudable characteristics was her jealousy...', '忌妒心强是埃玛的一个不那么让人称道的个性特点。');
INSERT INTO `words` VALUES (3900, 'laugh', 'lɑːf', 'v.', '笑;发笑;(特别表示因成功而)处于有利地位;', 'He was about to offer an explanation, but she was beginning to laugh...', '他正要解释，她却笑起来了。');
INSERT INTO `words` VALUES (3901, 'laughter', 'ˈlɑːftə(r)', 'n.', '笑;笑声;', 'Their laughter filled the corridor...', '整个走廊都回荡着他们的笑声。');
INSERT INTO `words` VALUES (3902, 'launch', 'lɔːntʃ', 'v.', '开始从事，发起，发动(尤指有组织的活动);(首次)上市，发行;使(船，尤指新船)下水;', 'NASA plans to launch a satellite to study cosmic rays...', '美国国家航空航天局计划发射一颗卫星上天，对宇宙射线进行研究。');
INSERT INTO `words` VALUES (3903, 'laundry', 'ˈlɔːndri', 'n.', '要(或正在)洗的衣物;刚洗好的衣物;洗衣物;', 'I\'ll do your laundry...', '我来帮你洗衣服。');
INSERT INTO `words` VALUES (3904, 'lavatory', 'ˈlævətri', 'n.', '抽水马桶;厕所;卫生间;', '...the ladies\' lavatory at the University of London.', '伦敦大学的女厕所');
INSERT INTO `words` VALUES (3905, 'lavish', 'ˈlævɪʃ', 'adj.', '大量的;给人印象深刻的;耗资巨大的;', '...a lavish party  to celebrate Bryan\'s fiftieth birthday...', '庆祝布赖恩50寿辰的盛大宴会');
INSERT INTO `words` VALUES (3906, 'lavishly', '', 'adv.', '非常大方地；浪费地；极其丰富地;', '...a lavish party  to celebrate Bryan\'s fiftieth birthday...', '庆祝布赖恩50寿辰的盛大宴会');
INSERT INTO `words` VALUES (3907, 'law', 'lɔː', 'n.', '法律(体系);法规;(针对某项罪行、协议等的一条)法律;', 'Obscene and threatening phone calls are against the law...', '猥亵和恐吓电话都是违法的。');
INSERT INTO `words` VALUES (3908, 'lawful', 'ˈlɔːfl', 'adj.', '法定的;法律承认的;合法的;', 'It was lawful for the doctors to treat her in whatever way they considered was in her best interests...', '医生们采用他们认为对她最有利的方法为她治疗是合法的。');
INSERT INTO `words` VALUES (3909, 'lawfully', '', 'adv.', '守法地，合法地;', 'It was lawful for the doctors to treat her in whatever way they considered was in her best interests...', '医生们采用他们认为对她最有利的方法为她治疗是合法的。');
INSERT INTO `words` VALUES (3910, 'lawless', 'ˈlɔːləs', 'adj.', '无法律的;不遵守法律的;目无法纪的;', 'The government recognised there were problems in urban areas but these could never be an excuse for lawless behaviour.', '政府承认市区存在问题，但决不可拿这些作为不法行为的借口。');
INSERT INTO `words` VALUES (3911, 'lawn', 'lɔːn', 'n.', '草坪;草地;上等细棉布(或麻布);', 'They were sitting on the lawn under a large beech tree.', '他们坐在一棵高大的山毛榉树下的草坪上。');
INSERT INTO `words` VALUES (3912, 'lawsuit', 'ˈlɔːsuːt', 'n.', '诉讼;起诉;', 'The dispute culminated last week in a lawsuit against the government.', '上周这一纠纷达到高潮，政府被告上了法庭。');
INSERT INTO `words` VALUES (3913, 'lawyer', 'ˈlɔːjə(r)', 'n.', '律师;', 'Prosecution and defense lawyers are expected to deliver closing arguments next week.', '下周控辩双方律师将进行结案陈词。');
INSERT INTO `words` VALUES (3914, 'lax', 'læks', 'adj.', '不严格的;不严厉的;马虎的;', 'One of the problem areas is lax security for airport personnel...', '问题之一是对机场员工的安检措施马虎松懈。');
INSERT INTO `words` VALUES (3915, 'laxative', 'ˈlæksətɪv', 'n.', '轻泻药;通便剂;有通便作用的饮食;', 'Foods that ferment quickly in the stomach are excellent natural laxatives.', '能在肠胃里迅速发酵的食物是绝佳的天然通便剂。');
INSERT INTO `words` VALUES (3916, 'layabout', 'ˈleɪəbaʊt', 'n.', '游手好闲的人;懒汉;二流子;', 'The plaintiff\'s sole witness, a gambler and layabout, was easily discredited.', '原告唯一的证人嗜赌成性，游手好闲，其证词一下子就被推翻了。');
INSERT INTO `words` VALUES (3917, 'layer', 'ˈleɪə(r)', 'n.', '层;表层;层次;', 'A fresh layer of snow covered the street...', '街上覆盖了一层新雪。');
INSERT INTO `words` VALUES (3918, 'layered', 'ˈleɪəd', 'adj.', '分层的;层叠的;', 'Maria wore a layered white dress that rustled when she moved.', '玛丽亚穿一袭白色节裙，一动裙子就窸窣作响。');
INSERT INTO `words` VALUES (3919, 'layman', 'ˈleɪmən', 'n.', '非专业人员;外行;门外汉;', 'The mere mention of the words \'heart failure\', can conjure up, to the layman, the prospect of imminent death...', '一提“心脏衰竭”四字，外行人就联想到命不久矣的情景。');
INSERT INTO `words` VALUES (3920, 'layoff', '\'leɪɒf', 'n.', '(通常指因工作岗位不足的)解雇，裁员，下岗;休养;疗养;', 'It will close more than 200 stores nationwide resulting in the layoffs of an estimated 2,000 employees.', '它将关闭全国200多家店铺，估计会有2,000名员工因此失业。');
INSERT INTO `words` VALUES (3921, 'layout', 'ˈleɪaʊt', 'n.', '布局;布置;设计;', 'He tried to recall the layout of the farmhouse...', '他努力回想农舍的布局。');
INSERT INTO `words` VALUES (3922, 'laze', 'leɪz', 'v.', '懒散;懒惰;偷懒;', 'Fred lazed in an easy chair...', '弗雷德优哉游哉地躺在安乐椅上。');
INSERT INTO `words` VALUES (3923, 'lazily', '', 'adv.', '怠惰地，懒散地;', 'Lazy and incompetent police officers are letting the public down...', '懒惰兼无能的警官令公众失望。');
INSERT INTO `words` VALUES (3924, 'laziness', 'ˈleɪzinəs', 'n.', '怠惰，懒散，徐缓，无精打采;', 'Lazy and incompetent police officers are letting the public down...', '懒惰兼无能的警官令公众失望。');
INSERT INTO `words` VALUES (3925, 'lazy', 'ˈleɪzi', 'adj.', '不愿工作的;懒散的;懒惰的;', 'Lazy and incompetent police officers are letting the public down...', '懒惰兼无能的警官令公众失望。');
INSERT INTO `words` VALUES (3926, 'lb', '', 'abbr.', '磅(源自拉丁语libra，约等于454克);', 'The baby was born three months early weighing only 3 lb 5oz.', '婴儿早产3个月，体重只有3磅5盎司。');
INSERT INTO `words` VALUES (3927, 'leaded', 'ˈledɪd', 'adj.', '加铅的;含铅的;铅皮覆盖的;', 'Japanese refiners stopped producing leaded petrol in December 1987.', '日本的炼油厂于1987年12月停止生产含铅汽油。');
INSERT INTO `words` VALUES (3928, 'leaden', 'ˈledn', 'adj.', '铅灰色的;沉闷的;阴郁的;', 'The weather was at its worst; bitterly cold, with leaden skies that gave minimum visibility.', '天气极其恶劣。寒风凛冽，天色晦暗，能见度极低。');
INSERT INTO `words` VALUES (3929, 'leader', 'ˈliːdə(r)', 'n.', '领导者;领袖;首领;', 'We now need a new leader of the party and a new style of leadership...', '我们现在需要一位新的政党领袖和一种新的领导作风。');
INSERT INTO `words` VALUES (3930, 'leaders', 'ˈliːdəz', 'n.', '领导者;领袖;首领;', 'We now need a new leader of the party and a new style of leadership...', '我们现在需要一位新的政党领袖和一种新的领导作风。');
INSERT INTO `words` VALUES (3931, 'leadership', 'ˈliːdəʃɪp', 'n.', '领导;领导地位;领导才能;', 'He is expected to hold talks with both the Croatian and Slovenian leaderships.', '预计他将与克罗地亚和斯洛文尼亚领导层举行会谈。');
INSERT INTO `words` VALUES (3932, 'leading', 'ˈliːdɪŋ , ˈledɪŋ', 'adj.', '最重要的;一流的;(赛跑或比赛中)领先的，最前的;', '...a leading member of Bristol\'s Sikh community...', '布里斯托尔锡克教社群一位出类拔萃的成员');
INSERT INTO `words` VALUES (3933, 'leaf', 'liːf', 'n.', '叶;叶片;叶子;', 'In the garden, the leaves of the horse chestnut had already fallen...', '花园里，七叶树已经落叶了。');
INSERT INTO `words` VALUES (3934, 'leafless', 'ˈliːfləs', 'adj.', '无叶的;', 'A beautiful fig tree that had stood in their yard was leafless and barren.', '原来他们院子里那棵漂亮的无花果树已不长一片树叶，不挂一个果子。');
INSERT INTO `words` VALUES (3935, 'leaflet', 'ˈliːflət', 'n.', '散页印刷品;传单;(宣传或广告)小册子;', 'Campaigners handed out leaflets on passive smoking.', '活动人士派发了有关被动吸烟的宣传页。');
INSERT INTO `words` VALUES (3936, 'leafy', 'ˈliːfi', 'adj.', '多叶的;叶茂的;多树木的;', 'His two-story brick home was graced with a patio and surrounded by tall, leafy trees.', '他那两层高的砖房有一个漂亮的露台，四周环拥绿叶成阴的大树。');
INSERT INTO `words` VALUES (3937, 'league', 'liːɡ', 'n.', '(体育运动队的)联合会，联赛;(质量、能力等的)等级，级别，水平;联盟;', '...the League of Nations.', '国际联盟');
INSERT INTO `words` VALUES (3938, 'leak', 'liːk', 'v.', '漏;渗漏;泄漏;', 'The roof leaked...', '屋顶漏雨。');
INSERT INTO `words` VALUES (3939, 'leakage', 'ˈliːkɪdʒ', 'n.', '泄漏量;漏损量;泄漏;', 'A leakage of kerosene has polluted water supplies...', '泄漏的煤油污染了水源。');
INSERT INTO `words` VALUES (3940, 'leaky', 'ˈliːki', 'adj.', '有漏洞的;有漏隙的;渗漏的;', '...the cost of repairing the leaky roof.', '维修漏雨屋顶的费用');
INSERT INTO `words` VALUES (3941, 'learner', 'ˈlɜːnə(r)', 'n.', '学习者;学习驾车者;学习驾驶员;', '...a new aid for younger children or slow learners...', '针对年幼者和学习迟缓者的新教辅手段');
INSERT INTO `words` VALUES (3942, 'learning', 'ˈlɜːnɪŋ', 'n.', '学习;知识;学问;', 'The brochure described the library as the focal point of learning on the campus.', '这本小册子把图书馆形容为校园学习的中心。');
INSERT INTO `words` VALUES (3943, 'lease', 'liːs', 'n.', '(房屋、设备或土地的)租约，租契;', 'He took up a 10 year lease on the house at Rossie Priory.', '他签了一份10年租约，租下了罗茜大宅的房子。');
INSERT INTO `words` VALUES (3944, 'leased', 'liːst', 'v.', '租用，租借，出租(尤指房地产或设备);', 'He took up a 10 year lease on the house at Rossie Priory.', '他签了一份10年租约，租下了罗茜大宅的房子。');
INSERT INTO `words` VALUES (3945, 'leasehold', 'ˈliːshəʊld', 'adj.', '租赁的;租用的;', 'I went into a leasehold property at four hundred and fifty pounds rent per year.', '我以每年450英镑的租金租了一所房子。');
INSERT INTO `words` VALUES (3946, 'least', 'liːst', 'det./pron.', '最小的;最少的;程度最轻的;', '...a dinner menu featuring at least 15 different sorts of fish...', '至少列有15道不同做法的鱼的菜单');
INSERT INTO `words` VALUES (3947, 'leather', 'ˈleðə(r)', 'n.', '皮革;(尤指骑摩托车人穿的)皮衣，皮外套;', 'He wore a leather jacket and dark trousers.', '他身穿皮夹克和深色裤子。');
INSERT INTO `words` VALUES (3948, 'leave', 'liːv', 'v.', '离开(某人或某处);离开居住地点(或群体、工作单位等);遗弃;', 'He would not be allowed to leave the country...', '他可能会被禁止离开该国。');
INSERT INTO `words` VALUES (3949, 'lecture', 'ˈlektʃə(r)', 'n.', '(通常指大学里的)讲座，讲课，演讲;(冗长的)教训，训斥，谴责;', '...a series of lectures by Professor Eric Robinson...', '埃里克·鲁宾逊教授的系列讲座');
INSERT INTO `words` VALUES (3950, 'lecturer', 'ˈlektʃərə(r)', 'n.', '讲课者;讲授者;讲演者;', '...a lecturer in law at Southampton University.', '南安普敦大学的一位法学讲师');
INSERT INTO `words` VALUES (3951, 'leeway', 'ˈliːweɪ', 'n.', '自由活动的空间;', 'Rarely do schoolteachers have leeway to teach classes the way they want...', '鲜有学校老师可随心所欲地教书的情况。');
INSERT INTO `words` VALUES (3952, 'left-handed', 'ˌleft ˈhændɪd', 'adj.', '惯用左手的;左撇子的;供惯用左手者使用的;', 'I noticed she was left-handed...', '我注意到她是个左撇子。');
INSERT INTO `words` VALUES (3953, 'left-hander', 'ˌleft ˈhændə(r)', 'n.', '惯用左手的人;左撇子;', 'Left-handers have trouble using can-openers, scissors, and potato peelers.', '左撇子在使用开罐刀、剪刀和土豆削皮器时会有困难。');
INSERT INTO `words` VALUES (3954, 'leftover', 'ˈleftəʊvə(r)', 'n.', '吃剩的食物;残羹剩饭;遗留物;', 'Refrigerate any leftovers...', '把剩菜放进冰箱里。');
INSERT INTO `words` VALUES (3955, 'leftovers', 'ˈlɛftˈəʊvəz', 'n.', '吃剩的食物;残羹剩饭;遗留物;', 'Refrigerate any leftovers...', '把剩菜放进冰箱里。');
INSERT INTO `words` VALUES (3956, 'leg', 'leɡ', 'n.', '腿;(尤指供食用的)动物的腿，腿肉;裤腿;', 'He was tapping his walking stick against his leg.', '他用手杖轻敲着腿。');
INSERT INTO `words` VALUES (3957, 'legacy', 'ˈleɡəsi', 'n.', '遗产;遗赠财物;遗留;', 'You could make a real difference to someone\'s life by leaving them a generous legacy.', '慷慨留下您的遗产，改写他人的人生。');
INSERT INTO `words` VALUES (3958, 'legal', 'ˈliːɡl', 'adj.', '与法律有关的;法律的;法律允许的;', 'He vowed to take legal action.', '他誓言要采取法律行动。');
INSERT INTO `words` VALUES (3959, 'legally', 'ˈliːɡəli', 'adv.', '合法地;在法律上;', 'He vowed to take legal action.', '他誓言要采取法律行动。');
INSERT INTO `words` VALUES (3960, 'legend', 'ˈledʒənd', 'n.', '传说;传奇故事;(尤指某领域中的)传奇人物;', '...the legends of ancient Greece...', '古希腊传说');
INSERT INTO `words` VALUES (3961, 'legendary', 'ˈledʒəndri', 'adj.', '非常著名的;享有盛名的;传奇的;', '...the legendary Jazz singer Adelaide Hall...', '大名鼎鼎的爵士乐歌手阿德莱德·霍尔');
INSERT INTO `words` VALUES (3962, 'leggings', 'ˈleɡɪŋz', 'n.', '女式紧身裤;护腿;绑腿;', 'She is wearing tight, black leggings and a baggy green jersey.', '她身穿黑色紧身裤和一件宽松的绿色针织套衫。');
INSERT INTO `words` VALUES (3963, 'legible', 'ˈledʒəbl', 'adj.', '清晰可读的;清楚的;', 'My handwriting isn\'t very legible.', '我写的字不那么好认。');
INSERT INTO `words` VALUES (3964, 'legion', 'ˈliːdʒən', 'n.', '(尤指古罗马的)军团;大量，大批(某类型的人);', '...the Sudan-based troops of the Libyan Islamic Legion...', '利比亚伊斯兰军团驻扎在苏丹的部队');
INSERT INTO `words` VALUES (3965, 'legislate', 'ˈledʒɪsleɪt', 'v.', '制定法律;立法;', 'Most member countries have already legislated against excessive overtime...', '大多数成员国均已立法禁止超时加班。');
INSERT INTO `words` VALUES (3966, 'legislation', 'ˌledʒɪsˈleɪʃn', 'n.', '法规;法律;立法;', '...a letter calling for legislation to protect women\'s rights.', '一封呼吁立法保护妇女权益的信');
INSERT INTO `words` VALUES (3967, 'legislative', 'ˈledʒɪslətɪv', 'adj.', '立法的;制定法律的;', 'Today\'s hearing was just the first step in the legislative process.', '今天的听证会只是展开立法程序的第一步。');
INSERT INTO `words` VALUES (3968, 'legislator', 'ˈledʒɪsleɪtə(r)', 'n.', '立法委员;', '...an attempt to get US legislators to change the system.', '旨在推动美国的立法者进行制度变革的努力');
INSERT INTO `words` VALUES (3969, 'legislature', 'ˈledʒɪslətʃə(r)', 'n.', '立法机关;', 'The proposals before the legislature include the creation of two special courts to deal exclusively with violent crimes.', '呈交立法机关的提案内容包括成立两个专门审理暴力犯罪的特别法庭。');
INSERT INTO `words` VALUES (3970, 'legs', 'leɡz', 'n.', '腿;(尤指供食用的)动物的腿，腿肉;裤腿;', 'He was tapping his walking stick against his leg.', '他用手杖轻敲着腿。');
INSERT INTO `words` VALUES (3971, 'leisure', 'ˈleʒə(r)', 'n.', '闲暇;空闲;休闲;', '...a relaxing way to fill my leisure time.', '我打发空余时间的轻松办法');
INSERT INTO `words` VALUES (3972, 'leisurely', 'ˈleʒəli', 'adj.', '不慌不忙的;慢悠悠的;', 'Lunch was a leisurely affair...', '午饭吃得很悠闲。');
INSERT INTO `words` VALUES (3973, 'lemon', 'ˈlemən', 'n.', '柠檬;柠檬汁;柠檬饮料;', '...a slice of lemon.', '一片柠檬');
INSERT INTO `words` VALUES (3974, 'lemonade', 'ˌleməˈneɪd', 'n.', '柠檬味汽水;柠檬饮料;一杯(或一瓶)柠檬饮料;', 'He was pouring ice and lemonade into tall glasses.', '他正把冰块和柠檬汽水倒进高脚杯里。');
INSERT INTO `words` VALUES (3975, 'lend', 'lend', 'v.', '借给;借出;(向…)贷(款);', 'The bank is reassessing its criteria for lending money...', '银行正重新评估其贷款标准。');
INSERT INTO `words` VALUES (3976, 'lender', 'ˈlendə(r)', 'n.', '放款人;', '...the six leading mortgage lenders.', '6大按揭贷款机构');
INSERT INTO `words` VALUES (3977, 'lending', 'ˈlendɪŋ', 'n.', '放款;贷放;', 'The bank is reassessing its criteria for lending money...', '银行正重新评估其贷款标准。');
INSERT INTO `words` VALUES (3978, 'length', 'leŋθ', 'n.', '长;长度;(持续)时间的长短;', 'It is about a metre in length.', '它长约1米。');
INSERT INTO `words` VALUES (3979, 'lengthen', 'ˈleŋθən', 'v.', '(使)变长;', 'The evening shadows were lengthening...', '残阳下的影子越拉越长。');
INSERT INTO `words` VALUES (3980, 'lengthwise', 'ˈlɛŋθwaɪz', 'adv.', '纵向地;纵长地;同 lengthways;', 'Peel the onion and cut it in half lengthwise.', '剥掉洋葱皮并把它纵向切成两半。');
INSERT INTO `words` VALUES (3981, 'lengthy', 'ˈleŋθi', 'adj.', '很长的;漫长的;冗长的;', '...a lengthy meeting.', '冗长的会议');
INSERT INTO `words` VALUES (3982, 'lenient', 'ˈliːniənt', 'adj.', '(惩罚或执法时)宽大的，宽容的，仁慈的;', 'He believes the government already is lenient with drug traffickers...', '他认为政府对毒贩已经很宽容了。');
INSERT INTO `words` VALUES (3983, 'lens', 'lenz', 'n.', '透镜;镜片;(眼球的)晶状体;', '...a camera lens...', '照相机镜头');
INSERT INTO `words` VALUES (3984, 'leonine', 'ˈliːənaɪn', 'adj.', '像狮子一样的;狮子般的;', '...a tall leonine grey-haired man.', '一头白发蓬松如狮的高个子男人');
INSERT INTO `words` VALUES (3985, 'lesion', 'ˈliːʒn', 'n.', '(因伤病导致皮肤或器官的)损伤，损害;', '...skin lesions.', '皮肤损伤');
INSERT INTO `words` VALUES (3986, 'less', 'les', 'det./pron.', '(与不可数名词连用)较少的，更少的;', 'People should eat less fat to reduce the risk of heart disease.', '人们应该减少脂肪摄入，以降低患心脏病的危险。');
INSERT INTO `words` VALUES (3987, 'lessen', 'ˈlesn', 'v.', '(使)变小，变少，减弱，减轻;', 'He is used to a lot of attention from his wife, which will inevitably lessen when the baby is born...', '他习惯了妻子对他的百般关注，但孩子出生后这种关注就会不可避免地减少。');
INSERT INTO `words` VALUES (3988, 'lesser', 'ˈlesə(r)', 'adj.', '较小的;较少的;次要的;', 'No medication works in isolation but is affected to a greater or lesser extent by many other factors...', '药物治疗不可能孤立地发生作用，多多少少会受很多其他因素的影响。');
INSERT INTO `words` VALUES (3989, 'lesson', 'ˈlesn', 'n.', '一节课;一课时;课;', 'It would be his last French lesson for months...', '这将是他以后几个月里的最后一堂法语课。');
INSERT INTO `words` VALUES (3990, 'lest', 'lest', 'conj.', '免得;以免;(引出产生某种情感的原因)唯恐，担心;', 'I was afraid to open the door lest he should follow me...', '我不敢开门，唯恐他还跟着我。');
INSERT INTO `words` VALUES (3991, 'lethal', 'ˈliːθl', 'adj.', '致命的;可致死的;危害极大的;', '...a lethal dose of sleeping pills.', '剂量足以致命的安眠药');
INSERT INTO `words` VALUES (3992, 'lethargic', 'ləˈθɑːdʒɪk', 'adj.', '慵懒的;没精打采的;懒洋洋的;', 'He felt too miserable and lethargic to get dressed.', '他心情低落，无精打采，完全没有心思穿衣整装。');
INSERT INTO `words` VALUES (3993, 'lethargy', 'ˈleθədʒi', 'n.', '无精打采;没有热情;冷漠;', 'Symptoms include tiredness, paleness, and lethargy.', '症状包括疲倦、脸色苍白、没有精神。');
INSERT INTO `words` VALUES (3994, 'letter', 'ˈletə(r)', 'n.', '信;函;字母;', 'I had received a letter from a very close friend.', '我收到一个很要好的朋友的来信。');
INSERT INTO `words` VALUES (3995, 'letterhead', 'ˈletəhed', 'n.', '信头(印于信笺上端的个人、公司或组织的名称和地址);', 'Colleagues at work enjoy having a letterhead with their name at the top.', '在职的同事可把其名字印在信头之上。');
INSERT INTO `words` VALUES (3996, 'lettering', 'ˈletərɪŋ', 'n.', '(用某种字体书写或印刷的)字母，字;写字;描字;', '...a small blue sign with white lettering.', '一块蓝底白字的小招牌');
INSERT INTO `words` VALUES (3997, 'letters', 'ˈletəz', 'n.', '信;函;字母;', 'I had received a letter from a very close friend.', '我收到一个很要好的朋友的来信。');
INSERT INTO `words` VALUES (3998, 'lever', 'ˈliːvə(r)', 'n.', '(车辆或机器的)操纵杆，控制杆;杠杆;施压的行为;', 'Push the tiny lever on the lock...', '按一下锁上的小柄。');
INSERT INTO `words` VALUES (3999, 'levy', 'ˈlevi', 'n.', '征收额;(尤指)税款;', '...an annual motorway levy on all drivers.', '以所有司机为征收对象的高速公路年税');
INSERT INTO `words` VALUES (4000, 'lexical', 'ˈleksɪkl', 'adj.', '词汇的;', 'We chose a few of the commonest lexical items in the languages.', '我们选取了各语言中最常见的几个词汇。');
INSERT INTO `words` VALUES (4001, 'lexicon', 'ˈleksɪkən', 'n.', '(某语言或学科、某人或群体使用的)全部词汇;(某学科或语言的)词汇表;(尤指希腊语或希伯来语等古代语言的)词典，字典;', '...the lexicon of management...', '管理学词汇');
INSERT INTO `words` VALUES (4002, 'liabilities', 'ˌlaɪəˈbɪlɪtiz', 'n.', '(法律上对某事物的)责任，义务;惹麻烦的人(或事);欠债;', 'As the president\'s prestige continues to fall, they\'re clearly beginning to consider him a liability.', '随着总统的声望持续下跌，他们显然开始视他为负累。');
INSERT INTO `words` VALUES (4003, 'liability', 'ˌlaɪəˈbɪləti', 'n.', '(法律上对某事物的)责任，义务;惹麻烦的人(或事);欠债;', 'As the president\'s prestige continues to fall, they\'re clearly beginning to consider him a liability.', '随着总统的声望持续下跌，他们显然开始视他为负累。');
INSERT INTO `words` VALUES (4004, 'liable', 'ˈlaɪəbl', 'adj.', '(法律上)负有偿付责任;可能(做某事);可能受…影响;', 'Only a small minority of the mentally ill are liable to harm themselves or others.', '在精神病患者中，只有一小部分有可能会伤害自己或他人。');
INSERT INTO `words` VALUES (4005, 'liaison', 'liˈeɪzn', 'n.', '联络;联系;联络员;', 'Liaison between police forces and the art world is vital to combat art crime.', '警方和艺术界的联手对于打击艺术品犯罪至关重要。');
INSERT INTO `words` VALUES (4006, 'liar', 'ˈlaɪə(r)', 'n.', '说谎者;撒谎者;', 'He was a liar and a cheat...', '他曾坑蒙拐骗。');
INSERT INTO `words` VALUES (4007, 'liberal', 'ˈlɪbərəl', 'adj.', '宽宏大度的;心胸宽阔的;开明的;', 'She is known to have liberal views on divorce and contraception.', '众所周知，她在离婚与避孕方面思想很开明。');
INSERT INTO `words` VALUES (4008, 'liberate', 'ˈlɪbəreɪt', 'v.', '解放;使自由;使摆脱约束(或限制);', 'They planned to march on and liberate the city...', '他们计划继续挺进，解放该城市。');
INSERT INTO `words` VALUES (4009, 'liberation', 'ˌlɪbə\'reɪʃ(ə)n', 'n.', '解放;摆脱束缚;释放;', 'They planned to march on and liberate the city...', '他们计划继续挺进，解放该城市。');
INSERT INTO `words` VALUES (4010, 'liberty', 'ˈlɪbəti', 'n.', '自由(自己选择生活方式而不受政府及权威限制);自由(不受关押或奴役的状态);自由(做某事的合法权利或行动自由);', 'Wit Wolzek claimed the legislation could impinge on privacy, self determination and respect for religious liberty...', '威特·沃尔泽克声称这项法律可能会对隐私权和自主权造成侵犯，并破坏对宗教自由的尊重。');
INSERT INTO `words` VALUES (4011, 'library', 'ˈlaɪbrəri', 'n.', '图书馆;藏书楼;图书室;', '...the local library...', '当地图书馆');
INSERT INTO `words` VALUES (4012, 'license', 'ˈlaɪsns', 'v.', '批准;许可;', '...a proposal that would require the state to license guns...', '要求该州对枪支实行许可管理的议案');
INSERT INTO `words` VALUES (4013, 'licensed', 'ˈlaɪsnst', 'adj.', '有售酒许可的;获准售酒的;获准拥有的;', 'There were about 250 people on board, about 100 more than the ferry was licensed to carry.', '渡船上大约有250名乘客，超出了核定载客量约100名。');
INSERT INTO `words` VALUES (4014, 'lick', 'lɪk', 'v.', '舔;舔吃;舔着喝;', 'She folded up her letter, licking the envelope flap with relish...', '她把信折好，用力舔了舔信封的折口。');
INSERT INTO `words` VALUES (4015, 'lieutenant', 'lefˈtenənt', 'n.', '(陆军)中尉;(海军或空军)上尉;仅低于…官阶的官员;', 'Lieutenant Campbell ordered the man at the wheel to steer for the gunboat.', '坎贝尔上尉命令舵手将船开向炮艇。');
INSERT INTO `words` VALUES (4016, 'life', 'laɪf', 'n.', '生命;人命;性命;', '...a baby\'s first minutes of life...', '婴儿生命的最初几分钟');
INSERT INTO `words` VALUES (4017, 'lifelong', 'ˈlaɪflɒŋ', 'adj.', '终身的;毕生的;', '...her lifelong friendship with Naomi.', '她与娜奥米之间终生不渝的友谊');
INSERT INTO `words` VALUES (4018, 'lifestyle', 'ˈlaɪfstaɪl', 'n.', '生活方式;工作方式;', 'They enjoyed an income and lifestyle that many people would envy.', '他们的收入和生活方式会令很多人都羡慕不已。');
INSERT INTO `words` VALUES (4019, 'lifetime', 'ˈlaɪftaɪm', 'n.', '一生;终身;有生之年;', 'During my lifetime I haven\'t got around to much travelling.', '我一生中没怎么去旅行过。');
INSERT INTO `words` VALUES (4020, 'lift', 'lɪft', 'v.', '(被)提起，举起，抬高，吊起;移开;移动;', 'The Colonel lifted the phone and dialed his superior...', '上校拿起电话，拨通了上司的号码。');
INSERT INTO `words` VALUES (4021, 'lighten', 'ˈlaɪtn', 'v.', '减轻，减少(工作量、债务、担忧等);(使)变明亮，变成淡色;(使)感到不那么悲伤(或担忧、严肃);', 'The sky began to lighten...', '天空开始变亮了。');
INSERT INTO `words` VALUES (4022, 'lighthearted', 'ˌlaɪt\'hɑː(r)tɪd', 'adj.', '无忧无虑的;', 'They were light-hearted and prepared to enjoy life.', '他们无忧无虑，随时准备享受生活。');
INSERT INTO `words` VALUES (4023, 'lighting', 'ˈlaɪtɪŋ', 'n.', '照明;灯光;布光;', '...the bright fluorescent lighting of the laboratory...', '实验室里明亮的日光灯照明');
INSERT INTO `words` VALUES (4024, 'lightning', 'ˈlaɪtnɪŋ', 'n.', '闪电;', 'One man died when he was struck by lightning...', '一人遭雷击致死。');
INSERT INTO `words` VALUES (4025, 'lightweight', 'ˈlaɪtweɪt', 'adj.', '(布料)轻量的，薄型的;不严肃的;给人印象不深的;', '...lightweight denim...', '轻磅牛仔面料');
INSERT INTO `words` VALUES (4026, 'likelihood', 'ˈlaɪklihʊd', 'n.', '可能;可能性;', 'The likelihood of infection is minimal...', '感染的可能性极小。');
INSERT INTO `words` VALUES (4027, 'likely', 'ˈlaɪkli', 'adj.', '可能的;预料的;有希望的;', 'Experts say a \'yes\' vote is still the likely outcome...', '专家说结果仍可能是投赞成票。');
INSERT INTO `words` VALUES (4028, 'like-minded', 'ˌlaɪk ˈmaɪndɪd', 'adj.', '想法相同的;志趣相投的;', '...the opportunity to mix with hundreds of like-minded people.', '与许许多多志趣相投的人交流的机会');
INSERT INTO `words` VALUES (4029, 'liken', 'ˈlaɪkən', 'v.', '把…比作;把…比拟为;', 'She likens marriage to slavery...', '她把婚姻比作奴役。');
INSERT INTO `words` VALUES (4030, 'likeness', 'ˈlaɪknəs', 'n.', '相像;相似;(尤指画得像的)肖像，画像;', 'These myths have a startling likeness to one another...', '这些神话彼此间有惊人的相似性。');
INSERT INTO `words` VALUES (4031, 'likewise', 'ˈlaɪkwaɪz', 'adv.', '同样地;类似地;也;', 'These requests are ignored. His requests for rent are likewise sometimes ignored...', '这些要求被置之不理。他关于支付租金的要求有时也同样未被理睬。');
INSERT INTO `words` VALUES (4032, 'liking', 'ˈlaɪkɪŋ', 'n.', '喜欢;喜好;嗜好;', 'She had a liking for good clothes...', '她喜欢高级服装。');
INSERT INTO `words` VALUES (4033, 'lilac', 'ˈlaɪlək', 'n.', '丁香;淡紫色;丁香紫;', 'Lilacs grew against the side wall.', '丁香树倚着边墙生长。');
INSERT INTO `words` VALUES (4034, 'lilt', 'lɪlt', 'n.', '(说话声的)抑扬顿挫;节奏欢快的旋律;轻快活泼的曲调;', 'Her voice is childlike, with a West Country lilt.', '她的声音如孩童般，带有英格兰西南部诸郡的那种抑扬顿挫。');
INSERT INTO `words` VALUES (4035, 'limb', 'lɪm', 'n.', '肢;臂;腿;', 'She would be able to stretch out her cramped limbs and rest for a few hours.', '她将能伸展一下蜷缩的四肢，休息几个小时。');
INSERT INTO `words` VALUES (4036, 'limbo', 'ˈlɪmbəʊ', 'n.', '林波舞(西印度群岛舞蹈，舞者向后弯腰钻过一次比一次降低的横杆);(尤指因等待他人作决定)处于不定状态;', 'I didn\'t know whether my family was alive or dead. I felt as if I was in limbo...', '我不知道家人是生是死，感觉自己茫然无措。');
INSERT INTO `words` VALUES (4037, 'lime', 'laɪm', 'n.', '石灰;酸橙;酸橙汁;', '...peeled slices of lime...', '剥了皮的酸橙片');
INSERT INTO `words` VALUES (4038, 'limelight', 'ˈlaɪmlaɪt', 'n.', '公众注意的中心;', 'Tony has now been thrust into the limelight, with a high-profile job.', '托尼现在一下子成了众人关注的中心，因为他有一份经常出镜、见报的工作。');
INSERT INTO `words` VALUES (4039, 'limestone', 'ˈlaɪmstəʊn', 'n.', '石灰岩;', '...high limestone cliffs...', '高耸的石灰岩峭壁');
INSERT INTO `words` VALUES (4040, 'limit', 'ˈlɪmɪt', 'n.', '限度;限制;极限;', 'Her love for him was being tested to its limits...', '她对他的爱经受着极其严峻的考验。');
INSERT INTO `words` VALUES (4041, 'limitation', 'ˌlɪmɪˈteɪʃn', 'n.', '限制;控制;起限制作用的规则(或事实、条件);', 'All the talk had been about the limitation of nuclear weapons.', '谈话都是围绕着限制核武器进行的。');
INSERT INTO `words` VALUES (4042, 'limitations', 'lɪmɪˈteɪʃnz', 'n.', '限制;控制;起限制作用的规则(或事实、条件);', 'All the talk had been about the limitation of nuclear weapons.', '谈话都是围绕着限制核武器进行的。');
INSERT INTO `words` VALUES (4043, 'limited', 'ˈlɪmɪtɪd', 'adj.', '有限的;受(…的)限制;', 'They may only have a limited amount of time to get their points across...', '他们可能只有有限的时间来说明自己的意思。');
INSERT INTO `words` VALUES (4044, 'limitless', 'ˈlɪmɪtləs', 'adj.', '无限制的;无界限的;无限度的;', '...a cheap and potentially limitless supply of energy...', '廉价而且可能永远不会枯竭的能源供应');
INSERT INTO `words` VALUES (4045, 'limp', 'lɪmp', 'adj.', '无力的;无生气的;无精神的;', 'I wasn\'t badly hurt, but I injured my thigh and had to limp...', '我伤得不重，但一条大腿伤到了，只好一瘸一拐地走。');
INSERT INTO `words` VALUES (4046, 'limpid', 'ˈlɪmpɪd', 'adj.', '清澈的;清晰的;透明的;', '...limpid blue eyes.', '清澈的蓝眼睛');
INSERT INTO `words` VALUES (4047, 'line', 'laɪn', 'n.', '线;线条;界线;', 'Draw a line down that page\'s  center.', '沿那一页的中心画一条竖线。');
INSERT INTO `words` VALUES (4048, 'lineage', 'ˈlɪniɪdʒ', 'n.', '世系;宗系;家系;', 'They can trace their lineage directly back to the 18th century.', '他们的世系可以直接追溯到18世纪。');
INSERT INTO `words` VALUES (4049, 'linear', 'ˈlɪniə(r)', 'adj.', '线的;直线的;线状的;', 'Her novel subverts the conventions of linear narrative. It has no neat chronology and no tidy denouement.', '她的小说颠覆了线性叙事的传统，没有清晰的时间线索，也没有简单明了的结局。');
INSERT INTO `words` VALUES (4050, 'linen', 'ˈlɪnɪn', 'n.', '亚麻布;亚麻织品;', '...a white linen suit.', '白色亚麻套装');
INSERT INTO `words` VALUES (4051, 'liner', 'ˈlaɪnə(r)', 'n.', '邮轮;衬里;内衬;', '...luxury ocean liners.', '豪华远洋客轮');
INSERT INTO `words` VALUES (4052, 'lines', 'laɪnz', 'n.', '线;线条;界线;', 'Draw a line down that page\'s  center.', '沿那一页的中心画一条竖线。');
INSERT INTO `words` VALUES (4053, 'lineup', 'ˈlaɪnʌp', 'n.', '列队辨认，阵容；一组人；电视节目时间表;', 'Ryan Giggs is likely to be in Wales\'s starting line-up for their World Cup qualifying match...', '瑞恩·吉格斯可能出现在威尔士队世界杯预选赛的首发阵容中。');
INSERT INTO `words` VALUES (4054, 'linger', 'ˈlɪŋɡə(r)', 'v.', '继续存留;缓慢消失;流连;', 'The scent of her perfume lingered on in the room...', '她的香水味道在房间里久久不散。');
INSERT INTO `words` VALUES (4055, 'lingering', 'ˈlɪŋɡərɪŋ', 'adj.', '拖延的;缠绵的;缓慢消失的;', 'The scent of her perfume lingered on in the room...', '她的香水味道在房间里久久不散。');
INSERT INTO `words` VALUES (4056, 'linguist', 'ˈlɪŋɡwɪst', 'n.', '通晓数国语言的人;语言学家;', 'Her brother was an accomplished linguist.', '她的哥哥能熟练地说好几种语言。');
INSERT INTO `words` VALUES (4057, 'linguistic', 'lɪŋˈɡwɪstɪk', 'adj.', '语言的;语言学的;', '...linguistic skills.', '语言技能');
INSERT INTO `words` VALUES (4058, 'linguistics', 'lɪŋˈɡwɪstɪks', 'n.', '语言学;', '...linguistic skills.', '语言技能');
INSERT INTO `words` VALUES (4059, 'lining', 'ˈlaɪnɪŋ', 'n.', '衬层;内衬;衬里;', '...a padded satin jacket with quilted lining.', '带夹棉衬里的缎面短外套');
INSERT INTO `words` VALUES (4060, 'link', 'lɪŋk', 'n.', '联系;连接;关系;', '...the link between smoking and lung cancer.', '吸烟与胃癌之间的关联');
INSERT INTO `words` VALUES (4061, 'linkage', 'ˈlɪŋkɪdʒ', 'n.', '连接;联系;链环;', 'No one disputes the direct linkage between the unemployment rate and crime...', '谁都不会质疑失业率与犯罪之间存在直接联系。');
INSERT INTO `words` VALUES (4062, 'linkup', '', 'n.', '连接（物）；联系（络）；会合；同盟;', '...a live satellite link-up with Bonn.', '与波恩的直播卫星连接');
INSERT INTO `words` VALUES (4063, 'linoleum', 'lɪˈnəʊliəm', 'n.', '油地毡;', '...a gray linoleum floor.', '灰色的油毡地面');
INSERT INTO `words` VALUES (4064, 'lip', 'lɪp', 'n.', '嘴唇;嘴唇…的;(容器或凹陷地方的)边，边沿;', 'Wade stuck the cigarette between his lips.', '韦德把香烟叼在嘴里。');
INSERT INTO `words` VALUES (4065, 'lips', 'lɪps', 'n.', '嘴唇;嘴唇…的;(容器或凹陷地方的)边，边沿;', 'Wade stuck the cigarette between his lips.', '韦德把香烟叼在嘴里。');
INSERT INTO `words` VALUES (4066, 'lipstick', 'ˈlɪpstɪk', 'n.', '口红;唇膏;', 'She was wearing red lipstick.', '她抹了红色唇膏。');
INSERT INTO `words` VALUES (4067, 'liquefy', 'ˈlɪkwɪfaɪ', 'v.', '(使)液化;', 'Heat the jam until it liquefies...', '把果酱加热至其熔化。');
INSERT INTO `words` VALUES (4068, 'liquid', 'ˈlɪkwɪd', 'n.', '液体;', 'Drink plenty of liquid...', '要大量饮水。');
INSERT INTO `words` VALUES (4069, 'liquidate', 'ˈlɪkwɪdeɪt', 'v.', '清算，清盘(停业后将资产出售，偿还债务);变卖;变现;', 'A unanimous vote was taken to liquidate the company.', '全体投票一致通过停业清理公司。');
INSERT INTO `words` VALUES (4070, 'liquidation', 'ˌlɪkwɪˈdeɪʃn', 'n.', '清盘;清算;清偿;', 'A unanimous vote was taken to liquidate the company.', '全体投票一致通过停业清理公司。');
INSERT INTO `words` VALUES (4071, 'liquidity', 'lɪˈkwɪdəti', 'n.', '资产流动性;资产变现能力;', 'The company maintains a high degree of liquidity.', '公司保持着很高的资产折现度。');
INSERT INTO `words` VALUES (4072, 'liquor', 'ˈlɪkə(r)', 'n.', '烈性酒;含酒精饮料;', 'The room was filled with cases of liquor.', '房间里堆满了整箱整箱的烈性酒。');
INSERT INTO `words` VALUES (4073, 'lisp', 'lɪsp', 'n.', '咬舌(语言缺陷，把s说成th);', 'He has a slight lisp.', '他说话有点咬舌。');
INSERT INTO `words` VALUES (4074, 'list', 'lɪst', 'n.', '一览表;名单;目录;', 'We are making a list of the top ten men we would not want to be married to...', '我们正在列一份我们最不愿意嫁的十大男人的名单。');
INSERT INTO `words` VALUES (4075, 'listen', 'ˈlɪsn', 'v.', '(注意地)听;倾听;听信;', 'He spent his time listening to the radio...', '他听收音机打发时间。');
INSERT INTO `words` VALUES (4076, 'listener', 'ˈlɪsənə(r)', 'n.', '听者;收听广播节目的人;', 'I\'m a regular listener to her show...', '我经常收听她的节目。');
INSERT INTO `words` VALUES (4077, 'listening', 'ˈlɪsnɪŋ', 'v.', '(注意地)听;倾听;听信;', 'He spent his time listening to the radio...', '他听收音机打发时间。');
INSERT INTO `words` VALUES (4078, 'listing', 'ˈlɪstɪŋ', 'n.', '(尤指按字母顺序排列的)表册，目录，列表;(报章或杂志有关某城市电影、戏剧等的)上映信息，演出信息;(表册上的)位置，项目;', 'A full listing of the companies will be published quarterly...', '公司名录大全每季度出版一次。');
INSERT INTO `words` VALUES (4079, 'literacy', 'ˈlɪtərəsi', 'n.', '读写能力;', 'Many adults have some problems with literacy and numeracy...', '很多成年人在读写和计算方面都有困难。');
INSERT INTO `words` VALUES (4080, 'literal', 'ˈlɪtərəl', 'adj.', '字面意义的;完全按原文的;缺乏想象力的;', 'In many cases, the people there are fighting, in a literal sense, for their homes.', '很多情况下，那里的人们是真刀真枪地在为家园而抗争。');
INSERT INTO `words` VALUES (4081, 'literally', 'ˈlɪtərəli', 'adv.', '按字面;字面上;(强调事实可能令人惊讶)真正地，确实地;', 'We\'ve  got to get the economy under control or it will literally eat us up...', '我们必须设法控制经济，不然它非把我们吞噬了不可。');
INSERT INTO `words` VALUES (4082, 'literary', 'ˈlɪtərəri', 'adj.', '文学的;文学上的;适于文学作品的;', 'Her literary criticism focuses on the way great literature suggests ideas...', '她的文学批评集中关注的是伟大的文学作品表达思想的方式。');
INSERT INTO `words` VALUES (4083, 'literate', 'ˈlɪtərət', 'adj.', '有读写能力的;有文化的;', 'Over one-quarter of the adult population are not fully literate.', '四分之一以上的成年人不具备完全的读写能力。');
INSERT INTO `words` VALUES (4084, 'literature', 'ˈlɪtrətʃə(r)', 'n.', '文学;文学作品;(某学科的)文献，著作，资料;', '...classic works of literature.', '文学经典');
INSERT INTO `words` VALUES (4085, 'litter', 'ˈlɪtə(r)', 'n.', '(在公共场所乱扔的)垃圾，废弃物，杂物;乱七八糟的东西;乱放的杂物;', 'If you see litter in the corridor, pick it up...', '在过道里看到垃圾的话请捡起来。');
INSERT INTO `words` VALUES (4086, 'livelihood', 'ˈlaɪvlihʊd', 'n.', '赚钱謀生的手段;生计;', '...fishermen who depend on the seas for their livelihood...', '靠大海为生的渔夫');
INSERT INTO `words` VALUES (4087, 'lively', 'ˈlaɪvli', 'adj.', '精力充沛的;生气勃勃的;活跃热情的;', 'She had a sweet, lively personality...', '她的性格可爱活泼。');
INSERT INTO `words` VALUES (4088, 'livestock', 'ˈlaɪvstɒk', 'n.', '牲畜;家畜;', 'The heavy rains and flooding killed scores of livestock.', '暴雨和大水淹死了许多牲口。');
INSERT INTO `words` VALUES (4089, 'living', 'ˈlɪvɪŋ', 'adj.', '活着的;活的;在使用的;', 'Father never talked about what he did for a living...', '父亲从不谈他的谋生之道。');
INSERT INTO `words` VALUES (4090, 'load', 'ləʊd', 'n.', '负载;负荷;装载量;', 'The three men seemed to have finished loading the truck...', '这3名男子似乎已把东西往卡车上装载完毕。');
INSERT INTO `words` VALUES (4091, 'loaded', 'ˈləʊdɪd', 'adj.', '装载的;满载而沉重的;充满…的;', 'That\'s a loaded question.', '这是个诱导性的问题。');
INSERT INTO `words` VALUES (4092, 'loading', 'ˈləʊdɪŋ', 'n.', '附加工资(因有特别技能或资格);附加费用;', 'The three men seemed to have finished loading the truck...', '这3名男子似乎已把东西往卡车上装载完毕。');
INSERT INTO `words` VALUES (4093, 'loan', 'ləʊn', 'n.', '贷款;借款;借出;', 'The country has no access to foreign loans or financial aid...', '该国得不到任何外国贷款或经济援助。');
INSERT INTO `words` VALUES (4094, 'loath', 'ləʊθ', 'adj.', '不情愿;不乐意;勉强;', 'She is loath to give up her hard-earned liberty...', '她不愿放弃好不容易得来的自由。');
INSERT INTO `words` VALUES (4095, 'lobby', 'ˈlɒbi', 'n.', '(公共建筑物进口处的)门厅，前厅，大厅;(英国议会的)民众接待厅;(就某问题企图影响从政者的)游说团体;', 'Carers from all over the UK lobbied Parliament last week to demand a better financial deal...', '英国各地的家庭看护人员上周向议会游说，要求改善经济待遇。');
INSERT INTO `words` VALUES (4096, 'lobster', 'ˈlɒbstə(r)', 'n.', '龙虾;(供食用的)龙虾肉;', 'She sold me a couple of live lobsters.', '她卖了几只活龙虾给我。');
INSERT INTO `words` VALUES (4097, 'local', 'ˈləʊkl', 'adj.', '地方的;当地的;本地的;', 'We\'d better check on the match in the local paper...', '我们最好在本地报纸上查一下这场比赛。');
INSERT INTO `words` VALUES (4098, 'locale', 'ləʊˈkɑːl', 'n.', '发生地点;现场;', 'An amusement park is the perfect locale for a bunch of irrepressible youngsters to have all sorts of adventures.', '游乐场是一帮精力过剩的年轻人寻求各种惊险刺激的绝佳场所。');
INSERT INTO `words` VALUES (4099, 'locality', 'ləʊˈkæləti', 'n.', '(围绕所处或提及的)地区;(某人或某物存在的)地方，地点;', 'Following the discovery of the explosives the president cancelled his visit to the locality...', '发现炸药后，总统取消了对该地的访问。');
INSERT INTO `words` VALUES (4100, 'locally', 'ˈləʊkəli', 'adv.', '局部地，在本地;', 'We\'d better check on the match in the local paper...', '我们最好在本地报纸上查一下这场比赛。');
INSERT INTO `words` VALUES (4101, 'locate', 'ləʊˈkeɪt', 'v.', '找出…的准确位置;确定…的准确地点;把…安置在(或建造于);', 'The scientists want to locate the position of the gene on a chromosome...', '科学家们希望找出该基因在染色体中的位置。');
INSERT INTO `words` VALUES (4102, 'located', 'ləʊˈkeɪtɪd', 'adj.', '位于;坐落在;', 'The restaurant is located near the cathedral...', '餐馆位于大教堂附近。');
INSERT INTO `words` VALUES (4103, 'location', 'ləʊˈkeɪʃn', 'n.', '地方;地点;位置;', 'The first thing he looked at was his  office\'s location...', '他首先看的是自己办公室的位置。');
INSERT INTO `words` VALUES (4104, 'lock', 'lɒk', 'v.', '(用锁)锁上;被锁住;把…锁起来;', 'Are you sure you locked the front door?...', '你确定锁了前门吗？');
INSERT INTO `words` VALUES (4105, 'locking', 'ˈlɒkɪŋ', 'v.', '(用锁)锁上;被锁住;把…锁起来;', 'Are you sure you locked the front door?...', '你确定锁了前门吗？');
INSERT INTO `words` VALUES (4106, 'lockup', 'ˈlɒkʌp', 'n.', '同 jail;(不与车主房子相连的)车库;', '...the 450 inmates at the maximum-security lock-up in Lucasville.', '被关在卢卡斯维尔一级警戒监狱的450名囚犯');
INSERT INTO `words` VALUES (4107, 'locomotive', 'ˌləʊkəˈməʊtɪv', 'n.', '机车;火车头;', 'Steam locomotives pumped out clouds of white smoke.', '蒸汽机车喷出一团团白烟。');
INSERT INTO `words` VALUES (4108, 'locus', 'ˈləʊkəs', 'n.', '(某事发生的)确切地点;(被视为某物的)中心，核心;', 'Barcelona is the locus of Spanish industry...', '巴塞罗那是西班牙工业中心。');
INSERT INTO `words` VALUES (4109, 'lodge', 'lɒdʒ', 'n.', '(供参加户外运动者暂住的)乡间小屋，小舍;(公园或宅第的)门口小屋，门房;管理员室;', '...a Victorian hunting lodge.', '维多利亚式狩猎度假屋');
INSERT INTO `words` VALUES (4110, 'lodging', 'ˈlɒdʒɪŋ', 'n.', '暂住;寄宿;借宿;', 'He was given free lodging in a three-room flat.', '他得以在一间三室公寓里免费住宿。');
INSERT INTO `words` VALUES (4111, 'lofty', 'ˈlɒfti', 'adj.', '巍峨的;高耸的;崇高的;', 'It was a bank that started out with grand ideas and lofty ideals...', '这是一家创办伊始抱有雄心壮志的银行。');
INSERT INTO `words` VALUES (4112, 'log', 'lɒɡ', 'n.', '原木;(某时期事件的)正式记录，日志;(尤指)航海日志，飞行日志;', 'He dumped the logs on the big stone hearth.', '他将一根根短棍木柴扔进巨大的石壁炉里。');
INSERT INTO `words` VALUES (4113, 'logged', 'lɒɡd', 'v.', '把…载入正式记录;记录;行驶，行进(若干距离或时间);', 'He dumped the logs on the big stone hearth.', '他将一根根短棍木柴扔进巨大的石壁炉里。');
INSERT INTO `words` VALUES (4114, 'logging', 'ˈlɒɡɪŋ', 'n.', '伐木作业;', 'Logging companies would have to leave a central area of the forest before the end of the year.', '伐木公司可能要在年底前把森林的一处中心地带留出来。');
INSERT INTO `words` VALUES (4115, 'logic', 'ˈlɒdʒɪk', 'n.', '思维方式;解释方法;逻辑;', 'Apart from criminal investigation techniques, students learn forensic medicine, philosophy and logic.', '除罪案调查技巧外，学生们还要学习法医学、哲学和逻辑学。');
INSERT INTO `words` VALUES (4116, 'logical', 'ˈlɒdʒɪkl', 'adj.', '必然的;合乎情理的;合乎常理的;', 'Only when each logical step has been checked by other mathematicians will the proof be accepted.', '只有每一逻辑步骤都被其他数学家验证之后，证明才能成立。');
INSERT INTO `words` VALUES (4117, 'loll', 'lɒl', 'v.', '懒洋洋地躺着(或坐着、站着);耷拉;下垂;', 'He was lolling on the sofa in the shadows near the fire...', '他懒洋洋地躺在炉火边一个背光的沙发上。');
INSERT INTO `words` VALUES (4118, 'loneliness', 'ˈləʊnlinəs', 'n.', '孤独;寂寞;', 'I have so many friends, but deep down, underneath, I have a fear of loneliness.', '我虽然有很多朋友，但心底里还是惧怕寂寞。');
INSERT INTO `words` VALUES (4119, 'lonely', 'ˈləʊnli', 'adj.', '孤独的;寂寞的;在孤单中度过的;', '...lonely people who just want to talk...', '只想找人倾诉的孤独的人');
INSERT INTO `words` VALUES (4120, 'lonesome', 'ˈləʊnsəm', 'adj.', '孤独的;寂寞的;人烟稀少的;', 'I\'ve grown so lonesome, thinking of you.', '想起你，我觉得非常寂寞。');
INSERT INTO `words` VALUES (4121, 'longevity', 'lɒnˈdʒevəti', 'n.', '长寿;长命;持久;', 'Human longevity runs in families...', '人类的长寿具有家族遗传性。');
INSERT INTO `words` VALUES (4122, 'longing', 'ˈlɒŋɪŋ', 'n.', '(对…的)渴望，热望;', 'He felt a longing for the familiar...', '他渴望找到知交。');
INSERT INTO `words` VALUES (4123, 'longitude', 'ˈlɒŋɡɪtjuːd', 'n.', '经度;', 'He noted the latitude and longitude, then made a mark on the admiralty chart.', '他记下纬度和经度，然后在航海图上做了个标记。');
INSERT INTO `words` VALUES (4124, 'loom', 'luːm', 'v.', '赫然耸现;(尤指)令人惊恐地隐现;显得突出;', 'Vincent loomed over me, as pale and grey as a tombstone.', '文森特赫然耸现在我面前，面色灰白得像一块墓碑。');
INSERT INTO `words` VALUES (4125, 'loop', 'luːp', 'n.', '环形;环状物;圆圈;', 'Mrs. Morrell reached for a loop of garden hose.', '莫雷尔太太伸手去拿绕成圈的花园浇水用的软管。');
INSERT INTO `words` VALUES (4126, 'loose', 'luːs', 'adj.', '未固定牢的;可分开的;未系(或捆)在一起的;', 'If a tooth feels very loose, your dentist may recommend that it\'s taken out...', '如果有一颗牙齿很松，牙医可能会建议你把它拔掉。');
INSERT INTO `words` VALUES (4127, 'loosely', 'ˈluːsli', 'adv.', '宽松地;松散地;不精确地;', 'If a tooth feels very loose, your dentist may recommend that it\'s taken out...', '如果有一颗牙齿很松，牙医可能会建议你把它拔掉。');
INSERT INTO `words` VALUES (4128, 'loosen', 'ˈluːsn', 'v.', '(使)放松，变松;解开，松开(衣服、头发等);松开，放开(手等);', 'Many business groups have been pressing the Federal Reserve to loosen interest rates...', '不少企业集团一直在向联邦储备委员会施压，要求其放松利率管制。');
INSERT INTO `words` VALUES (4129, 'lord', 'lɔːd', 'n.', '(英国)贵族;勋爵(英国贵族的称号);(英国用于某些高级官员的职位前)阁下，大人，大臣;', 'She married a lord and lives in this huge house in the Cotswolds...', '她嫁给了一位勋爵，住在科茨沃尔德的这幢大房子里。');
INSERT INTO `words` VALUES (4130, 'lorry', 'ˈlɒri', 'n.', '卡车;货运汽车;', '...a seven-ton lorry.', '7吨的卡车');
INSERT INTO `words` VALUES (4131, 'lose', 'luːz', 'v.', '遗失;丢失;(因事故、年老、死亡等)损失，丧失，失去;', 'A C Milan lost the Italian Cup Final...', 'AC米兰队输掉了意大利杯决赛。');
INSERT INTO `words` VALUES (4132, 'loss', 'lɒs', 'n.', '丧失;损失;丢失;', '...loss of sight...', '失明');
INSERT INTO `words` VALUES (4133, 'lot', 'lɒt', 'pron.', '大量;许多;', 'A lot of our land is used to grow crops for export...', '我们有很多土地被用来种植出口作物。');
INSERT INTO `words` VALUES (4134, 'lottery', 'ˈlɒtəri', 'n.', '(用发行彩票为政府、慈善机构等集资的)抽彩给奖法;碰运气的事;', '...the national lottery.', '国家博彩');
INSERT INTO `words` VALUES (4135, 'loud', 'laʊd', 'adj.', '喧闹的;响亮的;大声的;', 'Suddenly there was a loud bang...', '忽然传来一声巨响。');
INSERT INTO `words` VALUES (4136, 'loudness', 'ˈlaʊdnəs', 'n.', '高（大）声；响度，音量;', 'Suddenly there was a loud bang...', '忽然传来一声巨响。');
INSERT INTO `words` VALUES (4137, 'lounge', 'laʊndʒ', 'n.', '(机场等的)等候室;(旅馆、俱乐部等的)休息室;(私宅中的)起居室;', 'The Holmbergs were sitting before a roaring fire in the lounge, sipping their cocoa.', '霍姆伯格一家坐在客厅里，对着熊熊炉火呷着可可。');
INSERT INTO `words` VALUES (4138, 'lovable', 'ˈlʌvəbl', 'adj.', '可爱的;惹人爱的;讨人喜欢的;', 'His vulnerability makes him even more lovable.', '他的脆弱反倒让他更加讨人喜欢。');
INSERT INTO `words` VALUES (4139, 'love', 'lʌv', 'n.', '爱;热爱;慈爱;', 'Oh, Amy, I love you...', '哦，埃米，我爱你。');
INSERT INTO `words` VALUES (4140, 'lovely', 'ˈlʌvli', 'adj.', '美丽的;优美的;有吸引力的;', 'You look lovely, Marcia...', '你看上去真漂亮，马西娅。');
INSERT INTO `words` VALUES (4141, 'lover', 'ˈlʌvə(r)', 'n.', '(婚外的)情人，情侣;爱好者;热爱者;', 'Every Thursday she would meet her lover Leon...', '每周四她都会和情人利昂幽会。');
INSERT INTO `words` VALUES (4142, 'low', 'ləʊ', 'adj.', '低的;矮的;离地面近的;', '...the low garden wall that separated the front garden from next door...', '把前花园和隔壁隔开的低矮的花园围墙');
INSERT INTO `words` VALUES (4143, 'lower', 'ˈləʊə(r) , ˈlaʊə(r)', 'adj.', '(尤指位于同类物品或成对物品中另一个的)下面的，下方的;在底部的;近底部的;', 'She bit her lower lip.', '她咬了咬下唇。');
INSERT INTO `words` VALUES (4144, 'lowest', 'ˈləʊɪst', 'adv.', '低;向下;不高;', '...the low garden wall that separated the front garden from next door...', '把前花园和隔壁隔开的低矮的花园围墙');
INSERT INTO `words` VALUES (4145, 'loyal', 'ˈlɔɪəl', 'adj.', '忠诚的;忠实的;', 'They had remained loyal to the president...', '他们始终对总统忠心耿耿。');
INSERT INTO `words` VALUES (4146, 'loyalty', 'ˈlɔɪəlti', 'n.', '忠诚;忠实;忠心耿耿;', 'I have sworn an oath of loyalty to the monarchy...', '我已发誓效忠皇室。');
INSERT INTO `words` VALUES (4147, 'lubricate', 'ˈluːbrɪkeɪt', 'v.', '给…上润滑油;上油于;', 'Mineral oils are used to lubricate machinery.', '矿物油被用来润滑机器。');
INSERT INTO `words` VALUES (4148, 'lubrication', 'ˌluːbrɪˈkeɪʃn', 'n.', '润滑；润滑作用;', 'Mineral oils are used to lubricate machinery.', '矿物油被用来润滑机器。');
INSERT INTO `words` VALUES (4149, 'lucid', 'ˈluːsɪd', 'adj.', '表达清楚的;易懂的;(尤指生病期间或病愈后，糊涂状态中或过后)头脑清晰的，清醒的;', '...a lucid account of the history of mankind...', '对人类历史的清楚记述');
INSERT INTO `words` VALUES (4150, 'luck', 'lʌk', 'n.', '好运;幸运;侥幸;', 'I knew I needed a bit of luck to win...', '我知道自己要赢需要点运气。');
INSERT INTO `words` VALUES (4151, 'lucky', 'ˈlʌki', 'adj.', '有好运的;运气好的;幸运的;', 'I am luckier than most. I have a job...', '我比大部分人幸运。我有份工作。');
INSERT INTO `words` VALUES (4152, 'lucrative', 'ˈluːkrətɪv', 'adj.', '赚大钱的;获利多的;', 'Thousands of ex-army officers have found lucrative jobs in private security firms.', '数以千计的退伍军官在私营保安公司找到了收入不菲的工作。');
INSERT INTO `words` VALUES (4153, 'ludicrous', 'ˈluːdɪkrəs', 'adj.', '不合理的;不能当真的;', 'It was ludicrous to suggest that the visit could be kept secret...', '认为此次访问能够保密是可笑的。');
INSERT INTO `words` VALUES (4154, 'luggage', 'ˈlʌɡɪdʒ', 'n.', '行李;', 'Leave your luggage in the hotel...', '将您的行李放在酒店。');
INSERT INTO `words` VALUES (4155, 'lukewarm', 'ˌluːkˈwɔːm', 'adj.', '微温的;不冷不热的;温和的;', 'Wash your face with lukewarm water...', '用温水洗脸。');
INSERT INTO `words` VALUES (4156, 'lull', 'lʌl', 'n.', '(活动间的)平静时期，间歇;', 'There was a lull in political violence after the election of the current president.', '现任总统当选之后，政治暴力进入了一段暂时的平静期。');
INSERT INTO `words` VALUES (4157, 'lumber', 'ˈlʌmbə(r)', 'n.', '废旧家具;不用的大件物品;', 'It was made of soft lumber, spruce by the look of it...', '其制作材料是软木，看起来像是云杉。');
INSERT INTO `words` VALUES (4158, 'luminous', 'ˈluːmɪnəs', 'adj.', '夜光的;发光的;发亮的;', 'The luminous dial on the clock showed five minutes to seven.', '那只钟的夜光钟面显示时间为6点55分。');
INSERT INTO `words` VALUES (4159, 'lump', 'lʌmp', 'n.', '(通常为无定形的)块;肿块;隆起;', 'The potter shaped and squeezed the lump of clay into a graceful shape.', '制陶工人把黏土块捏塑成优美的形状。');
INSERT INTO `words` VALUES (4160, 'lunacy', 'ˈluːnəsi', 'n.', '愚蠢的行为;疯狂;精神病;', '...the lunacy of the tax system...', '税制的荒谬');
INSERT INTO `words` VALUES (4161, 'lunar', 'ˈluːnə(r)', 'adj.', '月球的;月亮的;', 'The vast volcanic slope was eerily reminiscent of a lunar landscape.', '开阔的火山坡让人心生怪异地联想起月球的地貌。');
INSERT INTO `words` VALUES (4162, 'lunatic', 'ˈluːnətɪk', 'n.', '精神错乱者;狂人;严重精神病患者，疯子(现为冒犯语);', 'Her son thinks she\'s an absolute raving lunatic.', '她儿子认为她是个十足的疯子。');
INSERT INTO `words` VALUES (4163, 'lunch', 'lʌntʃ', 'n.', '午餐;午饭;', 'Shall we meet somewhere for lunch?...', '我们找地方见面一起吃个午饭好不好？');
INSERT INTO `words` VALUES (4164, 'luncheon', 'ˈlʌntʃən', 'n.', '午餐，午宴(正式的午餐或正式用语);', 'Earlier this month, a luncheon for former UN staff was held in Vienna.', '本月早些时候在维也纳设午宴招待了前联合国工作人员。');
INSERT INTO `words` VALUES (4165, 'lunchtime', 'ˈlʌntʃtaɪm', 'n.', '午餐时间;', 'Could we meet at lunchtime?', '我们能不能午饭时间见？');
INSERT INTO `words` VALUES (4166, 'lunge', 'lʌndʒ', 'v.', '猛冲;猛扑;', 'He lunged at me, grabbing me violently...', '他向我扑过来，使劲把我抓住。');
INSERT INTO `words` VALUES (4167, 'lurch', 'lɜːtʃ', 'v.', '突然前倾(或向一侧倾斜);(突然感到恐怖或激动时心或胃)猛地一跳(或动);', 'As the car sped over a pothole she lurched forward...', '车子飞驶过一个坑洼时，她身子猛地向前一倾。');
INSERT INTO `words` VALUES (4168, 'lure', 'lʊə(r)', 'v.', '劝诱;引诱;诱惑;', 'He lured her to his home and shot her with his father\'s gun...', '他把她哄到家里，用他父亲的枪射杀了她。');
INSERT INTO `words` VALUES (4169, 'lurid', 'ˈlʊərɪd', 'adj.', '俗艳的;花哨的;(故意地)骇人听闻的，令人毛骨悚然的;', '...lurid accounts of Claire\'s  sexual exploits...', '有关克莱尔的性生活史露骨的描述');
INSERT INTO `words` VALUES (4170, 'lurk', 'lɜːk', 'v./n.', '(尤指为做不正当的事而)埋伏，潜伏;潜在;隐藏着;', 'He thought he saw someone lurking above the chamber during the address.', '他觉得自己看见有人在演讲时潜藏在会议厅顶上。');
INSERT INTO `words` VALUES (4171, 'luscious', 'ˈlʌʃəs', 'adj.', '美味的;甘美的;可口的;', '...a luscious young blonde...', '性感的金发女郎');
INSERT INTO `words` VALUES (4172, 'lush', 'lʌʃ', 'adj.', '茂盛的;茂密的;草木繁茂的;', '...the lush green meadows bordering the river...', '河畔郁郁葱葱的青草地');
INSERT INTO `words` VALUES (4173, 'lust', 'lʌst', 'n.', '强烈的性欲;色欲;淫欲;', 'His relationship with Angie was the first which combined lust with friendship...', '他和安吉的关系属第一种，即肉欲加友谊。');
INSERT INTO `words` VALUES (4174, 'lustful', 'ˈlʌstfl', 'adj.', '有强烈性欲的;淫荡的;好色的;', '...lustful thoughts.', '淫念');
INSERT INTO `words` VALUES (4175, 'lustrous', 'ˈlʌstrəs', 'adj.', '柔软光亮的;', '...a head of thick, lustrous, wavy brown hair.', '一头浓密、富有光泽的褐色鬈发');
INSERT INTO `words` VALUES (4176, 'lusty', 'ˈlʌsti', 'adj.', '健壮的;强壮的;', '...plants with large, lusty roots.', '根部粗大而壮实的植物');
INSERT INTO `words` VALUES (4177, 'luxuriant', 'lʌɡˈʒʊəriənt', 'adj.', '茂盛的;浓密的;丰富的;', 'There were two very large oak trees in front of our house with wide spreading branches and luxuriant foliage.', '我们家门口有两棵枝繁叶茂的大橡树。');
INSERT INTO `words` VALUES (4178, 'luxurious', 'lʌɡˈʒʊəriəs', 'adj.', '十分舒适的;奢侈的;', 'Our honeymoon was two days in Las Vegas at a luxurious hotel called Le Mirage...', '我们的蜜月是在拉斯维加斯一家名为“海市蜃楼”的豪华酒店住了两天。');
INSERT INTO `words` VALUES (4179, 'luxury', 'ˈlʌkʃəri', 'n.', '奢侈的享受;奢华;奢侈品;', 'By all accounts he leads a life of considerable luxury...', '据说他的生活颇为奢侈。');
INSERT INTO `words` VALUES (4180, 'lynch', 'lɪntʃ', 'v.', '用私刑处死(被认为有罪的人，通常为绞刑);', 'They were about to lynch him when reinforcements from the army burst into the room and rescued him.', '他们正准备动私刑绞死他时，援军闯入房间把他救下了。');
INSERT INTO `words` VALUES (4181, 'lyric', 'ˈlɪrɪk', 'adj.', '抒情的;吟唱的;为吟唱谱写的;', '...Kurt Weill\'s Broadway opera with lyrics by Langston Hughes.', '由兰斯顿·休斯作词的库尔特·韦尔的百老汇歌剧');
INSERT INTO `words` VALUES (4182, 'lyrical', 'ˈlɪrɪkl', 'adj.', '抒情的;', 'His paintings became more lyrical.', '他的画作变得更加热情奔放。');
INSERT INTO `words` VALUES (4183, 'lyrically', 'ˈlɪrɪkli', 'adv.', '情感强烈地;抒情地;歌词上;', 'His paintings became more lyrical.', '他的画作变得更加热情奔放。');
INSERT INTO `words` VALUES (4184, 'lyrics', 'ˈlɪrɪks', 'n.', '抒情诗;歌词;', '...Kurt Weill\'s Broadway opera with lyrics by Langston Hughes.', '由兰斯顿·休斯作词的库尔特·韦尔的百老汇歌剧');
INSERT INTO `words` VALUES (4185, 'ma', 'mɑː', 'n.', '母亲;妈妈;妈;', 'Ma was still at work when I got back.', '我回来的时候，妈妈还没下班。');
INSERT INTO `words` VALUES (4186, 'macabre', 'məˈkɑːbrə', 'adj.', '可怕的，恐怖的(尤指与死亡等相联系的);', 'Police have made a macabre discovery...', '警方有一个令人毛骨悚然的发现。');
INSERT INTO `words` VALUES (4187, 'macerate', 'ˈmæsəreɪt', 'v.', '把(食物等)浸软;被浸软;', 'I like to macerate the food in liqueur for a few minutes before serving...', '我喜欢先把这种食物在利口酒里浸泡几分钟再端上桌。');
INSERT INTO `words` VALUES (4188, 'mach', 'mɑːk', 'n.', '马赫，马赫数(速度单位，常用于计算飞行速度，1马赫等于音速);', 'The hot air permitted higher speeds before the critical Mach number was exceeded...', '热空气可使之在超过临界马赫数之前达到更快的速度。');
INSERT INTO `words` VALUES (4189, 'machine', 'məˈʃiːn', 'n.', '机器;机械装置;(不提全称时的简略说法)机器;', 'I put the coin in the machine and pulled the lever...', '我把硬币投入机器，拉动控制杆。');
INSERT INTO `words` VALUES (4190, 'machinery', 'məˈʃiːnəri', 'n.', '(统称)机器;(尤指)大型机器;机器的运转部分;', '...quality tools and machinery.', '质量上乘的工具和机械');
INSERT INTO `words` VALUES (4191, 'mackerel', 'ˈmækrəl', 'n.', '鲭(鱼);', 'They\'d gone out to fish for mackerel.', '他们已经出海去捕鲭鱼。');
INSERT INTO `words` VALUES (4192, 'macro', 'ˈmækrəʊ', 'n.', '宏指令;', '...coordinated programmes of regulation of the economy both at the macro level and at the micro level.', '宏观与微观手段相协调的经济调控计划');
INSERT INTO `words` VALUES (4193, 'macrocosm', 'ˈmækrəʊkɒzəm', 'n.', '大而完整的结构;宇宙;宏观世界;', 'The macrocosm of the universe is mirrored in the microcosm of the mind.', '心灵天地是万千宇宙的缩影。');
INSERT INTO `words` VALUES (4194, 'macroeconomic', 'ˌmækrəʊˌiːkəˈnɒmɪk', 'adj.', '宏观经济（的）;', '...the attempt to substitute low inflation for full employment as a goal of macro-economic policy.', '尝试以低通货膨胀率取代充分就业作为宏观经济政策的目标');
INSERT INTO `words` VALUES (4195, 'mad', 'mæd', 'adj.', '疯的;神经错乱的;有精神病的;', 'She was afraid of going mad.', '她担心自己会疯掉。');
INSERT INTO `words` VALUES (4196, 'madam', 'ˈmædəm', 'n.', '夫人;女士;非常自我的女子;', 'Try them on, madam.', '太太，试穿一下吧！');
INSERT INTO `words` VALUES (4197, 'madden', 'ˈmædn', 'v.', '使非常生气;使发疯;', 'He knew that what he was saying did not reach her. And the knowledge of it maddened him...', '他知道她没有听他的话，这让他大为光火。');
INSERT INTO `words` VALUES (4198, 'maddening', 'ˈmædnɪŋ', 'adj.', '令人生气的;让人沮丧的;使人恼火的;', 'Shopping in the January sales can be  maddening...', '在元月促销期间购物可能会让人大为恼火。');
INSERT INTO `words` VALUES (4199, 'madman', 'ˈmædmən', 'n.', '疯子;精神病患者;', 'He wanted to jump up and run outside, screaming like a madman.', '他想跳起来跑到外面去，像疯子那样尖叫一番。');
INSERT INTO `words` VALUES (4200, 'madness', 'ˈmædnəs', 'n.', '精神失常;疯狂;(会带来危险的)癫狂，愚蠢行为;', 'She was afraid of going mad.', '她担心自己会疯掉。');
INSERT INTO `words` VALUES (4201, 'magazine', 'ˌmæɡəˈziːn', 'n.', '杂志;期刊;(电视、广播)专题节目;', 'Her face is on the cover of a dozen or more magazines.', '她的面孔出现在十几种杂志的封面上。');
INSERT INTO `words` VALUES (4202, 'magic', 'ˈmædʒɪk', 'n.', '魔法;法术;巫术;', 'They believe in magic...', '他们相信巫术。');
INSERT INTO `words` VALUES (4203, 'magical', 'ˈmædʒɪkl', 'adj.', '有魔力的;用于巫术的;奇妙的;', '...the story of Sin-Sin, a little boy who has magical powers.', '一个有魔力的小男孩辛辛的故事');
INSERT INTO `words` VALUES (4204, 'magnanimous', 'mæɡˈnænɪməs', 'adj.', '宽宏的，大度的(尤指对敌人或对手);', 'I was prepared to be magnanimous, prepared to feel compassion for him...', '我准备表现得宽宏大量，同情他的境遇。');
INSERT INTO `words` VALUES (4205, 'magnate', 'ˈmæɡneɪt', 'n.', '权贵;要人;富豪;', '...a multimillionaire shipping magnate.', '资产达数百万的船业巨头');
INSERT INTO `words` VALUES (4206, 'magnet', 'ˈmæɡnət', 'n.', '磁铁;磁石;吸铁石;', 'Prospect Park, with its vast lake, is a magnet for all health freaks...', '拥有一大片湖区的普罗斯佩克特公园吸引着所有注重健康的人。');
INSERT INTO `words` VALUES (4207, 'magnetic', 'mæɡˈnetɪk', 'adj.', '像磁铁的;有磁性的;磁的;', '...magnetic particles.', '磁性微粒');
INSERT INTO `words` VALUES (4208, 'magnetism', 'ˈmæɡnətɪzəm', 'n.', '磁性;磁力;吸引力;', 'There was no doubting the animal magnetism of the man...', '毫无疑问，这个男人身上透着一种野性魅力。');
INSERT INTO `words` VALUES (4209, 'magnification', 'ˌmæɡnɪfɪˈkeɪʃn', 'n.', '放大;放大率;放大倍数;', 'Pores  are visible without magnification...', '毛孔无须放大就可以看见。');
INSERT INTO `words` VALUES (4210, 'magnificent', 'mæɡˈnɪfɪsnt', 'adj.', '壮丽的;宏伟的;值得赞扬的;', '...a magnificent country house in wooded grounds.', '绿树掩映中的一幢乡间豪宅');
INSERT INTO `words` VALUES (4211, 'magnify', 'ˈmæɡnɪfaɪ', 'v.', '放大;扩大;增强;', 'This version of the Digges telescope magnifies images 11 times...', '这种型号的迪格斯望远镜可将图像放大11倍。');
INSERT INTO `words` VALUES (4212, 'magnitude', 'ˈmæɡnɪtjuːd', 'n.', '巨大;重大;重要性;', 'An operation of this magnitude is going to be difficult...', '这么大的手术实施起来十分困难。');
INSERT INTO `words` VALUES (4213, 'maid', 'meɪd', 'n.', '女仆;侍女;(旅馆里的)女服务员;', 'A maid brought me breakfast at half past eight.', '8点半女佣给我送来了早餐。');
INSERT INTO `words` VALUES (4214, 'maiden', 'ˈmeɪdn', 'n.', '少女;处女;未婚女子;', '...stories of noble princes and their brave deeds on behalf of beautiful maidens.', '高贵的王子和他们英雄救美的故事');
INSERT INTO `words` VALUES (4215, 'mail', 'meɪl', 'n.', '邮政;邮递系统;邮件;', 'Your check is in the mail...', '您的支票正在邮递中。');
INSERT INTO `words` VALUES (4216, 'maim', 'meɪm', 'v.', '使残废;使受重伤;', 'Mines have been scattered in rice paddies and jungles, maiming and killing civilians...', '地雷遍布稻田与林间，很多平民被炸死或炸成重伤。');
INSERT INTO `words` VALUES (4217, 'main', 'meɪn', 'adj.', '主要的;最重要的;', '...one of the main tourist areas of Amsterdam...', '阿姆斯特丹的主要旅游区之一');
INSERT INTO `words` VALUES (4218, 'mainland', 'ˈmeɪnlænd', 'n.', '大陆;(不包括附近岛屿的)国土的主体;', 'She was going to Nanaimo to catch the ferry to the mainland.', '她准备去纳奈莫乘渡船回大陆。');
INSERT INTO `words` VALUES (4219, 'mainline', 'ˈmeɪnlaɪn', 'adj.', '传统的;主流的;', '...the first mainline railway to be built in Britain for almost a hundred years...', '近一百年来英国将要建设的首条铁路干线');
INSERT INTO `words` VALUES (4220, 'mainly', 'ˈmeɪnli', 'adv.', '主要地;首要地;大部分;', 'The stock-market scandal is refusing to go away, mainly because there\'s still no consensus over how it should be dealt with...', '股票市场的丑闻迟迟没有消散，这主要是因为人们仍未就如何应对它达成共识。');
INSERT INTO `words` VALUES (4221, 'mainstream', 'ˈmeɪnstriːm', 'n.', '主流思想;主流群体;', '...people outside  the economic mainstream...', '经济主流之外的人');
INSERT INTO `words` VALUES (4222, 'maintain', 'meɪnˈteɪn', 'v.', '维持;保持;维修;', 'The Department maintains many close contacts with the chemical industry...', '这个部门与化工行业保持着众多密切的联系。');
INSERT INTO `words` VALUES (4223, 'maintenance', 'ˈmeɪntənəns', 'n.', '维护;保养;维持;', '...maintenance work on government buildings...', '政府大楼的修缮工作');
INSERT INTO `words` VALUES (4224, 'maize', 'meɪz', 'n.', '玉蜀黍;玉米;', '...vast fields of maize.', '广袤的玉米地');
INSERT INTO `words` VALUES (4225, 'majestic', 'məˈdʒestɪk', 'adj.', '雄伟的;威严的;壮观的;', '...a majestic country home that once belonged to the Astor family...', '一座曾经属于阿斯特家族的气派不凡的乡间住宅');
INSERT INTO `words` VALUES (4226, 'majesty', 'ˈmædʒəsti', 'n.', '雄伟壮观;庄严;威严;', 'I quite agree, Your Majesty...', '我十分赞成，陛下。');
INSERT INTO `words` VALUES (4227, 'major', 'ˈmeɪdʒə(r)', 'adj.', '主要的;重要的;大的;', 'The major factor in the decision to stay or to leave was usually professional...', '职业上的考虑往往是决定去留的主要因素。');
INSERT INTO `words` VALUES (4228, 'majority', 'məˈdʒɒrəti', 'n.', '大部分;大多数;(获胜的)票数;', 'The majority of my patients come to me from out of town...', '大多数来找我看病的患者都是外省人。');
INSERT INTO `words` VALUES (4229, 'maker', 'ˈmeɪkə(r)', 'n.', '生产者;制造者;制订者;', '...Japan\'s two largest car makers.', '日本最大的两家汽车制造商');
INSERT INTO `words` VALUES (4230, 'makeshift', 'ˈmeɪkʃɪft', 'adj.', '临时替代的;权宜的;', '...the cardboard boxes and makeshift shelters of the homeless.', '无家可归者的纸箱与临时的栖身处');
INSERT INTO `words` VALUES (4231, 'makeup', 'ˈmeɪkʌp', 'n.', '化妆品;天性;性格;组成;', 'Normally she wore little make-up...', '她一般不怎么化妆。');
INSERT INTO `words` VALUES (4232, 'making', 'ˈmeɪkɪŋ', 'n.', '生产;制造;', '...Salamon\'s book about the making of this movie...', '萨拉蒙有关本片制作过程的书');
INSERT INTO `words` VALUES (4233, 'maladjusted', 'ˌmæləˈdʒʌstɪd', 'adj.', '适应不良的;心理失调的;', '...a school for maladjusted children.', '为心理失调的儿童开设的学校');
INSERT INTO `words` VALUES (4234, 'maladroit', 'ˌmæləˈdrɔɪt', 'adj.', '不灵巧的;笨拙的;', 'Some of his first interviews with the press were rather maladroit.', '他最早接受媒体采访时有几次显得很生涩。');
INSERT INTO `words` VALUES (4235, 'malady', 'ˈmælədi', 'n.', '严重问题;痼疾;疾病;', 'He was stricken at twenty-one with a crippling malady.', '他21岁时突患致残重疾。');
INSERT INTO `words` VALUES (4236, 'malaise', 'məˈleɪz', 'n.', '(广泛存在的)难以捉摸的问题，无法描述的问题;莫名的不适(或不快、不满等);', 'There is no easy short-term solution to Britain\'s chronic economic malaise...', '短期内迅速治愈英国经济顽症的灵丹妙药并不存在。');
INSERT INTO `words` VALUES (4237, 'malay', 'məˈleɪ', 'n.', '马来人;马来语;', '...the Malay community.', '马来人社群');
INSERT INTO `words` VALUES (4238, 'malcontent', 'ˈmælkəntent', 'n.', '不满者;牢骚满腹的人;反叛者;', 'Five years ago a band of malcontents, mainly half-educated radicals, seized power.', '5年前一群主要由文化程度不高的激进分子构成的不满现状者夺取了政权。');
INSERT INTO `words` VALUES (4239, 'male', 'meɪl', 'adj.', '男性的;雄性的;男的;', 'Many women achievers appear to pose a threat to their male colleagues...', '许多事业有成的女性似乎对她们的男同事构成了一种威胁。');
INSERT INTO `words` VALUES (4240, 'malefactor', 'ˈmælɪfæktə(r)', 'n.', '犯罪分子;作恶的人;道德败坏的人;', '...a well-known criminal lawyer who had saved many a malefactor from going to jail.', '一位使许多犯罪分子避免锒铛入狱的著名刑事律师');
INSERT INTO `words` VALUES (4241, 'malevolent', 'məˈlevələnt', 'adj.', '有恶意的;有坏心肠的;', 'Her stare was malevolent, her mouth a thin line.', '她目光狠毒，唇紧抿。');
INSERT INTO `words` VALUES (4242, 'malfunction', 'ˌmælˈfʌŋkʃn', 'v.', '运转失常;失灵;出现故障;', 'The radiation can damage microprocessors and computer memories, causing them to malfunction...', '辐射会损坏微处理器和电脑内存，使之失灵。');
INSERT INTO `words` VALUES (4243, 'malice', 'ˈmælɪs', 'n.', '恶意;怨恨;', 'There was a strong current of malice in many of his portraits...', '他的许多肖像画中都透着一股强烈的怨恨。');
INSERT INTO `words` VALUES (4244, 'malicious', 'məˈlɪʃəs', 'adj.', '怀有恶意的;恶毒的;', 'That might merely have been malicious gossip...', '那也许仅仅是些不怀好意的流言蜚语罢了。');
INSERT INTO `words` VALUES (4245, 'malign', 'məˈlaɪn', 'v.', '(公开地)诽谤，毁谤，中伤;', 'We maligned him dreadfully when you come to think of it...', '回头想想，我们狠狠地中伤了他。');
INSERT INTO `words` VALUES (4246, 'malignancy', 'məˈlɪɡnənsi', 'n.', '恶性肿瘤;恶性;恶毒;', 'Tissue that is removed during the operation is checked for signs of malignancy.', '检查手术切片，以判断是否为恶性。');
INSERT INTO `words` VALUES (4247, 'malignant', 'məˈlɪɡnənt', 'adj.', '恶性的;恶意的;恶毒的;', 'She developed a malignant breast tumour.', '她长了一个恶性的乳房肿瘤。');
INSERT INTO `words` VALUES (4248, 'malinger', 'məˈlɪŋɡə(r)', 'v.', '装病(尤指为逃避工作);', 'She was told by her doctor that she was malingering.', '医生告诉她，她这是在装病。');
INSERT INTO `words` VALUES (4249, 'malingering', 'məˈlɪŋɡərɪŋ', 'v.', '装病(尤指为逃避工作);', 'She was told by her doctor that she was malingering.', '医生告诉她，她这是在装病。');
INSERT INTO `words` VALUES (4250, 'malleable', 'ˈmæliəbl', 'adj.', '可锻造的;可轧压的;易成型的;', 'The malleable mayor of New York was under his control...', '这位听话的纽约市市长听命于他。');
INSERT INTO `words` VALUES (4251, 'malnutrition', 'ˌmælnjuˈtrɪʃn', 'n.', '营养不良;', 'Infections are more likely in those suffering from malnutrition.', '营养不良的人更有可能被传染。');
INSERT INTO `words` VALUES (4252, 'malodorous', 'ˌmælˈəʊdərəs', 'adj.', '恶臭的;', '...tons of malodorous garbage bags.', '数吨散发着恶臭的袋装垃圾');
INSERT INTO `words` VALUES (4253, 'malpractice', 'ˌmælˈpræktɪs', 'n.', '渎职;玩忽职守;', 'There were only one or two serious allegations of malpractice.', '只有一两起有关营私舞弊的严重指控。');
INSERT INTO `words` VALUES (4254, 'malt', 'mɔːlt', 'n.', '麦芽;', 'German beer has traditionally been made from just four ingredients — hops, malt, yeast and water.', '德国啤酒传统上只用4种原料——啤酒花、麦芽、酵母和水。');
INSERT INTO `words` VALUES (4255, 'maltreat', 'ˌmælˈtriːt', 'v.', '虐待;', 'He said that he was not tortured or maltreated during his detention...', '他说自己在押期间并未遭受拷问和虐待。');
INSERT INTO `words` VALUES (4256, 'mammalian', 'mæˈmeɪliən', 'adj.', '哺乳类的;哺乳动物的;', 'The disease can spread from one mammalian species to another.', '这种疾病能够在不同的哺乳类物种之间传播。');
INSERT INTO `words` VALUES (4257, 'mammoth', 'ˈmæməθ', 'n.', '猛犸(象);毛象;', '...the mammoth task of relocating the library...', '图书馆搬迁的艰巨任务');
INSERT INTO `words` VALUES (4258, 'man', 'mæn', 'n.', '成年男子;男人;人类;', 'He had not expected the young man to reappear before evening...', '他没想到这位年轻男子会在黄昏前再次出现。');
INSERT INTO `words` VALUES (4259, 'manage', 'ˈmænɪdʒ', 'v.', '完成(困难的事);勉力完成;能解决(问题);', 'Within two years he was managing the store...', '不出两年他就开始管理这家店了。');
INSERT INTO `words` VALUES (4260, 'manageable', 'ˈmænɪdʒəbl', 'adj.', '可操纵的;可处理的;', 'He will now try to cut down the task to a manageable size...', '他现在要努力把任务的规模降至可控的范围。');
INSERT INTO `words` VALUES (4261, 'management', 'ˈmænɪdʒmənt', 'n.', '经营;管理;经营者;', 'The zoo needed better management rather than more money...', '这座动物园需要更好的管理，而不是更多的资金。');
INSERT INTO `words` VALUES (4262, 'manager', 'ˈmænɪdʒə(r)', 'n.', '(企业、店铺等的)经理，经营者，老板;(演员、作曲家等的)经理人，经纪人，个人经理;(运动队的)主教练;', 'The chef, staff and managers are all Chinese.', '厨师、工作人员和经理都是中国人。');
INSERT INTO `words` VALUES (4263, 'managerial', 'ˌmænəˈdʒɪəriəl', 'adj.', '经理的;管理的;', '...his managerial skills.', '他的经营技巧');
INSERT INTO `words` VALUES (4264, 'managing', 'ˈmænɪdʒɪŋ', 'v.', '完成(困难的事);勉力完成;能解决(问题);', 'Within two years he was managing the store...', '不出两年他就开始管理这家店了。');
INSERT INTO `words` VALUES (4265, 'mandarin', 'ˈmændərɪn', 'n.', '政界要员;(尤指)内务官员;(旧时)中国政府高级官吏;', '...Foreign Office mandarins.', '外交部高官');
INSERT INTO `words` VALUES (4266, 'mandate', 'ˈmændeɪt', 'n.', '(政府或组织等经选举而获得的)授权;(政府的)任期;委托书;', 'The President and his supporters are almost certain to read this vote as a mandate for continued economic reform...', '总统及其支持者几乎肯定地认为这次投票是对继续进行经济改革的授权。');
INSERT INTO `words` VALUES (4267, 'mandatory', 'ˈmændətəri', 'adj.', '强制的;法定的;义务的;', '...the mandatory retirement age of 65...', '65岁的法定退休年龄');
INSERT INTO `words` VALUES (4268, 'mangle', 'ˈmæŋɡl', 'v.', '压碎;撕烂;严重损坏;', 'His body was crushed and mangled beyond recognition.', '他的身体被压得血肉模糊，根本无法辨认。');
INSERT INTO `words` VALUES (4269, 'mango', 'ˈmæŋɡəʊ', 'n.', '芒果;', 'Peel, stone and dice the mango.', '将芒果削皮、去核、切丁。');
INSERT INTO `words` VALUES (4270, 'mangrove', 'ˈmæŋɡrəʊv', 'n.', '红树林植物(生长在淤泥或河边的热带树木，有支柱根暴露在空气中);', '...mangrove swamps.', '红树林沼泽');
INSERT INTO `words` VALUES (4271, 'mangy', 'ˈmeɪndʒi', 'adj.', '患疥癣的;污秽的;糟糕的;', '...mangy old dogs.', '老癞皮狗');
INSERT INTO `words` VALUES (4272, 'manhood', 'ˈmænhʊd', 'n.', '成年;成年时期;男儿气质;', 'They were failing lamentably to help their sons grow from boyhood to manhood.', '他们抱憾未能帮助儿子们顺利地长大成人。');
INSERT INTO `words` VALUES (4273, 'mania', 'ˈmeɪniə', 'n.', '(通常指许多人共有的)强烈的欲望，狂热，极大的热情;躁狂症;', 'It seemed to some observers that the English had a mania for travelling...', '在某些观察家看来，英国人对旅行似乎抱有一种狂热。');
INSERT INTO `words` VALUES (4274, 'maniac', 'ˈmeɪniæk', 'n.', '行为极其危险(或狂暴、愚蠢)的人;疯子;狂人;', 'The cabin looked as if a maniac had been let loose there.', '小屋看上去像是被疯子在那撒过野一样。');
INSERT INTO `words` VALUES (4275, 'maniacal', 'məˈnaɪəkl', 'adj.', '狂野的;粗暴的;', 'He was almost maniacal in his pursuit of sporting records...', '他近乎发疯般地追求着打破体育纪录。');
INSERT INTO `words` VALUES (4276, 'manifest', 'ˈmænɪfest', 'v.', '表明，清楚显示(尤指情感、态度或品质);显现;使人注意到;', '...the manifest failure of the policies...', '政策明显的失败之处');
INSERT INTO `words` VALUES (4277, 'manifestation', 'ˌmænɪfeˈsteɪʃn', 'n.', '显示;表明;表示;', 'Different animals in the colony had different manifestations of the disease...', '在这一群体里，这种疾病在不同的动物身上有着不同的表现。');
INSERT INTO `words` VALUES (4278, 'manifesto', 'ˌmænɪˈfestəʊ', 'n.', '宣言;', 'The Tories are currently drawing up their election manifesto...', '保守党人目前正在起草竞选宣言。');
INSERT INTO `words` VALUES (4279, 'manifold', 'ˈmænɪfəʊld', 'adj.', '多的;多种多样的;许多种类的;', 'Gaelic can be heard here in manifold forms...', '在这里可以听见各不相同的盖尔语。');
INSERT INTO `words` VALUES (4280, 'manipulate', 'məˈnɪpjuleɪt', 'v.', '(暗中)控制，操纵，影响;(熟练地)操作，使用;正骨;', 'He is a very difficult character. He manipulates people...', '他是个极难对付的角色，总是把人玩弄于股掌之间。');
INSERT INTO `words` VALUES (4281, 'manipulation', 'məˌnɪpjʊˈleɪʃən', 'n.', '操作；操作法；管理措施；操作处理；处理;操纵证券市场；操纵证券交易;变换;', 'He is a very difficult character. He manipulates people...', '他是个极难对付的角色，总是把人玩弄于股掌之间。');
INSERT INTO `words` VALUES (4282, 'manipulator', 'məˈnɪpjuleɪtə(r)', 'n.', '操纵者;控制者;', 'Jean Brodie is a manipulator. She cons everybody.', '琼·布罗迪是个善于摆布他人的高手，欺骗了所有人。');
INSERT INTO `words` VALUES (4283, 'mankind', 'mænˈkaɪnd', 'n.', '人类;', '...the evolution of mankind.', '人类的进化');
