INSERT INTO `words` VALUES (4285, 'manner', 'ˈmænə(r)', 'n.', '方式;方法;举止;', 'She smiled again in a friendly manner...', '她又友好地微笑了一下。');
INSERT INTO `words` VALUES (4286, 'manners', 'ˈmænəz', 'n.', '方式;方法;举止;', 'She smiled again in a friendly manner...', '她又友好地微笑了一下。');
INSERT INTO `words` VALUES (4287, 'manpower', 'ˈmænpaʊə(r)', 'n.', '劳动力;人手;人力;', '...the shortage of skilled manpower in the industry...', '该行业熟练劳动力的短缺');
INSERT INTO `words` VALUES (4288, 'mansion', 'ˈmænʃn', 'n.', '公馆;宅第;用于公寓楼名;', '...an eighteenth century mansion in Hampshire...', '汉普郡的一座18世纪的宅邸');
INSERT INTO `words` VALUES (4289, 'mantle', 'ˈmæntl', 'n.', '(可继承的)责任，职责，衣钵;覆盖层;(尤指旧时的)披风，斗篷;', 'Glasgow has broadened its appeal since taking on the mantle of European City of Culture in 1990...', '自从1990年荣获“欧洲文化之都”的光荣称号以来，格拉斯哥的魅力大增。');
INSERT INTO `words` VALUES (4290, 'manual', 'ˈmænjuəl', 'adj.', '用手的;手工的;体力的;', '...skilled manual workers...', '熟练的体力劳动者');
INSERT INTO `words` VALUES (4291, 'manually', 'ˈmænjʊəli', 'adv.', '用手地；手动地；人工地;', '...skilled manual workers...', '熟练的体力劳动者');
INSERT INTO `words` VALUES (4292, 'manufacture', 'ˌmænjuˈfæktʃə(r)', 'v.', '(用机器)大量生产，成批制造;编造;捏造;', 'They manufacture the class of plastics known as thermoplastic materials...', '他们生产这类被称为热塑材料的塑料制品。');
INSERT INTO `words` VALUES (4293, 'manufactured', 'ˌmænjuˈfæktʃəd', 'v.', '(用机器)大量生产，成批制造;编造;捏造;', 'They manufacture the class of plastics known as thermoplastic materials...', '他们生产这类被称为热塑材料的塑料制品。');
INSERT INTO `words` VALUES (4294, 'manufacturer', 'ˌmænjuˈfæktʃərə(r)', 'n.', '生产者;制造者;生产商;', '...the world\'s largest doll manufacturer.', '全球最大的玩偶生产商');
INSERT INTO `words` VALUES (4295, 'manufacturing', 'ˌmænjuˈfæktʃərɪŋ', 'n.', '制造业;', 'They manufacture the class of plastics known as thermoplastic materials...', '他们生产这类被称为热塑材料的塑料制品。');
INSERT INTO `words` VALUES (4296, 'manure', 'məˈnjʊə(r)', 'n.', '粪肥;肥料;', '...bags of manure.', '一袋袋的肥料');
INSERT INTO `words` VALUES (4297, 'manuscript', 'ˈmænjuskrɪpt', 'n.', '手稿;原稿;(印刷术发明以前书籍或文献的)手写本，手抄本;', 'He had seen a manuscript of the book...', '他见过这本书的手稿。');
INSERT INTO `words` VALUES (4298, 'many', 'ˈmeni', 'det./pron.', '许多(与复数名词及动词连用，尤用于否定句或正式用语，表示大量;也用于疑问句以询问数字大小，并可与as、so 和 too 连用);(与复数动词连用)大多数人;', 'I don\'t think many people would argue with that...', '我认为多数人不会对此有异议。');
INSERT INTO `words` VALUES (4299, 'map', 'mæp', 'n.', '地图;', 'He unfolded the map and set it on the floor...', '他打开地图，放在了地板上。');
INSERT INTO `words` VALUES (4300, 'mapping', 'ˈmæpɪŋ', 'v.', '绘制…的地图;了解信息，提供信息(尤指其编排或组织方式);', 'He unfolded the map and set it on the floor...', '他打开地图，放在了地板上。');
INSERT INTO `words` VALUES (4301, 'mar', 'mɑː(r)', 'v.', '破坏;毁坏;损毁;', 'A number of problems marred the smooth running of this event...', '许多问题影响了这件事的顺利进行。');
INSERT INTO `words` VALUES (4302, 'marathon', 'ˈmærəθən', 'n.', '马拉松赛跑(距离约42公里，合26英里);马拉松式的活动(或工作);', '...running in his first marathon...', '参加他生平第一次马拉松比赛');
INSERT INTO `words` VALUES (4303, 'marble', 'ˈmɑːbl', 'n.', '大理石;(玻璃)弹子;弹子游戏;', 'The house has a superb staircase made from oak and marble...', '这座房子的楼梯堪称一流，是由橡木与大理石制成的。');
INSERT INTO `words` VALUES (4304, 'march', 'mɑːtʃ', 'v.', '齐步走;行进;(坚定地向某地)前进，进发;', 'A Scottish battalion was marching down the street...', '苏格兰军队的一个营正沿街行进。');
INSERT INTO `words` VALUES (4305, 'margin', 'ˈmɑːdʒɪn', 'n.', '页边空白;白边;(获胜者在时间或票数上领先的)幅度，差额，差数;', 'They could end up with a 50-point winning margin...', '他们最终可能会以50分的优势获胜。');
INSERT INTO `words` VALUES (4306, 'marginal', 'ˈmɑːdʒɪnl', 'adj.', '小的;微不足道的;不重要的;', 'This is a marginal improvement on October...', '这是对10月份的一个小小改善。');
INSERT INTO `words` VALUES (4307, 'marital', 'ˈmærɪtl', 'adj.', '婚姻的;夫妻关系的;', 'Caroline was keen to make her marital home in London...', '卡罗琳渴望婚后把家安在伦敦。');
INSERT INTO `words` VALUES (4308, 'maritime', 'ˈmærɪtaɪm', 'adj.', '海的;海事的;海运的;', '...the largest maritime museum of its kind.', '同类中最大的海洋博物馆');
INSERT INTO `words` VALUES (4309, 'mark', 'mɑːk', 'v.', '做记号;做标记;留下痕迹;', 'The dogs are always rubbing against the wall and making dirty marks...', '那些狗老是往墙上蹭，留下了斑斑污迹。');
INSERT INTO `words` VALUES (4310, 'markdown', 'ˈmɑːkdaʊn', 'n.', '减价;降价;', 'Customers know that our stocktake sales offer genuine markdowns across the store.', '顾客们知道我们的清仓大甩卖是实实在在的全场降价。');
INSERT INTO `words` VALUES (4311, 'marked', 'mɑːkt', 'adj.', '显而易见的;明显的;显著的;', 'There has been a marked increase in crimes against property...', '侵财案件明显增加了。');
INSERT INTO `words` VALUES (4312, 'marker', 'ˈmɑːkə(r)', 'n.', '(表示方位的)标记，记号;标志;标识;', 'He put a marker in his book and followed her out.', '他在书里夹了一个书签后便随她出去了。');
INSERT INTO `words` VALUES (4313, 'market', 'ˈmɑːkɪt', 'n.', '集市;市场;商场;', 'He sold boots on a market stall.', '他在集市上摆摊卖靴子。');
INSERT INTO `words` VALUES (4314, 'marketable', 'ˈmɑːkɪtəbl', 'adj.', '容易出售的;畅销的;有销路的;', '...telling them how to turn their prize research projects into marketable products.', '告诉他们如何把他们的获奖研究项目转化为适销产品');
INSERT INTO `words` VALUES (4315, 'marketing', 'ˈmɑːkɪtɪŋ', 'n.', '促销;营销;销售活动;', '...expert advice on production and marketing.', '对生产和营销的专业建议');
INSERT INTO `words` VALUES (4316, 'marketplace', 'ˈmɑːkɪtpleɪs', 'n.', '市场竞争;集市;市场;', 'It\'s our hope that we will play an increasingly greater role in the marketplace and, therefore, supply more jobs.', '我们希望在市场中扮演越来越重要的角色，从而提供更多的就业机会。');
INSERT INTO `words` VALUES (4317, 'marking', 'ˈmɑːkɪŋ', 'n.', '(兽类、鸟类或树木的)斑纹，花纹，斑点;(刷在道路、车辆等上的)线条，颜色，形状;批改;', 'A plane with Danish markings was overflying his vessel...', '一架带有丹麦标记的飞机从他的船上空飞过。');
INSERT INTO `words` VALUES (4318, 'maroon', 'məˈruːn', 'adj.', '紫褐色的;褐红色的;', '...maroon velvet curtains.', '绛紫色的天鹅绒窗帘');
INSERT INTO `words` VALUES (4319, 'marriage', 'ˈmærɪdʒ', 'n.', '结婚;婚姻;婚姻生活;', 'In a good marriage, both husband and wife work hard to solve any problems that arise...', '美满的婚姻中，夫妻双方会共同努力去解决出现的任何问题。');
INSERT INTO `words` VALUES (4320, 'married', 'ˈmærid', 'adj.', '已婚的;婚姻的;结婚的;', 'We have been married for 14 years...', '我们已经结婚14年了。');
INSERT INTO `words` VALUES (4321, 'marry', 'ˈmæri', 'v.', '(和某人)结婚;嫁;娶;', 'I thought he would change after we got married...', '我原以为我们结婚后他会改变的。');
INSERT INTO `words` VALUES (4322, 'mars', 'mɑːz', 'n.', '火星;', 'A number of problems marred the smooth running of this event...', '许多问题影响了这件事的顺利进行。');
INSERT INTO `words` VALUES (4323, 'marshy', 'ˈmɑːʃi', 'adj.', '沼泽般的;湿软泥泞的;', '...the broad, marshy plain of the River Spey.', '斯佩河流域宽阔的沼泽平原');
INSERT INTO `words` VALUES (4324, 'mart', 'mɑːt', 'n.', '贸易场所;集市;', '...the flower mart.', '花卉市场');
INSERT INTO `words` VALUES (4325, 'martial', 'ˈmɑːʃl', 'adj.', '战争的;军事的;', 'The paper was actually twice banned under the martial regime.', '在军政府时期，该报纸其实曾经两度遭禁。');
INSERT INTO `words` VALUES (4326, 'martinet', 'ˌmɑːtɪˈnet', 'n.', '严格执行纪律的人;', 'He\'s a retired Lieutenant Colonel and a bit of a martinet.', '他是个退役中校，有点军人作风。');
INSERT INTO `words` VALUES (4327, 'martyr', 'ˈmɑːtə(r)', 'n.', '殉道者;烈士;乞怜者(向人诉苦以博取同情);', '...a glorious martyr to the cause of liberty.', '为自由事业光荣献身的烈士');
INSERT INTO `words` VALUES (4328, 'martyrdom', 'ˈmɑːtədəm', 'n.', '殉难;殉道;', '...the martyrdom of Bishop Feliciano...', '费利恰诺主教的殉教');
INSERT INTO `words` VALUES (4329, 'masculine', 'ˈmæskjəlɪn', 'adj.', '男子汉的;男人的;像男人的;', '...masculine characteristics like a husky voice and facial hair.', '沙哑的嗓音和胡子拉碴的面孔之类的男性特征');
INSERT INTO `words` VALUES (4330, 'masculinity', 'ˌmæskjuˈlɪnəti', 'n.', '男子气概;男性;阳性;', '...a project on the link between masculinity and violence.', '关于男性与暴力之间关系的研究项目');
INSERT INTO `words` VALUES (4331, 'mash', 'mæʃ', 'n.', '(煮软的)谷物饲料;麦芽浆(用于酿制啤酒);糊状食物;', 'Mash the bananas with a fork.', '用叉子把香蕉捣烂。');
INSERT INTO `words` VALUES (4332, 'mashed', 'mæʃt', 'v.', '捣烂，捣碎(食物);', 'Mash the bananas with a fork.', '用叉子把香蕉捣烂。');
INSERT INTO `words` VALUES (4333, 'mask', 'mɑːsk', 'n.', '面具;面罩;假面具;', 'The gunman, whose mask had slipped, fled.', '面罩掉落的持枪歹徒逃之夭夭了。');
INSERT INTO `words` VALUES (4334, 'masked', 'mɑːskt', 'adj.', '戴着面具(或面罩)的;', 'Masked youths threw stones and firebombs.', '蒙面年轻人投掷了石块和燃烧弹。');
INSERT INTO `words` VALUES (4335, 'masquerade', 'ˌmæskəˈreɪd', 'n.', '掩藏;掩饰;化装舞会;', 'He masqueraded as a doctor and fooled everyone.', '他冒充医生，骗过了所有人。');
INSERT INTO `words` VALUES (4336, 'mass', 'mæs', 'n.', '团;块;堆;', 'On his desk is a mass of books and papers.', '他的书桌上有大堆的书籍和文件。');
INSERT INTO `words` VALUES (4337, 'massacre', 'ˈmæsəkə(r)', 'n.', '屠杀;残杀;(运动或比赛中的)惨败;', 'Maria lost her 62-year-old mother in the massacre.', '在大屠杀中玛丽亚失去了62岁的母亲。');
INSERT INTO `words` VALUES (4338, 'massage', 'ˈmæsɑːʒ', 'n.', '按摩;', 'Alex asked me if I wanted a massage...', '亚力克斯问我是否想做按摩。');
INSERT INTO `words` VALUES (4339, 'massive', 'ˈmæsɪv', 'adj.', '巨大的;大而重的;结实的;', 'There was evidence of massive fraud.', '有证据表明是巨额欺诈。');
INSERT INTO `words` VALUES (4340, 'mass-produce', 'ˌmæs prəˈdjuːs', 'v.', '(用机器)批量生产，大量生产;', '...the invention of machinery to mass-produce footwear.', '批量制鞋机器的发明');
INSERT INTO `words` VALUES (4341, 'master', 'ˈmɑːstə(r)', 'n.', '(男)主人，雇主;主宰;主人;', 'My master ordered me not to deliver the message except in private...', '我的主人命令我务必私下里传递消息。');
INSERT INTO `words` VALUES (4342, 'mastering', 'ˈmɑːstərɪŋ', 'v.', '精通;掌握;控制(情绪);', 'My master ordered me not to deliver the message except in private...', '我的主人命令我务必私下里传递消息。');
INSERT INTO `words` VALUES (4343, 'mastermind', 'ˈmɑːstəmaɪnd', 'n.', '(极具才智的)决策者;主谋;出谋划策者;', 'The finance minister will continue to mastermind Poland\'s economic reform...', '财政部长将继续策划波兰的经济改革。');
INSERT INTO `words` VALUES (4344, 'masterpiece', 'ˈmɑːstəpiːs', 'n.', '代表作; 杰作;名著;', 'His book, I must add, is a masterpiece.', '我必须补充一点：他的书是一部杰作。');
INSERT INTO `words` VALUES (4345, 'mastery', 'ˈmɑːstəri', 'n.', '精通;熟练掌握;控制;', 'He doesn\'t have mastery of the basic rules of grammar...', '他还没有完全掌握基本的语法规则。');
INSERT INTO `words` VALUES (4346, 'masticate', 'ˈmæstɪkeɪt', 'v.', '咀嚼食物;', 'Her mouth was working, as if she was masticating some tasty titbit...', '她的嘴在动，好像在嚼什么好吃的。');
INSERT INTO `words` VALUES (4347, 'mat', 'mæt', 'n.', '小地毯;垫子;(体育运动用的)厚垫子;', 'The food is served on polished tables with mats.', '饭菜摆放在擦得铮亮、铺着衬垫的餐桌上。');
INSERT INTO `words` VALUES (4348, 'match', 'mætʃ', 'n.', '火柴;比赛;竞赛;', 'He was watching a football match...', '他正在观看一场足球比赛。');
INSERT INTO `words` VALUES (4349, 'matching', 'ˈmætʃɪŋ', 'adj.', '(颜色、形状、款式等)相同的，相称的，相配的;', 'She bought more fabric in the same design so she could make matching curtains and cushions.', '她又买了些图案相同的布，好做些配套的窗帘和垫子。');
INSERT INTO `words` VALUES (4350, 'matchless', 'ˈmætʃləs', 'adj.', '无可匹敌的;无比的;无双的;', 'A timeless comic actor—his simplicity and his apparent ease are matchless...', '作为喜剧界的常青树——他的简单淳朴与自然大方无人能及。');
INSERT INTO `words` VALUES (4351, 'mate', 'meɪt', 'n.', '朋友;伙伴;(男人之间常用)哥儿们，伙计，老兄;', 'He\'s off drinking with his mates...', '他跟哥儿们出去喝酒了。');
INSERT INTO `words` VALUES (4352, 'material', 'məˈtɪəriəl', 'n.', '布料;材料;原料;', '...electrons in a conducting material such as a metal.', '金属等导电材料中的电子');
INSERT INTO `words` VALUES (4353, 'materialism', 'məˈtɪəriəlɪzəm', 'n.', '实利主义;物质主义;唯物主义;', '...the rising consumer materialism in society at large.', '全社会范围内日渐蔓延的消费至上意识');
INSERT INTO `words` VALUES (4354, 'materially', 'məˈtɪərɪəli', 'adv.', '物质上地；实质上地;重要地；很大地；颇多的;', '...electrons in a conducting material such as a metal.', '金属等导电材料中的电子');
INSERT INTO `words` VALUES (4355, 'materials', 'məˈtɪərɪəlz', 'n.', '布料;材料;原料;', '...electrons in a conducting material such as a metal.', '金属等导电材料中的电子');
INSERT INTO `words` VALUES (4356, 'maternal', 'məˈtɜːnl', 'adj.', '母亲的;母亲般慈爱的;作为母亲的;', 'She had little maternal instinct...', '她缺乏母性。');
INSERT INTO `words` VALUES (4357, 'maternity', 'məˈtɜːnəti', 'n.', '母亲身份;怀孕;', 'Your job will be kept open for your return after maternity leave...', '你的工作会一直保留到你休完产假回来。');
INSERT INTO `words` VALUES (4358, 'matey', 'ˈmeɪti', 'adj.', '友好;亲热;套近乎;', 'Frost had displayed his usual matey charm as the pair sipped orange juice on national TV.', '两个人在国家电视台节目上小口喝着橙汁的时候，弗罗斯特施展了他一贯的亲和魅力。');
INSERT INTO `words` VALUES (4359, 'math', 'mæθ', 'n.', '同 mathematics;数学;', 'He studied math in college.', '他上大学时专业是数学。');
INSERT INTO `words` VALUES (4360, 'mathematical', 'ˌmæθəˈmætɪkl', 'adj.', '数学的;(具有)数学(头脑)的;运算能力强的;', '...mathematical calculations...', '数学计算');
INSERT INTO `words` VALUES (4361, 'mathematically', 'ˌmæθɪˈmætɪkəli', 'adv.', '算术地;', '...mathematical calculations...', '数学计算');
INSERT INTO `words` VALUES (4362, 'mathematician', 'ˌmæθəməˈtɪʃn', 'n.', '数学家;', 'The risks can be so complex that banks hire mathematicians to puzzle them out.', '由于风险过高，银行便聘请数学家来解决这些问题。');
INSERT INTO `words` VALUES (4363, 'mathematics', 'ˌmæθəˈmætɪks', 'n.', '数学;运算;计算;', 'Elizabeth studied mathematics and classics.', '伊丽莎白学过数学和古典文学。');
INSERT INTO `words` VALUES (4364, 'maths', 'mæθs', 'n.', '同 mathematics;数学;', 'He taught science and maths.', '他教科学和数学这两门课。');
INSERT INTO `words` VALUES (4365, 'matriculate', 'məˈtrɪkjuleɪt', 'v.', '(正式)被大学录取;注册入大学;(从学校)毕业;', 'I had to matriculate if I wanted to do a degree.', '如果我想攻读学位，必须先被录取入学。');
INSERT INTO `words` VALUES (4366, 'matrimony', 'ˈmætrɪməni', 'n.', '婚配;', '...the bonds of matrimony.', '婚姻的纽带');
INSERT INTO `words` VALUES (4367, 'matrix', 'ˈmeɪtrɪks', 'n.', '矩阵;(人或社会成长发展的)社会环境，政治局势;线路网;', '...the matrix of their culture.', '他们文明的摇篮');
INSERT INTO `words` VALUES (4368, 'matron', 'ˈmeɪtrən', 'n.', '(学校的)女舍监;女护士长(现常称为senior nursing officer);上年纪的已婚妇女;', 'The Matron at the nursing home expressed a wish to attend...', '养老院的护士长说她愿意参加。');
INSERT INTO `words` VALUES (4369, 'matter', 'ˈmætə(r)', 'n.', '课题;事情;问题;', 'It was clear that she wanted to discuss some private matter...', '很明显，她想谈些私事。');
INSERT INTO `words` VALUES (4370, 'matter-of-fact', 'ˌmætər əv ˈfækt', 'adj.', '不动感情的;据实的;', 'John was doing his best to give Francis the news in a matter-of-fact way...', '约翰尽量不露声色地把消息告诉弗朗西斯。');
INSERT INTO `words` VALUES (4371, 'maturation', 'ˌmætʃuˈreɪʃn', 'n.', '成熟过程;成熟;成年过程;', 'The period of maturation is determined by the cellar master.', '酿熟期的长短由地窖的主人决定。');
INSERT INTO `words` VALUES (4372, 'mature', 'məˈtʃʊə(r)', 'adj.', '明白事理的;成熟的;像成人似的;', 'You will learn what to expect as your child matures physically...', '在孩子身体发育成熟时，你就知道会发生什么事了。');
INSERT INTO `words` VALUES (4373, 'maturity', 'məˈtʃʊərəti', 'n.', '(思想行为、作品等)成熟;成熟;成年;', 'Humans experience a delayed maturity; we arrive at all stages of life later than other mammals.', '人类发育较缓——在生命的各个阶段，我们都晚于其他哺乳动物。');
INSERT INTO `words` VALUES (4374, 'maudlin', 'ˈmɔːdlɪn', 'adj.', '(尤指醉酒时)言语伤感的，感情脆弱的，自怜的;渲染感情的;', 'Jimmy turned maudlin after three drinks.', '3杯酒下肚，吉米愁上心头。');
INSERT INTO `words` VALUES (4375, 'maul', 'mɔːl', 'v.', '袭击;撕咬;粗手粗脚地摆弄;', 'He had been mauled by a bear...', '他被熊袭击过。');
INSERT INTO `words` VALUES (4376, 'mauve', 'məʊv', 'adj.', '淡紫色的;', 'It bears clusters of mauve flowers in early summer.', '初夏时节，它开出了一簇簇淡紫色的花。');
INSERT INTO `words` VALUES (4377, 'maverick', 'ˈmævərɪk', 'n.', '持不同意见者;独行其是者;言行与众不同者;', 'He was too much of a maverick ever to hold high office.', '他太特立独行，永远不可能做高官。');
INSERT INTO `words` VALUES (4378, 'mawkish', 'ˈmɔːkɪʃ', 'adj.', '无病呻吟的;自作多情的;多愁善感的;', 'A sordid, sentimental plot unwinds, with an inevitable mawkish ending.', '一段灰暗而感伤的情节慢慢展开，最后是一个不可避免的幼稚可笑的结局。');
INSERT INTO `words` VALUES (4379, 'maxim', 'ˈmæksɪm', 'n.', '格言;箴言;座右铭;', 'I believe in the maxim \'if it ain\'t broke, don\'t fix it\'.', '我相信这样一条箴言：“不破不修”。');
INSERT INTO `words` VALUES (4380, 'maximum', 'ˈmæksɪməm', 'adj.', '最高的;最多的;最大极限的;', 'Under planning law the maximum height for a fence or hedge is 2 metres...', '规划法中规定，围栏或树篱最高为2米。');
INSERT INTO `words` VALUES (4381, 'may', 'meɪ', 'modal', '(有可能但不肯定)也许，可能;(转折前所述情况属实)也许;(征求同意或表示允许)可以;', 'We may have some rain today...', '今天可能会下点雨。');
INSERT INTO `words` VALUES (4382, 'mayday', 'ˈmeɪdeɪ', 'n.', '呼救信号，（船或飞机所发的）国际无线电求救信号（等于无线电报中的SOS）;缩写词为MD;', 'He raced to pick up the lifejackets while his stepmother sent out a Mayday call.', '他的继母发出无线电求救信号的时候，他飞跑着去拿救生衣。');
INSERT INTO `words` VALUES (4383, 'maybe', 'ˈmeɪbi', 'adv.', '(不确定)大概，或许，可能;(提出建议)或许，也许;(赞同并补充信息)或许;', 'Maybe she is in love...', '她大概是恋爱了。');
INSERT INTO `words` VALUES (4384, 'mayday', 'ˈmeɪdeɪ', 'n.', '呼救信号，（船或飞机所发的）国际无线电求救信号（等于无线电报中的SOS）;缩写词为MD;', 'He raced to pick up the lifejackets while his stepmother sent out a Mayday call.', '他的继母发出无线电求救信号的时候，他飞跑着去拿救生衣。');
INSERT INTO `words` VALUES (4385, 'mayhem', 'ˈmeɪhem', 'n.', '骚乱;慌乱;', 'Their arrival caused mayhem as crowds of refugees rushed towards them.', '他们到达后大批难民一拥而上，引起一片混乱。');
INSERT INTO `words` VALUES (4386, 'maze', 'meɪz', 'n.', '迷宫;纷繁复杂的规则;复杂难懂的细节;', 'The palace has extensive gardens, a maze, and tennis courts.', '这座宫殿有几座大花园、一处迷宫和几个网球场。');
INSERT INTO `words` VALUES (4387, 'me', 'mi', 'pron.', '(I 的宾格)我;', 'I had to make important decisions that would affect me for the rest of my life...', '我不得不作出将影响我后半生的重大决定。');
INSERT INTO `words` VALUES (4388, 'meal', 'miːl', 'n.', '早(或午、晚)餐;一顿饭;一餐所吃的食物;', 'She sat next to him throughout the meal...', '用餐时她一直坐在他身旁。');
INSERT INTO `words` VALUES (4389, 'meals', 'miːlz', 'n.', '早(或午、晚)餐;一顿饭;一餐所吃的食物;', 'She sat next to him throughout the meal...', '用餐时她一直坐在他身旁。');
INSERT INTO `words` VALUES (4390, 'mealtime', 'ˈmiːltaɪm', 'n.', '就餐时间;进餐时间;', 'At mealtimes he would watch her eat...', '用餐时，他会看着她吃饭。');
INSERT INTO `words` VALUES (4391, 'meander', 'miˈændə(r)', 'v.', '蜿蜒而行;迂回曲折;漫步;', '...roads that meandered round the edges of the fields...', '田地周围蜿蜒的道路');
INSERT INTO `words` VALUES (4392, 'meaning', 'ˈmiːnɪŋ', 'n.', '(声音、文字、信号等传递的)意义，意思;(想要表达的)意义;真正重要性;', 'I hadn\'t a clue to the meaning of \'activism\'...', '我根本不明白 activism 的意思。');
INSERT INTO `words` VALUES (4393, 'meaningful', 'ˈmiːnɪŋfl', 'adj.', '严肃的;重要的;重大的;', 'She believes these talks will be the start of a constructive and meaningful dialogue...', '她认为这些谈话将是一次具有建设性的、有意义的对话的开端。');
INSERT INTO `words` VALUES (4394, 'meaningless', 'ˈmiːnɪŋləs', 'adj.', '毫无意义的;毫无目的的;无价值的;', 'The sentence \'kicked the ball the man\' is meaningless...', 'kicked the ball the man 这句话毫无意义。');
INSERT INTO `words` VALUES (4395, 'meanings', 'ˈmiːnɪŋz', 'n.', '(声音、文字、信号等传递的)意义，意思;(想要表达的)意义;真正重要性;', 'I hadn\'t a clue to the meaning of \'activism\'...', '我根本不明白 activism 的意思。');
INSERT INTO `words` VALUES (4396, 'means', 'miːnz', 'n.', '方式;方法;途径;', 'The move is a means to fight crime...', '采取这项举措是为了打击犯罪。');
INSERT INTO `words` VALUES (4397, 'meantime', 'ˈmiːntaɪm', 'adv.', '在…期间；同时;', 'Eventually your child will leave home to lead her own life as a fully independent adult, but in the meantime she relies on your support...', '你的孩子最终将长大成人，完全独立，离开家过自己的生活，不过在此期间她会靠你帮助。');
INSERT INTO `words` VALUES (4398, 'meanwhile', 'ˈmiːnwaɪl', 'adv.', '同时;与此同时;其间;', 'Brush the aubergines with oil, add salt and pepper, and bake till soft. Meanwhile, heat the remaining oil in a heavy pan...', '把油涂在茄子上，加盐和胡椒烤至松软。与此同时，在厚锅中加热剩余的油。');
INSERT INTO `words` VALUES (4399, 'measly', 'ˈmiːzli', 'adj.', '很小的;很少的;不足的;', 'The average British bathroom measures a measly 3.5 square metres.', '英国人的卫生间平均只有区区3.5平方米。');
INSERT INTO `words` VALUES (4400, 'measurable', 'ˈmeʒərəbl', 'adj.', '可测量的;可度量的;显著的;', 'Both leaders seemed to expect measurable progress.', '两位领导人似乎都期望取得显著的进展。');
INSERT INTO `words` VALUES (4401, 'measure', 'ˈmeʒə(r)', 'v.', '测量;度量;(指尺寸、长短、数量等)量度为;', 'I continued to measure his progress against the charts in the doctor\'s office...', '我继续比照医生办公室里的图表来判断他病情的发展。');
INSERT INTO `words` VALUES (4402, 'measured', 'ˈmeʒəd', 'adj.', '缓慢谨慎的;慎重的;克制的;', 'The men spoke in soft, measured tones...', '这些人说话的语气轻柔而平稳。');
INSERT INTO `words` VALUES (4403, 'measurement', 'ˈmeʒəmənt', 'n.', '测量;度量;(某物的)尺寸，长度，数量;', 'We took lots of measurements...', '我们得到了许多测量结果。');
INSERT INTO `words` VALUES (4404, 'measures', 'ˈmeʒəz', 'v.', '测量;度量;(指尺寸、长短、数量等)量度为;', 'I continued to measure his progress against the charts in the doctor\'s office...', '我继续比照医生办公室里的图表来判断他病情的发展。');
INSERT INTO `words` VALUES (4405, 'meat', 'miːt', 'n.', '肉类;(某种)食用肉;重要的部分;', 'Meat and fish are relatively expensive.', '肉和鱼相对来说比较贵。');
INSERT INTO `words` VALUES (4406, 'mechanic', 'məˈkænɪk', 'n.', '机械师;机械修理工;技工;', 'If you smell gas fumes or burning, take the car to your mechanic...', '如果你闻见汽油冒烟或燃烧的异味，应把车送修。');
INSERT INTO `words` VALUES (4407, 'mechanical', 'məˈkænɪkl', 'adj.', '机动的;机械驱动的;机械的;', '...a small mechanical device that taps out the numbers.', '能够输出数字的小型机械装置');
INSERT INTO `words` VALUES (4408, 'mechanically', 'mɪˈkænɪkəli', 'adv.', '用机械；机械地;', '...a small mechanical device that taps out the numbers.', '能够输出数字的小型机械装置');
INSERT INTO `words` VALUES (4409, 'mechanics', 'məˈkænɪks', 'n.', '机械师;机械修理工;技工;', 'If you smell gas fumes or burning, take the car to your mechanic...', '如果你闻见汽油冒烟或燃烧的异味，应把车送修。');
INSERT INTO `words` VALUES (4410, 'mechanism', 'ˈmekənɪzəm', 'n.', '机械装置;机件;方法;', '...the locking mechanism...', '锁定装置');
INSERT INTO `words` VALUES (4411, 'medallist', 'ˈmedəlɪst', 'n.', '(通常指体育比赛的)奖牌获得者;', '...the Olympic gold medallists.', '奥运会金牌得主');
INSERT INTO `words` VALUES (4412, 'meddle', 'ˈmedl', 'v.', '管闲事;干涉;干预;', 'Already some people are asking whether scientists have any right to meddle in such matters...', '已经有人在问科学家是否有权干预这类事情。');
INSERT INTO `words` VALUES (4413, 'meddlesome', 'ˈmedlsəm', 'adj.', '好管闲事的;爱干预的;', '...a meddlesome member of the public.', '一个爱管闲事的公众人士');
INSERT INTO `words` VALUES (4414, 'media', 'ˈmiːdiə', 'n.', '大众传播媒介，大众传播工具(指电视、广播、报纸、互联网);', 'It is hard work and not a glamorous job as portrayed by the media.', '这是份苦差，并非像媒体描绘的那般令人向往。');
INSERT INTO `words` VALUES (4415, 'mediate', 'ˈmiːdieɪt', 'v.', '调停;调解;斡旋;', 'My mom was the one who mediated between Zelda and her mom...', '我妈妈充当了泽尔达和她妈妈之间的调解人。');
INSERT INTO `words` VALUES (4416, 'mediation', 'ˌmiːdiˈeɪʃn', 'n.', '仲裁；调停，居中调停；调解;', 'My mom was the one who mediated between Zelda and her mom...', '我妈妈充当了泽尔达和她妈妈之间的调解人。');
INSERT INTO `words` VALUES (4417, 'mediator', 'ˈmiːdieɪtə(r)', 'n.', '调停者;斡旋者;解决纷争的人(或机构);', 'My mom was the one who mediated between Zelda and her mom...', '我妈妈充当了泽尔达和她妈妈之间的调解人。');
INSERT INTO `words` VALUES (4418, 'medical', 'ˈmedɪkl', 'adj.', '伤病的;疾病的;医疗的;', 'Several police officers received medical treatment for cuts and bruises.', '几名警官因割伤和擦伤而接受了医治。');
INSERT INTO `words` VALUES (4419, 'medication', 'ˌmedɪˈkeɪʃn', 'n.', '药;药物;', 'She stopped taking the prescribed medications...', '她不再服用医生开的药。');
INSERT INTO `words` VALUES (4420, 'medicinal', 'məˈdɪsɪnl', 'adj.', '有疗效的;药用的;药的;', '...medicinal plants.', '药用植物');
INSERT INTO `words` VALUES (4421, 'medicine', 'ˈmedsn', 'n.', '医学;药;(尤指)药水;', 'He pursued a career in medicine...', '他从事医学。');
INSERT INTO `words` VALUES (4422, 'mediocre', 'ˌmiːdiˈəʊkə(r)', 'adj.', '平庸的;普通的;平常的;', 'His school record was mediocre.', '他在学校成绩平平。');
INSERT INTO `words` VALUES (4423, 'mediocrity', 'ˌmiːdiˈɒkrəti', 'n.', '平庸;普通;平常;', '...the mediocrity of most contemporary literature...', '大多数当代文学作品的平庸');
INSERT INTO `words` VALUES (4424, 'meditate', 'ˈmedɪteɪt', 'v.', '冥想;沉思;暗自策划;', 'He meditated on the problem...', '他仔细斟酌着这个问题。');
INSERT INTO `words` VALUES (4425, 'meditating', 'ˈmedɪteɪtɪŋ', 'v.', '冥想;沉思;暗自策划;', 'He meditated on the problem...', '他仔细斟酌着这个问题。');
INSERT INTO `words` VALUES (4426, 'meditation', 'ˌmedɪˈteɪʃn', 'n.', '冥想;沉思;深思;', 'Many busy executives have begun to practice yoga and meditation.', '许多忙碌的主管开始练习瑜伽和冥想。');
INSERT INTO `words` VALUES (4427, 'meditative', 'ˈmedɪtətɪv', 'adj.', '深思的;陷入沉思的;', 'Music can induce a meditative state in the listener...', '音乐能够引导倾听者沉思。');
INSERT INTO `words` VALUES (4428, 'medley', 'ˈmedli', 'n.', '混成曲(多首声乐曲或器乐曲串联在一起);混杂物;混合物;', '...a medley of traditional songs.', '传统歌曲集锦');
INSERT INTO `words` VALUES (4429, 'meek', 'miːk', 'adj.', '温顺的;谦恭的;驯服的;', 'He was a meek, mild-mannered fellow.', '他是一个温顺随和的人。');
INSERT INTO `words` VALUES (4430, 'meekly', '', 'adv.', '温顺地;逆来顺受地;', 'He was a meek, mild-mannered fellow.', '他是一个温顺随和的人。');
INSERT INTO `words` VALUES (4431, 'meet', 'miːt', 'v.', '相遇;相逢;遇见;', 'I have just met the man I want to spend the rest of my life with...', '我刚刚遇到了我想与之共度余生的男人。');
INSERT INTO `words` VALUES (4432, 'meeting', 'ˈmiːtɪŋ', 'n.', '会议;集会;(统称)与会者;', 'Can we have a meeting to discuss that?', '我们可以开会来讨论那件事吗？');
INSERT INTO `words` VALUES (4433, 'megawatt', 'ˈmeɡəwɒt', 'n.', '兆瓦，百万瓦特(电能计量单位);', 'The project is designed to generate around 30 megawatts of power for the national grid.', '该工程计划为国家电网发电约30兆瓦。');
INSERT INTO `words` VALUES (4434, 'melancholy', 'ˈmelənkəli', 'n.', '忧郁;伤悲;', '...a painter of haunting melancholy canvases...', '一位画作中饱含挥之不去的忧伤情绪的画家');
INSERT INTO `words` VALUES (4435, 'melee', 'ˈmeleɪ', 'n.', '混乱;混乱的人群;', 'A policeman was killed and scores of people were injured in the melee.', '混战中一名警察被打死，许多人受伤。');
INSERT INTO `words` VALUES (4436, 'mellifluous', 'meˈlɪfluəs', 'adj.', '甜美流畅的;悦耳动听的;', 'I grew up around people who had wonderful, mellifluous voices...', '从小到大，生活在我周围的人都有着悦耳动听的嗓音。');
INSERT INTO `words` VALUES (4437, 'mellow', 'ˈmeləʊ', 'adj.', '柔和丰富的;醇香的;甘美的;', '...a classic Queen Anne house of mellow red brick with a white portico.', '一座典型的安妮女王时代的房屋，有着色彩柔和的红砖和白色的门廊');
INSERT INTO `words` VALUES (4438, 'melodic', 'məˈlɒdɪk', 'adj.', '主旋律的;旋律的;', '...Schubert\'s effortless gift for melodic invention.', '舒伯特驾轻就熟的谱曲天分');
INSERT INTO `words` VALUES (4439, 'melodious', 'məˈləʊdiəs', 'adj.', '悦耳的;优美动听的;像音乐的;', 'She spoke in a quietly melodious voice.', '她说话轻声细语，嗓音甜美。');
INSERT INTO `words` VALUES (4440, 'melodramatic', 'ˌmelədrəˈmætɪk', 'adj.', '情节剧式的;夸大的;耸人听闻的;', '\'Don\'t you think you\'re being rather melodramatic?\' Jane asked...', '简问：“你难道不觉得自己有些太夸张了吗？”');
INSERT INTO `words` VALUES (4441, 'melt', 'melt', 'v.', '(使)熔化，融化;(使)软化，变得温柔;', 'The snow had melted, but the lake was still frozen solid...', '雪已经融化了，但是湖面上仍然冻得结结实实的。');
INSERT INTO `words` VALUES (4442, 'melting', 'ˈmeltɪŋ', 'adj.', '感人的;柔情似水的;令人爱怜的;', 'The snow had melted, but the lake was still frozen solid...', '雪已经融化了，但是湖面上仍然冻得结结实实的。');
INSERT INTO `words` VALUES (4443, 'member', 'ˈmembə(r)', 'n.', '成员;分子;会员;', 'He refused to name the members of staff involved...', '他拒绝说出所涉及员工的姓名。');
INSERT INTO `words` VALUES (4444, 'membership', 'ˈmembəʃɪp', 'n.', '会员资格;成员资格;(统称)会员，成员;', 'The country has also been granted membership of the World Trade Organisation...', '这个国家也被准予加入世界贸易组织。');
INSERT INTO `words` VALUES (4445, 'memento', 'məˈmentəʊ', 'n.', '纪念品;', 'More anglers are taking cameras when they go fishing to provide a memento of catches.', '更多的垂钓者去钓鱼的时候带上了照相机，以便钓上鱼后留个纪念。');
INSERT INTO `words` VALUES (4446, 'memoir', 'ˈmemwɑː(r)', 'n.', '(尤指名人的)回忆录;自传;传说;', 'He has just published a memoir in honour of his captain.', '他刚刚出了一本传记来纪念他的队长。');
INSERT INTO `words` VALUES (4447, 'memorable', 'ˈmemərəbl', 'adj.', '值得纪念的;难忘的;', '...the perfect setting for a nostalgic memorable day...', '度过令人难忘的怀旧一日的绝佳环境');
INSERT INTO `words` VALUES (4448, 'memorandum', 'ˌmeməˈrændəm', 'n.', '协议备忘录;建议书;报告;', '...a memorandum from the Ministry of Defence on its role...', '国防部发出的关于其职责的备忘录');
INSERT INTO `words` VALUES (4449, 'memorial', 'məˈmɔːriəl', 'n.', '纪念碑(或像等);纪念物;纪念品;', 'Building a memorial to Columbus has been his lifelong dream...', '为哥伦布建一座纪念碑是他一生的梦想。');
INSERT INTO `words` VALUES (4450, 'memory', 'ˈmeməri', 'n.', '记忆力;记性;记忆所及的时期;', 'All the details of the meeting are fresh in my memory...', '我对会议的所有细节都记忆犹新。');
INSERT INTO `words` VALUES (4451, 'menace', 'ˈmenəs', 'n.', '威胁;危险的人(或物);令人恐怖的氛围;', 'In my view you are a menace to the public.', '在我看来，你对公众是个祸害。');
INSERT INTO `words` VALUES (4452, 'menacing', 'ˈmenəsɪŋ', 'adj.', '威胁的;恐吓的;危险的;', 'The strong dark eyebrows give his face an oddly menacing look...', '两道浓黑的眉毛使他的脸看起来异常凶狠。');
INSERT INTO `words` VALUES (4453, 'mend', 'mend', 'v.', '修理;修补;缝补;', 'They took a long time to mend the roof...', '他们花了很长时间修缮屋顶。');
INSERT INTO `words` VALUES (4454, 'mendacity', 'menˈdæsəti', 'n.', '撒谎;捏造;说瞎话;', '...an astonishing display of cowardice and mendacity.', '懦弱和撒谎的表现令人吃惊');
INSERT INTO `words` VALUES (4455, 'menial', 'ˈmiːniəl', 'adj.', '不需技巧的;枯燥的;报酬低的;', '...low paid menial jobs, such as cleaning and domestic work.', '诸如打扫卫生和干家务之类工钱少又低贱的工作');
INSERT INTO `words` VALUES (4456, 'menstrual', 'ˈmenstruəl', 'adj.', '月经的;', '...the menstrual cycle.', '月经周期');
INSERT INTO `words` VALUES (4457, 'mental', 'ˈmentl', 'adj.', '思想的;精神的;思考的;', '...the mental development of children.', '儿童的智力发展');
INSERT INTO `words` VALUES (4458, 'mentality', 'menˈtæləti', 'n.', '心态;思想状况;思想方法;', '...a criminal mentality...', '犯罪心态');
INSERT INTO `words` VALUES (4459, 'mentally', 'ˈmentəli', 'adv.', '精神上;智力上;思想上;', '...the mental development of children.', '儿童的智力发展');
INSERT INTO `words` VALUES (4460, 'mention', 'ˈmenʃn', 'v.', '提到;写到;说到;', 'She did not mention her mother\'s absence...', '她并没有说起她母亲不在场的事。');
INSERT INTO `words` VALUES (4461, 'menu', 'ˈmenjuː', 'n.', '菜单;(功能选择)菜单，选单;', 'A waiter offered him the menu...', '一个服务生给他送上了菜单。');
INSERT INTO `words` VALUES (4462, 'mercantile', 'ˈmɜːkəntaɪl', 'adj.', '商业的;贸易的;', '...the emergence of a new mercantile class.', '新兴商业阶层的出现');
INSERT INTO `words` VALUES (4463, 'merchandiser', 'ˈmɜːtʃəndaɪzə', 'n.', '商人;商家;', 'In 1979, Liquor Barn thrived as a discount merchandiser.', '1979年，Liquor Barn 烈酒折扣店的生意十分红火。');
INSERT INTO `words` VALUES (4464, 'merchandising', 'ˈmɜːtʃəndaɪzɪŋ', 'n.', '推销;展销;(根据受欢迎的电影、人物或事件而生产的)衍生产品;', 'We are selling the full range of World Cup merchandising...', '我们出售世界杯的所有纪念商品。');
INSERT INTO `words` VALUES (4465, 'merchant', 'ˈmɜːtʃənt', 'n.', '商人;批发商;(尤指)进出口批发商;', 'Any knowledgeable wine merchant would be able to advise you.', '任何一个懂行的葡萄酒商人都可以给你提出建议。');
INSERT INTO `words` VALUES (4466, 'merciful', 'ˈmɜːsɪfl', 'adj.', '宽大的;仁慈的;慈悲的;', '...a merciful God...', '仁慈的上帝');
INSERT INTO `words` VALUES (4467, 'mercifully', 'ˈmɜːsɪfəli', 'adv.', '(不幸中)幸运地;仁慈地;宽大地;', 'Mercifully, a friend came to the rescue...', '幸好一个朋友赶来解了围。');
INSERT INTO `words` VALUES (4468, 'merciless', 'ˈmɜːsɪləs', 'adj.', '毫不怜悯的;无情的;残忍的;', 'Whistler, as always, was merciless.', '和以往一样，惠斯勒还是那么冷酷无情。');
INSERT INTO `words` VALUES (4469, 'mercurial', 'mɜːˈkjʊəriəl', 'adj.', '多变的;变幻莫测的;活泼的;', '...his mercurial temperament.', '他那反复无常的脾气');
INSERT INTO `words` VALUES (4470, 'mercy', 'ˈmɜːsi', 'n.', '仁慈;宽恕;幸运;', 'Neither side took prisoners or showed any mercy...', '双方都没有抓俘虏，也没有表现出丝毫的心慈手软。');
INSERT INTO `words` VALUES (4471, 'mere', 'mɪə(r)', 'adj.', '仅仅的;只不过;只凭…就足以;', '...successful exhibitions which go beyond mere success...', '不仅仅是成功而是大获成功的展览');
INSERT INTO `words` VALUES (4472, 'merely', 'ˈmɪəli', 'adv.', '仅仅;只不过;', 'Michael is now merely a good friend...', '迈克尔现在仅仅是个不错的朋友而已。');
INSERT INTO `words` VALUES (4473, 'meretricious', 'ˌmerəˈtrɪʃəs', 'adj.', '华而不实的;虚有其表的;金玉其外的;', '...vulgar, meretricious and shabby souvenirs.', '俗气花哨、质量低劣的纪念品');
INSERT INTO `words` VALUES (4474, 'merge', 'mɜːdʒ', 'v.', '(使)合并，结合，并入;相融;融入;', 'My life merged with his...', '我和他的生活合而为一。');
INSERT INTO `words` VALUES (4475, 'merger', 'ˈmɜːdʒə(r)', 'n.', '(机构或企业的)合并，归并;', '...a merger between two of Britain\'s biggest trades unions.', '英国两个最大的工会的合并');
INSERT INTO `words` VALUES (4476, 'merit', 'ˈmerɪt', 'n.', '优点;美德;价值;', 'The  argument seemed to have considerable merit...', '这个论点似乎颇有价值。');
INSERT INTO `words` VALUES (4477, 'merry', 'ˈmeri', 'adj.', '愉快的;高兴的;(圣诞节祝贺语)圣诞快乐;', 'He was much loved for his merry nature...', '他快乐的天性很是招人喜欢。');
INSERT INTO `words` VALUES (4478, 'mesh', 'meʃ', 'n.', '网状物;网状织物;陷阱;', 'The ground-floor windows are obscured by wire mesh.', '一楼的窗户装有防盗网。');
INSERT INTO `words` VALUES (4479, 'mess', 'mes', 'n.', '肮脏;杂乱;不整洁;', 'The house is a mess...', '屋里一片狼藉。');
INSERT INTO `words` VALUES (4480, 'message', 'ˈmesɪdʒ', 'n.', '(书面或口头的)信息，消息，音信;电邮(或手机)信息;(书籍、演讲等的)要旨，要点，教训;', 'I got a message you were trying to reach me...', '我收到一条留言，说你想跟我联系。');
INSERT INTO `words` VALUES (4481, 'messenger', 'ˈmesɪndʒə(r)', 'n.', '送信人;通信员;邮递员;', 'There will be a messenger at the Airport to collect the photographs from our courier...', '机场会有一名信使从我们的递送员手中取走照片。');
INSERT INTO `words` VALUES (4482, 'metabolic', 'ˌmetəˈbɒlɪk', 'adj.', '新陈代谢的;代谢的;', '...people who have inherited a low metabolic rate.', '因遗传而新陈代谢速度慢的人');
INSERT INTO `words` VALUES (4483, 'metal', 'ˈmetl', 'n.', '金属;', '...pieces of furniture in wood, metal and glass...', '用木料、金属和玻璃制成的一件件家具');
INSERT INTO `words` VALUES (4484, 'metallic', 'məˈtælɪk', 'adj.', '金属般的;有金属味(或声音)的;金属制的;', 'There was a metallic click and the gates swung open...', '随着一声金属碰击的咔嗒声，大门打开了。');
INSERT INTO `words` VALUES (4485, 'metaphor', 'ˈmetəfə(r)', 'n.', '暗喻;隐喻;', '...the avoidance of \'violent expressions and metaphors\' like \'kill two birds with one stone\'.', '避免使用“有暴力意味的表达和隐喻”，例如“一石二鸟”');
INSERT INTO `words` VALUES (4486, 'meteoric', 'ˌmiːtiˈɒrɪk', 'adj.', '迅速成功的;流星的;', '...his meteoric rise to fame...', '他的一举成名');
INSERT INTO `words` VALUES (4487, 'meter', 'ˈmiːtə(r)', 'n.', '(用于测量电、煤气、水等，以及时间和距离的)计量器，计量表;计;仪;', 'He was there to read the electricity meter...', '他到那里抄电表。');
INSERT INTO `words` VALUES (4488, 'method', 'ˈmeθəd', 'n.', '方法;办法;措施;', 'The pill is the most efficient method of birth control.', '服用避孕药是最有效的避孕方法。');
INSERT INTO `words` VALUES (4489, 'methodology', 'ˌmeθəˈdɒlədʒi', 'n.', '(从事某一活动的)方法，原则;', 'Teaching methodologies vary according to the topic...', '题目不同，教学法也不一样。');
INSERT INTO `words` VALUES (4490, 'meticulous', 'məˈtɪkjələs', 'adj.', '细心的;小心翼翼的;', 'He was so meticulous about everything...', '他对所有事情都一丝不苟。');
INSERT INTO `words` VALUES (4491, 'meticulously', '', 'adv.', '极仔细地；一丝不苟地;', 'He was so meticulous about everything...', '他对所有事情都一丝不苟。');
INSERT INTO `words` VALUES (4492, 'metric', 'ˈmetrɪk', 'adj.', '米制的;公制的;按公制制作的;', 'Around 180,000 metric tons of food aid is required...', '需要大约18万公吨的食品援助。');
INSERT INTO `words` VALUES (4493, 'metropolitan', 'ˌmetrəˈpɒlɪtən', 'adj.', '大城市的;大都会的;本土的;', '...the metropolitan district of Miami.', '迈阿密的都市区');
INSERT INTO `words` VALUES (4494, 'mexican', 'ˈmeksɪkən', 'adj.', '墨西哥的;', '...the Mexican border town of Tijuana.', '墨西哥边境的蒂华纳小镇');
INSERT INTO `words` VALUES (4495, 'microcosm', 'ˈmaɪkrəʊkɒzəm', 'n.', '缩影;具体而微者;', 'Kitchell says the city was a microcosm of all American culture during the \'60s...', '基切尔说这个城市曾经是20世纪60年代整个美国文化的缩影。');
INSERT INTO `words` VALUES (4496, 'microeconomics', 'ˌmaɪkrəʊiːkəˈnɒmɪks', 'n.', '微观经济学;', 'He has 250 students in his microeconomics module.', '他的微观经济学授课单元有250名学生。');
INSERT INTO `words` VALUES (4497, 'microscopic', 'ˌmaɪkrəˈskɒpɪk', 'adj.', '极小的;微小的;需用显微镜观察的;', '...microscopic fibres of protein.', '极为微细的蛋白质纤维');
INSERT INTO `words` VALUES (4498, 'mid', 'mɪd', 'adj.', '中间的；中央的（用于in, from, to等后或构成复合词）;', '...the mid-eighteenth century...', '18世纪中叶');
INSERT INTO `words` VALUES (4499, 'midday', 'ˌmɪdˈdeɪ', 'n.', '中午;正午;', 'At midday everyone would go down to Reg\'s Cafe...', '中午，大家都会下楼去雷吉餐馆。');
INSERT INTO `words` VALUES (4500, 'middle', 'ˈmɪdl', 'n.', '中间;中部;中央;', 'Howard stood in the middle of the room sipping a cup of coffee...', '霍华德站在房间中央，小口抿着一杯咖啡。');
INSERT INTO `words` VALUES (4501, 'middleman', 'ˈmɪdlmæn', 'n.', '中间商;经销商;经纪人;', 'Why don\'t they cut out the middleman and let us do it ourselves?', '他们为什么不甩开中间商，让我们自己干呢？');
INSERT INTO `words` VALUES (4502, 'middling', 'ˈmɪdlɪŋ', 'adj.', '(大小、品质、地位等)中间的，普通的，中等的;', 'The Beatles enjoyed only middling success until 1963.', '直到1963年，甲壳虫乐队还只是半红不紫。');
INSERT INTO `words` VALUES (4503, 'midnight', 'ˈmɪdnaɪt', 'n.', '午夜;子夜;', 'It was well after midnight by the time Anne returned to her apartment...', '安妮回到寓所的时候早就过了午夜。');
INSERT INTO `words` VALUES (4504, 'midst', 'mɪdst', 'n.', '中部;中间;', 'Congress is in the midst of rewriting the nation\'s banking laws...', '国会正在修订国家银行法规。');
INSERT INTO `words` VALUES (4505, 'mighty', 'ˈmaɪti', 'adj.', '强而有力的;巨大的;非凡的;', 'There was a flash and a mighty bang.', '一道闪光和一声巨响接踵而至。');
INSERT INTO `words` VALUES (4506, 'migrant', 'ˈmaɪɡrənt', 'n.', '(为工作)移居者;移民;候鸟;', 'The government divides asylum-seekers into economic migrants and genuine refugees.', '政府把寻求避难者划分为经济移民与真正的难民。');
INSERT INTO `words` VALUES (4507, 'migrate', 'maɪˈɡreɪt', 'v.', '(随季节变化)迁徙;移居;迁移;', 'People migrate to cities like Jakarta in search of work...', '人们为找工作而迁移到雅加达这类城市里。');
INSERT INTO `words` VALUES (4508, 'migration', 'maɪˈɡreɪʃn', 'n.', '迁移;移居;迁徙;', 'People migrate to cities like Jakarta in search of work...', '人们为找工作而迁移到雅加达这类城市里。');
INSERT INTO `words` VALUES (4509, 'mild', 'maɪld', 'adj.', '温和的;和善的;不严厉的;', 'Teddy turned to Mona with a look of mild confusion...', '泰迪表情略带困惑地向莫娜求助。');
INSERT INTO `words` VALUES (4510, 'mildew', 'ˈmɪldjuː', 'n.', '霉;霉菌;', 'The room smelled of mildew.', '房间里有股霉味。');
INSERT INTO `words` VALUES (4511, 'mile', 'maɪl', 'n.', '英里(= 1 609米或1 760码);大面积;长距离;', 'They drove 600 miles across the desert...', '他们驱车600英里穿越沙漠。');
INSERT INTO `words` VALUES (4512, 'mileage', 'ˈmaɪlɪdʒ', 'n.', '英里里程;(车辆使用某定量燃料可行驶的)英里数;好处;', 'Most of their mileage is in and around town.', '他们开车大多数时候是在城里和城市周边转悠。');
INSERT INTO `words` VALUES (4513, 'miles', 'maɪlz', 'n.', '英里(= 1 609米或1 760码);大面积;长距离;', 'They drove 600 miles across the desert...', '他们驱车600英里穿越沙漠。');
INSERT INTO `words` VALUES (4514, 'milestone', 'ˈmaɪlstəʊn', 'n.', '重要事件;重要阶段;转折点;', 'He said the launch of the party represented a milestone in Zambian history...', '他说该党的创立是赞比亚历史上的一个里程碑。');
INSERT INTO `words` VALUES (4515, 'militant', 'ˈmɪlɪtənt', 'adj.', '动武的;好战的;有战斗性的;', 'Militant mineworkers in the Ukraine have voted for a one-day stoppage next month.', '乌克兰激进的矿工们已经投票决定下个月罢工一天。');
INSERT INTO `words` VALUES (4516, 'military', 'ˈmɪlətri', 'adj.', '军事的;军队的;武装的;', 'Military action may become necessary...', '也许有必要采取军事行动。');
INSERT INTO `words` VALUES (4517, 'militia', 'məˈlɪʃə', 'n.', '民兵组织;国民卫队;', 'The troops will not attempt to disarm the warring militias.', '部队并不打算解除战斗中的民兵武装。');
INSERT INTO `words` VALUES (4518, 'milk', 'mɪlk', 'n.', '(牛或羊等的)奶;(人或哺乳动物的)奶，乳汁;(椰子等植物的)白色汁液，乳液;', 'He popped out to buy a pint of milk.', '他匆匆出门去买一品脱牛奶。');
INSERT INTO `words` VALUES (4519, 'milky', 'ˈmɪlki', 'adj.', '奶制的;含奶多的;奶的;', 'A milky mist filled the valley.', '白蒙蒙的薄雾弥漫山谷。');
INSERT INTO `words` VALUES (4520, 'millionaire', 'ˌmɪljəˈneə(r)', 'n.', '百万富翁;大富豪;', 'By the time he died, he was a millionaire.', '到他去世的时候，他已经是一位百万富翁了。');
INSERT INTO `words` VALUES (4521, 'mimetic', 'mɪˈmetɪk', 'adj.', '模仿的;拟态的;', 'Both realism and naturalism are mimetic systems or practices of representation.', '现实主义和自然主义都是一种模仿体系或表现性的做法。');
INSERT INTO `words` VALUES (4522, 'mimic', 'ˈmɪmɪk', 'v.', '模仿(人的言行举止);(尤指)做滑稽模仿;(外表或行为举止)像，似;', 'He could  mimic anybody...', '他可以模仿任何人。');
INSERT INTO `words` VALUES (4523, 'mince', 'mɪns', 'v.', '用绞肉机绞(食物，尤指肉);装腔作势地小步快走;', 'Brown the mince in a frying pan.', '在煎锅里把肉末煎至棕色。');
INSERT INTO `words` VALUES (4524, 'minded', 'ˈmaɪndɪd', 'adj.', '思维(或态度、性格)…的;对…有兴趣(或能理解);有…头脑;', 'The Home Office said at that time that it was minded to reject his application for political asylum...', '那时候内政部称其准备驳回他的政治避难申请。');
INSERT INTO `words` VALUES (4525, 'mindful', 'ˈmaɪndfl', 'adj.', '记着;想着;考虑到;', 'We must be mindful of the consequences of selfishness...', '我们必须考虑自私自利的后果。');
INSERT INTO `words` VALUES (4526, 'mindless', 'ˈmaɪndləs', 'adj.', '没头脑的;无谓的;盲目的;', '...a plot that mixes blackmail, extortion and mindless violence.', '集合了敲诈勒索和盲目暴力的故事情节');
INSERT INTO `words` VALUES (4527, 'mindset', 'ˈmaɪndset', 'n.', '观念模式;思维倾向;', 'The greatest challenge for the Americans is understanding the mindset of Eastern Europeans...', '对于美国人来说，最大的挑战在于理解东欧人的思维方式。');
INSERT INTO `words` VALUES (4528, 'mingle', 'ˈmɪŋɡl', 'v.', '(使)与…结合;使混合;使联结;', 'Now the cheers and applause mingled in a single sustained roar...', '现在，欢呼声和掌声汇成了一片持续的喧闹声。');
INSERT INTO `words` VALUES (4529, 'mini-', 'ˈmɪni', 'comb.', '小的;短的;', 'Provisions may be purchased from the mini-market...', '或许能在小型超市里买到日常用品。');
INSERT INTO `words` VALUES (4530, 'miniature', 'ˈmɪnətʃə(r)', 'adj.', '很小的;微型的;小型的;', '...miniature roses...', '小玫瑰花');
INSERT INTO `words` VALUES (4531, 'minibus', 'ˈmɪnibʌs', 'n.', '小型公共汽车;中巴;', 'He was then taken by minibus to the military base.', '他随后被一辆中巴车带到了军事基地。');
INSERT INTO `words` VALUES (4532, 'minicab', 'ˈmɪnikæb', 'n.', '(须电话预订而不能自由揽客的)出租汽车;', 'If you want a cheap ride, take a minicab.', '要想出行便宜，就坐小型出租车吧。');
INSERT INTO `words` VALUES (4533, 'minimal', 'ˈmɪnɪməl', 'adj.', '极小的;极少的;最小的;', 'The co-operation between the two is minimal...', '双方的合作是最低限度的。');
INSERT INTO `words` VALUES (4534, 'minimalist', 'ˈmɪnɪməlɪst', 'n.', '极简抽象派艺术家;简约主义者;', 'He was influenced by the minimalists in the 1970s.', '他受到20世纪70年代极简派艺术家的影响。');
INSERT INTO `words` VALUES (4535, 'minimum', 'ˈmɪnɪməm', 'adj.', '最低的;最小的;最低限度的;', 'He was only five feet nine, the minimum height for a policeman.', '他只有5英尺9英寸高，刚刚达到当警察的最低身高要求。');
INSERT INTO `words` VALUES (4536, 'mining', 'ˈmaɪnɪŋ', 'n.', '采矿;采矿业;', '...traditional industries such as coal mining and steel making...', '诸如采煤与钢铁制造之类的传统工业');
INSERT INTO `words` VALUES (4537, 'minion', 'ˈmɪniən', 'n.', '下属;小卒;杂役;', 'She delegated the job to one of her minions.', '她把这份工作委派给她的一个手下。');
INSERT INTO `words` VALUES (4538, 'minister', 'ˈmɪnɪstə(r)', 'n.', '(英国及其他许多国家的)部长，大臣;牧师;公使;', 'When the government had come to power, he had been named minister of culture...', '这届政府开始执政的时候，他被任命为文化部部长。');
INSERT INTO `words` VALUES (4539, 'ministerial', 'ˌmɪnɪˈstɪəriəl', 'adj.', '部长的;大臣的;', 'The prime minister\'s initial ministerial appointments haven\'t pleased all his supporters.', '首相最初的大臣任命没能让他所有的支持者都满意。');
INSERT INTO `words` VALUES (4540, 'ministry', 'ˈmɪnɪstri', 'n.', '(政府的)部;(尤指基督教新教的)全体牧师;神职;', '...the Ministry of Justice.', '司法部');
INSERT INTO `words` VALUES (4541, 'mink', 'mɪŋk', 'n.', '水貂;貂皮;貂皮大衣;', '...a proposal for a ban on the hunting of foxes, mink and hares.', '要求禁猎狐狸、水貂和野兔的提案');
INSERT INTO `words` VALUES (4542, 'minor', 'ˈmaɪnə(r)', 'adj.', '较小的;次要的;轻微的;', 'She is known in Italy for a number of minor roles in films...', '她因在电影中饰演了一些配角而在意大利为人所知。');
INSERT INTO `words` VALUES (4543, 'minority', 'maɪˈnɒrəti', 'n.', '少数;少数派;少数人;', 'Local authority nursery provision covers only a tiny minority of working mothers...', '当地政府提供的保育服务只有少数上班族母亲才能够享受到。');
INSERT INTO `words` VALUES (4544, 'mint', 'mɪnt', 'n.', '薄荷;薄荷糖;铸币厂;', 'Garnish with mint sprigs.', '用薄荷枝装点。');
INSERT INTO `words` VALUES (4545, 'minus', 'ˈmaɪnəs', 'prep.', '减;减去;零下;', 'One minus  one is zero...', '1减1等于0。');
INSERT INTO `words` VALUES (4546, 'minuscule', 'ˈmɪnəskjuːl', 'adj.', '极小的;微小的;', 'The film was shot in 17 days, a minuscule amount of time.', '这部电影仅用短短的17天便拍摄完毕。');
INSERT INTO `words` VALUES (4547, 'miracle', 'ˈmɪrəkl', 'n.', '圣迹;神迹;奇迹;', 'It is a miracle no one was killed...', '出人意料的是，没有人死亡。');
INSERT INTO `words` VALUES (4548, 'miraculous', 'mɪˈrækjələs', 'adj.', '奇迹般的;不可思议的;不平凡的;', 'The horse made a miraculous recovery to finish a close third.', '这匹马奇迹般地恢复了状态，以微弱的劣势位居第三。');
INSERT INTO `words` VALUES (4549, 'miraculously', '', 'adv.', '神奇地；非凡地；出乎意料地;', 'The horse made a miraculous recovery to finish a close third.', '这匹马奇迹般地恢复了状态，以微弱的劣势位居第三。');
INSERT INTO `words` VALUES (4550, 'mirage', 'ˈmɪrɑːʒ', 'n.', '幻景;海市蜃楼;幻想;', 'Through my half-closed eyelids I began to see mirages...', '透过我半睁半闭的双眼，我看到了海市蜃楼。');
INSERT INTO `words` VALUES (4551, 'mire', 'ˈmaɪə(r)', 'n.', '泥潭;泥沼;', '...a mire of poverty and ignorance.', '贫穷与无知的泥潭');
INSERT INTO `words` VALUES (4552, 'mirror', 'ˈmɪrə(r)', 'n.', '镜子;写照;反映某种情况的事物;', 'He absent-mindedly looked at himself in the mirror...', '他心不在焉地看着镜中的自己。');
INSERT INTO `words` VALUES (4553, 'mirroring', 'ˈmɪrərɪŋ', 'v.', '反映;映照;反射;', 'He absent-mindedly looked at himself in the mirror...', '他心不在焉地看着镜中的自己。');
INSERT INTO `words` VALUES (4554, 'mirth', 'mɜːθ', 'n.', '欢乐;欢笑;', 'That caused considerable mirth amongst pupils and sports masters alike...', '那个举动引得学生和体育老师大笑不止。');
INSERT INTO `words` VALUES (4555, 'misadventure', 'ˌmɪsədˈventʃə(r)', 'n.', '意外致死;厄运;恶运;', '...a series of misadventures...', '一连串的不幸事件');
INSERT INTO `words` VALUES (4556, 'misapprehension', 'ˌmɪsæprɪˈhenʃn', 'n.', '误解;误会;', 'Men still appear to be labouring under the misapprehension that women want hairy, muscular men...', '男人们似乎仍然错误地认为女人喜欢体毛浓重、肌肉强健的男人。');
INSERT INTO `words` VALUES (4557, 'misappropriate', 'ˌmɪsəˈprəʊprieɪt', 'v.', '私吞;挪用;', 'I took no money for personal use and have not misappropriated any funds whatsoever...', '我没有把钱款挪为己用，也没有挪用任何资金。');
INSERT INTO `words` VALUES (4558, 'misbehave', 'ˌmɪsbɪˈheɪv', 'v.', '行为不端;', 'When the children misbehaved she was unable to cope.', '孩子们调皮捣蛋的时候她管不了。');
INSERT INTO `words` VALUES (4559, 'miscarry', 'ˌmɪsˈkæri', 'v.', '流产;失败;', 'Many women who miscarry eventually have healthy babies.', '许多曾经小产的女人最终都生下了健康的宝宝。');
INSERT INTO `words` VALUES (4560, 'miscellaneous', 'ˌmɪsəˈleɪniəs', 'adj.', '混杂的;各种各样的;', '...a hoard of miscellaneous junk...', '收藏的一堆乱七八糟的破烂');
INSERT INTO `words` VALUES (4561, 'miscellany', 'mɪˈseləni', 'n.', '杂集;混合体;', '...glass cases filled with a miscellany of objects...', '塞满了各式各样东西的玻璃橱柜');
INSERT INTO `words` VALUES (4562, 'mischief', 'ˈmɪstʃɪf', 'n.', '淘气;恶作剧;顽皮;', 'The little lad was a real handful. He was always up to mischief...', '这个小家伙真是难管教，他总是搞恶作剧。');
INSERT INTO `words` VALUES (4563, 'mischievous', 'ˈmɪstʃɪvəs', 'adj.', '顽皮的;捣蛋的;招惹是非的;', 'She rocks back and forth on her chair like a mischievous child...', '她像个顽皮的孩子似的，坐在椅子上来回摇晃。');
INSERT INTO `words` VALUES (4564, 'misconception', 'ˌmɪskənˈsepʃn', 'n.', '错误认识;误解;', 'It is a misconception that Peggy was fabulously wealthy...', '人们误以为佩吉相当有钱。');
INSERT INTO `words` VALUES (4565, 'misconduct', 'ˌmɪsˈkɒndʌkt', 'n.', '失职;处理不当;行为不端;', 'He was dismissed from his job for gross misconduct after handing over confidential documents to the press...', '在把机密文件交给新闻界之后，他就因严重渎职而被解职了。');
INSERT INTO `words` VALUES (4566, 'misconstrue', 'ˌmɪskənˈstruː', 'v.', '误解(某人的言行);', 'An outsider might misconstrue the nature of the relationship...', '外人也许会误解这种关系的本质。');
INSERT INTO `words` VALUES (4567, 'miscreant', 'ˈmɪskriənt', 'n.', '缺德的人;不法之徒;', 'Local people demanded that the District Magistrate apprehend the miscreants.', '当地人要求地方法官逮捕那些歹徒。');
INSERT INTO `words` VALUES (4568, 'misdeed', 'ˌmɪsˈdiːd', 'n.', '恶行;不义之举;', '...the alleged financial misdeeds of his government.', '人们所指控的他的政府在财政方面的失误政策');
INSERT INTO `words` VALUES (4569, 'miser', 'ˈmaɪzə(r)', 'n.', '吝啬鬼;守财奴;', 'I\'m married to a miser.', '我嫁给了一个守财奴。');
INSERT INTO `words` VALUES (4570, 'miserable', 'ˈmɪzrəbl', 'adj.', '痛苦的;非常难受的;可怜的;', 'I took a series of badly paid secretarial jobs which made me really miserable...', '我做了几份薪酬微薄的秘书工作，这让我非常郁闷。');
INSERT INTO `words` VALUES (4571, 'miserably', '', 'adv.', '痛苦地；艰苦地；令人难受地;悲惨地；可悲地;糟糕地；少得可怜地;', 'I took a series of badly paid secretarial jobs which made me really miserable...', '我做了几份薪酬微薄的秘书工作，这让我非常郁闷。');
INSERT INTO `words` VALUES (4572, 'miserly', 'ˈmaɪzəli', 'adj.', '吝啬的;小气的;极少的;', 'He is miserly with both his time and his money.', '他对时间和金钱都很吝啬。');
INSERT INTO `words` VALUES (4573, 'misery', 'ˈmɪzəri', 'n.', '痛苦;悲惨;穷困;', 'All that money brought nothing but sadness and misery and tragedy.', '那笔钱带来的只有伤心、痛苦和悲剧。');
INSERT INTO `words` VALUES (4574, 'misfire', 'ˌmɪsˈfaɪə(r)', 'v.', '不奏效;不起作用;不起动;', 'Some of their policies had misfired.', '他们的一些政策已经失败了。');
INSERT INTO `words` VALUES (4575, 'misfit', 'ˈmɪsfɪt', 'n.', '与别人合不来的人;行为(或思想)怪异的人;', 'I have been made to feel a social and psychological misfit for not wanting children.', '因为不想要孩子，让我感到自己在社会和心理上与别人格格不入。');
INSERT INTO `words` VALUES (4576, 'misfortune', 'ˌmɪsˈfɔːtʃuːn', 'n.', '厄运;不幸;不幸的事故(或情况、事件);', 'She seemed to enjoy the misfortunes of others...', '她似乎喜欢幸灾乐祸。');
INSERT INTO `words` VALUES (4577, 'misgiving', 'ˌmɪsˈɡɪvɪŋ', 'n.', '疑虑;顾虑;', 'She had some misgivings about what she was about to do...', '她对自己即将要做的事情存有一些顾虑。');
INSERT INTO `words` VALUES (4578, 'misgivings', 'mɪsˈgɪvɪŋz', 'n.', '疑虑;顾虑;', 'She had some misgivings about what she was about to do...', '她对自己即将要做的事情存有一些顾虑。');
INSERT INTO `words` VALUES (4579, 'misguided', 'ˌmɪsˈɡaɪdɪd', 'adj.', '(因理解或判断失误)搞错的;', 'In a misguided attempt to be funny, he manages only offensiveness...', '他本想表现得风趣一点，却弄巧成拙地冒犯了别人。');
INSERT INTO `words` VALUES (4580, 'mishandle', 'ˌmɪsˈhændl', 'v.', '处理不当;粗暴对待;胡乱操作;', 'She completely mishandled an important project purely through lack of attention...', '她之所以彻底搞砸了一个重大项目纯粹是因为不够上心。');
INSERT INTO `words` VALUES (4581, 'mishap', 'ˈmɪshæp', 'n.', '小事故;晦气;', 'After a number of mishaps she did manage to get back to Germany...', '经历了一番波折之后，她终于设法返回德国。');
INSERT INTO `words` VALUES (4582, 'misinform', 'ˌmɪsɪnˈfɔːm', 'v.', '误报;误传;', 'He has been misinformed by members of his own party...', '他从自己党内成员那里得到了错误的消息。');
INSERT INTO `words` VALUES (4583, 'misinterpret', 'ˌmɪsɪnˈtɜːprət', 'v.', '误解;误释;', 'He was amazed that he\'d misinterpreted the situation so completely...', '他十分震惊地发现自己竟然完全误解了情况。');
INSERT INTO `words` VALUES (4584, 'misjudge', 'ˌmɪsˈdʒʌdʒ', 'v.', '形成错误认识;错看;对(时间、距离等)判断错误;', 'Perhaps I had misjudged him, and he was not so predictable after all...', '也许我看错了他，毕竟他不是可以让人一眼看透的人。');
INSERT INTO `words` VALUES (4585, 'mislay', 'ˌmɪsˈleɪ', 'v.', '随意搁置，乱放(而一时找不到);', 'I appear to have mislaid my jumper.', '我好像忘了把套头衫放哪儿了。');
INSERT INTO `words` VALUES (4586, 'mislead', 'ˌmɪsˈliːd', 'v.', '误导;引入歧途;使误信;', 'Jack was furious with his London doctors for having misled him...', '杰克对伦敦的医生欺骗自己的行为感到非常生气。');
INSERT INTO `words` VALUES (4587, 'misleading', 'ˌmɪsˈliːdɪŋ', 'adj.', '误导的;引入歧途的;', 'It would be misleading to say that we were friends...', '说我们是朋友会让别人产生误解。');
INSERT INTO `words` VALUES (4588, 'mismanage', 'ˌmɪsˈmænɪdʒ', 'v.', '对…处理失当;对…管理不当;', '75% of voters think the President has mismanaged the economy.', '75%的选民认为总统对经济管理不善。');
INSERT INTO `words` VALUES (4589, 'mismanagement', 'ˌmɪsˈmænɪdʒmənt', 'n.', '管理不善;处置不当;', '...the Government\'s economic mismanagement...', '政府对经济的管理不当');
INSERT INTO `words` VALUES (4590, 'misnomer', 'ˌmɪsˈnəʊmə(r)', 'n.', '使用不恰当(或不准确)的名称;用词不当;', 'Herbal \'tea\' is something of a misnomer because these drinks contain no tea at all.', '花草“茶”是一个误称，因为这类饮料里面根本不含茶。');
INSERT INTO `words` VALUES (4591, 'misplace', 'ˌmɪsˈpleɪs', 'v.', '随意搁置，乱放(而一时找不到);', 'He misplaces his reading glasses with such regularity that aides carry extras...', '他老是记不起把自己阅读时用的放大镜放在什么地方，所以助手们会随身携带备用的。');
INSERT INTO `words` VALUES (4592, 'misrepresent', 'ˌmɪsˌreprɪˈzent', 'v.', '误传;不实报道;歪曲;', 'He said that the press had misrepresented him as arrogant and bullying...', '他说新闻媒体把他歪曲成了一个傲慢而又霸道的人。');
INSERT INTO `words` VALUES (4593, 'misshapen', 'ˌmɪsˈʃeɪpən', 'adj.', '畸形的;扭曲变形的;', '...misshapen vegetables...', '奇形怪状的蔬菜');
INSERT INTO `words` VALUES (4594, 'missile', 'ˈmɪsaɪl', 'n.', '导弹;发射物;投掷物;', 'The authorities offered to stop firing missiles if the rebels agreed to stop attacking civilian targets.', '当局表示，如果叛乱分子答应停止攻击平民目标，就不再发射导弹。');
INSERT INTO `words` VALUES (4595, 'missing', 'ˈmɪsɪŋ', 'adj.', '找不到的;不在的;丢失的;', 'It was only an hour or so later that I discovered that my gun was missing...', '仅仅约一个小时之后，我发现我的枪不见了。');
INSERT INTO `words` VALUES (4596, 'mission', 'ˈmɪʃn', 'n.', '官方使命;使团的使命;使团;', 'Salisbury sent him on a diplomatic mission to North America...', '索尔兹伯里派他到北美执行一项外交任务。');
INSERT INTO `words` VALUES (4597, 'missive', 'ˈmɪsɪv', 'n.', '信函;(尤指)长信，公函;', '...the customary missive from your dear mother.', '你老母亲来的一封家信');
INSERT INTO `words` VALUES (4598, 'misspell', 'ˌmɪsˈspel', 'v.', '拼错;写错;', 'Sorry I misspelled your last name.', '对不起，我把你的姓拼错了。');
INSERT INTO `words` VALUES (4599, 'misspend', 'ˌmɪsˈspend', 'v.', '挥霍，浪费，滥用(时间或金钱);', 'Much of the money was grossly misspent...', '大部分的钱都被大笔挥霍掉了。');
INSERT INTO `words` VALUES (4600, 'missus', 'ˈmɪsɪz', 'n.', '老婆;妻子;(称呼不知姓名的妇女)大姐;', 'That\'s what bugs my missus more than anything...', '那种事最容易激怒我太太。');
INSERT INTO `words` VALUES (4601, 'mist', 'mɪst', 'n.', '薄雾;水汽;液体喷雾;', 'Thick mist made flying impossible...', '浓雾致使无法飞行。');
INSERT INTO `words` VALUES (4602, 'mistake', 'mɪˈsteɪk', 'n.', '(言语或行为上的)错误，失误;(用词或数字等上的)错误，口误，笔误;', 'They made the big mistake of thinking they could seize its border with a relatively small force...', '他们大错特错，居然认为以较少的兵力就能够夺取其边境地区。');
INSERT INTO `words` VALUES (4603, 'mistaken', 'mɪˈsteɪkən', 'adj.', '错误;不正确;判断错误的;', 'I see I was mistaken about you...', '我明白我以前错怪你了。');
INSERT INTO `words` VALUES (4604, 'mistakenly', '', 'adv.', '错误地，被误解地;', 'I see I was mistaken about you...', '我明白我以前错怪你了。');
INSERT INTO `words` VALUES (4605, 'mister', 'ˈmɪstə(r)', 'n.', '先生(Mr的全写，书写时不常用);(儿童常用，称呼不知姓名的男子)先生;', 'Look, Mister, we know our job, so don\'t try to tell us what to do.', '嘿，先生，我们知道自己该干什么，不用你指挥。');
INSERT INTO `words` VALUES (4606, 'mistreat', 'ˌmɪsˈtriːt', 'v.', '虐待;', '...a lad who mistreats a horse...', '虐待马的马夫');
INSERT INTO `words` VALUES (4607, 'mistress', 'ˈmɪstrəs', 'n.', '情妇;(尤指私立学校的)女教师;(尤指旧时雇用仆人的)女主人，主妇;', 'She was his mistress for three years...', '她曾做过他3年的情妇。');
INSERT INTO `words` VALUES (4608, 'mistrust', 'ˌmɪsˈtrʌst', 'v.', '猜疑;不信任;', 'There was mutual mistrust between the two men.', '两个男人互相猜疑。');
INSERT INTO `words` VALUES (4609, 'mistrustful', 'ˌmɪsˈtrʌstfl', 'adj.', '不信任的;怀疑的;', 'He had always been mistrustful of women.', '他总是不相信女人。');
INSERT INTO `words` VALUES (4610, 'mists', 'mɪsts', 'n.', '薄雾;水汽;液体喷雾;', 'Thick mist made flying impossible...', '浓雾致使无法飞行。');
INSERT INTO `words` VALUES (4611, 'misty', 'ˈmɪsti', 'adj.', '多雾的;薄雾笼罩的;模糊的;', 'It\'s a bit misty this morning...', '今晨有薄雾。');
INSERT INTO `words` VALUES (4612, 'misunderstand', 'ˌmɪsʌndəˈstænd', 'v.', '误解;误会;', 'They have simply misunderstood what rock and roll is...', '他们根本没有理解什么是摇滚乐。');
INSERT INTO `words` VALUES (4613, 'misunderstanding', 'ˌmɪsʌndəˈstændɪŋ', 'n.', '误解;误会;意见不一;', 'There has been some misunderstanding of our publishing aims...', '关于我们的出版目的存在着某些误解。');
INSERT INTO `words` VALUES (4614, 'mite', 'maɪt', 'n.', '螨(状似蜘蛛的微小动物，在动植物、地毯等上生活);(可怜的)小孩子，小动物;少量;', 'I can\'t help feeling just a mite uneasy about it...', '我对此不禁感到一丝不安。');
INSERT INTO `words` VALUES (4615, 'mitigate', 'ˈmɪtɪɡeɪt', 'v.', '减轻;缓和;', '...ways of mitigating the effects of an explosion...', '减轻爆炸威力的种种方法');
INSERT INTO `words` VALUES (4616, 'mix', 'mɪks', 'v.', '(使)混合，掺和，融合;调配;配制;', 'Oil and water don\'t mix...', '油和水不相溶。');
INSERT INTO `words` VALUES (4617, 'mixed', 'mɪkst', 'adj.', '混合的;混杂的;人员混杂的;', 'I came home from the meeting with mixed feelings...', '开完会回到家里，我心里百感交集。');
INSERT INTO `words` VALUES (4618, 'mixer', 'ˈmɪksə(r)', 'n.', '搅拌器;混合器;调酒用的饮料;', '...an electric mixer.', '电动搅拌器');
INSERT INTO `words` VALUES (4619, 'mixture', 'ˈmɪkstʃə(r)', 'n.', '混合;结合体;混合物;', 'They looked at him with a mixture of horror, envy, and awe.', '他们带着一种恐惧、嫉妒和敬畏交织在一起的复杂心情望着他。');
INSERT INTO `words` VALUES (4620, 'mm', 'əm', 'abbr.', '毫米;', '...a 135mm lens.', '135毫米的透镜');
INSERT INTO `words` VALUES (4621, 'mnemonic', 'nɪˈmɒnɪk', 'n.', '帮助记忆的词句(或诗歌等);助记符号;', '...mnemonic devices.', '助记工具');
INSERT INTO `words` VALUES (4622, 'mnemonics', 'ni(ː)ˈmɒnɪks', 'n.', '帮助记忆的词句(或诗歌等);助记符号;', '...mnemonic devices.', '助记工具');
INSERT INTO `words` VALUES (4623, 'moan', 'məʊn', 'v.', '呻吟;抱怨;呼啸;', 'Tony moaned in his sleep and then turned over on his side...', '托尼在睡梦中发出呻吟声，然后翻了个身。');
INSERT INTO `words` VALUES (4624, 'mob', 'mɒb', 'n.', '人群;(尤指)暴民;一群，一帮(有共同点的人);', 'Bottles and cans were hurled on the terraces by the mob...', '暴徒向看台扔瓶子和罐子。');
INSERT INTO `words` VALUES (4625, 'mobile', 'ˈməʊbaɪl', 'adj.', '非固定的;可移动的;行动方便;', '...special mobile units where men can have their fingerprints taken and donate a specimen of blood.', '人们可以取指纹和捐献血样的特设流动点');
INSERT INTO `words` VALUES (4626, 'mobility', 'məʊˈbɪləti', 'n.', '(住处、社会阶层、职业方面的)流动能力;移动的能力;易于行走的能力;', '...special mobile units where men can have their fingerprints taken and donate a specimen of blood.', '人们可以取指纹和捐献血样的特设流动点');
INSERT INTO `words` VALUES (4627, 'mock', 'mɒk', 'v.', '嘲笑;(模仿)嘲弄;不尊重;', 'I thought you were mocking me...', '我还以为你是在嘲笑我。');
INSERT INTO `words` VALUES (4628, 'mockery', 'ˈmɒkəri', 'n.', '嘲笑;愚弄;笑柄;', 'Was there a glint of mockery in his eyes?...', '他的眼里是不是闪过一丝讥讽？');
INSERT INTO `words` VALUES (4629, 'mode', 'məʊd', 'n.', '方式;风格;样式;', '...the capitalist mode of production...', '资本主义生产方式');
INSERT INTO `words` VALUES (4630, 'modem', 'ˈməʊdem', 'n.', '调制解调器;', 'He sent his work to his publishers by modem.', '他通过网络把作品发给了出版商。');
INSERT INTO `words` VALUES (4631, 'moderation', 'ˌmɒdəˈreɪʃn', 'n.', '适度;适中;合理;', 'The United Nations Secretary General called on all parties to show moderation.', '联合国秘书长呼吁各方要表现出克制。');
INSERT INTO `words` VALUES (4632, 'moderator', 'ˈmɒdəreɪtə(r)', 'n.', '调解人;调停人;仲裁人;', '...a former moderator of the General Assembly of the Church of Scotland.', '苏格兰长老会最高会议的前任主席');
INSERT INTO `words` VALUES (4633, 'modern', 'ˈmɒdn', 'adj.', '现代的;当代的;近代的;', '...the problem of materialism in modern society.', '现代社会的实利主义问题');
INSERT INTO `words` VALUES (4634, 'modest', 'ˈmɒdɪst', 'adj.', '些许的;不太大(或太贵、太重要等)的;谦虚的;', '...the modest home of a family who lived off the land...', '靠土地过活的一户人家的简朴住房');
INSERT INTO `words` VALUES (4635, 'modestly', '\'mɒdɪstli', 'adv.', '谨慎地;切勿浪费;', '...the modest home of a family who lived off the land...', '靠土地过活的一户人家的简朴住房');
INSERT INTO `words` VALUES (4636, 'modesty', 'ˈmɒdəsti', 'n.', '谦虚;谦逊;庄重;', 'His modesty does him credit, for the food he produces speaks for itself...', '他的谦逊值得赞扬，因为他做的食物品质之高显而易见。');
INSERT INTO `words` VALUES (4637, 'modicum', 'ˈmɒdɪkəm', 'n.', '少量，一点点(好事或愉快的事);', 'I\'d like to think I\'ve had a modicum of success.', '我倒觉得我取得了一点成绩。');
INSERT INTO `words` VALUES (4638, 'modification', 'ˌmɒdɪfɪˈkeɪʃn', 'n.', '修改;改进;改变;', 'The club members did agree to modify their recruitment policy...', '俱乐部成员的确同意修改吸纳新成员的政策。');
INSERT INTO `words` VALUES (4639, 'modified', 'ˈmɒdɪfaɪd', 'v.', '调整;稍作修改;使更适合;', 'The club members did agree to modify their recruitment policy...', '俱乐部成员的确同意修改吸纳新成员的政策。');
INSERT INTO `words` VALUES (4640, 'modify', 'ˈmɒdɪfaɪ', 'v.', '调整;稍作修改;使更适合;', 'The club members did agree to modify their recruitment policy...', '俱乐部成员的确同意修改吸纳新成员的政策。');
INSERT INTO `words` VALUES (4641, 'modish', 'ˈməʊdɪʃ', 'adj.', '时髦的;流行的;', '...a short checklist of much that is modish at the moment.', '时下潮品的简明一览表');
INSERT INTO `words` VALUES (4642, 'modular', 'ˈmɒdjələ(r)', 'adj.', '分单元的(由独立单元组成，学生可选修);组合式的;模块化的;', 'They ended up buying a prebuilt modular home on a two-acre lot.', '最后他们购买了一幢建在两英亩土地上的预制组合式房屋。');
INSERT INTO `words` VALUES (4643, 'modulate', 'ˈmɒdjuleɪt', 'v.', '调节(嗓音的大小、强弱、高低等);变调;转调;', 'He carefully modulated his voice.', '他小心地压低了声音。');
INSERT INTO `words` VALUES (4644, 'modulation', 'ˌmɒdjʊˈleɪʃən', 'n.', '调制；调谐；变调；缓和;', 'He carefully modulated his voice.', '他小心地压低了声音。');
INSERT INTO `words` VALUES (4645, 'module', 'ˈmɒdjuːl', 'n.', '单元(尤指英国大学课程的一部分);模块;功能块;', 'These courses cover a twelve-week period and are organised into three four-week modules.', '这些课程要上12周，分为3个单元，每单元4周。');
INSERT INTO `words` VALUES (4646, 'moist', 'mɔɪst', 'adj.', '微湿的;湿润的;', 'Wipe off any excess make-up with a clean, moist cotton flannel...', '用干净的湿绒布擦去多余的化妆品。');
INSERT INTO `words` VALUES (4647, 'moisten', 'ˈmɔɪsn', 'v.', '(使)变得潮湿，变得湿润;', 'She took a sip of water to moisten her dry throat.', '她抿了一口水，润一下发干的喉咙。');
INSERT INTO `words` VALUES (4648, 'moisture', 'ˈmɔɪstʃə(r)', 'n.', '潮气;水汽;水分;', 'When the soil is dry, more moisture is lost from the plant...', '土壤干燥时，植物会流失更多的水分。');
INSERT INTO `words` VALUES (4649, 'molecular', 'məˈlekjələ(r)', 'adj.', '分子的;', '...the molecular structure of fuel.', '燃料的分子结构');
INSERT INTO `words` VALUES (4650, 'molecule', 'ˈmɒlɪkjuːl', 'n.', '分子;', '...the hydrogen bonds between water molecules.', '水分子间的氢键');
INSERT INTO `words` VALUES (4651, 'molest', 'məˈlest', 'v.', '对(儿童)进行性骚扰;攻击;伤害;', 'He was accused of sexually molesting a female colleague.', '他被指控对一位女同事进行性骚扰。');
INSERT INTO `words` VALUES (4652, 'mollify', 'ˈmɒlɪfaɪ', 'v.', '使平静;抚慰;', 'The investigation was undertaken primarily to mollify pressure groups.', '展开调查主要是为了安抚那些压力集团。');
INSERT INTO `words` VALUES (4653, 'mollycoddle', 'ˈmɒlikɒdl', 'v.', '溺爱;宠爱;', 'Christopher accused me of mollycoddling Andrew.', '克里斯托弗指责我太宠着安德鲁。');
INSERT INTO `words` VALUES (4654, 'molten', 'ˈməʊltən', 'adj.', '熔化的;熔融的;', 'The molten metal is poured into the mould.', '把熔化的金属倒入铸模。');
INSERT INTO `words` VALUES (4655, 'mom', 'mɒm', 'n.', '妈妈;', 'We waited for Mom and Dad to get home...', '我们等爸爸妈妈回家。');
INSERT INTO `words` VALUES (4656, 'moment', 'ˈməʊmənt', 'n.', '片刻;瞬间;准确时刻;', 'In a moment he was gone...', '一转眼他就不见了。');
INSERT INTO `words` VALUES (4657, 'momentarily', 'ˈməʊməntrəli', 'adv.', '短促地;片刻地;立即;', 'She paused momentarily when she saw them.', '看到他们，她停了片刻。');
INSERT INTO `words` VALUES (4658, 'momentary', 'ˈməʊməntri', 'adj.', '短促的;短暂的;片刻的;', '...a momentary lapse of concentration...', '片刻的走神儿');
INSERT INTO `words` VALUES (4659, 'momentous', 'məˈmentəs', 'adj.', '关键的;重要的;重大的;', '...the momentous decision to send in the troops...', '派遣部队的重大决定');
INSERT INTO `words` VALUES (4660, 'momentum', 'məˈmentəm', 'n.', '推进力;动力;势头;', 'This campaign is really gaining momentum...', '该运动的势头越来越猛。');
INSERT INTO `words` VALUES (4661, 'mommy', 'ˈmɒmi', 'n.', '妈咪(尤为儿语);', 'Mommy and I went in an aeroplane.', '我和妈妈登上一架飞机。');
INSERT INTO `words` VALUES (4662, 'monarchy', 'ˈmɒnəki', 'n.', '君主制;君主政体;君主国;', 'In a few years we may no longer have a monarchy.', '几年后君主政体可能将不复存在。');
INSERT INTO `words` VALUES (4663, 'monday', 'ˈmʌndeɪ', 'n.', '星期一;', 'I went back to work on Monday...', '我周一回去上班了。');
INSERT INTO `words` VALUES (4664, 'monetary', 'ˈmʌnɪtri', 'adj.', '货币的，钱的(尤指一国的金融);', 'Some countries tighten monetary policy to avoid inflation...', '一些国家实行紧缩银根的货币政策，以避免通货膨胀。');
INSERT INTO `words` VALUES (4665, 'money', 'ˈmʌni', 'n.', '钱;薪水;收入;', 'A lot of the money that you pay at the cinema goes back to the film distributors...', '你在电影院消费的一大部分钱都会回到电影发行商手里。');
INSERT INTO `words` VALUES (4666, 'mongolian', 'mɒŋˈɡəʊliən', '', '【医】蒙古的，蒙古族（语）的;', '...the Mongolian capital, Ulan Bator.', '蒙古国首都，乌兰巴托');
INSERT INTO `words` VALUES (4667, 'monitor', 'ˈmɒnɪtə(r)', 'n.', '显示屏;监视器;(计算机)显示器;', 'Officials had not been allowed to monitor the voting...', '以前不允许官员监督投票。');
INSERT INTO `words` VALUES (4668, 'monk', 'mʌŋk', 'n.', '僧侣;修道士;', '...saffron-robed Buddhist monks.', '身着藏红色僧袍的佛教僧侣');
INSERT INTO `words` VALUES (4669, 'mono', 'ˈmɒnəʊ', 'adj.', '单声道的;', 'This model has a mono soundtrack.', '这款汽车有单声道音响设备。');
INSERT INTO `words` VALUES (4670, 'mono-', 'ˈmɒnəʊ', 'comb.', '单;单一;', '...high in mono-unsaturated fats.', '单不饱和脂肪含量高');
INSERT INTO `words` VALUES (4671, 'monochrome', 'ˈmɒnəkrəʊm', 'adj.', '黑白的;单色的;', '...color and monochrome monitors.', '彩色和黑白显示器');
INSERT INTO `words` VALUES (4672, 'monogamous', 'məˈnɒɡəməs', 'adj.', '单偶的;一夫一妻制的;有单一性伴侣的;', 'Do you believe that men are not naturally monogamous?...', '你相信人类并非本来就是一夫一妻制的吗？');
INSERT INTO `words` VALUES (4673, 'monogamy', 'məˈnɒɡəmi', 'n.', '一夫一妻(制);单配偶;单配性;', 'People still opt for monogamy and marriage.', '人们仍然选择一夫一妻制。');
INSERT INTO `words` VALUES (4674, 'monograph', 'ˈmɒnəɡrɑːf', 'n.', '专论;专题文章;专著;', '...a monograph on her favourite author, John Masefield.', '关于她最喜欢的作者约翰·梅斯菲尔德的一本专著');
INSERT INTO `words` VALUES (4675, 'monolithic', 'ˌmɒnəˈlɪθɪk', 'adj.', '(组织或体系)庞大而僵化的，庞大而单一的;(建筑等)庞大而无特点的，巨大而单调的;', '...an authoritarian and monolithic system.', '庞大僵化的独裁体制');
INSERT INTO `words` VALUES (4676, 'monologue', 'ˈmɒnəlɒɡ', 'n.', '滔滔不绝的讲话;个人的长篇大论;(戏剧、电影等的)独白;', 'Morris ignored the question and continued his monologue.', '莫里斯不理会这个问题，继续他的长篇大论。');
INSERT INTO `words` VALUES (4677, 'monopoly', 'məˈnɒpəli', 'n.', '垄断;专营服务;被垄断的商品(或服务);', '...Russian moves to end a state monopoly on land ownership.', '俄国人采取的终止国家垄断土地所有权的措施');
INSERT INTO `words` VALUES (4678, 'monotone', 'ˈmɒnətəʊn', 'n.', '单调;单调的声音;', 'The evidence was read out to the court in a dull monotone.', '证据被当庭呆板地宣读了一遍。');
INSERT INTO `words` VALUES (4679, 'monotonous', 'məˈnɒtənəs', 'adj.', '单调乏味的;', 'It\'s monotonous work, like most factory jobs...', '与工厂大部分工作一样，这份工作也很单调乏味。');
INSERT INTO `words` VALUES (4680, 'monotony', 'məˈnɒtəni', 'n.', '单调乏味;千篇一律;', 'A night on the town may help to break the monotony of the week.', '进城住一晚或许有助于消除一周生活的单调。');
INSERT INTO `words` VALUES (4681, 'monsoon', 'ˌmɒnˈsuːn', 'n.', '(南亚地区的)雨季，雨季的降雨;季风，季节风(盛行于南亚地区，夏季刮西南风，带来雨水，冬季刮东北风);', '...the end of the monsoon.', '雨季的尾声');
INSERT INTO `words` VALUES (4682, 'monstrous', 'ˈmɒnstrəs', 'adj.', '丑恶的;道德败坏的;骇人的;', 'She endured the monstrous behaviour for years...', '多年来，她一直忍受着这种骇人听闻的暴行。');
INSERT INTO `words` VALUES (4683, 'montage', 'ˌmɒnˈtɑːʒ', 'n.', '蒙太奇;剪辑组合物;剪辑;', '...a photo montage of some of Italy\'s top television stars.', '一些意大利顶尖级电视明星的图片合集');
INSERT INTO `words` VALUES (4684, 'month', 'mʌnθ', 'n.', '月;月份;约30天的时间;', 'The trial is due to begin next month.', '审判将于下个月开始。');
INSERT INTO `words` VALUES (4685, 'monthly', 'ˈmʌnθli', 'adj.', '每月的;每月一次的;按月结算的;', 'Many people are now having trouble making their monthly house payments...', '现在很多人在支付房款月供上都有困难。');
INSERT INTO `words` VALUES (4686, 'monumental', 'ˌmɒnjuˈmentl', 'adj.', '重要的;意义深远的;不朽的;', 'It had been a monumental blunder to give him the assignment.', '当时把这项任务给他是大错特错了。');
INSERT INTO `words` VALUES (4687, 'mood', 'muːd', 'n.', '情绪;心情;坏心境;', 'He is clearly in a good mood today...', '明摆着，他今天心情不错。');
INSERT INTO `words` VALUES (4688, 'moodiness', '', 'n.', '喜怒无常;情绪;', 'David\'s mother was unstable and moody...', '戴维的妈妈情绪不稳定，心情也不好。');
INSERT INTO `words` VALUES (4689, 'moody', 'ˈmuːdi', 'adj.', '情绪多变的;喜怒无常的;脾气坏的;', 'David\'s mother was unstable and moody...', '戴维的妈妈情绪不稳定，心情也不好。');
INSERT INTO `words` VALUES (4690, 'moon', 'muːn', 'n.', '月球;月亮;月相;', '...the first man on the moon...', '登上月球的第一人');
INSERT INTO `words` VALUES (4691, 'moonlight', 'ˈmuːnlaɪt', 'n.', '月光;', 'They walked along the road in the moonlight...', '他们在月光下沿路散步。');
INSERT INTO `words` VALUES (4692, 'moonlighting', 'ˈmuːnlaɪtɪŋ', 'v.', '(暗中)兼职，从事第二职业;', 'They walked along the road in the moonlight...', '他们在月光下沿路散步。');
INSERT INTO `words` VALUES (4693, 'moonlit', 'ˈmuːnlɪt', 'adj.', '月光照耀的;', '...a beautiful moonlit night.', '美丽的月夜');
INSERT INTO `words` VALUES (4694, 'moor', 'mɔː(r)', 'n.', '旷野;荒野;高沼;', 'Colliford is higher, right up on the moors...', '科利福德位置更高，就在荒野之上。');
INSERT INTO `words` VALUES (4695, 'moot', 'muːt', 'adj.', '(因不大可能发生而)无考虑意义的;', 'Plans have been mooted for a 450,000-strong Ukrainian army...', '建立一支拥有45万名士兵的乌克兰军队的计划被提出供讨论。');
INSERT INTO `words` VALUES (4696, 'mope', 'məʊp', 'v.', '闷闷不乐;自怨自艾;', 'Get on with life and don\'t sit back and mope.', '活跃一些享受生活，不要闲呆着闷闷不乐的。');
INSERT INTO `words` VALUES (4697, 'moral', 'ˈmɒrəl', 'adj.', '道德的;道义上的;道德上的;', '...Western ideas and morals...', '西方的思想和道德规范');
INSERT INTO `words` VALUES (4698, 'morale', 'məˈrɑːl', 'n.', '士气;', 'Many pilots are suffering from low morale...', '很多飞行员目前士气低落。');
INSERT INTO `words` VALUES (4699, 'moralistic', 'ˌmɒrəˈlɪstɪk', 'adj.', '是非观念坚定的;道学的;说教的;', 'He has become more moralistic...', '他变得更喜欢说教了。');
INSERT INTO `words` VALUES (4700, 'morality', 'məˈræləti', 'n.', '道德;道德准则;道义;', '...standards of morality and justice in society.', '社会道德和正义的标准');
INSERT INTO `words` VALUES (4701, 'morally', 'ˈmɒrəli', 'adv.', '道义上;道德上;', '...Western ideas and morals...', '西方的思想和道德规范');
INSERT INTO `words` VALUES (4702, 'morass', 'məˈræs', 'n.', '困境;陷阱;沼泽;', 'I tried to drag myself out of the morass of despair.', '我试图从绝望的困境中走出来。');
INSERT INTO `words` VALUES (4703, 'moratorium', 'ˌmɒrəˈtɔːriəm', 'n.', '暂停，中止(尤指经官方同意的);', 'The House voted to impose a one-year moratorium on nuclear testing.', '众议院投票表决将核试验暂停一年。');
INSERT INTO `words` VALUES (4704, 'morbid', 'ˈmɔːbɪd', 'adj.', '病态的;不正常的;病的;', 'Some people have a morbid fascination with crime.', '一些人对犯罪有一种病态的痴迷。');
INSERT INTO `words` VALUES (4705, 'mordant', 'ˈmɔːdnt', 'adj.', '尖刻而又风趣的;讽刺幽默的;', 'A wicked, mordant sense of humour has come to the fore in Blur\'s world.', '布勒的世界里已出现了一种恶意、刻薄的幽默感。');
INSERT INTO `words` VALUES (4706, 'more', 'mɔː(r)', 'det./pron.', '(数、量等)更多的，更大的;', 'More and more people are surviving heart attacks...', '越来越多的心脏病患者存活下来。');
INSERT INTO `words` VALUES (4707, 'moreover', 'mɔːrˈəʊvə(r)', 'adv.', '此外;而且;', 'She saw that there was indeed a man immediately behind her. Moreover, he was observing her strangely...', '她看见真有一个男的紧跟在她身后，而且在怪异地注视自己。');
INSERT INTO `words` VALUES (4708, 'mores', 'ˈmɔːreɪz', 'n.', '风俗习惯;传统;', '...the accepted mores of British society.', '英国社会接受的习俗');
INSERT INTO `words` VALUES (4709, 'moribund', 'ˈmɒrɪbʌnd', 'adj.', '行将灭亡的;即将倒闭的;濒于崩溃的;', '...the moribund economy.', '毫无生机的经济');
INSERT INTO `words` VALUES (4710, 'morn', 'mɔːn', 'n.', '早晨;上午;', '...one cold February morn.', '二月一个寒冷的早晨');
INSERT INTO `words` VALUES (4711, 'morning', 'ˈmɔːnɪŋ', 'n.', '早晨;上午;午夜至正午的时间;', 'During the morning your guide will take you around the city...', '导游上午会带你们游览全城。');
INSERT INTO `words` VALUES (4712, 'moron', 'ˈmɔːrɒn', 'n.', '笨蛋;蠢货;', 'I used to think that Gordon was a moron...', '我曾以为戈登是个白痴。');
INSERT INTO `words` VALUES (4713, 'moronic', 'məˈrɒnɪk', 'adj.', '白痴的;低能的;愚鲁的;', 'Most of them are just moronic...', '他们大部分不过是废物。');
INSERT INTO `words` VALUES (4714, 'morose', 'məˈrəʊs', 'adj.', '阴郁的;脾气不好的;闷闷不乐的;', 'She was morose, pale, and reticent.', '她面色苍白、忧郁孤僻，并且沉默寡言。');
INSERT INTO `words` VALUES (4715, 'morrow', 'ˈmɒrəʊ', 'n.', '次日;明天;', 'We do depart for Wales on the morrow.', '我们明天确实要前往威尔士。');
INSERT INTO `words` VALUES (4716, 'morsel', 'ˈmɔːsl', 'n.', '少量，一块(食物);', '...a delicious little morsel of meat.', '一小块美味可口的肉');
INSERT INTO `words` VALUES (4717, 'mortal', 'ˈmɔːtl', 'adj.', '不能永生的;终将死亡的;导致死亡的;', 'A man is deliberately designed to be mortal. He grows, he ages, and he dies.', '人注定终有一死：长大、衰老，然后消亡。');
INSERT INTO `words` VALUES (4718, 'mortality', 'mɔːˈtæləti', 'n.', '生命的有限;死亡数量;死亡率;', '...the relationships between mortality and unemployment...', '死亡人数和失业人数之间的关系');
INSERT INTO `words` VALUES (4719, 'mortally', 'ˈmɔːtəli', 'adv.', '致死;致命;极端;', 'A man is deliberately designed to be mortal. He grows, he ages, and he dies.', '人注定终有一死：长大、衰老，然后消亡。');
INSERT INTO `words` VALUES (4720, 'mortar', 'ˈmɔːtə(r)', 'n.', '灰泥;砂浆;迫击炮;', 'The two sides exchanged fire with artillery, mortars and small arms...', '双方交火时动用了大炮、迫击炮和轻武器。');
INSERT INTO `words` VALUES (4721, 'mortgage', 'ˈmɔːɡɪdʒ', 'n.', '按揭(由银行等提供房产抵押借款);按揭贷款;', '...an increase in mortgage rates.', '房屋抵押贷款利率的提高');
INSERT INTO `words` VALUES (4722, 'mortification', 'ˌmɔːtɪfɪˈkeɪʃn', 'n.', '屈辱感;窘迫感;', 'The chairman tried to disguise his mortification.', '主席试图掩饰自己的窘迫。');
INSERT INTO `words` VALUES (4723, 'mortify', 'ˈmɔːtɪfaɪ', 'v.', '使难堪;使羞愧;', 'Jane mortified her family by leaving her husband.', '简离开丈夫的做法使她的家庭蒙羞。');
INSERT INTO `words` VALUES (4724, 'mosaic', 'məʊˈzeɪɪk', 'n.', '镶嵌图案;马赛克;', '...a Roman villa which once housed a fine collection of mosaics...', '曾收藏有一批精美的马赛克镶嵌画的罗马别墅');
INSERT INTO `words` VALUES (4725, 'moss', 'mɒs', 'n.', '苔藓;地衣;', '...ground covered over with moss.', '长满青苔的地面');
INSERT INTO `words` VALUES (4726, 'mossy', 'ˈmɒsi', 'adj.', '苔藓覆盖的;长满苔藓的;', '...a mossy wall.', '长满青苔的墙壁');
INSERT INTO `words` VALUES (4727, 'most', 'məʊst', 'det./pron.', '(数量上)最多，最大;大多数;几乎所有;', 'Most of the houses in the capital don\'t have piped water...', '首都的大部分房屋没有自来水。');
INSERT INTO `words` VALUES (4728, 'mostly', 'ˈməʊstli', 'adv.', '主要地;一般地;通常;', 'I am working with mostly highly motivated people...', '和我共事的人大多积极性极高。');
INSERT INTO `words` VALUES (4729, 'mother', 'ˈmʌðə(r)', 'n.', '母亲;妈妈;对女修道院院长的尊称;', 'She sat on the edge of her mother\'s bed...', '她坐在母亲床边。');
INSERT INTO `words` VALUES (4730, 'motherhood', 'ˈmʌðəhʊd', 'n.', '母亲身份;母性;', '...women who try to combine work and motherhood.', '试图做好工作又要当好母亲的女性');
INSERT INTO `words` VALUES (4731, 'mothering', 'ˈmʌðərɪŋ', 'n.', '呵护;照料;', 'She sat on the edge of her mother\'s bed...', '她坐在母亲床边。');
INSERT INTO `words` VALUES (4732, 'motherland', 'ˈmʌðəlænd', 'n.', '祖国;', '...love for the motherland.', '爱国之情');
INSERT INTO `words` VALUES (4733, 'motif', 'məʊˈtiːf', 'n.', '装饰图案;装饰图形;(文学作品或音乐的)主题， 主旨， 动机;', '...a rose motif.', '玫瑰图案');
INSERT INTO `words` VALUES (4734, 'motion', 'ˈməʊʃn', 'n.', '运动;移动;动;', '...the laws governing light, sound, and motion...', '有关声、光和运动的定律');
INSERT INTO `words` VALUES (4735, 'motionless', 'ˈməʊʃnləs', 'adj.', '静止的;一动不动的;', 'He has this ability of being able to remain as motionless as a statue, for hours on end...', '他有本事连续几个小时一动不动，像雕塑一样。');
INSERT INTO `words` VALUES (4736, 'motivate', 'ˈməʊtɪveɪt', 'v.', '成为…的动机;是…的原因;推动…甘愿苦干;', 'They are motivated by a need to achieve...', '对成功的渴望激励着他们。');
INSERT INTO `words` VALUES (4737, 'motivated', 'ˈməʊtɪveɪtɪd', 'v.', '成为…的动机;是…的原因;推动…甘愿苦干;', 'They are motivated by a need to achieve...', '对成功的渴望激励着他们。');
INSERT INTO `words` VALUES (4738, 'motivation', 'ˌməʊtɪˈveɪʃn', 'n.', '动机;动力;诱因;', 'Money is my motivation...', '金钱是我的动力。');
INSERT INTO `words` VALUES (4739, 'motive', 'ˈməʊtɪv', 'n.', '动机;原因;目的;', 'Police have ruled out robbery as a motive for the killing.', '警方已排除因抢劫而杀人的可能。');
INSERT INTO `words` VALUES (4740, 'motley', 'ˈmɒtli', 'adj.', '混杂的;杂七杂八的;', '...a motley collection of vans, old buses, cattle-trucks, and even a fire engine...', '杂七杂八的一堆车，有运货车、旧公共汽车、运牛车，甚至救火车');
INSERT INTO `words` VALUES (4741, 'motor', 'ˈməʊtə(r)', 'n.', '发动机;马达;汽车;', 'She got in and started the motor.', '她坐上车，发动了引擎。');
INSERT INTO `words` VALUES (4742, 'motoring', 'ˈməʊtərɪŋ', 'adj.', '开汽车的;', '...a three-month sentence for motoring offences...', '因违法驾车而被判3个月徒刑');
INSERT INTO `words` VALUES (4743, 'motorway', 'ˈməʊtəweɪ', 'n.', '(英国)高速公路;', '...the M1 motorway.', 'M1 高速公路');
INSERT INTO `words` VALUES (4744, 'mottled', 'ˈmɒtld', 'adj.', '斑驳的;杂色的;', '...mottled green and yellow leaves.', '黄绿相间的树叶');
INSERT INTO `words` VALUES (4745, 'motto', 'ˈmɒtəʊ', 'n.', '座右铭;格言;箴言;', 'Our motto is \'Plan for the worst and hope for the best\'.', '我们的格言是“做最坏的打算，抱最大的希望”。');
INSERT INTO `words` VALUES (4746, 'mound', 'maʊnd', 'n.', '土墩;小丘;小山岗;', 'The bulldozers piled up huge mounds of dirt...', '推土机堆起一堆一堆的泥土。');
INSERT INTO `words` VALUES (4747, 'mount', 'maʊnt', 'v.', '准备;安排;组织开展;', 'The ANC announced it was mounting a major campaign of mass political protests.', '非洲人国民大会宣布他们正在开展一次大规模政治抗议活动。');
INSERT INTO `words` VALUES (4748, 'mountain', 'ˈmaʊntən', 'n.', '高山;山岳;许多;', 'Ben Nevis, in Scotland, is Britain\'s highest mountain.', '苏格兰境内的本尼维斯山是英国最高的山。');
INSERT INTO `words` VALUES (4749, 'mountainous', 'ˈmaʊntənəs', 'adj.', '多山的;巨大的;山一般的;', '...the mountainous region of Campania.', '坎帕尼亚的多山地区');
INSERT INTO `words` VALUES (4750, 'mountainside', 'ˈmaʊntənsaɪd', 'n.', '山坡;', 'The couple trudged up the dark mountainside.', '这对夫妇在黑黝黝的山坡上艰难跋涉。');
INSERT INTO `words` VALUES (4751, 'mounting', 'ˈmaʊntɪŋ', 'adj.', '上升的;增长的;', 'The ANC announced it was mounting a major campaign of mass political protests.', '非洲人国民大会宣布他们正在开展一次大规模政治抗议活动。');
INSERT INTO `words` VALUES (4752, 'mourn', 'mɔːn', 'v.', '(因失去…而)哀悼，忧伤;', 'Joan still mourns her father...', '琼仍在为父亲的死伤心。');
INSERT INTO `words` VALUES (4753, 'mournful', 'ˈmɔːnfl', 'adj.', '忧伤的;悲痛的;', 'He looked mournful, even near to tears...', '他看起来很悲伤，几乎要流泪了。');
INSERT INTO `words` VALUES (4754, 'mourning', 'ˈmɔːnɪŋ', 'n.', '伤逝;哀悼;丧服;', 'The period of mourning and bereavement may be long...', '丧亲和哀悼的悲伤会持续很久。');
INSERT INTO `words` VALUES (4755, 'mouse', 'maʊs', 'n.', '老鼠;耗子;鼠标;', '...a mouse running in a wheel in its cage.', '在笼中转轮上奔跑的老鼠');
INSERT INTO `words` VALUES (4756, 'moustache', 'məˈstɑːʃ', 'n.', '上唇的胡子;髭;长髭;', 'He was short and bald and had a moustache.', '他个头不高，秃顶，留着八字胡。');
INSERT INTO `words` VALUES (4757, 'mouthful', 'ˈmaʊθfʊl', 'n.', '一口，一满口(的量);又长又拗口的词(或短语);', 'She gulped down a mouthful of coffee...', '她咽下一大口咖啡。');
INSERT INTO `words` VALUES (4758, 'mouthpiece', 'ˈmaʊθpiːs', 'n.', '(电话的)话筒;(乐器的)吹口;喉舌;', 'He shouted into the mouthpiece.', '他对着电话话筒大叫。');
INSERT INTO `words` VALUES (4759, 'movable', 'ˈmuːvəbl', 'adj.', '可移动的;活动的;不固定的;', 'It\'s a vinyl doll with movable arms and legs...', '这是一个四肢可以活动的乙烯基塑料玩具娃娃。');
INSERT INTO `words` VALUES (4760, 'move', 'muːv', 'v.', '(使)改变位置，移动;变化;改变;', 'She moved the sheaf of papers into position...', '她把那捆报纸挪到合适的地方。');
INSERT INTO `words` VALUES (4761, 'movement', 'ˈmuːvmənt', 'n.', '(身体部位的)运动，转动;移动;迁移;', 'It\'s part of a broader Hindu nationalist movement that\'s gaining strength throughout the country.', '这是一场凝聚全国力量的更大规模的印度教民族主义运动的一部分。');
INSERT INTO `words` VALUES (4762, 'mover', 'ˈmuːvə(r)', 'n.', '行动者;运动…的人(或事物);运送物品的机器(或人);', 'We found him a nice horse — a good mover who could gallop.', '我们发现他是匹好马，跑起来风驰电掣。');
INSERT INTO `words` VALUES (4763, 'movie', 'ˈmuːvi', 'n.', '电影;', 'In the first movie Tony Curtis ever made he played a grocery clerk.', '托尼·柯蒂斯在其首部电影里，扮演了一个食品杂货店店员。');
INSERT INTO `words` VALUES (4764, 'movies', 'ˈmuːviz', 'n.', '电影;', 'In the first movie Tony Curtis ever made he played a grocery clerk.', '托尼·柯蒂斯在其首部电影里，扮演了一个食品杂货店店员。');
INSERT INTO `words` VALUES (4765, 'moving', 'ˈmuːvɪŋ', 'adj.', '动人的;令人感动的;移动的;', 'It is very moving to see how much strangers can care for each other...', '看到陌生人之间这般彼此关照，实在令人感动。');
INSERT INTO `words` VALUES (4766, 'mow', 'məʊ', 'v.', '刈;割;修剪;', 'He continued to mow the lawn and do other routine chores.', '他继续剪草坪，并做些其他日常杂务。');
INSERT INTO `words` VALUES (4767, 'mph', 'ˌem piː ˈeɪtʃ', 'abbr.', '每小时所行英里数;英里每小时;', 'Inside these zones, traffic speeds are restricted to 20 mph.', '在这些区域内，交通限速是20英里/小时。');
INSERT INTO `words` VALUES (4768, 'much', 'mʌtʃ', 'det./pron.', '(与不可数名词连用，尤用于否定句;或与how连用以询问数量;也可与as、so和too连用)许多，大量，多少;', 'She laughs too much...', '她笑得太多了。');
INSERT INTO `words` VALUES (4769, 'muck', 'mʌk', 'n.', '(牲畜的)粪便，粪肥;脏东西;泥浆;', 'This congealed muck was interfering with the filter.', '这团凝固的污物堵塞了过滤器。');
INSERT INTO `words` VALUES (4770, 'mud', 'mʌd', 'n.', '泥;淤泥;泥浆;', 'His uniform was crumpled, untidy, splashed with mud...', '他的制服皱巴巴的，邋遢不堪，还溅上了泥点。');
INSERT INTO `words` VALUES (4771, 'muddle', 'ˈmʌdl', 'v.', '弄乱;搅混;使困惑;', 'My thoughts are all in a muddle...', '我的思维陷入一片混乱。');
INSERT INTO `words` VALUES (4772, 'muddy', 'ˈmʌdi', 'adj.', '多泥的;泥泞的;含泥的;', '...a muddy track...', '泥泞的小道');
INSERT INTO `words` VALUES (4773, 'muff', 'mʌf', 'n.', '暖手筒;皮手筒;', 'He muffed his opening speech.', '他的开幕讲话搞砸了。');
INSERT INTO `words` VALUES (4774, 'muffin', 'ˈmʌfɪn', 'n.', '英格兰松饼(通常烤热加黄油吃);杯状小松糕(常含小块水果等);', '...breakfasts of pancakes, blueberry muffins, eggs, and bacon.', '有煎饼、蓝莓松饼、鸡蛋和熏肉的早餐');
INSERT INTO `words` VALUES (4775, 'muffle', 'ˈmʌfl', 'v.', '压抑(声音);使(声音)降低;使听不清;', 'Blake held his handkerchief over the mouthpiece to muffle his voice...', '布莱克把手绢包在话筒上，以使自己的声音听起来模糊不清。');
INSERT INTO `words` VALUES (4776, 'muffled', 'ˈmʌfld', 'adj.', '沉闷的;压抑的;模糊不清的;', '...children muffled in scarves and woolly hats.', '裹着围巾，戴着毛线帽的孩子们');
INSERT INTO `words` VALUES (4777, 'mug', 'mʌɡ', 'n.', '(不用茶碟的有柄的)大杯，缸子， 马克杯;一缸子(的量);(人的)脸;', 'He spooned instant coffee into two of the mugs.', '他用勺子把速溶咖啡舀入两个杯子中。');
INSERT INTO `words` VALUES (4778, 'mugging', 'ˈmʌɡɪŋ', 'n.', '公然行凶抢劫案;拦路抢劫罪;', 'He spooned instant coffee into two of the mugs.', '他用勺子把速溶咖啡舀入两个杯子中。');
INSERT INTO `words` VALUES (4779, 'muggy', 'ˈmʌɡi', 'adj.', '闷热潮湿的;', 'It was muggy and overcast.', '天气闷热潮湿，而且天色阴沉。');
INSERT INTO `words` VALUES (4780, 'multi', '\'mʌlti', 'n.', '多种;多数;', '...the introduction of multi-party democracy.', '多党民主制的实行');
INSERT INTO `words` VALUES (4781, 'multicultural', 'ˌmʌltiˈkʌltʃərəl', 'adj.', '多元文化的;多种文化融合的;', '...children growing up in a multicultural society...', '在多元文化社会中长大的孩子们');
INSERT INTO `words` VALUES (4782, 'multifarious', 'ˌmʌltɪˈfeəriəs', 'adj.', '多种的;各种各样的;多样的;', 'Spain is a composite of multifarious traditions and people...', '西班牙是多种传统和多个民族的合成体。');
INSERT INTO `words` VALUES (4783, 'multilateral', 'ˌmʌltiˈlætərəl', 'adj.', '多边的;多国的;有多条边的;', 'Many want to abandon the multilateral trade talks in Geneva.', '很多国家想退出在日内瓦举行的多边贸易会谈。');
INSERT INTO `words` VALUES (4784, 'multilingual', 'ˌmʌltiˈlɪŋɡwəl', 'adj.', '说(或用)多种语言的;用多种语言书写(或印刷)的;', '...a multilingual country.', '多语种国家');
INSERT INTO `words` VALUES (4785, 'multimedia', 'ˌmʌltiˈmiːdiə', 'adj.', '多媒体的;使用多媒体的;', '...the case of an insurance company using multimedia to improve customer service in its branches.', '保险公司使用多媒体改进其分支机构的客户服务的例子');
INSERT INTO `words` VALUES (4786, 'multiple', 'ˈmʌltɪpl', 'adj.', '数量多的;多种多样的;', 'He died of multiple injuries...', '他死于多处受伤。');
INSERT INTO `words` VALUES (4787, 'multiples', 'ˈmʌltɪplz', 'n.', '倍数;', 'He died of multiple injuries...', '他死于多处受伤。');
INSERT INTO `words` VALUES (4788, 'multiplication', 'ˌmʌltɪplɪˈkeɪʃn', 'n.', '乘;相乘;增加;', 'There will be simple tests in addition, subtraction, multiplication and division.', '会有对加、减、乘、除运算的简单测试。');
INSERT INTO `words` VALUES (4789, 'multiplicity', 'ˌmʌltɪˈplɪsəti', 'n.', '多样性;多种多样;', '...a writer who uses a multiplicity of styles.', '运用多种写作风格的作家');
INSERT INTO `words` VALUES (4790, 'multiply', 'ˈmʌltɪplaɪ', 'v.', '乘;乘以;成倍增加;', 'Such disputes multiplied in the eighteenth and nineteenth centuries...', '这类纠纷在18和19世纪大量增多。');
INSERT INTO `words` VALUES (4791, 'multitude', 'ˈmʌltɪtjuːd', 'n.', '众多;大量;群众;', 'There are a multitude of small quiet roads to cycle along...', '有很多可以骑车的宁静小路。');
INSERT INTO `words` VALUES (4792, 'mum', 'mʌm', 'n.', '妈妈;妈;', 'He misses his mum...', '他想念他的妈妈。');
INSERT INTO `words` VALUES (4793, 'mumble', 'ˈmʌmbl', 'v.', '嘟哝;口齿不清地说;', 'Her grandmother mumbled in her  sleep...', '她祖母含混不清地说着梦话。');
INSERT INTO `words` VALUES (4794, 'mundane', 'mʌnˈdeɪn', 'adj.', '单调的;平凡的;', 'Be willing to do even mundane tasks.', '即使平凡的工作也乐意承担。');
INSERT INTO `words` VALUES (4795, 'municipal', 'mjuːˈnɪsɪpl', 'adj.', '市政的;地方政府的;', 'The municipal authorities gave the go-ahead for the march.', '市政当局批准了这次游行。');
INSERT INTO `words` VALUES (4796, 'murder', 'ˈmɜːdə(r)', 'n.', '谋杀;凶杀;困难的事;', 'The three accused, aged between 19 and 20, are charged with attempted murder...', '3个年龄在19到20岁之间的被告被控谋杀未遂。');
INSERT INTO `words` VALUES (4797, 'murderer', 'ˈmɜːdərə(r)', 'n.', '杀人犯;杀人凶手;', 'One of these men may have been the murderer.', '他们其中有一个人可能是杀人凶手。');
INSERT INTO `words` VALUES (4798, 'murmur', 'ˈmɜːmə(r)', 'v.', '低语;喃喃细语;连续发出低沉的声音;', 'He turned and murmured something to the professor...', '他转身对教授小声说了些什么。');
INSERT INTO `words` VALUES (4799, 'muscle', 'ˈmʌsl', 'n.', '肌肉;肌;体力;', 'Keeping your muscles strong and in tone helps you to avoid back problems...', '保持肌肉强壮发达有利于预防背部疾病。');
INSERT INTO `words` VALUES (4800, 'muscular', 'ˈmʌskjələ(r)', 'adj.', '肌肉的;强壮的;肌肉发达的;', 'As a general rule, all muscular effort is enhanced by breathing in as the effort is made...', '通常，肌肉的力量能通过使劲儿吸气增强。');
INSERT INTO `words` VALUES (4801, 'muse', 'mjuːz', 'n.', '(作家、画家等的)灵感;创作冲动的源泉;缪斯(古希腊和罗马神话中执掌诗歌、音乐和其他文学艺术分支的九位女神之一);', 'Many of the papers muse on the fate of the President...', '很多报纸都在揣测总统的命运。');
INSERT INTO `words` VALUES (4802, 'museum', 'mjuˈziːəm', 'n.', '博物馆;', 'For months Malcolm had wanted to visit the Parisian art museums.', '几个月来马尔科姆一直想参观巴黎的艺术博物馆。');
INSERT INTO `words` VALUES (4803, 'mushroom', 'ˈmʌʃrʊm', 'n.', '蘑菇;蕈;伞菌;', 'There are many types of wild mushrooms.', '野生蘑菇有很多种。');
INSERT INTO `words` VALUES (4804, 'mushrooms', 'ˈmʌʃrʊmz', 'n.', '蘑菇;蕈;伞菌;', 'There are many types of wild mushrooms.', '野生蘑菇有很多种。');
INSERT INTO `words` VALUES (4805, 'music', 'ˈmjuːzɪk', 'n.', '音乐;乐曲;乐曲创作(或演奏)艺术;', '...classical music.', '古典音乐');
INSERT INTO `words` VALUES (4806, 'musical', 'ˈmjuːzɪkl', 'adj.', '音乐的;有音乐的;有音乐天赋的;', 'We have a wealth of musical talent in this region...', '在这一地区有大量的音乐人才。');
INSERT INTO `words` VALUES (4807, 'musician', 'mjuˈzɪʃn', 'n.', '音乐家;作曲家;乐师;', 'He was a brilliant musician.', '他是一位才华横溢的音乐家。');
INSERT INTO `words` VALUES (4808, 'mustard', 'ˈmʌstəd', 'n.', '芥末酱;芥;芥菜;', '...a pot of mustard...', '一罐芥子酱');
INSERT INTO `words` VALUES (4809, 'muster', 'ˈmʌstə(r)', 'v.', '找寻，聚集，激起(支持、勇气等);集合，召集，集结(尤指部队);赶拢(牛、羊);', 'He travelled around West Africa trying to muster support for his movement...', '他在西非四处奔走，为自己的运动争取支持。');
INSERT INTO `words` VALUES (4810, 'mutate', 'mjuːˈteɪt', 'v.', '(使)变异，突变;转变;转换;', 'The virus mutates in the carrier\'s body...', '病毒会在携带者体内发生变异。');
INSERT INTO `words` VALUES (4811, 'mutation', 'mjuːˈteɪʃn', 'n.', '(生物物种的)变异，突变;(形式或结构的)转变，改变;', 'The virus mutates in the carrier\'s body...', '病毒会在携带者体内发生变异。');
INSERT INTO `words` VALUES (4812, 'mute', 'mjuːt', 'adj.', '沉默的;不出声的;无声的;', 'He was mute, distant, and indifferent...', '他一句话也不说，显得疏远而冷漠。');
INSERT INTO `words` VALUES (4813, 'mutter', 'ˈmʌtə(r)', 'v.', '嘀咕;嘟囔;(私下)抱怨，发牢骚;', '\'God knows what\'s happening in that madman\'s mind,\' she muttered...', '她咕哝着：“天知道那个疯子在想什么。”');
INSERT INTO `words` VALUES (4814, 'mutton', 'ˈmʌtn', 'n.', '羊肉;', '...a leg of mutton.', '羊腿');
INSERT INTO `words` VALUES (4815, 'mutual', 'ˈmjuːtʃuəl', 'adj.', '相互的;彼此的;共有的;', 'The East and the West can work together for their mutual benefit and progress...', '东西方可以为彼此共同的利益和发展而合作。');
INSERT INTO `words` VALUES (4816, 'myriad', 'ˈmɪriəd', 'n.', '无数;大量;', 'They face a myriad of problems bringing up children...', '他们在抚养孩子的过程中遇到了各种各样的问题。');
INSERT INTO `words` VALUES (4817, 'mysteries', 'ˈmɪstəriz', 'n.', '神秘的事物;不可理解之事;奥秘;', 'The source of the gunshots still remains a mystery.', '枪炮声来自何处仍是个谜。');
INSERT INTO `words` VALUES (4818, 'mysterious', 'mɪˈstɪəriəs', 'adj.', '神秘的;奇怪的;不易解释的;', 'He died in mysterious circumstances...', '他死得很蹊跷。');
INSERT INTO `words` VALUES (4819, 'mysteriously', 'mɪ\'stɪərɪəslɪ', 'adv.', '神秘地;', 'He died in mysterious circumstances...', '他死得很蹊跷。');
INSERT INTO `words` VALUES (4820, 'mystery', 'ˈmɪstri', 'n.', '神秘的事物;不可理解之事;奥秘;', 'The source of the gunshots still remains a mystery.', '枪炮声来自何处仍是个谜。');
INSERT INTO `words` VALUES (4821, 'mystic', 'ˈmɪstɪk', 'n.', '潜修者;神秘主义者;', '...an Indian mystic known as Bhagwan Shree Rajneesh.', '人称巴格万·什里·劳伊尼希的印度神秘主义者');
INSERT INTO `words` VALUES (4822, 'mystify', 'ˈmɪstɪfaɪ', 'v.', '迷惑;使迷惑不解;使糊涂;', 'The audience must have been totally mystified by the plot...', '观众一定是让剧情完全搞晕了。');
INSERT INTO `words` VALUES (4823, 'myth', 'mɪθ', 'n.', '神话;神话故事;虚构的东西;', 'There is a famous Greek myth in which Icarus flew too near to the Sun.', '有一个著名的希腊神话，讲的是伊卡洛斯飞得离太阳过近的故事。');
INSERT INTO `words` VALUES (4824, 'mythic', 'ˈmɪθɪk', 'adj.', '著名的;神话般的;', '...the mythic figure of King  Arthur.', '亚瑟王这一传奇人物');
INSERT INTO `words` VALUES (4825, 'mythical', 'ˈmɪθɪkl', 'adj.', '神话的;神话里的;并不存在的;', '...the Hydra, the mythical beast that had seven or more heads.', '许德拉，神话里有7个或更多脑袋的怪兽');
INSERT INTO `words` VALUES (4826, 'mythological', 'ˌmɪθəˈlɒdʒɪkl', 'adj.', '神话的;神话学的;', 'In Greek mythology, the god Zeus took the form of a swan to seduce Leda...', '在希腊神话里，主神宙斯化作天鹅引诱了勒达。');
INSERT INTO `words` VALUES (4827, 'mythology', 'mɪˈθɒlədʒi', 'n.', '(统称)神话;某文化(或社会等)的神话;虚幻的想法;', 'In Greek mythology, the god Zeus took the form of a swan to seduce Leda...', '在希腊神话里，主神宙斯化作天鹅引诱了勒达。');
INSERT INTO `words` VALUES (4828, 'nab', 'næb', 'v.', '捉住;当场逮捕;获得;', 'He killed 12 people before the authorities finally nabbed him...', '他杀了12人后才最终被当局抓获。');
INSERT INTO `words` VALUES (4829, 'nadir', 'ˈneɪdɪə(r)', 'n.', '最糟糕的时刻;最低点;', '1945 to 1946 was the nadir of Truman\'s presidency.', '1945至1946年的那段时间是杜鲁门总统执政最艰难的时期。');
INSERT INTO `words` VALUES (4830, 'nag', 'næɡ', 'v.', '唠叨;不停地抱怨;不断困扰;', 'The more Sarah nagged her, the more stubborn Cissie became...', '萨拉越对茜茜唠叨，茜茜越是固执。');
INSERT INTO `words` VALUES (4831, 'nagging', 'ˈnæɡɪŋ', 'adj.', '纠缠不休的;难以摆脱的;唠叨的;', 'He complained of a nagging pain between his shoulder blades.', '他主诉肩胛骨之间有个地方总是疼。');
INSERT INTO `words` VALUES (4832, 'nail', 'neɪl', 'n.', '指甲;趾甲;钉;', 'A mirror hung on a nail above the washstand...', '脸盆架上方用钉子挂着一面镜子。');
INSERT INTO `words` VALUES (4833, 'nails', 'neɪlz', 'n.', '指甲;趾甲;钉;', 'A mirror hung on a nail above the washstand...', '脸盆架上方用钉子挂着一面镜子。');
INSERT INTO `words` VALUES (4834, 'naive', 'naɪˈiːv', 'adj.', '缺乏经验的;幼稚的;无知的;', 'It\'s naive to think that teachers are always tolerant...', '认为老师们总那么宽容是幼稚的。');
INSERT INTO `words` VALUES (4835, 'naively', '', 'adv.', '无邪地；天真烂漫地;', 'It\'s naive to think that teachers are always tolerant...', '认为老师们总那么宽容是幼稚的。');
INSERT INTO `words` VALUES (4836, 'naivety', '', 'n.', '无邪的行为；天真烂漫（等于naivete）;', 'It\'s naive to think that teachers are always tolerant...', '认为老师们总那么宽容是幼稚的。');
INSERT INTO `words` VALUES (4837, 'naked', 'ˈneɪkɪd', 'adj.', '裸体的;裸露的;不穿衣服的;', 'Her naked body was found wrapped in a sheet in a field...', '在田里发现了她的尸体，全身赤裸，裹在一条床单里。');
INSERT INTO `words` VALUES (4838, 'name', 'neɪm', 'n.', '名字;名称;名誉;', '\'What\'s his name?\'—\'Peter.\'...', '“他叫什么？”——“彼得。” ');
INSERT INTO `words` VALUES (4839, 'named', 'neɪmd', 'v.', '命名;给…取名;说出…的名称;', '\'What\'s his name?\'—\'Peter.\'...', '“他叫什么？”——“彼得。” ');
INSERT INTO `words` VALUES (4840, 'name-drop', 'ˈneɪm drɒp', 'v.', '抬出名人显要以提高身价;说出名人名字以抬高自己;', 'The assistant carried on talking to his mate, name-dropping all the famous riders he knew...', '那位助手继续对同伴讲个不停，搬出他认识的所有知名自行车赛手。');
INSERT INTO `words` VALUES (4841, 'nameless', 'ˈneɪmləs', 'adj.', '无名的;不知名的;匿名的;', 'They can have their cases rejected, without reasons being given, by nameless officials.', '他们的诉求可能会被一些不知姓名的官员们莫名其妙地驳回。');
INSERT INTO `words` VALUES (4842, 'namely', 'ˈneɪmli', 'adv.', '即;也就是;', 'One group of people seems to be forgotten, namely pensioners...', '有一类人似乎被遗忘了，就是那些领养老金的人。');
INSERT INTO `words` VALUES (4843, 'names', 'neɪmz', 'n.', '名字;名称;名誉;', '\'What\'s his name?\'—\'Peter.\'...', '“他叫什么？”——“彼得。” ');
INSERT INTO `words` VALUES (4844, 'namesake', 'ˈneɪmseɪk', 'n.', '同名的人(或物);', 'He is putting together a four-man team, including his son and namesake Tony o\'Reilly Jnr...', '他正在组建一个4人小队，包括和自己同名的儿子小托尼·奥赖利。');
INSERT INTO `words` VALUES (4845, 'naming', 'ˈneɪmɪŋ', 'v.', '命名;给…取名;说出…的名称;', '\'What\'s his name?\'—\'Peter.\'...', '“他叫什么？”——“彼得。” ');
INSERT INTO `words` VALUES (4846, 'nan', 'næn , nɑːn', 'n.', '奶奶（小孩儿语）;', 'I was brought up by my nan.', '我是奶奶带大的。');
INSERT INTO `words` VALUES (4847, 'nap', 'næp', 'n.', '(日间的)小睡，打盹;(某些织物表面的)短绒毛;赛马情报;', 'Use your lunch hour to have a nap in your chair...', '利用午饭时间坐在椅子上打个盹吧。');
INSERT INTO `words` VALUES (4848, 'napkin', 'ˈnæpkɪn', 'n.', '餐巾;餐巾纸;', 'She was taking tiny bites of a hot dog and daintily wiping her lips with a napkin.', '她小口地吃着热狗，用餐巾优雅地擦着嘴。');
INSERT INTO `words` VALUES (4849, 'narcissism', 'ˈnɑːsɪsɪzəm', 'n.', '自我陶醉，自赏，自恋(尤指对自己的容貌);', 'Those who suffer from narcissism become self-absorbed or chronic show-offs.', '被自恋症折磨的人会变得只专注于自己的事情，或者不断地自我炫耀。');
INSERT INTO `words` VALUES (4850, 'narcotic', 'nɑːˈkɒtɪk', 'n.', '致幻毒品;麻醉品;麻醉性镇痛药;', 'He was indicted for dealing in narcotics...', '他因从事毒品交易被起诉。');
INSERT INTO `words` VALUES (4851, 'narrate', 'nəˈreɪt', 'v.', '讲(故事);叙述;给(纪录片或节目)作解说;', 'The three of them narrate the same events from three perspectives...', '他们三人从三个不同的视角讲述了同样的事件。');
INSERT INTO `words` VALUES (4852, 'narration', 'nəˈreɪʃn', 'n.', '(尤指小说、电影或戏剧中的)叙述，讲述;(电影、戏剧等中对情节的)解说;旁白;', 'The three of them narrate the same events from three perspectives...', '他们三人从三个不同的视角讲述了同样的事件。');
INSERT INTO `words` VALUES (4853, 'narrative', 'ˈnærətɪv', 'n.', '(尤指小说中的)描述;讲故事;叙述;', '...a fast-moving narrative...', '快节奏的叙事');
INSERT INTO `words` VALUES (4854, 'narrator', 'nəˈreɪtə(r)', 'n.', '(书、戏剧或电影中的)叙述者，讲述者;(电视节目中的)幕后解说员;旁白员;', 'The three of them narrate the same events from three perspectives...', '他们三人从三个不同的视角讲述了同样的事件。');
INSERT INTO `words` VALUES (4855, 'narrow', 'ˈnærəʊ', 'adj.', '狭窄的;窄小的;勉强的;', '...through the town\'s narrow streets...', '穿过该镇狭窄的街道');
INSERT INTO `words` VALUES (4856, 'narrowly', 'ˈnærəʊli', 'adv.', '勉强地;以毫厘之差;狭隘地;', 'He grimaced and looked narrowly at his colleague.', '他做了个鬼脸，然后仔细打量着同事。');
INSERT INTO `words` VALUES (4857, 'narrow-minded', 'ˌnærəʊ ˈmaɪndɪd', 'adj.', '气量小的;小心眼的;狭隘的;', '...a narrow-minded bigot.', '偏执的盲从者');
INSERT INTO `words` VALUES (4858, 'nasal', 'ˈneɪzl', 'adj.', '鼻的;与鼻子相关的;带鼻音的;', '...inflamed nasal passages.', '鼻腔发炎');
INSERT INTO `words` VALUES (4859, 'nascent', 'ˈnæsnt', 'adj.', '新生的;萌芽的;未成熟的;', '...Kenya\'s nascent democracy.', '新生的肯尼亚民主政治');
INSERT INTO `words` VALUES (4860, 'nasty', 'ˈnɑːsti', 'adj.', '极差的;令人厌恶的;令人不悦的;', '...an extremely nasty murder...', '令人发指的谋杀');
INSERT INTO `words` VALUES (4861, 'nation', 'ˈneɪʃn', 'n.', '国家;民族;国民;', 'Such policies would require unprecedented cooperation between nations...', '这样的政策要求国家间进行空前的合作。');
INSERT INTO `words` VALUES (4862, 'national', 'ˈnæʃnəl', 'adj.', '国家的;民族的;全国的;', 'Ruling parties have lost ground in national and local elections.', '执政党在全国和地区选举中均败北。');
INSERT INTO `words` VALUES (4863, 'nationalism', 'ˈnæʃnəlɪzəm', 'n.', '国家主义;民族主义;民族自豪感;', '...the rising tide of Slovak nationalism.', '不断高涨的斯洛伐克民族独立主义浪潮');
INSERT INTO `words` VALUES (4864, 'nationalist', 'ˈnæʃnəlɪst', 'n.', '国家主义者;民族主义者;怀有本民族优越感者;', 'The crisis has set off a wave of nationalist feelings in Quebec.', '这一危机已在魁北克激起了一波民族独立主义情绪。');
INSERT INTO `words` VALUES (4865, 'nationality', 'ˌnæʃəˈnæləti', 'n.', '国籍;(构成国家一部分的)民族;', 'Asked his nationality, he said British...', '被问及国籍时，他说是英国。');
INSERT INTO `words` VALUES (4866, 'nationally', 'ˈnæʃnəli', 'adv.', '全国性地;与某国相关地;', 'Ruling parties have lost ground in national and local elections.', '执政党在全国和地区选举中均败北。');
INSERT INTO `words` VALUES (4867, 'nationwide', 'ˌneɪʃnˈwaɪd', 'adj.', '全国性的;遍及全国的;全国范围的;', 'The rising number of car crimes is a nationwide problem.', '汽车犯罪数量不断飙升是全国性的问题。');
INSERT INTO `words` VALUES (4868, 'native', 'ˈneɪtɪv', 'adj.', '出生地的;儿时居住地的;本地的;', 'It was his first visit to his native country since 1948...', '这是自1948年以来他首次回到自己的祖国。');
INSERT INTO `words` VALUES (4869, 'nativity', 'nəˈtɪvəti', 'n.', '耶稣降生;圣诞;耶稣降生图;', 'They admired the tableau of the Nativity.', '他们欣赏着耶稣降生的场景造型。');
INSERT INTO `words` VALUES (4870, 'natty', 'ˈnæti', 'adj.', '整洁时髦的;设计精妙的;聪明的;', '...a natty pin stripe suit...', '笔挺时尚的细条纹西装');
INSERT INTO `words` VALUES (4871, 'natural', 'ˈnætʃrəl', 'adj.', '自然的;天然的;正常的;', 'It is only natural for youngsters to crave the excitement of driving a fast car...', '年轻人追求开快车的刺激是太正常不过的事情。');
INSERT INTO `words` VALUES (4872, 'naturalistic', 'ˌnætʃrəˈlɪstɪk', 'adj.', '自然主义的;自然主义风格的;写实的;', 'These drawings are among his most naturalistic.', '这几幅画是最能体现他自然主义手法的作品。');
INSERT INTO `words` VALUES (4873, 'naturally', 'ˈnætʃrəli', 'adv.', '顺理成章地;自然地;当然地;', 'When things go wrong, all of us naturally feel disappointed and frustrated...', '出问题时，我们大家自然都感到失望和沮丧。');
INSERT INTO `words` VALUES (4874, 'naturalness', 'ˈnætʃrəlnəs', 'n.', '自然状态;自然;逼真;', 'It is only natural for youngsters to crave the excitement of driving a fast car...', '年轻人追求开快车的刺激是太正常不过的事情。');
INSERT INTO `words` VALUES (4875, 'nature', 'ˈneɪtʃə(r)', 'n.', '自然界;大自然;自然;', 'The most amazing thing about nature is its infinite variety.', '大自然最让人惊叹的是它的无限多样性。');
INSERT INTO `words` VALUES (4876, 'naughty', 'ˈnɔːti', 'adj.', '顽皮的;淘气的;不听话的;', 'Girls, you\'re being very naughty...', '姑娘们，你们真是淘气。');
INSERT INTO `words` VALUES (4877, 'nausea', 'ˈnɔːziə', 'n.', '恶心;作呕;反胃;', 'I was overcome with a feeling of nausea.', '我觉得想吐。');
INSERT INTO `words` VALUES (4878, 'nauseate', 'ˈnɔːzieɪt', 'v.', '使恶心;使作呕;使厌恶;', 'The smell of frying nauseated her...', '油炸的气味让她想吐。');
INSERT INTO `words` VALUES (4879, 'nauseous', 'ˈnɔːziəs', 'adj.', '恶心的;想呕吐的;令人作呕的;', 'If the patient is poorly nourished, the drugs make them feel nauseous...', '如果患者进食欠佳，药物会让他们感觉作呕。');
INSERT INTO `words` VALUES (4880, 'nautical', 'ˈnɔːtɪkl', 'adj.', '航海的;海员的;船舶的;', '...a nautical chart of the region you sail.', '你航行途经地区的航海图');
INSERT INTO `words` VALUES (4881, 'naval', 'ˈneɪvl', 'adj.', '海军的;', 'He was the senior serving naval officer.', '他那时是现役海军高级军官。');
INSERT INTO `words` VALUES (4882, 'navigable', 'ˈnævɪɡəbl', 'adj.', '可航行的;适于通航的;', '...the navigable portion of the Nile.', '尼罗河适合航行的河段');
INSERT INTO `words` VALUES (4883, 'navigate', 'ˈnævɪɡeɪt', 'v.', '导航;确定(船、飞机、汽车等)的位置和方向;航行;', 'Captain Cook safely navigated his ship without accident for 100 voyages...', '库克船长驾驶的船安全出航100次无事故。');
INSERT INTO `words` VALUES (4884, 'navigation', 'ˌnævɪˈɡeɪʃn', 'n.', '导航;领航;航行;', 'Pack ice around Iceland was becoming a threat to navigation.', '冰岛周围的浮冰群曾一度对船只航行构成威胁。');
INSERT INTO `words` VALUES (4885, 'navigational', 'ˌnævɪˈɡeɪʃənl', 'adj.', '航行的;导航的;', 'The crash was a direct result of inadequate navigational aids.', '坠机是导航不力的直接后果。');
INSERT INTO `words` VALUES (4886, 'navigator', 'ˈnævɪɡeɪtə(r)', 'n.', '(飞机、船舶等上的)航行者，航海者，驾驶员，领航员;', 'He became an RAF navigator during the war.', '他在战争期间成为皇家空军的领航员。');
INSERT INTO `words` VALUES (4887, 'navy', 'ˈneɪvi', 'n.', '海军;海军部队;', 'The government announced an order for three Type 23 frigates for the Royal Navy yesterday...', '政府昨天宣布为皇家海军订购3艘23型护卫舰。');
INSERT INTO `words` VALUES (4888, 'nay', 'neɪ', 'adv.', '(强调刚提及之事)不仅如此，而且;不;', 'He was grateful for and proud of his son\'s remarkable, nay, unique performance...', '他为儿子出色的，不，应该是独一无二的表演心怀感激和骄傲。');
INSERT INTO `words` VALUES (4889, 'near', 'nɪə(r)', 'adj.', '距离近;不远;不久以后;', 'Don\'t come near me...', '别靠近我。');
INSERT INTO `words` VALUES (4890, 'nearby', 'ˌnɪəˈbaɪ', 'adj.', '附近的;邻近的;', 'He might easily have been seen by someone who lived nearby...', '住在附近的人很可能有谁见过他。');
INSERT INTO `words` VALUES (4891, 'nearest', 'ˈnɪərɪst', 'adv.', '距离不远;在附近;不久以后;', 'Don\'t come near me...', '别靠近我。');
INSERT INTO `words` VALUES (4892, 'nearly', 'ˈnɪəli', 'adv.', '几乎;差不多;将近;', 'Goldsworth stared at me in silence for nearly twenty seconds...', '将近20秒的时间，戈兹沃思就那么一声不响地盯着我看。');
INSERT INTO `words` VALUES (4893, 'nearsighted', 'ˌnɪəˈsaɪtɪd', 'adj.', '近视的;', 'The girl squinted at the photograph. She seemed to be nearsighted.', '女孩眯起眼睛看照片。她好像近视。');
INSERT INTO `words` VALUES (4894, 'neat', 'niːt', 'adj.', '整洁的;整齐的;有序的;', 'So they left her in the neat little house, alone with her memories...', '于是他们把她一个人留在整洁的小房子里，只有记忆陪着她。');
INSERT INTO `words` VALUES (4895, 'neatly', 'ni:tlɪ', 'adv.', '整洁地；干净地;灵巧地；利索地;恰好地；极好地;', 'So they left her in the neat little house, alone with her memories...', '于是他们把她一个人留在整洁的小房子里，只有记忆陪着她。');
INSERT INTO `words` VALUES (4896, 'neatness', '', 'n.', '整洁；干净;', 'So they left her in the neat little house, alone with her memories...', '于是他们把她一个人留在整洁的小房子里，只有记忆陪着她。');
INSERT INTO `words` VALUES (4897, 'nebula', 'ˈnebjələ', 'n.', '星云;外观似星云的星系;', '...the Great Nebula.', '猎户大星云');
INSERT INTO `words` VALUES (4898, 'nebulae', 'ˈnɛbjʊliː', 'n.', '星云;外观似星云的星系;', '...the Great Nebula.', '猎户大星云');
INSERT INTO `words` VALUES (4899, 'nebulous', 'ˈnebjələs', 'adj.', '模糊的;不清楚的;', 'The notions we children were able to form of the great world beyond were exceedingly nebulous...', '我们这些孩子所能形成的对外部世界的认识是非常模糊的。');
INSERT INTO `words` VALUES (4900, 'necessaries', 'ˈnesəsəriz', 'n.', '必需品;(尤指)生活必需品;', 'I kept the engine running because it might be necessary to leave fast...', '我没让发动机熄火，因为也许需要迅速离开。');
INSERT INTO `words` VALUES (4901, 'necessarily', 'ˌnesəˈserəli', 'adv.', '必然地;不可避免地;', 'Anger is not necessarily the most useful or acceptable reaction to such events...', '对于这种事，发火不见得是最有用或最能被接受的反应。');
INSERT INTO `words` VALUES (4902, 'necessary', 'ˈnesəsəri', 'adj.', '必需的;必要的;必然的;', 'I kept the engine running because it might be necessary to leave fast...', '我没让发动机熄火，因为也许需要迅速离开。');
INSERT INTO `words` VALUES (4903, 'necessitate', 'nəˈsesɪteɪt', 'v.', '使成为必要;', 'A prolonged drought had necessitated the introduction of water rationing...', '由于持续干旱，用水需要实行配给了。');
INSERT INTO `words` VALUES (4904, 'necessity', 'nəˈsesəti', 'n.', '必然;必要;需要;', 'There is agreement on the necessity of reforms...', '对改革的必要性已经达成了共识。');
INSERT INTO `words` VALUES (4905, 'neck', 'nek', 'n.', '颈;脖子;衣领;', 'She threw her arms round his neck and hugged him warmly...', '她伸出双臂搂住他的脖子，热情地拥抱他。');
INSERT INTO `words` VALUES (4906, 'necklace', 'ˈnekləs', 'n.', '项链;', '...a diamond necklace and matching earrings.', '钻石项链和与之相配的耳环');
INSERT INTO `words` VALUES (4907, 'necropolis', 'nəˈkrɒpəlɪs', 'n.', '墓场;(尤指古代城市的)大墓地;', '...a small Etruscan museum and necropolis 3 km east of the village.', '村庄以东3公里外的小型伊特鲁里亚博物馆和墓葬群');
INSERT INTO `words` VALUES (4908, 'necrosis', 'neˈkrəʊsɪs', 'n.', '(器官或组织细胞的)坏死;', '...liver necrosis.', '肝坏死');
INSERT INTO `words` VALUES (4909, 'needful', 'ˈniːdfl', 'adj.', '必要的;必需的;必然的;', 'The section of society most needful of such guidance is the young male.', '最需要这种指导的社会群体是青年男性。');
INSERT INTO `words` VALUES (4910, 'needless', 'ˈniːdləs', 'adj.', '不必要的;可以避免的;', 'But his death was so needless...', '但他的死太没有必要了。');
INSERT INTO `words` VALUES (4911, 'needlessly', '', 'adv.', '不需要地，不必要地;', 'But his death was so needless...', '但他的死太没有必要了。');
INSERT INTO `words` VALUES (4912, 'needlework', 'ˈniːdlwɜːk', 'n.', '缝制品;刺绣品;女红;', 'She did beautiful needlework and she embroidered table napkins.', '她的针线活很漂亮，并且还会刺绣台布。');
INSERT INTO `words` VALUES (4913, 'needy', 'ˈniːdi', 'adj.', '缺乏生活必需品的;贫困的;穷困的人;', '...a multinational force aimed at ensuring that food and medicine get to needy Somalis.', '旨在确保将食物和药品送至贫困的索马里民众手中的多国部队');
INSERT INTO `words` VALUES (4914, 'nefarious', 'nɪˈfeəriəs', 'adj.', '罪恶的;不道德的;', 'Why make a whole village prisoner if it was not to some nefarious purpose?', '如果不是出于某种恶毒的目的，为什么把整村人囚禁起来？');
INSERT INTO `words` VALUES (4915, 'negate', 'nɪˈɡeɪt', 'v.', '取消;使无效;否定;', 'These weaknesses negated his otherwise progressive attitude towards the staff.', '这些缺点抵消了他在其他方面对待员工还算开明的态度。');
INSERT INTO `words` VALUES (4916, 'negation', 'nɪˈɡeɪʃn', 'n.', '反面;对立面;否定;', 'Unintelligible legislation is the negation of the rule of law and of parliamentary democracy.', '晦涩的法律法规有悖于法治和议会民主。');
INSERT INTO `words` VALUES (4917, 'negative', 'ˈneɡətɪv', 'adj.', '坏的;有害的;消极的;', 'The news from overseas is overwhelmingly negative...', '来自海外的消息特别不容乐观。');
INSERT INTO `words` VALUES (4918, 'negatively', '', 'adv.', '否定地，消极地;负（阴性）地;', 'The news from overseas is overwhelmingly negative...', '来自海外的消息特别不容乐观。');
INSERT INTO `words` VALUES (4919, 'neglect', 'nɪˈɡlekt', 'v.', '疏于照顾;未予看管;忽略;', 'The woman denied that she had neglected her child...', '那位女士否认疏于照管自己的孩子。');
INSERT INTO `words` VALUES (4920, 'neglected', 'nɪˈɡlektɪd', 'adj.', '被忽略的;被忽视的;未被重视的;', 'The woman denied that she had neglected her child...', '那位女士否认疏于照管自己的孩子。');
INSERT INTO `words` VALUES (4921, 'neglectful', 'nɪˈɡlektfl', 'adj.', '马虎的;不重视的;忽视的;', '...neglectful parents.', '漫不经心的父母');
INSERT INTO `words` VALUES (4922, 'negligence', 'ˈneɡlɪdʒəns', 'n.', '疏忽;失职;失误;', 'The soldiers were ordered to appear before a disciplinary council on charges of negligence.', '士兵们被控玩忽职守，被勒令接受纪律委员会的审查。');
INSERT INTO `words` VALUES (4923, 'negligent', 'ˈneɡlɪdʒənt', 'adj.', '疏忽的;造成过失的;放松的;', 'The jury determined that the airline was negligent in training and supervising the crew...', '陪审团裁定航空公司在对全体机组人员的培训和管理问题上存在疏漏。');
INSERT INTO `words` VALUES (4924, 'negligible', 'ˈneɡlɪdʒəbl', 'adj.', '微不足道的;不重要的;不值一提的;', 'The pay that the soldiers received was negligible...', '士兵拿到的军饷少得可怜。');
INSERT INTO `words` VALUES (4925, 'negotiable', 'nɪˈɡəʊʃiəbl', 'adj.', '可协商的;可讨论的;流通的;', 'He warned that his economic programme for the country was not negotiable...', '他提醒说，他为这个国家制定的经济计划没有商量的余地。');
INSERT INTO `words` VALUES (4926, 'negotiate', 'nɪˈɡəʊʃieɪt', 'v.', '谈判;磋商;协商;', 'It is not clear whether the president is willing to negotiate with the democrats...', '还不清楚总统是否愿意和民主人士洽谈。');
INSERT INTO `words` VALUES (4927, 'negotiation', 'nɪˌɡəʊʃiˈeɪʃn', 'n.', '谈判;磋商;协商;', 'We have had meaningful negotiations and I believe we are very close to a deal...', '我们已进行了有益的磋商，我相信我们很快能成交。');
INSERT INTO `words` VALUES (4928, 'negotiator', 'nɪˈɡəʊʃieɪtə(r)', 'n.', '谈判代表;协商者;', '...the rebels\' chief negotiator at the peace talks...', '和谈中叛乱分子的首席谈判代表');
INSERT INTO `words` VALUES (4929, 'negroid', '', 'n.', '黑种人;', '...negroid hair.', '黑人的头发');
INSERT INTO `words` VALUES (4930, 'neigh', 'neɪ', 'v.', '(马)嘶鸣;', 'The mare neighed once more, turned and disappeared amongst the trees.', '母马又嘶鸣了一声，扭头消失在树林里。');
INSERT INTO `words` VALUES (4931, 'neither', 'ˈnaɪðə(r)', 'det./pron.', '两者都不;', 'Professor Hisamatsu spoke neither English nor German...', '久松教授既不会讲英语，也不会讲德语。');
INSERT INTO `words` VALUES (4932, 'nemesis', 'ˈneməsɪs', 'n.', '报应;应得的惩罚;不可避免的失败;', 'He believes AIDS is our collective nemesis...', '他认为艾滋病是对我们所有人的惩罚。');
INSERT INTO `words` VALUES (4933, 'neolithic', 'ˌniːəˈlɪθɪk', 'adj.', '新石器时代的;', '...neolithic culture.', '新石器时代文化');
INSERT INTO `words` VALUES (4934, 'neologism', 'niˈɒlədʒɪzəm', 'n.', '新词;新语汇;新义;', 'The newspaper used the neologism \'dinks\', Double Income No Kids.', '报纸使用了新词“丁克族”，即“双收入无子女”。');
INSERT INTO `words` VALUES (4935, 'neon', 'ˈniːɒn', 'n.', '氖;氖气;', 'In the city squares the neon lights flashed in turn.', '在城市广场上，霓虹灯依次闪烁。');
INSERT INTO `words` VALUES (4936, 'neophyte', 'ˈniːəfaɪt', 'n.', '初学者;新手;生手;', '...the self-proclaimed political neophyte Ross Perot.', '自称政治新人的罗斯·佩罗');
INSERT INTO `words` VALUES (4937, 'nephew', 'ˈnefjuː', 'n.', '侄子;外甥;', 'I am planning a 25th birthday party for my nephew.', '我在为侄子筹划他25岁的生日聚会。');
INSERT INTO `words` VALUES (4938, 'nepotism', 'ˈnepətɪzəm', 'n.', '裙带关系;任人唯亲;', 'Many will regard his appointment as the kind of nepotism British banking ought to avoid.', '很多人会把他的任命看作是英国银行业应该避免的一种裙带关系。');
INSERT INTO `words` VALUES (4939, 'nerd', 'nɜːd', 'n.', '令人厌烦的人;愚蠢的人;落伍的人;', 'Mark claimed he was made to look a nerd.', '马克称自己被搞得像个傻瓜。');
INSERT INTO `words` VALUES (4940, 'nerve', 'nɜːv', 'n.', '神经;神经质;神经紧张;', '...spinal nerves.', '脊神经');
INSERT INTO `words` VALUES (4941, 'nerve-racking', 'ˈnɜːv rækɪŋ', 'adj.', '令人十分紧张的;令人焦虑不安的;', 'The women and children spent a nerve-racking day outside waiting while fighting continued around them...', '女人和孩子们在外面紧张不安地挨过了一天，而战斗仍在她们身边继续。');
INSERT INTO `words` VALUES (4942, 'nerves', 'nɜːvz', 'n.', '神经;神经质;神经紧张;', '...spinal nerves.', '脊神经');
INSERT INTO `words` VALUES (4943, 'nervous', 'ˈnɜːvəs', 'adj.', '焦虑的;担忧的;惶恐的;', 'The party has become deeply nervous about its prospects of winning the next election...', '该党对赢得下一次选举的前景深感忧虑。');
INSERT INTO `words` VALUES (4944, 'nervously', '', 'adv.', '神经质地；焦急地；提心吊胆地;', 'The party has become deeply nervous about its prospects of winning the next election...', '该党对赢得下一次选举的前景深感忧虑。');
INSERT INTO `words` VALUES (4945, 'nervousness', '', 'n.', '神经质，焦躁，胆小;', 'The party has become deeply nervous about its prospects of winning the next election...', '该党对赢得下一次选举的前景深感忧虑。');
INSERT INTO `words` VALUES (4946, 'nervy', 'ˈnɜːvi', 'adj.', '焦虑的;紧张的;莽撞的;', 'Sometimes dad was nice to us, but sometimes he was bad-tempered and nervy...', '爸爸有时对我们很好，但有时他脾气暴躁，焦虑不安。');
INSERT INTO `words` VALUES (4947, 'nest', 'nest', 'n.', '鸟巢;鸟窝;巢穴;', 'I can see an eagle\'s nest on the rocks.', '我能看到岩石上的鹰巢。');
INSERT INTO `words` VALUES (4948, 'nested', 'ˈnestɪd', 'v.', '筑巢;巢居;嵌套(信息);', 'I can see an eagle\'s nest on the rocks.', '我能看到岩石上的鹰巢。');
INSERT INTO `words` VALUES (4949, 'nestle', 'ˈnesl', 'v.', '依偎;舒适地坐(或卧);抱;', 'John took one child into the crook of each arm and let them nestle against him...', '约翰一只臂弯搂着一个孩子，让他们舒服地靠着他。');
INSERT INTO `words` VALUES (4950, 'nestling', 'ˈneslɪŋ , ˈnestlɪŋ', 'n.', '雏鸟;未离巢的小鸟;', 'She fluttered around me like a mother bird at her nestlings.', '她在我周围忙来忙去，像鸟妈妈对鸟宝宝一样。');
INSERT INTO `words` VALUES (4951, 'nether', 'ˈneðə(r)', 'adj.', '较低的;下方的;', 'He was escorted back to the nether regions of Main Street.', '他被护送回中央大道南面的地方。');
INSERT INTO `words` VALUES (4952, 'nettle', 'ˈnetl', 'n.', '荨麻;', 'The nettles stung their legs.', '荨麻扎了他们的腿。');
INSERT INTO `words` VALUES (4953, 'network', 'ˈnetwɜːk', 'n.', '网络;网状系统;关系网;', '...Strasbourg, with its rambling network of medieval streets...', '中世纪街道纵横交织的斯特拉斯堡');
INSERT INTO `words` VALUES (4954, 'networking', 'ˈnetwɜːkɪŋ', 'n.', '人际关系网;', 'If executives fail to exploit the opportunities of networking they risk being left behind.', '如果高管们未能抓住机会建立关系网，他们就有落后的风险。');
INSERT INTO `words` VALUES (4955, 'neural', 'ˈnjʊərəl', 'adj.', '神经的;神经系统的;', '...neural pathways in the brain.', '大脑里的神经通路');
INSERT INTO `words` VALUES (4956, 'neuron', 'ˈnjʊərɒn', 'n.', '神经元;', 'Information is transferred along each neuron by means of an electrical impulse.', '信息在电脉冲作用下经神经元传递。');
INSERT INTO `words` VALUES (4957, 'neurosis', 'njʊəˈrəʊsɪs', 'n.', '神经(官能)症;过分的恐惧(或焦虑);', 'He was anxious to the point of neurosis...', '他焦虑得都要得神经症了。');
INSERT INTO `words` VALUES (4958, 'neurotic', 'njʊəˈrɒtɪk', 'adj.', '神经(官能)症的;神经质的;神经过敏的;', 'He was almost neurotic about being followed...', '他对被跟踪几乎到了神经过敏的程度。');
INSERT INTO `words` VALUES (4959, 'neutral', 'ˈnjuːtrəl', 'adj.', '中立的;持平的;无倾向性的;', 'Let\'s meet on neutral territory...', '我们在中立地区会面吧。');
INSERT INTO `words` VALUES (4960, 'neutrality', 'njuːˈtræləti', 'n.', '中立;中立状态;', 'Let\'s meet on neutral territory...', '我们在中立地区会面吧。');
INSERT INTO `words` VALUES (4961, 'neutron', 'ˈnjuːtrɒn', 'n.', '中子;', 'Each atomic cluster is made up of neutrons and protons.', '原子簇是由中子和质子构成的。');
INSERT INTO `words` VALUES (4962, 'never', 'ˈnevə(r)', 'adv.', '从不;绝不;从未;', 'I have never lost the weight I put on in my teens...', '我十几岁时增加的体重一直没有减下去。');
INSERT INTO `words` VALUES (4963, 'nevertheless', 'ˌnevəðəˈles', 'adv.', '尽管如此;不过;然而;', 'Most marriages fail after between five and nine years. Nevertheless, people continue to get married.', '大部分婚姻在婚后第五至第九年间失败，然而，人们仍会选择结婚。');
INSERT INTO `words` VALUES (4964, 'new', 'njuː', 'adj.', '刚出现的;新的;新近推出的;', 'They\'ve just opened a new hotel in the Stoke area...', '他们刚在斯托克地区开了一家新宾馆。');
INSERT INTO `words` VALUES (4965, 'newborn', 'ˈnjuːbɔːn', 'adj.', '新生的;初生的;', 'This equipment has saved the lives of a number of new born children.', '这套设备已经挽救了许多新生儿的生命。');
INSERT INTO `words` VALUES (4966, 'newcomer', 'ˈnjuːkʌmə(r)', 'n.', '新来者;新手;', 'He must be a newcomer to town and he obviously didn\'t understand our local customs...', '他一定是刚到镇上来的，明显不了解我们这儿的风俗。');
INSERT INTO `words` VALUES (4967, 'newly', 'ˈnjuːli', 'adv.', '最近;新近;', 'She was young at the time, and newly married.', '她那时年龄不大，刚结婚。');
INSERT INTO `words` VALUES (4968, 'news', 'njuːz', 'n.', '消息;音信;媒体对重要事情的报道;', 'We waited and waited for news of him...', '我们左等右等，盼着他的消息。');
INSERT INTO `words` VALUES (4969, 'newsletter', 'ˈnjuːzletə(r)', 'n.', '(某组织的)内部通讯，简讯;', 'The organization now has around 18,000 members who receive a quarterly newsletter.', '该组织现有大约18,000名成员，他们会收到一份内部通讯季刊。');
INSERT INTO `words` VALUES (4970, 'newspaper', 'ˈnjuːzpeɪpə(r)', 'n.', '报纸;报;旧报纸;', 'He was carrying a newspaper...', '他拿着一份报纸。');
INSERT INTO `words` VALUES (4971, 'next', 'nekst', 'adj.', '下一个的;紧接着的;接下来的;', 'I got up early the next morning.', '第二天早晨我早早地起床了。');
INSERT INTO `words` VALUES (4972, 'nibble', 'ˈnɪbl', 'v.', '小口咬，一点点地咬(食物);(对…)略微表现出兴趣;', 'He started to nibble his biscuit...', '他开始啃饼干了。');
INSERT INTO `words` VALUES (4973, 'nice', 'naɪs', 'adj.', '令人愉快的;宜人的;吸引人的;', 'I think silk ties can be quite nice...', '我认为丝绸领带相当不错。');
INSERT INTO `words` VALUES (4974, 'nicely', 'ˈnaɪsli', 'adv.', '有吸引力;令人满意;令人愉快;', 'She has a bit of private money, so they manage quite nicely...', '她有一点私房钱，所以他们过得很舒心。');
INSERT INTO `words` VALUES (4975, 'nicety', 'ˈnaɪsəti', 'n.', '细节;细微的差别;精确;', 'By the end of term, girls will have learnt the niceties of dinner party conversation...', '到学期结束，女生们将完成对晚宴谈话礼仪细节的学习。');
INSERT INTO `words` VALUES (4976, 'niche', 'niːʃ', 'n.', '舒适或称心的工作(或生活等);(产品的)商机;市场定位;', 'I think we have found a niche in the toy market...', '我想我们已经在玩具市场找到了一个商机。');
INSERT INTO `words` VALUES (4977, 'nickname', 'ˈnɪkneɪm', 'n.', '绰号;诨名;外号;', 'Red got his nickname for his red hair.', '“红毛”的绰号源于他的红头发。');
INSERT INTO `words` VALUES (4978, 'nicotine', 'ˈnɪkətiːn', 'n.', '尼古丁;烟碱;', 'Nicotine produces a feeling of well-being in the smoker...', '尼古丁给吸烟者带来一种愉悦的感觉。');
INSERT INTO `words` VALUES (4979, 'niece', 'niːs', 'n.', '侄女;甥女;', '...his niece from America, the daughter of his eldest sister.', '他从美国来的外甥女，他大姐的女儿');
INSERT INTO `words` VALUES (4980, 'nigerian', 'naɪˈdʒɪəriən', 'n.', '尼日利亚人;尼日利亚裔;', '...the Nigerian government.', '尼日利亚政府');
INSERT INTO `words` VALUES (4981, 'niggardly', 'ˈnɪɡədli', 'adj.', '吝啬的;小气的;不大度的;', 'Officials say the EU, which is supposed to provide most of the food needs, is being particularly niggardly.', '官员们说应该提供所需的大部分食品的欧盟现在特别小气。');
INSERT INTO `words` VALUES (4982, 'niggle', 'ˈnɪɡl', 'n.', '轻微的批评;小牢骚;一丝挥不去的烦恼(或疑虑等);', 'I realise now that the things which used to niggle and annoy me just don\'t really matter...', '我现在意识到那些曾令我烦恼不快的事情其实无关紧要。');
INSERT INTO `words` VALUES (4983, 'niggling', 'ˈnɪɡlɪŋ', 'adj.', '(不严重却不断)烦人的，疼痛的;不重要的;微不足道的;', 'Both players have been suffering from niggling injuries.', '两名运动员一直在忍受轻微的伤痛。');
INSERT INTO `words` VALUES (4984, 'nigh', 'naɪ', 'adv.', '几乎;差不多;靠近;', 'The end of the world may be nigh, but do we really care?...', '世界末日可能不远了，但我们真的在意吗？');
INSERT INTO `words` VALUES (4985, 'night', 'naɪt', 'n.', '夜;夜晚;晚上，夜晚(夜里就寝前的一段时间);', 'He didn\'t sleep a wink all night...', '他一夜没合眼。');
INSERT INTO `words` VALUES (4986, 'nightfall', 'ˈnaɪtfɔːl', 'n.', '黄昏;傍晚;', 'I need to get to Lyon by nightfall...', '我需要在黄昏前到达里昂。');
INSERT INTO `words` VALUES (4987, 'nightlife', 'ˈnaɪtlaɪf', 'n.', '夜生活;', '...Hamburg\'s energetic nightlife...', '汉堡活跃的夜生活');
INSERT INTO `words` VALUES (4988, 'nightly', 'ˈnaɪtli', 'adj./adv.', '每夜的;每晚的;', 'I\'m sure we watched the nightly news, and then we turned on the movie...', '我肯定我们是先看了晚间新闻，然后才开始看的电影。');
INSERT INTO `words` VALUES (4989, 'nightmare', 'ˈnaɪtmeə(r)', 'n.', '噩梦;梦魇;可怕的经历;', 'All the victims still suffered nightmares...', '所有受害者仍然被恶梦困扰。');
INSERT INTO `words` VALUES (4990, 'nighttime', 'ˈnaɪttaɪm', 'n.', '黑夜；夜间;', 'They wanted someone responsible to look after the place at night-time...', '他们想要个有责任心的人夜间照管这个地方。');
INSERT INTO `words` VALUES (4991, 'nihilism', 'ˈnaɪɪlɪzəm', 'n.', '虚无主义;', 'Why should a great community like a university be afraid of nihilism?', '为什么像大学这样人才济济的社区要害怕虚无主义呢？');
INSERT INTO `words` VALUES (4992, 'nil', 'nɪl', 'n.', '(数码)零;(体育比赛中的)0分;无;', 'They beat Argentina one-nil in the final.', '他们在决赛中以 1 比 0 击败了阿根廷队。');
INSERT INTO `words` VALUES (4993, 'nimble', 'ˈnɪmbl', 'adj.', '灵活的;敏捷的;思路敏捷的;', 'Everything had been stitched by Molly\'s nimble fingers...', '每一件东西都是莫莉灵巧的双手缝制出来的。');
INSERT INTO `words` VALUES (4994, 'nine', 'naɪn', 'num.', '九;', 'We still sighted nine yachts.', '我们还是看见了9艘游艇。');
INSERT INTO `words` VALUES (4995, 'nineteen', 'ˌnaɪnˈtiːn', 'num.', '十九;', 'They have nineteen days to make up their minds.', '他们有19天时间去作决定。');
INSERT INTO `words` VALUES (4996, 'nineteenth', 'ˌnaɪnˈtiːnθ', 'ord.', '第十九;', '...my nineteenth birthday.', '我的19岁生日');
INSERT INTO `words` VALUES (4997, 'ninetieth', 'ˈnaɪntiəθ', 'ord.', '第九十;', 'He celebrates his ninetieth birthday on Friday.', '他周五过90大寿。');
INSERT INTO `words` VALUES (4998, 'ninety', 'ˈnaɪnti', 'n.', '九十九十;九十年代;九十岁;', 'It was decided she had to stay another ninety days.', '已经决定了，她不得不再呆90天。');
INSERT INTO `words` VALUES (4999, 'ninth', 'naɪnθ', 'ord.', '第九;', '...January the ninth.', '1月9日');
INSERT INTO `words` VALUES (5000, 'nip', 'nɪp', 'v.', '啃咬;掐;咬住;', 'Should I nip out and get some groceries?...', '要我赶紧出去一趟买些食品吗？');
INSERT INTO `words` VALUES (5001, 'nipping', 'ˈnɪpɪŋ', 'adj.', '锋利的；刺骨的；尖酸刻薄的;', 'Should I nip out and get some groceries?...', '要我赶紧出去一趟买些食品吗？');
INSERT INTO `words` VALUES (5002, 'nipple', 'ˈnɪpl', 'n.', '乳头;乳头状物品;(机器的)喷嘴;', 'Sore nipples can inhibit the milk supply.', '乳头发炎会抑制出奶。');
INSERT INTO `words` VALUES (5003, 'nippy', 'ˈnɪpi', 'adj.', '灵巧的;敏捷的;冷的;', 'It could get suddenly nippy in the evenings.', '晚上会突然变得很冷。');
INSERT INTO `words` VALUES (5004, 'nirvana', 'nɪəˈvɑːnə', 'n.', '湼盘(超脱一切烦恼的境界);', 'Entering the realm of Nirvana is only possible for those who have become pure.', '心无杂念，方可入灭。');
INSERT INTO `words` VALUES (5005, 'nitrate', 'ˈnaɪtreɪt', 'n.', '硝酸盐;硝酸盐类化肥;', 'High levels of nitrate occur in eastern England because of the heavy use of fertilizers.', '英格兰东部因大量使用化肥导致土壤硝酸盐浓度过高。');
INSERT INTO `words` VALUES (5006, 'nitrous', 'ˈnaɪtrəs', 'adj.', '源自氮的;含氮的;与氮有关的;', '...nitrous oxides.', '氮氧化物');
INSERT INTO `words` VALUES (5007, 'no', 'nəʊ , nəʊz', 'int.', '(用于否定的回答或陈述)不;没有;不是;', '\'Any problems?\' — \'No, I\'m O.K.\'...', '“有问题吗？”——“没有，我挺好的。”');
INSERT INTO `words` VALUES (5008, 'nobility', 'nəʊˈbɪləti', 'n.', '贵族;高贵的品质;', 'They married into the nobility and entered the highest ranks of state administration...', '他们同贵族阶层联姻，进入了国家最高行政层。');
INSERT INTO `words` VALUES (5009, 'noble', 'ˈnəʊbl', 'adj.', '崇高的;品质高尚的;宏伟的;', 'He was an upright and noble man who was always willing to help in any way he could...', '他是一个正直高尚的人，总是愿意尽其所能帮助他人。');
INSERT INTO `words` VALUES (5010, 'nobody', 'ˈnəʊbədi', 'pron.', '没有人，无人，谁也不;', 'They were shut away in a little room where nobody could overhear...', '他们被关在一个小屋里，没人能听到他们的动静。');
INSERT INTO `words` VALUES (5011, 'nod', 'nɒd', 'v.', '点头;点头致意;点头示意;', '\'Are you okay?\' I asked. She nodded and smiled...', '“你没事吧?”我问她。她点点头，笑了。');
INSERT INTO `words` VALUES (5012, 'node', 'nəʊd', 'n.', '茎节;(根或枝上的)瘤，节，结;结点;', 'Cut them off cleanly through the stem just below the node.', '就从茎节下面，沿茎干把它们全砍掉。');
INSERT INTO `words` VALUES (5013, 'noise', 'nɔɪz', 'n.', '声音;响声;噪音;', 'There was too much noise in the room and he needed peace...', '屋子里太吵闹，他需要安静。');
INSERT INTO `words` VALUES (5014, 'noisy', 'ˈnɔɪzi', 'adj.', '吵闹的;聒噪的;嘈杂的;', '...my noisy old typewriter...', '我那台噪音极大的旧打字机');
INSERT INTO `words` VALUES (5015, 'nomenclature', 'nəˈmenklətʃə(r)', 'n.', '(尤指某学科的)命名法;', '...mistakes arising from ignorance of the nomenclature of woody plants.', '因不了解木本植物命名法而产生的错误');
INSERT INTO `words` VALUES (5016, 'nominal', 'ˈnɒmɪnl', 'adj.', '名义上的;有名无实的;很小的;', 'As he was still not allowed to run a company, his wife became its nominal head...', '因为他仍未获准经营公司，他妻子成了名义上的老板。');
INSERT INTO `words` VALUES (5017, 'nominate', 'ˈnɒmɪneɪt', 'v.', '提名;推荐;任命;', 'Under party rules each candidate has to be nominated by 55 Labour MPs...', '根据党章，每个候选人必须得到55名工党下院议员的提名。');
INSERT INTO `words` VALUES (5018, 'nomination', 'ˌnɒmɪˈneɪʃn', 'n.', '提名;推荐;任命;', '...his candidacy for the Republican presidential nomination.', '他参加共和党总统提名角逐的候选资格');
INSERT INTO `words` VALUES (5019, 'nominee', 'ˌnɒmɪˈniː', 'n.', '被提名人;被任命者;(投资等的)名义持有人;', 'His nominee for vice president was elected only after a second ballot...', '他提名的副总统在两轮投票后才当选。');
INSERT INTO `words` VALUES (5020, 'none', 'nʌn', 'pron.', '没有一个;毫无;', 'She did none of the maintenance on the vehicle itself...', '她对车子本身没有进行半点养护。');
INSERT INTO `words` VALUES (5021, 'nonetheless', 'ˌnʌnðəˈles', 'adv.', '尽管如此;', 'There was still a long way to go. Nonetheless, some progress had been made...', '路还很长，不过已经取得了一些进展。');
INSERT INTO `words` VALUES (5022, 'nonsense', 'ˈnɒnsns', 'n.', '谬论;胡扯;胡言乱语;', 'Most orthodox doctors however dismiss this as complete nonsense.', '但大多数传统的医生认为此说法完全是胡说八道。');
INSERT INTO `words` VALUES (5023, 'nonsmoker', 'ˈnɒnˈsməʊkə', 'n.', '不吸烟的人;', 'Nobody will be allowed to smoke in an office if there are non-smokers present.', '如果有不吸烟的人在场，任何人不得在办公室吸烟。');
INSERT INTO `words` VALUES (5024, 'noon', 'nuːn', 'n.', '正午;中午;', 'The long day of meetings started at noon...', '会议接连不断的漫长一天从中午开始。');
INSERT INTO `words` VALUES (5025, 'nor', 'nɔː(r)', 'conj./adv.', '也不;(用于肯定动词前，表示同意刚提及的否定命题)也不;', 'Neither Mr Rose nor Mr Woodhead was available for comment yesterday...', '昨天罗斯先生和伍德黑德先生都无暇发表意见。');
INSERT INTO `words` VALUES (5026, 'norm', 'nɔːm', 'n.', '常态;正常行为;规范;', '...the commonly accepted norms of democracy.', '被人们普遍接受的民主准则');
INSERT INTO `words` VALUES (5027, 'normal', 'ˈnɔːml', 'adj.', '典型的;正常的;一般的;', 'He has occasional injections to maintain his good health but otherwise he lives a normal life...', '他偶尔要注射药物保持身体健康，但除此之外他生活一切正常。');
INSERT INTO `words` VALUES (5028, 'normally', 'ˈnɔːməli', 'adv.', '通常;正常情况下;正常地;', 'All airports in the country are working normally today...', '今天国内所有机场都运转正常。');
INSERT INTO `words` VALUES (5029, 'north', 'nɔːθ', 'n.', '北;北方;北部;', 'In the north the ground becomes very cold as the winter snow and ice covers the ground...', '冬天冰雪覆盖大地，北方的地面变得非常寒冷。');
INSERT INTO `words` VALUES (5030, 'northeast', 'ˌnɔːθˈiːst', 'adv.', '向东北;在东北;', 'The land to the north-east fell away into meadows.', '陆地向东北方向倾斜延伸成了牧场。');
INSERT INTO `words` VALUES (5031, 'northern', 'ˈnɔːðən', 'adj.', '北方的;向北的;北部的;', 'Their two children were immigrants to Northern Ireland from Pennsylvania...', '他们的两个孩子是从宾夕法尼亚州移民到北爱尔兰的。');
INSERT INTO `words` VALUES (5032, 'northward', '', 'n.', '北方，北部地区;', 'Tropical storm Marco is pushing northward up Florida\'s coast.', '热带风暴“马可”正沿佛罗里达海岸向北推进。');
INSERT INTO `words` VALUES (5033, 'northwest', 'ˌnɔːθˈwɛst , nɔːˈwɛst', 'adv.', '向西北;在西北;', '...Ushant, five miles out to the north-west.', '西北方向5英里外的阿申特岛');
INSERT INTO `words` VALUES (5034, 'norwegian', 'nɔːˈwiːdʒən', 'n.', '挪威人;挪威语;', 'The main road from Murmansk to the Norwegian border is still closed to foreigners...', '从摩尔曼斯克到挪威边界的主路仍对外国人关闭。');
INSERT INTO `words` VALUES (5035, 'nose', 'nəʊz', 'n.', '鼻;鼻子;有…鼻子的;', 'She wiped her nose with a tissue...', '她用纸巾擦了擦鼻子。');
INSERT INTO `words` VALUES (5036, 'nostalgia', 'nɒˈstældʒə', 'n.', '怀旧;念旧;', 'He might be influenced by nostalgia for his happy youth...', '也许是对年轻时幸福时光的怀恋影响了他。');
INSERT INTO `words` VALUES (5037, 'not', 'nɒt', 'adv.', '(构成动词be、do和have及情态动词can或must等的否定形式，常缩略为n\'t)不，没有;(否定后面的词或短语，或作否定的回答)不;(用于hope、expect、believe等动词后，作为否定的回答)不;', 'The sanctions are not working the way they were intended...', '制裁没有发挥预期的作用。');
INSERT INTO `words` VALUES (5038, 'notable', 'ˈnəʊtəbl', 'adj.', '值得注意的;显著的;重要的;', 'The proposed new structure is notable not only for its height, but for its shape...', '拟建的新建筑令人瞩目，不仅是因其高度，也因其外形。');
INSERT INTO `words` VALUES (5039, 'notably', 'ˈnəʊtəbli', 'adv.', '尤其;特别;极大程度上;', 'The divorce would be granted when more important problems, notably the fate of the children, had been decided...', '更为重要的问题，尤其是孩子的问题得到解决后，离婚会获批准。');
INSERT INTO `words` VALUES (5040, 'notary', 'ˈnəʊtəri', 'n.', '公证人;', 'She is the town clerk and a certified public accountant and notary public.', '她身兼城镇文书、执业会计师和公证人数职。');
INSERT INTO `words` VALUES (5041, 'notation', 'nəʊˈteɪʃn', 'n.', '(数学、科学和音乐中的)符号，记号，谱号;', 'Musical notation was conceived for the C major scale and each line and space represents a note in this scale.', '五线谱以C大音阶构成，每一个线和间代表这个音阶的音符。');
INSERT INTO `words` VALUES (5042, 'notch', 'nɒtʃ', 'n.', '等级;档次;位阶;', 'Average earnings in the economy moved up another notch in August...', '8月份这个国家的平均收入又上了一个台阶。');
INSERT INTO `words` VALUES (5043, 'note', 'nəʊt', 'n.', '笔记;记录;短笺;', 'Stevens wrote him a note asking him to come to his apartment...', '史蒂文斯给他写了张便条，请他去自己的公寓。');
INSERT INTO `words` VALUES (5044, 'notebook', 'ˈnəʊtbʊk', 'n.', '笔记本;笔记本计算机;笔记本电脑;', 'He brought out a notebook and pen from his pocket.', '他从口袋里掏出笔记本和钢笔。');
INSERT INTO `words` VALUES (5045, 'noted', 'ˈnəʊtɪd', 'adj.', '(以…)见称，闻名，著名;', '...a television programme noted for its attacks on organised crime...', '以抨击有组织犯罪而知名的电视节目');
INSERT INTO `words` VALUES (5046, 'notes', 'nəʊts', 'n.', '笔记;记录;短笺;', 'Stevens wrote him a note asking him to come to his apartment...', '史蒂文斯给他写了张便条，请他去自己的公寓。');
INSERT INTO `words` VALUES (5047, 'noteworthy', 'ˈnəʊtwɜːði', 'adj.', '值得注意的;显著的;重要的;', 'It is noteworthy that the programme has been shifted from its original August slot to July...', '值得注意的是该节目已从原来8月份的档期换到了7月份。');
INSERT INTO `words` VALUES (5048, 'nothing', 'ˈnʌθɪŋ', 'pron.', '没有什么;没有一件东西;无关紧要的东西;', 'I\'ve done nothing much since coffee time...', '咖啡时间过后，我什么都没干。');
INSERT INTO `words` VALUES (5049, 'notice', 'ˈnəʊtɪs', 'n.', '注意;理会;察觉;', 'People should not hesitate to contact the police if they\'ve noticed anyone acting suspiciously...', '如果发现形迹可疑的人，应该马上报警。');
INSERT INTO `words` VALUES (5050, 'noticeable', 'ˈnəʊtɪsəbl', 'adj.', '显著的;显而易见的;', 'It is noticeable that women do not have the rivalry that men have...', '显而易见，女性不像男人们那样好勇斗狠。');
INSERT INTO `words` VALUES (5051, 'notification', 'ˌnəʊtɪfɪˈkeɪʃn', 'n.', '通知;通告;告示;', 'Names of the dead and injured are being withheld pending notification of relatives...', '在正式通知亲属前，伤亡人员名单暂时保密。');
INSERT INTO `words` VALUES (5052, 'notify', 'ˈnəʊtɪfaɪ', 'v.', '(正式)通报，通知;', 'The skipper notified the coastguard of the tragedy...', '船长向海岸警卫队报告了这起灾难。');
INSERT INTO `words` VALUES (5053, 'notion', 'ˈnəʊʃn', 'n.', '观念;信念;理解;', 'We each have a notion of just what kind of person we\'d like to be...', '我们每人都有一个自己想成为什么样的人的想法。');
INSERT INTO `words` VALUES (5054, 'notorious', 'nəʊˈtɔːriəs', 'adj.', '声名狼藉的;臭名昭著的;', '...an area notorious for drugs, crime and violence...', '因毒品、犯罪和暴力而臭名昭著的地区');
INSERT INTO `words` VALUES (5055, 'notwithstanding', 'ˌnɒtwɪθˈstændɪŋ', 'prep.', '虽然;尽管;', 'He despised William Pitt, notwithstanding the similar views they both held.', '尽管他和威廉·皮特看法相似，却瞧不起他。');
INSERT INTO `words` VALUES (5056, 'nourish', 'ˈnʌrɪʃ', 'v.', '抚养;滋养;养育;', 'The food she eats nourishes both her and the baby.', '她吃的食物给她和婴儿提供了营养。');
INSERT INTO `words` VALUES (5057, 'nourishment', 'ˈnʌrɪʃmənt', 'n.', '营养;营养品;', 'The mother provides the embryo with nourishment and a place to grow...', '母亲为胎儿提供营养和成长的空间。');
INSERT INTO `words` VALUES (5058, 'novel', 'ˈnɒvl', 'n.', '(长篇)小说;', '...a novel by Herman Hesse...', '一部赫尔曼·黑塞的小说');
INSERT INTO `words` VALUES (5059, 'novelist', 'ˈnɒvəlɪst', 'n.', '小说家;', '...a romantic novelist.', '浪漫派小说家');
INSERT INTO `words` VALUES (5060, 'novelty', 'ˈnɒvlti', 'n.', '新奇;新颖;新鲜;', 'In the contemporary western world, rapidly changing styles cater to a desire for novelty and individualism.', '在当代西方社会，快速变化的时尚迎合了人们追求新奇和个性的需求。');
INSERT INTO `words` VALUES (5061, 'november', 'nəʊˈvembə(r)', 'n.', '十一月;', 'He arrived in London in November 1939...', '他于1939年11月抵达伦敦。');
INSERT INTO `words` VALUES (5062, 'novice', 'ˈnɒvɪs', 'n.', '新手;初学者;初学修士(或修女);', 'I\'m a novice at these things, Lieutenant. You\'re the professional...', '中尉，做这些事情我是新手，你是行家。');
INSERT INTO `words` VALUES (5063, 'now', 'naʊ', 'adv.', '现在;目前;此刻;', 'She\'s a widow now...', '她现在是寡妇。');
INSERT INTO `words` VALUES (5064, 'nowadays', 'ˈnaʊədeɪz', 'adv.', '现今;现在;目前;', 'Nowadays it\'s acceptable for women to be ambitious. But it wasn\'t then...', '如今，女性胸怀大志也能被接受，但那时候不是这样。');
INSERT INTO `words` VALUES (5065, 'nowhere', 'ˈnəʊweə(r)', 'adv.', '无处;哪里都不;', 'Nowhere is language a more serious issue than in Hawaii...', '没有哪个地方像夏威夷那样，语言成了如此重要的问题。');
INSERT INTO `words` VALUES (5066, 'noxious', 'ˈnɒkʃəs', 'adj.', '有毒的;有害的;', 'Many household products give off noxious fumes.', '很多家用产品散发有害气体。');
INSERT INTO `words` VALUES (5067, 'nuance', 'ˈnjuːɑːns', 'n.', '(意义、声音、颜色、感情等方面的)细微差别;', 'We can use our eyes and facial expressions to communicate virtually every subtle nuance of emotion there is.', '我们用眼睛和面部表情几乎就能传达出情感上的每一丝微妙变化。');
INSERT INTO `words` VALUES (5068, 'nuclear', 'ˈnjuːkliə(r)', 'adj.', '原子能的;核能的;核武器的;', '...a nuclear power station.', '核电站');
INSERT INTO `words` VALUES (5069, 'nucleus', 'ˈnjuːkliəs', 'n.', '核;原子核;细胞核;', 'Neutrons and protons are bound together in the nucleus of an atom.', '在原子核里，中子和质子是结合在一起的。');
INSERT INTO `words` VALUES (5070, 'nuisance', 'ˈnjuːsns', 'n.', '麻烦事;讨厌的人(或东西);妨害行为;', 'He could be a bit of a nuisance when he was drunk...', '他醉酒时有点讨厌。');
INSERT INTO `words` VALUES (5071, 'null', 'nʌl', 'adj.', '零值的;等于零的;', 'A spokeswoman said the agreement had been declared null and void...', '女发言人称该协议已宣布无效。');
INSERT INTO `words` VALUES (5072, 'nullify', 'ˈnʌlɪfaɪ', 'v.', '使失去法律效力;废止;使无效;', 'He used his broad executive powers to nullify decisions by local governments...', '他利用自己适用范围广泛的行政权力废止当地政府所作的决定。');
INSERT INTO `words` VALUES (5073, 'numb', 'nʌm', 'adj.', '麻木的;失去知觉的;迟钝的;', 'He could feel his fingers growing numb at their tips...', '他能感到指尖正在变得麻木。');
INSERT INTO `words` VALUES (5074, 'number', 'ˈnʌmbə(r)', 'n.', '数字;数;数量;', 'No, I don\'t know the room number...', '不，我不知道房间号。');
INSERT INTO `words` VALUES (5075, 'numbering', 'ˈnʌmbərɪŋ', 'v.', '标号;给…编号;总计;', 'No, I don\'t know the room number...', '不，我不知道房间号。');
INSERT INTO `words` VALUES (5076, 'numeral', 'ˈnjuːmərəl', 'n.', '数字;数码;', '...a flat, square wristwatch with classic Roman numerals.', '一块扁平、有标准罗马数字的正方形手表');
INSERT INTO `words` VALUES (5077, 'numerate', 'ˈnjuːmərət', 'adj.', '识数的;有计算能力的;', 'Your children should be literate and numerate.', '你的孩子应该会识字算数。');
INSERT INTO `words` VALUES (5078, 'numerical', 'njuːˈmerɪkl', 'adj.', '数字的;用数字表示的;', 'Your job is to group them by letter and put them in numerical order.', '你的工作是把他们按字母顺序分组，然后按数字顺序排序。');
INSERT INTO `words` VALUES (5079, 'numerous', 'ˈnjuːmərəs', 'adj.', '众多的;许多的;', 'Sex crimes were just as numerous as they are today...', '当时性犯罪和现在一样多。');
INSERT INTO `words` VALUES (5080, 'nun', 'nʌn', 'n.', '修女;尼姑;', 'Mr Thomas was taught by the Catholic nuns whose school he attended.', '托马斯先生是在天主教修女创办的学校接受的教育。');
INSERT INTO `words` VALUES (5081, 'nurse', 'nɜːs', 'n.', '护士;(旧时雇主家中的)女保育员，保姆，女仆;', 'She had spent 29 years as a nurse...', '她做了29年的护士。');
INSERT INTO `words` VALUES (5082, 'nursery', 'ˈnɜːsəri', 'n.', '婴儿室;(供游戏的)儿童室;苗圃;', 'This nursery will be able to cater for 29 children...', '这家托儿所能照料29个孩子。');
INSERT INTO `words` VALUES (5083, 'nurture', 'ˈnɜːtʃə(r)', 'v.', '养育;养护;培养;', 'Parents want to know the best way to nurture and raise their child to adulthood...', '父母希望知道养育孩子长大成人的最佳方法。');
INSERT INTO `words` VALUES (5084, 'nut', 'nʌt', 'n.', '坚果;螺母;螺帽;', 'Nuts and seeds are good sources of vitamin E.', '坚果和种子里含有丰富的维生素E。');
INSERT INTO `words` VALUES (5085, 'nutrient', 'ˈnjuːtriənt', 'n.', '营养素;营养物;', '...the role of vegetable fibres, vitamins, minerals and other essential nutrients.', '植物纤维、维生素、矿物质和其他必不可少的营养物质的作用');
INSERT INTO `words` VALUES (5086, 'nutrition', 'njuˈtrɪʃn', 'n.', '营养;滋养;营养的补给;', 'There are alternative sources of nutrition to animal meat...', '存在替代肉类的其他营养来源。');
INSERT INTO `words` VALUES (5087, 'nylon', 'ˈnaɪlɒn', 'n.', '尼龙;(女用)尼龙长袜，尼龙连裤袜;', 'Europe\'s largest producer of nylon is based in Belgium...', '欧洲最大的尼龙制造商总部设在比利时。');
INSERT INTO `words` VALUES (5088, 'oak', 'əʊk', 'n.', '栎树;橡树;橡木;', 'Many large oaks were felled during the war.', '很多高大的橡树在战争中遭到了砍伐。');
INSERT INTO `words` VALUES (5089, 'oath', 'əʊθ', 'n.', '宣誓;誓言;(表示愤怒、惊异等的)咒骂，诅咒的话;', 'He took an oath of loyalty to the government...', '他宣誓效忠政府。');
INSERT INTO `words` VALUES (5090, 'obedience', 'əˈbiːdiəns', 'n.', '服从；顺从，遵从;', 'He was very respectful at home and obedient to his parents...', '他在家里对父母十分恭敬顺从。');
INSERT INTO `words` VALUES (5091, 'obedient', 'əˈbiːdiənt', 'adj.', '顺从的;忠顺的;唯命是从的;', 'He was very respectful at home and obedient to his parents...', '他在家里对父母十分恭敬顺从。');
INSERT INTO `words` VALUES (5092, 'obesity', 'əʊˈbiːsɪti', 'n.', '肥胖症;', 'The tendency to become obese is at least in part hereditary...', '发胖至少有一部分是源于遗传。');
INSERT INTO `words` VALUES (5093, 'obey', 'əˈbeɪ', 'v.', '服从;遵守;顺从;', 'Cissie obeyed her mother without question...', '茜茜对她母亲言听计从。');
INSERT INTO `words` VALUES (5094, 'objection', 'əbˈdʒekʃn', 'n.', '反对的理由;反对;异议;', 'Some managers have recently raised objection to the PFA handling these negotiations...', '一些主教练最近就英格兰职业足球运动员协会负责这些谈判提出了异议。');
INSERT INTO `words` VALUES (5095, 'objective', 'əbˈdʒektɪv', 'n.', '目标;目的;(望远镜或显微镜的)物镜;', 'Our main objective was the recovery of the child safe and well...', '我们的主要目标是将孩子安然无恙地找回来。');
INSERT INTO `words` VALUES (5096, 'obligation', 'ˌɒblɪˈɡeɪʃn', 'n.', '义务;职责;责任;', 'When teachers assign homework, students usually feel an obligation to do it...', '老师布置作业时，学生通常认为完成作业是一种义务。');
INSERT INTO `words` VALUES (5097, 'obligatory', 'əˈblɪɡətri', 'adj.', '(按法律、规定等)必须的，强制的;习惯性的;随大溜的;', 'Most women will be offered an ultrasound scan during pregnancy, although it\'s not obligatory...', '大多数处于妊娠期的妇女将被建议接受超声波检查，不过这不是强制性的。');
INSERT INTO `words` VALUES (5098, 'oblige', 'əˈblaɪdʒ', 'v.', '(以法律、义务等)强迫，迫使;(根据要求或需要)帮忙，效劳;', 'The storm got worse and worse. Finally, I was obliged to abandon the car and continue on foot...', '暴风雨愈发猛烈，我最终不得不弃车步行。');
INSERT INTO `words` VALUES (5099, 'obliging', 'əˈblaɪdʒɪŋ', 'adj.', '乐于助人的;热情的;', 'He is an extremely pleasant and obliging man.', '他是个非常友善、古道热肠的人。');
INSERT INTO `words` VALUES (5100, 'oblique', 'əˈbliːk', 'adj.', '间接的;不直截了当的;拐弯抹角的;', 'It was an oblique reference to his mother...', '这影射到了他的母亲。');
INSERT INTO `words` VALUES (5101, 'obliterate', 'əˈblɪtəreɪt', 'v.', '毁掉;覆盖;清除;', 'Their warheads are enough to obliterate the world several times over...', '他们拥有的弹头足以将世界毁灭好几次。');
INSERT INTO `words` VALUES (5102, 'oblivious', 'əˈblɪviəs', 'adj.', '不知道;未注意;未察觉;', 'She lay motionless where she was, oblivious to pain...', '她一动不动地躺在原地，已经感觉不到疼痛。');
INSERT INTO `words` VALUES (5103, 'oblong', 'ˈɒblɒŋ', 'adj.', '矩形的;长方形的;椭圆形的;', '...an oblong table.', '长方形的桌子');
INSERT INTO `words` VALUES (5104, 'obnoxious', 'əbˈnɒkʃəs', 'adj.', '极讨厌的;可憎的;令人作呕的;', 'One of the parents was a most obnoxious character. No-one liked him...', '其中一位家长极为惹人厌，没人喜欢他。');
INSERT INTO `words` VALUES (5105, 'obscene', 'əbˈsiːn', 'adj.', '淫秽的;猥亵的;下流的;', 'I\'m not prudish but I think these photographs are obscene...', '我并不是个守旧的人，但是我认为这些照片不堪入目。');
INSERT INTO `words` VALUES (5106, 'obscure', 'əbˈskjʊə(r)', 'adj.', '无名的;鲜为人知的;费解的;', 'The origin of the custom is obscure...', '这一习俗的起源鲜为人知。');
INSERT INTO `words` VALUES (5107, 'observance', 'əbˈzɜːvəns', 'n.', '(对法律、习俗的)遵守，奉行;(节日的)纪念;宗教(或传统节日)的仪式;', 'Local councils should use their powers to ensure strict observance of laws.', '地方政务委员会应当行使权力以保证人们严格遵守法律。');
INSERT INTO `words` VALUES (5108, 'observation', 'ˌɒbzəˈveɪʃn', 'n.', '观察;观测;监视;', '...careful observation of the movement of the planets...', '对行星运动的仔细观测');
INSERT INTO `words` VALUES (5109, 'observe', 'əbˈzɜːv', 'v.', '看到;注意到;观察到;', 'Stern also studies and observes the behaviour of babies...', '斯特恩还研究观察婴儿的行为。');
INSERT INTO `words` VALUES (5110, 'observer', 'əbˈzɜːvə(r)', 'n.', '观察者;观测者;目击者;', 'A casual observer would have taken them to be three men out for an evening stroll...', '乍一看的话，还以为他们三人是晚上出来散步的。');
INSERT INTO `words` VALUES (5111, 'obsess', 'əbˈses', 'v.', '使痴迷;使迷恋;使着迷;', 'I must admit that maps obsess me...', '我得承认我对地图十分着迷。');
INSERT INTO `words` VALUES (5112, 'obsession', 'əbˈseʃn', 'n.', '痴迷;着魔;困扰;', 'She would try to forget her obsession with Christopher...', '她会努力忘记对克里斯托弗的迷恋。');
INSERT INTO `words` VALUES (5113, 'obsessive', 'əbˈsesɪv', 'adj.', '着迷的;迷恋的;难以释怀的;', 'Williams is obsessive about motor racing...', '威廉斯对赛车到了痴迷的程度。');
INSERT INTO `words` VALUES (5114, 'obsolescence', 'ˌɒbsəˈlesns', 'n.', '过时;陈旧;淘汰;', 'The aircraft was nearing obsolescence by early 1942.', '这种飞机在1942年年初就濒临淘汰了。');
INSERT INTO `words` VALUES (5115, 'obsolescent', 'ˌɒbsəˈlesnt', 'adj.', '过时的;废弃的;淘汰的;', '...outmoded, obsolescent equipment.', '陈旧老化的设备');
INSERT INTO `words` VALUES (5116, 'obsolete', 'ˈɒbsəliːt', 'adj.', '淘汰的;废弃的;过时的;', 'So much equipment becomes obsolete almost as soon as it\'s made.', '这么多设备几乎一生产出来就要被淘汰。');
INSERT INTO `words` VALUES (5117, 'obstacle', 'ˈɒbstəkl', 'n.', '障碍;阻碍;绊脚石;', 'Most competition cars will only roll over if they hit an obstacle...', '大多数赛车只有在撞上障碍物时才会翻车。');
INSERT INTO `words` VALUES (5118, 'obstetrics', 'əbˈstetrɪks', 'n.', '产科学;', 'The training covers extended periods in obstetrics and gynaecology.', '该培训包括产科学和妇科学的增加课时。');
INSERT INTO `words` VALUES (5119, 'obstinacy', '', 'n.', '顽固，固执；顽强;（病痛等的）难治，难解除;顽固的事例；固执行为;', 'He is obstinate and determined and will not give up...', '他非常顽固，而且又铁了心，是不会放弃的。');
INSERT INTO `words` VALUES (5120, 'obstinate', 'ˈɒbstɪnət', 'adj.', '执拗的;固执的;顽固的;', 'He is obstinate and determined and will not give up...', '他非常顽固，而且又铁了心，是不会放弃的。');
INSERT INTO `words` VALUES (5121, 'obstruct', 'əbˈstrʌkt', 'v.', '阻挡;阻塞;遮断;', 'Tractors and container lorries have completely obstructed the road.', '拖拉机和集装箱货车已经将这条路完全堵死了。');
INSERT INTO `words` VALUES (5122, 'obstruction', 'əbˈstrʌkʃn', 'n.', '阻挡;阻碍;妨碍;', 'John was irritated by drivers parking near his house and causing an obstruction.', '约翰对那些在他家附近泊车堵住通道的司机感到恼火。');
INSERT INTO `words` VALUES (5123, 'obtain', 'əbˈteɪn', 'v.', '(尤指经努力)获得，赢得;存在;流行;', 'Evans was trying to obtain a false passport and other documents...', '埃文斯正试图取得一本假护照和其他证件。');
INSERT INTO `words` VALUES (5124, 'obtainable', 'əbˈteɪnəbl', 'adj.', '可获得;可得到;', 'The dried herb is obtainable from health shops.', '这种干药草能在保健店里买到。');
INSERT INTO `words` VALUES (5125, 'obvious', 'ˈɒbviəs', 'adj.', '明显的;显然的;易理解的;', '...the need to rectify what is an obvious injustice...', '矫正明显不公的需要');
INSERT INTO `words` VALUES (5126, 'obviously', 'ˈɒbviəsli', 'adv.', '(用于陈述认为别人已知道或希望别人同意的事)显然，明显地;(用于说明某种情况或事实)显而易见，明显，不言而喻;', 'Obviously, they\'ve had sponsorship from some big companies...', '很显然，他们已经得到某些大公司的赞助。');
INSERT INTO `words` VALUES (5127, 'occasion', 'əˈkeɪʒn', 'n.', '某次;…的时候;特别的事情(或仪式、庆典);', 'I often think fondly of an occasion some years ago at Covent Garden...', '我时常愉快地回想起几年前在科文特花园的一个时刻。');
INSERT INTO `words` VALUES (5128, 'occasional', 'əˈkeɪʒənl', 'adj.', '偶尔的;偶然的;临时的;', 'I\'ve had occasional mild headaches all my life...', '间或发作的轻微头疼困扰了我一辈子。');
INSERT INTO `words` VALUES (5129, 'occasionally', 'əˈkeɪʒnəli', 'adv.', '偶然;偶尔;有时候;', 'I\'ve had occasional mild headaches all my life...', '间或发作的轻微头疼困扰了我一辈子。');
INSERT INTO `words` VALUES (5130, 'occidental', 'ˌɒksɪˈdentl', 'adj.', '西方(国家)的;欧美的;', 'In some respects the African mind works rather differently from the occidental one.', '在某些方面，非洲人的思维方式与西方人相差甚远。');
INSERT INTO `words` VALUES (5131, 'occupation', 'ˌɒkjuˈpeɪʃn', 'n.', '工作;职业;消遣;', 'I suppose I was looking for an occupation which was going to be an adventure...', '我想我在找的是一份具有冒险性的工作。');
INSERT INTO `words` VALUES (5132, 'occupy', 'ˈɒkjupaɪ', 'v.', '使用，占用(空间、面积、时间等);使用(房屋、建筑);居住;', 'There were over 40 tenants, all occupying one wing of the hospital...', '共有40多位房客，他们全部住在这家医院的一个病区。');
INSERT INTO `words` VALUES (5133, 'occur', 'əˈkɜː(r)', 'v.', '发生;出现;存在于;', 'If headaches only occur at night, lack of fresh air and oxygen is often the cause...', '如果只是在晚上头疼，那常常是由于缺乏新鲜空气和氧气的缘故。');
INSERT INTO `words` VALUES (5134, 'occurrence', 'əˈkʌrəns', 'n.', '发生的事情;存在的事物;发生;', 'Complaints seemed to be an everyday occurrence...', '似乎每天都有投诉。');
INSERT INTO `words` VALUES (5135, 'ocean', 'ˈəʊʃn', 'n.', '大海;海洋;(五大洋之一的)洋;', 'There were few sights as beautiful as the calm ocean on a warm night.', '鲜有景致能与温暖夜色下风平浪静的大海媲美。');
INSERT INTO `words` VALUES (5136, 'oceanography', 'ˌəʊʃəˈnɒɡrəfi', 'n.', '海洋学;', 'The latest techniques in oceanography are now available to many more scientists.', '更多的科学家现在能够接触到海洋学的最新技术。');
INSERT INTO `words` VALUES (5137, 'octet', 'ɒkˈtet', 'n.', '八重唱组合;八重奏乐团;八重奏(曲);', '...the Stan Tracey Octet.', '斯坦·特雷西八重奏乐团');
INSERT INTO `words` VALUES (5138, 'october', 'ɒkˈtəʊbə(r)', 'n.', '十月;', 'Most seasonal hiring is done in early October...', '大部分季节性招聘在10月初进行。');
INSERT INTO `words` VALUES (5139, 'odd', 'ɒd', 'adj.', '奇怪的;怪异的;反常的;', 'He\'d always been odd, but not to this extent...', '他一直比较怪异，不过并没有到这种程度。');
INSERT INTO `words` VALUES (5140, 'odds', 'ɒdz', 'n.', '(事物发生的)可能性，概率，几率，机会;不利条件;掣肘的事情;', 'What are the odds of finding a parking space right outside the door?...', '就在门口找到停车位的可能性有多大？');
INSERT INTO `words` VALUES (5141, 'odyssey', 'ˈɒdəsi', 'n.', '艰苦的跋涉;漫长而充满风险的历程;', 'The march to Travnik was the final stretch of a 16-hour odyssey.', '去特拉夫尼克的这段路是长达16小时艰险旅行的最后一程。');
INSERT INTO `words` VALUES (5142, 'of', 'ɒv , əv', 'prep.', '属于(某人);关于(某人);属于(某物);', 'The average age of the women interviewed was only 21.5.', '参加面试的女性平均年龄才21.5岁。');
INSERT INTO `words` VALUES (5143, 'offend', 'əˈfend', 'v.', '得罪;冒犯;令人不适;', 'He apologizes for his comments and says he had no intention of offending the community...', '他为自己发表的评论道歉并称无意冒犯该社群。');
INSERT INTO `words` VALUES (5144, 'offended', 'əˈfendɪd', 'v.', '得罪;冒犯;令人不适;', 'He apologizes for his comments and says he had no intention of offending the community...', '他为自己发表的评论道歉并称无意冒犯该社群。');
INSERT INTO `words` VALUES (5145, 'offender', 'əˈfendə(r)', 'n.', '犯罪者;违法者;罪犯;', 'The authorities often know that sex offenders will attack again when they are released.', '当局一般都知道性犯罪者在获释后往往会再次犯案。');
INSERT INTO `words` VALUES (5146, 'offensive', 'əˈfensɪv', 'adj.', '冒犯的;得罪人的;无礼的;', 'Some friends of his found the play horribly offensive.', '他的一些朋友觉得该剧非常让人反感。');
INSERT INTO `words` VALUES (5147, 'offer', 'ˈɒfə(r)', 'v.', '主动提出;自愿给予;提供(东西或机会);', 'He has offered seats at the conference table to the Russian leader and the president of Kazakhstan...', '他已邀请俄罗斯领导人和哈萨克斯坦总统参加会谈。');
INSERT INTO `words` VALUES (5148, 'offering', 'ˈɒfərɪŋ', 'n.', '用品;剧作;作品;', 'It was very, very good, far better than vegetarian offerings in many a posh restaurant.', '这道菜真是人间美味，远远超过很多豪华餐厅的素食菜品。');
INSERT INTO `words` VALUES (5149, 'office', 'ˈɒfɪs', 'n.', '办公室;办公楼;(某人的)办公室;', 'He had an office big enough for his desk and chair, plus his VDU...', '他有一间足以容纳书桌、椅子和视频显示器的办公室。');
INSERT INTO `words` VALUES (5150, 'officer', 'ˈɒfɪsə(r)', 'n.', '军官;(政府或大机构的)官员，高级职员;警察的头衔;', '...a retired British army officer...', '退役的英国陆军军官');
INSERT INTO `words` VALUES (5151, 'official', 'əˈfɪʃl', 'adj.', '公务的;公职的;公事的;', 'According to the official figures, over one thousand people died during the revolution...', '根据官方数字，1,000多人在这场革命中丧生。');
INSERT INTO `words` VALUES (5152, 'officially', 'əˈfɪʃəli', 'adv.', '正式地;官方地;公开地;', 'According to the official figures, over one thousand people died during the revolution...', '根据官方数字，1,000多人在这场革命中丧生。');
INSERT INTO `words` VALUES (5153, 'offshore', 'ˌɒfˈʃɔː(r)', 'adj.', '海上的;近海的;向海的;', '...Britain\'s offshore oil industry.', '英国的近海石油工业');
INSERT INTO `words` VALUES (5154, 'offspring', 'ˈɒfsprɪŋ', 'n.', '孩子;子女;后代;', 'Eleanor was now less anxious about her offspring than she had once been.', '埃莉诺现在不像以往那样为孩子忧心了。');
INSERT INTO `words` VALUES (5155, 'often', 'ˈɒfn', 'adv.', '时常;常常;往往;', 'They often spent Christmas at Prescott Hill...', '他们经常在普雷斯科特山过圣诞节。');
INSERT INTO `words` VALUES (5156, 'oh', 'əʊ', 'int.', '(表示领悟)哦，唔;(表示惊奇、恐惧、高兴等)啊，哈，哎哟;(用以引起注意)喂，嘿，嗨;', '\'Had you seen the car before?\' — \'Oh yes, it was always in the drive.\'...', '“之前你见过那辆车吗？”——“哦，见过，它总是停在车道上。”');
INSERT INTO `words` VALUES (5157, 'ohm', 'əʊm', 'n.', '欧姆(电阻单位);', '...a resistance of 40 ohms.', '40欧姆的电阻');
INSERT INTO `words` VALUES (5158, 'oil', 'ɔɪl', 'n.', '石油;原油;燃油;', 'The company buys and sells about 600,000 barrels of oil a day.', '该公司每天买卖约60万桶石油。');
INSERT INTO `words` VALUES (5159, 'okay', '\'əʊ\'keɪ', 'adj.', '同“O.K.”;', '...a shooting range where it\'s OK to use weapons...', '可以使用武器的射击场');
INSERT INTO `words` VALUES (5160, 'old', 'əʊld', 'adj.', '具体年龄;(多少)岁;年纪;', '...a white-haired old man...', '白发老人');
INSERT INTO `words` VALUES (5161, 'olympics', 'əʊˈlɪmpɪks', 'n.', '奥林匹克运动会;', '...the reigning Olympic champion.', '现任奥运会冠军');
INSERT INTO `words` VALUES (5162, 'omen', 'ˈəʊmən', 'n.', '预兆;前兆;征兆;', 'Her appearance at this moment is an omen of disaster...', '她在此刻出现预示着一场灾难。');
INSERT INTO `words` VALUES (5163, 'ominous', 'ˈɒmɪnəs', 'adj.', '预兆的;恶兆的;不吉利的;', 'There was an ominous silence at the other end of the phone...', '电话那头出现了不祥的沉默。');
INSERT INTO `words` VALUES (5164, 'omission', 'əˈmɪʃn', 'n.', '省略;删除;免除;', 'The duke was surprised by his wife\'s omission from the guest list.', '公爵对他的夫人未在获邀宾客之列感到意外。');
INSERT INTO `words` VALUES (5165, 'omit', 'əˈmɪt', 'v.', '删除;忽略;漏掉;', 'Omit the salt in this recipe...', '省略菜谱中放盐这一步。');
INSERT INTO `words` VALUES (5166, 'once', 'wʌns', 'adv.', '仅一次;一次;曾;', 'I met Wilma once, briefly...', '我曾经和威尔玛有过短短的一面之缘。');
INSERT INTO `words` VALUES (5167, 'oncoming', 'ˈɒnkʌmɪŋ', 'adj.', '迎面而来的;即将来临的;', 'She was thrown from his car after it skidded into the path of an oncoming car.', '他的车滑行到正有车迎面驶来的逆向车道后，她被抛出了汽车。');
INSERT INTO `words` VALUES (5168, 'one', 'wʌn', 'num./det.', '一;(正式用语或表强调，用在hundred、thousand等或度量单位之前)一;(表强调)单独一个，仅仅一个;', 'They had three sons and one daughter.', '他们有3个儿子，1个女儿。');
INSERT INTO `words` VALUES (5169, 'ongoing', 'ˈɒnɡəʊɪŋ', 'adj.', '持续存在的;仍在进行的;不断发展的;', 'There is an ongoing debate on the issue...', '对此问题的争论一直没有间断过。');
INSERT INTO `words` VALUES (5170, 'onion', 'ˈʌnjən', 'n.', '洋葱;葱头;', 'Will you chop an onion up for me?...', '你能帮我把一个洋葱切碎吗？');
INSERT INTO `words` VALUES (5171, 'online', 'ˌɒnˈlaɪn', 'adj.', '在线的;联网的;联机的;', '...the first bank to go online.', '第一家开通网上业务的银行');
INSERT INTO `words` VALUES (5172, 'onlooker', 'ˈɒnlʊkə(r)', 'n.', '旁观者;', 'A handful of onlookers stand in the field watching.', '少数几个旁观者站在现场观看。');
INSERT INTO `words` VALUES (5173, 'onset', 'ˈɒnset', 'n.', '开端，发生，肇始(尤指不快的事件);', 'Most of the passes have been closed with the onset of winter...', '冬天来临，大部分关口均已关闭。');
INSERT INTO `words` VALUES (5174, 'onto', 'ˈɒntə', 'prep.', '(与动词连用，表示朝某处或某位置运动)向，朝;朝向，面向(某个方向);', 'I took my bags inside, lowered myself onto the bed and switched on the TV...', '我把包拎进屋，往床上一倒，然后打开了电视机。');
INSERT INTO `words` VALUES (5175, 'opaque', 'əʊˈpeɪk', 'adj.', '不透明的;不透光的;浑浊的;', 'You can always use opaque glass if you need to block a street view.', '如果不想从室内看到街景，可以使用不透明玻璃。');
INSERT INTO `words` VALUES (5176, 'open', 'ˈəʊpən', 'adj.', '开放的;敞开的;张开的;', 'He opened the window and looked out...', '他打开窗户往外看。');
INSERT INTO `words` VALUES (5177, 'opener', 'ˈəʊpnə(r)', 'n.', '开启的工具;揭幕赛;开场戏;', '...a tin opener.', '开罐器');
INSERT INTO `words` VALUES (5178, 'opening', 'ˈəʊpənɪŋ , ˈəʊpnɪŋ', 'n.', '孔;洞;缺口;', 'They returned to take part in the season\'s opening game.', '他们回来参加本赛季的开幕赛。');
INSERT INTO `words` VALUES (5179, 'openly', 'ˈəʊpənli', 'adv.', '公开地;毫不隐瞒地;', 'We can now talk openly about AIDS which we couldn\'t before...', '现在我们能公开谈论以前讳言不提的艾滋病问题。');
INSERT INTO `words` VALUES (5180, 'opera', 'ˈɒprə', 'n.', '歌剧;歌剧剧本;歌剧艺术;', '...a one-act opera about contemporary women in America.', '关于当代美国女性的独幕歌剧');
INSERT INTO `words` VALUES (5181, 'operate', 'ˈɒpəreɪt', 'v.', '运转;工作;操作;', 'Until his death in 1986 Greenwood owned and operated an enormous pear orchard.', '到他1986年去世前，格林伍德一直拥有并管理着一片面积广阔的梨园。');
INSERT INTO `words` VALUES (5182, 'operating', 'ˈɒpəreɪtɪŋ', 'adj.', '经营的;营业的;业务上的;', 'The group made operating profits of £80m before interest.', '该集团创造了 8,000万英镑的息前营业利润。');
INSERT INTO `words` VALUES (5183, 'operation', 'ˌɒpəˈreɪʃn', 'n.', '手术;(有组织的)活动，行动;(包括许多部分的)企业，公司;', 'The rescue operation began on Friday afternoon...', '救援行动于星期五下午开始。');
INSERT INTO `words` VALUES (5184, 'operational', 'ˌɒpəˈreɪʃənl', 'adj.', '操作的;运转的;运营的;', 'The whole system will be fully operational by December 1995.', '整个系统到1995年12月将可全面投入使用。');
INSERT INTO `words` VALUES (5185, 'operative', 'ˈɒpərətɪv', 'n.', '工作人员;(尤指)技术工人，操作员;密探;', 'The commercial telephone service was no longer operative...', '商业电话服务已不再运营。');
INSERT INTO `words` VALUES (5186, 'operator', 'ˈɒpəreɪtə(r)', 'n.', '操作人员;技工;电话员;', 'He dialled the operator and put in a call for Rome.', '他拨了接线员的号，让他接通一个到罗马的电话。');
INSERT INTO `words` VALUES (5187, 'opiate', 'ˈəʊpiət', 'n.', '鸦片制剂;麻醉剂;', 'One woman suffered such severe pain she had to take opiates to control it.', '有一位妇女剧痛难忍，不得不服用鸦片制剂来镇痛。');
INSERT INTO `words` VALUES (5188, 'opine', 'əʊˈpaɪn', 'v.', '表达，发表(意见);', '\'She\'s probably had a row with her boyfriend,\' Charles opined...', '“她大概跟男朋友吵架了，”查尔斯说道。');
INSERT INTO `words` VALUES (5189, 'opinion', 'əˈpɪnjən', 'n.', '意见;想法;看法;', 'I wasn\'t asking for your opinion, Dick...', '我并不是在征求你的意见，迪克。');
INSERT INTO `words` VALUES (5190, 'opinionated', 'əˈpɪnjəneɪtɪd', 'adj.', '固执己见的;顽固的;', 'Sue is the extrovert in the family; opinionated, talkative and passionate about politics.', '苏是家中的外向行动派，性格固执，能说会道，而且对政治非常热衷。');
INSERT INTO `words` VALUES (5191, 'opponent', 'əˈpəʊnənt', 'n.', '对手;竞争者;反对者;', '...Mr Kennedy\'s opponent in the leadership contest...', '与肯尼迪先生争夺领导权的对手');
INSERT INTO `words` VALUES (5192, 'opportune', 'ˈɒpətjuːn', 'adj.', '恰好的;适当的;恰当的;', 'I believe that I have arrived at a very opportune moment...', '我想我到得正是时候。');
INSERT INTO `words` VALUES (5193, 'opportunist', 'ˌɒpəˈtjuːnɪst', 'adj.', '机会主义的;投机的;见风转舵的;', '...corrupt and opportunist politicians.', '贪污受贿、投机取巧的政客');
INSERT INTO `words` VALUES (5194, 'opportunity', 'ˌɒpəˈtjuːnəti', 'n.', '机会;时机;', 'I had an opportunity to go to New York and study...', '我曾经有一个去纽约学习的机会。');
INSERT INTO `words` VALUES (5195, 'oppose', 'əˈpəʊz', 'v.', '反对(计划、政策等);抵制;阻挠;', 'Mr Taylor was not bitter towards those who had opposed him...', '泰勒先生对那些曾反对过他的人并未怀恨在心。');
INSERT INTO `words` VALUES (5196, 'opposed', 'əˈpəʊzd', 'adj.', '强烈反对;截然不同;', 'I am utterly opposed to any form of terrorism...', '我坚决反对任何形式的恐怖主义。');
INSERT INTO `words` VALUES (5197, 'opposing', 'əˈpəʊzɪŋ', 'adj.', '对立的;相竞争的;对抗的;', 'I have a friend who has the opposing view and felt that the war was immoral...', '我有一位朋友的看法截然不同，他认为这场战争是不道德的。');
INSERT INTO `words` VALUES (5198, 'opposite', 'ˈɒpəzɪt', 'adj.', '对面的;另一边的;相反的;', 'Jennie had sat opposite her at breakfast.', '珍妮吃早餐时坐在她对面。');
INSERT INTO `words` VALUES (5199, 'opposition', 'ˌɒpəˈzɪʃn', 'n.', '(强烈的)反对，反抗，对抗;(事业、竞赛、游戏等的)对手，敌手，竞争者;反对党;', 'The government is facing a new wave of opposition in the form of a student strike...', '政府面临着学生通过罢课掀起的新一轮反对浪潮。');
INSERT INTO `words` VALUES (5200, 'oppress', 'əˈpres', 'v.', '压迫;欺压;压制;', 'These people often are oppressed by the governments of the countries they find themselves in...', '这些人经常受到所在国政府的压迫。');
INSERT INTO `words` VALUES (5201, 'oppression', 'əˈpreʃn', 'n.', '压迫;压制;', '...an attempt to escape political oppression.', '企图逃避政治压迫的努力');
INSERT INTO `words` VALUES (5202, 'oppressive', 'əˈpresɪv', 'adj.', '压迫的;压制的;高压的;', 'The new laws will be just as oppressive as those they replace.', '新法律只会与原来的法律一样苛严。');
INSERT INTO `words` VALUES (5203, 'oppressor', 'əˈpresə(r)', 'n.', '压迫者;残暴的统治者;暴君;', 'Lacking sovereignty, they could organise no defence against their oppressors.', '没有主权他们就无法组织起来反抗压迫者。');
INSERT INTO `words` VALUES (5204, 'opprobrium', 'əˈprəʊbriəm', 'n.', '(众人的)谴责，责难，抨击;', 'His political opinions have attracted the opprobrium of the Left.', '他的政治观点遭到左派的抨击。');
INSERT INTO `words` VALUES (5205, 'opt', 'ɒpt', 'v.', '选择;挑选;', 'Depending on your circumstances you may wish to opt for one method or the other...', '依据自身情况的不同，你可能希望选择这种或那种方法。');
INSERT INTO `words` VALUES (5206, 'optic', 'ˈɒptɪk', 'adj.', '眼的;视觉的;', 'The reason for this is that the optic nerve is a part of the brain.', '这其中的原因在于视神经是大脑的一部分。');
INSERT INTO `words` VALUES (5207, 'optical', 'ˈɒptɪkl', 'adj.', '视力的;视觉的;光学的;', '...optical telescopes.', '光学望远镜');
INSERT INTO `words` VALUES (5208, 'optimism', 'ˈɒptɪmɪzəm', 'n.', '乐观;乐观主义;', 'The Indian Prime Minister has expressed optimism about India\'s future relations with the USA.', '印度总理对未来的印美关系表示乐观。');
INSERT INTO `words` VALUES (5209, 'optimist', 'ˈɒptɪmɪst', 'n.', '乐观的人;乐天派;', 'Optimists reckon house prices will move up with inflation this year.', '乐观人士认为今年的房价将会随通货膨胀而上涨。');
INSERT INTO `words` VALUES (5210, 'optimistic', 'ˌɒptɪˈmɪstɪk', 'adj.', '乐观的;抱乐观看法的;', 'The President says she is optimistic that an agreement can be worked out soon...', '总统说她对很快达成协议持乐观态度。');
INSERT INTO `words` VALUES (5211, 'optimum', 'ˈɒptɪməm', 'adj.', '最佳的;最适宜的;最佳结果;', 'Aim to do some physical activity three times a week for optimum health.', '为了达到最佳的健康状况，要力争每周进行3次身体锻炼。');
INSERT INTO `words` VALUES (5212, 'option', 'ˈɒpʃn', 'n.', '可选择的事物;选择;选择权;', 'He\'s argued from the start that America and its allies are putting too much emphasis on the military option...', '他从一开始就认为美国及其盟国过于强调军事手段。');
INSERT INTO `words` VALUES (5213, 'optional', 'ˈɒpʃənl', 'adj.', '可选择的;选修的;', 'A holiday isn\'t an optional extra. In this stressful, frantic world it\'s a must...', '休假并非可有可无。在这个紧张而忙碌的世界，它是必不可少的。');
INSERT INTO `words` VALUES (5214, 'or', 'ɔː(r)', 'conj.', '(用以引出另一种可能性)或，或者，还是;(用于否定句，提出两种或多种事物时)也不;(用于警告或忠告)否则，不然;', '\'Tea or coffee?\' John asked...', '“喝茶还是喝咖啡？”约翰问道。');
INSERT INTO `words` VALUES (5215, 'oral', 'ˈɔːrəl', 'adj.', '口头的;用口的;口腔的;', '...the written and oral traditions of ancient cultures.', '通过文字记载和口头传诵留存下来的古代文化传统');
INSERT INTO `words` VALUES (5216, 'orange', 'ˈɒrɪndʒ', 'n.', '橙子;柑橘;橙汁;', '...men in bright orange uniforms.', '身着亮橙色制服的人');
INSERT INTO `words` VALUES (5217, 'orbit', 'ˈɔːbɪt', 'n.', '(天体等运行的)轨道;(人、组织等的)影响范围，势力范围;', 'Mars and Earth have orbits which change with time...', '火星与地球的运行轨道随着时间而变化。');
INSERT INTO `words` VALUES (5218, 'orchestra', 'ˈɔːkɪstrə', 'n.', '管弦乐队;', '...the Royal Liverpool Philharmonic Orchestra.', '皇家利物浦爱乐乐团');
INSERT INTO `words` VALUES (5219, 'ordeal', 'ɔːˈdiːl', 'n.', '磨难;折磨;煎熬;', '...the painful ordeal of the last eight months...', '过去8个月的痛苦煎熬');
INSERT INTO `words` VALUES (5220, 'orderly', 'ˈɔːdəli', 'adj.', '整洁的;有秩序的;有条理的;', 'The organizers guided them in orderly fashion out of the building...', '组织者引领他们井然有序地走出大楼。');
INSERT INTO `words` VALUES (5221, 'ordinarily', 'ˈɔːdnrəli', 'adv.', '普通地;平常地;正常地;', 'The streets would ordinarily have been full of people. There was no one...', '街上通常应该人满为患，当时却空无一人。');
INSERT INTO `words` VALUES (5222, 'ordinary', 'ˈɔːdnri', 'adj.', '普通的;平常的;一般的;', 'I strongly suspect that most ordinary people would agree with me...', '我坚定地认为绝大部分老百姓会赞同我的观点。');
INSERT INTO `words` VALUES (5223, 'ore', 'ɔː(r)', 'n.', '矿石;矿砂;矿;', '...a huge iron ore mine.', '巨大的铁矿山');
INSERT INTO `words` VALUES (5224, 'organ', 'ˈɔːɡən', 'n.', '(人体或动植物的)器官;阴茎;阳物;', '...damage to the muscles and internal organs.', '对肌肉与内脏的损伤');
INSERT INTO `words` VALUES (5225, 'organic', 'ɔːˈɡænɪk', 'adj.', '有机的;不使用化肥的;绿色的;', 'Organic farming is expanding everywhere.', '有机农业正在各地迅速发展起来。');
INSERT INTO `words` VALUES (5226, 'organism', 'ˈɔːɡənɪzəm', 'n.', '有机体;生物;(尤指)微生物;', 'Not all chemicals normally present in living organisms are harmless.', '并不是所有常见于生物体中的化学物质都无害。');
INSERT INTO `words` VALUES (5227, 'orient', 'ˈɔːrient', 'v.', '朝向;面对;确定方向;', 'You will need the time to orient yourself to your new way of eating.', '你还需要一些时间才能适应新的饮食方式。');
INSERT INTO `words` VALUES (5228, 'oriental', 'ˌɔːriˈentl', 'adj.', '东方(尤指中国和日本)的;东方人的;', 'There were Oriental carpets on the floors.', '地上铺的是来自东方的地毯。');
INSERT INTO `words` VALUES (5229, 'orientation', 'ˌɔːriənˈteɪʃn', 'n.', '方向;目标;定向;', '...a marketing orientation...', '营销定位');
INSERT INTO `words` VALUES (5230, 'oriented', 'ˈɔːrientɪd', 'v.', '朝向;面对;确定方向;', 'It seems almost inevitable that North African economies will still be primarily oriented towards Europe...', '看来北非经济体几乎注定还将以欧洲为主导。');
INSERT INTO `words` VALUES (5231, 'origin', 'ˈɒrɪdʒɪn', 'n.', '起源;源头;起因;', '...theories about the origin of life...', '有关生命起源的各种理论');
INSERT INTO `words` VALUES (5232, 'original', 'əˈrɪdʒənl', 'adj.', '原来的;起初的;最早的;', 'The original plan was to hold an indefinite stoppage...', '最初的计划是无限期停工。');
INSERT INTO `words` VALUES (5233, 'originality', 'əˌrɪdʒəˈnæləti', 'n.', '独创性;创意;独特构思;', 'The original plan was to hold an indefinite stoppage...', '最初的计划是无限期停工。');
INSERT INTO `words` VALUES (5234, 'originally', 'əˈrɪdʒənəli', 'adv.', '原来;起初;', 'The plane has been kept in service far longer than originally intended...', '这架飞机已经超期服役很长时间了。');
INSERT INTO `words` VALUES (5235, 'originate', 'əˈrɪdʒɪneɪt', 'v.', '起源;发源;发端于;', 'The disease originated in Africa...', '这种病源于非洲。');
INSERT INTO `words` VALUES (5236, 'ornament', 'ˈɔːnəmənt , ˈɔːnəment', 'n.', '装饰品;首饰;饰物;', '...a shelf containing a few photographs and ornaments.', '摆放着几张照片和些许装饰品的架子');
INSERT INTO `words` VALUES (5237, 'ornamental', 'ˌɔːnəˈmentl', 'adj.', '装饰性的;点缀的;', '...ornamental trees.', '观赏树');
INSERT INTO `words` VALUES (5238, 'orphan', 'ˈɔːfn', 'n.', '孤儿;', 'I\'m an orphan and pretty much grew up on my own.', '我是个孤儿，差不多是靠自己长大的。');
INSERT INTO `words` VALUES (5239, 'oscillate', 'ˈɒsɪleɪt', 'v.', '(情感或行为)摇摆，变化;摆动;振动;', 'I checked to see if the needle indicating volume was oscillating.', '我查看了那根音量指针是否在摆动。');
INSERT INTO `words` VALUES (5240, 'oscillation', 'ˌɒsɪˈleɪʃn', 'n.', '摆动;摇摆;振动;', 'I checked to see if the needle indicating volume was oscillating.', '我查看了那根音量指针是否在摆动。');
INSERT INTO `words` VALUES (5241, 'osmosis', 'ɒzˈməʊsɪs', 'n.', '透析;渗透;耳濡目染;', '...the processes of diffusion and osmosis.', '扩散与渗透过程');
INSERT INTO `words` VALUES (5242, 'ostensible', 'ɒˈstensəbl', 'adj.', '表面的;宣称的;假托的;', 'The ostensible purpose of these meetings was to gather information on financial strategies.', '这些会议声称旨在收集有关金融策略的信息。');
INSERT INTO `words` VALUES (5243, 'ostentation', 'ˌɒstenˈteɪʃn', 'n.', '(对财富、知识、技能的)炫耀，卖弄，夸示;', 'Consumers are abandoning the excess and ostentation of the 1980s...', '消费者摒弃了20世纪80年代铺张摆阔的陋习。');
INSERT INTO `words` VALUES (5244, 'other', 'ˈʌðə(r)', 'adj./pron.', '另外;其他;(指两个人或事物中的第二个)那个，另一个;', 'They were just like any other young couple...', '他们同别的年轻夫妇没什么两样。');
INSERT INTO `words` VALUES (5245, 'otherwise', 'ˈʌðəwaɪz', 'adv.', '否则;不然;除此以外;', 'Make a note of the questions you want to ask. You will invariably forget some of them otherwise...', '把想提的问题记下来，否则总是会漏掉其中的一些。');
INSERT INTO `words` VALUES (5246, 'ounce', 'aʊns', 'n.', '盎司(重量单位，¹⁄₁₆磅，等于28.35克);少许;少量;', '...four ounces of sugar.', '4盎司糖');
INSERT INTO `words` VALUES (5247, 'oust', 'aʊst', 'v.', '剥夺;罢免;革职;', 'The leaders have been ousted from power by nationalists...', '这些领导人被民族主义者赶下了台。');
INSERT INTO `words` VALUES (5248, 'outage', 'ˈaʊtɪdʒ', 'n.', '停电(等)期间;', 'A windstorm in Washington is causing power outages throughout the region.', '风暴造成整个华盛顿地区电力供应中断。');
INSERT INTO `words` VALUES (5249, 'outbreak', 'ˈaʊtbreɪk', 'n.', '(暴力、疾病等坏事的)爆发，突然发生;', 'The four-day festival ended a day early after an outbreak of violence involving hundreds of youths.', '一场有数百名年轻人卷入的暴力活动发生后，为期4天的节庆活动被迫提前一天结束。');
INSERT INTO `words` VALUES (5250, 'outcast', 'ˈaʊtkɑːst', 'n.', '被抛弃者;被排斥者;', 'He had always been an outcast, unwanted and alone...', '他一直都被视为异类，无人理睬，形单影只。');
INSERT INTO `words` VALUES (5251, 'outcome', 'ˈaʊtkʌm', 'n.', '结果;效果;', 'Mr. Singh said he was pleased with the outcome...', '辛格先生说他对这一结果感到满意。');
INSERT INTO `words` VALUES (5252, 'outcry', 'ˈaʊtkraɪ', 'n.', '呐喊;怒吼;强烈的抗议;', 'The killing caused an international outcry...', '这起谋杀引起了国际社会的强烈抗议。');
INSERT INTO `words` VALUES (5253, 'outdated', 'ˌaʊtˈdeɪtɪd', 'adj.', '过时的;陈旧的;', '...outdated and inefficient factories...', '陈旧、低效的工厂');
INSERT INTO `words` VALUES (5254, 'outdo', 'ˌaʊtˈduː', 'v.', '胜过;优于;', 'It was important for me to outdo them, to feel better than they were...', '超过他们，感到自己比他们更优秀，这点对我来说很重要。');
INSERT INTO `words` VALUES (5255, 'outdoor', 'ˈaʊtdɔː(r)', 'adj.', '户外的;室外的;', 'If you enjoy outdoor activities, this is the trip for you...', '如果你喜欢户外运动，这一旅行很适合你。');
INSERT INTO `words` VALUES (5256, 'outdoors', 'ˌaʊtˈdɔːz', 'adv.', '在户外;在野外;', 'It was warm enough to be outdoors all afternoon...', '天气很暖和，整个下午都可以呆在户外。');
INSERT INTO `words` VALUES (5257, 'outer', 'ˈaʊtə(r)', 'adj.', '外表的;外边的;远离中心的;', 'He heard a voice in the outer room.', '他听到外屋有人说话。');
INSERT INTO `words` VALUES (5258, 'outermost', 'ˈaʊtəməʊst', 'adj.', '最外边的;最远的;', '...the outermost corners of each room.', '每间屋最远的角落');
INSERT INTO `words` VALUES (5259, 'outfit', 'ˈaʊtfɪt', 'n.', '全套服装，装束(尤指为某场合或目的);团队;小组;', 'She was wearing an outfit she\'d bought the previous day...', '她穿着前一天买的那套衣服。');
INSERT INTO `words` VALUES (5260, 'outgoing', 'ˈaʊtɡəʊɪŋ', 'adj.', '爱交际的;友好的;外向的;', '...the outgoing director of the Edinburgh International Festival.', '即将卸任的爱丁堡国际艺术节主席');
INSERT INTO `words` VALUES (5261, 'outing', 'ˈaʊtɪŋ', 'n.', '(集体)出外游玩(或学习等);远足;参赛;', 'One evening, she made a rare outing to the local discotheque.', '她有天晚上破天荒地去了一次当地的迪斯科舞厅。');
INSERT INTO `words` VALUES (5262, 'outlandish', 'aʊtˈlændɪʃ', 'adj.', '古怪的;极不寻常的;奇特的;', 'They appeared at parties in outlandish clothes...', '他们穿着奇装异服参加各种聚会。');
INSERT INTO `words` VALUES (5263, 'outlaw', 'ˈaʊtlɔː', 'v.', '宣布…不合法;使…成为非法;(旧时)剥夺(某人的)法律权益;', 'In 1975 gambling was outlawed...', '1975年赌博被宣布为非法。');
INSERT INTO `words` VALUES (5264, 'outlay', 'ˈaʊtleɪ', 'n.', '(启动新项目的)开支，费用;', 'Apart from the capital outlay of buying the machine, dishwashers can actually save you money...', '除了购置所用开支，洗碗机实际上能为你省钱。');
INSERT INTO `words` VALUES (5265, 'outlet', 'ˈaʊtlet', 'n.', '(感情、思想、精力发泄的)出路;表现机会;专营店;', '...the largest retail outlet in the city.', '该市最大的零售商店');
INSERT INTO `words` VALUES (5266, 'outline', 'ˈaʊtlaɪn', 'v.', '概述;略述;显示…的轮廓;', 'The mayor outlined his plan to clean up the town\'s image...', '市长概述了他整顿市容的计划。');
INSERT INTO `words` VALUES (5267, 'outlines', 'ˈaʊtlaɪnz', 'v.', '概述;略述;显示…的轮廓;', 'The mayor outlined his plan to clean up the town\'s image...', '市长概述了他整顿市容的计划。');
INSERT INTO `words` VALUES (5268, 'outlook', 'ˈaʊtlʊk', 'n.', '观点;见解;世界观;', 'The illness had a profound effect on his outlook...', '这场病对他的人生观产生了深刻的影响。');
INSERT INTO `words` VALUES (5269, 'outlying', 'ˈaʊtlaɪɪŋ', 'adj.', '边远的;偏远的;远离市镇的;', 'Tourists can visit outlying areas like the Napa Valley Wine Country...', '游客可以前往像纳帕谷酒乡这样远离城市喧嚣的地方游览。');
INSERT INTO `words` VALUES (5270, 'outmoded', 'ˌaʊtˈməʊdɪd', 'adj.', '过时的;已无用的;', 'Romania badly needs aid to modernise its outmoded industries...', '罗马尼亚亟需援助以使其落后的工业现代化。');
INSERT INTO `words` VALUES (5271, 'outnumber', 'ˌaʊtˈnʌmbə(r)', 'v.', '(在数量上)压倒，比…多;', '...a town where men outnumber women four to one...', '男性比女性多3倍的城镇');
INSERT INTO `words` VALUES (5272, 'outperform', 'ˌaʊtpəˈfɔːm', 'v.', '(效益上)超过，胜过;', 'In recent years the Austrian economy has outperformed most other industrial economies...', '近几年，奥地利的经济发展超过了其他多数工业国。');
INSERT INTO `words` VALUES (5273, 'output', 'ˈaʊtpʊt', 'n.', '(人、机器、机构的)产量;输出;输出功率;', 'Manual workers need a good breakfast for high-energy output...', '体力劳动者身体消耗巨大，早餐要丰盛一些。');
INSERT INTO `words` VALUES (5274, 'outrageous', 'aʊtˈreɪdʒəs', 'adj.', '骇人的;无法容忍的;反常的;', 'I must apologise for my outrageous behaviour...', '我必须为自己极端无礼的行为道歉。');
INSERT INTO `words` VALUES (5275, 'outset', 'ˈaʊtset', 'n.', '开端 开始；起始;', 'Decide at the outset what kind of learning programme you want to follow...', '开始时就选定自己的学习方案。');
INSERT INTO `words` VALUES (5276, 'outsider', 'ˌaʊtˈsaɪdə(r)', 'n.', '外人;局外人;(组织、行业)外部的人;', 'The most likely outcome may be to subcontract much of the work to an outsider.', '最可能的结果是将大量的工作分包出去。');
INSERT INTO `words` VALUES (5277, 'outskirts', 'ˈaʊtskɜːts', 'n.', '(市镇的)边缘地带;市郊;', 'Hours later we reached the outskirts of New York.', '我们几小时后到达了纽约市郊。');
INSERT INTO `words` VALUES (5278, 'outspoken', 'aʊtˈspəʊkən', 'adj.', '直率;坦诚;', 'Some church leaders have been outspoken in their support for political reform in Kenya...', '一些教会领袖明确表示支持肯尼亚的政治改革。');
INSERT INTO `words` VALUES (5279, 'outstanding', 'aʊtˈstændɪŋ', 'adj.', '优秀的;杰出的;出色的;', 'Derartu is an outstanding athlete and deserved to win.', '德拉尔图是位优秀的运动员，赢得比赛也是理所当然。');
INSERT INTO `words` VALUES (5280, 'outstrip', 'ˌaʊtˈstrɪp', 'v.', '比…大(或重要等);超过;胜过;', 'In the mid-eighteenth century the production of food far outstripped the rise in population...', '18世纪中叶食物的产量远远超过人口的增长。');
INSERT INTO `words` VALUES (5281, 'outward', 'ˈaʊtwəd', 'adj.', '表面的;外表的;外出的;', 'Tickets must be bought seven days in advance, with outward and return journey dates specified.', '购票需提前7天，并确定往返日期。');
INSERT INTO `words` VALUES (5282, 'outwards', 'ˈaʊtwədz', 'adv.', '向外;朝外;', 'The top door opened outwards...', '顶上的门朝外开。');
INSERT INTO `words` VALUES (5283, 'outweigh', 'ˌaʊtˈweɪ', 'v.', '重于;大于;超过;', 'The medical benefits of x-rays far outweigh the risk of having them...', 'X光在医学上的益处远甚于它带来的风险。');
INSERT INTO `words` VALUES (5284, 'oval', 'ˈəʊvl', 'adj.', '椭圆形的;卵形的;', 'He was a man in his late thirties, with fine, dark hair and a pale oval face.', '他年近四十，头发乌黑亮泽，长着一张苍白的鹅蛋脸。');
INSERT INTO `words` VALUES (5285, 'ovation', 'əʊˈveɪʃn', 'n.', '热烈鼓掌;热烈欢迎;', 'They had lost by a wide margin, but their supporters gave them a defiant, loyal ovation.', '他们以较大的差距败北，但支持者们还是给予了热烈的掌声，以示对对手的不服和对他们的忠诚。');
INSERT INTO `words` VALUES (5286, 'oven', 'ˈʌvn', 'n.', '烤箱;烤炉;', 'Put the onions and ginger in the oven and let them roast for thirty minutes.', '将洋葱和姜放进烤箱烘烤30分钟。');
INSERT INTO `words` VALUES (5287, 'overcast', 'ˌəʊvəˈkɑːst', 'adj.', '阴天的;多云的;阴暗的;', 'For three days it was overcast...', '连续3天都是阴天。');
INSERT INTO `words` VALUES (5288, 'overcharge', 'ˌəʊvəˈtʃɑːdʒ', 'v.', '多收(某人的)钱;', 'If you feel a taxi driver has overcharged you, say so...', '如果你觉得出租车司机敲了你的竹杠，就说出来。');
INSERT INTO `words` VALUES (5289, 'overdo', 'ˌəʊvəˈduː', 'v.', '做得过分;做得过火;夸张;', '...a recognition by the US central bank that it may have overdone its tightening when it pushed rates up to 6 per cent...', '美国中央银行承认将利率上调至6%可能是过度紧缩了银根');
INSERT INTO `words` VALUES (5290, 'overdraft', 'ˈəʊvədrɑːft', 'n.', '透支额;透支安排;', 'When I left Cambridge I had a £10,000 overdraft...', '我离开剑桥时，透支了1万英镑。');
INSERT INTO `words` VALUES (5291, 'overdue', 'ˌəʊvəˈdjuː', 'adj.', '(到期)未付的，未做的，未还的;过期的;早该发生的;', 'This debate is long overdue...', '这场辩论早该进行了。');
INSERT INTO `words` VALUES (5292, 'overhear', 'ˌəʊvəˈhɪə(r)', 'v.', '偶然听到;无意中听到;', 'I overheard two doctors discussing my case.', '我无意中听到两位医生讨论我的病情。');
INSERT INTO `words` VALUES (5293, 'overheat', 'ˌəʊvəˈhiːt', 'v.', '变得过热;使过热;发展过热;', 'The engine was overheating and the car was not handling well...', '发动机过热，车子不好操纵。');
INSERT INTO `words` VALUES (5294, 'overlook', 'ˌəʊvəˈlʊk', 'v.', '忽略;未注意到;(对不良现象等)不予理会，视而不见;', 'Pretty and comfortable rooms overlook a flower-filled garden...', '漂亮舒适的房间俯视着花团锦簇的花园。');
INSERT INTO `words` VALUES (5295, 'overnight', 'ˌəʊvəˈnaɪt , ˈəʊvənaɪt', 'adv.', '在夜间;在晚上;突然;', 'The weather remained calm overnight...', '整晚都悄然无风。');
INSERT INTO `words` VALUES (5296, 'overpass', 'ˈəʊvəpɑːs', 'n.', '上跨式立交;天桥;跨线桥;', '...a $16 million highway overpass over Route 1.', '造价1,600万美元的1号公路上的上跨公路桥');
INSERT INTO `words` VALUES (5297, 'overrun', 'ˈəʊvərʌn , ˌəʊvəˈrʌn', 'v.', '泛滥;横行;肆虐;', 'A group of rebels overran the port area and most of the northern suburbs...', '一群叛乱分子迅速占领了港口地区和大部分城北郊区。');
INSERT INTO `words` VALUES (5298, 'overseas', 'ˌəʊvəˈsiːz', 'adj.', '外国的;海外的;', 'He has returned to South Africa from his long overseas trip.', '他结束了漫长的海外旅行回到南非。');
INSERT INTO `words` VALUES (5299, 'oversee', 'ˌəʊvəˈsiː', 'v.', '监督;监视;', 'Use a surveyor or architect to oversee and inspect the different stages of the work.', '请一位房产检视员或建筑师来监督并检查不同阶段的工作。');
INSERT INTO `words` VALUES (5300, 'oversight', 'ˈəʊvəsaɪt', 'n.', '疏忽;忽略;失察;', 'William was angered and embarrassed by his oversight...', '威廉因为自己的疏忽而羞愤交加。');
INSERT INTO `words` VALUES (5301, 'overt', 'əʊˈvɜːt', 'adj.', '公开的;明显的;不隐瞒的;', 'His recent productions have been beautifully crafted works with little overt political content...', '他最近所创作的都是些精雕细琢的作品，少有鲜明的政治内容。');
INSERT INTO `words` VALUES (5302, 'overtake', 'ˌəʊvəˈteɪk', 'v.', '超过;赶上;(在数量或重要性方面)大于;', 'When he eventually overtook the last truck he pulled over to the inside lane...', '他终于超过了最后那辆卡车后便驶回了内车道。');
INSERT INTO `words` VALUES (5303, 'overtime', 'ˈəʊvətaɪm', 'n.', '加班;加班的时间;加班费;', 'He would work overtime, without pay, to finish a job...', '他会为了完成工作无偿加班。');
INSERT INTO `words` VALUES (5304, 'overture', 'ˈəʊvətʃʊə(r)', 'n.', '(歌剧或芭蕾舞的)序曲，前奏曲;友好姿态;建议;', 'The programme opened with the overture to Wagner\'s Flying Dutchman.', '节目以瓦格纳的歌剧《漂泊的荷兰人》的序曲开场。');
INSERT INTO `words` VALUES (5305, 'overturn', 'ˌəʊvəˈtɜːn', 'v.', '倾倒;倾覆;翻掉;', 'The lorry veered out of control, overturned and smashed into a wall...', '卡车突然失控，翻倒撞上了一堵墙。');
INSERT INTO `words` VALUES (5306, 'overview', 'ˈəʊvəvjuː', 'n.', '概述;纵览;概论;', 'The central section of the book is a historical overview of drug use.', '该书的核心部分对毒品的使用作了历史概述。');
INSERT INTO `words` VALUES (5307, 'overweight', 'ˌəʊvəˈweɪt', 'adj.', '太胖的;超重的;超过限制重量的;', 'Being even moderately overweight increases your risk of developing high blood pressure.', '即使是轻度超重也会增加患高血压的风险。');
INSERT INTO `words` VALUES (5308, 'overwhelm', 'ˌəʊvəˈwelm', 'v.', '(感情或感觉)充溢，难以禁受;压倒;击败;', 'He was overwhelmed by a longing for times past...', '他一心渴望回到从前。');
INSERT INTO `words` VALUES (5309, 'overwhelming', 'ˌəʊvəˈwelmɪŋ', 'adj.', '巨大的;压倒性的;无法抗拒的;', 'The task won\'t feel so overwhelming if you break it down into small, easy-to-accomplish steps...', '如果将这个任务细分为简单易行的若干步骤来做，便不会感到如此无所适从。');
INSERT INTO `words` VALUES (5310, 'overwork', 'ˌəʊvəˈwɜːk', 'v.', '(使)过度劳累，过分努力;', 'He\'s overworking and has got a lot on his mind...', '他工作太累，而且非常操心。');
INSERT INTO `words` VALUES (5311, 'overwrought', 'ˌəʊvəˈrɔːt', 'adj.', '过度紧张的;过分忧烦的;紧张激动的;', 'One overwrought member had to be restrained by friends.', '一位成员过于激动，朋友们不得不进行拦阻。');
INSERT INTO `words` VALUES (5312, 'owe', 'əʊ', 'v.', '欠(债);欠(账);欠(情);', 'The company owes money to more than 60 banks...', '这家公司欠下60多家银行的债。');
INSERT INTO `words` VALUES (5313, 'owing', 'ˈəʊɪŋ', 'adj.', '拖欠;未付;未偿还;', 'The company owes money to more than 60 banks...', '这家公司欠下60多家银行的债。');
INSERT INTO `words` VALUES (5314, 'own', 'əʊn', 'adj./pron.', '(用于强调)自己的，本人的;自己做的;为自己的;', 'My wife decided I should have my own shop...', '我妻子决定让我自己开店。');
INSERT INTO `words` VALUES (5315, 'owner', 'ˈəʊnə(r)', 'n.', '物主;所有权人;主人;', 'The owner of the store was sweeping his floor when I walked in...', '我进去的时候这家商店的店主正在扫地。');
INSERT INTO `words` VALUES (5316, 'ownership', 'ˈəʊnəʃɪp', 'n.', '所有权;产权;物主身份;', 'On January 23rd, America decided to relax its rules on the foreign ownership of its airlines.', '1月23日，美国决定放宽对外国持有美国航空公司所有权的管制规定。');
INSERT INTO `words` VALUES (5317, 'oxide', 'ˈɒksaɪd', 'n.', '氧化物;', 'Atoms of iron in the nail combine with atoms of oxygen from the air to form molecules of iron oxide, or rust.', '钉子里的铁原子与空气中的氧原子结合后形成氧化铁分子，也就是锈。');
INSERT INTO `words` VALUES (5318, 'oxygen', 'ˈɒksɪdʒən', 'n.', '氧;氧气;', 'The human brain needs to be without oxygen for only four minutes before permanent damage occurs.', '人的大脑缺氧4分钟后便会造成永久性损伤。');
INSERT INTO `words` VALUES (5319, 'oyster', 'ˈɔɪstə(r)', 'n.', '牡蛎;蚝;', 'He had two dozen oysters and enjoyed every one of them.', '他吃了两打牡蛎，每一只都吃得津津有味。');
INSERT INTO `words` VALUES (5320, 'ozone', 'ˈəʊzəʊn', 'n.', '臭氧;海边的清新空气;', 'Ozone is best known for its role in screening the Earth from harmful ultraviolet rays from the Sun...', '臭氧最广为人知的作用是保护地球免受太阳紫外线的伤害。');
INSERT INTO `words` VALUES (5321, 'pace', 'peɪs , ˈpɑːkeɪ', 'n.', '(移动的)速度;步速;发生的速度;', 'Many people were not satisfied with the pace of change.', '很多人对变革的步伐不满意。');
INSERT INTO `words` VALUES (5322, 'pacemaker', 'ˈpeɪsmeɪkə(r)', 'n.', '心脏起搏器;领跑人;领跑动物;', 'She was fitted with a pacemaker after suffering serious heart trouble.', '她患上严重的心脏病后安装了心脏起搏器。');
INSERT INTO `words` VALUES (5323, 'pacific', 'pəˈsɪfɪk', 'adj.', '平静的;和平的;爱和平的;', 'The Liberals were traditionally seen as the more pacific party.', '传统上，自由党人被认为更加爱好和平。');
INSERT INTO `words` VALUES (5324, 'pacify', 'ˈpæsɪfaɪ', 'v.', '使平静;平息;抚慰;', 'Is this a serious step, or is this just something to pacify the critics?...', '这一措施是来真的？还是仅仅为了安抚批评人士？');
INSERT INTO `words` VALUES (5325, 'pacing', 'ˈpeɪsɪŋ', 'v.', '来回踱步;走来走去;确定速度;', 'Many people were not satisfied with the pace of change.', '很多人对变革的步伐不满意。');
INSERT INTO `words` VALUES (5326, 'pack', 'pæk', 'v.', '收拾(行李);装(箱);包装;', 'When I was 17, I packed my bags and left home...', '17岁时，我背起行囊离开了家。');
INSERT INTO `words` VALUES (5327, 'package', 'ˈpækɪdʒ', 'n.', '包;盒;袋;', 'I tore open the package.', '我撕开了那个包裹。');
INSERT INTO `words` VALUES (5328, 'packaging', 'ˈpækɪdʒɪŋ', 'n.', '包装材料;外包装;包装工作;', 'It is selling very well, in part because the packaging is so attractive.', '这个卖得非常好，部分原因是包装非常吸引人。');
INSERT INTO `words` VALUES (5329, 'packet', 'ˈpækɪt', 'n.', '(商品的)小包装纸袋，小硬纸板盒;(邮政)小件包裹;一笔巨款;', 'Cook the rice according to instructions on the packet.', '按照包装袋上的说明煮饭。');
INSERT INTO `words` VALUES (5330, 'pact', 'pækt', 'n.', '条约;协议;公约;', 'Last month he signed a new non-aggression pact with Germany...', '上个月，他与德国签订了新的互不侵犯条约。');
INSERT INTO `words` VALUES (5331, 'pad', 'pæd', 'n.', '(吸收液体、保洁或保护用的)软垫，护垫，垫状物;便笺本;拍纸簿;', 'He withdrew the needle and placed a pad of cotton-wool over the spot.', '他把针头拔出，在扎针处按了一块药棉。');
INSERT INTO `words` VALUES (5332, 'padding', 'ˈpædɪŋ', 'n.', '衬料;衬垫;赘语;', '...the foam rubber padding on the headphones...', '头戴式耳机上的泡沫橡胶垫料');
INSERT INTO `words` VALUES (5333, 'paddle', 'ˈpædl', 'n.', '桨;船桨;(机具的)桨状部分;', 'We might be able to push ourselves across with the paddle.', '我们也许可以用桨把自己推过去。');
INSERT INTO `words` VALUES (5334, 'page', 'peɪdʒ', 'n.', '(书刊或纸张的)页，面，张，版;(计算机的)页面，版面;历史篇章，历史篇页(指历史大事或时期);', 'Where\'s your book? Take it out and turn to page 4.', '你的书呢？拿出来，翻到第4页。');
INSERT INTO `words` VALUES (5335, 'paging', 'ˈpeɪdʒɪŋ', 'v.', '(在公共传呼系统上)呼叫;用传呼机传呼(某人);', 'Where\'s your book? Take it out and turn to page 4.', '你的书呢？拿出来，翻到第4页。');
INSERT INTO `words` VALUES (5336, 'pain', 'peɪn', 'n.', '(身体上的)疼痛;痛苦;苦恼;', '...back pain.', '背痛');
INSERT INTO `words` VALUES (5337, 'painful', 'ˈpeɪnfl', 'adj.', '令人疼痛的;令人痛苦(或难过、难堪)的;不愉快的;', 'Her glands were swollen and painful...', '她腺体肿痛。');
INSERT INTO `words` VALUES (5338, 'painless', 'ˈpeɪnləs', 'adj.', '无痛的;愉快的;轻松的;', 'Acupuncture treatment is gentle, painless, and, invariably, most relaxing...', '针灸疗法柔和，无痛，而且总是令人非常放松。');
INSERT INTO `words` VALUES (5339, 'pains', 'peɪnz', 'n.', '(身体上的)疼痛;痛苦;苦恼;', '...back pain.', '背痛');
INSERT INTO `words` VALUES (5340, 'painstaking', 'ˈpeɪnzteɪkɪŋ', 'adj.', '需细心的;辛苦的;需专注的;', 'Forensic experts carried out a painstaking search of the debris.', '法医专家对残骸进行了极其仔细的搜索。');
INSERT INTO `words` VALUES (5341, 'paint', 'peɪnt', 'n.', '油漆;油漆涂层;绘画颜料;', '...a pot of red paint...', '一罐红漆');
INSERT INTO `words` VALUES (5342, 'painting', 'ˈpeɪntɪŋ', 'n.', '绘画;油画;作画;', '...a large oil-painting of Queen Victoria.', '维多利亚女王的大幅油画');
INSERT INTO `words` VALUES (5343, 'pair', 'peə(r)', 'n.', '一双;一对;分两个相连接部分的物体;', '...a pair of socks.', '一双袜子');
INSERT INTO `words` VALUES (5344, 'pairs', 'peəz', 'n.', '一双;一对;分两个相连接部分的物体;', '...a pair of socks.', '一双袜子');
INSERT INTO `words` VALUES (5345, 'palace', 'ˈpæləs', 'n.', '王宫;宫殿;总统府;', '...Buckingham Palace...', '白金汉宫');
INSERT INTO `words` VALUES (5346, 'palatable', 'ˈpælətəbl', 'adj.', '可口的;味美的;宜人的;', '...flavourings and preservatives, designed to make the food look more palatable.', '用于使食物看起来更加可口的调味料和防腐剂');
INSERT INTO `words` VALUES (5347, 'pale', 'peɪl', 'adj.', '灰白的;苍白的;白皙的;', 'Migrating birds filled the pale sky...', '灰白的天空中满是迁徙的飞鸟。');
INSERT INTO `words` VALUES (5348, 'palpitate', 'ˈpælpɪteɪt', 'v.', '急速跳动，悸动(尤指因恐惧或兴奋);', 'He felt suddenly faint, and his heart began to palpitate.', '他突然感到眩晕，心脏开始悸动。');
INSERT INTO `words` VALUES (5349, 'pamper', 'ˈpæmpə(r)', 'v.', '细心照顾;精心护理;娇惯;', 'Why don\'t you let your mother pamper you for a while?...', '何不让你母亲宠你一段时间呢？');
INSERT INTO `words` VALUES (5350, 'pan', 'pæn , pɑːn', 'n.', '平锅;平底锅;一锅的量;', 'Heat the butter and oil in a large pan.', '把黄油和食用油放到大平底锅里加热。');
INSERT INTO `words` VALUES (5351, 'panacea', 'ˌpænəˈsiːə', 'n.', '万灵药;万能之计;', 'Membership of the ERM is not a panacea for Britain\'s economic problems...', '加入欧洲汇率机制并不是解决英国经济问题的万灵丹。');
INSERT INTO `words` VALUES (5352, 'panel', 'ˈpænl', 'n.', '(门、墙等上面的)嵌板，镶板，方格板块;(车身的)金属板，板金;(衣服上的)镶条，嵌条，饰片;', 'He assembled a panel of scholars to advise him...', '他组织了一个学者小组为他出谋划策。');
INSERT INTO `words` VALUES (5353, 'panic', 'ˈpænɪk', 'n.', '惊恐;恐慌;人心惶惶的局面;', 'An earthquake hit the capital, causing panic among the population...', '首都发生了地震，引发民众恐慌。');
INSERT INTO `words` VALUES (5354, 'panorama', 'ˌpænəˈrɑːmə', 'n.', '全景;(某专题或事件的)全面叙述，综合研究;全景画卷;', 'Horton looked out over a panorama of fertile valleys and gentle hills.', '霍顿向外望去，肥沃的谷地和平缓的丘陵尽收眼底。');
INSERT INTO `words` VALUES (5355, 'panoramic', 'ˌpænəˈræmɪk', 'adj.', '全景的;远景的;', 'The terrain\'s high points provide a panoramic view of Los Angeles.', '在这个地区的制高点能够看到洛杉矶的全景。');
INSERT INTO `words` VALUES (5356, 'pant', 'pænt', 'v.', '气喘;喘息;', 'She climbed rapidly until she was panting with the effort.', '她爬得很快，结果累得气喘吁吁。');
INSERT INTO `words` VALUES (5357, 'pants', 'pænts', 'n.', '内裤;短裤;裤子;', 'I put on my bra and pants.', '我戴上乳罩，穿上内裤。');
INSERT INTO `words` VALUES (5358, 'paper', 'ˈpeɪpə(r)', 'n.', '纸;纸张;报纸;', 'He wrote his name down on a piece of paper for me...', '他把他的名字写在一张纸上交给我。');
INSERT INTO `words` VALUES (5359, 'paperback', 'ˈpeɪpəbæk', 'n.', '平装书;简装书;', 'She said she would buy the book when it comes out in paperback.', '她说等那本书出平装本时再买。');
INSERT INTO `words` VALUES (5360, 'papers', 'ˈpeɪpəz', 'n.', '纸;纸张;报纸;', 'He wrote his name down on a piece of paper for me...', '他把他的名字写在一张纸上交给我。');
INSERT INTO `words` VALUES (5361, 'par', 'pɑː(r)', 'n.', '标准杆数;(股票的)面值，票面价值;', 'Parts of Glasgow are on a par with the worst areas of London and Liverpool for burglaries.', '格拉斯哥部分地区的入室盗窃案与伦敦、利物浦最乱的地区一样猖狂。');
INSERT INTO `words` VALUES (5362, 'parachute', 'ˈpærəʃuːt', 'n.', '降落伞;', 'They fell 41,000 ft. before opening their parachutes...', '他们下降了41,000英尺后才拉开伞。');
INSERT INTO `words` VALUES (5363, 'parade', 'pəˈreɪd', 'n.', '游行;检阅;阅兵;', 'A military parade marched slowly and solemnly down Pennsylvania Avenue.', '阅兵队列沿宾夕法尼亚大道缓慢而庄严地行进。');
INSERT INTO `words` VALUES (5364, 'paradise', 'ˈpærədaɪs', 'n.', '(某些宗教所指的)天堂，天国;天堂，乐土，乐园(指美好的环境);(某类活动或某类人的)乐园，完美去处;', 'The Koran describes paradise as a place containing a garden of delight...', '《古兰经》将天国描述成一个乐园。');
INSERT INTO `words` VALUES (5365, 'paradox', 'ˈpærədɒks', 'n.', '矛盾的人(或事物、情况);似非而是的隽语;悖论;', 'The paradox is that the region\'s most dynamic economies have the most primitive financial systems...', '矛盾之处在于这个地区最有活力的经济体却有着最落后的金融体系。');
INSERT INTO `words` VALUES (5366, 'paragraph', 'ˈpærəɡrɑːf', 'n.', '段;段落;', 'The length of a paragraph depends on the information it conveys...', '段落的长度取决于它所传达的信息。');
INSERT INTO `words` VALUES (5367, 'paralysis', 'pəˈræləsɪs', 'n.', '麻痹;瘫痪;(活动、工作等)能力的完全丧失;', '...paralysis of the leg.', '腿部麻痹');
INSERT INTO `words` VALUES (5368, 'parameter', 'pəˈræmɪtə(r)', 'n.', '决定因素;规范;范围;', 'That would be enough to make sure we fell within the parameters of our loan agreement.', '那就足以确保我们符合贷款协议的范围。');
INSERT INTO `words` VALUES (5369, 'paramount', 'ˈpærəmaʊnt', 'adj.', '至为重要的;首要的;至高无上的;', 'The child\'s welfare must be seen as paramount...', '必须把儿童福利摆在首位。');
INSERT INTO `words` VALUES (5370, 'paranoia', 'ˌpærəˈnɔɪə', 'n.', '妄想症;偏执狂;(对别人的)无端恐惧，多疑;', 'The mood is one of paranoia and expectation of war.', '那是一种对战争既畏惧又期待的心理。');
INSERT INTO `words` VALUES (5371, 'paraphernalia', 'ˌpærəfəˈneɪliə', 'n.', '(尤指某活动所需的)装备，大量用品，私人物品;', '...a large courtyard full of builders\' paraphernalia...', '堆满建筑工人所需装备的大庭院');
INSERT INTO `words` VALUES (5372, 'paraphrase', 'ˈpærəfreɪz', 'v.', '(用更容易理解的文字)解释，释义，意译;', 'Parents, to paraphrase Philip Larkin, can seriously damage your health...', '菲利普·拉金的意思是:父母可能会严重损害你的健康。');
INSERT INTO `words` VALUES (5373, 'pardon', 'ˈpɑːdn', 'int.', '(用于请求别人重复某事)什么，请再说一遍;抱歉;对不起;', '\'Will you let me open it?\' — \'Pardon?\' — \'Can I open it?\'...', '“我能打开它吗？”——“请再说一遍？”——“我能打开它吗？”');
INSERT INTO `words` VALUES (5374, 'parent', 'ˈpeərənt', 'n.', '父亲(或母亲);(动、植物的)亲本，亲代，父本，母本;创始机构;', 'Children need their parents...', '孩子们需要父母。');
INSERT INTO `words` VALUES (5375, 'parish', 'ˈpærɪʃ', 'n.', '堂区;教区;(英格兰)乡村的行政小区;', '...the parish of St Mark\'s, Lakenham...', '莱克纳姆圣马克教区');
INSERT INTO `words` VALUES (5376, 'parity', 'ˈpærəti', 'n.', '(尤指薪金或地位)平等，相同，对等;(两国货币的)平价;', 'Women have yet to achieve wage or occupational parity in many fields...', '女性在很多领域还没能争取到薪金、职位方面的平等。');
INSERT INTO `words` VALUES (5377, 'park', 'pɑːk', 'n.', '公园;专用区;园区;', '...Regent\'s Park...', '摄政公园');
INSERT INTO `words` VALUES (5378, 'parking', 'ˈpɑːkɪŋ', 'n.', '停车;泊车;停车场;', 'In many towns parking is allowed only on one side of the street...', '很多小镇只允许将车停在街道的一侧。');
INSERT INTO `words` VALUES (5379, 'parliament', 'ˈpɑːləmənt', 'n.', '议会;国会;英国议会(包括下议院和上议院);', 'Parliament today approved the policy, but it has not yet become law.', '议会于今天批准了这项政策，但尚未成为法律。');
INSERT INTO `words` VALUES (5380, 'parody', 'ˈpærədi', 'n.', '滑稽模仿作品(文章、音乐作品或表演的滑稽仿作等);拙劣的模仿;荒诞不经的事;', '\'The Scarlet Capsule\' was a parody of the popular 1959 TV series \'The Quatermass Experiment\'...', '《红胶囊》戏仿了1959年大受欢迎的电视连续剧《夸特马斯试验》。');
INSERT INTO `words` VALUES (5381, 'partake', 'pɑːˈteɪk', 'v.', '吃，喝，享用(尤指给予的东西);参加;参与;', 'They were happy to partake of our feast, but not to share our company.', '他们喜欢享用我们准备的丰盛大餐，但是不喜欢和我们呆在一起。');
INSERT INTO `words` VALUES (5382, 'partial', 'ˈpɑːʃl', 'adj.', '部分的;不完全的;热爱;', 'He managed to reach a partial agreement with both republics.', '他设法与两个共和国都达成了部分协议。');
INSERT INTO `words` VALUES (5383, 'partiality', 'ˌpɑːʃiˈæləti', 'n.', '偏袒;特别喜爱;酷爱;', 'He managed to reach a partial agreement with both republics.', '他设法与两个共和国都达成了部分协议。');
INSERT INTO `words` VALUES (5384, 'partially', 'ˈpɑːʃəli', 'adv.', '部分地;不完全地;', 'Lisa is deaf in one ear and partially blind.', '莉萨一只耳朵失聪，而且部分失明。');
INSERT INTO `words` VALUES (5385, 'participant', 'pɑːˈtɪsɪpənt', 'n.', '参与者;参加者;', '40 of the course participants are offered employment with the company...', '上这门课的人中有40个被该公司聘用。');
INSERT INTO `words` VALUES (5386, 'participate', 'pɑːˈtɪsɪpeɪt', 'v.', '参加;参与;', 'They expected him to participate in the ceremony...', '他们希望他参加典礼。');
INSERT INTO `words` VALUES (5387, 'participation', 'pɑːˌtɪsɪˈpeɪʃn', 'n.', '参加;参与;', 'They expected him to participate in the ceremony...', '他们希望他参加典礼。');
INSERT INTO `words` VALUES (5388, 'particle', 'ˈpɑːtɪkl', 'n.', '颗粒;微粒;粒子;', '...a particle of hot metal...', '热金属微粒');
INSERT INTO `words` VALUES (5389, 'particular', 'pəˈtɪkjələ(r)', 'adj.', '专指的，特指的(与泛指相对);不寻常的;格外的;', 'I remembered a particular story about a postman who was a murderer...', '我记得有个故事，讲的是一个邮递员杀人的事情。');
INSERT INTO `words` VALUES (5390, 'particularly', 'pəˈtɪkjələli', 'adv.', '特别;尤其;', 'Keep your office space looking good, particularly your desk...', '保持办公环境的整洁，尤其是你的桌子。');
INSERT INTO `words` VALUES (5391, 'particulars', 'pəˈtɪkjʊləz', 'n.', '细节;详情;', 'You will find all the particulars in Chapter 9...', '你在第9章会找到所有细节。');
INSERT INTO `words` VALUES (5392, 'particulate', 'pɑːˈtɪkjələt', 'adj.', '微粒(形式)的;颗粒(状)的;', '...the particulate pollution in our atmosphere.', '我们大气中的颗粒物污染');
INSERT INTO `words` VALUES (5393, 'parting', 'ˈpɑːtɪŋ', 'n.', '离别;分手;分别;', 'Parting from any one of you for even a short time is hard...', '离开你们中任何一个人哪怕只是一小段时间都舍不得。');
INSERT INTO `words` VALUES (5394, 'partisan', 'ˌpɑːtɪˈzæn', 'adj.', '(对某个人、团体或思想)过分支持的，偏护的，盲目拥护的;', 'He is clearly too partisan to be a referee.', '他倾向性过于明显，当不了裁判。');
INSERT INTO `words` VALUES (5395, 'partition', 'pɑːˈtɪʃn', 'n.', '隔断;隔扇;隔板墙;', '...new offices divided only by glass partitions...', '只用玻璃隔板隔开的新办公室');
INSERT INTO `words` VALUES (5396, 'partly', 'ˈpɑːtli', 'adv.', '一定程度上;部分地;', 'It\'s partly my fault...', '这在一定程度上是我的错。');
INSERT INTO `words` VALUES (5397, 'partner', 'ˈpɑːtnə(r)', 'n.', '配偶;性伴侣;合伙人;', 'Wanting other friends doesn\'t mean you don\'t love your partner.', '想交其他朋友并不意味着你不爱你的伴侣。');
INSERT INTO `words` VALUES (5398, 'partnership', 'ˈpɑːtnəʃɪp', 'n.', '伙伴关系;合伙人身份;合作关系;', '...the partnership between Germany\'s banks and its businesses...', '德国的银行和企业之间的伙伴关系');
INSERT INTO `words` VALUES (5399, 'partridge', 'ˈpɑːtrɪdʒ', 'n.', '山鹑;山鹑肉;', '...to shoot a partridge.', '打山鹑');
INSERT INTO `words` VALUES (5400, 'party', 'ˈpɑːti', 'n.', '政党;党派;聚会;', '...a member of the Labour party.', '工党党员');
INSERT INTO `words` VALUES (5401, 'pass', 'pɑːs', 'v.', '通过;走过;沿某方向前进;', 'As she passed the library door, the telephone began to ring...', '她经过图书室门口时，电话响了。');
INSERT INTO `words` VALUES (5402, 'passable', 'ˈpɑːsəbl', 'adj.', '过得去的;尚可的;通行无阻;', 'Stan puffed out his thin cheeks in a passable imitation of his dad...', '斯坦鼓起瘦瘦的脸颊，像模像样地模仿着他的爸爸。');
INSERT INTO `words` VALUES (5403, 'passage', 'ˈpæsɪdʒ', 'n.', '通道;走廊;(体内通气、输液等的)管路;', 'Harry stepped into the passage and closed the door behind him.', '哈里走进过道，随手关上了门。');
INSERT INTO `words` VALUES (5404, 'passageway', 'ˈpæsɪdʒweɪ', 'n.', '走廊;通道;过道;', 'Outside, in the passageway, I could hear  people moving about...', '我能听到有人在外面走廊里走动。');
INSERT INTO `words` VALUES (5405, 'passenger', 'ˈpæsɪndʒə(r)', 'n.', '乘客;旅客;白吃饭的人;', 'Mr Fullemann was a passenger in the car when it crashed.', '出车祸时，福勒曼先生正在那辆车上。');
INSERT INTO `words` VALUES (5406, 'passerby', 'ˈpɑːsəˈbaɪ', 'n.', '过路人;经过者;', 'A passer-by described what he saw moments after the car bomb had exploded...', '一位过路人描述了汽车炸弹爆炸后他看到的情形。');
INSERT INTO `words` VALUES (5407, 'passion', 'ˈpæʃn', 'n.', '强烈情感;激情;盛怒;', '...my passion for a dark-haired, slender boy named James.', '我对一个名叫詹姆斯的黑头发高挑个儿男孩的热恋');
INSERT INTO `words` VALUES (5408, 'passionate', 'ˈpæʃənət', 'adj.', '拥有(或表现出)强烈性爱的;情意绵绵的;怒不可遏的;', '...his passionate commitment to peace...', '他热情满怀地献身于和平事业');
INSERT INTO `words` VALUES (5409, 'passionately', '\'pæʃənətlɪ', 'adv.', '非常;充满热情地;激动地;', '...his passionate commitment to peace...', '他热情满怀地献身于和平事业');
INSERT INTO `words` VALUES (5410, 'passive', 'ˈpæsɪv', 'adj.', '消极的;被动的;(动词形式)被动语态的;', 'His passive attitude made things easier for me...', '他顺从的态度让我做起事来要轻松些。');
INSERT INTO `words` VALUES (5411, 'passivity', 'pæˈsɪvəti', 'n.', '被动;消极状态;', 'His passive attitude made things easier for me...', '他顺从的态度让我做起事来要轻松些。');
INSERT INTO `words` VALUES (5412, 'passport', 'ˈpɑːspɔːt', 'n.', '护照;途径;路子;', 'You should take your passport with you when changing money.', '兑换货币时应该带着护照。');
INSERT INTO `words` VALUES (5413, 'password', 'ˈpɑːswɜːd', 'n.', '暗语;暗号;口令;', 'Advance and give the password...', '上前说出口令。');
INSERT INTO `words` VALUES (5414, 'past', 'pɑːst', 'adj.', '过去的;昔日的;刚过去的;', 'In the past, about a third of the babies born to women with diabetes were lost...', '过去，患有糖尿病的女性生下的孩子中约有1/3夭折了。');
INSERT INTO `words` VALUES (5415, 'paste', 'peɪst', 'n.', '面团;肉(或鱼等)酱(作涂抹料或烹饪用);糨糊;', 'He then sticks it back together with flour paste...', '然后他用面糊把它重新粘了起来。');
INSERT INTO `words` VALUES (5416, 'pastel', 'ˈpæstl', 'n.', '彩色粉笔;蜡笔;粉笔;', '...delicate pastel shades.', '淡雅的色调');
INSERT INTO `words` VALUES (5417, 'pastiche', 'pæˈstiːʃ', 'n.', '刻意模仿的文艺作品;模仿作品;(集多种风格于一身的)混成作品，集锦;', 'Peter Baker\'s bathroom is a brilliant pastiche of expensive interior design.', '彼得·贝克的浴室是高档室内设计的集大成之作。');
INSERT INTO `words` VALUES (5418, 'pastime', 'ˈpɑːstaɪm', 'n.', '消遣;休闲活动;', 'His favourite pastime is golf.', '他最喜欢的消遣是打高尔夫。');
INSERT INTO `words` VALUES (5419, 'pastoral', 'ˈpɑːstərəl', 'adj.', '牧灵的，牧师的，教牧的(有关圣职人员及教师对个人幸福的关顾);田园的;乡村生活的;', '...the pastoral care of the sick...', '牧师对病人的关怀');
INSERT INTO `words` VALUES (5420, 'pasture', 'ˈpɑːstʃə(r)', 'n.', '牧场;牧草地;生活状况;', 'The cows are out now, grazing in the pasture.', '牛现在已经放出去了，正在牧场上吃草。');
INSERT INTO `words` VALUES (5421, 'pat', 'pæt', 'v.', '(喜爱地)轻拍;', '\'Don\'t you worry about any of this,\' she said patting me on the knee...', '“这个你什么都不用担心，”她轻拍着我的膝盖说。');
INSERT INTO `words` VALUES (5422, 'patch', 'pætʃ', 'n.', '色斑;斑点;(与周围不同的)小块，小片;', '...the bald patch on the top of his head...', '他头顶上的秃斑');
INSERT INTO `words` VALUES (5423, 'patched', 'pætʃt', 'v.', '打补丁;缝补;修补;', '...the bald patch on the top of his head...', '他头顶上的秃斑');
INSERT INTO `words` VALUES (5424, 'patchwork', 'ˈpætʃwɜːk', 'n.', '(不同图案杂色布块的)拼缝物;拼布工艺;拼凑之物;', '...beds covered in patchwork quilts.', '盖着百衲被的床');
INSERT INTO `words` VALUES (5425, 'patchy', 'ˈpætʃi', 'adj.', '零散的;散落的;分布不匀的;', 'Thick patchy fog and irresponsible driving were to blame...', '局部浓雾和驾驶员不负责任是罪魁祸首。');
INSERT INTO `words` VALUES (5426, 'pate', 'peɪt', 'n.', '头顶;(尤指)秃顶，光顶;', '...Bryan\'s bald pate.', '布赖恩的秃顶');
INSERT INTO `words` VALUES (5427, 'paternal', 'pəˈtɜːnl', 'adj.', '父亲的;慈父般的;父系的;', '...paternal love for his children...', '对子女的父爱');
INSERT INTO `words` VALUES (5428, 'paternalism', 'pəˈtɜːnəlɪzəm', 'n.', '家长作风;家长式管理;专制;', 'The government should be guided by the criteria of efficiency and not State paternalism.', '政府应遵循高效原则，杜绝国家家长作风。');
INSERT INTO `words` VALUES (5429, 'paternity', 'pəˈtɜːnəti', 'n.', '父亲的身份(或地位);', 'He was tricked into marriage by a false accusation of paternity.', '他被诬赖成孩子的父亲而被骗结婚。');
INSERT INTO `words` VALUES (5430, 'path', 'pɑːθ', 'n.', '小路;小径;路线;', 'We followed the path along the clifftops...', '我们沿着悬崖顶上的小路走。');
INSERT INTO `words` VALUES (5431, 'pathetic', 'pəˈθetɪk', 'adj.', '可怜的;可悲的;令人怜惜的;', '...a pathetic little dog with a curly tail...', '可怜的卷尾巴小狗');
INSERT INTO `words` VALUES (5432, 'pathological', 'ˌpæθəˈlɒdʒɪkl', 'adj.', '不理智的;无道理的;无法控制的;', 'He experiences chronic, almost pathological jealousy...', '他长期心怀近乎病态的嫉妒。');
INSERT INTO `words` VALUES (5433, 'pathos', 'ˈpeɪθɒs', 'n.', '感染力;令人产生悲悯共鸣的力量;', '...the pathos of man\'s isolation...', '人类所处的孤立状态令人感伤');
INSERT INTO `words` VALUES (5434, 'pathway', 'ˈpɑːθweɪ', 'n.', '小路;路径;途径;', 'Richard was coming up the pathway.', '理查德正沿着小路走过来。');
INSERT INTO `words` VALUES (5435, 'patience', 'ˈpeɪʃns', 'n.', '耐心;忍耐力;毅力;', 'He doesn\'t have the patience to wait...', '他没有耐心等待。');
INSERT INTO `words` VALUES (5436, 'patient', 'ˈpeɪʃnt', 'n.', '接受治疗者，病人(尤指医院里的);(某个医生或牙医等的)病人;受动者;', 'The earlier the treatment is given, the better the patient\'s chances...', '病人越早接受治疗，康复的可能性就越大。');
INSERT INTO `words` VALUES (5437, 'patiently', '\'peɪʃntlɪ', 'adv.', '耐心;耐心地;憨厚地;', 'The earlier the treatment is given, the better the patient\'s chances...', '病人越早接受治疗，康复的可能性就越大。');
INSERT INTO `words` VALUES (5438, 'patina', 'ˈpætɪnə', 'n.', '(金属表面的)绿锈，铜锈，氧化层;薄层;(木器或皮革的)光泽;', 'The trophy is very impressive and rather special because it has a beautiful green patina...', '这个奖杯给人印象非常深刻，相当特别，因为它表面有一层漂亮的绿锈。');
INSERT INTO `words` VALUES (5439, 'patriarch', 'ˈpeɪtriɑːk', 'n.', '(男性)家长，族长，酋长;德高望重的男长者;(东正教和天主教的)牧首，宗主教;', 'The patriarch of the house, Mr Jawad, rules it with a ferocity renowned throughout the neighbourhood...', '那户人家的家长贾瓦德先生治家严苛，名闻乡里。');
INSERT INTO `words` VALUES (5440, 'patriarchal', 'ˌpeɪtriˈɑːkl', 'adj.', '男人统治的;男性主宰的;族长的;', 'To feminists she is a classic victim of the patriarchal society...', '在女权主义者眼中，她是父权社会的典型受害者。');
INSERT INTO `words` VALUES (5441, 'patriarchy', 'ˈpeɪtriɑːki', 'n.', '男性统治的社会(或制度、国家);男权政治;父权制;', 'The main cause of women\'s and children\'s oppression is patriarchy.', '妇女儿童受压迫的主要原因是父权制。');
INSERT INTO `words` VALUES (5442, 'patrician', 'pəˈtrɪʃn', 'adj.', '贵族的;上流社会的;', '...the patrician banker Sir Charles Villiers.', '出身名门的银行家查尔斯·维利尔斯爵士');
INSERT INTO `words` VALUES (5443, 'patrimony', 'ˈpætrɪməni', 'n.', '遗产;祖传财产;文化遗产;', 'I left my parents\' house, relinquished my estate and my patrimony.', '我离开了父母的家，放弃了我的房产和祖传财产。');
INSERT INTO `words` VALUES (5444, 'patriot', 'ˈpætriət', 'n.', '爱国者;', 'They were staunch British patriots and had portraits of the Queen in their flat.', '他们是英国坚定的爱国者，并在公寓里挂着女王的肖像。');
INSERT INTO `words` VALUES (5445, 'patriotic', 'ˌpætriˈɒtɪk', 'adj.', '爱国的;', 'Woosnam is fiercely patriotic...', '伍斯纳姆非常爱国。');
INSERT INTO `words` VALUES (5446, 'patriotism', 'ˈpætriətɪzəm', 'n.', '爱国主义;爱国精神;', 'He was a country boy who had joined the army out of a sense of patriotism and adventure...', '他是个乡下小伙儿，参军是出于爱国之心和冒险精神。');
INSERT INTO `words` VALUES (5447, 'patrol', 'pəˈtrəʊl', 'v.', '巡逻;巡查;(尤指威胁性地)逛荡，闲逛;', 'Prison officers continued to patrol the grounds within the jail.', '狱警继续巡视监狱。');
INSERT INTO `words` VALUES (5448, 'patron', 'ˈpeɪtrən', 'n.', '(艺术家的)赞助人，资助者;名义赞助人(支持慈善组织等的名人，名字常用于有关的广告宣传中);老主顾;', 'Catherine the Great was a patron of the arts and sciences.', '叶卡捷琳娜大帝资助发展艺术和科学。');
INSERT INTO `words` VALUES (5449, 'patronage', 'ˈpætrənɪdʒ', 'n.', '资助;赞助;(掌权者给予提挈以换取支持的)互惠互利;', '...government patronage of the arts in Europe...', '欧洲政府对艺术的资助');
INSERT INTO `words` VALUES (5450, 'patter', 'ˈpætə(r)', 'n.', '吧嗒吧嗒的响声;急速的轻拍声;(为推销或娱乐的)不间断说话;', 'Rain pattered gently outside, dripping on to the roof from the pines.', '外面，松树上的雨水滴落在屋顶上，嗒嗒作响。');
INSERT INTO `words` VALUES (5451, 'pattern', 'ˈpætn', 'n.', '模式;方式;范例;', 'All three attacks followed the same pattern...', '3次袭击模式相同。');
INSERT INTO `words` VALUES (5452, 'paucity', 'ˈpɔːsəti', 'n.', '少量;少许;贫乏;', 'Even the film\'s impressive finale can\'t hide the first hour\'s paucity of imagination.', '这部电影的结局可圈可点，即使这样也无法掩盖前一个小时想象力贫乏的缺憾。');
INSERT INTO `words` VALUES (5453, 'paunch', 'pɔːntʃ', 'n.', '(男人的)大肚子，啤酒肚;', 'He finished his dessert and patted his paunch...', '他吃完甜点，拍了拍他的大肚子。');
INSERT INTO `words` VALUES (5454, 'paunchy', 'ˈpɔːntʃi', 'adj.', '(男子)大腹便便的;', 'He was a heavy man, paunchy, with a lined, kind face.', '他是个大腹便便的胖子，长着皱纹的脸很和善。');
INSERT INTO `words` VALUES (5455, 'pauper', 'ˈpɔːpə(r)', 'n.', '穷人;贫民;乞丐;', 'He did die a pauper and is buried in an unmarked grave.', '他死的时候一文不名，葬在一处无名墓穴中。');
INSERT INTO `words` VALUES (5456, 'pause', 'pɔːz', 'v.', '暂停;停顿;(按暂停键)暂停放音，暂停放像;', '\'It\'s rather embarrassing,\' he began, and paused...', '“非常令人难堪，”他开口道，然后停顿了一下。');
INSERT INTO `words` VALUES (5457, 'pave', 'peɪv', 'v.', '(用砖石)铺(地);', 'The avenue had never been paved, and deep mud made it impassable in winter.', '这条大街从未铺过路面，冬天泥泞不堪，无法通行。');
INSERT INTO `words` VALUES (5458, 'pavement', 'ˈpeɪvmənt', 'n.', '(马路边的)人行道;石板铺的地面;路面;', 'He was hurrying along the pavement.', '他在人行道上疾行。');
INSERT INTO `words` VALUES (5459, 'pavilion', 'pəˈvɪliən', 'n.', '(公共活动或展览用的)临时建筑物;(运动场旁设立的)运动员席，看台;大型文体馆;', '...the cricket pavilion.', '板球运动员更衣室');
INSERT INTO `words` VALUES (5460, 'paving', 'ˈpeɪvɪŋ', 'n.', '石板等铺的地面;铺料;铺地的材料;', 'In the centre of the paving stood a statue.', '铺面的中央是一尊雕塑。');
INSERT INTO `words` VALUES (5461, 'paw', 'pɔː', 'n.', '(动物的)爪;(人的)手;', 'The kitten was black with white front paws and a white splotch on her chest...', '那只黑色小猫长着白色前爪，胸口上有一块白斑。');
INSERT INTO `words` VALUES (5462, 'pawn', 'pɔːn', 'n.', '(国际象棋的)兵，卒;被利用的人;走卒;', 'He is contemplating pawning his watch.', '他正在考虑抵押他的手表。');
INSERT INTO `words` VALUES (5463, 'pay', 'peɪ', 'v.', '付费;付酬;交纳;', 'Accommodation is free — all you pay for is breakfast and dinner...', '住宿免费——你只需付早餐和正餐的钱。');
INSERT INTO `words` VALUES (5464, 'payable', 'ˈpeɪəbl', 'adj.', '应付;可偿付;应付予(抬头人、收款人等);', 'Purchase tax was not payable on goods for export.', '出口商品不需要交购买税。');
INSERT INTO `words` VALUES (5465, 'payer', 'ˈpeɪə(r)', 'n.', '付款人;交款人;', 'Lower interest rates pleased millions of mortgage payers.', '利率下调让数百万抵押贷款还款人感到高兴。');
INSERT INTO `words` VALUES (5466, 'payment', 'ˈpeɪmənt', 'n.', '付款;支付;收款;', 'Thousands of its customers are in arrears with loans and mortgage payments...', '有数千客户拖欠其贷款和抵押贷款还款。');
INSERT INTO `words` VALUES (5467, 'payoff', '\'peɪˌɒf', 'n.', '报酬;结清;算清;', 'If such materials became generally  available to the optics industry the payoffs from such a breakthrough would be enormous...', '如果这些材料能够普遍用于光学产业，那么这一突破带来的收益将非常巨大。');
INSERT INTO `words` VALUES (5468, 'payout', 'ˈpeɪaʊt', 'n.', '付出的巨款;', 'And there\'s time to win more, with a £10,000 payout still to play for over the next few days.', '还有时间多赢些，后面几天还有一笔1万英镑的奖金。');
INSERT INTO `words` VALUES (5469, 'payroll', 'ˈpeɪrəʊl', 'n.', '(公司员工的)工资名单;(公司的)工资总支出;', 'They had 87,000 employees on the payroll.', '他们在编员工有87,000名。');
INSERT INTO `words` VALUES (5470, 'pc', 'ˌpiː ˈsiː', 'abbr.', '个人电脑;个人计算机;警察;', 'The PCs took her to the local station...', '警员们把她带到了当地警察局。');
INSERT INTO `words` VALUES (5471, 'peace', 'piːs', 'n.', '和平;太平;平静;', 'Peace talks involving other rebel leaders and government representatives broke up without agreement last week, but are due to resume shortly...', '上个星期，其他叛军领导人和政府代表之间的和谈没能达成协议，宣告破裂，但是和谈不久会重开。');
INSERT INTO `words` VALUES (5472, 'peaceable', 'ˈpiːsəbl', 'adj.', '不惹事的;和平的;安宁的;', '...an attempt by ruthless  people to impose their will on a peaceable majority...', '残忍无道的人将其意志强加给大多数平和的人的企图');
INSERT INTO `words` VALUES (5473, 'peaceful', 'ˈpiːsfl', 'adj.', '不诉诸战争(或暴力、争论)的;和平的;安静的;', 'He has attempted to find a peaceful solution to the Ossetian conflict...', '他试图寻求和平解决奥赛梯冲突的方案。');
INSERT INTO `words` VALUES (5474, 'peacefully', 'ˈpiːsfəli', 'adv.', '安详地;无痛苦地;', 'He died peacefully on 10th December after a short illness.', '病了一小段时间之后，他于12月10日安详辞世。');
INSERT INTO `words` VALUES (5475, 'peacetime', 'ˈpiːstaɪm', 'n.', '和平时期;', 'The British could afford to reduce defence spending in peacetime without excessive risk...', '英国可以在和平时期减少国防开支，而不会引来太大风险。');
INSERT INTO `words` VALUES (5476, 'peacock', 'ˈpiːkɒk', 'n.', '雄孔雀;', '...peacocks strutting slowly across the garden.', '园中趾高气扬地慢步走过的孔雀');
INSERT INTO `words` VALUES (5477, 'peak', 'piːk', 'n.', '顶峰;高峰;山峰;', 'The party\'s membership has fallen from a peak of fifty-thousand after the Second World War...', '二战后该党党员人数已从5万人的峰值下降了。');
INSERT INTO `words` VALUES (5478, 'peal', 'piːl', 'n.', '响亮的声音;轰轰的响声;洪亮的钟声;', 'Church bells pealed at the stroke of midnight.', '教堂大钟在午夜敲响。');
INSERT INTO `words` VALUES (5479, 'peanut', 'ˈpiːnʌt', 'n.', '花生;很少的钱;', '...a packet of peanuts...', '一包花生');
INSERT INTO `words` VALUES (5480, 'peanuts', 'ˈpiːnʌts', 'n.', '花生;很少的钱;', '...a packet of peanuts...', '一包花生');
INSERT INTO `words` VALUES (5481, 'pearl', 'pɜːl', 'n.', '珍珠;人造珍珠;(形状或颜色)像珍珠之物;', 'She wore a string of pearls at her throat...', '她戴了一串珍珠项链。');
INSERT INTO `words` VALUES (5482, 'pearly', 'ˈpɜːli', 'adj.', '珍珠的;似珍珠的;', '...the pearly light of early morning.', '黎明时柔和的阳光');
INSERT INTO `words` VALUES (5483, 'peasant', 'ˈpeznt', 'n.', '(尤指昔日或贫穷国家的)农民，小农，佃农;老粗;土包子;', '...the peasants in the Peruvian highlands...', '秘鲁高地的农民');
INSERT INTO `words` VALUES (5484, 'peasantry', 'ˈpezntri', 'n.', '(统称)(一个地区或国家的)农民;', 'The Russian peasantry stood on the brink of disappearance.', '俄罗斯农民濒临消亡。');
INSERT INTO `words` VALUES (5485, 'peccadillo', 'ˌpekəˈdɪləʊ', 'n.', '过失;岔子;轻罪;', 'People are prepared to be tolerant of extra-marital peccadilloes by public figures.', '人们可以容忍公众人物婚外的不检点行为。');
INSERT INTO `words` VALUES (5486, 'peck', 'pek', 'v.', '啄;鹐;匆匆地轻吻;', 'It was winter and the sparrows were pecking at whatever they could find...', '时值冬季，麻雀啄食着能够找到的任何东西。');
INSERT INTO `words` VALUES (5487, 'pectoral', 'ˈpektərəl', 'adj.', '胸部的;胸的;', 'I was required to hold out my arms and flex my pectorals.', '我被要求伸展手臂，收紧胸肌。');
INSERT INTO `words` VALUES (5488, 'peculiar', 'pɪˈkjuːliə(r)', 'adj.', '怪异的;奇怪的;不寻常的;', 'Mr Kennet has a rather peculiar sense of humour...', '肯尼特先生有种很奇怪的幽默感。');
INSERT INTO `words` VALUES (5489, 'peculiarity', 'pɪˌkjuːliˈærəti', 'n.', '怪异的性质(或习惯);怪癖;(人、物、地等的)个性，特色，特点;', 'Joe\'s other peculiarity was that he was constantly munching hard candy.', '乔还有一个怪癖，就是喜欢不停地嚼硬糖。');
INSERT INTO `words` VALUES (5490, 'peculiarly', 'pɪˈkjuːliəli', 'adv.', '很;不寻常地;特别;', 'Mr Kennet has a rather peculiar sense of humour...', '肯尼特先生有种很奇怪的幽默感。');
INSERT INTO `words` VALUES (5491, 'pedagogical', 'ˌpɛdəˈgɒdʒɪkəl', 'adj.', '教学论的;教育学的;', '...the pedagogical methods used in the classroom.', '课堂上使用的教学法');
INSERT INTO `words` VALUES (5492, 'pedagogue', 'ˈpedəɡɒɡ', 'n.', '教师;好为人师的人;', 'De Gaulle was a born pedagogue who used the public platform and the television screen to great effect.', '戴高乐生来喜欢教育人，他充分利用了公共讲坛和电视荧屏。');
INSERT INTO `words` VALUES (5493, 'pedant', 'ˈpednt', 'n.', '迂夫子;书呆子;学究;', 'I am no pedant and avoid being dogmatic concerning English grammar and expression.', '我不是书呆子，不想在英语语法和表达方式上过于教条主义。');
INSERT INTO `words` VALUES (5494, 'pedantic', 'pɪˈdæntɪk', 'adj.', '迂腐的;学究气的;', 'His lecture was so pedantic and uninteresting.', '他的讲座学究气太浓，没意思。');
INSERT INTO `words` VALUES (5495, 'peddle', 'ˈpedl', 'v.', '挨户销售;巡回销售;兜售，宣传，传播(思想、消息);', 'His attempts to peddle his paintings around London\'s tiny gallery scene proved unsuccessful.', '他到伦敦小画廊兜售自己的画作，结果碰了一鼻子灰。');
INSERT INTO `words` VALUES (5496, 'pedestal', 'ˈpedɪstl', 'n.', '(柱子或雕塑等的)底座，基座;', '...a larger than life sized bronze statue on a granite pedestal.', '安放在花岗岩底座上的比真人尺寸还要大的青铜雕像');
INSERT INTO `words` VALUES (5497, 'pedestrian', 'pəˈdestriən', 'n.', '行人;步行者;', 'In Los Angeles a pedestrian is a rare spectacle...', '在洛杉矶很少见到步行者。');
INSERT INTO `words` VALUES (5498, 'pedicure', 'ˈpedɪkjʊə(r)', 'n.', '足部保养;足部护理;', 'I have a manicure and a pedicure every week.', '我每周做一次指甲护理和足部护理。');
INSERT INTO `words` VALUES (5499, 'pedigree', 'ˈpedɪɡriː', 'n.', '动物血统记录;动物纯种系谱;家谱;', '60 per cent of dogs and ten per cent of cats have pedigrees.', '60%的狗和10%的猫有血统记录。');
INSERT INTO `words` VALUES (5500, 'peek', 'piːk', 'v.', '窥视;偷看;微露出;', 'On two occasions she had peeked at him through a crack in the wall.', '她曾两次透过墙缝窥视他。');
INSERT INTO `words` VALUES (5501, 'peel', 'piːl', 'v.', '剥(水果、蔬菜等的)皮;去皮;剥掉;', '...grated lemon peel.', '擦碎的柠檬皮');
INSERT INTO `words` VALUES (5502, 'peeler', 'ˈpiːlə(r)', 'n.', '去皮器;削皮器;', '...a potato peeler.', '土豆削皮器');
INSERT INTO `words` VALUES (5503, 'peeling', 'ˈpiːlɪŋ', 'v.', '剥(水果、蔬菜等的)皮;去皮;剥掉;', '...grated lemon peel.', '擦碎的柠檬皮');
INSERT INTO `words` VALUES (5504, 'peep', 'piːp', 'v.', '(尤指通过小孔)窥视，偷看;微露出;部分现出;', 'Children came to peep at him round the doorway...', '孩子们围在门口偷看他。');
INSERT INTO `words` VALUES (5505, 'peephole', 'ˈpiːphəʊl', 'n.', '(墙或门上等的)窥视孔，瞭望孔;', 'The guards checked at the peephole before entering.', '卫兵进入前透过门镜检查了一下。');
INSERT INTO `words` VALUES (5506, 'peer', 'pɪə(r)', 'n.', '身份(或地位)相同的人;同龄人;同辈;', 'I had been peering at a computer print-out that made no sense at all...', '我一直盯着看一张毫无意义的计算机打印稿。');
INSERT INTO `words` VALUES (5507, 'peerless', 'ˈpɪələs', 'adj.', '无双的;杰出的;出众的;', '...two days of clear sunshine under peerless blue skies.', '无比蔚蓝的天空下的两个艳阳天');
INSERT INTO `words` VALUES (5508, 'peevish', 'ˈpiːvɪʃ', 'adj.', '爱生气的;易怒的;脾气坏的;', 'Aubrey had slept little and that always made him peevish...', '奥布里睡眠不足，这让他总是易怒。');
INSERT INTO `words` VALUES (5509, 'peg', 'peɡ', 'n.', '(木、金属或塑料)钉子，楔子，橛子，短桩;(木制或金属)帐篷短桩;晾衣夹子;', 'His work jacket hung on the peg in the kitchen.', '他的工作服挂在厨房里的挂钩上。');
INSERT INTO `words` VALUES (5510, 'pejorative', 'pɪˈdʒɒrətɪv', 'adj.', '贬损的;轻蔑的;', 'I agree I am ambitious, and I don\'t see that as a pejorative term...', '我承认我有野心，但我不认为这是个贬义词。');
INSERT INTO `words` VALUES (5511, 'pellet', 'ˈpelɪt', 'n.', '小球;团粒;丸;', 'He was shot in the head by an air gun pellet...', '他被气枪弹丸击中头部。');
INSERT INTO `words` VALUES (5512, 'pell-mell', 'ˌpel ˈmel', 'adv.', '匆忙地;仓促;混乱地;', 'All three of us rushed pell-mell into the kitchen.', '我们三人全都匆忙跑进厨房。');
INSERT INTO `words` VALUES (5513, 'pellucid', 'pəˈluːsɪd', 'adj.', '清澈的;清晰的;', '...her pellucid blue eyes...', '她清澈的蓝眼睛');
INSERT INTO `words` VALUES (5514, 'pelt', 'pelt', 'v.', '投物攻击;向…投掷;倾泻;', '...a bed covered with beaver pelts.', '铺着海狸毛皮的床');
INSERT INTO `words` VALUES (5515, 'penal', 'ˈpiːnl', 'adj.', '惩罚的;刑罚的;应受刑罚的;', '...director-general of penal affairs at the justice ministry.', '司法部刑事处处长');
INSERT INTO `words` VALUES (5516, 'penalty', 'ˈpenəlti', 'n.', '惩罚;处罚;刑罚;', 'One of those arrested could face the death penalty...', '被捕的那些人中有一人可能被判死刑。');
INSERT INTO `words` VALUES (5517, 'penance', 'ˈpenəns', 'n.', '补赎;悔罪;修和圣事;', '...a time of fasting, penance and pilgrimage...', '斋戒、补赎和朝圣的时代');
INSERT INTO `words` VALUES (5518, 'penchant', 'ˈpɒ̃ʃɒ̃', 'n.', '爱好;嗜爱;', '...a stylish woman with a penchant for dark glasses...', '喜欢戴墨镜的时髦女子');
INSERT INTO `words` VALUES (5519, 'pencil', 'ˈpensl', 'n.', '铅笔;', 'I found a pencil and some blank paper in her desk...', '我在她的桌子里找到一支铅笔和几张白纸。');
INSERT INTO `words` VALUES (5520, 'pending', 'ˈpendɪŋ', 'prep.', '在等待…时期;直到…为止;', 'The cause of death was listed as pending...', '死亡原因被列为待定。');
INSERT INTO `words` VALUES (5521, 'pendulous', 'ˈpendjələs', 'adj.', '悬垂摆动的;', '...a stout, gloomy man with a pendulous lower lip.', '下嘴唇松垂、一脸阴郁的胖子');
INSERT INTO `words` VALUES (5522, 'penetrate', 'ˈpenətreɪt', 'v.', '穿过;进入;渗透，打入(组织、团体等);', 'X-rays can penetrate many objects...', 'X射线能够穿透很多物体。');
INSERT INTO `words` VALUES (5523, 'penetrating', 'ˈpenətreɪtɪŋ', 'adj.', '锐利的;犀利的;尖利的;', 'Mary heard the penetrating bell of an ambulance...', '玛丽听到了刺耳的救护车警铃声。');
INSERT INTO `words` VALUES (5524, 'penetration', 'ˌpenəˈtreɪʃn', 'n.', '穿透;渗透;进入;', 'X-rays can penetrate many objects...', 'X射线能够穿透很多物体。');
INSERT INTO `words` VALUES (5525, 'peninsula', 'pəˈnɪnsjələ', 'n.', '半岛;', '...the political situation in the Korean peninsula...', '朝鲜半岛的政治局势');
INSERT INTO `words` VALUES (5526, 'penitent', 'ˈpenɪtənt', 'adj.', '忏悔的;后悔的;愧疚的;', 'Robert Gates sat before them, almost penitent about the past...', '罗伯特·盖茨坐在他们面前，对过去的事几乎无颜以对。');
INSERT INTO `words` VALUES (5527, 'pennant', 'ˈpenənt', 'n.', '(船上用作信号等的)三角旗;(奖给棒球等联赛优胜队的)锦旗;', 'The second car was flying the Ghanaian pennant.', '第二辆车插着加纳的三角旗。');
INSERT INTO `words` VALUES (5528, 'penniless', 'ˈpeniləs', 'adj.', '一文不名的;穷困的;', 'They\'d soon be penniless and homeless if she couldn\'t find suitable work.', '她要找不到合适的工作，他们很快就会身无分文，无家可归。');
INSERT INTO `words` VALUES (5529, 'pension', 'ˈpenʃn , ˈpɒ̃sjɒ̃', 'n.', '养老金;退休金;抚恤金;', '...struggling by on a pension.', '靠养老金苦苦支撑');
INSERT INTO `words` VALUES (5530, 'pensive', 'ˈpensɪv', 'adj.', '沉思的;忧伤的;忧戚的;', 'He looked suddenly sombre, pensive.', '他突然看起来很阴郁，一副忧虑的样子。');
INSERT INTO `words` VALUES (5531, 'penthouse', 'ˈpenthaʊs', 'n.', '顶层豪华公寓;阁楼套房;', '...her swish Manhattan penthouse.', '她位于曼哈顿的顶层高档豪华公寓');
INSERT INTO `words` VALUES (5532, 'penultimate', 'penˈʌltɪmət', 'adj.', '倒数第二的;', '...on the penultimate day of the Asian Games.', '在亚运会结束的倒数第二天');
INSERT INTO `words` VALUES (5533, 'penury', 'ˈpenjəri', 'n.', '贫困;贫穷;', 'He was brought up in penury, without education.', '他从小一贫如洗，没有受过教育。');
INSERT INTO `words` VALUES (5534, 'people', 'ˈpiːpl', 'n.', '人;人们;大家;', 'Millions of people have lost their homes.', '数百万人流离失所。');
INSERT INTO `words` VALUES (5535, 'pepper', 'ˈpepə(r)', 'n.', '胡椒粉;甜椒;柿子椒;', 'Season with salt and pepper.', '用盐和胡椒调味。');
INSERT INTO `words` VALUES (5536, 'peppered', 'ˈpepəd', 'v.', '(在食物上)撒胡椒粉;', 'Season with salt and pepper.', '用盐和胡椒调味。');
INSERT INTO `words` VALUES (5537, 'peppery', 'ˈpepəri', 'adj.', '胡椒味的;辣的;脾气不好的;', '...a crisp green salad with a few peppery radishes.', '用几个辣味萝卜拌的脆口蔬菜色拉');
INSERT INTO `words` VALUES (5538, 'per', 'pə(r)', 'prep.', '每;每一;', 'Social Security refused to pay her more than £17 per week...', '社会保障部门拒绝每周向她支付17英镑以上的钱。');
INSERT INTO `words` VALUES (5539, 'perceive', 'pəˈsiːv', 'v.', '注意到;意识到;察觉到;', 'A key task is to get pupils to perceive for themselves the relationship between success and effort...', '关键任务是让学生们自己认识到成功和努力之间的关系。');
INSERT INTO `words` VALUES (5540, 'percentage', 'pəˈsentɪdʒ', 'n.', '百分率;百分比;利润的分成;', 'Only a few vegetable-origin foods have such a high percentage of protein.', '只有几种蔬菜食品有如此高的蛋白质含量。');
INSERT INTO `words` VALUES (5541, 'perceptible', 'pəˈseptəbl', 'adj.', '可察觉到的;看得出的;可感知的;', 'Pasternak gave him a barely perceptible smile...', '帕斯特纳克给了他一个几乎无法察觉的微笑。');
INSERT INTO `words` VALUES (5542, 'perception', 'pəˈsepʃn', 'n.', '知觉;感知;洞察力;', 'He is interested in how our perceptions of death affect the way we live.', '他感兴趣的是我们对死亡的看法如何影响我们的生活。');
INSERT INTO `words` VALUES (5543, 'perceptive', 'pəˈseptɪv', 'adj.', '理解力强的;有洞察力的;思维敏捷的;', 'He was one of the most perceptive US political commentators.', '他是美国最具洞察力的政治评论家之一。');
INSERT INTO `words` VALUES (5544, 'perceptual', 'pəˈseptʃuəl', 'adj.', '知觉的;感知的;', 'Some children have more finely trained perceptual skills than others.', '一些孩子比其他孩子具备更为训练有素的感知技能。');
INSERT INTO `words` VALUES (5545, 'percolate', 'ˈpɜːkəleɪt', 'v.', '渗入;渗透;渗漏;', 'New fashions took a long time to percolate down.', '新时尚要很长时间才能在大众中流行起来。');
INSERT INTO `words` VALUES (5546, 'percussive', 'pəˈkʌsɪv', 'adj.', '打击声的;打击乐器声的;', '...using all manner of percussive effects.', '运用各种打击乐效果');
INSERT INTO `words` VALUES (5547, 'peremptory', 'pəˈremptəri', 'adj.', '强硬的;强制的;不容分辩的;', 'With a brief, almost peremptory gesture he pointed to a chair.', '他做了一个简单的手势，近乎霸道地指着椅子。');
INSERT INTO `words` VALUES (5548, 'perennial', 'pəˈreniəl', 'adj.', '长久的;持续的;反复出现的;', '...the perennial urban problems of drugs and homelessness...', '毒品和无家可归这两个一直困扰城市的问题');
INSERT INTO `words` VALUES (5549, 'perennially', '', 'adv.', '永久地;', '...the perennial urban problems of drugs and homelessness...', '毒品和无家可归这两个一直困扰城市的问题');
INSERT INTO `words` VALUES (5550, 'perfection', 'pəˈfekʃn', 'n.', '完善;完美;最后加工;', 'His quest for perfection is relentless...', '他不断追求完美。');
INSERT INTO `words` VALUES (5551, 'perfectly', 'ˈpɜːfɪktli', 'adv.', '完全地;非常;十分;', 'There\'s no reason why you can\'t have a perfectly normal child...', '你没有理由不能拥有一个完全正常的孩子。');
INSERT INTO `words` VALUES (5552, 'perfidious', 'pəˈfɪdiəs', 'adj.', '不可信任的;背叛的;不忠的;', 'Their feet will trample on the dead bodies of their perfidious aggressors.', '他们将从背信弃义的侵略者的尸体上踏过。');
INSERT INTO `words` VALUES (5553, 'perforate', 'ˈpɜːfəreɪt', 'v.', '打孔;穿孔;打眼;', 'I refused to wear headphones because they can perforate your eardrums.', '我不愿戴耳机，因为它可能会使鼓膜穿孔。');
INSERT INTO `words` VALUES (5554, 'perforation', 'ˌpɜːfəˈreɪʃn', 'n.', '齿孔;穿孔;穿通;', 'Tear off the form along the perforations and send it to Sales...', '沿着纸孔撕下表格，寄给销售部。');
INSERT INTO `words` VALUES (5555, 'perforce', 'pəˈfɔːs', 'adv.', '必须;必定;势必;', 'The war in 1939 perforce ushered in an era of more grime and drabness.', '1939年的战争必然导致一个更加肮脏、更加灰暗的时代的到来。');
INSERT INTO `words` VALUES (5556, 'perform', 'pəˈfɔːm', 'v.', '做;履行;执行;', 'We\'re looking for people of all ages who have performed outstanding acts of bravery, kindness or courage...', '我们正在寻找那些见义勇为、乐于助人、勇气可嘉的人，年龄不限。');
INSERT INTO `words` VALUES (5557, 'performance', 'pəˈfɔːməns', 'n.', '表演;演出;艺术上的表现;', 'Inside the theatre, they were giving a performance of Bizet\'s Carmen.', '在剧院里，他们正在演出比才的作品《卡门》。');
INSERT INTO `words` VALUES (5558, 'performer', 'pəˈfɔːmə(r)', 'n.', '表演者;演出者;演员;', 'A performer in evening dress plays classical selections on the violin.', '一名身穿晚礼服的表演者用小提琴演奏了几段古典音乐。');
INSERT INTO `words` VALUES (5559, 'perfume', 'ˈpɜːfjuːm', 'n.', '香水;芳香;香味;', 'The hall smelled of her mother\'s perfume.', '大厅里弥漫着她母亲身上的香水味。');
INSERT INTO `words` VALUES (5560, 'perfunctory', 'pəˈfʌŋktəri', 'adj.', '敷衍的;例行公事般的;潦草的;', 'She gave the list only a perfunctory glance...', '她马马虎虎地扫了一眼清单。');
INSERT INTO `words` VALUES (5561, 'perhaps', 'pəˈhæps', 'adv.', '可能;大概;也许;', 'Millson regarded her thoughtfully. Perhaps she was right...', '米尔森若有所思地注视着她。或许她是对的。');
INSERT INTO `words` VALUES (5562, 'peril', 'ˈperəl', 'n.', '严重危险;祸害;险情;', '...the perils of the sea...', '海洋的凶险');
INSERT INTO `words` VALUES (5563, 'perilous', 'ˈperələs', 'adj.', '危险的;艰险的;', '...a perilous journey across the war-zone...', '横穿交战区的危险之旅');
INSERT INTO `words` VALUES (5564, 'perimeter', 'pəˈrɪmɪtə(r)', 'n.', '(土地的)外缘，边缘;周长;', '...the perimeter of the airport...', '机场四周');
INSERT INTO `words` VALUES (5565, 'period', 'ˈpɪəriəd', 'n.', '一段时间;时期;(人生或国家历史的)阶段，时代;', 'This crisis might last for a long period of time.', '这次危机可能持续很长时间。');
INSERT INTO `words` VALUES (5566, 'periodic', 'ˌpɪəriˈɒdɪk', 'adj.', '间发性的;定期的;周期的;', 'Periodic checks are taken to ensure that high standards are maintained.', '定期检查以确保维持高水准。');
INSERT INTO `words` VALUES (5567, 'periodical', 'ˌpɪəriˈɒdɪkl', 'n.', '(学术)期刊;', 'The walls would be lined with books and periodicals.', '这几面墙会摆放书籍和期刊。');
INSERT INTO `words` VALUES (5568, 'periodically', 'ˌpɪərɪˈɒdɪkəli', 'adv.', '定期;周期性;定期出版地;', 'The walls would be lined with books and periodicals.', '这几面墙会摆放书籍和期刊。');
INSERT INTO `words` VALUES (5569, 'peripatetic', 'ˌperipəˈtetɪk', 'adj.', '巡回工作的;流动的;', 'Her father was in the army and the family led a peripatetic existence.', '她父亲是军人，所以全家人随军过着一种流动的生活。');
INSERT INTO `words` VALUES (5570, 'peripheral', 'pəˈrɪfərəl', 'adj.', '次要的;附带的;外围的;', 'Companies are increasingly keen to contract out peripheral activities like training...', '公司越来越倾向于把像培训这样的非核心业务外包出去。');
INSERT INTO `words` VALUES (5571, 'periphery', 'pəˈrɪfəri', 'n.', '边缘;周围;外围;', 'Geographically, the UK is on the periphery of Europe, while Paris is at the heart of the continent...', '从地理位置上讲，英国处于欧洲边缘，而巴黎却位于欧洲大陆的中心位置。');
INSERT INTO `words` VALUES (5572, 'perish', 'ˈperɪʃ', 'v.', '死亡;暴死;丧失;', 'Most of the butterflies perish in the first frosts of autumn.', '大多数蝴蝶会在秋天的第一场霜冻来临时纷纷死去。');
INSERT INTO `words` VALUES (5573, 'perishable', 'ˈperɪʃəbl', 'adj.', '易腐烂的;易变质的;', '...perishable food like fruit, vegetables and meat...', '水果、蔬菜、肉类等易变质的食品');
INSERT INTO `words` VALUES (5574, 'perishing', 'ˈperɪʃɪŋ', 'adj.', '冰冷的;酷寒的;讨厌的;', 'Most of the butterflies perish in the first frosts of autumn.', '大多数蝴蝶会在秋天的第一场霜冻来临时纷纷死去。');
INSERT INTO `words` VALUES (5575, 'perjure', 'ˈpɜːdʒə(r)', 'v.', '作伪证;发假誓;', 'Witnesses lied and perjured themselves...', '证人撒谎作伪证。');
INSERT INTO `words` VALUES (5576, 'perjury', 'ˈpɜːdʒəri', 'n.', '伪证;伪誓;伪证罪;', 'This witness has committed perjury and no reliance can be placed on her evidence.', '这名证人作了伪证，她的证词不可信。');
INSERT INTO `words` VALUES (5577, 'perk', 'pɜːk', 'n.', '(工资之外的)补贴，津贴，额外待遇;', '...a company car, private medical insurance and other perks...', '公司配车、提供私人医疗保险以及其他额外待遇');
INSERT INTO `words` VALUES (5578, 'perky', 'ˈpɜːki', 'adj.', '高兴的;快活的;精力充沛的;', 'He wasn\'t quite as perky as normal...', '他并不像往常那么高兴。');
INSERT INTO `words` VALUES (5579, 'permanence', 'ˈpɜːmənəns', 'n.', '永久;持久性;', 'Heavy drinking can cause permanent damage to the brain...', '酗酒能造成永久性大脑损伤。');
INSERT INTO `words` VALUES (5580, 'permanent', 'ˈpɜːmənənt', 'adj.', '永久的;永恒的;长久的;', 'Heavy drinking can cause permanent damage to the brain...', '酗酒能造成永久性大脑损伤。');
INSERT INTO `words` VALUES (5581, 'permanently', 'ˈpɜːmənəntli', 'adv.', '永久地;永远;长期;', 'Heavy drinking can cause permanent damage to the brain...', '酗酒能造成永久性大脑损伤。');
INSERT INTO `words` VALUES (5582, 'permeability', 'ˌpɜːmjəˈbɪlɪti', 'n.', '渗透性;渗蚀度;(气球气体的)透出量;', 'A number of products have been developed which are permeable to air and water.', '已开发出许多可渗水透气的产品。');
INSERT INTO `words` VALUES (5583, 'permeable', 'ˈpɜːmiəbl', 'adj.', '可渗透的;可渗入的;', 'A number of products have been developed which are permeable to air and water.', '已开发出许多可渗水透气的产品。');
INSERT INTO `words` VALUES (5584, 'permeate', 'ˈpɜːmieɪt', 'v.', '渗透;弥漫;扩散;', 'Bias against women permeates every level of the judicial system...', '司法体制中各个层面都有歧视女性的现象。');
INSERT INTO `words` VALUES (5585, 'permissible', 'pəˈmɪsəbl', 'adj.', '容许的;许可的;', 'Religious practices are permissible under the Constitution...', '宪法允许进行宗教活动。');
INSERT INTO `words` VALUES (5586, 'permission', 'pəˈmɪʃn', 'n.', '准许;许可;批准;', 'He asked permission to leave the room...', '他请求准许离开房间。');
INSERT INTO `words` VALUES (5587, 'permissive', 'pəˈmɪsɪv', 'adj.', '放任的;纵容的;姑息的;', '...the permissive tolerance of the 1960s...', '20世纪60年代的“姑息纵容”');
INSERT INTO `words` VALUES (5588, 'permutation', 'ˌpɜːmjuˈteɪʃn', 'n.', '排列(方式);组合(方式);置换;', 'Variation among humans is limited to the possible permutations of our genes.', '人类的变异受限于我们的基因可能存在哪些排列。');
INSERT INTO `words` VALUES (5589, 'pernicious', 'pəˈnɪʃəs', 'adj.', '有害的，恶性的(尤指潜移默化地);', 'I did what I could, but her mother\'s influence was pernicious...', '我已尽力了，但是她母亲贻害颇深。');
INSERT INTO `words` VALUES (5590, 'perpendicular', 'ˌpɜːpənˈdɪkjələ(r)', 'adj.', '垂直的;成直角的;垂直式的(英国14、15世纪盛行的建筑风格，以使用垂直线和大拱为特征);', 'We made two slits for the eyes and a perpendicular line for the nose...', '我们划了两道缝儿做眼睛,另外划了一条竖线做鼻子。');
INSERT INTO `words` VALUES (5591, 'perpetrate', 'ˈpɜːpətreɪt', 'v.', '犯(罪);做(错事);干(坏事);', 'A high proportion of crime in any country is perpetrated by young males in their teens and twenties...', '在任何国家的犯罪者中，十几二十几岁的青年男性都占了很高的比例。');
INSERT INTO `words` VALUES (5592, 'perpetrator', 'ˈpɜːpətreɪtə(r)', 'n.', '作恶者;行凶者;犯罪者;', 'A high proportion of crime in any country is perpetrated by young males in their teens and twenties...', '在任何国家的犯罪者中，十几二十几岁的青年男性都占了很高的比例。');
INSERT INTO `words` VALUES (5593, 'perpetual', 'pəˈpetʃuəl', 'adj.', '不间断的;持续的;长久的;', '...the creation of a perpetual union.', '永久联盟的创立');
INSERT INTO `words` VALUES (5594, 'perpetually', 'pəˈpɛtʃʊəli', 'adv.', '常常;永远;没完没了地;', '...the creation of a perpetual union.', '永久联盟的创立');
INSERT INTO `words` VALUES (5595, 'perpetuate', 'pəˈpetʃueɪt', 'v.', '使永久化;使持久化;使持续;', 'We must not perpetuate the religious divisions of the past...', '我们绝对不能让过去的宗教分裂继续下去。');
INSERT INTO `words` VALUES (5596, 'perpetuity', 'ˌpɜːpəˈtjuːəti', 'n.', '永远不得转让性；永远不得转让的产业；永久持有权；永续年金；终身年金;', 'The US Government gave the land to the tribe in perpetuity.', '美国政府将这块土地永久性划拨给该部族。');
INSERT INTO `words` VALUES (5597, 'perplex', 'pəˈpleks', 'v.', '迷惑;使困惑;', 'It perplexed him because he was tackling it the wrong way.', '由于处理不当，这个问题令他大伤脑筋。');
INSERT INTO `words` VALUES (5598, 'perplexed', 'pəˈplekst', 'adj.', '困惑的;迷惑不解的;', 'She is perplexed about what to do for her daughter...', '她不知道该为女儿做些什么。');
INSERT INTO `words` VALUES (5599, 'perplexing', 'pəˈpleksɪŋ', 'adj.', '令人不解的;不知道如何处置的;', 'It took years to understand many perplexing diseases...', '许多疑难病症要耗时数年才能搞明白。');
INSERT INTO `words` VALUES (5600, 'perplexity', 'pəˈpleksəti', 'n.', '困惑;迷惘;难以理解的事物;', 'He began counting them and then, with growing perplexity, counted them a second time.', '他开始数，然后更加困惑，于是又数了一遍。');
INSERT INTO `words` VALUES (5601, 'perquisite', 'ˈpɜːkwɪzɪt', 'n.', '特权;利益;', '...cost-free long-distance calls, a perquisite of her employment.', '免费长途电话，她享受的一种工作补贴');
INSERT INTO `words` VALUES (5602, 'persecute', 'ˈpɜːsɪkjuːt', 'v.', '(因种族、宗教或政治信仰)迫害，残害，压迫;骚扰;打扰;', 'Mr Weaver and his family have been persecuted by the authorities for their beliefs...', '韦弗先生和他的家人由于其信仰而遭到当局的迫害。');
INSERT INTO `words` VALUES (5603, 'persecution', 'ˌpɜːsɪˈkjuːʃn', 'n.', '(尤指因宗教、政治信仰或种族而受到的)迫害;', '...the persecution of minorities.', '对少数派的迫害');
INSERT INTO `words` VALUES (5604, 'persevere', 'ˌpɜːsəˈvɪə(r)', 'v.', '坚持;孜孜以求;', '...his ability to persevere despite obstacles and setbacks.', '他锲而不舍、百折不挠的劲头儿');
INSERT INTO `words` VALUES (5605, 'persevering', 'ˌpɜːsəˈvɪərɪŋ', 'adj.', '坚韧不拔的;不屈不挠的;', '...his ability to persevere despite obstacles and setbacks.', '他锲而不舍、百折不挠的劲头儿');
INSERT INTO `words` VALUES (5606, 'persian', 'ˈpɜːʃn', 'n.', '波斯人;波斯语;', '...the Persian Empire.', '波斯帝国');
INSERT INTO `words` VALUES (5607, 'persist', 'pəˈsɪst', 'v.', '顽强地坚持;执著地做;维持;', 'Contact your doctor if the cough persists...', '如果还是咳嗽就去看医生。');
INSERT INTO `words` VALUES (5608, 'persistence', 'pəˈsɪstəns', 'n.', '坚持;锲而不舍;持续存在;', 'Skill comes only with practice, patience and persistence...', '只有不断练习，有耐心有毅力，才能学会一门技艺。');
INSERT INTO `words` VALUES (5609, 'persistent', 'pəˈsɪstənt', 'adj.', '执著的;不屈不挠的;坚持不懈的;', 'Her position as national leader has been weakened by persistent fears of another coup attempt...', '由于人们一直担心会再次发生政变,她的国家领导人地位被削弱了。');
INSERT INTO `words` VALUES (5610, 'persistently', 'pəˈsɪstəntli', 'adv.', '一次又一次地;一直;坚持不懈地;', 'The allegations have been persistently denied by ministers...', '这些说法被部长们一再否认。');
INSERT INTO `words` VALUES (5611, 'persnickety', 'pəˈsnɪkəti', 'adj.', '挑剔的;吹毛求疵的;', 'He is a very rigorous man, very persnickety.', '他是个很严厉的人，非常挑剔。');
INSERT INTO `words` VALUES (5612, 'persona', 'pəˈsəʊnə', 'n.', '伪装;假象;人格面具;', '...the contradictions between her private life and the public persona...', '她私生活和公众形象间的冲突');
INSERT INTO `words` VALUES (5613, 'personable', 'ˈpɜːsənəbl', 'adj.', '品貌兼优;英俊潇洒;', 'The people I met were intelligent, mature, personable.', '我见到的是一些聪明、成熟、品貌兼优的人。');
INSERT INTO `words` VALUES (5614, 'personage', 'ˈpɜːsənɪdʒ', 'n.', '要人;名人;', '...MPs, film stars and other important personages.', '国会议员、电影明星和其他要人');
INSERT INTO `words` VALUES (5615, 'personal', 'ˈpɜːsənl', 'adj.', '个人的;私人的;人际的;', 'He learned this lesson the hard way — from his own personal experience...', '吃一堑长一智，他这个教训来之不易——是从他自己的亲身经历中得来的。');
INSERT INTO `words` VALUES (5616, 'personalities', 'ˌpɜːsəˈnælɪtiz', 'n.', '性格;个性;人格;', 'She has such a kind, friendly personality...', '她个性善良友好。');
INSERT INTO `words` VALUES (5617, 'personality', 'ˌpɜːsəˈnæləti', 'n.', '性格;个性;人格;', 'She has such a kind, friendly personality...', '她个性善良友好。');
INSERT INTO `words` VALUES (5618, 'personally', 'ˈpɜːsənəli', 'adv.', '就本人而言;就个人意见;本人;', 'Personally I think it\'s a waste of time...', '在我看来，这是浪费时间。');
INSERT INTO `words` VALUES (5619, 'personification', 'pəˌsɒnɪfɪˈkeɪʃn', 'n.', '体现某品质或特点的人;化身;典型;', 'Janis Joplin was the personification of the \'60s female rock singer...', '贾尼斯·乔普林是20世纪60年代摇滚女歌手的代表。');
INSERT INTO `words` VALUES (5620, 'personify', 'pəˈsɒnɪfaɪ', 'v.', '是…的典型;集中表现;拟人化;', 'She seemed to personify goodness and nobility...', '她宛如善良和高贵的化身。');
INSERT INTO `words` VALUES (5621, 'personnel', 'ˌpɜːsəˈnel', 'n.', '(组织或军队中的)全体人员，职员;人事部门;', 'Five other US military personnel were killed in various incidents...', '另有5名美军士兵死于各种意外。');
INSERT INTO `words` VALUES (5622, 'perspective', 'pəˈspektɪv', 'n.', '态度;观点;思考方法;', 'He says the death of his father 18 months ago has given him a new perspective on life...', '他说18个月前父亲的去世让他对人生有了新的认识。');
INSERT INTO `words` VALUES (5623, 'perspicacious', 'ˌpɜːspɪˈkeɪʃəs', 'adj.', '敏锐的;有洞察力的;精辟的;', '...one of the most perspicacious and perceptive historians of that period.', '那个时期最敏锐、最有洞察力的历史学家之一');
INSERT INTO `words` VALUES (5624, 'perspiration', 'ˌpɜːspəˈreɪʃn', 'n.', '汗;汗珠;排汗;', 'His hands were wet with perspiration.', '他双手被汗水浸湿了。');
INSERT INTO `words` VALUES (5625, 'perspire', 'pəˈspaɪə(r)', 'v.', '出汗;排汗;发汗;', 'He began to perspire heavily.', '他开始大量出汗。');
INSERT INTO `words` VALUES (5626, 'persuade', 'pəˈsweɪd', 'v.', '劝说;说服;使信服;', 'My husband persuaded me to come...', '我丈夫说服我前来。');
INSERT INTO `words` VALUES (5627, 'persuasion', 'pəˈsweɪʒn', 'n.', '说服;劝说;(宗教或政治)信仰;', 'Only after much persuasion from Ellis had she agreed to hold a show at all...', '埃利斯劝了她半天后她才同意办一场展览。');
INSERT INTO `words` VALUES (5628, 'persuasive', 'pəˈsweɪsɪv', 'adj.', '有说服力的;令人信服的;', 'What do you think were some of the more persuasive arguments on the other side?...', '你认为对方哪些观点更有说服力？');
INSERT INTO `words` VALUES (5629, 'pert', 'pɜːt', 'adj.', '无礼的;冒失的;轻佻的;', '...a pert redhead in uniform.', '身穿制服、作风泼辣的红发女子');
INSERT INTO `words` VALUES (5630, 'pertain', 'pəˈteɪn', 'v.', '存在;适用;', 'The restrictions he imposed pertained to the type and height of buildings and the activities for which they could be used...', '他限制的是建筑物的种类和高度以及它们的用途。');
INSERT INTO `words` VALUES (5631, 'pertinent', 'ˈpɜːtɪnənt', 'adj.', '有关的;恰当的;相宜的;', 'She had asked some pertinent questions...', '她问了一些相关的问题。');
INSERT INTO `words` VALUES (5632, 'perturb', 'pəˈtɜːb', 'v.', '使焦虑;使不安;', 'What perturbs me is that magazine articles are so much shorter nowadays.', '让我不安的是现在杂志文章都短多了。');
INSERT INTO `words` VALUES (5633, 'perturbation', 'ˌpɜːtəˈbeɪʃn', 'n.', '忧虑;不安;烦恼;', '...perturbations in Jupiter\'s gravitational field.', '木星重力场的微小变化');
INSERT INTO `words` VALUES (5634, 'perturbed', 'pəˈtɜːbd', 'adj.', '烦恼的;担心的;不安的;', 'He apparently was not perturbed by the prospect of a policeman coming to call...', '显然，对警察可能会上门来访，他并没有表现得很不安。');
INSERT INTO `words` VALUES (5635, 'perusal', 'pəˈruːzl', 'n.', '阅读;读;', 'Peter Cooke undertook to send each of us a sample contract for perusal...', '彼得·库克答应给我们每人寄送一份合同样本供阅读。');
INSERT INTO `words` VALUES (5636, 'peruse', 'pəˈruːz', 'v.', '细读;研读;', 'We perused the company\'s financial statements for the past five years...', '我们翻阅了公司过去5年来的财务报表。');
INSERT INTO `words` VALUES (5637, 'pervade', 'pəˈveɪd', 'v.', '渗透;弥漫;遍及;', 'The smell of sawdust and glue pervaded the factory...', '工厂里弥漫着锯屑和胶水的气味。');
INSERT INTO `words` VALUES (5638, 'pervasive', 'pəˈveɪsɪv', 'adj.', '遍布的;充斥各处的;弥漫的;', '...the pervasive influence of the army in national life...', '在国民生活中军队的影响无处不在');
INSERT INTO `words` VALUES (5639, 'perverse', 'pəˈvɜːs', 'adj.', '执拗的;任性的;不通情理的;', 'It would be perverse to stop this healthy trend...', '阻止这种健康发展的趋势是没有道理的。');
INSERT INTO `words` VALUES (5640, 'pesky', 'ˈpeski', 'adj.', '恼人的;讨厌的;', '...as if he were a pesky tourist asking silly questions of a busy man.', '好像他是个让人讨厌的旅客，尽向忙碌的人问些愚蠢的问题');
INSERT INTO `words` VALUES (5641, 'pessimism', 'ˈpesɪmɪzəm', 'n.', '悲观;悲观情绪;悲观主义;', '...universal pessimism about the economy...', '对经济所持的普遍悲观态度');
INSERT INTO `words` VALUES (5642, 'pessimist', 'ˈpesɪmɪst', 'n.', '悲观主义者;悲观论者;', 'I\'m a natural pessimist; I usually expect the worst...', '我是天生的悲观主义者。我总是做最坏的打算。');
INSERT INTO `words` VALUES (5643, 'pessimistic', 'ˌpesɪˈmɪstɪk', 'adj.', '悲观的;悲观主义的;', 'Not everyone is so pessimistic about the future...', '不是每个人都对未来如此悲观。');
INSERT INTO `words` VALUES (5644, 'pest', 'pest', 'n.', '害虫;害兽;害鸟;', '...crops which are resistant to some of the major insect pests and diseases...', '对一些主要病虫害有抵御能力的作物');
INSERT INTO `words` VALUES (5645, 'pester', 'ˈpestə(r)', 'v.', '打扰;纠缠;烦扰;', 'I thought she\'d stop pestering me, but it only seemed to make her worse...', '我原以为她会停止纠缠我，但结果她似乎是变本加厉了。');
INSERT INTO `words` VALUES (5646, 'pestilential', 'ˌpestɪˈlenʃl', 'adj.', '极讨厌的;极烦人的;瘟疫的;', '...people who were dependent for their water supply on this pestilential stream.', '那些靠这条水质受污、可致疫病的河流来取水的人们');
INSERT INTO `words` VALUES (5647, 'pet', 'pet', 'n.', '宠物;宠儿;宝贝;', 'It is plainly cruel to keep turtles as pets.', '显然，养海龟作宠物十分残忍。');
INSERT INTO `words` VALUES (5648, 'petal', 'ˈpetl', 'n.', '花瓣;', '...bowls of dried rose petals.', '几钵干玫瑰花瓣');
INSERT INTO `words` VALUES (5649, 'petition', 'pəˈtɪʃn', 'n.', '请愿书;申诉书;申请书;', 'We recently presented the government with a petition signed by 4,500 people.', '最近我们向政府递交了有4,500人签名的请愿书。');
INSERT INTO `words` VALUES (5650, 'petrify', 'ˈpetrɪfaɪ', 'v.', '使吓呆;使惊呆;(使)石化;', 'Prison petrifies me and I don\'t want to go there.', '监狱太可怕了，我不想去那里。');
INSERT INTO `words` VALUES (5651, 'pets', 'pets', 'n.', '宠物;宠儿;宝贝;', 'It is plainly cruel to keep turtles as pets.', '显然，养海龟作宠物十分残忍。');
INSERT INTO `words` VALUES (5652, 'petty', 'ˈpeti', 'adj.', '小的;琐碎的;次要的;', 'He was miserable all the time and rows would start over petty things.', '他总是愁眉苦脸的，常因小事发怒。');
INSERT INTO `words` VALUES (5653, 'petulance', 'ˈpetjuləns', 'n.', '任性;小题大做;', 'His petulance made her impatient.', '他的任性让她无法忍受。');
INSERT INTO `words` VALUES (5654, 'petulant', 'ˈpetjulənt', 'adj.', '闹脾气的;爱耍性子的;赌气的;', 'His critics say he\'s just being silly and petulant...', '批评家说他就是愚蠢和任性。');
INSERT INTO `words` VALUES (5655, 'pew', 'pjuː', 'n.', '教堂长椅;', 'Claire sat in the front pew.', '克莱尔坐在前排长木椅上。');
INSERT INTO `words` VALUES (5656, 'phd', 'ˌpiː eɪtʃ ˈdiː', 'n.', '哲学博士学位，博士学位(全写为 Doctor of Philosophy，授予完成某学科研究者的高级学位);', 'He is more highly educated, with a PhD in Chemistry.', '他受过更高的教育，拥有化学博士学位。');
INSERT INTO `words` VALUES (5657, 'phantom', 'ˈfæntəm', 'n.', '鬼;鬼魂;幽灵;', 'They vanished down the stairs like two phantoms...', '他们像两个幽灵似的消失在了楼下。');
INSERT INTO `words` VALUES (5658, 'pharmaceutical', 'ˌfɑːməˈsuːtɪkl', 'adj.', '制药的;配药的;卖药的;', '...a Swiss pharmaceutical company...', '瑞士制药公司');
INSERT INTO `words` VALUES (5659, 'pharmacy', 'ˈfɑːməsi', 'n.', '药房;药店;医药柜台;', 'Make sure you understand exactly how to take your medicines before you leave the pharmacy.', '在离开药房之前，一定要弄明白自己的药该怎么吃。');
INSERT INTO `words` VALUES (5660, 'phase', 'feɪz', 'n.', '阶段;时期;月相;', 'This autumn, 6000 residents will participate in the first phase of the project...', '今年秋天，将有6000名居民参加该项目的第一阶段。');
INSERT INTO `words` VALUES (5661, 'phenomenal', 'fəˈnɒmɪnl', 'adj.', '了不起的;非凡的;', 'Exports of Australian wine are growing at a phenomenal rate...', '澳大利亚葡萄酒出口正在以惊人的速度增长。');
INSERT INTO `words` VALUES (5662, 'phenomenon', 'fəˈnɒmɪnən', 'n.', '现象;杰出的人;非凡的人(或事物);', '...scientific explanations of natural phenomena...', '自然现象的科学解释');
INSERT INTO `words` VALUES (5663, 'philanthropic', 'ˌfɪlənˈθrɒpɪk', 'adj.', '慈善的;乐善好施的;', 'Some of the best services for the ageing are sponsored by philanthropic organizations.', '一些一流老人服务机构是由慈善组织资助的。');
INSERT INTO `words` VALUES (5664, 'philanthropy', 'fɪˈlænθrəpi', 'n.', '博爱;慈善;乐善好施;', '...a retired banker well known for his philanthropy.', '以乐善好施著称的退休银行家');
INSERT INTO `words` VALUES (5665, 'philosopher', 'fəˈlɒsəfə(r)', 'n.', '哲学家;深思的人;善于思考的人;', '...the Greek philosopher Plato.', '希腊哲学家柏拉图');
INSERT INTO `words` VALUES (5666, 'philosophical', 'ˌfɪləˈsɒfɪkl', 'adj.', '哲学的;达观的;处乱不惊的;', 'He was not accustomed to political or philosophical discussions.', '他不习惯政治或哲学讨论。');
INSERT INTO `words` VALUES (5667, 'philosophy', 'fəˈlɒsəfi', 'n.', '哲学;哲学体系;思想体系;', 'He studied philosophy and psychology at Cambridge.', '他在剑桥大学学习哲学和心理学。');
INSERT INTO `words` VALUES (5668, 'phlegmatic', 'fleɡˈmætɪk', 'adj.', '冷静的;镇定的;不易冲动的;', '...a most phlegmatic man, steadily working on as the rain splashed down.', '大雨倾盆而下时仍能沉稳工作的非常冷静的人');
INSERT INTO `words` VALUES (5669, 'phobia', 'ˈfəʊbiə', 'n.', '恐怖症，恐惧症(无名的极度恐惧);(构成名词)对…的恐惧症;', 'The man had a phobia about flying.', '这人害怕坐飞机。');
INSERT INTO `words` VALUES (5670, 'phone', 'fəʊn', 'n.', '电话;电话系统;电话机;', 'You can buy insurance over the phone...', '你可以通过电话购买保险。');
INSERT INTO `words` VALUES (5671, 'phoney', 'ˈfəʊni', 'adj.', '假的;冒充的;欺骗的;', 'He\'d telephoned with some phoney excuse she didn\'t believe for a minute...', '他找了某个她根本不会相信的借口给她打电话。');
INSERT INTO `words` VALUES (5672, 'phonograph', 'ˈfəʊnəɡrɑːf', 'n.', '留声机;', '...old phonograph records.', '旧的留声机唱片');
INSERT INTO `words` VALUES (5673, 'photo', 'ˈfəʊtəʊ', 'n.', '同 photograph;照片;相片;', 'We must take a photo!...', '我们一定要拍张照片！');
INSERT INTO `words` VALUES (5674, 'photogenic', 'ˌfəʊtəʊˈdʒenɪk', 'adj.', '上镜的;上相的;', 'I\'ve got a million photos of my boy. He\'s very photogenic.', '我给儿子拍了好多照片，他非常上镜。');
INSERT INTO `words` VALUES (5675, 'photograph', 'ˈfəʊtəɡrɑːf', 'n.', '照片;相片;', 'He wants to take some photographs of the house...', '他想给这幢房子拍一些照片。');
INSERT INTO `words` VALUES (5676, 'photographer', 'fəˈtɒɡrəfə(r)', 'n.', '拍照者;摄影师;', '...a group of TV cameramen and press photographers.', '一群电视台的摄影师和报社摄影记者');
INSERT INTO `words` VALUES (5677, 'photographic', 'ˌfəʊtəˈɡræfɪk', 'adj.', '摄影的;摄制的;照片的;', '...photographic equipment...', '摄影器材');
INSERT INTO `words` VALUES (5678, 'photography', 'fəˈtɒɡrəfi', 'n.', '照相术;摄影;', 'Photography is one of her hobbies.', '摄影是她的爱好之一。');
INSERT INTO `words` VALUES (5679, 'phrase', 'freɪz', 'n.', '短语;词组;成语;', 'He used a phrase I hate: \'You have to be cruel to be kind.\'', '他说了一句我讨厌的话：“要想善良，就得残忍。”');
INSERT INTO `words` VALUES (5680, 'physical', 'ˈfɪzɪkl', 'adj.', '身体的;肉体的;躯体的;', '...the physical and mental problems caused by the illness...', '这种病引起的生理和心理的问题');
INSERT INTO `words` VALUES (5681, 'physically', 'ˈfɪzɪkli', 'adv.', '身体上;肉体上;依据自然规律;', '...the physical and mental problems caused by the illness...', '这种病引起的生理和心理的问题');
INSERT INTO `words` VALUES (5682, 'physicist', 'ˈfɪzɪsɪst', 'n.', '物理学家;物理学研究者;', '...a nuclear physicist.', '核物理学家');
INSERT INTO `words` VALUES (5683, 'physics', 'ˈfɪzɪks', 'n.', '物理学;', '...the laws of physics.', '物理定律');
INSERT INTO `words` VALUES (5684, 'physiognomy', 'ˌfɪziˈɒnəmi', 'n.', '容貌;相貌;面相;', 'He was fascinated by her physiognomy — the prominent nose, brooding eyes and thick hair.', '他被她的外表——高挺的鼻梁、深邃的双眸和浓密的秀发——迷住了。');
INSERT INTO `words` VALUES (5685, 'physiological', 'ˌfɪziə\'lɒdʒɪk(ə)l', 'adj.', '生理学(上)的;', '...the Nobel Prize for Medicine and Physiology.', '诺贝尔医学和生理学奖');
INSERT INTO `words` VALUES (5686, 'physiologist', 'ˌfɪziˈɒlədʒɪst', 'n.', '生理学家;生理学研究者;', '...the Nobel Prize for Medicine and Physiology.', '诺贝尔医学和生理学奖');
INSERT INTO `words` VALUES (5687, 'physiology', 'ˌfɪziˈɒlədʒi', 'n.', '生理学;生理机能;', '...the Nobel Prize for Medicine and Physiology.', '诺贝尔医学和生理学奖');
INSERT INTO `words` VALUES (5688, 'physiotherapy', 'ˌfɪziəʊˈθerəpi', 'n.', '物理治疗法;理疗;', 'He\'ll need intensive physiotherapy.', '他需要高强度理疗。');
INSERT INTO `words` VALUES (5689, 'physique', 'fɪˈziːk', 'n.', '体格;体形;', 'He has the physique and energy of a man half his age.', '他的体格和精力比得上年龄只有他一半大的人。');
INSERT INTO `words` VALUES (5690, 'pianist', 'ˈpɪənɪst', 'n.', '钢琴弹奏者;钢琴家;', '...the brilliant Romanian pianist Radu Lupu.', '才华横溢的罗马尼亚钢琴家拉度·鲁普');
INSERT INTO `words` VALUES (5691, 'piazza', 'piˈætsə', 'n.', '(尤指意大利城镇中的)广场;', 'They were seated at a table outside a pub in a pleasant piazza close by St Paul\'s...', '在圣保罗教堂旁一个怡人的广场上，他们坐在一家酒馆外的桌边。');
INSERT INTO `words` VALUES (5692, 'pic', 'pɪk', 'n.', '图片;画片;', '\'Angels with Dirty Faces\' is a Cagney gangster pic.', '《一世之雄》是一部由卡格尼主演的黑帮片。');
INSERT INTO `words` VALUES (5693, 'picaresque', 'ˌpɪkəˈresk', 'adj.', '流浪汉小说题材的;', '...a picaresque novel about the life and crimes of Joey Blueglass.', '关于乔伊·蓝玻的生平和罪行的流浪汉小说');
INSERT INTO `words` VALUES (5694, 'pick', 'pɪk', 'v.', '选择;挑选;采;', 'Mr Nowell had picked ten people to interview for six sales jobs in London...', '诺埃尔先生为伦敦的6个销售职位选出了10人进行面试。');
INSERT INTO `words` VALUES (5695, 'picket', 'ˈpɪkɪt', 'n.', '(罢工期间纠察妥协分子的)纠察员，纠察队;罢工警戒;(军营的)警戒哨，警戒队，哨兵;', 'The miners went on strike and picketed the power stations...', '矿工开始罢工，并在发电站周围设置了纠察队。');
INSERT INTO `words` VALUES (5696, 'picking', 'ˈpɪkɪŋ', 'v.', '选择;挑选;采;', 'Mr Nowell had picked ten people to interview for six sales jobs in London...', '诺埃尔先生为伦敦的6个销售职位选出了10人进行面试。');
INSERT INTO `words` VALUES (5697, 'pickle', 'ˈpɪkl', 'n.', '泡菜;腌菜;菜酱;', 'Another strong Yorkshire country tradition is making pickles and chutneys.', '另一个在约克郡盛行的民间传统是做泡菜和酸辣酱。');
INSERT INTO `words` VALUES (5698, 'picky', 'ˈpɪki', 'adj.', '挑剔的;难伺候的;', 'Some people are very picky about who they choose to share their lives with...', '有些人对选择什么样的人共度一生非常挑剔。');
INSERT INTO `words` VALUES (5699, 'picnic', 'ˈpɪknɪk', 'n.', '野餐;野餐食物;', 'We\'re going on a picnic tomorrow...', '我们明天去野餐。');
INSERT INTO `words` VALUES (5700, 'pictograph', 'ˈpɪktəˌgrɑːf', 'n.', '图画文字;象形文字;', '...a pictogram of a pine tree.', '表示一棵松树的图画文字');
INSERT INTO `words` VALUES (5701, 'pictorial', 'pɪkˈtɔːriəl', 'adj.', '用图片的;有插图的;画片的;', '...a pictorial history of the Special Air Service.', '空军特勤团图片史');
INSERT INTO `words` VALUES (5702, 'picture', 'ˈpɪktʃə(r)', 'n.', '图画;绘画;相片;', 'A picture of Rory O\'Moore hangs in the dining room at Kildangan.', '一幅罗里·奥穆尔的画像挂在基尔丹根的餐厅里。');
INSERT INTO `words` VALUES (5703, 'pictures', 'ˈpɪktʃəz', 'n.', '图画;绘画;相片;', 'A picture of Rory O\'Moore hangs in the dining room at Kildangan.', '一幅罗里·奥穆尔的画像挂在基尔丹根的餐厅里。');
INSERT INTO `words` VALUES (5704, 'picturesque', 'ˌpɪktʃəˈresk', 'adj.', '优美的;古色古香的;生动的;', 'Alte, in the hills northwest of Loule, is the Algarve\'s most picturesque village.', '位于洛莱西北部山区的阿尔特，是阿尔加维风景最美丽的村庄。');
INSERT INTO `words` VALUES (5705, 'piddling', 'ˈpɪdlɪŋ', 'adj.', '琐碎的;鸡毛蒜皮的;', '...arguing over piddling amounts of money.', '为一点小钱争吵');
INSERT INTO `words` VALUES (5706, 'pie', 'paɪ', 'n.', '果馅饼;果馅派;肉馅饼;', '...a pork pie.', '猪肉馅饼');
INSERT INTO `words` VALUES (5707, 'piebald', 'ˈpaɪbɔːld', 'adj.', '花斑的;有黑白两色的;', '...a piebald pony.', '黑白斑小型马');
INSERT INTO `words` VALUES (5708, 'piece', 'piːs', 'n.', '片;块;段;', '...a piece of cake.', '一块蛋糕');
INSERT INTO `words` VALUES (5709, 'piecemeal', 'ˈpiːsmiːl', 'adj.', '逐渐做成(或发生)的;零敲碎打的;零散的;', 'Instead of the government\'s piecemeal approach, what is needed is a radical shake-up of 16-19 education.', '需要的不是政府零敲碎打的措施，而是要对16至19岁年龄段的教育进行彻底变革。');
INSERT INTO `words` VALUES (5710, 'pieces', 'ˈpiːsɪz', 'n.', '片;块;段;', '...a piece of cake.', '一块蛋糕');
INSERT INTO `words` VALUES (5711, 'piecework', 'ˈpiːswɜːk', 'n.', '计件工作;', 'All my men are on piece-work...', '我所有的工人做的都是计件工。');
INSERT INTO `words` VALUES (5712, 'pier', 'pɪə(r)', 'n.', '(常设有娱乐场所的)突堤;(突入湖、河、海中的)码头;突码头;', '...Brighton Pier.', '布赖顿码头');
INSERT INTO `words` VALUES (5713, 'pierce', 'pɪəs', 'v.', '扎;刺破;穿透;', 'One bullet pierced the left side of his chest...', '一颗子弹射入他的左胸。');
INSERT INTO `words` VALUES (5714, 'piercing', 'ˈpɪəsɪŋ', 'adj.', '锐利的;逼人的;尖利的;', 'A piercing scream split the air.', '刺耳的尖叫声划破长空。');
INSERT INTO `words` VALUES (5715, 'pig', 'pɪɡ', 'n.', '猪;令人不快(或讨厌)的人;贪婪(或肮脏)的人;', '...the grunting of the pigs.', '猪的呼噜声');
INSERT INTO `words` VALUES (5716, 'piggyback', 'ˈpɪɡibæk', 'n.', '背着;肩驮;', 'They give each other piggy-back rides.', '他们轮换驮着对方。');
INSERT INTO `words` VALUES (5717, 'pigment', 'ˈpɪɡmənt', 'n.', '色素;颜料;', 'The Romans used natural pigments on their fabrics and walls.', '古罗马人在织物和墙壁上使用天然颜料。');
INSERT INTO `words` VALUES (5718, 'pigtail', 'ˈpɪɡteɪl', 'n.', '辫子;', '...a little girl with pigtails.', '扎辫子的小女孩');
INSERT INTO `words` VALUES (5719, 'pile', 'paɪl', 'n.', '摞;垛;沓;', '...a pile of sand...', '一堆沙子');
INSERT INTO `words` VALUES (5720, 'pilfer', 'ˈpɪlfə(r)', 'v.', '偷窃(小东西);小偷小摸;(尤指员工)偷窃;', 'Staff were pilfering behind the bar...', '店员在柜台后有些小偷小摸的行为。');
INSERT INTO `words` VALUES (5721, 'pilgrimage', 'ˈpɪlɡrɪmɪdʒ', 'n.', '朝圣之旅;参拜之行;瞻仰之旅;', '...the pilgrimage to Mecca.', '去麦加的朝圣');
INSERT INTO `words` VALUES (5722, 'piling', 'ˈpaɪlɪŋ', 'n.', '(房屋、桥梁等的)桩，桩结构;', '...bridges set on stone pilings.', '架在石桩上的桥梁');
INSERT INTO `words` VALUES (5723, 'pill', 'pɪl', 'n.', '药丸;药片;口服避孕药;', 'Why do I have to take all these pills?', '我为什么非得服下所有这些药丸呢？');
INSERT INTO `words` VALUES (5724, 'pillage', 'ˈpɪlɪdʒ', 'v.', '抢劫;劫掠;掠夺;', 'Soldiers went on a rampage, pillaging stores and shooting.', '士兵们横冲直撞，洗劫商店并且开枪射击。');
INSERT INTO `words` VALUES (5725, 'pillar', 'ˈpɪlə(r)', 'n.', '柱子，桥墩(尤指兼作装饰的);纪念柱;柱状物;', '...the pillars supporting the roof.', '支撑屋顶的柱子');
INSERT INTO `words` VALUES (5726, 'pillory', 'ˈpɪləri', 'v.', '公开批评;抨击;', 'A man has been forced to resign as a result of being pilloried by some of the press.', '一人因为受到一些媒体的抨击已被迫辞职。');
INSERT INTO `words` VALUES (5727, 'pilot', 'ˈpaɪlət', 'n.', '飞行员;(飞行器)驾驶员;领航员;', 'He spent seventeen years as an airline pilot.', '他当了17年的航空公司飞行员。');
INSERT INTO `words` VALUES (5728, 'pimple', 'ˈpɪmpl', 'n.', '丘疹;粉刺;小脓包;', '...spots and pimples...', '斑点和丘疹');
INSERT INTO `words` VALUES (5729, 'pin', 'pɪn', 'n.', '大头针;胸针;饰针;', '...needles and pins...', '针和别针');
INSERT INTO `words` VALUES (5730, 'pince-nez', 'ˌpæ̃s ˈneɪ', 'n.', '(旧时的)夹鼻眼镜;', 'His secretary was a tall woman in pince-nez.', '他的秘书是位戴着夹鼻眼镜的高个女士。');
INSERT INTO `words` VALUES (5731, 'pincers', 'ˈpɪnsəz', 'n.', '钳子;(蟹、虾等的)螯;', 'His surgical instruments were a knife and a pair of pincers.', '他的外科手术器械是一把刀和一把钳子。');
INSERT INTO `words` VALUES (5732, 'pinch', 'pɪntʃ', 'v.', '拧;捏;掐;', 'She pinched his arm as hard as she could...', '她使出浑身的力气拧他的胳膊。');
INSERT INTO `words` VALUES (5733, 'pine', 'paɪn', 'n.', '松树;松木;', '...high mountains covered in pine trees.', '长满松树的高山');
INSERT INTO `words` VALUES (5734, 'pinhead', 'ˈpɪnhed', 'n.', '大头针的平头;', 'It may even be possible to make computers the size of a pinhead one day.', '甚至有一天计算机都可能做成钉头那样大小。');
INSERT INTO `words` VALUES (5735, 'pinion', 'ˈpɪnjən', 'v.', '捆住，缚住(双臂);固定住;', 'At nine the next morning Bentley was pinioned, hooded and hanged.', '次日上午9点，本特里被捆住双臂、戴上头罩，然后绞死了。');
INSERT INTO `words` VALUES (5736, 'pink', 'pɪŋk', 'adj.', '粉红色的;与同性恋者有关的;政治观点偏左的;', '...pink lipstick.', '粉红色的口红');
INSERT INTO `words` VALUES (5737, 'pinkie', '', 'n.', '小（手）指，=pinky;尖头帆船;', 'He pushes his glasses up his nose with his pinkie.', '他用小手指把眼镜向上推了推。');
INSERT INTO `words` VALUES (5738, 'pinnacle', 'ˈpɪnəkl', 'n.', '顶点;顶峰;鼎盛时期;', 'A walker fell 80ft from a rocky pinnacle.', '一位徒步者从一块80英尺高的尖锥形岩石上摔了下来。');
INSERT INTO `words` VALUES (5739, 'pinpoint', 'ˈpɪnpɔɪnt', 'v.', '明确指出，确定(位置或时间);准确解释(或说明);', 'It was almost impossible to pinpoint the cause of death.', '几乎不可能确认死因。');
INSERT INTO `words` VALUES (5740, 'pinprick', 'ˈpɪnprɪk', 'n.', '(光等的)点;小孔;针孔;', '...a pinprick of light...', '一丝光线');
INSERT INTO `words` VALUES (5741, 'pinstripe', 'ˈpɪnstraɪp', 'n.', '(深色西装衣料上)白色细条纹;带有白色细条纹的深色衣料;细条纹西服;', 'He wore an expensive, dark blue pinstripe suit.', '他身穿一套件价格不菲的深蓝色细条纹西装。');
INSERT INTO `words` VALUES (5742, 'pint', 'paɪnt', 'n.', '品脱(容量单位，为⅛加仑，在英国等国家约合0.568升，在美国约合0.473升);一品脱啤酒(尤用于酒吧);', '...a pint of milk...', '一品脱牛奶');
INSERT INTO `words` VALUES (5743, 'pioneer', 'ˌpaɪəˈnɪə(r)', 'n.', '先锋;先驱;带头人;', '...one of the leading pioneers of British photo journalism...', '英国新闻摄影界的领军人物之一');
INSERT INTO `words` VALUES (5744, 'pioneering', 'ˌpaɪəˈnɪərɪŋ', 'adj.', '开拓性的;先驱性的;探索性的;', 'The school has won awards for its pioneering work with the community...', '这所学校因其针对该社区具有开创性的工作而获奖。');
INSERT INTO `words` VALUES (5745, 'pious', 'ˈpaɪəs', 'adj.', '虔诚的;虔敬的;道貌岸然的;', 'He was brought up by pious female relatives.', '他是由虔诚的女性亲属带大的。');
INSERT INTO `words` VALUES (5746, 'pipe', 'paɪp', 'n.', '管子;管道;烟斗;', 'They had accidentally damaged a gas pipe while drilling...', '打钻时，他们不小心钻破了一根燃气管道。');
INSERT INTO `words` VALUES (5747, 'pipeline', 'ˈpaɪplaɪn', 'n.', '输油管道，输气管道，输送管线(通常指地下的);', 'A consortium plans to build a natural-gas pipeline from Russia to supply eastern Germany.', '一家财团计划建造一条从俄罗斯向德国东部输送天然气的管道。');
INSERT INTO `words` VALUES (5748, 'piping', 'ˈpaɪpɪŋ', 'n.', '(某种或某长度的)管道，管子;(衣服、靠垫等的)绲边;(用糖霜等裱成的)糕点条纹花饰;', '...rolls of bright yellow plastic piping.', '成卷的明黄色塑料管');
INSERT INTO `words` VALUES (5749, 'piquant', 'ˈpiːkənt', 'adj.', '辛辣的;开胃的;刺激的;', '...a crisp mixed salad with an unusually piquant dressing.', '浇上特别辛辣的调料的脆口什锦色拉');
INSERT INTO `words` VALUES (5750, 'pique', 'piːk', 'n.', '怨恨;愤恨;恼怒;', 'Mimi had gotten over her pique at Susan\'s refusal to accept the job.', '米米已不再为苏姗拒绝接受这份工作的事生气了。');
INSERT INTO `words` VALUES (5751, 'piracy', 'ˈpaɪrəsi', 'n.', '海上抢劫;盗版行为;非法复制;', 'Seven of the fishermen have been formally charged with piracy.', '其中7名渔夫因海盗行为遭正式起诉。');
INSERT INTO `words` VALUES (5752, 'pirate', 'ˈpaɪrət', 'n.', '(尤指旧时的)海盗;盗版者;盗印者;', 'In the nineteenth century, pirates roamed the seas.', '19世纪时海盗猖獗。');
INSERT INTO `words` VALUES (5753, 'pirouette', 'ˌpɪruˈet', 'n.', '(尤指芭蕾舞中的)单脚尖旋转;', 'Virginia Zucci of the Russian Ballet was famous for her pirouettes.', '俄罗斯芭蕾舞团的维尔吉尼娅·祖奇以她的单脚尖旋转而著称。');
INSERT INTO `words` VALUES (5754, 'pit', 'pɪt', 'n.', '深洞;深坑;矿井;', 'It was a better community then when all the pits were working.', '所有的煤矿都在开工的那个时候，这个社区的情况要更好一些。');
INSERT INTO `words` VALUES (5755, 'pitch', 'pɪtʃ', 'n.', '(体育比赛的)场地;球场;(感情、活动等的)程度，力度;', 'There was a swimming-pool, cricket pitches, playing fields...', '有一个游泳池、几个板球场和运动场。');
INSERT INTO `words` VALUES (5756, 'pitch-black', 'ˌpɪtʃ ˈblæk', 'adj.', '漆黑的;乌黑的;', '...a cold pitch-black winter morning.', '寒冷、漆黑的冬日早晨');
INSERT INTO `words` VALUES (5757, 'pitcher', 'ˈpɪtʃə(r)', 'n.', '(尤指旧时的)带柄的陶罐;投球手;', '...a pitcher of iced water.', '一罐冰水');
INSERT INTO `words` VALUES (5758, 'pitfall', 'ˈpɪtfɔːl', 'n.', '危险;困难;(尤指)陷阱，隐患;', 'The pitfalls of working abroad are numerous...', '到国外工作存在许多陷阱。');
INSERT INTO `words` VALUES (5759, 'pithy', 'ˈpɪθi', 'adj.', '言简意赅的;精练的;', 'His pithy advice to young painters was, \'Above all, keep your colours fresh.\'...', '他对年轻画家言简意赅的建议是，“首要的一点，保持色彩鲜艳。”');
INSERT INTO `words` VALUES (5760, 'pitiable', 'ˈpɪtiəbl', 'adj.', '值得同情的;可怜的;卑鄙的;', 'Her grandmother seemed to her a pitiable figure.', '祖母在她看来是个可怜的人。');
INSERT INTO `words` VALUES (5761, 'pitiful', 'ˈpɪtɪfl', 'adj.', '令人同情的;可怜的;卑微的;', 'He sounded both pitiful and eager to get what he wanted...', '他听起来既可怜兮兮的，又对想要的东西充满渴望。');
INSERT INTO `words` VALUES (5762, 'pitiless', 'ˈpɪtiləs', 'adj.', '冷酷的;无情的;严酷而无尽的;', 'He saw the pitiless eyes of his enemy...', '他看到了敌人冷酷的眼神。');
INSERT INTO `words` VALUES (5763, 'pittance', 'ˈpɪtns', 'n.', '微薄的工资;极少的报酬;', 'Her secretaries work tirelessly for a pittance.', '她的秘书们为一点微薄的工资不知疲倦地工作。');
INSERT INTO `words` VALUES (5764, 'pity', 'ˈpɪti', 'n.', '同情;怜悯;(用于表示失望)遗憾，可惜;', 'He felt a sudden tender pity for her...', '他突然涌起一股对她的温柔怜悯之情。');
INSERT INTO `words` VALUES (5765, 'pivot', 'ˈpɪvət', 'n.', '支点;枢轴;中心点;', 'Forming the pivot of the exhibition is a large group of watercolours.', '整个展览的重点是一大组水彩画。');
INSERT INTO `words` VALUES (5766, 'pivotal', 'ˈpɪvətl', 'adj.', '关键性的;核心的;', 'The Court of Appeal has a pivotal role in the English legal system...', '上诉法院在英格兰司法体系中起着中心作用。');
INSERT INTO `words` VALUES (5767, 'pix', 'pɪks', 'n.', '(pics 的非正式拼法)照片，电影;', '...splendid pix by ace photographer Mike Goldwater.', '一流摄影师迈克·戈得华特超棒的照片');
INSERT INTO `words` VALUES (5768, 'pizza', 'ˈpiːtsə', 'n.', '比萨饼;意大利饼;', '...the last piece of pizza...', '最后一块比萨饼');
INSERT INTO `words` VALUES (5769, 'pizzicato', 'ˌpɪtsɪˈkɑːtəʊ', 'adj./adv.', '弹拨的(地);拨奏的(地);', '...a piece designed to be played pizzicato.', '专门为拨弦设计的一段音乐');
INSERT INTO `words` VALUES (5770, 'placard', 'ˈplækɑːd', 'n.', '标语牌;广告牌;招贴;', 'The protesters sang songs and waved placards.', '抗议者唱着歌，挥舞着标语牌。');
INSERT INTO `words` VALUES (5771, 'placate', 'pləˈkeɪt', 'v.', '安抚;平息(怒气);', 'He smiled, trying to placate me...', '他微笑着，想要安抚我。');
INSERT INTO `words` VALUES (5772, 'place', 'pleɪs', 'n.', '位置;地点;场所;', '...Temple Mount, the place where the Temple actually stood.', '圣殿山，圣殿真正坐落的地方');
INSERT INTO `words` VALUES (5773, 'placement', 'ˈpleɪsmənt', 'n.', '(对人的)安置，安排;实习工作;实习课;', 'The treatment involves the placement of twenty-two electrodes in the inner ear.', '这一治疗需要在内耳放置22个电极。');
INSERT INTO `words` VALUES (5774, 'placenta', 'pləˈsentə', 'n.', '胎盘;', 'The drug can be transferred to the baby via the placenta.', '毒品会通过胎盘传给胎儿。');
INSERT INTO `words` VALUES (5775, 'placid', 'ˈplæsɪd', 'adj.', '温和的;平和的;文静的;', 'She was a placid child who rarely cried...', '她是个性情温和的孩子，很少哭闹。');
INSERT INTO `words` VALUES (5776, 'plagiarism', 'ˈpleɪdʒərɪzəm', 'n.', '抄袭;剽窃;剽窃作品;', 'Now he\'s in real trouble. He\'s accused of plagiarism.', '现在他是真遇到麻烦了。他被指控剽窃。');
INSERT INTO `words` VALUES (5777, 'plague', 'pleɪɡ', 'n.', '死亡率高的传染病;(老鼠或昆虫等肆虐造成的)灾害，祸患;', 'A cholera plague had been killing many prisoners of war at the time.', '当时霍乱疫情已在夺去很多战俘的生命。');
INSERT INTO `words` VALUES (5778, 'plaid', 'plæd', 'n.', '格子呢;毛呢;方格花呢长披肩(苏格兰民族服饰的一部分);', 'Eddie wore blue jeans and a plaid shirt.', '埃迪穿着蓝色牛仔裤和方格布衬衫。');
INSERT INTO `words` VALUES (5779, 'plain', 'pleɪn', 'adj.', '清楚的;明显的;浅白的;', 'In general, a plain carpet makes a room look bigger...', '一般来说，单色的地毯使房间显得更宽敞。');
INSERT INTO `words` VALUES (5780, 'plainly', 'ˈpleɪnli', 'adv.', '清晰地;明显地;清楚地;', 'The judge\'s conclusion was plainly wrong...', '法官的结论明摆着是错的。');
INSERT INTO `words` VALUES (5781, 'plains', 'pleɪnz', 'n.', '平原;', 'In general, a plain carpet makes a room look bigger...', '一般来说，单色的地毯使房间显得更宽敞。');
INSERT INTO `words` VALUES (5782, 'plainspoken', 'ˈpleɪnˈspəʊkən', 'adj.', '直言不讳的;直截了当的;', '...a plain-spoken American full of scorn for pomp and pretense.', '看不惯炫耀和虚伪、直言不讳的一个美国人');
INSERT INTO `words` VALUES (5783, 'plaintive', 'ˈpleɪntɪv', 'adj.', '(声音)悲伤的，哀怨的;', 'They lay on the firm sands, listening to the plaintive cry of the seagulls...', '他们躺在硬实的沙地上，听着海鸥的哀鸣。');
INSERT INTO `words` VALUES (5784, 'plait', 'plæt', 'n.', '辫状物;发辫;辫子;', 'Joanna parted her hair, and then began to plait it into two thick braids.', '乔安娜将头发分开，然后开始把它编成两条粗辫子。');
INSERT INTO `words` VALUES (5785, 'plan', 'plæn', 'n.', '计划;打算;(详细)规划，方案;', 'The three leaders had worked out a peace plan...', '3位领导人已制订出了一个和平方案。');
INSERT INTO `words` VALUES (5786, 'plane', 'pleɪn', 'n.', '飞机;平面;(思想、存在或发展的)水平，程度，阶段，境界;', 'He had plenty of time to catch his plane...', '他有足够的时间赶飞机。');
INSERT INTO `words` VALUES (5787, 'planes', 'pleɪnz', 'n.', '飞机;平面;(思想、存在或发展的)水平，程度，阶段，境界;', 'He had plenty of time to catch his plane...', '他有足够的时间赶飞机。');
INSERT INTO `words` VALUES (5788, 'planet', 'ˈplænɪt', 'n.', '行星;地球(尤指环境);', 'The picture shows six of the eight planets in the solar system.', '这幅图标示了太阳系八大行星中的6颗。');
INSERT INTO `words` VALUES (5789, 'planetary', 'ˈplænətri', 'adj.', '行星的;', 'Within our own galaxy there are probably tens of thousands of planetary systems.', '在我们自己的星系里可能有数以万计的行星系。');
INSERT INTO `words` VALUES (5790, 'plangent', 'ˈplændʒənt', 'adj.', '洪亮的;轰鸣的;哀婉的;', '...plangent violins.', '哀婉的小提琴声');
INSERT INTO `words` VALUES (5791, 'plank', 'plæŋk', 'n.', '木板;板条;(政党等的)政策准则，政纲的核心;', 'It was very strong, made of three solid planks of wood.', '它由3块坚固的木板做成，很结实。');
INSERT INTO `words` VALUES (5792, 'plankton', 'ˈplæŋktən', 'n.', '浮游生物;', '...its usual diet of plankton and other small organisms.', '它通常赖以为食的浮游生物和其他微生物');
INSERT INTO `words` VALUES (5793, 'planner', 'ˈplænə(r)', 'n.', '城市规划者;设计者;规划者;', '...a panel that includes city planners, art experts and historians.', '由城市规划者、艺术专家和历史学家组成的专门小组');
INSERT INTO `words` VALUES (5794, 'planning', 'ˈplænɪŋ', 'n.', '计划制订;规划过程;', 'The trip needs careful planning...', '这趟行程需要周密计划。');
INSERT INTO `words` VALUES (5795, 'plant', 'plɑːnt', 'n.', '植物;发电厂;工厂;', 'Water each plant as often as required.', '按要求经常给每一株植物浇水。');
INSERT INTO `words` VALUES (5796, 'plantation', 'plɑːnˈteɪʃn', 'n.', '种植园，种植场(尤指热带国家种植咖啡、甘蔗、橡胶等的大庄园);木材林地;人造林;', '...banana plantations in Costa Rica.', '哥斯达黎加的香蕉种植园');
INSERT INTO `words` VALUES (5797, 'plaque', 'plæk', 'n.', '(纪念性的)匾牌，匾额;纪念匾;牙斑;', 'After touring the hospital, Her Majesty unveiled a commemorative plaque.', '女王陛下巡视完这家医院后，为纪念匾揭了幕。');
INSERT INTO `words` VALUES (5798, 'plaster', 'ˈplɑːstə(r)', 'n.', '灰泥;熟石膏;膏药;', 'There were huge cracks in the plaster, and the green shutters were faded...', '灰泥上出现多条大裂缝，绿色百叶窗也褪色了。');
INSERT INTO `words` VALUES (5799, 'plastered', 'ˈplɑːstəd', 'adj.', '醉;', 'His hair was plastered down to his scalp by the rain.', '他的头发淋雨后紧贴在头皮上。');
INSERT INTO `words` VALUES (5800, 'plastic', 'ˈplæstɪk', 'n.', '塑料;塑料学;信用卡;', '...a wooden crate, sheltered from wetness by sheets of plastic...', '用塑料薄膜防潮的木板箱');
INSERT INTO `words` VALUES (5801, 'plasticity', 'plæˈstɪsəti', 'n.', '可塑性;塑性;', '...a wooden crate, sheltered from wetness by sheets of plastic...', '用塑料薄膜防潮的木板箱');
INSERT INTO `words` VALUES (5802, 'plastics', 'ˈplæstɪks', 'n.', '塑料;塑料学;信用卡;', '...a wooden crate, sheltered from wetness by sheets of plastic...', '用塑料薄膜防潮的木板箱');
INSERT INTO `words` VALUES (5803, 'plate', 'pleɪt', 'n.', '盘子;碟子;一盘所盛之量;', 'Anita pushed her plate away; she had eaten virtually nothing.', '安妮塔把自己的盘子推开;她几乎什么都没吃。');
INSERT INTO `words` VALUES (5804, 'plateau', 'ˈplætəʊ', 'n.', '高原;(发展、增长后的)稳定期，停滞期;', 'A broad valley opened up leading to a high, flat plateau of cultivated land.', '一条宽阔的山谷展现在眼前，通往一处平整的开垦地高原。');
INSERT INTO `words` VALUES (5805, 'platform', 'ˈplætfɔːm', 'n.', '站台;月台;讲台;', 'Nick finished what he was saying and jumped down from the platform.', '尼克讲完后，从讲台上跳了下来。');
INSERT INTO `words` VALUES (5806, 'platitude', 'ˈplætɪtjuːd', 'n.', '陈词滥调;老生常谈;', 'Why couldn\'t he say something original instead of spouting the same old platitudes?', '他为什么就不能讲些有新意的话，而不是喋喋不休地说些老生常谈呢？');
INSERT INTO `words` VALUES (5807, 'platter', 'ˈplætə(r)', 'n.', '大平盘;', 'The food was being served on silver platters.', '食物是用银质大浅盘盛着的。');
INSERT INTO `words` VALUES (5808, 'plausible', 'ˈplɔːzəbl', 'adj.', '有道理的;可信的;巧言令色的;', 'A more plausible explanation would seem to be that people are fed up with the Conservative government...', '貌似更加合理的解释似乎是，人们已经厌倦了保守党政府。');
INSERT INTO `words` VALUES (5809, 'play', 'pleɪ', 'v.', '玩耍;游戏;玩乐;', '...invite the children round to play...', '邀请孩子们来玩');
INSERT INTO `words` VALUES (5810, 'playback', 'ˈpleɪbæk', 'n.', '录音(或录像、电话留言等的)播放;(播放的)录音，录像;', 'I heard a playback of one of the tapes.', '我听到了其中一盘录音带的回放。');
INSERT INTO `words` VALUES (5811, 'playboy', 'ˈpleɪbɔɪ', 'n.', '寻欢作乐的有钱男子;花花公子;纨绔子弟;', 'Father was a rich playboy.', '父亲是一个有钱的花花公子。');
INSERT INTO `words` VALUES (5812, 'player', 'ˈpleɪə(r)', 'n.', '游戏者;运动员;参赛选手;', '...his greatness as a player...', '他作为运动员的伟大之处');
INSERT INTO `words` VALUES (5813, 'playful', 'ˈpleɪfl', 'adj.', '有趣的;爱嬉戏的;爱玩的;', '...a playful kiss on the tip of his nose.', '在他鼻尖上的戏吻');
INSERT INTO `words` VALUES (5814, 'playhouse', 'ˈpleɪhaʊs', 'n.', '(用于剧场名称)剧院;游戏房(供儿童进入玩耍的大模型房子);', 'The Theatre Royal is one of the oldest playhouses in Britain.', '皇家剧院是英国最古老的剧场之一。');
INSERT INTO `words` VALUES (5815, 'playmate', 'ˈpleɪmeɪt', 'n.', '(儿童)玩耍的伙伴;', 'The young girl loved to play with her playmates.', '这个小女孩喜欢和她的伙伴一起玩。');
INSERT INTO `words` VALUES (5816, 'playoff', '\'pleɪˌɒf', 'n.', '(平分后要决出胜负的)附加赛;(常规赛季之后的)季后赛;', 'Nick Faldo was beaten by Peter Baker in a play-off.', '尼克·福尔多在附加赛中被彼得·贝克击败。');
INSERT INTO `words` VALUES (5817, 'plaything', 'ˈpleɪθɪŋ', 'n.', '玩物;玩乐对象;玩具;', '...an untidy garden scattered with children\'s playthings.', '到处散落着玩具的凌乱的花园');
INSERT INTO `words` VALUES (5818, 'playtime', 'ˈpleɪtaɪm', 'n.', '(学校的)游戏时间，课间休息时间;娱乐时间;', 'Any child who is caught will be kept in at playtime.', '凡是被抓住的孩子在课间游戏时要被留在屋里。');
INSERT INTO `words` VALUES (5819, 'plaza', 'ˈplɑːzə', 'n.', '(尤指西班牙语城镇的)露天广场;购物中心;', 'Across the busy plaza, vendors sell hot dogs and croissant sandwiches.', '在热闹广场的另一边，摊贩叫卖着热狗和羊角三明治。');
INSERT INTO `words` VALUES (5820, 'plea', 'pliː', 'n.', '请求;恳求;(被告或被告律师的)抗辩，答辩，辩护;', 'Mr Nicholas made his emotional plea for help in solving the killing.', '尼古拉斯先生恳请帮助以解决这起凶杀案。');
INSERT INTO `words` VALUES (5821, 'plead', 'pliːd', 'v.', '乞求;恳求;(在法庭)申辩，认罪，辩护;', 'The lady pleaded with her daughter to come back home...', '这位女士恳求她的女儿回家。');
INSERT INTO `words` VALUES (5822, 'pleading', 'ˈpliːdɪŋ', 'n.', '恳求;央求;诉状;', '...his pleading eyes.', '他恳求的目光');
INSERT INTO `words` VALUES (5823, 'pleasant', 'ˈpleznt', 'adj.', '令人愉快的;可喜的;宜人的;', 'I\'ve got a pleasant little apartment...', '我有一套舒适的小公寓。');
INSERT INTO `words` VALUES (5824, 'pleasantly', 'ˈplɛzntli', '', '愉快地;愉快的;高兴地;', 'I\'ve got a pleasant little apartment...', '我有一套舒适的小公寓。');
INSERT INTO `words` VALUES (5825, 'please', 'pliːz', 'int.', '(用于客气地请求或吩咐)请，请问;(用于加强请求或陈述的语气)请千万，请务必，的确;(表示接受的客气话)太感谢了，太好了;', 'Can you help us please?...', '能请你帮帮我们吗？');
INSERT INTO `words` VALUES (5826, 'pleased', 'pliːzd', 'adj.', '高兴;满意;愉快;', 'Felicity seemed pleased at the suggestion...', '费莉西蒂好像对这个建议很满意。');
INSERT INTO `words` VALUES (5827, 'pleasing', 'ˈpliːzɪŋ', 'adj.', '令人高兴的;令人满意的;', 'This area of France has a pleasing climate in August...', '法国的这一地区在8月份气候宜人。');
INSERT INTO `words` VALUES (5828, 'pleasurable', 'ˈpleʒərəbl', 'adj.', '愉快的;快活的;适意的;', 'The most pleasurable experience of the evening was the wonderful fireworks display...', '这个晚上最让人高兴的就是精彩的焰火表演。');
INSERT INTO `words` VALUES (5829, 'pleasure', 'ˈpleʒə(r)', 'n.', '高兴;快乐;愉快;', 'Watching sport gave him great pleasure...', '观看体育比赛给他以极大的愉悦。');
INSERT INTO `words` VALUES (5830, 'plebeian', 'pləˈbiːən', 'adj.', '平民的;百姓的;下层社会的;', 'In the 1790s Tom Paine taught plebeian radicals that mankind would live in harmony were it not for the vested interest which princes, diplomats and soldiers had in promoting wars to enrich themselves.', '汤姆·潘恩在18世纪90年代教育平民激进分子时论述到，如果不是为了实现王公、外交官和士兵借发起战争而使自己富有的潜在利益，人类本会生活在和谐之中。');
INSERT INTO `words` VALUES (5831, 'pledge', 'pledʒ', 'n.', '保证;诺言;誓约;', 'The meeting ended with a pledge to step up cooperation between the six states of the region.', '会议以承诺加快这一地区六国间的合作而结束。');
INSERT INTO `words` VALUES (5832, 'plenary', 'ˈpliːnəri', 'adj.', '全体参加的;无限的;完全的;', 'The programme was approved at a plenary session of the Central Committee last week.', '这个方案在上周的中央委员会全体会议上获得了通过。');
INSERT INTO `words` VALUES (5833, 'plenipotentiary', 'ˌplenɪpəˈtenʃəri', 'n.', '(尤指在国外代表政府的)全权代表;全权大使;', '...the Polish government in exile\'s plenipotentiary in  Warsaw.', '波兰流亡政府在华沙的全权代表');
INSERT INTO `words` VALUES (5834, 'plenitude', 'ˈplenɪtjuːd', 'n.', '大量;充裕;众多;', 'The music brought him a feeling of plenitude and freedom.', '这音乐带给他一种完满和自由的感觉。');
INSERT INTO `words` VALUES (5835, 'plentiful', 'ˈplentɪfl', 'adj.', '大量的;众多的;充足的;', 'Fish are plentiful in the lake.', '这个湖里鱼很多。');
INSERT INTO `words` VALUES (5836, 'plenty', 'ˈplenti', 'pron.', '大量;众多;充足;', 'There was still plenty of time to take Jill out for pizza...', '还有充裕的时间带吉尔出去吃比萨饼。');
INSERT INTO `words` VALUES (5837, 'plethora', 'ˈpleθərə', 'n.', '过多;过量;过剩;', 'A plethora of new operators will be allowed to enter the market.', '大批新的运营商将获准进入该市场。');
INSERT INTO `words` VALUES (5838, 'pliable', 'ˈplaɪəbl', 'adj.', '易弯曲的;柔韧的;易受影响的;', 'As your baby grows bigger, his bones become less pliable...', '随着宝宝的成长，其骨骼的柔韧性会降低。');
INSERT INTO `words` VALUES (5839, 'pliant', 'ˈplaɪənt', 'adj.', '绵软顺从的;柔顺的;温顺的;', 'She\'s proud and stubborn, you know, under that pliant exterior.', '你要知道，在温顺的外表下，她既自傲又固执。');
INSERT INTO `words` VALUES (5840, 'plight', 'plaɪt', 'n.', '苦难;困境;苦境;', '...the worsening plight of Third World countries plagued by debts.', '受债务困扰的第三世界国家日益加深的苦境');
INSERT INTO `words` VALUES (5841, 'plod', 'plɒd', 'v.', '艰难地行走;吃力地行进;', 'Crowds of French and British families plodded around in yellow plastic macs.', '成群结队的法国和英国家庭穿着黄色塑料雨衣步履沉重地走来走去。');
INSERT INTO `words` VALUES (5842, 'plodding', 'ˈplɒdɪŋ', 'adj.', '老牛拖破车似的;做事慎重而呆板的;', 'Crowds of French and British families plodded around in yellow plastic macs.', '成群结队的法国和英国家庭穿着黄色塑料雨衣步履沉重地走来走去。');
INSERT INTO `words` VALUES (5843, 'plot', 'plɒt', 'n.', '故事情节;布局;阴谋;', 'Security forces have uncovered a plot to overthrow the government...', '安全部队揭露了一起推翻政府的阴谋。');
INSERT INTO `words` VALUES (5844, 'plotter', 'ˈplɒtə(r)', 'n.', '阴谋家;秘密策划者;(计算机)绘图仪，描绘器;', 'Coup plotters tried to seize power in Moscow.', '政变谋划者试图夺取莫斯科的政权。');
INSERT INTO `words` VALUES (5845, 'ploy', 'plɔɪ', 'n.', '计谋;策略;手法;', 'Christmas should be a time of excitement and wonder, not a cynical marketing ploy.', '圣诞节应该是兴奋和美妙的时刻，而不该是一种肆无忌惮的营销策略。');
INSERT INTO `words` VALUES (5846, 'pluck', 'plʌk', 'v.', '摘;拔;拔掉，退去(死禽的毛);', 'I plucked a lemon from the tree...', '我从树上采了一个柠檬。');
INSERT INTO `words` VALUES (5847, 'plucky', 'ˈplʌki', 'adj.', '勇敢的;有胆量的;刚毅的;', 'The plucky schoolgirl amazed doctors by hanging on to life for nearly two months.', '这名勇敢的女生坚持不放弃生命近两个月的精神令医生感到震惊。');
INSERT INTO `words` VALUES (5848, 'plumb', 'plʌm', 'v.', '探索;钻研;探究;', 'She never abandoned her attempts to plumb my innermost emotions...', '她从未放弃过探究我内心深处情感的尝试。');
INSERT INTO `words` VALUES (5849, 'plumbing', 'ˈplʌmɪŋ', 'n.', '(建筑物的)管路系统，自来水管道;水暖工的工作;', 'The electrics and the plumbing were sound.', '电路和水暖设备都完好无损。');
INSERT INTO `words` VALUES (5850, 'plume', 'pluːm', 'n.', '飘升之物;翎;羽毛;', 'The rising plume of black smoke could be seen all over Kabul.', '整个喀布尔都能看到腾起的黑烟。');
INSERT INTO `words` VALUES (5851, 'plummet', 'ˈplʌmɪt', 'v.', '暴跌;速降;', 'In Tokyo share prices have plummeted for the sixth successive day...', '东京股价已连续第6天猛跌。');
INSERT INTO `words` VALUES (5852, 'plump', 'plʌmp', 'adj.', '丰腴的;微胖的;松软的;', 'Maria was small and plump with a mass of curly hair...', '玛丽亚身材矮小丰满，一头卷发。');
INSERT INTO `words` VALUES (5853, 'plunder', 'ˈplʌndə(r)', 'v.', '(尤指战乱时用武力)抢劫，掠夺;', 'They plundered and burned the market town of Leominster...', '他们洗劫并焚烧了集镇莱姆斯特。');
INSERT INTO `words` VALUES (5854, 'plunge', 'plʌndʒ', 'v.', '使突然前冲(或下落);暴跌;骤降;', 'At least 50 people died when a bus plunged into a river...', '一辆公共汽车栽进河里，造成至少50人丧生。');
INSERT INTO `words` VALUES (5855, 'plural', 'ˈplʊərəl', 'n.', '(名词或动词的)复数，复数形式;', '\'Data\' is the Latin plural form of \'datum\'.', 'data 在拉丁文中是 datum 的复数形式。');
INSERT INTO `words` VALUES (5856, 'plurality', 'plʊəˈræləti', 'n.', '众多;大量;(未超过半数的)最多数票;', 'Federalism implies a plurality of political authorities, each with its own powers.', '联邦制意味着存在众多各自都拥有权力的政治管理机构。');
INSERT INTO `words` VALUES (5857, 'plus', 'plʌs , pluː', 'prep.', '加;和;也;', 'Send a cheque for £18.99 plus £2 for postage and packing...', '请寄上18.99英镑的支票，外加2英镑的邮资和包装费。');
INSERT INTO `words` VALUES (5858, 'plush', 'plʌʃ', 'n.', '(丝或棉的)长毛绒;', '...a plush, four-storey, Georgian house in Mayfair.', '坐落在梅费尔的乔治王朝时期风格的4层豪宅');
INSERT INTO `words` VALUES (5859, 'plutocracy', 'pluːˈtɒkrəsi', 'n.', '富豪统治;财阀当政;富豪统治的国家;', 'Financial, not moral, considerations will prevail in a plutocracy.', '在富豪当政的国家里，人们见利忘义。');
INSERT INTO `words` VALUES (5860, 'ply', 'plaɪ', 'v.', '定时往来;定期行驶;(娴熟地)使用;', 'Elsie, who had been told that Maria wasn\'t well, plied her with food...', '埃尔茜得知玛丽亚身体不适，就不停地给她弄吃的。');
INSERT INTO `words` VALUES (5861, 'pm', 'ˌpiː ˈem', 'n.', '首相，总理(全写为prime minister);', 'The PM pledged to make life better for the poorest families.', '首相承诺改善最贫困家庭的生活状况。');
INSERT INTO `words` VALUES (5862, 'pneumatic', 'njuːˈmætɪk', 'adj.', '充气的;由压缩空气操作的;气动的;', '...the sound of a pneumatic drill hammering away.', '风钻钻个不停的轰鸣声');
INSERT INTO `words` VALUES (5863, 'pneumonia', 'njuːˈməʊniə', 'n.', '肺炎;', 'She nearly died of pneumonia.', '她差点儿死于肺炎。');
INSERT INTO `words` VALUES (5864, 'poach', 'pəʊtʃ', 'v.', '水煮，炖，煨(尤指鱼);去壳水煮蛋;(在他人地界)偷猎，偷捕;', 'Many wildlife parks are regularly invaded by people poaching game.', '很多野生动物园都时常遭到偷猎之徒的侵入。');
INSERT INTO `words` VALUES (5865, 'pocket', 'ˈpɒkɪt', 'n.', '衣袋;口袋;兜;', 'He took his flashlight from his jacket pocket and switched it on...', '他从夹克口袋里拿出手电筒，打开开关。');
INSERT INTO `words` VALUES (5866, 'pocketbook', 'ˈpɒkɪtbʊk', 'n.', '(个人或国家的)财政状况，财力，钱袋子;记事本;小笔记本;', 'People feel pinched in their pocketbooks and insecure about their futures.', '人们感到荷包拮据，对未来缺乏安全感。');
INSERT INTO `words` VALUES (5867, 'pod', 'pɒd', 'n.', '荚;荚果;(飞机的)吊舱，发射架;', '...fresh peas in the pod.', '豆荚里的新鲜豌豆');
INSERT INTO `words` VALUES (5868, 'poet', 'ˈpəʊɪt', 'n.', '诗人;', 'He was a painter and poet.', '他既是画家又是诗人。');
INSERT INTO `words` VALUES (5869, 'poetic', 'pəʊˈetɪk', 'adj.', '诗歌的;诗的;像诗一般的;', 'Nikolai Demidenko gave an exciting yet poetic performance.', '尼古拉·杰米坚科的表演激动人心且富有诗意。');
INSERT INTO `words` VALUES (5870, 'poetical', 'pəʊˈɛtɪkəl', 'adj.', '同 poetic;诗歌的;诗的;', '...a work of real merit and genuine poetical feeling.', '一部名至实归、抒发真挚诗情的作品');
INSERT INTO `words` VALUES (5871, 'poetry', 'ˈpəʊətri', 'n.', '诗集;诗歌;诗作;', '...Russian poetry...', '俄罗斯诗歌');
INSERT INTO `words` VALUES (5872, 'poignancy', 'ˈpɔɪnjənsi', 'n.', '辛酸;惨痛;', 'The film contains moments of almost unbearable poignancy...', '电影中有几段几乎让人辛酸得不忍目睹。');
INSERT INTO `words` VALUES (5873, 'poignant', 'ˈpɔɪnjənt', 'adj.', '令人沉痛的;悲惨的;酸楚的;', '...a poignant combination of beautiful surroundings and tragic history.', '美丽山水和悲惨历史让人黯然神伤的结合');
INSERT INTO `words` VALUES (5874, 'point', 'pɔɪnt', 'n.', '论点;观点;见解;', 'We disagree with every point Mr Blunkett makes...', '布伦基特先生提出的观点我们一个都不同意。');
INSERT INTO `words` VALUES (5875, 'pointed', 'ˈpɔɪntɪd', 'adj.', '尖的;有尖头的;尖锐的;', '...a pointed roof.', '尖屋顶');
INSERT INTO `words` VALUES (5876, 'pointedly', 'ˈpɔɪntɪdli', 'adv.', '明确地;尖锐地;直言不讳地;', '...a pointed roof.', '尖屋顶');
INSERT INTO `words` VALUES (5877, 'pointer', 'ˈpɔɪntə(r)', 'n.', '提示;建议;标志;', 'I hope at least my daughter was able to offer you some useful pointers...', '我希望至少我的女儿为你提供了一些有用的建议。');
INSERT INTO `words` VALUES (5878, 'pointing', 'ˈpɔɪntɪŋ', 'n.', '(勾墙砖、石缝用的)灰泥，灰浆;勾缝;', 'He did the pointing in the stonework himself.', '他自己完成了建筑石造部分的勾嵌。');
INSERT INTO `words` VALUES (5879, 'pointless', 'ˈpɔɪntləs', 'adj.', '无意义的;无目标的;不值得做的;', 'Violence is always pointless...', '暴力总是毫无意义的。');
INSERT INTO `words` VALUES (5880, 'points', 'pɔɪnts', 'n.', '论点;观点;见解;', 'We disagree with every point Mr Blunkett makes...', '布伦基特先生提出的观点我们一个都不同意。');
INSERT INTO `words` VALUES (5881, 'poise', 'pɔɪz', 'n.', '沉着自信;稳重;自若;', 'What amazed him even more than her appearance was her poise...', '她的容貌让他惊叹，而更让他惊讶的是她的沉着。');
INSERT INTO `words` VALUES (5882, 'poised', 'pɔɪzd', 'adj.', '处于准备状态;蓄势待发;(暂时)平衡，稳固;', 'He studied the keyboard carefully, one finger poised.', '他仔细研究了键盘，一个手指随时准备敲击。');
INSERT INTO `words` VALUES (5883, 'poison', 'ˈpɔɪzn', 'n.', '毒药;毒物;毒素;', 'Poison from the weaver fish causes paralysis, swelling, and nausea...', '龙的毒素会引起瘫痪、肿胀和恶心。');
INSERT INTO `words` VALUES (5884, 'poisoning', 'ˈpɔɪəznɪŋ , ˈpɔɪzənɪŋ', 'n.', '中毒;服毒;毒害;', 'Poison from the weaver fish causes paralysis, swelling, and nausea...', '龙的毒素会引起瘫痪、肿胀和恶心。');
INSERT INTO `words` VALUES (5885, 'poisonous', 'ˈpɔɪzənəs', 'adj.', '引起中毒的;有毒的;分泌毒素的;', 'All parts of the yew tree are poisonous, including the berries.', '紫杉的各个部分都有毒，包括浆果。');
INSERT INTO `words` VALUES (5886, 'poke', 'pəʊk', 'v.', '(用手指或其他东西)捅，杵;推;捅;', 'Lindy poked him in the ribs.', '琳迪戳了戳他的肋部。');
INSERT INTO `words` VALUES (5887, 'poker', 'ˈpəʊkə(r)', 'n.', '扑克牌游戏;通条;拨火棍;', 'Lon and I play in the same weekly poker game.', '我和朗每周和同一帮人聚在一起打扑克。');
INSERT INTO `words` VALUES (5888, 'polar', 'ˈpəʊlə(r)', 'adj.', '极地的;近地极的;南极(或北极)的;', '...the rigours of life in the polar regions...', '在极地地区生活的艰苦');
INSERT INTO `words` VALUES (5889, 'polarity', 'pəˈlærəti', 'n.', '(两种倾向、意见等的)截然对立，两极化;极性(两极并存的状态);', '...the ideological polarity between nations.', '国家间意识形态的截然对立');
INSERT INTO `words` VALUES (5890, 'polaroid', 'ˈpəʊlərɔɪd', 'n.', '宝丽来照相机;拍立得照相机;宝丽来一次成像照片;', 'He called the Polaroid camera a godsend to his work...', '他称宝丽来照相机是老天爷特意为他的工作赐予的宝贝。');
INSERT INTO `words` VALUES (5891, 'pole', 'pəʊl', 'n.', '柱子;杆子;棍;', 'The truck crashed into a telegraph pole...', '卡车撞到了电线杆上。');
INSERT INTO `words` VALUES (5892, 'polemic', 'pəˈlemɪk', 'n.', '激烈争论;辩论文章;论战;', '...a polemic against the danger of secret societies...', '抨击秘密社团危险性的论辩');
INSERT INTO `words` VALUES (5893, 'polemical', 'pəˈlemɪkl', 'adj.', '争论的;挑起辩论的;', 'Daniels is at his best when he\'s cool and direct, rather than combative and polemical.', '丹尼尔斯冷静、坦率时状态最佳；而好斗、爱辩时则不然。');
INSERT INTO `words` VALUES (5894, 'polemics', 'pɒˈlɛmɪks', 'n.', '激烈争论;辩论文章;论战;', '...a polemic against the danger of secret societies...', '抨击秘密社团危险性的论辩');
INSERT INTO `words` VALUES (5895, 'police', 'pəˈliːs', 'n.', '警察部门;警方;', 'The police are also looking for a second car...', '警方也在寻找另一辆车。');
INSERT INTO `words` VALUES (5896, 'policy', 'ˈpɒləsi', 'n.', '政策;方针;原则;', '...plans which include changes in foreign policy and economic reforms.', '包括转变外交政策和改革经济的计划');
INSERT INTO `words` VALUES (5897, 'polio', 'ˈpəʊliəʊ', 'n.', '脊髓灰质炎;小儿麻痹症;', 'Gladys was crippled by polio at the age of 3.', '格拉迪丝3岁时因小儿麻痹症腿瘸了。');
INSERT INTO `words` VALUES (5898, 'polish', 'ˈpɒlɪʃ', 'n.', '擦光剂;上光剂; 亮光剂;', 'The still air smelt faintly of furniture polish.', '凝滞的空气中隐隐有股家具上光剂的味道。');
INSERT INTO `words` VALUES (5899, 'polished', 'ˈpɒlɪʃt', 'adj.', '抛光的;磨光的;擦亮的;', 'He is polished, charming, articulate and an excellent negotiator.', '他文雅、迷人、能言善辩，是一名优秀的谈判人。');
INSERT INTO `words` VALUES (5900, 'politburo', 'ˈpɒlɪtbjʊərəʊ', 'n.', '(共产党的)政治局;', 'The Politburo has been meeting in Peking to discuss the situation.', '政治局已在北京召开会议讨论形势。');
INSERT INTO `words` VALUES (5901, 'polite', 'pəˈlaɪt', 'adj.', '有礼貌的;客气的;儒雅的;', 'Everyone around him was trying to be polite, but you could tell they were all bored...', '他周围的每一个人都极力表现出彬彬有礼的样子，但能够看出他们都感到厌烦。');
INSERT INTO `words` VALUES (5902, 'politely', 'pə\'laɪtli', 'adv.', '彬彬有礼地;斯文地;非常客气;', 'Everyone around him was trying to be polite, but you could tell they were all bored...', '他周围的每一个人都极力表现出彬彬有礼的样子，但能够看出他们都感到厌烦。');
INSERT INTO `words` VALUES (5903, 'politeness', 'pə\'laɪtnəs', 'n.', '殷勤;', 'Everyone around him was trying to be polite, but you could tell they were all bored...', '他周围的每一个人都极力表现出彬彬有礼的样子，但能够看出他们都感到厌烦。');
INSERT INTO `words` VALUES (5904, 'politic', 'ˈpɒlətɪk', 'adj.', '谨慎的;得当的;明智的;', 'Many towns often found it politic to change their allegiance.', '很多城镇经常发现改变拥戴对象才是上策。');
INSERT INTO `words` VALUES (5905, 'political', 'pəˈlɪtɪkl', 'adj.', '政治的;政府的;政权的;', 'All other political parties there have been completely banned...', '那里其他所有政党已被彻底取缔。');
INSERT INTO `words` VALUES (5906, 'politically', 'pəˈlɪtɪkli', 'adv.', '政治上;', 'All other political parties there have been completely banned...', '那里其他所有政党已被彻底取缔。');
INSERT INTO `words` VALUES (5907, 'politician', 'ˌpɒləˈtɪʃn', 'n.', '从政者;政治家;政客;', 'They have arrested a number of leading opposition politicians.', '他们已经逮捕了数名反对党领袖。');
INSERT INTO `words` VALUES (5908, 'politics', 'ˈpɒlətɪks', 'n.', '政治;政治事务(或活动);权术;', 'The key question in British politics was how long the prime minister could survive...', '英国政治的关键问题是首相能坚持多久。');
INSERT INTO `words` VALUES (5909, 'polity', 'ˈpɒləti', 'n.', '政治组织;国家组织;政体;', '...the role of religious belief in a democratic polity.', '民主实体中宗教信仰的作用');
INSERT INTO `words` VALUES (5910, 'poll', 'pəʊl', 'n.', '民意测验;民意调查;选举投票;', 'Polls show that the European treaty has gained support in Denmark...', '民意测验显示该欧盟条约已在丹麦获得支持。');
INSERT INTO `words` VALUES (5911, 'pollinate', 'ˈpɒləneɪt', 'v.', '授粉;传粉;', 'Many of the indigenous insects are needed to pollinate the local plants.', '需要很多种土生昆虫给当地植物授粉。');
INSERT INTO `words` VALUES (5912, 'pollination', 'ˌpɒlə\'neɪʃ(ə)n', 'n.', '传粉;', 'Many of the indigenous insects are needed to pollinate the local plants.', '需要很多种土生昆虫给当地植物授粉。');
INSERT INTO `words` VALUES (5913, 'polling', 'ˈpəʊlɪŋ', 'n.', '投票;民意测验;', 'There has been a busy start to polling in today\'s local elections...', '今天地方选举的投票一开始就人头攒动。');
INSERT INTO `words` VALUES (5914, 'pollutant', 'pəˈluːtənt', 'n.', '污染物;污染物质;', 'A steady stream of California traffic clogs the air with pollutants.', '加利福尼亚川流不息的车流使空气中充斥着污染物。');
INSERT INTO `words` VALUES (5915, 'pollute', 'pəˈluːt', 'v.', '污染;弄脏;', 'Heavy industry pollutes our  rivers with noxious chemicals...', '重工业产生的有毒化学品会污染我们的河流。');
INSERT INTO `words` VALUES (5916, 'polluted', 'pəˈluːtɪd', 'v.', '污染;弄脏;', 'Heavy industry pollutes our  rivers with noxious chemicals...', '重工业产生的有毒化学品会污染我们的河流。');
INSERT INTO `words` VALUES (5917, 'pollution', 'pəˈluːʃn', 'n.', '污染;玷污;弄脏;', 'The fine was for the company\'s pollution of the air near its plants...', '罚款是因该公司对厂房周边空气造成了污染。');
INSERT INTO `words` VALUES (5918, 'polyester', 'ˌpɒliˈestə(r)', 'n.', '聚酯纤维;涤纶;', '...a green polyester shirt.', '绿色聚酯纤维衬衫');
INSERT INTO `words` VALUES (5919, 'polyglot', 'ˈpɒliɡlɒt', 'adj.', '通晓(或使用)多种语言的;用多种语言写成的;', '...Chicago\'s polyglot population.', '芝加哥的多语种人口');
INSERT INTO `words` VALUES (5920, 'polygraph', 'ˈpɒliɡræf', 'n.', '测谎(试验);', 'Hill\'s lawyers announced she had taken and passed a polygraph test.', '希尔的律师宣布她已接受并通过了测谎。');
INSERT INTO `words` VALUES (5921, 'polystyrene', 'ˌpɒliˈstaɪriːn', 'n.', '聚苯乙烯;', '...polystyrene cups.', '聚苯乙烯茶杯');
INSERT INTO `words` VALUES (5922, 'pomp', 'pɒmp', 'n.', '排场;气派;盛况;', '...the pomp and splendour of the English aristocracy.', '英格兰贵族的气派和显赫');
INSERT INTO `words` VALUES (5923, 'pomposity', 'pɒmˈpɒsəti', 'n.', '自负;自命不凡;', 'He hated pomposity and disliked being called a genius.', '他憎恶自负的作派，而且不喜欢被称为天才。');
INSERT INTO `words` VALUES (5924, 'pompous', 'ˈpɒmpəs', 'adj.', '虚华的;言辞浮夸的;', 'He was somewhat pompous and had a high opinion of his own capabilities.', '他有点自大，自视甚高。');
INSERT INTO `words` VALUES (5925, 'pond', 'pɒnd', 'n.', '池塘(尤指人工的);', 'She chose a bench beside the duck pond and sat down.', '她在鸭塘边找了条长椅坐下。');
INSERT INTO `words` VALUES (5926, 'ponder', 'ˈpɒndə(r)', 'v.', '沉思;考虑;琢磨;', 'I found myself constantly pondering the question: \'How could anyone do these things?\'...', '我发现自己总在思考这个问题：“怎么会有人做出这样的事情呢？”');
INSERT INTO `words` VALUES (5927, 'ponderous', 'ˈpɒndərəs', 'adj.', '慢条斯理的;沉闷乏味的;缓慢的;', 'He had a dense, ponderous style.', '他的行文晦涩而生硬。');
INSERT INTO `words` VALUES (5928, 'ponytail', 'ˈpəʊniteɪl', 'n.', '马尾辫;', 'Her long, fine hair was swept back in a ponytail.', '她纤细的长发在脑后梳成了马尾辫。');
INSERT INTO `words` VALUES (5929, 'pool', 'puːl', 'n.', '水坑，水塘，池塘(尤指自然形成的);一滩(液体);一小片(液体或光);', '...a heated indoor pool...', '室内温水泳池');
INSERT INTO `words` VALUES (5930, 'pools', 'puːlz', 'n.', '水坑，水塘，池塘(尤指自然形成的);一滩(液体);一小片(液体或光);', '...a heated indoor pool...', '室内温水泳池');
INSERT INTO `words` VALUES (5931, 'poor', 'pɔː(r)', 'adj.', '贫穷的;贫寒的;清贫的;', 'The reason our schools cannot afford better teachers is because people here are poor...', '我们的学校请不起好老师的原因是因为这里的人穷。');
INSERT INTO `words` VALUES (5932, 'poorly', 'ˈpɔːli', 'adv.', '糟糕地;不如意;不足;', 'I\'ve just phoned Julie and she\'s still poorly.', '我刚给朱莉打了电话，她身体还是不舒服。');
INSERT INTO `words` VALUES (5933, 'pop', 'pɒp', 'n.', '流行音乐;流行乐曲;(尤用作称呼)爸，爹;', '...the perfect combination of Caribbean rhythms, European pop, and American soul...', '加勒比海韵律、欧洲流行音乐和美国灵乐的完美结合');
