INSERT INTO `words` VALUES (5937, 'popular', 'ˈpɒpjələ(r)', 'adj.', '受喜爱的;受欢迎的;当红的;', 'This is the most popular ball game ever devised...', '这是有史以来发明出的最受欢迎的球类运动。');
INSERT INTO `words` VALUES (5938, 'popularity', 'ˌpɒpjuˈlærəti', 'n.', '受欢迎;普及;流行;', 'This is the most popular ball game ever devised...', '这是有史以来发明出的最受欢迎的球类运动。');
INSERT INTO `words` VALUES (5939, 'popularly', 'ˈpɒpjələli', 'adv.', '普遍地;广泛地;一般地;', '...the Mesozoic era, more popularly known as the age of dinosaurs.', '中生代，即一般所称的恐龙时代');
INSERT INTO `words` VALUES (5940, 'populate', 'ˈpɒpjuleɪt', 'v.', '居住于;生活于;构成…的人口;', 'Before all this the island was populated by native American Arawaks.', '在所有这一切发生之前，这个岛上居住的是印第安阿拉瓦克人。');
INSERT INTO `words` VALUES (5941, 'populated', 'ˈpɒpjuleɪtɪd', 'v.', '居住于;生活于;构成…的人口;', 'Before all this the island was populated by native American Arawaks.', '在所有这一切发生之前，这个岛上居住的是印第安阿拉瓦克人。');
INSERT INTO `words` VALUES (5942, 'population', 'ˌpɒpjuˈleɪʃn', 'n.', '(地区、国家等的)人口，人口数量;(统称)某领域的生物;族群;', 'Bangladesh now has a population of about 110 million.', '孟加拉国现有大约1.1亿人口。');
INSERT INTO `words` VALUES (5943, 'populist', 'ˈpɒpjəlɪst', 'adj.', '平民主义的;民粹主义的;', '...Jose Sarney, the current populist president...', '若泽·萨尔内，现任平民主义总统');
INSERT INTO `words` VALUES (5944, 'populous', 'ˈpɒpjələs', 'adj.', '人口众多的;人口密集的;', 'Indonesia, with 216 million people, is the fourth most populous country in the world.', '有2.16亿人的印度尼西亚是世界第四人口大国。');
