INSERT INTO `words` VALUES (5947, 'pore', 'pɔː(r)', 'n.', '(皮肤上的)毛孔;(植物的)气孔;孔隙;', 'The size of your pores is determined by the amount of oil they produce.', '毛孔大小取决于其出油多少。');
