INSERT INTO `words` VALUES (5950, 'pornographic', 'ˌpɔːnəˈɡræfɪk', 'adj.', '下流的;黄色的;色情的;', 'I found out he\'d been watching pornographic videos.', '我发现他一直在看黄色录像。');
INSERT INTO `words` VALUES (5951, 'pornography', 'pɔːˈnɒɡrəfi', 'n.', '淫秽作品;色情书刊(或音像制品等);', 'A nationwide campaign against pornography began in the summer.', '一场全国性打击淫秽作品的行动在夏季打响了。');
