INSERT INTO `words` VALUES (5953, 'port', 'pɔːt', 'n.', '港口城市;港市;口岸;', 'Port-Louis is an attractive little fishing port.', '路易港是个漂亮的小渔港。');
INSERT INTO `words` VALUES (5954, 'portability', 'ˌpɔːtəˈbɪlɪti', 'n.', '可携带，轻便;', 'There was a little portable television switched on behind the bar...', '柜台后面有一台开着的便携式小型电视机。');
INSERT INTO `words` VALUES (5955, 'portable', 'ˈpɔːtəbl', 'adj.', '便携式的;手提的;轻便的;', 'There was a little portable television switched on behind the bar...', '柜台后面有一台开着的便携式小型电视机。');
INSERT INTO `words` VALUES (5956, 'portal', 'ˈpɔːtl', 'n.', '壮观的大门;豪华的入口;门户网站;', 'I went in through the royal portal.', '我从宏伟的正门进去。');
INSERT INTO `words` VALUES (5957, 'portend', 'pɔːˈtend', 'v.', '预兆，预示，预告(坏事);', 'The change did not portend a basic improvement in social conditions.', '该变化没有预示社会状况的根本改观。');
INSERT INTO `words` VALUES (5958, 'portent', 'ˈpɔːtent', 'n.', '预兆;征兆;先兆;', 'The savage civil war there could be a portent of what\'s to come in the rest of the region...', '那里发生的野蛮内战可能预示这一地区其他地方将会发生什么。');
INSERT INTO `words` VALUES (5959, 'portentous', 'pɔːˈtentəs', 'adj.', '预示(坏事)的;先兆的;煞有介事的;', 'There was nothing portentous or solemn about him. He was bubbling with humour.', '他一点也不装腔作势或故作严肃，浑身散发着幽默。');
INSERT INTO `words` VALUES (5960, 'portfolio', 'pɔːtˈfəʊliəʊ', 'n.', '文件夹;公事包;(求职时用以证明资历的)作品，整套照片;', 'After dinner that evening,  Edith showed them a portfolio of her own political cartoons.', '那天吃过晚饭，伊迪丝向他们展示了她的一组政治漫画作品。');
INSERT INTO `words` VALUES (5961, 'portion', 'ˈpɔːʃn', 'n.', '部分;(食物的)一份，一客;分享的部分;', 'Damage was confined to a small portion of the castle...', '城堡仅有一小部分受损。');
INSERT INTO `words` VALUES (5962, 'portly', 'ˈpɔːtli', 'adj.', '发福的;发胖的;', '...a portly middle-aged man.', '一个胖胖的中年男人');
INSERT INTO `words` VALUES (5963, 'portrait', 'ˈpɔːtreɪt', 'n.', '肖像;半身画像;半身照;', 'Lucian Freud has been asked to paint a portrait of the Queen.', '卢西恩·弗洛伊德已受邀为女王画肖像画。');
INSERT INTO `words` VALUES (5964, 'portray', 'pɔːˈtreɪ', 'v.', '描绘;描画;描写;', 'In 1975 he portrayed the king in a Los Angeles revival of \'Camelot\'.', '他1975年在洛杉矶重新上演的剧目《卡米洛》中扮演国王。');
INSERT INTO `words` VALUES (5965, 'portrayal', 'pɔːˈtreɪəl', 'n.', '描绘;描述;描写;', 'Mr Ying is well-known for his portrayal of a prison guard in the film \'The Last Emperor\'.', '英先生因其在电影《末代皇帝》中塑造的监狱看守形象而闻名。');
INSERT INTO `words` VALUES (5966, 'portuguese', 'ˌpɔːtʃuˈɡiːz', 'adj.', '葡萄牙的;', '...a former Portuguese colony.', '以前的葡萄牙殖民地');
INSERT INTO `words` VALUES (5967, 'pose', 'pəʊz', 'v.', '造成(威胁、问题等);引起;产生;', 'This could pose a threat to jobs in the coal industry...', '这可能会给煤炭产业的就业造成威胁。');
INSERT INTO `words` VALUES (5968, 'poser', 'ˈpəʊzə(r)', 'n.', '难题;困难;棘手的事;', 'He\'s such a poser.', '他太做作。');
INSERT INTO `words` VALUES (5969, 'poseur', 'pəʊˈzɜː(r)', 'n.', '装腔作势的人;装模作样的人;', 'I am sometimes accused of being an inveterate poseur.', '有时有人说我惯于装模作样。');
INSERT INTO `words` VALUES (5970, 'posh', 'pɒʃ', 'adj.', '优雅豪华的;富丽堂皇的;上流社会的;', 'Celebrating a promotion, I took her to a posh hotel for a cocktail.', '为庆祝晋升，我带她去豪华饭店喝了鸡尾酒。');
INSERT INTO `words` VALUES (5971, 'posit', 'ˈpɒzɪt', 'v.', '假设;认定;认为…为实;', 'Several writers have posited the idea of a universal consciousness...', '有几个作者都假设存在普遍意识。');
INSERT INTO `words` VALUES (5972, 'position', 'pəˈzɪʃn', 'n.', '位置;地方;恰当位置;', 'The ship was identified, and its name and position were reported to the coastguard...', '那艘船已找到，其名字和位置已报告给海岸警卫队。');
INSERT INTO `words` VALUES (5973, 'positive', 'ˈpɒzətɪv', 'adj.', '积极乐观的;自信的;积极的;', 'Be positive about your future and get on with living a normal life...', '要对自己的未来充满信心，继续过一种正常的生活。');
INSERT INTO `words` VALUES (5974, 'positively', 'ˈpɒzətɪvli', 'adv.', '绝对地;肯定地;乐观地;', 'This is positively the worst thing that I can even imagine...', '这确实是我能想象到的最糟糕的事情。');
INSERT INTO `words` VALUES (5975, 'posse', 'ˈpɒsi', 'n.', '一群，一队，一伙(有共同之处的人);(美国旧时由县治安官调集、协助捉拿罪犯的)地方武装团队;(与毒品有关的)青年犯罪团伙;', '...a posse of reporters...', '一群记者');
INSERT INTO `words` VALUES (5976, 'possess', 'pəˈzes', 'v.', '有;拥有;具有(特质);', 'He was then arrested and charged with possessing an offensive weapon...', '他随后被逮捕，并被控拥有攻击性武器。');
INSERT INTO `words` VALUES (5977, 'possessed', 'pəˈzest', 'adj.', '着了魔;', 'She even claimed the couple\'s daughter was possessed by the devil...', '她甚至宣称那对夫妇的女儿被魔鬼附了体。');
INSERT INTO `words` VALUES (5978, 'possession', 'pəˈzeʃn', 'n.', '具有;拥有;个人财产;', 'Those documents are now in the possession of the Guardian...', '那些文件目前在《卫报》手里。');
INSERT INTO `words` VALUES (5979, 'possessions', 'pəˈzɛʃənz', 'n.', '具有;拥有;个人财产;', 'Those documents are now in the possession of the Guardian...', '那些文件目前在《卫报》手里。');
INSERT INTO `words` VALUES (5980, 'possessive', 'pəˈzesɪv', 'adj.', '要求悉心关爱的;占有欲强的;不愿分享的;', 'Danny could be very jealous and possessive about me...', '丹尼很爱吃醋，对我的占有欲很强。');
INSERT INTO `words` VALUES (5981, 'possibility', 'ˌpɒsəˈbɪləti', 'n.', '可能;可能性;可选择的方法;', 'We were not in the least worried about the possibility that sweets could rot the teeth...', '我们以前完全不担心吃糖果有可能会蛀牙。');
INSERT INTO `words` VALUES (5982, 'possible', 'ˈpɒsəbl', 'adj.', '可能;能做到(或取得);可能存在(或发生)的;', 'If it is possible to find out where your brother is, we shall...', '如果能找出你兄弟在哪儿，我们就会去找。');
INSERT INTO `words` VALUES (5983, 'possibly', 'ˈpɒsəbli', 'adv.', '可能;或许;强调惊奇、恼怒等;', 'Exercise will not only lower blood pressure but possibly protect against heart attacks...', '锻炼不仅会降低血压，而且可能对心脏病有预防效果。');
INSERT INTO `words` VALUES (5984, 'postal', 'ˈpəʊstl', 'adj.', '邮政的;邮递的;邮寄物品的;', 'Compensation for lost or damaged mail will be handled by the postal service...', '对丢失或毁坏邮件的赔偿将由邮政部门处理。');
INSERT INTO `words` VALUES (5985, 'posterity', 'pɒˈsterəti', 'n.', '后代;后裔;子孙;', 'A photographer recorded the scene on video for posterity...', '摄影师用摄像机为后代记录下了这个场景。');
INSERT INTO `words` VALUES (5986, 'posthumous', 'ˈpɒstjʊməs', 'adj.', '死后发生(或做、出版等)的;', 'His family and supporters have campaigned  for many years for a posthumous pardon.', '他的家人和支持者为争取其获得身后赦免而开展了多年的运动。');
INSERT INTO `words` VALUES (5987, 'posting', 'ˈpəʊstɪŋ', 'n.', '派驻;(发送到互联网讨论组的)帖子，消息，信息;', 'He was rewarded with a posting to New York...', '作为奖励，他被派往纽约工作。');
INSERT INTO `words` VALUES (5988, 'postmark', 'ˈpəʊstmɑːk', 'n.', '邮戳;', 'All the letters bore an Aberdeen postmark.', '所有的信件上都盖有阿伯丁郡的邮戳。');
INSERT INTO `words` VALUES (5989, 'postpone', 'pəˈspəʊn', 'v.', '延迟;延期;展缓;', 'He decided to postpone the expedition until the following day...', '他决定把探险推迟到第二天。');
INSERT INTO `words` VALUES (5990, 'postprandial', 'ˌpəʊstˈprændiəl', 'adj.', '饭后的;餐后的;', '...a post-prandial nap.', '饭后小睡');
INSERT INTO `words` VALUES (5991, 'postscript', 'ˈpəʊstskrɪpt', 'n.', '(加于信末的)附言，又及;补充;补编;', 'A brief, handwritten postscript lay beneath his signature.', '在他的签名下，有一句简短的手写附言。');
INSERT INTO `words` VALUES (5992, 'posture', 'ˈpɒstʃə(r)', 'n.', '(坐立的)姿势;态度;看法;', 'You can make your stomach look flatter instantly by improving your posture...', '通过采取更好的坐姿，你的腹部马上就会看起来平坦一些。');
INSERT INTO `words` VALUES (5993, 'pot', 'pɒt', 'n.', '锅;(盛食品的)罐，瓶，壶;(某种用途的)容器;', '...metal cooking pots.', '金属炒锅');
INSERT INTO `words` VALUES (5994, 'potency', 'ˈpəʊtnsi', 'n.', '影响力;支配力;效力;', 'They testify to the extraordinary potency of his personality...', '这些事情证明了他非凡的人格魅力。');
INSERT INTO `words` VALUES (5995, 'potent', 'ˈpəʊtnt', 'adj.', '有强效的;有力的;烈性的;', 'Their most potent weapon was the Exocet missile...', '飞鱼导弹是他们最有攻击力的武器。');
INSERT INTO `words` VALUES (5996, 'potential', 'pəˈtenʃl', 'adj.', '潜在的;可能的;', 'The firm has identified 60 potential customers at home and abroad...', '该公司已在国内外找到60个潜在客户。');
INSERT INTO `words` VALUES (5997, 'potentiality', 'pəˌtenʃiˈæləti', 'n.', '潜力;潜在的可能性;', 'The breathtaking potentialities of mechanization set the minds of manufacturers and merchants on fire...', '机械化的惊人潜力使制造厂和商人们激动不已。');
INSERT INTO `words` VALUES (5998, 'potentially', 'pə\'tenʃəli', 'adv.', '潜在地;', 'The firm has identified 60 potential customers at home and abroad...', '该公司已在国内外找到60个潜在客户。');
INSERT INTO `words` VALUES (5999, 'potting', 'ˈpɒtɪŋ', 'v.', '把…栽入盆中;种盆栽;击(球)入袋;', '...metal cooking pots.', '金属炒锅');
INSERT INTO `words` VALUES (6000, 'poultry', 'ˈpəʊltri', 'n.', '家禽;禽的肉;', 'Dr Binger keeps poultry, pigs and goats...', '班热博士饲养家禽、猪和羊。');
INSERT INTO `words` VALUES (6001, 'pounce', 'paʊns', 'v.', '猛扑;突袭;', 'He pounced on the photographer, beat him up and smashed his camera...', '他突然扑向摄影师，痛打了他一顿，还摔碎了他的照相机。');
INSERT INTO `words` VALUES (6002, 'pound', 'paʊnd', 'n.', '英镑(英国货币单位，等于100便士);镑(英国以外某些国家的货币单位);英镑与外币的比值;', 'Beer cost three pounds a bottle...', '啤酒每瓶3英镑。');
INSERT INTO `words` VALUES (6003, 'pounding', 'ˈpaʊndɪŋ', 'n.', '连续的重击声;剧烈的心跳(声);遭重创的情景;', 'Sarajevo took one of its worst poundings in weeks.', '萨拉热窝遭受了几个星期以来最严重的一次破坏。');
INSERT INTO `words` VALUES (6004, 'pour', 'pɔː(r)', 'v.', '使(液体)连续流出;倾倒;倒出;', 'Pour a pool of sauce on two plates and arrange the meat neatly...', '在两个盘子里倒少许沙司酱，然后把肉摆放整齐。');
INSERT INTO `words` VALUES (6005, 'pout', 'paʊt', 'v.', '(恼怒或性感地)撅嘴;', 'He whined and pouted when he did not get what he wanted.', '他要是没得到想要的东西就会发牢骚、撅嘴。');
INSERT INTO `words` VALUES (6006, 'poverty', 'ˈpɒvəti', 'n.', '贫穷;贫困;贫乏;', 'According to World Bank figures, 41 per cent of Brazilians live in absolute poverty...', '根据世界银行的统计数字，41%的巴西人生活在赤贫中。');
INSERT INTO `words` VALUES (6007, 'powder', 'ˈpaʊdə(r)', 'n.', '粉末;细面;扑面粉;', 'Put a small amount of the powder into a container  and mix with water...', '把少量粉末放入容器，加水混合。');
INSERT INTO `words` VALUES (6008, 'powdered', 'ˈpaʊdəd', 'adj.', '制成粉状的;干燥成粉的;研成粉末的;', 'There are only two tins of powdered milk left.', '就剩下两听奶粉了。');
INSERT INTO `words` VALUES (6009, 'power', 'ˈpaʊə(r)', 'n.', '控制力;影响力;操纵力;', 'She interviewed six women who have reached positions of great power and influence...', '她采访了6名身居高位，并且颇具影响力的女士。');
INSERT INTO `words` VALUES (6010, 'powerful', 'ˈpaʊəfl', 'adj.', '有权势的;有影响力的;强有力的;', 'You\'re a powerful man — people will listen to you.', '你是有影响力的人，大家会听你的。');
INSERT INTO `words` VALUES (6011, 'powerfully', '', 'adv.', '强大地，强烈地;有效地;', 'You\'re a powerful man — people will listen to you.', '你是有影响力的人，大家会听你的。');
INSERT INTO `words` VALUES (6012, 'powerless', 'ˈpaʊələs', 'adj.', '无影响力的;无权的;无能为力;', 'If you don\'t have money, you\'re powerless.', '如果你没有钱，就寸步难行。');
INSERT INTO `words` VALUES (6013, 'practicable', 'ˈpræktɪkəbl', 'adj.', '可行的;行得通的;', 'It is not reasonably practicable to offer her the original job back...', '让她继续做原来的工作在情理上行不通。');
INSERT INTO `words` VALUES (6014, 'practical', 'ˈpræktɪkl', 'adj.', '实际的;真实的;客观存在的;', 'We can offer you practical suggestions on how to increase the fibre in your daily diet...', '我们能就如何增加你日常饮食中的纤维素量提供实际的建议。');
INSERT INTO `words` VALUES (6015, 'practicality', 'ˌpræktɪˈkæləti', 'n.', '可行性;适用性;实事求是;', 'Decisions about your children should be based on the practicalities of everyday life.', '有关孩子们的决定，应该从日常生活的实际情况出发。');
INSERT INTO `words` VALUES (6016, 'practically', 'ˈpræktɪkli', 'adv.', '几乎;差不多;很接近;', 'He\'d known the old man practically all his life...', '他几乎打记事起就认识这个老人。');
INSERT INTO `words` VALUES (6017, 'practice', 'ˈpræktɪs', 'n.', '实践;实际行动;通常的做法;', 'Some firms have cut workers\' pay below the level set in their contract, a practice that is illegal in Germany...', '一些公司把工人薪水减至低于合同规定的水平，这种做法在德国是非法的。');
INSERT INTO `words` VALUES (6018, 'pragmatic', 'præɡˈmætɪk', 'adj.', '实用的;讲求实效的;务实的;', 'Robin took a pragmatic look at her situation.', '罗宾实际地考虑了一下她的处境。');
INSERT INTO `words` VALUES (6019, 'pragmatism', 'ˈpræɡmətɪzəm', 'n.', '实用主义;务实思想;实用观点;', 'She had a reputation for clear thinking and pragmatism.', '她曾以思路清晰、观点务实而闻名。');
INSERT INTO `words` VALUES (6020, 'pragmatist', '', 'n.', '实用主义者，务实主义者;', 'She had a reputation for clear thinking and pragmatism.', '她曾以思路清晰、观点务实而闻名。');
INSERT INTO `words` VALUES (6021, 'praise', 'preɪz', 'n.', '赞扬;称赞;赞美;', 'The American president praised Turkey for its courage...', '美国总统赞扬了土耳其的勇气。');
INSERT INTO `words` VALUES (6022, 'praiseworthy', 'ˈpreɪzwɜːði', 'adj.', '值得称赞的;值得表扬的;', '...the government\'s praiseworthy efforts to improve efficiency in health and education.', '政府在提高卫生和教育事业的效率上所付出的值得赞扬的努力');
INSERT INTO `words` VALUES (6023, 'prance', 'prɑːns', 'v.', '阔步行走;神气地快速走动;腾跃;', 'He was horrified at the thought of his son prancing about on a stage in tights.', '一想到儿子身穿紧身衣在舞台上神气活现地走来走去，他就感到震惊。');
INSERT INTO `words` VALUES (6024, 'prattle', 'ˈprætl', 'v.', '闲扯;唠叨;', 'Lou prattled on about various trivialities till I wanted to scream...', '卢就各种芝麻小事唠叨个没完，后来我烦得都想尖叫了。');
INSERT INTO `words` VALUES (6025, 'prawn', 'prɔːn', 'n.', '对虾;大虾;明虾;', '...a prawn sandwich.', '对虾三明治');
INSERT INTO `words` VALUES (6026, 'pray', 'preɪ', 'v.', '祈祷;祷告;企盼;', 'He spent his time in prison praying and studying...', '他把狱中的时间都用于祈祷和学习。');
INSERT INTO `words` VALUES (6027, 'prayer', 'preə(r)', 'n.', '祷告，祈祷(的内容);祈祷文;经文;', 'They had joined a religious order and dedicated their lives to prayer and good works...', '他们已加入一个宗教团体，终生都将祈祷和行善。');
INSERT INTO `words` VALUES (6028, 'preach', 'priːtʃ', 'v.', '布道，讲道(尤指教堂中礼拜时);宣传，宣扬，宣讲(教义、生活方式、体制等);说教;', 'At High Mass the priest preached a sermon on the devil...', '牧师在大弥撒上布道驱邪。');
INSERT INTO `words` VALUES (6029, 'preaching', 'ˈpriːtʃɪŋ', 'v.', '布道，讲道(尤指教堂中礼拜时);宣传，宣扬，宣讲(教义、生活方式、体制等);说教;', 'At High Mass the priest preached a sermon on the devil...', '牧师在大弥撒上布道驱邪。');
INSERT INTO `words` VALUES (6030, 'preamble', 'ˈpriːæmbl', 'n.', '序言;绪论;导言;', 'The controversy has arisen over the text of the preamble to the unification treaty...', '针对统一条约的序文出现了争论。');
INSERT INTO `words` VALUES (6031, 'precarious', 'prɪˈkeəriəs', 'adj.', '不稳的;不确定的;不保险的;', 'Our financial situation had become precarious.', '我们的财务状况已变得不稳定了。');
INSERT INTO `words` VALUES (6032, 'precaution', 'prɪˈkɔːʃn', 'n.', '预防措施;预防;防备;', 'Could he not, just as a precaution, move to a place of safety?...', '就算仅仅是为了以防万一，难道他就不能挪到安全的地方吗？');
INSERT INTO `words` VALUES (6033, 'precautionary', 'prɪˈkɔːʃənəri', 'adj.', '预防的;防备的;警惕的;', 'The local administration says the curfew is a precautionary measure.', '地方当局说宵禁是一种预防措施。');
INSERT INTO `words` VALUES (6034, 'precede', 'prɪˈsiːd', 'v.', '在…之前发生(或出现);先于;走在…前面;', 'Intensive negotiations between the main parties preceded the vote...', '主要政党在选举前进行了集中的谈判。');
INSERT INTO `words` VALUES (6035, 'precedence', 'ˈpresɪdəns', 'n.', '优先;优先权;', 'Have as much fun as possible at college, but don\'t let it take precedence over work...', '在大学要尽情娱乐，但不要把它看得比学习还重要。');
INSERT INTO `words` VALUES (6036, 'precedent', 'ˈpresɪdənt', 'n.', '可援用参考的具体例子;实例;范例;', 'The trial could set an important precedent for dealing with large numbers of similar cases...', '这次审判能为处理大量类似案例开创重要的先例。');
INSERT INTO `words` VALUES (6037, 'precedents', 'ˈprɛsɪdənts', 'n.', '可援用参考的具体例子;实例;范例;', 'The trial could set an important precedent for dealing with large numbers of similar cases...', '这次审判能为处理大量类似案例开创重要的先例。');
INSERT INTO `words` VALUES (6038, 'preceding', 'prɪˈsiːdɪŋ', 'v.', '在…之前发生(或出现);先于;走在…前面;', 'Intensive negotiations between the main parties preceded the vote...', '主要政党在选举前进行了集中的谈判。');
INSERT INTO `words` VALUES (6039, 'precept', 'ˈpriːsept', 'n.', '(思想、行为的)准则，规范;', '...an electoral process based on the precept that all men are born equal.', '以所有人生而平等这一准则为基础的选举程序');
INSERT INTO `words` VALUES (6040, 'precinct', 'ˈpriːsɪŋkt', 'n.', '步行商业区;选区;警区;', 'The Centre was a pedestrian precinct with a bandstand in the middle.', '该中心是步行商业区，正中央有一个室外音乐演奏台。');
INSERT INTO `words` VALUES (6041, 'precious', 'ˈpreʃəs', 'adj.', '珍奇的;珍稀的;宝贵的;', 'After four months in foreign parts, every hour at home was precious...', '在国外呆了4个月后，在家的每一刻都是宝贵的。');
INSERT INTO `words` VALUES (6042, 'precipitous', 'prɪˈsɪpɪtəs', 'adj.', '陡峭的;险峻的;峭拔的;', 'The town is perched on the edge of a steep, precipitous cliff.', '该镇坐落在陡峭险峻的悬崖边上。');
INSERT INTO `words` VALUES (6043, 'precise', 'prɪˈsaɪs', 'adj.', '准确的;确切的;精确的;', 'I can remember the precise moment when my daughter came to see me and her new baby brother in hospital...', '我能清楚地回想起我女儿来医院看我和她刚出生的弟弟的那一刻。');
INSERT INTO `words` VALUES (6044, 'precisely', 'prɪˈsaɪsli', 'adv.', '准确地;恰好地;精确地;', 'Nobody knows precisely how many people are still living in the camp...', '没有人确切知道还有多少人仍住在露营地。');
INSERT INTO `words` VALUES (6045, 'precision', 'prɪˈsɪʒn', 'n.', '精确;准确;细致;', 'The choir sang with precision...', '唱诗班的演唱准确无误。');
INSERT INTO `words` VALUES (6046, 'preclude', 'prɪˈkluːd', 'v.', '使行不通;阻止;妨碍;', 'At 84, John feels his age precludes too much travel...', '84岁的约翰感到自己年事已高，不能进行太多的旅行。');
INSERT INTO `words` VALUES (6047, 'precocious', 'prɪˈkəʊʃəs', 'adj.', '(能力或行为)早熟的;', 'Margaret was always a precocious child...', '玛格丽特一直是个早熟的孩子。');
INSERT INTO `words` VALUES (6048, 'preconceived', 'ˌpriːkənˈsiːvd', 'adj.', '事先形成的;预想的;', 'Five minutes after he had arrived for the interview, I had abandoned my preconceived ideas about boxers...', '他到达采访地点不过5分钟，我就摒弃了自己对拳击手的那些成见。');
INSERT INTO `words` VALUES (6049, 'preconception', 'ˌpriːkənˈsepʃn', 'n.', '事先形成的观念;先入之见;预想;', 'Did you have any preconceptions about the sort of people who did computing?...', '你对从事计算机工作的人有什么成见吗？');
INSERT INTO `words` VALUES (6050, 'precondition', 'ˌpriːkənˈdɪʃn', 'n.', '先决条件;前提;', 'They made multi-party democracy a precondition for giving aid.', '他们把实施多党民主作为给予援助的前提。');
INSERT INTO `words` VALUES (6051, 'precursor', 'priˈkɜːsə(r)', 'n.', '先驱;先锋;前身;', 'Real tennis, an ancient precursor of the modern game, originated in the eleventh century...', '室内网球，现代网球运动的古老前身，起源于11世纪。');
INSERT INTO `words` VALUES (6052, 'predate', 'ˌpriːˈdeɪt', 'v.', '(在日期上)早于，先于;', 'His troubles predated the recession...', '他的问题在经济萧条前就出现了。');
INSERT INTO `words` VALUES (6053, 'predatory', 'ˈpredətri', 'adj.', '捕食性的;(在金钱或性关系上)欺负弱小的，压榨他人的;', '...predatory birds like the eagle.', '像鹰一样的捕食性动物');
INSERT INTO `words` VALUES (6054, 'predecessor', 'ˈpriːdəsesə(r)', 'n.', '前任;原先的东西;被替代的事物;', 'He maintained that he learned everything he knew from his predecessor Kenneth Sisam.', '他坚称他所知道的一切都获自前任肯尼思·赛瑟姆。');
INSERT INTO `words` VALUES (6055, 'predicament', 'prɪˈdɪkəmənt', 'n.', '尴尬的处境;困境;窘境;', 'Hank explained our predicament...', '汉克说明了我们的艰难处境。');
INSERT INTO `words` VALUES (6056, 'predict', 'prɪˈdɪkt', 'v.', '预言;预告;预报;', 'The latest opinion polls are predicting a very close contest...', '最近的民意测验预测竞争将会非常激烈。');
INSERT INTO `words` VALUES (6057, 'predictable', 'prɪˈdɪktəbl', 'adj.', '可预见的;可预料的;意料之中的;', 'This was a predictable reaction, given the bitter hostility between the two countries...', '考虑到这两个国家之间激烈的敌对情绪，这种反应是可以预见的。');
INSERT INTO `words` VALUES (6058, 'predictably', '', 'adv.', '可推断;', 'This was a predictable reaction, given the bitter hostility between the two countries...', '考虑到这两个国家之间激烈的敌对情绪，这种反应是可以预见的。');
INSERT INTO `words` VALUES (6059, 'prediction', 'prɪˈdɪkʃn', 'n.', '预言;预测;预告;', 'He was unwilling to make a prediction about which books would sell in the coming year...', '他不愿意预测来年哪些书会有销路。');
INSERT INTO `words` VALUES (6060, 'predictive', 'prɪˈdɪktɪv', 'adj.', '预测的;预言的;前瞻的;', '...the predictive branch of economics.', '经济学在经济预测方面的分支');
INSERT INTO `words` VALUES (6061, 'predictor', 'prɪˈdɪktə(r)', 'n.', '预测器;预示物;', 'Opinion polls are an unreliable predictor of election outcomes...', '民意测验并不能对选举结果进行可靠预测。');
INSERT INTO `words` VALUES (6062, 'predilection', 'ˌpriːdɪˈlekʃn', 'n.', '喜爱;偏爱;钟爱;', '...his predilection for fast cars and fast horses.', '他对跑车和骏马的偏爱');
INSERT INTO `words` VALUES (6063, 'predisposition', 'ˌpriːdɪspəˈzɪʃn', 'n.', '倾向;癖性;(易患某种病的)体质;', '...a woman\'s predisposition to use the right side of her brain...', '女性使用右脑的倾向');
INSERT INTO `words` VALUES (6064, 'predominance', 'prɪˈdɒmɪnəns', 'n.', '(数量上的)优势;主导地位;支配地位;', 'Another interesting note was the predominance of London club players.', '另一个有趣的现象是，伦敦俱乐部的球员在数量上占优势。');
INSERT INTO `words` VALUES (6065, 'predominant', 'prɪˈdɒmɪnənt', 'adj.', '显著的;明显的;盛行的;', 'Amanda\'s predominant emotion was that of confusion.', '阿曼达主要还是感到困惑。');
INSERT INTO `words` VALUES (6066, 'predominantly', 'prɪˈdɒmɪnəntli', 'adv.', '主要地;多数情况下;', 'The landscape has remained predominantly rural in appearance.', '这里的风景看上去还是以田园风光为主。');
INSERT INTO `words` VALUES (6067, 'predominate', 'prɪˈdɒmɪneɪt', 'v.', '(数量上)占优势;以…为主;占主导地位;', 'In older age groups women predominate because men tend to die younger...', '在老年群体中女性占大多数，因为男性的寿命往往较短。');
INSERT INTO `words` VALUES (6068, 'preen', 'priːn', 'v.', '刻意打扮，精心修饰(并自我欣赏);顾盼自雄;沾沾自喜;', '50% of men under 35 spend at least 20 minutes preening themselves every morning in the bathroom...', '50%的35岁以下男性每天早上至少花20分钟在盥洗室精心打扮。');
INSERT INTO `words` VALUES (6069, 'prefer', 'prɪˈfɜː(r)', 'v.', '较喜欢;喜欢…多于…;', 'Does he prefer a particular sort of music?...', '他有特别喜欢的音乐吗？');
INSERT INTO `words` VALUES (6070, 'preferable', 'ˈprefrəbl', 'adj.', '较适合;更可取;', 'A big earthquake a long way off is preferable to a smaller one nearby...', '远处发生大地震总比在附近发生小地震好。');
INSERT INTO `words` VALUES (6071, 'preferably', '\'pref(ə)rəbli', 'adv.', '最好;宁可;更可取地;', 'A big earthquake a long way off is preferable to a smaller one nearby...', '远处发生大地震总比在附近发生小地震好。');
INSERT INTO `words` VALUES (6072, 'preference', 'ˈprefrəns', 'n.', '偏爱;爱好;喜爱;', 'Parents can express a preference for the school their child attends...', '父母可以表达对自己孩子所上学校的偏好。');
INSERT INTO `words` VALUES (6073, 'preferential', 'ˌprefəˈrenʃl', 'adj.', '优先的;优惠的;优待的;', 'Despite her status, the Duchess will not be given preferential treatment.', '虽然地位显赫，但是公爵夫人并不会得到优待。');
INSERT INTO `words` VALUES (6074, 'preferred', 'prɪˈfɜːd', 'v.', '较喜欢;喜欢…多于…;', 'Does he prefer a particular sort of music?...', '他有特别喜欢的音乐吗？');
INSERT INTO `words` VALUES (6075, 'prefigure', 'ˌpriːˈfɪɡə(r)', 'v.', '预示;预兆;', 'The wall through Berlin was finally ruptured, prefiguring the reunification of Germany.', '柏林墙终于倒塌了，预示着德国的重新统一。');
INSERT INTO `words` VALUES (6076, 'pregnancy', 'ˈpreɡnənsi', 'n.', '怀孕;妊娠;孕期;', 'It would be wiser to cut out all alcohol during pregnancy...', '在怀孕期间最好滴酒不沾。');
INSERT INTO `words` VALUES (6077, 'pregnant', 'ˈpreɡnənt', 'adj.', '怀孕的;妊娠的;饱含;', 'Lena got pregnant and married...', '莉娜怀孕并结婚了。');
INSERT INTO `words` VALUES (6078, 'prehistoric', 'ˌpriːhɪˈstɒrɪk', 'adj.', '史前的;有文字记载以前的;远古的;', '...the famous prehistoric cave paintings of Lascaux.', '拉斯科著名的史前山洞壁画');
INSERT INTO `words` VALUES (6079, 'prejudice', 'ˈpredʒudɪs', 'n.', '偏见;成见;', 'There was a deep-rooted racial prejudice long before the two countries became rivals and went to war...', '早在这两个国家成为敌人并且兵戎相见之前，它们之间就有了根深蒂固的种族偏见。');
INSERT INTO `words` VALUES (6080, 'prejudiced', 'ˈpredʒudɪst , ˈpredʒədɪst', 'adj.', '有偏见的;带成见的;偏爱的;', 'Some landlords and landladies are racially prejudiced...', '一些房东有种族偏见。');
INSERT INTO `words` VALUES (6081, 'prejudicial', 'ˌpredʒuˈdɪʃl', 'adj.', '有害的;不利的;会造成损害的;', 'You could face up to eight years in jail for spreading rumours considered prejudicial to security...', '散布危害社会安全的谣言将被判入狱8年。');
INSERT INTO `words` VALUES (6082, 'preliminary', 'prɪˈlɪmɪnəri', 'adj.', '预备性的;初步的;开始的;', 'Preliminary results show the Republican party with 11 percent of the vote...', '初步结果显示共和党获得了11%的选票。');
INSERT INTO `words` VALUES (6083, 'prelude', 'ˈpreljuːd', 'n.', '序曲;前奏曲;序幕;', 'Most unions see privatisation as an inevitable prelude to job losses...', '大多数工会认为私有化会不可避免地导致失业。');
INSERT INTO `words` VALUES (6084, 'premarital', 'ˌpriːˈmærɪtl', 'adj.', '婚前的;', 'I rejected the teaching that premarital sex was immoral.', '我反对宣扬婚前性行为不道德的说教。');
INSERT INTO `words` VALUES (6085, 'premature', 'ˈpremətʃə(r)', 'adj.', '未成熟的;过早的;提前的;', 'Accidents are still the number one cause of premature death for Americans...', '交通事故仍然是造成美国人过早死亡的头号因素。');
INSERT INTO `words` VALUES (6086, 'prematurely', 'ˌprɛməˈtjʊəli', 'adv.', '早熟地;', 'Accidents are still the number one cause of premature death for Americans...', '交通事故仍然是造成美国人过早死亡的头号因素。');
INSERT INTO `words` VALUES (6087, 'premeditated', 'ˌpriːˈmedɪteɪtɪd', 'adj.', '预谋的;事先策划的;', 'In a case of premeditated murder a life sentence is mandatory...', '蓄意杀人依法要判无期徒刑。');
INSERT INTO `words` VALUES (6088, 'premeditation', 'ˌpriːˌmedɪˈteɪʃn', 'n.', '预谋;预先策划;', 'The judge finally concluded there was insufficient evidence of premeditation.', '法官最终作出判定，没有足够证据证明那是有预谋的。');
INSERT INTO `words` VALUES (6089, 'premier', 'ˈpremiə(r)', 'adj.', '首要的;最著名的;最成功的;', '...Australian premier Paul Keating.', '澳大利亚总理保罗·基廷');
INSERT INTO `words` VALUES (6090, 'premiere', 'ˈpremieə(r)', 'n.', '(电影、戏剧的)首次公演，首映;', 'A new Czechoslovak film has had its premiere at the Karlovy Vary film festival.', '一部捷克斯洛伐克新电影在卡罗维发利电影节上首映。');
INSERT INTO `words` VALUES (6091, 'premium', 'ˈpriːmiəm', 'n.', '保险费;额外费用;附加费;', 'It is too early to say whether insurance premiums will be affected.', '现在说保险费是否会受影响还为时过早。');
INSERT INTO `words` VALUES (6092, 'premonition', 'ˌpreməˈnɪʃn', 'n.', '(尤指不祥的)预感;', 'He had an unshakable premonition that he would die.', '他有一种强烈的预感，觉得自己会死。');
INSERT INTO `words` VALUES (6093, 'prenatal', 'ˌpriːˈneɪtl', 'adj.', '孕期的;产前的;', 'I\'d met her briefly in a prenatal class.', '我曾经在一次产前辅导课上和她有过一面之缘。');
INSERT INTO `words` VALUES (6094, 'preoccupation', 'priˌɒkjuˈpeɪʃn', 'n.', '盘算;思虑;长久思考的事情;', 'Karouzos\'s poetry shows a profound preoccupation with the Orthodox Church...', '卡鲁佐斯的诗歌表明他对东正教的执着。');
INSERT INTO `words` VALUES (6095, 'preoccupied', 'priˈɒkjupaɪd', 'adj.', '心事重重;一门心思;', 'Tom Banbury was preoccupied with the missing Shepherd child and did not want to devote time to the new murder...', '汤姆·班伯里一心寻找谢泼德家失踪的孩子，不想花时间处理这起新的谋杀案。');
INSERT INTO `words` VALUES (6096, 'preoccupy', 'priˈɒkjupaɪ', 'v.', '使日夜思考;使忧心忡忡;', 'Crime and the fear of crime preoccupy the community.', '犯罪和对犯罪的恐惧一直困扰着这个社区。');
INSERT INTO `words` VALUES (6097, 'prep', 'prep', 'n.', '(某些私立学校的)课外作业，备课;', 'After prepping the boat, they sailed it down to Carloforte.', '备好船后，他们就驶向了南边的卡洛福泰。');
INSERT INTO `words` VALUES (6098, 'preparation', 'ˌprepəˈreɪʃn', 'n.', '准备;预备;准备工作;', 'Rub the surface of the wood in preparation for the varnish...', '打磨木头的表面，为刷清漆做准备。');
INSERT INTO `words` VALUES (6099, 'preparatory', 'prɪˈpærətri', 'adj.', '预备的;筹备的;', 'At least a year\'s preparatory work will be necessary before building can start.', '在开始动工之前至少要做一年的准备工作。');
INSERT INTO `words` VALUES (6100, 'prepare', 'prɪˈpeə(r)', 'v.', '使做好准备;把…预备好;使(自己)有准备;', 'Two technicians were preparing a videotape recording of last week\'s programme...', '两个技术人员正在准备上周节目的录像带。');
INSERT INTO `words` VALUES (6101, 'prepared', 'prɪˈpeəd', 'adj.', '准备好;有所准备;愿意;', 'Are you prepared to take industrial action?...', '你们愿意采取劳工行动吗？');
INSERT INTO `words` VALUES (6102, 'preponderance', 'prɪˈpɒndərəns', 'n.', '优势;多数;主体;', '...a preponderance of bright, middle-class children in one group.', '一个小组中聪明伶俐的中产阶级家庭的孩子占大多数');
INSERT INTO `words` VALUES (6103, 'preposition', 'ˌprepəˈzɪʃn', 'n.', '介词;', 'There is nothing in the rules of grammar to suggest that ending a sentence with a preposition is wrong.', '语法规则中并没有规定句子以介词结尾是错误的。');
INSERT INTO `words` VALUES (6104, 'preposterous', 'prɪˈpɒstərəs', 'adj.', '荒唐的;极不合情理的;怪诞的;', 'The whole idea was preposterous.', '整个想法都荒唐透顶。');
INSERT INTO `words` VALUES (6105, 'prepubescent', '', 'adj.', '青春期前的;身体发育成熟前的;', '...prepubescent boys and girls.', '还未进入青春期的少男少女');
INSERT INTO `words` VALUES (6106, 'prerequisite', 'ˌpriːˈrekwəzɪt', 'n.', '先决条件;前提;必备条件;', 'Good self-esteem is a prerequisite for a happy life...', '良好的自尊是幸福生活的前提。');
INSERT INTO `words` VALUES (6107, 'prerogative', 'prɪˈrɒɡətɪv', 'n.', '特权;优先权;', 'Constitutional changes are exclusively the prerogative of the parliament...', '修改宪法是议会独有的权力。');
INSERT INTO `words` VALUES (6108, 'presage', 'ˈpresɪdʒ', 'v.', '预兆，警示，预言(尤指不祥之事);', '...the dawn\'s loud chorus that seemed to presage a bright hot summer\'s day.', '似乎预示着今天会是一个阳光灿烂的炎炎夏日的清晨嘹亮的鸟叫声');
INSERT INTO `words` VALUES (6109, 'preschooler', '', 'n.', '学龄前儿童;', '...preschoolers and toddlers.', '学龄前儿童和初学走路的孩子');
INSERT INTO `words` VALUES (6110, 'prescient', 'ˈpresiənt', 'adj.', '预知的;先觉的;', '...\'Bob Roberts\', an eerily prescient comedy about a populist multimillionaire political candidate.', '《天生赢家》，一部关于一位拥有数千万家财的平民党政治候选人的怪诞预言性喜剧');
INSERT INTO `words` VALUES (6111, 'prescribe', 'prɪˈskraɪb', 'v.', '给…开(药);让…采用(疗法);开(处方);', 'Our doctor diagnosed a throat infection and prescribed antibiotic and junior aspirin...', '我们的医生诊断是喉部感染，并且开了一些抗生素和小剂量阿司匹林。');
INSERT INTO `words` VALUES (6112, 'prescribed', 'prɪˈskraɪbd', 'v.', '给…开(药);让…采用(疗法);开(处方);', 'Our doctor diagnosed a throat infection and prescribed antibiotic and junior aspirin...', '我们的医生诊断是喉部感染，并且开了一些抗生素和小剂量阿司匹林。');
INSERT INTO `words` VALUES (6113, 'prescription', 'prɪˈskrɪpʃn', 'n.', '处方;药方;医生开的药;', 'You will have to take your prescription to a chemist.', '你得拿着处方去找药剂师。');
INSERT INTO `words` VALUES (6114, 'presence', 'ˈprezns', 'n.', '在场;出席;存在;', 'They argued that his  presence in the village could only stir up trouble...', '他们认为他在村里出现只会招惹麻烦。');
INSERT INTO `words` VALUES (6115, 'presentation', 'ˌpreznˈteɪʃn', 'n.', '提交;授予;颁发;', 'We serve  traditional French food cooked in a lighter way, keeping the presentation simple...', '我们供应制作清淡、样式简洁的传统法国菜肴。');
INSERT INTO `words` VALUES (6116, 'present-day', 'ˌpreznt ˈdeɪ', 'adj.', '现在的;当今的，现代的;', 'Even by present-day standards these were large aircraft.', '即使按照现在的标准，这些飞机也挺大的。');
INSERT INTO `words` VALUES (6117, 'presenter', 'prɪˈzentə(r)', 'n.', '(广播、电视)节目主持人;演讲人;发言人;', 'Most people think being a television presenter is exciting...', '很多人认为当电视节目主持人是一件刺激的事情。');
INSERT INTO `words` VALUES (6118, 'presentiment', 'prɪˈzentɪmənt', 'n.', '预感;(尤指)不祥之感;', 'I had a presentiment that he represented a danger to me...', '我预感他会给我带来危险。');
INSERT INTO `words` VALUES (6119, 'presently', 'ˈprezntli', 'adv.', '此刻;现在;眼下;', 'She is presently developing a number of projects...', '她现在正着手开发多个项目。');
INSERT INTO `words` VALUES (6120, 'preservation', 'ˌprezəˈveɪʃn', 'n.', '保护;维护;保存;', 'We will do everything to preserve peace.', '我们会尽全力维持和平。');
INSERT INTO `words` VALUES (6121, 'preservative', 'prɪˈzɜːvətɪv', 'n.', '防腐剂;保护剂;', 'Nitrates are used as preservatives in food manufacture...', '硝酸盐在食品加工业中被用作防腐剂。');
INSERT INTO `words` VALUES (6122, 'preserve', 'prɪˈzɜːv', 'v.', '保护;维护;保留;', 'We will do everything to preserve peace.', '我们会尽全力维持和平。');
INSERT INTO `words` VALUES (6123, 'preside', 'prɪˈzaɪd', 'v.', '主持(会议、仪式等);担任(会议)主席;', 'The PM presided over a meeting of his inner Cabinet...', '首相主持了一次核心内阁会议。');
INSERT INTO `words` VALUES (6124, 'presidency', 'ˈprezɪdənsi', 'n.', '主席的职位(或任期等);总统的职位(或任期等);', 'Britain will support him as a candidate for the presidency of the organisation...', '英国会支持他竞选这个组织的主席职位。');
INSERT INTO `words` VALUES (6125, 'president', 'ˈprezɪdənt', 'n.', '总统;国家主席;(机构、俱乐部、学院等的)会长，院长，主席;', '...President Mubarak...', '穆巴拉克总统');
INSERT INTO `words` VALUES (6126, 'presidential', 'ˌprezɪˈdenʃl', 'adj.', '总统的;与总统有关的;', '...Peru\'s presidential election...', '秘鲁总统选举');
INSERT INTO `words` VALUES (6127, 'press', 'pres', 'n.', '报章杂志;报刊;印刷媒体;', 'He pressed his back against the door...', '他后背紧紧靠在门上。');
INSERT INTO `words` VALUES (6128, 'pressing', 'ˈpresɪŋ', 'adj.', '紧急的;急迫的;难以推却的;', 'It is one of the most pressing problems facing this country...', '这是该国面临的最紧迫的问题之一。');
INSERT INTO `words` VALUES (6129, 'pressure', 'ˈpreʃə(r)', 'n.', '压力;挤压;压强;', 'She kicked at the door with her foot, and the pressure was enough to open it...', '她用脚踢门，力气之大足以把门踹开。');
INSERT INTO `words` VALUES (6130, 'prestige', 'preˈstiːʒ', 'n.', '威信;声望;威望;', '...efforts to build up the prestige of the United Nations...', '为树立联合国的威望所作的努力');
INSERT INTO `words` VALUES (6131, 'prestigious', 'preˈstɪdʒəs', 'adj.', '有威望的;声誉高的;', 'It\'s one of the best equipped and most prestigious schools in the country.', '这是国内设备最先进、最有威望的学校之一。');
INSERT INTO `words` VALUES (6132, 'presumably', 'prɪˈzjuːməbli', 'adv.', '很可能;大概;想必是;', 'Presumably the front door was locked when you came down this morning?...', '早上你下楼的时候，前门大概是锁上的吧？');
INSERT INTO `words` VALUES (6133, 'presume', 'prɪˈzjuːm', 'v.', '假设;假定;推定;', 'I presume you\'re here on business...', '我想你是来这里出差的。');
INSERT INTO `words` VALUES (6134, 'presumption', 'prɪˈzʌmpʃn', 'n.', '可能的事;认为真实的事;非分的行为;', '...the presumption that a defendant is innocent until proved guilty...', '被告在被证实有罪之前都被视为无罪的假定');
INSERT INTO `words` VALUES (6135, 'presumptuous', 'prɪˈzʌmptʃuəs', 'adj.', '自负的;冒昧的;放肆的;', 'It would be presumptuous to judge what the outcome will be.', '现在就判断结果将会怎样未免有些冒昧。');
INSERT INTO `words` VALUES (6136, 'presupposition', 'ˌpriːsʌpəˈzɪʃn', 'n.', '假设的事情;假定;预设;', '...the presupposition that human life must be sustained for as long as possible.', '人的生命一定可以尽可能延长的假设');
INSERT INTO `words` VALUES (6137, 'pretend', 'prɪˈtend', 'v.', '假装;佯装;(在游戏中)装扮，扮作，模拟;', 'I pretend that things are really okay when they\'re not...', '当情况不好的时候我假装一切顺利。');
INSERT INTO `words` VALUES (6138, 'pretended', 'prɪˈtendɪd', 'v.', '假装;佯装;(在游戏中)装扮，扮作，模拟;', 'I pretend that things are really okay when they\'re not...', '当情况不好的时候我假装一切顺利。');
INSERT INTO `words` VALUES (6139, 'pretender', 'prɪˈtendə(r)', 'n.', '(头衔的)觊觎者，冒充者;', '...the Comte de Paris, pretender to the French throne.', '巴黎伯爵，觊觎法国王位之人');
INSERT INTO `words` VALUES (6140, 'pretension', 'prɪˈtenʃn', 'n.', '虚饰;虚夸;自命;', 'Her wide-eyed innocence soon exposes the pretensions of the art world...', '她的天真烂漫很快就暴露了艺术界的矫揉造作。');
INSERT INTO `words` VALUES (6141, 'pretentious', 'prɪˈtenʃəs', 'adj.', '炫耀的;虚夸的;自命不凡的;', 'His response was full of pretentious nonsense...', '他的答复是一派荒谬的虚夸之词。');
INSERT INTO `words` VALUES (6142, 'preternatural', 'ˌpriːtəˈnætʃrəl', 'adj.', '不寻常的;超自然的;难以解释的;', 'Their parents had an almost preternatural ability to understand what was going on in their children\'s minds.', '他们的父母有着一种近乎神奇的能力，能够洞悉他们的孩子脑子里在想些什么。');
INSERT INTO `words` VALUES (6143, 'pretext', 'ˈpriːtekst', 'n.', '借口;托词;', 'They wanted a pretext for subduing the region by force...', '他们想找借口用武力征服这个地区。');
INSERT INTO `words` VALUES (6144, 'pretty', 'ˈprɪti', 'adv.', '颇;相当;十分;', 'She\'s a very charming and very pretty girl.', '她是一个非常迷人的漂亮女孩。');
INSERT INTO `words` VALUES (6145, 'prevail', 'prɪˈveɪl', 'v.', '普遍存在;盛行;流行;', 'We hope that common sense would prevail...', '我们希望情理会占上风。');
INSERT INTO `words` VALUES (6146, 'prevailing', 'prɪˈveɪlɪŋ', 'adj.', '普遍的;盛行的;流行的;', 'The direction of the prevailing winds should be taken into account.', '应该将盛行风的方向考虑在内。');
INSERT INTO `words` VALUES (6147, 'prevalence', '\'prevələns', 'n.', '流行;卓越;', 'This condition is more prevalent in women than in men...', '这种病症在女性当中比在男性当中更为普遍。');
INSERT INTO `words` VALUES (6148, 'prevalent', 'ˈprevələnt', 'adj.', '流行的;普遍存在的;盛行的;', 'This condition is more prevalent in women than in men...', '这种病症在女性当中比在男性当中更为普遍。');
INSERT INTO `words` VALUES (6149, 'prevaricate', 'prɪˈværɪkeɪt', 'v.', '支吾搪塞;闪烁其词;吞吞吐吐;', 'British ministers continued to prevaricate.', '英国的大臣们仍然闪烁其词。');
INSERT INTO `words` VALUES (6150, 'prevent', 'prɪˈvent', 'v.', '阻止;阻碍;阻挠;', 'These methods prevent pregnancy...', '这些方法能防止怀孕。');
INSERT INTO `words` VALUES (6151, 'prevention', 'prɪˈvenʃn', 'n.', '预防;防止;防范;', 'These methods prevent pregnancy...', '这些方法能防止怀孕。');
INSERT INTO `words` VALUES (6152, 'preventive', 'prɪˈventɪv', 'adj.', '预防性的;防备的;', 'Too much is spent on expensive curative medicine and too little on preventive medicine...', '在昂贵的治疗性药物上花费太多，在预防性药物上的支出则太少。');
INSERT INTO `words` VALUES (6153, 'preview', 'ˈpriːvjuː', 'n.', '预映;预演;预展;', 'He had gone to see the preview of a play.', '他去看一场戏剧的预演了。');
INSERT INTO `words` VALUES (6154, 'previous', 'ˈpriːviəs', 'adj.', '先前的;以往的;(时间上)稍前的;', 'She has a teenage daughter from a previous marriage...', '她前一段婚姻有一个十几岁的女儿。');
INSERT INTO `words` VALUES (6155, 'previously', 'ˈpriːviəsli', 'adv.', '先前;以前;(一段时间)以前;', 'Guyana\'s railways were previously owned by private companies...', '圭亚那的铁路以前是归私有企业所有的。');
INSERT INTO `words` VALUES (6156, 'prey', 'preɪ', 'n.', '猎物;受害者;受骗者;', 'Electric rays stun their prey with huge electrical discharges...', '电鳐能放出强大的电流把猎物击晕。');
INSERT INTO `words` VALUES (6157, 'price', 'praɪs', 'n.', '价格;价钱;物价;', '...a sharp increase in the price of petrol...', '汽油价格猛涨');
INSERT INTO `words` VALUES (6158, 'priceless', 'ˈpraɪsləs', 'adj.', '无价的;极珍贵的;极重要的;', 'They are priceless, unique and irreplaceable.', '它们是独一无二、不可替代的无价之宝。');
INSERT INTO `words` VALUES (6159, 'pricey', 'ˈpraɪsi', 'adj.', '昂贵的;', 'Medical insurance is very pricey.', '医疗保险费用昂贵。');
INSERT INTO `words` VALUES (6160, 'pricing', 'ˈpraɪsɪŋ', 'n.', '定价;作价;计价;', '...a sharp increase in the price of petrol...', '汽油价格猛涨');
INSERT INTO `words` VALUES (6161, 'prick', 'prɪk', 'v.', '扎;刺;戳;', 'Prick the potatoes and rub the skins with salt...', '将马铃薯戳一些洞，并用盐揉搓外皮。');
INSERT INTO `words` VALUES (6162, 'prickle', 'ˈprɪkl', 'v.', '刺痛;扎疼;有刺痛感;', 'He paused, feeling his scalp prickling under his hat...', '他停了下来，觉得帽子下面头皮刺痒。');
INSERT INTO `words` VALUES (6163, 'prickly', 'ˈprɪkli', 'adj.', '多刺的;引起刺痛的;扎疼的;', 'The bunk mattress was hard, the blankets prickly and slightly damp...', '这个铺位的床垫很硬，毯子也很扎人，而且还有点潮。');
INSERT INTO `words` VALUES (6164, 'pride', 'praɪd', 'n.', '自豪;骄傲;得意感;', '...the sense of pride in a job well done...', '做好一项工作的自豪感');
INSERT INTO `words` VALUES (6165, 'priest', 'priːst', 'n.', '(天主教、圣公会、东正教的)司祭，神父，司铎;(非基督教会的)教士，祭司，僧侣;', 'He had trained to be a Catholic priest.', '他受训成为了一名天主教神父。');
INSERT INTO `words` VALUES (6166, 'prim', 'prɪm', 'adj.', '一本正经的;循规蹈矩的;古板的;', 'We tend to imagine that the Victorians were very prim and proper.', '我们倾向于把维多利亚时代的人想象得非常古板、中规中矩。');
INSERT INTO `words` VALUES (6167, 'primal', 'ˈpraɪml', 'adj.', '原始的;最初的;根源的;', 'Jealousy is a primal emotion.', '嫉妒是最原始的情感。');
INSERT INTO `words` VALUES (6168, 'primarily', 'praɪˈmerəli', 'adv.', '主要地;根本地;', '...a book aimed primarily at high-energy physicists...', '一本主要针对高能物理学家的书');
INSERT INTO `words` VALUES (6169, 'primary', 'ˈpraɪməri', 'adj.', '主要的;最重要的;基本的;', 'That\'s the primary reason the company\'s share price has held up so well...', '这是公司股票价格一直居高不下的主要原因。');
INSERT INTO `words` VALUES (6170, 'prime', 'praɪm', 'adj.', '主要的;首要的;基本的;', 'Political stability, meanwhile, will be a prime concern...', '同时，政治稳定性将放在首位。');
INSERT INTO `words` VALUES (6171, 'primitive', 'ˈprɪmətɪv', 'adj.', '原始的;远古的;人类或动物发展早期的;', '...studies of primitive societies.', '对原始社会的研究');
INSERT INTO `words` VALUES (6172, 'primordial', 'praɪˈmɔːdiəl', 'adj.', '原生的;原始的;基本的;', 'Twenty million years ago, Idaho was populated by dense primordial forest.', '2,000万年前，爱达荷州到处都是茂密的原始森林。');
INSERT INTO `words` VALUES (6173, 'prince', 'prɪns', 'n.', '王子;王孙;亲王;', '...Prince Edward and other royal guests...', '爱德华王子和其他皇室宾客');
INSERT INTO `words` VALUES (6174, 'princely', 'ˈprɪnsli', 'adj.', '巨额的;庞大的;雄伟的;', 'It\'ll cost them the princely sum of seventy-five pounds.', '这将花费他们75英镑巨款。');
INSERT INTO `words` VALUES (6175, 'princess', 'ˌprɪnˈses', 'n.', '(除女王或王后外的)王室女成员;(尤指)公主;王妃;', 'Princess Anne topped the guest list.', '安妮公主排在宾客名单之首。');
INSERT INTO `words` VALUES (6176, 'principal', 'ˈprɪnsəpl', 'adj.', '最重要的;主要的;', 'The principal reason for my change of mind is this.', '这是我改变主意的最主要原因。');
INSERT INTO `words` VALUES (6177, 'principality', 'ˌprɪnsɪˈpæləti', 'n.', '王公治理的国家;公国;侯国;', '...the tiny principality of Liechtenstein.', '面积很小的列支敦士登公国');
INSERT INTO `words` VALUES (6178, 'principally', 'ˈprɪnsəpli', 'adv.', '主要地;', 'This is principally because the major export markets are slowing...', '这主要是因为主要出口市场发展缓慢。');
INSERT INTO `words` VALUES (6179, 'principle', 'ˈprɪnsəpl', 'n.', '道德原则;行为准则;规范;', 'Buck never allowed himself to be bullied into doing anything that went against his principles...', '巴克从来不会因为受到威胁而做违背自己原则的事情。');
INSERT INTO `words` VALUES (6180, 'print', 'prɪnt', 'v.', '在纸上印;打印;印刷;', 'He started to print his own posters to distribute abroad...', '他开始印刷自己的海报并发往国外。');
INSERT INTO `words` VALUES (6181, 'printable', 'ˈprɪntəbl', 'adj.', '适宜刊印(或阅读)的;', 'His team-mates opened hotel windows, shouting \'Jump!\' and somewhat less printable banter.', '他的队友打开了酒店的窗户，大叫着“跳！”，还开了一些上不了台面的玩笑。');
INSERT INTO `words` VALUES (6182, 'printing', 'ˈprɪntɪŋ', 'n.', '印刷;印刷术;(书籍的)一次印刷;', 'The American edition of \'Cloud Street\' is already in its third printing.', '美国版的《云街》已经是第三次印刷了。');
INSERT INTO `words` VALUES (6183, 'printout', 'ˈprɪntaʊt', 'n.', '(计算机)打印件，打印资料;', '...a computer printout of various financial projections.', '各种财政计划的计算机打印资料');
INSERT INTO `words` VALUES (6184, 'prior', 'ˈpraɪə(r)', 'adj.', '先前的;较早的;在前的;', 'He claimed he had no prior knowledge of the protest...', '他声称自己事先并不知道这次抗议的事。');
INSERT INTO `words` VALUES (6185, 'priority', 'praɪˈɒrəti', 'n.', '优先事项;最重要的事;首要事情;', 'Being a parent is her first priority...', '做好母亲是她的头等大事。');
INSERT INTO `words` VALUES (6186, 'priory', 'ˈpraɪəri', 'n.', '小修道院;小隐修院;', '...Lindisfarne Priory on Holy Island.', '霍利岛上的林迪斯芳修道院');
INSERT INTO `words` VALUES (6187, 'prison', 'ˈprɪzn', 'n.', '监狱;牢狱;看守所;', 'The prison\'s inmates are being kept in their cells...', '这个监狱的犯人被关在他们的囚室里。');
INSERT INTO `words` VALUES (6188, 'prisoner', 'ˈprɪznə(r)', 'n.', '囚犯;犯人;羁押候审者;', 'The committee is concerned about the large number of prisoners sharing cells.', '这个委员会对牢房拥挤问题表示担忧。');
INSERT INTO `words` VALUES (6189, 'prissy', 'ˈprɪsi', 'adj.', '谨小慎微的;大惊小怪的;拘泥谨慎的;', 'I grew to dislike the people from my background — they were rather uptight and prissy.', '我变得讨厌和我有着同样出身背景的人——他们太保守、太一本正经了。');
INSERT INTO `words` VALUES (6190, 'pristine', 'ˈprɪstiːn', 'adj.', '崭新的;清新的;未开发的;', 'Now the house is in pristine condition.', '现在，这所房子一尘不染。');
INSERT INTO `words` VALUES (6191, 'privacy', 'ˈprɪvəsi', 'n.', '隐私;私密;不受公众干扰的状态;', 'He saw the publication of this book as an embarrassing invasion of his privacy...', '他觉得这本书的出版侵犯了他的私生活，让他十分难堪。');
INSERT INTO `words` VALUES (6192, 'private', 'ˈpraɪvət', 'adj.', '私有的;私用的;自用的;', '...a joint venture with private industry...', '与私营企业建立的合资企业');
INSERT INTO `words` VALUES (6193, 'privately', 'ˈpraɪvətli', 'adv.', '(买卖)通过私人地;', 'The whole process makes buying a car privately as painless as buying from a garage...', '整个过程使得从私人手中购买汽车与从汽车库购买一样方便。');
INSERT INTO `words` VALUES (6194, 'privation', 'praɪˈveɪʃn', 'n.', '贫困;匮乏;艰难;', 'They endured five years of privation during the second world war...', '第二次世界大战期间，他们忍受了5年的贫困生活。');
INSERT INTO `words` VALUES (6195, 'privilege', 'ˈprɪvəlɪdʒ', 'n.', '特殊利益;优惠待遇;(有钱有势者的)特权，特殊待遇;', 'The Russian Federation has issued a decree abolishing special privileges for government officials.', '俄罗斯联邦已经颁布法令废除政府官员的特权。');
INSERT INTO `words` VALUES (6196, 'privileged', 'ˈprɪvəlɪdʒd', 'adj.', '有特权的;受特别优待的;荣幸;', 'They were, by and large, a very wealthy, privileged elite.', '他们基本上是一些非常有钱有势的精英。');
INSERT INTO `words` VALUES (6197, 'privy', 'ˈprɪvi', 'adj.', '准许知情;可参与秘事;', 'Only three people, including a policeman, will be privy to the facts.', '只会允许3个人，其中包括一名警察，了解这些内情。');
INSERT INTO `words` VALUES (6198, 'pro', 'prəʊ', 'n.', '从事某职业的人;职业运动员;职业选手;', 'In the professional theater, there is a tremendous need to prove that you\'re a pro...', '在专业戏剧界，非常有必要证明你是专业演员。');
INSERT INTO `words` VALUES (6199, 'probabilistic', 'ˌprɒbəbəˈlɪstɪk', 'adj.', '基于概率的;或然的;盖然性的;', '...probabilistic exposure to risk.', '可能遭遇风险');
INSERT INTO `words` VALUES (6200, 'probability', 'ˌprɒbəˈbɪləti', 'n.', '可能性;或然性;很可能发生的事;', 'Without a transfusion, the victim\'s probability of dying was 100%...', '如果不输血，患者必死无疑。');
INSERT INTO `words` VALUES (6201, 'probable', 'ˈprɒbəbl', 'adj.', '很可能发生(或存在等)的;', 'It is probable that the medication will suppress the symptom without treating the condition...', '这种药物有可能治标不治本。');
INSERT INTO `words` VALUES (6202, 'probably', 'ˈprɒbəbli', 'adv.', '几乎肯定;很可能;大概;', 'The White House probably won\'t make this plan public until July...', '白宫可能要等到7月份才会公开这个计划。');
INSERT INTO `words` VALUES (6203, 'probation', 'prəˈbeɪʃn', 'n.', '缓刑制;缓刑;试用期;', 'The thief was put on probation for two years.', '这个小偷被处以两年缓刑。');
INSERT INTO `words` VALUES (6204, 'probe', 'prəʊb', 'v.', '盘问;追问;探究;', 'The more they probed into his background, the more inflamed their suspicions would become...', '他们越调查他的背景，疑团就越多。');
INSERT INTO `words` VALUES (6205, 'probing', 'ˈprəʊbɪŋ', 'adj.', '探查性的;追根究底的;逼视的;', 'The more they probed into his background, the more inflamed their suspicions would become...', '他们越调查他的背景，疑团就越多。');
INSERT INTO `words` VALUES (6206, 'probity', 'ˈprəʊbəti', 'n.', '正直;诚实;', 'He asserted his innocence and his financial probity.', '他宣称自己是清白的，在钱财上没有弄虚作假。');
INSERT INTO `words` VALUES (6207, 'problem', 'ˈprɒbləm', 'n.', '棘手的问题;难题;困难;', '...the economic problems of the inner city...', '内城区的经济问题');
INSERT INTO `words` VALUES (6208, 'problematic', 'ˌprɒbləˈmætɪk', 'adj.', '造成困难的;产生问题的;', 'Some places are more problematic than others for women traveling alone.', '对那些独自旅行的女性来说，在有些地方遇到的问题会比其他地方更多。');
INSERT INTO `words` VALUES (6209, 'procedural', 'prəˈsiːdʒərəl', 'adj.', '程序上的;程序性的;', 'A Spanish judge rejected the suit on procedural grounds...', '一个西班牙法官基于程序上的原因驳回了诉讼。');
INSERT INTO `words` VALUES (6210, 'procedure', 'prəˈsiːdʒə(r)', 'n.', '(正常)程序，手续，步骤;(商业、法律或政治上的)程序;手术;', 'A biopsy is usually a minor surgical procedure...', '活组织检查通常是一个很小的外科手术。');
INSERT INTO `words` VALUES (6211, 'proceeding', 'prəˈsiːdɪŋ', 'n.', '诉讼;诉讼程序;事件;', '...criminal proceedings against the former prime minister...', '对前首相的刑事诉讼');
INSERT INTO `words` VALUES (6212, 'proceedings', 'prəˈsiːdɪŋz', 'n.', '诉讼;诉讼程序;事件;', '...criminal proceedings against the former prime minister...', '对前首相的刑事诉讼');
INSERT INTO `words` VALUES (6213, 'process', 'ˈprəʊses , prəˈses', 'n.', '(为达到某一目标的)过程;进程;(事物发展，尤指自然变化的)过程，步骤，流程;', 'There was total agreement to start the peace process as soon as possible...', '全体一致同意尽快开始和平进程。');
INSERT INTO `words` VALUES (6214, 'processing', 'ˈprəʊsesɪŋ , prəˈsesɪŋ', 'v.', '加工;处理;审阅，审核，处理(文件、请求等);', 'There was total agreement to start the peace process as soon as possible...', '全体一致同意尽快开始和平进程。');
INSERT INTO `words` VALUES (6215, 'procession', 'prəˈseʃn', 'n.', '(人或车辆的)队列，行列;列队行进;游行;', '...a funeral procession.', '出殡的队伍');
INSERT INTO `words` VALUES (6216, 'proclaim', 'prəˈkleɪm', 'v.', '宣布;宣告;声明;', 'The Boers rebelled against British rule, proclaiming their independence on 30 December 1880...', '布尔人反抗英国统治，并于1880年12月30日宣布独立。');
INSERT INTO `words` VALUES (6217, 'proclamation', 'ˌprɒkləˈmeɪʃn', 'n.', '宣言;公告;声明;', '...a proclamation of independence.', '独立声明');
INSERT INTO `words` VALUES (6218, 'proclivity', 'prəˈklɪvəti', 'n.', '(常指对坏事的)倾向，癖好;', 'He was indulging his own sexual proclivities.', '他沉浸在自己的过度纵欲中。');
INSERT INTO `words` VALUES (6219, 'procrastination', 'prəʊˌkræstɪ\'neɪʃ(ə)n', 'n.', '拖延;', 'Most often we procrastinate when faced with something we do not want to do.', '面对不想做的事情，我们经常拖延。');
INSERT INTO `words` VALUES (6220, 'procreate', 'ˈprəʊkrieɪt', 'v.', '繁殖;生育;生殖;', 'Most young women  feel a biological need to procreate.', '大多数的年轻妇女都有生儿育女的生理需要。');
INSERT INTO `words` VALUES (6221, 'procure', 'prəˈkjʊə(r)', 'v.', '(设法)获得，取得，得到;诱使(妇女)卖淫;', 'It remained very difficult to procure food, fuel and other daily necessities.', '食物、燃料和其他日用必需品仍然很难得到。');
INSERT INTO `words` VALUES (6222, 'procurement', 'prəˈkjʊəmənt', 'n.', '(尤指为政府或机构)采购，购买;', 'Russia was cutting procurement of new weapons \'by about 80 per cent\', he said.', '他说，俄罗斯新武器的采购量将削减“大约80％”。');
INSERT INTO `words` VALUES (6223, 'prod', 'prɒd', 'v.', '戳;杵;捅;', 'He prodded Murray with the shotgun...', '他用猎枪捅了捅默里。');
INSERT INTO `words` VALUES (6224, 'prodigal', 'ˈprɒdɪɡl', 'adj.', '浪费的;挥霍的;大手大脚的;', '...the parable of the prodigal son.', '关于浪子回头的寓言');
INSERT INTO `words` VALUES (6225, 'prodigious', 'prəˈdɪdʒəs', 'adj.', '巨大的;伟大的;', 'This business generates cash in prodigious amounts...', '这种业务收益丰厚。');
INSERT INTO `words` VALUES (6226, 'prodigy', 'ˈprɒdədʒi', 'n.', '(年轻的)天才，奇才，精英;神童;', '...a Russian tennis prodigy.', '俄罗斯的网球奇才');
INSERT INTO `words` VALUES (6227, 'producer', 'prəˈdjuːsə(r)', 'n.', '生产商;制造商;产地;', 'Vanya Kewley is a freelance film producer.', '万尼亚·丘利是自由电影制片人。');
INSERT INTO `words` VALUES (6228, 'product', 'ˈprɒdʌkt', 'n.', '产品;制品;(自然、化学或工业过程的)产物，生成物;', 'Try to get the best product at the lowest price...', '尽量以最低的价格买到最好的产品。');
INSERT INTO `words` VALUES (6229, 'production', 'prəˈdʌkʃn', 'n.', '生产;制造;制作;', 'That model won\'t go into production before late 1990.', '那种型号在20世纪90年代末以前不会批量生产。');
INSERT INTO `words` VALUES (6230, 'productive', 'prəˈdʌktɪv', 'adj.', '生产的;(尤指)多产的;有效益的;', 'Training makes workers highly productive...', '培训提高了工人们的生产力。');
INSERT INTO `words` VALUES (6231, 'productively', 'prə\'dʌktɪvli', 'adv.', '有成果地;', 'Training makes workers highly productive...', '培训提高了工人们的生产力。');
INSERT INTO `words` VALUES (6232, 'productivity', 'ˌprɒdʌkˈtɪvəti', 'n.', '生产率;生产效率;', 'The third-quarter results reflect continued improvements in productivity...', '第三季度的结果表明生产率持续上升。');
INSERT INTO `words` VALUES (6233, 'profane', 'prəˈfeɪn', 'adj.', '亵渎神灵的;亵圣的;非宗教的;', '...profane language.', '亵渎的话语');
INSERT INTO `words` VALUES (6234, 'profanity', 'prəˈfænəti', 'n.', '亵圣;对神灵的亵渎;(亵圣的)诅咒语;', 'To desecrate a holy spring is considered profanity.', '圣泉俗用被认为是亵渎神灵的行为。');
INSERT INTO `words` VALUES (6235, 'profess', 'prəˈfes', 'v.', '妄称;伪称;声称;', 'She professed to hate her nickname...', '她自称痛恨自己的外号。');
INSERT INTO `words` VALUES (6236, 'profession', 'prəˈfeʃn', 'n.', '(需要专门技能，尤指需要较高教育水平的某一)行业，职业;(某)职业界;业内人士;', 'Harper was a teacher by profession...', '哈珀的职业是教师。');
INSERT INTO `words` VALUES (6237, 'professional', 'prəˈfeʃənl', 'adj.', '职业的;专业的;有职业的;', 'His professional career started at Liverpool University.', '他的职业生涯是从利物浦大学开始的。');
INSERT INTO `words` VALUES (6238, 'professionally', 'prəˈfeʃənəli', 'adv.', '在工作上;在职业上;娴熟地;', 'His professional career started at Liverpool University.', '他的职业生涯是从利物浦大学开始的。');
INSERT INTO `words` VALUES (6239, 'professor', 'prəˈfesə(r)', 'n.', '教授;(大学的)讲师，教员;', '...Professor Cameron...', '卡梅伦教授');
INSERT INTO `words` VALUES (6240, 'proffer', 'ˈprɒfə(r)', 'v.', '端着(给…);递上;提出，提供(建议、解释等);', 'He rose and proffered a silver box full of cigarettes.', '他站起身，伸手递过一个装满香烟的银盒子。');
INSERT INTO `words` VALUES (6241, 'proficiency', 'prəˈfɪʃnsi', 'n.', '熟练;精通;', 'Evidence of basic proficiency in English is part of the admission requirement.', '英语基本熟练是入学条件之一。');
INSERT INTO `words` VALUES (6242, 'proficient', 'prəˈfɪʃnt', 'adj.', '熟练的;娴熟的;精通的;', 'A great number of Egyptians are proficient in foreign languages.', '很多埃及人都精通外语。');
INSERT INTO `words` VALUES (6243, 'profile', 'ˈprəʊfaɪl', 'n.', '面部的侧影;侧面轮廓;概述;', 'His handsome profile was turned away from us.', '他英俊的侧影从我们眼前转开了。');
INSERT INTO `words` VALUES (6244, 'profiling', 'ˈprəʊfaɪlɪŋ', 'n.', '(有关人或事物的)资料搜集;', 'His handsome profile was turned away from us.', '他英俊的侧影从我们眼前转开了。');
INSERT INTO `words` VALUES (6245, 'profit', 'ˈprɒfɪt', 'n.', '利润;收益;赢利;', 'The bank made pre-tax profits of £3.5 million...', '这家银行的税前利润为350万英镑。');
INSERT INTO `words` VALUES (6246, 'profitability', 'ˌprɒfɪtəˈbɪlɪti', 'n.', '收益性；利益率;', 'Drug manufacturing is the most profitable business in America...', '制药业是美国利润最丰厚的业务。');
INSERT INTO `words` VALUES (6247, 'profitable', 'ˈprɒfɪtəbl', 'adj.', '有利润的;赢利的;有益的;', 'Drug manufacturing is the most profitable business in America...', '制药业是美国利润最丰厚的业务。');
INSERT INTO `words` VALUES (6248, 'profiteer', 'ˌprɒfɪˈtɪə(r)', 'n.', '奸商;投机商;牟取暴利的人;', '...a new social class composed largely of war profiteers and gangsters.', '主要由战争投机商和强盗组成的新的社会阶层');
INSERT INTO `words` VALUES (6249, 'profligate', 'ˈprɒflɪɡət', 'adj.', '挥霍的;浪费的;', '...the most profligate consumer of energy in the world.', '世界上能源挥霍最严重的国家');
INSERT INTO `words` VALUES (6250, 'profound', 'prəˈfaʊnd', 'adj.', '巨大的;深切的;深远的;', '...discoveries which had a profound effect on many areas of medicine.', '对很多医学领域产生了深刻影响的发现');
INSERT INTO `words` VALUES (6251, 'profoundly', 'prəˈfaʊndli', 'adv.', '极大地;深刻地;严重地;', '...discoveries which had a profound effect on many areas of medicine.', '对很多医学领域产生了深刻影响的发现');
INSERT INTO `words` VALUES (6252, 'profundity', 'prəˈfʌndəti', 'n.', '(理解或处理问题的)深刻性，彻底性;巨大;严重;', 'The profundity of this book is achieved with breathtaking lightness.', '这本书极为深入浅出。');
INSERT INTO `words` VALUES (6253, 'profuse', 'prəˈfjuːs', 'adj.', '大量的;众多的;丰富的;', '...a remedy that produces profuse sweating.', '使人大量出汗的疗法');
INSERT INTO `words` VALUES (6254, 'profusion', 'prəˈfjuːʒn', 'n.', '大量;众多;', 'The Dart is a delightful river with a profusion of wild flowers along its banks...', '达特河是一条令人心旷神怡的河，岸边到处开着野花。');
INSERT INTO `words` VALUES (6255, 'progenitor', 'prəʊˈdʒenɪtə(r)', 'n.', '(人或动、植物等的)祖先，祖代;创始人;先驱;', 'He was also a progenitor of seven presidents of Nicaragua.', '他也是尼加拉瓜7任总统的祖先。');
INSERT INTO `words` VALUES (6256, 'progeny', 'ˈprɒdʒəni', 'n.', '子孙;幼崽;幼兽;', 'Davis was never loquacious on the subject of his progeny.', '戴维斯说到他的子女时总是寡言少语。');
INSERT INTO `words` VALUES (6257, 'prognosis', 'prɒɡˈnəʊsɪs', 'n.', '(对病情的)预断，预后;预测;预言;', 'If the cancer is caught early the prognosis is excellent.', '如果癌症在早期发现，预后效果还是非常好的。');
INSERT INTO `words` VALUES (6258, 'program', 'ˈprəʊɡræm', 'n.', '程序;编码指令;', 'The chances of an error occurring in a computer program increase with the size of the program.', '计算机程序越长，出错的概率也越大。');
INSERT INTO `words` VALUES (6259, 'programmable', 'ˈprəʊɡræməbl', 'adj.', '程控的;可编程序的;', 'Most CD-players are programmable.', '大多数的激光唱机都是可程控的。');
INSERT INTO `words` VALUES (6260, 'progression', 'prəˈɡreʃn', 'n.', '(进入另一阶段的)发展;前进;进程;', 'Both drugs slow the progression of HIV, but neither cures the disease...', '这两种药物都可以延缓艾滋病病情发展，但是都不能治愈这种疾病。');
INSERT INTO `words` VALUES (6261, 'progressive', 'prəˈɡresɪv', 'adj.', '进步的;先进的;开明的;', '...a progressive businessman who had voted for Roosevelt in 1932 and 1936...', '在1932年和1936年投票支持罗斯福的开明商人');
INSERT INTO `words` VALUES (6262, 'progressively', 'prəˈɡresɪvli', 'adv.', '持续稳定地;逐步地;愈益;', '...a progressive businessman who had voted for Roosevelt in 1932 and 1936...', '在1932年和1936年投票支持罗斯福的开明商人');
INSERT INTO `words` VALUES (6263, 'prohibit', 'prəˈhɪbɪt', 'v.', '(尤指以法令)禁止;阻止;使不可能;', '...a law that prohibits tobacco advertising in newspapers and magazines...', '禁止在报刊杂志上刊登香烟广告的法律');
INSERT INTO `words` VALUES (6264, 'prohibition', 'ˌprəʊɪˈbɪʃn', 'n.', '(尤指通过法律的)禁止，阻止;禁令;禁律;', '...a prohibition on discrimination.', '禁止歧视的法律');
INSERT INTO `words` VALUES (6265, 'prohibitive', 'prəˈhɪbətɪv', 'adj.', '高昂得令人难以承受的;贵得买不起的;(以法令)禁止的;', 'The cost of private treatment can be prohibitive.', '自费治疗的费用高得让人望而却步。');
INSERT INTO `words` VALUES (6266, 'projection', 'prəˈdʒekʃn', 'n.', '预测;推断;设想;', '...the company\'s projection of 11 million visitors for the first year.', '公司对第一年将有1,100万游客的预测');
INSERT INTO `words` VALUES (6267, 'projector', 'prəˈdʒektə(r)', 'n.', '放映机;投影仪;', '...a 35-millimetre slide projector.', '35毫米的幻灯机');
INSERT INTO `words` VALUES (6268, 'proletarian', 'ˌprəʊləˈteəriən', 'adj.', '无产者的;无产阶级的;工人阶级的;', '...a proletarian revolution.', '无产阶级革命');
INSERT INTO `words` VALUES (6269, 'proliferate', 'prəˈlɪfəreɪt', 'v.', '迅速繁殖(或增殖);猛增;', 'Computerized data bases are proliferating fast...', '计算机化的数据库正在激增。');
INSERT INTO `words` VALUES (6270, 'proliferation', 'prəˌlɪfəˈreɪʃn', 'n.', '激增;涌现;增殖;', 'Computerized data bases are proliferating fast...', '计算机化的数据库正在激增。');
INSERT INTO `words` VALUES (6271, 'prolific', 'prəˈlɪfɪk', 'adj.', '多产的;创作丰富的;丰硕的;', 'She is a prolific writer of novels and short stories...', '她是一位多产的作家，写了很多小说和短篇故事。');
INSERT INTO `words` VALUES (6272, 'prologue', 'ˈprəʊlɒɡ', 'n.', '序言;序幕;开场白;', 'The prologue to the novel is written in the form of a newspaper account.', '这本小说的序言是以报纸报道的形式写的。');
INSERT INTO `words` VALUES (6273, 'prolong', 'prəˈlɒŋ', 'v.', '延长;', 'Mr Chesler said foreign military aid was prolonging the war...', '切斯勒先生说国外的军事援助拉长了战事。');
INSERT INTO `words` VALUES (6274, 'prolonged', 'prəˈlɒŋd', 'adj.', '持久的;长期的;', '...a prolonged period of low interest rates.', '长期的低利率');
INSERT INTO `words` VALUES (6275, 'prominence', 'ˈprɒmɪnəns', 'n.', '重要;突出;卓越;', 'He came to prominence during the World Cup in Italy...', '他在意大利的世界杯赛中声名鹊起。');
INSERT INTO `words` VALUES (6276, 'prominent', 'ˈprɒmɪnənt', 'adj.', '重要的;著名的;杰出的;', '...a prominent member of the Law Society.', '法律协会的重要成员');
INSERT INTO `words` VALUES (6277, 'prominently', '\'prɒmɪnəntli', 'adv.', '显著地;', '...a prominent member of the Law Society.', '法律协会的重要成员');
INSERT INTO `words` VALUES (6278, 'promiscuous', 'prəˈmɪskjuəs', 'adj.', '淫乱的;滥交的;大杂烩的;', 'She is perceived as vain, spoilt and promiscuous...', '她被认为是个被人惯坏了的、既虚荣又淫乱的女人。');
INSERT INTO `words` VALUES (6279, 'promise', 'ˈprɒmɪs', 'v.', '许诺;承诺;答应;', 'The post office has promised to resume first class mail delivery to the area on Friday...', '邮局承诺于星期五恢复对这个地区第一类邮件的递送。');
INSERT INTO `words` VALUES (6280, 'promises', 'ˈprɒmɪsɪz', 'v.', '许诺;承诺;答应;', 'The post office has promised to resume first class mail delivery to the area on Friday...', '邮局承诺于星期五恢复对这个地区第一类邮件的递送。');
INSERT INTO `words` VALUES (6281, 'promising', 'ˈprɒmɪsɪŋ', 'adj.', '有希望的;有前途的;有出息的;', 'A school has honoured one of its brightest and most promising former pupils.', '一所学校给曾在这里就读的最聪明、最有前途的一位学生授予了荣誉头衔。');
INSERT INTO `words` VALUES (6282, 'promontory', 'ˈprɒməntri', 'n.', '岬，岬角(深入海中的狭长高地);', '...a promontory jutting out into the bay.', '向海湾伸出的海角');
INSERT INTO `words` VALUES (6283, 'promote', 'prəˈməʊt', 'v.', '促进;推动;促销;', 'You don\'t have to sacrifice environmental protection to promote economic growth...', '不必为了促进经济增长而牺牲环境保护。');
INSERT INTO `words` VALUES (6284, 'promoter', 'prəˈməʊtə(r)', 'n.', '(艺术演出或体育比赛的)筹办人，发起者，赞助者;倡导者;支持者;', '...one of the top boxing promoters in Britain.', '英国最顶尖的拳击比赛主办者之一');
INSERT INTO `words` VALUES (6285, 'promotion', 'prəˈməʊʃn', 'n.', '提升;提拔;晋升;', 'Consider changing jobs or trying for promotion.', '考虑换个工作或者争取升职。');
INSERT INTO `words` VALUES (6286, 'promotional', 'prəˈməʊʃənl', 'adj.', '广告宣传的;推销的;', 'You can use the logo in all your promotional material.', '你们可以将这个标志用在所有的宣传材料中。');
INSERT INTO `words` VALUES (6287, 'prompt', 'prɒmpt', 'adj.', '立即;迅速的;及时的;', 'Japan\'s recession has prompted consumers to cut back on buying cars...', '日本经济的不景气使得消费者在购买车辆上减少了开支。');
INSERT INTO `words` VALUES (6288, 'prompting', 'ˈprɒmptɪŋ', 'n.', '劝说;催促;督促;', 'New York needed little prompting from their coach Bill Parcells.', '纽约队不太需要他们的教练比尔·帕索斯的鼓动。');
INSERT INTO `words` VALUES (6289, 'promptly', 'ˈprɒmptli', 'adv.', '迅速地;立即;及时地;', 'Sister Francesca entered the chapel, took her seat, and promptly fell asleep.', '弗朗西丝卡修女走进小教堂，坐下来，很快就睡着了。');
INSERT INTO `words` VALUES (6290, 'promulgate', 'ˈprɒmlɡeɪt', 'v.', '传播;传扬;宣传;', 'The shipping industry promulgated a voluntary code.', '航运业对自律守则进行了宣传。');
INSERT INTO `words` VALUES (6291, 'prone', 'prəʊn', 'adj.', '易于遭受;有做(坏事)的倾向;有做…倾向的;', 'For all her experience, she was still prone to nerves...', '尽管有经验，她还是容易紧张。');
INSERT INTO `words` VALUES (6292, 'pronounce', 'prəˈnaʊns', 'v.', '发音;读(音);正式宣布(或公布、授予等);', 'Have I pronounced your name correctly?...', '你的名字我念得对吗？');
INSERT INTO `words` VALUES (6293, 'pronounced', 'prəˈnaʊnst', 'adj.', '显著的;很明显的;表达明确的;', 'Most of the art exhibitions have a  pronounced Scottish theme.', '大多数的艺术展品带有明显的苏格兰风格。');
INSERT INTO `words` VALUES (6294, 'pronouncement', 'prəˈnaʊnsmənt', 'n.', '声明;公告;宣告;', '...the President\'s latest pronouncements about the protection of minorities.', '总统最近发布的保护少数民族的声明');
INSERT INTO `words` VALUES (6295, 'pronunciation', 'prəˌnʌnsiˈeɪʃn', 'n.', '发音;读音;(某人的)发音;', 'She gave the word its French pronunciation...', '她读出了该词的法语发音。');
INSERT INTO `words` VALUES (6296, 'proof', 'pruːf', 'n.', '证据;证明;检验;', 'You have to have proof of residence in the state of Texas, such as a Texas ID card...', '你必须有在得克萨斯州的居住证明，比如得克萨斯身份证。');
INSERT INTO `words` VALUES (6297, 'proofread', 'ˈpruːfriːd , ˈpruːfred', 'v.', '校阅;校对;勘校;', 'I didn\'t even have the chance to proofread my own report.', '我甚至没有机会校对自己的报告。');
INSERT INTO `words` VALUES (6298, 'prop', 'prɒp', 'n.', '支柱;支撑物;支持者;', 'He rocked back in the chair and propped his feet on the desk...', '他往椅背上一靠，把脚搭在桌子上。');
INSERT INTO `words` VALUES (6299, 'propaganda', 'ˌprɒpəˈɡændə', 'n.', '宣传;鼓吹;', 'The Front adopted an aggressive propaganda campaign against its rivals.', '该阵线采用激进的宣传手段对付它的对手。');
INSERT INTO `words` VALUES (6300, 'propagandist', 'ˌprɒpəˈɡændɪst', 'n.', '鼓吹者;宣传者;', 'He was in large part a journalist and propagandist...', '他很大程度上是个新闻记者和宣传者。');
INSERT INTO `words` VALUES (6301, 'propagate', 'ˈprɒpəɡeɪt', 'v.', '传播;宣传;繁殖;', 'They propagated political doctrines which promised to tear apart the fabric of British society.', '他们鼓吹能颠覆英国社会结构的政治学说。');
INSERT INTO `words` VALUES (6302, 'propagation', 'ˌprɒpə\'ɡeɪʃ(ə)n', 'n.', '传播;扩展;宣传;', 'They propagated political doctrines which promised to tear apart the fabric of British society.', '他们鼓吹能颠覆英国社会结构的政治学说。');
INSERT INTO `words` VALUES (6303, 'propane', 'ˈprəʊpeɪn', 'n.', '丙烷;', '...a propane gas cylinder.', '丙烷汽缸');
INSERT INTO `words` VALUES (6304, 'propel', 'prəˈpel', 'v.', '推动;驱动;推进;', 'The tiny rocket is attached to the spacecraft and is designed to propel it toward Mars.', '小火箭捆绑在宇宙飞船上，用于推动飞船飞向火星。');
INSERT INTO `words` VALUES (6305, 'propeller', 'prəˈpelə(r)', 'n.', '螺旋桨(飞机或轮船的推进器);', '...a fixed three-bladed propeller.', '固定住的三叶型螺旋桨');
INSERT INTO `words` VALUES (6306, 'propensity', 'prəˈpensəti', 'n.', '(行为方面的)倾向;习性;', 'Mr Bint has a propensity to put off decisions to the last minute...', '宾特先生习惯拖到最后时刻才作决定。');
INSERT INTO `words` VALUES (6307, 'proper', 'ˈprɒpə(r)', 'adj.', '正确的;恰当的;符合规则的;', 'Two out of five people lack a proper job...', '每五人中有两人没有正当的工作。');
INSERT INTO `words` VALUES (6308, 'properly', 'ˈprɒpəli', 'adv.', '正确地;适当地;得体地;', 'You\'re too thin. You\'re not eating properly...', '你太瘦了。你的饮食不合理。');
INSERT INTO `words` VALUES (6309, 'property', 'ˈprɒpəti', 'n.', '所有物;财产;财物;', 'Richard could easily destroy her personal property to punish her for walking out on him...', '理查德能轻易毁掉她的私人财产来惩罚她对他的背叛。');
INSERT INTO `words` VALUES (6310, 'prophecy', 'ˈprɒfəsi', 'n.', '预言;预言能力;', 'The youth, too, fulfilled the prophecy.', '那个年轻人，同样地，让预言说中了。');
INSERT INTO `words` VALUES (6311, 'prophesy', 'ˈprɒfəsaɪ', 'v.', '预告;预言;', 'He prophesied that within five years his opponent would either be dead or in prison...', '他预言5年内他的敌人要么会死去，要么会进监狱。');
INSERT INTO `words` VALUES (6312, 'prophet', 'ˈprɒfɪt', 'n.', '(基督教、犹太教和伊斯兰教的)先知;穆罕默德(伊斯兰教的创始人);预言家;', '...the sacred name of the Holy Prophet of Islam.', '伊斯兰先知的圣名');
INSERT INTO `words` VALUES (6313, 'prophetic', 'prəˈfetɪk', 'adj.', '正确预言的;有预见的;预言家的;', 'This ominous warning soon proved prophetic...', '这个不祥的警告很快证明是有预见性的。');
INSERT INTO `words` VALUES (6314, 'prophylactic', 'ˌprɒfəˈlæktɪk', 'adj.', '预防疾病的;', 'Vaccination and other prophylactic measures can be carried out.', '可以采取疫苗接种和其他预防措施。');
INSERT INTO `words` VALUES (6315, 'propitiate', 'prəˈpɪʃieɪt', 'v.', '使息怒;', 'I\'ve never gone out of my way to propitiate people...', '我从未刻意取悦别人。');
INSERT INTO `words` VALUES (6316, 'propitious', 'prəˈpɪʃəs', 'adj.', '吉利的;吉庆的;吉祥的;', 'They should wait for the most propitious moment between now and the next election...', '他们应等待从现在到下次选举之间的最有利时机。');
INSERT INTO `words` VALUES (6317, 'proponent', 'prəˈpəʊnənt', 'n.', '倡导者;支持者;拥护者;', 'Halsey was identified as a leading proponent of the values of progressive education.', '哈尔西被认为是进步教育价值观的主要拥护者。');
INSERT INTO `words` VALUES (6318, 'proportion', 'prəˈpɔːʃn', 'n.', '部分;份额;比例;', 'A large proportion of the dolphins in that area will eventually die...', '那片区域里的很大一部分海豚终将死去。');
INSERT INTO `words` VALUES (6319, 'proportional', 'prəˈpɔːʃənl', 'adj.', '相称的;成比例的;均衡的;', 'Loss of weight is directly proportional to the rate at which the disease is progressing.', '体重的下降与病情发展的速度成正比。');
INSERT INTO `words` VALUES (6320, 'proportionally', '', '', '按比例地，相配合地，适当地;', 'Loss of weight is directly proportional to the rate at which the disease is progressing.', '体重的下降与病情发展的速度成正比。');
INSERT INTO `words` VALUES (6321, 'proposal', 'prəˈpəʊzl', 'n.', '提议;建议;动议;', 'The President is to put forward new proposals for resolving the country\'s constitutional crisis...', '总统将提出解决国家宪法危机的新议案。');
INSERT INTO `words` VALUES (6322, 'propose', 'prəˈpəʊz', 'v.', '提议;建议;打算;', 'Britain is about to propose changes to some institutions...', '英国将提议对一些机构进行改革。');
INSERT INTO `words` VALUES (6323, 'proposed', 'prəˈpəʊzd', 'v.', '提议;建议;打算;', 'Britain is about to propose changes to some institutions...', '英国将提议对一些机构进行改革。');
INSERT INTO `words` VALUES (6324, 'proposition', 'ˌprɒpəˈzɪʃn', 'n.', '提议，建议(尤指业务上的);欲做的事;待处理的问题;', 'Making easy money has always been an attractive proposition...', '轻松赚钱总是件诱人的事。');
INSERT INTO `words` VALUES (6325, 'propound', 'prəˈpaʊnd', 'v.', '提出(主意或解释)供考虑;', 'Zoologist Eugene Morton has propounded a general theory of the vocal sounds that animals make.', '动物学家尤金·莫顿提出了一个有关动物发声的概括性理论。');
INSERT INTO `words` VALUES (6326, 'proprietary', 'prəˈpraɪətri', 'adj.', '专卖的;专营的;专利的;', '...some proprietary brands of dog food...', '一些狗粮的专利品牌');
INSERT INTO `words` VALUES (6327, 'proprietor', 'prəˈpraɪətə(r)', 'n.', '业主;所有人;', '...the proprietor of a local restaurant...', '一家当地餐馆的业主');
INSERT INTO `words` VALUES (6328, 'propriety', 'prəˈpraɪəti', 'n.', '得体的举止;有分寸的行为;行为规范;', 'Their sense of social propriety is eroded.', '他们的社交礼仪意识淡薄。');
INSERT INTO `words` VALUES (6329, 'propulsion', 'prəˈpʌlʃn', 'n.', '推动力;推进;', 'Interest in jet propulsion was now growing at the Air Ministry.', '航空部对喷气推进的兴趣正在增加。');
INSERT INTO `words` VALUES (6330, 'prosaic', 'prəˈzeɪɪk', 'adj.', '平庸的;没有诗意(或美感)的;平淡的;', 'His instructor offered a more prosaic explanation for the surge in interest...', '对于利息的急剧增加，他的导师给出了一个更为平淡无奇的解释。');
INSERT INTO `words` VALUES (6331, 'proscribe', 'prəˈskraɪb', 'v.', '宣布禁止;', 'In some cultures surgery is proscribed...', '在一些文化里，外科手术是被禁止的。');
INSERT INTO `words` VALUES (6332, 'proscription', 'prəˈskrɪpʃn', 'n.', '禁止;排斥;', '...the proscription against any religious service.', '禁止任何宗教仪式');
INSERT INTO `words` VALUES (6333, 'prose', 'prəʊz', 'n.', '散文;', 'Shute\'s prose is stark and chillingly unsentimental...', '舒特的散文平铺直叙，没有感情，让人感觉冷冰冰的。');
INSERT INTO `words` VALUES (6334, 'prosecute', 'ˈprɒsɪkjuːt', 'v.', '起诉;控告;检举;', 'The police have decided not to prosecute because the evidence is not strong enough...', '由于证据不足，警方已决定不起诉。');
INSERT INTO `words` VALUES (6335, 'prosecuted', 'ˈprɒsɪkjuːtɪd', 'v.', '起诉;控告;检举;', 'The police have decided not to prosecute because the evidence is not strong enough...', '由于证据不足，警方已决定不起诉。');
INSERT INTO `words` VALUES (6336, 'prosecution', 'ˌprɒsɪˈkjuːʃn', 'n.', '(被)起诉，检举;诉讼;原告，控方(包括原告和原告律师等);', 'Yesterday the head of government called for the prosecution of those responsible for the deaths.', '昨天，政府首脑要求起诉那些该为这些人的死负责的人。');
INSERT INTO `words` VALUES (6337, 'prospective', 'prəˈspektɪv', 'adj.', '有望的;可能的;预期的;', 'The story should act as a warning to other prospective buyers...', '这篇报道应该对其他潜在的购买者起到警示作用。');
INSERT INTO `words` VALUES (6338, 'prosper', 'ˈprɒspə(r)', 'v.', '繁荣;兴旺;成功;', 'The high street banks continue to prosper...', '商业街上的银行仍旧一派繁荣景象。');
INSERT INTO `words` VALUES (6339, 'prosperity', 'prɒˈsperəti', 'n.', '兴旺;繁荣;成功;', '...a new era of peace and prosperity.', '和平繁荣的新时代');
INSERT INTO `words` VALUES (6340, 'prosperous', 'ˈprɒspərəs', 'adj.', '繁荣的;成功的;兴旺的;', '...the youngest son of a relatively prosperous British family...', '一个相对富裕的英国家族中最小的儿子');
INSERT INTO `words` VALUES (6341, 'prostitute', 'ˈprɒstɪtjuːt', 'n.', '卖淫者;娼妓;妓女;', 'He admitted last week he paid for sex with a prostitute.', '他承认上个星期嫖娼了。');
INSERT INTO `words` VALUES (6342, 'prostitution', 'ˌprɒstɪˈtjuːʃn', 'n.', '卖淫;为娼;当男妓;', 'She eventually drifts into prostitution.', '她最终沦为妓女。');
INSERT INTO `words` VALUES (6343, 'protagonist', 'prəˈtæɡənɪst', 'n.', '(戏剧、电影、书的)主要人物，主人公，主角;(比赛、斗争中的)主要人物，主要参与者;(政策、运动的)倡导者，拥护者;', '...the main protagonists of their countries\' integration into the world market.', '积极呼吁他们的国家融入世界市场的主要倡导者');
INSERT INTO `words` VALUES (6344, 'protean', 'ˈprəʊtiən', 'adj.', '多变的;易变的;变幻无常的;', 'He is a protean stylist who can move from blues to ballads and grand symphony.', '他风格多变，从布鲁斯、乡村音乐到雄壮的交响乐都能驾驭。');
INSERT INTO `words` VALUES (6345, 'protect', 'prəˈtekt', 'v.', '保护;防护;(制定法律)保护;', 'So, what can women do to protect themselves from heart disease?...', '那么，女性怎样做才能使自己不得心脏病呢？');
INSERT INTO `words` VALUES (6346, 'protected', 'prəˈtektɪd', 'adj.', '受保护的;', 'In England, thrushes are a protected species so you will not find them on any menu.', '在英格兰，歌鸫是受保护物种，因此你在任何菜单上都找不到。');
INSERT INTO `words` VALUES (6347, 'protection', 'prəˈtekʃn', 'n.', '保护;防卫;保护物;', 'Such a diet is widely believed to offer protection against a number of cancers...', '这种饮食结构被广泛认为可以预防多种癌症。');
INSERT INTO `words` VALUES (6348, 'protectionism', 'prəˈtekʃənɪzəm', 'n.', '(贸易)保护主义;', '...talks to promote free trade and avert increasing protectionism.', '为促进自由贸易和遏制贸易保护主义抬头进行的谈判');
INSERT INTO `words` VALUES (6349, 'protective', 'prəˈtektɪv', 'adj.', '保护的;防护的;出于(对…的)保护;', 'Protective gloves reduce the absorption of chemicals through the skin...', '防护手套可以减少皮肤对化学物质的吸收。');
INSERT INTO `words` VALUES (6350, 'protector', 'prəˈtektə(r)', 'n.', '保护人(或组织、装置等);', 'Many mothers see their son as a potential protector and provider.', '许多母亲将儿子看成是未来的保护人和赡养人。');
INSERT INTO `words` VALUES (6351, 'protein', 'ˈprəʊtiːn', 'n.', '蛋白质;', 'Fish was a major source of protein for the working man.', '鱼肉曾是劳动者的主要蛋白质来源。');
INSERT INTO `words` VALUES (6352, 'protocol', 'ˈprəʊtəkɒl', 'n.', '礼仪;外交礼节;条约草案;', 'He has become something of a stickler for the finer observances of royal protocol.', '他已经变得非常刻板，哪怕是最细微的皇室礼仪也会遵循。');
INSERT INTO `words` VALUES (6353, 'prototype', 'ˈprəʊtətaɪp', 'n.', '原型;雏形;最初形态;', 'Chris Retzler has built a prototype of a machine called the wave rotor.', '克里斯·雷茨勒已制造出了一种叫波转子的机器的样机。');
INSERT INTO `words` VALUES (6354, 'protrude', 'prəˈtruːd', 'v.', '突出;伸出;鼓出;', '...a huge round mass of smooth rock protruding from the water...', '一块光滑的巨型圆石露出水面');
INSERT INTO `words` VALUES (6355, 'protrusion', 'prəˈtruːʒn', 'n.', '突出物;凸起;伸出;', 'He grabbed at a protrusion of rock with his right hand.', '他用右手抓住了一块凸起的岩石。');
INSERT INTO `words` VALUES (6356, 'protuberance', 'prəˈtjuːbərəns', 'n.', '突出物;隆起部分;', '...a protuberance on the upper jawbone.', '下颌骨上部的隆起部分');
INSERT INTO `words` VALUES (6357, 'protuberant', 'prəˈtjuːbərənt', 'adj.', '隆起的;鼓出的;凸起的;', '...a high-beaked nose and large protuberant eyes.', '高鼻梁，又大又凸出的眼睛');
INSERT INTO `words` VALUES (6358, 'proud', 'praʊd', 'adj.', '骄傲的;自豪的;得意的;', 'I felt proud of his efforts...', '我为他的努力感到骄傲。');
INSERT INTO `words` VALUES (6359, 'proudly', 'ˈpraʊdli', 'adv.', '得意地;自豪地;骄傲地;', 'I felt proud of his efforts...', '我为他的努力感到骄傲。');
INSERT INTO `words` VALUES (6360, 'prove', 'pruːv', 'v.', '证明;证实;后来被发现是;', 'We have been accused of exaggerating before, but unfortunately all our reports proved to be true...', '以前一直有人指责我们夸大事实，但遗憾的是，我们所有的报道都证明是真实的。');
INSERT INTO `words` VALUES (6361, 'provenance', 'ˈprɒvənəns', 'n.', '发源地;起源;出处;', 'Kato was fully aware of the provenance of these treasures...', '加藤完全清楚这些珍宝的来源。');
INSERT INTO `words` VALUES (6362, 'proverb', 'ˈprɒvɜːb', 'n.', '谚语;格言;', 'An old Arab proverb says, \'The enemy of my enemy is my friend\'.', '一句古老的阿拉伯谚语说，“敌人的敌人是朋友。”');
INSERT INTO `words` VALUES (6363, 'proverbial', 'prəˈvɜːbiəl', 'adj.', '谚语的;谚语表达的;如谚语所说的;', 'The limousine sped off down the road in the proverbial cloud of dust...', '豪华轿车沿公路飞驰而去，那真是卷起一路风尘。');
INSERT INTO `words` VALUES (6364, 'provide', 'prəˈvaɪd', 'v.', '提供;供应;给予;', 'I\'ll be glad to provide a copy of this...', '我很愿意提供它的一个副本。');
INSERT INTO `words` VALUES (6365, 'provided', 'prəˈvaɪdɪd', 'conj.', '如果;假如;在…条件下;', 'The other banks are going to be very eager to help, provided that they see that he has a specific plan...', '如果看到他有具体的计划，其他银行会很愿意提供帮助。');
INSERT INTO `words` VALUES (6366, 'providence', 'ˈprɒvɪdəns', 'n.', '上帝;苍天;天佑;', 'The first Slav pope, John Paul, said that providence had chosen him to proclaim the spiritual unity of Europe...', '第一位斯拉夫教皇约翰·保罗声称上天选择了他来昭示欧洲的精神统一。');
INSERT INTO `words` VALUES (6367, 'providential', 'ˌprɒvɪˈdenʃl', 'adj.', '天缘巧合的;及时的;适时的;', 'He explained the yellow fever epidemic as a providential act to discourage urban growth...', '他解释说黄热病的流行恰好减缓了城市的扩张。');
INSERT INTO `words` VALUES (6368, 'provider', 'prəˈvaɪdə(r)', 'n.', '供应者;提供者;供养人;', 'I\'ll be glad to provide a copy of this...', '我很愿意提供它的一个副本。');
INSERT INTO `words` VALUES (6369, 'providing', 'prəˈvaɪdɪŋ', 'conj.', '如果;假如;只要;', 'I do believe in people being able to do what they want to do, providing they\'re not hurting someone else...', '我真的认为人们只要不伤害别人，自己想做什么就可以做什么。');
INSERT INTO `words` VALUES (6370, 'province', 'ˈprɒvɪns', 'n.', '省份;(某些国家的)一级行政区;首都以外的地区;', '...the Algarve, Portugal\'s southernmost province.', '阿尔加维， 葡萄牙最南部的省份');
INSERT INTO `words` VALUES (6371, 'provincial', 'prəˈvɪnʃl', 'adj.', '省的;一级行政区的;都城以外的;', 'Jeremy Styles, 34, was the house manager for a provincial theatre for ten years.', '杰里米·斯泰尔斯，34岁，在一家地方剧院担任了10年经理。');
INSERT INTO `words` VALUES (6372, 'provincialism', 'prəˈvɪnʃəlɪzəm', 'n.', '胸襟狭隘;陈腐态度;排外主义;', '...the stifling bourgeois provincialism of Buxton.', '巴克斯顿令人窒息的资产阶级狭隘观念');
INSERT INTO `words` VALUES (6373, 'provision', 'prəˈvɪʒn', 'n.', '提供;供给;给养;', 'The department is responsible for the provision of residential care services.', '该部门负责提供家庭护理服务。');
INSERT INTO `words` VALUES (6374, 'provisional', 'prəˈvɪʒənl', 'adj.', '临时的;暂时的;暂定的;', '...the possibility of setting up a provisional coalition government...', '建立一个临时联合政府的可能性');
INSERT INTO `words` VALUES (6375, 'provisions', 'prəˈvɪʒnz', 'n.', '提供;供给;给养;', 'The department is responsible for the provision of residential care services.', '该部门负责提供家庭护理服务。');
INSERT INTO `words` VALUES (6376, 'proviso', 'prəˈvaɪzəʊ', 'n.', '限制条款;附文;但书;', 'I told Norman I would invest in his venture as long as he agreed to one proviso...', '我告诉诺曼只要他答应一个附带条件，我就会为他的项目投资。');
INSERT INTO `words` VALUES (6377, 'provocation', 'ˌprɒvəˈkeɪʃn', 'n.', '挑衅;刺激;激怒;', 'He denies murder on the grounds of provocation...', '他否认激情杀人。');
INSERT INTO `words` VALUES (6378, 'provocative', 'prəˈvɒkətɪv', 'adj.', '挑衅的;煽动性的;激起争端的;', 'He has made a string of outspoken and sometimes provocative speeches in recent years...', '他近些年发表了一连串直言不讳、有时带煽动性的演说。');
INSERT INTO `words` VALUES (6379, 'provoke', 'prəˈvəʊk', 'v.', '激起;引起;引发;', 'He started beating me when I was about fifteen but I didn\'t do anything to provoke him...', '在我快15岁时他开始打我，可我并没有任何招惹他的举动。');
INSERT INTO `words` VALUES (6380, 'provoking', 'prəˈvəʊkɪŋ', 'v.', '激起;引起;引发;', 'He started beating me when I was about fifteen but I didn\'t do anything to provoke him...', '在我快15岁时他开始打我，可我并没有任何招惹他的举动。');
INSERT INTO `words` VALUES (6381, 'prowess', 'ˈpraʊəs', 'n.', '非凡的技能;高超的技艺;造诣;', 'He\'s always bragging about his prowess as a cricketer...', '他总是吹嘘自己板球水平高超。');
INSERT INTO `words` VALUES (6382, 'prowl', 'praʊl', 'v.', '潜行(为捕猎等);潜行(图谋不轨等);(因无聊、焦躁等)徘徊，走来走去;', 'Policemen prowled around the building.', '警察在大楼四周暗中巡查。');
INSERT INTO `words` VALUES (6383, 'proximity', 'prɒkˈsɪməti', 'n.', '(时间或空间)接近，邻近，靠近;', 'Part of the attraction is Darwin\'s proximity to Asia...', '达尔文市的魅力一部分在于它靠近亚洲。');
INSERT INTO `words` VALUES (6384, 'proxy', 'ˈprɒksi', 'n.', '代理权;代表权;代理人;', 'Those not attending the meeting may vote by proxy.', '那些未出席会议的可以请他人代为投票。');
INSERT INTO `words` VALUES (6385, 'prudence', 'ˈpruːdns', 'n.', '谨慎;慎重;精明;', 'Western businessmen are showing remarkable prudence in investing in the region...', '西方商人在向该地区投资时表现出非同一般的深谋远虑。');
INSERT INTO `words` VALUES (6386, 'prudent', 'ˈpruːdnt', 'adj.', '谨慎的;慎重的;精明的;', 'It is always prudent to start any exercise programme gradually at first...', '刚开始一项锻炼计划时循序渐进总是明智的。');
INSERT INTO `words` VALUES (6387, 'prudish', 'ˈpruːdɪʃ', 'adj.', '(对性问题)大惊小怪的，迂腐守旧的，伪善的;', 'I\'m not prudish but I think these photographs are obscene.', '我并不是假正经的人，但我觉得这些照片非常淫秽。');
INSERT INTO `words` VALUES (6388, 'prurience', 'ˈprʊəriəns', 'n.', '好色;淫秽;', 'Nobody ever lost money by overestimating the public\'s prurience.', '从未有人因为高估公众的好色之心而赔钱。');
INSERT INTO `words` VALUES (6389, 'prurient', 'ˈprʊəriənt', 'adj.', '好色的;下作的;淫亵的;', 'We read the gossip written about them with prurient interest.', '我们翻看他们的八卦时带着不洁的想法。');
INSERT INTO `words` VALUES (6390, 'pry', 'praɪ', 'v.', '探听，打听，探查(隐私);', 'We do not want people prying into our affairs...', '我们不希望别人窥探我们的私事。');
INSERT INTO `words` VALUES (6391, 'ps', 'ˌpiː ˈes', 'n.', '附言，又及(全写为postscript，用于信末);', 'PS. Please show your friends this letter and the enclosed leaflet.', '附:请把这封信和内装的小册子给你的朋友们看看。');
INSERT INTO `words` VALUES (6392, 'psalm', 'sɑːm', 'n.', '(《圣经》中的)圣咏;赞美诗;赞美歌;', 'He recited a verse of the twenty-third psalm.', '他背诵了《诗篇》第23篇中的一节。');
INSERT INTO `words` VALUES (6393, 'pseudo', 'ˈsjuːdəʊ', 'adj.', '假的；伪的；冒充的;', '...pseudo-intellectual images.', '伪知识分子的形象');
INSERT INTO `words` VALUES (6394, 'pseudonym', 'ˈsuːdənɪm', 'n.', '假名;化名;笔名;', 'Both plays were published under the pseudonym of Philip Dayre.', '两个剧本都是以菲利普·戴尔的笔名出版的。');
INSERT INTO `words` VALUES (6395, 'psyche', 'ˈsaɪki', 'n.', '灵魂;心灵;精神;', '\'It probably shows up a deeply immature part of my psyche,\' he confesses...', '“它可能表现了我内心很不成熟的一面，”他承认道。');
INSERT INTO `words` VALUES (6396, 'psychedelic', 'ˌsaɪkəˈdelɪk', 'adj.', '引起幻觉的;使人精神恍惚的;产生迷幻效果的;', 'Grof describes his research with psychedelic drugs and the experiences they triggered.', '格罗夫描述了他对迷幻药物及其带来的体验的研究情况。');
INSERT INTO `words` VALUES (6397, 'psychiatric', 'ˌsaɪkiˈætrɪk', 'adj.', '精神病的;精神病学的;', 'We finally insisted that he seek psychiatric help.', '我们最后坚持要求他寻求精神科医师的帮助。');
INSERT INTO `words` VALUES (6398, 'psychiatrist', 'saɪˈkaɪətrɪst', 'n.', '精神病学家;精神科医生;', 'Alex will probably be seeing a psychiatrist for many months or even years.', '亚历克斯今后好几个月甚至几年都可能要去看精神科医生。');
INSERT INTO `words` VALUES (6399, 'psychic', 'ˈsaɪkɪk', 'adj.', '关于通灵的;超自然的;有特异功能的;', 'Trevor helped police by using his psychic powers.', '特雷弗用他的通灵能力帮助了警方。');
INSERT INTO `words` VALUES (6400, 'psycho', 'ˈsaɪkəʊ', 'n.', '精神病患者;精神病人;', 'Some psycho picked her up, and killed her.', '有个变态搭上了她，然后把她杀了。');
INSERT INTO `words` VALUES (6401, 'psychological', 'ˌsaɪkəˈlɒdʒɪkl', 'adj.', '心灵的;心理的;精神上的;', 'John received constant physical and psychological abuse from his father...', '约翰不断遭到父亲身体和心理上的双重虐待。');
INSERT INTO `words` VALUES (6402, 'psychologically', 'ˌsaɪkəˈlɒdʒɪkəli', 'adv.', '心理上地；心理学地;', 'John received constant physical and psychological abuse from his father...', '约翰不断遭到父亲身体和心理上的双重虐待。');
INSERT INTO `words` VALUES (6403, 'psychologist', 'saɪˈkɒlədʒɪst', 'n.', '心理学家;心理学研究者;', 'Psychologists tested a group of six-year-olds with a video.', '心理学家用一段录像对一组6岁的儿童进行了测试。');
INSERT INTO `words` VALUES (6404, 'psychology', 'saɪˈkɒlədʒi', 'n.', '心理学;心理;心理特征;', '...Professor of Psychology at Bedford College.', '贝德福德学院的心理学教授');
INSERT INTO `words` VALUES (6405, 'psychopathic', 'ˌsaɪkəˈpæθɪk', 'adj.', '精神变态的;变态人格的;', '...a report labelling him psychopathic.', '称他精神变态的报告');
INSERT INTO `words` VALUES (6406, 'psychosis', 'saɪˈkəʊsɪs', 'n.', '精神病;', 'He may have some kind of neurosis or psychosis later in life.', '他晚年可能患有某种神经症或精神病。');
INSERT INTO `words` VALUES (6407, 'psychosomatic', 'ˌsaɪkəʊsəˈmætɪk', 'adj.', '由心理负担导致的;由精神压力引起的;身心的;', 'Doctors refused to treat her, claiming that her problems were all psychosomatic.', '医生拒绝对她进行治疗，声称她的问题完全是心理作用引起的。');
INSERT INTO `words` VALUES (6408, 'psychotherapy', 'ˌsaɪkəʊˈθerəpi', 'n.', '心理治疗;精神治疗;', 'For milder depressions, certain forms of psychotherapy do work well.', '对较轻微的抑郁症，某些形式的心理治疗效果的确不错。');
INSERT INTO `words` VALUES (6409, 'psychotic', 'saɪˈkɒtɪk', 'n.', '精神病患者;', 'The man, who police believe is psychotic, is thought to be responsible for eight attacks.', '这个警方认定为精神错乱的男子被认为是8起袭击事件的制造者。');
INSERT INTO `words` VALUES (6410, 'pub', 'pʌb', 'n.', '酒吧;酒馆;', 'He was in the pub until closing time...', '他在酒吧里一直呆到打烊。');
INSERT INTO `words` VALUES (6411, 'puberty', 'ˈpjuːbəti', 'n.', '青春期;', 'Margaret had reached the age of puberty.', '玛格丽特已到了青春期。');
INSERT INTO `words` VALUES (6412, 'pubescent', 'pjuːˈbesnt', 'adj.', '青春期的;到达发育期的;', '...a horde of pubescent boys and girls who tug at his microphone.', '拽扯他的麦克风的一群少男少女');
INSERT INTO `words` VALUES (6413, 'public', 'ˈpʌblɪk', 'adj.', '平民的;大众的;公众的;', 'Lauderdale House is now open to the public...', '劳德代尔艺术教育中心现已向公众开放。');
INSERT INTO `words` VALUES (6414, 'publication', 'ˌpʌblɪˈkeɪʃn', 'n.', '(书刊等的)出版，发行;出版物;发表;', 'The guide is being translated into several languages for publication near Christmas...', '该手册正被译成数种语言，将在临近圣诞节时出版。');
INSERT INTO `words` VALUES (6415, 'publicity', 'pʌbˈlɪsəti', 'n.', '(媒体的)关注，宣传，报道;宣传业;广告宣传工作;', 'Much advance publicity was given to the talks...', '对于这次会谈有很多前期报道。');
INSERT INTO `words` VALUES (6416, 'publicly', '\'pʌblɪkli', 'adv.', '当众地，公开地；以公众名义;由公众、由政府（出资或持有等）;', 'Lauderdale House is now open to the public...', '劳德代尔艺术教育中心现已向公众开放。');
INSERT INTO `words` VALUES (6417, 'publish', 'ˈpʌblɪʃ', 'v.', '出版;发行;(在报刊)发表，刊登，登载;', 'They publish reference books...', '他们出版工具书。');
INSERT INTO `words` VALUES (6418, 'publisher', 'ˈpʌblɪʃə(r)', 'n.', '出版人(或机构);发行人(或机构);', 'The publishers planned to produce the journal on a weekly basis.', '出版者计划将该期刊定为周刊。');
INSERT INTO `words` VALUES (6419, 'publishing', 'ˈpʌblɪʃɪŋ', 'n.', '出版(业);发行(业);', 'I had a very high-powered job in publishing.', '我曾在出版业担任要职。');
INSERT INTO `words` VALUES (6420, 'pucker', 'ˈpʌkə(r)', 'v.', '皱起;使起褶子;撅起;', 'Toby\'s face puckered...', '托比的脸皱了起来。');
INSERT INTO `words` VALUES (6421, 'puckish', 'ˈpʌkɪʃ', 'adj.', '顽皮的;淘气的;爱恶作剧的;', 'He had a puckish sense of humour.', '他有种恶作剧式的幽默感。');
INSERT INTO `words` VALUES (6422, 'pudding', 'ˈpʊdɪŋ', 'n.', '(餐末的)甜食，甜点;热布丁糕(用面粉、油、蛋制作，加水果、果酱等);热布丁饼(用面粉、油、蛋制作，通常用肉填充);', '...a cherry sponge pudding with warm custard.', '涂着热蛋奶沙司的樱桃松糕布丁');
INSERT INTO `words` VALUES (6423, 'puddle', 'ˈpʌdl', 'n.', '水洼;小水坑;(尤指)雨水坑;', 'The road was shiny with puddles, but the rain was at an end.', '马路上到处是水洼，闪着光，不过雨停了。');
INSERT INTO `words` VALUES (6424, 'puerile', 'ˈpjʊəraɪl', 'adj.', '愚蠢的;幼稚的;孩子气的;', 'Concert organisers branded the group\'s actions as puerile...', '音乐会的组织者指称该乐队的行为愚蠢幼稚。');
INSERT INTO `words` VALUES (6425, 'puff', 'pʌf', 'v.', '吸，抽(香烟、烟斗等);使喷出，冒出(烟或蒸汽);急促喘息;', 'He lit a cigar and puffed at it twice...', '他点了一支雪茄，吸了两口。');
INSERT INTO `words` VALUES (6426, 'puffs', 'pʌfs', 'v.', '吸，抽(香烟、烟斗等);使喷出，冒出(烟或蒸汽);急促喘息;', 'He lit a cigar and puffed at it twice...', '他点了一支雪茄，吸了两口。');
INSERT INTO `words` VALUES (6427, 'pugnacious', 'pʌɡˈneɪʃəs', 'adj.', '爱争执的;好斗的;爱滋事的;', 'The President was in a pugnacious mood when he spoke to journalists about the rebellion.', '总统和记者说起叛乱时，情绪激昂。');
INSERT INTO `words` VALUES (6428, 'pugnacity', 'pʌɡˈnæsəti', 'n.', '好斗;好争;好战;', 'He is legendary for his fearlessness and pugnacity.', '他是出了名的大胆好斗。');
INSERT INTO `words` VALUES (6429, 'puke', 'pjuːk', 'v.', '吐;呕吐;', 'He\'s puked...', '他吐了出来。');
INSERT INTO `words` VALUES (6430, 'pull', 'pʊl', 'v.', '拉;拽;扯;', 'They have pulled out patients\' teeth unnecessarily...', '他们毫无必要地拔掉了病人的牙。');
INSERT INTO `words` VALUES (6431, 'pulley', 'ˈpʊli', 'n.', '滑轮;滑轮组;滑车;', 'The weights are moved via a cable and pulley system.', '重物通过缆绳和滑轮系统运送。');
INSERT INTO `words` VALUES (6432, 'pulmonary', 'ˈpʌlmənəri', 'adj.', '肺的;肺部的;与肺有关的;', '...respiratory and pulmonary disease.', '呼吸系统和肺部疾病');
INSERT INTO `words` VALUES (6433, 'pulp', 'pʌlp', 'n.', '浆状物;纸浆;(瓜果等的)肉质部分，髓;', 'The olives are crushed to a pulp by stone rollers.', '橄榄被石磙碾成了酱。');
INSERT INTO `words` VALUES (6434, 'pulsate', 'pʌlˈseɪt', 'v.', '有规律地跳动(或发声);均匀震动;搏动;', '...a star that pulsates.', '一颗脉动的恒星');
INSERT INTO `words` VALUES (6435, 'pulse', 'pʌls', 'n.', '脉搏;脉率;强劲的音乐节拍;', 'Mahoney\'s pulse was racing, and he felt confused.', '马奥尼脉搏跳得很快，他感到很慌乱。');
INSERT INTO `words` VALUES (6436, 'pump', 'pʌmp', 'n.', '抽水机;泵;打气筒;', '...pumps that circulate the fuel around in the engine...', '使燃料在发动机内循环的泵');
INSERT INTO `words` VALUES (6437, 'pumpkin', 'ˈpʌmpkɪn', 'n.', '南瓜;南瓜大果;', 'Quarter the pumpkin and remove the seeds.', '将南瓜切成4份，去掉籽。');
INSERT INTO `words` VALUES (6438, 'punch', 'pʌntʃ', 'v.', '拳打;以拳痛击;给…打孔;', 'After punching him on the chin she wound up hitting him over the head...', '她先挥拳打他的下巴，然后又打他的脑袋。');
INSERT INTO `words` VALUES (6439, 'punching', 'ˈpʌntʃɪŋ', 'v.', '拳打;以拳痛击;给…打孔;', 'After punching him on the chin she wound up hitting him over the head...', '她先挥拳打他的下巴，然后又打他的脑袋。');
INSERT INTO `words` VALUES (6440, 'punctilious', 'pʌŋkˈtɪliəs', 'adj.', '一丝不苟的;循规蹈矩的;', 'He was punctilious about being ready and waiting in the entrance hall exactly on time...', '他一丝不苟地作好准备并准时地在门厅等候。');
INSERT INTO `words` VALUES (6441, 'punctual', 'ˈpʌŋktʃuəl', 'adj.', '按时的;准时的;守时的;', 'He\'s always very punctual. I\'ll see if he\'s here yet.', '他总是很准时。我去看看他是否已经来了。');
INSERT INTO `words` VALUES (6442, 'punctuality', 'ˌpʌŋktjʊˈælɪti', 'n.', '严守时间;敏捷;', 'He\'s always very punctual. I\'ll see if he\'s here yet.', '他总是很准时。我去看看他是否已经来了。');
INSERT INTO `words` VALUES (6443, 'punctually', 'ˈpʌŋktjʊəli', 'adv.', '准时地;', 'He\'s always very punctual. I\'ll see if he\'s here yet.', '他总是很准时。我去看看他是否已经来了。');
INSERT INTO `words` VALUES (6444, 'punctuate', 'ˈpʌŋktʃueɪt', 'v.', '不时打断;给…加标点符号;', 'The silence of the night was punctuated by the distant rumble of traffic.', '夜晚的宁静不时被远处车辆的隆隆声打断。');
INSERT INTO `words` VALUES (6445, 'punctuation', 'ˌpʌŋktʃuˈeɪʃn', 'n.', '标点符号;标点符号用法;', 'He was known for his poor grammar and punctuation.', '他不通语法和乱用标点是出了名的。');
INSERT INTO `words` VALUES (6446, 'puncture', 'ˈpʌŋktʃə(r)', 'n.', '(轮胎上刺破的)小孔，小洞;(尤指皮肤上被刺破的)扎孔;刺伤;', 'Somebody helped me mend the puncture.', '有个人帮我修补扎破的轮胎。');
INSERT INTO `words` VALUES (6447, 'pundit', 'ˈpʌndɪt', 'n.', '行家;权威;专家;', '...a well known political pundit.', '著名的政治专家');
INSERT INTO `words` VALUES (6448, 'pungency', '', 'n.', '辛辣；刺激性；尖刻;', 'The more herbs you use, the more pungent the sauce will be.', '用的香草越多，调味汁的味道就越浓烈。');
INSERT INTO `words` VALUES (6449, 'pungent', 'ˈpʌndʒənt', 'adj.', '味道(或气味)强烈的;刺激性的;说穿的;', 'The more herbs you use, the more pungent the sauce will be.', '用的香草越多，调味汁的味道就越浓烈。');
INSERT INTO `words` VALUES (6450, 'punish', 'ˈpʌnɪʃ', 'v.', '处罚;惩罚;对…判罪;', 'I don\'t believe that George ever had to punish the children...', '我认为乔治根本没有必要处罚孩子们。');
INSERT INTO `words` VALUES (6451, 'punishment', 'ˈpʌnɪʃmənt', 'n.', '惩罚;处罚;刑罚;', '...a group which campaigns against the physical punishment of children...', '一个反对体罚儿童的团体');
INSERT INTO `words` VALUES (6452, 'punitive', 'ˈpjuːnətɪv', 'adj.', '惩罚性的;刑罚的;处罚的;', '...a punitive bombing raid...', '惩罚性空袭');
INSERT INTO `words` VALUES (6453, 'punk', 'pʌŋk', 'n.', '鬅客摇滚乐(流行于20世纪70年代后期和80年代初期);鬅客摇滚乐迷;小流氓;', 'I was never really into punk.', '我从未真正喜欢过朋克摇滚乐。');
INSERT INTO `words` VALUES (6454, 'puny', 'ˈpjuːni', 'adj.', '弱小的;孱弱的;不起眼的;', '...a puny, bespectacled youth...', '戴眼镜的瘦弱青年');
INSERT INTO `words` VALUES (6455, 'pup', 'pʌp', 'n.', '幼小动物;', 'I\'ll get you an Alsatian pup for Christmas.', '我会送你一只阿尔萨斯小狗作为圣诞节礼物。');
INSERT INTO `words` VALUES (6456, 'pupa', 'ˈpjuːpə', 'n.', '蛹;', 'The pupae remain dormant in the soil until they emerge as adult moths in the winter.', '蛹蛰伏在土壤中，直到冬天它们才蜕变成蛾子飞出来。');
INSERT INTO `words` VALUES (6457, 'pupil', 'ˈpjuːpl', 'n.', '学生;(尤指)小学生;弟子;', 'Over a third of those now at secondary school in Wales attend schools with over 1,000 pupils...', '威尔士现在的初中生中有1/3以上的人就读的学校学生总数超过1,000人。');
INSERT INTO `words` VALUES (6458, 'puppy', 'ˈpʌpi', 'n.', '小狗;幼犬;傲慢小子;', 'One Sunday he began trying to teach the two puppies to walk on a leash.', '一个星期天，他开始尝试训练两只小狗系上皮带走路。');
INSERT INTO `words` VALUES (6459, 'purchase', 'ˈpɜːtʃəs', 'n.', '购买;采购;购买的东西;', 'He purchased a ticket and went up on the top deck...', '他买了一张票，然后上到顶层甲板。');
INSERT INTO `words` VALUES (6460, 'purchaser', 'ˈpɜːtʃəsə(r)', 'n.', '购买人;采购人员;买主;', 'He purchased a ticket and went up on the top deck...', '他买了一张票，然后上到顶层甲板。');
INSERT INTO `words` VALUES (6461, 'pure', 'pjʊə(r)', 'adj.', '纯的;纯净的;纯粹的;', '...a carton of pure orange juice.', '一盒纯橙汁');
INSERT INTO `words` VALUES (6462, 'purebred', 'ˈpjʊəbrɛd', 'adj.', '(动物)纯种的;', '...pure-bred Arab horses.', '纯种阿拉伯马');
INSERT INTO `words` VALUES (6463, 'puree', 'ˈpjʊəreɪ', 'n.', '(用水果、熟的蔬菜压成的)酱，糊，泥;', '...a can of tomato puree...', '一罐西红柿酱');
INSERT INTO `words` VALUES (6464, 'purely', 'ˈpjʊəli', 'adv.', '仅仅;完全;', 'It is a racing machine, designed purely for speed...', '那是一个比赛用的机器，完全是为提高速度设计的。');
INSERT INTO `words` VALUES (6465, 'purgative', 'ˈpɜːɡətɪv', 'n.', '泻药;通便药物;', 'The doctors attempted to reduce his high fever by inducing diarrhea with a purgative.', '医生们尝试用通便药物催泻以使他退烧。');
INSERT INTO `words` VALUES (6466, 'purgatory', 'ˈpɜːɡətri', 'n.', '炼狱;受难的处所(或状态);惩戒所;', 'Prayers were said for souls in Purgatory.', '祷告词是为炼狱中的灵魂念的。');
INSERT INTO `words` VALUES (6467, 'purge', 'pɜːdʒ', 'v.', '清除，清洗(组织中的异己分子);净化(心灵、风气等);涤荡(污秽);', 'The leadership voted to purge the party of \'hostile and anti-party elements\'...', '领导层投票决定清除党内的“敌对和反党分子”。');
INSERT INTO `words` VALUES (6468, 'purification', 'ˌpjʊərɪfɪ\'keɪʃ(ə)n', 'n.', '提纯;斋戒;清洗;', 'I take wheat and yeast tablets daily to purify the blood...', '我每天服用干酵母片以净化血液。');
INSERT INTO `words` VALUES (6469, 'purify', 'ˈpjʊərɪfaɪ', 'v.', '使(某物)洁净;净化;洗涤(思想);', 'I take wheat and yeast tablets daily to purify the blood...', '我每天服用干酵母片以净化血液。');
INSERT INTO `words` VALUES (6470, 'puritan', 'ˈpjʊərɪtən', 'n.', '禁欲者;苦行者;清教徒(属于16和17世纪的英国教会);', 'Bykov had forgotten that Malinin was something of a puritan...', '贝科夫已经忘记了马利宁是个清教徒式的人物。');
INSERT INTO `words` VALUES (6471, 'puritanical', 'ˌpjʊərɪˈtænɪkl', 'adj.', '清教徒式的;道德极严格的;', '...puritanical fathers...', '古板严厉的父亲');
INSERT INTO `words` VALUES (6472, 'purloin', 'pɜːˈlɔɪn', 'v.', '偷窃;擅自使用;', 'Each side purloins the other\'s private letters.', '双方彼此都偷对方的私人信件。');
INSERT INTO `words` VALUES (6473, 'purple', 'ˈpɜːpl', 'adj.', '紫色的;华丽的文辞;雕琢的章句;', 'She wore purple and green silk.', '她穿着紫绿相间的绸缎衣服。');
INSERT INTO `words` VALUES (6474, 'purport', 'pəˈpɔːt , ˈpɜːpɔːt', 'v.', '自称;标榜;', '...a book that purports to tell the whole truth.', '声称要讲述全部事实的一本书');
INSERT INTO `words` VALUES (6475, 'purpose', 'ˈpɜːpəs', 'n.', '意图;目的;用途;', 'The purpose of the occasion was to raise money for medical supplies...', '那次活动是为筹集资金购买医药物资。');
INSERT INTO `words` VALUES (6476, 'purposeful', 'ˈpɜːpəsfl', 'adj.', '有意义的;有目的的;坚毅的;', 'She had a purposeful air, and it became evident that this was not a casual visit.', '她一副无事不登三宝殿的神情，很明显，这不是一次随意造访。');
INSERT INTO `words` VALUES (6477, 'purposefully', 'ˈpɜːpəsfʊli', 'adv.', '有意;', 'She had a purposeful air, and it became evident that this was not a casual visit.', '她一副无事不登三宝殿的神情，很明显，这不是一次随意造访。');
INSERT INTO `words` VALUES (6478, 'purr', 'pɜː(r)', 'v.', '(猫)发出呼噜声，惬意地打呼噜;轰隆作响;轰鸣着移动;', 'The plump ginger kitten had settled comfortably in her arms and was purring enthusiastically.', '胖乎乎的姜黄色小猫惬意地躺在她的怀里，大声地打着呼噜。');
INSERT INTO `words` VALUES (6479, 'pursuance', 'pəˈsjuːəns', 'n.', '执行;进行;实行;', 'He ordered disclosure of a medical report to the Metropolitan Police in pursuance of an investigation of murder.', '为配合调查一宗谋杀案，他下令向伦敦警察厅披露一份医疗报告。');
INSERT INTO `words` VALUES (6480, 'pursuant', 'pəˈsjuːənt', 'adj.', '依照，根据，按照(尤指规则或法律);', 'He should continue to act pursuant to the United Nations Security Council resolutions.', '他应该继续依照联合国安理会的决议行事。');
INSERT INTO `words` VALUES (6481, 'pursue', 'pəˈsjuː', 'v.', '追求;致力于;执行;', 'It became harder for women married to diplomats to pursue their own interests...', '与外交官结婚的女性要追求自己的爱好就变得更加困难了。');
INSERT INTO `words` VALUES (6482, 'pursuer', 'pəˈsjuːə(r)', 'n.', '追赶者;追寻者;追捕者;', 'They had shaken off their pursuers.', '他们甩掉了追赶者。');
INSERT INTO `words` VALUES (6483, 'pursuit', 'pəˈsjuːt', 'n.', '追求;寻找;追赶;', '...a young man whose relentless pursuit of excellence is conducted with single-minded determination.', '一心一意、坚定不移地追求完美的年轻人');
INSERT INTO `words` VALUES (6484, 'purvey', 'pəˈveɪ', 'v.', '提供，供应(食物、服务或信息);', '...one who would, for a hefty fee, purvey strategic advice to private corporations.', '一个会为了一笔数目可观的酬金而将策略性建议透露给私营企业的人');
INSERT INTO `words` VALUES (6485, 'purveyor', 'pəˈveɪə(r)', 'n.', '提供者;供应商;供应公司;', '...purveyors of gourmet foods.', '美味佳肴的提供者');
INSERT INTO `words` VALUES (6486, 'purview', 'ˈpɜːvjuː', 'n.', '(组织、活动等的)范围，权限;', 'That, however, was beyond the purview of the court; it was a diplomatic matter.', '但是，那已不在法庭权限之内；那是个外交问题。');
INSERT INTO `words` VALUES (6487, 'push', 'pʊʃ', 'v.', '推动(人或物);移动(身体部位);推进(道路);', 'The woman pushed back her chair and stood up...', '那女人把椅子往后一推站了起来。');
INSERT INTO `words` VALUES (6488, 'push-button', 'ˈpʊʃ bʌtn', 'adj.', '按键式的;用按钮操作的;', '...push-button phones.', '按键式电话');
INSERT INTO `words` VALUES (6489, 'pusher', 'ˈpʊʃə(r)', 'n.', '贩毒者;毒品贩子;', 'He was accused of acting as a carrier for drug pushers.', '他被指控为一些毒贩运毒。');
INSERT INTO `words` VALUES (6490, 'pushover', 'ˈpʊʃəʊvə(r)', 'n.', '轻易的事;容易获得的胜利;容易说服的人;', 'He is a tough negotiator. We did not expect to find him a pushover and he has not been one.', '他是个难对付的谈判者。我们没指望说服他，而且也没说服他。');
INSERT INTO `words` VALUES (6491, 'pushy', 'ˈpʊʃi', 'adj.', '执意强求的;死缠硬磨的;', 'She was a confident and pushy young woman...', '她是个自信又固执己见的年轻女性。');
INSERT INTO `words` VALUES (6492, 'pusillanimous', 'ˌpjuːsɪˈlænɪməs', 'adj.', '胆怯的;怯懦的;', 'The authorities have been too pusillanimous in merely condemning the violence.', '当局对暴行只是进行了谴责，真是太胆小怕事了。');
INSERT INTO `words` VALUES (6493, 'putative', 'ˈpjuːtətɪv', 'adj.', '推定的;认定的;公认的;', '...a putative father.', '推定的父亲');
INSERT INTO `words` VALUES (6494, 'putrefaction', 'ˌpjuːtrɪˈfækʃn', 'n.', '腐烂;(尸体)腐化;', '...the lingering stench of putrefaction.', '挥之不散的腐臭');
INSERT INTO `words` VALUES (6495, 'putrefy', 'ˈpjuːtrɪfaɪ', 'v.', '腐烂;腐化;', 'The meat in all of the open flasks putrefied.', '所有开口瓶中的肉都腐烂了。');
INSERT INTO `words` VALUES (6496, 'putrid', 'ˈpjuːtrɪd', 'adj.', '腐烂的;腐臭的;令人厌恶(或恶心)的;', '...a foul, putrid stench.', '难闻的腐臭');
INSERT INTO `words` VALUES (6497, 'putsch', 'pʊtʃ', 'n.', '政变;武力夺取政权;', 'Spectacular changes have taken place at the top since the failed putsch.', '政变失败后，高层出现了急剧的变化。');
INSERT INTO `words` VALUES (6498, 'putt', 'pʌt', 'v.', '轻击;推球入洞;', '...a 5-foot putt.', '五英尺远的轻击');
INSERT INTO `words` VALUES (6499, 'puzzle', 'ˈpʌzl', 'n.', '谜;智力游戏;不解之谜;', 'My sister puzzles me and causes me anxiety.', '我妹妹总让我捉摸不透，弄得我焦虑不安。');
INSERT INTO `words` VALUES (6500, 'puzzled', 'ˈpʌzld', 'adj.', '困惑的;迷惑不解的;', 'Critics remain puzzled by the British election results...', '批评家仍然对英国大选的结果困惑不解。');
INSERT INTO `words` VALUES (6501, 'puzzlement', 'ˈpʌzlmənt', 'n.', '迷惘;困惑;', 'He frowned in puzzlement.', '他困惑地皱着眉头。');
INSERT INTO `words` VALUES (6502, 'puzzling', 'ˈpʌzlɪŋ', 'v.', '迷惑;使困惑;', 'My sister puzzles me and causes me anxiety.', '我妹妹总让我捉摸不透，弄得我焦虑不安。');
INSERT INTO `words` VALUES (6503, 'pygmy', 'ˈpɪɡmi', 'n.', '俾格米人(生活于非洲和东南亚部分地区，身材矮小);矮小的人(或物);侏儒;', 'Reaching a maximum height of 56cm the pygmy goat is essentially a pet.', '因为最高仅为56厘米，这种矮种山羊基本上作为宠物来养。');
INSERT INTO `words` VALUES (6504, 'pylon', 'ˈpaɪlən', 'n.', '电缆塔;', '...electricity pylons.', '电缆塔');
INSERT INTO `words` VALUES (6505, 'pyramid', 'ˈpɪrəmɪd', 'n.', '(古埃及的)金字塔;锥体;棱锥体;', 'We set off to see the Pyramids and Sphinx.', '我们出发去看金字塔和狮身人面像。');
INSERT INTO `words` VALUES (6506, 'quack', 'kwæk', 'n.', '(鸭子的)呱呱声，嘎嘎声;江湖郎中;冒牌医生;', 'I went everywhere for treatment, tried all sorts of quacks.', '我四处求医，看过了各种各样的江湖郎中。');
INSERT INTO `words` VALUES (6507, 'quad', 'kwɒd', 'n.', '同 quadruplet;同 quadrangle;四胞胎之一;', '...a 34-year-old mother of quads.', '生了四胞胎的34岁的母亲');
INSERT INTO `words` VALUES (6508, 'quadrant', 'ˈkwɒdrənt', 'n.', '四分之一圆(或圆周);象限;象限仪，四分仪(常用于测量海上方位或观看星辰);', 'A symbol appears in an upper quadrant of the screen.', '一个符号出现在屏幕上方的一个象限里。');
INSERT INTO `words` VALUES (6509, 'quadruple', 'kwɒˈdruːpl , ˈkwɒdrʊpl', 'v.', '(使)变为四倍;', 'Norway has quadrupled its exports to the EU...', '挪威对欧盟的出口已经增加了3倍。');
INSERT INTO `words` VALUES (6510, 'quaff', 'kwɒf', 'v.', '豪饮;痛饮;开怀畅饮;', 'He\'s quaffed many a glass of champagne in his time.', '他年轻时曾经开怀畅饮过不少香槟美酒。');
INSERT INTO `words` VALUES (6511, 'quagmire', 'ˈkwæɡmaɪə(r)', 'n.', '泥淖;湿地;泥沼;', 'His people had fallen further and further into a quagmire of confusion...', '他的人民在混乱不安的泥潭里越陷越深。');
INSERT INTO `words` VALUES (6512, 'quail', 'kweɪl', 'n.', '鹌鹑;鹌鹑肉;', 'I\'ve shot hundreds of quail with that gun.', '我用那只枪打死过几百只鹌鹑。');
INSERT INTO `words` VALUES (6513, 'quaint', 'kweɪnt', 'adj.', '新奇有趣的;古色古香的;', '...a small, quaint town with narrow streets and traditional half-timbered houses...', '一座有着狭窄街道和传统的露木架房屋的古色古香的小镇');
INSERT INTO `words` VALUES (6514, 'quake', 'kweɪk', 'v.', '(因恐惧或紧张)发抖，颤抖，哆嗦;震动;颤动;', 'The quake destroyed mud buildings in many remote villages.', '这次地震摧毁了许多偏远乡村的土坯房。');
INSERT INTO `words` VALUES (6515, 'qualification', 'ˌkwɒlɪfɪˈkeɪʃn', 'n.', '(通过考试或学习课程取得的)资格;学历;(通过经验或具备技能而取得的)资格;', 'They will be encouraged to mix academic A-levels with vocational qualifications...', '将鼓励他们在取得优秀学习成绩的同时也要获得一定的职业资质。');
INSERT INTO `words` VALUES (6516, 'qualified', 'ˈkwɒlɪfaɪd', 'adj.', '具备…的学历(或资历);具备…的知识(或技能);符合资格;', 'Demand has far outstripped supply of qualified teachers...', '有教学资格的教师远远供不应求。');
INSERT INTO `words` VALUES (6517, 'qualifier', 'ˈkwɒlɪfaɪə(r)', 'n.', '(击败对手可进入某竞赛的)合格者;预选赛;资格赛;', 'Last week Wales lost 5-1 to Romania in a World Cup qualifier.', '在上周的世界杯预选赛中，威尔士队1比5负于罗马尼亚队。');
INSERT INTO `words` VALUES (6518, 'qualify', 'ˈkwɒlɪfaɪ', 'v.', '取得资格(或学历);合格;使合格;', 'But when I\'d qualified and started teaching it was a different story...', '可是等我获得资格开始教书的时候，却是另一番情形了。');
INSERT INTO `words` VALUES (6519, 'qualitative', 'ˈkwɒlɪtətɪv', 'adj.', '质量的;定性的;性质的;', 'There are qualitative differences in the way children and adults think...', '孩子和成年人的思维方式有质的不同。');
INSERT INTO `words` VALUES (6520, 'quality', 'ˈkwɒləti', 'n.', '质量;品质;上乘;', 'Everyone can greatly improve the quality of life...', '每个人都可以大大改善生活质量。');
INSERT INTO `words` VALUES (6521, 'qualm', 'kwɑːm', 'n.', '(对自己行为的)顾虑，不安;', 'I have no qualms about recommending this approach...', '我会毫不犹豫地推荐这一方法。');
INSERT INTO `words` VALUES (6522, 'quandary', 'ˈkwɒndəri', 'n.', '困惑;进退两难;困窘;', 'The government appears to be in a quandary about what to do with so many people.', '政府似乎陷入了左右为难的境地，不知道该拿这么多人怎么办。');
INSERT INTO `words` VALUES (6523, 'quantifiable', 'ˈkwɒntɪfaɪəbl', 'adj.', '可以定量的;能量化的;', 'A clearly quantifiable measure of quality is not necessary.', '没有必要对质量进行明确的定量测量。');
INSERT INTO `words` VALUES (6524, 'quantify', 'ˈkwɒntɪfaɪ', 'v.', '以数量表述;量化;', 'It is difficult to quantify an exact figure as firms are reluctant to declare their losses.', '由于各家公司都不愿意公布所受的损失，所以很难得出一个精确的数字。');
INSERT INTO `words` VALUES (6525, 'quantitative', 'ˈkwɒntɪtətɪv', 'adj.', '数量的;量化的;定量性的;', '...the advantages of quantitative and qualitative research.', '定量研究与定性研究的优势');
INSERT INTO `words` VALUES (6526, 'quantity', 'ˈkwɒntəti', 'n.', '数量;数额;数目;', '...a small quantity of water.', '少量的水');
INSERT INTO `words` VALUES (6527, 'quantum', 'ˈkwɒntəm', 'n.', '量子;', 'Both quantum mechanics and chaos theory suggest a world constantly in flux.', '量子力学和混沌理论都表明世界永远处于不断变化中。');
INSERT INTO `words` VALUES (6528, 'quarantine', 'ˈkwɒrəntiːn', 'n.', '(为防传染的)隔离期;检疫;', 'She was sent home to Oxford and put in quarantine...', '她被送回牛津的家中隔离起来。');
INSERT INTO `words` VALUES (6529, 'quarrel', 'ˈkwɒrəl', 'n.', '口角;争吵;拌嘴;', 'I had a terrible quarrel with my other brothers...', '我跟其他几个兄弟大吵了一架。');
INSERT INTO `words` VALUES (6530, 'quarrelsome', 'ˈkwɒrəlsəm', 'adj.', '爱争吵的;好口角的;', 'Benedict had been a wild boy and a quarrelsome young man.', '贝内迪克特小时候野性难驯，年轻时又爱与人争吵。');
INSERT INTO `words` VALUES (6531, 'quarry', 'ˈkwɒri', 'n.', '采石场;被追猎的动物(或人);追捕的对象;', '...an old limestone quarry.', '废旧的石灰岩采石场');
INSERT INTO `words` VALUES (6532, 'quart', 'kwɔːt', 'n.', '夸脱(液量单位，在英国和加拿大等于2品脱或1.14升，在美国等于0.94升);', 'Pick up a quart of milk or a loaf of bread.', '拿1夸脱牛奶或一条面包。');
INSERT INTO `words` VALUES (6533, 'quarter', 'ˈkwɔːtə(r)', 'n.', '四等份之一;(正点之前或之后的)15分钟，一刻钟;三个月时间;', 'A quarter of the residents are over 55 years old...', '1/4的居民年龄在55岁以上。');
INSERT INTO `words` VALUES (6534, 'quarterly', 'ˈkwɔːtəli', 'adj.', '季度的;每季的;', '...the latest Bank of Japan quarterly survey of 5,000 companies.', '日本银行对5,000家公司的最新季度调查');
INSERT INTO `words` VALUES (6535, 'quartet', 'kwɔːˈtet', 'n.', '四重奏乐团;四重唱组合;四重奏(曲);', '...a string quartet.', '四重奏弦乐团');
INSERT INTO `words` VALUES (6536, 'quartz', 'kwɔːts', 'n.', '石英;', '...a quartz crystal.', '石英晶体');
INSERT INTO `words` VALUES (6537, 'quash', 'kwɒʃ', 'v.', '宣布(法庭的裁决)无效;撤销(判决);制止;', 'The Appeal Court has quashed the convictions of all eleven people.', '上诉法庭已经撤销对所有11人的判决。');
INSERT INTO `words` VALUES (6538, 'quasi', 'ˈkwɑːzi(ː)', 'adj.', '准的，类似的，外表的;', 'The flame is a quasi-religious emblem of immortality.', '火焰可以算作不朽的宗教象征。');
INSERT INTO `words` VALUES (6539, 'quaver', 'ˈkweɪvə(r)', 'v.', '(嗓音因紧张或害怕等)颤抖，颤动;', 'Her voice quavered and she fell silent.', '她声音发颤然后便默不作声了。');
INSERT INTO `words` VALUES (6540, 'quay', 'kiː', 'n.', '码头;埠头;', 'Jack and Stephen were waiting for them on the quay.', '杰克和斯蒂芬正在码头上等他们。');
INSERT INTO `words` VALUES (6541, 'queasy', 'ˈkwiːzi', 'adj.', '恶心的;欲吐的;稍感紧张的;', 'He was very prone to seasickness and already felt queasy.', '他快晕船了，已经感到恶心了。');
INSERT INTO `words` VALUES (6542, 'queen', 'kwiːn', 'n.', '女王;女酋长;女首领;', '...Queen Victoria...', '维多利亚女王');
INSERT INTO `words` VALUES (6543, 'queer', 'kwɪə(r)', 'adj.', '奇怪的;反常的;妖里妖气的;', 'If you ask me, there\'s something a bit queer going on.', '要是你问我的话，我觉得有点儿不太对劲。');
INSERT INTO `words` VALUES (6544, 'quell', 'kwel', 'v.', '制止;平息;镇压;', 'Troops eventually quelled the unrest.', '军队最终平息了动乱。');
INSERT INTO `words` VALUES (6545, 'quench', 'kwentʃ', 'v.', '解(渴);止(渴);扑灭;', 'He stopped to quench his thirst at a stream.', '他停了下来，在小溪边饮水止渴。');
INSERT INTO `words` VALUES (6546, 'querulous', 'ˈkwerələs', 'adj.', '抱怨的;显得恼怒的;', 'A querulous male voice said, \'Look, are you going to order, or what?\'', '一个男人不满地问：“喂，你到底要不要点菜？”');
INSERT INTO `words` VALUES (6547, 'query', 'ˈkwɪəri', 'n.', '疑问;询问;问号;', 'If you have any queries about this insurance, please contact Travel Insurance Services Limited.', '如果您对此项保险有什么疑问，请与旅行保险服务有限公司联系。');
INSERT INTO `words` VALUES (6548, 'quest', 'kwest', 'n.', '探索，寻找，追求(幸福等);', 'My quest for a better bank continues.', '我继续寻找一家更好的银行。');
INSERT INTO `words` VALUES (6549, 'question', 'ˈkwestʃən', 'n.', '问题;疑问;(待讨论或处理的)事情;', 'They asked a great many questions about England...', '他们问了一大堆有关英格兰的问题。');
INSERT INTO `words` VALUES (6550, 'questionable', 'ˈkwestʃənəbl', 'adj.', '可疑的;有问题的;未必准确(或正确)的;', 'He has been dogged by allegations of questionable business practices...', '他一直被那些指控他有不正当商业行为的说法所困扰。');
INSERT INTO `words` VALUES (6551, 'questioner', 'ˈkwestʃənə(r)', 'n.', '(广播节目或公开辩论等的)提问人;', 'He agreed with the questioner.', '他赞同提问者的观点。');
INSERT INTO `words` VALUES (6552, 'questioning', 'ˈkwestʃənɪŋ', 'n.', '提问;询问;盘问;', 'He raised a questioning eyebrow.', '他探询地挑起了眉毛。');
INSERT INTO `words` VALUES (6553, 'questionnaire', 'ˌkwestʃəˈneə(r)', 'n.', '调查表;问卷;', 'Headteachers will be asked to fill in a questionnaire.', '校长们将被要求完成一份问卷调查。');
INSERT INTO `words` VALUES (6554, 'quibble', 'ˈkwɪbl', 'v.', '(为小事)争论，发牢骚;斤斤计较;吹毛求疵;', 'Council members spent the day quibbling over the final wording of the resolution...', '委员会成员一整天都在为决议的最终措词争论不休。');
INSERT INTO `words` VALUES (6555, 'quick', 'kwɪk', 'adj.', '快的;迅速的;时间短暂的;', 'You\'ll have to be quick. The flight leaves in about three hours...', '你得快点了，飞机大约3小时后起飞。');
INSERT INTO `words` VALUES (6556, 'quicken', 'ˈkwɪkən', 'v.', '(使)加快，加速;变得更活跃;使更活跃;', 'Ainslie\'s pulse quickened in alarm...', '由于惊恐，安斯利的脉搏加快了。');
INSERT INTO `words` VALUES (6557, 'quickie', 'ˈkwɪki', 'n.', '简短的事;短暂的事;瞬间完事的性交;', '...a quickie divorce...', '闪电离婚');
INSERT INTO `words` VALUES (6558, 'quickly', 'ˈkwɪkli', 'adv.', '迅速地;很快地;不久;', 'You\'ll have to be quick. The flight leaves in about three hours...', '你得快点了，飞机大约3小时后起飞。');
INSERT INTO `words` VALUES (6559, 'quicksand', 'ˈkwɪksænd', 'n.', '流沙;难以摆脱的困境;危险局面;', 'The sandbank was uncertain, like quicksand under his feet.', '那个沙坝并不稳固，他脚下如同踩着流沙一般。');
INSERT INTO `words` VALUES (6560, 'quid', 'kwɪd', 'n.', '一英镑;', 'It cost him five hundred quid.', '这花了他500英镑。');
INSERT INTO `words` VALUES (6561, 'quiescent', 'kwiˈesnt', 'adj.', '沉寂的;静态的;静止状态的;', '...a society which was politically quiescent and above all deferential.', '政治上波澜不兴、甚至马首是瞻的社会');
INSERT INTO `words` VALUES (6562, 'quiet', 'ˈkwaɪət', 'adj.', '轻声的;轻柔的;安静的;', 'Tania kept the children reasonably quiet and contented...', '塔妮娅把孩子们哄得安安静静而又心满意足。');
INSERT INTO `words` VALUES (6563, 'quieten', 'ˈkwaɪətn', 'v.', '(使)安静下来，平静些;', 'She tried to quieten her breathing...', '她努力让呼吸平缓下来。');
INSERT INTO `words` VALUES (6564, 'quietly', '\'kwaɪətli', 'adv.', '轻声地;安静地;平静地；文静地；温和地;在暗中,秘密地;不张扬地;', 'Tania kept the children reasonably quiet and contented...', '塔妮娅把孩子们哄得安安静静而又心满意足。');
INSERT INTO `words` VALUES (6565, 'quietness', 'ˈkwaɪətnɪs', 'n.', '宁静，平静;', 'Tania kept the children reasonably quiet and contented...', '塔妮娅把孩子们哄得安安静静而又心满意足。');
INSERT INTO `words` VALUES (6566, 'quill', 'kwɪl', 'n.', '翎(鸟的翅膀或尾部的大羽毛);翎笔;羽管笔;', 'She dipped a quill in ink, then began to write.', '她将羽毛笔在墨水里蘸了一下，随后开始书写。');
INSERT INTO `words` VALUES (6567, 'quilt', 'kwɪlt', 'n.', '加衬芯床罩;', '...an old patchwork quilt.', '一床旧的百衲被');
INSERT INTO `words` VALUES (6568, 'quintessence', 'kwɪnˈtesns', 'n.', '典范;范例;精髓;', 'He was the quintessence of all that Eva most deeply loathed.', '他是伊娃最深恶痛绝的那种人中的典型。');
INSERT INTO `words` VALUES (6569, 'quip', 'kwɪp', 'n.', '俏皮话;妙语;', 'The commentators make endless quips about the female players\' appearance.', '评论员们没完没了地拿女选手的容貌打趣。');
INSERT INTO `words` VALUES (6570, 'quirk', 'kwɜːk', 'n.', '怪异的性格(或行为);怪癖;(尤指偶发的)怪事，奇事;', 'By a tantalising quirk of fate, the pair have been drawn to meet in the first round of the championship...', '仿佛是命运的捉弄，这对选手在锦标赛第一轮比赛中就狭路相逢。');
INSERT INTO `words` VALUES (6571, 'quite', 'kwaɪt', 'adv.', '颇;相当;某种程度上;', 'I felt quite bitter about it at the time...', '当时我对这件事感到相当怨愤。');
INSERT INTO `words` VALUES (6572, 'quitter', 'ˈkwɪtə(r)', 'n.', '有始无终的人;虎头蛇尾的人;半途而废者;', 'He won\'t resign because he\'s not a quitter.', '他不会辞职的，因为他不是一个轻言放弃的人。');
INSERT INTO `words` VALUES (6573, 'quiver', 'ˈkwɪvə(r)', 'v.', '轻微颤动;抖动;抽动;', 'Her bottom lip quivered and big tears rolled down her cheeks.', '她的下唇颤抖着，豆大的泪珠顺着面颊滚落下来。');
INSERT INTO `words` VALUES (6574, 'quixotic', 'kwɪkˈsɒtɪk', 'adj.', '想入非非的;异想天开的;堂吉诃德式的;', 'He has always lived his life by a hopelessly quixotic code of honour.', '他生活中一直遵循一种完全不切实际的堂吉诃德式的道德准则。');
INSERT INTO `words` VALUES (6575, 'quiz', 'kwɪz', 'n.', '知识竞赛;智力游戏;小测验;', 'We\'ll have a quiz at the end of the show.', '节目的最后我们要举行一次智力竞赛。');
INSERT INTO `words` VALUES (6576, 'quizzical', 'ˈkwɪzɪkl', 'adj.', '诧异的;感到好笑的;', 'He gave Robin a mildly quizzical glance.', '他有些惊讶地瞥了罗宾一眼。');
INSERT INTO `words` VALUES (6577, 'quorum', 'ˈkwɔːrəm', 'n.', '(会议的)法定人数;', '...enough deputies to make a quorum.', '达到法定人数的足够的代表');
INSERT INTO `words` VALUES (6578, 'quota', 'ˈkwəʊtə', 'n.', '定额;限额;配额;', 'The quota of four tickets per person had been reduced to two.', '每人可购买的票的限额已经由四张降至两张。');
INSERT INTO `words` VALUES (6579, 'quotation', 'kwəʊˈteɪʃn', 'n.', '引语;引文;语录;', 'He illustrated his argument with quotations from Pasternak...', '他引用帕斯捷尔纳克的话来证明自己的论点。');
INSERT INTO `words` VALUES (6580, 'quote', 'kwəʊt', 'v.', '引用;引述;举例说明;', 'He quoted Mr Polay as saying that peace negotiations were already underway...', '他引用波利先生的话，说和谈已在进行之中。');
INSERT INTO `words` VALUES (6581, 'quotidian', 'kwɒˈtɪdiən', 'adj.', '寻常的;普通的;司空见惯的;', '...the minutiae of their quotidian existence.', '他们日常生活中的琐事');
INSERT INTO `words` VALUES (6582, 'quotient', 'ˈkwəʊʃnt', 'n.', '商(除法所得的结果);', 'Being rich doesn\'t actually increase your happiness quotient...', '有钱不见得能提高你的幸福指数。');
INSERT INTO `words` VALUES (6583, 'rabble', 'ˈræbl', 'n.', '乌合之众;聚众的暴民;贱民;', 'He seems to attract a rabble of supporters more loyal to the man than to the cause.', '他似乎吸引了一群忠于他个人超过忠于这项事业的乌合之众的支持。');
INSERT INTO `words` VALUES (6584, 'rabid', 'ˈræbɪd', 'adj.', '极端的;疯狂的;激烈的;', 'The party has distanced itself from the more rabid nationalist groups in the country.', '此党派与国内较偏激的民族主义团体保持了距离。');
INSERT INTO `words` VALUES (6585, 'race', 'reɪs', 'n.', '赛跑;速度竞赛;竞争;', 'The women\'s race was won by the American, Patti Sue Plumer.', '女子赛跑的冠军被美国人帕蒂·休·普卢默夺得。');
INSERT INTO `words` VALUES (6586, 'racer', 'ˈreɪsə(r)', 'n.', '参赛的人(或动物);专供比赛用的车辆(或小艇等);赛车;', 'Tim Powell is a former champion powerboat racer.', '蒂姆·鲍威尔曾是汽艇比赛冠军。');
INSERT INTO `words` VALUES (6587, 'racial', 'ˈreɪʃl', 'adj.', '种族的;种族间的;人种的;', '...the protection of national and racial minorities.', '对少数民族和少数人种的保护');
INSERT INTO `words` VALUES (6588, 'racially', '', 'adv.', '按人种地，人种上;', '...the protection of national and racial minorities.', '对少数民族和少数人种的保护');
INSERT INTO `words` VALUES (6589, 'racing', 'ˈreɪsɪŋ', 'n.', '赛马;速度比赛;', 'Mr Honda was himself a keen racing driver in his younger days...', '本田先生自己年轻时就是位热衷赛车的车手。');
INSERT INTO `words` VALUES (6590, 'racism', 'ˈreɪsɪzəm', 'n.', '种族歧视;种族迫害;种族主义;', 'There is a feeling among some black people that the level of racism is declining.', '一些黑人感到种族歧视的程度在下降。');
INSERT INTO `words` VALUES (6591, 'racist', 'ˈreɪsɪst', 'adj.', '种族主义的;', 'You have to acknowledge that we live in a racist society.', '你得承认我们生活在一个有种族偏见的社会中。');
INSERT INTO `words` VALUES (6592, 'raconteur', 'ˌrækɒnˈtɜː(r)', 'n.', '善于讲故事的人;', 'He spoke eight languages and was a noted raconteur.', '他能说8种语言，是个出了名的讲故事高手。');
INSERT INTO `words` VALUES (6593, 'radar', 'ˈreɪdɑː(r)', 'n.', '雷达;', 'Pilots complained that the radars in the Mirages malfunctioned during conditions of high humidity...', '飞行员抱怨说在高湿度条件下，“幻影”战斗机上的雷达出现了故障。');
INSERT INTO `words` VALUES (6594, 'radial', 'ˈreɪdiəl', 'adj.', '放射状的;辐射状的;', 'The white marble floors were inlaid in a radial pattern of brass.', '白色的大理石地板镶嵌在铜质的放射形图案中。');
INSERT INTO `words` VALUES (6595, 'radiance', 'ˈreɪdiəns', 'n.', '容光焕发;红光满面;(散发出来的)光辉;', 'She has the vigour and radiance of someone young enough to be her granddaughter...', '她拥有与孙女辈的年轻人一样的活力和容光。');
INSERT INTO `words` VALUES (6596, 'radiant', 'ˈreɪdiənt', 'adj.', '喜气洋洋的;容光焕发的;面色红润的;', 'Kathy smiled at her daughter\'s radiant face...', '凯茜对着女儿容光焕发的面孔微笑着。');
INSERT INTO `words` VALUES (6597, 'radiate', 'ˈreɪdieɪt', 'v.', '(使品质或情感)显出，流露;(使热、光、能量)辐射，放射，发散;自中心辐射出;', '...the various walks which radiate from the Heritage Centre...', '从遗产中心向四周延伸出的许许多多步行道');
INSERT INTO `words` VALUES (6598, 'radiation', 'ˌreɪdiˈeɪʃn', 'n.', '辐射;放射线;辐射的热(或能量等);', 'They suffer from health problems and fear the long term effects of radiation...', '他们遭受着健康问题的困扰并且害怕辐射的长期影响。');
INSERT INTO `words` VALUES (6599, 'radical', 'ˈrædɪkl', 'adj.', '根本的;彻底的;完全的;', 'The country needs a period of calm without more surges of radical change...', '国家需要一段时间的稳定，其间不要再有重大的变革。');
INSERT INTO `words` VALUES (6600, 'radically', '\'rædɪkli', 'adv.', '根本地;彻底地;', 'The country needs a period of calm without more surges of radical change...', '国家需要一段时间的稳定，其间不要再有重大的变革。');
INSERT INTO `words` VALUES (6601, 'radio', 'ˈreɪdiəʊ', 'n.', '无线电广播;无线电广播节目;收音机;', 'The last 12 months have been difficult ones for local radio...', '对于当地广播台来说，过去的12个月很艰难。');
INSERT INTO `words` VALUES (6602, 'radioactive', 'ˌreɪdiəʊˈæktɪv', 'adj.', '放射性的;有辐射的;', 'The government has been storing radioactive waste at Fernald for 50 years.', '政府在弗纳德存放放射性废料已达50年之久。');
INSERT INTO `words` VALUES (6603, 'radioactivity', 'ˌreɪdiəʊæk\'tɪvəti', 'n.', '放射性;放射(现象);', 'The government has been storing radioactive waste at Fernald for 50 years.', '政府在弗纳德存放放射性废料已达50年之久。');
INSERT INTO `words` VALUES (6604, 'radius', 'ˈreɪdiəs', 'n.', '半径(长度);半径范围;周围;', 'Nigel has searched for work in a ten-mile radius around his home.', '奈杰尔一直在他家周围10英里的范围内找工作。');
INSERT INTO `words` VALUES (6605, 'raffish', 'ˈræfɪʃ', 'adj.', '放荡不羁的;', 'He was handsome in a raffish kind of way...', '他的帅是一种放荡不羁的帅。');
INSERT INTO `words` VALUES (6606, 'raffle', 'ˈræfl', 'n.', '抽彩;', 'Any more raffle tickets? Twenty-five pence each or five for a pound.', '还有要买奖券的吗？每张25便士，1英镑5张。');
INSERT INTO `words` VALUES (6607, 'raft', 'rɑːft', 'n.', '木排;筏;橡皮艇;', '...a river trip on bamboo rafts through dense rainforest.', '坐竹筏穿过茂密雨林的河上之旅');
INSERT INTO `words` VALUES (6608, 'rafter', 'ˈrɑːftə(r)', 'n.', '椽子;', 'From the rafters of the thatched roofs hung strings of dried onions and garlic.', '茅草屋顶的椽子上挂着几串干洋葱和蒜。');
INSERT INTO `words` VALUES (6609, 'rag', 'ræɡ', 'n.', '抹布;破布;质量低劣的报纸;', 'He was wiping his hands on an oily rag.', '他正在用一块满是油污的破布擦手。');
INSERT INTO `words` VALUES (6610, 'ragamuffin', 'ˈræɡəmʌfɪn', 'n.', '衣着破旧肮脏的人(尤指儿童);雷戈音乐爱好者;雷戈音乐表演者;', 'They looked like little ragamuffins.', '他们看上去像小叫花子。');
INSERT INTO `words` VALUES (6611, 'rage', 'reɪdʒ', 'n.', '暴怒;狂怒;(某情况引起的)愤怒，暴力行为;', 'He was red-cheeked with rage...', '他气得满脸通红。');
INSERT INTO `words` VALUES (6612, 'ragged', 'ˈræɡɪd', 'adj.', '破旧的;褴褛的;衣衫褴褛的;', 'The five survivors eventually reached safety, ragged, half-starved and exhausted.', '5位幸存者最终到达安全之地时已经衣衫褴褛、饥肠辘辘、精疲力尽。');
INSERT INTO `words` VALUES (6613, 'raging', 'ˈreɪdʒɪŋ', 'adj.', '强烈的;极其强大的;猛烈的;', 'The field trip involved crossing a raging torrent.', '实地考察时要穿过一条水流湍急的河。');
INSERT INTO `words` VALUES (6614, 'raid', 'reɪd', 'n.', '突然袭击;突击检查;突然搜查;', 'The guerrillas raided banks and destroyed a police barracks and an electricity substation.', '游击队突袭了银行，摧毁了一处警察驻地和一个变电站。');
INSERT INTO `words` VALUES (6615, 'raider', 'ˈreɪdə(r)', 'n.', '袭击者;抢劫者;', 'The raiders escaped with cash and jewellery.', '劫匪携现金和珠宝逃跑了。');
INSERT INTO `words` VALUES (6616, 'rail', 'reɪl', 'n.', '栏杆;扶手;围栏;', 'They had to walk across an emergency footbridge, holding onto a rope that served as a rail...', '他们必须握着一条绳索充当扶手穿过一条紧急人行天桥。');
INSERT INTO `words` VALUES (6617, 'railing', 'ˈreɪlɪŋ', 'n.', '金属围栏;金属栏杆;', 'He walked out on to the balcony where he rested his arms on the railing.', '他走上阳台，把胳膊搭在栏杆上。');
INSERT INTO `words` VALUES (6618, 'railroad', 'ˈreɪlrəʊd', 'n.', '铁路;铁路公司;', '...railroad tracks that led to nowhere...', '向远方延伸的铁轨');
INSERT INTO `words` VALUES (6619, 'railway', 'ˈreɪlweɪ', 'n.', '铁路;铁道;铁路公司;', 'The road ran beside a railway.', '公路建在铁路旁边。');
INSERT INTO `words` VALUES (6620, 'raiment', 'ˈreɪmənt', 'n.', '衣服;服装;', 'I want nothing but raiment and daily bread.', '我只求温饱，别无他求。');
INSERT INTO `words` VALUES (6621, 'rain', 'reɪn', 'n.', '雨;雨水;(热带地区的)雨季;', 'I hope you didn\'t get soaked standing out in the rain...', '我希望你站在外面没有被雨淋透。');
INSERT INTO `words` VALUES (6622, 'rainbow', 'ˈreɪnbəʊ', 'n.', '虹;彩虹;', 'Oh look, a rainbow!', '看哪，彩虹！');
INSERT INTO `words` VALUES (6623, 'rained', 'reɪnd', 'v.', '下雨;(使)大量降落，雨点般落下;', 'I hope you didn\'t get soaked standing out in the rain...', '我希望你站在外面没有被雨淋透。');
INSERT INTO `words` VALUES (6624, 'rainfall', 'ˈreɪnfɔːl', 'n.', '降雨量;下雨;', 'There have been four years of below average rainfall.', '已经有4年降雨量低于平均值了。');
INSERT INTO `words` VALUES (6625, 'raining', 'ˈreɪnɪŋ', 'v.', '下雨;(使)大量降落，雨点般落下;', 'I hope you didn\'t get soaked standing out in the rain...', '我希望你站在外面没有被雨淋透。');
INSERT INTO `words` VALUES (6626, 'rainstorm', 'ˈreɪnstɔːm', 'n.', '暴风雨;', 'His car collided with another car during a heavy rainstorm.', '他的车在暴雨中和另一辆车相撞了。');
INSERT INTO `words` VALUES (6627, 'rainy', 'ˈreɪni', 'adj.', '阴雨的;多雨的;', 'They walked along the promenade on a rainy night...', '他们在一个雨夜沿着海滨道散步。');
INSERT INTO `words` VALUES (6628, 'raise', 'reɪz', 'v.', '提升;举起;提起;', 'He raised his hand to wave...', '他举手挥动起来。');
INSERT INTO `words` VALUES (6629, 'raised', 'reɪzd', 'adj.', '凸起的;提高的;升高的;', 'He raised his hand to wave...', '他举手挥动起来。');
INSERT INTO `words` VALUES (6630, 'raisin', 'ˈreɪzn', 'n.', '葡萄干;', 'For breakfast I have porridge made with water, to which I add raisins.', '早餐我喝用水煮的麦片粥，还往里加葡萄干。');
INSERT INTO `words` VALUES (6631, 'rakish', 'ˈreɪkɪʃ', 'adj.', '放荡的;肆无忌惮的;(把帽子)潇洒地歪戴着的;', '...a soft-brimmed hat which he wore at a rakish angle.', '他潇洒地歪戴着的一顶软边帽');
INSERT INTO `words` VALUES (6632, 'rally', 'ˈræli', 'n.', '公众集会，群众大会(尤指支持某信念或政党的);(汽车、摩托车等的)拉力赛;(争夺一分的)往返拍击;', 'About three thousand people held a rally to mark international human rights day...', '大约3,000人举行了一场大会来庆祝国际人权日。');
INSERT INTO `words` VALUES (6633, 'ram', 'ræm', 'v.', '和…相撞;撞击;塞进;', 'The thieves fled, ramming the policeman\'s car...', '小偷撞了警车后逃走了。');
INSERT INTO `words` VALUES (6634, 'ramble', 'ˈræmbl', 'v.', '漫游，漫步，闲逛(尤指在乡间);漫谈;闲聊;', '...an hour\'s ramble through the woods.', '一个小时的林间漫步');
INSERT INTO `words` VALUES (6635, 'rambling', 'ˈræmblɪŋ', 'adj.', '向四处延伸的;规划凌乱的;冗长而含糊的;', '...that rambling house and its bizarre contents.', '那间格局不规则的房子及其奇异的室内摆设');
INSERT INTO `words` VALUES (6636, 'rambunctious', 'ræmˈbʌŋkʃəs', 'adj.', '喧闹的;欢闹的;', '...a very rambunctious and energetic class.', '极其喧闹活跃的课堂');
INSERT INTO `words` VALUES (6637, 'ramification', 'ˌræmɪfɪˈkeɪʃn', 'n.', '(众多复杂而又难以预料的)结果，后果;', 'The book analyses the social and political ramifications of AIDS for the gay community.', '这本书分析了艾滋病给同性恋群体所带来的社会和政治影响。');
INSERT INTO `words` VALUES (6638, 'ramp', 'ræmp', 'n.', '斜坡;坡道;(装车或上下飞机的)活动梯，活动坡道;', 'Lillian was coming down the ramp from the museum.', '莉莲正从博物馆那边的斜坡上走下来。');
INSERT INTO `words` VALUES (6639, 'rampant', 'ˈræmpənt', 'adj.', '泛滥的;猖獗的;疯长的;', 'Inflation is rampant and industry in decline.', '通胀势头严重，工业不断下滑。');
INSERT INTO `words` VALUES (6640, 'rampart', 'ˈræmpɑːt', 'n.', '壁垒;城墙;', '...a walk along the ramparts of the Old City.', '沿着老城的城墙漫步');
INSERT INTO `words` VALUES (6641, 'ramshackle', 'ˈræmʃækl', 'adj.', '摇摇欲坠的;破烂不堪的;组织松散(不能持久)的;', 'They entered the shop, which was a curious ramshackle building.', '他们进了商店，那是幢年久失修的奇特建筑。');
INSERT INTO `words` VALUES (6642, 'ranch', 'rɑːntʃ', 'n.', '牧场，大农场(尤指北美或澳大利亚的);', 'He lives on a cattle ranch in Australia.', '他在澳大利亚靠一个牧牛场为生。');
INSERT INTO `words` VALUES (6643, 'rancher', 'ˈrɑːntʃə(r)', 'n.', '大农场(或牧场)主;大农场(或牧场)工人;', '...a cattle rancher.', '牧牛场场主');
INSERT INTO `words` VALUES (6644, 'rancid', 'ˈrænsɪd', 'adj.', '(含油食品)变质的，变味的，哈喇的;', 'Butter is perishable and can go rancid.', '黄油很容易变质，而且会发臭。');
INSERT INTO `words` VALUES (6645, 'rancorous', 'ˈræŋkərəs', 'adj.', '充满怨恨的;', 'The deal ended after a series of rancorous disputes.', '经过一连串充满敌意的争吵，交易泡汤了。');
INSERT INTO `words` VALUES (6646, 'random', 'ˈrændəm', 'adj.', '随机的，随意的(非事先决定或不规则);', 'The survey used a random sample of two thousand people across England and Wales...', '该调查在英格兰和威尔士随机抽样了2,000人。');
INSERT INTO `words` VALUES (6647, 'randomly', '\'rændəmli', 'adv.', '随机;随意;未加计划地;', 'The survey used a random sample of two thousand people across England and Wales...', '该调查在英格兰和威尔士随机抽样了2,000人。');
INSERT INTO `words` VALUES (6648, 'randomness', 'ˈrændəmnəs', 'n.', '随机性;随意，无安排;', 'The survey used a random sample of two thousand people across England and Wales...', '该调查在英格兰和威尔士随机抽样了2,000人。');
INSERT INTO `words` VALUES (6649, 'randy', 'ˈrændi', 'adj.', '性兴奋的;性欲冲动的;', 'It was extremely hot and I was feeling rather randy.', '场面非常刺激撩人，我感到欲火难耐。');
INSERT INTO `words` VALUES (6650, 'range', 'reɪndʒ', 'n.', '一系列;(变动或浮动的)范围，界限，区间;类;', 'A wide range of colours and patterns are available...', '各种颜色和样式都有。');
INSERT INTO `words` VALUES (6651, 'ranger', 'ˈreɪndʒə(r)', 'n.', '园林管理员;护林人;(英国14至19岁的)女童子军;', 'Bill Justice is a park ranger at the Carlsbad Caverns National Park.', '比尔·贾斯蒂斯是卡尔斯巴德洞窟国家公园的管理员。');
INSERT INTO `words` VALUES (6652, 'rank', 'ræŋk', 'n.', '(尤指较高的)地位，级别;军衔;军阶;', 'He eventually rose to the rank of captain...', '他终于升职当上了船长。');
INSERT INTO `words` VALUES (6653, 'ranking', 'ˈræŋkɪŋ', 'n.', '地位，排名，排位(尤指在体育运动中);(某项体育运动的)最佳运动员排名表;', '...the 25 leading teams in the world rankings...', '世界排名前25的队');
INSERT INTO `words` VALUES (6654, 'rankle', 'ˈræŋkl', 'v.', '使人耿耿于怀(或怨恨不已、痛苦不已);', 'They paid him only £10 for it and it really rankled...', '他们仅为此付给他10英镑，这真让他感到愤怒。');
INSERT INTO `words` VALUES (6655, 'ransack', 'ˈrænsæk', 'v.', '洗劫;(为找东西)把…翻腾得乱七八糟;', 'Demonstrators ransacked and burned the house where he was staying...', '示威者搜遍了他的住处，并放火烧了那里。');
INSERT INTO `words` VALUES (6656, 'ransom', 'ˈrænsəm', 'n.', '赎金;', 'Her kidnapper successfully extorted a £75,000 ransom for her release...', '绑匪成功地勒索了7.5万英镑才将她释放。');
INSERT INTO `words` VALUES (6657, 'rant', 'rænt', 'v.', '怒吼;咆哮;大声抱怨;', 'As the boss began to rant, I stood up and went out...', '老板开始咆哮的时候，我起身走了出去。');
INSERT INTO `words` VALUES (6658, 'rap', 'ræp', 'n.', '叩击;快速的敲击;说唱音乐(节奏快而强，配有快速念白的现代音乐);', 'For some people, rap — the music of the hip-hop generation — is just so much noise...', '对某些人来说，说唱乐——嘻哈一族的音乐——简直就是噪音。');
INSERT INTO `words` VALUES (6659, 'rapacious', 'rəˈpeɪʃəs', 'adj.', '贪婪的;贪欲的;强取的;', 'He had a rapacious appetite for bird\'s nest soup.', '他吃燕窝汤吃个没够。');
INSERT INTO `words` VALUES (6660, 'rapacity', 'rəˈpæsəti', 'n.', '贪婪;贪得无厌;', 'He argued that the overcrowded cities were the product of a system based on \'selfishness\' and \'rapacity\'.', '他认为，过于拥挤的城市是一个建立在“自私”和“贪婪”基础上的体系的产物。');
INSERT INTO `words` VALUES (6661, 'rape', 'reɪp', 'v.', '强奸;强暴;', 'A young woman was brutally raped in her own home...', '一名年轻女子在自己家中惨遭强暴。');
INSERT INTO `words` VALUES (6662, 'rapid', 'ˈræpɪd', 'adj.', '瞬间的;短时间内发生的;迅速的;', '...the country\'s rapid economic growth in the 1980\'s...', '20世纪80年代该国经济的迅速增长');
INSERT INTO `words` VALUES (6663, 'rapidly', '\'ræpɪdlɪ', 'adv.', '迅速;高速;急速地;', '...the country\'s rapid economic growth in the 1980\'s...', '20世纪80年代该国经济的迅速增长');
INSERT INTO `words` VALUES (6664, 'rapids', 'ˈræpɪdz', 'n.', '(河的)急流;湍急的河水;', 'His canoe was there, on the river below the rapids.', '他的独木舟在险滩下面的河水中。');
INSERT INTO `words` VALUES (6665, 'rapist', 'ˈreɪpɪst', 'n.', '强奸犯;强奸者;', 'The convicted murderer and rapist is scheduled to be executed next Friday.', '那名被判有罪的强奸杀人犯定在下周五处决。');
INSERT INTO `words` VALUES (6666, 'rapport', 'ræˈpɔː(r)', 'n.', '亲善;融洽;和谐;', 'He said he wanted \'to establish a rapport with the Indian people\'...', '他说他想“同印度人民建立友好关系”。');
INSERT INTO `words` VALUES (6667, 'rapprochement', 'ræˈprɒʃmɒ̃', 'n.', '友好关系的恢复;和解;', 'There have been growing signs of a rapprochement with Vietnam.', '同越南恢复友好关系的迹象越来越明显了。');
INSERT INTO `words` VALUES (6668, 'rapt', 'ræpt', 'adj.', '全神贯注的;专心致志的;', 'I noticed that everyone was watching me with rapt attention...', '我注意到大家都在专注地看着我。');
INSERT INTO `words` VALUES (6669, 'rapture', 'ˈræptʃə(r)', 'n.', '狂喜;欢天喜地;兴高采烈;', 'The film was shown to gasps of rapture at the Democratic Convention...', '在民主党大会上放映的电影使人们十分欣喜。');
INSERT INTO `words` VALUES (6670, 'rapturous', 'ˈræptʃərəs', 'adj.', '兴高采烈的;狂喜的;热烈的;', 'The students gave him a rapturous welcome...', '学生们欢天喜欢地迎接他。');
INSERT INTO `words` VALUES (6671, 'rare', 'reə(r)', 'adj.', '稀少的;稀罕的;珍贵的;', '...the black-necked crane, one of the rarest species in the world...', '黑颈鹤，世界最稀有的物种之一');
INSERT INTO `words` VALUES (6672, 'rarefied', 'ˈreərɪfaɪd', 'adj.', '高深精妙的;曲高和寡的;稀薄的;', '...the fear and loneliness that working class students can feel when they are plunged into the rarefied atmosphere of university...', '工人阶级出身的学生面对大学静雅高深的氛围时产生的恐惧和孤独');
INSERT INTO `words` VALUES (6673, 'rarely', 'ˈreəli', 'adv.', '罕有;很少;不常;', 'They battled against other Indian tribes, but rarely fought with the whites...', '他们同其他印第安部落作战，但很少与白人并肩对敌。');
INSERT INTO `words` VALUES (6674, 'rarity', 'ˈreərəti', 'n.', '珍品;稀有物;稀有;', 'Motorized wheelchairs are a rarity here...', '电动轮椅在这里很罕见。');
INSERT INTO `words` VALUES (6675, 'rascal', 'ˈrɑːskl', 'n.', '无赖;坏蛋;淘气鬼;', 'What\'s that old rascal been telling you?', '那个老家伙一直在跟你说些什么？');
INSERT INTO `words` VALUES (6676, 'rash', 'ræʃ', 'n.', '皮疹;疹;大量;', 'It would be rash to rely on such evidence...', '依靠这样的证据是草率的。');
INSERT INTO `words` VALUES (6677, 'rashly', 'ˈræʃli', 'adv.', '贸然;鲁莽地，轻率地，仓促地;', 'It would be rash to rely on such evidence...', '依靠这样的证据是草率的。');
INSERT INTO `words` VALUES (6678, 'raspy', 'ˈrɑːspi', 'adj.', '沙哑刺耳的;', 'Both men sang in a deep, raspy tone...', '两个男人的歌声都很低沉、沙哑。');
INSERT INTO `words` VALUES (6679, 'rat', 'ræt', 'n.', '老鼠;耗子;讨厌的人;', 'This was demonstrated in a laboratory experiment with rats.', '在实验室用老鼠进行的实验证明了这一点。');
INSERT INTO `words` VALUES (6680, 'rate', 'reɪt', 'n.', '速度;进度;比率;', 'The rate at which hair grows can be agonisingly slow...', '头发生长的速度可能极其缓慢。');
INSERT INTO `words` VALUES (6681, 'rated', 'ˈreɪtɪd', 'v.', '评估;评价;估价;', 'The rate at which hair grows can be agonisingly slow...', '头发生长的速度可能极其缓慢。');
INSERT INTO `words` VALUES (6682, 'rather', 'ˈrɑːðə(r) , ˌrɑːˈðɜː(r)', 'adv.', '(常用于表示轻微的批评、失望或惊讶)相当，在某种程度上;(与动词连用以减弱语气)有点儿，稍微;(纠正所说的话或提供更确切的信息)更确切地讲，更准确地说;', 'The problem was psychological rather than physiological...', '问题是心理方面的而不是生理方面的。');
INSERT INTO `words` VALUES (6683, 'ratification', 'ˌrætɪfɪˈkeɪʃn', 'n.', '批准;', 'The EU will now complete ratification of the treaty by June 1.', '欧盟将于6月1日前完成条约的批准。');
INSERT INTO `words` VALUES (6684, 'ratify', 'ˈrætɪfaɪ', 'v.', '正式批准;使正式生效;', 'The parliaments of Australia and Indonesia have yet to ratify the treaty.', '澳大利亚和印度尼西亚的国会尚未批准该条约。');
INSERT INTO `words` VALUES (6685, 'rating', 'ˈreɪtɪŋ', 'n.', '等级;级别;收视率;', '...a value-for-money rating of ten out of ten...', '被评定为100%物有所值');
INSERT INTO `words` VALUES (6686, 'ratio', 'ˈreɪʃiəʊ', 'n.', '比率;比例;', 'In 1978 there were 884 students at a lecturer/student ratio of 1:15...', '1978年有884名学生，师生比为1比15。');
INSERT INTO `words` VALUES (6687, 'ration', 'ˈræʃn', 'n.', '(食品、燃料等短缺时的)配给量，定量;(给战士或食品短缺地区的人提供的)定量口粮;正常量;', 'The meat ration was down to one pound per person per week...', '肉的配给量降到了每人每周一磅。');
INSERT INTO `words` VALUES (6688, 'rational', 'ˈræʃnəl', 'adj.', '合理的;理性的;明智的;', 'He\'s asking you to look at both sides of the case and come to a rational decision...', '他是要求你看到问题的两面后再作出理性的决定。');
INSERT INTO `words` VALUES (6689, 'rationale', 'ˌræʃəˈnɑːl', 'n.', '基本原理;根本原因;', 'However, the rationale for such initiatives is not, of course, solely economic...', '然而，这种提议的根据当然并不仅仅是经济方面的。');
INSERT INTO `words` VALUES (6690, 'rationalism', 'ˈræʃnəlɪzəm', 'n.', '理性主义;唯理论;', 'Coleridge was to spend the next thirty years attacking rationalism.', '柯尔律治在之后的30年里一直抨击理性主义。');
INSERT INTO `words` VALUES (6691, 'rationality', 'ˌræʃə\'nælətɪ', 'n.', '合理性;推理力;理由;', 'He\'s asking you to look at both sides of the case and come to a rational decision...', '他是要求你看到问题的两面后再作出理性的决定。');
INSERT INTO `words` VALUES (6692, 'rationally', '\'ræʃ(ə)n(ə)li', 'adv.', '理性地;', 'He\'s asking you to look at both sides of the case and come to a rational decision...', '他是要求你看到问题的两面后再作出理性的决定。');
INSERT INTO `words` VALUES (6693, 'rationing', 'ˈræʃnɪŋ , ˈræʃənɪŋ', 'n.', '定量配给政策;配给制;', 'The municipal authorities here are preparing for food rationing.', '这里的市政当局正准备实施食物配给制度。');
INSERT INTO `words` VALUES (6694, 'rattan', 'ræˈtæn', 'n.', '藤(东南亚蔓生植物，茎干多用于做家具);', '...a light airy room set with cloth-covered tables and rattan chairs.', '放置着铺有桌布的桌子和藤条椅的敞亮的房间');
INSERT INTO `words` VALUES (6695, 'rattle', 'ˈrætl', 'v.', '(使)发出咔嗒咔嗒的声音;(运行时)发出连续短促的高声;使紧张;', 'She slams the kitchen door so hard I hear dishes rattle...', '她把厨房门重重地关上，我甚至听到了碟子的震颤声。');
INSERT INTO `words` VALUES (6696, 'rattlesnake', 'ˈrætlsneɪk', 'n.', '响尾蛇(产于美洲);', 'He had been bitten by a rattlesnake.', '他被响尾蛇咬过。');
INSERT INTO `words` VALUES (6697, 'raucous', 'ˈrɔːkəs', 'adj.', '刺耳的;尖厉的;', 'They heard a bottle being smashed, then more raucous laughter.', '他们听见酒瓶摔碎的声音，然后是一阵更喧闹的笑声。');
INSERT INTO `words` VALUES (6698, 'ravage', 'ˈrævɪdʒ', 'v.', '毁坏;损坏;严重损害;', 'For two decades the country has been ravaged by civil war and foreign intervention.', '20年来，这个国家一直被内战外侵所蹂躏。');
INSERT INTO `words` VALUES (6699, 'rave', 'reɪv', 'v.', '热烈谈论(或书写);(热情洋溢地)奋笔疾书;咆哮;', 'She cried and raved for weeks, and people did not know what to do...', '她哭喊了好几个星期，人们不知如何是好。');
INSERT INTO `words` VALUES (6700, 'ravenous', 'ˈrævənəs', 'adj.', '极其饥饿的;严重的;十分的;', 'Amy realized that she had eaten nothing since leaving Bruton Street, and she was ravenous.', '埃米意识到自己从离开布鲁顿大街之后什么东西都没吃，她饿极了。');
INSERT INTO `words` VALUES (6701, 'ravine', 'rəˈviːn', 'n.', '沟壑;溪谷;', 'The bus is said to have overturned and fallen into a ravine.', '据说巴士发生翻车，跌进了峡谷。');
INSERT INTO `words` VALUES (6702, 'ravish', 'ˈrævɪʃ', 'v.', '强暴;强奸;使狂喜;', 'She\'ll never know how close she came to being dragged off and ravished.', '她将永远不知道她差点就被人拖走强暴了。');
INSERT INTO `words` VALUES (6703, 'ravishing', 'ˈrævɪʃɪŋ', 'adj.', '极其美丽的;', 'She looked ravishing.', '她看起来漂亮极了。');
INSERT INTO `words` VALUES (6704, 'raw', 'rɔː', 'adj.', '生的;未烹制的;未煮的;', 'We import raw materials and energy and export mainly industrial products.', '我们进口原材料和能源，主要出口工业产品。');
INSERT INTO `words` VALUES (6705, 'rawhide', 'ˈrɔːhaɪd', 'n.', '生皮;未经加工的皮革;', 'At his belt he carried a rawhide whip.', '他腰间别着生牛皮制成的鞭子。');
INSERT INTO `words` VALUES (6706, 'ray', 'reɪ', 'n.', '光线;(热或其他能量的)射线;(好事或所希望事物的)一点，少量;', '...the first rays of light spread over the horizon...', '地平线上出现的第一缕曙光');
INSERT INTO `words` VALUES (6707, 'raze', 'reɪz', 'v.', '彻底摧毁;将…夷为平地;', 'Dozens of villages have been razed...', '几十个村庄被夷为平地。');
INSERT INTO `words` VALUES (6708, 're', 'reɪ , riː', 'prep.', '(用于商业信函等开头介绍主题或回复电子邮件)关于，事由;', 'Dear Mrs Cox, Re: Household Insurance. We note from our files that we have not yet received your renewal instructions.', '亲爱的考克斯夫人，关于房屋保险一事，我们的文件显示尚未收到您的续保通知。');
INSERT INTO `words` VALUES (6709, 'reach', 'riːtʃ', 'v.', '到达;抵达;引起…的注意;', 'He did not stop until he reached the door...', '他走到门边才停下。');
INSERT INTO `words` VALUES (6710, 'react', 'riˈækt', 'v.', '起反应;(对…)作出反应;回应;', 'They reacted violently to the news...', '他们对这条新闻反应强烈。');
INSERT INTO `words` VALUES (6711, 'reaction', 'riˈækʃn', 'n.', '反应;回应;(对旧观念等的)抗拒;', 'Reaction to the visit is mixed...', '对这次访问的反应不一。');
INSERT INTO `words` VALUES (6712, 'reactionary', 'riˈækʃənri', 'n.', '反动分子;反对政治(或社会)变革者;', 'It grew ever more clear to everyone that the Minister was too reactionary, too blinkered...', '大家都越来越清楚部长太保守、心胸太狭窄。');
INSERT INTO `words` VALUES (6713, 'reactivate', 'riˈæktɪveɪt', 'v.', '使恢复活动;使重新出现;', '...a series of economic reforms to reactivate the economy...', '一系列旨在重振经济的经济改革');
INSERT INTO `words` VALUES (6714, 'reactive', 'riˈæktɪv', 'adj.', '反应的;有反应的;回应的;', 'Ozone is a highly reactive form of oxygen gas.', '臭氧是一种非常活跃的氧气形态。');
INSERT INTO `words` VALUES (6715, 'readable', 'ˈriːdəbl', 'adj.', '可读性强的;通俗易懂的;清晰可辨的;', 'This is an impeccably researched and very readable book.', '这本书中的研究缜密透彻，可读性很强。');
INSERT INTO `words` VALUES (6716, 'reader', 'ˈriːdə(r)', 'n.', '读者;读书…的人;阅读量大的人;', 'If you are a regular reader of Homes & Gardens you will know what an invaluable source of inspiration it is...', '如果你常读《家和花园》杂志的话，就会知道它是一个多么宝贵的灵感源泉。');
INSERT INTO `words` VALUES (6717, 'readership', 'ˈriːdəʃɪp', 'n.', '(统称某报刊杂志等的)读者;(英国大学)准教授职位;', 'Its readership has grown to over 15,000 subscribers...', '它的订阅人数上升到了15,000多人。');
INSERT INTO `words` VALUES (6718, 'readily', 'ˈredɪli', 'adv.', '快捷地;轻而易举地;便利地;', 'I asked her if she would allow me to interview her, and she readily agreed...', '我问她是否允许我采访她，她欣然同意了。');
INSERT INTO `words` VALUES (6719, 'readiness', 'ˈredinəs', 'n.', '准备就绪;愿意;乐意;', '...their readiness to co-operate with the new US envoy.', '他们乐意和新任美国特使合作');
INSERT INTO `words` VALUES (6720, 'reading', 'ˈriːdɪŋ', 'n.', '阅读;读书活动;宣读;', 'I have always loved reading.', '我一直喜欢读书。');
INSERT INTO `words` VALUES (6721, 'readjust', 'ˌriːəˈdʒʌst', 'v.', '重新适应;再适应;稍作改变;', 'I can understand why astronauts find it difficult to readjust to life on earth...', '我能够理解为什么宇航员发现重新适应地球上的生活不容易。');
INSERT INTO `words` VALUES (6722, 'readout', '', 'n.', '(电子测量仪器的)信息读出，读数;', 'The system provides a digital readout of the vehicle\'s speed...', '这个系统提供车辆速度的电子读数。');
INSERT INTO `words` VALUES (6723, 'ready', 'ˈredi', 'adj.', '准备好;准备完毕;已完成;', 'It took her a long time to get ready for church...', '她花了很长时间才做好去教堂的准备。');
INSERT INTO `words` VALUES (6724, 'ready-made', 'ˌredi ˈmeɪd', 'adj.', '预制的;已做好的;现成的;', 'We rely quite a bit on ready-made meals — they are so convenient...', '我们很是依赖现成食物——它们真的很方便。');
INSERT INTO `words` VALUES (6725, 'reaffirm', 'ˌriːəˈfɜːm', 'v.', '重申;再次确定;', 'He reaffirmed his commitment to the country\'s economic reform programme...', '他重申了实施国家经济改革计划的决心。');
INSERT INTO `words` VALUES (6726, 'real', 'ˈriːəl', 'adj.', '真实的;实际存在的;非凭空想象的;', 'No, it wasn\'t a dream. It was real...', '不，它不是一个梦，它是真实的。');
INSERT INTO `words` VALUES (6727, 'realism', 'ˈriːəlɪzəm', 'n.', '务实作风;现实主义方式;现实性;', 'It was time now to show more political realism...', '现在是拿出更多政治务实精神的时候了。');
INSERT INTO `words` VALUES (6728, 'realist', 'ˈriːəlɪst', 'n.', '现实主义者;务实的人;现实主义作家(或画家等);', 'I see myself not as a cynic but as a realist...', '我觉得自己不是一个愤世嫉俗的人，而是一个现实主义者。');
INSERT INTO `words` VALUES (6729, 'realistic', 'ˌriːəˈlɪstɪk', 'adj.', '现实的;实际的;实事求是的;', 'Police have to be realistic about violent crime...', '警方需要对暴力犯罪持务实态度。');
INSERT INTO `words` VALUES (6730, 'realistically', 'ˌriːəˈlɪstɪkli', 'adv.', '(表明你认为实际实现的可能性)如实地，实际地;切合实际地;明智地;', 'Realistically, there is never one right answer.', '说实在的，永远没有一个正确答案。');
INSERT INTO `words` VALUES (6731, 'reality', 'riˈæləti', 'n.', '现实;实际情况;事实;', 'Fiction and reality were increasingly blurred...', '虚构和现实越来越难以区分了。');
INSERT INTO `words` VALUES (6732, 'really', 'ˈriːəli', 'adv.', '(表明事实或真相)事实上，真正地，真实地;(强调观点等)确实，的确;加强形容词或副词的语气;', 'I\'m very sorry. I really am...', '我很抱歉。真的。');
INSERT INTO `words` VALUES (6733, 'realm', 'relm', 'n.', '领域;场所;王国;', '...the realm of politics...', '政治领域');
INSERT INTO `words` VALUES (6734, 'real-time', 'ˌriːəl ˈtaɪm', 'adj.', '(计算机处理过程)实时的;', '...real-time language translations.', '实时语言转换');
INSERT INTO `words` VALUES (6735, 'ream', 'riːm', 'n.', '大量的文字(或写作);令(纸张的记数单位，等于500张);', 'Their specific task is to sort through the reams of information and try to determine what it may mean...', '他们的具体任务就是将大量信息分类并努力弄清它们的意思。');
INSERT INTO `words` VALUES (6736, 'reap', 'riːp', 'v.', '取得(成果);收获;收割(庄稼);', 'You\'ll soon begin to reap the benefits of being fitter...', '你很快就会体会到身体更健康带来的好处。');
INSERT INTO `words` VALUES (6737, 'reappear', 'ˌriːəˈpɪə(r)', 'v.', '再次出现;重新出现;', 'Thirty seconds later she reappeared and beckoned them forward.', '30秒后她又出现了，示意他们往前来。');
INSERT INTO `words` VALUES (6738, 'rear', 'rɪə(r)', 'n.', '后部;屁股;臀部;', 'He settled back in the rear of the taxi.', '他靠坐在出租车的后排座上。');
INSERT INTO `words` VALUES (6739, 'rearrange', 'ˌriːəˈreɪndʒ', 'v.', '重新排列;改变位置;重新安排;', 'When she returned, she found Malcolm had rearranged all her furniture...', '她回家时，发现马尔科姆把她所有的家具都重新摆放了。');
INSERT INTO `words` VALUES (6740, 'reason', 'ˈriːzn', 'n.', '原因;理由;解释;', 'There is a reason for every important thing that happens...', '每件重要事情的发生都有原因。');
INSERT INTO `words` VALUES (6741, 'reasonable', 'ˈriːznəbl', 'adj.', '公平的;合理的;有理由的;', 'He\'s a reasonable sort of chap...', '他是个理智的家伙。');
INSERT INTO `words` VALUES (6742, 'reasonably', 'ˈriːznəbli', 'adv.', '尚可;过得去;合乎逻辑地;', 'He\'s a reasonable sort of chap...', '他是个理智的家伙。');
INSERT INTO `words` VALUES (6743, 'reasoning', 'ˈriːznɪŋ , ˈriːzənɪŋ', 'n.', '推想;推理;理性的观点;', '...the reasoning behind the decision...', '决定背后的论证');
INSERT INTO `words` VALUES (6744, 'reassemble', 'ˌriːəˈsembl', 'v.', '重新装配(或组装);重新集结;再次集合;', 'We will now try to reassemble pieces of the wreckage...', '我们现在会尽量把残骸重新组合起来。');
INSERT INTO `words` VALUES (6745, 'reassessment', 'ˌriːəˈsesmənt', 'n.', '重新考虑;重新评估;', '...the moment when we make a reassessment of ourselves.', '我们重新审视自己的时刻');
INSERT INTO `words` VALUES (6746, 'reassurance', 'ˌriːəˈʃʊərəns', 'n.', '(能消除疑虑等的)肯定;能消除疑虑的说话(或行动);保证;', 'She needed reassurance that she belonged somewhere...', '她需要一种归属感让她安心。');
INSERT INTO `words` VALUES (6747, 'reassure', 'ˌriːəˈʃʊə(r)', 'v.', '使…安心;打消…的疑虑;', 'I tried to reassure her, \'Don\'t worry about it. We won\'t let it happen again.\'...', '我试图使她安心，“不要担心。我们不会让它再次发生了。”');
INSERT INTO `words` VALUES (6748, 'reassuring', 'ˌriːəˈʃʊərɪŋ', 'adj.', '令人感到宽慰的;令人放心的;', 'It was reassuring to hear John\'s familiar voice...', '听到约翰熟悉的声音让人放心。');
INSERT INTO `words` VALUES (6749, 'rebate', 'ˈriːbeɪt', 'n.', '退还款;返还(退还的部分货价);折扣;', '...a tax rebate...', '退税');
INSERT INTO `words` VALUES (6750, 'rebellion', 'rɪˈbeljən', 'n.', '谋反;叛乱;反叛;', 'The British soon put down the rebellion.', '英国人很快平息了叛乱。');
INSERT INTO `words` VALUES (6751, 'rebellious', 'rɪˈbeljəs', 'adj.', '反叛的;叛逆的;桀骜不驯的;', '...a rebellious teenager...', '叛逆少年');
INSERT INTO `words` VALUES (6752, 'rebirth', 'ˌriːˈbɜːθ', 'n.', '新生;复活;复兴;', '...the rebirth of democracy in Latin America...', '民主在拉丁美洲的复兴');
INSERT INTO `words` VALUES (6753, 'reborn', 'ˌriːˈbɔːn', 'v.', '复兴;再次流行;再生;', 'Shilling has been reborn as an artist...', '希林以艺术家的身份再次出现。');
INSERT INTO `words` VALUES (6754, 'rebuff', 'rɪˈbʌf', 'n.', '粗暴回绝;生硬的拒绝;', 'He wanted sex with Julie but she rebuffed him...', '他想和朱莉上床但被她拒绝了。');
INSERT INTO `words` VALUES (6755, 'rebuild', 'ˌriːˈbɪld', 'v.', '重建;重组;重新装配;', 'They say they will stay to rebuild their homes rather than retreat to refugee camps...', '他们说他们将留下重建家园而不是躲到难民营里。');
INSERT INTO `words` VALUES (6756, 'rebuke', 'rɪˈbjuːk', 'v.', '指责;批评;', 'The president rebuked the House and Senate for not passing those bills within 100 days.', '总统谴责参众两院没有在100天之内通过那些法案。');
INSERT INTO `words` VALUES (6757, 'rebut', 'rɪˈbʌt', 'v.', '反驳;驳斥;证明(言论等)错误;', 'He spent most of his speech rebutting criticisms of his foreign policy.', '他讲演的大部分时间都在反驳对他外交政策的批评。');
INSERT INTO `words` VALUES (6758, 'rebuttal', 'rɪˈbʌtl', 'n.', '辩驳;反驳;驳斥;', 'He is conducting a point-by-point rebuttal of charges from former colleagues...', '他正在对前同事的指责进行逐条反驳。');
INSERT INTO `words` VALUES (6759, 'recalcitrant', 'rɪˈkælsɪtrənt', 'adj.', '不守规章的;不服从指挥的;桀骜不驯的;', 'The danger is that recalcitrant local authorities will reject their responsibilities...', '危险在于抗命不遵的地方当局将拒绝履行他们的职责。');
INSERT INTO `words` VALUES (6760, 'recant', 'rɪˈkænt', 'v.', '公开宣布放弃(原先的信仰、观点等);', 'White House officials ordered Williams to recant.', '白宫官员命令威廉斯公开宣布放弃自己的观点。');
INSERT INTO `words` VALUES (6761, 'recapitulate', 'ˌriːkəˈpɪtʃuleɪt', 'v.', '重述;概括;', 'Let\'s just recapitulate the essential points...', '我们再来回顾一下要点。');
INSERT INTO `words` VALUES (6762, 'recapture', 'ˌriːˈkæptʃə(r)', 'v.', '赢回;夺回;抓回;', 'They said the bodies were found when rebels recaptured the area.', '他们说尸体是在叛军重新占领这个地区时发现的。');
INSERT INTO `words` VALUES (6763, 'recede', 'rɪˈsiːd', 'v.', '逐渐远离;渐渐远去;逐渐减弱;', 'Luke\'s footsteps receded into the night...', '卢克在夜色中渐行渐远。');
INSERT INTO `words` VALUES (6764, 'receipt', 'rɪˈsiːt', 'n.', '收据;收条;接收;', 'I wrote her a receipt for the money.', '我给她写了一张收钱的收条。');
INSERT INTO `words` VALUES (6765, 'receive', 'rɪˈsiːv', 'v.', '拿到;接到;收到;', 'They will receive their awards at a ceremony in Stockholm...', '他们会在斯德哥尔摩举行的仪式上接受颁奖。');
INSERT INTO `words` VALUES (6766, 'received', 'rɪˈsiːvd', 'adj.', '被承认的;被一致认可的;', 'He was among the first to question the received wisdom of the time...', '他是那个时代首先质疑当时被普遍认可的处世之道的人之一。');
INSERT INTO `words` VALUES (6767, 'receiving', 'rɪˈsiːvɪŋ', 'v.', '拿到;接到;收到;', 'They will receive their awards at a ceremony in Stockholm...', '他们会在斯德哥尔摩举行的仪式上接受颁奖。');
INSERT INTO `words` VALUES (6768, 'recent', 'ˈriːsnt', 'adj.', '近来的;新近的;', 'In the most recent attack one man was shot dead and two others were wounded...', '在最近的一次袭击中，一名男子遇袭身亡，另外两人受伤。');
INSERT INTO `words` VALUES (6769, 'recently', 'ˈriːsntli', 'adv.', '不久前;最近;', 'The bank recently opened a branch in Germany...', '这家银行最近在德国开设了分行。');
INSERT INTO `words` VALUES (6770, 'reception', 'rɪˈsepʃn', 'n.', '接待处;接待区;接待仪式;', 'Have him bring a car round to the reception...', '让他把车开到前台来。');
INSERT INTO `words` VALUES (6771, 'receptive', 'rɪˈseptɪv', 'adj.', '(对新观点、建议等)愿意倾听的，乐于接受的;', 'The voters had seemed receptive to his ideas...', '选民看上去很愿意接受他的想法。');
INSERT INTO `words` VALUES (6772, 'receptor', 'rɪˈseptə(r)', 'n.', '感受器;受体;', '...the information receptors in our brain.', '我们大脑里的信息感受器');
INSERT INTO `words` VALUES (6773, 'recess', 'ˈriːses , rɪˈses', 'n.', '休会期;休庭;壁龛;', 'The conference broke for a recess...', '会议暂时休会。');
INSERT INTO `words` VALUES (6774, 'recession', 'rɪˈseʃn', 'n.', '经济衰退;经济萎缩;退后;', 'The recession caused sales to drop off...', '经济不景气使销量下降。');
INSERT INTO `words` VALUES (6775, 'recessive', 'rɪˈsesɪv', 'adj.', '隐性的;', 'Sickle-cell anaemia is passed on through a recessive gene.', '镰状细胞贫血通过隐性基因遗传给后代。');
INSERT INTO `words` VALUES (6776, 'recharge', 'ˌriːˈtʃɑːdʒ', 'v.', '给(电池)充电;充电;恢复体力;', 'He is using your mains electricity to recharge his car battery.', '他在用你家里的电源给他的车载蓄电池充电。');
INSERT INTO `words` VALUES (6777, 'recipe', 'ˈresəpi', 'n.', '烹饪法;食谱;方法;', '...a traditional recipe for oatmeal biscuits.', '燕麦饼干的传统制作配方');
INSERT INTO `words` VALUES (6778, 'recipient', 'rɪˈsɪpiənt', 'n.', '受方;接受者;', '...the largest recipient of American foreign aid...', '美国对外援助的最大受惠者');
INSERT INTO `words` VALUES (6779, 'reciprocal', 'rɪˈsɪprəkl', 'adj.', '互惠的;相应的;', 'They expected a reciprocal gesture before more hostages could be freed...', '在更多人质获得自由之前他们期望看到对方也有相应的表示。');
INSERT INTO `words` VALUES (6780, 'reciprocate', 'rɪˈsɪprəkeɪt', 'v.', '回报;回应;沿直线往复移动;', '...he reciprocated Mr Prescott\'s good wishes...', '他对普雷斯科特先生的良好祝愿报以同样的祝福');
INSERT INTO `words` VALUES (6781, 'reciprocity', 'ˌresɪˈprɒsəti', 'n.', '互惠;互助;互换;', 'They gave assurances they would press for reciprocity with Greece in the issuing of visas.', '他们保证将推进本国与希腊两国间签证的互相发放。');
INSERT INTO `words` VALUES (6782, 'recital', 'rɪˈsaɪtl', 'n.', '音乐演奏会;诗歌朗诵会;(口述)逐一列举;', '...a solo recital by the harpsichordist Maggie Cole.', '大键琴演奏家玛吉·科尔的个人独奏会');
INSERT INTO `words` VALUES (6783, 'recitation', 'ˌresɪˈteɪʃn', 'n.', '朗诵;朗读;逐一列举;', 'The transmission began with a recitation from the Koran.', '节目一开始是一段《古兰经》朗诵。');
INSERT INTO `words` VALUES (6784, 'recite', 'rɪˈsaɪt', 'v.', '(尤指对听众)背诵，吟诵，朗诵;(口头)列举;逐一讲述;', 'They recited poetry to one another.', '他们互相朗诵诗歌。');
INSERT INTO `words` VALUES (6785, 'reckless', 'ˈrekləs', 'adj.', '鲁莽的;不计后果的;无所顾忌的;', 'She loved to ride; on horseback, she was reckless and utterly without fear...', '她喜欢骑马；在马背上，她纵横驰骋，毫无畏惧。');
INSERT INTO `words` VALUES (6786, 'recklessly', 'ˈrɛklɪsli', 'adv.', '不顾一切;鲁莽地;一刻也不停顿;', 'She loved to ride; on horseback, she was reckless and utterly without fear...', '她喜欢骑马；在马背上，她纵横驰骋，毫无畏惧。');
INSERT INTO `words` VALUES (6787, 'reckon', 'ˈrekən', 'v.', '想;认为;被普遍认为是;', 'Toni reckoned that it must be about three o\'clock...', '托妮估计当时肯定是3点钟左右。');
INSERT INTO `words` VALUES (6788, 'reckoning', 'ˈrekənɪŋ', 'n.', '估计;估算;计算;', 'By my reckoning we were seven or eight kilometres from Borj Mechaab.', '据我估计我们离梅卡巴堡有七八公里远。');
INSERT INTO `words` VALUES (6789, 'reclaim', 'rɪˈkleɪm', 'v.', '取回;拿回;要求归还;', 'In 1986, they got the right to reclaim South African citizenship...', '1986年，他们取得了恢复南非公民身份的权利。');
INSERT INTO `words` VALUES (6790, 'reclamation', 'ˌrekləˈmeɪʃn', 'n.', '(荒地等的)开垦，改造;', '...centuries of sea-wall construction and the reclamation of dry land from the marshes...', '几个世纪以来建造海堤并将沼泽地改造成为良田的行动');
INSERT INTO `words` VALUES (6791, 'recline', 'rɪˈklaɪn', 'v.', '斜倚;斜躺;向后倚靠;', 'She proceeded to recline on a chaise longue...', '她接着躺在了一个躺椅上。');
INSERT INTO `words` VALUES (6792, 'recluse', 'rɪˈkluːs', 'n.', '隐居者;喜欢独处的人;', 'His widow became a virtual recluse for the remainder of her life.', '他的寡妻孤寂地度过了余生。');
INSERT INTO `words` VALUES (6793, 'recognition', 'ˌrekəɡˈnɪʃn', 'n.', '认出;认识;识别;', 'George said, \'Ida, how are you?\' She frowned for a moment and then recognition dawned. \'George Black. Well, I never.\'...', '乔治说：“艾达，你好吗？”她皱了一会儿眉头，然后才认出他。“乔治·布莱克，噢，我一直都不好。”');
INSERT INTO `words` VALUES (6794, 'recollect', 'ˌrekəˈlekt', 'v.', '记起;回忆起;记得;', 'Ramona spoke with warmth when she recollected the doctor who used to be at the county hospital...', '拉蒙纳回忆起曾在乡村医院工作的那位医生时，言语中透着温情。');
INSERT INTO `words` VALUES (6795, 'recollection', 'ˌrekəˈlekʃn', 'n.', '记忆力;回忆;记忆;', 'Pat has vivid recollections of the trip...', '帕特清清楚楚地记得那次旅行。');
INSERT INTO `words` VALUES (6796, 'recommend', 'ˌrekəˈmend', 'v.', '推荐;举荐;介绍;', 'I have just spent a holiday there and would recommend it to anyone...', '我刚刚在那里度过假，很想把它推荐给每一个人。');
INSERT INTO `words` VALUES (6797, 'recommendation', 'ˌrekəmenˈdeɪʃn', 'n.', '正式建议;提议;推荐;', 'The committee\'s recommendations are unlikely to be made public...', '委员会的建议不大可能公开。');
INSERT INTO `words` VALUES (6798, 'recommended', 'ˌrekəˈmendɪd', 'v.', '推荐;举荐;介绍;', 'I have just spent a holiday there and would recommend it to anyone...', '我刚刚在那里度过假，很想把它推荐给每一个人。');
INSERT INTO `words` VALUES (6799, 'recompense', 'ˈrekəmpens', 'n.', '赔偿;补偿;报酬;', 'He demands no financial recompense for his troubles...', '他没有对自己遭遇的麻烦要求经济赔偿。');
INSERT INTO `words` VALUES (6800, 'reconcile', 'ˈrekənsaɪl', 'v.', '使和谐一致;调和;使配合;', 'It\'s difficult to reconcile the demands of my job and the desire to be a good father...', '我很难做到既兼顾工作，又达成做个好父亲的愿望。');
INSERT INTO `words` VALUES (6801, 'reconciliation', 'ˌrekənsɪliˈeɪʃn', 'n.', '调解;和解;协调;', '...an appeal for reconciliation between Catholics and Protestants...', '天主教徒和新教徒之间达成和解的诉求');
INSERT INTO `words` VALUES (6802, 'recondite', 'ˈrekəndaɪt', 'adj.', '深奥的;晦涩的;', 'Her poems are modishly experimental in style and recondite in subject-matter.', '她的诗在风格上是时髦的实验派，主题艰深难懂。');
INSERT INTO `words` VALUES (6803, 'reconnaissance', 'rɪˈkɒnɪsns', 'n.', '侦察;', 'The helicopter was returning from a reconnaissance mission...', '直升机完成侦察任务后正在返航。');
INSERT INTO `words` VALUES (6804, 'reconsider', 'ˌriːkənˈsɪdə(r)', 'v.', '重新考虑;重新审议;', 'We want you to reconsider your decision to resign from the board...', '我们希望你重新考虑辞去董事会职务的决定。');
INSERT INTO `words` VALUES (6805, 'reconstitute', 'ˌriːˈkɒnstɪtjuːt', 'v.', '重组;重新设立;使(脱水食物等)恢复原状;', 'Slowly Jewish communities were reconstituted and Jewish life began anew.', '渐渐地，犹太社区得到重建，犹太人的生活重新开始。');
INSERT INTO `words` VALUES (6806, 'reconstruct', 'ˌriːkənˈstrʌkt', 'v.', '修复;重建;重造;', 'The government must reconstruct the shattered economy...', '政府必须重建支离破碎的经济。');
INSERT INTO `words` VALUES (6807, 'reconstructed', 'ˌriːkənˈstrʌktɪd', 'v.', '修复;重建;重造;', 'The government must reconstruct the shattered economy...', '政府必须重建支离破碎的经济。');
INSERT INTO `words` VALUES (6808, 'reconstruction', 'ˌriːkənˈstrʌkʃn', 'n.', '重建;改造;复原;', '...America\'s part in the post-war reconstruction of Germany.', '美国在战后德国重建中的作用');
INSERT INTO `words` VALUES (6809, 'recorder', 'rɪˈkɔːdə(r)', 'n.', '录音机;录像机;竖笛;', 'Rodney put the recorder on the desk top and pushed the play button.', '罗德尼把录音机放在桌面上，按下了播放键。');
INSERT INTO `words` VALUES (6810, 'recording', 'rɪˈkɔːdɪŋ', 'n.', '录制的音像;录音;录像;', '...a video recording of a police interview.', '警方讯问的录像带');
INSERT INTO `words` VALUES (6811, 'recoup', 'rɪˈkuːp', 'v.', '收回(成本);弥补(亏损);', 'Insurance companies are trying to recoup their losses by increasing premiums.', '保险公司正试图通过提高保费来弥补损失。');
INSERT INTO `words` VALUES (6812, 'recourse', 'rɪˈkɔːs', 'n.', '依靠;依赖;求助;', 'It enabled its members to settle their differences without recourse to war...', '这样，其成员就可以不必诉诸战争来解决分歧。');
INSERT INTO `words` VALUES (6813, 'recover', 'rɪˈkʌvə(r)', 'v.', '恢复健康;康复;痊愈;', 'He is recovering from a knee injury...', '他的膝伤正在康复。');
INSERT INTO `words` VALUES (6814, 'recoverable', 'rɪˈkʌvərəbl', 'adj.', '可收回的;可重新获得的;可开采的;', 'If you decide not to buy, the money you have spent on the survey is not recoverable.', '如果你决定不买，你在调查上花的钱是收不回来的。');
INSERT INTO `words` VALUES (6815, 'recovery', 'rɪˈkʌvəri', 'n.', '恢复;痊愈;改善;', 'He made a remarkable recovery from a shin injury...', '他的胫骨伤恢复得相当不错。');
INSERT INTO `words` VALUES (6816, 'recreate', 'ˌriːkriˈeɪt', 'v.', '再现;再创造;', 'The rooms are furnished and recreate the atmosphere of the castle\'s medieval heyday...', '房间都进行了布置，重新营造出该城堡中世纪全盛时期的氛围。');
INSERT INTO `words` VALUES (6817, 'recreational', 'ˌrekriˈeɪʃənl', 'adj.', '娱乐的;消遣的;', '...parks and other recreational facilities.', '公园和其他休闲设施');
INSERT INTO `words` VALUES (6818, 'recrimination', 'rɪˌkrɪmɪˈneɪʃn', 'n.', '指责;反诉;反控;', 'The bitter rows and recriminations have finally ended the relationship...', '激烈的争吵和相互指责最终为他们的感情画上了句号。');
INSERT INTO `words` VALUES (6819, 'recruit', 'rɪˈkruːt', 'v.', '吸收(新成员);征募(新兵);动员…(提供帮助);', 'The police are trying to recruit more black and Asian officers...', '警方正在试图招募更多黑人和亚裔警官。');
INSERT INTO `words` VALUES (6820, 'recruiter', 'rɪ\'kru:tə', 'n.', '征兵人员;', 'The police are trying to recruit more black and Asian officers...', '警方正在试图招募更多黑人和亚裔警官。');
INSERT INTO `words` VALUES (6821, 'rectangular', 'rekˈtæŋɡjələ(r)', 'adj.', '长方形的;矩形的;', '...a rectangular table.', '长方形桌子');
INSERT INTO `words` VALUES (6822, 'rectification', 'ˌrektɪfɪˈkeɪʃn', 'n.', '纠正;修正;矫正;', '...the rectification of an injustice.', '对不公正的纠正');
INSERT INTO `words` VALUES (6823, 'rectify', 'ˈrektɪfaɪ', 'v.', '矫正;纠正;改正;', 'Only an act of Congress could rectify the situation...', '只有国会法案才能矫正这种形势。');
INSERT INTO `words` VALUES (6824, 'rectitude', 'ˈrektɪtjuːd', 'n.', '公正;正直;诚实;', '...people of the utmost moral rectitude.', '最为正直的人');
INSERT INTO `words` VALUES (6825, 'rector', 'ˈrektə(r)', 'n.', '(圣公会的)教区牧师，堂区主持人;(英国某些学校的)校长;', 'He was rector of All Hallows Church in Wellingborough.', '他是韦灵伯勒万圣教堂的教区长。');
INSERT INTO `words` VALUES (6826, 'recumbent', 'rɪˈkʌmbənt', 'adj.', '躺倒的;躺着的;', 'He looked down at the recumbent figure.', '他低头看躺着的那个人。');
INSERT INTO `words` VALUES (6827, 'recuperate', 'rɪˈkuːpəreɪt', 'v.', '康复;恢复;恢复健康;', 'I went away to the country to recuperate...', '我去了乡下休养。');
INSERT INTO `words` VALUES (6828, 'recuperative', 'rɪˈkuːpərətɪv', 'adj.', '有助于恢复的;', 'Human beings have great recuperative powers.', '人类具有很强的自我康复能力。');
INSERT INTO `words` VALUES (6829, 'recur', 'rɪˈkɜː(r)', 'v.', '再发生;反复出现;', '...a theme that was to recur frequently in his work.', '一个在他的作品中反复出现的主题');
INSERT INTO `words` VALUES (6830, 'recurrence', 'rɪˈkʌrəns', 'n.', '重现;复发;', 'Police are out in force to prevent a recurrence of the violence.', '警方出动大量警力防止暴力事件再次发生。');
INSERT INTO `words` VALUES (6831, 'recurrent', 'rɪˈkʌrənt', 'adj.', '反复出现的;重复发生的;', 'Race is a recurrent theme in the work.', '种族问题是该作品里多次出现的主题。');
INSERT INTO `words` VALUES (6832, 'recycle', 'ˌriːˈsaɪkl', 'v.', '回收利用;再利用;再次应用，重新使用(概念、方法、玩笑等);', 'The objective would be to recycle 98 per cent of domestic waste...', '目标是对 98%的生活垃圾进行回收利用。');
INSERT INTO `words` VALUES (6833, 'recycling', 'ˌriːˈsaɪklɪŋ', 'v.', '回收利用;再利用;再次应用，重新使用(概念、方法、玩笑等);', 'The objective would be to recycle 98 per cent of domestic waste...', '目标是对 98%的生活垃圾进行回收利用。');
INSERT INTO `words` VALUES (6834, 'red', 'red', 'adj.', '红的;红色的;充血的;', '...a bunch of red roses...', '一束红玫瑰');
INSERT INTO `words` VALUES (6835, 'redden', 'ˈredn', 'v.', '(使)变红;', 'He was working himself up to a fury, his face reddening...', '他勃然大怒，脸涨得通红。');
INSERT INTO `words` VALUES (6836, 'reddish', 'ˈredɪʃ', 'adj.', '微红的;略带红色的;', 'He had reddish brown hair...', '他的头发是红棕色的。');
INSERT INTO `words` VALUES (6837, 'redecorate', 'ˌriːˈdekəreɪt', 'v.', '(用涂料或壁纸)重新装饰，再次装修;', 'Americans redecorate their houses and offices every few years...', '美国人每隔几年就会把家里和办公室重新粉刷一下。');
INSERT INTO `words` VALUES (6838, 'redeem', 'rɪˈdiːm', 'v.', '补救;弥补;掩饰…之不足;', 'He had realized the mistake he had made and wanted to redeem himself...', '他已经认识到了自己的错误，想要进行弥补。');
INSERT INTO `words` VALUES (6839, 'redefine', 'ˌriːdɪˈfaɪn', 'v.', '改变…的本质(或界限);重新定义;使重新考虑;', 'We will finally have to redefine our relationship with neighbouring states in north Africa...', '我们最终将不得不重新界定我们和北非邻国的关系。');
INSERT INTO `words` VALUES (6840, 'redemption', 'rɪˈdempʃn', 'n.', '拯救;救赎;赎回(股票等);', 'He craves redemption for his sins.', '他渴望赎清自己的罪孽。');
INSERT INTO `words` VALUES (6841, 'redesign', 'ˌriːdɪˈzaɪn', 'v.', '重新设计;', 'The hotel has recently been redesigned and redecorated...', '这家酒店最近重新设计和装修过。');
INSERT INTO `words` VALUES (6842, 'red-faced', 'ˌred ˈfeɪst', 'adj.', '脸色涨红的;红脸的;', 'A red-faced Mr Jones was led away by police...', '一位涨红了脸的琼斯先生被警察带走了。');
INSERT INTO `words` VALUES (6843, 'red-hot', 'ˌred ˈhɒt', 'adj.', '炽热的;赤热的;热得发红的;', '...red-hot iron.', '烧红了的烙铁');
INSERT INTO `words` VALUES (6844, 'redirect', 'ˌriːdəˈrekt , ˈriːdərekt', 'v.', '(以新的方式或目的)重新使用;改寄;改变投递方向;', 'Controls were used to redistribute or redirect resources.', '采用了管控措施来重新分配资源或改变其流向。');
INSERT INTO `words` VALUES (6845, 'redo', 'ˌriːˈduː', 'v.', '重做;换一种方式做;', 'They had redone their sums.', '他们已重算了一次总数。');
INSERT INTO `words` VALUES (6846, 'redolent', 'ˈredələnt', 'adj.', '使人想到;使人联想起;有…的强烈气味;', '...percussion instruments, redolent of Far Eastern cultures.', '使人联想起远东文化的打击乐器');
INSERT INTO `words` VALUES (6847, 'redoubtable', 'rɪˈdaʊtəbl', 'adj.', '令人敬畏的;可敬的;', '...the redoubtable Mr Brooks...', '令人敬畏的布鲁克斯先生');
INSERT INTO `words` VALUES (6848, 'redound', 'rɪˈdaʊnd', 'v.', '促进;增进;有助益;', 'The success in the Middle East redounds to his benefit...', '在中东的胜利给他带来了好处。');
INSERT INTO `words` VALUES (6849, 'redraw', 'ˌriːˈdrɔː', 'v.', '重新描绘，修改(边界、计划、安排等);', 'They have redrawn the country\'s boundaries along ethnic lines...', '他们已经按照民族界线重新划定了国家的边界。');
INSERT INTO `words` VALUES (6850, 'reduce', 'rɪˈdjuːs', 'v.', '减少，缩小(尺寸、数量、价格等);(使)蒸发;减轻体重;', 'It reduces the risks of heart disease...', '这降低了患心脏病的风险。');
INSERT INTO `words` VALUES (6851, 'reduction', 'rɪˈdʌkʃn', 'n.', '减少;缩小;降低;', '...a future reduction in UK interest rates...', '未来英国利率的下调');
INSERT INTO `words` VALUES (6852, 'redundancy', 'rɪˈdʌndənsi', 'n.', '(因劳动力过剩而造成的)裁员，解雇;多余;累赘;', 'The ministry has said it hopes to avoid compulsory redundancies.', '该部称希望避免强制性裁员。');
INSERT INTO `words` VALUES (6853, 'redundant', 'rɪˈdʌndənt', 'adj.', '被裁减的;多余的;不需要的;', 'My husband was made redundant late last year.', '我丈夫去年年底被裁了。');
INSERT INTO `words` VALUES (6854, 'reef', 'riːf', 'n.', '礁;礁脉;缩帆部;', 'An unspoilt coral reef encloses the bay.', '未受破坏的珊瑚礁环绕在海湾周围。');
INSERT INTO `words` VALUES (6855, 'reek', 'riːk', 'v.', '散发臭气;发出难闻的气味;明显带有，强烈地意味着(令人不快或起疑的特性);', 'Your breath reeks of stale cigar smoke...', '你满嘴都是难闻的雪茄味。');
INSERT INTO `words` VALUES (6856, 'reel', 'riːl', 'n.', '卷轴;卷盘;卷筒;', '...a 30m reel of cable.', '一卷30米长的电缆');
INSERT INTO `words` VALUES (6857, 'refer', 'rɪˈfɜː(r)', 'v.', '谈及;提到;提及;称…(为);指;', 'In his speech, he referred to a recent trip to Canada.', '他在讲演中提到了前不久的加拿大之行。');
INSERT INTO `words` VALUES (6858, 'reference', 'ˈrefrəns', 'n.', '说到(或写到)的事;提到;谈及;', 'He made no reference to any agreement...', '他没有提到什么协议。');
INSERT INTO `words` VALUES (6859, 'referendum', 'ˌrefəˈrendəm', 'n.', '全民投票;全民公决;', 'Estonia said it too planned to hold a referendum on independence.', '爱沙尼亚称其也计划就独立问题进行全民公决。');
INSERT INTO `words` VALUES (6860, 'referral', 'rɪˈfɜːrəl', 'n.', '送交，转送(到能提供专门帮助的人或地方那里);', 'Legal Aid can often provide referral to other types of agencies...', '法律援助机构通常可以帮助引荐到其他类型的机构去。');
INSERT INTO `words` VALUES (6861, 'refinance', 'ˌriːˈfaɪnæns', 'v.', '再筹资金，再融资(以偿还债务);', 'A loan was arranged to refinance existing debt...', '办理贷款以清偿现有债务。');
INSERT INTO `words` VALUES (6862, 'refine', 'rɪˈfaɪn', 'v.', '精炼;提纯;去除杂质;', 'Oil is refined to remove naturally occurring impurities.', '油经过提炼去除天然存在的杂质。');
INSERT INTO `words` VALUES (6863, 'refined', 'rɪˈfaɪnd', 'adj.', '精炼的;提纯的;精制的;', '...refined sugar.', '精制白糖');
INSERT INTO `words` VALUES (6864, 'refinement', 'rɪˈfaɪnmənt', 'n.', '(精细的)改进，改善;改良品;经过改进的东西;', 'Older cars inevitably lack the latest safety refinements.', '老款的汽车难免缺少经过改进的最新安全保护装置。');
INSERT INTO `words` VALUES (6865, 'reflect', 'rɪˈflekt', 'v.', '反映;映出(影像);反射(声、光、热等);', 'The Los Angeles riots reflected the bitterness between the black and Korean communities in the city...', '洛杉矶的暴乱反映出这个城市的黑人社区和韩裔社区之间的仇恨。');
INSERT INTO `words` VALUES (6866, 'reflection', 'rɪˈflekʃn', 'n.', '映像;映照出的影像;(声、光、热等的)反射;', 'Meg stared at her reflection in the bedroom mirror.', '梅格注视着卧室中镜子里的自己。');
INSERT INTO `words` VALUES (6867, 'reflective', 'rɪˈflektɪv', 'adj.', '沉思的;深思的;(指物体表面)反射热的，反光的;', 'I walked on in a reflective mood to the car...', '我心事重重地向车子走去。');
INSERT INTO `words` VALUES (6868, 'reflex', 'ˈriːfleks', 'n.', '反射动作;本能反应;反射作用;', 'Walsh fumbled in his pocket, a reflex from his smoking days...', '沃尔什在口袋里摸索着，这是他抽烟时养成的习惯动作。');
INSERT INTO `words` VALUES (6869, 'reform', 'rɪˈfɔːm', 'v.', '改革;改进;改良;', 'The party embarked on a programme of economic reform...', '这个党开始进行经济改革。');
INSERT INTO `words` VALUES (6870, 'refract', 'rɪˈfrækt', 'v.', '使(光线)折射;使产生折射;', 'As we age the lenses of the eyes thicken, and thus refract light differently.', '随着我们年龄的增长，眼睛的晶状体变厚，因此对光线的折射也会发生变化。');
INSERT INTO `words` VALUES (6871, 'refraction', 'rɪˈfrækʃn', 'n.', '折射（程度）;折射角;', 'As we age the lenses of the eyes thicken, and thus refract light differently.', '随着我们年龄的增长，眼睛的晶状体变厚，因此对光线的折射也会发生变化。');
INSERT INTO `words` VALUES (6872, 'refractory', 'rɪˈfræktəri', 'adj.', '难以驾驭的;行为乖戾的;难以诊治的;', '...refractory priests who refused to side with the king.', '拒绝支持国王的桀骜不驯的牧师');
INSERT INTO `words` VALUES (6873, 'refrain', 'rɪˈfreɪn', 'v.', '克制;节制;避免;', 'Mrs Hardie refrained from making any comment...', '哈迪夫人忍住不作任何评论。');
INSERT INTO `words` VALUES (6874, 'refresh', 'rɪˈfreʃ', 'v.', '使恢复精力;使凉爽;重新斟满;', 'The lotion cools and refreshes the skin...', '这种润肤液凉爽清新，能够焕发肌肤的活力。');
INSERT INTO `words` VALUES (6875, 'refreshing', 'rɪˈfreʃɪŋ', 'adj.', '令人耳目一新的;别具一格的;使人精力充沛的;', 'It\'s refreshing to hear somebody speaking common sense...', '听到有人说话通情达理真是令人耳目一新。');
INSERT INTO `words` VALUES (6876, 'refrigerate', 'rɪˈfrɪdʒəreɪt', 'v.', '使冷却;使变冷;冷藏;', 'Refrigerate the dough overnight.', '将面团冷藏过夜。');
INSERT INTO `words` VALUES (6877, 'refuge', 'ˈrefjuːdʒ', 'n.', '庇护;避难;避难所;', 'They took refuge in a bomb shelter...', '他们在防空洞中避难。');
INSERT INTO `words` VALUES (6878, 'refurbish', 'ˌriːˈfɜːbɪʃ', 'v.', '再装修;清理装修;', 'We have spent money on refurbishing the offices...', '我们已经花钱对办公室进行了翻新。');
INSERT INTO `words` VALUES (6879, 'refusal', 'rɪˈfjuːzl', 'n.', '拒绝;回绝;', '...her refusal to accept change...', '她对变革的拒绝');
INSERT INTO `words` VALUES (6880, 'refutation', 'ˌrefjuˈteɪʃn', 'n.', '(对论点、指责或理论的)反驳，证伪，驳斥;', 'He prepared a complete refutation of the Republicans\' most serious charges.', '他准备全面反驳共和党人对他最严重的指控。');
INSERT INTO `words` VALUES (6881, 'refute', 'rɪˈfjuːt', 'v.', '驳斥;批驳;反驳;', 'It was the kind of rumour that it is impossible to refute.', '这是那种让人根本没法批驳的谣言。');
INSERT INTO `words` VALUES (6882, 'regain', 'rɪˈɡeɪn', 'v.', '重新获得，恢复(能力或品质等);回到(原位);返回;', 'Troops have regained control of the city...', '军队重新控制了该市。');
INSERT INTO `words` VALUES (6883, 'regal', 'ˈriːɡl', 'adj.', '帝王的;王室的;豪华的;', 'He sat with such regal dignity...', '他端坐在那里，浑身透着王者的威严。');
INSERT INTO `words` VALUES (6884, 'regale', 'rɪˈɡeɪl', 'v.', '(以故事或笑话)极力取悦;', 'He was constantly regaled with tales of woe.', '别人老是给他讲些倒霉事儿来逗他开心。');
INSERT INTO `words` VALUES (6885, 'regard', 'rɪˈɡɑːd', 'v.', '将…认为;把…视为;看待;', 'He was regarded as the most successful Chancellor of modern times...', '他被认为是现代最为成功的财政大臣。');
INSERT INTO `words` VALUES (6886, 'regarding', 'rɪˈɡɑːdɪŋ', 'prep.', '关于;至于;', 'He refused to divulge any information regarding the man\'s whereabouts.', '关于那个人的行踪他拒绝透露任何信息。');
INSERT INTO `words` VALUES (6887, 'regardless', 'rɪˈɡɑːdləs', 'adv.', '不顾;不加理会;', 'It takes in anybody regardless of religion, colour, or creed...', '任何人，不论宗教、肤色或信仰，都可以加入。');
INSERT INTO `words` VALUES (6888, 'regards', 'rɪˈɡɑːdz', 'v.', '将…认为;把…视为;看待;', 'He was regarded as the most successful Chancellor of modern times...', '他被认为是现代最为成功的财政大臣。');
INSERT INTO `words` VALUES (6889, 'regenerate', 'rɪˈdʒenəreɪt', 'v.', '使振兴;使复兴;发展壮大;', 'The government will continue to try to regenerate inner city areas.', '政府将继续努力重建内城地区。');
INSERT INTO `words` VALUES (6890, 'regeneration', 'rɪˌdʒenə\'reɪʃ(ə)n', 'n.', '再生;改造;回收;', 'The government will continue to try to regenerate inner city areas.', '政府将继续努力重建内城地区。');
INSERT INTO `words` VALUES (6891, 'regenerative', 'rɪˈdʒenərətɪv', 'adj.', '(能力或过程)再生的，再造的;', '...the regenerative power of nature.', '大自然的再造能力');
INSERT INTO `words` VALUES (6892, 'reggae', 'ˈreɡeɪ', 'n.', '雷盖(西印度群岛的一种节奏强劲的流行音乐);', 'Bob Marley provided them with their first taste of Reggae music.', '鲍勃·马利让他们首次接触到了雷盖音乐。');
INSERT INTO `words` VALUES (6893, 'regime', 'reɪˈʒiːm', 'n.', '(尤指未通过公正选举的)统治方式，统治制度，政权，政体;组织方法;管理体制;', '...the collapse of the Fascist regime at the end of the war...', '在战争末期法西斯政权的倒台');
INSERT INTO `words` VALUES (6894, 'regimen', 'ˈredʒɪmən', 'n.', '生活规则;养生之道;养生法;', 'Whatever regimen has been prescribed should be rigorously followed.', '不管制订的是什么样的养生计划，都要严格遵守。');
INSERT INTO `words` VALUES (6895, 'regimental', 'ˌredʒɪˈmentl', 'adj.', '团的;团队的;', 'Mills was regimental colonel.', '米尔斯当过团长。');
INSERT INTO `words` VALUES (6896, 'regimentation', 'ˌredʒɪmenˈteɪʃn', 'n.', '严格管理;严格控制;严密组织;', 'Democracy is incompatible with excessive, bureaucratic regimentation of social life.', '受到过度的、官僚化的管制的社会生活中不存在民主。');
INSERT INTO `words` VALUES (6897, 'region', 'ˈriːdʒən', 'n.', '(通常界限不明的)地区，区域，地方;行政区;(一国除首都以外的)全部地区，所有区域;', '...Barcelona, capital of the autonomous region of Catalonia.', '加泰罗尼亚自治区首府巴塞罗那');
INSERT INTO `words` VALUES (6898, 'regional', 'ˈriːdʒənl', 'adj.', '地区的;区域的;地方的;', '...the autonomous regional government of Andalucia.', '安达卢西亚自治区政府');
INSERT INTO `words` VALUES (6899, 'register', 'ˈredʒɪstə(r)', 'v.', '登记;注册;(正式地或公开地)发表意见，提出主张;', '...registers of births, deaths and marriages...', '出生、死亡和婚姻记录');
INSERT INTO `words` VALUES (6900, 'registered', 'ˈredʒɪstəd', 'adj.', '(信、包裹)挂号的;', 'He asked his mother to send it by registered mail.', '他让他妈妈用挂号邮件寄。');
INSERT INTO `words` VALUES (6901, 'registration', 'ˌredʒɪˈstreɪʃn', 'n.', '登记;注册;挂号;', 'They have campaigned strongly for compulsory registration of dogs...', '他们强烈呼吁实行犬类强制登记。');
INSERT INTO `words` VALUES (6902, 'registry', 'ˈredʒɪstri', 'n.', '登记处;注册处;', 'It agreed to set up a central registry of arms sales.', '它同意成立一个中央武器销售登记处。');
INSERT INTO `words` VALUES (6903, 'regression', 'rɪˈɡreʃn', 'n.', '倒退;回归;退化;', '...if your child regresses to babyish behaviour...', '如果你的孩子退行到婴儿状态');
INSERT INTO `words` VALUES (6904, 'regressive', 'rɪˈɡresɪv', 'adj.', '退化的;倒退的;退步的;', 'This regressive behaviour is more common in boys.', '这种行为上的倒退现象在男孩中更加普遍。');
INSERT INTO `words` VALUES (6905, 'regret', 'rɪˈɡret', 'v.', '感到遗憾;惋惜;懊悔;', 'I simply gave in to him, and I\'ve regretted it ever since...', '我就向他屈服了，从那以后我一直后悔。');
INSERT INTO `words` VALUES (6906, 'regretful', 'rɪˈɡretfl', 'adj.', '后悔的;失望的;令人惋惜的;', 'Mr Griffin gave a regretful smile...', '格里芬先生遗憾地笑了笑。');
INSERT INTO `words` VALUES (6907, 'regretfully', 'rɪˈɡretfəli', 'adv.', '遗憾地;痛惜地;失望地;', 'Mr Griffin gave a regretful smile...', '格里芬先生遗憾地笑了笑。');
INSERT INTO `words` VALUES (6908, 'regrettable', 'rɪˈɡretəbl', 'adj.', '令人惋惜的;可惜的;令人遗憾的;', '...an investigation into what the army described as a regrettable incident...', '对军方称之为令人遗憾的事件的调查');
INSERT INTO `words` VALUES (6909, 'regular', 'ˈreɡjələ(r)', 'adj.', '规则的;有规律的;间隙均匀的;', 'Take regular exercise...', '定期进行锻炼。');
INSERT INTO `words` VALUES (6910, 'regularity', 'ˌreɡjuˈlærəti', 'n.', '规律性;经常性;匀称;', 'Children seek out regularities and rules in acquiring language.', '儿童在学习语言过程中会找出各种规律和规则。');
INSERT INTO `words` VALUES (6911, 'regularly', 'ˈreɡjələli', 'adv.', '有规律地;间隙均匀地;经常;', 'Take regular exercise...', '定期进行锻炼。');
INSERT INTO `words` VALUES (6912, 'regulate', 'ˈreɡjuleɪt', 'v.', '(用规则条例)约束，控制，管理;调节，控制(速度、压力、温度等);', 'The powers of the European Commission to regulate competition are increasing...', '欧盟委员会对竞争的监管权越来越大。');
INSERT INTO `words` VALUES (6913, 'regulation', 'ˌreɡjuˈleɪʃn', 'n.', '章程;规章制度;规则;', 'The European Union has proposed new regulations to control the hours worked by its employees...', '欧盟已经就其雇员工作时间的控制问题拟定了新的规章。');
INSERT INTO `words` VALUES (6914, 'regulator', 'ˈreɡjuleɪtə(r)', 'n.', '(某行业等的)监管者，监管机构;(速度、温度、压力的)自动调节器;', 'Congress is being asked to investigate why it took so long for government regulators to shut the plant down...', '人们要求国会调查政府监管机构为何花了这么长时间才关闭该工厂。');
INSERT INTO `words` VALUES (6915, 'regulatory', 'ˈreɡjələtəri', 'adj.', '(对工商业)具有监管权的，监管的;', 'Congress is being asked to investigate why it took so long for government regulators to shut the plant down...', '人们要求国会调查政府监管机构为何花了这么长时间才关闭该工厂。');
INSERT INTO `words` VALUES (6916, 'regurgitate', 'rɪˈɡɜːdʒɪteɪt', 'v.', '使(咽下的食物)返回到口中;反刍;照搬;', 'You can get sick to death of a friend regurgitating her partner\'s opinions.', '对于一个只会机械地重复其搭档观点的朋友，你可能会无比腻烦。');
INSERT INTO `words` VALUES (6917, 'rehabilitate', 'ˌriːəˈbɪlɪteɪt', 'v.', '使(重病患者)康复;使(长期服刑者)恢复正常生活;恢复…的名誉;', 'Considerable efforts have been made to rehabilitate patients who have suffered in this way.', '为帮助遭受这种病痛折磨的病人康复付出了巨大努力。');
INSERT INTO `words` VALUES (6918, 'rehabilitation', 'ˌri:əˌbɪlɪ\'teɪʃn', 'n.', '恢复;善后;平反;', 'Considerable efforts have been made to rehabilitate patients who have suffered in this way.', '为帮助遭受这种病痛折磨的病人康复付出了巨大努力。');
INSERT INTO `words` VALUES (6919, 'rehearsal', 'rɪˈhɜːsl', 'n.', '排演;排练;预演;', 'The band was scheduled to begin rehearsals for a concert tour...', '这支乐队准备开始为巡演排练。');
INSERT INTO `words` VALUES (6920, 'rehearse', 'rɪˈhɜːs', 'v.', '排练;排演;默诵;', 'A group of actors are rehearsing a play about Joan of Arc...', '一群演员正在排演一出关于圣女贞德的戏。');
INSERT INTO `words` VALUES (6921, 'reign', 'reɪn', 'n.', '君主统治时期;任期;当政期;', 'Confusion reigned about how the debate would end...', '在这一争论如何结束的问题上人们陷入了混乱。');
INSERT INTO `words` VALUES (6922, 'reimburse', 'ˌriːɪmˈbɜːs', 'v.', '偿还;补偿;', 'I\'ll be happy to reimburse you for any expenses you might have incurred...', '我将很乐意赔偿您所发生的任何费用。');
INSERT INTO `words` VALUES (6923, 'reimbursement', 'ˌriːɪmˈbɜːsmənt', 'n.', '补偿;赔偿;偿付;', 'She is demanding reimbursement for medical and other expenses...', '她正在索赔医药费和其他花费。');
INSERT INTO `words` VALUES (6924, 'reincarnate', 'ˌriːɪnˈkɑːneɪt', 'v.', '使投胎;转世;使再生;', '...their belief that human souls were reincarnated in the bodies of turtles.', '他们认为人的灵魂转世投胎到海龟身体里的信仰');
INSERT INTO `words` VALUES (6925, 'reincarnation', 'ˌriːɪnkɑːˈneɪʃn', 'n.', '转世说;(灵魂的)转世化身，化身;', 'Many African tribes believe in reincarnation.', '许多非洲的部落都相信人会转世。');
INSERT INTO `words` VALUES (6926, 'reinforce', 'ˌriːɪnˈfɔːs', 'v.', '加强;充实;使更强烈;', 'A stronger European Parliament would, they fear, only reinforce the power of the larger countries...', '他们担心一个更加强大的欧洲议会只会增强大国的力量。');
INSERT INTO `words` VALUES (6927, 'reinforcement', 'ˌriːɪnˈfɔːsmənt', 'n.', '援军;增援警力;(感情或思想等的)巩固，加强，强化;', '...the despatch of police and troop reinforcements.', '派遣警察和军队支援');
INSERT INTO `words` VALUES (6928, 'reinstate', 'ˌriːɪnˈsteɪt', 'v.', '使恢复原职;使重返岗位;把…放回原处;', 'The governor is said to have agreed to reinstate five senior workers who were dismissed...', '据说州长已同意给5名被解雇的高级雇员复职。');
INSERT INTO `words` VALUES (6929, 'reiterate', 'riˈɪtəreɪt', 'v.', '反复地说;重申;', 'He reiterated his opposition to the creation of a central bank...', '他重申了自己反对成立央行的立场。');
INSERT INTO `words` VALUES (6930, 'rejoice', 'rɪˈdʒɔɪs', 'v.', '非常高兴;深感欣喜;', 'Garbo plays the Queen, rejoicing in the love she has found with Antonio...', '嘉宝饰演王后，陶醉于和安东尼奥的热恋中。');
INSERT INTO `words` VALUES (6931, 'rejoicing', 'rɪˈdʒɔɪsɪŋ', 'n.', '喜庆;欢庆;', 'Garbo plays the Queen, rejoicing in the love she has found with Antonio...', '嘉宝饰演王后，陶醉于和安东尼奥的热恋中。');
INSERT INTO `words` VALUES (6932, 'rejuvenate', 'rɪˈdʒuːvəneɪt', 'v.', '使年轻;使更有活力;', 'Shelley was advised that the Italian climate would rejuvenate him...', '有人建议雪莱去意大利，那里的天气会使他恢复活力。');
INSERT INTO `words` VALUES (6933, 'rekindle', 'ˌriːˈkɪndl', 'v.', '使重新活跃;使复苏;', 'Ben Brantley\'s article on Sir Ian McKellen rekindled many memories...', '本·布兰特利的那篇关于伊恩·麦凯伦爵士的文章唤起了许多记忆。');
INSERT INTO `words` VALUES (6934, 'relate', 'rɪˈleɪt', 'v.', '联系;使有联系;把…联系起来;', 'Other recommendations relate to the details of how such data is stored...', '其他建议涉及此类信息储存方面的一些细节。');
INSERT INTO `words` VALUES (6935, 'related', 'rɪˈleɪtɪd', 'adj.', '相关的;有联系的;属同一家族的;', 'The philosophical problems of chance and of free will are closely related.', '或然性和自由意志这两个哲学问题是紧密相关的。');
INSERT INTO `words` VALUES (6936, 'relation', 'rɪˈleɪʃn', 'n.', '(人、团体、国家之间的)关系，联系，交往;(事物之间的)关系，关联;亲戚;', 'Greece has established full diplomatic relations with Israel...', '希腊已经和以色列建立了全面的外交关系。');
INSERT INTO `words` VALUES (6937, 'relations', 'rɪˈleɪʃnz', 'n.', '(人、团体、国家之间的)关系，联系，交往;(事物之间的)关系，关联;亲戚;', 'Greece has established full diplomatic relations with Israel...', '希腊已经和以色列建立了全面的外交关系。');
INSERT INTO `words` VALUES (6938, 'relationship', 'rɪˈleɪʃnʃɪp', 'n.', '(人、团体、国家之间的)关系，联系;情爱关系;性爱关系;', '...the friendly relationship between France and Britain...', '法国和英国之间的友好关系');
INSERT INTO `words` VALUES (6939, 'relative', 'ˈrelətɪv', 'adj.', '相比较而言的;比较的;相对的;', 'Do relatives of yours still live in Siberia?...', '你的亲属仍然住在西伯利亚吗？');
INSERT INTO `words` VALUES (6940, 'relatively', 'ˈrelətɪvli', 'adv.', '相当程度上;相当地;相对地;', 'The sums needed are relatively small...', '需要的金额相对较小。');
INSERT INTO `words` VALUES (6941, 'relax', 'rɪˈlæks', 'v.', '放松;休息;宽慰;', 'I ought to relax and stop worrying about it...', '我应该放松一下，不要再担心它了。');
INSERT INTO `words` VALUES (6942, 'relaxation', 'ˌriːlækˈseɪʃn', 'n.', '放松;休息;消遣;', 'You should be able to find the odd moment for relaxation...', '你应该能抽空休息一下。');
INSERT INTO `words` VALUES (6943, 'relaxed', 'rɪˈlækst', 'adj.', '放松的;冷静的;镇定的;', 'As soon as I had made the final decision, I felt a lot more relaxed...', '我一作出最后的决定就感到轻松多了。');
INSERT INTO `words` VALUES (6944, 'relaxing', 'rɪˈlæksɪŋ', 'adj.', '有助于休息的;令人放松的;轻松的;', 'I find cooking very relaxing...', '我发现烹饪可以令人非常放松。');
INSERT INTO `words` VALUES (6945, 'release', 'rɪˈliːs', 'v.', '释放;放出;放走;', 'He was released from custody the next day...', '第二天他被从拘留所里放了出来。');
INSERT INTO `words` VALUES (6946, 'relegate', 'ˈrelɪɡeɪt', 'v.', '使贬职;使降级;降低…的地位;', 'Might it not be better to relegate the King to a purely ceremonial function?...', '让国王变成纯粹发挥典礼仪式上象征作用的元首会不会更好？');
INSERT INTO `words` VALUES (6947, 'relent', 'rɪˈlent', 'v.', '终于答应;不再拒绝;变缓和;', 'Finally his mother relented and gave permission for her youngest son to marry.', '最后他母亲作出让步，允许她的小儿子结婚。');
INSERT INTO `words` VALUES (6948, 'relentless', 'rɪˈlentləs', 'adj.', '不停的;持续强烈的;不减弱的;', 'The pressure now was relentless.', '压力现在没完没了。');
INSERT INTO `words` VALUES (6949, 'relentlessly', 'rɪˈlɛntlɪsli', 'adv.', '无情地;', 'The pressure now was relentless.', '压力现在没完没了。');
INSERT INTO `words` VALUES (6950, 'relevance', 'ˈreləvəns', 'n.', '重要性;意义;相关性;', 'Politicians\' private lives have no relevance to their public roles...', '政治家的私生活与他们的公众角色不相关。');
INSERT INTO `words` VALUES (6951, 'relevant', 'ˈreləvənt', 'adj.', '紧密相关的;切题的;有价值的;', 'We are trying to make politics more relevant to younger people.', '我们试图使政治活动更加贴近年轻人。');
INSERT INTO `words` VALUES (6952, 'reliability', 'rɪˌlaɪə\'bɪlɪti', 'n.', '可靠性;', 'She was efficient and reliable...', '她办事很有效率，也很可靠。');
INSERT INTO `words` VALUES (6953, 'reliable', 'rɪˈlaɪəbl', 'adj.', '可信赖的;可依靠的;真实可信的;', 'She was efficient and reliable...', '她办事很有效率，也很可靠。');
INSERT INTO `words` VALUES (6954, 'reliance', 'rɪˈlaɪəns', 'n.', '依赖;依靠;信任;', '...the country\'s increasing reliance on foreign aid.', '这个国家日益依赖外国援助');
INSERT INTO `words` VALUES (6955, 'relic', 'ˈrelɪk', 'n.', '遗物;遗迹;遗风;', 'The tower is a relic of grim days when big houses had to be fortified against invaders...', '这座塔是那些灰暗岁月的遗迹，那时大房子不得不被加固,以抵御侵略者。');
INSERT INTO `words` VALUES (6956, 'relics', 'ˈrɛlɪks', 'n.', '遗物;遗迹;遗风;', 'The tower is a relic of grim days when big houses had to be fortified against invaders...', '这座塔是那些灰暗岁月的遗迹，那时大房子不得不被加固,以抵御侵略者。');
INSERT INTO `words` VALUES (6957, 'relief', 'rɪˈliːf', 'n.', '(不快过后的)宽慰，轻松;解脱;(焦虑、痛苦等的)减轻，消除，缓和;', 'I breathed a sigh of relief...', '我如释重负地松了口气。');
INSERT INTO `words` VALUES (6958, 'relieve', 'rɪˈliːv', 'v.', '解除，减轻，缓和(不快或痛苦);减轻(问题的严重性);缓和;', 'Drugs can relieve much of the pain...', '药物能够大大地缓解病痛。');
INSERT INTO `words` VALUES (6959, 'relieved', 'rɪˈliːvd', 'adj.', '感到宽慰的;放心的;显得开心的;', 'We are all relieved to be back home...', '回到家里我们都感到安心了。');
INSERT INTO `words` VALUES (6960, 'religion', 'rɪˈlɪdʒən', 'n.', '宗教;宗教信仰;教派;', '...his understanding of Indian philosophy and religion...', '他对印度哲学和宗教信仰的理解');
INSERT INTO `words` VALUES (6961, 'religious', 'rɪˈlɪdʒəs', 'adj.', '宗教信仰的;宗教的;笃信宗教的;', '...religious groups.', '宗教团体');
INSERT INTO `words` VALUES (6962, 'relinquish', 'rɪˈlɪŋkwɪʃ', 'v.', '(尤指不情愿地)放弃;', 'He does not intend to relinquish power.', '他没有打算放弃权力。');
INSERT INTO `words` VALUES (6963, 'relish', 'ˈrelɪʃ', 'v.', '享受;从…获得乐趣;渴望;', 'I relish the challenge of doing jobs that others turn down...', '我喜欢挑战别人拒绝做的工作。');
INSERT INTO `words` VALUES (6964, 'relive', 'ˌriːˈlɪv', 'v.', '(尤指在想象中)再次体验，重温;回味;', 'There is no point in reliving the past...', '回味过去没有任何意义。');
INSERT INTO `words` VALUES (6965, 'reload', 'ˌriːˈləʊd', 'v.', '给…再装填(子弹或胶卷等);给(计算机)重新装入(数据或程序);再装满(容器、车辆、机器等);', 'She reloaded the gun as quickly as she could...', '她以最快的速度再次给枪装上子弹。');
INSERT INTO `words` VALUES (6966, 'relocate', 'ˌriːləʊˈkeɪt', 'v.', '(使)搬迁，迁移;', 'If the company was to relocate, most employees would move...', '如果这个公司要搬迁，多数员工也要搬家。');
INSERT INTO `words` VALUES (6967, 'relocation', 'ˌriːləʊ\'keɪʃ(ə)n', 'n.', '迁移;转移;调动;', 'If the company was to relocate, most employees would move...', '如果这个公司要搬迁，多数员工也要搬家。');
INSERT INTO `words` VALUES (6968, 'reluctance', 'rɪ\'lʌktəns', 'n.', '不情愿;磁阻;厌恶;', 'Mr Spero was reluctant to ask for help...', '斯佩罗先生不愿意寻求帮助。');
INSERT INTO `words` VALUES (6969, 'reluctant', 'rɪˈlʌktənt', 'adj.', '不情愿的;勉强的;', 'Mr Spero was reluctant to ask for help...', '斯佩罗先生不愿意寻求帮助。');
INSERT INTO `words` VALUES (6970, 'reluctantly', 'rɪ\'lʌktəntli', 'adv.', '勉强;懊丧地;小心地;', 'Mr Spero was reluctant to ask for help...', '斯佩罗先生不愿意寻求帮助。');
INSERT INTO `words` VALUES (6971, 'rely', 'rɪˈlaɪ', 'v.', '依赖;依靠;信赖;信任;', 'They relied heavily on the advice of their professional advisers...', '他们非常依赖职业咨询师提供的建议。');
INSERT INTO `words` VALUES (6972, 'remain', 'rɪˈmeɪn', 'v.', '仍然是;保持不变;剩余;', 'The three men remained silent...', '这3个人保持着沉默。');
INSERT INTO `words` VALUES (6973, 'remainder', 'rɪˈmeɪndə(r)', 'n.', '其他人员;剩余物;剩余时间;', 'He gulped down the remainder of his coffee...', '他把剩下的咖啡一口气喝了下去。');
INSERT INTO `words` VALUES (6974, 'remaining', 'rɪˈmeɪnɪŋ', 'adj.', '仍需做的;还需处理的;', 'The three parties will meet next month to work out remaining differences...', '三方将在下个月会面，以解决余下的分歧。');
INSERT INTO `words` VALUES (6975, 'remains', 'rɪˈmeɪnz', 'n.', '剩余物;残留物;剩饭菜;', 'The three men remained silent...', '这3个人保持着沉默。');
INSERT INTO `words` VALUES (6976, 'remark', 'rɪˈmɑːk', 'n.', '谈论;言论;评述;', 'I remarked that I would go shopping that afternoon...', '我说过我那天下午要去购物。');
INSERT INTO `words` VALUES (6977, 'remarkable', 'rɪˈmɑːkəbl', 'adj.', '非凡的;奇异的;显著的;', 'He was a remarkable man...', '他是个不同寻常的人。');
INSERT INTO `words` VALUES (6978, 'remarkably', 'rɪˈmɑː(r)kəbli', 'adv.', '非常;极为;格外;', 'He was a remarkable man...', '他是个不同寻常的人。');
INSERT INTO `words` VALUES (6979, 'remarks', 'rɪˈmɑːks', 'n.', '谈论;言论;评述;', 'I remarked that I would go shopping that afternoon...', '我说过我那天下午要去购物。');
INSERT INTO `words` VALUES (6980, 'remedial', 'rɪˈmiːdiəl', 'adj.', '旨在解决问题的;补救的;纠正的;', '...children who required special remedial education...', '要求特殊辅导教育的儿童');
INSERT INTO `words` VALUES (6981, 'remedy', 'ˈremədi', 'n.', '处理方法;改进措施;补偿;', 'The remedy lies in the hands of the government.', '解决良策掌握在政府手中。');
INSERT INTO `words` VALUES (6982, 'remember', 'rɪˈmembə(r)', 'v.', '回想起;记得;记起;', 'You wouldn\'t remember me. I was in another group...', '你不会记得我。我那时在另外一组。');
INSERT INTO `words` VALUES (6983, 'remembrance', 'rɪˈmembrəns', 'n.', '纪念;记忆;回忆;', 'They wore black in remembrance of those who had died.', '他们身穿黑色衣服，以纪念那些亡故者。');
INSERT INTO `words` VALUES (6984, 'remind', 'rɪˈmaɪnd', 'v.', '提醒;使想起;', 'So she simply welcomed him and reminded him of the last time they had met...', '于是她只是对他表示欢迎，并且提醒说他们上次见过面。');
INSERT INTO `words` VALUES (6985, 'reminder', 'rɪˈmaɪndə(r)', 'n.', '引起回忆的事物;提醒人的事物;(告知该做某事的)通知单，提示信;', 'The British are about to be given a sharp reminder of what fighting abroad really means...', '有些事将很快让英国人清楚意识到海外作战究竟意味着什么。');
INSERT INTO `words` VALUES (6986, 'reminisce', 'ˌremɪˈnɪs', 'v.', '回忆，追忆，缅怀(昔日的快乐时光);', 'Ray and I ate our meal and reminisced about the trip...', '雷和我吃着饭，回忆起那次旅行。');
INSERT INTO `words` VALUES (6987, 'reminiscence', 'ˌremɪˈnɪsns', 'n.', '怀旧的谈话;回忆录;回忆;', 'Here I am boring you with my reminiscences.', '我要拿旧事来烦你了。');
INSERT INTO `words` VALUES (6988, 'reminiscent', 'ˌremɪˈnɪsnt', 'adj.', '使回忆起(人或事);回忆过去的;怀旧的;', 'The decor was reminiscent of a municipal arts-and-leisure centre...', '这种布置让人想起都市里的艺术休闲中心。');
INSERT INTO `words` VALUES (6989, 'remiss', 'rɪˈmɪs', 'adj.', '疏忽;懈怠;玩忽职守;', 'I would be remiss if I did not do something about it.', '如果我对此不做点儿什么，就是不负责任。');
INSERT INTO `words` VALUES (6990, 'remission', 'rɪˈmɪʃn', 'n.', '(重病的)缓解期，减轻期;减刑;减少服刑时间;', 'Brain scans have confirmed that the disease is in remission...', '脑部扫描已经证实疾病得到了控制。');
INSERT INTO `words` VALUES (6991, 'remittance', 'rɪˈmɪtns', 'n.', '汇款金额;汇付;汇款;', 'Please enclose your remittance, making cheques payable to Thames Valley Technology.', '请附上汇款，支票收款人为泰晤士河谷科技公司。');
INSERT INTO `words` VALUES (6992, 'remnant', 'ˈremnənt', 'n.', '残余部分;剩余部分;(织物的)零头，零料;', 'After twenty-four hours of fighting, the remnants of the force were fleeing...', '战斗持续了24个小时后，该部队残部打算溜掉。');
INSERT INTO `words` VALUES (6993, 'remonstrate', 'ˈremənstreɪt', 'v.', '抗议;抱怨;埋怨;', 'He remonstrated with the referee...', '他向裁判抗议。');
INSERT INTO `words` VALUES (6994, 'remorse', 'rɪˈmɔːs', 'n.', '懊悔;非常遗憾;自责;', 'He was full of remorse...', '他充满了悔恨。');
INSERT INTO `words` VALUES (6995, 'remorseful', 'rɪˈmɔːsfl', 'adj.', '懊悔的;悔恨的;', 'He was genuinely remorseful.', '他真心实意地感到懊悔。');
INSERT INTO `words` VALUES (6996, 'remote', 'rɪˈməʊt', 'adj.', '偏远的;偏僻的;遥远的;', 'Landslides have cut off many villages in remote areas.', '滑坡使边远地区的许多村庄与外界隔绝。');
INSERT INTO `words` VALUES (6997, 'remotely', 'rɪˈməʊtli', 'adv.', '微弱地;细微地;程度很低地;', 'We had never seen anything remotely like it before...', '我们以前从来没有见过任何类似的事情。');
INSERT INTO `words` VALUES (6998, 'remoteness', '', 'n.', '远离，遥远;', 'Landslides have cut off many villages in remote areas.', '滑坡使边远地区的许多村庄与外界隔绝。');
INSERT INTO `words` VALUES (6999, 'removable', 'rɪˈmuːvəbl', 'adj.', '可去除的;可取出的;', '...a cake tin with a removable base.', '有活动底座的蛋糕模');
INSERT INTO `words` VALUES (7000, 'removal', 'rɪˈmuːvl', 'n.', '移动;调动;去除;', 'What they expected to be the removal of a small lump turned out to be major surgery...', '他们原以为只需切除小肿块的手术最后变成了一次大手术。');
INSERT INTO `words` VALUES (7001, 'remove', 'rɪˈmuːv', 'v.', '移开;拿开;去掉;', 'As soon as the cake is done, remove it from the oven...', '蛋糕一烤好，立即把它从烤箱中取出。');
INSERT INTO `words` VALUES (7002, 'removed', 'rɪˈmuːvd', 'adj.', '相异的;不同的;', 'He found it hard to concentrate on conversation so far removed from his present preoccupations...', '他发现很难专注于谈话，因为谈话内容和自己目前的当务之急毫不相干。');
INSERT INTO `words` VALUES (7003, 'remunerate', 'rɪˈmjuːnəreɪt', 'v.', '酬劳;付酬给;', 'You will be remunerated and so will your staff.', '你将得到酬劳，你的职员也如此。');
INSERT INTO `words` VALUES (7004, 'remuneration', 'rɪˌmjuːnəˈreɪʃn', 'n.', '酬金;薪水;报酬;', '...the continuing marked increase in the remuneration of the company\'s directors...', '公司董事薪酬的持续显著增长');
INSERT INTO `words` VALUES (7005, 'remunerative', 'rɪˈmjuːnərətɪv', 'adj.', '报酬丰厚的;', 'A doctor advised her to seek remunerative employment.', '一个医生建议她去找有酬劳的工作。');
INSERT INTO `words` VALUES (7006, 'renaissance', 'rɪˈneɪsns', 'n.', '文艺复兴(欧洲14、15和16世纪时，人们以古希腊罗马的思想文化来繁荣文学艺术);(某一学科或艺术形式等衰落后的)复兴;', '...the Renaissance masterpieces in London\'s galleries...', '伦敦美术馆里文艺复兴时期的杰作');
INSERT INTO `words` VALUES (7007, 'renal', 'ˈriːnl', 'adj.', '肾脏的;与肾脏相关的;', 'He collapsed from acute renal failure...', '他的身体因为严重的肾衰竭而垮掉了。');
INSERT INTO `words` VALUES (7008, 'rename', 'ˌriːˈneɪm', 'v.', '重新命名;给…改名;', 'Tel Aviv\'s Kings Square was renamed Yitzhak Rabin Square...', '特拉维夫的国王广场更名为伊扎克·拉宾广场。');
INSERT INTO `words` VALUES (7009, 'rend', 'rend', 'v.', '撕开;撕碎;', '...pain that rends the heart.', '钻心的疼痛');
INSERT INTO `words` VALUES (7010, 'render', 'ˈrendə(r)', 'v.', '使成为;使变得;使处于某状态;', 'It contained so many errors as to render it worthless.', '它错误太多,所以毫无用处。');
INSERT INTO `words` VALUES (7011, 'rendering', 'ˈrendərɪŋ', 'n.', '演奏;扮演;表演;', '...a rendering of Verdi\'s Requiem by the BBC Symphony Orchestra.', '由BBC交响乐团演奏的威尔第的《安魂曲》');
INSERT INTO `words` VALUES (7012, 'rendition', 'renˈdɪʃn', 'n.', '表演;演唱;演奏;', 'The musicians burst into a rousing rendition of \'Paddy Casey\'s Reel\'.', '这些音乐家激情演绎了《帕迪·凯西的里尔舞曲》。');
INSERT INTO `words` VALUES (7013, 'renege', 'rɪˈniːɡ', 'v.', '违背(诺言);背信弃义;食言;', 'If someone reneged on a deal, they could never trade here again.', '谁要是违背了约定，就永远不能再在这里进行交易。');
INSERT INTO `words` VALUES (7014, 'renew', 'rɪˈnjuː', 'v.', '重新开始;中止后继续;使继续有效;', 'He renewed his attack on government policy towards Europe...', '他重新开始对政府的欧洲政策进行抨击。');
INSERT INTO `words` VALUES (7015, 'renewable', 'rɪˈnjuːəbl', 'adj.', '可更新的;可再生的;可恢复的;', '...renewable energy sources.', '可再生能源');
INSERT INTO `words` VALUES (7016, 'renewal', 'rɪˈnjuːəl', 'n.', '恢复;更新;重新开始;', 'They will discuss the possible renewal of diplomatic relations...', '他们将讨论恢复外交关系的可能性。');
INSERT INTO `words` VALUES (7017, 'renounce', 'rɪˈnaʊns', 'v.', '声明放弃;宣布放弃;宣布与…决裂;', 'After a period of imprisonment she renounced terrorism...', '在被囚禁一段时间之后，她宣布放弃恐怖主义。');
INSERT INTO `words` VALUES (7018, 'renovate', 'ˈrenəveɪt', 'v.', '修复;翻新;重新粉刷;', 'The couple spent thousands renovating the house...', '这对夫妇花了几千元来翻新房子。');
INSERT INTO `words` VALUES (7019, 'renovation', 'ˌrenə\'veɪʃ(ə)n', 'n.', '革新;翻修;修整;', 'The couple spent thousands renovating the house...', '这对夫妇花了几千元来翻新房子。');
INSERT INTO `words` VALUES (7020, 'renown', 'rɪˈnaʊn', 'n.', '名誉;声望;', 'She used to be a singer of some renown...', '她曾是位小有名气的歌手。');
INSERT INTO `words` VALUES (7021, 'renowned', 'rɪˈnaʊnd', 'adj.', '有名的;闻名的;受尊敬的;', 'The area is renowned for its Romanesque churches...', '这个地区以其罗马式的教堂闻名。');
INSERT INTO `words` VALUES (7022, 'rent', 'rent', 'n.', '租金;破裂处;裂口;', 'She rents a house with three other girls...', '她和其他3个女孩合租一套房子。');
INSERT INTO `words` VALUES (7023, 'rental', 'ˈrentl', 'n.', '租金;出租;租赁;', 'We can organise car rental from Chicago O\'Hare Airport.', '我们可以安排提供从芝加哥奥黑尔机场出发的租车服务。');
INSERT INTO `words` VALUES (7024, 'rented', 'ˈrentɪd', 'adj.', '租用的;租借的;', 'She rents a house with three other girls...', '她和其他3个女孩合租一套房子。');
INSERT INTO `words` VALUES (7025, 'rent-free', 'ˌrent ˈfriː', 'adj.', '免租金的;不收租金的;', 'He was given a new rent-free apartment.', '他得到了一个新的免租金公寓。');
INSERT INTO `words` VALUES (7026, 'renunciation', 'rɪˌnʌnsiˈeɪʃn', 'n.', '声明摒弃;宣布放弃;弃绝物质享受;', 'The talks were dependent on a renunciation of terrorism.', '会谈以放弃恐怖主义为前提。');
INSERT INTO `words` VALUES (7027, 'reopen', 'ˌriːˈəʊpən', 'v.', '重新开业;重新开放(商店、剧场等);重新处理;', 'Iran reopened its embassy in London...', '伊朗在伦敦重开使馆。');
INSERT INTO `words` VALUES (7028, 'rep', 'rep', 'n.', '推销员;销售代表;代表;', 'I\'d  been working as a sales rep for a photographic company.', '我一直在一家摄影公司做销售代表。');
INSERT INTO `words` VALUES (7029, 'repair', 'rɪˈpeə(r)', 'v.', '修理;修补;修缮;', 'Goldsmith has repaired the roof to ensure the house is wind-proof...', '戈德史密斯修理了屋顶，以确保房屋能够抗风。');
INSERT INTO `words` VALUES (7030, 'repairman', 'rɪˈpeəmæn', 'n.', '修理工;', '...a cheerful telephone repairman.', '开朗的电话修理工');
INSERT INTO `words` VALUES (7031, 'repairmen', '', 'n.', '修理工;', '...a cheerful telephone repairman.', '开朗的电话修理工');
INSERT INTO `words` VALUES (7032, 'repairs', 'rɪˈpeəz', 'v.', '修理;修补;修缮;', 'Goldsmith has repaired the roof to ensure the house is wind-proof...', '戈德史密斯修理了屋顶，以确保房屋能够抗风。');
INSERT INTO `words` VALUES (7033, 'reparation', 'ˌrepəˈreɪʃn', 'n.', '(战败国的)赔款;赔偿;弥补;', 'Israel accepted billions of dollars in war reparations.', '以色列得到了几十亿美元的战争赔款。');
INSERT INTO `words` VALUES (7034, 'repartee', 'ˌrepɑːˈtiː', 'n.', '机智而又巧妙的应对(或回答);', 'She was good at repartee.', '她善于机智地应答。');
INSERT INTO `words` VALUES (7035, 'repatriate', 'ˌriːˈpætrieɪt', 'v.', '遣送回国;遣返;寄(钱)回国;', 'It was not the policy of the government to repatriate genuine refugees...', '遣返真正的难民回国并非政府的政策。');
INSERT INTO `words` VALUES (7036, 'repay', 'rɪˈpeɪ', 'v.', '归还;偿还;清偿;', 'It will take 30 years to repay the loan...', '还清贷款需要30年的时间。');
INSERT INTO `words` VALUES (7037, 'repeal', 'rɪˈpiːl', 'v.', '废除，撤销，废止(法规);', 'The government has just repealed the law segregating public facilities.', '政府刚刚废止了不同种族分开使用公共设施的法令。');
INSERT INTO `words` VALUES (7038, 'repeat', 'rɪˈpiːt', 'v.', '重复;重说;重写;', 'He repeated that he had been misquoted...', '他反复说他的话被错误引用了。');
INSERT INTO `words` VALUES (7039, 'repeated', 'rɪˈpiːtɪd', 'adj.', '重复的;反复发生的;', 'Mr Lawssi apparently did not return the money, despite repeated reminders...', '尽管被提醒了很多次，劳西先生显然没有把钱退回来。');
INSERT INTO `words` VALUES (7040, 'repeatedly', 'rɪˈpiːtɪdli', 'adv.', '重复地;反复地;再三地;', 'Both men have repeatedly denied the allegations...', '两个人都一再否认指控。');
INSERT INTO `words` VALUES (7041, 'repeating', 'rɪˈpiːtɪŋ', 'v.', '重复;重说;重写;', 'He repeated that he had been misquoted...', '他反复说他的话被错误引用了。');
INSERT INTO `words` VALUES (7042, 'repel', 'rɪˈpel', 'v.', '击退;驱逐;推开;', 'They have fifty thousand troops along the border ready to repel any attack.', '他们有5万人的部队驻扎在边境，随时准备击退任何进攻。');
INSERT INTO `words` VALUES (7043, 'repent', 'rɪˈpent', 'v.', '后悔;悔过;忏悔;', 'Those who refuse to repent, he said, will be punished...', '那些拒不悔改的人，他说，将会受到惩罚。');
INSERT INTO `words` VALUES (7044, 'repentance', 'rɪˈpentəns', 'n.', '后悔;懊悔;悔过;', 'They showed no repentance during their trial.', '在他们受审的过程中没有表现出一丝悔意。');
INSERT INTO `words` VALUES (7045, 'repentant', 'rɪˈpentənt', 'adj.', '后悔的;悔过的;表示悔改的;', 'He was feeling guilty and depressed, repentant and scared.', '他感到内疚、沮丧、后悔和害怕。');
INSERT INTO `words` VALUES (7046, 'repercussion', 'ˌriːpəˈkʌʃn', 'n.', '(间接的)影响，反响，恶果;', 'It was an effort which was to have painful repercussions...', '这么做会带来极坏的负面影响。');
INSERT INTO `words` VALUES (7047, 'repertoire', 'ˈrepətwɑː(r)', 'n.', '(总称某人的)可表演项目;(某人的)全部才能，全部本领;', 'Meredith D\'Ambrosio has thousands of songs in her repertoire.', '梅雷迪思·丹布罗西奥会唱几千首歌曲。');
INSERT INTO `words` VALUES (7048, 'repertory', 'ˈrepətri', 'n.', '保留剧目轮演;', '...a well-known repertory company in Boston...', '波士顿有名的剧目轮演剧团');
INSERT INTO `words` VALUES (7049, 'repetition', 'ˌrepəˈtɪʃn', 'n.', '重复;重做;重说;', 'Today the city government has taken measures to prevent a repetition of last year\'s confrontation...', '今天市政府已经采取措施，防止去年的冲突重演。');
INSERT INTO `words` VALUES (7050, 'repetitive', 'rɪˈpetətɪv', 'adj.', '重复乏味的;多次重复的;', '...factory workers who do repetitive jobs...', '从事重复性工作的工厂工人');
INSERT INTO `words` VALUES (7051, 'rephrase', 'ˌriːˈfreɪz', 'v.', '换个说法说，改变词句(以使意思更清楚);', 'Holidays can be horrendous. All right, I\'ll rephrase that. The first few days are invariably hell...', '假期可能会糟糕透顶。好吧，让我换一种说法。最初的几天总是如同炼狱。');
INSERT INTO `words` VALUES (7052, 'replace', 'rɪˈpleɪs', 'v.', '代替;取代;(用…)替换;', 'The council tax replaces the poll tax next April.', '市政税在明年4月将取代人头税。');
INSERT INTO `words` VALUES (7053, 'replaceable', 'rɪˈpleɪsəbl', 'adj.', '可替换的;可代替的;', '...replaceable butane gas cartridges.', '可替换丁烷气贮气瓶');
INSERT INTO `words` VALUES (7054, 'replacement', 'rɪˈpleɪsmənt', 'n.', '替换;更换;替代品;', '...the replacement of damaged or lost books.', '受损或丢失图书的更换或补充');
INSERT INTO `words` VALUES (7055, 'replenish', 'rɪˈplenɪʃ', 'v.', '补充;重新装满;', 'Three hundred thousand tons of cereals are needed to replenish stocks...', '需要30万吨谷类补充库存。');
INSERT INTO `words` VALUES (7056, 'replete', 'rɪˈpliːt', 'adj.', '充满;充足;很饱;', 'The Harbor was replete with boats...', '港口里满是船只。');
INSERT INTO `words` VALUES (7057, 'replica', 'ˈreplɪkə', 'n.', '复制品;仿制品;', '...a human-sized replica of the Statue of Liberty...', '真人大小的自由女神像复制品');
INSERT INTO `words` VALUES (7058, 'replicate', 'ˈreplɪkeɪt', 'v.', '复制;(精确地)仿制;再造;', 'He invited her to his laboratory to see if she could replicate the experiment.', '他邀请她到他的实验室看她能否复制该实验。');
INSERT INTO `words` VALUES (7059, 'replication', 'ˌreplɪ\'keɪʃ(ə)n', 'n.', '(绘画等的)复制;拷贝;重复(实验);', 'He invited her to his laboratory to see if she could replicate the experiment.', '他邀请她到他的实验室看她能否复制该实验。');
INSERT INTO `words` VALUES (7060, 'reply', 'rɪˈplaɪ', 'v.', '回答;答复;回应;', '\'That\'s a nice dress,\' said Michael. \'Thanks,\' she replied solemnly...', '“那件衣服很好看，”迈克尔说。“谢谢，”她严肃地回答。');
INSERT INTO `words` VALUES (7061, 'report', 'rɪˈpɔːt', 'v.', '汇报;报告;通报;', 'They had been called in to clear drains after local people reported a foul smell...', '当地居民举报闻到恶臭气味后，他们被叫来清理排水沟。');
INSERT INTO `words` VALUES (7062, 'reportage', 'ˌrepɔːˈtɑːʒ', 'n.', '新闻报道;报道风格;报道文体;', '...the magazine\'s acclaimed mix of reportage and fashion.', '该杂志令人称赞的新闻报道和时尚信息的组合');
INSERT INTO `words` VALUES (7063, 'reportedly', 'rɪˈpɔːtɪdli', 'adv.', '据说;据报道;据传闻;', 'More than two hundred people have reportedly been killed in the past week\'s fighting...', '据说在上一周的战斗中有200多人丧生。');
INSERT INTO `words` VALUES (7064, 'reporter', 'rɪˈpɔːtə(r)', 'n.', '记者;通讯员;', '...a TV reporter.', '电视记者');
INSERT INTO `words` VALUES (7065, 'repose', 'rɪˈpəʊz', 'n.', '休息;睡眠;平静;', 'He had a still, almost blank face in repose...', '他休息时面部平静，几乎毫无表情。');
INSERT INTO `words` VALUES (7066, 'repository', 'rɪˈpɒzətri', 'n.', '仓库;贮藏室;存放处;', 'A church in Moscow became a repository for police files.', '莫斯科的一座教堂成为警方存放档案的地点。');
INSERT INTO `words` VALUES (7067, 'repossess', 'ˌriːpəˈzes', 'v.', '(因买者未如期付款而)收回(商品)，重新拥有(房地产等);', 'His car was repossessed by the company...', '他的汽车因为欠款被公司收回。');
INSERT INTO `words` VALUES (7068, 'reprehensible', 'ˌreprɪˈhensəbl', 'adj.', '不道德的;应受指责的;应受谴责的;', 'Mr Cramer said the violence by anti-government protestors was reprehensible.', '克拉默先生说反政府示威者的暴力活动应该受到谴责。');
INSERT INTO `words` VALUES (7069, 'represent', 'ˌreprɪˈzent', 'v.', '代表;作为…的代言人;维护…的利益;', '...the politicians we elect to represent us...', '我们选举出来代表我们的政界人士');
INSERT INTO `words` VALUES (7070, 'representation', 'ˌreprɪzenˈteɪʃn', 'n.', '表现;描述;描绘;', 'Puerto Ricans are U.S. citizens but they have no representation in Congress.', '波多黎各人是美国公民，但是他们在国会里没有代表。');
INSERT INTO `words` VALUES (7071, 'representative', 'ˌreprɪˈzentətɪv', 'n.', '代表;销售代表;销售代理;', '...trade union representatives...', '工会代表');
INSERT INTO `words` VALUES (7072, 'repress', 'rɪˈpres', 'v.', '克制;压抑;抑制;', 'People who repress their emotions risk having nightmares...', '压抑情感的人容易做噩梦。');
INSERT INTO `words` VALUES (7073, 'repressed', 'rɪˈprest', 'adj.', '压抑的;克制的;受压抑的;', 'Some have charged that the Puritans were sexually repressed and inhibited.', '一些人谴责说清教徒的性欲受到压抑，并且个性拘谨。');
INSERT INTO `words` VALUES (7074, 'repression', 'rɪˈpreʃn', 'n.', '压制;镇压;抑制;', '...a society conditioned by violence and repression.', '受暴力和镇压制约的社会');
INSERT INTO `words` VALUES (7075, 'repressive', 'rɪˈpresɪv', 'adj.', '压制的;专制的;严厉的;', 'The military regime in power was unpopular and repressive.', '当权的军政府压制人民，不得人心。');
INSERT INTO `words` VALUES (7076, 'reprieve', 'rɪˈpriːv', 'v.', '撤销…的死刑;缓期执行…的死刑;取消关闭;', 'Fourteen people, waiting to be hanged for the murder of a former prime minister, have been reprieved.', '因谋杀前首相而即将处以绞刑的14个人已获缓刑。');
INSERT INTO `words` VALUES (7077, 'reprimand', 'ˈreprɪmɑːnd', 'v.', '申斥;训斥;斥责;', 'He was reprimanded by a teacher for talking in the corridor...', '他因为在走廊里讲话受到了一位老师的斥责。');
INSERT INTO `words` VALUES (7078, 'reprisal', 'rɪˈpraɪzl', 'n.', '报复;报复行动;', 'There were fears that some of the Western hostages might be killed in reprisal...', '有人担心一些西方人质可能会受到报复性杀害。');
INSERT INTO `words` VALUES (7079, 'reproach', 'rɪˈprəʊtʃ', 'n.', '责备;批评;责备的话语;', 'She is quick to reproach anyone who doesn\'t live up to her own high standards...', '只要有人不符合她自己的高标准，她马上就会加以指责。');
INSERT INTO `words` VALUES (7080, 'reprobate', 'ˈreprəbeɪt', 'n.', '堕落的人;不道德的人;', '...a drunken reprobate.', '一个行为不检的醉鬼');
INSERT INTO `words` VALUES (7081, 'reproduce', 'ˌriːprəˈdjuːs', 'v.', '复制;再生产;再制造;', 'I shall not try to reproduce the policemen\'s English...', '我不会试图去模仿那个警察讲的英语。');
INSERT INTO `words` VALUES (7082, 'reproduction', 'ˌriːprəˈdʌkʃn', 'n.', '生殖;繁殖;复制;', '...a reproduction of a popular religious painting.', '一幅颇受欢迎的宗教画复制品');
INSERT INTO `words` VALUES (7083, 'reproductive', 'ˌriːprəˈdʌktɪv', 'adj.', '生殖的;繁殖的;', '...the female reproductive system.', '女性生殖系统');
INSERT INTO `words` VALUES (7084, 'reproof', 'rɪˈpruːf', 'n.', '责备;谴责;非难;', 'She raised her eyebrows in reproof.', '她竖起眉毛，满含责备。');
INSERT INTO `words` VALUES (7085, 'reprove', 'rɪˈpruːv', 'v.', '指责;责备;非难;', '\'There\'s no call for talk like that,\' Mrs Evans reproved him...', '“没有必要说那种话，”埃文斯夫人斥责他道。');
INSERT INTO `words` VALUES (7086, 'reptilian', 'repˈtɪliən', 'adj.', '爬行的;爬行动物的;像爬行动物的;有爬行动物特征的;', '...a prehistoric jungle occupied by reptilian creatures.', '爬行动物主宰的史前丛林');
INSERT INTO `words` VALUES (7087, 'republic', 'rɪˈpʌblɪk', 'n.', '共和国;共和政体;', 'In 1918, Austria became a republic.', '1918年，奥地利成为共和国。');
INSERT INTO `words` VALUES (7088, 'republican', 'rɪˈpʌblɪkən', 'n.', '拥护共和政体的人;共和主义者;共和党党员;', '...the nations that had adopted the republican form of government.', '实行共和政体的国家');
INSERT INTO `words` VALUES (7089, 'repudiate', 'rɪˈpjuːdieɪt', 'v.', '拒绝;不接受;回绝;', 'Leaders urged people to turn out in large numbers to repudiate the violence...', '领导们力劝人们集体出面反对暴力。');
INSERT INTO `words` VALUES (7090, 'repugnance', 'rɪˈpʌɡnəns', 'n.', '嫌恶;恶心;强烈的反感;', 'The odour of vitamin in skin is repugnant to insects...', '昆虫很讨厌皮肤内维生素的气味。');
INSERT INTO `words` VALUES (7091, 'repugnant', 'rɪˈpʌɡnənt', 'adj.', '使嫌恶;使反感;不得人心;', 'The odour of vitamin in skin is repugnant to insects...', '昆虫很讨厌皮肤内维生素的气味。');
INSERT INTO `words` VALUES (7092, 'repulse', 'rɪˈpʌls', 'v.', '使厌恶;使反感;击退;', 'Evil has charisma. Though people are repulsed by it, they also are drawn to its power.', '邪恶具有吸引力。虽然人们憎恶它，但还是被它的力量所吸引。');
INSERT INTO `words` VALUES (7093, 'repulsion', 'rɪˈpʌlʃn', 'n.', '嫌恶感;强烈的反感;憎恶;', 'She gave a dramatic shudder of repulsion.', '她恶心得直发抖。');
INSERT INTO `words` VALUES (7094, 'repulsive', 'rɪˈpʌlsɪv', 'adj.', '令人厌恶的;令人反感的;十分讨厌的;', '...repulsive fat white slugs.', '让人恶心的白白胖胖的鼻涕虫');
INSERT INTO `words` VALUES (7095, 'reputable', 'ˈrepjətəbl', 'adj.', '声誉好的;值得信赖的;', 'You are well advised to buy your car through a reputable dealer.', '强烈建议您通过值得信赖的经销商购车。');
INSERT INTO `words` VALUES (7096, 'reputation', 'ˌrepjuˈteɪʃn', 'n.', '名誉;名声;', 'Alice Munro has a reputation for being a very depressing writer.', '艾丽斯·芒罗以文风沉郁闻名。');
INSERT INTO `words` VALUES (7097, 'repute', 'rɪˈpjuːt', 'n.', '名誉;名声;', 'He was a writer of repute...', '他曾经是一位颇有名望的作者。');
INSERT INTO `words` VALUES (7098, 'reputed', 'rɪˈpjuːtɪd', 'adj.', '所谓;普遍认为;号称;', 'The monster, which is reputed to live in the deep dark water of a Scottish loch...', '相传生活在苏格兰一个漆黑深水湖中的妖怪');
INSERT INTO `words` VALUES (7099, 'request', 'rɪˈkwest', 'n.', '(正式或礼貌的)要求，请求;要求的事;', 'Mr Dennis said he had requested access to a telephone...', '丹尼斯先生说他已经请求使用电话。');
INSERT INTO `words` VALUES (7100, 'requested', 'rɪˈkwestɪd', 'v.', '(正式或礼貌地)请求，要求;', 'Mr Dennis said he had requested access to a telephone...', '丹尼斯先生说他已经请求使用电话。');
INSERT INTO `words` VALUES (7101, 'require', 'rɪˈkwaɪə(r)', 'v.', '需要;依靠;依赖;', 'If you require further information, you should consult the registrar...', '如果需要进一步了解信息，请咨询教务主任。');
INSERT INTO `words` VALUES (7102, 'required', 'rɪˈkwaɪəd', 'v.', '需要;依靠;依赖;', 'If you require further information, you should consult the registrar...', '如果需要进一步了解信息，请咨询教务主任。');
INSERT INTO `words` VALUES (7103, 'requirement', 'rɪˈkwaɪəmənt', 'n.', '所需的(或所要的)东西;必要条件;必备的条件;', 'Its products met all legal requirements...', '它的产品符合所有的法律要求。');
INSERT INTO `words` VALUES (7104, 'requisite', 'ˈrekwɪzɪt', 'adj.', '必需的;必备的;必不可少的;', 'She filled in the requisite paperwork.', '她完成了必要的书面工作。');
INSERT INTO `words` VALUES (7105, 'requisition', 'ˌrekwɪˈzɪʃn', 'n.', '正式要求;征用;需要;', 'Authorities requisitioned hotel rooms to lodge more than 3,000 stranded Christmas vacationers.', '当局征用旅馆房间安顿了3,000多名无处落脚的圣诞度假者。');
INSERT INTO `words` VALUES (7106, 'reschedule', 'ˌriːˈʃedjuːl', 'v.', '将…改期;修改…的时间表;重新安排;', 'Since I\'ll be away, I\'d like to reschedule the meeting...', '既然我到时候不在，我想重新安排一下会议的时间。');
INSERT INTO `words` VALUES (7107, 'rescind', 'rɪˈsɪnd', 'v.', '废除;取消;撤销;', 'Trade Union leaders have demanded the government rescind the price rise.', '工会领导已经要求政府阻止价格上涨。');
INSERT INTO `words` VALUES (7108, 'rescue', 'ˈreskjuː', 'v.', '营救;援救;抢救;', 'Helicopters rescued nearly 20 people from the roof of the burning building...', '直升机从失火大楼的楼顶救出了将近20人。');
INSERT INTO `words` VALUES (7109, 'rescuer', 'ˈrɛskjʊə', 'n.', '救助者;救星;', 'Helicopters rescued nearly 20 people from the roof of the burning building...', '直升机从失火大楼的楼顶救出了将近20人。');
INSERT INTO `words` VALUES (7110, 'research', 'rɪˈsɜːtʃ', 'n.', '研究;调查;探索;', '65 percent of the 1987 budget went for nuclear weapons research and production.', '1987年65％的预算经费被用于核武器的研究和生产。');
INSERT INTO `words` VALUES (7111, 'researcher', 'rɪˈsɜː(r)tʃə(r)', 'n.', '研究员;科研工作者;', '65 percent of the 1987 budget went for nuclear weapons research and production.', '1987年65％的预算经费被用于核武器的研究和生产。');
INSERT INTO `words` VALUES (7112, 'resell', 'ˌriːˈsel', 'v.', '转售;转卖;', 'Shopkeepers buy them in bulk and resell them for £150 each...', '店主们先大量购进，然后再以150英镑的单价将它们转卖出去。');
INSERT INTO `words` VALUES (7113, 'resemblance', 'rɪˈzembləns', 'n.', '相似;相像;', 'There was a remarkable resemblance between him and Pete...', '他和皮特之间有着惊人的相似之处。');
INSERT INTO `words` VALUES (7114, 'resemble', 'rɪˈzembl', 'v.', '看起来像;显得像;像;', 'Some of the commercially produced venison resembles beef in flavour...', '有些商业化养殖的鹿肉味道和牛肉很相似。');
INSERT INTO `words` VALUES (7115, 'resent', 'rɪˈzent', 'v.', '愤恨;感到气愤;愤愤不平;', 'She resents her mother for being so tough on her...', '她怨恨母亲对她太严厉。');
INSERT INTO `words` VALUES (7116, 'resentful', 'rɪˈzentfl', 'adj.', '感到气愤的;憎恨的;愤慨的;', 'At first I felt very resentful and angry about losing my job...', '起初我对丢掉工作感到非常怨恨和恼怒。');
INSERT INTO `words` VALUES (7117, 'resentment', 'rɪˈzentmənt', 'n.', '愤恨;怨恨;', 'She expressed resentment at being interviewed by a social worker...', '她表达了对被一位社工采访的愤恨。');
INSERT INTO `words` VALUES (7118, 'reservation', 'ˌrezəˈveɪʃn', 'n.', '预订;预约;保留意见;', 'I told him my main reservation about his film was the ending...', '我告诉他我主要是对他电影的结尾有保留意见。');
INSERT INTO `words` VALUES (7119, 'reserve', 'rɪˈzɜːv', 'v.', '预订，预约(座位、席位、房间等);保留;贮备;', 'A double room with a balcony overlooking the sea had been reserved for him...', '特地为他预留了一间带阳台的海景双人房。');
INSERT INTO `words` VALUES (7120, 'reserved', 'rɪˈzɜːvd', 'adj.', '内向的;寡言少语的;矜持的;', 'He was unemotional, quite quiet, and reserved...', '他感情淡漠，沉默寡言，性格内敛。');
INSERT INTO `words` VALUES (7121, 'reshape', 'ˌriːˈʃeɪp', 'v.', '改变…的形状(或结构);重塑;改组;', 'If they succeed on Europe, then they will have reshaped the political and economic map of the world...', '如果他们在欧洲获得成功，他们将会改变世界政治和经济格局。');
INSERT INTO `words` VALUES (7122, 'reside', 'rɪˈzaɪd', 'v.', '居住在;定居于;', 'Margaret resides with her invalid mother in a London suburb...', '玛格丽特同她病弱的母亲住在伦敦郊区。');
INSERT INTO `words` VALUES (7123, 'residence', 'ˈrezɪdəns', 'n.', '住所;住房;(尤指)宅第，豪宅;', '...hotels and private residences.', '旅馆和私人住宅');
INSERT INTO `words` VALUES (7124, 'residency', 'ˈrezɪdənsi', 'n.', '(艺术家、作家、音乐家为某机构工作的)驻留时间，驻留期;居住;定居;', 'He applied for British residency...', '他申请了在英国的居住权。');
INSERT INTO `words` VALUES (7125, 'resident', 'ˈrezɪdənt', 'n.', '居民;住户;(旅馆的)住宿者，旅客，房客;', 'The Archbishop called upon the government to build more low cost homes for local residents...', '大主教呼吁政府为当地居民建造更多低成本的住宅。');
INSERT INTO `words` VALUES (7126, 'residential', 'ˌrezɪˈdenʃl', 'adj.', '适合居住的;住宅的;需要在某地居住的;', '...a smart residential area...', '整洁漂亮的住宅区');
INSERT INTO `words` VALUES (7127, 'residual', 'rɪˈzɪdjuəl', 'adj.', '剩余的;残留的;', '...residual radiation from nuclear weapons testing...', '核武器实验后残留的辐射');
INSERT INTO `words` VALUES (7128, 'residue', 'ˈrezɪdjuː', 'n.', '剩余物;残留物;残渣;', 'Always using the same shampoo means that a residue can build up on the hair.', '总是用同一种洗发水就意味着某种残留物会在头发上越积越多。');
INSERT INTO `words` VALUES (7129, 'resign', 'rɪˈzaɪn', 'v.', '辞职;辞去(某职务);', 'A hospital administrator has resigned over claims he lied to get the job...', '一位医院的管理人员辞职了，因为有人指责他为了得到这份工作而撒了谎。');
INSERT INTO `words` VALUES (7130, 'resignation', 'ˌrezɪɡˈneɪʃn', 'n.', '辞职;辞职信;辞呈;', 'Mr Morgan has offered his resignation and it has been accepted.', '摩根先生己经递交了辞呈，并已获准。');
INSERT INTO `words` VALUES (7131, 'resigned', 'rɪˈzaɪnd', 'adj.', '逆来顺受的;顺从的;', 'He is resigned to the noise and mess...', '他勉强接受了这里的喧嚣和脏乱。');
INSERT INTO `words` VALUES (7132, 'resilience', 'rɪˈzɪliəns', 'n.', '快速恢复的能力;适应力;还原能力;', 'Cotton is more resistant to being squashed and polyester is more resilient.', '棉布更抗压，而涤纶更有弹性。');
INSERT INTO `words` VALUES (7133, 'resilient', 'rɪˈzɪliənt', 'adj.', '可迅速恢复的;有适应力的;有弹性(或弹力)的;', 'Cotton is more resistant to being squashed and polyester is more resilient.', '棉布更抗压，而涤纶更有弹性。');
INSERT INTO `words` VALUES (7134, 'resin', 'ˈrezɪn', 'n.', '树脂;合成树脂;', 'The resin from which the oil is extracted comes from a small, tough tree.', '用于提取这种油的树脂取自于一种矮小坚韧的树。');
INSERT INTO `words` VALUES (7135, 'resist', 'rɪˈzɪst', 'v.', '抵制;阻挡;反抗;', 'She says she will resist a single European currency being imposed...', '她说她会反对在欧洲推行单一货币。');
INSERT INTO `words` VALUES (7136, 'resistance', 'rɪˈzɪstəns', 'n.', '反对;抵制;抗拒;', 'The US wants big cuts in European agricultural export subsidies, but this is meeting resistance.', '美国想大幅度削减欧洲农业出口补贴，但此举遭到了抵制。');
INSERT INTO `words` VALUES (7137, 'resistant', 'rɪˈzɪstənt', 'adj.', '抵抗的;有抵抗力的;抵制的;', 'Some people are very resistant to the idea of exercise.', '有些人十分反对锻炼。');
INSERT INTO `words` VALUES (7138, 'resolute', 'ˈrezəluːt', 'adj.', '坚决的;有决心的;', 'Voters perceive him as a decisive and resolute international leader...', '投票者认为他是一位坚定果断的国际领导人。');
INSERT INTO `words` VALUES (7139, 'resolutely', 'ˈrɛzəluːtli', 'adv.', '坚定地;奋力;', 'Voters perceive him as a decisive and resolute international leader...', '投票者认为他是一位坚定果断的国际领导人。');
INSERT INTO `words` VALUES (7140, 'resolution', 'ˌrezəˈluːʃn', 'n.', '决议;正式决定;(问题、分歧等的)解决，消除;', 'He replied that the UN had passed two major resolutions calling for a complete withdrawal.', '他答复说联合国已经通过了两项号召全面撤军的重要决议。');
INSERT INTO `words` VALUES (7141, 'resolve', 'rɪˈzɒlv', 'v.', '解决(问题或困难);决心;决定;', 'We must find a way to resolve these problems before it\'s too late...', '我们必须及时找到解决这些问题的方法。');
INSERT INTO `words` VALUES (7142, 'resolved', 'rɪˈzɒlvd', 'adj.', '下定决心;坚定;', 'Barnes was resolved to moving on when his contract expired.', '合同到期以后，巴恩斯决定做点别的。');
INSERT INTO `words` VALUES (7143, 'resonance', 'ˈrezənəns', 'n.', '洪亮;响亮;共鸣;', 'The ideas of order, security, family, religion and country had the same resonance for them as for Michael.', '秩序、安全、家庭、宗教和国家这些概念对于他们和迈克尔来说有着同样特殊的意义。');
INSERT INTO `words` VALUES (7144, 'resonant', 'ˈrezənənt', 'adj.', '嘹亮的;响亮的;回响的;', 'His voice sounded oddly resonant in the empty room...', '他的声音在这空荡荡的房间里听起来异常嘹亮。');
INSERT INTO `words` VALUES (7145, 'resonate', 'ˈrezəneɪt', 'v.', '产生共鸣;发出回响;回荡;', 'The bass guitar began to thump so loudly that it resonated in my head.', '低音电吉他开始砰砰作响，巨大的声音一直在我的脑海里回响。');
INSERT INTO `words` VALUES (7146, 'resort', 'rɪˈzɔːt', 'n.', '旅游胜地;度假胜地;诉诸;', 'His punishing work schedule had made him resort to drugs...', '异常紧张的工作安排使他开始吸毒。');
INSERT INTO `words` VALUES (7147, 'resound', 'rɪˈzaʊnd', 'v.', '回响;回荡;回荡着声音;', 'A roar of approval resounded through the Ukrainian parliament...', '一片赞成声在乌克兰议会中回响。');
INSERT INTO `words` VALUES (7148, 'resounding', 'rɪˈzaʊndɪŋ', 'adj.', '巨大的;令人瞩目的;响亮的;', 'There was a resounding slap as Andrew struck him violently across the face...', '安德鲁狠狠掴了他一巴掌，声音响亮干脆。');
INSERT INTO `words` VALUES (7149, 'resource', 'rɪˈsɔːs', 'n.', '资源;财力;有助于实现目标的东西;', 'Some families don\'t have the resources to feed themselves properly...', '有些家庭无力维持生活。');
INSERT INTO `words` VALUES (7150, 'resourceful', 'rɪˈsɔːsfl', 'adj.', '机敏的;足智多谋的;随机应变的;', 'He was amazingly inventive and resourceful, and played a major role in my career...', '他极具创造力，而且足智多谋，在我的职业生涯中起到了重要的作用。');
INSERT INTO `words` VALUES (7151, 'resources', 'rɪˈsɔːsɪz', 'n.', '资源;财力;有助于实现目标的东西;', 'Some families don\'t have the resources to feed themselves properly...', '有些家庭无力维持生活。');
INSERT INTO `words` VALUES (7152, 'respect', 'rɪˈspekt', 'n.', '尊敬;敬意;尊重;', 'I want him to respect me as a career woman...', '我希望他把我当作一个职业女性来尊重。');
INSERT INTO `words` VALUES (7153, 'respectability', 'rɪˌspektəˈbɪləti', 'n.', '体面;名望;得体;', 'He came from a perfectly respectable middle-class family...', '他出身于一个非常正派的中产阶级家庭。');
INSERT INTO `words` VALUES (7154, 'respectable', 'rɪˈspektəbl', 'adj.', '体面的;得体的;值得尊敬的;', 'He came from a perfectly respectable middle-class family...', '他出身于一个非常正派的中产阶级家庭。');
INSERT INTO `words` VALUES (7155, 'respecter', 'rɪˈspektə(r)', 'n.', '(信仰的)拥护者;重视(某种观念)的人;', 'Ford was a respecter of proprieties and liked to see things done properly.', '福特非常重视礼仪，喜欢把事情办得妥帖得体。');
INSERT INTO `words` VALUES (7156, 'respectful', 'rɪˈspektfl', 'adj.', '表示敬意的;尊敬的;', 'He was always so polite and respectful...', '他总是彬彬有礼，尊敬他人。');
INSERT INTO `words` VALUES (7157, 'respectfully', 'rɪ\'spektf(ə)li', 'adv.', '恭敬地;', 'He was always so polite and respectful...', '他总是彬彬有礼，尊敬他人。');
INSERT INTO `words` VALUES (7158, 'respecting', 'rɪˈspektɪŋ', 'prep.', '关于;', 'I want him to respect me as a career woman...', '我希望他把我当作一个职业女性来尊重。');
INSERT INTO `words` VALUES (7159, 'respective', 'rɪˈspektɪv', 'adj.', '分别的;各自的;', 'Steve and I were at very different stages in our respective careers...', '史蒂夫和我在各自的职业生涯中处于迥然不同的阶段。');
INSERT INTO `words` VALUES (7160, 'respectively', 'rɪˈspektɪvli', 'adv.', '分别;各自;顺序为;', 'Their sons, Ben and Jonathan, were three and six respectively...', '他们的儿子，本和乔纳森，分别是3岁和6岁。');
INSERT INTO `words` VALUES (7161, 'respiration', 'ˌrespəˈreɪʃn', 'n.', '呼吸;', 'His respiration grew fainter throughout the day.', '一天当中，他的呼吸越来越微弱。');
INSERT INTO `words` VALUES (7162, 'respirator', 'ˈrespəreɪtə(r)', 'n.', '人工呼吸器;防毒面具;口罩;', 'She was so ill that she was put on a respirator.', '她病得很严重，被戴上了人工呼吸器。');
INSERT INTO `words` VALUES (7163, 'respiratory', 'rəˈspɪrətri', 'adj.', '呼吸的;', '...people with severe respiratory problems...', '有严重呼吸问题的人');
INSERT INTO `words` VALUES (7164, 'respite', 'ˈrespaɪt', 'n.', '暂停;暂缓;短暂的延缓;', 'It was some weeks now since they had had any respite from shellfire.', '他们已经好几周没有在炮火中获得任何喘息的机会了。');
INSERT INTO `words` VALUES (7165, 'resplendent', 'rɪˈsplendənt', 'adj.', '辉煌的;灿烂的;华丽的;', 'Bessie, resplendent in royal blue velvet, was hovering beside the table.', '贝茜身穿华丽的深蓝色天鹅绒衣服，在桌旁徘徊。');
INSERT INTO `words` VALUES (7166, 'respond', 'rɪˈspɒnd', 'v.', '(口头或书面)回答，回应;作出反应;响应;', 'They are likely to respond positively to the President\'s request for aid...', '他们可能会对总统的援助要求作出积极回应。');
INSERT INTO `words` VALUES (7167, 'respondent', 'rɪˈspɒndənt', 'n.', '回答问题的人;(尤指)调查对象;被告;', '60 percent of the respondents said they disapproved of the president\'s performance.', '60％的调查对象表示不认可总统的表现。');
INSERT INTO `words` VALUES (7168, 'response', 'rɪˈspɒns', 'n.', '(口头的或书面的)回答，答复;反应;响应;', 'There has been no response to his remarks from the government...', '政府尚未对他的言论作出回应。');
INSERT INTO `words` VALUES (7169, 'responsibility', 'rɪˌspɒnsəˈbɪləti', 'n.', '责任;负责;事故责任;', 'Each manager had responsibility for just under 600 properties...', '每位经理负责将近600处房产。');
INSERT INTO `words` VALUES (7170, 'responsible', 'rɪˈspɒnsəbl', 'adj.', '有责任;负责;承担义务;', 'He still felt responsible for her death...', '他仍然觉得自己对她的死负有责任。');
INSERT INTO `words` VALUES (7171, 'responsibly', 'rɪˈspɒnsəbli', 'adv.', '明事理地;认真负责地;可信赖地;', 'He still felt responsible for her death...', '他仍然觉得自己对她的死负有责任。');
INSERT INTO `words` VALUES (7172, 'responsive', 'rɪˈspɒnsɪv', 'adj.', '反应敏捷;反应积极;反应热烈的;', 'Harriet was an easy, responsive little girl...', '哈丽雅特是一个随和机灵的小女孩。');
INSERT INTO `words` VALUES (7173, 'restart', 'ˈriːstɑːt , ˌriːˈstɑːt', 'v.', '(使)重新开始;', 'The commissioners agreed to restart talks as soon as possible...', '委员们同意尽快重新开始会谈。');
INSERT INTO `words` VALUES (7174, 'restate', 'ˌriːˈsteɪt', 'v.', '重申;重新表述;', 'He continued throughout to restate his opposition to violence...', '他一直不断重申他反对暴力。');
INSERT INTO `words` VALUES (7175, 'restaurant', 'ˈrestrɒnt', 'n.', '餐馆;餐厅;', 'They ate in an Italian restaurant in Forth Street...', '他们在第四大街的一家意大利餐馆吃了饭。');
INSERT INTO `words` VALUES (7176, 'restful', 'ˈrestfl', 'adj.', '闲适宁静的;使人感到悠闲的;', 'Adjust the lighting so it is soft and restful...', '调节灯光使其看上去既柔和又能给人带来舒缓感。');
INSERT INTO `words` VALUES (7177, 'restitution', 'ˌrestɪˈtjuːʃn', 'n.', '归还(真正物主);归还(赃物等);赔偿;', 'The victims are demanding full restitution...', '受害人要求全额赔偿。');
INSERT INTO `words` VALUES (7178, 'restive', 'ˈrestɪv', 'adj.', '难驾驭的;焦躁不安的;不耐烦的;', 'The audience grew restive.', '观众变得不耐烦了。');
INSERT INTO `words` VALUES (7179, 'restless', 'ˈrestləs', 'adj.', '坐立不安的;不耐烦的;没有真正休息的;', 'By 1982, she was restless and needed a new impetus for her talent.', '到了1982年，她感到厌倦了，需要新的动力来激发她的才能。');
INSERT INTO `words` VALUES (7180, 'restlessly', '', 'adv.', '不安地，慌张地，无休止地;', 'By 1982, she was restless and needed a new impetus for her talent.', '到了1982年，她感到厌倦了，需要新的动力来激发她的才能。');
INSERT INTO `words` VALUES (7181, 'restock', 'ˌriːˈstɒk', 'v.', '更新(旧物品);补充(货源);再补给;', 'I have to restock the freezer...', '我得往冰箱里再放点东西。');
INSERT INTO `words` VALUES (7182, 'restorative', 'rɪˈstɔːrətɪv', 'adj.', '恢复健康的;促使康复的;整容的;', 'She opened the door to her bedroom, thinking how restorative a hot bath would feel tonight.', '她打开了卧室的门，想着今晚洗个热水澡该多么令人惬意。');
INSERT INTO `words` VALUES (7183, 'restore', 'rɪˈstɔː(r)', 'v.', '恢复(某种情况或感受);使复原;使复位;', 'The army has recently been brought in to restore order...', '最近军队被调来重整秩序。');
INSERT INTO `words` VALUES (7184, 'restrain', 'rɪˈstreɪn', 'v.', '(尤指用武力)制止，阻止，管制;约束(自己);控制(自己);', 'Wally gripped my arm, partly to restrain me and partly to reassure me...', '沃利抓住我的手臂，一是想制止我，二是想稳住我。');
INSERT INTO `words` VALUES (7185, 'restrained', 'rɪˈstreɪnd', 'adj.', '克制的;有节制的;不艳丽的;', 'In the circumstances he felt he\'d been very restrained...', '在那种情况下，他觉得自己一直非常冷静。');
INSERT INTO `words` VALUES (7186, 'restraint', 'rɪˈstreɪnt', 'n.', '约束力;管制措施;制约因素;', 'The Prime Minister is calling for new restraints on trade unions...', '首相正在呼吁对工会施加新的限制措施。');
INSERT INTO `words` VALUES (7187, 'restrict', 'rɪˈstrɪkt', 'v.', '限制，限定(数量、范围等);束缚;妨碍;', 'There is talk of raising the admission requirements to restrict the number of students on campus...', '有传闻称将通过提高录取条件来限制在校学生的数目。');
INSERT INTO `words` VALUES (7188, 'restricted', 'rɪˈstrɪktɪd', 'adj.', '(大小或数量)有限的，很小的;(指能做的事)有限的，受限制的;受(法规)制约的;', '...the monotony of a heavily restricted diet...', '受到严格限制的饮食的单调');
INSERT INTO `words` VALUES (7189, 'restricting', 'rɪˈstrɪktɪŋ', 'v.', '限制，限定(数量、范围等);束缚;妨碍;', 'There is talk of raising the admission requirements to restrict the number of students on campus...', '有传闻称将通过提高录取条件来限制在校学生的数目。');
INSERT INTO `words` VALUES (7190, 'restriction', 'rɪˈstrɪkʃn', 'n.', '限制规定;限制法规;限制;', '...the lifting of restrictions on political parties and the news media...', '取消对政治党派和新闻媒体的限制');
INSERT INTO `words` VALUES (7191, 'restrictive', 'rɪˈstrɪktɪv', 'adj.', '限制性的;约束的;', 'Britain is to adopt a more restrictive policy on arms sales.', '英国将对武器出售采取更具限制性的政策。');
INSERT INTO `words` VALUES (7192, 'restructure', 'ˌriːˈstrʌktʃə(r)', 'v.', '调整结构;改组;重建;', 'The President called on educators and politicians to help him restructure American education...', '总统号召教育家和政治家们帮助他重新调整美国的教育体系。');
INSERT INTO `words` VALUES (7193, 'result', 'rɪˈzʌlt', 'n.', '后果;结果;结果(包括比分、得票、获胜者或当选者名单等);', 'Compensation is available for people who have developed asthma as a direct result of their work...', '直接因工作原因患上哮喘的人会获得赔偿。');
INSERT INTO `words` VALUES (7194, 'resultant', 'rɪˈzʌltənt', 'adj.', '因而发生的;因此而产生的;', 'At least a quarter of a million people have died in the fighting and the resultant famines.', '至少25万人死于这场战斗和由此引发的饥荒。');
INSERT INTO `words` VALUES (7195, 'resulting', 'rɪˈzʌltɪŋ', 'v.', '(因…)发生;(随…)产生;', 'Compensation is available for people who have developed asthma as a direct result of their work...', '直接因工作原因患上哮喘的人会获得赔偿。');
INSERT INTO `words` VALUES (7196, 'resume', 'rɪˈzjuːm', 'n.', '摘要;概述;概要;', 'After the war he resumed his duties at Emmanuel College...', '战争过后，他重新开始了在伊曼纽尔学院的工作。');
INSERT INTO `words` VALUES (7197, 'resurgence', 'rɪˈsɜːdʒəns', 'n.', '复苏;复兴;', 'Police say drugs traffickers are behind the resurgence of violence.', '警方说毒贩是暴力活动重新抬头的罪魁祸首。');
INSERT INTO `words` VALUES (7198, 'resurgent', 'rɪˈsɜːdʒənt', 'adj.', '复兴的;恢复生机活力的;再度流行的;', '...the threat from the resurgent nationalist movement.', '重新兴起的民族主义运动的威胁');
INSERT INTO `words` VALUES (7199, 'resurrect', 'ˌrezəˈrekt', 'v.', '重新应用;恢复使用;使复兴;', 'Attempts to resurrect the ceasefire have already failed once...', '为再次停火作出的努力已经失败了一次。');
INSERT INTO `words` VALUES (7200, 'resuscitate', 'rɪˈsʌsɪteɪt', 'v.', '使苏醒;使恢复知觉;', 'A policeman and then a paramedic tried to resuscitate her.', '一名警察和一位护理人员先后试图救活她。');
INSERT INTO `words` VALUES (7201, 'resuscitation', 'rɪˌsʌsɪˈteɪʃn', 'n.', '复活;复兴;', 'A policeman and then a paramedic tried to resuscitate her.', '一名警察和一位护理人员先后试图救活她。');
INSERT INTO `words` VALUES (7202, 'retailer', 'ˈriːteɪlə(r)', 'n.', '零售商;零售店;', 'Furniture and carpet retailers are among those reporting the sharpest annual decline in sales.', '据称年销售额下滑幅度最大的零售商中包括家具和地毯零售商。');
INSERT INTO `words` VALUES (7203, 'retain', 'rɪˈteɪn', 'v.', '保持;持有;保留;', 'The interior of the shop still retains a nineteenth-century atmosphere...', '这家商店的内部装修仍然保留着19世纪的风格。');
INSERT INTO `words` VALUES (7204, 'retainer', 'rɪˈteɪnə(r)', 'n.', '(聘请律师等的)预付费用，保留金;(为保留租房而付的)租房订金;牙齿固位体;', 'I\'ll need a five-hundred-dollar retainer...', '我将收取500美元的定金。');
INSERT INTO `words` VALUES (7205, 'retaliate', 'rɪˈtælieɪt', 'v.', '报复;反击;复仇;', 'I was sorely tempted to retaliate...', '我非常想报复。');
INSERT INTO `words` VALUES (7206, 'retaliation', 'rɪˌtæliˈeɪʃn', 'n.', '报复;', 'I was sorely tempted to retaliate...', '我非常想报复。');
INSERT INTO `words` VALUES (7207, 'retaliatory', 'rɪˈtæliətri', 'adj.', '报复性的;反击的;', 'There\'s been talk of a retaliatory blockade to prevent supplies getting through.', '有人在谈论实施报复性封锁，阻止供应物资通过。');
INSERT INTO `words` VALUES (7208, 'retardation', 'ˌriːtɑːˈdeɪʃn', 'n.', '延迟;减缓;落后;迟缓;', '...other parents whose children had mental retardation.', '其他智障儿童的家长');
INSERT INTO `words` VALUES (7209, 'retarded', 'rɪˈtɑːdɪd', 'adj.', '迟钝的;弱智的;智力发育迟缓的;', '...a special school for mentally retarded children.', '为智障孩子设立的特殊学校');
INSERT INTO `words` VALUES (7210, 'retell', 'ˌriːˈtel', 'v.', '(通常以不同的方式)复述，重新讲述;', 'Lucilla often asks her sisters to retell the story...', '露西拉经常要姐姐们把这个故事反复讲给她听。');
INSERT INTO `words` VALUES (7211, 'retention', 'rɪˈtenʃn', 'n.', '保持;维持;保留;', 'They supported the retention of a strong central government.', '他们赞成保留一个强大的中央政府。');
INSERT INTO `words` VALUES (7212, 'retentive', 'rɪˈtentɪv', 'adj.', '有记性的;记忆力强的;', 'Luke had an amazingly retentive memory.', '卢克记忆力惊人。');
INSERT INTO `words` VALUES (7213, 'reticence', '', 'n.', '沉默;含蓄;', 'She is so reticent about her achievements...', '她对自己的成就缄口不言。');
INSERT INTO `words` VALUES (7214, 'reticent', 'ˈretɪsnt', 'adj.', '寡言少语;不愿与人交谈;有保留;', 'She is so reticent about her achievements...', '她对自己的成就缄口不言。');
INSERT INTO `words` VALUES (7215, 'retinue', 'ˈretɪnjuː', 'n.', '随行人员;扈从;', 'Mind trainers are now part of a tennis star\'s retinue.', '现在，网球明星的随行人员中有些是心理培训师。');
INSERT INTO `words` VALUES (7216, 'retire', 'rɪˈtaɪə(r)', 'v.', '(令)退职;(使)退休;(因伤)退出(比赛等);', 'At the age when most people retire, he is ready to face a new career...', '在大多数人都退休的年龄，他却准备好面对新的职业。');
INSERT INTO `words` VALUES (7217, 'retired', 'rɪˈtaɪəd', 'adj.', '已退休的;已退职的;', '...a seventy-three-year-old retired teacher from Florida.', '佛罗里达州73岁的退休教师');
INSERT INTO `words` VALUES (7218, 'retirement', 'rɪˈtaɪəmənt', 'n.', '退休;退职;退休年龄;', '...the proportion of the population who are over retirement age...', '超过退休年龄的人口比例');
INSERT INTO `words` VALUES (7219, 'retiring', 'rɪˈtaɪərɪŋ', 'adj.', '不爱与人交往的;', 'I\'m still that shy, retiring little girl who was afraid to ask for sweets in the shop.', '我仍然是那个羞怯孤僻，不敢在商店买糖果的小女孩。');
INSERT INTO `words` VALUES (7220, 'retool', 'ˌriːˈtuːl', 'v.', '更换，重新装配(机器设备);重新安排;重组;', 'Each time the product changes, the machines have to be retooled.', '每次更换产品，就得重新装备机器。');
INSERT INTO `words` VALUES (7221, 'retort', 'rɪˈtɔːt', 'v.', '(生气或幽默地)反驳，回嘴;', 'Was he afraid, he was asked. \'Afraid of what?\' he retorted...', '有人问他是否害怕。“害怕什么？”他反问道。');
INSERT INTO `words` VALUES (7222, 'retouch', 'ˌriːˈtʌtʃ', 'v.', '修饰，修整(图片或照片等);', 'He said the photographs had been retouched...', '他说这些照片已经修饰过。');
INSERT INTO `words` VALUES (7223, 'retrace', 'rɪˈtreɪs', 'v.', '沿原路返回;折回;重走(别人走过的路线);', 'He retraced his steps to the spot where he\'d left the case.', '他折回到他丢下箱子的地方。');
INSERT INTO `words` VALUES (7224, 'retract', 'rɪˈtrækt', 'v.', '撤销，收回(说过的话);撤回，收回(协议、承诺等);缩回;', 'Mr Smith hurriedly sought to retract the statement, but it had just been broadcast on national radio...', '史密斯先生想赶快收回这项声明，但它刚刚已经在国家电台播出了。');
INSERT INTO `words` VALUES (7225, 'retractable', 'rɪˈtræktəbl', 'adj.', '可缩进的;可拉回的;', 'A 20,000-seat arena with a retractable roof is planned.', '一个有两万个座位、带可开合顶棚的体育场正在规划中。');
INSERT INTO `words` VALUES (7226, 'retracted', 'rɪˈtræktɪd', 'v.', '撤销，收回(说过的话);撤回，收回(协议、承诺等);缩回;', 'Mr Smith hurriedly sought to retract the statement, but it had just been broadcast on national radio...', '史密斯先生想赶快收回这项声明，但它刚刚已经在国家电台播出了。');
INSERT INTO `words` VALUES (7227, 'retraction', 'rɪˈtrækʃn', 'n.', '撤销;收回;拉回;', 'Mr Smith hurriedly sought to retract the statement, but it had just been broadcast on national radio...', '史密斯先生想赶快收回这项声明，但它刚刚已经在国家电台播出了。');
INSERT INTO `words` VALUES (7228, 'retreat', 'rɪˈtriːt', 'v.', '退却;撤退;离开;', '\'I\'ve already got a job,\' I said quickly, and retreated from the room...', '“我已经有了一份工作。”我马上说道，并从房间里退了出来。');
INSERT INTO `words` VALUES (7229, 'retrench', 'rɪˈtrentʃ', 'v.', '节约;紧缩开支;缩减(人员);', 'Shortly afterwards, cuts in defence spending forced the aerospace industry to retrench.', '不久之后，国防开支的削减迫使航空航天业紧缩开支。');
INSERT INTO `words` VALUES (7230, 'retribution', 'ˌretrɪˈbjuːʃn', 'n.', '严惩;惩罚;报应;', 'He didn\'t want any further involvement for fear of retribution.', '他担心受到惩罚，不想再牵涉其中。');
INSERT INTO `words` VALUES (7231, 'retrieval', 'rɪˈtriːvl', 'n.', '取回;索回;数据检索;', '...electronic storage and retrieval systems.', '电子存储和检索系统');
INSERT INTO `words` VALUES (7232, 'retrieve', 'rɪˈtriːv', 'v.', '取回;索回;检索数据;', 'He reached over and retrieved his jacket from the back seat...', '他伸手从后座上取回了自己的夹克。');
INSERT INTO `words` VALUES (7233, 'retroactive', 'ˌretrəʊˈæktɪv', 'adj.', '(决定或行为)有追溯效力的;', 'There are few precedents for this sort of retroactive legislation.', '这种有追溯力的法规鲜有先例。');
INSERT INTO `words` VALUES (7234, 'retrofit', 'ˈretrəʊfɪt', 'v.', '给机器设备装配(新部件);翻新;改型;', 'Much of this business involves retrofitting existing planes...', '这项业务主要是改造更新现有的飞机。');
INSERT INTO `words` VALUES (7235, 'retrograde', 'ˈretrəɡreɪd', 'adj.', '倒退的;退化的;退步的;', 'The Prime Minister described transferring education to central government funding as \'a retrograde step\'.', '首相称将教育投入转由中央政府拨款是“一种倒退”。');
INSERT INTO `words` VALUES (7236, 'retrospect', 'ˈretrəspekt', 'n.', '回顾，回想;', 'In retrospect, I wish that I had thought about alternative courses of action...', '回想起来，我真希望自己当时考虑过其他做法。');
INSERT INTO `words` VALUES (7237, 'retrospective', 'ˌretrəˈspektɪv', 'adj.', '回顾的;涉及以往的;有追溯效力的;', '...a retrospective of the films of Judy Garland...', '朱迪·加兰电影回顾展');
INSERT INTO `words` VALUES (7238, 'return', 'rɪˈtɜːn', 'v.', '回来;回去;返回;', 'Blair will return to London tonight...', '今晚布莱尔将返回伦敦。');
INSERT INTO `words` VALUES (7239, 'returnable', 'rɪˈtɜːnəbl', 'adj.', '可退还的;应归还的;必须交还的;', 'All beverages must be sold in returnable containers.', '所有的饮料都必须装在可回收的容器中出售。');
INSERT INTO `words` VALUES (7240, 'returned', 'rɪˈtɜːnd', 'v.', '回来;回去;返回;', 'Blair will return to London tonight...', '今晚布莱尔将返回伦敦。');
INSERT INTO `words` VALUES (7241, 'returnee', 'rɪˌtɜːˈniː', 'n.', '回国的人;归国者;', 'The number of returnees could go as high as half a million.', '回国人员的数量可能高达50万。');
INSERT INTO `words` VALUES (7242, 'returns', 'rɪˈtɜːnz', 'v.', '回来;回去;返回;', 'Blair will return to London tonight...', '今晚布莱尔将返回伦敦。');
INSERT INTO `words` VALUES (7243, 'reunion', 'ˌriːˈjuːniən', 'n.', '重逢;团聚;聚会;', 'The Association holds an annual reunion...', '这个协会每年聚会一次。');
INSERT INTO `words` VALUES (7244, 'reunite', 'ˌriːjuˈnaɪt , ˌriːjuːˈnaɪt', 'v.', '(使)重逢，再次相聚;(使)再结合，再联合;', 'She and her youngest son were finally allowed to be reunited with their family...', '她和小儿子终于得到允许和家人团聚。');
INSERT INTO `words` VALUES (7245, 'reunited', 'ˌriːjuːˈnaɪtɪd', 'v.', '(使)重逢，再次相聚;(使)再结合，再联合;', 'She and her youngest son were finally allowed to be reunited with their family...', '她和小儿子终于得到允许和家人团聚。');
INSERT INTO `words` VALUES (7246, 'reusable', 'ˌriːˈjuːzəbl', 'adj.', '可重复使用的;可再次使用的;', 'The average family in Europe throws as much as £3,000 worth of reusable materials into its dustbin each year.', '欧洲的普通家庭每年扔进垃圾桶的可再利用材料的价值高达3,000英镑。');
INSERT INTO `words` VALUES (7247, 'rev', 'rev', 'v.', '(使)快速运转;', 'The engine started, revved and the car jerked away down the hill...', '引擎发动了，油门加大后车冲下了山坡。');
INSERT INTO `words` VALUES (7248, 'revamp', 'ˈriːvæmp , ˌriːˈvæmp', 'v.', '改变;修改;(通常指)改进外观，翻新;', 'All Italy\'s political parties have accepted that it is time to revamp the system...', '所有的意大利政党都已经承认现在是时候对体制进行改组了。');
INSERT INTO `words` VALUES (7249, 'reveal', 'rɪˈviːl', 'v.', '揭示;显示;透露;', 'She has refused to reveal the whereabouts of her daughter...', '她不肯透露她女儿的行踪。');
INSERT INTO `words` VALUES (7250, 'revealing', 'rɪˈviːlɪŋ', 'adj.', '揭露真相的;发人深省的;暴露的;', '...a revealing interview.', '揭秘访谈');
INSERT INTO `words` VALUES (7251, 'revelation', 'ˌrevəˈleɪʃn', 'n.', '被暴露的真相;被曝光的秘闻;披露;', '...the seemingly everlasting revelations about his private life.', '对他的私生活看似没完没了的曝光');
INSERT INTO `words` VALUES (7252, 'revelry', 'ˈrevlri', 'n.', '狂欢作乐;', 'As we got close to Broadway, we heard the sounds of revelry getting louder and louder.', '我们接近百老汇时，听到人们饮酒狂欢的声音越来越大。');
INSERT INTO `words` VALUES (7253, 'revenge', 'rɪˈvendʒ', 'n.', '报复;报仇;(曾经失败一方的)雪耻，打败对手;', 'The attackers were said to be taking revenge on the 14-year-old, claiming he was a school bully...', '据说，这些攻击者是在报复这个14岁的孩子，说他是学校的小霸王。');
INSERT INTO `words` VALUES (7254, 'revenue', 'ˈrevənjuː', 'n.', '财政收入;税收收入;收益;', '...a boom year at the cinema, with record advertising revenue and the highest ticket sales since 1980...', '电影院非常景气的一年，创下1980年以来广告及票房收入新高');
INSERT INTO `words` VALUES (7255, 'reverb', 'ˈriːvɜːb', 'n.', '混响(效果);', 'The unit includes built-in digital effects like reverb.', '这个设备具有诸如混响之类的内置数码音效。');
INSERT INTO `words` VALUES (7256, 'reverberate', 'rɪˈvɜːbəreɪt', 'v.', '回响;回荡;(由于强大的噪音)震颤，摇晃;', 'The sound of the tank guns reverberated through the little Bavarian town...', '坦克炮声在巴伐利亚小镇回荡。');
INSERT INTO `words` VALUES (7257, 'revere', 'rɪˈvɪə(r)', 'v.', '尊敬;崇敬;', 'The Chinese revered corn as a gift from heaven...', '中国人将谷物奉为上天的恩赐。');
INSERT INTO `words` VALUES (7258, 'reverence', 'ˈrevərəns', 'n./v.', '尊敬;崇敬;', '...showing a deep reverence for their religion.', '对他们的宗教表示了深深的敬意');
INSERT INTO `words` VALUES (7259, 'reverend', 'ˈrevərənd', 'adj.', '(尊称神职人员)尊敬的，可敬的;', 'The service was led by the Reverend Jim Simons.', '这次礼拜仪式是由吉姆·西蒙斯牧师大人主持的。');
INSERT INTO `words` VALUES (7260, 'reverent', 'ˈrevərənt', 'adj.', '非常尊敬的;深表崇敬的;', '...the reverent hush of a rapt audience...', '一位全神贯注的观众心存恭敬的静默');
INSERT INTO `words` VALUES (7261, 'reverie', 'ˈrevəri', 'n.', '幻想;白日梦;梦想;', 'The announcer\'s voice brought Holden out of his reverie.', '播音员的声音将霍尔登从幻想中惊醒。');
INSERT INTO `words` VALUES (7262, 'reversal', 'rɪˈvɜːsl', 'n.', '颠倒;彻底转变;反转;', 'The Financial Times says the move represents a complete reversal of previous US policy...', '《金融时报》说此举一反美国以往的政策。');
INSERT INTO `words` VALUES (7263, 'reverse', 'rɪˈvɜːs', 'v.', '颠倒;彻底转变;使完全相反;', 'They have made it clear they will not reverse the decision to increase prices...', '他们已经明确表示不会更改提价的决定。');
INSERT INTO `words` VALUES (7264, 'reversion', 'rɪˈvɜːʃn', 'n.', '倒退;回复;回归;', 'This is a reversion to the system under which the Royals were paid for nearly 300 years.', '又恢复了旧时的体制，王室就是在这种制度下接受了近300年的俸禄。');
INSERT INTO `words` VALUES (7265, 'revert', 'rɪˈvɜːt', 'v.', '恢复，回复(到以前的状态、制度或行为);回到，重提，重新考虑(某个话题);(财产、权力、金钱等)归还，归属;', 'Jackson said her boss became increasingly depressed and reverted to smoking heavily.', '杰克逊说她的老板情绪越来越低落，又开始拼命抽烟了。');
INSERT INTO `words` VALUES (7266, 'review', 'rɪˈvjuː', 'n.', '评审，审查，检查，检讨(以进行必要的修改);(对书籍、戏剧、电影等的)评介，评论;报告;', 'The president ordered a review of US economic aid to Jordan...', '总统下令对美国向约旦提供的经济援助进行审查。');
INSERT INTO `words` VALUES (7267, 'reviewer', 'rɪˈvjuːə(r)', 'n.', '评论家;评论撰写者;检查者;', '...the reviewer for the Times Literary Supplement.', '《泰晤士报文学副刊》的评论员');
INSERT INTO `words` VALUES (7268, 'revile', 'rɪˈvaɪl', 'v.', '辱骂;斥责;', 'He was just as feared and reviled as his tyrannical parents...', '他和他专横残暴的父母一样为人惧怕和憎恨。');
INSERT INTO `words` VALUES (7269, 'revise', 'rɪˈvaɪz', 'v.', '改变，修改(意见或计划);修改，修订(书刊、估算等);复习;', 'He soon came to revise his opinion of the profession.', '他很快就改变了对这个职业的看法。');
INSERT INTO `words` VALUES (7270, 'revision', 'rɪˈvɪʒn', 'n.', '(一项、一轮等)修订，修改;修订，修改(的进行);复习;', 'The phase of writing that is actually most important is revision...', '实际上写作过程中最重要的阶段是修改。');
INSERT INTO `words` VALUES (7271, 'revisit', 'ˌriːˈvɪzɪt', 'v.', '再访;重游;重提;', 'In the summer, when we returned to Canada, we revisited this lake at dawn.', '夏天我们回到加拿大后，在黎明时分重游了这个湖泊。');
INSERT INTO `words` VALUES (7272, 'revival', 'rɪˈvaɪvl', 'n.', '(状况或力量的)进步，振兴，复苏;复兴;再流行;', 'This return to realism has produced a revival of interest in a number of artists...', '这种重回现实主义的趋势已经重新激起了人们对许多艺术家的兴趣。');
INSERT INTO `words` VALUES (7273, 'revive', 'rɪˈvaɪv', 'v.', '(使)苏醒，复活;重新使用;使重做;', '...an attempt to revive the British economy...', '试图复兴英国经济的努力');
INSERT INTO `words` VALUES (7274, 'revocation', 'ˌrevəˈkeɪʃn', 'n.', '(法律等的)撤销，废除;', 'The government revoked her husband\'s license to operate migrant labor crews.', '政府撤销了她丈夫管理外来打工人群的许可证。');
INSERT INTO `words` VALUES (7275, 'revoke', 'rɪˈvəʊk', 'v.', '取消;废除;使无效;', 'The government revoked her husband\'s license to operate migrant labor crews.', '政府撤销了她丈夫管理外来打工人群的许可证。');
INSERT INTO `words` VALUES (7276, 'revolt', 'rɪˈvəʊlt', 'n.', '(尤指针对政府的)反抗，违抗;起义;叛乱;', 'It was undeniably a revolt by ordinary people against their leaders...', '不可否认，这是一场平民百姓反抗领导人的起义。');
INSERT INTO `words` VALUES (7277, 'revolting', 'rɪˈvəʊltɪŋ', 'adj.', '令人作呕的;极其讨厌的;', 'The smell in the cell was revolting...', '牢房的气味令人作呕。');
INSERT INTO `words` VALUES (7278, 'revolution', 'ˌrevəˈluːʃn', 'n.', '革命;巨变;大变革;', 'The period since the revolution has been one of political turmoil.', '革命爆发后就陷入了政治动乱期。');
INSERT INTO `words` VALUES (7279, 'revolutionary', 'ˌrevəˈluːʃənəri', 'adj.', '革命的;彻底变革的;巨变的;', 'Do you know anything about the revolutionary movement?', '你了解这场革命运动吗？');
INSERT INTO `words` VALUES (7280, 'revolve', 'rɪˈvɒlv', 'v.', '旋转;环绕;转动;', 'Since childhood, her life has revolved around tennis...', '从孩童时期开始，她的生活就以网球为中心。');
INSERT INTO `words` VALUES (7281, 'revolving', 'rɪˈvɒlvɪŋ', 'adj.', '旋转的;', 'Since childhood, her life has revolved around tennis...', '从孩童时期开始，她的生活就以网球为中心。');
INSERT INTO `words` VALUES (7282, 'revulsion', 'rɪˈvʌlʃn', 'n.', '嫌恶;恶心;惊恐;', '...their revulsion at the act of desecration...', '他们对亵渎神灵的行为的憎恶');
INSERT INTO `words` VALUES (7283, 'reward', 'rɪˈwɔːd', 'n.', '奖励;回报;报酬;', 'A bonus of up to 5 per cent can be added to a pupil\'s final exam marks as a reward for good spelling, punctuation and grammar...', '小学生期末考试的分数最多可以加5分，作为对其拼写、标点和语法正确的奖励。');
INSERT INTO `words` VALUES (7284, 'rewarding', 'rɪˈwɔːdɪŋ', 'adj.', '值得做的;有益的;报酬高的;', '...a career which she found stimulating and rewarding...', '她觉得刺激而有意义的职业');
INSERT INTO `words` VALUES (7285, 'rework', 'ˌriːˈwɜːk', 'v.', '修改;重做;再加工;', 'She reworked a lot of her compositions to make them more danceable...', '她改编了自己的很多乐曲，使其更适合作舞曲。');
INSERT INTO `words` VALUES (7286, 'rhapsody', 'ˈræpsədi', 'n.', '狂想曲;(言语或文字的)狂热表达，充满欣喜的表达;', '...George Gershwin\'s Rhapsody In Blue.', '乔治·格什温的《蓝色狂想曲》');
INSERT INTO `words` VALUES (7287, 'rhetoric', 'ˈretərɪk', 'n.', '华而不实的言语;花言巧语;修辞技巧;', 'What is required is immediate action, not rhetoric...', '需要的是立刻采取行动，而不是说些华而不实的空话。');
INSERT INTO `words` VALUES (7288, 'rhetorical', 'rɪˈtɒrɪkl', 'adj.', '反问的;反诘的;辞藻华丽的;', 'He grimaced slightly, obviously expecting no answer to his rhetorical question...', '他微微皱了下眉头，显然并不期待任何人回答他的反问。');
INSERT INTO `words` VALUES (7289, 'rhyme', 'raɪm', 'n.', '押韵词;同韵词;押韵的短诗;', 'June always rhymes with moon in old love songs.', '在老情歌中June总是和moon成韵。');
INSERT INTO `words` VALUES (7290, 'rhythm', 'ˈrɪðəm', 'n.', '节奏;韵律;律动;', 'His music of that period fused the rhythms of Jazz with classical forms...', '他那个时期的音乐将爵士乐的韵律和古典音乐的形式融合在了一起。');
INSERT INTO `words` VALUES (7291, 'rhythmic', 'ˈrɪðmɪk', 'adj.', '有节奏(或规律)的;节奏分明的;', 'Good breathing is slow, rhythmic and deep.', '健康的呼吸方式缓慢深沉而有节奏。');
INSERT INTO `words` VALUES (7292, 'rib', 'rɪb', 'n.', '肋骨;排骨;(船或屋顶等的)肋拱，肋材;', 'Her heart was thumping against her ribs...', '她的心脏紧抵着肋骨砰砰跳动。');
INSERT INTO `words` VALUES (7293, 'ribald', 'ˈrɪbld', 'adj.', '猥亵诙谐的;', '...her ribald comments about a fellow guest\'s body language.', '她对同席客人的肢体语言所作出的粗俗评论');
INSERT INTO `words` VALUES (7294, 'ribbed', 'rɪbd', 'adj.', '有棱纹的;', '...ribbed cashmere sweaters.', '罗纹羊绒衫');
INSERT INTO `words` VALUES (7295, 'ribbon', 'ˈrɪbən', 'n.', '(用于捆绑或装饰的)带子;丝带;带状物;', 'She had tied back her hair with a peach satin ribbon.', '她用一根桃色缎带把头发扎在脑后。');
INSERT INTO `words` VALUES (7296, 'rice', 'raɪs', 'n.', '大米;稻米;稻;', '...a meal consisting of chicken, rice and vegetables...', '有鸡肉、米饭和蔬菜的一顿饭');
INSERT INTO `words` VALUES (7297, 'rich', 'rɪtʃ', 'adj.', '富有的;富裕的;富人;', 'You\'re going to be a very rich man...', '你将会成为一个非常有钱的人。');
INSERT INTO `words` VALUES (7298, 'riches', 'ˈrɪtʃɪz', 'n.', '财富;财产;', 'You\'re going to be a very rich man...', '你将会成为一个非常有钱的人。');
INSERT INTO `words` VALUES (7299, 'richly', 'ˈrɪtʃli', 'adv.', '富丽堂皇地;(色彩)鲜艳富丽地;(口味)醇美地;', '...an opulent display of richly coloured fabrics.', '各种色彩艳丽的织物的华美展示');
INSERT INTO `words` VALUES (7300, 'richness', 'ˈrɪtʃnəs', 'n.', '丰富;富饶;浓烈;', 'You\'re going to be a very rich man...', '你将会成为一个非常有钱的人。');
INSERT INTO `words` VALUES (7301, 'rick', 'rɪk', 'n.', '干草堆;草垛;', 'Kernaghan missed the United game after he ricked his neck...', '克纳汉因为扭伤脖子而错过了联赛。');
INSERT INTO `words` VALUES (7302, 'rickety', 'ˈrɪkəti', 'adj.', '不结实的;不稳固的;易折断的;', 'Mona climbed the rickety wooden stairway.', '莫娜爬上了摇摇晃晃的木楼梯。');
INSERT INTO `words` VALUES (7303, 'ride', 'raɪd', 'v.', '驾驭马匹;骑马;骑马(消遣);', 'I saw a girl riding a horse...', '我看见一个骑马的女孩。');
INSERT INTO `words` VALUES (7304, 'rider', 'ˈraɪdə(r)', 'n.', '骑手;骑马(或自行车、摩托车)的人;公文的附加材料;', 'She is a very good and experienced rider...', '她是一位经验丰富的优秀骑师。');
INSERT INTO `words` VALUES (7305, 'ridicule', 'ˈrɪdɪkjuːl', 'n.', '嘲笑;奚落;讥笑;', 'I admired her all the more for allowing them to ridicule her and never striking back...', '她能听任他们嘲笑却从不还击，这让我更加敬佩她。');
INSERT INTO `words` VALUES (7306, 'ridiculous', 'rɪˈdɪkjələs', 'adj.', '愚蠢的;荒谬的;荒唐的;', 'It is ridiculous to suggest we are having a romance...', '暗示我们正在谈恋爱的说法真是太荒谬了。');
INSERT INTO `words` VALUES (7307, 'riding', 'ˈraɪdɪŋ', 'n.', '骑马;区(英格兰约克郡以前的东、西、北三个行政分区之一);', 'The next morning we went riding again...', '第二天早上，我们又去骑马了。');
INSERT INTO `words` VALUES (7308, 'rife', 'raɪf', 'adj.', '(坏事)盛行，普遍;充斥，充满(坏事);', 'Speculation is rife that he will be sacked...', '人们纷纷猜测他会被解雇。');
INSERT INTO `words` VALUES (7309, 'rifle', 'ˈraɪfl', 'n.', '步枪;来复枪;', 'They shot him at point blank range with an automatic rifle...', '他们用自动步枪近距离开枪射杀了他。');
INSERT INTO `words` VALUES (7310, 'rift', 'rɪft', 'n.', '分裂;分歧;严重不和;', 'The interview reflected a growing rift between the President and the government...', '这段采访反映了总统和政府之间的裂痕越来越大。');
INSERT INTO `words` VALUES (7311, 'rig', 'rɪɡ', 'v.', '(以不正当的手段)操纵，控制;(给船只)装帆，提供索具;(秘密地)安装，装配;', 'She accused her opponents of rigging the vote...', '她谴责对手操纵投票。');
INSERT INTO `words` VALUES (7312, 'righteous', 'ˈraɪtʃəs', 'adj.', '公正的;正直的;正当的;', 'Aren\'t you afraid of being seen as a righteous crusader?...', '你不怕被人看作是一个装腔作势的改革者？');
INSERT INTO `words` VALUES (7313, 'righteousness', '\'raɪtʃəsnəs', 'n.', '正义;', 'Aren\'t you afraid of being seen as a righteous crusader?...', '你不怕被人看作是一个装腔作势的改革者？');
INSERT INTO `words` VALUES (7314, 'rightful', 'ˈraɪtfl', 'adj.', '正确的;公正的;合法的;', 'The Baltics\' own democratic traditions would help them to regain their rightful place in Europe...', '波罗的海各国本身的民主传统将有助于其恢复在欧洲应有的地位。');
INSERT INTO `words` VALUES (7315, 'right-hand', 'ˈraɪt hænd', 'adj.', '右手的;右边的;右面的;', '...a church on the right-hand side of the road.', '道路右侧的一座教堂');
INSERT INTO `words` VALUES (7316, 'rigid', 'ˈrɪdʒɪd', 'adj.', '死板的;僵硬的;固执的;', 'Several colleges in our study have rigid rules about student conduct...', '在我们的研究中，有几所大学制定了严格的学生行为规则。');
INSERT INTO `words` VALUES (7317, 'rigidity', 'rɪ\'dʒɪdətɪ', 'n.', '僵硬;强直;严格;', 'Several colleges in our study have rigid rules about student conduct...', '在我们的研究中，有几所大学制定了严格的学生行为规则。');
INSERT INTO `words` VALUES (7318, 'rigidly', '\'rɪdʒɪdli', 'adv.', '严格;僵;古板;', 'Several colleges in our study have rigid rules about student conduct...', '在我们的研究中，有几所大学制定了严格的学生行为规则。');
INSERT INTO `words` VALUES (7319, 'rigmarole', 'ˈrɪɡmərəʊl', 'n.', '(不必要的)冗长复杂的手续;冗长曲折的故事;', 'Then the whole rigmarole starts over again...', '然后，整个漫长复杂的过程又重新开始了。');
INSERT INTO `words` VALUES (7320, 'rigorous', 'ˈrɪɡərəs', 'adj.', '谨慎的;细致的;彻底的;', 'The selection process is based on rigorous tests of competence and experience.', '选拔过程是基于对能力和经验的严格测试。');
INSERT INTO `words` VALUES (7321, 'rigorously', 'ˈrɪgərəsli', 'adv.', '严厉地;', 'The selection process is based on rigorous tests of competence and experience.', '选拔过程是基于对能力和经验的严格测试。');
INSERT INTO `words` VALUES (7322, 'rile', 'raɪl', 'v.', '惹恼;激怒;', 'Cancellations and late departures rarely rile him.', '他很少因为约定取消或出发太晚而恼火。');
INSERT INTO `words` VALUES (7323, 'rim', 'rɪm', 'n.', '(圆形物体的)边沿;轮辋;轮圈;', 'She looked at him over the rim of her glass.', '她的目光越过玻璃杯的边沿看着他。');
INSERT INTO `words` VALUES (7324, 'rind', 'raɪnd', 'n.', '(某些水果的)厚皮，外壳;(熏肉和某些干酪等食物的)外皮;', '...grated lemon rind.', '磨碎的柠檬皮');
INSERT INTO `words` VALUES (7325, 'rink', 'rɪŋk', 'n.', '溜冰场;旱冰场;', 'The other skaters were ordered off the rink.', '其他的溜冰者都被勒令离开溜冰场。');
INSERT INTO `words` VALUES (7326, 'rinse', 'rɪns', 'v.', '(用清水)冲洗，洗涮;冲掉…的皂液;漂洗;', 'After shampooing, always rinse the hair several times in clear water...', '每次用完洗发剂后都要用清水将头发冲洗几次。');
INSERT INTO `words` VALUES (7327, 'riot', 'ˈraɪət', 'n.', '暴乱;骚乱;丰富多彩;', 'Twelve inmates have been killed during a riot at the prison.', '12名犯人在一场监狱暴乱中被杀害。');
INSERT INTO `words` VALUES (7328, 'riotous', 'ˈraɪətəs', 'adj.', '骚乱的;暴乱的;狂欢的;', '...aristocrats who wasted their inheritances in riotous living.', '在放纵的生活中将遗产挥霍一空的贵族');
INSERT INTO `words` VALUES (7329, 'rip', 'rɪp', 'v.', '(突然或猛烈地)撕破，裂开;猛地扯开;突然拉开;', 'I felt the banner rip as we were pushed in opposite directions...', '当我们被推向相反的方向时，我感觉横幅被撕裂了。');
INSERT INTO `words` VALUES (7330, 'ripe', 'raɪp', 'adj.', '成熟的;口味浓郁的;醇美可口的;', 'Always choose firm, but ripe fruit.', '一定要挑选硬实但已成熟的水果。');
INSERT INTO `words` VALUES (7331, 'ripen', 'ˈraɪpən', 'v.', '(使)成熟;', 'I\'m waiting for the apples to ripen...', '我正在等待苹果成熟。');
INSERT INTO `words` VALUES (7332, 'riposte', 'rɪˈpɒst', 'n.', '机敏的回答;巧妙的反驳;反应;', 'Laura glanced at Grace, expecting a cheeky riposte.', '劳拉匆匆瞥了格雷斯一眼，想着他会厚着脸皮巧妙地反驳一下。');
INSERT INTO `words` VALUES (7333, 'rise', 'raɪz', 'n.', '(数量或水平的)增加，提高;加薪;工资增长;', 'He watched the smoke rise from his cigarette...', '他注视着烟雾从香烟上升起。');
INSERT INTO `words` VALUES (7334, 'rising', 'ˈraɪzɪŋ', 'n.', '起义;叛乱;', 'He watched the smoke rise from his cigarette...', '他注视着烟雾从香烟上升起。');
INSERT INTO `words` VALUES (7335, 'risk', 'rɪsk', 'n.', '危险;风险;危险人物;', 'There is a small risk of brain damage from the procedure...', '该手术有导致脑损伤的轻微风险。');
INSERT INTO `words` VALUES (7336, 'risky', 'ˈrɪski', 'adj.', '有危险(或风险)的;', 'Investing in airlines is a very risky business...', '投资航空业风险很大。');
INSERT INTO `words` VALUES (7337, 'rite', 'raɪt', 'n.', '(宗教等的)仪式，典礼;', 'Most traditional societies have transition rites at puberty.', '大多数传统社会都为青春期的孩子举行成人礼。');
INSERT INTO `words` VALUES (7338, 'ritual', 'ˈrɪtʃuəl', 'n.', '程序;仪规;礼节;', 'This is the most ancient, and holiest of the Shinto rituals...', '这是最古老、最神圣的神道教仪式。');
INSERT INTO `words` VALUES (7339, 'ritualistic', 'ˌrɪtʃuəˈlɪstɪk', 'adj.', '仪式的;老套的;例行公事的;', 'Each evening she bursts into her apartment with a ritualistic shout of \'Honey I\'m home!\'', '每天晚上她都会像往常一样一边嚷着“亲爱的，我回来啦！”一边冲进公寓。');
INSERT INTO `words` VALUES (7340, 'rivalry', 'ˈraɪvlri', 'n.', '竞争;竞赛;较量;', '...the rivalry between the Inkatha and the ANC...', '英卡塔自由党和非洲国民大会之间的敌对');
INSERT INTO `words` VALUES (7341, 'river', 'ˈrɪvə(r)', 'n.', '河;江;(液体)涌流;', '...a chemical works on the banks of the river.', '跨河而建的一家化工厂');
INSERT INTO `words` VALUES (7342, 'riverside', 'ˈrɪvəsaɪd', 'n.', '河畔;河岸;', 'They walked back along the riverside.', '他们沿着河边走回来了。');
INSERT INTO `words` VALUES (7343, 'rivet', 'ˈrɪvɪt', 'n.', '铆钉;', 'As a child I remember being riveted by my grandfather\'s appearance...', '我记得小时候觉得祖父的相貌很迷人。');
INSERT INTO `words` VALUES (7344, 'riveting', 'ˈrɪvɪtɪŋ', 'adj.', '吸引人的;引人入胜的;', 'I find snooker riveting though I don\'t play myself.', '虽然我自己不打斯诺克，但是我觉得它挺令人着迷。');
INSERT INTO `words` VALUES (7345, 'road', 'rəʊd', 'n.', '路;道路;公路;', 'There was very little traffic on the roads...', '这些公路上车辆稀少。');
INSERT INTO `words` VALUES (7346, 'roadblock', 'ˈrəʊdblɒk', 'n.', '路障;障碍;', 'The city police set up roadblocks to check passing vehicles.', '该市警察设置了路障以检查过往车辆。');
INSERT INTO `words` VALUES (7347, 'roadside', 'ˈrəʊdsaɪd', 'n.', '路边;路旁;', 'Bob was forced to leave the car at the roadside and run for help...', '鲍勃被迫把车停在路边，然后跑去求助了。');
INSERT INTO `words` VALUES (7348, 'roadway', 'ˈrəʊdweɪ', 'n.', '道路;车行道;', 'Marks in the roadway seem to indicate that he skidded taking a sharp turn.', '从车道上的痕迹来看，他似乎在急转弯时打滑了。');
INSERT INTO `words` VALUES (7349, 'roam', 'rəʊm', 'v.', '徜徉;闲逛;漫步;', 'Barefoot children roamed the streets...', '光脚丫的孩子们在街上游荡。');
INSERT INTO `words` VALUES (7350, 'roaming', 'ˈrəʊmɪŋ', 'n.', '(移动电话的)漫游;', 'International Roaming is your digital mobile phone\'s passport to travel but the cost of calls is high.', '国际漫游服务可使数字手机在全球畅通无阻，但话费却很高。');
INSERT INTO `words` VALUES (7351, 'roar', 'rɔː(r)', 'v.', '吼叫;咆哮;叫喊;', 'A police car roared past...', '一辆警车呼啸而过。');
INSERT INTO `words` VALUES (7352, 'roast', 'rəʊst', 'v.', '烘，烤，焙(肉等);烘烤，焙，炒(坚果、豆子等);(对某人)非常生气，严厉批评;', 'I personally would rather roast a chicken whole.', '我个人更愿意烤整只鸡。');
INSERT INTO `words` VALUES (7353, 'roasted', 'ˈrəʊstɪd', 'v.', '烘，烤，焙(肉等);烘烤，焙，炒(坚果、豆子等);(对某人)非常生气，严厉批评;', 'I personally would rather roast a chicken whole.', '我个人更愿意烤整只鸡。');
INSERT INTO `words` VALUES (7354, 'rob', 'rɒb', 'v.', '抢劫;掠夺;盗取;', 'Mrs Yacoub was robbed of her £3,000 designer watch at her West London home...', '雅各布夫人那块价值3,000英镑的名牌手表在西伦敦的家中被人抢走了。');
INSERT INTO `words` VALUES (7355, 'robber', 'ˈrɒbə(r)', 'n.', '强盗;盗贼;抢劫犯;', 'Armed robbers broke into a jeweller\'s through a hole in the wall.', '持械抢劫者通过墙上的一个洞闯进了一家珠宝店。');
INSERT INTO `words` VALUES (7356, 'robbery', 'ˈrɒbəri', 'n.', '盗窃;抢劫;掠夺;', 'The gang members committed dozens of armed robberies...', '这个帮派的成员犯下了数十宗持械抢劫案。');
INSERT INTO `words` VALUES (7357, 'robe', 'rəʊb', 'n.', '袍服，礼袍(常于典礼中穿着以显示身份);', 'Pope John Paul II knelt in his white robes before the simple altar.', '教皇约翰·保罗二世身着白色长袍在简朴的圣坛前面跪下来。');
INSERT INTO `words` VALUES (7358, 'robot', 'ˈrəʊbɒt', 'n.', '机器人;(尤指故事中的)机器人;交通信号灯;', '...very light-weight robots that we could send to the moon for planetary exploration.', '可以发射到月球上进行行星探测的极轻型机器人');
INSERT INTO `words` VALUES (7359, 'robust', 'rəʊˈbʌst', 'adj.', '强健的;强壮的;结实的;', 'More women than men go to the doctor. Perhaps men are more robust or worry less?...', '看医生的女性多于男性。也许男性更强壮或者顾虑要少一些？');
INSERT INTO `words` VALUES (7360, 'rock', 'rɒk', 'n.', '岩石;石山;礁石;', 'The hills above the valley are bare rock...', '山谷上方的小山全是光秃秃的石头。');
INSERT INTO `words` VALUES (7361, 'rocker', 'ˈrɒkə(r)', 'n.', '(摇椅底部的)弧形摇杆;摇滚青年(尤指20世纪60年代的某些英国青年，喜欢穿皮夹克、骑摩托车、听摇滚乐);摇滚乐表演者;', 'Claire spent the morning in a wicker rocker on the screened porch with a book.', '克莱尔整个早上都在装着纱窗的走廊里坐在柳条摇椅上看书。');
INSERT INTO `words` VALUES (7362, 'rocking', 'ˈrɒkɪŋ', 'v.', '(使)轻轻摇晃，缓缓摆动;惊吓;使震惊;', 'The hills above the valley are bare rock...', '山谷上方的小山全是光秃秃的石头。');
INSERT INTO `words` VALUES (7363, 'rocky', 'ˈrɒki', 'adj.', '岩石的;多岩石的;困难的;', 'The paths are often very rocky so strong boots are advisable.', '路上通常石头很多，所以最好穿结实耐磨的靴子。');
INSERT INTO `words` VALUES (7364, 'rod', 'rɒd', 'n.', '杆;竿;棒;', '...a 15-foot thick roof that was reinforced with steel rods.', '用钢筋加固的15英尺厚的屋顶');
INSERT INTO `words` VALUES (7365, 'roe', 'rəʊ', 'n.', '鱼子;', '...cod\'s roe.', '鳕鱼子');
INSERT INTO `words` VALUES (7366, 'rogue', 'rəʊɡ', 'n.', '无赖;捣蛋鬼;骗子;', 'Mr Ward wasn\'t a rogue at all.', '沃德先生根本不是恶棍。');
INSERT INTO `words` VALUES (7367, 'roguish', 'ˈrəʊɡɪʃ', 'adj.', '调皮捣蛋的;顽皮的;', 'She was a mature lady with dyed ginger hair and a roguish grin.', '她是一位成熟的淑女，有一头染成姜黄色的头发和顽皮的笑容。');
INSERT INTO `words` VALUES (7368, 'roil', 'rɔɪl', 'v.', '(水)激荡，翻滚，翻腾;扰乱;搅乱;使混乱;', 'The water roiled to his left as he climbed carefully at the edge of the waterfall.', '当他在瀑布边缘小心攀爬时，水就在他左边奔腾。');
INSERT INTO `words` VALUES (7369, 'role', 'rəʊl', 'n.', '职能;地位;角色;', '...the drug\'s role in preventing more serious effects of infection...', '这种药物在防止感染加重方面的作用');
INSERT INTO `words` VALUES (7370, 'roll', 'rəʊl', 'n.', '卷;卷轴;一管;', 'The ball rolled into the net...', '球滚进了网中。');
INSERT INTO `words` VALUES (7371, 'roller-skate', 'ˈrəʊlə skeɪt', 'n.', '四轮轮滑鞋;四轮旱冰鞋;', 'A boy of about ten came up on roller-skates.', '一个10岁左右、脚蹬旱冰鞋的男孩滑了过来。');
INSERT INTO `words` VALUES (7372, 'rollicking', 'ˈrɒlɪkɪŋ', 'adj.', '嬉闹的;喧闹嬉戏的;', 'Tony Benn\'s diaries are a rollicking read.', '托尼·本的日记读起来很有趣。');
INSERT INTO `words` VALUES (7373, 'rolling', 'ˈrəʊlɪŋ', 'adj.', '起伏的;规则的;周而复始的;', '...the rolling countryside of south western France.', '法国西南部绵延起伏的乡村');
INSERT INTO `words` VALUES (7374, 'roman', 'ˈrəʊmən', 'adj.', '古罗马的;古罗马帝国的;罗马的;', '...the fall of the Roman Empire.', '古罗马帝国的覆灭');
INSERT INTO `words` VALUES (7375, 'romance', 'rəʊˈmæns', 'n.', '(通常指短暂的)浪漫史，爱情关系，风流韵事;恋爱;爱情;', 'After a whirlwind romance the couple announced their engagement in July.', '在闪电式的恋爱后，两人在7月宣布订婚。');
INSERT INTO `words` VALUES (7376, 'romantic', 'rəʊˈmæntɪk', 'adj.', '浪漫的;爱情的;情爱的;', 'When we\'re together, all he talks about is business. I wish he were more romantic...', '我们在一起的时候，他总是谈论生意。我希望他能更浪漫一点。');
INSERT INTO `words` VALUES (7377, 'romanticism', 'rəʊˈmæntɪsɪzəm', 'n.', '浪漫主义(18世纪末19世纪初盛行于艺术、音乐及文学领域，以情感强烈、想象和回归自然为特征);浪漫精神;浪漫的态度;', 'Her determined romanticism was worrying me...', '她坚定的浪漫主义情怀让我感到担忧。');
INSERT INTO `words` VALUES (7378, 'romp', 'rɒmp', 'v.', '嬉戏喧闹;', 'Mr Foster romped home with 141 votes.', '福斯特先生以141票轻松获胜。');
INSERT INTO `words` VALUES (7379, 'rookie', 'ˈrʊki', 'n.', '新手;生手;(第一年参加比赛的)新队员;', 'I don\'t want to have another rookie to train.', '我不想再训练新兵了。');
INSERT INTO `words` VALUES (7380, 'room', 'ruːm', 'n.', '房间;室;有…室的;', 'A minute later he excused himself and left the room...', '一分钟之后，他说了声抱歉就离开了房间。');
INSERT INTO `words` VALUES (7381, 'roomy', 'ˈruːmi', 'adj.', '宽敞的;宽大的;', 'The car is roomy and a good choice for anyone who needs to carry equipment.', '这种车非常宽敞，对于需要携带装备的人来说是不错的选择。');
INSERT INTO `words` VALUES (7382, 'root', 'ruːt', 'n.', '根;根茎;根部;', '...the twisted roots of an apple tree.', '苹果树盘绕的根');
INSERT INTO `words` VALUES (7383, 'rope', 'rəʊp', 'n.', '粗绳;线缆;绳索;', 'He tied the rope around his waist.', '他把绳索系在自己的腰上。');
INSERT INTO `words` VALUES (7384, 'rosette', 'rəʊˈzet', 'n.', '玫瑰形饰物(用缎带制成，政党或运动队的支持者所佩戴，亦作为获奖的标志);玫瑰形的东西;', 'I saw her father with his rosette on, canvassing for the Conservatives...', '我看见她父亲戴着玫瑰花结，正在为保守党游说拉票。');
INSERT INTO `words` VALUES (7385, 'roster', 'ˈrɒstə(r)', 'n.', '值勤名单;候选名单;', 'The next day he put himself first on the new roster for domestic chores...', '第二天，他把自己排在了新的家务值日表的第一位。');
INSERT INTO `words` VALUES (7386, 'rostrum', 'ˈrɒstrəm', 'n.', '讲坛;指挥台;领奖台;', 'As he stood on the winner\'s rostrum, he sang the words of the national anthem.', '他站在冠军领奖台上，唱起了国歌。');
INSERT INTO `words` VALUES (7387, 'rosy', 'ˈrəʊzi', 'adj.', '粉红色的;红润的;美好的;', 'Bethan\'s round, rosy face seemed hardly to have aged at all...', '贝唐红扑扑的圆脸看起来似乎一点也没有变老。');
INSERT INTO `words` VALUES (7388, 'rot', 'rɒt', 'v.', '(使)腐烂，腐败变质;', 'If we don\'t unload it soon, the grain will start rotting in the silos...', '如果我们不立刻把粮食卸下来，它们就会在筒仓里开始腐烂。');
INSERT INTO `words` VALUES (7389, 'rotary', 'ˈrəʊtəri', 'adj.', '旋转的;绕轴转动的;转动的;', '...turning linear into rotary motion.', '把直线运动改成旋转运动');
INSERT INTO `words` VALUES (7390, 'rotate', 'rəʊˈteɪt', 'v.', '(使)旋转，转动;(工作)由…轮值;(人员)轮换，轮值;', 'The Earth rotates round the sun...', '地球围绕太阳转。');
INSERT INTO `words` VALUES (7391, 'rotating', 'rəʊˈteɪtɪŋ', 'v.', '(使)旋转，转动;(工作)由…轮值;(人员)轮换，轮值;', 'The Earth rotates round the sun...', '地球围绕太阳转。');
INSERT INTO `words` VALUES (7392, 'rotation', 'rəʊˈteɪʃn', 'n.', '旋转;转动;(旋转的)一周，一圈;', '...the daily rotation of the earth upon its axis.', '地球每天绕地轴自转');
INSERT INTO `words` VALUES (7393, 'rote', 'rəʊt', 'n.', '死记硬背;', 'He is very sceptical about the value of rote learning...', '他非常怀疑死记硬背是否有用。');
INSERT INTO `words` VALUES (7394, 'rotten', 'ˈrɒtn', 'adj.', '腐烂的;腐败的;腐朽的;', 'The smell outside this building is overwhelming — like rotten eggs...', '这栋大楼外面的气味让人无法忍受——就像臭鸡蛋的味道。');
INSERT INTO `words` VALUES (7395, 'rotund', 'rəʊˈtʌnd', 'adj.', '圆胖的;肥圆的;', 'A rotund, smiling, red-faced gentleman appeared.', '一位身材圆胖、面带笑容、满脸通红的先生出现了。');
INSERT INTO `words` VALUES (7396, 'rotunda', 'rəʊˈtʌndə', 'n.', '圆形建筑，圆形大厅(尤指带有圆顶的);', '...the rotunda of the Hotel des Invalides in Paris.', '巴黎荣军院的圆顶建筑');
INSERT INTO `words` VALUES (7397, 'rough', 'rʌf', 'adj.', '粗糙的;不平滑的;高低不平的;', 'His hands were rough and calloused, from years of karate practice...', '由于多年练习空手道，他的双手粗糙不平，布满老茧。');
INSERT INTO `words` VALUES (7398, 'roughly', 'ˈrʌfli', 'adv.', '大约;大致;差不多;', 'His hands were rough and calloused, from years of karate practice...', '由于多年练习空手道，他的双手粗糙不平，布满老茧。');
INSERT INTO `words` VALUES (7399, 'rounded', 'ˈraʊndɪd', 'adj.', '圆形的;全面的;一应俱全的;', '...a low rounded hill...', '低矮的圆形小山');
INSERT INTO `words` VALUES (7400, 'roundup', 'ˈraʊndʌp', 'n.', '综述，摘要;集拢，赶拢（家畜等）;', 'First, we have this roundup of the day\'s news...', '首先来看今天的新闻摘要。');
INSERT INTO `words` VALUES (7401, 'rouse', 'raʊz', 'v.', '唤醒;使醒来;使活跃起来;', 'Hilton roused him at eight-thirty by rapping on the door...', '希尔顿8点半敲门把他叫醒了。');
INSERT INTO `words` VALUES (7402, 'rout', 'raʊt', 'n.', '溃败;彻底失败;', '...the Battle of Hastings at which the Norman army routed the English opposition.', '诺曼军队彻底击溃英格兰军队的黑斯廷斯战役');
INSERT INTO `words` VALUES (7403, 'routine', 'ruːˈtiːn', 'n.', '常规;正常顺序;生活乏味;', 'The players had to change their daily routine and lifestyle...', '这些运动员不得不改变他们的日常生活习惯和方式。');
INSERT INTO `words` VALUES (7404, 'routinely', 'ruːˈtiːnli', 'adv.', '例行地;常规地;通常地;惯常地;毫不意外地;', 'Vitamin K is routinely given in the first week of life to prevent bleeding.', '出生后第一周通常要服用维生素K，以防止出血。');
INSERT INTO `words` VALUES (7405, 'rove', 'rəʊv', 'v.', '漫游;漂泊;流浪;', '...roving about the town in the dead of night and seeing something peculiar...', '夜深人静时在小镇游走，看见一些怪异的景象');
INSERT INTO `words` VALUES (7406, 'rowdy', 'ˈraʊdi', 'adj.', '吵闹的;惹是生非的;捣乱的;', 'He has complained to the police about rowdy neighbours...', '他向警方投诉邻居们太吵闹。');
INSERT INTO `words` VALUES (7407, 'rower', 'ˈrəʊə(r)', 'n.', '划船者;', '...the first rower ever to win golds at four Olympic Games.', '第一个在4届奥运会上都获得金牌的赛艇运动员');
INSERT INTO `words` VALUES (7408, 'rowing', 'ˈrəʊɪŋ , ˈraʊɪŋ', 'n.', '划船;划艇运动;', '...competitions in rowing, swimming and water skiing.', '划船、游泳和滑水比赛');
INSERT INTO `words` VALUES (7409, 'royal', 'ˈrɔɪəl', 'adj.', '国王的;女王的;皇家的;', '...an invitation to a royal garden party.', '参加皇家游园会的邀请');
INSERT INTO `words` VALUES (7410, 'royalist', 'ˈrɔɪəlɪst', 'n.', '君主主义者;保皇主义者;保皇党人;', 'He was hated by the royalists.', '他受到保皇党人的憎恨。');
INSERT INTO `words` VALUES (7411, 'royalty', 'ˈrɔɪəlti', 'n.', '王室成员;版税;矿区土地使用费(由采矿或石油公司等付给土地所有人);', 'Royalty and government leaders from all around the world are gathering in Japan.', '世界各国的王室成员和政府首脑齐聚日本。');
INSERT INTO `words` VALUES (7412, 'rub', 'rʌb', 'v.', '擦;磨;搓;', 'He rubbed his arms and stiff legs...', '他按摩着自己的胳膊和僵硬的双腿。');
INSERT INTO `words` VALUES (7413, 'rubber', 'ˈrʌbə(r)', 'n.', '橡胶;橡皮;黑板擦;', '...the smell of burning rubber.', '橡胶燃烧的气味');
INSERT INTO `words` VALUES (7414, 'rubbery', 'ˈrʌbəri', 'adj.', '似橡胶的;有弹性的;虚弱的;', 'She had the most rubbery face...', '她表情极为丰富。');
INSERT INTO `words` VALUES (7415, 'rubbish', 'ˈrʌbɪʃ', 'n.', '垃圾;废弃物;劣质的东西;', '...unwanted household rubbish...', '无用的家庭垃圾');
INSERT INTO `words` VALUES (7416, 'rubble', 'ˈrʌbl', 'n.', '碎石;碎砖;', 'Thousands of bodies are still buried under the rubble...', '成千上万的尸体仍然被埋在碎石瓦砾下。');
INSERT INTO `words` VALUES (7417, 'rubicund', 'ˈruːbɪkənd', 'adj.', '健康红润的;', 'She watched the colour drain from Colin\'s rubicund face.', '她看见科林原本红润的脸渐渐失去了血色。');
INSERT INTO `words` VALUES (7418, 'ruby', 'ˈruːbi', 'n.', '红宝石;深红色;', '...a ruby and diamond ring.', '红宝石钻戒');
INSERT INTO `words` VALUES (7419, 'ruck', 'rʌk', 'n.', '自由密集争球;拥在一起的人群;混乱打斗的人群;', 'There\'ll be a huge ruck with the cops as they try to take photographs...', '他们试图拍照时将会和警察发生大规模的肢体冲突。');
INSERT INTO `words` VALUES (7420, 'ruddy', 'ˈrʌdi', 'adj.', '红润健康的;红色的;(表示生气)讨厌的，可恶的;', 'He had a naturally ruddy complexion...', '他的脸色天生红润。');
INSERT INTO `words` VALUES (7421, 'rude', 'ruːd', 'adj.', '粗鲁的;无礼的;粗野的;', 'He\'s rude to her friends and obsessively jealous...', '他对她的朋友很无礼而且嫉妒心重。');
INSERT INTO `words` VALUES (7422, 'rudely', 'ˈruːdli', 'adv.', '粗鲁地;无礼地;突然地;', 'He\'s rude to her friends and obsessively jealous...', '他对她的朋友很无礼而且嫉妒心重。');
INSERT INTO `words` VALUES (7423, 'rudeness', 'ˈruːdnɪs', 'n.', '粗鲁;原始;直率;', 'He\'s rude to her friends and obsessively jealous...', '他对她的朋友很无礼而且嫉妒心重。');
INSERT INTO `words` VALUES (7424, 'rudimentary', 'ˌruːdɪˈmentri', 'adj.', '基础的;基本的;未充分发展的;', '...a kind of rudimentary kitchen...', '略显简陋的厨房');
INSERT INTO `words` VALUES (7425, 'rudiments', 'ˈruːdɪmənts', 'n.', '基础;基本原理(或技能);', 'She helped to build a house, learning the rudiments of brick-laying as she went along.', '她参与了盖房子，并在施工过程中学习了砌砖的基本技巧。');
INSERT INTO `words` VALUES (7426, 'rue', 'ruː', 'v.', '对…感到懊恼;懊悔;', 'Tavare was probably ruing his decision.', '塔瓦雷可能正在为自己作出的决定感到后悔。');
INSERT INTO `words` VALUES (7427, 'rueful', 'ˈruːfl', 'adj.', '悲伤的;懊悔的;沮丧的;', 'He shook his head and gave me a rueful smile...', '他摇了摇头，遗憾地对我笑了笑。');
INSERT INTO `words` VALUES (7428, 'ruffian', 'ˈrʌfiən', 'n.', '暴徒;恶棍;', '...gangs of ruffians who lurk about intent on troublemaking.', '潜伏在周围意图制造麻烦的匪帮');
INSERT INTO `words` VALUES (7429, 'ruffle', 'ˈrʌfl', 'v.', '弄皱;弄乱;使不平整;', '\'Don\'t let that get you down,\' he said ruffling Ben\'s dark curls.', '“不要为那个感到沮丧，”他一边说，一边抚弄着本乌黑的卷发。');
INSERT INTO `words` VALUES (7430, 'rug', 'rʌɡ', 'n.', '小地毯;垫子;(盖腿的)厚毯子;', 'A Persian rug covered the hardwood floors.', '硬木地板上铺着一张波斯地毯。');
INSERT INTO `words` VALUES (7431, 'rugged', 'ˈrʌɡɪd', 'adj.', '崎岖的;凹凸不平的;多岩石的;', '...rugged mountainous terrain.', '崎岖不平的山地');
INSERT INTO `words` VALUES (7432, 'ruin', 'ˈruːɪn', 'v.', '毁坏;破坏;糟蹋;', 'My wife was ruining her health through worry...', '忧虑严重损害了我妻子的健康。');
INSERT INTO `words` VALUES (7433, 'ruined', 'ˈruːɪnd', 'adj.', '毁坏的;严重受损的;', '...a ruined church.', '荒废的教堂');
INSERT INTO `words` VALUES (7434, 'ruinous', 'ˈruːɪnəs', 'adj.', '耗资巨大的;无法承担的;破坏性的;', 'Many Britons will still fear the potentially ruinous costs of their legal system.', '很多英国人仍然会对其司法体系可能产生的高昂费用表示担忧。');
INSERT INTO `words` VALUES (7435, 'rule', 'ruːl', 'n.', '规则;规章;条例;', '...a thirty-two-page pamphlet explaining the rules of basketball...', '说明篮球规则的32页的小册子');
INSERT INTO `words` VALUES (7436, 'ruler', 'ˈruːlə(r)', 'n.', '统治者;支配者;直尺;', '...the former military ruler of Lesotho...', '莱索托的前军事统治者');
INSERT INTO `words` VALUES (7437, 'ruling', 'ˈruːlɪŋ', 'n.', '裁决;裁定;判决;', '...the Mexican voters\' growing dissatisfaction with the ruling party.', '墨西哥的选民们对执政党越来越多的不满');
INSERT INTO `words` VALUES (7438, 'rum', 'rʌm', 'n.', '朗姆酒(一种用甘蔗汁酿制的烈性酒);一杯朗姆酒;', '...a bottle of rum.', '一瓶朗姆酒');
INSERT INTO `words` VALUES (7439, 'rumble', 'ˈrʌmbl', 'v.', '发出持续而低沉的声音;发出隆隆声;轰鸣着缓慢行进;', 'The silence of the night was punctuated by the distant rumble of traffic...', '夜晚的寂静被远处车流的隆隆声打破。');
INSERT INTO `words` VALUES (7440, 'ruminate', 'ˈruːmɪneɪt', 'v.', '沉思;认真思考;', 'He ruminated on the terrible wastage that typified American life...', '他认真地考虑了美国生活中典型的严重浪费问题。');
INSERT INTO `words` VALUES (7441, 'rummage', 'ˈrʌmɪdʒ', 'v.', '翻寻;乱翻;搜寻;', 'They rummage through piles of second-hand clothes for something that fits...', '他们在一堆堆的二手衣服里翻找着合身的衣服。');
INSERT INTO `words` VALUES (7442, 'rump', 'rʌmp', 'n.', '(兽类的)臀部;臀肉牛排;(人的)屁股蛋子;', 'The rump of the party does in fact still have considerable assets...', '事实上，这个党派的残余分子确实仍然拥有可观的资产。');
INSERT INTO `words` VALUES (7443, 'rumple', 'ˈrʌmpl', 'v.', '弄皱;弄乱;', 'I leaned forward to rumple his hair, but he jerked out of the way.', '我探身过去抚弄他的头发，但他马上躲开了。');
INSERT INTO `words` VALUES (7444, 'rumpus', 'ˈrʌmpəs', 'n.', '喧闹;吵吵嚷嚷;', 'He had actually left the company a year before the rumpus started.', '实际上，在争论开始一年前他就已经离开公司了。');
INSERT INTO `words` VALUES (7445, 'runaway', 'ˈrʌnəweɪ', 'adj.', '逃跑的;出走的;失控的;', 'Our Grand Sale in June was a runaway success.', '我们6月份的大型促销一举成功。');
INSERT INTO `words` VALUES (7446, 'runner', 'ˈrʌnə(r)', 'n.', '奔跑的人，奔跑的动物(尤指参加速度比赛者);走私者;偷运者;', '...a marathon runner...', '马拉松长跑选手');
INSERT INTO `words` VALUES (7447, 'runner-up', 'ˌrʌnər ˈʌp', 'n.', '第二名;亚军;非冠军的获奖者;', 'The ten runners-up will receive a case of wine.', '10位亚军会得到一箱葡萄酒。');
INSERT INTO `words` VALUES (7448, 'running', 'ˈrʌnɪŋ', 'n.', '跑;跑步(运动);管理;', 'We chose to do cross-country running.', '我们选择了越野赛跑。');
INSERT INTO `words` VALUES (7449, 'runny', 'ˈrʌni', 'adj.', '流鼻涕的;流眼泪的;太稀的;', 'Warm the honey until it becomes runny.', '将蜂蜜加热直到它变稀。');
INSERT INTO `words` VALUES (7450, 'runoff', 'ˈrʌnɒf', 'n.', '(胜负难分时的)加赛，决胜赛;(得票相近时的)决胜投票，决胜选举;', 'There will be a run-off between these two candidates on December 9th...', '12月9日将举行这两位候选人的决胜选举。');
INSERT INTO `words` VALUES (7451, 'runway', 'ˈrʌnweɪ', 'n.', '飞机跑道;', 'The plane started taxiing down the runway.', '飞机开始在跑道上滑行。');
INSERT INTO `words` VALUES (7452, 'rupee', 'ruːˈpiː', 'n.', '卢比(印度、巴基斯坦等国的货币单位);', 'He earns 20 rupees a day.', '他一天挣20卢比。');
INSERT INTO `words` VALUES (7453, 'rural', 'ˈrʊərəl', 'adj.', '乡村的;农村的;似农村的;', 'These plants have a tendency to grow in the more rural areas.', '这些植物大多生长在偏远的乡村。');
INSERT INTO `words` VALUES (7454, 'ruse', 'ruːz', 'n.', '诡计;骗术;', 'It is now clear that this was a ruse to divide them.', '现在已清楚这是一个离间他们的诡计。');
INSERT INTO `words` VALUES (7455, 'rush', 'rʌʃ', 'v.', '迅速移动;急促;快速运输;', 'A schoolgirl rushed into a burning flat to save a man\'s life...', '一位女学生冲进起火的公寓里去救一名男子。');
INSERT INTO `words` VALUES (7456, 'rust', 'rʌst', 'n.', '锈;铁锈;(植物的)锈病;', '...a decaying tractor, red with rust...', '锈迹斑斑的破拖拉机');
INSERT INTO `words` VALUES (7457, 'rustic', 'ˈrʌstɪk', 'adj.', '乡村(人)的;乡村(人)特色的;淳朴的;', '...the rustic charm of a country lifestyle.', '乡村生活的质朴魅力');
INSERT INTO `words` VALUES (7458, 'rustle', 'ˈrʌsl', 'v.', '(使)发出轻轻的摩擦声，发出沙沙声;偷窃(牲口);', 'The leaves rustled in the wind...', '树叶在风中沙沙作响。');
INSERT INTO `words` VALUES (7459, 'rustler', 'ˈrʌslə(r)', 'n.', '盗窃牲口的人;', '...the old Wyoming Trail once used by cattle rustlers and outlaws.', '偷牛贼和逃犯曾走过的古老的怀俄明小路');
INSERT INTO `words` VALUES (7460, 'rusty', 'ˈrʌsti', 'adj.', '生锈的;荒疏;荒废;', 'We spent years travelling around in a rusty old van.', '我们多年来都开着一辆生锈的旧货车四处旅行。');
INSERT INTO `words` VALUES (7461, 'rut', 'rʌt', 'n.', '车辙;刻板乏味的生活;(雄鹿等雄性动物的)发情期;', 'I don\'t like being in a rut — I like to keep moving on...', '我不喜欢一成不变的生活——我喜欢不断进取。');
INSERT INTO `words` VALUES (7462, 'ruthless', 'ˈruːθləs', 'adj.', '残酷无情的;残忍的;', 'The President was ruthless in dealing with any hint of internal political dissent.', '在处理任何内部政治分歧的苗头时，总统都毫不留情。');
INSERT INTO `words` VALUES (7463, 'rye', 'raɪ', 'n.', '黑麦;黑麦粒;', 'One of the first crops that I grew when we came here was rye.', '我们来到这里时，我种的第一批作物里就有黑麦。');
INSERT INTO `words` VALUES (7464, 'sabbath', 'ˈsæbəθ', 'n.', '安息日(犹太教定为星期六，基督教定为星期日);', '...a religious man who kept the Sabbath...', '过安息日的教徒');
INSERT INTO `words` VALUES (7465, 'sabbatical', 'səˈbætɪkl', 'n.', '(尤指供大学教师进行学术研究或旅行的)公休假，休假;', 'He took a year\'s sabbatical from the Foreign Office...', '他向外交部请了一年的假。');
INSERT INTO `words` VALUES (7466, 'sabotage', 'ˈsæbətɑːʒ', 'n.', '(为防止敌人利用或表示抗议而对设备、交通等进行的)蓄意毁坏;故意妨碍;捣乱;', 'The main pipeline supplying water was sabotaged by rebels.', '供水主管道被叛乱分子蓄意破坏了。');
INSERT INTO `words` VALUES (7467, 'sac', 'sæk', 'n.', '(人、动植物体内的)囊，液囊，气囊;', 'The lungs consist of millions of tiny air sacs.', '肺由数百万个小气囊组成。');
INSERT INTO `words` VALUES (7468, 'saccharine', 'ˈsækərɪn', 'adj.', '情感过分强烈而显夸张的;故作多情的;', '...a saccharine sequel to the Peter Pan story...', '《彼得·潘》这个故事煽情造作的续集');
INSERT INTO `words` VALUES (7469, 'sachet', 'ˈsæʃeɪ', 'n.', '(塑料或纸质)密封小袋;(置于衣物中的)小香囊，小香袋;', '...individual sachets of instant coffee.', '独立包装的小袋速溶咖啡');
INSERT INTO `words` VALUES (7470, 'sack', 'sæk', 'n.', '麻布(或厚纸、塑料等)大袋;(厚纸的)购物袋;一满袋;', '...a sack of potatoes.', '一袋土豆');
INSERT INTO `words` VALUES (7471, 'sacrament', 'ˈsækrəmənt', 'n.', '圣事，圣礼(如婚配、圣洗或圣餐等);圣餐(包括面饼和葡萄酒);', '...the holy sacrament of baptism.', '受洗圣礼');
INSERT INTO `words` VALUES (7472, 'sacred', 'ˈseɪkrɪd', 'adj.', '上帝的;神的;神圣的;', 'The owl is sacred for many Californian Indian people.', '对于很多加利福尼亚的印第安人而言，猫头鹰是圣物。');
INSERT INTO `words` VALUES (7473, 'sacrifice', 'ˈsækrɪfaɪs', 'n.', '牺牲;舍弃;祭献;', 'She sacrificed family life to her career...', '她为事业舍弃了家庭。');
INSERT INTO `words` VALUES (7474, 'sacrilege', 'ˈsækrəlɪdʒ', 'n.', '(对圣物或圣地的)亵渎;亵圣;', 'Stealing from a place of worship was regarded as sacrilege.', '从礼拜场所偷东西是对神灵的亵渎。');
INSERT INTO `words` VALUES (7475, 'sacrilegious', 'ˌsækrəˈlɪdʒəs', 'adj.', '亵渎神圣的;大不敬的;无礼的;', 'A number of churches were sacked and sacrilegious acts committed.', '很多教堂都被洗劫一空，而且作出了诸多渎神之举。');
INSERT INTO `words` VALUES (7476, 'sacrosanct', 'ˈsækrəʊsæŋkt', 'adj.', '神圣不容更改(或置疑)的;', 'Freedom of the press is sacrosanct.', '新闻自由是神圣不可侵犯的。');
INSERT INTO `words` VALUES (7477, 'sad', 'sæd', 'adj.', '悲哀的;难过的;显得悲哀的;', 'The relationship had been important to me and its loss left me feeling sad and empty...', '这份感情对我很重要，失去它让我感到悲伤而又空虚。');
INSERT INTO `words` VALUES (7478, 'sadden', 'ˈsædn', 'v.', '使悲伤;使伤心;使难过;', 'The cruelty in the world saddens me incredibly.', '人世间的残酷无情让我感到无比悲哀。');
INSERT INTO `words` VALUES (7479, 'sadist', 'ˈseɪdɪst', 'n.', '施虐狂者;(尤指)性施虐狂者;', 'Psychoanalysts tend to regard both sadism and masochism as arising from childhood deprivation.', '精神分析学家往往认为施虐狂和受虐狂都是由于儿时缺乏关爱造成的。');
INSERT INTO `words` VALUES (7480, 'sadistic', 'səˈdɪstɪk', 'adj.', '施虐狂的;虐待狂的;', 'The prisoners rioted against mistreatment by sadistic guards...', '囚犯因不堪忍受狱警施虐而发动了暴乱。');
INSERT INTO `words` VALUES (7481, 'safari', 'səˈfɑːri', 'n.', '(尤指在东非、非洲南部的)观赏(或捕猎)野兽的旅行;游猎;长途旅行;', 'He\'d like to go on safari to photograph snakes and tigers.', '他想进行一次观兽旅行，拍摄一些蛇类和老虎的照片。');
INSERT INTO `words` VALUES (7482, 'safe', 'seɪf', 'adj.', '处境(或情况)安全;不损害(或危害)健康的;安全的;', 'Officials arrived to assess whether it is safe to bring emergency food supplies into the city...', '官员们赶来评估向该市调拨紧急救援食品是否安全。');
INSERT INTO `words` VALUES (7483, 'safeguard', 'ˈseɪfɡɑːd', 'v.', '保护;保障;捍卫;', 'They will press for international action to safeguard the ozone layer...', '他们将督促国际社会采取行动保护臭氧层。');
INSERT INTO `words` VALUES (7484, 'safekeeping', '', 'n.', '安全照看;妥善保管;', 'Hampton had been given the bills for safekeeping by a business partner.', '一个生意伙伴曾将账单交给汉普顿代为保管。');
INSERT INTO `words` VALUES (7485, 'safely', 'ˈseɪfli', 'adv.', '未受损伤(或损坏);未丢失;安全地;', 'The waste is safely locked away until it is no longer radioactive...', '废料被安全储存起来，直到其不再有辐射性。');
INSERT INTO `words` VALUES (7486, 'safety', 'ˈseɪfti', 'n.', '安全;平安;安全性;', 'The report goes on to make a number of recommendations to improve safety on aircraft.', '该报告接着给出了一系列改善飞机安全性能的建议。');
INSERT INTO `words` VALUES (7487, 'saffron', 'ˈsæfrən', 'n.', '藏红花粉(用作食用色素);橘黄色;', '...saffron rice.', '番红花饭');
INSERT INTO `words` VALUES (7488, 'sag', 'sæɡ , sɑːɡ', 'v.', '(尤指由于承重或受压)中间下垂，下凹;减弱;减少;', 'The shirt\'s cuffs won\'t sag and lose their shape after washing...', '这件衬衫的袖口洗后照样挺括，不变形。');
INSERT INTO `words` VALUES (7489, 'saga', 'ˈsɑːɡə', 'n.', '萨迦(尤指古代挪威或冰岛讲述冒险经历和英雄业绩的长篇故事);(讲述许多年间发生的事情的)长篇故事，长篇小说;一连串的事件(或经历);', '...a 600 page saga about 18th century slavery.', '关于 18 世纪奴隶制的总计 600 页的长篇记述');
INSERT INTO `words` VALUES (7490, 'sagacious', 'səˈɡeɪʃəs', 'adj.', '精明练达的;洞察事理的;', '...a sagacious leader.', '精明练达的领导者');
INSERT INTO `words` VALUES (7491, 'sagacity', 'səˈɡæsəti', 'n.', '聪慧;精明;有决策力;', '...a man of great sagacity and immense experience.', '精明练达、阅历丰富的人');
INSERT INTO `words` VALUES (7492, 'sage', 'seɪdʒ', 'n.', '鼠尾草(可用作调料);哲人;智者;', '...ancient Chinese sages.', '中国先哲');
INSERT INTO `words` VALUES (7493, 'sail', 'seɪl', 'v.', '(船)航行;(人)乘船航行;驾驶(或乘坐)帆船航行(尤指作为体育运动);', 'The white sails billow with the breezes they catch.', '一张张白帆随着微风舞动。');
INSERT INTO `words` VALUES (7494, 'sailing', 'ˈseɪlɪŋ', 'n.', '帆船运动;(乘帆船的)航行;(从某港口开出的)航班;', 'There was swimming and sailing down on the lake.', '有人在湖上游泳和扬帆。');
INSERT INTO `words` VALUES (7495, 'saintly', 'ˈseɪntli', 'adj.', '像圣人的;非常圣洁善良的;', '...his saintly mother...', '他圣洁善良的母亲');
INSERT INTO `words` VALUES (7496, 'sake', 'seɪk , ˈsɑːki', 'n.', '缘故，理由，目的;利益;', 'Let\'s assume for the sake of argument that we manage to build a satisfactory database...', '为了便于讨论，不妨假定我们成功地建立了一个令人满意的数据库。');
INSERT INTO `words` VALUES (7497, 'salacious', 'səˈleɪʃəs', 'adj.', '淫秽的;色情的;', 'The newspapers once again filled their columns with salacious details.', '赤裸裸的猥亵细节再度充斥了报纸的版面。');
INSERT INTO `words` VALUES (7498, 'salad', 'ˈsæləd', 'n.', '(生吃的)蔬菜色拉，蔬菜沙拉;(拌有肉、鱼、奶酪等的)混合色拉，混合沙拉;(或生或熟，多拌有蛋黄酱，与面食、豆类等一起食用的)蔬菜色拉;', '...a salad of tomato, onion and cucumber.', '西红柿、洋葱和黄瓜拌成的色拉');
INSERT INTO `words` VALUES (7499, 'salaried', 'ˈsælərid', 'adj.', '领薪水的;付给薪水的;', '...salaried employees...', '拿薪水的雇员');
INSERT INTO `words` VALUES (7500, 'salary', 'ˈsæləri', 'n.', '薪金，薪水(尤指按月发放的);', '...the lawyer was paid a huge salary...', '那个律师薪水很高。');
INSERT INTO `words` VALUES (7501, 'sale', 'seɪl', 'n.', '出售;销售;销售量;', 'Efforts were made to limit the sale of alcohol.', '过去曾采取措施限制酒的销售。');
INSERT INTO `words` VALUES (7502, 'saleable', 'ˈseɪləbl', 'adj.', '适销的;有销路的;', 'The Oxfam shops depend on regular supplies of saleable items.', '乐施商店靠出售一些人们定期捐赠的适销物品维持经营。');
INSERT INTO `words` VALUES (7503, 'sales', 'ˈseɪlz', 'n.', '出售;销售;销售量;', 'Efforts were made to limit the sale of alcohol.', '过去曾采取措施限制酒的销售。');
INSERT INTO `words` VALUES (7504, 'salesman', 'ˈseɪlzmən', 'n.', '售货员;推销员;', '...an insurance salesman.', '保险推销员');
INSERT INTO `words` VALUES (7505, 'salesmanship', 'ˈseɪlzmənʃɪp', 'n.', '推销术;销售技巧;', 'I was captured by his brilliant salesmanship.', '我被他高明的推销技巧给征服了。');
INSERT INTO `words` VALUES (7506, 'salient', 'ˈseɪliənt', 'adj.', '最重要的;显著的;突出的;', 'He read the salient facts quickly...', '他快速浏览了重要的事实。');
INSERT INTO `words` VALUES (7507, 'saline', 'ˈseɪlaɪn', 'adj.', '盐的;含盐的;咸的;', '...a saline solution.', '盐溶液');
INSERT INTO `words` VALUES (7508, 'salinity', 'səˈlɪnɪti', 'n.', '盐分;盐浓度;含盐量;', '...a saline solution.', '盐溶液');
INSERT INTO `words` VALUES (7509, 'sallow', 'ˈsæləʊ', 'adj.', '灰黄的;蜡黄的;', 'She had lank hair and sallow skin.', '她头发平直，皮肤发黄。');
INSERT INTO `words` VALUES (7510, 'sally', 'ˈsæli', 'n.', '俏皮话;出击;突袭;', 'He had thus far succeeded in fending off my conversational sallies.', '他迄今为止一直不为我说的俏皮话所动。');
INSERT INTO `words` VALUES (7511, 'salmonella', 'ˌsælməˈnelə', 'n.', '沙门菌;', 'He was suffering from salmonella poisoning.', '他因感染了沙门氏菌而食物中毒。');
INSERT INTO `words` VALUES (7512, 'salon', 'ˈsælɒn', 'n.', '美发厅;美容厅;高级服装店;', '...a new hair salon.', '新开的美发厅');
INSERT INTO `words` VALUES (7513, 'salt', 'sɔːlt', 'n.', '盐;食盐;盐(金属和酸组成的化学物质);', 'Season lightly with salt and pepper.', '略微加点盐和胡椒调味。');
INSERT INTO `words` VALUES (7514, 'salty', 'ˈsɔːlti', 'adj.', '含盐的;咸的;有趣的;', '...salty foods such as ham and bacon.', '火腿和熏肉等咸味食品');
INSERT INTO `words` VALUES (7515, 'salubrious', 'səˈluːbriəs', 'adj.', '环境宜人的;清洁而有益健康的;', '...your salubrious lochside hotel.', '你入住的那家舒适宜人的湖边旅馆');
INSERT INTO `words` VALUES (7516, 'salutary', 'ˈsæljətri', 'adj.', '有益的(尽管往往让人不愉快);', 'It was a salutary experience to be in the minority...', '敌众我寡时倒是可以磨炼人。');
INSERT INTO `words` VALUES (7517, 'salutation', 'ˌsæljuˈteɪʃn', 'n.', '招呼;致意;打招呼;', 'Jackson nodded a salutation...', '杰克逊点头打招呼。');
INSERT INTO `words` VALUES (7518, 'salute', 'səˈluːt', 'v.', '(尤指军队中)敬礼;致敬;表示敬意;', 'One of the company stepped out and saluted the General...', '人群中走出一人向将军敬礼。');
INSERT INTO `words` VALUES (7519, 'salvage', 'ˈsælvɪdʒ', 'n.', '(对财物等的)抢救;抢救出的财物;', 'The team\'s first task was to decide what equipment could be salvaged...', '该队的首要任务是决定可以抢救哪些设备。');
INSERT INTO `words` VALUES (7520, 'salvation', 'sælˈveɪʃn', 'n.', '得救;救恩;救世;', 'The church\'s message of salvation has changed the lives of many.', '基督教中耶稣拯救灵魂的教义改变了很多人的生活。');
INSERT INTO `words` VALUES (7521, 'salve', 'sælv', 'n.', '药膏;软膏;油膏;', 'I give myself treats and justify them to salve my conscience.', '我常犒劳自己一番,然后找个说辞让自已良心稍安。');
INSERT INTO `words` VALUES (7522, 'salver', 'ˈsælvə(r)', 'n.', '金属托盘(正式场合用于上饮料或食物);', '...silver salvers laden with flutes of champagne.', '银制托盘上放着几杯斟在长笛杯里的香槟酒');
INSERT INTO `words` VALUES (7523, 'salvo', 'ˈsælvəʊ', 'n.', '齐射;齐投;奇袭;', 'They were to fire a salvo of blanks, after the national anthem.', '奏完国歌后，他们将同时发射空弹。');
INSERT INTO `words` VALUES (7524, 'same', 'seɪm', 'adj.', '同一的;相同的;(与…)相同的，一模一样的;', 'The houses were all the same — square, close to the street, needing paint...', '房子都一模一样——方方正正、临近街道、需要粉刷。');
INSERT INTO `words` VALUES (7525, 'sameness', 'ˈseɪmnəs', 'n.', '相同性;同一性;千篇一律;', 'He grew bored by the sameness of the speeches.', '这些千篇一律的演讲让他心生厌烦。');
INSERT INTO `words` VALUES (7526, 'sample', 'ˈsɑːmpl', 'n.', '(抽查的)样本，样品;(化验的)取样，样本，样;(作为标准或代表的)样品，货样;', 'You\'ll receive samples of paint, curtains and upholstery...', '你将收到涂料、窗帘、家具垫衬套的样品。');
INSERT INTO `words` VALUES (7527, 'sampling', 'ˈsɑːmplɪŋ', 'n.', '抽样;取样;(乐曲的)节录，选录;', 'You\'ll receive samples of paint, curtains and upholstery...', '你将收到涂料、窗帘、家具垫衬套的样品。');
INSERT INTO `words` VALUES (7528, 'sanctify', 'ˈsæŋktɪfaɪ', 'v.', '使神圣化;使正当化;使合法化;', 'She is trying to make amends for her marriage not being sanctified.', '她正努力弥补自己那段未受牧师认可的婚姻。');
INSERT INTO `words` VALUES (7529, 'sanctimonious', 'ˌsæŋktɪˈməʊniəs', 'adj.', '装作圣洁的;伪善的;道貌岸然的;', 'He writes smug, sanctimonious rubbish...', '他写的都是些自我陶醉、故作清高的垃圾作品。');
INSERT INTO `words` VALUES (7530, 'sanction', 'ˈsæŋkʃn', 'n.', '制裁;(正式)许可，批准;约束;', 'He may now be ready to sanction the use of force...', '他现在可能打算批准使用武力。');
INSERT INTO `words` VALUES (7531, 'sanctity', 'ˈsæŋktəti', 'n.', '神圣不可侵犯;神圣性;圣洁性;', '...the sanctity of human life.', '人类生命的神圣');
INSERT INTO `words` VALUES (7532, 'sanctuary', 'ˈsæŋktʃuəri', 'n.', '鸟兽保护区;禁猎区;庇护;', 'His church became a sanctuary for thousands of people who fled the civil war.', '他的教堂成为成千上万逃离内战的人们的避难所。');
INSERT INTO `words` VALUES (7533, 'sanctum', 'ˈsæŋktəm', 'n.', '(不受干扰的)私室，密室;圣所;', 'His bedroom\'s his inner sanctum.', '卧室是他的一方净土。');
INSERT INTO `words` VALUES (7534, 'sand', 'sænd', 'n.', '沙;沙滩;', 'They all walked barefoot across the damp sand to the water\'s edge.', '他们全都光着脚走过潮湿的沙地来到水边。');
INSERT INTO `words` VALUES (7535, 'sands', 'sændz', 'n.', '沙;沙滩;', 'They all walked barefoot across the damp sand to the water\'s edge.', '他们全都光着脚走过潮湿的沙地来到水边。');
INSERT INTO `words` VALUES (7536, 'sandstone', 'ˈsændstəʊn', 'n.', '砂岩;', '...the reddish sandstone walls.', '淡红色的砂岩墙');
INSERT INTO `words` VALUES (7537, 'sandwich', 'ˈsænwɪtʃ', 'n.', '夹心面包片;三明治;夹心蛋糕;', '...a ham sandwich.', '火腿三明治');
INSERT INTO `words` VALUES (7538, 'sandy', 'ˈsændi', 'adj.', '铺满沙子的;含沙的;沙褐色的;', '...long, sandy beaches.', '绵延的沙滩');
INSERT INTO `words` VALUES (7539, 'sane', 'seɪn', 'adj.', '精神健全的;神志正常的;明智的;', 'He seemed perfectly sane...', '他似乎神志完全正常。');
INSERT INTO `words` VALUES (7540, 'sangfroid', 'sɒŋˈfrwɑː', 'n.', '镇定;沉着;', 'He behaves throughout with a certain sang-froid.', '他从头至尾都表现出一定的冷静沉着。');
INSERT INTO `words` VALUES (7541, 'sanguine', 'ˈsæŋɡwɪn', 'adj.', '充满信心的;乐观的;', 'He\'s remarkably sanguine about the problems involved...', '他对所涉及的问题态度极为乐观。');
INSERT INTO `words` VALUES (7542, 'sanitary', 'ˈsænətri', 'adj.', '卫生的;环境卫生的;公共卫生的;', 'Sanitary conditions are appalling...', '卫生条件之差令人震惊。');
INSERT INTO `words` VALUES (7543, 'sanitation', 'ˌsænɪˈteɪʃn', 'n.', '卫生设备;卫生设施体系;', '...the hazards of contaminated water and poor sanitation.', '水污染和恶劣卫生环境的危害');
INSERT INTO `words` VALUES (7544, 'sanity', 'ˈsænəti', 'n.', '精神健全;神志正常;明智;', 'He and his wife finally had to move from their apartment just to preserve their sanity.', '为了不致发疯，他和妻子最终只得从公寓中搬出。');
INSERT INTO `words` VALUES (7545, 'sap', 'sæp', 'n.', '(植物体内运送养分的)液，汁;笨蛋;易上当的人;', 'I was afraid the sickness had sapped my strength...', '我担心患病已让我元气大伤。');
INSERT INTO `words` VALUES (7546, 'sapling', 'ˈsæplɪŋ', 'n.', '幼树;', '...newly planted saplings swaying gently in the spring breeze.', '随春风摇曳的新栽小树');
INSERT INTO `words` VALUES (7547, 'sapphire', 'ˈsæfaɪə(r)', 'n.', '蓝宝石;宝蓝色;天蓝色;', '...a sapphire engagement ring.', '一枚镶着蓝宝石的订婚戒指');
INSERT INTO `words` VALUES (7548, 'sarcasm', 'ˈsɑːkæzəm', 'n.', '讽刺;嘲讽;挖苦;', '\'What a pity,\' Graham said with a hint of sarcasm...', '“太遗憾了，”格雷厄姆略带挖苦地说道。');
INSERT INTO `words` VALUES (7549, 'sarcastic', 'sɑːˈkæstɪk', 'adj.', '讽刺的;嘲讽的;挖苦的;', 'She poked fun at people\'s shortcomings with sarcastic remarks.', '她冷嘲热讽地拿别人的缺点开玩笑。');
INSERT INTO `words` VALUES (7550, 'sarcophagus', 'sɑːˈkɒfəɡəs', 'n.', '(尤指古代有雕饰的)石棺;', '...an Egyptian sarcophagus.', '埃及石棺');
INSERT INTO `words` VALUES (7551, 'sardine', 'ˌsɑːˈdiːn', 'n.', '沙丁鱼;', 'They opened a tin of sardines.', '他们打开了一罐沙丁鱼。');
INSERT INTO `words` VALUES (7552, 'sardonic', 'sɑːˈdɒnɪk', 'adj.', '轻慢的;轻蔑的;嘲弄的;', '...a big, sardonic man, who intimidated his students.', '令学生胆怯的身材魁梧、爱嘲讽人的男人');
INSERT INTO `words` VALUES (7553, 'sartorial', 'sɑːˈtɔːriəl', 'adj.', '服装的;(尤指)男装的;衣着的;', '...Sebastian\'s sartorial elegance...', '塞巴斯蒂安精致考究的穿着');
INSERT INTO `words` VALUES (7554, 'sash', 'sæʃ', 'n.', '(尤指制服的)腰带，肩带，饰带;(垂直推拉窗任何一扇的)窗扇;', 'She wore a white dress with a thin blue sash...', '她身着一件系有蓝色细腰带的白色连衣裙。');
INSERT INTO `words` VALUES (7555, 'satanic', 'səˈtænɪk', 'adj.', '崇拜撒旦的;道德败坏的;邪恶的;', '...satanic cults.', '撒旦崇拜');
INSERT INTO `words` VALUES (7556, 'satellite', 'ˈsætəlaɪt', 'n.', '人造卫星;卫星;卫星城;', 'The rocket launched two communications satellites...', '火箭发射了两颗通信卫星。');
INSERT INTO `words` VALUES (7557, 'satiate', 'ˈseɪʃieɪt', 'v.', '满足;', 'The dinner was enough to satiate the gourmets...', '晚餐足以让这些美食家们大饱口福。');
INSERT INTO `words` VALUES (7558, 'satin', 'ˈsætɪn', 'n.', '缎子;', '...a peach satin ribbon.', '桃色缎带');
INSERT INTO `words` VALUES (7559, 'satire', 'ˈsætaɪə(r)', 'n.', '讽刺;讥讽;讽刺作品;', 'The commercial side of the Christmas season is an easy target for satire.', '圣诞季商业化的一面很容易招来讥讽。');
INSERT INTO `words` VALUES (7560, 'satiric', 'səˈtɪrɪk', 'adj.', '同 satirical;讽刺的;讥讽的;', '...Ibsen\'s satiric attack on bourgeois convention.', '易卜生对中产阶级礼数的讽刺性抨击');
INSERT INTO `words` VALUES (7561, 'satirical', 'səˈtɪrɪkl', 'adj.', '讽刺的;讥讽的;', '...a satirical novel about London life in the late 80s.', '讽刺 80 年代末伦敦生活的一部小说');
INSERT INTO `words` VALUES (7562, 'satirist', 'ˈsætərɪst', 'n.', '讽刺作家;惯于讽刺的人;', 'He built a reputation in the 1970s as a social satirist.', '他在 20 世纪 70 年代作为一名社会讽刺作家声名鹊起。');
INSERT INTO `words` VALUES (7563, 'satisfaction', 'ˌsætɪsˈfækʃn', 'n.', '满足;满意;欣慰;', 'She felt a small glow of satisfaction...', '她有了一丝满足感。');
INSERT INTO `words` VALUES (7564, 'satisfactorily', 'ˌsætɪs\'fæktərəlɪ', 'adv.', '满意地;可靠地;顺心;', 'I never got a satisfactory answer...', '我从未得到令我满意的答复。');
INSERT INTO `words` VALUES (7565, 'satisfactory', 'ˌsætɪsˈfæktəri', 'adj.', '令人满意的;够好的;可以的;', 'I never got a satisfactory answer...', '我从未得到令我满意的答复。');
INSERT INTO `words` VALUES (7566, 'satisfied', 'ˈsætɪsfaɪd', 'adj.', '满意的;满足的;欣慰的;', 'We are not satisfied with these results.', '我们对这些结果并不满意。');
INSERT INTO `words` VALUES (7567, 'satisfy', 'ˈsætɪsfaɪ', 'v.', '使满意;使满足;满足(要求、需要等);', 'The pace of change has not been quick enough to satisfy everyone...', '变化的速度还不够快，不能让所有人都满意。');
INSERT INTO `words` VALUES (7568, 'satisfying', 'ˈsætɪsfaɪɪŋ', 'adj.', '令人满意(或满足)的;', 'I found wood carving satisfying.', '我觉得木雕能给人带来满足感。');
INSERT INTO `words` VALUES (7569, 'saturate', 'ˈsætʃəreɪt', 'v.', '使湿透;浸透;使充满;', 'In the last days before the vote, both sides are saturating the airwaves...', '选举前最后几天，竞选双方占用了所有的广播电台和电视台。');
INSERT INTO `words` VALUES (7570, 'saturated', 'ˈsætʃəreɪtɪd', 'adj.', '湿透;浸透;(溶液)饱和的;', '...foods rich in cholesterol and saturated fats.', '富含胆固醇和饱和脂肪的食物');
INSERT INTO `words` VALUES (7571, 'saturation', 'ˌsætʃəˈreɪʃn', 'n.', '饱和;饱和状态;饱和度;', 'Reforms have led to the saturation of the market with goods...', '改革已经使得市场上商品饱和。');
INSERT INTO `words` VALUES (7572, 'saturday', 'ˈsætədeɪ', 'n.', '星期六;', 'She had a call from him on Saturday morning at the studio...', '星期六早上她在录音室接到他打来的电话。');
INSERT INTO `words` VALUES (7573, 'saturnine', 'ˈsætənaɪn', 'adj.', '严肃而令人畏惧的;阴沉的;', 'He had a rather forbidding, saturnine manner.', '他的举止相当乖戾阴郁，令人望而生畏。');
INSERT INTO `words` VALUES (7574, 'sauce', 'sɔːs', 'n.', '调味汁;酱;讨厌的话(或举动);', '...pasta cooked in a sauce of garlic, tomatoes, and cheese.', '用蒜、西红柿和奶酪作酱汁做成的意大利面');
INSERT INTO `words` VALUES (7575, 'saucepan', 'ˈsɔːspən', 'n.', '(带盖，有一长柄或两耳的)深煮锅;', 'Cook the potatoes and turnips in a large saucepan.', '用大炖锅煮土豆和芜菁。');
INSERT INTO `words` VALUES (7576, 'saucy', 'ˈsɔːsi', 'adj.', '粗鲁的;粗俗的;不雅的;', '...a saucy joke.', '荤段子');
INSERT INTO `words` VALUES (7577, 'saudi', 'ˈsaʊdi', 'n.', '沙特阿拉伯人;', 'Saudi officials have dismissed such reports as rumours.', '沙特阿拉伯的官员已经将此类报告视作谣言而不予理睬。');
INSERT INTO `words` VALUES (7578, 'saunter', 'ˈsɔːntə(r)', 'v.', '悠闲地走;漫步;闲逛;', 'We watched our fellow students saunter into the building...', '我们看着同学们悠闲地走进了大楼。');
INSERT INTO `words` VALUES (7579, 'sausage', 'ˈsɒsɪdʒ', 'n.', '香肠;腊肠;', '...sausages and chips.', '香肠和薯条');
INSERT INTO `words` VALUES (7580, 'savage', 'ˈsævɪdʒ', 'adj.', '凶恶的;凶残的;损害严重的;', 'This was a savage attack on a defenceless young girl.', '这是对一个毫无自卫能力的年轻女孩的野蛮袭击。');
INSERT INTO `words` VALUES (7581, 'savagely', '\'sævɪdʒli', 'adv.', '野蛮地;', 'This was a savage attack on a defenceless young girl.', '这是对一个毫无自卫能力的年轻女孩的野蛮袭击。');
INSERT INTO `words` VALUES (7582, 'savant', 'səˈvɑːnt', 'n.', '博学之士;学者;专家;', '... the opinion of savants on the composition of the lunar surface.', '专家对于月球表面构造的见解');
INSERT INTO `words` VALUES (7583, 'save', 'seɪv', 'v.', '救;救助;挽救;', '...a final attempt to save 40,000 jobs in Britain\'s troubled aero industry...', '为保住陷入困境中的英国航空业的 4 万份工作所作的最后一次努力');
INSERT INTO `words` VALUES (7584, 'saver', 'ˈseɪvə(r)', 'n.', '储户;存户;有助于节省的事物;', 'Low interest rates are bad news for savers.', '低利率对于储蓄者来说是个坏消息。');
INSERT INTO `words` VALUES (7585, 'saving', 'ˈseɪvɪŋ', 'n.', '节省物;节省;节约;', 'Fill in the form below and you will be making a saving of £6.60 on a one-year subscription.', '填写以下表格，全年订阅即可为您节省 6.60英镑。');
INSERT INTO `words` VALUES (7586, 'savings', 'ˈseɪvɪŋz', 'n.', '节省物;节省;节约;', 'Fill in the form below and you will be making a saving of £6.60 on a one-year subscription.', '填写以下表格，全年订阅即可为您节省 6.60英镑。');
INSERT INTO `words` VALUES (7587, 'savoir-faire', 'ˌsævwɑː ˈfeə(r)', 'n.', '机敏处世的本领;遇事不乱的能力;随机应变的才干;', 'He was full of jocularity and savoir-faire.', '他幽默诙谐，处世机敏。');
INSERT INTO `words` VALUES (7588, 'savvy', 'ˈsævi', 'n.', '实际知识;见识;了解;', 'He is known for his political savvy and strong management skills.', '他以睿智的政治头脑和强势的管理手腕出名。');
INSERT INTO `words` VALUES (7589, 'sawdust', 'ˈsɔːdʌst', 'n.', '锯末;', '...a layer of sawdust.', '一层锯木屑');
INSERT INTO `words` VALUES (7590, 'say', 'seɪ', 'v.', '说;讲;告诉;', '\'I\'m sorry,\' he said...', '“对不起，”他说。');
INSERT INTO `words` VALUES (7591, 'saying', 'ˈseɪɪŋ', 'n.', '谚语;格言;警句;', 'We also realize the truth of that old saying: Charity begins at home...', '我们也明白了那句老话的道理：仁爱始于家庭。');
INSERT INTO `words` VALUES (7592, 'says', 'sez', 'v.', '说;讲;告诉;', '\'I\'m sorry,\' he said...', '“对不起，”他说。');
INSERT INTO `words` VALUES (7593, 'say-so', 'ˈseɪ səʊ', 'n.', '许可;准许;', 'Directors call the shots and nothing happens on set without their say-so.', '导演发号施令，拍摄现场的一切都必须得到他们的许可。');
INSERT INTO `words` VALUES (7594, 'scab', 'skæb', 'n.', '痂;(动物)疥癣;(植物)疮痂病，斑点病;', 'The area can be very painful until scabs form after about ten days.', '这个伤口会疼痛难忍，直到大约 10 天后结痂才会好些。');
INSERT INTO `words` VALUES (7595, 'scabrous', 'ˈskeɪbrəs', 'adj.', '猥亵的;淫秽的;有伤风化的;', '...the scabrous lower reaches of the film business.', '低俗的情色电影业');
INSERT INTO `words` VALUES (7596, 'scaffold', 'ˈskæfəʊld', 'n.', '断头台;绞刑架;脚手架;', 'Moore ascended the scaffold and addressed the executioner.', '穆尔走上断头台，和刽子手说话。');
INSERT INTO `words` VALUES (7597, 'scald', 'skɔːld', 'v.', '烫伤;', 'A patient scalded herself in a hot bath.', '一个病人在洗热水澡时烫伤了。');
INSERT INTO `words` VALUES (7598, 'scalding', 'ˈskɔːldɪŋ', 'adj.', '滚烫的;灼热的;', 'I tried to sip the tea but it was scalding.', '我想抿一口茶，可是太烫了。');
INSERT INTO `words` VALUES (7599, 'scale', 'skeɪl', 'n.', '(尤指与其他事物相比较时的)规模，范围，程度;等级;级别;', 'However, he underestimates the scale of the problem...', '然而，他低估了问题的严重性。');
INSERT INTO `words` VALUES (7600, 'scaling', 'ˈskeɪlɪŋ', 'v.', '攀登;到达…顶点;去鳞;', 'However, he underestimates the scale of the problem...', '然而，他低估了问题的严重性。');
INSERT INTO `words` VALUES (7601, 'scalp', 'skælp', 'n.', '头皮;(旧时美洲土著从被杀的敌人头上剥下作为战利品的)带发头皮;(表示某人已被打败或已受到惩罚的)标志;', 'He smoothed his hair back over his scalp...', '他把头发向后理顺。');
INSERT INTO `words` VALUES (7602, 'scaly', 'ˈskeɪli', 'adj.', '有鳞屑的;有皮屑的;', 'The brown rat has prominent ears and a long scaly tail.', '那只棕鼠两只耳朵向前支着，一条长尾巴布满鳞片。');
INSERT INTO `words` VALUES (7603, 'scam', 'skæm', 'n.', '欺诈;诈财骗局;', 'They believed they were participating in an insurance scam, not a murder...', '他们认为自己是在参与一起保险诈骗，而不是谋杀。');
INSERT INTO `words` VALUES (7604, 'scamper', 'ˈskæmpə(r)', 'v.', '欢快地奔走;蹦蹦跳跳;', 'Children scampered off the yellow school bus and into the playground...', '孩子们欢快地跑下黄色的校车，冲进运动场。');
INSERT INTO `words` VALUES (7605, 'scan', 'skæn', 'v.', '细看;察看;审视;', 'She scanned the advertisement pages of the newspapers...', '她浏览了一下报纸的广告页。');
INSERT INTO `words` VALUES (7606, 'scandal', 'ˈskændl', 'n.', '丑行;使人震惊的丑事;丑闻;', '...a financial scandal.', '金融丑闻');
INSERT INTO `words` VALUES (7607, 'scandalous', 'ˈskændələs', 'adj.', '可耻的;不可原谅的;讲述丑闻的;', 'They would be sacked for criminal or scandalous behaviour...', '若有犯罪或不道德的行为，他们就会被解雇。');
INSERT INTO `words` VALUES (7608, 'scandinavian', 'ˌskændɪˈneɪviən', 'n.', '斯堪的纳维亚人;', 'The Baltic republics have called on the Scandinavian countries for help.', '波罗的海诸共和国已呼吁斯堪的纳维亚诸国提供援助。');
INSERT INTO `words` VALUES (7609, 'scanner', 'ˈskænə(r)', 'n.', '扫描仪;扫描器;扫描设备;', '...brain scanners.', '大脑扫描仪');
INSERT INTO `words` VALUES (7610, 'scant', 'skænt', 'adj.', '一丁点的;微小的;不足的;', 'She began to berate the police for paying scant attention to the theft from her car.', '她开始痛斥警方对她车内被窃一案不够关注。');
INSERT INTO `words` VALUES (7611, 'scanty', 'ˈskænti', 'adj.', '不足的;欠缺的;太少的;', 'So far, what scanty evidence we have points to two suspects.', '迄今为止，我们掌握的能够指证两个嫌疑犯的证据少得可怜。');
INSERT INTO `words` VALUES (7612, 'scapegoat', 'ˈskeɪpɡəʊt', 'n.', '替罪羊;代人受过者;', 'I don\'t deserve to be made the scapegoat for a couple of bad results.', '我不应该为一些不好的结果顶罪。');
INSERT INTO `words` VALUES (7613, 'scar', 'skɑː(r)', 'n.', '伤疤;伤痕;瘢痕;', 'He had a scar on his forehead.', '他的前额有一道疤。');
INSERT INTO `words` VALUES (7614, 'scarce', 'skeəs', 'adj.', '缺乏的;不足的;稀少的;', 'Food was scarce and expensive...', '食物供不应求，而且价格昂贵。');
INSERT INTO `words` VALUES (7615, 'scarcely', 'ˈskeəsli', 'adv.', '勉强;刚;几乎不;', 'He could scarcely breathe...', '他几乎喘不过气来。');
INSERT INTO `words` VALUES (7616, 'scarcity', 'ˈskeəsəti', 'n.', '缺乏;不足;稀少;', '...an ever increasing scarcity of water.', '水资源的日益匮乏');
INSERT INTO `words` VALUES (7617, 'scare', 'skeə(r)', 'v.', '惊吓;使害怕;使恐惧;', 'You\'re scaring me...', '你吓着我了。');
INSERT INTO `words` VALUES (7618, 'scared', 'skeəd', 'adj.', '害怕;恐惧;畏惧;', 'I\'m certainly not scared of him...', '我当然不怕他。');
INSERT INTO `words` VALUES (7619, 'scarf', 'skɑːf', 'n.', '围巾;披巾;头巾;', 'He reached up to loosen the scarf around his neck.', '他伸手松了松脖子上的围巾。');
INSERT INTO `words` VALUES (7620, 'scarlet', 'ˈskɑːlət', 'adj.', '猩红的;鲜红的;', '...her scarlet lipstick.', '她鲜红色的唇膏');
INSERT INTO `words` VALUES (7621, 'scars', 'skɑːz', 'n.', '伤疤;伤痕;瘢痕;', 'He had a scar on his forehead.', '他的前额有一道疤。');
INSERT INTO `words` VALUES (7622, 'scary', 'ˈskeəri', 'adj.', '恐怖的;吓人的;', 'I think prison is going to be a scary thing for Harry...', '我觉得蹲监狱对于哈里会是件很可怕的事。');
INSERT INTO `words` VALUES (7623, 'scathing', 'ˈskeɪðɪŋ', 'adj.', '严厉批评的;无情抨击的;尖刻地斥责的;', 'His report was scathing about Loyalist and Republican terror groups...', '他的报告猛烈抨击了忠英派和共和军的恐怖组织。');
INSERT INTO `words` VALUES (7624, 'scatter', 'ˈskætə(r)', 'v.', '撒;撒播;散开;', 'She tore the rose apart and scattered the petals over the grave...', '她掰开玫瑰花，将花瓣撒在坟墓上。');
INSERT INTO `words` VALUES (7625, 'scattered', 'ˈskætəd', 'adj.', '分散的;零散的;疏落的;', 'He picked up the scattered toys...', '他收拾好扔得到处都是的玩具。');
INSERT INTO `words` VALUES (7626, 'scavenge', 'ˈskævɪndʒ', 'v.', '(从废弃物中)觅食;捡破烂;拾荒;', 'Many are orphans, their parents killed as they scavenged for food...', '很多人是孤儿，他们的父母在垃圾中找吃的时丢了性命。');
INSERT INTO `words` VALUES (7627, 'scavenger', 'ˈskævɪndʒə(r)', 'n.', '食腐肉的兽(或鸟);捡破烂的人;拾荒者;', 'Many are orphans, their parents killed as they scavenged for food...', '很多人是孤儿，他们的父母在垃圾中找吃的时丢了性命。');
INSERT INTO `words` VALUES (7628, 'scenario', 'səˈnɑːriəʊ', 'n.', '设想;方案;预测;', '...the nightmare scenario of a divided and irrelevant Royal Family...', '对于王室四分五裂、无足轻重的可怕设想');
INSERT INTO `words` VALUES (7629, 'scene', 'siːn', 'n.', '(尤指不愉快事件发生的)地点，现场;事件;场面;', 'I found the scene in which Percy proposed to Olive tremendously poignant.', '我觉得珀西向奥利芙求婚的一幕太让人心酸了。');
INSERT INTO `words` VALUES (7630, 'scenery', 'ˈsiːnəri', 'n.', '风景;景色;风光;', '...the island\'s spectacular scenery...', '岛上壮丽的风景');
INSERT INTO `words` VALUES (7631, 'scenic', 'ˈsiːnɪk', 'adj.', '风景优美的;舞台布景的;', 'This is an extremely scenic part of America.', '这是美国风景非常秀丽的一个地区。');
INSERT INTO `words` VALUES (7632, 'scent', 'sent', 'n.', '香味;(人的)气味，气息;(动物留下的)臭迹，遗臭;', 'Flowers are chosen for their scent as well as their look.', '选花不仅要观其形还要闻其香。');
INSERT INTO `words` VALUES (7633, 'schedule', 'ˈʃedjuːl', 'n.', '工作计划;日程安排;(电视或广播)节目表;', 'He has been forced to adjust his schedule...', '他被迫调整了自己的日程安排。');
INSERT INTO `words` VALUES (7634, 'scheduled', 'ˈʃedjuːld', 'v.', '安排;为…安排时间;预定;', 'He has been forced to adjust his schedule...', '他被迫调整了自己的日程安排。');
INSERT INTO `words` VALUES (7635, 'schema', 'ˈskiːmə', 'n.', '(计划或理论的)提要，纲要;', '...a definite position in the schema of the economic process.', '经济进程纲要中的明确位置');
INSERT INTO `words` VALUES (7636, 'schematic', 'skiːˈmætɪk', 'adj.', '略图的;简表的;严谨的;', 'This is represented in the schematic diagram below.', '这正如下面的这幅简略图所示。');
INSERT INTO `words` VALUES (7637, 'scheme', 'skiːm', 'n.', '计划;方案;体系;', '...schemes to help combat unemployment.', '有助于减少失业的方案');
INSERT INTO `words` VALUES (7638, 'scheming', 'ˈskiːmɪŋ', 'adj.', '惯搞阴谋的;诡计多端的;狡诈的;', '...schemes to help combat unemployment.', '有助于减少失业的方案');
INSERT INTO `words` VALUES (7639, 'schism', 'ˈskɪzəm', 'n.', '分裂;宗派活动;(尤指)教会分裂;', '...the great schism which divided the Christian world in the 11th century...', '11 世纪划分基督教世界的大规模教会分裂');
INSERT INTO `words` VALUES (7640, 'scholar', 'ˈskɒlə(r)', 'n.', '学者;奖学金获得者;聪颖勤奋的学生;', 'The library attracts thousands of scholars and researchers.', '那个图书馆吸引了成千上万的学者和研究人员。');
INSERT INTO `words` VALUES (7641, 'scholarly', 'ˈskɒləli', 'adj.', '勤奋好学的;有学问的;学术的;', 'He was an intellectual, scholarly man.', '他才华横溢，知识渊博。');
INSERT INTO `words` VALUES (7642, 'scholarship', 'ˈskɒləʃɪp', 'n.', '奖学金;学问;学术;', 'He got a scholarship to the Pratt Institute of Art.', '他获得了普拉特艺术学院的奖学金。');
INSERT INTO `words` VALUES (7643, 'scholastic', 'skəˈlæstɪk', 'adj.', '学校的;教育的;学业的;', '...the values which encouraged her scholastic achievement.', '促使她取得学术成就的价值观');
INSERT INTO `words` VALUES (7644, 'school', 'skuːl', 'n.', '(中、小)学校;上学;上学阶段;', '...a boy who was in my class at school...', '上学时和我一个班的男孩');
INSERT INTO `words` VALUES (7645, 'schoolboy', 'ˈskuːlbɔɪ', 'n.', '(学校的)男生;', '...a group of ten-year-old schoolboys.', '一群 10 岁的男学生');
INSERT INTO `words` VALUES (7646, 'schoolgirl', 'ˈskuːlɡɜːl', 'n.', '(学校的)女生;', '...half a dozen giggling schoolgirls. 6', '个咯咯笑的女生');
INSERT INTO `words` VALUES (7647, 'schoolhouse', 'ˈskuːlhaʊs', 'n.', '(尤指旧时乡村学校的)校舍;(小型学校旁边的)教师住房;', 'McCreary lives in a converted schoolhouse outside Charlottesville.', '麦克里里住在夏洛茨维尔市外一幢校舍改建的小楼里。');
INSERT INTO `words` VALUES (7648, 'schooling', 'ˈskuːlɪŋ', 'n.', '学校教育;', 'He had little formal schooling.', '他几乎没受过正式的学校教育。');
INSERT INTO `words` VALUES (7649, 'schoolmate', 'ˈskuːlmeɪt', 'n.', '(尤指关系要好的)同学，同窗，校友;', 'He started the magazine with a schoolmate...', '他和一个同学创办了那份杂志。');
INSERT INTO `words` VALUES (7650, 'schoolwork', 'ˈskuːlwɜːk', 'n.', '学校作业;课堂作业;课外作业;', 'My mother would help me with my schoolwork.', '我的母亲会帮我做功课。');
INSERT INTO `words` VALUES (7651, 'schoolyard', 'ˈskuːljɑːd', 'n.', '校园;(学校的)露天操场;', '...the sound of the kids in the schoolyard.', '操场上孩子们的叫喊声');
INSERT INTO `words` VALUES (7652, 'science', 'ˈsaɪəns', 'n.', '科学;自然科学;自然科学的学习与研究;', 'The best discoveries in science are very simple.', '最伟大的科学发现都非常简单。');
INSERT INTO `words` VALUES (7653, 'scientific', 'ˌsaɪənˈtɪfɪk', 'adj.', '科学(上)的;关于科学的;细致严谨的;', 'Scientific research is widely claimed to be the source of the high standard of living in the US.', '科学研究被普遍认为是美国高生活水准的源泉。');
INSERT INTO `words` VALUES (7654, 'scientifically', 'ˌsaɪənˈtɪfɪk(ə)li', 'adv.', '按科学方法;', 'Scientific research is widely claimed to be the source of the high standard of living in the US.', '科学研究被普遍认为是美国高生活水准的源泉。');
INSERT INTO `words` VALUES (7655, 'scientist', 'ˈsaɪəntɪst', 'n.', '科学家;', 'Scientists have collected more data than expected.', '科学家们已经收集到了比预期更多的数据。');
INSERT INTO `words` VALUES (7656, 'sci-fi', 'ˈsaɪ faɪ', 'n.', 'science fiction 的缩约形式;', '...a sci-fi film.', '科幻片');
INSERT INTO `words` VALUES (7657, 'scintilla', 'sɪnˈtɪlə', 'n.', '一星半点;毫厘;', 'He says there is \'not a scintilla of evidence\' to link him to any controversy.', '他说“没有丝毫证据”能将他跟任何争议联系在一起。');
INSERT INTO `words` VALUES (7658, 'scion', 'ˈsaɪən', 'n.', '(尤指名门望族的)子弟;幼枝;(尤指)接穗;', 'Nabokov was the scion of an aristocratic family.', '纳博科夫是一个贵族家庭的阔少。');
INSERT INTO `words` VALUES (7659, 'scoff', 'skɒf', 'v.', '嘲笑;讥讽;贪婪地吃;', 'At first I scoffed at the notion...', '刚开始我对那种想法嗤之以鼻。');
INSERT INTO `words` VALUES (7660, 'scold', 'skəʊld', 'v.', '训斥，责骂(孩子);', 'If he finds out, he\'ll scold me...', '如果他知道了，肯定会责骂我的。');
INSERT INTO `words` VALUES (7661, 'scoop', 'skuːp', 'n.', '勺;铲子;一勺(的量);', 'Michael knelt next to her and scooped her into his arms.', '迈克尔挨着她跪下，一下把她揽入怀里。');
INSERT INTO `words` VALUES (7662, 'scope', 'skəʊp', 'n.', '(做或实现某事的)机会，能力;(题目、组织、活动等的)范围;…镜(观察仪器);', 'He believed in giving his staff scope for initiative...', '他认为应给员工提供发挥主动性的机会。');
INSERT INTO `words` VALUES (7663, 'scorch', 'skɔːtʃ', 'v.', '(把…)烫坏，烧煳;烤焦(物体表面);(使)枯黄，枯萎(尤指因曝晒或化学品的作用);', 'The bomb scorched the side of the building.', '炸弹烧焦了建筑物的一侧。');
INSERT INTO `words` VALUES (7664, 'scorching', 'ˈskɔːtʃɪŋ', 'adj.', '酷热的;猛烈的;激烈的;', 'That race was run in scorching weather...', '那次赛跑是在酷热的天气中进行的。');
INSERT INTO `words` VALUES (7665, 'scoreboard', 'ˈskɔːbɔːd', 'n.', '记分牌;', 'The figures flash up on the scoreboard.', '分数在记分牌上亮起。');
INSERT INTO `words` VALUES (7666, 'scorer', 'ˈskɔːrə(r)', 'n.', '得分运动员;得分手;(在游戏或比赛中的)记分人，记分员;', '...David Hirst, the scorer of 11 goals this season.', '戴维·赫斯特，本赛季有 11个入球的得分手');
INSERT INTO `words` VALUES (7667, 'scorn', 'skɔːn', 'n.', '轻蔑;鄙视;', 'Researchers greeted the proposal with scorn...', '研究员们对这个建议嗤之以鼻。');
INSERT INTO `words` VALUES (7668, 'scornful', 'ˈskɔːnfl', 'adj.', '轻蔑的;鄙夷的;', 'He is deeply scornful of politicians.', '他非常看不起政客。');
INSERT INTO `words` VALUES (7669, 'scornfully', 'ˈskɔːnfʊli', 'adv.', '轻蔑地;倨傲无礼地;以讽刺的口吻;', 'He is deeply scornful of politicians.', '他非常看不起政客。');
INSERT INTO `words` VALUES (7670, 'scotch', 'skɒtʃ', 'v.', '阻止;挫败;平息;', 'They have scotched rumours that they are planning a special London show.', '他们辟谣说并没有在筹划伦敦专场秀。');
INSERT INTO `words` VALUES (7671, 'scottish', 'ˈskɒtɪʃ', 'adj.', '苏格兰的;苏格兰人的;', '...Scottish football.', '苏格兰足球');
INSERT INTO `words` VALUES (7672, 'scoundrel', 'ˈskaʊndrəl', 'n.', '无赖;恶棍;', 'He is a lying scoundrel!', '他是个无耻的骗子！');
INSERT INTO `words` VALUES (7673, 'scour', 'ˈskaʊə(r)', 'v.', '(彻底地)搜寻，搜查，翻找;(用粗糙的物体)擦净，擦亮;冲刷成;', 'Rescue crews had scoured an area of 30 square miles...', '救援人员已经搜遍了30平方英里的范围。');
INSERT INTO `words` VALUES (7674, 'scourge', 'skɜːdʒ', 'n.', '祸害;祸根;灾害;', '...the best chance in 20 years to end the scourge of terrorism...', '20 年来消灭恐怖主义祸害的最佳机会');
INSERT INTO `words` VALUES (7675, 'scout', 'skaʊt', 'n.', '童子军;童子军成员;侦察员;', 'They sent two men out in front as scouts.', '他们派两人在前面侦察。');
INSERT INTO `words` VALUES (7676, 'scowl', 'skaʊl', 'v.', '怒视(某人或某物);', 'He scowled, and slammed the door behind him...', '他怒气冲冲，摔门而去。');
INSERT INTO `words` VALUES (7677, 'scraggy', 'ˈskræɡi', 'adj.', '骨瘦如柴的;面黄肌瘦的;', '...his scraggy neck.', '他那干瘦的脖子');
INSERT INTO `words` VALUES (7678, 'scramble', 'ˈskræmbl', 'v.', '(迅速而吃力地)爬，攀登;争抢;抢占;', 'Tourists were scrambling over the rocks looking for the perfect camera angle...', '游客们在正爬上岩石寻找最佳的拍摄角度。');
INSERT INTO `words` VALUES (7679, 'scrap', 'skræp', 'n.', '碎片，小块(纸、织物等);丝毫;一丁点;', 'A crumpled scrap of paper was found in her handbag...', '在她的手提包里发现了一张皱巴巴的小纸片。');
INSERT INTO `words` VALUES (7680, 'scrape', 'skreɪp', 'v.', '刮掉;削去;擦坏;', 'She went round the car scraping the frost off the windows...', '她把结霜的车窗挨个儿擦干净。');
INSERT INTO `words` VALUES (7681, 'scraping', 'ˈskreɪpɪŋ', 'n.', '刮屑;削片;', 'She went round the car scraping the frost off the windows...', '她把结霜的车窗挨个儿擦干净。');
INSERT INTO `words` VALUES (7682, 'scrappy', 'ˈskræpi', 'adj.', '散乱的;不连贯的;支离破碎的;', '...a scrappy affair...', '计划不周的事件');
INSERT INTO `words` VALUES (7683, 'scratch', 'skrætʃ', 'v.', '挠，搔(痒处);划破，抓破，划伤，抓伤(皮肤);(尤指意外地)擦破，划损，刮坏;', 'He scratched himself under his arm...', '他在胳膊下面挠了挠。');
INSERT INTO `words` VALUES (7684, 'scrawl', 'skrɔːl', 'v.', '马马虎虎(或潦草)地写;', 'He scrawled a hasty note to his wife...', '他给妻子草草写了个便条。');
INSERT INTO `words` VALUES (7685, 'scream', 'skriːm', 'v.', '(因伤痛、害怕、激动等)尖叫;(向某人或为某事)高声喊，大声叫;发出大而尖的声音;', 'Women were screaming; some of the houses nearest the bridge were on fire...', '女人们在尖叫;离桥最近的几所房屋起火了。');
INSERT INTO `words` VALUES (7686, 'screaming', 'ˈskriːmɪŋ', 'v.', '(因伤痛、害怕、激动等)尖叫;(向某人或为某事)高声喊，大声叫;发出大而尖的声音;', 'Women were screaming; some of the houses nearest the bridge were on fire...', '女人们在尖叫;离桥最近的几所房屋起火了。');
INSERT INTO `words` VALUES (7687, 'scree', 'skriː', 'n.', '碎石坡;岩屑堆;', 'Occasionally scree fell in a shower of dust and noise...', '偶尔会有碎石滚下，扬起大团尘土，噼啪作响。');
INSERT INTO `words` VALUES (7688, 'screech', 'skriːtʃ', 'v.', '尖叫;发出尖锐刺耳的声音;尖声地说;', 'A black Mercedes screeched to a halt beside the helicopter...', '一辆黑色的梅赛德斯轿车在直升机旁嘎的一声刹住了。');
INSERT INTO `words` VALUES (7689, 'screw', 'skruː', 'n.', '螺丝钉;螺丝;(对螺丝的)旋拧;', 'Each bracket is fixed to the wall with just three screws.', '每个托架只用了 3 颗螺钉固定在墙上。');
INSERT INTO `words` VALUES (7690, 'scribble', 'ˈskrɪbl', 'v.', '草草记下，匆匆书写(尤指因时间仓促);胡写;乱画;', 'She scribbled a note to tell Mum she\'d gone out...', '她匆匆写了个便条告诉妈妈她已外出。');
INSERT INTO `words` VALUES (7691, 'script', 'skrɪpt', 'n.', '剧本;电影剧本;广播(或讲话等)稿;', 'Jenny\'s writing a film script.', '珍妮在写一个电影剧本。');
INSERT INTO `words` VALUES (7692, 'scripture', 'ˈskrɪptʃə(r)', 'n.', '《圣经》;(某宗教的)圣典，经文，经典;', '...a quote from scripture.', '引自经文的语句');
INSERT INTO `words` VALUES (7693, 'scroll', 'skrəʊl', 'n.', '(供书写的)长卷纸，卷轴;(石刻或木雕的)涡卷形装饰;', 'Ancient scrolls were found in caves by the Dead Sea.', '死海旁边的山洞里发现了古代的卷轴。');
INSERT INTO `words` VALUES (7694, 'scrolling', 'ˈskrəʊlɪŋ', 'v.', '滚屏;滚动;', 'Ancient scrolls were found in caves by the Dead Sea.', '死海旁边的山洞里发现了古代的卷轴。');
INSERT INTO `words` VALUES (7695, 'scrounge', 'skraʊndʒ', 'v.', '白要;白拿;', 'Williams had to scrounge enough money to get his car out of the car park...', '威廉斯不得不讨来足够的钱把车开出停车场。');
INSERT INTO `words` VALUES (7696, 'scrub', 'skrʌb', 'v.', '擦洗;刷洗;取消(原有安排);', 'Surgeons began to scrub their hands and arms with soap and water before operating...', '外科医生们在做手术前开始用肥皂和水清洗双手和胳膊。');
INSERT INTO `words` VALUES (7697, 'scruffy', 'ˈskrʌfi', 'adj.', '不整洁的;邋遢的;', '...a young man, pale, scruffy and unshaven.', '脸色苍白、邋邋遢遢、胡子拉碴的年轻人');
INSERT INTO `words` VALUES (7698, 'scrumptious', 'ˈskrʌmpʃəs', 'adj.', '美味的;非常好吃的;', '...a scrumptious apple pie.', '美味可口的苹果派');
INSERT INTO `words` VALUES (7699, 'scrunch', 'skrʌntʃ', 'v.', '发咔嚓咔嚓声;发出嘎吱声;把…揉成一团;', 'The sand on the floor scrunched under our feet...', '地板上的沙子在我们脚下嘎吱作响。');
INSERT INTO `words` VALUES (7700, 'scruple', 'ˈskruːpl', 'n.', '(道德上的)顾忌，顾虑;', '...a man with no moral scruples.', '道德败坏的人');
INSERT INTO `words` VALUES (7701, 'scrupulous', 'ˈskruːpjələs', 'adj.', '仔细的;细致的;一丝不苟的;', 'You\'re being very scrupulous, but to what end?...', '你确实刚正不阿，但图了什么呢？');
INSERT INTO `words` VALUES (7702, 'scrutiny', 'ˈskruːtəni', 'n.', '仔细检查;认真彻底的审查;', 'His private life came under media scrutiny...', '他的私生活受到媒体的密切关注。');
INSERT INTO `words` VALUES (7703, 'scud', 'skʌd', 'v.', '飞掠;疾飞;', '...heavy, rain-laden clouds scudding across from the south-west.', '乌黑的雨云从西南方向掠过');
INSERT INTO `words` VALUES (7704, 'scuff', 'skʌf', 'v.', '磨损;磨坏;拖着脚走;', 'Constant wheelchair use will scuff almost any floor surface...', '任何地板上经常有轮椅走动几乎都会有所磨损。');
INSERT INTO `words` VALUES (7705, 'scuffle', 'ˈskʌfl', 'n.', '(短时间与某人的)小扭打，冲突;', 'Violent scuffles broke out between rival groups demonstrating for and against independence.', '支持和反对独立的两支游行队伍间爆发了激烈的冲突。');
INSERT INTO `words` VALUES (7706, 'sculpt', 'skʌlpt', 'v.', '雕刻;雕塑;使具有某种形状;', 'An artist sculpted a full-size replica of her head...', '一位艺术家按实际大小雕刻了一个她的头部模型。');
INSERT INTO `words` VALUES (7707, 'sculpture', 'ˈskʌlptʃə(r)', 'n.', '雕像;雕塑品;雕刻品;', '...stone sculptures of figures and animals.', '人像和动物的石雕');
INSERT INTO `words` VALUES (7708, 'scurrilous', 'ˈskʌrələs', 'adj.', '恶语毁谤的;用污言秽语谩骂的;辱骂的;', 'Scurrilous and untrue stories were being invented.', '有人正在捏造虚假诽谤的故事。');
INSERT INTO `words` VALUES (7709, 'scurry', 'ˈskʌri', 'v.', '碎步疾跑;', 'The attack began, sending residents scurrying for cover...', '袭击发动后，居民们纷纷奔逃躲避。');
INSERT INTO `words` VALUES (7710, 'scuttle', 'ˈskʌtl', 'v.', '碎步疾跑;(故意)破坏，阻止，阻挠;凿沉(船);', 'Two very small children scuttled away in front of them...', '两个小毛孩在他们前面屁颠屁颠地跑开了。');
INSERT INTO `words` VALUES (7711, 'sea', 'siː', 'n.', '海;海洋;海面情况;', 'Most of the kids have never seen the sea...', '多数孩子从未见过海。');
INSERT INTO `words` VALUES (7712, 'seaboard', 'ˈsiːbɔːd', 'n.', '沿海地区;海滨;', '...the Eastern seaboard of the USA.', '美国的东海岸');
INSERT INTO `words` VALUES (7713, 'seafaring', 'ˈsiːfeərɪŋ', 'adj.', '海上劳作(或航行)的;', 'The Lebanese were a seafaring people.', '黎巴嫩人曾是一个航海民族。');
INSERT INTO `words` VALUES (7714, 'seafood', 'ˈsiːfuːd', 'n.', '海味，海鲜(尤指甲壳类);', '...a seafood restaurant.', '海鲜餐馆');
INSERT INTO `words` VALUES (7715, 'seaman', 'ˈsiːmən', 'n.', '水兵;水手;海员;', 'The men emigrate to work as seamen.', '男人们移居国外当海员。');
INSERT INTO `words` VALUES (7716, 'seamanship', 'ˈsiːmənʃɪp', 'n.', '航海术;船舶驾驶术;', '...the art of seamanship and navigation.', '驾船和航海的技术');
INSERT INTO `words` VALUES (7717, 'seamless', 'ˈsiːmləs', 'adj.', '无(接)缝的;(两部分之间)无空隙的，不停顿的;', 'It was a seamless procession of wonderful electronic music.', '这是一曲优美流畅的电子乐曲。');
INSERT INTO `words` VALUES (7718, 'seamy', 'ˈsiːmi', 'adj.', '污秽的;肮脏丑恶的;', '...Hamburg\'s seamy St Pauli\'s district.', '汉堡肮脏污秽的圣保利街区');
INSERT INTO `words` VALUES (7719, 'seaport', 'ˈsiːpɔːt', 'n.', '海港城市;', '...the Baltic seaport of Rostock.', '波罗的海沿岸的海港城市罗斯托克');
INSERT INTO `words` VALUES (7720, 'sear', 'sɪə(r)', 'v.', '烧灼;灼伤;烤焦;', 'Grass fires have seared the land near the farming village of Basekhai.', '草地野火烧焦了巴斯克亥农庄附近的土地。');
INSERT INTO `words` VALUES (7721, 'search', 'sɜːtʃ', 'n.', '搜索;搜寻;搜查;', 'The Turkish security forces have started searching for the missing men...', '土耳其安全部队已经开始搜寻失踪人员。');
INSERT INTO `words` VALUES (7722, 'searching', 'ˈsɜːtʃɪŋ', 'adj.', '深挖细究的;认真彻底的;', 'They asked her some searching questions on moral philosophy and logic.', '他们深入询问了她一些有关伦理学和逻辑学的问题。');
INSERT INTO `words` VALUES (7723, 'searing', 'ˈsɪərɪŋ', 'adj.', '灼人的;火辣辣的;猛烈批评的;', 'She woke to feel a searing pain in her feet.', '她醒来感到脚上火辣辣地痛。');
INSERT INTO `words` VALUES (7724, 'seashore', 'ˈsiːʃɔː(r)', 'n.', '海岸;海滨;', 'She takes her inspiration from shells and stones she finds on the seashore.', '她从在海滩上捡到的贝壳和石子中汲取灵感。');
INSERT INTO `words` VALUES (7725, 'seaside', 'ˈsiːsaɪd', 'n.', '(尤指人们游玩、度假的)海边，海滨;', 'I went to spend a few days at the seaside...', '我去海滨玩了几天。');
INSERT INTO `words` VALUES (7726, 'season', 'ˈsiːzn', 'n.', '季;季节;(热带地区的)旱 / 雨季;', 'Autumn\'s my favourite season.', '秋天是我最喜欢的季节。');
INSERT INTO `words` VALUES (7727, 'seasonal', 'ˈsiːzənl', 'adj.', '季节性的;随季节变化的;节令性的;', '...blaming seasonal factors for the unemployment increase in the west...', '把西部失业率的上升归咎于季节性因素');
INSERT INTO `words` VALUES (7728, 'seasoned', 'ˈsiːznd', 'adj.', '富有经验的;老于此道的;调好味的;', 'The author is a seasoned academic...', '作者是一位经验丰富的学者。');
INSERT INTO `words` VALUES (7729, 'seasoning', 'ˈsiːznɪŋ , ˈsiːzənɪŋ', 'n.', '调味品;作料;', 'Mix the meat with the onion, carrot, and some seasoning.', '把肉和洋葱、胡萝卜及一些佐料搅拌在一起。');
INSERT INTO `words` VALUES (7730, 'seasons', 'ˈsiːznz', 'n.', '季;季节;(热带地区的)旱 / 雨季;', 'Autumn\'s my favourite season.', '秋天是我最喜欢的季节。');
INSERT INTO `words` VALUES (7731, 'seat', 'siːt', 'n.', '座位，坐处(如椅子等);有…座位的;有…座的;', 'Stephen returned to his seat...', '斯蒂芬回到自己的座位上。');
INSERT INTO `words` VALUES (7732, 'seated', 'ˈsiːtɪd', 'v.', '向…提供座位;(使)就座;坐;', 'Stephen returned to his seat...', '斯蒂芬回到自己的座位上。');
INSERT INTO `words` VALUES (7733, 'seating', 'ˈsiːtɪŋ', 'n.', '可坐的地方;座位;', 'The stadium has been fitted with seating for over eighty thousand spectators.', '这个体育场配备了8万多个观众座席。');
INSERT INTO `words` VALUES (7734, 'seaweed', 'ˈsiːwiːd', 'n.', '海草;海藻;', '...seaweed washed up on a beach.', '被冲到海滩上的海草');
INSERT INTO `words` VALUES (7735, 'sec', 'sek', 'n.', '片刻;霎时;', 'Can you just hang on a sec?...', '您能稍等一会儿吗？');
INSERT INTO `words` VALUES (7736, 'secede', 'sɪˈsiːd', 'v.', '退出，脱离(组织等);', 'Singapore seceded from the Federation of Malaysia and became an independent sovereign state...', '新加坡脱离马来西亚联邦成为一个独立的主权国家。');
INSERT INTO `words` VALUES (7737, 'secession', 'sɪˈseʃn', 'n.', '(地区或集团从所属的国家或上级集团的)退出，脱离;', '...the Ukraine\'s secession from the Soviet Union.', '乌克兰之退出苏联');
INSERT INTO `words` VALUES (7738, 'secluded', 'sɪˈkluːdɪd', 'adj.', '僻静的;清静的;不受打扰的;', 'We were tucked away in a secluded corner of the room...', '我们被藏匿到房间里一个隐蔽的角落。');
INSERT INTO `words` VALUES (7739, 'seclusion', 'sɪˈkluːʒn', 'n.', '清静;隐居;与世隔绝;', 'She lived in seclusion with her husband on their farm in Panama...', '她与丈夫隐居在他们在巴拿马的农场。');
INSERT INTO `words` VALUES (7740, 'secondary', 'ˈsekəndri', 'adj.', '次要的;从属的;辅助的;', 'The street erupted in a huge explosion, with secondary explosions in the adjoining buildings...', '街上发生了剧烈爆炸，邻近的建筑里也发生了程度较轻的爆炸。');
INSERT INTO `words` VALUES (7741, 'second-guess', 'ˌsekənd ˈɡes', 'v.', '猜测;预言;事后批评(或品评);', 'Editors and contributors are trying to second-guess the future.', '编辑们和撰稿者们在试图预测未来。');
INSERT INTO `words` VALUES (7742, 'secondhand', '\'sekənd\'hænd', 'adj.', '二手货的;旧的;间接的;', 'Buying a second-hand car can be a risky business.', '买二手车可能有风险。');
INSERT INTO `words` VALUES (7743, 'secondly', 'ˈsekəndli', 'adv.', '(用于列举)第二，其次;', 'You need, firstly, a strong independent board of directors and secondly, an experienced and dedicated staff.', '你首先需要一个强大的独立董事会，其次需要一批富有经验并且爱岗敬业的员工。');
INSERT INTO `words` VALUES (7744, 'second-rate', 'ˌsekənd ˈreɪt', 'adj.', '二流的;平庸的;普通的;', '...second-rate restaurants.', '二流餐馆');
INSERT INTO `words` VALUES (7745, 'secrecy', 'ˈsiːkrəsi', 'n.', '保密;秘密;', 'The British government has thrown a blanket of secrecy over the details...', '英国政府对细节讳莫如深。');
INSERT INTO `words` VALUES (7746, 'secret', 'ˈsiːkrət', 'adj.', '秘密的;保密的;外人不得而知的;', 'Soldiers have been training at a secret location...', '士兵们一直在一个秘密场所训练。');
INSERT INTO `words` VALUES (7747, 'secretarial', 'ˌsekrəˈteəriəl', 'adj.', '秘书的;文秘工作的;', 'I was doing temporary secretarial work...', '我在做临时的秘书工作。');
INSERT INTO `words` VALUES (7748, 'secretariat', 'ˌsekrəˈteəriət', 'n.', '(大型国际组织、政治组织的)秘书处，书记处;', '...the UN secretariat.', '联合国秘书处');
INSERT INTO `words` VALUES (7749, 'secrete', 'sɪˈkriːt', 'v.', '分泌;隐藏，藏匿(小物件);', 'The sweat glands secrete water.', '汗腺分泌汗液。');
INSERT INTO `words` VALUES (7750, 'secretion', 'sɪˈkriːʃn', 'n.', '分泌;分泌物;', '...the secretion of adrenaline.', '肾上腺素的分泌');
INSERT INTO `words` VALUES (7751, 'secretive', 'ˈsiːkrətɪv', 'adj.', '(思想、情感等)不外露的;惯于掩藏自己的;有城府的;', 'Billionaires are usually fairly secretive about the exact amount that they\'re worth.', '亿万富翁通常对自己身家的确切数目讳莫如深。');
INSERT INTO `words` VALUES (7752, 'secretly', 'ˈsiːkrətli', 'adv.', '秘密地;暗暗;暗自;', 'Soldiers have been training at a secret location...', '士兵们一直在一个秘密场所训练。');
INSERT INTO `words` VALUES (7753, 'sectarian', 'sekˈteəriən', 'adj.', '(宗教)教派的，派性的;', 'He was the fifth person to be killed in sectarian violence...', '他是宗派暴力中的第 5 个罹难者。');
INSERT INTO `words` VALUES (7754, 'section', 'ˈsekʃn', 'n.', '部分;部门;部件;', 'He said it was wrong to single out any section of society for Aids testing...', '他说把社会中的任何一部分人抽选出来做艾滋病检查都是不对的。');
INSERT INTO `words` VALUES (7755, 'sectional', 'ˈsekʃənl', 'adj.', '(社团或组织中)某群体的，某阶层的;组合式的;剖面的;', 'He criticized the selfish attitude of certain sectional interests.', '他批评了某些利益团体自私自利的态度。');
INSERT INTO `words` VALUES (7756, 'sections', 'ˈsekʃnz', 'n.', '部分;部门;部件;', 'He said it was wrong to single out any section of society for Aids testing...', '他说把社会中的任何一部分人抽选出来做艾滋病检查都是不对的。');
INSERT INTO `words` VALUES (7757, 'sector', 'ˈsektə(r)', 'n.', '(尤指一国经济的)部门，领域，行业;(尤指军事管制的)区域，地带;扇形;', '...the nation\'s manufacturing sector.', '该国的制造业');
INSERT INTO `words` VALUES (7758, 'secular', 'ˈsekjələ(r)', 'adj.', '现世的;世俗的;非宗教的;', 'He spoke about preserving the country as a secular state.', '他谈及了保持该国世俗化的问题。');
INSERT INTO `words` VALUES (7759, 'secure', 'sɪˈkjʊə(r)', 'adj.', '安心的;有把握的;可靠的;', 'Federal leaders continued their efforts to secure a ceasefire...', '联邦领导人继续争取促成停火协议。');
INSERT INTO `words` VALUES (7760, 'securely', 'sɪ\'kjʊəlɪ', 'adv.', '安心地;', 'Federal leaders continued their efforts to secure a ceasefire...', '联邦领导人继续争取促成停火协议。');
INSERT INTO `words` VALUES (7761, 'securities', 'sɪˈkjʊərɪtiz', 'n.', '保护措施;安全工作;保卫部门;', 'They are now under a great deal of pressure to tighten their airport security...', '他们现在承受着加强机场安保措施的巨大压力。');
INSERT INTO `words` VALUES (7762, 'security', 'sɪˈkjʊərəti', 'n.', '保护措施;安全工作;保卫部门;', 'They are now under a great deal of pressure to tighten their airport security...', '他们现在承受着加强机场安保措施的巨大压力。');
INSERT INTO `words` VALUES (7763, 'sedate', 'sɪˈdeɪt', 'adj.', '镇定的;泰然的;不慌不忙的;', 'She took them to visit her sedate, elderly cousins...', '她带他们去拜访她那些不苟言笑的表兄表姐们。');
INSERT INTO `words` VALUES (7764, 'sedative', 'ˈsedətɪv', 'n.', '镇静剂;', 'They use opium as a sedative, rather than as a narcotic.', '他们把鸦片用作镇静剂而不是毒品。');
INSERT INTO `words` VALUES (7765, 'sedentary', 'ˈsedntri', 'adj.', '需要久坐的;惯于久坐不动的;定居的;', 'Obesity and a sedentary lifestyle has been linked with an increased risk of heart disease.', '肥胖和久坐不动的生活方式被认为会增加患心脏病的几率。');
INSERT INTO `words` VALUES (7766, 'sediment', 'ˈsedɪmənt', 'n.', '沉淀物;沉积物;', 'Many organisms that die in the sea are soon buried by sediment.', '海洋里死亡的许多生物会很快被沉淀物掩埋。');
INSERT INTO `words` VALUES (7767, 'sedition', 'sɪˈdɪʃn', 'n.', '煽动叛乱的言论(或行动);', 'Government officials charged him with sedition.', '政府官员指控他煽动人们造反。');
INSERT INTO `words` VALUES (7768, 'seditious', 'sɪˈdɪʃəs', 'adj.', '煽动叛乱的;煽动反政府的;反动性的;', 'He fell under suspicion for distributing seditious pamphlets.', '他因散发反政府传单而遭到怀疑。');
INSERT INTO `words` VALUES (7769, 'seduce', 'sɪˈdjuːs', 'v.', '诱奸;诱骗;唆使;', 'The view of lake and plunging cliffs seduces visitors...', '湖光旖旎、峭壁峥嵘，游客们被深深吸引了。');
INSERT INTO `words` VALUES (7770, 'seduction', 'sɪˈdʌkʃn', 'n.', '诱奸;诱惑力;魅力;', 'The view of lake and plunging cliffs seduces visitors...', '湖光旖旎、峭壁峥嵘，游客们被深深吸引了。');
INSERT INTO `words` VALUES (7771, 'seductive', 'sɪˈdʌktɪv', 'adj.', '诱人的;迷人的;有魅力的;', 'It\'s a seductive argument.', '这是一个非常吸引人的论点。');
INSERT INTO `words` VALUES (7772, 'see', 'siː', 'v.', '看见;见到;看出;', 'You can\'t see colours at night...', '晚上看不见颜色。');
INSERT INTO `words` VALUES (7773, 'seed', 'siːd', 'n.', '种子;籽;起源;', '...a packet of cabbage seed...', '一包卷心菜籽');
INSERT INTO `words` VALUES (7774, 'seedy', 'ˈsiːdi', 'adj.', '肮脏的;污七八糟的;乌烟瘴气的;', 'Frank ran dodgy errands for a seedy local villain...', '弗兰克为当地一个臭名远扬的恶棍跑腿，干些骗人勾当。');
INSERT INTO `words` VALUES (7775, 'seeing', 'ˈsiːɪŋ', 'v.', '看见;见到;看出;', 'You can\'t see colours at night...', '晚上看不见颜色。');
INSERT INTO `words` VALUES (7776, 'seek', 'siːk', 'v.', '寻找;寻求;谋求;', 'They have had to seek work as labourers...', '他们只好找体力活儿干。');
INSERT INTO `words` VALUES (7777, 'seeker', 'ˈsiːkə(r)', 'n.', '寻找者;寻求者;追求者;', 'I am a seeker after truth...', '我追求真理。');
INSERT INTO `words` VALUES (7778, 'seem', 'siːm', 'v.', '(涉及推断)好像，似乎，看来;(用以缓和语气)感到好像，觉得似乎;(表示不确切或客气)看来好像;', 'We heard a series of explosions. They seemed quite close by...', '我们听到一连串的爆炸声，似乎就在附近。');
INSERT INTO `words` VALUES (7779, 'seeming', 'ˈsiːmɪŋ', 'adj.', '看似…(而实际未必)的;表面上的;貌似…的;', 'Wall Street analysts have been highly critical of the company\'s seeming inability to control costs...', '华尔街的分析家们对该公司似乎无力控制成本一事一直都严厉指责。');
INSERT INTO `words` VALUES (7780, 'seemingly', 'ˈsiːmɪŋli', 'adv.', '看似;貌似;表面上;', 'A seemingly endless line of trucks waits in vain to load up.', '一眼望不到头的一长列卡车眼巴巴地等着装货。');
INSERT INTO `words` VALUES (7781, 'seemly', 'ˈsiːmli', 'adj.', '合适的;得体的;合乎礼仪的;', 'Self-assertion was not thought seemly in a woman.', '人们认为女性不应该过于自信。');
INSERT INTO `words` VALUES (7782, 'seep', 'siːp', 'v.', '渗;渗透;', 'Radioactive water had seeped into underground reservoirs...', '放射性水已经渗入地下蓄水层中。');
INSERT INTO `words` VALUES (7783, 'seepage', 'ˈsiːpɪdʒ', 'n.', '渗;渗透;渗液;', 'Chemical seepage has caused untold damage.', '化学品泄漏已造成不可估量的损失。');
INSERT INTO `words` VALUES (7784, 'seesaw', 'ˈsiː sɔː , ˈsiːsɔː', 'adv.', '上下动地[的];前后动地[的];交互地[的];', 'There was a sandpit, a seesaw and a swing in the playground.', '游乐场上有一处沙坑、一块跷跷板和一个秋千架。');
INSERT INTO `words` VALUES (7785, 'seethe', 'siːð', 'v.', '强压怒火;生闷气;充满，遍布，到处都是(人、动物);', 'She took it calmly at first but under the surface was seething...', '她起初平静地接受了，但内心却气呼呼的。');
INSERT INTO `words` VALUES (7786, 'segmented', 'seɡˈmentɪd', 'adj.', '分割的;切断的;划分的;', '...segmented oranges.', '切成瓣的橙子');
INSERT INTO `words` VALUES (7787, 'segregate', 'ˈseɡrɪɡeɪt', 'v.', '隔离并区别对待(不同种族、宗教或性别的人);(使)分开，分离，隔离;', 'Police segregated the two rival camps of protesters...', '警察把两个敌对阵营的抗议人群隔离开来。');
INSERT INTO `words` VALUES (7788, 'segregation', 'ˌseɡrɪˈɡeɪʃn', 'n.', '(对不同种族、宗教或性别的人所采取的)隔离并区别对待，隔离政策;隔离(或分离)措施;', 'The Supreme Court unanimously ruled that racial segregation in schools was unconstitutional...', '联邦最高法院一致裁定学校实行的种族隔离措施违反宪法。');
INSERT INTO `words` VALUES (7789, 'seismic', 'ˈsaɪzmɪk', 'adj.', '地震的;地震引起的;影响深远的;', 'Earthquakes produce two types of seismic waves...', '地震产生两种地震波。');
INSERT INTO `words` VALUES (7790, 'seize', 'siːz', 'v.', '抓住;捉住;夺;', '\'Leigh,\' he said seizing my arm to hold me back.', '“莉，”他说着一把抓住我的胳膊把我拉了回来。');
INSERT INTO `words` VALUES (7791, 'seizure', 'ˈsiːʒə(r)', 'n.', '起获;没收;充公;', '...a mild cardiac seizure...', '轻度的心脏病发作');
INSERT INTO `words` VALUES (7792, 'seldom', 'ˈseldəm', 'adv.', '不常;很少;难得;', 'They seldom speak...', '他们很少发言。');
INSERT INTO `words` VALUES (7793, 'select', 'sɪˈlekt', 'v.', '选择;挑选;选拔;', 'Voters are selecting candidates for both US Senate seats and for 52 congressional seats...', '选民正在为美国参议院议席和 52 个众议院议席挑选候选人。');
INSERT INTO `words` VALUES (7794, 'selected', 'sɪˈlektɪd', 'v.', '选择;挑选;选拔;', 'Voters are selecting candidates for both US Senate seats and for 52 congressional seats...', '选民正在为美国参议院议席和 52 个众议院议席挑选候选人。');
INSERT INTO `words` VALUES (7795, 'selection', 'sɪˈlekʃn', 'n.', '选择;挑选;选拔;', '...Darwin\'s principles of natural selection...', '达尔文的自然选择理论');
INSERT INTO `words` VALUES (7796, 'selective', 'sɪˈlektɪv', 'adj.', '选择性的;有选择的;认真挑选的;', 'Selective breeding may result in a greyhound running faster and seeing better than a wolf.', '选择性交配繁殖有可能培育出比狼速度更快、视力更佳的灵犭是。');
INSERT INTO `words` VALUES (7797, 'selectively', 'sɪ\'lektɪvli', 'adv.', '选择地;', 'Selective breeding may result in a greyhound running faster and seeing better than a wolf.', '选择性交配繁殖有可能培育出比狼速度更快、视力更佳的灵犭是。');
INSERT INTO `words` VALUES (7798, 'selector', 'sɪˈlektə(r)', 'n.', '(运动队队员的)选拔人;选择器;转换器;', '...the England cricket selectors.', '英格兰板球队的选拔委员');
INSERT INTO `words` VALUES (7799, 'self', 'self', 'n.', '(自己的)通常的行为方式，本来面目，惯常心态;个性;自我;', 'You\'re looking more like your usual self...', '你现在看上去较像你平时的样子了。');
INSERT INTO `words` VALUES (7800, 'self-assertion', 'ˌself əˈsɜːʃn', 'n.', '主见;自我主张;', '...her silence and lack of self-assertion...', '她的沉默和缺乏主见');
INSERT INTO `words` VALUES (7801, 'self-confidence', 'ˌself ˈkɒnfɪdəns', 'n.', '自信;', 'With the end of my love affair, I lost all the self-confidence I once had...', '恋爱告吹后，我一时间自信全无。');
INSERT INTO `words` VALUES (7802, 'self-conscious', 'ˌself ˈkɒnʃəs', 'adj.', '(因顾虑自己的外表或表现)局促不安的，害羞的，不自然的;刻意的;自己意识到的;', 'I felt a bit self-conscious in my swimming costume...', '我穿着游泳衣觉得有点害羞。');
INSERT INTO `words` VALUES (7803, 'self-contained', 'ˌself kənˈteɪnd', 'adj.', '(指人)独立的，自立的;(指事物)自给的，独立的;独门独户的;', 'He seems completely self-contained and he doesn\'t miss you when you\'re not there...', '他似乎十分独立，你不在的时候也并不想念你。');
INSERT INTO `words` VALUES (7804, 'self-control', 'ˌself kənˈtrəʊl', 'n.', '自制力;自我控制;', 'His self-control, reserve and aloofness were almost inhuman...', '他自制、矜持和冷漠得几乎有些不近人情。');
INSERT INTO `words` VALUES (7805, 'self-discipline', 'ˌself ˈdɪsəplɪn', 'n.', '自律能力;自我约束能力;', 'Exercising at home alone requires a tremendous amount of self-discipline.', '自己在家锻炼需要很强的自我约束能力。');
INSERT INTO `words` VALUES (7806, 'self-esteem', 'ˌself ɪˈstiːm', 'n.', '自尊(心);', 'Poor self-esteem is at the centre of many of the difficulties we experience in our relationships.', '缺乏自尊心是我们之间关系所遭遇的重重困难中的核心问题。');
INSERT INTO `words` VALUES (7807, 'self-evident', 'ˌself ˈevɪdənt', 'adj.', '显而易见的;不言而喻的;明摆着的;', 'It is self-evident that we will never have enough resources to meet the demand...', '不言而喻，我们永远都不会有足够的资源来满足需求。');
INSERT INTO `words` VALUES (7808, 'self-explanatory', 'ˌself ɪkˈsplænətri', 'adj.', '无须解释的;明白易晓的;一目了然的;', 'I hope the graphs on the following pages are self-explanatory.', '我希望后面几页的图表能一目了然。');
INSERT INTO `words` VALUES (7809, 'self-expression', 'ˌself ɪkˈspreʃn', 'n.', '自我表现，自我表达(尤指通过写作、绘画、舞蹈等活动);', 'The conflict between the urge for self-expression and the pressure to conform is a central one for her...', '想要表现自我的欲望和被迫从众的压力对她来说是一个最主要的冲突。');
INSERT INTO `words` VALUES (7810, 'self-fulfilling', 'ˌself fʊlˈfɪlɪŋ', 'adj.', '(预言等)自我应验的，自我实现的;', 'Fear of failure can become a self-fulfilling prophecy.', '害怕失败可能会真的导致失败。');
INSERT INTO `words` VALUES (7811, 'self-governing', 'ˌself ˈɡʌvənɪŋ', 'adj.', '(地区、组织)自治的，自己管理自己的;', '...a self-governing province...', '自治省');
INSERT INTO `words` VALUES (7812, 'self-help', 'ˌself ˈhelp', 'n.', '自助;自立;', 'She helped her Mum set up a self-help group for parents with overweight children.', '她帮助母亲建起了一个互助会，帮助那些有肥胖孩子的家长。');
INSERT INTO `words` VALUES (7813, 'self-image', 'ˌself ˈɪmɪdʒ', 'n.', '自我形象;自我印象;', 'Children who have a positive self-image are less likely to present behaviour and discipline problems.', '自我感觉良好的孩子较少出现行为和纪律上的问题。');
INSERT INTO `words` VALUES (7814, 'selfish', 'ˈselfɪʃ', 'adj.', '自私的;', 'I think I\'ve been very selfish. I\'ve been mainly concerned with myself.', '我觉得我一直非常自私。总是只关心自己。');
INSERT INTO `words` VALUES (7815, 'selfishness', '\'selfɪʃ(ə)nəs', 'adj.', '自私的;', 'I think I\'ve been very selfish. I\'ve been mainly concerned with myself.', '我觉得我一直非常自私。总是只关心自己。');
INSERT INTO `words` VALUES (7816, 'self-knowledge', 'ˌself ˈnɒlɪdʒ', 'n.', '自我了解;自知之明;', 'The more self-knowledge we have, the more control we can exert over our feelings and behaviour.', '我们越有自知之明就越能控制自己的感情和行为。');
INSERT INTO `words` VALUES (7817, 'selfless', 'ˈselfləs', 'adj.', '无私的;', 'She was a wonderful companion and her generosity to me was entirely selfless...', '她是一个极好的伴侣，她对我慷慨大方，一心只为我着想。');
INSERT INTO `words` VALUES (7818, 'self-pity', 'ˌself ˈpɪti', 'n.', '自怜;', 'I was unable to shake off my self-pity...', '我无法摆脱自怜自哀的情绪。');
INSERT INTO `words` VALUES (7819, 'self-possession', 'ˌself pəˈzeʃn', 'n.', '沉着;镇静;泰然自若;', 'She found her customary self-possession had deserted her.', '她发现自己不像平时那样处事不惊了。');
INSERT INTO `words` VALUES (7820, 'self-reliance', 'ˌself rɪˈlaɪəns', 'n.', '自主;自立;自力更生;', 'People learned self-reliance because they had to...', '人们被迫学会了自力更生。');
INSERT INTO `words` VALUES (7821, 'self-respect', 'ˌself rɪˈspekt', 'n.', '自尊(心);', 'They have lost not only their jobs, but their homes, their self-respect and even their reason for living.', '他们不仅丢掉了工作，还失去了居所、自尊乃至生存的理由。');
INSERT INTO `words` VALUES (7822, 'self-rule', 'ˌself ˈruːl', 'n.', '自治;', 'The agreement gives the territory limited self-rule.', '这个协议规定该地区享有有限的自治权。');
INSERT INTO `words` VALUES (7823, 'self-sacrifice', 'ˌself ˈsækrɪfaɪs', 'n.', '自我牺牲;', 'I thanked my parents for all their self-sacrifice on my behalf.', '我感谢父母为我做出的所有自我牺牲。');
INSERT INTO `words` VALUES (7824, 'selfsame', 'ˈselfseɪm', 'adj.', '(强调两者完全相同)同一的;', 'You find yourself worshipped by the self-same people who beat you up at school.', '你发现原来在学校里痛扁你的同一群人现在成了你的崇拜者。');
INSERT INTO `words` VALUES (7825, 'self-satisfaction', 'ˌself sætɪsˈfækʃn', 'n.', '自鸣得意;沾沾自喜;', 'He tried hard not to smile in smug self-satisfaction.', '他强忍住沾沾自喜的笑容。');
INSERT INTO `words` VALUES (7826, 'self-serving', 'ˌself ˈsɜːvɪŋ', 'adj.', '只为个人打算的;一心谋私利的;', 'They suggest that my motives for proposing reform are self-serving and mercenary.', '他们暗示说我之所以提议改革是贪图金钱，想谋取私利。');
INSERT INTO `words` VALUES (7827, 'self-sufficient', 'ˌself səˈfɪʃnt', 'adj.', '自给自足的;自立的;', 'This enabled the country to become self-sufficient in sugar...', '这使得该国的食糖能自给自足。');
INSERT INTO `words` VALUES (7828, 'self-sustaining', 'ˌself səˈsteɪnɪŋ', 'adj.', '(过程、体制)自我运行的，自我维持的;', 'Asia\'s emerging economies will be on a self-sustaining cycle of growth...', '亚洲的新兴经济体会出现自我维持的循环增长。');
INSERT INTO `words` VALUES (7829, 'self-taught', 'ˌself ˈtɔːt', 'adj.', '自学的;自修的;', 'Paul is self-taught and became interested in photography just four years ago.', '保罗是自学的，他四年前才开始对摄影产生兴趣。');
INSERT INTO `words` VALUES (7830, 'sell', 'sel', 'v.', '出让;转让;出售;', 'I sold everything I owned except for my car and my books...', '我把我所有的东西都卖掉了，只剩下汽车和书。');
INSERT INTO `words` VALUES (7831, 'seller', 'ˈselə(r)', 'n.', '卖者;销售者;卖方;', '...a flower seller.', '卖花人');
INSERT INTO `words` VALUES (7832, 'semantic', 'sɪˈmæntɪk', 'adj.', '语义的;', 'He did not want to enter into a semantic debate.', '他不想就语义进行争辩。');
INSERT INTO `words` VALUES (7833, 'semblance', 'ˈsembləns', 'n.', '表象;假象;外观;', 'At least a semblance of normality has been restored to parts of the country...', '该国的部分地区至少已经恢复了表面的常态。');
INSERT INTO `words` VALUES (7834, 'semi-', 'ˈsemi', 'pref.', '半;部分;', 'He found Isabel\'s room in semi-darkness.', '他觉得伊莎贝尔的房间光线有些昏暗。');
INSERT INTO `words` VALUES (7835, 'semicircle', 'ˈsemisɜːkl', 'n.', '半圆;半圆弧线;半圆形;', 'They stood in a semi-circle round the teacher\'s chair and answered questions.', '他们围着老师的椅子站成半圆形回答问题。');
INSERT INTO `words` VALUES (7836, 'seminal', 'ˈsemɪnl', 'adj.', '(对以后的发展)影响深远的，有重大意义的;精液的;含精液的;', '...author of the seminal book \'Animal Liberation\'...', '有着巨大影响的《动物解放》一书的作者');
INSERT INTO `words` VALUES (7837, 'seminar', 'ˈsemɪnɑː(r)', 'n.', '(大学教师带领学生作专题讨论的)研讨课;研讨会;培训会;', '...a series of half-day seminars to help businessmen get the best value from investing in information technology.', '旨在帮助商界人士从投资信息技术中获得最大价值的一系列半日研讨会');
INSERT INTO `words` VALUES (7838, 'semitropical', '', 'adj.', '副热带的;亚热带的;(花草、树木)副热带的，亚热带的;', '...a semi-tropical island.', '一个亚热带的岛屿');
INSERT INTO `words` VALUES (7839, 'senate', 'ˈsenət', 'n.', '参议院(美国、澳大利亚、加拿大、法国等国家的两个立法机构之一;美国许多州议会也设有参议院);(某些国家的)大学理事会，大学评议会;', 'The Senate is expected to pass the bill shortly.', '预计参议院不久就会通过此议案。');
INSERT INTO `words` VALUES (7840, 'send', 'send', 'v.', '邮寄;发送;传达;', 'Myra Cunningham sent me a note thanking me for dinner...', '迈拉·坎宁安给我寄了张便笺感谢我招待的晚餐。');
INSERT INTO `words` VALUES (7841, 'sender', 'ˈsendə(r)', 'n.', '发送人;邮寄人;', 'The sender of the best letter every week will win a cheque for £20.', '每周寄来的作品中，最优秀者将赢得 20 英镑的支票。');
INSERT INTO `words` VALUES (7842, 'senior', 'ˈsiːniə(r)', 'adj.', '级别(或地位)高的;成人的;高级水平的;', '...senior officials in the Israeli government.', '以色列政府的高级官员');
INSERT INTO `words` VALUES (7843, 'seniority', 'ˌsiːniˈɒrəti', 'n.', '年长;级别高;资历;', 'He has said he will fire editorial employees without regard to seniority.', '他说过不管资历如何,任何编辑人员他都有可能解雇。');
INSERT INTO `words` VALUES (7844, 'sensation', 'senˈseɪʃn', 'n.', '感觉;知觉;感觉能力;', 'Floating can be a very pleasant sensation...', '漂浮会是一种非常惬意的感觉。');
INSERT INTO `words` VALUES (7845, 'sensational', 'senˈseɪʃənl', 'adj.', '轰动的;引起哗然的;哗众取宠的;', 'The world champions suffered a sensational defeat.', '世界冠军遭遇惨败，引起了一片哗然。');
INSERT INTO `words` VALUES (7846, 'sensationalism', 'senˈseɪʃənəlɪzəm', 'n.', '(指行文或报道)耸人听闻，哗众取宠;', 'The report criticises the newspaper for sensationalism.', '这篇报道批评了报纸哗众取宠的作风。');
INSERT INTO `words` VALUES (7847, 'sense', 'sens', 'n.', '感觉官能(即视、听、嗅、味、触五觉);(对重大事情的)感觉，意识;理解力;', 'She stared at him again, unable to believe the evidence of her senses.', '她再度盯着他看，无法相信自己的眼睛和耳朵。');
INSERT INTO `words` VALUES (7848, 'senseless', 'ˈsensləs', 'adj.', '无意义的;无目的的;失去知觉;', '...people whose lives have been destroyed by acts of senseless violence...', '生活被那些无谓的暴力行为所毁的人们');
INSERT INTO `words` VALUES (7849, 'sensibility', 'ˌsensəˈbɪləti', 'n.', '(尤指文艺方面的)感受能力，鉴赏力，敏感性;(尤指易受伤害或影响的)感情;', 'Everything he writes demonstrates the depth of his sensibility.', '他笔下的一切都显示出他深深的感悟。');
INSERT INTO `words` VALUES (7850, 'sensible', 'ˈsensəbl', 'adj.', '明智的;理智的;合理的;', 'It might be sensible to get a solicitor...', '也许应该请一个诉状律师。');
INSERT INTO `words` VALUES (7851, 'sensibly', '\'sensəbli', 'adv.', '显著地;明显地;聪明地;', 'It might be sensible to get a solicitor...', '也许应该请一个诉状律师。');
INSERT INTO `words` VALUES (7852, 'sensitive', 'ˈsensətɪv', 'adj.', '体贴的;体恤的;善解人意的;', 'The classroom teacher must be sensitive to a child\'s needs...', '班主任必须对小学生的需求体察入微。');
INSERT INTO `words` VALUES (7853, 'sensitivity', 'ˌsensəˈtɪvəti', 'n.', '体贴;体恤;体察;', 'The classroom teacher must be sensitive to a child\'s needs...', '班主任必须对小学生的需求体察入微。');
INSERT INTO `words` VALUES (7854, 'sensor', 'ˈsensə(r)', 'n.', '(探测光、热、压力等的)传感器，敏感元件，探测设备;', 'The latest Japanese vacuum cleaners contain sensors that detect the amount of dust and type of floor.', '日本的最新真空吸尘器配有可探测灰尘多少和地板类型的传感器。');
INSERT INTO `words` VALUES (7855, 'sensory', 'ˈsensəri', 'adj.', '感觉的;感官的;', '...sensory information passing through the spinal cord.', '经过脊髓传送的感官信息');
INSERT INTO `words` VALUES (7856, 'sensual', 'ˈsenʃuəl', 'adj.', '感官的;肉欲的;愉悦感官的;', 'He was a very sensual person...', '他是个登徒子。');
INSERT INTO `words` VALUES (7857, 'sensuous', 'ˈsenʃuəs', 'adj.', '愉悦感官的;肉感的;性感的;', 'The film is ravishing to look at and boasts a sensuous musical score...', '这部电影画面迷人，配乐悦耳动听。');
INSERT INTO `words` VALUES (7858, 'sentient', 'ˈsentiənt', 'adj.', '有感觉能力的;有知觉力的;', '...sentient creatures human and nonhuman alike.', '有知觉能力的人类和非人类生物');
INSERT INTO `words` VALUES (7859, 'sentiment', 'ˈsentɪmənt', 'n.', '(基于情感的)观点，看法;情绪;(失之过度或不恰当的)伤感，柔情，哀伤;', 'Public sentiment rapidly turned anti-American...', '公众情绪迅速转变，开始反对美国。');
INSERT INTO `words` VALUES (7860, 'sentimental', 'ˌsentɪˈmentl', 'adj.', '情感的(而非理性的);(失之过度或不恰当地)伤感的，充满柔情的;多愁善感的;', 'I\'m trying not to be sentimental about the past...', '我努力不让自己表现得感怀过去。');
INSERT INTO `words` VALUES (7861, 'sentimentality', 'ˌsentɪmenˈtæləti', 'n.', '感伤情调;多愁善感;', 'I\'m trying not to be sentimental about the past...', '我努力不让自己表现得感怀过去。');
INSERT INTO `words` VALUES (7862, 'sentry', 'ˈsentri', 'n.', '哨兵;', 'The sentry would not let her enter...', '警卫不让她进去。');
INSERT INTO `words` VALUES (7863, 'separable', 'ˈsepərəbl', 'adj.', '可分开的;可分隔的;可分离的;', 'Character is not separable from physical form but is governed by it.', '性格和外表是分不开的，而且观其形知其性。');
INSERT INTO `words` VALUES (7864, 'separated', 'ˈsepəreɪtɪd', 'adj.', '(和某人)分居的;', 'Most single parents are either divorced or separated...', '大多数单身父母要么离婚了要么分居了。');
INSERT INTO `words` VALUES (7865, 'separately', 'ˈseprətli', 'adv.', '单独地;分别地;', 'Cook each vegetable separately until just tender...', '每样蔬菜都单独烹调，煮到稍稍变软就好。');
INSERT INTO `words` VALUES (7866, 'separation', 'ˌsepəˈreɪʃn', 'n.', '分离;分开;分割;', 'He believes in the separation of the races.', '他认为应该实施种族隔离。');
INSERT INTO `words` VALUES (7867, 'separatist', 'ˈseprətɪst', 'n.', '分离主义者;独立主义者;', '...the Basque separatist movement...', '巴斯克分裂主义运动');
INSERT INTO `words` VALUES (7868, 'september', 'sepˈtembə(r)', 'n.', '九月;', 'Her son, Jerome, was born in September...', '她儿子杰尔姆生于 9 月。');
INSERT INTO `words` VALUES (7869, 'septic', 'ˈseptɪk', 'adj.', '感染病菌的;脓毒性的;腐败性的;', 'A flake of plaster from the ceiling fell into his eye, which became septic.', '天花板上的一小片熟石膏掉到他眼里，致使他的眼睛感染了。');
INSERT INTO `words` VALUES (7870, 'sepulchral', 'səˈpʌlkrəl', 'adj.', '阴沉的;阴森森的;死一般的;', '\'He\'s gone,\' Rory whispered in sepulchral tones.', '“他已经走了，”罗里阴沉地小声说道。');
INSERT INTO `words` VALUES (7871, 'sequel', 'ˈsiːkwəl', 'n.', '(书、电影、戏剧等的)续篇，续集;后续的事;随之而来的事;', 'She is currently writing a sequel to Daphne du Maurier\'s \'Rebecca\'...', '她现在正在续写达夫妮·杜穆里埃的小说《蝴蝶梦》。');
INSERT INTO `words` VALUES (7872, 'sequence', 'ˈsiːkwəns', 'n.', '一系列;一连串;顺序;', '...the sequence of events which led to the murder.', '导致谋杀发生的一连串事件');
INSERT INTO `words` VALUES (7873, 'sequencing', 'ˈsiːkwənsɪŋ', 'n.', '(对基因、DNA的)排序;', '...the US government\'s own gene sequencing programme.', '美国政府自己的基因排序项目');
INSERT INTO `words` VALUES (7874, 'sequential', 'sɪˈkwenʃl', 'adj.', '按次序的;顺序的;序列的;', '...the sequential story of the universe...', '宇宙的变迁史');
INSERT INTO `words` VALUES (7875, 'sequentially', 'sɪˈkwɛnʃəli', 'adv.', '继续地，从而;', '...the sequential story of the universe...', '宇宙的变迁史');
INSERT INTO `words` VALUES (7876, 'sequester', 'sɪˈkwestə(r)', 'v.', '隔离(避免陪审团与公众接触);', 'Everything he owned was sequestered.', '他的所有财产都被扣押了。');
INSERT INTO `words` VALUES (7877, 'sequestrate', 'ˈsiːkwəstreɪt', 'v.', '强制管理，扣押(债务人资产);', 'He tried to prevent union money from being sequestrated by the courts.', '他试图阻止法院扣押工会的资产。');
INSERT INTO `words` VALUES (7878, 'serenade', 'ˌserəˈneɪd', 'n.', '(男子在所爱慕的女子窗下歌唱或演奏的)小夜曲;(尤指供小型乐队演奏的)小夜曲;', 'In the interval a blond boy dressed in white serenaded the company on the flute...', '幕间休息时,一位身着白衣的金发男孩用长笛为来宾演奏了一支小夜曲。');
INSERT INTO `words` VALUES (7879, 'serendipity', 'ˌserənˈdɪpəti', 'n.', '巧事;机缘凑巧;', 'Some of the best effects in my garden have been the result of serendipity.', '我园子里最珍贵的几件物品是机缘巧合之下意外所得。');
INSERT INTO `words` VALUES (7880, 'serene', 'səˈriːn', 'adj.', '平静的;宁静的;安详的;', 'She looked as calm and serene as she always did...', '她看上去和以往一样平静安详。');
INSERT INTO `words` VALUES (7881, 'serenity', 'sə\'renəti', 'n.', '宁静;平静;安详;', 'She looked as calm and serene as she always did...', '她看上去和以往一样平静安详。');
INSERT INTO `words` VALUES (7882, 'serge', 'sɜːdʒ', 'n.', '哔叽;', 'He wore a blue serge suit.', '他穿着一套蓝色哔叽西服。');
INSERT INTO `words` VALUES (7883, 'sergeant', 'ˈsɑːdʒənt', 'n.', '陆军(或空军)中士;(英国警察)巡佐;(美国警察)警佐;', 'A sergeant with a detail of four men came into view.', '一名中士和一个四人小分队出现在眼前。');
INSERT INTO `words` VALUES (7884, 'serial', 'ˈsɪəriəl', 'n.', '电视连续剧;广播连续剧;杂志连载小说;', '...one of BBC television\'s most popular serials, Eastenders...', '英国广播公司最受欢迎的电视连续剧之一——《伦敦东区》');
INSERT INTO `words` VALUES (7885, 'series', 'ˈsɪəriːz', 'n.', '一系列;连续;接连;', '...a series of meetings with students and political leaders.', '与学生和政治领袖的一系列会面');
INSERT INTO `words` VALUES (7886, 'serious', 'ˈsɪəriəs', 'adj.', '不好的;严重的;有危险的;', 'Crime is an increasingly serious problem in Russian society...', '犯罪猖獗是俄罗斯社会面临的一个日益严重的问题。');
INSERT INTO `words` VALUES (7887, 'seriously', 'ˈsɪəriəsli', 'adv.', '严重地;严肃地;认真地;', 'Seriously, I only smoke in the evenings.', '真的，我只在晚上抽烟。');
INSERT INTO `words` VALUES (7888, 'seriousness', 'ˈsɪəriəsnəs', 'n.', '严重;认真;严肃;', 'Crime is an increasingly serious problem in Russian society...', '犯罪猖獗是俄罗斯社会面临的一个日益严重的问题。');
INSERT INTO `words` VALUES (7889, 'serpent', 'ˈsɜːpənt', 'n.', '蛇;(尤指)大蛇;', '...the serpent in the Garden of Eden.', '伊甸园里的那条蛇');
INSERT INTO `words` VALUES (7890, 'serpentine', 'ˈsɜːpəntaɪn', 'adj.', '弯弯曲曲的;蜿蜒的;盘旋的;', '...serpentine woodland pathways.', '蜿蜒的林间小路');
INSERT INTO `words` VALUES (7891, 'serrated', 'səˈreɪtɪd', 'adj.', '锯齿状的;', 'Bread knives should have a serrated edge.', '面包刀的刀刃应该有波浪齿。');
INSERT INTO `words` VALUES (7892, 'serried', 'ˈserid', 'adj.', '(行列)密排的，密集的，靠拢的;', '...serried rows of law books and law reports.', '排得密密匝匝的几排法律书籍和判例汇编');
INSERT INTO `words` VALUES (7893, 'serve', 'sɜːv', 'v.', '(给某人)提供;端上;够…吃(或用);', 'It is unfair to soldiers who have served their country well for many years...', '这对那些为国效忠多年的士兵们来说是不公平的。');
INSERT INTO `words` VALUES (7894, 'serviceable', 'ˈsɜːvɪsəbl', 'adj.', '能用的;', 'His Arabic was not as good as his English, but serviceable enough...', '他的阿拉伯语没有他的英语好，但是够用了。');
INSERT INTO `words` VALUES (7895, 'servile', 'ˈsɜːvaɪl', 'adj.', '奴性的;逢迎的;恭顺的;', 'He was subservient and servile...', '他低声下气、卑躬屈膝。');
INSERT INTO `words` VALUES (7896, 'serving', 'ˈsɜːvɪŋ', 'n.', '(供一个人吃的)一份食物;', 'Quantities will vary according to how many servings of soup you want to prepare...', '量的多少根据你想准备的汤的份数而定。');
INSERT INTO `words` VALUES (7897, 'servitude', 'ˈsɜːvɪtjuːd', 'n.', '奴役(状况);任人差遣(的状况);', '...a life of servitude.', '受奴役的一生');
INSERT INTO `words` VALUES (7898, 'sesame', 'ˈsesəmi', 'n.', '芝麻;', '...sesame seeds...', '芝麻籽');
INSERT INTO `words` VALUES (7899, 'session', 'ˈseʃn', 'n.', '一场;一节;一段时间;', '...an emergency session of parliament...', '议会紧急会议');
INSERT INTO `words` VALUES (7900, 'setback', 'ˈsetbæk', 'n.', '挫折;阻碍;', 'The move represents a setback for the Middle East peace process...', '这个行动意味着中东和平进程受挫。');
INSERT INTO `words` VALUES (7901, 'setback', 'ˈsetbæk', 'n.', '挫折;阻碍;', 'The move represents a setback for the Middle East peace process...', '这个行动意味着中东和平进程受挫。');
INSERT INTO `words` VALUES (7902, 'setting', 'ˈsetɪŋ', 'n.', '环境;背景;(戏剧、小说等的)情节背景;', 'Rome is the perfect setting for romance...', '罗马是最适合谈情说爱的地方。');
INSERT INTO `words` VALUES (7903, 'settle', 'ˈsetl', 'v.', '结束(争论、争端等);解决(分歧、纠纷等);(最终)决定，确定，安排好;', 'They agreed to try to settle their dispute by negotiation...', '他们同意通过谈判来努力解决纠纷。');
INSERT INTO `words` VALUES (7904, 'settled', 'ˈsetld', 'adj.', '不大可能变动的;稳定的;舒适自在的;', 'He decided to lead a more settled life with his partner...', '他决定和爱人一起过一种更加安定的生活。');
INSERT INTO `words` VALUES (7905, 'settlement', 'ˈsetlmənt', 'n.', '(解决纷争的)协议;解决;处理;', 'Our objective must be to secure a peace settlement...', '我们一定要达成和平协议。');
INSERT INTO `words` VALUES (7906, 'settler', 'ˈsetlə(r)', 'n.', '移民;殖民者;', 'The village was founded by settlers from the Volga region.', '来自伏尔加地区的移民建起了这个村庄。');
INSERT INTO `words` VALUES (7907, 'setup', 'ˈsɛtʌp', 'n.', '体制;组织方式;建制;陷害;栽赃;(软件或硬件的)安装，设置;', 'It appears to be an idyllic domestic set-up...', '这似乎是一种田园诗般的家庭组成格局。');
INSERT INTO `words` VALUES (7908, 'seven', 'ˈsevn', 'num.', '七;', 'Sarah and Ella have been friends for seven years.', '萨拉和埃拉已经有7年的交情了。');
INSERT INTO `words` VALUES (7909, 'seventeen', 'ˌsevnˈtiːn', 'num.', '十七;', 'Jenny is seventeen years old.', '珍妮 17 岁了。');
INSERT INTO `words` VALUES (7910, 'seventeenth', 'ˌsevnˈtiːnθ', 'ord.', '第十七;', 'She gave birth to Annabel just after her seventeenth birthday.', '她 17 岁生日刚过便生下了安娜贝尔。');
INSERT INTO `words` VALUES (7911, 'seventh', 'ˈsevnθ', 'ord.', '第七;', 'I was the seventh child in a family of 11.', '我在家里的 11 个孩子中排行老七。');
INSERT INTO `words` VALUES (7912, 'seventieth', 'ˈsevntiəθ', 'ord.', '第七十;', '...the seventieth anniversary of the discovery of Tutankhamun\'s tomb.', '图坦卡蒙墓发现 70 周年纪念日');
INSERT INTO `words` VALUES (7913, 'seventy', 'ˈsevnti', 'num.', '七十;七十几;七十年代;', 'Seventy people were killed.', '70 人丧生。');
INSERT INTO `words` VALUES (7914, 'sever', 'ˈsevə(r)', 'v.', '切开;割断;切下;', 'Richardson severed his right foot in a motorbike accident.', '理查森在一次摩托车事故中失去了他的右脚。');
INSERT INTO `words` VALUES (7915, 'several', 'ˈsevrəl', 'det./pron.', '几个;数个;一些;', 'I had lived two doors away from this family for several years...', '我和这家人是好几年的邻居了，中间就隔着两家。');
INSERT INTO `words` VALUES (7916, 'severance', 'ˈsevərəns', 'n.', '断绝;中断;解雇;', '...his bitter sense of severance from his family.', '他与家人断绝关系的痛楚');
INSERT INTO `words` VALUES (7917, 'severe', 'sɪˈvɪə(r)', 'adj.', '极为恶劣的;十分严重的;严厉的;', '...a business with severe cash flow problems...', '有严重资金周转问题的公司');
INSERT INTO `words` VALUES (7918, 'severely', 'sɪ\'vɪə(r)li', 'adv.', '严重地;严厉地;严格地;', '...a business with severe cash flow problems...', '有严重资金周转问题的公司');
INSERT INTO `words` VALUES (7919, 'severity', 'sɪ\'verəti', 'n.', '严重;严厉;严肃;', '...a business with severe cash flow problems...', '有严重资金周转问题的公司');
INSERT INTO `words` VALUES (7920, 'sew', 'səʊ', 'v.', '缝;做针线活;缝制;', 'She sewed the dresses on the sewing machine...', '她在缝纫机上缝制衣服。');
INSERT INTO `words` VALUES (7921, 'sewage', 'ˈsuːɪdʒ', 'n.', '(下水道的)污水，污物;', '...the MPs\' call for more treatment of raw sewage.', '议员们关于对未经净化的污水加强处理的呼吁');
INSERT INTO `words` VALUES (7922, 'sewer', 'ˈsuːə(r)', 'n.', '污水管;下水道;阴沟;', '...the city\'s sewer system.', '城市排水系统');
INSERT INTO `words` VALUES (7923, 'sewing', 'ˈsəʊɪŋ', 'n.', '缝纫;缝制中的衣物;', 'Her mother had always done all the sewing.', '所有的针线活一直都是她母亲来做。');
INSERT INTO `words` VALUES (7924, 'sex', 'seks', 'n.', '性别;性;男性;', '...an entertainment star who appeals to all ages and both sexes.', '男女老幼都喜欢的娱乐明星');
INSERT INTO `words` VALUES (7925, 'sexism', 'ˈseksɪzəm', 'n.', '(尤指对女性的)性别歧视，性别偏见;', 'Groups like ours are committed to eradicating homophobia, racism and sexism.', '一些像我们这样的团体致力于消除对同性恋的憎恶、种族歧视和性别歧视。');
INSERT INTO `words` VALUES (7926, 'sexist', 'ˈseksɪst', 'n.', '性别歧视者;', 'Old-fashioned sexist attitudes are still common...', '陈旧的性别歧视态度仍然常见。');
INSERT INTO `words` VALUES (7927, 'sextet', 'seksˈtet', 'n.', '六重奏乐团;六重唱组合;六重奏(曲);', '...the Paul Rogers Sextet.', '保罗·罗杰斯六人乐队');
INSERT INTO `words` VALUES (7928, 'sexual', 'ˈsekʃuəl', 'adj.', '性行为的;性的;生殖的;', 'This was the first sexual relationship I had had...', '这是我第一次与人发生性关系。');
INSERT INTO `words` VALUES (7929, 'sexuality', 'ˌsekʃuˈæləti', 'n.', '性征;性感觉;性欲;', 'In Britain, the growing discussion of women\'s sexuality raised its own disquiet.', '在英国，对女性性欲的讨论日趋高涨，使得这一讨论本身就开始引起了不安。');
INSERT INTO `words` VALUES (7930, 'sexually', 'ˈsɛksjʊəli', 'adv.', '性;', 'This was the first sexual relationship I had had...', '这是我第一次与人发生性关系。');
INSERT INTO `words` VALUES (7931, 'sexy', 'ˈseksi', 'adj.', '性感的;引起性欲的;性欲发作的;', 'She was one of the sexiest women I had seen...', '她是我见过的最性感的女人之一。');
INSERT INTO `words` VALUES (7932, 'sh', 'ʃ', 'int.', '(用以让别人安静)嘘;', 'Sh! You want to listen or don\'t you?', '嘘！你到底想不想听啊？');
INSERT INTO `words` VALUES (7933, 'shabby', 'ˈʃæbi', 'adj.', '破旧的;破败的;破烂的;', 'His clothes were old and shabby...', '他的衣服又旧又破。');
INSERT INTO `words` VALUES (7934, 'shackle', 'ˈʃækl', 'v.', '给(某人)戴镣铐;束缚;阻挠;', 'The trade unions are shackled by the law.', '工会受法律的制约。');
INSERT INTO `words` VALUES (7935, 'shade', 'ʃeɪd', 'n.', '阴凉处;背阴;(树)荫;', 'In the mornings the sky appeared a heavy shade of mottled gray...', '清晨，天空呈现出斑驳的深灰色。');
INSERT INTO `words` VALUES (7936, 'shading', 'ˈʃeɪdɪŋ', 'n.', '(绘画的)明暗法;(地图、图表等中)颜色浓淡强调某些部分的运用;(同一事物不同层面之间的)细微差别;', 'The conservatory will get very hot in summer unless shading is used.', '夏天如果不用遮光罩的话，暖房会变得特别热。');
INSERT INTO `words` VALUES (7937, 'shadow', 'ˈʃædəʊ', 'n.', '阴影;影子;昏暗处;', 'An oak tree cast its shadow over a tiny round pool...', '一棵橡树的影子投射在一洼圆形的小水池上。');
INSERT INTO `words` VALUES (7938, 'shadowy', 'ˈʃædəʊi', 'adj.', '阴暗的;幽暗的;阴影中的;', 'I watched him from a shadowy corner.', '我躲在一个阴暗的角落里观察他。');
INSERT INTO `words` VALUES (7939, 'shady', 'ˈʃeɪdi', 'adj.', '背阴的;阴凉的;多阴的;', 'After flowering, place the pot in a shady spot in the garden...', '花期过后，将花盆放到花园里的背阴处。');
INSERT INTO `words` VALUES (7940, 'shaft', 'ʃɑːft', 'n.', '(电梯的)升降机井;通风井;竖井;', 'He was found dead at the bottom of a lift shaft.', '他被发现死在了电梯井的底部。');
INSERT INTO `words` VALUES (7941, 'shaggy', 'ˈʃæɡi', 'adj.', '长而乱的;乱蓬蓬的;头发(或皮毛)蓬乱的;', 'Tim has longish, shaggy hair.', '蒂姆留着蓬乱的长发。');
INSERT INTO `words` VALUES (7942, 'shaky', 'ˈʃeɪki', 'adj.', '颤抖的;颤巍巍的;不稳固的;', 'A shaky ceasefire is holding after three days of fighting between rival groups...', '敌对武装团伙在经过3天的交战后暂时停火。');
INSERT INTO `words` VALUES (7943, 'shallow', 'ˈʃæləʊ', 'adj.', '浅的;肤浅的;浅薄的;', 'Put the milk in a shallow dish...', '将牛奶倒入一个浅盘里。');
INSERT INTO `words` VALUES (7944, 'sham', 'ʃæm', 'n.', '假象;假情假义;伪善;', 'The government\'s promises were exposed as a hollow sham...', '实情被揭穿了:政府的承诺原来是空洞的谎言。');
INSERT INTO `words` VALUES (7945, 'shamble', 'ˈʃæmbl', 'v.', '拖着脚走;蹒跚;', 'The conductor shambled to the next carriage.', '售票员慵懒地拖着脚走向下一节车厢。');
INSERT INTO `words` VALUES (7946, 'shambles', 'ˈʃæmblz', 'n.', '混乱局面;无序的场面;凌乱不堪;', 'The ship\'s interior was an utter shambles...', '船里面一片狼藉。');
INSERT INTO `words` VALUES (7947, 'shame', 'ʃeɪm', 'n.', '羞耻;羞愧;惭愧;', 'She felt a deep sense of shame...', '她深感羞愧。');
INSERT INTO `words` VALUES (7948, 'shameful', 'ˈʃeɪmfl', 'adj.', '可耻的;丢脸的;', '...the most shameful episode in US naval history.', '美国海军最屈辱的一段历史');
INSERT INTO `words` VALUES (7949, 'shameless', 'ˈʃeɪmləs', 'adj.', '无耻的;没廉耻的;不要脸的;', '...a shameless attempt to stifle democratic debate.', '妄想扼杀民主讨论的无耻企图');
INSERT INTO `words` VALUES (7950, 'shampoo', 'ʃæmˈpuː', 'n.', '洗发剂;香波;(洗地毯、家具罩套、汽车等的)洗涤剂;', '...a bottle of shampoo.', '一瓶洗发液');
INSERT INTO `words` VALUES (7951, 'shape', 'ʃeɪp', 'n.', '形状;外形;样子;', 'Each mirror is made to order and can be designed to almost any shape or size.', '每面镜子都是定制的，几乎可以设计成任何形状和尺寸。');
INSERT INTO `words` VALUES (7952, 'shapeless', 'ˈʃeɪpləs', 'adj.', '无定形的;不成形的;样子不好看的;', 'Aunt Mary wore shapeless black dresses.', '玛丽姨妈总是穿一些没款没形的黑色裙子。');
INSERT INTO `words` VALUES (7953, 'shard', 'ʃɑːd', 'n.', '(玻璃、金属等的)碎片;', 'Eyewitnesses spoke of rocks and shards of glass flying in the air.', '目击者称空中石块和玻璃碎片四溅。');
INSERT INTO `words` VALUES (7954, 'share', 'ʃeə(r)', 'v.', '共有;合用;分配;', 'This is why Sir Colin Marshall, British Airways\' chairman, has been so keen to buy shares in US-AIR...', '这就是为什么英国航空公司主席科林·马歇尔爵士一直如此热衷于购买美国航空公司的股票。');
INSERT INTO `words` VALUES (7955, 'shareholder', 'ˈʃeəhəʊldə(r)', 'n.', '股东;', '...a shareholders\' meeting.', '股东大会');
INSERT INTO `words` VALUES (7956, 'sharing', 'ˈʃeərɪŋ', 'v.', '共有;合用;分配;', 'This is why Sir Colin Marshall, British Airways\' chairman, has been so keen to buy shares in US-AIR...', '这就是为什么英国航空公司主席科林·马歇尔爵士一直如此热衷于购买美国航空公司的股票。');
INSERT INTO `words` VALUES (7957, 'sharp', 'ʃɑːp', 'adj.', '锋利的;锐利的;尖的;', 'The other end of the twig is sharpened into a sharp point to use as a toothpick...', '细枝的另一端被削尖用作牙签。');
INSERT INTO `words` VALUES (7958, 'sharpen', 'ˈʃɑːpən', 'v.', '(使)变得锋利，变得清晰;(使感觉或感情)加强，加重，变得更明显;使尖锐;', 'Her gaze sharpened, as if she had seen something unusual...', '她眼睛一亮，仿佛看见了什么不寻常的事情。');
INSERT INTO `words` VALUES (7959, 'sharpener', 'ˈʃɑːpnə(r)', 'n.', '磨具;削具;', '...a pencil sharpener.', '卷笔刀');
INSERT INTO `words` VALUES (7960, 'sharp-eyed', 'ˌʃɑːp ˈaɪd', 'adj.', '眼尖的;目光敏锐的;', 'A sharp-eyed shop assistant spotted the fake.', '一个眼尖的售货员发现了假货。');
INSERT INTO `words` VALUES (7961, 'sharply', 'ˈʃɑːpli', 'adv.', '尖刻地;严厉地;猛烈地;', 'The other end of the twig is sharpened into a sharp point to use as a toothpick...', '细枝的另一端被削尖用作牙签。');
INSERT INTO `words` VALUES (7962, 'sharpness', 'ˈʃɑːpnəs', 'n.', '锐度;尖锐;锐利;', 'The other end of the twig is sharpened into a sharp point to use as a toothpick...', '细枝的另一端被削尖用作牙签。');
INSERT INTO `words` VALUES (7963, 'shatter', 'ˈʃætə(r)', 'v.', '(使)破碎，碎裂;(使感情、希望或信念等)粉碎，破灭;被粉碎;', '...safety glass that won\'t shatter if it\'s broken...', '被打破后也不会碎裂的安全玻璃');
INSERT INTO `words` VALUES (7964, 'shave', 'ʃeɪv', 'v.', '剃(须发);(尤指)刮脸;(少量地)削减，调低，降价;', 'He took a bath and shaved before dinner...', '晚餐前他洗了澡，刮了脸。');
INSERT INTO `words` VALUES (7965, 'shaving', 'ˈʃeɪvɪŋ', 'n.', '刨花;削片;削屑;', 'The floor was covered with shavings from his wood carvings.', '地上满是他刻木雕时落下的碎屑。');
INSERT INTO `words` VALUES (7966, 'shavings', 'ˈʃeɪvɪŋz', 'n.', '(刨等削下的)削片;刨花;', 'The floor was covered with shavings from his wood carvings.', '地上满是他刻木雕时落下的碎屑。');
INSERT INTO `words` VALUES (7967, 'sheaf', 'ʃiːf', 'n.', '一叠，一沓，一扎(纸);(收割的)小麦捆;', 'He took out a sheaf of papers and leafed through them.', '他拿出一沓报纸，匆匆翻阅了一下。');
INSERT INTO `words` VALUES (7968, 'shear', 'ʃɪə(r)', 'v.', '给(羊)剪(羊毛);剪(头发);切断;', 'In the Hebrides they shear their sheep later than anywhere else.', '在赫伯里兹，剪羊毛的时间比其他任何地方都要晚。');
INSERT INTO `words` VALUES (7969, 'shears', 'ʃɪəz', 'n.', '大剪刀(用来修剪灌木、树篱等);', 'In the Hebrides they shear their sheep later than anywhere else.', '在赫伯里兹，剪羊毛的时间比其他任何地方都要晚。');
INSERT INTO `words` VALUES (7970, 'sheathe', 'ʃiːð', 'v.', '把…插入鞘中;给某物加护套(或护层、护皮);', 'The television was sheathed in a snug coverlet.', '电视机用一个床罩严严实实地盖住了。');
INSERT INTO `words` VALUES (7971, 'sheen', 'ʃiːn', 'n.', '光泽;光辉;光彩;', 'The carpet had a silvery sheen to it.', '这地毯有种银色的光泽。');
INSERT INTO `words` VALUES (7972, 'sheep', 'ʃiːp', 'n.', '羊;绵羊;', '...grassland on which a flock of sheep were grazing.', '有一群羊正在吃草的草场');
INSERT INTO `words` VALUES (7973, 'sheepish', 'ˈʃiːpɪʃ', 'adj.', '窘迫的;难为情的;不好意思的;', 'The couple leapt apart when she walked in on them and later came downstairs looking sheepish.', '她走进屋时撞见这对情侣，搞得两人赶紧分开，后来又面带窘色地走下楼来。');
INSERT INTO `words` VALUES (7974, 'sheer', 'ʃɪə(r)', 'adj.', '用来强调事物的大小、程度或数量;完全的;纯粹的;', 'His music is sheer delight...', '听他的音乐完全是一种享受。');
INSERT INTO `words` VALUES (7975, 'sheet', 'ʃiːt', 'n.', '床单;被单;一张(通常指标准尺寸的纸);', 'Once a week, a maid changes the sheets.', '女仆每星期换一次床单。');
INSERT INTO `words` VALUES (7976, 'sheets', 'ʃiːts', 'n.', '床单;被单;一张(通常指标准尺寸的纸);', 'Once a week, a maid changes the sheets.', '女仆每星期换一次床单。');
INSERT INTO `words` VALUES (7977, 'shelf', 'ʃelf', 'n.', '(固定在墙上的或橱柜、书架等的)架子，搁板;(悬崖上或海底)突出的岩石;陆架;', 'He took a book from the shelf.', '他从书架上拿了一本书。');
INSERT INTO `words` VALUES (7978, 'shell', 'ʃel', 'n.', '(蛋、坚果、某些种子和某些动物的)壳;壳状物;炮弹;', 'They cracked the nuts and removed their shells...', '他们砸开坚果,剥去硬壳。');
INSERT INTO `words` VALUES (7979, 'shell', 'ʃel', 'n.', '(蛋、坚果、某些种子和某些动物的)壳;壳状物;炮弹;', 'They cracked the nuts and removed their shells...', '他们砸开坚果,剥去硬壳。');
INSERT INTO `words` VALUES (7980, 'shellfish', 'ˈʃelfɪʃ', 'n.', '水生有壳动物;', 'Fish and shellfish are the specialities.', '鱼和贝类是特色菜。');
INSERT INTO `words` VALUES (7981, 'shelter', 'ˈʃeltə(r)', 'n.', '居所;住处;遮蔽，庇护，避难(避雨、躲避危险或攻击);', 'The city\'s bomb shelters were being prepared for possible air raids.', '为防御可能发生的空袭，该城市正在建防空洞。');
INSERT INTO `words` VALUES (7982, 'sheltered', 'ˈʃeltəd', 'adj.', '有遮蔽物(不受恶劣天气侵袭)的;受庇护的;受到保护的;', '...a shallow-sloping beach next to a sheltered bay.', '有天然屏障的海湾旁的一个缓坡海滩');
INSERT INTO `words` VALUES (7983, 'shelve', 'ʃelv', 'v.', '搁置，停止(计划);把…放在架子(或搁板)上;倾斜;', 'Atlanta has shelved plans to include golf in the 1996 Games...', '亚特兰大已搁置了将高尔夫球纳入 1996 年奥运会的计划。');
INSERT INTO `words` VALUES (7984, 'shelves', 'ʃelvz', 'n.', '(固定在墙上的或橱柜、书架等的)架子，搁板;(悬崖上或海底)突出的岩石;陆架;', 'Atlanta has shelved plans to include golf in the 1996 Games...', '亚特兰大已搁置了将高尔夫球纳入 1996 年奥运会的计划。');
INSERT INTO `words` VALUES (7985, 'sherbet', 'ˈʃɜːbət', 'n.', '果汁冰糕;', '...sherbet dips.', '果味汽水粉糖酱');
INSERT INTO `words` VALUES (7986, 'sheriff', 'ˈʃerɪf', 'n.', '县治安官，城镇治安官(美国民选地方官员);郡督(英格兰和威尔士官员，为英王在各郡和部分城市的代表);(苏格兰)法官;', '...the local sheriff.', '当地的县治安官');
INSERT INTO `words` VALUES (7987, 'sherry', 'ˈʃeri', 'n.', '雪利酒(烈性葡萄酒，原产自西班牙南部);一杯雪利酒;', 'I poured us a glass of sherry.', '我给大家倒了杯雪利酒。');
INSERT INTO `words` VALUES (7988, 'shibboleth', 'ˈʃɪbəleθ', 'n.', '过时的观点;陈旧的原则;陈词滥调;', 'It is time to go beyond the shibboleth that conventional forces cannot deter.', '是时候摆脱那些传统力量无法遏制的陈规陋习了。');
INSERT INTO `words` VALUES (7989, 'shield', 'ʃiːld', 'n.', '盾(牌);保护人;保护物;', 'He used his left hand as a shield against the reflecting sunlight.', '他用左手挡住反射过来的阳光。');
INSERT INTO `words` VALUES (7990, 'shielding', 'ˈʃiːldɪŋ', 'v.', '保护某人或某物(免遭危险、伤害或不快);给…加防护罩;', 'He used his left hand as a shield against the reflecting sunlight.', '他用左手挡住反射过来的阳光。');
INSERT INTO `words` VALUES (7991, 'shift', 'ʃɪft', 'v.', '转移;挪动;赶快;', 'He stopped, shifting his cane to his left hand...', '他停了下来，将手杖移到左手。');
INSERT INTO `words` VALUES (7992, 'shifting', 'ˈʃɪftɪŋ', 'adj.', '不断移动的;流动的;', '...the shifting sand beneath their feet...', '他们脚下的流沙');
INSERT INTO `words` VALUES (7993, 'shiftless', 'ˈʃɪftləs', 'adj.', '没志气的;不思上进的;混日子的;', '...a shiftless husband.', '不求上进的丈夫');
INSERT INTO `words` VALUES (7994, 'shifty', 'ˈʃɪfti', 'adj.', '看着不可靠的;贼眉鼠眼的;显得心里有鬼的;', 'He had a shifty face and previous convictions.', '他看起来贼眉鼠眼，而且有犯罪前科。');
INSERT INTO `words` VALUES (7995, 'shimmer', 'ˈʃɪmə(r)', 'v.', '发出微弱的闪光;闪烁;', 'The lights shimmered on the water...', '水面上波光粼粼。');
INSERT INTO `words` VALUES (7996, 'shin', 'ʃɪn', 'n.', '胫;胫部;', 'She punched him on the nose and kicked him in the shins.', '她冲他鼻子打了一拳，又在他小腿上踢了一脚。');
INSERT INTO `words` VALUES (7997, 'shingle', 'ˈʃɪŋɡl', 'n.', '(海滨或河边的)卵石滩;墙面板;木瓦;', '...a beach of sand and shingle.', '碎石沙滩');
INSERT INTO `words` VALUES (7998, 'shining', 'ˈʃaɪnɪŋ', 'adj.', '(成就)辉煌的，值得钦佩的;(品质)出色的，卓越的;', 'She is a shining example to us all...', '她是我们所有人的光辉榜样。');
INSERT INTO `words` VALUES (7999, 'shiny', 'ˈʃaɪni', 'adj.', '光亮的;锃亮的;反光的;', 'Her blonde hair was shiny and clean.', '她的金发干净而有光泽。');
INSERT INTO `words` VALUES (8000, 'ship', 'ʃɪp', 'n.', '(大)船;舰;', 'Within ninety minutes the ship was ready for departure...', '90分钟之内轮船准备出发。');
INSERT INTO `words` VALUES (8001, 'shipment', 'ˈʃɪpmənt', 'n.', '运输;运送;装运;', 'Food shipments could begin in a matter of weeks.', '几周内就可以开始运送食物了。');
INSERT INTO `words` VALUES (8002, 'shipping', 'ˈʃɪpɪŋ', 'n.', '船舶;航运;海运;', '...the international shipping industry...', '国际航运业');
INSERT INTO `words` VALUES (8003, 'shipshape', 'ˈʃɪpʃeɪp', 'adj.', '整洁;井井有条;良好可用;', 'The house only needs an occasional coat of paint to keep it shipshape.', '房子只需偶尔粉刷一次就可以保持整洁。');
INSERT INTO `words` VALUES (8004, 'shipwreck', 'ˈʃɪprek', 'n.', '船舶失事;海难;失事的船;', 'He was drowned in a shipwreck off the coast of Spain.', '他在西班牙海岸附近的一次海难中溺水身亡。');
INSERT INTO `words` VALUES (8005, 'shire', 'ˈʃaɪə(r)', 'n.', '郡(现在用于英国部分郡名，如Hampshire、Yorkshire);英格兰中部诸郡;', 'Smart country people are fleeing back to the shires.', '精明的乡下人正纷纷逃回各郡。');
INSERT INTO `words` VALUES (8006, 'shirk', 'ʃɜːk', 'v.', '逃避(工作);偷懒;', 'We in the Congress can\'t shirk our responsibility...', '我们作为国会议员不能逃避责任。');
INSERT INTO `words` VALUES (8007, 'shirttail', 'ˈʃɜːtteɪl', 'n.', '衬衫下摆;', 'He wore sandals and old jeans and his shirt-tails weren\'t tucked in.', '他穿着凉鞋、旧仔裤，衬衫下摆也没塞进裤子里。');
INSERT INTO `words` VALUES (8008, 'shiver', 'ˈʃɪvə(r)', 'v.', '颤抖，哆嗦(因寒冷、恐惧、激动等);', 'He shivered in the cold...', '他冻得发抖。');
INSERT INTO `words` VALUES (8009, 'shivery', 'ˈʃɪvəri', 'adj.', '颤抖的，战栗的，哆嗦的(因寒冷、恐惧、患病等);', 'She felt shivery and a little sick.', '她感到身体发抖,而且有点恶心。');
INSERT INTO `words` VALUES (8010, 'shoal', 'ʃəʊl', 'n.', '鱼群;浅滩;水下沙洲;', 'Among them swam shoals of fish.', '鱼群在他们中间游过。');
INSERT INTO `words` VALUES (8011, 'shock', 'ʃɒk', 'n.', '震惊;惊愕;令人震惊的事;', 'The extent of the violence came as a shock...', '暴力程度令人震惊。');
INSERT INTO `words` VALUES (8012, 'shocked', 'ʃɒkt', 'v.', '使震惊;使惊愕;使气愤;', 'The extent of the violence came as a shock...', '暴力程度令人震惊。');
INSERT INTO `words` VALUES (8013, 'shocking', 'ˈʃɒkɪŋ', 'adj.', '令人气愤的;惹人憎恶的;不道德的;', 'The media coverage was shocking...', '当时媒体的报道糟透了。');
INSERT INTO `words` VALUES (8014, 'shoddy', 'ˈʃɒdi', 'adj.', '做工粗糙的;粗制滥造的;劣质的;', 'I\'m normally quick to complain about shoddy service...', '对劣质服务我通常会立刻投诉。');
INSERT INTO `words` VALUES (8015, 'shoe', 'ʃuː', 'n.', '鞋;', '...a pair of shoes...', '一双鞋');
INSERT INTO `words` VALUES (8016, 'shoelace', 'ˈʃuːleɪs', 'n.', '鞋带;', 'He began to tie his shoelaces.', '他开始系鞋带。');
INSERT INTO `words` VALUES (8017, 'shoes', 'ʃuːz', 'n.', '鞋;', '...a pair of shoes...', '一双鞋');
INSERT INTO `words` VALUES (8018, 'shoot', 'ʃuːt', 'v.', '开(枪或其他武器);射击;发射;', 'The police had orders to shoot anyone who attacked them...', '警察接到命令可以枪击任何攻击他们的人。');
INSERT INTO `words` VALUES (8019, 'shooter', 'ˈʃuːtə(r)', 'n.', '射手;射击武器;枪;', 'An eyewitness identified him as the shooter...', '一位目击者认出他就是那个开枪者。');
INSERT INTO `words` VALUES (8020, 'shooting', 'ˈʃuːtɪŋ', 'n.', '枪击;枪杀;狩猎;', 'Two more bodies were found nearby after the shooting...', '枪击事件后附近又发现了两具尸体。');
INSERT INTO `words` VALUES (8021, 'shoots', 'ʃuːts', 'n.', '幼苗;嫩芽;新枝;', 'The police had orders to shoot anyone who attacked them...', '警察接到命令可以枪击任何攻击他们的人。');
INSERT INTO `words` VALUES (8022, 'shop', 'ʃɒp', 'n.', '商店;店铺;工厂;', '...health food shops.', '健康食品商店');
INSERT INTO `words` VALUES (8023, 'shoplift', 'ˈʃɒplɪft', 'v.', '在商店行窃;', 'He openly shoplifted from a supermarket...', '他公然在超市偷窃。');
INSERT INTO `words` VALUES (8024, 'shoplifter', '\'ʃɒplɪftə(r)', 'n.', '（貌似在商店购物实则行窃的）商店窃贼;', 'He openly shoplifted from a supermarket...', '他公然在超市偷窃。');
INSERT INTO `words` VALUES (8025, 'shopper', 'ˈʃɒpə(r)', 'n.', '购物者;(商店的)顾客;', '...health food shops.', '健康食品商店');
INSERT INTO `words` VALUES (8026, 'shopping', 'ˈʃɒpɪŋ', 'n.', '购物;从商店采买的东西;', 'I\'ll do the shopping this afternoon.', '今天下午我会去买东西。');
INSERT INTO `words` VALUES (8027, 'shore', 'ʃɔː(r)', 'n.', '(海洋、湖泊等大水域的)岸，滨;国家(尤指濒海国家);', 'They walked down to the shore.', '他们走到了湖边。');
INSERT INTO `words` VALUES (8028, 'shorn', 'ʃɔːn', 'adj.', '剪短的;修剪过的;被剥夺了…的;被剥去…的;', '...his shorn hair.', '他剪短了的头发');
INSERT INTO `words` VALUES (8029, 'shortage', 'ˈʃɔːtɪdʒ', 'n.', '不足;缺少;短缺;', 'A shortage of funds is preventing the UN from monitoring relief...', '资金短缺使联合国无法督导救援工作。');
INSERT INTO `words` VALUES (8030, 'short-circuit', 'ˌʃɔːt ˈsɜːkɪt', 'v.', '(使)短路;(做事)抄近路，走捷径;', 'Carbon dust and oil build up in large motors and cause them to short-circuit...', '碳尘和燃油在大发动机里越积越多，造成了发动机短路。');
INSERT INTO `words` VALUES (8031, 'shortcoming', 'ˈʃɔːtkʌmɪŋ', 'n.', '缺点;短处;', 'Marriages usually break down as a result of the shortcomings of both partners...', '婚姻常因为配偶双方的缺点而破裂。');
INSERT INTO `words` VALUES (8032, 'shorten', 'ˈʃɔːtn', 'v.', '(使)变短，缩短;', 'Smoking can shorten your life...', '吸烟会缩短你的寿命。');
INSERT INTO `words` VALUES (8033, 'shortfall', 'ˈʃɔːtfɔːl', 'n.', '缺口;差额;亏空;', 'The government has refused to make up a £30,000 shortfall in funding.', '政府拒不补上 30,000 英镑的资金缺口。');
INSERT INTO `words` VALUES (8034, 'shorthand', 'ˈʃɔːthænd', 'n.', '速记(法);(对某事)简略的表达方式;', 'Ben took notes in shorthand.', '本用速记法记笔记。');
INSERT INTO `words` VALUES (8035, 'short-lived', 'ˌʃɔːt ˈlɪvd', 'adj.', '短暂的;', 'Any hope that the speech would end the war was short-lived...', '期待这场演讲能结束战争的希望很快便破灭了。');
INSERT INTO `words` VALUES (8036, 'shortly', 'ˈʃɔːtli', 'adv.', '不多时;不久;立刻;', 'Their trial will shortly begin...', '他们的审判很快就要开始了。');
INSERT INTO `words` VALUES (8037, 'shortsighted', 'ˌʃɔːtˈsaɪtɪd', 'adj.', '近视的;目光短浅的;无远见的;', 'Testing showed her to be very short-sighted.', '检查表明她近视得很厉害。');
INSERT INTO `words` VALUES (8038, 'short-term', 'ˌʃɔːt ˈtɜːm', 'adj.', '短期的;近期的;', 'Investors weren\'t concerned about short-term profits over the next few years...', '投资者对未来几年里的短期收益并不感兴趣。');
INSERT INTO `words` VALUES (8039, 'shoulder', 'ˈʃəʊldə(r)', 'n.', '肩;肩膀;肩胛;', 'She led him to an armchair, with her arm round his shoulder...', '她揽着他的肩膀，将他领到一把扶手椅旁。');
INSERT INTO `words` VALUES (8040, 'shoulders', 'ˈʃəʊldəz', 'n.', '肩;肩膀;肩胛;', 'She led him to an armchair, with her arm round his shoulder...', '她揽着他的肩膀，将他领到一把扶手椅旁。');
INSERT INTO `words` VALUES (8041, 'shout', 'ʃaʊt', 'v.', '大声说;叫;嚷;', 'He had to shout to make himself heard above the near gale-force wind...', '在呼啸的大风中他得大声喊叫才能让别人听见。');
INSERT INTO `words` VALUES (8042, 'shove', 'ʃʌv', 'v.', '猛推;乱挤;推撞;', 'He shoved her out of the way...', '他一把将她推开。');
INSERT INTO `words` VALUES (8043, 'show', 'ʃəʊ', 'v.', '表明;证明;给…看;', 'Research shows that a high-fibre diet may protect you from bowel cancer...', '研究表明高纤维饮食可预防肠癌。');
INSERT INTO `words` VALUES (8044, 'showdown', 'ˈʃəʊdaʊn', 'n.', '决出胜负的较量;最后的决战;', 'The Prime Minister is preparing for a showdown with Ministers.', '首相正准备和部长们摊牌。');
INSERT INTO `words` VALUES (8045, 'shower', 'ˈʃaʊə(r)', 'n.', '淋浴器;淋浴间;淋浴;', 'She heard him turn on the shower.', '她听见他打开了淋浴喷头。');
INSERT INTO `words` VALUES (8046, 'showing', 'ˈʃəʊɪŋ', 'n.', '放映;表现;', 'Research shows that a high-fibre diet may protect you from bowel cancer...', '研究表明高纤维饮食可预防肠癌。');
INSERT INTO `words` VALUES (8047, 'showpiece', 'ˈʃəʊpiːs', 'n.', '(供展示用的)样品;', 'The factory was to be a showpiece of Western investment in the East...', '这家工厂将会成为西方在东方投资的样板。');
INSERT INTO `words` VALUES (8048, 'showroom', 'ˈʃəʊruːm', 'n.', '商品陈列室;展销厅;', '...a car showroom...', '汽车展销厅');
INSERT INTO `words` VALUES (8049, 'showy', 'ˈʃəʊi', 'adj.', '显眼的;艳丽的;花哨的;', 'Since he was color blind, he favored large, showy flowers...', '因为他是色盲，所以他喜爱大朵艳丽的花。');
INSERT INTO `words` VALUES (8050, 'shred', 'ʃred', 'v.', '切碎;撕碎;', 'They may be shredding documents...', '他们可能是在粉碎文件。');
INSERT INTO `words` VALUES (8051, 'shredder', 'ˈʃredə(r)', 'n.', '切碎机;(尤指)碎纸机;', '...a document shredder.', '文件粉碎机');
INSERT INTO `words` VALUES (8052, 'shrewd', 'ʃruːd', 'adj.', '精明的;敏锐的;有眼光的;', 'She\'s a shrewd businesswoman...', '她是个精明的女商人。');
INSERT INTO `words` VALUES (8053, 'shriek', 'ʃriːk', 'v.', '尖叫;尖声说;', 'She shrieked and leapt from the bed...', '她尖叫一声从床上跳了起来。');
INSERT INTO `words` VALUES (8054, 'shrill', 'ʃrɪl', 'adj.', '刺耳的;尖声的;尖厉的;', 'Shrill cries and startled oaths flew up around us as pandemonium broke out...', '骚乱爆发时，我们周围充斥着尖厉的哭喊声和难以入耳的咒骂声。');
INSERT INTO `words` VALUES (8055, 'shrimp', 'ʃrɪmp', 'n.', '虾;小虾;', 'Add the shrimp and cook for 30 seconds...', '放入小虾，煮 30 秒。');
INSERT INTO `words` VALUES (8056, 'shrine', 'ʃraɪn', 'n.', '圣地;圣祠;神庙;', '...the holy shrine of Mecca.', '麦加圣地');
INSERT INTO `words` VALUES (8057, 'shrink', 'ʃrɪŋk', 'v.', '(使)缩水，收缩，缩小，皱缩;(使)缩小，减少;退缩;', 'All my jumpers have shrunk.', '我所有的套头毛衣都缩水了。');
INSERT INTO `words` VALUES (8058, 'shrinkage', 'ˈʃrɪŋkɪdʒ', 'n.', '缩小;收缩;收缩量;', 'Allow for some shrinkage in both length and width.', '长、宽上都预留出一定的缩水量。');
INSERT INTO `words` VALUES (8059, 'shrinking', 'ˈʃrɪŋkɪŋ', 'v.', '(使)缩水，收缩，缩小，皱缩;(使)缩小，减少;退缩;', 'All my jumpers have shrunk.', '我所有的套头毛衣都缩水了。');
INSERT INTO `words` VALUES (8060, 'shrub', 'ʃrʌb', 'n.', '灌木;', '...flowering shrubs.', '开花灌木');
INSERT INTO `words` VALUES (8061, 'shrug', 'ʃrʌɡ', 'v.', '耸肩(表示不知道或不在乎);', 'I shrugged, as if to say, \'Why not?\'...', '我耸了耸肩，仿佛在说，“为什么不?”');
INSERT INTO `words` VALUES (8062, 'shrunken', 'ˈʃrʌŋkən', 'adj.', '皱缩的;干枯的;', 'She now looked small, shrunken and pathetic...', '她现在看上去瘦小而干瘪，很可怜。');
INSERT INTO `words` VALUES (8063, 'shuck', 'ʃʌk', 'n.', '(坚果或牡蛎、蛤等的)壳;(植物的)荚;外皮;', '...corn shucks.', '玉米的包皮');
INSERT INTO `words` VALUES (8064, 'shudder', 'ˈʃʌdə(r)', 'v.', '(因寒冷、害怕或激动)发抖，打颤，战栗;强烈震动;剧烈抖动;', 'Lloyd had urged her to eat caviar. She had shuddered at the thought...', '劳埃德鼓动她吃鱼子酱。可她一想到这个就打哆嗦。');
INSERT INTO `words` VALUES (8065, 'shuffle', 'ˈʃʌfl', 'v.', '拖着脚走;(笨拙或尴尬地)把脚动来动去;坐立不安;', 'Moira shuffled across the kitchen...', '莫伊拉拖着脚穿过厨房。');
INSERT INTO `words` VALUES (8066, 'shun', 'ʃʌn', 'v.', '避开;回避;避免;', 'From that time forward everybody shunned him...', '从那时候起，每个人都有意回避他。');
INSERT INTO `words` VALUES (8067, 'shunt', 'ʃʌnt', 'v.', '使(火车或火车车厢)转轨;调往，转至(次要的地方);', 'He has spent most of his life being shunted between his mother, father and various foster families...', '他一生中的大部分时间都在他母亲、父亲及各种各样的寄养家庭之间辗转度过。');
INSERT INTO `words` VALUES (8068, 'shutdown', 'ˈʃʌtdaʊn', 'n.', '停业;停工;关闭;', 'The shutdown is the latest in a series of painful budget measures...', '此次关闭是一系列艰难预算措施中最新实施的一项。');
INSERT INTO `words` VALUES (8069, 'shutter', 'ˈʃʌtə(r)', 'n.', '活动护窗;百叶窗;(照相机的)快门;', 'There are a few things you should check before pressing the shutter release.', '按下快门之前有几个地方要检查一下。');
INSERT INTO `words` VALUES (8070, 'shy', 'ʃaɪ', 'adj.', '羞怯的;腼腆的;怕生的;', 'She was a shy, quiet-spoken girl...', '她是个害羞、说话轻声细语的女孩。');
INSERT INTO `words` VALUES (8071, 'shyly', '', 'adv.', '羞怯地，胆怯地，小心地;', 'She was a shy, quiet-spoken girl...', '她是个害羞、说话轻声细语的女孩。');
INSERT INTO `words` VALUES (8072, 'shyness', '\'ʃaɪnɪs', 'n.', '羞怯;', 'She was a shy, quiet-spoken girl...', '她是个害羞、说话轻声细语的女孩。');
INSERT INTO `words` VALUES (8073, 'sibilant', 'ˈsɪbɪlənt', 'adj.', '发咝咝声的;', 'A sibilant murmuring briefly pervaded the room.', '一时间房间里充满了窃窃私语声。');
INSERT INTO `words` VALUES (8074, 'sibling', 'ˈsɪblɪŋ', 'n.', '兄;弟;姐;', 'His siblings are mostly in their early twenties...', '他的兄弟姐妹大多二十出头。');
INSERT INTO `words` VALUES (8075, 'sick', 'sɪk', 'adj.', '(身体或精神)生病的，有病的;想呕吐;恶心;', 'He\'s very sick. He needs medication...', '他病得很厉害，需要药物治疗。');
INSERT INTO `words` VALUES (8076, 'sickbed', 'ˈsɪkbed', 'n.', '病床;', 'Michael left his sickbed to entertain his house guests.', '迈克尔走下病床去招待客人。');
INSERT INTO `words` VALUES (8077, 'sicken', 'ˈsɪkən', 'v.', '使大为震惊;使愤怒;患病;', 'The notion that art should be controlled by intellectuals sickened him...', '他讨厌艺术应由知识分子所掌控的观点。');
INSERT INTO `words` VALUES (8078, 'sickening', 'ˈsɪkənɪŋ', 'adj.', '让人厌恶的;令人作呕的;令人震惊的;', 'This was a sickening attack on a pregnant and defenceless woman...', '这一起对毫无防御能力的孕妇的袭击，令人无比震惊。');
INSERT INTO `words` VALUES (8079, 'sickly', 'ˈsɪkli', 'adj.', '常生病的;多病的;爱闹病的;', 'He had been a sickly child.', '他曾是个体弱多病的孩子。');
INSERT INTO `words` VALUES (8080, 'sickness', 'ˈsɪknəs', 'n.', '疾病;不健康;…病;', 'In fifty-two years of working he had one week of sickness...', '在52 年的工作中他仅病过一周。');
INSERT INTO `words` VALUES (8081, 'side', 'saɪd', 'n.', '(由想象的中线分出的)一边，一侧;(事物左方或右方的) 一旁，一边;侧面;', 'On one side of the main entrance there\'s a red plaque.', '大门口的一侧有块红色的匾额。');
INSERT INTO `words` VALUES (8082, 'sideline', 'ˈsaɪdlaɪn', 'n.', '兼职;副业;兼营业务;', 'It was quite a lucrative sideline...', '这份兼职很赚钱。');
INSERT INTO `words` VALUES (8083, 'sides', 'saɪdz', 'n.', '(由想象的中线分出的)一边，一侧;(事物左方或右方的) 一旁，一边;侧面;', 'On one side of the main entrance there\'s a red plaque.', '大门口的一侧有块红色的匾额。');
INSERT INTO `words` VALUES (8084, 'sideshow', 'ˈsaɪdʃəʊ', 'n.', '(游园会或马戏中穿插的)小节目，杂耍;次要活动;附带事件;', 'In the end, the meeting was a sideshow to a political storm that broke Thursday...', '最终，那次会议成了星期四爆发的政治风暴的一个附属事件。');
INSERT INTO `words` VALUES (8085, 'sidestep', 'ˈsaɪdstep', 'v.', '回避，规避(问题等);横跨一步躲过;侧移一步闪过;', 'Rarely, if ever, does he sidestep a question...', '他极少逃避问题。');
INSERT INTO `words` VALUES (8086, 'sidewalk', 'ˈsaɪdwɔːk', 'n.', '人行道;', 'Two men and a woman were walking briskly down the sidewalk toward him.', '两名男子和一名女子正轻快地沿人行道朝他走过去。');
INSERT INTO `words` VALUES (8087, 'sideways', 'ˈsaɪdweɪz', 'adv.', '往(或向、从)一侧;侧着;侧面朝前;', 'Piercey glanced sideways at her...', '皮尔西斜眼看了看她。');
INSERT INTO `words` VALUES (8088, 'sidle', 'ˈsaɪdl', 'v.', '犹犹豫豫地走;羞怯地走;悄悄地走;', 'A young man sidled up to me and said, \'May I help you?\'', '一个年轻人悄悄走向我说道，“我可以帮你吗？”');
INSERT INTO `words` VALUES (8089, 'siege', 'siːdʒ', 'n.', '(军队对城镇的)围困，包围，围攻，封锁;(警察对建筑物的)包围;', 'We must do everything possible to lift the siege...', '我们必须尽一切可能解除包围。');
INSERT INTO `words` VALUES (8090, 'siesta', 'siˈestə', 'n.', '(尤指在气候炎热的国家的)午睡，午休;', 'They have a siesta during the hottest part of the day.', '他们会在一天中最炎热的时刻小睡一会儿。');
INSERT INTO `words` VALUES (8091, 'sieve', 'sɪv', 'n.', '滤器;筛子;笊篱;', 'Press the raspberries through a fine sieve to form a puree.', '用一个细筛子将山莓压碎过滤，以制成山莓泥。');
INSERT INTO `words` VALUES (8092, 'sift', 'sɪft', 'v.', '筛(面粉或颗粒较细的物质);细查;详审;', 'Sift the flour and baking powder into a medium-sized mixing bowl.', '将面粉和发酵粉筛入中等大小的搅拌碗中。');
INSERT INTO `words` VALUES (8093, 'sigh', 'saɪ', 'v.', '叹气;叹息;叹着气说;', 'Michael sighed wearily...', '迈克尔疲惫地叹了口气。');
INSERT INTO `words` VALUES (8094, 'sighs', 'saɪz', 'v.', '叹气;叹息;叹着气说;', 'Michael sighed wearily...', '迈克尔疲惫地叹了口气。');
INSERT INTO `words` VALUES (8095, 'sight', 'saɪt', 'n.', '视力;视觉;看见;', 'My sight is failing, and I can\'t see to read any more...', '我的视力在下降，看不了书了。');
INSERT INTO `words` VALUES (8096, 'sighting', 'ˈsaɪtɪŋ', 'n.', '看见，目睹(不寻常或短暂出现的事物);', '...the sighting of a rare sea bird at Lundy island...', '在兰迪岛上看到一种珍稀海鸟');
INSERT INTO `words` VALUES (8097, 'sights', 'saɪts', 'n.', '视力;视觉;看见;', 'My sight is failing, and I can\'t see to read any more...', '我的视力在下降，看不了书了。');
INSERT INTO `words` VALUES (8098, 'sightseeing', 'ˈsaɪtsiːɪŋ', 'n.', '观光;游览;', '...a day\'s sight-seeing in Venice.', '威尼斯一日观光游');
INSERT INTO `words` VALUES (8099, 'sign', 'saɪn', 'n.', '迹象;征兆;预兆;', 'Equations are generally written with a two-bar equals sign.', '等式通常用两道横的等号来表示。');
INSERT INTO `words` VALUES (8100, 'signatory', 'ˈsɪɡnətri', 'n.', '(协议的)签署者，签署方，签署国;', 'Both countries are signatories to the Nuclear Non-Proliferation Treaty.', '两个国家都是《核不扩散条约》的签约国。');
INSERT INTO `words` VALUES (8101, 'signature', 'ˈsɪɡnətʃə(r)', 'n.', '签名;署名;签字;', 'I was writing my signature at the bottom of the page.', '我正往那页纸的底部签名。');
INSERT INTO `words` VALUES (8102, 'signed', 'saɪnd', 'v.', ' 签(名);署(名);签字;', 'Equations are generally written with a two-bar equals sign.', '等式通常用两道横的等号来表示。');
INSERT INTO `words` VALUES (8103, 'signer', 'ˈsaɪnə(r)', 'n.', '手语使用者;手语译员;', 'I\'m keen on providing signers for deaf people and readers for the blind.', '我热心于为聋哑人提供手语员，为盲人提供朗读员。');
INSERT INTO `words` VALUES (8104, 'significance', 'sɪɡˈnɪfɪkəns', 'n.', '(尤指对将来有影响的)重要性，意义;意思;含义;', 'Ideas about the social significance of religion have changed over time...', '关于宗教的社会意义的看法已经随着时间的变迁而改变。');
INSERT INTO `words` VALUES (8105, 'significant', 'sɪɡˈnɪfɪkənt', 'adj.', '有重大意义的;显著的;有某种意义的;', 'A small but significant number of 11-year-olds are illiterate.', '数量不多但仍有相当一部分 11 岁孩子是文盲。');
INSERT INTO `words` VALUES (8106, 'significantly', 'sɪɡˈnɪfɪkəntli', 'adv.', '有重大意义地;显著地;明显地;', 'A small but significant number of 11-year-olds are illiterate.', '数量不多但仍有相当一部分 11 岁孩子是文盲。');
INSERT INTO `words` VALUES (8107, 'signify', 'ˈsɪɡnɪfaɪ', 'v.', '表示;说明;预示;', 'The contrasting approaches to Europe signified a sharp difference between the major parties...', '对欧洲截然不同的态度表明两大政党间存在巨大差异。');
INSERT INTO `words` VALUES (8108, 'signing', 'ˈsaɪnɪŋ', 'n.', '签署;签字;(运动队、唱片公司或电影公司的)签约受聘者，签约受雇者;', 'Spain\'s top priority is the signing of an EMU treaty.', '西班牙的当务之急是签署《欧洲经济和货币联盟条约》。');
INSERT INTO `words` VALUES (8109, 'signpost', 'ˈsaɪnpəʊst', 'n.', '路标;', 'The lights of the car lit up a signpost...', '车灯照亮了路标。');
INSERT INTO `words` VALUES (8110, 'silence', 'ˈsaɪləns', 'n.', '寂静;无声;沉默;', 'They stood in silence...', '他们默不作声地站着。');
INSERT INTO `words` VALUES (8111, 'silent', 'ˈsaɪlənt', 'adj.', '不说话的;沉默的;很少说话的;', 'Trish was silent because she was reluctant to put her thoughts into words...', '特里西一直不说话，因为她不愿意吐露内心的想法。');
INSERT INTO `words` VALUES (8112, 'silently', 'ˈsaɪləntli', 'adv.', '默默地;不说话地;悄悄地;', 'Trish was silent because she was reluctant to put her thoughts into words...', '特里西一直不说话，因为她不愿意吐露内心的想法。');
INSERT INTO `words` VALUES (8113, 'silhouette', 'ˌsɪluˈet', 'n.', '(浅色背景衬托出的)暗色轮廓;(人的)体形;(事物的)形状;', 'The dark silhouette of the castle ruins stood out boldly against the fading light.', '城堡遗迹的黑色轮廓在暮色映衬下清晰可见。');
INSERT INTO `words` VALUES (8114, 'silicate', 'ˈsɪlɪkeɪt', 'n.', '硅酸盐;硅酸盐矿物;', '...large amounts of aluminum silicate.', '大量的硅酸铝');
INSERT INTO `words` VALUES (8115, 'silicon', 'ˈsɪlɪkən', 'n.', '硅;', '...a thin layer of silicon oxide...', '一层薄薄的氧化硅');
INSERT INTO `words` VALUES (8116, 'silk', 'sɪlk', 'n.', '(蚕)丝;丝织物;丝绸;', 'They continued to get their silks from China...', '他们继续从中国购买丝绸。');
INSERT INTO `words` VALUES (8117, 'silken', 'ˈsɪlkən', 'adj.', '丝绸一样的;柔软光洁的;柔和的;', '...her long silken hair.', '她那丝滑的长发');
INSERT INTO `words` VALUES (8118, 'silky', 'ˈsɪlki', 'adj.', '丝绸一样的;柔软光洁的;柔和的;', '...dresses in seductively silky fabrics...', '迷人的丝绸长裙');
INSERT INTO `words` VALUES (8119, 'sill', 'sɪl', 'n.', '(车体的)门槛;', 'Whitlock was perched on the sill of the room\'s only window.', '惠特洛克坐在房间里仅有的那扇窗户的边沿上。');
INSERT INTO `words` VALUES (8120, 'silly', 'ˈsɪli', 'adj.', '愚蠢的;不明事理的;没头脑的;', 'My best friend tells me that I am silly to be upset about this...', '我最好的朋友说我要是为这事而沮丧难过就真够傻的。');
INSERT INTO `words` VALUES (8121, 'silt', 'sɪlt', 'n.', '(沉积在河口或港口的)泥沙，淤泥，粉沙;', 'The lake was almost solid with silt and vegetation.', '湖里几乎快被淤泥和植物填满了。');
INSERT INTO `words` VALUES (8122, 'silver', 'ˈsɪlvə(r)', 'n.', '银;银币(银或似银金属制成的硬币);银器;', '...a hand-crafted brooch made from silver.', '一枚手工银制胸针');
INSERT INTO `words` VALUES (8123, 'silverware', 'ˈsɪlvəweə(r)', 'n.', '银器，镀银器皿(尤指餐具);(体育比赛中的)银杯;', 'There was a serving spoon missing when Nina put the silverware back in its box.', '尼娜将银餐具收拾到盒子里去的时候发现少了一把调羹。');
INSERT INTO `words` VALUES (8124, 'silvery', 'ˈsɪlvəri', 'adj.', '闪着银光的;银色的;银铃般的;', '...a small, intense man with silvery hair...', '满头银发、个子矮小的热情男子');
INSERT INTO `words` VALUES (8125, 'simian', 'ˈsɪmiən', 'adj.', '像猿(或猴)的;猿(或猴)的;', 'Ada had a wrinkled, simian face.', '埃达有一张布满皱纹、长得像猿猴的脸。');
INSERT INTO `words` VALUES (8126, 'similar', 'ˈsɪmələ(r)', 'adj.', '相像的;相仿的;类似的;', '...a savoury cake with a texture similar to that of carrot cake...', '口感和胡萝卜蛋糕相似的一种美味蛋糕');
INSERT INTO `words` VALUES (8127, 'similarity', 'ˌsɪməˈlærəti', 'n.', '相像性;相仿性;类似性;', 'The astonishing similarity between my brother and my first-born son...', '我弟弟和我的大儿子长得惊人地相似');
INSERT INTO `words` VALUES (8128, 'similarly', 'ˈsɪmələli', 'adv.', '相似地;类似地;差不多地;', 'Most of the men who now gathered round him again were similarly dressed.', '现在再次聚集到他周围的人穿戴大都差不多。');
INSERT INTO `words` VALUES (8129, 'simmer', 'ˈsɪmə(r)', 'v.', '用文火炖;煨;充满(难以控制的感情，尤指愤怒);', 'Make an infusion by boiling and simmering the rhubarb and camomile together...', '将大黄和甘菊一起煮沸再用文火炖,熬成汤药。');
INSERT INTO `words` VALUES (8130, 'simper', 'ˈsɪmpə(r)', 'v.', '矫揉造作地笑;扭捏作态地笑;', 'The maid lowered her chin and simpered.', '侍女低头痴笑着。');
INSERT INTO `words` VALUES (8131, 'simpering', 'ˈsɪmpərɪŋ', 'v.', '矫揉造作地笑;扭捏作态地笑;', 'The maid lowered her chin and simpered.', '侍女低头痴笑着。');
INSERT INTO `words` VALUES (8132, 'simple', 'ˈsɪmpl', 'adj.', '易于理解的;易做的;简单的;', '...simple pictures and diagrams.', '简单的图画和图表');
INSERT INTO `words` VALUES (8133, 'simple-minded', 'ˌsɪmpl ˈmaɪndɪd', 'adj.', '智力低下的;愚蠢的;头脑简单的;', 'Sylvie was a simple-minded romantic...', '西尔维是个天真的爱幻想的人。');
INSERT INTO `words` VALUES (8134, 'simpleton', 'ˈsɪmpltən', 'n.', '傻瓜;易上当受骗的人;', '\'But Ian\'s such a simpleton\', she laughed...', '“可是伊恩太傻了，”她笑着说道。');
INSERT INTO `words` VALUES (8135, 'simplicity', 'sɪmˈplɪsəti', 'n.', '简单(性);容易(性);质朴;', 'The apparent simplicity of his plot is deceptive...', '他的故事情节貌似简单，其实不然。');
INSERT INTO `words` VALUES (8136, 'simplification', 'ˌsɪmplɪfɪˈkeɪʃn', 'n.', '简化;简化的事物;', 'Like any such diagram, it is a simplification.', '像其他这种表格一样，这是简化了的。');
INSERT INTO `words` VALUES (8137, 'simplified', 'ˈsɪmplɪfaɪd', 'v.', '使简化;使简易;', '...a plan to simplify the complex social security system...', '简化复杂的社会保障体系的计划');
INSERT INTO `words` VALUES (8138, 'simplify', 'ˈsɪmplɪfaɪ', 'v.', '使简化;使简易;', '...a plan to simplify the complex social security system...', '简化复杂的社会保障体系的计划');
INSERT INTO `words` VALUES (8139, 'simplistic', 'sɪmˈplɪstɪk', 'adj.', '(把问题、局面等)过分简单化的;', 'He has a simplistic view of the treatment of eczema...', '他对湿疹治疗的看法过于简单化。');
INSERT INTO `words` VALUES (8140, 'simply', 'ˈsɪmpli', 'adv.', '(强调简单)仅仅，只，不过;(强调某说法)确实，简直;简单地;', 'The table is simply a chipboard circle on a base...', '这张桌子只不过是在底座上安了一块圆形刨花板。');
INSERT INTO `words` VALUES (8141, 'simulate', 'ˈsɪmjuleɪt', 'v.', '假装;冒充;装作;', 'They rolled about on the Gilligan Road, simulating a bloodthirsty fight...', '他们在吉利根路上翻滚，模拟一场暴力厮杀。');
INSERT INTO `words` VALUES (8142, 'simulated', 'ˈsɪmjuleɪtɪd', 'adj.', '假装的;仿造的;模拟的;', 'They rolled about on the Gilligan Road, simulating a bloodthirsty fight...', '他们在吉利根路上翻滚，模拟一场暴力厮杀。');
INSERT INTO `words` VALUES (8143, 'simulation', 'ˌsɪmjuˈleɪʃn', 'n.', '模拟;仿真;假装;', 'Training includes realistic simulation of casualty procedures.', '培训包括对急救过程的真实模拟。');
INSERT INTO `words` VALUES (8144, 'simulator', 'ˈsɪmjuleɪtə(r)', 'n.', '模拟装置;', '...pilots practising a difficult landing in a flight simulator.', '在飞行模拟装置中练习高难度着陆的飞行员');
INSERT INTO `words` VALUES (8145, 'simultaneous', 'ˌsɪmlˈteɪniəs', 'adj.', '同时发生(或进行)的;同步的;', '...the simultaneous release of the book and the album...', '书和唱片专辑的同步发行');
INSERT INTO `words` VALUES (8146, 'simultaneously', 'ˌsɪməlˈteɪniəsli', 'adv.', '同时;联立;急切地;', '...the simultaneous release of the book and the album...', '书和唱片专辑的同步发行');
INSERT INTO `words` VALUES (8147, 'sin', 'sɪn', 'n.', '罪，罪恶，罪过(对神的违犯或对宗教戒律、道德规范的违犯);罪行，犯罪(违犯宗教戒律、道德规范的行为);过错;', 'The Vatican\'s teaching on abortion is clear: it is a sin...', '罗马教廷对堕胎的教义非常清楚：这是罪过。');
INSERT INTO `words` VALUES (8148, 'since', 'sɪns', 'prep.', '自…以后;从…以来;(表示气愤)何曾，什么时候;', 'Jacques Arnold has been a member of parliament since 1987...', '雅克·阿诺德自 1987 年以来一直是议会议员。');
INSERT INTO `words` VALUES (8149, 'sincere', 'sɪnˈsɪə(r)', 'adj.', '真诚的;诚挚的;诚恳的;', 'He\'s sincere in his views...', '他的观点很诚恳。');
INSERT INTO `words` VALUES (8150, 'sincerely', 'sɪnˈsɪəli', 'adv.', '真诚地;诚实地;', '\'Congratulations,\' he said sincerely...', '“祝贺你，”他真诚地说。');
INSERT INTO `words` VALUES (8151, 'sincerity', 'sɪn\'serəti', 'n.', '诚意;真挚;纯粹;', 'He\'s sincere in his views...', '他的观点很诚恳。');
INSERT INTO `words` VALUES (8152, 'sinecure', 'ˈsɪnɪkjʊə(r)', 'n.', '闲职;挂名职位;', 'She found him an exalted sinecure as a Fellow of the Library of Congress.', '她给他找了一个级别很高的闲职：国会图书馆研究员。');
INSERT INTO `words` VALUES (8153, 'sinew', 'ˈsɪnjuː', 'n.', '肌腱;力量的来源;关键环节;', '...the sinews of the neck.', '脖子上的肌腱');
INSERT INTO `words` VALUES (8154, 'sinewy', 'ˈsɪnjuːi', 'adj.', '肌肉发达的;矫健的;强健的;', 'A short, sinewy young man...', '一名个子不高、肌肉发达的年轻男子');
INSERT INTO `words` VALUES (8155, 'sinful', 'ˈsɪnfl', 'adj.', '不道德的;邪恶的;', '\'I am a sinful man,\' he said.', '“我是个邪恶的人，”他说。');
INSERT INTO `words` VALUES (8156, 'sing', 'sɪŋ', 'v.', '唱(歌);演唱;鸣;', 'I can\'t sing...', '我不会唱歌。');
INSERT INTO `words` VALUES (8157, 'singe', 'sɪndʒ', 'v.', '(尤指不小心)烤焦，烫煳;', 'The electric fire had begun to singe the bottoms of his trousers...', '电暖炉的火刚开始烧着他的裤腿。');
INSERT INTO `words` VALUES (8158, 'singer', 'ˈsɪŋə(r)', 'n.', '唱歌的人;歌唱家;歌手;', 'My mother was a singer in a dance band.', '我妈妈是一个舞蹈乐队的歌手。');
INSERT INTO `words` VALUES (8159, 'singing', 'ˈsɪŋɪŋ', 'n.', '唱歌;歌唱;', '...a people\'s carnival, with singing and dancing in the streets.', '人们在街头载歌载舞的民族狂欢节');
INSERT INTO `words` VALUES (8160, 'single', 'ˈsɪŋɡl', 'adj.', '仅有一个的;单一的;单个的;', 'A single shot rang out...', '传来一声响亮的枪声。');
INSERT INTO `words` VALUES (8161, 'single-handed', 'ˌsɪŋɡl ˈhændɪd', 'adv.', '独自;单枪匹马地;独立;', 'I brought up my seven children single-handed...', '我独自一人抚养了 7 个孩子。');
INSERT INTO `words` VALUES (8162, 'singsong', 'ˈsɪŋsɒŋ', 'n.', '(众人自娱自乐的)合唱，歌唱会;', 'He started to speak in a nasal sing-song voice.', '他开始用一种带有鼻音的、抑扬顿挫的声调说话。');
INSERT INTO `words` VALUES (8163, 'singular', 'ˈsɪŋɡjələ(r)', 'n.', '单数;单数形式;', '...the fifteen case endings of the singular form of the Finnish noun...', '芬兰语的名词单数形式的 15 种格尾');
INSERT INTO `words` VALUES (8164, 'singularity', 'ˌsɪŋɡjuˈlærəti', 'n.', '奇特;奇怪;异常;', '...the fifteen case endings of the singular form of the Finnish noun...', '芬兰语的名词单数形式的 15 种格尾');
INSERT INTO `words` VALUES (8165, 'singularly', 'ˈsɪŋɡjələli', 'adv.', '非常;特别;异常地;', '...the fifteen case endings of the singular form of the Finnish noun...', '芬兰语的名词单数形式的 15 种格尾');
INSERT INTO `words` VALUES (8166, 'sinister', 'ˈsɪnɪstə(r)', 'adj.', '邪恶的;险恶的;不祥的;', 'There was something sinister about him that she found disturbing.', '他身上有种邪恶的东西让她很不安。');
INSERT INTO `words` VALUES (8167, 'sink', 'sɪŋk', 'v.', '下沉;下陷;沉没;', 'The sink was full of dirty dishes.', '水槽里堆满了脏碟子。');
INSERT INTO `words` VALUES (8168, 'sinker', 'ˈsɪŋkə(r)', 'n.', '(钓丝或渔网上的)铅锤，坠子;', 'We fell for it hook, line, and sinker...', '我们对它完全是一见倾心。');
INSERT INTO `words` VALUES (8169, 'sinner', 'ˈsɪnə(r)', 'n.', '罪人;', 'The Vatican\'s teaching on abortion is clear: it is a sin...', '罗马教廷对堕胎的教义非常清楚：这是罪过。');
INSERT INTO `words` VALUES (8170, 'sinuous', 'ˈsɪnjuəs', 'adj.', '弯曲有致的;蜿蜒的;', '...the silent, sinuous approach of a snake through the long grass...', '一条蛇灵活地扭动身躯，穿过高高的草丛悄悄地靠近');
INSERT INTO `words` VALUES (8171, 'sip', 'sɪp', 'v.', '小口喝;抿;', 'Jessica sipped her drink thoughtfully...', '杰茜卡小口地喝着饮料，若有所思。');
INSERT INTO `words` VALUES (8172, 'siphon', 'ˈsaɪfn', 'n.', '虹吸管;', 'She puts a piece of plastic tubing in her mouth and starts siphoning gas from a huge metal drum...', '她把一根塑料管放进嘴里，开始从一个巨大的金属桶里吸气。');
INSERT INTO `words` VALUES (8173, 'sir', 'sɜː(r)', 'n.', '(对不认识的男性的尊称)先生;(正式信函中对不知其名的男性收信人的称呼)先生，阁下;(贵族头衔，用于爵士或准男爵的名字或姓名前面)爵士;', 'Excuse me sir, but would you mind telling me what sort of car that is?...', '先生，请您告诉我那是什么类型的车好吗？');
INSERT INTO `words` VALUES (8174, 'sire', 'ˈsaɪə(r)', 'n.', '雄性种兽;(尤指)公种马;(旧时对国王的称呼)陛下;', 'Comet also sired the champion foal out of Spinway Harvest.', '“彗星”还跟“斯平威·哈维斯特”生下了这匹出类拔萃的小马驹。');
INSERT INTO `words` VALUES (8175, 'siren', 'ˈsaɪrən', 'n.', '汽笛;警报器;塞壬(古希腊神话中半人半鸟或半人半鱼的女海妖，以美妙歌声诱使航海者驶向礁石或进入危险水域);', 'It sounds like an air raid siren.', '那听起来像是空袭警报。');
INSERT INTO `words` VALUES (8176, 'sirloin', 'ˈsɜːlɔɪn', 'n.', '牛里脊肉;牛上腰肉;', '...sirloin steaks.', '里脊牛排');
INSERT INTO `words` VALUES (8177, 'sister', 'ˈsɪstə(r)', 'n.', '姐;妹;(称志同道合者)姐妹;', 'His sister Sarah helped him.', '他姐姐萨拉帮助了他。');
INSERT INTO `words` VALUES (8178, 'sit', 'sɪt', 'v.', '坐;使坐;使就座;', 'Mother was sitting in her chair in the kitchen...', '妈妈坐在厨房里的椅子上。');
INSERT INTO `words` VALUES (8179, 'sitcom', 'ˈsɪtkɒm', 'n.', '情景喜剧;', '...the classic \'70s TV sitcom \'Rising Damp\'.', '70 年代的经典情景喜剧《旧楼记》');
INSERT INTO `words` VALUES (8180, 'site', 'saɪt', 'n.', '(建筑物、城镇等的)地点，位置，建筑工地;现场;发生地;', 'He became a hod carrier on a building site.', '他在建筑工地上当起了小工。');
INSERT INTO `words` VALUES (8181, 'sit-in', 'ˈsɪt ɪn', 'n.', '(建筑物内的)静坐罢工，静坐示威;', 'The campaigners held a sit-in outside the Supreme Court.', '运动参与者在最高法院门外举行了静坐示威。');
INSERT INTO `words` VALUES (8182, 'sitting', 'ˈsɪtɪŋ', 'n.', '(法院或议会的)开会，会议，开庭(期间);(饭店等)一批，一次(顾客就餐时间);一次(坐着活动的时间);', 'Dinner was in two sittings.', '分两批吃晚饭。');
INSERT INTO `words` VALUES (8183, 'situate', 'ˈsɪtʃueɪt', 'v.', '使位于;使坐落于;将…置于;', 'How do we situate Christianity in the context of modern physics and psychology?', '我们怎样把基督教放在现代物理学和心理学的背景下来考虑呢？');
INSERT INTO `words` VALUES (8184, 'situated', 'ˈsɪtʃueɪtɪd', 'adj.', '位于;坐落在;处于…状况;', 'His hotel is situated in one of the loveliest places on the Loire...', '他下榻的旅馆位于卢瓦河畔最漂亮的地方之一。');
INSERT INTO `words` VALUES (8185, 'situation', 'ˌsɪtʃuˈeɪʃn', 'n.', '情况;状况;形势;', 'Army officers said the situation was under control...', '陆军军官称局面已经得到了控制。');
INSERT INTO `words` VALUES (8186, 'six', 'sɪks', 'num.', '六;', '...a glorious career spanning more than six decades.', '延续 60 余载的辉煌事业');
INSERT INTO `words` VALUES (8187, 'sixteen', 'ˌsɪksˈtiːn', 'num.', '十六;', '...exams taken at the age of sixteen...', '16 岁时参加的考试');
INSERT INTO `words` VALUES (8188, 'sixteenth', 'ˌsɪksˈtiːnθ', 'ord.', '第十六;', '...the sixteenth century AD.', '公元 16 世纪');
INSERT INTO `words` VALUES (8189, 'sixth', 'sɪksθ', 'ord.', '第六;', '...the sixth round of the World Cup.', '世界杯第六轮的比赛');
INSERT INTO `words` VALUES (8190, 'sixtieth', 'ˈsɪkstiəθ', 'ord.', '第六十;', 'He is to retire on his sixtieth birthday.', '他准备在 60 岁生日那天退休。');
INSERT INTO `words` VALUES (8191, 'sixty', 'ˈsɪksti', 'num.', '六十;六十几;六十年代;', '...the sunniest April in Britain for more than sixty years.', '英国 60 多年以来阳光最灿烂的4月');
INSERT INTO `words` VALUES (8192, 'size', 'saɪz', 'n.', '大小;大量;大规模;', 'Scientists have found the bones of a hoofed grazing animal about the size of a small horse...', '科学家们已经发现了一种约有马驹般大小的有蹄草食性动物的骨骼。');
INSERT INTO `words` VALUES (8193, 'sizeable', 'ˈsaɪzəbl', 'adj.', '相当大的;', 'Harry inherited the house and a sizeable chunk of land.', '哈里继承了这座房子以及相当大的一片土地。');
INSERT INTO `words` VALUES (8194, 'sizzle', 'ˈsɪzl', 'v.', '发出(油煎食物的)咝咝声;', 'The sausages and burgers sizzled on the barbecue.', '香肠和牛肉饼在烤架上咝咝作响。');
INSERT INTO `words` VALUES (8195, 'skein', 'skeɪn', 'n.', '一束，一绞(毛线、线或纱);', '...a skein of wool.', '一束羊毛');
INSERT INTO `words` VALUES (8196, 'skeletal', 'ˈskelətl', 'adj.', '骨骼的;骨瘦如柴的;梗概的;', '...the skeletal remains of seven adults.', '7 具成人遗骸');
INSERT INTO `words` VALUES (8197, 'skeleton', 'ˈskelɪtn', 'n.', '骨骼;骨架;骨骼标本;', '...a human skeleton.', '人体骨骼');
INSERT INTO `words` VALUES (8198, 'sketch', 'sketʃ', 'n.', '素描;速写;草图;', '...a sketch of a soldier by Orpen.', '奥彭所画的一幅士兵素描');
INSERT INTO `words` VALUES (8199, 'sketchy', 'ˈsketʃi', 'adj.', '粗略的;概略的;不完备的;', 'Details of what actually happened are still sketchy...', '对于已发生事实的详细情况知道的仍然有限。');
INSERT INTO `words` VALUES (8200, 'skew', 'skjuː', 'v.', '歪曲;曲解;使不公允;', 'The arithmetic of nuclear running costs has been skewed by the fall in the cost of other fuels...', '核运营成本的计算由于其他燃料的降价而出现了偏差。');
INSERT INTO `words` VALUES (8201, 'ski', 'skiː', 'n.', '滑雪板;', '...a pair of skis.', '一副滑雪板');
INSERT INTO `words` VALUES (8202, 'skid', 'skɪd', 'v.', '侧滑;打滑;滑行;', 'The car pulled up too fast and skidded on the dusty shoulder of the road...', '由于刹车太急，车子侧滑到了满是尘土的路肩上。');
INSERT INTO `words` VALUES (8203, 'skiing', 'ˈskiːɪŋ', 'n.', '滑雪(运动);', '...a pair of skis.', '一副滑雪板');
INSERT INTO `words` VALUES (8204, 'skill', 'skɪl', 'n.', '技巧;技艺;技术;', 'Most of us will know someone who is always learning new skills, or studying new fields.', '我们大多数人都会认识某个总是在不停地学习新技术或研究新领域的人。');
INSERT INTO `words` VALUES (8205, 'skilled', 'skɪld', 'adj.', '有技能的;熟练的;需要专门技术的;', 'Not all doctors are skilled in helping their patients make choices.', '并非所有医生都善于帮助病人作出正确选择。');
INSERT INTO `words` VALUES (8206, 'skim', 'skɪm', 'v.', '撇去(液体上的油脂或乳脂等);(使)掠过，擦过，滑过;浏览;', 'Rough seas today prevented specially equipped ships from skimming oil off the water\'s surface...', '今天海面上风浪很大，那些配有专门设备的船只无法展开清除海面浮油的工作。');
INSERT INTO `words` VALUES (8207, 'skimming', 'ˈskɪmɪŋ', 'v.', '撇去(液体上的油脂或乳脂等);(使)掠过，擦过，滑过;浏览;', 'Rough seas today prevented specially equipped ships from skimming oil off the water\'s surface...', '今天海面上风浪很大，那些配有专门设备的船只无法展开清除海面浮油的工作。');
INSERT INTO `words` VALUES (8208, 'skimp', 'skɪmp', 'v.', '节省，吝惜(时间、钱等);', 'Many families must skimp on their food and other necessities just to meet the monthly rent.', '就为了交齐每月的租金，许多家庭不得不省吃俭用。');
INSERT INTO `words` VALUES (8209, 'skimpy', 'ˈskɪmpi', 'adj.', '小而暴露的;(数量或大小)不足的，不够的;', '...skimpy underwear...', '太过暴露的内衣');
INSERT INTO `words` VALUES (8210, 'skin', 'skɪn', 'n.', '皮;皮肤;…皮肤的;', 'His skin is clear and smooth...', '他的皮肤光洁平滑。');
INSERT INTO `words` VALUES (8211, 'skinny', 'ˈskɪni', 'adj.', '极瘦的;干瘦的;皮包骨的;', 'He was quite a skinny little boy...', '他是个瘦得皮包骨头的小男孩。');
INSERT INTO `words` VALUES (8212, 'skip', 'skɪp', 'v.', '蹦蹦跳跳地走;跳绳;不做(应做的事等);', 'They saw the man with a little girl skipping along behind him...', '他们看见那个男人身后还跟着一个蹦蹦跳跳的小姑娘。');
INSERT INTO `words` VALUES (8213, 'skipper', 'ˈskɪpə(r)', 'n.', '(小船或渔船的)船长;(运动队的)队长;', '...the skipper of an English fishing boat...', '一艘英国渔船的船长');
INSERT INTO `words` VALUES (8214, 'skipping', 'ˈskɪpɪŋ', 'v.', '蹦蹦跳跳地走;跳绳;不做(应做的事等);', 'They saw the man with a little girl skipping along behind him...', '他们看见那个男人身后还跟着一个蹦蹦跳跳的小姑娘。');
INSERT INTO `words` VALUES (8215, 'skirmish', 'ˈskɜːmɪʃ', 'n.', '小规模战斗;小冲突;(尤指)遭遇战;', 'Border skirmishes between India and Pakistan were common.', '印度和巴基斯坦边境上常常发生小规模战斗。');
INSERT INTO `words` VALUES (8216, 'skit', 'skɪt', 'n.', '幽默短文，滑稽短剧，幽默讽刺小品(常用模仿手法);', '...clever skits on popular songs.', '巧妙地讽刺了流行歌曲的滑稽短剧');
INSERT INTO `words` VALUES (8217, 'skittish', 'ˈskɪtɪʃ', 'adj.', '易惊而难以驾驭的;轻浮的;易变的;', 'The declining dollar gave heart to skittish investors.', '美元的衰落给总是担惊受怕的投资者们带来了信心。');
INSERT INTO `words` VALUES (8218, 'skive', 'skaɪv', 'v.', '躲避(工作);逃(学);', 'The company treated me as though I were skiving.', '公司对待我的态度就好像是我上班时溜号了似的。');
INSERT INTO `words` VALUES (8219, 'skulduggery', 'skʌlˈdʌɡəri', 'n.', '阴谋诡计;花招;欺骗;', '...accusations of political skulduggery.', '指控其搞政治阴谋');
INSERT INTO `words` VALUES (8220, 'skulk', 'skʌlk', 'v.', '潜伏;偷偷摸摸地走动;鬼鬼祟祟地活动;', 'You, meanwhile, will be skulking in the safety of the car...', '与此同时，你会安全地藏在汽车里。');
INSERT INTO `words` VALUES (8221, 'skull', 'skʌl', 'n.', '颅骨;头(盖)骨;脑袋;', 'Her husband was later treated for a fractured skull.', '她丈夫后来接受了颅骨骨折治疗。');
INSERT INTO `words` VALUES (8222, 'sky', 'skaɪ', 'n.', '天;天空;', 'The sun is already high in the sky.', '已经日上三竿。');
INSERT INTO `words` VALUES (8223, 'skyline', 'ˈskaɪlaɪn', 'n.', '(建筑物、树、山等在天空映衬下的)轮廓线;', 'The village church dominates the skyline.', '乡村教堂在天空映衬下的轮廓非常醒目。');
INSERT INTO `words` VALUES (8224, 'skyrocket', 'ˈskaɪrɒkɪt', 'v.', '飞涨;猛涨;', 'Production has dropped while prices and unemployment have skyrocketed.', '物价和失业率猛涨的同时，生产却下降了。');
INSERT INTO `words` VALUES (8225, 'slab', 'slæb', 'n.', '(石、木等坚硬物质的)厚板;厚片;厚块;', '...slabs of stone.', '厚重的石板');
INSERT INTO `words` VALUES (8226, 'slack', 'slæk', 'adj.', '不紧的;松弛的;萧条的;', 'The boy\'s jaw went slack.', '男孩张大了嘴巴。');
INSERT INTO `words` VALUES (8227, 'slacken', 'ˈslækən', 'v.', '(使)放慢，减缓，萧条;(使)变得松弛;', 'Inflationary pressures continued to slacken last month...', '上个月，通货膨胀的压力继续减小。');
INSERT INTO `words` VALUES (8228, 'slacker', 'ˈslækə(r)', 'n.', '偷懒的人;怠惰的人;', 'He\'s not a slacker, he\'s the best worker they\'ve got.', '他可不是个会偷懒的人，他是他们最好的工人。');
INSERT INTO `words` VALUES (8229, 'slake', 'sleɪk', 'v.', '缓和(口渴);解(渴);满足(欲望);', 'I slaked my thirst with three cans of Coke.', '我喝了 3 罐可乐解渴。');
INSERT INTO `words` VALUES (8230, 'slam', 'slæm', 'v.', '(使…)砰地关上;用力一放;使劲一推;', 'She slammed the door and locked it behind her...', '她砰的一下关上门，随手锁上了。');
INSERT INTO `words` VALUES (8231, 'slander', 'ˈslɑːndə(r)', 'n.', '口头诽谤;口头诽谤罪;', 'Dr. Bach is now suing the company for slander...', '巴赫博士目前正以诽谤罪起诉该公司。');
INSERT INTO `words` VALUES (8232, 'slanderous', 'ˈslɑːndərəs', 'adj.', '诋毁性的;诽谤性的;中伤的;', 'Herr Kohler wanted an explanation for what he described as \'slanderous\' remarks.', '赫尔·科勒希望有人能够为那些他称之为“造谣中伤”的言辞作出解释。');
INSERT INTO `words` VALUES (8233, 'slang', 'slæŋ', 'n.', '俚语;', 'Archie liked to think he kept up with current slang.', '阿奇喜欢那种紧跟潮流，讲满口时髦新词的感觉。');
INSERT INTO `words` VALUES (8234, 'slant', 'slɑːnt', 'v.', '(使)倾斜，歪斜;有倾向性地陈述;有偏向地报道;', 'The morning sun slanted through the glass roof...', '清晨的阳光透过玻璃屋顶，斜照了进来。');
INSERT INTO `words` VALUES (8235, 'slap', 'slæp', 'v.', '(用手掌)打，拍，掴;(尤指生气地)啪的一声放下，随意扔放;啪地击打(或撞上);', 'He would push or slap her once in a while...', '他偶尔会推搡她或是打她的耳光。');
INSERT INTO `words` VALUES (8236, 'slapdash', 'ˈslæpdæʃ', 'adj.', '仓促马虎的;毛躁的;潦草的;', '...a slapdash student...', '粗心大意的学生');
INSERT INTO `words` VALUES (8237, 'slapstick', 'ˈslæpstɪk', 'n.', '打闹剧;粗俗滑稽剧;', '...slapstick comedy.', '粗俗的滑稽喜剧');
INSERT INTO `words` VALUES (8238, 'slash', 'slæʃ', 'v.', '(用利器)砍，劈;大幅度削减;大大降低;', 'He came within two minutes of bleeding to death after slashing his wrists.', '若再晚两分钟割腕的他就会因失血过多而死。');
INSERT INTO `words` VALUES (8239, 'slate', 'sleɪt', 'n.', '板岩;石板;(盖房顶的)石板瓦;', '... a stone-built cottage, with a traditional slate roof.', '传统石板瓦屋顶的石屋');
INSERT INTO `words` VALUES (8240, 'slaughter', 'ˈslɔːtə(r)', 'n.', '屠宰;宰杀;(尤指战争中的)屠杀，杀戮;', 'Thirty four people were slaughtered while queuing up to cast their votes...', '有 34 人在排队投票时遭到残杀。');
INSERT INTO `words` VALUES (8241, 'slave', 'sleɪv', 'n.', '奴隶;完全受(某事物)控制的人;完全依赖(某事物)的人;', 'The state of Liberia was formed a century and a half ago by freed slaves from the United States.', '150年前，从美国获得了自由归来的奴隶建立了利比里亚。');
INSERT INTO `words` VALUES (8242, 'slaver', 'ˈslævə(r) , ˈsleɪvə(r)', 'v.', '(尤指因饥饿或兴奋)流口水，垂涎;', 'Mad guard dogs slavered at the end of their chains.', '拴在链子上的看门狗狂躁地流着口水。');
INSERT INTO `words` VALUES (8243, 'slavery', 'ˈsleɪvəri', 'n.', '奴隶身份;奴隶制;蓄奴;', 'My people have survived 400 years of slavery.', '我们的人民从 400 年的奴隶制中挺了过来。');
INSERT INTO `words` VALUES (8244, 'slay', 'sleɪ', 'v.', '(在战争或搏斗中)杀，杀死;杀害;残害;', '...the hill where St George slew the dragon.', '圣乔治屠龙的那座山');
INSERT INTO `words` VALUES (8245, 'sleazy', 'ˈsliːzi', 'adj.', '(尤指涉及性行为的)肮脏的，污秽的，乌烟瘴气的，藏污纳垢的;不正派的;道德败坏的;', '...sleazy bars.', '破破烂烂的酒吧');
INSERT INTO `words` VALUES (8246, 'sledge', 'sledʒ', 'n.', '雪橇;', 'She travelled 14,000 miles by sledge across Siberia to Kamchatka.', '她乘雪橇穿越西伯利亚到达了堪察加半岛，行程共 1.4 万英里。');
INSERT INTO `words` VALUES (8247, 'sleek', 'sliːk', 'adj.', '光滑的;光亮的;线条流畅的;', '...sleek black hair...', '乌黑闪亮的头发');
INSERT INTO `words` VALUES (8248, 'sleep', 'sliːp', 'v.', '睡;睡觉;入睡;', 'They were exhausted from lack of sleep...', '由于缺乏睡眠，他们非常疲惫。');
INSERT INTO `words` VALUES (8249, 'sleeper', 'ˈsliːpə(r)', 'n.', '睡得…的人;睡觉者;睡眠者;', 'I\'m a very light sleeper and I can hardly get any sleep at all...', '我睡觉很轻，几乎就没睡着。');
INSERT INTO `words` VALUES (8250, 'sleepily', '', 'adv.', '想睡地，疲倦地;', 'I was beginning to feel amazingly sleepy...', '我开始感到异常困倦。');
INSERT INTO `words` VALUES (8251, 'sleeping', 'ˈsliːpɪŋ', 'adj.', '同住宿有关的;睡觉的;', 'On the top floor we have sleeping quarters for women and children.', '我们在顶楼有供女人和孩子睡觉的地方。');
INSERT INTO `words` VALUES (8252, 'sleepless', 'ˈsliːpləs', 'adj.', '没有睡觉的;不眠的;睡不着;', 'I have sleepless nights worrying about her.', '我好几个晚上都睡不着，一直担心她。');
INSERT INTO `words` VALUES (8253, 'sleeplessness', '', 'n.', '失眠，警觉;', 'I have sleepless nights worrying about her.', '我好几个晚上都睡不着，一直担心她。');
INSERT INTO `words` VALUES (8254, 'sleepwalk', 'ˈsliːpwɔːk', 'v.', '梦游;', 'He once sleepwalked to the middle of the road outside his home at 1 a.m.', '有一次，他在凌晨1点钟梦游走到了家门外的马路中央。');
INSERT INTO `words` VALUES (8255, 'sleepwalking', 'ˈsliːpwɔːkɪŋ', 'v.', '梦游;', 'He once sleepwalked to the middle of the road outside his home at 1 a.m.', '有一次，他在凌晨1点钟梦游走到了家门外的马路中央。');
INSERT INTO `words` VALUES (8256, 'sleepy', 'ˈsliːpi', 'adj.', '困倦的;瞌睡的;安静的;', 'I was beginning to feel amazingly sleepy...', '我开始感到异常困倦。');
INSERT INTO `words` VALUES (8257, 'sleet', 'sliːt', 'n.', '雨夹雪;', '...blinding snow, driving sleet and wind.', '漫天大雪、猛烈的冻雨和大风');
INSERT INTO `words` VALUES (8258, 'sleeve', 'sliːv', 'n.', '袖子;有…袖子的;唱片套;', 'His sleeves were rolled up to his elbows...', '他的袖子卷到了肘部。');
INSERT INTO `words` VALUES (8259, 'sleeves', 'sliːvz', 'n.', '袖子;有…袖子的;唱片套;', 'His sleeves were rolled up to his elbows...', '他的袖子卷到了肘部。');
INSERT INTO `words` VALUES (8260, 'slender', 'ˈslendə(r)', 'adj.', '苗条的;纤细的;细的;', 'She was slender, with delicate wrists and ankles.', '她身量苗条，手腕和脚踝曲线玲珑。');
INSERT INTO `words` VALUES (8261, 'slice', 'slaɪs', 'n.', '(切下的食物)薄片，片;部分;份额;', 'Try to eat at least four slices of bread a day.', '每天尽量至少吃 4 片面包。');
INSERT INTO `words` VALUES (8262, 'slick', 'slɪk', 'adj.', '华而不实的;虚有其表的;取巧的;', 'There\'s a big difference between an amateur video and a slick Hollywood production...', '业余人士拍摄的视频片段和制作精良的好莱坞影片之间存在着巨大的差距。');
INSERT INTO `words` VALUES (8263, 'slid', 'slɪd', 'v.', '(使)滑行，滑动;(使)快捷而悄声地移动;逐渐降低;', 'She slid the door open...', '她滑开了门。');
INSERT INTO `words` VALUES (8264, 'slide', 'slaɪd', 'v.', '(使)滑行，滑动;(使)快捷而悄声地移动;逐渐降低;', 'She slid the door open...', '她滑开了门。');
INSERT INTO `words` VALUES (8265, 'slight', 'slaɪt', 'adj.', '轻微的;略微的;细小的;', 'Doctors say he has made a slight improvement...', '医生说他的病情稍有好转。');
INSERT INTO `words` VALUES (8266, 'slightly', 'ˈslaɪtli', 'adv.', '略微;稍微;身材瘦小的;', 'His family then moved to a slightly larger house...', '然后他全家搬到了一个稍微大一点的房子里。');
INSERT INTO `words` VALUES (8267, 'slim', 'slɪm', 'adj.', '苗条的;纤细的;单薄的;', 'The young woman was tall and slim...', '那个年轻的女子身材苗条修长。');
INSERT INTO `words` VALUES (8268, 'slime', 'slaɪm', 'n.', '污浊的泥浆;(令人不快的)黏液;', 'There was an unappealing film of slime on top of the pond.', '池塘表面有一层黏腻腻的污泥。');
INSERT INTO `words` VALUES (8269, 'slimy', 'ˈslaɪmi', 'adj.', '似泥浆的;粘有黏液的;谄媚的;', 'His feet slipped in the slimy mud...', '他的脚在泥泞中不住地打滑。');
INSERT INTO `words` VALUES (8270, 'sling', 'slɪŋ', 'v.', '(随便地)扔，丢;挂;吊;', 'Marla was recently seen slinging her shoes at Trump...', '最近有人看见马拉朝特朗普扔鞋子。');
INSERT INTO `words` VALUES (8271, 'slink', 'slɪŋk', 'v.', '偷偷摸摸地走;躲躲闪闪地走;溜;', 'He decided that he couldn\'t just slink away, so he went and sat next to his wife.', '他决定不能偷偷溜走，于是便走到妻子身边坐下。');
INSERT INTO `words` VALUES (8272, 'slip', 'slɪp', 'v.', '滑倒;滑跤;滑落;', 'He had slipped on an icy pavement...', '他在一条结了冰的人行道上滑了一跤。');
INSERT INTO `words` VALUES (8273, 'slippery', 'ˈslɪpəri', 'adj.', '滑的;滑得抓不住(或站不稳、难以行走)的;油滑的;', 'The tiled floor was wet and slippery...', '瓷砖地面又湿又滑。');
INSERT INTO `words` VALUES (8274, 'slipshod', 'ˈslɪpʃɒd', 'adj.', '马虎的;敷衍了事的;', 'The hotel had always been run in a slipshod way.', '该酒店的管理一向马虎。');
INSERT INTO `words` VALUES (8275, 'slither', 'ˈslɪðə(r)', 'v.', '滑行;蛇行;爬行;', 'Robert lost his footing and slithered down the bank.', '罗伯特失足滑下了河岸。');
INSERT INTO `words` VALUES (8276, 'sliver', 'ˈslɪvə(r)', 'n.', '(切下或碎裂的)小块，薄片;', 'Not a sliver of glass remains where the windows were...', '窗户上连一点儿玻璃碎片都没有留下。');
INSERT INTO `words` VALUES (8277, 'slob', 'slɒb', 'n.', '懒惰而邋遢的人;', 'My boyfriend used to call me a fat slob.', '我男朋友过去常常叫我“邋遢的懒小胖”。');
INSERT INTO `words` VALUES (8278, 'slobber', 'ˈslɒbə(r)', 'v.', '流涎;流口水;', '...slobbering on his eternal cigarette end.', '永远都叼着烟头的嘴上流着口水');
INSERT INTO `words` VALUES (8279, 'slog', 'slɒɡ', 'v.', '埋头苦干;坚持不懈地做;顽强地走;', 'They secure their degrees by slogging through an intensive 11-month course...', '他们苦学了 11 个月的强化课程，拿到了学位。');
INSERT INTO `words` VALUES (8280, 'slogan', 'ˈsləʊɡən', 'n.', '标语;口号;', 'They could campaign on the slogan \'We\'ll take less of your money\'.', '他们可能打出这样的竞选口号：“我们会让您少出钱”。');
INSERT INTO `words` VALUES (8281, 'slop', 'slɒp', 'v.', '晃荡;(常指)溢出，溅出;倒出;', 'A little cognac slopped over the edge of the glass...', '有一点白兰地溢出了玻璃杯。');
INSERT INTO `words` VALUES (8282, 'slope', 'sləʊp', 'n.', '斜坡;坡地;山坡;', 'Saint-Christo is perched on a mountain slope.', '圣克里斯托教堂坐落在一个山坡上。');
INSERT INTO `words` VALUES (8283, 'sloping', 'ˈsləʊpɪŋ', 'v.', '倾斜;有坡度;悄悄地走;', 'Saint-Christo is perched on a mountain slope.', '圣克里斯托教堂坐落在一个山坡上。');
INSERT INTO `words` VALUES (8284, 'sloppy', 'ˈslɒpi', 'adj.', '马虎的;凌乱的;草率的;', 'He has little patience for sloppy work from colleagues...', '他对同事们草草敷衍的工作鲜有耐心。');
INSERT INTO `words` VALUES (8285, 'slot', 'slɒt', 'n.', '(投放或插入东西的)窄缝，扁口;(名单、日程安排或广播节目表中的)位置，时间，机会;', 'He dropped a coin into the slot and dialed.', '他往投币孔里投了一枚硬币，然后拨了电话。');
INSERT INTO `words` VALUES (8286, 'sloth', 'sləʊθ', 'n.', '树懒(南美洲热带动物，行动缓慢);懒散;怠惰;', 'He admitted a lack of motivation and a feeling of sloth.', '他承认自己缺乏动力，总想犯懒。');
INSERT INTO `words` VALUES (8287, 'slothful', 'ˈsləʊθfl', 'adj.', '懒散的;怠惰的;', 'He was not slothful: he had been busy all night.', '他可不懒，昨晚他忙了整整一夜。');
INSERT INTO `words` VALUES (8288, 'slotting', 'ˈslɒtɪŋ', 'v.', '投放;插入;(被)塞进;', 'He dropped a coin into the slot and dialed.', '他往投币孔里投了一枚硬币，然后拨了电话。');
INSERT INTO `words` VALUES (8289, 'slouch', 'slaʊtʃ', 'v.', '没精打采地站(或坐、走);低头垂肩地站(或坐、走);', 'Try not to slouch when you are sitting down...', '坐的时候尽量不要无精打采的。');
INSERT INTO `words` VALUES (8290, 'slough', 'slʌf , slaʊ', 'v.', '蜕(皮);使脱落;', 'All reptiles have to slough their skin to grow...', '所有爬行动物生长过程中都要蜕皮。');
INSERT INTO `words` VALUES (8291, 'slovenly', 'ˈslʌvnli', 'adj.', '邋遢的;衣冠不整的;凌乱的;', 'Lisa was irritated by the slovenly attitude of her boyfriend Sean.', '莉萨对男朋友肖恩吊儿郎当的态度非常恼火。');
INSERT INTO `words` VALUES (8292, 'slow', 'sləʊ', 'adj.', '缓慢的;迟缓的;耗时的;', 'The traffic is heavy and slow...', '交通拥挤，车流缓慢。');
INSERT INTO `words` VALUES (8293, 'slowdown', 'ˈsləʊdaʊn', 'n.', '减速;减缓;', 'There has been a sharp slowdown in economic growth.', '经济增长的速度明显放慢。');
INSERT INTO `words` VALUES (8294, 'slower', 'ˈsləʊə', 'adv.', '慢速地;缓慢地;', 'The traffic is heavy and slow...', '交通拥挤，车流缓慢。');
INSERT INTO `words` VALUES (8295, 'slowly', 'ˈsləʊli', 'adv.', '慢速地;缓慢地;迟缓地;', 'The traffic is heavy and slow...', '交通拥挤，车流缓慢。');
INSERT INTO `words` VALUES (8296, 'sluggish', 'ˈslʌɡɪʃ', 'adj.', '缓慢的;迟缓的;懒洋洋的;', 'The economy remains sluggish...', '经济的发展仍然非常缓慢。');
INSERT INTO `words` VALUES (8297, 'slum', 'slʌm', 'n.', '贫民窟;棚屋区;', '...a slum area of St Louis.', '圣路易斯的贫民区');
INSERT INTO `words` VALUES (8298, 'slumber', 'ˈslʌmbə(r)', 'n.', '睡眠;', 'He had fallen into exhausted slumber...', '他累得睡着了。');
INSERT INTO `words` VALUES (8299, 'slump', 'slʌmp', 'v.', '(价格、价值、数量等)骤降，猛跌，锐减;重重地坐下(或倒下);', 'Net profits slumped by 41%...', '净利润暴跌了 41%。');
INSERT INTO `words` VALUES (8300, 'slur', 'slɜː(r)', 'v.', '含混不清地说话(通常因醉酒或疲劳);连奏;连唱;', 'This is yet another slur on the integrity of the Metropolitan Police.', '这是对伦敦市警察局刚正廉明作风的又一次诽谤。');
INSERT INTO `words` VALUES (8301, 'slurp', 'slɜːp', 'v.', '(喝东西时)发出啧啧的声音;啧啧地响;', 'He blew on his soup before slurping it off the spoon...', '他吹了吹勺子里的汤，然后啧啧地喝了下去。');
INSERT INTO `words` VALUES (8302, 'sly', 'slaɪ', 'adj.', '诡诈的;狡诈的;诡秘的(表示自己知道别人不知道的秘密);', 'His lips were spread in a sly smile...', '他心照不宣地咧嘴一笑。');
INSERT INTO `words` VALUES (8303, 'smack', 'smæk', 'v.', '用巴掌打;掴;啪的一声使劲放(或扔、甩等);', 'She smacked me on the side of the head.', '她狠狠地拍了一下我的脑袋。');
INSERT INTO `words` VALUES (8304, 'small', 'smɔːl', 'adj.', '(尺寸、数量、程度等)小的;(服装、食品、家用设施等)小号的，小型的;(同类事物中)小的;', 'She is small for her age...', '她看上去比同龄人个头小。');
INSERT INTO `words` VALUES (8305, 'smallish', 'ˈsmɔːlɪʃ', 'adj.', '相当小的;颇小的;', 'Some smallish firms may close...', '一些比较小的公司可能会关闭。');
INSERT INTO `words` VALUES (8306, 'small-scale', 'ˌsmɔːl ˈskeɪl', 'adj.', '小型的;小范围的;小规模的;', '...the small-scale production of farmhouse cheeses in Devon.', '德文郡农家乳酪的小规模生产');
INSERT INTO `words` VALUES (8307, 'smarmy', 'ˈsmɑːmi', 'adj.', '过分殷勤的;谄媚的;', 'Rick is slightly smarmy and eager to impress.', '里克有些好奉承，急着要给人留下好印象。');
INSERT INTO `words` VALUES (8308, 'smart', 'smɑːt', 'adj.', '衣冠楚楚的;衣着讲究的;整洁而漂亮的;', 'He was smart and well groomed but not good looking...', '他仪容整洁，穿戴漂亮，就是人长得不好看。');
INSERT INTO `words` VALUES (8309, 'smash', 'smæʃ', 'v.', '(哗啦一声)打碎，打破，破碎;(使)猛烈撞击，猛烈碰撞;(用力)撞开，击穿，闯过;', 'Someone smashed a bottle...', '有人摔碎了一个瓶子。');
INSERT INTO `words` VALUES (8310, 'smashing', 'ˈsmæʃɪŋ', 'adj.', '非常好的;十分愉快的;', 'It was smashing. I really enjoyed it...', '真的很棒，我由衷地喜欢。');
INSERT INTO `words` VALUES (8311, 'smattering', 'ˈsmætərɪŋ', 'n.', '(尤指对语言)略知，浅知;', 'I had acquired a smattering of Greek.', '我学会了一丁点希腊语。');
INSERT INTO `words` VALUES (8312, 'smear', 'smɪə(r)', 'v.', '(用油性或稀软物质)胡乱涂抹;弄脏;弄上油污;', 'My sister smeared herself with suntan oil and slept by the swimming pool...', '我姐姐在身上抹了防晒油，躺在游泳池边睡着了。');
INSERT INTO `words` VALUES (8313, 'smelly', 'ˈsmeli', 'adj.', '有难闻气味的;有臭味的;', 'He had extremely smelly feet...', '他的脚奇臭无比。');
INSERT INTO `words` VALUES (8314, 'smile', 'smaɪl', 'v.', '微笑;笑;微笑着说;', 'When he saw me, he smiled and waved...', '他看见我的时候，微笑着朝我挥挥手。');
INSERT INTO `words` VALUES (8315, 'smiling', 'ˈsmaɪlɪŋ', 'v.', '微笑;笑;微笑着说;', 'When he saw me, he smiled and waved...', '他看见我的时候，微笑着朝我挥挥手。');
INSERT INTO `words` VALUES (8316, 'smirk', 'smɜːk', 'v.', '自鸣得意地笑;傻笑;', 'Two men looked at me, nudged each other and smirked...', '两个男人一面看着我，一面用胳膊肘顶顶对方，笑容里夹杂着一丝得意。');
INSERT INTO `words` VALUES (8317, 'smite', 'smaɪt', 'v.', '重打;猛击;攻击;', '...the heroic leader charging into battle, sword held high, ready to smite the enemy.', '英勇的首领高举利剑投入了战斗，准备狠狠地打击敌人。');
INSERT INTO `words` VALUES (8318, 'smithereens', 'ˌsmɪðəˈriːnz', 'n.', '小片;碎片;', 'She dropped the vase and smashed it to smithereens...', '她失手将花瓶摔得粉碎。');
INSERT INTO `words` VALUES (8319, 'smitten', 'ˈsmɪtn', 'adj.', '突然爱上的;一下子爱上的;痛感;', 'They were totally smitten with each other.', '他们完全被对方迷住了。');
INSERT INTO `words` VALUES (8320, 'smock', 'smɒk', 'n.', '(多为女性穿的)宽松式衬衫;罩衣;工作服;', 'She was wearing wool slacks and a paisley smock.', '她穿着羊毛宽松裤和涡旋花纹宽袍。');
INSERT INTO `words` VALUES (8321, 'smog', 'smɒɡ', 'n.', '烟雾(烟与雾混合的空气污染物，尤见于城市);', 'Cars cause pollution, both smog and acid rain.', '汽车造成的污染既有烟雾也有酸雨。');
INSERT INTO `words` VALUES (8322, 'smoke', 'sməʊk', 'n.', '烟;吸烟;抽烟;', 'A cloud of black smoke blew over the city...', '一团黑烟吹过城市的上空。');
INSERT INTO `words` VALUES (8323, 'smoked', 'sməʊkt', 'adj.', '(玻璃)用烟处理过的，烟灰色的;', '...a white van with smoked glass windows.', '装有烟灰色玻璃窗的白色货车');
INSERT INTO `words` VALUES (8324, 'smoker', 'ˈsməʊkə(r)', 'n.', '吸烟者;', 'A cloud of black smoke blew over the city...', '一团黑烟吹过城市的上空。');
INSERT INTO `words` VALUES (8325, 'smoking', 'ˈsməʊkɪŋ', 'n.', '吸烟;抽烟;', 'Smoking is now banned in many places of work.', '现在许多工作场所禁止吸烟。');
INSERT INTO `words` VALUES (8326, 'smoky', 'ˈsməʊki', 'adj.', '烟雾弥漫的;多烟的;冒出大量烟的;', 'His main problem was the extremely smoky atmosphere at work.', '他面临的主要问题就是：工作场所总是烟雾弥漫。');
INSERT INTO `words` VALUES (8327, 'smooth', 'smuːð', 'adj.', '平整的;平坦的;平滑的;', '...a rich cream that keeps skin soft and smooth.', '能让皮肤永葆柔嫩光滑的营养霜');
INSERT INTO `words` VALUES (8328, 'smoothing', 'ˈsmuːðɪŋ', 'v.', '使平整;使平坦;使平滑;', '...a rich cream that keeps skin soft and smooth.', '能让皮肤永葆柔嫩光滑的营养霜');
INSERT INTO `words` VALUES (8329, 'smoothly', 'ˈsmuːðli', 'adv.', '平稳地;连续而流畅地;顺利地;', '...a rich cream that keeps skin soft and smooth.', '能让皮肤永葆柔嫩光滑的营养霜');
INSERT INTO `words` VALUES (8330, 'smother', 'ˈsmʌðə(r)', 'v.', '使窒息而死;闷死;(用某物)厚厚地覆盖;', 'The girl\'s parents were also burned as they tried to smother the flames.', '女孩的父母试图扑灭大火的时候也被烧伤了。');
INSERT INTO `words` VALUES (8331, 'smudge', 'smʌdʒ', 'n.', '(模糊的)污迹，污痕;', 'There was a dark smudge on his forehead.', '他的额头上有一个黑乎乎的污点。');
INSERT INTO `words` VALUES (8332, 'smug', 'smʌɡ', 'adj.', '沾沾自喜的;自鸣得意的;', 'Thomas and his wife looked at each other in smug satisfaction.', '托马斯和夫人得意地互相望了一眼。');
INSERT INTO `words` VALUES (8333, 'smuggle', 'ˈsmʌɡl', 'v.', '走私;私运;偷运;', 'My message is \'If you try to smuggle drugs you are stupid\'...', '我的意思是“你要是企图走私毒品，你就是一个笨蛋”。');
INSERT INTO `words` VALUES (8334, 'smuggler', 'ˈsmʌɡlə(r)', 'n.', '走私者;', '...drug smugglers.', '毒品走私犯');
INSERT INTO `words` VALUES (8335, 'smuggling', 'ˈsmʌɡlɪŋ', 'n.', '走私(罪);', 'My message is \'If you try to smuggle drugs you are stupid\'...', '我的意思是“你要是企图走私毒品，你就是一个笨蛋”。');
INSERT INTO `words` VALUES (8336, 'smutty', 'ˈsmʌti', 'adj.', '淫秽的;下流的;猥亵的;', '...smutty jokes...', '荤段子');
INSERT INTO `words` VALUES (8337, 'snack', 'snæk', 'n.', '点心;小吃;快餐;', 'Lunch was a snack in the fields.', '午饭是在野外吃的便餐。');
INSERT INTO `words` VALUES (8338, 'snag', 'snæɡ', 'n.', '(尤指潜在的、意外的、不严重的)问题，困难，障碍，麻烦;突出物;尖齿;', 'A police clampdown on car thieves hit a snag when villains stole one of their cars...', '警方打击偷车贼的行动遭遇了一点小挫折：匪徒偷走了一辆警车。');
INSERT INTO `words` VALUES (8339, 'snap', 'snæp', 'v.', '(使喀嚓)断裂，绷断;(使啪地)打开，关上，移到某位置;厉声说;', 'He shifted his weight and a twig snapped...', '他挪了一下身体的重心，一根树枝咔嚓一声断了。');
INSERT INTO `words` VALUES (8340, 'snappish', 'ˈsnæpɪʃ', 'adj.', '(对人说话的方式)恶狠狠的，疾言厉色的，发急的;', '\'That is beautiful, Tony,\' Momma said, no longer sounding at all snappish.', '“真美呀，托尼，”妈妈说道，话音里的恶声恶气不见了。');
INSERT INTO `words` VALUES (8341, 'snappy', 'ˈsnæpi', 'adj.', '精练的;简洁的;漂亮入时的;', 'Each film gets a snappy two-line summary.', '每部影片都配有简洁明快的双行内容提要。');
INSERT INTO `words` VALUES (8342, 'snarl', 'snɑːl', 'v.', '龇牙低吼;咆哮着说;不耐烦地说;', 'He raced ahead up into the bush, barking and snarling...', '它嗥叫着冲向前，一头钻进了灌木丛。');
INSERT INTO `words` VALUES (8343, 'snarled', 'snɑːld', 'v.', '龇牙低吼;咆哮着说;不耐烦地说;', 'He raced ahead up into the bush, barking and snarling...', '它嗥叫着冲向前，一头钻进了灌木丛。');
INSERT INTO `words` VALUES (8344, 'snatch', 'snætʃ', 'v.', '一把抓起;一下夺过;夺去;', 'Mick snatched the cards from Archie\'s hand...', '米克一把夺过阿奇手里的牌。');
INSERT INTO `words` VALUES (8345, 'snazzy', 'ˈsnæzi', 'adj.', '漂亮而时髦的;吸引人的;', '...snazzy swimsuits.', '新潮的泳衣');
INSERT INTO `words` VALUES (8346, 'sneaking', 'ˈsniːkɪŋ', 'adj.', '(指不愿公开的感觉)暗中的，私下的;', 'I have a sneaking suspicion that they are going to succeed...', '我隐隐觉得他们恐怕要成功了。');
INSERT INTO `words` VALUES (8347, 'sneer', 'snɪə(r)', 'v.', '嘲笑;讥讽;嗤笑;', 'There is too great a readiness to sneer at anything the Opposition does...', '对反对党的任何举动都想加以嘲笑的心理过于急切了。');
INSERT INTO `words` VALUES (8348, 'sneeze', 'sniːz', 'v.', '打喷嚏;', 'What exactly happens when we sneeze?...', '我们打喷嚏究竟是怎么回事？');
INSERT INTO `words` VALUES (8349, 'sneezing', 'ˈsniːzɪŋ', 'v.', '打喷嚏;', 'What exactly happens when we sneeze?...', '我们打喷嚏究竟是怎么回事？');
INSERT INTO `words` VALUES (8350, 'snide', 'snaɪd', 'adj.', '讽刺的;挖苦的;', 'He made a snide comment about her weight...', '他说了一句很刻薄的话评论她的体重。');
INSERT INTO `words` VALUES (8351, 'sniff', 'snɪf', 'v.', '抽鼻子(尤指哭泣、患感冒等时出声地用鼻子吸气);(吸着气)嗅，闻;抱怨;', 'She wiped her face and sniffed loudly...', '她抹了一把脸，很响地抽着鼻子。');
INSERT INTO `words` VALUES (8352, 'snigger', 'ˈsnɪɡə(r)', 'v.', '窃笑;暗笑;', 'Suddenly, three schoolkids sitting near me started sniggering...', '3 个坐在我附近的学童突然偷偷地笑了起来。');
INSERT INTO `words` VALUES (8353, 'snip', 'snɪp', 'v.', '(用剪刀快速)剪，剪断，剪开;', 'He has now begun to snip away at the piece of paper...', '现在他已经开始剪这张纸。');
INSERT INTO `words` VALUES (8354, 'snipe', 'snaɪp', 'v.', '狙击;打冷枪;冷言冷语地指摘;', 'The Spanish media were still sniping at the British press yesterday.', '西班牙媒体昨天仍然在攻击英国媒体。');
INSERT INTO `words` VALUES (8355, 'snob', 'snɒb', 'n.', '势利小人;谄上欺下的人;自以为优越的人;', 'Going to a private school had made her a snob...', '上私立学校后，她变得很势利。');
INSERT INTO `words` VALUES (8356, 'snobbish', 'ˈsnɒbɪʃ', 'adj.', '势利的;自命不凡的;', 'They had a snobbish dislike for their intellectual and social inferiors...', '他们非常势利，不喜欢智力和社会地位不如自己的人。');
INSERT INTO `words` VALUES (8357, 'snooker', 'ˈsnuːkə(r)', 'n.', '斯诺克(供两人打的落袋台球，打球人用球杆打白色母球，按一定顺序撞15个红球和6个其他颜色的球入袋);(斯诺克比赛中的)障碍球;', '...a game of snooker...', '一局斯诺克比赛');
INSERT INTO `words` VALUES (8358, 'snoop', 'snuːp', 'v.', '窥探;打探;探听;', 'Ricardo was the one she\'d seen snooping around Kim\'s hotel room.', '她看见在金的宾馆房间周围窥探的那个人正是里卡多。');
INSERT INTO `words` VALUES (8359, 'snooty', 'ˈsnuːti', 'adj.', '傲慢的;目中无人的;', '...snooty intellectuals...', '妄自尊大的知识分子');
INSERT INTO `words` VALUES (8360, 'snore', 'snɔː(r)', 'v.', '打鼾;打呼噜;', 'His mouth was open, and he was snoring.', '他张着嘴巴，打着呼噜。');
INSERT INTO `words` VALUES (8361, 'snort', 'snɔːt', 'v.', '(表示气愤或被逗乐)喷鼻息，哼;用鼻子吸(毒品);', 'Harrell snorted with laughter...', '哈勒尔扑哧一声笑了。');
INSERT INTO `words` VALUES (8362, 'snout', 'snaʊt', 'n.', '(猪等动物的)口鼻部，吻;(人的)鼻子;吻状突出物;', 'Two alligators rest their snouts on the water\'s surface.', '两只鳄鱼的大嘴栖息在水面上。');
INSERT INTO `words` VALUES (8363, 'snow', 'snəʊ', 'n.', '雪;雪花;积雪;', 'In Mid-Wales six inches of snow blocked roads...', '在威尔士的中部地区，6英寸厚的大雪阻塞了道路。');
INSERT INTO `words` VALUES (8364, 'snowfall', 'ˈsnəʊfɔːl', 'n.', '下雪;降雪(量);', 'The total rain and snowfall amounted to 50mm.', '降雨量和降雪量共达 50 毫米。');
INSERT INTO `words` VALUES (8365, 'snowy', 'ˈsnəʊi', 'adj.', '被雪覆盖的;下雪多的;雪白的;', '...the snowy peaks of the Bighorn Mountains.', '比格霍恩山白雪皑皑的群峰');
INSERT INTO `words` VALUES (8366, 'snub', 'snʌb', 'v.', '冷落;怠慢;拒不出席;', 'He snubbed her in public and made her feel an idiot...', '他当众冷落她，让她觉得自己像一个白痴似的。');
INSERT INTO `words` VALUES (8367, 'snuffle', 'ˈsnʌfl', 'v.', '(因感冒或哭泣)鼻子呼哧出声，抽鼻子;哧哧地嗅;', 'She snuffled and wiped her nose on the back of her hand.', '她抽了抽鼻子并用手背擦了擦。');
INSERT INTO `words` VALUES (8368, 'snuffling', 'ˈsnʌflɪŋ', 'v.', '(因感冒或哭泣)鼻子呼哧出声，抽鼻子;哧哧地嗅;', 'She snuffled and wiped her nose on the back of her hand.', '她抽了抽鼻子并用手背擦了擦。');
INSERT INTO `words` VALUES (8369, 'snug', 'snʌɡ', 'adj.', '温暖舒适的;保暖的;贴身的;', 'They lay snug and warm amid the blankets.', '他们舒舒服服、暖暖和和地躺在毯子里。');
INSERT INTO `words` VALUES (8370, 'snuggle', 'ˈsnʌɡl', 'v.', '(使)依偎，紧贴，蜷伏;', 'Jane snuggled up against his shoulder...', '简依偎着他的肩膀。');
INSERT INTO `words` VALUES (8371, 'snugly', '', 'adv.', '紧贴地;贴身地;', 'They lay snug and warm amid the blankets.', '他们舒舒服服、暖暖和和地躺在毯子里。');
INSERT INTO `words` VALUES (8372, 'soak', 'səʊk', 'v.', '浸泡;浸湿;浸透;', 'Soak the beans for 2 hours...', '将豆子浸泡 2 小时。');
INSERT INTO `words` VALUES (8373, 'soaked', 'səʊkt', 'adj.', '湿透;(和名词组成形容词)浸透了…的，被…浸湿的;', 'I have to check my tent — it got soaked last night in the storm...', '我得检查一下帐篷——昨天夜里暴风雨把它淋透了。');
INSERT INTO `words` VALUES (8374, 'soaking', 'ˈsəʊkɪŋ', 'adj.', '湿透的;湿淋淋的;', 'My face and raincoat were soaking wet.', '我的脸上、雨衣上处处湿淋淋的。');
INSERT INTO `words` VALUES (8375, 'soap', 'səʊp', 'n.', '肥皂;', '...a bar of lavender soap.', '一块薰衣草香皂');
INSERT INTO `words` VALUES (8376, 'soapy', 'ˈsəʊpi', 'adj.', '满是肥皂的;涂满肥皂的;有肥皂味的;', 'Wash your hands thoroughly with hot soapy water before handling any food.', '在拿吃的之前，用热肥皂水把手好好地洗干净。');
INSERT INTO `words` VALUES (8377, 'soar', 'sɔː(r)', 'v.', '急升;猛增;升空;', 'Insurance claims are expected to soar...', '预计保险索赔数量会急剧增加。');
INSERT INTO `words` VALUES (8378, 'soaring', 'ˈsɔːrɪŋ', 'v.', '急升;猛增;升空;', 'Insurance claims are expected to soar...', '预计保险索赔数量会急剧增加。');
INSERT INTO `words` VALUES (8379, 'sob', 'sɒb', 'v.', '抽噎;啜泣;呜咽;', 'She began to sob again, burying her face in the pillow...', '她把脸埋在枕头里，又开始抽噎起来。');
INSERT INTO `words` VALUES (8380, 'sober', 'ˈsəʊbə(r)', 'adj.', '未醉;持重的;冷静的;', 'When Dad was sober he was a good father.', '不喝醉的时候，爸爸是一个好父亲。');
INSERT INTO `words` VALUES (8381, 'sobriquet', 'ˈsəʊbrɪkeɪ', 'n.', '绰号;外号;', 'From his staff he earned the sobriquet \'Mumbles\'.', '他的员工给他起了个“咕哝虫”的绰号。');
INSERT INTO `words` VALUES (8382, 'so-called', 'ˌsəʊ ˈkɔːld', 'adj.', '(表示不认同)所谓的;(引出约定俗成的称谓)人称…的，号称…的;', 'These are the facts that explode their so-called economic miracle...', '这些就是用来粉碎他们所谓的经济奇迹的事实。');
INSERT INTO `words` VALUES (8383, 'sociable', 'ˈsəʊʃəbl', 'adj.', '好交际的;合群的;友好的;', 'She was, and remained, extremely sociable, enjoying dancing, golf, tennis, skating and bicycling...', '她从过去到现在一直非常热衷于交际，喜欢跳舞、打高尔夫球、打网球、溜冰、骑自行车。');
INSERT INTO `words` VALUES (8384, 'social', 'ˈsəʊʃl', 'adj.', '社会的;社会上的;社会地位的;', '...the worst effects of unemployment, low pay and other social problems.', '失业、低工资以及其他社会问题造成的最糟糕的影响');
INSERT INTO `words` VALUES (8385, 'socialist', 'ˈsəʊʃəlɪst', 'n.', '社会主义者;社会党党员;', '...members of the ruling Socialist party...', '执政的社会主义党党员');
INSERT INTO `words` VALUES (8386, 'socialistic', 'ˌsəʊʃəˈlɪstɪk', 'adj.', '有社会主义倾向的;有一定社会主义特点的;', '...a design to move our economy in a socialistic direction.', '使我们的经济走上社会主义道路的计划');
INSERT INTO `words` VALUES (8387, 'socially', '\'səʊʃ(ə)li', 'adv.', '社会上;交际上;和睦地;', '...the worst effects of unemployment, low pay and other social problems.', '失业、低工资以及其他社会问题造成的最糟糕的影响');
INSERT INTO `words` VALUES (8388, 'society', 'səˈsaɪəti', 'n.', '社会(以群体形式生活在一起的人的总称);社会(共同遵守一定的习俗、法律等的特定群体);社团;', 'This reflects attitudes and values prevailing in society...', '这反映了社会上盛行的态度和价值观。');
INSERT INTO `words` VALUES (8389, 'socioeconomic', 'ˌsəʊsɪəʊˌɛkəˈnɒmɪk', 'adj.', '社会经济的;', 'Suicide is often connected with socio-economic deprivation.', '自杀经常与社会经济权利的丧失有关。');
INSERT INTO `words` VALUES (8390, 'sock', 'sɒk', 'n.', '短袜;(尤指用拳头)猛击，重击;', '...a pair of knee-length socks.', '一对及膝长的袜子');
INSERT INTO `words` VALUES (8391, 'socks', 'sɒks', 'n.', '短袜;(尤指用拳头)猛击，重击;', '...a pair of knee-length socks.', '一对及膝长的袜子');
INSERT INTO `words` VALUES (8392, 'sodden', 'ˈsɒdn', 'adj.', '湿透的;湿漉漉的;被…湿透的;', 'We stripped off our sodden clothes...', '我们扒下了湿透的衣服。');
INSERT INTO `words` VALUES (8393, 'sodium', 'ˈsəʊdiəm', 'n.', '钠;', 'The fish or seafood is heavily salted with pure sodium chloride.', '鱼或海鲜被抹了厚厚的纯盐腌制起来。');
INSERT INTO `words` VALUES (8394, 'soft', 'sɒft', 'adj.', '软的;柔软的;硬度较低的;', 'Regular use of a body lotion will keep the skin soft and supple...', '经常使用润肤露可使皮肤柔软而富有弹性。');
INSERT INTO `words` VALUES (8395, 'soften', 'ˈsɒfn', 'v.', '(使)变软，软化;(使)柔和，和缓;(使)态度缓和，变温和，变宽厚;', 'Soften the butter mixture in a small saucepan...', '在小平底锅里将黄油混合物化软。');
INSERT INTO `words` VALUES (8396, 'softly', 'ˈsɒftli', 'adv.', '轻轻地;轻柔地;温和地;', 'Regular use of a body lotion will keep the skin soft and supple...', '经常使用润肤露可使皮肤柔软而富有弹性。');
INSERT INTO `words` VALUES (8397, 'softness', 'ˈsɒftnəs', 'n.', '柔软;温柔;', 'Regular use of a body lotion will keep the skin soft and supple...', '经常使用润肤露可使皮肤柔软而富有弹性。');
INSERT INTO `words` VALUES (8398, 'soft-soap', 'ˌsɒft ˈsəʊp', 'v.', '(为让某人做某事)说好听的，奉承，灌迷魂汤;劝诱;', 'The government is not soft-soaping the voters here.', '政府并非在讨好这里的选民。');
INSERT INTO `words` VALUES (8399, 'software', 'ˈsɒftweə(r)', 'n.', '软件;', '...the people who write the software for big computer projects.', '大型计算机项目的软件开发人员');
INSERT INTO `words` VALUES (8400, 'soggy', 'ˈsɒɡi', 'adj.', '湿而软的;潮湿的;受潮的;', '...soggy cheese sandwiches.', '湿乎乎的乳酪三明治');
INSERT INTO `words` VALUES (8401, 'soil', 'sɔɪl', 'n.', '土壤;国土;领土;', 'We have the most fertile soil in Europe.', '我们拥有欧洲最肥沃的土地。');
INSERT INTO `words` VALUES (8402, 'solace', 'ˈsɒləs', 'n.', '安慰;慰藉;给以安慰的人(或事物);', 'I found solace in writing when my father died three years ago...', '我父亲 3 年前去世的时候，我从写作中找到了安慰。');
INSERT INTO `words` VALUES (8403, 'solar', 'ˈsəʊlə(r)', 'adj.', '太阳的;太阳能的;', 'A total solar eclipse is due to take place some time tomorrow.', '明天某个时刻会发生日全食。');
INSERT INTO `words` VALUES (8404, 'solder', 'ˈsəʊldə(r)', 'n.', '焊料;焊锡;', 'Fewer workers are needed to solder circuit boards...', '焊接电路板需要的工人更少了。');
INSERT INTO `words` VALUES (8405, 'sole', 'səʊl', 'adj.', '仅有的;唯一的;独占的;', 'Their sole aim is to destabilize the Indian government.', '他们唯一的目的就是要动摇印度政府的统治。');
INSERT INTO `words` VALUES (8406, 'solely', 'ˈsəʊlli', 'adv.', '仅;只;唯;', 'Too often we make decisions based solely upon what we see in the magazines...', '太多的时候，我们仅仅根据在杂志上面所看到的东西就作出了决定。');
INSERT INTO `words` VALUES (8407, 'solemn', 'ˈsɒləm', 'adj.', '冷峻的;表情严肃的;庄严的;', 'His solemn little face broke into smiles...', '他严肃的小脸蛋上突然绽放出笑容。');
INSERT INTO `words` VALUES (8408, 'solemnity', 'səˈlemnəti', 'n.', '庄严;庄重;严肃;', 'His solemn little face broke into smiles...', '他严肃的小脸蛋上突然绽放出笑容。');
INSERT INTO `words` VALUES (8409, 'solemnly', '\'sɒləmli', 'adv.', '郑重;一本正经地;沉着脸;', 'His solemn little face broke into smiles...', '他严肃的小脸蛋上突然绽放出笑容。');
INSERT INTO `words` VALUES (8410, 'solicit', 'səˈlɪsɪt', 'v.', '索求，请求…给予(援助、钱或信息);征求;筹集;', 'He\'s already solicited their support on health care reform...', '他已就医疗改革问题请求他们的支持。');
INSERT INTO `words` VALUES (8411, 'solicitation', 'səˌlɪsɪˈteɪʃn', 'n.', '(对金钱、帮助、支援等的)请求;(意见的)征求;', 'Republican leaders are making open solicitation of the Italian-American vote...', '共和党领袖公开向意大利裔美国人拉选票。');
INSERT INTO `words` VALUES (8412, 'solicitous', 'səˈlɪsɪtəs', 'adj.', '操心的;关怀的;关切的;', 'He was so solicitous of his guests.', '他对他的客人们非常关切。');
INSERT INTO `words` VALUES (8413, 'solicitude', 'səˈlɪsɪtjuːd', 'n.', '牵挂;关怀;关切;', 'He is full of tender solicitude towards my sister.', '他对我妹妹满心牵挂。');
INSERT INTO `words` VALUES (8414, 'solid', 'ˈsɒlɪd', 'adj.', '固体的;坚硬的;无空隙的;', '...the potential of greatly reducing our solid waste problem...', '大幅减少我们固体废料问题的可能性');
INSERT INTO `words` VALUES (8415, 'solidarity', 'ˌsɒlɪˈdærəti', 'n.', '团结;齐心协力;同心同德;', 'Supporters want to march tomorrow to show solidarity with their leaders.', '为了显示和领袖的同心同德，支持者们希望明天举行游行。');
INSERT INTO `words` VALUES (8416, 'solidify', 'səˈlɪdɪfaɪ', 'v.', '(使)凝固，变硬，变得结实;(使)变得坚定，变得稳固，巩固;', 'The thicker lava would have taken two weeks to solidify...', '较厚的熔岩需要两周的时间才能凝固。');
INSERT INTO `words` VALUES (8417, 'solidly', 'ˈsɒlɪdli', 'adv.', '坚固地;结实地;牢固地;', '...the potential of greatly reducing our solid waste problem...', '大幅减少我们固体废料问题的可能性');
INSERT INTO `words` VALUES (8418, 'solitary', 'ˈsɒlətri', 'adj.', '独自的;单独的;喜欢(或惯于)独处的;', 'Paul was a shy, pleasant, solitary man...', '保罗是一个羞怯、友善、惯于独处的男人。');
INSERT INTO `words` VALUES (8419, 'solitude', 'ˈsɒlətjuːd', 'n.', '独处;独居;', 'He enjoyed his moments of solitude before the pressures of the day began...', '他喜欢在白天的压力到来之前享受独处的时光。');
INSERT INTO `words` VALUES (8420, 'solo', 'ˈsəʊləʊ', 'adj.', '独自的;单独的;独唱的;', 'He had just completed his final solo album.', '他刚刚录完了他的最后一张独唱唱片。');
INSERT INTO `words` VALUES (8421, 'soluble', 'ˈsɒljəbl', 'adj.', '可溶的;可解决的;可解的;', 'Uranium is soluble in sea water.', '铀可溶解于海水。');
INSERT INTO `words` VALUES (8422, 'solution', 'səˈluːʃn', 'n.', '解决办法;处理手段;答案;', 'Although he has sought to find a peaceful solution, he is facing pressure to use greater military force.', '尽管他试图寻求和平解决，但现在却面临使用更多武力的压力。');
INSERT INTO `words` VALUES (8423, 'solve', 'sɒlv', 'v.', '解决;处理;解答;', 'Their domestic reforms did nothing to solve the problem of unemployment...', '他们的国内改革未能解决失业问题。');
INSERT INTO `words` VALUES (8424, 'solvent', 'ˈsɒlvənt', 'n.', '溶剂;溶媒;', 'They\'re going to have to show that the company is now solvent.', '他们必须要证明公司目前具备偿还能力。');
INSERT INTO `words` VALUES (8425, 'some', 'səm , sʌm', 'det.', '(与不可数名词或复数可数名词连用)一些，若干;(指一集体中的某些成员或一事物的某些类型)某些，部分，有的;好些;', 'Robin opened some champagne...', '罗宾开了一些香槟。');
INSERT INTO `words` VALUES (8426, 'somehow', 'ˈsʌmhaʊ', 'adv.', '以某种方式(或方法);由于某种未知的原因;不知为什么;', 'We\'ll  manage somehow, you and me. I know we will...', '我和你，我们总能应付过去的。我知道我们会的。');
INSERT INTO `words` VALUES (8427, 'someone', 'ˈsʌmwʌn', 'pron.', '某人;重要人物;', 'Her father was shot by someone trying to rob his small retail store...', '她的父亲被企图抢劫他那间小零售店的人开枪打死了。');
INSERT INTO `words` VALUES (8428, 'something', 'ˈsʌmθɪŋ', 'pron.', '某事;某物;想来重要(或值得注意)的事物;', 'He realized right away that there was something wrong...', '他马上意识到什么地方出了问题。');
INSERT INTO `words` VALUES (8429, 'sometime', 'ˈsʌmtaɪm', 'adv.', '在某时(不确切或尚未确定);', 'The sales figures won\'t  be released until sometime next month...', '下个月的某个时间才会公布销售数据。');
INSERT INTO `words` VALUES (8430, 'sometimes', 'ˈsʌmtaɪmz', 'adv.', '有时;间或;', 'During the summer, my skin sometimes gets greasy...', '夏天我的皮肤有时候会出油。');
INSERT INTO `words` VALUES (8431, 'somewhat', 'ˈsʌmwɒt', 'adv.', '有点;有几分;稍微;', 'He concluded that Oswald was somewhat abnormal...', '他断定奥斯瓦尔德有点不正常。');
INSERT INTO `words` VALUES (8432, 'somewhere', 'ˈsʌmweə(r)', 'adv.', '在某处;到某处;', 'I\'ve got a feeling I\'ve seen him before somewhere...', '我有种感觉——我以前曾在哪里见过他。');
INSERT INTO `words` VALUES (8433, 'somnolent', 'ˈsɒmnələnt', 'adj.', '瞌睡的;困倦的;睡意蒙眬的;', 'The sedative makes people very somnolent.', '这种镇静剂会让人瞌睡。');
INSERT INTO `words` VALUES (8434, 'son', 'sʌn', 'n.', '儿子;(年长者对年轻男子或男孩的爱称)孩子;(某个地方、国家等的)男性成员，子孙;', 'He shared a pizza with his son Laurence...', '他和儿子劳伦斯分吃了一张比萨饼。');
INSERT INTO `words` VALUES (8435, 'song', 'sɒŋ', 'n.', '歌;歌曲;(统称)歌曲;', '...a voice singing a Spanish song.', '正在演唱一首西班牙歌曲的声音');
INSERT INTO `words` VALUES (8436, 'songwriter', 'ˈsɒŋraɪtə(r)', 'n.', '歌词作者;歌曲的词曲作者;', '...one of rock\'n\'roll\'s greatest songwriters.', '最伟大的摇滚乐作曲家之一');
INSERT INTO `words` VALUES (8437, 'sonic', 'ˈsɒnɪk', 'adj.', '声音的;声速的;', 'He activated the door with the miniature sonic transmitter.', '他用微型声波发射器开启了门。');
INSERT INTO `words` VALUES (8438, 'sonority', 'səˈnɒrəti', 'n.', '(声音的)深沉响亮，洪亮;', 'The lower strings contribute a splendid richness of sonority.', '下弦能够产生雄壮浑厚的音响效果。');
INSERT INTO `words` VALUES (8439, 'sonorous', 'ˈsɒnərəs', 'adj.', '雄浑的;浑厚的;', '\'Doctor McKee?\' the man called in an even, sonorous voice.', '“是麦基博士吗？”那个男人用平稳而洪亮的声音喊道。');
INSERT INTO `words` VALUES (8440, 'soon', 'suːn', 'adv.', '很快;马上;不久;', 'You\'ll be hearing from us very soon...', '你很快就会收到我们的来信。');
INSERT INTO `words` VALUES (8441, 'soot', 'sʊt', 'n.', '煤烟子;油烟;', '... a wall blackened by soot.', '被煤烟熏黑的墙壁');
INSERT INTO `words` VALUES (8442, 'soothe', 'suːð', 'v.', '安慰;抚慰;劝慰;', 'He would take her in his arms and soothe her...', '他会把她揽在怀中抚慰她。');
INSERT INTO `words` VALUES (8443, 'soothing', 'ˈsuːðɪŋ', 'v.', '安慰;抚慰;劝慰;', 'He would take her in his arms and soothe her...', '他会把她揽在怀中抚慰她。');
INSERT INTO `words` VALUES (8444, 'sop', 'sɒp', 'n.', '(为缓和某人愤怒或失望情绪而赠与的)小东西;', 'This is an obvious sop to the large Irish-American audience...', '这明显是讨好广大爱尔兰裔美国观众的一种手段。');
INSERT INTO `words` VALUES (8445, 'sophisticated', 'səˈfɪstɪkeɪtɪd', 'adj.', '见多识广的;老练的;见过世面的;', 'Honeybees use one of the most sophisticated communication systems of any insect.', '蜜蜂之间所用的交流方式是昆虫中最为复杂的方式之一。');
INSERT INTO `words` VALUES (8446, 'sophistication', 'səˌfɪstɪˈkeɪʃn', 'n.', '世故;复杂巧妙;高水平;', '...the sophistication of one of the world\'s richest cities...', '作为世界上最富庶的城市之一，它的精致和优雅…');
INSERT INTO `words` VALUES (8447, 'sophistry', 'ˈsɒfɪstri', 'n.', '诡辩术;诡辩;', 'Political selection is more dependent on sophistry and less on economic literacy.', '政治选举更多地取决于诡辩的能力，而不在于是否懂经济。');
INSERT INTO `words` VALUES (8448, 'soporific', 'ˌsɒpəˈrɪfɪk', 'adj.', '催眠的;让人瞌睡的;', 'The warmth of the room and the monotony of the speaker\'s voice grew soporific.', '房间里的热气和发言人单调的声音让人昏昏欲睡。');
INSERT INTO `words` VALUES (8449, 'soprano', 'səˈprɑːnəʊ', 'n.', '女高音;童声高音;女高音歌手;', 'She was the main soprano at the Bolshoi theatre.', '她是莫斯科大剧院首席女高音歌手。');
INSERT INTO `words` VALUES (8450, 'sordid', 'ˈsɔːdɪd', 'adj.', '卑鄙的;丑恶的;无耻的;', 'He sat with his head buried in his hands as his sordid double life was revealed...', '自己卑鄙的双重生活被揭穿后，他双手抱头坐在那里。');
INSERT INTO `words` VALUES (8451, 'sore', 'sɔː(r)', 'adj.', '(发炎)疼痛的;酸痛的;气恼;', 'It\'s years since  I\'ve had a sore throat like I did last night...', '我的喉咙已经好几年没有像昨晚那样疼了。');
INSERT INTO `words` VALUES (8452, 'sorely', 'ˈsɔːli', 'adv.', '严重地;非常;', 'I for one was sorely disappointed.', '我本人就感到特别失望。');
INSERT INTO `words` VALUES (8453, 'sorrow', 'ˈsɒrəʊ', 'n.', '悲伤;悲痛;悲哀;', 'It was a time of great sorrow...', '这是一个非常悲伤的时刻。');
INSERT INTO `words` VALUES (8454, 'sorrowful', 'ˈsɒrəʊfl', 'adj.', '悲伤的;悲痛的;悲哀的;', 'His father\'s face looked suddenly soft and sorrowful...', '他父亲的面容忽然温和起来，充满了忧伤。');
INSERT INTO `words` VALUES (8455, 'sorry', 'ˈsɒri', 'adj.', '难过;惋惜;同情;', '\'We\'re all talking at the same time.\' — \'Yeah. Sorry.\'...', '“我们俩在同时讲话。”——“噢，抱歉。”');
INSERT INTO `words` VALUES (8456, 'sort', 'sɔːt', 'n.', '种类;类别;品种;', 'What sort of school did you go to?...', '你上的是哪类学校？');
INSERT INTO `words` VALUES (8457, 'sorting', 'ˈsɔːtɪŋ', 'v.', '整理;把…分类;妥善处理;', 'What sort of school did you go to?...', '你上的是哪类学校？');
INSERT INTO `words` VALUES (8458, 'so-so', 'ˌsəʊ ˈsəʊ', 'adj./adv.', '一般的;普通的;中等的;', 'Their lunch was only so-so...', '他们的午餐只能算是凑合。');
INSERT INTO `words` VALUES (8459, 'soul', 'səʊl', 'n.', '灵魂;心性;内心;', 'She went to pray for the soul of her late husband...', '她去为亡夫的灵魂祈祷。');
INSERT INTO `words` VALUES (8460, 'soulless', 'ˈsəʊlləs', 'adj.', '没有生气的;呆板的;乏味的;', '...a clean and soulless hotel.', '一家干净而冷清的旅店');
INSERT INTO `words` VALUES (8461, 'sounding', 'ˈsaʊndɪŋ', 'n.', '意见调查;征询意见;水深测量;', 'There were 15 minutes between the first air raid alert and the sounding of the all-clear signal.', '第一次空袭警报和空袭警报解除信号之间间隔了 15 分钟。');
INSERT INTO `words` VALUES (8462, 'soundly', 'ˈsaʊndli', 'adv.', '酣(睡);(睡得)沉;明智地;', 'Duke was soundly defeated in this month\'s Louisiana governor\'s race.', '杜克在本月的路易斯安那州长竞选中大败而归。');
INSERT INTO `words` VALUES (8463, 'soundproof', 'ˈsaʊndpruːf', 'adj.', '隔音的;', 'The studio isn\'t soundproof.', '这个摄影棚并不隔音。');
INSERT INTO `words` VALUES (8464, 'soup', 'suːp', 'n.', '汤;羹;', '...home-made chicken soup.', '家常的鸡汤');
INSERT INTO `words` VALUES (8465, 'sour', 'ˈsaʊə(r)', 'adj.', '酸的;有酸味的;酸腐的;', 'The stewed apple was sour even with honey.', '炖煮的苹果即便加了蜂蜜还是很酸。');
INSERT INTO `words` VALUES (8466, 'source', 'sɔːs', 'n.', '来源;出处;信息来源;', '...over 40 per cent of British adults use television as their major source of information about the arts...', '40 %以上的英国成年人把电视当作获取艺术文化信息的主要途径。');
INSERT INTO `words` VALUES (8467, 'south', 'saʊθ', 'n.', '南;南方;南部;', 'The town lies ten miles to the south of here...', '那个小镇位于这里以南 10 英里处。');
INSERT INTO `words` VALUES (8468, 'southeast', 'ˌsaʊθˈiːst', 'adv.', '向东南方;朝东南;位于…的东南方;在…的东南;', 'The city of Chengdu lies some seven hundred miles to the South-East.', '成都市位于东南方大约 700 英里处。');
INSERT INTO `words` VALUES (8469, 'southeastern', 'ˈsaʊθˈiːstən', 'adj.', '东南的，东南部的;', '...this city on the south-eastern edge of the United States.', '这座位于美国东南边境的城市');
INSERT INTO `words` VALUES (8470, 'southern', 'ˈsʌðən', 'adj.', '南方的;向南的;南部的;', 'The Everglades National Park stretches across the southern tip of Florida.', '大沼泽地国家公园位于佛罗里达州的最南端。');
INSERT INTO `words` VALUES (8471, 'southerner', 'ˈsʌðənə(r)', 'n.', '南方人;', 'Bob Wilson is a Southerner, from Texas...', '鲍勃·威尔逊是南方人，来自得克萨斯州。');
INSERT INTO `words` VALUES (8472, 'southward', 'ˈsaʊθwəd', 'adv./adj.', '向南方（的）;', 'They drove southward...', '他们驾车南行。');
INSERT INTO `words` VALUES (8473, 'southwest', 'ˌsaʊθˈwɛst', 'adv.', '向西南方;朝西南;位于…的西南方;在…的西南;', '...the village of Popplewell, some six miles to the south-west.', '位于西南方约6英里处的波普尔维尔村');
INSERT INTO `words` VALUES (8474, 'southwestern', 'ˈsaʊθˈwɛstən', 'adj.', '西南的，西南部的;', '...towns and villages in south-western Azerbaijan.', '阿塞拜疆西南部的城镇和村庄');
INSERT INTO `words` VALUES (8475, 'souvenir', 'ˌsuːvəˈnɪə(r)', 'n.', '纪念物;纪念品;(度假时买回来送人的)礼物;', '...a souvenir of the summer of 1992.', '1992 年夏天的一件纪念品');
INSERT INTO `words` VALUES (8476, 'sovereign', 'ˈsɒvrɪn', 'n.', '君主;元首;金镑(旧时英国金币，面值一英镑);', 'Lithuania and Armenia signed a treaty in Vilnius recognising each other as independent sovereign states...', '立陶宛和亚美尼亚在维尔纽斯签订条约，承认彼此为独立主权国家。');
INSERT INTO `words` VALUES (8477, 'sovereignty', 'ˈsɒvrənti', 'n.', '主权;最高统治权;最高权威;', 'Britain\'s concern to protect national sovereignty is far from new.', '英国对捍卫国家主权的关切早已不是什么新鲜事了。');
INSERT INTO `words` VALUES (8478, 'soviet', 'ˈsəʊviət', 'n.', '苏维埃(前苏联的各级代表会议);苏联人;', '...Soviet athletes.', '苏联的运动员');
INSERT INTO `words` VALUES (8479, 'spa', 'spɑː', 'n.', '矿泉疗养地;矿泉城;休闲健身中心;', '...Fiuggi, a spa town famous for its water.', '菲乌吉——一个以水质绝佳而闻名的温泉疗养城市');
INSERT INTO `words` VALUES (8480, 'space', 'speɪs', 'n.', '(可利用的)空地，空间;空;空隙;', 'Under the plan, bits of open space — fields, golf-course borders and small parks — will be preserved.', '在这份规划里，一些开放空间——运动场、高尔夫球场边缘以及小公园——将得以保留。');
INSERT INTO `words` VALUES (8481, 'spacing', 'ˈspeɪsɪŋ', 'n.', '(空间)间隔;(尤指)字距，行距;(时间)间隔;', 'Please type or write clearly in double spacing on one side of A4 paper only.', '请用A4纸、以双倍行距单面打印或誊抄，字迹务必清晰。');
INSERT INTO `words` VALUES (8482, 'spacious', 'ˈspeɪʃəs', 'adj.', '宽敞的;', 'The house has a spacious kitchen and dining area.', '这座房子有一个宽敞的厨房和用餐区。');
INSERT INTO `words` VALUES (8483, 'spade', 'speɪd', 'n.', '锹;铲;(纸牌中的)黑桃;', '... a garden spade...', '园艺铲');
INSERT INTO `words` VALUES (8484, 'spades', 'speɪdz', 'n.', '锹;铲;(纸牌中的)黑桃;', '... a garden spade...', '园艺铲');
INSERT INTO `words` VALUES (8485, 'span', 'spæn', 'n.', '持续时间;范围;包括的种类;', 'The batteries had a life span of six hours...', '这些电池的寿命为 6 小时。');
INSERT INTO `words` VALUES (8486, 'spangle', 'ˈspæŋɡl', 'v.', '用闪光片布满(或装饰);', '...robes that glittered with spangles.', '缀满闪闪发光的饰片的袍子');
INSERT INTO `words` VALUES (8487, 'spanish', 'ˈspænɪʃ', 'adj.', '西班牙的;', '...a Spanish sherry.', '西班牙雪利酒');
INSERT INTO `words` VALUES (8488, 'spank', 'spæŋk', 'v.', '打(小孩的)屁股;', 'When I used to do that when I was a kid, my mom would spank me.', '我还是个孩子的时候常常干那种事，每次妈妈总是打我的屁股。');
INSERT INTO `words` VALUES (8489, 'spanking', 'ˈspæŋkɪŋ', 'n.', '打屁股(尤指打小孩);', 'Andrea gave her son a sound spanking.', '安德烈娅给了儿子结结实实一顿痛打。');
INSERT INTO `words` VALUES (8490, 'spar', 'spɑː(r)', 'v.', '练习拳击;虚晃一拳;(多指在友好气氛中)辩论，争论;', 'With protective gear on you can spar with a partner...', '戴上护具后可以和对手练习轻拳出击。');
INSERT INTO `words` VALUES (8491, 'spare', 'speə(r)', 'adj.', '不用的;闲置的;备用的;', 'If possible keep a spare pair of glasses accessible in case your main pair is broken or lost...', '如果有可能的话，最好准备一副备用眼镜，以防常用的那副眼镜打碎或者丢失。');
INSERT INTO `words` VALUES (8492, 'sparing', 'ˈspeərɪŋ', 'adj.', '慎用;俭省的;吝惜的;', 'I\'ve not been sparing with the garlic...', '我总是用很多大蒜。');
INSERT INTO `words` VALUES (8493, 'sparingly', '\'speərɪŋli', 'adv.', '很少;细嚼慢咽地;节俭地;', 'I\'ve not been sparing with the garlic...', '我总是用很多大蒜。');
INSERT INTO `words` VALUES (8494, 'spark', 'spɑːk', 'n.', '火花;火星;电火花;', 'The fire gradually got bigger and bigger. Sparks flew off in all directions.', '火越燃越烈，火星四溅。');
INSERT INTO `words` VALUES (8495, 'sparkle', 'ˈspɑːkl', 'v.', '闪烁;闪耀;生气勃勃;', 'The jewels on her fingers sparkled...', '她手指上戴的首饰闪闪发光。');
INSERT INTO `words` VALUES (8496, 'sparkling', 'ˈspɑːklɪŋ', 'adj.', '闪烁的;闪耀的;起泡的;', '...a glass of sparkling wine.', '一杯葡萄汽酒');
INSERT INTO `words` VALUES (8497, 'sparks', 'spɑːks', 'n.', '火花;火星;电火花;', 'The fire gradually got bigger and bigger. Sparks flew off in all directions.', '火越燃越烈，火星四溅。');
INSERT INTO `words` VALUES (8498, 'sparse', 'spɑːs', 'adj.', '稀少的;稀疏的;零落的;', 'Many slopes are rock fields with sparse vegetation...', '许多山坡都是石头地，植被稀疏。');
INSERT INTO `words` VALUES (8499, 'sparsely', 'spɑ:slɪ', 'adv.', '稀少;稀疏地;节省地;', 'Many slopes are rock fields with sparse vegetation...', '许多山坡都是石头地，植被稀疏。');
INSERT INTO `words` VALUES (8500, 'spartan', 'ˈspɑːtn', 'adj.', '斯巴达式的;简朴的;清苦的;', 'Their spartan lifestyle prohibits a fridge or a phone...', '他们不使用冰箱和电话，过着简朴的生活。');
INSERT INTO `words` VALUES (8501, 'spasm', 'ˈspæzəm', 'n.', '痉挛;抽搐;(感情或反应)一阵阵发作，阵发;', 'A muscular spasm in the coronary artery can cause a heart attack...', '冠状动脉的肌肉痉挛可能导致心脏病。');
INSERT INTO `words` VALUES (8502, 'spasmodic', 'spæzˈmɒdɪk', 'adj.', '一阵阵的;阵发性的;断断续续的;', 'He managed to stifle the spasmodic sobs of panic rising in his throat...', '他惊慌得差一点哭了起来，不过还是竭力地克制住了。');
INSERT INTO `words` VALUES (8503, 'spate', 'speɪt', 'n.', '一连串，接二连三(通常指不愉快的事物);', '...the recent spate of attacks on horses.', '最近发生的一连串袭马事件');
INSERT INTO `words` VALUES (8504, 'spatial', 'ˈspeɪʃl', 'adj.', '空间的;', '...the spatial distribution of black employment and population in South Africa.', '南非黑人就业与人口的空间分布');
INSERT INTO `words` VALUES (8505, 'spatter', 'ˈspætə(r)', 'v.', '溅;洒;洒落;', 'He stared at the rain spattering on the glass...', '他凝视着雨点溅落在玻璃上。');
INSERT INTO `words` VALUES (8506, 'spatula', 'ˈspætʃələ', 'n.', '(烹饪、油漆等活动中用以调拌或涂敷的)铲，刮勺，抹刀，刮刀;压舌板;', 'Spoon the batter into the prepared pan, smoothing over the top with a spatula.', '将面糊舀入备好的平底锅中，用刮铲将表面抹平。');
INSERT INTO `words` VALUES (8507, 'spawn', 'spɔːn', 'v.', '产卵;引发;引起;', '...her passion for collecting frog spawn.', '她对采集蛙卵的热爱');
INSERT INTO `words` VALUES (8508, 'speak', 'spiːk', 'v.', '谈;谈话;交谈;', 'He tried to speak, but for once, his voice had left him...', '他想说话，却一时语塞。');
INSERT INTO `words` VALUES (8509, 'speaker', 'ˈspiːkə(r)', 'n.', '发言者;演讲者;说话者;', 'Among the speakers at the gathering was Treasury Secretary Nicholas Brady...', '与会的发言人中有财政部长尼古拉斯·布莱迪。');
INSERT INTO `words` VALUES (8510, 'speaking', 'ˈspiːkɪŋ', 'n.', '发表讲话;演讲;', 'It would also train women union members in public speaking and decision-making...', '它还将培养工会的女性成员公开演讲和决策的能力。');
INSERT INTO `words` VALUES (8511, 'special', 'ˈspeʃl', 'adj.', '特殊的;特别的;不寻常的;', 'You\'re very special to me, darling...', '亲爱的，你对我来说很重要。');
INSERT INTO `words` VALUES (8512, 'specialist', 'ˈspeʃəlɪst', 'n.', '专家;专科医生;', 'If you are housebound, you can arrange for a home visit from a specialist adviser...', '如果你出不了门，可以预约专家顾问上门就诊。');
INSERT INTO `words` VALUES (8513, 'speciality', 'ˌspeʃiˈæləti', 'n.', '特产;特色菜;专业;', 'My father was a historian of repute. His speciality was the history of Germany...', '我的父亲是一名颇有名气的历史学家，他专门研究德国历史。');
INSERT INTO `words` VALUES (8514, 'specially', 'ˈspeʃəli', 'adv.', '专门地;特意;格外;', '...a soap specially designed for those with sensitive skins...', '特别为皮肤敏感者设计的香皂');
INSERT INTO `words` VALUES (8515, 'specialty', 'ˈspeʃəlti', 'n.', '专业;专长;(某个地方的)特色食品，特产;', 'His specialty is international law...', '他的专业是国际法。');
INSERT INTO `words` VALUES (8516, 'species', 'ˈspiːʃiːz', 'n.', '种，物种(分类上小于属);', 'Pandas are an endangered species...', '熊猫是濒危物种。');
INSERT INTO `words` VALUES (8517, 'specific', 'spəˈsɪfɪk', 'adj.', '明确的;具体的;特定的;', 'Massage may help to increase blood flow to specific areas of the body...', '按摩有助于增加身体特定部位的血液流量。');
INSERT INTO `words` VALUES (8518, 'specifically', 'spəˈsɪfɪkli', 'adv.', '明确地;具体地;特意;', '...the first nursing home designed specifically for people with AIDS...', '首家专为艾滋病人设计的疗养院');
INSERT INTO `words` VALUES (8519, 'specification', 'ˌspesɪfɪˈkeɪʃn', 'n.', '规格;规范;明细单;', 'Troll\'s exclusive, personalized luggage is made to our own exacting specifications in heavy-duty PVC/nylon...', '特罗尔专用的、个性化的行李箱是按照我们自己严格的规格要求用耐用聚氯乙烯/尼龙材料定制的。');
INSERT INTO `words` VALUES (8520, 'specifics', 'spəˈsɪfɪks', 'n.', '详情;细节;', 'Things improved when we got down to the specifics...', '当我们深入到具体细节时，情况有所改观。');
INSERT INTO `words` VALUES (8521, 'specify', 'ˈspesɪfaɪ', 'v.', '具体说明;明确规定;详述;', 'They specified a spacious entrance hall...', '他们专门指定要一个宽敞的大门厅。');
INSERT INTO `words` VALUES (8522, 'specimen', 'ˈspesɪmən', 'n.', '样品;样本;标本;', '200,000 specimens of fungus are kept at the Komarov Botanical Institute.', '有 20 万份菌类标本被保存在科马罗夫植物研究所。');
INSERT INTO `words` VALUES (8523, 'specious', 'ˈspiːʃəs', 'adj.', '似是而非的;貌似有理的;', 'It is unlikely that the Duke was convinced by such specious arguments.', '公爵不太可能相信这种似是而非的论点。');
INSERT INTO `words` VALUES (8524, 'speck', 'spek', 'n.', '小点;污点;', '...a speck of blood.', '一点血迹');
INSERT INTO `words` VALUES (8525, 'spectacle', 'ˈspektəkl', 'n.', '精彩的表演;壮观的场面;壮观的景象;', 'He looked at me over the tops of his spectacles.', '他从眼镜框的上方看了看我。');
INSERT INTO `words` VALUES (8526, 'spectacles', 'ˈspɛktəklz', 'n.', '精彩的表演;壮观的场面;壮观的景象;', 'He looked at me over the tops of his spectacles.', '他从眼镜框的上方看了看我。');
INSERT INTO `words` VALUES (8527, 'spectacular', 'spekˈtækjələ(r)', 'adj.', '壮观的;壮丽的;令人惊叹的;', '...spectacular views of the Sugar Loaf Mountain...', '甜面包山的壮观景色');
INSERT INTO `words` VALUES (8528, 'spectacularly', '', '', '壮观地，令人吃惊地;', '...spectacular views of the Sugar Loaf Mountain...', '甜面包山的壮观景色');
INSERT INTO `words` VALUES (8529, 'spectator', 'spekˈteɪtə(r)', 'n.', '(尤指体育比赛的)观看者，观众;', 'Thirty thousand spectators watched the final game.', '3 万名观众观看了决赛。');
INSERT INTO `words` VALUES (8530, 'spectators', 'spɛkˈteɪtəz', 'n.', '(尤指体育比赛的)观看者，观众;', 'Thirty thousand spectators watched the final game.', '3 万名观众观看了决赛。');
INSERT INTO `words` VALUES (8531, 'spectral', 'ˈspektrəl', 'adj.', '鬼一般的;幽灵似的;鬼的;', 'She is compelling, spectral, fascinating, an unforgettably unique performer.', '她极具吸引力，清幽如鬼魅，令人着迷，令人难忘，是个独具特色的演员。');
INSERT INTO `words` VALUES (8532, 'speculate', 'ˈspekjuleɪt', 'v.', '推测;猜测;推断;', 'Critics of the project speculate about how many hospitals could be built instead...', '对该项目持批评态度的人在推测如果改作他用不知可以建多少所医院。');
INSERT INTO `words` VALUES (8533, 'speculation', 'ˌspekjuˈleɪʃn', 'n.', '推测;猜测;推断;', 'Critics of the project speculate about how many hospitals could be built instead...', '对该项目持批评态度的人在推测如果改作他用不知可以建多少所医院。');
INSERT INTO `words` VALUES (8534, 'speculative', 'ˈspekjələtɪv', 'adj.', '推测的;猜测的;推断的;', 'The papers ran speculative stories about the mysterious disappearance of Eddie Donagan...', '各家报纸都刊登了有关埃迪·多纳根神秘失踪的猜测性报道。');
INSERT INTO `words` VALUES (8535, 'speech', 'spiːtʃ', 'n.', '演说;讲话;发言;', '...the development of speech in children...', '儿童言语能力的发展');
INSERT INTO `words` VALUES (8536, 'speechless', 'ˈspiːtʃləs', 'adj.', '(尤指气得或惊讶得)说不出话的;', 'Alex was almost speechless with rage and despair.', '亚历克斯又生气又绝望，几乎说不出话来。');
INSERT INTO `words` VALUES (8537, 'speedy', 'ˈspiːdi', 'adj.', '迅速的;尽快的;快速的;', 'We wish Bill a speedy recovery...', '我们祝愿比尔快快康复。');
INSERT INTO `words` VALUES (8538, 'speller', 'ˈspelə(r)', 'n.', '拼写能力强(或差)的人;', 'I am an absolutely appalling speller.', '我的拼写能力极差。');
INSERT INTO `words` VALUES (8539, 'spelling', 'ˈspelɪŋ', 'n.', '拼写;拼写能力;拼法;', 'In most languages adjectives have slightly different spellings for masculine and feminine...', '在多数语言中，形容词的阳性和阴性的拼法略为不同。');
INSERT INTO `words` VALUES (8540, 'spend', 'spend', 'v.', '用，花(钱);花(时间);度过;', 'By the end of the holiday I had spent all my money...', '到假期结束时，我已经花光了自己所有的钱。');
INSERT INTO `words` VALUES (8541, 'sperm', 'spɜːm', 'n.', '精子;精液;', 'Conception occurs when a single sperm fuses with an egg...', '当一个精子和一个卵子结合时受孕就发生了。');
INSERT INTO `words` VALUES (8542, 'spew', 'spjuː', 'v.', '(使)喷出，涌出;呕吐;', 'The volcano spewed out more scorching volcanic ashes, gases and rocks...', '火山喷出更多灼热的火山灰、气体和岩块。');
INSERT INTO `words` VALUES (8543, 'spherical', 'ˈsferɪkl', 'adj.', '球形的;球状的;', '...purple and gold spherical earrings.', '紫色和金色的球形耳环');
INSERT INTO `words` VALUES (8544, 'sphinx', 'sfɪŋks', 'n.', '斯芬克斯，狮身人面像(古埃及石像;在希腊神话中，斯芬克斯说话惯用谜语);', 'It suited him to play the sphinx.', '让他演神秘莫测的人很合适。');
INSERT INTO `words` VALUES (8545, 'spice', 'spaɪs', 'n.', '(调味)香料;额外的趣味(或刺激等);', '...herbs and spices.', '香草和香料');
INSERT INTO `words` VALUES (8546, 'spiced', 'spaɪst', 'adj.', '加了香料的;加了佐料的;', 'Every dish was served heavily spiced.', '端上来的每道菜都加了很多佐料。');
INSERT INTO `words` VALUES (8547, 'spicy', 'ˈspaɪsi', 'adj.', '加有香料的;用香料调味的;刺激的;', 'Thai food is hot and spicy.', '泰国菜味道辛辣浓烈。');
INSERT INTO `words` VALUES (8548, 'spidery', 'ˈspaɪdəri', 'adj.', '(像蜘蛛腿一样)细长的;', 'He saw her spidery writing on the envelope.', '他在信封上看到了她那细长的笔迹。');
INSERT INTO `words` VALUES (8549, 'spike', 'spaɪk', 'n.', '尖状物;尖头;尖刺;', '...a 15-foot wall topped with iron spikes...', '顶端装有铁刺的 15 英尺高的墙');
INSERT INTO `words` VALUES (8550, 'spikes', 'spaɪks', 'n.', '尖状物;尖头;尖刺;', '...a 15-foot wall topped with iron spikes...', '顶端装有铁刺的 15 英尺高的墙');
INSERT INTO `words` VALUES (8551, 'spiky', 'ˈspaɪki', 'adj.', '有尖刺的;刺猬式的;动辄生气的;', 'Her short spiky hair is damp with sweat.', '她的刺头被汗打湿了。');
INSERT INTO `words` VALUES (8552, 'spin', 'spɪn', 'v.', '(使)快速旋转;(使)急转身，猛转回头，急转弯;纺(线);', 'The latest discs, used for small portable computers, spin 3600 times a minute...', '用在小型便携式计算机上的最新光盘转速为每分钟 3,600 转。');
INSERT INTO `words` VALUES (8553, 'spinal', 'ˈspaɪnl', 'adj.', '脊的;脊柱的;脊髓的;', '...spinal fluid.', '脊椎液');
INSERT INTO `words` VALUES (8554, 'spindly', 'ˈspɪndli', 'adj.', '长而纤弱的;细长而瘦弱的;', 'I did have rather spindly legs.', '我的腿的确比较瘦长。');
INSERT INTO `words` VALUES (8555, 'spineless', 'ˈspaɪnləs', 'adj.', '没有骨气的;怯懦的;无脊椎的;', '...bureaucrats and spineless politicians.', '官僚和懦弱的政客们');
INSERT INTO `words` VALUES (8556, 'spinning', 'ˈspɪnɪŋ', 'n.', '纺线(手艺);纺纱(手艺);(通常有教练带领的)动感单车练习;', 'The latest discs, used for small portable computers, spin 3600 times a minute...', '用在小型便携式计算机上的最新光盘转速为每分钟 3,600 转。');
INSERT INTO `words` VALUES (8557, 'spiny', 'ˈspaɪni', 'adj.', '有刺毛的;带刺的;多刺的;', '...a spiny lobster.', '刺龙虾');
INSERT INTO `words` VALUES (8558, 'spirit', 'ˈspɪrɪt', 'n.', '精神;心灵;情绪;', 'The human spirit is virtually indestructible...', '人的精神实际上是不可摧毁的。');
INSERT INTO `words` VALUES (8559, 'spirited', 'ˈspɪrɪtɪd', 'adj.', '精神饱满的;坚定的;勇猛的;', 'This television program provoked a spirited debate in the United Kingdom.', '这个电视节目在英国引起了激烈的辩论。');
INSERT INTO `words` VALUES (8560, 'spirits', 'ˈspɪrɪts', 'n.', '精神;心灵;情绪;', 'The human spirit is virtually indestructible...', '人的精神实际上是不可摧毁的。');
INSERT INTO `words` VALUES (8561, 'spiritual', 'ˈspɪrɪtʃuəl', 'adj.', '精神的;心灵的;宗教的;', 'She lived entirely by spiritual values, in a world of poetry and imagination.', '她追求精神上的满足，完全生活在一个诗歌和想象的世界里。');
INSERT INTO `words` VALUES (8562, 'spirituality', 'ˌspɪrɪtʃuˈæləti', 'n.', '精神性;灵性;', 'She lived entirely by spiritual values, in a world of poetry and imagination.', '她追求精神上的满足，完全生活在一个诗歌和想象的世界里。');
INSERT INTO `words` VALUES (8563, 'spiritually', 'ˈspɪrɪtjʊəli', 'adv.', '在精神上地;', 'She lived entirely by spiritual values, in a world of poetry and imagination.', '她追求精神上的满足，完全生活在一个诗歌和想象的世界里。');
INSERT INTO `words` VALUES (8564, 'spite', 'spaɪt', 'n.', '恶意;怨恨;', 'Josef Krips at the State Opera hired her in spite of the fact that she had never sung on stage...', '尽管她从未在舞台上演唱过，国家歌剧院的约瑟夫·克里普斯还是聘用了她。');
INSERT INTO `words` VALUES (8565, 'spiteful', 'ˈspaɪtfl', 'adj.', '恶意的;居心不良的;故意使人苦恼的;', 'He could be spiteful.', '他可能怀有恶意。');
INSERT INTO `words` VALUES (8566, 'spittle', 'ˈspɪtl', 'n.', '唾沫;口水;', 'Spittle oozed down his jaw.', '唾液顺着他的下巴流下来。');
INSERT INTO `words` VALUES (8567, 'splash', 'splæʃ', 'v.', '泼洒;哗啦哗啦地溅;噼里啪啦地落;', 'A lot of people were in the water, swimming or simply splashing about...', '水里有很多人在游泳或只是在戏水玩耍。');
INSERT INTO `words` VALUES (8568, 'splendid', 'ˈsplendɪd', 'adj.', '极佳的;非常好的;壮丽的;', 'The book includes a wealth of splendid photographs...', '该书中有大量精彩的照片。');
INSERT INTO `words` VALUES (8569, 'splendidly', '', 'adv.', '壮丽地;豪华地;', 'The book includes a wealth of splendid photographs...', '该书中有大量精彩的照片。');
INSERT INTO `words` VALUES (8570, 'splenetic', 'spləˈnetɪk', 'adj.', '脾气坏的;常发怒的;', '...retired military men with splenetic opinions.', '满腹怨言的退伍军人');
INSERT INTO `words` VALUES (8571, 'splice', 'splaɪs', 'v.', '绞接，捻接(两段绳子);胶接，粘接(胶片、磁带等);', 'He taught me to edit and splice film...', '他教我剪辑和粘接胶片。');
INSERT INTO `words` VALUES (8572, 'splinter', 'ˈsplɪntə(r)', 'n.', '(木头、金属、玻璃等的)尖碎片，尖细条;', '...splinters of glass.', '玻璃碎片');
INSERT INTO `words` VALUES (8573, 'splurge', 'splɜːdʒ', 'n.', '乱花钱;糟蹋钱;挥霍;', 'We splurged on Bohemian glass for gifts, and for ourselves.', '我们花了很多钱买波希米亚玻璃制品送人和自用。');
INSERT INTO `words` VALUES (8574, 'splutter', 'ˈsplʌtə(r)', 'v.', '气急败坏地说;慌张地说;发噗噗声;', '\'But it cannot be,\' he spluttered...', '“但这不可能，”他气急败坏地说道。');
INSERT INTO `words` VALUES (8575, 'spokesman', 'ˈspəʊksmən', 'n.', '发言人;', 'A UN spokesman said that the mission will carry 20 tons of relief supplies.', '联合国的一名男发言人说，该代表团将携带 20 吨救济物资。');
INSERT INTO `words` VALUES (8576, 'spokesperson', 'ˈspəʊkspɜːsn', 'n.', '发言人;', 'A spokesperson for Amnesty, Norma Johnston, describes some cases.', '大赦国际的一名发言人诺尔马·约翰斯顿讲述了几个案例。');
INSERT INTO `words` VALUES (8577, 'spokeswoman', 'ˈspəʊkswʊmən', 'n.', '发言人;', 'A United Nations spokeswoman in New York said the request would be considered.', '联合国的一名女发言人在纽约说，该要求会得到考虑。');
INSERT INTO `words` VALUES (8578, 'sponge', 'spʌndʒ', 'n.', '(或天然或人造、擦洗物品用的)海绵块;(用以填充家具、垫子等的)人造海绵;海绵动物;', '...a sponge mattress.', '海绵床垫');
INSERT INTO `words` VALUES (8579, 'spongy', 'ˈspʌndʒi', 'adj.', '海绵似的;柔软吸水的;', 'We liked the bike\'s spongy handgrip for keeping our hands away from cold metal...', '我们喜欢这辆自行车的软把手，有了它，我们的双手就不用再接触冰冷的金属了。');
INSERT INTO `words` VALUES (8580, 'sponsor', 'ˈspɒnsə(r)', 'n.', '(广播电视节目、音乐会或运动会的)赞助者，赞助商;为慈善活动捐资的人;义赛(或义演等)捐款者;', 'Mercury, in association with The Independent, is sponsoring Britain\'s first major Pop Art exhibition for over 20 years...', '《信使报》正携手《独立报》一起为 20 多年来英国举办的首届大型通俗艺术展览提供赞助。');
INSERT INTO `words` VALUES (8581, 'sponsorship', 'ˈspɒnsəʃɪp', 'n.', '资助;赞助款;主办;', 'Campbell is one of an ever-growing number of skiers in need of sponsorship...', '坎贝尔是越来越多需要得到资助的滑雪运动员之一。');
INSERT INTO `words` VALUES (8582, 'spontaneity', 'ˌspɒntəˈneɪəti', 'n.', '自发性;自然;', 'He had the spontaneity of a child.', '他有着孩童般的率性。');
INSERT INTO `words` VALUES (8583, 'spontaneous', 'spɒnˈteɪniəs', 'adj.', '自发的;非筹划安排的;常心血来潮的;', 'Diana\'s house was crowded with happy people whose spontaneous outbursts of song were accompanied by lively music...', '戴安娜的家里挤满了兴奋的人们，他们伴着欢快的音乐不由自主地引吭高歌。');
INSERT INTO `words` VALUES (8584, 'spontaneously', 'spɒn\'teɪnɪəslɪ', 'adv.', '自发;自然;自动;', 'Diana\'s house was crowded with happy people whose spontaneous outbursts of song were accompanied by lively music...', '戴安娜的家里挤满了兴奋的人们，他们伴着欢快的音乐不由自主地引吭高歌。');
INSERT INTO `words` VALUES (8585, 'spoof', 'spuːf', 'n.', '(对电影、电视节目等的)滑稽模仿;', '...a spoof on Hollywood life.', '拿好莱坞生活开玩笑的作品');
INSERT INTO `words` VALUES (8586, 'spoon', 'spuːn', 'n.', '勺;匙;调羹;', 'He stirred his coffee with a spoon.', '他用一把小勺搅着自己的咖啡。');
INSERT INTO `words` VALUES (8587, 'spoonful', 'ˈspuːnfʊl', 'n.', '一勺(的量);', 'He took a spoonful of the stew and ate it.', '他舀了一匙炖菜吃了。');
INSERT INTO `words` VALUES (8588, 'sporadic', 'spəˈrædɪk', 'adj.', '偶尔发生的;间或出现的;阵发性的;', '...a year of sporadic fighting over northern France...', '法国北部发生零星战斗的一年');
INSERT INTO `words` VALUES (8589, 'sporadically', 'spə\'rædɪkli', 'adv.', '偶发地;', '...a year of sporadic fighting over northern France...', '法国北部发生零星战斗的一年');
INSERT INTO `words` VALUES (8590, 'sport', 'spɔːt', 'n.', '体育运动;(某项)体育运动;(用作友好称呼，尤指对男子)朋友，老兄，哥们儿;', 'I\'d say football is my favourite sport...', '我最喜欢的运动要数足球了。');
INSERT INTO `words` VALUES (8591, 'sporting', 'ˈspɔːtɪŋ', 'adj.', '体育运动的;风格高的;有良好体育风尚的;', '...major sporting events, such as Wimbledon and the World Cup finals.', '主要的体育赛事，如温布尔登网球赛及世界杯决赛');
INSERT INTO `words` VALUES (8592, 'sports', 'spɔːts', 'n.', '体育运动;(某项)体育运动;(用作友好称呼，尤指对男子)朋友，老兄，哥们儿;', 'I\'d say football is my favourite sport...', '我最喜欢的运动要数足球了。');
INSERT INTO `words` VALUES (8593, 'sportsmanship', 'ˈspɔːtsmənʃɪp', 'n.', '运动员风范，体育精神(多指体育比赛中光明磊落、有气度);', 'The team also won praise for sportsmanship and fair play.', '该队还因其良好的体育精神及公平竞争精神而受到了表扬。');
INSERT INTO `words` VALUES (8594, 'sporty', 'ˈspɔːti', 'adj.', '爱好(或擅长)体育运动的;漂亮帅气的;适合运动时穿着的;', 'The steering and braking are exactly what you want from a sporty car.', '其转向和制动装置都完全符合一辆运动型汽车的标准。');
INSERT INTO `words` VALUES (8595, 'spot', 'spɒt', 'n.', '斑点;污迹;污渍;', 'The leaves have yellow areas on the top and underneath are powdery orange spots...', '叶子的表面有黄色斑块，背面则有桔色粉状圆点。');
INSERT INTO `words` VALUES (8596, 'spotless', 'ˈspɒtləs', 'adj.', '极清洁的;非常洁净的;', 'Each morning cleaners make sure everything is spotless...', '每天早晨，清洁工们会把一切都打扫得干干净净。');
INSERT INTO `words` VALUES (8597, 'spots', 'spɒts', 'n.', '斑点;污迹;污渍;', 'The leaves have yellow areas on the top and underneath are powdery orange spots...', '叶子的表面有黄色斑块，背面则有桔色粉状圆点。');
INSERT INTO `words` VALUES (8598, 'spotted', 'ˈspɒtɪd', 'adj.', '有花点的;有斑点的;', '...hand-painted spotted cups and saucers in green and blue...', '带有手绘圆点的绿色和蓝色茶杯和茶托');
INSERT INTO `words` VALUES (8599, 'spotty', 'ˈspɒti', 'adj.', '多丘疹的;多粉刺的;', 'She was rather fat, and her complexion was muddy and spotty.', '她比较胖，脸色暗淡、多斑。');
INSERT INTO `words` VALUES (8600, 'spout', 'spaʊt', 'n.', '(容器的)嘴;(喷出的)水柱，液体柱;', 'He replaced the boiler when the last one began to spout flames...', '原来的锅炉开始往外喷火苗后他便换了个新的。');
INSERT INTO `words` VALUES (8601, 'sprain', 'spreɪn', 'v.', '扭伤(关节);', 'He fell and sprained his ankle.', '他摔了一跤，扭伤了脚踝。');
INSERT INTO `words` VALUES (8602, 'sprawl', 'sprɔːl', 'v.', '伸开四肢坐(或躺);蔓延;杂乱无序地拓展;', 'She sprawled on the bed as he had left her, not even moving to cover herself up...', '他离开她后她就摊手摊脚地躺在床上，甚至都懒得往身上盖点儿东西。');
INSERT INTO `words` VALUES (8603, 'spray', 'spreɪ', 'n.', '浪花;水花;飞沫;', 'The moon was casting a rainbow through the spray from the waterfall...', '月亮在瀑布溅起的水雾上照出了一道彩虹。');
INSERT INTO `words` VALUES (8604, 'spread', 'spred', 'v.', '展开;打开;摊开;', 'She spread a towel on the sand and lay on it...', '她在沙滩上铺了一条毛巾，躺在上面。');
INSERT INTO `words` VALUES (8605, 'spreading', 'ˈspredɪŋ', 'v.', '展开;打开;摊开;', 'She spread a towel on the sand and lay on it...', '她在沙滩上铺了一条毛巾，躺在上面。');
INSERT INTO `words` VALUES (8606, 'spree', 'spriː', 'n.', '(常指过分)玩乐，作乐;纵乐;一阵，一通(犯罪活动);', 'Some Americans went on a spending spree in December to beat the new tax.', '一些美国人在 12 月疯狂花钱，以避开新税。');
INSERT INTO `words` VALUES (8607, 'sprightly', 'ˈspraɪtli', 'adj.', '精力充沛的;精神矍铄的;', '...the sprightly 85-year-old President.', '精神矍铄的 85 岁的总统');
INSERT INTO `words` VALUES (8608, 'spring', 'sprɪŋ', 'n.', '春天;春季;弹簧;', 'The Labor government of Western Australia has an election due next spring...', '西澳大利亚工党政府明年春天要举行选举。');
INSERT INTO `words` VALUES (8609, 'springboard', 'ˈsprɪŋbɔːd', 'n.', '(跳水或体操中的)跳板;(有助于开展某事的)基础，出发点;', 'The 1981 budget was the springboard for an economic miracle...', '1981 年的预算促成了一场经济奇迹的出现。');
INSERT INTO `words` VALUES (8610, 'springy', 'ˈsprɪŋi', 'adj.', '有弹性(或弹力)的;矫健的;有活力的;', 'Steam for about 12 mins until the cake is risen and springy to touch in the centre.', '蒸上 12 分钟左右，直到蛋糕膨起，中心摸起来很有弹性。');
INSERT INTO `words` VALUES (8611, 'sprinkle', 'ˈsprɪŋkl', 'v.', '撒;洒;把…撒(或洒)在…上;', 'Sprinkle the meat with salt and place  in the pan...', '在肉上撒上盐，然后放进平底锅。');
INSERT INTO `words` VALUES (8612, 'sprinkling', 'ˈsprɪŋklɪŋ', 'n.', '少量(落、撒或包含的某种物质);少数(分散在或包括在某处的人);', '...a light sprinkling of snow...', '薄薄的一层雪');
INSERT INTO `words` VALUES (8613, 'sprint', 'sprɪnt', 'v.', '短距离快速奔跑(或游泳);冲刺;', 'Rob Harmeling won the sprint in Bordeaux.', '罗布·哈梅林在波尔多获得短跑冠军。');
INSERT INTO `words` VALUES (8614, 'sprout', 'spraʊt', 'v.', '发芽;抽芽;抽条;', 'It only takes a few days for beans to sprout.', '豆子只要几天就能发芽。');
INSERT INTO `words` VALUES (8615, 'spruce', 'spruːs', 'n.', '云杉;云杉木;', 'Trees such as spruce, pine and oak have been planted.', '种植了云杉、松树和橡树等树木。');
INSERT INTO `words` VALUES (8616, 'spunk', 'spʌŋk', 'n.', '勇气;胆量;决心;', 'I admired her independence and her spunk.', '我敬佩她的独立精神和勇气。');
INSERT INTO `words` VALUES (8617, 'spur', 'spɜː(r)', 'n.', '马刺;靴刺;鞭策;', 'It\'s the money that spurs these fishermen to risk a long ocean journey in their flimsy boats...', '是金钱驱使这些渔民驾着破旧小船冒险出海远航。');
INSERT INTO `words` VALUES (8618, 'spurious', 'ˈspjʊəriəs', 'adj.', '虚假的;伪造的;建立在错误的观念(或思想方法)之上的;', 'He was arrested in 1979 on spurious corruption charges...', '他于1979年因被诬告贪污而被捕。');
INSERT INTO `words` VALUES (8619, 'spurn', 'spɜːn', 'v.', '(尤指傲慢地)拒绝;', 'He spurned the advice of management consultants...', '他拒绝了管理顾问们的建议。');
INSERT INTO `words` VALUES (8620, 'spurt', 'spɜːt', 'v.', '喷出;冒出;(短暂地)加速前进;', 'They spurted blood all over me. I nearly passed out...', '他们的血喷了我一身。我几乎昏了过去。');
INSERT INTO `words` VALUES (8621, 'sputter', 'ˈspʌtə(r)', 'v.', '(引擎、灯或火)发噼啪声;气急败坏地说;急促而语无伦次地说;', 'The truck sputtered and stopped...', '卡车噼啪响了几声停下了。');
INSERT INTO `words` VALUES (8622, 'spy', 'spaɪ', 'n.', '间谍;密探;', 'He was jailed for five years as an alleged British spy...', '他被指控为英国间谍，入狱关押了 5 年。');
INSERT INTO `words` VALUES (8623, 'squabble', 'ˈskwɒbl', 'v.', '(为琐事)争吵，发生口角;', 'Mother is devoted to Dad although they squabble all the time...', '母亲很爱父亲，尽管他们总是为琐事拌嘴。');
INSERT INTO `words` VALUES (8624, 'squad', 'skwɒd', 'n.', '(对付某类犯罪活动的)警察队伍;运动(代表)队;(军队的)班;', 'The building was evacuated and the bomb squad called...', '大楼里进行了人员疏散，并叫来了拆弹小组。');
INSERT INTO `words` VALUES (8625, 'squadron', 'ˈskwɒdrən', 'n.', '(空军或海军的)中队;', 'The government said it was preparing a squadron of eighteen Mirage fighter planes.', '政府称其正筹建一个由18架“幻影”战斗机组成的空军中队。');
INSERT INTO `words` VALUES (8626, 'squalid', 'ˈskwɒlɪd', 'adj.', '肮脏的;邋遢的;道德败坏的;', 'He followed her up a rickety staircase to a squalid bedsit...', '他跟着她上了一段摇摇晃晃的楼梯，来到了一间邋遢的卧室起居室两用房间。');
INSERT INTO `words` VALUES (8627, 'squall', 'skwɔːl', 'n.', '飑(常指暴风雨或暴风雪中突起的狂风);', 'The boat was hit by a squall north of the island.', '船在岛的北边遇到了狂飑。');
INSERT INTO `words` VALUES (8628, 'squalor', 'ˈskwɒlə(r)', 'n.', '肮脏;邋遢;', 'He was out of work and living in squalor.', '他失业了，生活潦倒。');
INSERT INTO `words` VALUES (8629, 'squander', 'ˈskwɒndə(r)', 'v.', '浪费，挥霍(金钱、时间等);', 'Hooker didn\'t squander his money on flashy cars or other vices...', '胡克没有将自己的钱浪费在豪华轿车或其他恶习上。');
INSERT INTO `words` VALUES (8630, 'square', 'skweə(r)', 'adj.', '正方形的;四方形的;成直角的;', 'Serve the cake warm or at room temperature, cut in squares...', '等蛋糕温热或凉至室温时切成四方块端上桌。');
INSERT INTO `words` VALUES (8631, 'squarely', 'ˈskweəli', 'adv.', '正对着地;径直地;不偏不倚地;', 'I kept the gun aimed squarely at his eyes.', '我一直将枪径直瞄准他的双眼。');
INSERT INTO `words` VALUES (8632, 'squash', 'skwɒʃ', 'v.', '压软(或挤软、压坏、压扁等);把…压(或挤)变形;(使)挤进;', 'Robert was lucky to escape with just a broken foot after being squashed against a fence by a car...', '罗伯特很幸运，他被一辆轿车挤压到一排栅栏上后只是一只脚骨折。');
INSERT INTO `words` VALUES (8633, 'squat', 'skwɒt', 'v.', '蹲坐;蹲;偷住，擅自占用(房子或地方);', 'He squatted, grunting at the pain in his knees...', '他蹲在那儿，双膝的伤痛得他直哼哼。');
INSERT INTO `words` VALUES (8634, 'squeak', 'skwiːk', 'v.', '短促而尖厉地叫;吱吱叫;嘎吱作响;', 'My boots squeaked a little as I walked...', '我走路时靴子有点儿嘎吱嘎吱响。');
INSERT INTO `words` VALUES (8635, 'squeal', 'skwiːl', 'v.', '尖声长叫;发出长而尖的声音;(尤指激动或紧张时)尖声说，高声嚷着说;', 'Jennifer squealed with delight and hugged me...', '珍妮弗高兴地尖叫起来，然后拥抱了我。');
INSERT INTO `words` VALUES (8636, 'squeamish', 'ˈskwiːmɪʃ', 'adj.', '易心烦意乱的;易恶心的;神经脆弱的;', 'I\'m terribly squeamish. I can\'t bear gory films...', '我非常容易受到惊吓。我受不了暴力血腥的电影。');
INSERT INTO `words` VALUES (8637, 'squeeze', 'skwiːz', 'v.', '挤压;捏;(从某物中)榨出，挤出，拧出;', 'He squeezed her arm reassuringly...', '他安慰地捏了捏她的胳膊。');
INSERT INTO `words` VALUES (8638, 'squelch', 'skweltʃ', 'v.', '发吧唧声，发扑哧声(如走在泥泞中似的);制止;压制;', 'He squelched across the turf...', '他扑哧扑哧地走过草皮。');
INSERT INTO `words` VALUES (8639, 'squint', 'skwɪnt', 'v.', '瞇着眼睛看;斜视;(人)患斜视;', 'The girl squinted at the photograph...', '女孩觑着眼看那张照片。');
INSERT INTO `words` VALUES (8640, 'squirm', 'skwɜːm', 'v.', '(因紧张、不舒服等)动来动去，来回扭动，坐卧不宁;十分尴尬;羞愧难当;', 'He had squirmed and wriggled and screeched when his father had washed his face...', '父亲给他洗脸时，他扭来扭去，尖声喊叫。');
INSERT INTO `words` VALUES (8641, 'squirt', 'skwɜːt', 'v.', '(使)喷射;喷;(用…)向…喷射;', 'Norman cut open his pie and squirted tomato sauce into it...', '诺尔曼切开自己的派，往里面挤了点番茄酱。');
INSERT INTO `words` VALUES (8642, 'stab', 'stæb', 'v.', '(用刀等锐器)刺，戳，捅;(用手指或尖物)戳，刺;', 'Somebody stabbed him in the stomach...', '有人朝他肚子上捅了一刀。');
INSERT INTO `words` VALUES (8643, 'stable', 'ˈsteɪbl', 'adj.', '稳定的;稳固的;牢固的;', 'The price of oil should remain stable for the rest of 1992.', '油价会在 1992 年剩下的时间里保持稳定。');
INSERT INTO `words` VALUES (8644, 'staccato', 'stəˈkɑːtəʊ', 'adj.', '断音的;断奏的;发不连贯爆发性声音的;', 'He spoke in Arabic, a short staccato burst.', '他突然用阿拉伯语叽里呱啦说了一串话。');
INSERT INTO `words` VALUES (8645, 'stack', 'stæk', 'n.', '(通常指码放整齐的)一叠，一摞，一堆;大量;许多;', 'There were stacks of books on the bedside table and floor.', '床头桌和地板上有几摞书。');
INSERT INTO `words` VALUES (8646, 'stadium', 'ˈsteɪdiəm', 'n.', '体育场;运动场;', '...a baseball stadium.', '棒球场');
INSERT INTO `words` VALUES (8647, 'staff', 'stɑːf', 'n.', '全体职工(或雇员);(大、中、小学的)管理人员，行政人员;(军队的)全体参谋人员;', 'The staff were very good...', '职员都很出色。');
INSERT INTO `words` VALUES (8648, 'stage', 'steɪdʒ', 'n.', '(发展或进展的)时期，阶段，状态;段;步;', 'The way children talk about or express their feelings depends on their age and stage of development...', '儿童谈论或表达自己感情的方式取决于他们的年龄和所处的发育阶段。');
INSERT INTO `words` VALUES (8649, 'stagger', 'ˈstæɡə(r)', 'v.', '摇摇晃晃地走;蹒跚;踉跄;', 'He lost his balance, staggered back against the rail and toppled over...', '他失去平衡，往后趔趄了一下，撞到栏杆摔倒了。');
INSERT INTO `words` VALUES (8650, 'staggering', 'ˈstæɡərɪŋ', 'adj.', '令人难以相信的;', '...a three-year contract reputed to be worth a staggering £5,000-a-week...', '据称高达每周 5,000 英镑的三年期合同');
INSERT INTO `words` VALUES (8651, 'staging', 'ˈsteɪdʒɪŋ', 'n.', '演出形式;演出风格;临时工作台(或工作架);', 'The way children talk about or express their feelings depends on their age and stage of development...', '儿童谈论或表达自己感情的方式取决于他们的年龄和所处的发育阶段。');
INSERT INTO `words` VALUES (8652, 'stagnant', 'ˈstæɡnənt', 'adj.', '(水或空气)不流动而污浊的;停滞的;不发展的;', 'He is seeking advice on how to revive the stagnant economy...', '他正在就如何振兴低迷的经济征求意见。');
INSERT INTO `words` VALUES (8653, 'stagnate', 'stæɡˈneɪt', 'v.', '停滞;不发展;不进步;', 'Industrial production is stagnating...', '工业生产正停滞不前。');
INSERT INTO `words` VALUES (8654, 'stagnation', 'stæɡ\'neɪʃ(ə)n', 'n.', '停滞;滞止;萧条;', 'Industrial production is stagnating...', '工业生产正停滞不前。');
INSERT INTO `words` VALUES (8655, 'staid', 'steɪd', 'adj.', '没意思的;古板的;一本正经的;', '...a staid seaside resort.', '了无生气的海滨度假胜地');
INSERT INTO `words` VALUES (8656, 'stain', 'steɪn', 'v.', '(被)玷污;留下污渍;给…染色(或着色);', 'Remove stains by soaking in a mild solution of bleach.', '用温和的漂白剂浸泡以去除污迹。');
INSERT INTO `words` VALUES (8657, 'stair', 'steə(r)', 'n.', '楼梯;梯级;', 'Nancy began to climb the stairs...', '南希开始爬楼梯。');
INSERT INTO `words` VALUES (8658, 'staircase', 'ˈsteəkeɪs', 'n.', '(建筑物内的)楼梯;', 'They walked down the staircase together.', '他们一起下了楼梯。');
INSERT INTO `words` VALUES (8659, 'stairs', 'steəz', 'n.', '楼梯;梯级;', 'Nancy began to climb the stairs...', '南希开始爬楼梯。');
INSERT INTO `words` VALUES (8660, 'stake', 'steɪk', 'n.', '桩;标桩;篱笆桩;', 'The tension was naturally high for a game with so much at stake...', '对于一场成败攸关的比赛来说气氛自然非常紧张。');
INSERT INTO `words` VALUES (8661, 'stale', 'steɪl', 'adj.', '不新鲜的;(空气)污浊的;(烟味)难闻的;', 'Their daily diet consisted of a lump of stale bread, a bowl of rice and stale water.', '他们的日常饮食包括一块不新鲜的面包、一碗米饭和变味的水。');
INSERT INTO `words` VALUES (8662, 'stalemate', 'ˈsteɪlmeɪt', 'n.', '(辩论或竞赛中出现的)僵局，僵持局面;僵局;和棋;', 'President Bush has ended the stalemate over moves to cut the country\'s budget deficit...', '布什总统已结束了在削减国家预算赤字的措施上形成的僵局。');
INSERT INTO `words` VALUES (8663, 'stalk', 'stɔːk', 'n.', '(叶)柄;(花)梗;(果实的)柄;', 'A single pale blue flower grows up from each joint on a long stalk.', '一条长长的茎秆的每个节上都会长出一朵浅蓝色的花。');
INSERT INTO `words` VALUES (8664, 'stall', 'stɔːl', 'n.', '货摊，摊位，售货亭(尤指集市上的);牲畜棚;马厩;', 'The Social Democratic Party has vowed to try to stall the bill until the current session ends.', '社会民主党誓言要将该议案拖到本次议会会议结束之后。');
INSERT INTO `words` VALUES (8665, 'stalls', 'stɔːlz', 'n.', '货摊，摊位，售货亭(尤指集市上的);牲畜棚;马厩;', 'The Social Democratic Party has vowed to try to stall the bill until the current session ends.', '社会民主党誓言要将该议案拖到本次议会会议结束之后。');
INSERT INTO `words` VALUES (8666, 'stalwart', 'ˈstɔːlwət', 'n.', '(政党等组织的)忠诚拥护者，坚定分子;', 'His free-trade policies aroused suspicion among Tory stalwarts...', '他的自由贸易政策在保守党的忠实拥护者中间引起了怀疑。');
INSERT INTO `words` VALUES (8667, 'stamina', 'ˈstæmɪnə', 'n.', '耐力;耐性;持久力;', 'You have to have a lot of stamina to be a top-class dancer.', '你必须有很强的毅力才能成为一流的舞蹈演员。');
INSERT INTO `words` VALUES (8668, 'stammer', 'ˈstæmə(r)', 'v.', '口吃;结结巴巴地说;', 'Five per cent of children stammer at some point...', '5%的儿童在某个时期会口吃。');
INSERT INTO `words` VALUES (8669, 'stamp', 'stæmp', 'n.', '邮票;印;章;', '...a book of stamps...', '邮票册');
INSERT INTO `words` VALUES (8670, 'stampede', 'stæmˈpiːd', 'n.', '(人群的)奔逃，蜂拥;(兽群的)惊跑，狂奔;热潮;', 'There was a stampede for the exit.', '大家都争先恐后地朝出口涌去。');
INSERT INTO `words` VALUES (8671, 'stance', 'stæns', 'n.', '(公开表明的)观点，态度，立场;(尤指体育运动中的)站立姿势;', 'The Congress had agreed  to reconsider its stance on the armed struggle...', '国会已同意重新考虑其对武装斗争的态度。');
INSERT INTO `words` VALUES (8672, 'stand', 'stænd', 'v.', '站立;立;直立;', 'She was standing beside my bed staring down at me...', '她站在我的床边低头盯着我。');
INSERT INTO `words` VALUES (8673, 'stand-alone', 'ˈstænd ələʊn', 'adj.', '独立的;', 'They plan to relaunch it as a stand-alone company.', '他们计划让它作为一个独立公司重新上市。');
INSERT INTO `words` VALUES (8674, 'standard', 'ˈstændəd', 'n.', '(品质的)标准，水平，规格，规范;正常的水平;应达到的标准;', 'The standard of professional cricket has never been lower...', '职业板球的水平从来没有这么低过。');
INSERT INTO `words` VALUES (8675, 'standby', 'ˈstændbaɪ', 'n.', '后备人员;备用物品;', 'Canned varieties of beans and pulses are a good standby...', '各种豆类罐头制品是很好的常备食品。');
INSERT INTO `words` VALUES (8676, 'stand-in', 'ˈstænd ɪn', 'n.', '代行职务者;(尤指影视中危险动作的)替身演员;', 'He was a stand-in for my regular doctor.', '他临时代替平常给我看病的那位医生。');
INSERT INTO `words` VALUES (8677, 'standing', 'ˈstændɪŋ', 'adj.', '长期存在的;永久性的;常设的;', '...an artist of international standing...', '具有国际名望的艺术家');
INSERT INTO `words` VALUES (8678, 'standoffish', 'ˈstændˈɔːfɪʃ', 'adj.', '冷漠的;冷淡的;疏远的;', 'He can be quite stand-offish and rude, even to his friends.', '他有时会很冷淡，很粗鲁，甚至对自己的朋友也是如此。');
INSERT INTO `words` VALUES (8679, 'standout', 'ˈstændaʊt', 'n.', '突出的人(或事物);', 'In the earlier rounds, Ferguson and Dickinson were the standouts...', '在最初的几轮中，弗格森和迪金森表现突出。');
INSERT INTO `words` VALUES (8680, 'standpoint', 'ˈstændpɔɪnt', 'n.', '立场;观点;', 'He believes that from a military standpoint, the situation is under control...', '他认为从军事角度看，形势已得到了控制。');
INSERT INTO `words` VALUES (8681, 'standstill', 'ˈstændstɪl', 'n.', '停止;停顿;停滞;', 'Abruptly the group ahead of us came to a standstill...', '我们前面的小组突然停了下来。');
INSERT INTO `words` VALUES (8682, 'staple', 'ˈsteɪpl', 'adj.', '主要的;基本的;重要的;', 'Rice is the staple food of more than half the world\'s  population...', '稻米是全世界一半以上人口的主食。');
INSERT INTO `words` VALUES (8683, 'star', 'stɑː(r)', 'n.', '恒星;星;星状物;', 'The night was dark, the stars hidden behind cloud.', '夜很黑，星星都躲在云的后面。');
INSERT INTO `words` VALUES (8684, 'starboard', 'ˈstɑːbəd', 'n.', '(船舶或飞机的)右舷，右侧;', 'He detected a ship moving down the starboard side of the submarine.', '他发现有一艘船正在潜艇的右侧航行。');
INSERT INTO `words` VALUES (8685, 'starch', 'stɑːtʃ', 'n.', '淀粉;含淀粉的食物;(浆衣服、床单等用的)淀粉浆;', 'She reorganised her eating so that she was taking more fruit and vegetables and less starch, salt, and fat.', '她重新调整了自己的饮食，多吃水果、蔬菜，少吃淀粉、盐和脂肪。');
INSERT INTO `words` VALUES (8686, 'starchy', 'ˈstɑːtʃi', 'adj.', '富含淀粉的;拘束的;古板的;', '...starchy and sticky glutinous rices.', '含大量淀粉、黏性大的糯米');
INSERT INTO `words` VALUES (8687, 'star-crossed', 'ˈstɑː krɒst', 'adj.', '命运乖蹇的;不幸的;', '...star-crossed lovers parted by war and conflict.', '因战乱而分离的不幸恋人');
INSERT INTO `words` VALUES (8688, 'stare', 'steə(r)', 'v.', '盯着看;凝视;注视;', 'Tamara stared at him in disbelief, shaking her head...', '塔玛拉一边狐疑地盯着他看，一边摇着头。');
INSERT INTO `words` VALUES (8689, 'stark', 'stɑːk', 'adj.', '了无修饰的;荒凉的;粗陋的;', 'UK companies face a stark choice if they want to stay competitive...', '英国公司如果想要保持竞争力就不得不面对严峻的选择。');
INSERT INTO `words` VALUES (8690, 'starring', 'ˈstɑːrɪŋ', 'v.', '主演;担任主角;使主演;', 'The night was dark, the stars hidden behind cloud.', '夜很黑，星星都躲在云的后面。');
INSERT INTO `words` VALUES (8691, 'starry', 'ˈstɑːri', 'adj.', '布满星星的;像星星的;闪闪发光的;', 'She stared up at the starry sky.', '她抬头凝视繁星点点的夜空。');
INSERT INTO `words` VALUES (8692, 'start', 'stɑːt', 'v.', '开始，着手，动手(做或使用);(使)发生，开始进行;开动;', 'John then unlocked the front door and I started to follow him up the stairs...', '约翰接着打开前门，我开始跟他上楼。');
INSERT INTO `words` VALUES (8693, 'starting', 'ˈstɑːtɪŋ', 'v.', '开始，着手，动手(做或使用);(使)发生，开始进行;开动;', 'John then unlocked the front door and I started to follow him up the stairs...', '约翰接着打开前门，我开始跟他上楼。');
INSERT INTO `words` VALUES (8694, 'startle', 'ˈstɑːtl', 'v.', '使惊吓;使吓一跳;使大吃一惊;', 'The telephone startled him...', '电话铃声吓了他一跳。');
INSERT INTO `words` VALUES (8695, 'startled', 'ˈstɑːtld', 'v.', '使惊吓;使吓一跳;使大吃一惊;', 'The telephone startled him...', '电话铃声吓了他一跳。');
INSERT INTO `words` VALUES (8696, 'startling', 'ˈstɑːtlɪŋ', 'adj.', '惊人的;让人震惊的;极鲜亮的;', 'Sometimes the results may be rather startling.', '有时结果可能会令人吃惊。');
INSERT INTO `words` VALUES (8697, 'starts', 'stɑːts', 'v.', '开始，着手，动手(做或使用);(使)发生，开始进行;开动;', 'John then unlocked the front door and I started to follow him up the stairs...', '约翰接着打开前门，我开始跟他上楼。');
INSERT INTO `words` VALUES (8698, 'start-up', 'ˈstɑːt ʌp', 'adj.', '(新企业或工程)开办阶段的，启动时期的;', 'That is enough to pay the start-up costs for fourteen research projects...', '那足够用来支付 14 个科研项目的启动费用了。');
INSERT INTO `words` VALUES (8699, 'starvation', 'stɑːˈveɪʃn', 'n.', '饥饿;挨饿;饿死;', 'Over three hundred people have died of starvation since the beginning of the year.', '从年初起，已有 300 多人饿死。');
INSERT INTO `words` VALUES (8700, 'starve', 'stɑːv', 'v.', '(使)挨饿，饿死;缺乏…的;急需…的;', 'A number of the prisoners we saw are starving...', '我们看到的一些囚犯快要饿死了。');
INSERT INTO `words` VALUES (8701, 'starved', 'stɑːvd', 'v.', '(使)挨饿，饿死;缺乏…的;急需…的;', 'A number of the prisoners we saw are starving...', '我们看到的一些囚犯快要饿死了。');
INSERT INTO `words` VALUES (8702, 'starving', 'ˈstɑːvɪŋ', 'adj.', '饿极了的;饿得慌的;', 'Apart from anything else I was starving.', '别的不说，我饿坏了。');
INSERT INTO `words` VALUES (8703, 'stash', 'stæʃ', 'v.', '存放;贮藏;隐藏;', 'We went for the bottle of whiskey that we had stashed behind the bookcase...', '我们去拿那瓶藏在书架后面的威士忌。');
INSERT INTO `words` VALUES (8704, 'state', 'steɪt', 'n.', '状态;状况;情况;', 'Mexico is a secular state and does not have diplomatic relations with the Vatican...', '墨西哥是个世俗国家，和梵蒂冈没有外交关系。');
INSERT INTO `words` VALUES (8705, 'stated', 'ˈsteɪtɪd', 'v.', '陈述;说明;声明;', 'Mexico is a secular state and does not have diplomatic relations with the Vatican...', '墨西哥是个世俗国家，和梵蒂冈没有外交关系。');
INSERT INTO `words` VALUES (8706, 'stately', 'ˈsteɪtli', 'adj.', '宏大的;壮观的;气宇不凡的;', 'Instead of moving at his usual stately pace, he was almost running.', '他没像平时那样优雅庄重地走着，而是几乎跑了起来。');
INSERT INTO `words` VALUES (8707, 'statement', 'ˈsteɪtmənt', 'n.', '说明;说法;表白;', 'Andrew now disowns that statement, saying he was depressed when he made it...', '安德鲁现在推翻了那个说法，说那是自己在情绪低落时说的话。');
INSERT INTO `words` VALUES (8708, 'statesman', 'ˈsteɪtsmən', 'n.', '政治家;', 'Hamilton is a great statesman and political thinker.', '汉密尔顿是个伟大的政治家和政治思想家。');
INSERT INTO `words` VALUES (8709, 'statewide', 'ˈsteɪtwaɪd', 'adj./adv.', '(美国)全州性的(地)，在全州范围内的(地);', 'Each year they compete in a prominent statewide bicycle race...', '每年他们都要参加著名的全州自行车大赛。');
INSERT INTO `words` VALUES (8710, 'static', 'ˈstætɪk', 'adj.', '静止的;静态的;停滞的;', 'The number of young people obtaining qualifications has remained static or decreased...', '获得资格证的年轻人数量一直维持不变或出现下降。');
INSERT INTO `words` VALUES (8711, 'station', 'ˈsteɪʃn', 'n.', '火车站;公共汽车站;长途汽车站;', 'Ingrid went with him to the railway station to see him off...', '英格丽德和他一起去火车站送他。');
INSERT INTO `words` VALUES (8712, 'stationary', 'ˈsteɪʃənri', 'adj.', '不动的;静止的;固定的;', 'Stationary cars in traffic jams cause a great deal of pollution...', '交通堵塞中静止不动的汽车产生了大量污染。');
INSERT INTO `words` VALUES (8713, 'statistic', 'stəˈtɪstɪk', 'n.', '统计数字;统计资料;统计学;', 'Official statistics show real wages declining by 24%...', '官方统计数字表明实际工资下降了 24%。');
INSERT INTO `words` VALUES (8714, 'statistical', 'stəˈtɪstɪkl', 'adj.', '统计数据的;统计学的;', 'The report contains a great deal of statistical information...', '报告中包含很多统计资料。');
INSERT INTO `words` VALUES (8715, 'statistically', 'stə\'tɪstɪkli', 'adv.', '统计地;统计上地;', 'The report contains a great deal of statistical information...', '报告中包含很多统计资料。');
INSERT INTO `words` VALUES (8716, 'statistics', 'stəˈtɪstɪks', 'n.', '统计数字;统计资料;统计学;', 'Official statistics show real wages declining by 24%...', '官方统计数字表明实际工资下降了 24%。');
INSERT INTO `words` VALUES (8717, 'stature', 'ˈstætʃə(r)', 'n.', '声望;名望;身高;', 'It\'s more than his physical stature that makes him remarkable...', '他的与众不同之处不光是他的身高。');
INSERT INTO `words` VALUES (8718, 'status', 'ˈsteɪtəs', 'n.', '状态;地位;身份;', 'People of higher status tend more to use certain drugs.', '社会地位较高的人往往更容易吸食某种毒品。');
INSERT INTO `words` VALUES (8719, 'statute', 'ˈstætʃuːt', 'n.', '成文法;法令;法规;', 'The new statute covers the care for, bringing up and protection of children...', '新法令涵盖了儿童保育、养育和保护的相关内容。');
INSERT INTO `words` VALUES (8720, 'statutory', 'ˈstætʃətri', 'adj.', '法定的;依法必须执行的;', 'We had a statutory duty to report to Parliament.', '我们有向议会报告的法定义务。');
INSERT INTO `words` VALUES (8721, 'staunch', 'stɔːntʃ', 'adj.', '忠实的;坚定的;', 'He\'s a staunch supporter of controls on government spending.', '他坚决支持控制政府开支。');
INSERT INTO `words` VALUES (8722, 'stave', 'steɪv', 'n.', '棍;棒;木柱;', 'Many of the men had armed themselves with staves and pieces of iron.', '很多人用棍棒和铁器将自己武装起来。');
INSERT INTO `words` VALUES (8723, 'stay', 'steɪ', 'v.', '停留;待;保持;', '\'Stay here,\' Trish said. \'I\'ll bring the car down the drive to take you back.\'...', '“呆这别动，”特里西说,“我会把车开过来送你回去。”');
INSERT INTO `words` VALUES (8724, 'stead', 'sted', 'n.', '代替;用处，好处;', 'We hope you will consent to act in his stead...', '我们希望你会同意代理他的职务。');
INSERT INTO `words` VALUES (8725, 'steadfast', 'ˈstedfɑːst', 'adj.', '坚定的;不动摇的;', 'He remained steadfast in his belief that he had done the right thing.', '他一直坚信自己做得对。');
INSERT INTO `words` VALUES (8726, 'steadily', '\'stedɪlɪ', 'adv.', '稳定地;坚定地;坚固地;', 'Despite the steady progress of building work, the campaign against it is still going strong...', '尽管建筑施工已在稳步进行，但反对活动仍然高涨。');
INSERT INTO `words` VALUES (8727, 'steady', 'ˈstedi', 'adj.', '(发展、增长等)稳步的，持续的，匀速的;稳定的;恒定的;', 'Despite the steady progress of building work, the campaign against it is still going strong...', '尽管建筑施工已在稳步进行，但反对活动仍然高涨。');
INSERT INTO `words` VALUES (8728, 'steal', 'stiːl', 'v.', '偷;窃取;偷偷地(或悄悄地)移动;', 'He was accused of stealing a small boy\'s bicycle...', '他被指控偷了一个小男孩的自行车。');
INSERT INTO `words` VALUES (8729, 'stealth', 'stelθ', 'n.', '偷偷摸摸;不声张的活动;秘密行动;', 'He claimed Tony Blair is trying to get us into the euro by stealth...', '他声称托尼·布莱尔正试图神不知鬼不觉地让我们加入欧元体系。');
INSERT INTO `words` VALUES (8730, 'stealthily', '', 'adv.', '暗地里，偷偷摸摸地;潜;', 'I would creep in and with stealthy footsteps explore the second-floor...', '我会偷偷溜进去，轻手轻脚地查看一下二楼。');
INSERT INTO `words` VALUES (8731, 'stealthy', 'ˈstelθi', 'adj.', '偷偷摸摸的;不声张的;秘密的;', 'I would creep in and with stealthy footsteps explore the second-floor...', '我会偷偷溜进去，轻手轻脚地查看一下二楼。');
INSERT INTO `words` VALUES (8732, 'steam', 'stiːm', 'n.', '水蒸气;蒸汽;蒸汽动力;', 'In an electric power plant the heat converts water into high-pressure steam.', '在发电厂里，热能将水转化成高压蒸汽。');
INSERT INTO `words` VALUES (8733, 'steamed', 'stiːmd', 'v.', '蒸发;散发蒸汽;冒水汽;', 'In an electric power plant the heat converts water into high-pressure steam.', '在发电厂里，热能将水转化成高压蒸汽。');
INSERT INTO `words` VALUES (8734, 'steel', 'stiːl', 'n.', '钢;钢铁工业;兵器;', '...steel pipes.', '钢管');
INSERT INTO `words` VALUES (8735, 'steep', 'stiːp', 'adj.', '陡的;陡峭的;突然的;', 'San Francisco is built on 40 hills and some are very steep.', '旧金山建在 40 座小山丘上，其中一些非常陡峭。');
INSERT INTO `words` VALUES (8736, 'steepen', 'ˈstiːpən', 'v.', '(使)变陡;', 'The road steepened and then levelled out suddenly.', '路变得陡直起来，然后又突然变平坦了。');
INSERT INTO `words` VALUES (8737, 'steer', 'stɪə(r)', 'v.', '驾驶(船、汽车等);掌控方向盘;行驶;', 'What is it like to steer a ship this size?...', '驾驶这么大的船感觉怎么样？');
INSERT INTO `words` VALUES (8738, 'stellar', 'ˈstelə(r)', 'adj.', '星的;恒星的;优秀的;', 'A stellar wind streams outward from the star.', '从那颗恒星上不断吹出恒星风。');
INSERT INTO `words` VALUES (8739, 'stem', 'stem', 'n.', '(花草的)茎;(花或叶的)梗，柄;(高脚酒杯的)脚;', 'All my problems stem from drink...', '我所有的问题都源于酗酒。');
INSERT INTO `words` VALUES (8740, 'stench', 'stentʃ', 'n.', '臭气;恶臭;', 'The stench of burning rubber was overpowering.', '烧焦的橡胶臭气熏天。');
INSERT INTO `words` VALUES (8741, 'stentorian', 'stenˈtɔːriən', 'adj.', '洪亮的;', 'He bellowed in a stentorian voice.', '他用洪亮的声音大声喊叫。');
INSERT INTO `words` VALUES (8742, 'step', 'step', 'n.', '迈步;脚步声;步伐;', 'I took a step towards him...', '我朝他迈了一步。');
INSERT INTO `words` VALUES (8743, 'stereo', 'ˈsteriəʊ', 'n.', '立体声音响(或录音机等);立体声音响(系统);', '...loudspeakers that give all-around stereo sound.', '具有环绕立体声效果的扩音器');
INSERT INTO `words` VALUES (8744, 'stereotype', 'ˈsteriətaɪp', 'n.', '模式化观念(或形象);老一套;刻板印象;', 'There\'s always been a stereotype about successful businessmen...', '人们对于成功商人一直都有一种固定印象。');
INSERT INTO `words` VALUES (8745, 'stereotyped', 'ˈsteriətaɪpt', 'v.', '对…形成模式化(或类型化)的看法;', 'There\'s always been a stereotype about successful businessmen...', '人们对于成功商人一直都有一种固定印象。');
INSERT INTO `words` VALUES (8746, 'sterile', 'ˈsteraɪl', 'adj.', '不能生育的;不育的;无菌的;', 'He always made sure that any cuts were protected by sterile dressings...', '他总是坚持要用无菌纱布包扎伤口。');
INSERT INTO `words` VALUES (8747, 'sterling', 'ˈstɜːlɪŋ', 'n.', '英镑(英国货币);', 'The stamps had to be paid for in sterling.', '这些邮票必须用英镑购买。');
INSERT INTO `words` VALUES (8748, 'stern', 'stɜːn', 'adj.', '严厉的;苛刻的;要求别人服从的;', 'Mr Straw issued a stern warning to those who persist in violence...', '斯特劳先生向那些坚持进行暴力活动的人发出了严正警告。');
INSERT INTO `words` VALUES (8749, 'sternly', 'ˈstɜːnli', 'adv.', '严厉地;严肃地;一本正经地;', 'Mr Straw issued a stern warning to those who persist in violence...', '斯特劳先生向那些坚持进行暴力活动的人发出了严正警告。');
INSERT INTO `words` VALUES (8750, 'stew', 'stjuː', 'n.', '炖的菜，煨的菜(有肉和蔬菜);', 'She served him a bowl of beef stew...', '她为他端上了一碗炖牛肉。');
INSERT INTO `words` VALUES (8751, 'stickler', 'ˈstɪklə(r)', 'n.', '非常看重(某品质或行为)的人;', 'I\'m a bit of a stickler for accuracy...', '我总是要求精确。');
INSERT INTO `words` VALUES (8752, 'sticky', 'ˈstɪki', 'adj.', '黏(性)的;一面带黏胶的;闷热的;', '...sticky toffee...', '黏牙的太妃糖');
INSERT INTO `words` VALUES (8753, 'stiff', 'stɪf', 'adj.', '不易弯曲(或活动)的;硬的;挺的;', 'The furniture was stiff, uncomfortable, too delicate, and too neat...', '家具又硬又不舒服，太不结实而且太小巧。');
INSERT INTO `words` VALUES (8754, 'stiffen', 'ˈstɪfn', 'v.', '(尤指因气愤或害怕，使浑身或身体的一部分)变僵硬，变僵直，绷紧;(使)难以弯曲，难以活动，发僵;(使)变强硬，变坚定;', 'Ada stiffened at the sound of his voice...', '埃达听到他的声音吓得人都僵住了。');
INSERT INTO `words` VALUES (8755, 'stiffly', '\'stɪfli', 'adv.', '僵硬地;一动也不动;冷冷地;', 'The furniture was stiff, uncomfortable, too delicate, and too neat...', '家具又硬又不舒服，太不结实而且太小巧。');
INSERT INTO `words` VALUES (8756, 'stiffness', 'stɪfnəs', 'n.', '刚度;僵硬;硬度;', 'The furniture was stiff, uncomfortable, too delicate, and too neat...', '家具又硬又不舒服，太不结实而且太小巧。');
INSERT INTO `words` VALUES (8757, 'stifle', 'ˈstaɪfl', 'v.', '压制;扼杀;阻止;', 'Regulations on children stifled creativity...', '给孩子们定的各种规矩扼杀了他们的创造力。');
INSERT INTO `words` VALUES (8758, 'stifling', 'ˈstaɪflɪŋ', 'adj.', '令人窒息的;极其闷热的;(情况)令人窒息的，令人压抑的;', 'The stifling heat of the little room was beginning to make me nauseous.', '小房间里令人窒息的热气开始让我感到恶心。');
INSERT INTO `words` VALUES (8759, 'stigma', 'ˈstɪɡmə', 'n.', '耻辱;羞耻;(花的)柱头;', 'There is a strong argument for remaining an unmarried mother. There\'s no stigma attached any more...', '有强有力的理由来支持未婚妈妈。这再也不是什么丢脸的事了。');
INSERT INTO `words` VALUES (8760, 'stilt', 'stɪlt', 'n.', '(支撑建筑物高出地面或水面的)桩子，支柱;高跷;', 'They inhabit reed huts built on stilts above the water.', '他们住在建于水中木桩之上的芦苇草屋里。');
INSERT INTO `words` VALUES (8761, 'stilted', 'ˈstɪltɪd', 'adj.', '生硬的;不自然的;', 'We made polite, stilted conversation...', '我们客套了一番。');
INSERT INTO `words` VALUES (8762, 'stimulate', 'ˈstɪmjuleɪt', 'v.', '促进;激发;激励;', 'America\'s priority is rightly to stimulate its economy...', '美国的首要任务自然是刺激经济。');
INSERT INTO `words` VALUES (8763, 'stimulated', 'ˈstɪmjuleɪtɪd', 'v.', '促进;激发;激励;', 'America\'s priority is rightly to stimulate its economy...', '美国的首要任务自然是刺激经济。');
INSERT INTO `words` VALUES (8764, 'stimulating', 'ˈstɪmjuleɪtɪŋ', 'adj.', '趣味盎然的;激励人的;振奋人心的;', 'America\'s priority is rightly to stimulate its economy...', '美国的首要任务自然是刺激经济。');
INSERT INTO `words` VALUES (8765, 'stimulation', 'ˌstɪmjʊˈleɪʃən', 'n.', '激励;兴奋(作用);', 'America\'s priority is rightly to stimulate its economy...', '美国的首要任务自然是刺激经济。');
INSERT INTO `words` VALUES (8766, 'stimuli', 'ˈstɪmjʊlaɪ', 'n.', '促进因素;激励因素;刺激物;', 'Interest rates could fall soon and be a stimulus to the US economy...', '利率可能很快就会下调，从而刺激美国经济。');
INSERT INTO `words` VALUES (8767, 'stimulus', 'ˈstɪmjələs', 'n.', '促进因素;激励因素;刺激物;', 'Interest rates could fall soon and be a stimulus to the US economy...', '利率可能很快就会下调，从而刺激美国经济。');
INSERT INTO `words` VALUES (8768, 'sting', 'stɪŋ', 'v.', '刺;蜇;叮;', 'The nettles stung their legs...', '荨麻扎了他们的腿。');
INSERT INTO `words` VALUES (8769, 'stingy', 'ˈstɪndʒi', 'adj.', '小气的;吝啬的;', 'The West is stingy with aid...', '西方在援助上有点吝啬。');
INSERT INTO `words` VALUES (8770, 'stink', 'stɪŋk', 'v.', '有臭味;有难闻的气味;让人觉得很糟糕;', 'Get away from me —your breath stinks...', '离我远点——你有口臭。');
INSERT INTO `words` VALUES (8771, 'stinky', 'ˈstɪŋki', 'adj.', '发恶臭的;十分难闻的;令人厌恶的;', '...sweaty, stinky socks.', '汗津津的臭袜子');
INSERT INTO `words` VALUES (8772, 'stint', 'stɪnt', 'n.', '从事某项工作(或活动)的时间;', 'He is returning to this country after a five-year stint in Hong Kong.', '他在香港工作了5 年后即将回国。');
INSERT INTO `words` VALUES (8773, 'stipulate', 'ˈstɪpjuleɪt', 'v.', '规定;明确要求;', 'She could have stipulated that she would pay when she collected the computer...', '她本可以要求她来取电脑时付钱。');
INSERT INTO `words` VALUES (8774, 'stipulation', 'ˌstɪpjʊ\'leɪʃ(ə)n', 'n.', '规定;约定;合同;', 'She could have stipulated that she would pay when she collected the computer...', '她本可以要求她来取电脑时付钱。');
INSERT INTO `words` VALUES (8775, 'stir', 'stɜː(r)', 'v.', '搅动;搅和;搅拌;', 'Stir the soup for a few seconds...', '将汤搅和几秒钟。');
INSERT INTO `words` VALUES (8776, 'stirring', 'ˈstɜːrɪŋ', 'n.', '(感情、想法或发展的)开始，出现，萌动，酝酿;', 'The Prime Minister made a stirring speech...', '首相发表了一篇鼓舞人心的演讲。');
INSERT INTO `words` VALUES (8777, 'stitch', 'stɪtʃ', 'n.', '(缝纫的)一针，针脚;缝;(编织的)一针;', 'Fold the fabric and stitch the two layers together...', '将布料对折，然后将两层缝在一起。');
INSERT INTO `words` VALUES (8778, 'stock', 'stɒk', 'n.', '(商店的)现货，存货，库存;储备物;备用物;', '...the buying and selling of stocks and shares...', '证券和股票买卖');
INSERT INTO `words` VALUES (8779, 'stockade', 'stɒˈkeɪd', 'n.', '(防御用的)栅栏，围桩;', '...the inner stockade.', '内层围栏');
INSERT INTO `words` VALUES (8780, 'stocking', 'ˈstɒkɪŋ', 'n.', '长筒女袜;', '...a pair of nylon stockings.', '一双尼龙长筒袜');
INSERT INTO `words` VALUES (8781, 'stockpile', 'ˈstɒkpaɪl', 'n.', '囤聚的物资;', 'People are stockpiling food for the coming winter.', '人们正在为即将到来的冬天贮备大量食物。');
INSERT INTO `words` VALUES (8782, 'stodgy', 'ˈstɒdʒi', 'adj.', '吃下去感觉撑的;易饱的;滞涩的;', 'He was disgusted with the stodgy pizzas on sale in London.', '他讨厌吃伦敦卖的难以下咽的比萨饼。');
INSERT INTO `words` VALUES (8783, 'stoic', 'ˈstəʊɪk', 'n.', '斯多葛派人(对痛苦或困难能默默承受或泰然处之);', 'The kids of Kobe try to be as stoic as their parents in this tragic situation.', '在这种悲惨的情形面前，神户的孩子们努力学着像他们的父母一样去坦然面对。');
INSERT INTO `words` VALUES (8784, 'stoicism', 'ˈstəʊɪsɪzəm', 'n.', '对痛苦的默默承受或泰然处之;坚忍;', 'They bore their plight with stoicism and fortitude.', '他们以坚忍刚毅的精神直面困境。');
INSERT INTO `words` VALUES (8785, 'stoke', 'stəʊk', 'v.', '给…添加(燃料);煽动;激起;', 'She was stoking the stove with sticks of maple.', '她正在往炉子里添枫树枝。');
INSERT INTO `words` VALUES (8786, 'stolid', 'ˈstɒlɪd', 'adj.', '不动感情的;不关心的;淡漠的;', 'He glanced furtively at the stolid faces of the two detectives.', '他偷偷地瞄了一下两名侦探面无表情的脸孔。');
INSERT INTO `words` VALUES (8787, 'stomach', 'ˈstʌmək', 'n.', '胃;腹部;', 'He had an upset stomach...', '他胃不舒服。');
INSERT INTO `words` VALUES (8788, 'stomp', 'stɒmp', 'v.', '迈着重重的步子走(或跳舞、移动);', 'He turned his back on them and stomped off up the hill...', '他转身不理他们，噔噔噔地爬上了山。');
INSERT INTO `words` VALUES (8789, 'stonewall', 'ˈstəʊnwɔːl', 'v.', '防守挡击(政治上指通过沉默或冗长发言等手段阻碍议事或拖延决议);', 'The administration is just stonewalling in an attempt to hide their political embarrassment...', '政府只是在模棱两可地拖延时间，企图掩盖自己的政治窘态。');
INSERT INTO `words` VALUES (8790, 'stonework', 'ˈstəʊnwɜːk', 'n.', '(建筑物的)石造部分;', '...the crumbling stonework of the derelict church.', '废弃教堂摇摇欲坠的石结构');
INSERT INTO `words` VALUES (8791, 'stony', 'ˈstəʊni', 'adj.', '多石的;石头的;冷漠的;', 'The steep, stony ground is well drained.', '陡峭的石头地面排水性能很好。');
INSERT INTO `words` VALUES (8792, 'stool', 'stuːl', 'n.', '凳子;大便;粪便;', 'O\'Brien sat on a bar stool and leaned his elbows on the counter.', '奥布赖恩坐在一张吧凳上，胳膊肘靠在吧台上。');
INSERT INTO `words` VALUES (8793, 'stoop', 'stuːp', 'v.', '俯身;弯腰;(站立或行走时)弓背;', 'She was taller than he was and stooped slightly.', '她比他高，而且略微有些驼背。');
INSERT INTO `words` VALUES (8794, 'stop', 'stɒp', 'v.', '(使)停止，停下;(使)中断，停止;(使)结束，终止;', 'Stop throwing those stones!...', '不要再扔那些石头了！');
INSERT INTO `words` VALUES (8795, 'stopover', 'ˈstɒpəʊvə(r)', 'n.', '中途停留;', 'The President had talks during a brief stopover in Lisbon with his Portuguese counterpart...', '总统在里斯本作短暂停留期间和葡萄牙总统进行了会谈。');
INSERT INTO `words` VALUES (8796, 'stoppage', 'ˈstɒpɪdʒ', 'n.', '停工;罢工;中断比赛;', 'Mineworkers in the Ukraine have voted for a one-day stoppage next month.', '乌克兰矿工们已投票决定下个月罢工一天。');
INSERT INTO `words` VALUES (8797, 'stopper', 'ˈstɒpə(r)', 'n.', '瓶塞;', '...a bottle of colourless liquid sealed with a cork stopper.', '用软木塞封口的一瓶无色液体');
INSERT INTO `words` VALUES (8798, 'stopping', 'ˈstɒpɪŋ', 'v.', '(使)停止，停下;(使)中断，停止;(使)结束，终止;', 'Stop throwing those stones!...', '不要再扔那些石头了！');
INSERT INTO `words` VALUES (8799, 'storage', 'ˈstɔːrɪdʒ', 'n.', '贮存，贮藏(空间);存储(方式);付费托管;', '...the storage of toxic waste...', '有毒废弃物的存放');
INSERT INTO `words` VALUES (8800, 'store', 'stɔː(r)', 'n.', '(大型)百货商店;商店;店铺;', 'Bombs were planted in stores in Manchester and Blackpool.', '炸弹被安放在曼彻斯特和布莱克浦的几家商店里。');
INSERT INTO `words` VALUES (8801, 'storehouse', 'ˈstɔːhaʊs', 'n.', '仓库;货栈;(信息或知识等的)宝库;', 'Some of the cottages were still lived in by old folk; others had been turned into barns and storehouses.', '有些村舍里仍住着老人;有些已经改作粮仓和仓库。');
INSERT INTO `words` VALUES (8802, 'storeroom', 'ˈstɔːruːm', 'n.', '贮藏室;', '...a storeroom filled with massive old furniture covered with dust.', '堆满了尘封的大件旧式家具的贮藏室');
INSERT INTO `words` VALUES (8803, 'storm', 'stɔːm', 'n.', '暴风雨;和风暴有关的恶劣天气;(群情迸发的)浪潮;', '...the violent storms which whipped America\'s East Coast.', '席卷美国东海岸的猛烈暴风雨');
INSERT INTO `words` VALUES (8804, 'stormy', 'ˈstɔːmi', 'adj.', '有暴风雨(或暴风雪)的;群情激愤的;激烈争吵的;', 'It had been a night of stormy weather, with torrential rain and high winds.', '这是一个暴风雨之夜，大雨倾盆，强风呼啸。');
INSERT INTO `words` VALUES (8805, 'story', 'ˈstɔːri', 'n.', '(虚构的)故事;小说;(真实情况的)叙述，描述;', 'The second story in the book is titled \'The Scholar\'...', '书中的第二个故事题为《学者》。');
INSERT INTO `words` VALUES (8806, 'storybook', 'ˈstɔːribʊk', 'n.', '儿童故事书;童话书;', 'As a child she learned to draw by tracing pictures out of her mother\'s old storybooks.', '小时候她通过描摹母亲旧童话书里的插图学会了画画。');
INSERT INTO `words` VALUES (8807, 'storyteller', 'ˈstɔːritelə(r)', 'n.', '讲故事的人;故事(或小说)作者;', 'He was the one who first set down the stories of the Celtic storytellers.', '他是首位将凯尔特说书人讲的故事记载下来的人。');
INSERT INTO `words` VALUES (8808, 'stout', 'staʊt', 'adj.', '肥胖的;肥壮的;粗壮结实的;', 'He was a tall, stout man with gray hair.', '他是个又高又胖、头发灰白的男子。');
INSERT INTO `words` VALUES (8809, 'stove', 'stəʊv', 'n.', '(用于取暖的)炉子，火炉;', 'She put the kettle on the gas stove.', '她将水壶放在煤气炉上。');
INSERT INTO `words` VALUES (8810, 'stow', 'stəʊ', 'v.', '妥善放置;把…收好;', 'I helped her stow her bags in the boot of the car...', '我帮她将包放进汽车行李箱里。');
INSERT INTO `words` VALUES (8811, 'stowage', 'ˈstəʊɪdʒ', 'n.', '(船或飞机上)存放物品处;', 'Stowage is provided in lined lockers beneath the berths.', '铺位下方有成排的储物柜可以存放东西。');
INSERT INTO `words` VALUES (8812, 'stowaway', 'ˈstəʊəweɪ', 'n.', '偷乘船(或飞机)者;', 'The crew discovered the stowaway about two days into their voyage.', '船员在开船约两天后发现了那名偷乘者。');
INSERT INTO `words` VALUES (8813, 'straddle', 'ˈstrædl', 'v.', '骑;跨坐;分腿站立;', 'He sat down, straddling the chair.', '他跨坐在椅子上。');
INSERT INTO `words` VALUES (8814, 'strafe', 'strɑːf', 'v.', '低空扫射(或轰炸);', 'It seemed that the plane was going to swoop down and strafe the town, so we dived for cover.', '飞机看起来好像要俯冲下来轰炸小镇，所以我们赶紧蹲下躲避。');
INSERT INTO `words` VALUES (8815, 'straggle', 'ˈstræɡl', 'v.', '蔓生;杂乱地蔓延;散布;', 'They came straggling up the cliff road...', '他们零零散散地沿着悬崖小路走上来。');
INSERT INTO `words` VALUES (8816, 'straight', 'streɪt', 'adv.', '笔直地;平正地;成直线;', 'Keep the boat in a straight line...', '让船保持直线航行。');
INSERT INTO `words` VALUES (8817, 'straighten', 'ˈstreɪtn', 'v.', '(使)变直，变正;挺直，端正(身体);', 'She sipped her coffee and straightened a picture on the wall.', '她抿了口咖啡，然后把墙上的一幅画扶正。');
INSERT INTO `words` VALUES (8818, 'straightforward', 'ˌstreɪtˈfɔːwəd', 'adj.', '简单的;易懂的;不复杂的;', 'Disposable nappies are fairly straightforward to put on...', '一次性尿布很容易穿上。');
INSERT INTO `words` VALUES (8819, 'strain', 'streɪn', 'n.', '压力;重负;重压之下出现的问题(或担忧等);', 'The prison service is already under considerable strain...', '监狱系统已经承受了巨大的压力。');
INSERT INTO `words` VALUES (8820, 'strained', 'streɪnd', 'adj.', '神色不宁的;紧张的;憔悴的;', 'She looked a little pale and strained...', '她看起来面色有点苍白，神情忧虑。');
INSERT INTO `words` VALUES (8821, 'strait', 'streɪt', 'n.', '海峡;(联结两大水域的)水道;(尤指经济拮据引起的)困境，境况窘迫;', 'An estimated 1600 vessels pass through the strait annually.', '估计每年有 1,600 艘船通过该海峡。');
INSERT INTO `words` VALUES (8822, 'straits', 'streɪts', 'n.', '海峡;(联结两大水域的)水道;(尤指经济拮据引起的)困境，境况窘迫;', 'An estimated 1600 vessels pass through the strait annually.', '估计每年有 1,600 艘船通过该海峡。');
INSERT INTO `words` VALUES (8823, 'strand', 'strænd', 'n.', '(线、绳、金属线、毛发等的)股，缕;(观点、计划、故事等的)部分，方面;(海洋、湖或河的)岸，滨;', 'She tried to blow a gray strand of hair from her eyes.', '她竭力想把眼前的一缕白发吹开。');
INSERT INTO `words` VALUES (8824, 'stranded', 'ˈstrændɪd', 'v.', '使滞留;使搁浅;', 'She tried to blow a gray strand of hair from her eyes.', '她竭力想把眼前的一缕白发吹开。');
INSERT INTO `words` VALUES (8825, 'strange', 'streɪndʒ', 'adj.', '奇怪的;奇特的;异常的;', 'Then a strange thing happened...', '然后一件奇怪的事发生了。');
INSERT INTO `words` VALUES (8826, 'strangely', 'ˈstreɪndʒli', 'adv.', '异常地;奇怪地;不可思议地;', 'Strangely, the race didn\'t start until 8.15pm...', '很奇怪，赛跑直到晚上 8 点 15 分才开始。');
INSERT INTO `words` VALUES (8827, 'stranger', 'ˈstreɪndʒə(r)', 'n.', '陌生人;外地人;新来者;', 'Telling a complete stranger about your life is difficult...', '要对一个素昧平生的人谈自己的生活经历是比较困难。');
INSERT INTO `words` VALUES (8828, 'strangle', 'ˈstræŋɡl', 'v.', '扼死;勒死;掐死;', 'He tried to strangle a border policeman and steal his gun...', '他企图勒死一名边防警察并偷走他的枪。');
INSERT INTO `words` VALUES (8829, 'strangulation', 'ˌstræŋɡjuˈleɪʃn', 'n.', '扼死;勒死;掐死;', 'He is charged with the strangulation of two students.', '他被指控勒死了两名学生。');
INSERT INTO `words` VALUES (8830, 'strap', 'stræp', 'n.', '带子;', 'Nancy gripped the strap of her beach bag...', '南希抓住了自己沙滩包的带子。');
INSERT INTO `words` VALUES (8831, 'stratagem', 'ˈstrætədʒəm', 'n.', '(为取胜或迷惑对手的)计策，计谋;', 'Trade discounts may be used as a competitive stratagem to secure customer loyalty.', '商业折扣可以用作维护顾客忠诚度的一种竞争手段。');
INSERT INTO `words` VALUES (8832, 'strategic', 'strəˈtiːdʒɪk', 'adj.', '根据全局而安排的;战略性的;战略上的;', '...the new strategic thinking which NATO leaders produced at the recent London summit...', '北约领导人在最近召开的伦敦首脑会议上提出的新的战略思想');
INSERT INTO `words` VALUES (8833, 'strategist', 'ˈstrætədʒɪst', 'n.', '战略家;善于筹划部署的人;', 'Military strategists had devised a plan that guaranteed a series of stunning victories.', '军事战略家们制订出的计划确保了一连串的辉煌胜利。');
INSERT INTO `words` VALUES (8834, 'strategy', 'ˈstrætədʒi', 'n.', '策略;计策;行动计划;', 'Next week, health ministers gather in Amsterdam to agree a strategy for controlling malaria...', '下周，卫生部长们将在阿姆斯特丹聚首共商防治疟疾的策略。');
INSERT INTO `words` VALUES (8835, 'stratification', 'ˌstrætɪfɪˈkeɪʃn', 'n.', '分层;成层;', 'She was concerned about the stratification of American society.', '她很关注美国社会的阶层分化。');
INSERT INTO `words` VALUES (8836, 'stratospheric', 'ˌstrætəˈsferɪk', 'adj.', '平流层的;', '...stratospheric ozone.', '平流层的臭氧');
INSERT INTO `words` VALUES (8837, 'stratum', 'ˈstrɑːtəm', 'n.', '层;岩层;地层;', 'It was an enormous task that affected every stratum of society...', '这是一项影响到社会各阶层的重大工作。');
INSERT INTO `words` VALUES (8838, 'straw', 'strɔː', 'n.', '(收割后干燥的)禾秆，麦秆，稻草;一根禾秆(或麦秆、稻草);(喝饮料用的)吸管;', 'The barn was full of bales of straw...', '谷仓里堆满了成捆的稻草。');
INSERT INTO `words` VALUES (8839, 'strawberry', 'ˈstrɔːbəri', 'n.', '草莓;', '...strawberries and cream.', '奶油草莓');
INSERT INTO `words` VALUES (8840, 'stray', 'streɪ', 'v.', '迷路;偏离;走失;', 'Tourists often get lost and stray into dangerous areas...', '游客常会迷路并误入危险区域。');
INSERT INTO `words` VALUES (8841, 'streak', 'striːk', 'n.', '条纹;条痕;(尤指不好的)性格特征;', 'There are these dark streaks on the surface of the moon...', '月球表面有这样一些黑色的条痕。');
INSERT INTO `words` VALUES (8842, 'stream', 'striːm', 'n.', '小河;溪;流;', 'There was a small stream at the end of the garden.', '花园的尽头有一条小河。');
INSERT INTO `words` VALUES (8843, 'streaming', 'ˈstriːmɪŋ', 'n.', '(数据的)流播;', '...web sites that feature streaming media.', '以流式媒体为主要特色的网站');
INSERT INTO `words` VALUES (8844, 'streamline', 'ˈstriːmlaɪn', 'v.', '使成流线型;使(系统、机构等)效率更高;(尤指)使增产节约;', 'They\'re making efforts to streamline their normally cumbersome bureaucracy...', '他们正努力精简本来繁冗复杂的官僚体制。');
INSERT INTO `words` VALUES (8845, 'streamlined', 'ˈstriːmlaɪnd', 'adj.', '流线型的;', '...these beautifully streamlined and efficient cars.', '这些漂亮的流线型高性能汽车');
INSERT INTO `words` VALUES (8846, 'street', 'striːt', 'n.', '大街;街道;', 'He lived at 66 Bingfield Street...', '他住在宾菲尔德大街 66 号。');
INSERT INTO `words` VALUES (8847, 'streetlight', '', 'n.', '街灯;', 'As the day darkened the streetlights came on.', '天色渐暗，街灯亮了。');
INSERT INTO `words` VALUES (8848, 'strength', 'streŋθ', 'n.', '体力;力气;力量;', 'She has always been encouraged to swim to build up the strength of her muscles...', '一直有人鼓励她去游泳以强健体魄。');
INSERT INTO `words` VALUES (8849, 'strengthen', 'ˈstreŋθn', 'v.', '加强;增强;巩固;', '...the new constitution, which strengthens the government and enables it to balance and check the powers of parliament and president...', '巩固了政府的地位并使其能够制衡国会和总统权力的新宪法');
INSERT INTO `words` VALUES (8850, 'strenuous', 'ˈstrenjuəs', 'adj.', '费力的;繁重的;艰苦的;', 'Avoid strenuous exercise in the evening...', '晚上不要做剧烈运动。');
INSERT INTO `words` VALUES (8851, 'stress', 'stres', 'n.', '精神压力;心理负担;紧张;', 'The spokesman stressed that the measures did not amount to an overall ban...', '该发言人强调这些措施并不等同于全面禁止。');
INSERT INTO `words` VALUES (8852, 'stressed', 'strest', 'adj.', '焦虑不安;心力交瘁;重读的;', 'Work out what situations or people make you feel stressed and avoid them.', '弄清是什么状况或什么人让你焦虑不安，然后避开他们。');
INSERT INTO `words` VALUES (8853, 'stressful', 'ˈstresfl', 'adj.', '压力重的;紧张的;', 'I think I\'ve got one of the most stressful jobs there is.', '我想我得到的这份工作是其中最繁重的工作之一。');
INSERT INTO `words` VALUES (8854, 'stretch', 'stretʃ', 'v.', '拉长;拽宽;撑大;', 'The procession stretched for several miles...', '游行队伍长达数英里。');
INSERT INTO `words` VALUES (8855, 'stretcher', 'ˈstretʃə(r)', 'n.', '担架;', 'The two ambulance attendants quickly put Plover on a stretcher and got him into the ambulance.', '两名救护人员迅速地将普洛弗放到担架上抬进救护车里。');
INSERT INTO `words` VALUES (8856, 'strew', 'struː', 'v.', '把…布满(或散布在);在…上布满(或散播);布满;', 'The racoons knock over the rubbish bins in search of food, and strew the contents all over the ground...', '浣熊弄翻垃圾箱找吃的，把垃圾弄得满地都是。');
INSERT INTO `words` VALUES (8857, 'strewn', 'struːn', 'adj.', '布满的;撒满的;', 'The front room was strewn with books and clothes...', '起居室里到处都是书和衣服。');
INSERT INTO `words` VALUES (8858, 'strict', 'strɪkt', 'adj.', '严格的(指必须恪守);要求严格的;严厉的;', 'The officials had issued strict instructions that we were not to get out of the jeep...', '有关官员严令禁止我们下吉普车。');
INSERT INTO `words` VALUES (8859, 'strictly', 'ˈstrɪktli', 'adv.', '严格地;(强调在一切情况都是如此)绝对地，无论如何;完全地;', 'He seemed fond of her in a strictly professional way...', '他似乎只是出于职业缘故而喜欢她。');
INSERT INTO `words` VALUES (8860, 'stricture', 'ˈstrɪktʃə(r)', 'n.', '指摘;非难;限制;', '...Mencken\'s strictures on the 1920s, with its self-righteous prohibition on alcohol and unconventional ideas...', '门肯对19世纪20年代自以为是的禁酒令和对非传统思想的禁锢的指摘');
INSERT INTO `words` VALUES (8861, 'stride', 'straɪd', 'v.', '大步走;阔步行走;', 'They were joined by a newcomer who came striding across a field...', '一个新来的人大步穿过一片田地，加入到他们的行列。');
INSERT INTO `words` VALUES (8862, 'stridency', 'ˈstraɪdənsi', 'n.', '强硬;激烈;咄咄逼人;', 'Many voters were alarmed by the President\'s new stridency.', '很多选民对总统最近表现出来的强势感到不安。');
INSERT INTO `words` VALUES (8863, 'strident', 'ˈstraɪdnt', 'adj.', '刺耳的;强硬的;咄咄逼人的;', 'She was increasingly seen as a strident feminist.', '她愈加被视为强硬的女权主义者。');
INSERT INTO `words` VALUES (8864, 'strife', 'straɪf', 'n.', '冲突;争斗;倾轧;', 'Money is a major cause of strife in many marriages...', '金钱是造成很多婚姻不和的一个主要原因。');
INSERT INTO `words` VALUES (8865, 'striker', 'ˈstraɪkə(r)', 'n.', '罢工者;罢课者;罢市者;', '...and the England striker scored his sixth goal of the season.', '这名英格兰队前锋踢进了他在本赛季的第 6 个进球');
INSERT INTO `words` VALUES (8866, 'striking', 'ˈstraɪkɪŋ', 'adj.', '引人注目的;异乎寻常的;显著的;', 'The most striking feature of those statistics is the high proportion of suicides...', '那些统计数据最引人注目之处是其极高的自杀率。');
INSERT INTO `words` VALUES (8867, 'strikingly', '\'straɪkɪŋli', 'adv.', '极;醒目地;', 'The most striking feature of those statistics is the high proportion of suicides...', '那些统计数据最引人注目之处是其极高的自杀率。');
INSERT INTO `words` VALUES (8868, 'string', 'strɪŋ', 'n.', '细绳;线;带子;', 'He held out a small bag tied with string.', '他拿出一个系着细绳的小袋子。');
INSERT INTO `words` VALUES (8869, 'stringent', 'ˈstrɪndʒənt', 'adj.', '严格的;严厉的;紧缩的;', 'He announced that there would be more stringent controls on the possession of weapons...', '他宣布将对武器的持有实行更严格的控制。');
INSERT INTO `words` VALUES (8870, 'strings', 'strɪŋz', 'n.', '细绳;线;带子;', 'He held out a small bag tied with string.', '他拿出一个系着细绳的小袋子。');
INSERT INTO `words` VALUES (8871, 'strip', 'strɪp', 'v.', '脱光衣服;脱掉大部分衣服;扒光…的衣服;', '...a new kind of manufactured wood made by pressing strips of wood together and baking them...', '一种用木条压紧并烘烤而制成的新型人造木材');
INSERT INTO `words` VALUES (8872, 'stripe', 'straɪp', 'n.', '条纹;线条;(军装或警服上表示等级的)条，杠;', 'She wore a bright green jogging suit with a white stripe down the sides...', '她身穿一套裤腿两边有白色条纹的亮绿色慢跑服。');
INSERT INTO `words` VALUES (8873, 'striped', 'straɪpt', 'adj.', '有条纹的;', '...a bottle green and maroon striped tie.', '有深绿色和栗色条纹的领带');
INSERT INTO `words` VALUES (8874, 'stripes', 'ˈstraɪps', 'n.', '条纹;线条;(军装或警服上表示等级的)条，杠;', 'She wore a bright green jogging suit with a white stripe down the sides...', '她身穿一套裤腿两边有白色条纹的亮绿色慢跑服。');
INSERT INTO `words` VALUES (8875, 'stripling', 'ˈstrɪplɪŋ', 'n.', '年轻男子;小伙子;', '...a stripling of 20.', '20 岁的小伙子');
INSERT INTO `words` VALUES (8876, 'stripy', 'ˈstraɪpi', 'adj.', '有条纹的，有斑纹的;', 'He was wearing a stripy shirt and baggy blue trousers.', '他身着一件条纹衬衫和一条宽松的蓝裤子。');
INSERT INTO `words` VALUES (8877, 'stroke', 'strəʊk', 'n.', '击球(动作);(打、击等的)一下，一击;划水动作;', 'Carla, curled up on the sofa, was smoking a cigarette and stroking her cat...', '卡拉正蜷缩在沙发上，一边吸着烟，一边轻抚着她的猫咪。');
INSERT INTO `words` VALUES (8878, 'stroll', 'strəʊl', 'v.', '散步;溜达;闲逛;', 'He collected some orange juice from the refrigerator and, glass in hand, strolled to the kitchen window...', '他从冰箱里拿了点橘子汁，然后手拿杯子溜达到厨房的窗口。');
INSERT INTO `words` VALUES (8879, 'strolling', 'ˈstrəʊlɪŋ', 'v.', '散步;溜达;闲逛;', 'He collected some orange juice from the refrigerator and, glass in hand, strolled to the kitchen window...', '他从冰箱里拿了点橘子汁，然后手拿杯子溜达到厨房的窗口。');
INSERT INTO `words` VALUES (8880, 'strong', 'strɒŋ', 'adj.', '强壮的;强健的;强的;', 'I\'m not strong enough to carry him...', '我力气不够大，抱不动他。');
INSERT INTO `words` VALUES (8881, 'strongest', 'ˈstrɒŋgɪst', 'adj.', '强壮的;强健的;强的;', 'I\'m not strong enough to carry him...', '我力气不够大，抱不动他。');
INSERT INTO `words` VALUES (8882, 'stronghold', 'ˈstrɒŋhəʊld', 'n.', '有广泛支持的地方;势力强大的地方;堡垒;', 'The westernmost part of north Wales is a stronghold of Welsh-speakers...', '北威尔士最西部是说威尔士语人的聚居区。');
INSERT INTO `words` VALUES (8883, 'strongly', '\'strɒŋli', 'adv.', '强烈地;坚决地;大力地;', 'I\'m not strong enough to carry him...', '我力气不够大，抱不动他。');
INSERT INTO `words` VALUES (8884, 'strong-minded', 'ˌstrɒŋ ˈmaɪndɪd', 'adj.', '有主见的;坚持己见的;', 'She is a strong-minded, independent woman.', '她是一个独立、有主见的女人。');
INSERT INTO `words` VALUES (8885, 'structural', 'ˈstrʌktʃərəl', 'adj.', '结构(或构造)上的;', 'The explosion caused little structural damage to the office towers themselves.', '爆炸对办公大楼本身几乎没有造成结构上的损坏。');
INSERT INTO `words` VALUES (8886, 'structure', 'ˈstrʌktʃə(r)', 'n.', '结构;构造;结构体;', 'The typical family structure of Freud\'s patients involved two parents and two children...', '弗洛伊德的病人的典型家庭结构是父母二人和两个孩子。');
INSERT INTO `words` VALUES (8887, 'structured', 'ˈstrʌktʃəd', 'v.', '使形成体系;系统安排;精心组织;', 'The typical family structure of Freud\'s patients involved two parents and two children...', '弗洛伊德的病人的典型家庭结构是父母二人和两个孩子。');
INSERT INTO `words` VALUES (8888, 'struggle', 'ˈstrʌɡl', 'v.', '奋斗;努力;争取;', 'They had to struggle against all kinds of adversity...', '他们不得不同一切困境做斗争。');
INSERT INTO `words` VALUES (8889, 'strum', 'strʌm', 'v.', '弹奏(吉他等乐器);', 'In the corner, one youth sat alone, softly strumming a guitar...', '一个青年独自坐在角落里，轻轻地弹着吉他。');
INSERT INTO `words` VALUES (8890, 'strut', 'strʌt', 'v.', '趾高气扬地走;高视阔步;', 'He struts around town like he owns the place.', '他在城里大摇大摆地晃荡，好像那是他的地盘。');
INSERT INTO `words` VALUES (8891, 'stub', 'stʌb', 'n.', '(烟、铅笔等的)残余部分，残端;存根;票根;', 'He pulled the stub of a pencil from behind his ear.', '他从耳后拿出一小段铅笔头。');
INSERT INTO `words` VALUES (8892, 'stubble', 'ˈstʌbl', 'n.', '(作物收割后留在地里的)茬;胡子茬;', 'The stubble was burning in the fields.', '地里的麦茬正在燃烧。');
INSERT INTO `words` VALUES (8893, 'stubborn', 'ˈstʌbən', 'adj.', '固执的;执拗的;顽固的;', 'He is a stubborn character used to getting his own way...', '他性格固执，惯于一意孤行。');
INSERT INTO `words` VALUES (8894, 'stubbornly', '\'stʌbənli', 'adv.', '顽固地;顽强;傲然地;', 'He is a stubborn character used to getting his own way...', '他性格固执，惯于一意孤行。');
INSERT INTO `words` VALUES (8895, 'stubbornness', 'ˈstʌbənnɪs', 'n.', '固执;', 'He is a stubborn character used to getting his own way...', '他性格固执，惯于一意孤行。');
INSERT INTO `words` VALUES (8896, 'stuck-up', 'ˌstʌk ˈʌp', 'adj.', '自命不凡的;趾高气扬的;', 'She was a famous actress, but she wasn\'t a bit stuck-up.', '她是著名演员，却丝毫没有明星架子。');
INSERT INTO `words` VALUES (8897, 'stud', 'stʌd', 'n.', '钉状首饰;耳钉;鼻钉;', 'You see studs on lots of London front doors.', '在伦敦，你能看到许多人家的大门上都装有饰钉。');
INSERT INTO `words` VALUES (8898, 'student', 'ˈstjuːdnt', 'n.', '大学生;研究生;学生;', 'Warren\'s eldest son is an art student, at St Martin\'s.', '沃伦的大儿子是圣马丁学院艺术系的学生。');
INSERT INTO `words` VALUES (8899, 'studied', 'ˈstʌdid', 'adj.', '刻意的;精心安排的;', '\'We both have an interesting 10 days coming up,\' said Alex Ferguson with studied understatement.', '亚历克斯·弗格森故作轻描淡写地说：“我们都即将迎来愉快的 10 天。”');
INSERT INTO `words` VALUES (8900, 'studies', 'ˈstʌdiz', 'n.', '学习;研究;功课;', '...a relaxed and happy atmosphere that will allow you to study to your full potential...', '能让人充分发挥学习潜质的轻松愉快的环境');
INSERT INTO `words` VALUES (8901, 'studio', 'ˈstjuːdiəʊ', 'n.', '(广播、电视的)录音室，录像室，演播室，制作室;(音乐)录音棚;电影摄影棚;', 'She was in her studio again, painting onto a large canvas.', '她又回到了画室，在一张大帆布上作画。');
INSERT INTO `words` VALUES (8902, 'study', 'ˈstʌdi', 'n.', '学习;研究;功课;', '...a relaxed and happy atmosphere that will allow you to study to your full potential...', '能让人充分发挥学习潜质的轻松愉快的环境');
INSERT INTO `words` VALUES (8903, 'stuff', 'stʌf', 'n.', '(事物名称不详、无关紧要或所指事物明显时用)东西，物品，玩意儿;(泛指)活儿，话，念头，东西;基本特征;', 'I\'d like some coffee, and I don\'t object to the powdered stuff if it\'s all you\'ve got...', '我想来点咖啡，如果你只有速溶的那种也可以。');
INSERT INTO `words` VALUES (8904, 'stuffing', 'ˈstʌfɪŋ', 'n.', '(烹饪前塞入鸡等膛内的)填料;(垫子、玩具等的)填充物;', 'Chestnuts can be used at Christmas time, as a stuffing for turkey, guinea fowl or chicken.', '栗子可以在圣诞节时作火鸡、珍珠鸡或家鸡的填料。');
INSERT INTO `words` VALUES (8905, 'stuffy', 'ˈstʌfi', 'adj.', '闷热的;闷人的;通风不畅的;', 'Why were grown-ups always so stuffy and slow to recognize good ideas?', '为什么成年人总是那么一本正经，对好点子反应那么迟缓呢？');
INSERT INTO `words` VALUES (8906, 'stultify', 'ˈstʌltɪfaɪ', 'v.', '使迟钝;使厌烦;使呆滞;', 'This attitude stultifies scientific progress...', '这种态度会扼杀科学的进步。');
INSERT INTO `words` VALUES (8907, 'stumble', 'ˈstʌmbl', 'v.', '绊脚;跌跌撞撞地走;蹒跚而行;', 'He stumbled and almost fell...', '他差点被绊倒。');
INSERT INTO `words` VALUES (8908, 'stump', 'stʌmp', 'n.', '树墩;树桩;残余部分;', 'If you have a tree stump, check it for fungus...', '要是有树桩的话，检查一下上面有没有长菌类。');
INSERT INTO `words` VALUES (8909, 'stun', 'stʌn', 'v.', '使昏迷;(尤指)打昏;使震惊(或惊愕、目瞪口呆);', 'Many cinema-goers were stunned by the film\'s violent and tragic end.', '许多来看电影的人都被这部影片暴力而悲惨的结局所震惊。');
INSERT INTO `words` VALUES (8910, 'stunning', 'ˈstʌnɪŋ', 'adj.', '极有魅力的;绝妙的;给人以深刻印象的;', 'She was 55 and still a stunning woman...', '她已经 55 岁了，但依然美艳动人。');
INSERT INTO `words` VALUES (8911, 'stunt', 'stʌnt', 'n.', '(尤指电影中的)特技表演;意在引人注意的花招;噱头;', 'In a bold promotional stunt for the movie, he smashed his car into a passing truck.', '为了给电影制造噱头以吸引眼球，他把汽车猛地撞向一辆驶过的卡车。');
INSERT INTO `words` VALUES (8912, 'stupefy', 'ˈstjuːpɪfaɪ', 'v.', '使惊讶(或惊呆、思维不清、神志不清);', '...a violent slap on the side of the head, which stunned and stupefied him.', '他的一边脑袋上猛地挨了一掌，使他顿时觉得晕头转向，昏天黑地');
INSERT INTO `words` VALUES (8913, 'stupendous', 'stjuːˈpendəs', 'adj.', '极大的;令人惊叹的;了不起的;', 'He was a man of stupendous stamina and energy...', '他是个有着超强耐力、精力充沛的人。');
INSERT INTO `words` VALUES (8914, 'stupid', 'ˈstjuːpɪd', 'adj.', '欠考虑的;糊涂的;笨的;', 'I\'ll never do anything so stupid again...', '我再也不会做这种傻事了。');
INSERT INTO `words` VALUES (8915, 'stupidity', 'stjuːˈpɪdəti', 'n.', '愚蠢行为;糊涂;愚蠢;', 'I\'ll never do anything so stupid again...', '我再也不会做这种傻事了。');
INSERT INTO `words` VALUES (8916, 'stupor', 'ˈstjuːpə(r)', 'n.', '(尤指由于醉酒、吸毒或震动而出现的)神志不清，恍惚，麻痹状态;', 'He fell back onto the sofa in a drunken stupor...', '他烂醉如泥，瘫倒在沙发上。');
INSERT INTO `words` VALUES (8917, 'sturdy', 'ˈstɜːdi', 'adj.', '结实的;坚固的;强壮的;', 'She was a short, sturdy woman in her early sixties...', '她是个 60 岁出头、身材矮小结实的女人。');
INSERT INTO `words` VALUES (8918, 'stutter', 'ˈstʌtə(r)', 'v.', '口吃;结结巴巴地说;突突地吃力行驶(或艰难启动、艰难运转);', 'He spoke with a pronounced stutter.', '他说话明显结巴。');
INSERT INTO `words` VALUES (8919, 'style', 'staɪl', 'n.', '方式;作风;样式;', 'Our children\'s different needs and learning styles created many problems...', '孩子们不同的需求和学习方式给我们带来了许多问题。');
INSERT INTO `words` VALUES (8920, 'stylish', 'ˈstaɪlɪʃ', 'adj.', '时髦的;新潮的;高雅的;', '...a very attractive and very stylish woman of 27.', '气质迷人、格调高雅的 27 岁女子');
INSERT INTO `words` VALUES (8921, 'stylist', 'ˈstaɪlɪst', 'n.', '发型师;语言风格优美(或独特)的人;文体家;', 'Choose a stylist recommended by someone whose hair you like.', '选择一个你觉得发型很好看的人向你推荐的发型师。');
INSERT INTO `words` VALUES (8922, 'stymie', 'ˈstaɪmi', 'v.', '阻挠;阻碍;阻止;', 'Companies have been stymied by the length of time it takes to reach an agreement...', '由于耗时过长，各公司一直未能达成协议。');
INSERT INTO `words` VALUES (8923, 'suave', 'swɑːv', 'adj.', '精明练达的;圆滑的;', 'He is a suave, cool and cultured man.', '他是个世故、冷静、有教养的人。');
INSERT INTO `words` VALUES (8924, 'sub', 'sʌb', 'n.', '替补队员;(向俱乐部等定期交纳的)会员费;助理编辑;', 'We had a few injuries and had to use youth team kids as subs.', '我们有几名队员有伤，只好用青年队的小队员做替补。');
INSERT INTO `words` VALUES (8925, 'subconscious', 'ˌsʌbˈkɒnʃəs', 'adj.', '下意识的;潜意识的;', '...the hidden power of the subconscious...', '潜意识的潜在力量');
INSERT INTO `words` VALUES (8926, 'subconsciously', 'ˌsʌbˈkɒnʃəsli', '', '下意识地;潜意识地;本能地;', '...the hidden power of the subconscious...', '潜意识的潜在力量');
INSERT INTO `words` VALUES (8927, 'subcontractor', 'ˈsʌbkəntræktə(r)', 'n.', '分包商;分包人;', 'The company was considered as a possible subcontractor to build the aeroplane.', '该公司被视为能够承担该飞机制造任务的潜在分包商之一。');
INSERT INTO `words` VALUES (8928, 'subculture', 'ˈsʌbkʌltʃə(r)', 'n.', '(某群体特有的)亚文化行为观念， 次文化;', '...the latest American subculture.', '最近的美国亚文化');
INSERT INTO `words` VALUES (8929, 'subcutaneous', 'ˌsʌbkjuˈteɪniəs', 'adj.', '皮下的;', '...subcutaneous fat.', '皮下脂肪');
INSERT INTO `words` VALUES (8930, 'subdivide', 'ˈsʌbdɪvaɪd', 'v.', '(被)再分割，再分;', 'The verbs were subdivided into transitive and intransitive categories.', '动词可细分为及物动词和不及物动词。');
INSERT INTO `words` VALUES (8931, 'subdivision', 'ˌsʌbdɪˈvɪʒn', 'n.', '再分割;再分;细分;', 'Months are a conventional subdivision of the year.', '通常可将一年再按月细分。');
INSERT INTO `words` VALUES (8932, 'subdue', 'səbˈdjuː', 'v.', '制伏;征服;控制;', 'Senior government officials admit they have not been able to subdue the rebels.', '政府高级官员承认他们还没能制服反叛者。');
INSERT INTO `words` VALUES (8933, 'subgroup', 'ˈsʌbɡruːp', 'n.', '小组;(团体中的)部分;', 'The Action Group worked by dividing its tasks among a large number of subgroups.', '行动小组通过将其任务分配给多个下属小组来完成工作。');
INSERT INTO `words` VALUES (8934, 'subjection', 'səbˈdʒekʃn', 'n.', '征服;镇压;屈从;', '...their complete subjection to their captors.', '他们完全屈服于俘获他们的那些人');
INSERT INTO `words` VALUES (8935, 'subjective', 'səbˈdʒektɪv', 'adj.', '主观的(非客观的);主观的(非现实世界的);主语的;', 'We know that taste in art is a subjective matter...', '我们知道艺术品位因人而异。');
INSERT INTO `words` VALUES (8936, 'subjugate', 'ˈsʌbdʒuɡeɪt', 'v.', '征服;制伏;使屈服;', 'Their costly and futile attempt to subjugate the Afghans lasted just 10 years.', '他们大动干戈想要控制阿富汗地区，但只持续了10年就无果而终了。');
INSERT INTO `words` VALUES (8937, 'sublimate', 'ˈsʌblɪmeɪt', 'v.', '升华，使高尚化(把性欲冲动等转移到工作、锻炼、艺术等社会可接受的活动中);', 'He could try to sublimate the problem by writing, in detail, about it...', '他可以努力通过详细记述该问题而使其受到人们的正视。');
INSERT INTO `words` VALUES (8938, 'sublimation', 'ˌsʌblɪ\'meɪʃ(ə)n', 'n.', '纯化;升华;', 'He could try to sublimate the problem by writing, in detail, about it...', '他可以努力通过详细记述该问题而使其受到人们的正视。');
INSERT INTO `words` VALUES (8939, 'sublime', 'səˈblaɪm', 'adj.', '崇高的;壮丽的;宏伟的;', 'Sublime music floats on a scented summer breeze to the spot where you lie.', '优美的音乐随着充满香气的夏日微风飘到了你躺着的地方。');
INSERT INTO `words` VALUES (8940, 'subliminal', 'ˌsʌbˈlɪmɪnl', 'adj.', '下意识的;潜意识的;', 'Colour has a profound, though often subliminal influence on our senses and moods.', '颜色会极大地影响我们的感官和情绪，而这种影响常常是潜意识的。');
INSERT INTO `words` VALUES (8941, 'submarine', 'ˌsʌbməˈriːn', 'n.', '潜艇;潜艇三明治，长卷三明治(用长卷面包纵向切开，内夹各种食物);', '...a nuclear submarine.', '核潜艇');
INSERT INTO `words` VALUES (8942, 'submerge', 'səbˈmɜːdʒ', 'v.', '(使)潜入水中，没入水中，浸没，淹没;湮没，湮灭，掩盖(思想、感情等);', 'Hippos are unable to submerge in the few remaining water holes...', '在仅剩的几个水坑里，河马根本无法没入水中。');
INSERT INTO `words` VALUES (8943, 'submerged', 'səbˈmɜːdʒd', 'adj.', '在水中的;淹没的;水下的;', 'My right toe struck against a submerged rock...', '我的右脚趾踢到水中的一块石头上了。');
INSERT INTO `words` VALUES (8944, 'submission', 'səbˈmɪʃn', 'n.', '屈服;投降;归顺;', 'The army intends to take the city or simply starve it into submission.', '军队打算占领该城市或干脆截断其粮食补给迫使其投降。');
INSERT INTO `words` VALUES (8945, 'submissive', 'səbˈmɪsɪv', 'adj.', '唯命是从的;顺从的;驯服的;', 'Most doctors want their patients to be submissive.', '大部分医生希望病人能听从医嘱。');
INSERT INTO `words` VALUES (8946, 'submit', 'səbˈmɪt', 'v.', '提交，呈递(文件、建议等);顺从;屈服;', 'In desperation, Mrs. Jones submitted to an operation on her right knee to relieve the pain...', '绝望之际，琼斯夫人只好同意在其右膝上做手术以减轻疼痛。');
INSERT INTO `words` VALUES (8947, 'subpoena', 'səˈpiːnə', 'n.', '(传唤证人出庭的)传票;', 'He has been served with a subpoena to answer the charges in court.', '他接到传票要出庭应诉。');
INSERT INTO `words` VALUES (8948, 'subscribe', 'səbˈskraɪb', 'v.', '定期订购(或订阅等);定期交纳(会员费);定期(向慈善机构)捐款;', 'I\'ve personally never subscribed to the view that either sex is superior to the other.', '我个人从来都不同意性别有优劣之分的观点。');
INSERT INTO `words` VALUES (8949, 'subscriber', 'səbˈskraɪbə(r)', 'n.', '(报刊的)订阅人，订购者，订户;(慈善机关等的)定期捐款者，定期捐助者;消费者;', 'I have been a subscriber to Railway Magazine for many years.', '我订阅《铁路杂志》已有很多年了。');
INSERT INTO `words` VALUES (8950, 'subscription', 'səbˈskrɪpʃn', 'n.', '(报刊等的)订阅费，订购款，订阅，订购;(向慈善机构的)定期捐款;(俱乐部的)会员费;', 'You can become a member by paying the yearly subscription.', '支付一年的会员费就可以成为会员。');
INSERT INTO `words` VALUES (8951, 'subsequent', 'ˈsʌbsɪkwənt', 'adj.', '随后的;后来的;之后的;', '...the increase of population in subsequent years...', '随后几年人口的增长');
INSERT INTO `words` VALUES (8952, 'subsequently', 'ˈsʌbsɪkwəntli', 'adv.', '随后;后来;之后;', '...the increase of population in subsequent years...', '随后几年人口的增长');
INSERT INTO `words` VALUES (8953, 'subservient', 'səbˈsɜːviənt', 'adj.', '恭顺的;驯服的;谄媚的;', 'She is expected to be subservient to her uncle.', '她被要求对叔叔恭敬顺从。');
INSERT INTO `words` VALUES (8954, 'subset', 'ˈsʌbset', 'n.', '分组;小组;子集;', '...subsets of the population such as men, women, ethnic groups, etc.', '男性、女性、种族等人口的细分');
INSERT INTO `words` VALUES (8955, 'subside', 'səbˈsaɪd', 'v.', '趋于平静;平息;减弱;', 'The pain had subsided during the night...', '晚间疼痛已经减轻了。');
INSERT INTO `words` VALUES (8956, 'subsidiary', 'səbˈsɪdiəri', 'adj.', '辅助的;附带的;次要的;', '...British Asia Airways, a subsidiary of British Airways.', '英国亚洲航空公司，英国航空公司的子公司');
INSERT INTO `words` VALUES (8957, 'subsidy', 'ˈsʌbsədi', 'n.', '补贴;补助金;津贴;', 'European farmers are planning a massive demonstration against farm subsidy cuts...', '欧洲农场主正策划一场抗议削减农场补贴的大规模示威游行。');
INSERT INTO `words` VALUES (8958, 'subsist', 'səbˈsɪst', 'v.', '(尤指靠有限的食物或钱)维持生活，度日;存在;有效;', 'The prisoners subsisted on one mug of the worst quality porridge three times a day...', '犯人们每日三餐靠一碗稀粥度日。');
INSERT INTO `words` VALUES (8959, 'subsistence', 'səbˈsɪstəns', 'n.', '勉强维持生活;', '...below the subsistence level...', '低于最低生活水平');
INSERT INTO `words` VALUES (8960, 'subsoil', 'ˈsʌbsɔɪl', 'n.', '底土;心土;', '...the chalk subsoil on the site.', '那个工地的白垩底土');
INSERT INTO `words` VALUES (8961, 'subspecies', 'ˈsʌbˌspiːʃiːz', 'n.', '(动物、植物的)亚种;', 'Several other sub-species of gull are found in the region.', '在该地区还发现了海鸥的其他亚种。');
INSERT INTO `words` VALUES (8962, 'substance', 'ˈsʌbstəns', 'n.', '物质;物品;东西;', 'There\'s absolutely no regulation of cigarettes to make sure that they don\'t include poisonous substances...', '根本没有法规来确保香烟不含有毒物质。');
INSERT INTO `words` VALUES (8963, 'substandard', 'ˌsʌbˈstændəd', 'adj.', '不达标的;不合格的;', 'Residents in general are poor and undereducated, and live in sub-standard housing.', '居民们普遍都很贫穷，受教育程度低，居住条件较差。');
INSERT INTO `words` VALUES (8964, 'substantial', 'səbˈstænʃl', 'adj.', '大量的;价值巨大的;重大的;', 'The party has just lost office and with it a substantial number of seats...', '该党刚刚竞选失利，同时还失去了许多席位。');
INSERT INTO `words` VALUES (8965, 'substantially', 'səbˈstænʃəli', 'adv.', '非常;大大地;基本上;', 'The percentage of girls in engineering has increased substantially...', '学工科的女孩的比例已经大大增加了。');
INSERT INTO `words` VALUES (8966, 'substantiate', 'səbˈstænʃieɪt', 'v.', '证实;证明;', 'There is little scientific evidence to substantiate the claims.', '这些主张几乎找不到科学依据来证实。');
INSERT INTO `words` VALUES (8967, 'substantive', 'səbˈstæntɪv', 'adj.', '实质性的;本质上的;重大的;', 'They plan to meet again in Rome very soon to begin substantive negotiations.', '他们计划不久在罗马再次会晤以开始实质性的谈判。');
INSERT INTO `words` VALUES (8968, 'substitute', 'ˈsʌbstɪtjuːt', 'n.', '代替者;代替物;代用品;', 'They were substituting violence for dialogue...', '他们在用暴力取代对话。');
INSERT INTO `words` VALUES (8969, 'substitution', 'ˌsʌbstɪ\'tjuːʃ(ə)n', 'n.', '置换;取代;代换;', 'They were substituting violence for dialogue...', '他们在用暴力取代对话。');
INSERT INTO `words` VALUES (8970, 'substratum', 'ˈsʌbstrɑːtəm', 'n.', '(尤指岩石或土壤的)下层，底层;', '...its deep substratum of chalk...', '深厚的白垩地层');
INSERT INTO `words` VALUES (8971, 'subsume', 'səbˈsjuːm', 'v.', '将…归入(或纳入);', 'After that the two alliances might be subsumed into a new European security system...', '从那以后，这两个联盟将可能会被纳入一个新的欧洲安全体系之中。');
INSERT INTO `words` VALUES (8972, 'subterfuge', 'ˈsʌbtəfjuːdʒ', 'n.', '(通常指欺骗性)秘密手段;诡计;', 'Most people can see right through that type of subterfuge...', '大多数人能一眼看透这种伎俩。');
INSERT INTO `words` VALUES (8973, 'subterranean', 'ˌsʌbtəˈreɪniən', 'adj.', '地下的;', 'London has 9 miles of such subterranean passages.', '伦敦像这样的地下通道有9英里长。');
INSERT INTO `words` VALUES (8974, 'subtitle', 'ˈsʌbtaɪtl', 'n.', '(电影或电视上的)字幕;副标题;小标题;', '\'Kathleen\' was, as its 1892 subtitle asserted, \'An Irish Drama\'.', '正如 1892 年剧本的副标题所强调的，《凯瑟琳》是一部“爱尔兰戏剧”。');
INSERT INTO `words` VALUES (8975, 'subtle', 'ˈsʌtl', 'adj.', '不易察觉的;不明显的;微妙的;', '...the slow and subtle changes that take place in all living things...', '发生在所有生物身上的缓慢而细微的变化');
INSERT INTO `words` VALUES (8976, 'subtlety', 'ˈsʌtlti', 'n.', '细微;微妙;狡猾;', 'His fascination with the subtleties of human behaviour makes him a good storyteller...', '他对人们行为上的细微差别极感兴趣，这使他成了一个很会讲故事的人。');
INSERT INTO `words` VALUES (8977, 'subtly', '\'sʌtli', 'adv.', '巧妙地;微妙地;难解地;', '...the slow and subtle changes that take place in all living things...', '发生在所有生物身上的缓慢而细微的变化');
INSERT INTO `words` VALUES (8978, 'subtract', 'səbˈtrækt', 'v.', '减;减去;', 'Mandy subtracted the date of birth from the date of death...', '曼迪用去世的日期减去出生的日期。');
INSERT INTO `words` VALUES (8979, 'subtraction', 'səb\'trækʃ(ə)n', 'n.', '扣除;减法;', 'Mandy subtracted the date of birth from the date of death...', '曼迪用去世的日期减去出生的日期。');
INSERT INTO `words` VALUES (8980, 'subtropical', 'ˌsʌbˈtrɒpɪkl', 'adj.', '亚热带的;副热带的;', '...the sub-tropical region of the Chapare.', '查帕尔省的亚热带地区');
INSERT INTO `words` VALUES (8981, 'suburb', 'ˈsʌbɜːb', 'n.', '郊区;城外;', 'Anna was born in 1923 in Ardwick, a suburb of Manchester.', '安娜于1923年出生在曼彻斯特的郊区阿德维克。');
INSERT INTO `words` VALUES (8982, 'suburban', 'səˈbɜːbən', 'adj.', '郊区的;城外的;平淡乏味的;', '...a comfortable suburban home.', '城外舒适的住所');
INSERT INTO `words` VALUES (8983, 'subversion', 'səbˈvɜːʃn', 'n.', '(对政府、政治制度的)颠覆，破坏;', 'He was arrested in parliament on charges of subversion for organizing the demonstration.', '他因组织示威活动在议会上被以颠覆破坏罪名逮捕。');
INSERT INTO `words` VALUES (8984, 'subversive', 'səbˈvɜːsɪv', 'adj.', '颠覆性的;暗中起破坏作用的;', 'This courageous and subversive movie has attracted widespread critical support.', '这部大胆而具颠覆性的电影引起了评论界广泛的支持。');
INSERT INTO `words` VALUES (8985, 'subvert', 'səbˈvɜːt', 'v.', '颠覆;暗中破坏;使背叛;', '...an alleged plot to subvert the state.', '涉嫌颠覆国家的阴谋');
INSERT INTO `words` VALUES (8986, 'subway', 'ˈsʌbweɪ', 'n.', '地下铁道;地铁交通;(穿越马路等的)地下人行道;', '...the Bay Area Rapid Transit subway system...', '旧金山海湾地区地铁捷运系统');
INSERT INTO `words` VALUES (8987, 'succeed', 'səkˈsiːd', 'v.', '达到目的;实现目标;办到;', 'We have already succeeded in working out ground rules with the Department of Defense...', '我们已经成功地与国防部敲定了基本原则。');
INSERT INTO `words` VALUES (8988, 'success', 'səkˈses', 'n.', '成功;胜利;发财;', 'It\'s important for the long-term success of any diet that you vary your meals.', '如果想要成功做到长期节食，很重要的一点是要膳食多样化。');
INSERT INTO `words` VALUES (8989, 'successful', 'səkˈsesfl', 'adj.', '达到目的;有成效的;获得成功的;', 'How successful will this new treatment be?...', '这一新的疗法会有多大成效？');
INSERT INTO `words` VALUES (8990, 'successfully', 'sək\'sesfəlɪ', 'adv.', '顺利地;', 'How successful will this new treatment be?...', '这一新的疗法会有多大成效？');
INSERT INTO `words` VALUES (8991, 'succession', 'səkˈseʃn', 'n.', '一连串;一系列;连续的人(或事物);', 'Adams took a succession of jobs which have stood him in good stead...', '亚当斯连着做过几份让他颇为受益的工作。');
INSERT INTO `words` VALUES (8992, 'successive', 'səkˈsesɪv', 'adj.', '连续的;接连的;相继的;', 'Jackson was the winner for a second successive year...', '杰克逊已经是连续第二年获胜了。');
INSERT INTO `words` VALUES (8993, 'successively', 'səkˈsɛsɪvli', 'adv.', '连续;先后;渐渐;', 'Jackson was the winner for a second successive year...', '杰克逊已经是连续第二年获胜了。');
INSERT INTO `words` VALUES (8994, 'successor', 'səkˈsesə(r)', 'n.', '接替者;继任者;接替的事物;', 'He set out several principles that he hopes will guide his successors...', '他制订了几条原则，希望可以引导继任者。');
INSERT INTO `words` VALUES (8995, 'succinct', 'səkˈsɪŋkt', 'adj.', '简明的;言简意赅的;', 'The book gives an admirably succinct account of the technology and its history...', '这本书极其简明扼要地介绍了这项技术及其发展史。');
INSERT INTO `words` VALUES (8996, 'succulent', 'ˈsʌkjələnt', 'adj.', '汁多味美的;肉质的;多汁的;', 'Cook pieces of succulent chicken with ample garlic and a little sherry.', '烹饪鲜美滑嫩的鸡肉时多加些大蒜，并滴入少许雪利酒。');
INSERT INTO `words` VALUES (8997, 'succumb', 'səˈkʌm', 'v.', '屈服;屈从;抵挡不住(攻击、疾病、诱惑等);', 'Don\'t succumb to the temptation to have just one cigarette...', '不要经不住诱惑，只抽一支烟也不行。');
INSERT INTO `words` VALUES (8998, 'suck', 'sʌk', 'v.', '吮吸;吸;咂;', 'They waited in silence and sucked their sweets...', '他们安静地等着，嘴里咂着糖果。');
INSERT INTO `words` VALUES (8999, 'sucker', 'ˈsʌkə(r)', 'n.', '容易上当受骗的人;没有主见的人;不由得对…入迷的人;', 'But that is what the suckers want so you give it them...', '但那就是那些笨蛋们想要的，给他们就是了。');
INSERT INTO `words` VALUES (9000, 'suckle', 'ˈsʌkl', 'v.', '给…喂奶;给…哺乳;吸奶;', 'A young woman suckling a baby is one of life\'s most natural and delightful scenes.', '年轻女性哺育婴儿是生命中最自然、最令人愉悦的一幕。');
INSERT INTO `words` VALUES (9001, 'sucrose', 'ˈsuːkrəʊz', 'n.', '蔗糖;', '...simple sugars like sucrose, glucose and fructose.', '诸如蔗糖、葡萄糖和果糖等单糖');
INSERT INTO `words` VALUES (9002, 'suction', 'ˈsʌkʃn', 'n.', '吸;抽吸;吸出;', 'Dustbags act as a filter and suction will be reduced if they are too full...', '集尘袋起着过滤的作用，所集的灰尘太多时其吸附作用就会减小。');
INSERT INTO `words` VALUES (9003, 'sudden', 'ˈsʌdn', 'adj.', '突然的;忽然的;骤然的;', 'He had been deeply affected by the sudden death of his father-in-law...', '岳父的猝死使他十分伤心。');
INSERT INTO `words` VALUES (9004, 'suddenly', 'ˈsʌdənli', 'adv.', '突然;忽然;猛地;', 'Suddenly, she looked ten years older...', '突然间她看起来老了 10 岁。');
INSERT INTO `words` VALUES (9005, 'sue', 'suː', 'v.', '控告;提起诉讼;(尤指在法庭上)提出请求;', 'Mr Warren sued him for libel over the remarks...', '沃伦先生控告他散布诽谤性言论。');
INSERT INTO `words` VALUES (9006, 'suede', 'sweɪd', 'n.', '绒面革;仿麂皮;', 'Albert wore a brown suede jacket and jeans.', '艾伯特穿着棕色绒面夹克和牛仔裤。');
INSERT INTO `words` VALUES (9007, 'suffer', 'ˈsʌfə(r)', 'v.', '(因疾病、痛苦、悲伤等)受苦，受难，受折磨;遭受;蒙受;', 'Within a few days she had become seriously ill, suffering great pain and discomfort...', '几天的时间，她就病得很严重了，疼痛难忍，周身不舒服。');
INSERT INTO `words` VALUES (9008, 'sufferer', 'ˈsʌfərə(r)', 'n.', '患病者;受苦者;受难者;', 'Frequently sufferers of this kind of allergy are also sufferers of asthma.', '患有这种过敏症的人也经常会患有哮喘病。');
INSERT INTO `words` VALUES (9009, 'suffering', 'ˈsʌfərɪŋ', 'n.', '疼痛;痛苦;折磨;', 'They began to recover slowly from their nightmare of pain and suffering...', '他们开始从痛苦的梦魇中慢慢恢复过来。');
INSERT INTO `words` VALUES (9010, 'suffice', 'səˈfaɪs', 'v.', '足够;足以;', 'A cover letter should never exceed one page; often a far shorter letter will suffice.', '附信不应超过一页，通常来说，一封相对非常简短的信就足够了。');
INSERT INTO `words` VALUES (9011, 'sufficiency', 'səˈfɪʃnsi', 'n.', '足量;充足;', 'When foods from different plant sources are eaten together, deficiency in one is compensated for by sufficiency in another...', '多种蔬果类食物一起吃的话，一种食物所缺乏的营养会由另一种富含该营养的食物予以补足。');
INSERT INTO `words` VALUES (9012, 'sufficient', 'səˈfɪʃnt', 'adj.', '足够的;充足的;', 'One metre of fabric is sufficient to cover the exterior of an 18-in-diameter hatbox...', '一米布足以包裹住直径为 18 英寸的帽盒。');
INSERT INTO `words` VALUES (9013, 'sufficiently', 'sə\'fɪʃ(ə)ntlɪ', 'adv.', '足以;十分;充分地;', 'One metre of fabric is sufficient to cover the exterior of an 18-in-diameter hatbox...', '一米布足以包裹住直径为 18 英寸的帽盒。');
INSERT INTO `words` VALUES (9014, 'suffocate', 'ˈsʌfəkeɪt', 'v.', '(使)窒息而死;(把…)闷死;让人感觉闷热;', 'He either suffocated, or froze to death...', '他要么是闷死的，要么是冻死的。');
INSERT INTO `words` VALUES (9015, 'suffocating', 'ˈsʌfəkeɪtɪŋ', 'adj.', '令人呼吸困难的;闷的;使人窒息的;', 'He either suffocated, or froze to death...', '他要么是闷死的，要么是冻死的。');
INSERT INTO `words` VALUES (9016, 'suffrage', 'ˈsʌfrɪdʒ', 'n.', '选举权;投票权;', 'He was an advocate of universal suffrage as a basis for social equality.', '他提倡普选权，认为这是社会公平的基础。');
INSERT INTO `words` VALUES (9017, 'suffuse', 'səˈfjuːz', 'v.', '布满;弥漫于;充满;', 'A dull red flush suffused Selby\'s face.', '塞尔比的脸庞泛起了淡淡的红晕。');
INSERT INTO `words` VALUES (9018, 'sugar', 'ˈʃʊɡə(r)', 'n.', '食糖;一匙糖;一块方糖;', '...bags of sugar...', '几袋食糖');
INSERT INTO `words` VALUES (9019, 'sugary', 'ˈʃʊɡəri', 'adj.', '含糖的;甜的;(态度等)甜腻腻的，媚人的;', 'Sugary canned drinks rot your teeth.', '喝含糖的罐装饮料会滋生蛀牙。');
INSERT INTO `words` VALUES (9020, 'suggest', 'səˈdʒest', 'v.', '建议;提议;推荐;', 'He suggested a link between class size and test results of seven-year-olds...', '他认为 7 岁大的学生的测试分数和班级的规模有关系。');
INSERT INTO `words` VALUES (9021, 'suggested', 'səˈdʒestɪd', 'v.', '建议;提议;推荐;', 'He suggested a link between class size and test results of seven-year-olds...', '他认为 7 岁大的学生的测试分数和班级的规模有关系。');
INSERT INTO `words` VALUES (9022, 'suggestible', 'səˈdʒestəbl', 'adj.', '易受他人影响的;', '...highly suggestible and compliant individuals.', '那些毫无主见、对别人言听计从的人');
INSERT INTO `words` VALUES (9023, 'suggestion', 'səˈdʒestʃən', 'n.', '建议;提议;使人作(尤其是不好的事情的)推测的理由;', 'The dietitian was helpful, making suggestions as to how I could improve my diet...', '这位营养学家就如何改善饮食给我提出了些建议，对我很有帮助。');
INSERT INTO `words` VALUES (9024, 'suggestive', 'səˈdʒestɪv', 'adj.', '使人想起…的;引起联想的;性暗示的;', 'The fingers were gnarled, lumpy, with long, curving nails suggestive of animal claws...', '手指粗糙，关节突出，指甲又长又弯，让人想起动物的爪子。');
INSERT INTO `words` VALUES (9025, 'suicidal', 'ˌsuːɪˈsaɪdl', 'adj.', '想自杀的;有自杀倾向的;自杀性的;', 'I was suicidal and just couldn\'t stop crying...', '我当时想自杀，一直哭个不停。');
INSERT INTO `words` VALUES (9026, 'suicide', 'ˈsuːɪsaɪd', 'n.', '自杀;自杀性行为;自毁;', 'She tried to commit suicide on several occasions.', '她自杀过好几次。');
INSERT INTO `words` VALUES (9027, 'suit', 'suːt', 'n.', '套装;西装;西服;', '...a dark pinstriped business suit.', '黑色的细条纹正装');
INSERT INTO `words` VALUES (9028, 'suitable', 'ˈsuːtəbl', 'adj.', '合适的;适宜的;适当;', 'Employers usually decide within five minutes whether someone is suitable for the job...', '雇主通常在 5 分钟内就能决定一个人是否适合这份工作。');
INSERT INTO `words` VALUES (9029, 'suitably', 'ˈsuːtəbli', 'adv.', '合适地;适宜地;适当地;', 'There are problems in recruiting suitably qualified scientific officers for NHS laboratories...', '很难为国民医疗保健系统的实验室招募到资历合适的高级科研人员。');
INSERT INTO `words` VALUES (9030, 'suitcase', 'ˈsuːtkeɪs', 'n.', '(旅行用的)手提箱;', 'It did not take Andrew long to pack a suitcase.', '安德鲁很快就整理好了手提箱。');
INSERT INTO `words` VALUES (9031, 'suite', 'swiːt', 'n.', '(尤指旅馆的)一套房间，套房;一套家具;组曲(由三个或更多相关部分组成);', 'They had a fabulous time during their week in a suite at the Paris Hilton.', '他们在巴黎希尔顿酒店的套房里度过了非常愉快的一周。');
INSERT INTO `words` VALUES (9032, 'suited', 'ˈsuːtɪd', 'adj.', '合适;适宜;适当;', 'The area is well suited to road cycling as well as off-road riding...', '这个地方很适合举行自行车公路赛和越野赛。');
INSERT INTO `words` VALUES (9033, 'suitor', 'ˈsuːtə(r)', 'n.', '求婚者;有意收购另一公司的公司;', 'My mother had a suitor who adored her.', '我妈妈曾经有一个爱慕她的追求者。');
INSERT INTO `words` VALUES (9034, 'sulk', 'sʌlk', 'v.', '面有愠色;生闷气;', 'He turned his back and sulked.', '他转过身去，面带愠色。');
INSERT INTO `words` VALUES (9035, 'sulky', 'ˈsʌlki', 'adj.', '面有愠色的;生闷气的;', 'I was quite sulky, so I didn\'t take part in much.', '我很烦，所以没有积极参与。');
INSERT INTO `words` VALUES (9036, 'sullen', 'ˈsʌlən', 'adj.', '面有愠色的;闷闷不乐的;郁郁寡欢的;', 'The offenders lapsed into a sullen silence...', '那些冒犯者都闷不作声了。');
INSERT INTO `words` VALUES (9037, 'sully', 'ˈsʌli', 'v.', '败坏;有损于;弄脏;', 'The City\'s reputation has been sullied by scandals like those at Lloyd\'s...', '类似劳埃德保险公司的那些丑闻令伦敦名声扫地。');
INSERT INTO `words` VALUES (9038, 'sultan', 'ˈsʌltən', 'n.', '苏丹(某些穆斯林国家统治者的称号);', '...during the reign of Sultan Abdul Hamid.', '在苏丹阿卜杜勒·哈米德统治期间');
INSERT INTO `words` VALUES (9039, 'sultry', 'ˈsʌltri', 'adj.', '闷热的;姿色迷人的;风情万种的;', 'The climax came one sultry August evening.', '在 8 月一个闷热的夜晚，高潮到来了。');
INSERT INTO `words` VALUES (9040, 'sum', 'sʌm', 'n.', '金额;款项;和;', 'Large sums of money were lost...', '损失了大笔钱财。');
INSERT INTO `words` VALUES (9041, 'summary', 'ˈsʌməri', 'n.', '总结;概括;概要;', 'What follows is a brief summary of the process...', '接下来是对此流程的一个简短概括。');
INSERT INTO `words` VALUES (9042, 'summation', 'sʌˈmeɪʃn', 'n.', '总结;概括;汇总物;', 'Her introduction is a model of fairness, a lively summation of Irish history.', '她的导论是客观公正的典范，是对爱尔兰历史的生动概括。');
INSERT INTO `words` VALUES (9043, 'summer', 'ˈsʌmə(r)', 'n.', '夏天;夏季;', 'I escaped the heatwave in London earlier this summer and flew to Cork...', '今年初夏我逃离了伦敦的炎热，飞往科克去避暑。');
INSERT INTO `words` VALUES (9044, 'summit', 'ˈsʌmɪt', 'n.', '最高点;顶点;山顶;', '...next week\'s Washington summit.', '下周的华盛顿峰会 ');
INSERT INTO `words` VALUES (9045, 'summon', 'ˈsʌmən', 'v.', '传唤，传讯(出庭);召唤;召集，召开(会议);', 'Howe summoned a doctor and hurried over...', '豪叫了医生，然后匆忙赶了过来。');
INSERT INTO `words` VALUES (9046, 'summons', 'ˈsʌmənz', 'n.', '(法院的)传唤，传票;召唤;召见令;', 'I received a summons to the Palace from Sir Robert Fellowes, the Queen\'s private secretary.', '我被女王的私人秘书罗伯特·费洛斯爵士召往白金汉宫。');
INSERT INTO `words` VALUES (9047, 'sumptuous', 'ˈsʌmptʃuəs', 'adj.', '华贵的;豪华的;奢华的;', '...a sumptuous feast...', '盛宴');
INSERT INTO `words` VALUES (9048, 'sun', 'sʌn', 'n.', '太阳;日;太阳的光和热;', 'The sun was now high in the southern sky...', '太阳正高挂在南边的天空上。');
INSERT INTO `words` VALUES (9049, 'sunbathe', 'ˈsʌnbeɪð', 'v.', '沐日光浴;晒太阳;', 'Franklin swam and sunbathed at the pool every morning.', '每天上午富兰克林都在游泳池中游泳，在池边晒太阳。');
INSERT INTO `words` VALUES (9050, 'sunbeam', 'ˈsʌnbiːm', 'n.', '(一束)阳光;', 'A sunbeam slants through the west window.', '一束阳光从西边的窗户斜射进来。');
INSERT INTO `words` VALUES (9051, 'sunburn', 'ˈsʌnbɜːn', 'n.', '晒斑;晒伤;', 'The risk and severity of sunburn depend on the body\'s natural skin colour...', '晒伤的风险和严重程度取决于人体的自然肤色。');
INSERT INTO `words` VALUES (9052, 'sunburnt', '\'sʌnˌbɜː(r)nt', '', '晒黑的;“sunburned”的变体;', 'A badly sunburned face or back is extremely painful.', '严重的脸部或背部晒伤会非常疼痛。');
INSERT INTO `words` VALUES (9053, 'sundae', 'ˈsʌndeɪ', 'n.', '圣代冰激凌(加甜汁、果仁、水果粒等);', '...a chocolate sundae.', '巧克力圣代');
INSERT INTO `words` VALUES (9054, 'sunday', 'ˈsʌndeɪ', 'n.', '星期日;星期天;每逢星期日出版的报纸;', 'I thought we might go for a drive on Sunday...', '我想星期天我们可以开车去兜兜风。');
INSERT INTO `words` VALUES (9055, 'sunder', 'ˈsʌndə(r)', 'v.', '(尤指强制地)分开，使分离，割裂;', 'The city is being sundered by racial tension...', '该城市因种族关系紧张正在形成分裂。');
INSERT INTO `words` VALUES (9056, 'sundown', 'ˈsʌndaʊn', 'n.', '日落时分;', 'The fighting broke out about two hours after sundown.', '日落后大约两小时战斗打响了。');
INSERT INTO `words` VALUES (9057, 'sundries', 'ˈsʌndriz', 'n.', '杂物;杂项物品;', 'The inn gift shop stocks quality Indian crafts and sundries.', '旅馆的礼品店里备有上好的印度手工艺品和其他一些特色礼品。');
INSERT INTO `words` VALUES (9058, 'sundry', 'ˈsʌndri', 'adj.', '杂项的;', 'Scientists, business people, and sundry others gathered on Monday for the official opening...', '星期一的正式开幕式上,科学家、商界人士和其他各色人等齐聚一堂。');
INSERT INTO `words` VALUES (9059, 'sunglasses', 'ˈsʌnɡlɑːsɪz', 'n.', '太阳镜;墨镜;', 'She slipped on a pair of sunglasses.', '她迅速戴上一副太阳镜。');
INSERT INTO `words` VALUES (9060, 'sunken', 'ˈsʌŋkən', 'adj.', '沉没的;沉入水底的;凹陷的，深陷的(尤因疾病、年老或饥饿);', 'The sunken sailing-boat was a glimmer of white on the bottom...', '沉没的帆船在海底闪烁着白光。');
INSERT INTO `words` VALUES (9061, 'sunlight', 'ˈsʌnlaɪt', 'n.', '阳光;日光;', 'I saw her sitting at a window table, bathed in sunlight.', '我看见她坐在靠窗的桌旁，沐浴在阳光中。');
INSERT INTO `words` VALUES (9062, 'sunlit', 'ˈsʌnlɪt', 'adj.', '阳光照耀的;', 'Her house has two big sunlit rooms with floor-to-ceiling windows.', '她的房子有两间向阳的带落地窗的大屋子。');
INSERT INTO `words` VALUES (9063, 'sunny', 'ˈsʌni', 'adj.', '阳光充足的;欢乐的;快乐的;', 'The weather was surprisingly warm and sunny...', '天气出奇地暖和，阳光充足。');
INSERT INTO `words` VALUES (9064, 'sunrise', 'ˈsʌnraɪz', 'n.', '日出;朝霞;', 'The rain began before sunrise.', '日出前下起了雨。');
INSERT INTO `words` VALUES (9065, 'sunset', 'ˈsʌnset', 'n.', '日落;傍晚;晚霞;', 'The dance ends at sunset.', '舞会在日落时分结束。');
INSERT INTO `words` VALUES (9066, 'sunshine', 'ˈsʌnʃaɪn', 'n.', '阳光;日光;欢乐;', 'In the marina yachts sparkle in the sunshine...', '码头上游艇在阳光下闪闪发光。');
INSERT INTO `words` VALUES (9067, 'sunup', 'ˈsʌnʌp', 'n.', '日出;', 'We worked from sunup to sunset.', '我们从日出工作到日落。');
INSERT INTO `words` VALUES (9068, 'sup', 'sʌp', 'v.', '(小口地)喝;呷;啜;', 'We supped mulled wine.', '我们小口地喝着热葡萄酒。');
INSERT INTO `words` VALUES (9069, 'super', 'ˈsuːpə(r)', 'adj.', '顶好的;超级的;顶呱呱的;', 'We had a super time...', '我们玩得很痛快。');
INSERT INTO `words` VALUES (9070, 'superannuated', 'ˌsuːpərˈænjueɪtɪd', 'adj.', '年老不能工作的;陈旧不中用的;过时的;', '...the superannuated idealism of the Sixties.', '早已过时的 60 年代的理想主义');
INSERT INTO `words` VALUES (9071, 'superb', 'suːˈpɜːb', 'adj.', '极佳的;卓越的;质量极高的;', 'There is a superb 18-hole golf course 6 miles away...', '6 英里外有一个一流的 18 洞高尔夫球场。');
INSERT INTO `words` VALUES (9072, 'superbly', '', 'adv.', '极好地;庄重地;', 'There is a superb 18-hole golf course 6 miles away...', '6 英里外有一个一流的 18 洞高尔夫球场。');
INSERT INTO `words` VALUES (9073, 'supercilious', 'ˌsuːpəˈsɪliəs', 'adj.', '傲慢的;高傲的;', 'His manner is supercilious and arrogant...', '他非常傲慢自大。');
INSERT INTO `words` VALUES (9074, 'superficial', 'ˌsuːpəˈfɪʃl', 'adj.', '粗略的;肤浅的;粗枝大叶的;', 'This guy is a superficial yuppie with no intellect whatsoever...', '这个家伙是个肤浅的雅皮士，没有什么头脑。');
INSERT INTO `words` VALUES (9075, 'superficially', 'ˌsu:pə\'fɪʃəlɪ', 'adv.', '从表面上看;', 'This guy is a superficial yuppie with no intellect whatsoever...', '这个家伙是个肤浅的雅皮士，没有什么头脑。');
INSERT INTO `words` VALUES (9076, 'superfluity', 'ˌsuːpəˈfluːəti', 'n.', '多余;过剩;冗余;', 'The city has a superfluity of five-star hotels.', '这个城市的五星级宾馆太多了。');
INSERT INTO `words` VALUES (9077, 'superfluous', 'suːˈpɜːfluəs', 'adj.', '过剩的;过多的;多余的;', 'My presence at the afternoon\'s proceedings was superfluous...', '我去参加下午的活动是多此一举。');
INSERT INTO `words` VALUES (9078, 'superhighway', 'ˌsuːpəˈhaɪweɪ', 'n.', '(超级)高速公路;(信息)高速公路;', 'He took off for the city on the eight-lane superhighway.', '他沿着八车道的高速公路向城市驶去。');
INSERT INTO `words` VALUES (9079, 'superimpose', 'ˌsuːpərɪmˈpəʊz', 'v.', '使(图像甲)叠映在(图像乙)上;使重叠;使叠加;', 'His picture was superimposed on a muscular body...', '他的照片被移花接木，放在了一个肌肉发达的身体上。');
INSERT INTO `words` VALUES (9080, 'superintend', 'ˌsuːpərɪnˈtend', 'v.', '主管;监督;监管;', 'During the interval, Linton superintended a prize draw.', '中间休息时，林顿组织了一次抽奖。');
INSERT INTO `words` VALUES (9081, 'superintendent', 'ˌsuːpərɪnˈtendənt', 'n.', '主管人;负责人;监管人;', 'He was stopped at the airport by an assistant superintendent of police.', '他在机场被一名助理警长扣留了。');
INSERT INTO `words` VALUES (9082, 'superior', 'suːˈpɪəriə(r)', 'adj.', '(在品质上)更好的;占优势;更胜一筹;', 'We have a relationship infinitely superior to those of many of our friends...', '我们的关系要比我们其他许多朋友的关系好得多。');
INSERT INTO `words` VALUES (9083, 'superiority', 'suːˌpɪəriˈɒrəti', 'n.', '优越(性);优势;优越感;', 'The US will need a three-to-one superiority in forces to be sure of a successful attack...', '美国需要在军力上具有3比1的优势才能确保进攻胜利。');
INSERT INTO `words` VALUES (9084, 'superlative', 'suːˈpɜːlətɪv', 'adj.', '极佳的;卓越的;最优秀的;', 'Some superlative wines are made in this region...', '一些上好的葡萄酒就是在这个地区酿造的。');
INSERT INTO `words` VALUES (9085, 'superman', 'ˈsuːpəmæn', 'n.', '有非凡才能的人;超人;', 'Collor nurtured the idea that he was a superman, who single-handedly could resolve Brazil\'s crisis.', '科洛尔一直希望自己有超凡能力，凭一己之力就能解决巴西的危机。');
INSERT INTO `words` VALUES (9086, 'supermarket', 'ˈsuːpəmɑːkɪt', 'n.', '超级市场;超市;', 'Most of us do our food shopping in the supermarket...', '我们大多数人去超市购买食品。');
INSERT INTO `words` VALUES (9087, 'supernatural', 'ˌsuːpəˈnætʃrəl', 'adj.', '超自然的;神奇的;神灵魔怪的;', 'The Nakani were evil spirits who looked like humans and possessed supernatural powers.', '纳卡尼是邪恶的魔鬼，长得像人类但具有超能力。');
INSERT INTO `words` VALUES (9088, 'supernova', 'ˈsuːpənəʊvə', 'n.', '超新星;', 'At least one supernova occurs per decade in our galaxy.', '在我们的星系中每 10 年至少有一颗超新星出现。');
INSERT INTO `words` VALUES (9089, 'superpower', 'ˈsuːpəpaʊə(r)', 'n.', '超级大国;', 'The United States could claim to be both a military and an economic superpower.', '美国在军事和经济上都称得上是超级大国。');
INSERT INTO `words` VALUES (9090, 'supersede', 'ˌsuːpəˈsiːd', 'v.', '取代，替代(已非最佳选择或已过时的事物);', 'Hand tools are relics of the past that have now been superseded by the machine.', '手工工具是历史的遗物，现在已经被机器所取代。');
INSERT INTO `words` VALUES (9091, 'supersonic', 'ˌsuːpəˈsɒnɪk', 'adj.', '超声速的;', 'There was a huge bang; it sounded like a supersonic jet.', '传来一声巨响，听起来像是超音速喷气式飞机的声音。');
INSERT INTO `words` VALUES (9092, 'superstar', 'ˈsuːpəstɑː(r)', 'n.', '超级明星;', 'He was more than a footballing superstar, he was a celebrity.', '他不仅仅是足球场上的巨星，也是一位社会名流。');
INSERT INTO `words` VALUES (9093, 'superstition', 'ˌsuːpəˈstɪʃn', 'n.', '迷信;迷信观念(或思想);', 'Fortune-telling is a very much debased art surrounded by superstition...', '算命是一种非常低级的把戏，充满了迷信色彩。');
INSERT INTO `words` VALUES (9094, 'superstitious', 'ˌsuːpəˈstɪʃəs', 'adj.', '迷信的;有迷信观念的;', 'Jean was extremely superstitious and believed the colour green brought bad luck.', '琼非常迷信，认为绿色会带来厄运。');
INSERT INTO `words` VALUES (9095, 'superstructure', 'ˈsuːpəstrʌktʃə(r)', 'n.', '(船舶的)上层结构;(建筑物的)上部结构;(社会的)上层建筑;', 'We might try to clear up some of the cabins in the superstructure.', '我们可以设法清理出主甲板之上的一些船舱。');
INSERT INTO `words` VALUES (9096, 'supervise', 'ˈsuːpəvaɪz', 'v.', '监督;管理;指导;', 'University teachers have refused to supervise students\' examinations...', '大学教师拒绝监考。');
INSERT INTO `words` VALUES (9097, 'supervision', 'ˌsuːpəˈvɪʒn', 'n.', '监督;管理;', 'A toddler requires close supervision and firm control at all times...', '刚学步的孩子时刻需要寸步不离的照看，并要牢牢看紧。');
INSERT INTO `words` VALUES (9098, 'supervisor', 'ˈsuːpəvaɪzə(r)', 'n.', '监督人;指导者;主管人;', '...a full-time job as a supervisor at a factory...', '在一家工厂担任主管的全职工作');
INSERT INTO `words` VALUES (9099, 'supervisory', 'ˌsuːpəˈvaɪzəri', 'adj.', '管理的;监督的;指导的;', 'Most supervisory boards meet only twice a year.', '大多数监事会一年只开两次会议。');
INSERT INTO `words` VALUES (9100, 'supine', 'ˈsuːpaɪn', 'adj.', '仰卧的;平躺着的;得过且过的;', '...bedridden persons confined to the supine position.', '那些只能平躺在床上的久病不起的人。');
INSERT INTO `words` VALUES (9101, 'supper', 'ˈsʌpə(r)', 'n.', '晚饭;晚餐;夜宵;', 'Some guests like to dress for supper.', '一些客人喜欢穿礼服去赴晚宴。Y See Usage Note at meal. 见 meal 的用法说明');
INSERT INTO `words` VALUES (9102, 'suppertime', '', 'n.', '晚饭时间;夜宵时间;', 'They\'ll be back by suppertime...', '他们晚饭前会回来。');
INSERT INTO `words` VALUES (9103, 'supplant', 'səˈplɑːnt', 'v.', '取代，替代(尤指年老者或落后于时代的事物);', 'He may be supplanted by a younger man...', '他可能会被一个年龄比他小的人取代。');
INSERT INTO `words` VALUES (9104, 'supple', 'ˈsʌpl', 'adj.', '(身体)柔软的，灵活的;柔韧性好的;易弯曲的;', 'The leather is supple and sturdy enough to last for years...', '该皮革柔软而结实，足以用上数年。');
INSERT INTO `words` VALUES (9105, 'supplement', 'ˈsʌplɪmənt , ˈsʌplɪment', 'n.', '增补(物);补充(物);添加物;', '...people doing extra jobs outside their regular jobs to supplement their incomes...', '除了正常的工作外还要额外兼职以贴补收入的人');
INSERT INTO `words` VALUES (9106, 'supplemental', 'ˌsʌplɪˈmɛntl', 'adj.', '同 supplementary;增补性的;补充性的;', 'You\'ll probably be able to buy supplemental insurance at an extra cost...', '再额外多付一些钱的话，你或许就能买到附加险。');
INSERT INTO `words` VALUES (9107, 'supplementary', 'ˌsʌplɪˈmentri', 'adj.', '增补性的;补充性的;额外的;', '...the question of whether or not we need to take supplementary vitamins...', '是否需要补充维生素的问题');
INSERT INTO `words` VALUES (9108, 'supplicant', 'ˈsʌplɪkənt', 'n.', '(尤指向神灵或有权势者)恳求者，哀求者，祈求者;', 'He flung himself down in the flat submissive posture of a mere supplicant.', '他以一个卑微的哀求者绝对谦恭的姿态猛地趴在地上。');
INSERT INTO `words` VALUES (9109, 'supplier', 'səˈplaɪə(r)', 'n.', '供应者;供货商;供货方;', '...Hillsdown Holdings, one of the UK\'s biggest food suppliers...', '希尔斯顿控股公司，英国最大的食品供应商之一 ');
INSERT INTO `words` VALUES (9110, 'supplies', 'səˈplaɪz', 'n.', '供应量;供给量;储备;', '...an agreement not to produce or supply chemical weapons.', '不生产或供应化学武器的协议');
INSERT INTO `words` VALUES (9111, 'supply', 'səˈplaɪ', 'n.', '供应量;供给量;储备;', '...an agreement not to produce or supply chemical weapons.', '不生产或供应化学武器的协议');
INSERT INTO `words` VALUES (9112, 'support', 'səˈpɔːt', 'v.', '支持;拥护;鼓励;', 'The vice president insisted that he supported the hard-working people of New York...', '副总统坚称他支持辛勤工作的纽约人。');
INSERT INTO `words` VALUES (9113, 'supporter', 'səˈpɔːtə(r)', 'n.', '支持者;拥护者;(运动队的)支持者;', 'The fourth night of violence in the German city of Rostock was triggered by football supporters...', '发生在德国城市罗斯托克的第四起夜间暴乱是由足球球迷们挑起的。');
INSERT INTO `words` VALUES (9114, 'supporters', 'səˈpɔːtəz', 'n.', '支持者;拥护者;(运动队的)支持者;', 'The fourth night of violence in the German city of Rostock was triggered by football supporters...', '发生在德国城市罗斯托克的第四起夜间暴乱是由足球球迷们挑起的。');
INSERT INTO `words` VALUES (9115, 'supporting', 'səˈpɔːtɪŋ', 'adj.', '(演员、角色)次要的;配角的;能确证的;', '...the winner of the best supporting actress award.', '最佳女配角奖获得者');
INSERT INTO `words` VALUES (9116, 'supportive', 'səˈpɔːtɪv', 'adj.', '给予帮助的;支持的;鼓励的;', 'They were always supportive of each other...', '他们一直互相帮助。');
INSERT INTO `words` VALUES (9117, 'suppose', 'səˈpəʊz', 'v.', '(根据所知)认为，推断，料想;假定;假设;', 'Suppose someone gave you an egg and asked you to describe exactly what was inside...', '假设有人给了你一枚鸡蛋并要你准确描述鸡蛋里面有什么。');
INSERT INTO `words` VALUES (9118, 'supposing', 'səˈpəʊzɪŋ', 'conj.', '假定;假设;设想;', 'Suppose someone gave you an egg and asked you to describe exactly what was inside...', '假设有人给了你一枚鸡蛋并要你准确描述鸡蛋里面有什么。');
INSERT INTO `words` VALUES (9119, 'supposition', 'ˌsʌpəˈzɪʃn', 'n.', '推测的想法;推断的结论;推测;', 'There\'s a popular supposition that we\'re publicly funded but the bulk of our money comes from competitive contracts...', '人们普遍认为我们是受公共基金的资助，然而我们大部分资金是通过签订竞争性合同得来的。');
INSERT INTO `words` VALUES (9120, 'suppress', 'səˈpres', 'v.', '镇压;(武力)平定;压制;', '...drug traffickers, who continue to flourish despite international attempts to suppress them...', '在国际社会强力打击下活动依然猖獗的贩毒分子');
INSERT INTO `words` VALUES (9121, 'suppression', 'səˈpreʃn', 'n.', '镇压;压制;抑制;', '...drug traffickers, who continue to flourish despite international attempts to suppress them...', '在国际社会强力打击下活动依然猖獗的贩毒分子');
INSERT INTO `words` VALUES (9122, 'supremacy', 'suːˈpreməsi', 'n.', '至高无上;最大权力;最高权威;', 'The conservative old guard had re-established its political supremacy...', '守旧的保守派已经重新掌控了政治大权。');
INSERT INTO `words` VALUES (9123, 'supreme', 'suːˈpriːm', 'adj.', '(级别或地位)最高的，至高无上的;(程度)很大的，最大的;', 'MacArthur was Supreme Commander for the allied powers in the Pacific.', '麦克阿瑟将军曾是太平洋战区盟军的最高统帅。');
INSERT INTO `words` VALUES (9124, 'supremely', 'suːˈpriːmli', 'adv.', '极其;极为;', 'MacArthur was Supreme Commander for the allied powers in the Pacific.', '麦克阿瑟将军曾是太平洋战区盟军的最高统帅。');
INSERT INTO `words` VALUES (9125, 'surcharge', 'ˈsɜːtʃɑːdʒ', 'n.', '额外费用;附加费;增收费;', 'The government introduced a 15% surcharge on imports...', '政府对进口产品加收 15 %的附加费。');
INSERT INTO `words` VALUES (9126, 'sure', 'ʃʊə(r)', 'adj.', '确信;确知;肯定;', 'He\'d never been in a class before and he was not even sure that he should have been teaching...', '他以前从未教过课，甚至不确定自己该不该教课。');
INSERT INTO `words` VALUES (9127, 'surefire', 'ˈʃʊəˌfaɪə', 'adj.', '必定成功的;十拿九稳的;', 'If something\'s a sure-fire hit then Radio One will play it.', '如果哪首歌一定会火，那么第一频道肯定会播出。');
INSERT INTO `words` VALUES (9128, 'surely', 'ˈʃʊəli', 'adv.', '(对自己的话很有把握，希望他人同意)想必;用于否定句，表示难以置信;无疑;', 'You\'re an intelligent woman, surely you realize by now that I\'m helping you...', '你是个聪明的女人，想必你现在明白了我是在帮你。');
INSERT INTO `words` VALUES (9129, 'sureness', 'ˈʃʊənəs', 'n.', '沉着自信;胸有成竹;有把握;', 'He\'d never been in a class before and he was not even sure that he should have been teaching...', '他以前从未教过课，甚至不确定自己该不该教课。');
INSERT INTO `words` VALUES (9130, 'surf', 'sɜːf', 'n.', '激浪;拍岸浪花;', '...surf rolling onto white sand beaches.', '涌上白色沙滩的激浪');
INSERT INTO `words` VALUES (9131, 'surface', 'ˈsɜːfɪs', 'n.', '表面;表层;面;', 'Ozone forms a protective layer between 12 and 30 miles above the Earth\'s surface.', '臭氧在地球表面上方 12 到 30 英里处形成一个保护层。');
INSERT INTO `words` VALUES (9132, 'surfeit', 'ˈsɜːfɪt', 'n.', '过量;', 'Rationing had put an end to a surfeit of biscuits long ago.', '定量供应很久以前就结束了饼干过剩的状况。');
INSERT INTO `words` VALUES (9133, 'surfer', 'ˈsɜːfə(r)', 'n.', '进行冲浪运动的人;(互联网上)冲浪者，漫游者;网迷;', '...surf rolling onto white sand beaches.', '涌上白色沙滩的激浪');
INSERT INTO `words` VALUES (9134, 'surge', 'sɜːdʒ', 'v.', '涌;汹涌;涌动;', 'Specialists see various reasons for the recent surge in inflation...', '专家们认为目前通货膨胀加剧有多种原因。');
INSERT INTO `words` VALUES (9135, 'surgeon', 'ˈsɜːdʒən', 'n.', '外科医生;', '...a heart surgeon.', '心脏外科医师');
INSERT INTO `words` VALUES (9136, 'surgery', 'ˈsɜːdʒəri', 'n.', '外科手术;外科学;应诊时间;', 'His father has just recovered from heart surgery...', '他父亲刚刚从心脏外科手术中康复过来。');
INSERT INTO `words` VALUES (9137, 'surgical', 'ˈsɜːdʒɪkl', 'adj.', '外科的;外科手术的;', '...an array of surgical instruments.', '一批外科手术器械');
INSERT INTO `words` VALUES (9138, 'surly', 'ˈsɜːli', 'adj.', '脾气坏的;乖戾的;态度粗暴的;', 'He became surly and rude towards me.', '他变得对我粗暴无礼。');
INSERT INTO `words` VALUES (9139, 'surmise', 'səˈmaɪz , ˈsɜːmaɪz', 'v.', '推测;猜测;', 'There\'s so little to go on, we can only surmise what happened...', '几乎毫无凭据，我们只能猜测发生了什么。');
INSERT INTO `words` VALUES (9140, 'surmount', 'səˈmaʊnt', 'v.', '克服;解决;处于(某物)上面;', 'I realized I had to surmount the language barrier.', '我意识到我不得不克服语言障碍。');
INSERT INTO `words` VALUES (9141, 'surname', 'ˈsɜːneɪm', 'n.', '姓;', 'She\'d never known his surname...', '她一直不知道他姓什么。');
INSERT INTO `words` VALUES (9142, 'surpass', 'səˈpɑːs', 'v.', '超过;胜过;优于;', 'He was determined to surpass the achievements of his older brothers...', '他决心超越几个哥哥的成就。');
INSERT INTO `words` VALUES (9143, 'surplus', 'ˈsɜːpləs', 'n.', '过剩;剩余;过剩量;', 'Germany suffers from a surplus of teachers.', '德国遭遇了教师过剩的问题。');
INSERT INTO `words` VALUES (9144, 'surprise', 'səˈpraɪz', 'n.', '意想不到(或突然)的事;令人惊奇的事(或消息等);惊奇;', 'I have a surprise for you: We are moving to Switzerland!...', '我有一个惊喜的消息要告诉你：我们要搬去瑞士了！');
INSERT INTO `words` VALUES (9145, 'surprised', 'səˈpraɪzd', 'adj.', '惊奇的;惊讶的;觉得奇怪的;', 'This lady was genuinely surprised at what happened to her pet...', '这位女士对发生在自己宠物身上的事感到非常惊讶。');
INSERT INTO `words` VALUES (9146, 'surprising', 'səˈpraɪzɪŋ', 'adj.', '令人吃惊的;使人惊奇的;出人意料的;', 'It is not surprising that children learn to read at different rates...', '小孩子识字的速度不同,这不足为奇。');
INSERT INTO `words` VALUES (9147, 'surprisingly', 'sə(r)ˈpraɪzɪŋli', 'adv.', '令人惊讶地;出乎意料地;', 'It is not surprising that children learn to read at different rates...', '小孩子识字的速度不同,这不足为奇。');
INSERT INTO `words` VALUES (9148, 'surreal', 'səˈriːəl', 'adj.', '离奇的;怪诞的;梦幻般的;', '\'Performance\' is undoubtedly one of the most surreal movies ever made.', '《迷幻演出》无疑是有史以来最离奇的电影之一。');
INSERT INTO `words` VALUES (9149, 'surrealist', 'səˈriːəlɪst', 'n.', '超现实主义艺术家(或作家);', 'Dali\'s shoe hat was undoubtedly the most surrealist idea he ever worked on with Schiaparelli.', '达利的鞋状帽子无疑是他与夏帕瑞丽合作出的最具超现实主义色彩的创意。');
INSERT INTO `words` VALUES (9150, 'surrender', 'səˈrendə(r)', 'v.', '投降;(被迫)放弃，交出;', 'General Martin Bonnet called on the rebels to surrender...', '马丁·邦尼特将军呼吁反叛者投降。');
INSERT INTO `words` VALUES (9151, 'surreptitious', 'ˌsʌrəpˈtɪʃəs', 'adj.', '秘密的;趁人不注意赶紧进行的;', 'He made a surreptitious entrance to the club through the little door in the brick wall...', '他从砖墙的小门偷偷进入俱乐部。');
INSERT INTO `words` VALUES (9152, 'surrogate', 'ˈsʌrəɡət', 'adj.', '替代的;代用的;', 'Martin had become Howard Cosell\'s surrogate son...', '马丁代为当起了霍华德·科塞尔的儿子。');
INSERT INTO `words` VALUES (9153, 'surround', 'səˈraʊnd', 'v.', '围绕;环绕;(使)包围，围住;', 'The small churchyard was surrounded by a rusted wrought-iron fence...', '不大的教堂墓地周边围着一圈生锈的熟铁栅栏。');
INSERT INTO `words` VALUES (9154, 'surrounding', 'səˈraʊndɪŋ', 'adj.', '周围的;附近的;', 'The small churchyard was surrounded by a rusted wrought-iron fence...', '不大的教堂墓地周边围着一圈生锈的熟铁栅栏。');
INSERT INTO `words` VALUES (9155, 'surroundings', 'səˈraʊndɪŋz', 'n.', '环境;', 'Schumacher adapted effortlessly to his new surroundings.', '舒马赫非常轻松地适应了新环境。');
INSERT INTO `words` VALUES (9156, 'surveillance', 'sɜːˈveɪləns', 'n.', '(对犯罪嫌疑人或可能发生犯罪的地方的)监视;', 'He was arrested after being kept under constant surveillance...', '在被连续监视了一段时间后，他被逮捕了。');
INSERT INTO `words` VALUES (9157, 'surveyor', 'səˈveɪə(r)', 'n.', '(土地)测量员，勘测员;(建筑物)鉴定人;检验员;', '...the surveyor\'s maps of the Queen Alexandra Range.', '测量员绘制亚历山德拉皇后山脉勘测图');
INSERT INTO `words` VALUES (9158, 'survival', 'səˈvaɪvl', 'n.', '生存;存活;幸存;', '...companies which have been struggling for survival in the advancing recession...', '在日益萧条的经济环境中挣扎求生的各个公司');
INSERT INTO `words` VALUES (9159, 'survive', 'səˈvaɪv', 'v.', '生存;存活;继续存在;', '...the sequence of events that left the eight pupils battling to survive in icy seas for over four hours...', '致使这 8 个小学生在冰冷的海水里挣扎求生四个多小时的一连串事件');
INSERT INTO `words` VALUES (9160, 'survivor', 'səˈvaɪvə(r)', 'n.', '幸存者;生还者;挺过困难者;', 'Officials said there were no survivors of the plane crash.', '官方说这次飞机失事无一人生还。');
INSERT INTO `words` VALUES (9161, 'susceptibility', 'səˌseptəˈbɪləti', 'n.', '易受影响(或伤害等)的特性;敏感性;过敏性;', '...his increased susceptibility to infections.', '他越来越容易受感染的体质');
INSERT INTO `words` VALUES (9162, 'susceptible', 'səˈseptəbl', 'adj.', '易受影响(或伤害等);敏感;过敏;', 'Young people are the most susceptible to advertisements...', '年轻人最容易受广告的影响。');
INSERT INTO `words` VALUES (9163, 'suspend', 'səˈspend', 'v.', '悬;挂;吊;', 'The union suspended strike action this week...', '这周工会暂停了罢工。');
INSERT INTO `words` VALUES (9164, 'suspended', 'səˈspendɪd', 'v.', '悬;挂;吊;', 'The union suspended strike action this week...', '这周工会暂停了罢工。');
INSERT INTO `words` VALUES (9165, 'suspense', 'səˈspens', 'n.', '(对即将发生的事等的)担心;焦虑;兴奋;', 'The suspense over the two remaining hostages ended last night when the police discovered the bullet ridden bodies.', '随着昨晚警方发现了弹痕累累的尸体，人们对剩余两名人质的担心也终告结束。');
INSERT INTO `words` VALUES (9166, 'suspension', 'səˈspenʃn', 'n.', '暂令停职(或停学、停赛等);暂缓;推迟;', 'A strike by British Airways ground staff has led to the suspension of flights between London and Manchester...', '英国航空公司地勤人员的罢工已经造成伦敦至曼彻斯特的航线停运。');
INSERT INTO `words` VALUES (9167, 'suspicion', 'səˈspɪʃn', 'n.', '怀疑;嫌疑;感觉;', 'There was a suspicion that this runner attempted to avoid the procedures for dope testing...', '这名赛跑选手涉嫌有意逃避兴奋剂检验。');
INSERT INTO `words` VALUES (9168, 'suspicious', 'səˈspɪʃəs', 'adj.', '感觉可疑的;怀疑的;令人怀疑的;', 'He was rightly suspicious of meeting me until I reassured him I was not writing about him...', '当然他很不放心见我，直到我再一次向他保证我不是在写他。');
INSERT INTO `words` VALUES (9169, 'suspiciously', 'səˈspɪʃəsli', 'adv.', '怀疑地;有疑心地;令人怀疑地;', 'The tan-coloured dog looks suspiciously like an American pit bull terrier...', '这只棕黄色的狗看上去非常像美国比特犬。');
INSERT INTO `words` VALUES (9170, 'sustain', 'səˈsteɪn', 'v.', '维持(生命、生存);使保持;使稳定持续;', 'But he has sustained his fierce social conscience from young adulthood through old age...', '而他从青年时期一直到老年都始终保持着强烈的社会良知。');
INSERT INTO `words` VALUES (9171, 'sustained', 'səˈsteɪnd', 'v.', '维持(生命、生存);使保持;使稳定持续;', 'But he has sustained his fierce social conscience from young adulthood through old age...', '而他从青年时期一直到老年都始终保持着强烈的社会良知。');
INSERT INTO `words` VALUES (9172, 'sustenance', 'ˈsʌstənəns', 'n.', '食物;营养;养料;', 'The state provided a basic quantity of food for daily sustenance, but little else.', '国家提供基本的日常食物供应，但除此之外就没什么了。');
INSERT INTO `words` VALUES (9173, 'swagger', 'ˈswæɡə(r)', 'v.', '神气十足地走;大摇大摆地走;', 'A broad shouldered man wearing a dinner jacket swaggered confidently up to the bar...', '穿晚礼服的宽肩膀男子神气十足地走向吧台。');
INSERT INTO `words` VALUES (9174, 'swallow', 'ˈswɒləʊ', 'v.', '吞下;咽下;(由于紧张等)做吞咽动作;', 'You are asked to swallow a capsule containing vitamin B...', '你要服一粒维生素 B 胶囊。');
INSERT INTO `words` VALUES (9175, 'swank', 'swæŋk', 'v.', '炫耀;卖弄;', 'I have always been against swanking about all the things I have been lucky enough to win.', '我一向不喜欢和别人炫耀自己因为幸运而取得的那些成就。');
INSERT INTO `words` VALUES (9176, 'swap', 'swɒp', 'v.', '交换(东西);交换(工作);用…替换;', 'Next week they will swap places and will repeat the switch weekly...', '下周他们将交换座位，并且以后每周换一次。');
INSERT INTO `words` VALUES (9177, 'swarthy', 'ˈswɔːði', 'adj.', '深肤色的;皮肤黝黑的;', 'He had a broad swarthy face.', '他有一张黝黑的大脸。');
INSERT INTO `words` VALUES (9178, 'sway', 'sweɪ', 'v.', '(使)摇摆，摇动;说服;使相信;', 'The people swayed back and forth with arms linked...', '人们手挽手，前后摇晃着。');
INSERT INTO `words` VALUES (9179, 'swear', 'sweə(r)', 'v.', '咒骂;诅咒;说脏话;', 'It\'s wrong to swear and shout...', '破口大骂是不对的。');
INSERT INTO `words` VALUES (9180, 'sweat', 'swet', 'n.', '汗;出汗;流汗;', 'Both horse and rider were dripping with sweat within five minutes...', '没过五分钟，马和骑手都大汗淋漓。');
INSERT INTO `words` VALUES (9181, 'sweatshop', 'ˈswetʃɒp', 'n.', '血汗工厂，血汗工场(工资低廉、劳动条件恶劣);', '...the dingy, hidden world of garment sweatshops.', '肮脏隐蔽的血汗服装工厂');
INSERT INTO `words` VALUES (9182, 'sweaty', 'ˈsweti', 'adj.', '满是汗的;汗津津的;汗水湿透的;', '...sweaty hands...', '汗津津的手');
INSERT INTO `words` VALUES (9183, 'swedish', 'ˈswiːdɪʃ', 'n.', '瑞典语;', 'Half of Swedish marriages end in divorce.', '瑞典人的婚姻有一半以离婚告终。');
INSERT INTO `words` VALUES (9184, 'sweep', 'swiːp', 'v.', '扫;打扫;清扫;', 'The owner of the store was sweeping his floor when I walked in...', '当我走进商店的时候，店主正在扫地。');
INSERT INTO `words` VALUES (9185, 'sweeping', 'ˈswiːpɪŋ', 'adj.', '影响广泛的;大范围的;根本性的;', '...the long sweeping curve of Rio\'s Guanabara Bay.', '里约热内卢瓜纳巴拉湾蜿蜒绵长的海岸线');
INSERT INTO `words` VALUES (9186, 'sweet', 'swiːt', 'adj.', '含糖的;甜的;香的;', '...a mug of sweet tea...', '一大杯甜茶');
INSERT INTO `words` VALUES (9187, 'sweeten', 'ˈswiːtn', 'v.', '使变甜;加糖于;讨好;', 'He liberally sweetened his coffee...', '他往咖啡里加了很多糖。');
INSERT INTO `words` VALUES (9188, 'sweetening', 'ˈswiːtnɪŋ', 'v.', '使变甜;加糖于;讨好;', 'He liberally sweetened his coffee...', '他往咖啡里加了很多糖。');
INSERT INTO `words` VALUES (9189, 'sweetheart', 'ˈswiːthɑːt', 'n.', '(用作称呼语)亲爱的，宝贝儿;恋人;爱人;', 'Happy birthday, sweetheart.', '生日快乐，亲爱的。');
INSERT INTO `words` VALUES (9190, 'sweetly', 'ˈswiːtli', 'adv.', '令人愉快地;可爱地;气味芬芳地;', 'He heard the car engine running sweetly beyond the open door.', '他听到敞开的门口外的汽车引擎运转正常。');
INSERT INTO `words` VALUES (9191, 'sweetness', 'ˈswiːtnəs', 'n.', '令人愉快;讨人喜欢;甜;', 'It has not all been sweetness and light between him and the Prime Minister.', '他和首相的关系并不总是那么愉快融洽。');
INSERT INTO `words` VALUES (9192, 'sweets', 'swiːts', 'n.', '糖果;(餐后的)甜食，甜点;(称呼亲爱的人)亲爱的，宝贝儿;', '...a mug of sweet tea...', '一大杯甜茶');
INSERT INTO `words` VALUES (9193, 'swelling', 'ˈswelɪŋ', 'n.', '膨胀;肿胀;肿胀处;', 'His eye was partly closed, and there was a swelling over his lid...', '他的眼睛半闭着，眼睑上有一个肿块。');
INSERT INTO `words` VALUES (9194, 'swelter', 'ˈsweltə(r)', 'v.', '热得难受;', 'They sweltered in temperatures rising to a hundred degrees...', '接近华氏 100 度的高温让他们感觉酷热难耐。');
INSERT INTO `words` VALUES (9195, 'swerve', 'swɜːv', 'v.', '突然转向;急转弯;', 'Drivers coming in the opposite direction swerved to avoid the bodies...', '从对面来的汽车司机急忙转向以免撞到那些尸体。');
INSERT INTO `words` VALUES (9196, 'swift', 'swɪft', 'adj.', '迅即发生的;马上做出的;迅速的;', 'Our task is to challenge the UN to make a swift decision...', '我们的任务是敦促联合国快速做出决策。');
INSERT INTO `words` VALUES (9197, 'swiftly', 'ˈswɪftli', 'adv.', '迅速地;', 'Our task is to challenge the UN to make a swift decision...', '我们的任务是敦促联合国快速做出决策。');
INSERT INTO `words` VALUES (9198, 'swig', 'swɪɡ', 'v.', '大口喝(酒等);', 'I swigged down two white wines...', '我把两杯白葡萄酒一饮而尽。');
INSERT INTO `words` VALUES (9199, 'swill', 'swɪl', 'v.', '冲洗;灌洗;涮;', 'A crowd of men were standing around swilling beer.', '一群人正站在一起痛饮啤酒。');
INSERT INTO `words` VALUES (9200, 'swim', 'swɪm', 'v.', '游水;游泳;游泳(作为娱乐);', 'She learned to swim when she was really tiny...', '她很小的时候就学会了游泳。');
INSERT INTO `words` VALUES (9201, 'swimmer', 'ˈswɪmə(r)', 'n.', '会游泳者;游泳者;', 'You don\'t have to worry about me. I\'m a good swimmer...', '你不用担心我。我水性很好。');
INSERT INTO `words` VALUES (9202, 'swimming', 'ˈswɪmɪŋ', 'n.', '游泳;游泳运动;', 'Swimming is probably the best form of exercise you can get.', '游泳或许是现有的最佳锻炼方式。');
INSERT INTO `words` VALUES (9203, 'swindle', 'ˈswɪndl', 'v.', '诈骗;骗取;', 'A City businessman swindled investors out of millions of pounds...', '一名来自伦敦商业区的商人骗取了投资者们数百万英镑。');
INSERT INTO `words` VALUES (9204, 'swing', 'swɪŋ', 'v.', '(使)摆动，摇摆，摇荡;纵身跃向;荡向;', 'The sail of the little boat swung crazily from one side to the other...', '小船的帆猛烈地左右摇摆着。');
INSERT INTO `words` VALUES (9205, 'swipe', 'swaɪp', 'v.', '挥拳打;扬起巴掌打;挥起(物体)击打;', 'She swiped at Rusty as though he was a fly...', '她打拉斯蒂就像在打一只苍蝇。');
INSERT INTO `words` VALUES (9206, 'swirl', 'swɜːl', 'v.', '(使)打旋，旋动，起旋涡;', 'She smiled, swirling the wine in her glass...', '她微笑着，旋动着杯子里的葡萄酒。');
INSERT INTO `words` VALUES (9207, 'swirling', 'ˈswɜːlɪŋ', 'v.', '(使)打旋，旋动，起旋涡;', 'She smiled, swirling the wine in her glass...', '她微笑着，旋动着杯子里的葡萄酒。');
INSERT INTO `words` VALUES (9208, 'swish', 'swɪʃ', 'v.', '刷地(或嗖地、呼地等)挥动;(使)快速空中移动;', 'A car swished by heading for the coast...', '一辆车嗖地驶过朝海岸开去。');
INSERT INTO `words` VALUES (9209, 'swiss', 'swɪs', 'adj.', '瑞士的;', '...an avalanche in the Swiss alps.', '瑞士阿尔卑斯山脉的一次雪崩');
INSERT INTO `words` VALUES (9210, 'switch', 'swɪtʃ', 'n.', '(电路的)开关，闸，转换器;(尤指突然彻底的)改变，转变;(铁路的)转辙器，道岔;', 'Leona put some detergent into the dishwasher, shut the door and pressed the switch.', '利昂娜将一些清洁剂放入洗碗机，关上门后按了开始键。');
INSERT INTO `words` VALUES (9211, 'switchboard', 'ˈswɪtʃbɔːd', 'n.', '(电话的)交换机，交换台， 总机;', 'He asked to be connected to the central switchboard at London University...', '他要求转接到伦敦大学的电话总机。');
INSERT INTO `words` VALUES (9212, 'swollen', 'ˈswəʊlən', 'adj.', '肿胀的;肿起来的;涨水的;', 'My eyes were so swollen I could hardly see.', '我的眼睛肿得很厉害，几乎看不到东西。');
INSERT INTO `words` VALUES (9213, 'swoon', 'swuːn', 'v.', '痴迷;对(某人)神魂颠倒;昏厥;', 'Virtually every woman in the \'20s swooned over Valentino...', '在 20 年代，几乎每位女性都痴迷于华伦天奴这个品牌。');
INSERT INTO `words` VALUES (9214, 'swoop', 'swuːp', 'v.', '(尤指为了袭击)向下猛冲，俯冲;突然袭击;突击搜查;', 'The terror ended when armed police swooped on the car...', '武警突袭了这辆车从而制止了这起恐怖活动。');
INSERT INTO `words` VALUES (9215, 'swot', 'swɒt', 'n.', '只知一味用功学习的人;书呆子;', 'They swotted for their A levels.', '他们为准备高级程度考试而刻苦学习。');
INSERT INTO `words` VALUES (9216, 'sycophant', 'ˈsɪkəfænt', 'n.', '阿谀奉承的人;谄媚者;拍马者;', '...a dictator surrounded by sycophants, frightened to tell him what he may not like.', '被一群不敢谏言的佞臣簇拥的独裁者');
INSERT INTO `words` VALUES (9217, 'sycophantic', 'ˌsɪkəˈfæntɪk', 'adj.', '谄媚的;拍马屁的;阿谀奉承的;', '...his clique of sycophantic friends...', '他那些互相吹捧的朋党');
INSERT INTO `words` VALUES (9218, 'syllable', 'ˈsɪləbl', 'n.', '音节;', 'We children called her Oma, accenting both syllables.', '我们这群小孩叫她Oma，两个音节都重读。');
INSERT INTO `words` VALUES (9219, 'syllabus', 'ˈsɪləbəs', 'n.', '教学大纲;', '...the GCSE history syllabus.', '英国中等教育证书考试的历史大纲');
INSERT INTO `words` VALUES (9220, 'symbiosis', 'ˌsɪmbaɪˈəʊsɪs', 'n.', '共生(关系);合作关系;互惠互利的关系;', '...the link between bacteria, symbiosis, and the evolution of plants and animals.', '细菌、共生和动植物进化之间的联系');
INSERT INTO `words` VALUES (9221, 'symbiotic', 'ˌsɪmbaɪˈɒtɪk', 'adj.', '共生的;互惠互利的;', '...fungi that have a symbiotic relationship with the trees of these northwestern forests...', '与西北部这些森林里的树木存在共生关系的真菌');
INSERT INTO `words` VALUES (9222, 'symbol', 'ˈsɪmbl', 'n.', '象征;符号;代号;', 'To them, the monarchy is the special symbol of nationhood...', '对他们来说，君主政体是国家地位的特殊象征。');
INSERT INTO `words` VALUES (9223, 'symbolic', 'sɪmˈbɒlɪk', 'adj.', '使用象征的;作为象征的;象征性的;', 'A lot of Latin-American officials are stressing the symbolic importance of the trip...', '许多拉丁美洲的官员都在强调此行的重要象征意义。');
INSERT INTO `words` VALUES (9224, 'symbolically', 'sɪmˈbɒlɪkəli', 'adv.', '象征性地;', 'A lot of Latin-American officials are stressing the symbolic importance of the trip...', '许多拉丁美洲的官员都在强调此行的重要象征意义。');
INSERT INTO `words` VALUES (9225, 'symbolism', 'ˈsɪmbəlɪzəm', 'n.', '(尤指文艺中的)象征主义，象征手法;', 'The scene is so rich in symbolism that any explanation risks spoiling the effect.', '该场景具有丰富的象征意义，任何解释都可能破坏其效果。');
INSERT INTO `words` VALUES (9226, 'symmetrical', 'sɪˈmetrɪkl', 'adj.', '对称的;', '...the neat rows of perfectly symmetrical windows.', '一排排整齐而且完全对称的窗子');
INSERT INTO `words` VALUES (9227, 'symmetry', 'ˈsɪmətri', 'n.', '对称;相似;相仿;', '...the incredible beauty and symmetry of a snowflake...', '雪花难以置信的美丽和对称');
INSERT INTO `words` VALUES (9228, 'sympathetic', 'ˌsɪmpəˈθetɪk', 'adj.', '同情的;有同情心的;表示同情的;', 'She was very sympathetic to the problems of adult students...', '她对成年学生的问题深表同情。');
INSERT INTO `words` VALUES (9229, 'sympathetically', 'ˌsɪmpə\'θetɪklɪ', 'adv.', '怜悯地;', 'She was very sympathetic to the problems of adult students...', '她对成年学生的问题深表同情。');
INSERT INTO `words` VALUES (9230, 'sympathy', 'ˈsɪmpəθi', 'n.', '同情;赞同;支持;', 'We expressed our sympathy for her loss...', '我们对她的损失表示同情。');
INSERT INTO `words` VALUES (9231, 'symposium', 'sɪmˈpəʊziəm', 'n.', '专题讨论会;研讨会;小型讨论会;', 'He had been taking part in an international symposium on population.', '他那时正参加一个有关人口问题的国际研讨会。');
INSERT INTO `words` VALUES (9232, 'symptom', 'ˈsɪmptəm', 'n.', '症状;征候;征兆;', 'One of the most common symptoms of schizophrenia is hearing imaginary voices.', '精神分裂症的最常见的一个症状就是幻听。');
INSERT INTO `words` VALUES (9233, 'symptomatic', 'ˌsɪmptəˈmætɪk', 'adj.', '作为症状的;(有)症状的;作为征候的;', 'The city\'s problems are symptomatic of the crisis that is spreading throughout the country.', '该市的问题是正在全国蔓延的危机的征兆显现。');
INSERT INTO `words` VALUES (9234, 'sync', 'sɪŋk', 'n.', 'synchronization 的缩略词;', 'Normally, when demand and supply are out of sync, you either increase the supply, or you adjust the price mechanism...', '通常，当供需失衡时，要么加大供给，要么调整价格机制。');
INSERT INTO `words` VALUES (9235, 'syndrome', 'ˈsɪndrəʊm', 'n.', '综合征;综合症状;典型意见;', 'Irritable bowel syndrome seems to affect more women than men...', '女性比男性更易患肠易激综合征。');
INSERT INTO `words` VALUES (9236, 'synergy', 'ˈsɪnədʒi', 'n.', '协同作用，协同增效作用(人或公司共同协作所产生的效果优于各自单独行动的效果);', 'Of course, there\'s quite obviously a lot of synergy between the two companies...', '当然，这两家公司间非常明显地产生了巨大的协同效应。');
INSERT INTO `words` VALUES (9237, 'synonym', 'ˈsɪnənɪm', 'n.', '同义词;', 'The term \'industrial democracy\' is often used as a synonym for worker participation.', '“工业民主”这个词常被用作“工人参与”的同义词。');
INSERT INTO `words` VALUES (9238, 'synonymous', 'sɪˈnɒnɪməs', 'adj.', '同义的;等同于…的;', 'Paris has always been synonymous with elegance, luxury and style...', '巴黎一直是优雅、奢华和时尚的代名词。');
INSERT INTO `words` VALUES (9239, 'synopsis', 'sɪˈnɒpsɪs', 'n.', '(著作、剧本等的)大纲，提要，概要，梗概;', 'For each title there is a brief synopsis of the book.', '每本书都对应有一个简短的介绍。');
INSERT INTO `words` VALUES (9240, 'syntactic', 'sɪnˈtæktɪk', 'adj.', '句法的;', '...three common syntactic devices in English.', '英语中 3 个常见的构句方法');
INSERT INTO `words` VALUES (9241, 'syntax', 'ˈsɪntæks', 'n.', '句法;句法规则;语构;', 'His grammar and syntax, both in oral and written expression, were much better than the average.', '无论口头还是笔头，他的文法和句法都非常出众。');
INSERT INTO `words` VALUES (9242, 'synthesis', 'ˈsɪnθəsɪs', 'n.', '综合;结合;综合体;', 'His novels are a rich synthesis of Balkan history and mythology...', '他的小说融合了大量巴尔干半岛的历史和神话故事。');
INSERT INTO `words` VALUES (9243, 'synthetic', 'sɪnˈθetɪk', 'adj.', '人造的;(人工)合成的;综合(型)的;', 'Boots made from synthetic materials can usually be washed in a machine.', '合成材料制成的靴子通常可以机洗。');
INSERT INTO `words` VALUES (9244, 'synthetics', 'sɪnˈθɛtɪks', 'n.', '合成物;合成材料;', 'Natural fabrics like silk and wool are better insulators than synthetics.', '丝绸和羊毛之类的纯天然织物比合成纤维的保暖性更好。');
INSERT INTO `words` VALUES (9245, 'syrian', 'ˈsɪriən', 'n.', '叙利亚人;叙利亚裔人;', '...the Syrian capital of Damascus.', '叙利亚首都大马士革');
INSERT INTO `words` VALUES (9246, 'syrup', 'ˈsɪrəp', 'n.', '糖水(罐头水果常用);糖浆;', '...canned fruit with sugary syrup.', '糖水水果罐头');
INSERT INTO `words` VALUES (9247, 'system', 'ˈsɪstəm', 'n.', '(思想或理论)体系;方法;制度;', 'The present system of funding for higher education is unsatisfactory.', '目前高等教育的经费体制并不令人满意。');
INSERT INTO `words` VALUES (9248, 'systematic', 'ˌsɪstəˈmætɪk', 'adj.', '成体系的;系统的;有条理的;', 'They went about their business in a systematic way...', '他们按部就班地做生意。');
INSERT INTO `words` VALUES (9249, 'systematically', 'ˌsɪstə\'mætɪklɪ', 'adv.', '有系统地;有组织地;', 'They went about their business in a systematic way...', '他们按部就班地做生意。');
INSERT INTO `words` VALUES (9250, 'systemic', 'sɪˈstiːmɪk', 'adj.', '涉及全系统的;系统的;影响全身的;', 'The economy is locked in a systemic crisis.', '经济陷入全面危机。');
INSERT INTO `words` VALUES (9251, 'tab', 'tæb', 'n.', '标签;签条;突耳;', 'A stupid medical clerk had slipped the wrong tab on his X-ray.', '一个蠢笨的医护人员一不留神在他的X光片上贴错了标签。');
INSERT INTO `words` VALUES (9252, 'tabernacle', 'ˈtæbənækl', 'n.', '(某些基督教派的)礼拜堂，会堂;圣所，会幕(古代犹太人在沙漠旅途中拜神用);', '...the vision which led Earl Paulk to create his first tabernacle in Atlanta.', '促使保尔克伯爵在亚特兰大建第一座教堂的远见卓识');
INSERT INTO `words` VALUES (9253, 'table', 'ˈteɪbl', 'n.', '桌子;台子;几;', 'She was sitting at the kitchen table eating a currant bun...', '她正坐在餐桌旁，吃着葡萄干圆面包。');
INSERT INTO `words` VALUES (9254, 'tableau', 'ˈtæbləʊ', 'n.', '舞台造型;静态画面;群像(尤指雕塑);', '...a nativity tableau.', '基督降生的舞台场景');
INSERT INTO `words` VALUES (9255, 'tablespoonful', 'ˈteɪbəlspʊnˌfʊl', 'n.', '一大汤匙(或大调羹)的量;', 'Grate a tablespoonful of fresh ginger into a pan.', '磨碎一汤匙鲜姜，放入平底锅。');
INSERT INTO `words` VALUES (9256, 'tablet', 'ˈtæblət', 'n.', '药片;片剂;丸;', 'It\'s time for your tablets, dear...', '该吃药了，亲爱的。');
INSERT INTO `words` VALUES (9257, 'tabloid', 'ˈtæblɔɪd', 'n.', '小报(版面通常比大报小一半);通俗小报(文短图多，内容多为名人逸事，常被视为不太严肃);', '\'The British tabloids called me leggy and stunning,\' she recalls.', '“当时的英国小报都说我‘双腿修长’、‘艳惊四座’，”她回忆道。');
INSERT INTO `words` VALUES (9258, 'taboo', 'təˈbuː', 'n.', '(文化或宗教习俗方面的)禁忌，忌讳，戒律;禁止;避讳;', 'The topic of addiction remains something of a taboo...', '毒瘾仍然是个有些忌讳的话题。');
INSERT INTO `words` VALUES (9259, 'tabs', 'tæbz', 'n.', '标签;签条;突耳;', 'A stupid medical clerk had slipped the wrong tab on his X-ray.', '一个蠢笨的医护人员一不留神在他的X光片上贴错了标签。');
INSERT INTO `words` VALUES (9260, 'tabulate', 'ˈtæbjuleɪt', 'v.', '列成表格;列表显示;', '...methods of collecting, tabulating and analysing numerical data...', '搜集信息、列表和分析数据的各种方法');
INSERT INTO `words` VALUES (9261, 'tacit', 'ˈtæsɪt', 'adj.', '心照不宣的;不言而喻的;默示的;', 'The question was a tacit admission that a mistake had indeed been made...', '问这个问题实际上等于默认确实出了差错。');
INSERT INTO `words` VALUES (9262, 'taciturn', 'ˈtæsɪtɜːn', 'adj.', '不苟言笑的;沉默寡言的;缄默不语的;', 'A taciturn man, he replied to my questions in monosyllables.', '他寡言少语，回答我的问题时只是往外蹦单字。');
INSERT INTO `words` VALUES (9263, 'tackle', 'ˈtækl', 'v.', '应付，处理，解决(难题或局面);与某人交涉;向某人提起(问题或困难情况);', 'The first reason to tackle these problems is to save children\'s lives...', '果断处理这些问题的首要原因是为了挽救孩子们的生命。');
INSERT INTO `words` VALUES (9264, 'tacky', 'ˈtæki', 'adj.', '低劣的;蹩脚的;俗气的;', '...a woman in a fake leopard-skin coat and tacky red sunglasses...', '身穿假豹皮大衣、戴着俗气的大红色太阳镜的女人');
INSERT INTO `words` VALUES (9265, 'tact', 'tækt', 'n.', '(处事、言谈等的)老练，圆通，得体，乖巧;', 'On this occasion the press have not been intrusive and they have shown great tact.', '媒体此次并没有过多纠缠，表现十分得体。');
INSERT INTO `words` VALUES (9266, 'tactful', 'ˈtæktfl', 'adj.', '圆通的;得体的;不得罪人的;', 'He had been extremely tactful in dealing with the financial question...', '他在处理这个财务问题时很有分寸。');
INSERT INTO `words` VALUES (9267, 'tactfully', '', 'adv.', '言行得体地，不得罪人地，能赢得好感地;', 'He had been extremely tactful in dealing with the financial question...', '他在处理这个财务问题时很有分寸。');
INSERT INTO `words` VALUES (9268, 'tactic', 'ˈtæktɪk', 'n.', '策略;手段;招数;', 'What sort of tactics will the President use to rally the people behind him?', '总统会用什么样的策略把人们号召起来支持他呢？');
INSERT INTO `words` VALUES (9269, 'tactical', 'ˈtæktɪkl', 'adj.', '战术上的;策略上的;有谋略的;', 'It\'s not yet clear whether the Prime Minister\'s resignation offer is a serious one, or whether it\'s simply a tactical move...', '现在还不清楚首相提出辞职是认真的，还是仅仅作为一种策略。');
INSERT INTO `words` VALUES (9270, 'tactics', 'ˈtæktɪks', 'n.', '策略;手段;招数;', 'What sort of tactics will the President use to rally the people behind him?', '总统会用什么样的策略把人们号召起来支持他呢？');
INSERT INTO `words` VALUES (9271, 'tactile', 'ˈtæktaɪl', 'adj.', '触觉的;有触觉的;能触知的;', 'The children are very tactile with warm, loving natures.', '小孩子说话时喜欢用上热烈、表达喜爱的肢体触碰。');
INSERT INTO `words` VALUES (9272, 'tactless', 'ˈtæktləs', 'adj.', '言行不得体的;得罪人的;不圆通的;', 'Throughout his school life Darius was tactless and egocentric...', '上学期间，达赖厄斯粗鲁冒失，而且总以自我为中心。');
INSERT INTO `words` VALUES (9273, 'tag', 'tæɡ', 'n.', '标签;标牌;称呼;', 'Staff wore name tags.', '工作人员都佩戴姓名牌。');
INSERT INTO `words` VALUES (9274, 'tail', 'teɪl', 'n.', '尾;尾巴;有…尾巴的;', 'The cattle were swinging their tails to disperse the flies.', '那些牛甩动着尾巴驱赶苍蝇。');
INSERT INTO `words` VALUES (9275, 'tailed', 'teɪld', 'v.', '跟踪;尾随;盯梢;', 'The cattle were swinging their tails to disperse the flies.', '那些牛甩动着尾巴驱赶苍蝇。');
INSERT INTO `words` VALUES (9276, 'tailgate', 'ˈteɪlɡeɪt', 'n.', '(卡车的)后拦板，后挡板;(三门或五门轿车的)尾门，舱盖式后背门;', 'He let down the tailgate so the dog could jump out.', '他放下后背门，好让狗跳出来。');
INSERT INTO `words` VALUES (9277, 'tailor-made', 'ˌteɪlə ˈmeɪd', 'adj.', '特制的;专门设置的;非常合适的;', '...tailor-made itineraries for tourists seeking something more individual than packaged holidays...', '为那些不喜欢包办旅行、寻求个性的游客们专门安排的游览路线');
INSERT INTO `words` VALUES (9278, 'tails', 'teɪlz', 'n.', '尾;尾巴;有…尾巴的;', 'The cattle were swinging their tails to disperse the flies.', '那些牛甩动着尾巴驱赶苍蝇。');
INSERT INTO `words` VALUES (9279, 'tailwind', 'ˈteɪlwɪnd', 'n.', '顺风;', 'A tailwind had cut the flying time by half an hour.', '由于顺风，飞行时间缩短了半小时。');
INSERT INTO `words` VALUES (9280, 'taint', 'teɪnt', 'v.', '使腐坏;污染;玷污，败坏(名声);', 'Opposition leaders said that the elections had been tainted by corruption.', '反对派领导人说，选举中存在舞弊现象。');
INSERT INTO `words` VALUES (9281, 'tainted', 'ˈteɪntɪd', 'v.', '使腐坏;污染;玷污，败坏(名声);', 'Opposition leaders said that the elections had been tainted by corruption.', '反对派领导人说，选举中存在舞弊现象。');
INSERT INTO `words` VALUES (9282, 'takeover', 'ˈteɪkəʊvə(r)', 'n.', '收购;接收;接管;', '...the government\'s takeover of the Bank of New England Corporation.', '政府对新英格兰银行的接管');
INSERT INTO `words` VALUES (9283, 'taker', 'ˈteɪkə(r)', 'n.', '接受者;收受人;', 'Over 100 buyers or investors were approached, but there were no takers...', '已接触过 100 多个买主或投资人，不过没人愿意接受。');
INSERT INTO `words` VALUES (9284, 'takings', 'ˈteɪkɪŋz', 'n.', '(某一时期的)进账，票房收入;', 'The pub said that their takings were fifteen to twenty thousand pounds a week.', '这家酒馆说，它一周的营业额在 1.5 万到 2 万英镑之间。');
INSERT INTO `words` VALUES (9285, 'tale', 'teɪl', 'n.', '(尤指充满惊险的)故事;历险记;(精彩但不一定完全真实的)讲述，叙述;', '...a collection of stories, poems and folk tales...', '由短篇故事、诗歌和传说汇成的作品集');
INSERT INTO `words` VALUES (9286, 'talent', 'ˈtælənt', 'n.', '天才;天资;天赋;', 'She is proud that both her children have a talent for music...', '她为自己的两个孩子都有音乐天赋而自豪。');
INSERT INTO `words` VALUES (9287, 'talented', 'ˈtæləntɪd', 'adj.', '有才能的;天才的;有才干的;', 'Howard is a talented pianist...', '霍华德是个很有天分的钢琴家。');
INSERT INTO `words` VALUES (9288, 'talk', 'tɔːk', 'v.', '说话;讲话;谈话;', 'He was too distressed to talk...', '他伤心得讲不出话来。');
INSERT INTO `words` VALUES (9289, 'talkative', 'ˈtɔːkətɪv', 'adj.', '爱多说话的;多嘴的;饶舌的;', 'He suddenly became very talkative, his face slightly flushed, his eyes much brighter.', '他的话突然多了起来，脸颊微微泛红，双眼明亮许多。');
INSERT INTO `words` VALUES (9290, 'talker', 'ˈtɔːkə(r)', 'n.', '说话…的人;爱说话的人;', '...a fluent talker.', '口若悬河的人');
INSERT INTO `words` VALUES (9291, 'tall', 'tɔːl', 'adj.', '高的;高大的;(用以表示或询问高度)有…高，身高;', 'Being tall can make you feel incredibly self-confident...', '个子高会使你感觉极其自信。');
INSERT INTO `words` VALUES (9292, 'tally', 'ˈtæli', 'n.', '记录;积分表;账;', 'They do not keep a tally of visitors to the palace, but it is very popular...', '他们没有对参观宫殿的人数进行统计，不过这地方很受欢迎。');
INSERT INTO `words` VALUES (9293, 'tame', 'teɪm', 'adj.', '养驯的;驯服的;平淡无奇的;', 'The deer never became tame; they would run away if you approached them.', '那些鹿一直没有被驯服：你一靠近，它们就跑开。');
INSERT INTO `words` VALUES (9294, 'tamer', 'ˈteɪmə(r)', 'n.', '驯兽师;驯养者;', 'The deer never became tame; they would run away if you approached them.', '那些鹿一直没有被驯服：你一靠近，它们就跑开。');
INSERT INTO `words` VALUES (9295, 'tamp', 'tæmp', 'v.', '捣实;压实;塞紧;', 'Then I tamp down the soil with the back of a rake...', '然后我用耙子的背将土壤拍实。');
INSERT INTO `words` VALUES (9296, 'tamper', 'ˈtæmpə(r)', 'v.', '干预;篡改;', 'I don\'t want to be accused of tampering with the evidence...', '我不希望被人控告篡改证据。');
INSERT INTO `words` VALUES (9297, 'tampering', 'ˈtæmpərɪŋ', 'v.', '干预;篡改;', 'I don\'t want to be accused of tampering with the evidence...', '我不希望被人控告篡改证据。');
INSERT INTO `words` VALUES (9298, 'tan', 'tæn', 'v.', '(使)晒成褐色，晒黑;鞣(革);硝(皮);', 'She is tall and blonde, with a permanent tan.', '她高个儿，一头金发，皮肤黝黑。');
INSERT INTO `words` VALUES (9299, 'tangential', 'tænˈdʒenʃl', 'adj.', '稍微沾边的;离题的;不相干的;', 'Too much time was spent discussing tangential issues...', '太多的时间花在了讨论那些无关紧要的问题上。');
INSERT INTO `words` VALUES (9300, 'tangible', 'ˈtændʒəbl', 'adj.', '有形的;实际的;真实的;', 'There should be some tangible evidence that the economy is starting to recover...', '应该有明显迹象表明经济开始复苏了。');
INSERT INTO `words` VALUES (9301, 'tangle', 'ˈtæŋɡl', 'n.', '(线、毛发等的)缠结的一团，乱团，乱糟糟的一堆;混乱;纷乱;', 'A tangle of wires is all that remains of the computer and phone systems...', '原来的计算机和电话系统如今只剩下乱糟糟的一团电线。');
INSERT INTO `words` VALUES (9302, 'tank', 'tæŋk', 'n.', '(贮放液体或气体的)箱，槽，罐;箱(或桶等)所装之物;一箱(或一桶等)的量;', '...an empty fuel tank...', '空空的油箱');
INSERT INTO `words` VALUES (9303, 'tanker', 'ˈtæŋkə(r)', 'n.', '运送大量液体或气体的轮船(或卡车);油轮;罐车;', 'A Greek oil tanker has run aground.', '一艘希腊油轮搁浅了。');
INSERT INTO `words` VALUES (9304, 'tantamount', 'ˈtæntəmaʊnt', 'adj.', '无异于;等于;效果与…一样坏;', 'What Bracey is saying is tantamount to heresy...', '布雷西的言论简直就是异端邪说。');
INSERT INTO `words` VALUES (9305, 'tantrum', 'ˈtæntrəm', 'n.', '(尤指儿童)耍脾气，使性子;', 'My son had a tantrum and banged his fist on the ground...', '我儿子大发脾气，一拳捶在了地上。');
INSERT INTO `words` VALUES (9306, 'tap', 'tæp', 'v.', '轻敲;轻拍;轻叩;', 'She turned on the taps.', '她打开了那些龙头。');
INSERT INTO `words` VALUES (9307, 'tape', 'teɪp', 'n.', '磁带;录音带;录像带;', 'Tape is expensive and loses sound quality every time it is copied...', '磁带成本高，而且每录制一次音质就会变差一些。');
INSERT INTO `words` VALUES (9308, 'taper', 'ˈteɪpə(r)', 'v.', '(使)逐渐变窄;', 'Unlike other trees, it doesn\'t taper very much. It stays fat all the way up...', '和别的树不同，它不是越往上越细，而是上下一样粗。');
INSERT INTO `words` VALUES (9309, 'tapestry', 'ˈtæpəstri', 'n.', '壁毯;挂毯;织锦;', 'The seats of the chairs had been recovered in tapestry...', '椅座上又铺回了花毯。');
INSERT INTO `words` VALUES (9310, 'tar', 'tɑː(r)', 'n.', '焦油;焦油沥青;柏油;', 'The oil has hardened to tar...', '油已经硬化成柏油了。');
INSERT INTO `words` VALUES (9311, 'tardiness', '\'tɑ:dɪnəs', 'n.', '拖拉;缓慢;迟缓;', 'He wept for the loss of his mother and his tardy recognition of her affection...', '他痛哭流涕，不仅因为失去了母亲，还因为这么晚才认识到了妈妈对他的爱。');
INSERT INTO `words` VALUES (9312, 'tardy', 'ˈtɑːdi', 'adj.', '行动缓慢的;拖拉的;迟缓的;', 'He wept for the loss of his mother and his tardy recognition of her affection...', '他痛哭流涕，不仅因为失去了母亲，还因为这么晚才认识到了妈妈对他的爱。');
INSERT INTO `words` VALUES (9313, 'target', 'ˈtɑːɡɪt', 'n.', '目标;指标;(攻击的)目标，对象;', 'The village lies beside a main road, making it an easy target for bandits...', '村子坐落在大路边上，很容易成为土匪袭击的对象。');
INSERT INTO `words` VALUES (9314, 'tariff', 'ˈtærɪf', 'n.', '关税;(旅馆、饭店或服务公司的)价目表，收费表;量刑标准;', 'America wants to eliminate tariffs on items such as electronics.', '美国打算取消电子产品等的关税。');
INSERT INTO `words` VALUES (9315, 'tarmac', 'ˈtɑːmæk', 'v.', '以柏油碎石铺筑(路面);', '...a strip of tarmac.', '狭长的柏油碎石路面');
INSERT INTO `words` VALUES (9316, 'tarnish', 'ˈtɑːnɪʃ', 'v.', '(使)失去光泽，暗淡;玷污，败坏，损坏(名声等);', 'The affair could tarnish the reputation of the prime minister...', '这一事件可能有损首相的名誉。');
INSERT INTO `words` VALUES (9317, 'tarpaulin', 'tɑːˈpɔːlɪn', 'n.', '柏油帆布;(防水)油布;', '...a piece of tarpaulin.', '一块油布');
INSERT INTO `words` VALUES (9318, 'tart', 'tɑːt', 'n.', '甜果馅饼;放荡的女人;骚货;', '...jam tarts.', '果酱馅饼');
INSERT INTO `words` VALUES (9319, 'tartan', 'ˈtɑːtn', 'n.', '(尤指源自苏格兰织物的)花格图案，方格花纹;(与苏格兰某家族有关的)花格图案;花格布料;', 'The corridors are carpeted in tartan.', '走廊里铺的是花格地毯。');
INSERT INTO `words` VALUES (9320, 'task', 'tɑːsk', 'n.', '(尤指艰巨或令人厌烦的)任务，工作;(尤指语言教学中旨在帮助达到某一学习目的的)活动;', 'Walker had the unenviable task of breaking the bad news to Hill...', '沃克不得不把这个坏消息告诉希尔。');
INSERT INTO `words` VALUES (9321, 'taste', 'teɪst', 'n.', '味道;滋味;味觉;', '...a keen sense of taste.', '灵敏的味觉');
INSERT INTO `words` VALUES (9322, 'tasteful', 'ˈteɪstfl', 'adj.', '高雅的;雅致的;优美的;', 'The decor is tasteful and restrained.', '装饰风格素雅简约。');
INSERT INTO `words` VALUES (9323, 'tasteless', 'ˈteɪstləs', 'adj.', '无味的;不可口的;不雅的;', '...a flat crammed with spectacularly tasteless objets d\'art.', '摆满了俗不可耐的小艺术品的公寓');
INSERT INTO `words` VALUES (9324, 'tasty', 'ˈteɪsti', 'adj.', '美味的;可口的;好吃的;', 'Try this tasty dish for supper with a crispy salad...', '晚饭就着鲜脆的色拉尝尝这道佳肴。');
INSERT INTO `words` VALUES (9325, 'tattered', 'ˈtætəd', 'adj.', '破烂的;破旧的;褴褛的;', 'He fled wearing only a sarong and a tattered shirt.', '他逃走时只穿着莎笼和破旧的衬衫。');
INSERT INTO `words` VALUES (9326, 'tatty', 'ˈtæti', 'adj.', '褴褛的;破烂的;破败的;', 'There were a lot of guest houses which were very tatty.', '有许多小旅馆都又脏又乱。');
INSERT INTO `words` VALUES (9327, 'taunt', 'tɔːnt', 'v.', '辱骂;嘲笑;讽刺;', 'A gang taunted a disabled man...', '一伙小混混嘲笑一位残疾人。');
INSERT INTO `words` VALUES (9328, 'taut', 'tɔːt', 'adj.', '拉紧的;绷紧的;显得紧张的(或焦虑的、不安的等);', 'When muscles are taut or cold there is more chance of injury or strain...', '肌肉绷紧或受凉时更容易损伤。');
INSERT INTO `words` VALUES (9329, 'tawdry', 'ˈtɔːdri', 'adj.', '俗丽而不值钱的;俗气的;花里胡哨的;', '...tawdry jewellery.', '廉价珠宝');
INSERT INTO `words` VALUES (9330, 'tawny', 'ˈtɔːni', 'adj.', '黄褐色的;茶色的;', 'She had tawny hair.', '她有一头黄褐色的头发。');
INSERT INTO `words` VALUES (9331, 'tax', 'tæks', 'n.', '税;税款;', 'No-one enjoys paying tax...', '没人喜欢纳税。');
INSERT INTO `words` VALUES (9332, 'taxi', 'ˈtæksi', 'n.', '出租汽车;计程车;的士;', 'The taxi drew up in front of the Riviera Club...', '出租车在里维埃拉俱乐部门口停了下来。');
INSERT INTO `words` VALUES (9333, 'taxing', 'ˈtæksɪŋ', 'adj.', '繁重的;费力的;伤脑筋的;', 'They were comparing notes on each other\'s progress towards solving that most taxing of all puzzles: the riddle of the human genome...', '他们就解决人类所面临的最大难题，即人类基因组之谜的进展情况交换了意见。');
INSERT INTO `words` VALUES (9334, 'tea', 'tiː', 'n.', '茶叶;茶;茶水;', '...a cup of tea...', '一杯茶');
INSERT INTO `words` VALUES (9335, 'teach', 'tiːtʃ', 'v.', '教(课程);讲授;教授;', 'The trainers have a programme to teach them vocational skills...', '培训师计划教他们一些职业技能。');
INSERT INTO `words` VALUES (9336, 'teacher', 'ˈtiːtʃə(r)', 'n.', '教师;教员;老师;', 'I\'m a teacher with 21 years\' experience.', '我是个有 21 年教龄的教师。');
INSERT INTO `words` VALUES (9337, 'teaching', 'ˈtiːtʃɪŋ', 'n.', '教学;授课;指导;', 'The Government funds university teaching.', '政府为高校教学提供资金。');
INSERT INTO `words` VALUES (9338, 'teachings', 'ˈtiːtʃɪŋz', 'n.', '教学;授课;指导;', 'The Government funds university teaching.', '政府为高校教学提供资金。');
INSERT INTO `words` VALUES (9339, 'team', 'tiːm', 'n.', '(游戏或运动的)队;(一起工作的)组，班;(同拉一辆车等的)一组牲畜，联畜;', 'The team failed to qualify for the African Nations Cup finals...', '该队未能闯入非洲国家杯决赛。');
INSERT INTO `words` VALUES (9340, 'teamwork', 'ˈtiːmwɜːk', 'n.', '协同工作;配合;', 'Today\'s complex buildings require close teamwork between the architect and the builders.', '如今的建筑结构复杂，需要建筑师和施工人员密切协作。');
INSERT INTO `words` VALUES (9341, 'tearful', 'ˈtɪəfl', 'adj.', '哭泣的;含泪的;令人伤心的;', 'She became very tearful when pressed to talk about it.', '她被逼谈论此事时开始泪如雨下。');
INSERT INTO `words` VALUES (9342, 'tease', 'tiːz', 'v.', '取笑;戏弄;揶揄;', 'He told her how the boys in East Poldown had set on him, teasing him...', '他告诉她东波尔顿的男孩们是如何攻击他、嘲笑他的。');
INSERT INTO `words` VALUES (9343, 'teaspoon', 'ˈtiːspuːn', 'n.', '茶匙;小匙;一茶匙(的量);', 'Drop the dough onto a baking sheet with a teaspoon.', '用茶匙把生面团舀到烤盘上。');
INSERT INTO `words` VALUES (9344, 'teaspoonful', 'ˈtiːˌspuːnfʊl', 'n.', '一茶匙的量;', '...a heaped teaspoonful of salt.', '满满一茶匙的盐');
INSERT INTO `words` VALUES (9345, 'technical', 'ˈteknɪkl', 'adj.', '技术的;技能的;工艺的;', 'In order to reach this limit a number of technical problems will have to be solved.', '要达到这个限度，还有很多技术问题必须解决。');
INSERT INTO `words` VALUES (9346, 'technicality', 'ˌteknɪˈkæləti', 'n.', '技术性细节;诉讼程序性细节，技术性细则(尤指似乎不合理的);', '...the technicalities of classroom teaching.', '课堂教学的具体步骤');
INSERT INTO `words` VALUES (9347, 'technically', 'ˈteknɪkli', 'adv.', '根据确切意义地;严格按照事实地;在专业上;', 'Nude bathing is technically illegal but there are plenty of unspoilt beaches where no one would ever know...', '严格说来，裸泳是违法的，但在许多人迹罕至的海滩，没人会知道。');
INSERT INTO `words` VALUES (9348, 'technician', 'tekˈnɪʃn', 'n.', '技术员;技师;(艺术、体育等的)技巧精湛者，精于技巧者;', '...a laboratory technician.', '实验室技术员');
INSERT INTO `words` VALUES (9349, 'technique', 'tekˈniːk', 'n.', '技巧;技艺;工艺;', '...tests performed using a new technique.', '用新技术进行的试验');
INSERT INTO `words` VALUES (9350, 'technocracy', 'tekˈnɒkrəsi', 'n.', '专家治国制度;专家政治;', '...the power of the Brussels technocracy.', '布鲁塞尔技术专家政治集团的权力');
INSERT INTO `words` VALUES (9351, 'technological', 'ˌteknəˈlɒdʒɪkl', 'adj.', '科技的;工艺的;技术的;', '...an era of very rapid technological change.', '科技发生迅猛变革的时代');
INSERT INTO `words` VALUES (9352, 'technologically', '', 'adv.', '技术上地;', '...an era of very rapid technological change.', '科技发生迅猛变革的时代');
INSERT INTO `words` VALUES (9353, 'technologist', 'tekˈnɒlədʒɪst', 'n.', '技术专家;工艺师;', 'Technology is changing fast...', '科技日新月异。');
INSERT INTO `words` VALUES (9354, 'technology', 'tekˈnɒlədʒi', 'n.', '科技;工艺;工程技术;', 'Technology is changing fast...', '科技日新月异。');
INSERT INTO `words` VALUES (9355, 'tectonic', 'tekˈtɒnɪk', 'adj.', '地壳构造的;', '...the tectonic plates of the Pacific region.', '太平洋地区的地壳构造板块');
INSERT INTO `words` VALUES (9356, 'tedious', 'ˈtiːdiəs', 'adj.', '冗长的;啰唆的;单调乏味的;', 'Such lists are long and tedious to read.', '这种表单读起来冗长乏味。');
INSERT INTO `words` VALUES (9357, 'tedium', 'ˈtiːdiəm', 'n.', '单调乏味;冗长;啰唆;', 'She began to wonder whether she wouldn\'t go mad with the tedium of the job.', '她开始怀疑自己会不会被枯燥的工作逼疯。');
INSERT INTO `words` VALUES (9358, 'teem', 'tiːm', 'v.', '倾注;倾泻;', 'For most of the year, the area is teeming with tourists.', '一年中大部分时间里，这个地区游人如织。');
INSERT INTO `words` VALUES (9359, 'teeming', 'ˈtiːmɪŋ', 'adj.', '大量的;充满(移动着的人、动物等)的;拥挤的;', 'For most of the year, the area is teeming with tourists.', '一年中大部分时间里，这个地区游人如织。');
INSERT INTO `words` VALUES (9360, 'teen', 'ˈtiːn', 'n.', '十几岁，青少年时期(指从 13 岁到 19 岁);', 'Most people who smoke began smoking in their teens...', '多数烟民从十几岁时就开始抽烟了。');
INSERT INTO `words` VALUES (9361, 'teenage', 'ˈtiːneɪdʒ', 'adj.', '十几岁的(指13至19岁);青少年的;', 'Almost one in four teenage girls now smoke.', '如今，十几岁的女孩几乎 4 个当中就有 1 个抽烟。');
INSERT INTO `words` VALUES (9362, 'teenager', 'ˈtiːneɪdʒə(r)', 'n.', '(13至19岁之间的)青少年，青少年男女;', 'As a teenager he attended Tulse Hill Senior High School.', '十几岁时，他上了塔尔斯山高中。');
INSERT INTO `words` VALUES (9363, 'teens', 'tiːnz', 'n.', '十几岁(13至19岁之间);', 'Most people who smoke began smoking in their teens...', '多数烟民从十几岁时就开始抽烟了。');
INSERT INTO `words` VALUES (9364, 'teeny', 'ˈtiːni', 'adj.', '很小的;极小的;十几岁青少年的;', '...little teeny bugs...', '丁点儿大的虫子');
INSERT INTO `words` VALUES (9365, 'teeter', 'ˈtiːtə(r)', 'v.', '摇晃;蹒跚行走;踉跄;', 'Three of the hotels are in receivership, and others are teetering on the brink of bankruptcy...', '有 3 家宾馆已破产在管，其他几家正在破产的边缘徘徊。');
INSERT INTO `words` VALUES (9366, 'teetotal', 'ˌtiːˈtəʊtl', 'adj.', '从不饮酒的;滴酒不沾的;', 'He will not be having a celebratory drink, as he is teetotal.', '他滴酒不沾，不会喝庆功酒的。');
INSERT INTO `words` VALUES (9367, 'teleconference', 'ˈtelikɒnfərəns', 'n.', '电话会议;电信会议;远程会议;', 'Managers at their factory in Birmingham hold a two-hour teleconference with head office in Stuttgart every day.', '伯明翰工厂的经理们每天和位于斯图加特的总部举行两小时的电话会议。');
INSERT INTO `words` VALUES (9368, 'telegram', 'ˈtelɪɡræm', 'n.', '电报(用电信号传递的信息);', 'Scores of congratulatory telegrams and letters greeted Franklin on his return...', '富兰克林一回来，就收到了好几十封贺电和贺信。');
INSERT INTO `words` VALUES (9369, 'telepathy', 'təˈlepəθi', 'n.', '传心术;通灵术;心灵感应;', 'Many of us find it very difficult to state our needs. We expect people to know by telepathy what we are feeling.', '我们许多人感觉很难用语言表达自己的需求，希望别人能通过心灵感应了解我们的感受。');
INSERT INTO `words` VALUES (9370, 'telephone', 'ˈtelɪfəʊn', 'n.', '电话;电话机;(电话机的)话筒，受话器;', 'They usually exchanged messages by telephone...', '他们一般通过电话互通消息。');
INSERT INTO `words` VALUES (9371, 'telescope', 'ˈtelɪskəʊp', 'n.', '望远镜;', 'It\'s hoped that the telescope will enable scientists to see deeper into the universe than ever before.', '人们希望望远镜能使科学家看到比以往更遥远的宇宙深处。');
INSERT INTO `words` VALUES (9372, 'televise', 'ˈtelɪvaɪz', 'v.', '用电视播送;', 'The Grand Prix will be televised by the BBC.', '汽车大奖赛将由英国广播公司进行电视转播。');
INSERT INTO `words` VALUES (9373, 'television', 'ˈtelɪvɪʒn', 'n.', '电视机;电视节目;电视;', 'She turned the television on and flicked around between news programmes.', '她打开电视，来回换台看不同的新闻节目。');
INSERT INTO `words` VALUES (9374, 'tell', 'tel', 'v.', '告诉;告知;提供(情况);', 'In the evening I returned to tell Phyllis our relationship was over...', '晚上我回来告诉菲莉丝我们之间完了。');
INSERT INTO `words` VALUES (9375, 'teller', 'ˈtelə(r)', 'n.', '(银行的)出纳，出纳员;出纳机;提款机;', 'Every bank pays close attention to the speed and accuracy of its tellers.', '各家银行都非常注重出纳员工作的速度和准确性。');
INSERT INTO `words` VALUES (9376, 'telling', 'ˈtelɪŋ', 'adj.', '强有力的;有明显效果的;显著的;', 'Herbert sat quietly through the telling of this saga...', '在聆听这个长篇传奇故事时，赫伯特自始至终安静地坐着。');
INSERT INTO `words` VALUES (9377, 'telltale', 'ˈtelteɪl', 'adj.', '暴露实情的;能说明问题的;', 'Only occasionally did the telltale redness around his eyes betray the fatigue he was suffering.', '只有从他发红的眼圈上偶尔能看出他非常疲惫。');
INSERT INTO `words` VALUES (9378, 'telly', 'ˈteli', 'n.', '电视机;电视节目;', 'After a hard day\'s work most people want to relax in front of the telly.', '辛苦了一天后，多数人想在电视机前放松一下。');
INSERT INTO `words` VALUES (9379, 'temerity', 'təˈmerəti', 'n.', '鲁莽;冒失;蛮勇;', 'He has even had the temerity to invoke the names of Martin Luther King Jr and Malcolm X in defence of his actions.', '他竟然敢搬出马丁·路德·金和马尔科姆·艾克斯的名字来为自己的行为辩解。');
INSERT INTO `words` VALUES (9380, 'temper', 'ˈtempə(r)', 'n.', '脾气;易怒的性情;怒气;', 'He had a temper and could be nasty...', '他脾气不大好，有时凶得很。');
INSERT INTO `words` VALUES (9381, 'temperament', 'ˈtemprəmənt', 'n.', '(人或动物的)气质，性情，性格，禀性;易冲动;(性情)暴躁;', 'His impulsive temperament regularly got him into difficulties...', '他容易冲动，经常因此惹麻烦。');
INSERT INTO `words` VALUES (9382, 'temperamental', 'ˌtemprəˈmentl', 'adj.', '喜怒无常的;容易激动的;反复无常的;', 'He is very temperamental and critical.', '他好使性子，还爱挑毛病。');
INSERT INTO `words` VALUES (9383, 'temperance', 'ˈtempərəns', 'n.', '(由于道德或宗教信仰而实行的)戒酒，禁酒，滴酒不沾;自我克制;克己;', '...a reformed alcoholic extolling the joys of temperance.', '对戒酒的好处赞不绝口的已成功戒酒的酒徒');
INSERT INTO `words` VALUES (9384, 'temperate', 'ˈtempərət', 'adj.', '气候温和的;温带的;温和的;', 'The Nile Valley keeps a temperate climate throughout the year.', '尼罗河流域一年四季气候温和。');
INSERT INTO `words` VALUES (9385, 'temperature', 'ˈtemprətʃə(r)', 'n.', '温度;气温;体温;', 'The temperature soared to above 100 degrees in the shade...', '阴凉处的温度骤升至100 多度。');
INSERT INTO `words` VALUES (9386, 'tempest', 'ˈtempɪst', 'n.', '大风暴;暴风雨;暴风雪;', 'Torrential rain and a howling tempest cut a swathe of destruction across the country.', '狂风暴雨横扫全国，所到之处，一片狼藉。');
INSERT INTO `words` VALUES (9387, 'tempestuous', 'temˈpestʃuəs', 'adj.', '激烈的;狂暴的;骚动的;', 'For years, the couple\'s tempestuous relationship made the headlines...', '有好多年，那对夫妻跌宕起伏的关系总是登上报纸头条。');
INSERT INTO `words` VALUES (9388, 'template', 'ˈtempleɪt', 'n.', '样板;模板;型板;', 'Trace around your template and transfer the design onto a sheet of card.', '沿着你的样板边画一圈，把图案描在一张硬纸板上。');
INSERT INTO `words` VALUES (9389, 'temple', 'ˈtempl', 'n.', '(非基督教的)庙宇，寺院，神殿，圣堂;太阳穴;鬓角;', '...a small Hindu temple.', '一座小的印度教庙宇');
INSERT INTO `words` VALUES (9390, 'tempo', 'ˈtempəʊ', 'n.', '(乐曲的)速度，拍子，节奏;(运动或活动的)速度;', '...owing to the slow tempo of change in an overwhelmingly rural country...', '由于以农业占绝对主导地位的国家变革速度缓慢');
INSERT INTO `words` VALUES (9391, 'temporal', 'ˈtempərəl', 'adj.', '世间的;世俗的;现世的;', '...their spiritual and temporal leader.', '他们的精神领袖兼世俗领导人');
INSERT INTO `words` VALUES (9392, 'temporarily', '\'temprərəlɪ', 'adv.', '暂时;', 'His job here is only temporary...', '他在这儿的工作只是临时的。');
INSERT INTO `words` VALUES (9393, 'temporary', 'ˈtemprəri', 'adj.', '短暂的;暂时的;临时的;', 'His job here is only temporary...', '他在这儿的工作只是临时的。');
INSERT INTO `words` VALUES (9394, 'tempt', 'tempt', 'v.', '引诱;诱惑;劝诱;', 'Reducing the income will further impoverish these families and could tempt an offender into further crime...', '降低收入只会使这些家庭更加贫穷，而且可能诱使不法分子进一步犯罪。');
INSERT INTO `words` VALUES (9395, 'temptation', 'tempˈteɪʃn', 'n.', '引诱;诱惑;煽诱人的事物;', 'Will they be able to resist the temptation to buy?', '他们能忍住诱惑不买吗？');
INSERT INTO `words` VALUES (9396, 'tempting', 'ˈtemptɪŋ', 'adj.', '吸引人的;诱人的;有吸引力的;', 'In the end, I turned down Raoul\'s tempting offer of the Palm Beach trip...', '最终，我回绝了拉乌尔提出的去棕榈海滩旅行的诱人邀请。');
INSERT INTO `words` VALUES (9397, 'ten', 'ten', 'num.', '十;', 'Over the past ten years things have changed.', '过去 10 年间，情况已发生了变化。');
INSERT INTO `words` VALUES (9398, 'tenable', 'ˈtenəbl', 'adj.', '说得过去的;站得住脚的;可保有，可保持，可担任(一段时间);', 'This argument is simply not tenable...', '这个论点根本站不住脚。');
INSERT INTO `words` VALUES (9399, 'tenacious', 'təˈneɪʃəs', 'adj.', '紧握的;不松手的;坚持的;', 'She is very tenacious and will work hard and long to achieve objectives...', '她是个锲而不舍的人，为了达到目标愿意长期苦干。');
INSERT INTO `words` VALUES (9400, 'tenacity', 'təˈnæsəti', 'n.', '坚韧;坚毅;', 'Talent, hard work and sheer tenacity are all crucial to career success.', '事业要成功，才能、勤奋和顽强的意志都至关重要。');
INSERT INTO `words` VALUES (9401, 'tenancy', 'ˈtenənsi', 'n.', '(房屋、土地等的)租用期限，租赁期限，租期;(房屋或土地的)租用，租赁;', 'His father took over the tenancy of the farm 40 years ago...', '他父亲 40 年前租下了这个农场。');
INSERT INTO `words` VALUES (9402, 'tenant', 'ˈtenənt', 'n.', '房客;租户;佃户;', 'Regulations placed clear obligations on the landlord for the benefit of the tenant...', '为维护房客的利益，条例明确规定了房东负有的各项义务。');
INSERT INTO `words` VALUES (9403, 'tend', 'tend', 'v.', '往往会;常常就;趋向;', 'A problem for manufacturers is that lighter cars tend to be noisy...', '制造商们遇到的一个问题是，重量较轻的汽车往往噪音大。');
INSERT INTO `words` VALUES (9404, 'tendency', 'ˈtendənsi', 'n.', '倾向;偏好;性情;', 'The army has become increasingly restless over the mounting separatist tendencies of the northern republics.', '军方对于北方几个共和国不断加剧的分裂倾向感到越来越不安。');
INSERT INTO `words` VALUES (9405, 'tendentious', 'tenˈdenʃəs', 'adj.', '倾向性的;有偏见的;有争议的;', 'His analysis was rooted in a somewhat tendentious reading of French history.', '他的分析源于对法国历史带有一定偏见的解读。');
INSERT INTO `words` VALUES (9406, 'tenement', 'ˈtenəmənt', 'n.', '(尤指城市贫困区的)经济公寓，廉租公寓;', '...elegant 19th century tenement buildings.', '几幢典雅的 19 世纪公寓大楼');
INSERT INTO `words` VALUES (9407, 'tenet', 'ˈtenɪt', 'n.', '原则;信条;教义;', 'Non-violence and patience are the central tenets of their faith...', '非暴力和忍耐是他们的信仰的核心原则。');
INSERT INTO `words` VALUES (9408, 'tenor', 'ˈtenə(r)', 'n.', '男高音;男高音歌手;男高音部;', '...a free, open-air concert given by the Italian tenor, Luciano Pavarotti.', '意大利男高音歌唱家卢西亚诺·帕瓦罗蒂举办的一场免费露天演唱会');
INSERT INTO `words` VALUES (9409, 'tense', 'tens', 'adj.', '神经紧张的;担心的;不能松弛的;', 'This gesture of goodwill did little to improve the tense atmosphere at the talks...', '这一友好姿态基本上没有改善会谈的紧张气氛。');
INSERT INTO `words` VALUES (9410, 'tensile', 'ˈtensaɪl', 'adj.', '张力的;拉力的;抗张的;', 'Certain materials can be manufactured with a high tensile strength.', '有些材料可以制造成抗拉强度大的产品。');
INSERT INTO `words` VALUES (9411, 'tension', 'ˈtenʃn', 'n.', '紧张局势(或关系、状况);矛盾;对立;', 'The tension between the two countries is likely to remain...', '那两个国家间的紧张局面可能会持续下去。');
INSERT INTO `words` VALUES (9412, 'tentative', 'ˈtentətɪv', 'adj.', '不确定的;不肯定的;暂定的;', 'Political leaders have reached a tentative agreement to hold a preparatory conference next month...', '政治领导人已就下个月举行预备会议达成初步协定。');
INSERT INTO `words` VALUES (9413, 'tentatively', 'ˈtɛntətɪvli', 'adv.', '初步;尝试性地;暂时性地;', 'Political leaders have reached a tentative agreement to hold a preparatory conference next month...', '政治领导人已就下个月举行预备会议达成初步协定。');
INSERT INTO `words` VALUES (9414, 'tenuous', 'ˈtenjuəs', 'adj.', '脆弱的;微弱的;缥缈的;', 'The cultural and historical links between the many provinces were seen to be very tenuous...', '许多省份之间的文化和历史纽带被认为是很脆弱的。');
INSERT INTO `words` VALUES (9415, 'tenure', 'ˈtenjə(r)', 'n.', '(尤指重要政治职务的)任期，任职;(尤指大学教师的)终身职位，长期聘用;(房地产的)保有权，保有期;', 'Lack of security of tenure was a reason for many families becoming homeless.', '居住权得不到保证是许多家庭居无定所的一个原因。');
INSERT INTO `words` VALUES (9416, 'tepid', 'ˈtepɪd', 'adj.', '不冷不热的;微温的;温吞的;', 'She bent her mouth to the tap and drank the tepid water.', '她把嘴伸到水龙头底下去喝那微温的水。');
INSERT INTO `words` VALUES (9417, 'term', 'tɜːm', 'n.', '词语;术语;措辞;', 'Our goods compete in terms of product quality, reliability and above all variety...', '我们的产品在质量、可靠性，尤其是品种方面颇具竞争力。');
INSERT INTO `words` VALUES (9418, 'terminal', 'ˈtɜːmɪnl', 'n.', '航空站;航空终点站;(火车、公共汽车或船的)终点站;', '...terminal cancer.', '晚期癌症');
INSERT INTO `words` VALUES (9419, 'terminally', '\'tɜːmɪnəli', 'adv.', '在末端;每期;在学期末尾;', '...terminal cancer.', '晚期癌症');
INSERT INTO `words` VALUES (9420, 'terminate', 'ˈtɜːmɪneɪt', 'v.', '(使)停止，结束，终止;到达终点站;', 'Her next remark abruptly terminated the conversation...', '她接下来说的话使谈话戛然而止。');
INSERT INTO `words` VALUES (9421, 'terminating', 'ˈtɜːmɪneɪtɪŋ', 'v.', '(使)停止，结束，终止;到达终点站;', 'Her next remark abruptly terminated the conversation...', '她接下来说的话使谈话戛然而止。');
INSERT INTO `words` VALUES (9422, 'termination', 'ˌtɜːmɪˈneɪʃn', 'n.', '结束;终止;末端;', 'Her next remark abruptly terminated the conversation...', '她接下来说的话使谈话戛然而止。');
INSERT INTO `words` VALUES (9423, 'terminology', 'ˌtɜːmɪˈnɒlədʒi', 'n.', '(某学科的)术语;有特别含义的用语;专门用语;', '...gastritis, which in medical terminology means an inflammation of the stomach.', '胃炎是一个医学术语，意思是胃部炎症');
INSERT INTO `words` VALUES (9424, 'terminus', 'ˈtɜːmɪnəs', 'n.', '(铁路或公共汽车路线的)终点站;', '...the London terminus of the Channel Tunnel rail link.', '英吉利海峡隧道连接铁路的伦敦终点站');
INSERT INTO `words` VALUES (9425, 'terms', 'tɜːmz', 'n.', '(协议、合同等的)条件，条款;(交易的)条件;价钱;', 'Our goods compete in terms of product quality, reliability and above all variety...', '我们的产品在质量、可靠性，尤其是品种方面颇具竞争力。');
INSERT INTO `words` VALUES (9426, 'terracotta', 'ˌterəˈkɒtə', 'n.', '(无釉的)赤陶土，赤陶;赤褐色;土红色;', '...plants in terracotta pots.', '赤土花盆里的植物');
INSERT INTO `words` VALUES (9427, 'terrace', 'ˈterəs', 'n.', '(相同的一排)排房，排屋;(尤指房屋或餐馆外的)露天平台，阳台;(足球场的)阶梯看台;', '...a terrace of stylish Victorian houses.', '一排雅致的维多利亚风格的房屋');
INSERT INTO `words` VALUES (9428, 'terrain', 'təˈreɪn', 'n.', '地形;地势;地带;', 'The terrain changed quickly from arable land to desert.', '那个地带很快就从耕地变成了沙漠。');
INSERT INTO `words` VALUES (9429, 'terrestrial', 'təˈrestriəl', 'adj.', '陆地的;陆栖的;陆生的;', 'Terrestrial and aquatic fauna may sometimes be found resting together under a loose stone.', '有时在松动的石头下可以同时看见栖息的陆生动物和水生动物。');
INSERT INTO `words` VALUES (9430, 'terrible', 'ˈterəbl', 'adj.', '非常讨厌的;令人极不快的;可怕的;', 'Tens of thousands more suffered terrible injuries in the world\'s worst industrial disaster...', '还有成千上万的人在那场全世界最严重的工业灾难中受了重伤。');
INSERT INTO `words` VALUES (9431, 'terribly', 'ˈterəbli', 'adv.', '非常;很;非常地;', 'Tens of thousands more suffered terrible injuries in the world\'s worst industrial disaster...', '还有成千上万的人在那场全世界最严重的工业灾难中受了重伤。');
INSERT INTO `words` VALUES (9432, 'terrific', 'təˈrɪfɪk', 'adj.', '极好的;绝妙的;了不起的;', 'What a terrific idea!...', '多么好的主意！');
INSERT INTO `words` VALUES (9433, 'terrified', 'ˈterɪfaɪd', 'adj.', '恐惧;很害怕;', 'Flying terrifies him...', '坐飞机让他害怕得要命。');
INSERT INTO `words` VALUES (9434, 'terrify', 'ˈterɪfaɪ', 'v.', '使恐惧;使十分害怕;使惊吓;', 'Flying terrifies him...', '坐飞机让他害怕得要命。');
INSERT INTO `words` VALUES (9435, 'terrifying', 'ˈterɪfaɪɪŋ', 'adj.', '吓人的;令人害怕的;', 'I still find it terrifying to find myself surrounded by large numbers of horses...', '我仍旧觉得被一大群马包围很可怕。');
INSERT INTO `words` VALUES (9436, 'territorial', 'ˌterəˈtɔːriəl', 'adj.', '领土的;地盘性的;', 'It is the only republic which has no territorial disputes with the others...', '它是唯一和其他国家没有领土争端的共和国。');
INSERT INTO `words` VALUES (9437, 'territory', 'ˈterətri', 'n.', '领土;版图;领地;', 'The government denies that any of its territory is under rebel control.', '政府否认有领土被反叛分子所控制。');
INSERT INTO `words` VALUES (9438, 'terror', 'ˈterə(r)', 'n.', '惊恐;恐惧;惊骇;', 'I shook with terror whenever I was about to fly in an aeroplane...', '每次坐飞机前，我都吓得直发抖。');
INSERT INTO `words` VALUES (9439, 'terrorist', 'ˈterərɪst', 'n.', '恐怖主义者;恐怖分子;', 'One American was killed and three were wounded in terrorist attacks.', '在几起恐怖袭击中，一名美国人遇难，三人受伤。');
INSERT INTO `words` VALUES (9440, 'terry', 'ˈteri', 'n.', '毛圈棉织物(多用以做毛巾);', '...a terry nappy.', '毛巾布尿布');
INSERT INTO `words` VALUES (9441, 'terse', 'tɜːs', 'adj.', '简要的;简短生硬的;', 'He issued a terse statement, saying he is discussing his future with colleagues before announcing his decision on Monday...', '他发表了简短声明，称他正在和同事讨论他的未来，并将于星期一宣布他的决定。');
INSERT INTO `words` VALUES (9442, 'tertiary', 'ˈtɜːʃəri', 'adj.', '第三的;第三位的;第三级的;', 'He must have come to know those philosophers through secondary or tertiary sources.', '他一定是通过第二手或第三手资料了解那些哲学家的。');
INSERT INTO `words` VALUES (9443, 'test', 'test', 'n.', '测验;考查;(医疗上的)检查，化验，检验;', 'Either measure the temperature with a bath thermometer or test the water with your wrist...', '要么用浴用温度计测量水温，要么用手腕试一下。');
INSERT INTO `words` VALUES (9444, 'testament', 'ˈtestəmənt', 'n.', '证据;证明;', 'Braka\'s house, just off Sloane Square, is a testament to his Gothic tastes...', '布拉卡那幢紧靠斯隆广场的房子是他喜好哥特式建筑的一个证据。');
INSERT INTO `words` VALUES (9445, 'testify', 'ˈtestɪfaɪ', 'v.', '(尤指出庭)作证;证实;证明;', 'Several eyewitnesses testified that they saw the officers hit Miller in the face...', '几位目击证人证明他们看见那几个警官打了米勒的脸。');
INSERT INTO `words` VALUES (9446, 'testimonial', 'ˌtestɪˈməʊniəl', 'n.', '证明信;介绍信;推荐信;', 'She could hardly expect her employer to provide her with testimonials to her character and ability.', '她可不敢指望雇主给她出具关于她品格和能力的证明书。');
INSERT INTO `words` VALUES (9447, 'testimony', 'ˈtestɪməni', 'n.', '证据;证明;证词;', 'His testimony was an important element of the Prosecution case...', '他的证词是这宗公诉案件中的一个重要因素。');
INSERT INTO `words` VALUES (9448, 'testing', 'ˈtestɪŋ', 'n.', '试验;测试;检查;', 'The most testing time is undoubtedly in the early months of your return to work...', '最考验人的时期莫过于重新回来上班的头几个月。');
INSERT INTO `words` VALUES (9449, 'testy', 'ˈtesti', 'adj.', '易怒的;暴躁的;', 'Ben\'s getting a little testy in his old age.', '上了年纪后本变得有点性急了。');
INSERT INTO `words` VALUES (9450, 'tether', 'ˈteðə(r)', 'v.', '拴(牲畜);', 'She was jealous, humiliated, and emotionally at the end of her tether...', '她既忌妒又感到屈辱，感情上已经到了崩溃的边缘。');
INSERT INTO `words` VALUES (9451, 'text', 'tekst', 'n.', '(书籍或杂志的)正文，本文(并非附注、图片等);文本;文档;', 'The text is precise and informative.', '正文内容准确，信息量大。');
INSERT INTO `words` VALUES (9452, 'textbook', 'ˈtekstbʊk', 'n.', '教科书;课本;教材;', 'She wrote a textbook on international law.', '她写了本国际法教材。');
INSERT INTO `words` VALUES (9453, 'textile', 'ˈtekstaɪl', 'n.', '纺织品;纺织业;', '...decorative textiles for the home.', '家用装饰织品');
INSERT INTO `words` VALUES (9454, 'textual', 'ˈtekstʃuəl', 'adj.', '文本的;篇章的;', '...close textual analysis of Shakespeare.', '对莎士比亚作品缜密的文本分析');
INSERT INTO `words` VALUES (9455, 'texture', 'ˈtekstʃə(r)', 'n.', '质地;手感;口感;', 'Aloe Vera is used in moisturisers to give them a wonderfully silky texture...', '芦荟用于润肤霜中，使之具有美妙的丝般润滑感。');
INSERT INTO `words` VALUES (9456, 'thai', 'taɪ', 'n.', '泰国人;泰语;', 'Thai food is terrific.', '泰国菜棒极了。');
INSERT INTO `words` VALUES (9457, 'than', 'ðæn , ðən', 'prep./conj.', '(用以引出比较的第二部分)比;(比较数量、距离等)多于，小于，少于;(表示一事紧跟另一事发生)就;', 'The radio only weighs a few ounces and is smaller than a cigarette packet...', '这台收音机重量仅为几盎司，比香烟盒还小。');
INSERT INTO `words` VALUES (9458, 'thank', 'θæŋk', 'v.', '谢谢，感谢(某人);(为某事)道谢;', 'Thank you very much for your call...', '非常感谢您的来电。');
INSERT INTO `words` VALUES (9459, 'thankful', 'ˈθæŋkfl', 'adj.', '感谢;感激;欣慰;', 'Most of the time I\'m just thankful that I\'ve got a job...', '多数时候我只是庆幸自己有份工作。');
INSERT INTO `words` VALUES (9460, 'thankfully', 'ˈθæŋkfəli', 'adv.', '(用以表示高兴)幸亏;高兴地;感激地;', 'Thankfully, she was not injured...', '好在她没有受伤。');
INSERT INTO `words` VALUES (9461, 'thanks', 'θæŋks', 'int.', '(表示感激)感谢，谢谢;(接受好意)好的;(婉言谢绝)不用了;', 'Thank you very much for your call...', '非常感谢您的来电。');
INSERT INTO `words` VALUES (9462, 'thanksgiving', 'ˌθæŋksˈɡɪvɪŋ', 'n.', '感恩节(美国定为十一月的第四个星期四，加拿大定为十月的第二个星期一，均为公休日);感恩(于上帝);', 'The Prince\'s unexpected recovery was celebrated with a thanksgiving service in St Paul\'s.', '人们在圣保罗大教堂为王子的意外康复举行了感恩祈祷仪式。');
INSERT INTO `words` VALUES (9463, 'thatch', 'θætʃ', 'n.', '(作为屋顶材料的)茅草，稻草，芦苇;茅草屋顶;草屋顶;', 'They would live in a small house with a green door and a new thatch.', '他们将住在一所新苫顶的绿门小房子里。');
INSERT INTO `words` VALUES (9464, 'thaw', 'θɔː', 'v.', '(结冰后)解冻，融化，融解;天气暖和得使冰雪融化(或解冻);(使冷冻食品)化冻;', 'It\'s so cold the snow doesn\'t get a chance to thaw...', '天太冷了，雪根本化不了。');
INSERT INTO `words` VALUES (9465, 'theatrical', 'θiˈætrɪkl', 'adj.', '戏剧的;演剧的;剧场的;', 'These are the prizes given for the most outstanding British theatrical performances of the year.', '这些奖项将要颁发给本年度英国最杰出的戏剧表演作品。');
INSERT INTO `words` VALUES (9466, 'theatricals', 'θiˈætrɪklz', 'n.', '戏剧演出;戏剧化动作;矫揉造作;', 'These are the prizes given for the most outstanding British theatrical performances of the year.', '这些奖项将要颁发给本年度英国最杰出的戏剧表演作品。');
INSERT INTO `words` VALUES (9467, 'thee', 'ðiː', 'pron.', '(第二人称单数的宾格)你;', 'I miss thee, beloved father.', '亲爱的父亲，孩儿想念您。');
INSERT INTO `words` VALUES (9468, 'theft', 'θeft', 'n.', '偷;偷窃;盗窃罪;', 'Art theft is now part of organised crime.', '盗窃艺术品如今成为一种有组织的犯罪活动。');
INSERT INTO `words` VALUES (9469, 'thematic', 'θɪˈmætɪk', 'adj.', '题目的;主题的;专题的;', '...assembling this material into thematic groups.', '按主题将这些材料整理分类');
INSERT INTO `words` VALUES (9470, 'theme', 'θiːm', 'n.', '(演讲、文章或艺术作品的)题目，主题，主题思想;(乐曲的)主题，主旋律;(学生的)作文;', 'The theme of the conference is renaissance Europe...', '大会的主题是文艺复兴时期的欧洲。');
INSERT INTO `words` VALUES (9471, 'then', 'ðen', 'adv.', '(指过去)当时，那时;(指将来)到那时，届时;然后;', 'He wanted to have a source of income after his retirement; until then, he wouldn\'t require additional money...', '他想退休后有份收入;在那以前，他并不需要额外的钱。');
INSERT INTO `words` VALUES (9472, 'thence', 'ðens', 'adv.', '从那里;然后;随之;', 'I ran straight up to Columbia County, then turned East, came down the Harlem Valley and thence home...', '我一直跑到哥伦比亚县，然后向东转，沿着哈勒姆山谷，从那里回到家。');
INSERT INTO `words` VALUES (9473, 'theocracy', 'θiˈɒkrəsi', 'n.', '神权政治;僧侣政体;神权国;', '...a medieval theocracy run by Buddhist monks.', '中世纪僧侣掌权的神权政体');
INSERT INTO `words` VALUES (9474, 'theological', 'ˌθiːə\'lɒdʒɪk(ə)l', 'adj.', '神学(上)的;神学性质的;根据圣经的;', '...questions of theology.', '神学问题');
INSERT INTO `words` VALUES (9475, 'theology', 'θiˈɒlədʒi', 'n.', '神学;宗教学;宗教信仰学;', '...questions of theology.', '神学问题');
INSERT INTO `words` VALUES (9476, 'theoretical', 'ˌθɪəˈretɪkl', 'adj.', '理论上的;理论上存在的;假设的;', '...theoretical physics.', '理论物理学');
INSERT INTO `words` VALUES (9477, 'theoretically', 'ˌθɪəˈretɪkli', 'adv.', '从理论上说;照理说;', 'Theoretically, the price is supposed to be marked on the shelf...', '照理说，价格应该标在货架上。');
INSERT INTO `words` VALUES (9478, 'theorist', 'ˈθɪərɪst', 'n.', '理论家;理论工作者;', '...theorists unaligned with any particular doctrine.', '不附和任何教条的理论家');
INSERT INTO `words` VALUES (9479, 'theory', 'ˈθɪəri', 'n.', '学说;论;说;', 'Marx produced a new theory about historical change based upon conflict between competing groups...', '马克思创立了一种新理论，认为历史的演变是基于对立集团之间的冲突之上的。');
INSERT INTO `words` VALUES (9480, 'therapeutic', 'ˌθerəˈpjuːtɪk', 'adj.', '治疗的;医疗的;治病的;', 'Astanga Yoga is a therapeutic physical exercise that focuses on breathing and relaxation.', '八步功法瑜伽注重呼吸和放松，是一种舒缓身心的锻炼方法。');
INSERT INTO `words` VALUES (9481, 'therapist', 'ˈθerəpɪst', 'n.', '(某治疗法的)治疗专家;', 'My therapist helped me feel my anger.', '我的治疗专家帮助我感受自己的怒气。');
INSERT INTO `words` VALUES (9482, 'therapy', 'ˈθerəpi', 'n.', '治疗;疗法;', 'In therapy, she began to let go of her obsession with Mike...', '在接受治疗的过程中，她开始逐渐从对迈克的迷恋中解脱出来。');
INSERT INTO `words` VALUES (9483, 'thereafter', 'ˌðeərˈɑːftə(r)', 'adv.', '之后;此后;以后;', 'Inflation will fall and thereafter so will interest rates...', '通货膨胀率将下跌，随后利率也将下降。');
INSERT INTO `words` VALUES (9484, 'thereby', 'ˌðeəˈbaɪ', 'adv.', '因此;由此;从而;', 'Our bodies can sweat, thereby losing heat by evaporation...', '我们的身体能出汗，由此可以蒸发散热。');
INSERT INTO `words` VALUES (9485, 'therefore', 'ˈðeəfɔː(r)', 'adv.', '因此;所以;因而;', 'Muscle cells need lots of fuel and therefore burn lots of calories...', '肌肉细胞需要很多能量，所以会燃烧大量卡路里。');
INSERT INTO `words` VALUES (9486, 'therein', 'ˌðeərˈɪn', 'adv.', '在那里，在其中(指提及的地点、物体、文件等);', 'By burning tree branches, pine needles, and pine cones, many not only warm their houses but improve the smell therein.', '许多人在家里烧树枝、松针和松果，不但给房子取暖，还改善了屋里的气味。');
INSERT INTO `words` VALUES (9487, 'thereof', 'ˌðeərˈɒv', 'adv.', '在其中;由此;', '...his belief in God — or the lack thereof.', '他对上帝的信仰——或者不信仰');
INSERT INTO `words` VALUES (9488, 'thereon', 'ˌðeərˈɒn', 'adv.', '以…为根据;由…而产生;', 'There was a card on each door with a guest\'s name inscribed thereon.', '每扇门上都有张卡，上面印着客人的名字。');
INSERT INTO `words` VALUES (9489, 'thereupon', 'ˌðeərəˈpɒn', 'adv.', '立即;随即;因此;', 'Some months ago angry demonstrators mounted a noisy demonstration beneath his window. His neighbours thereupon insisted upon more security.', '几个月前，愤怒的示威者在他的窗下发起了闹哄哄的抗议活动，随后他的邻居们坚决要求加强安全防范。');
INSERT INTO `words` VALUES (9490, 'thermal', 'ˈθɜːml', 'adj.', '热的;热量的;保暖的;', '...thermal power stations.', '热电厂');
INSERT INTO `words` VALUES (9491, 'thesis', 'ˈθiːsɪs', 'n.', '论文;毕业论文;学位论文;', 'This thesis does not stand up to close inspection.', '这个命题经不起仔细推敲。');
INSERT INTO `words` VALUES (9492, 'thick', 'θɪk', 'adj.', '厚的;粗的;(询问或说明厚度)有…厚;', 'For breakfast I had a thick slice of bread and syrup...', '早餐我吃了一片厚厚的涂了糖浆的面包。');
INSERT INTO `words` VALUES (9493, 'thicken', 'ˈθɪkən', 'v.', '(使)变厚，变浓，变稠;', 'Thicken the broth with the cornflour...', '加玉米面使汤变稠。');
INSERT INTO `words` VALUES (9494, 'thicket', 'ˈθɪkɪt', 'n.', '灌木丛;树丛;错综复杂;', '...a bamboo thicket.', '竹丛');
INSERT INTO `words` VALUES (9495, 'thickly', 'ˈθɪkli', 'adv.', '厚厚地;(树木、人口等)茂密，稠密;声音不清的，沙哑的(尤指因疾病或激动);', 'For breakfast I had a thick slice of bread and syrup...', '早餐我吃了一片厚厚的涂了糖浆的面包。');
INSERT INTO `words` VALUES (9496, 'thickness', 'ˈθɪknəs', 'n.', '厚;厚度;粗;', 'For breakfast I had a thick slice of bread and syrup...', '早餐我吃了一片厚厚的涂了糖浆的面包。');
INSERT INTO `words` VALUES (9497, 'thick-skinned', 'ˌθɪk ˈskɪnd', 'adj.', '厚脸皮的;不计较脸面的;(对批评或侮辱)麻木不仁的;', 'He was thick-skinned enough to cope with her taunts.', '他脸皮厚，不怕她嘲弄。');
INSERT INTO `words` VALUES (9498, 'thief', 'θiːf', 'n.', '贼;小偷;窃贼;', 'The thieves snatched the camera.', '窃贼偷走了相机。');
INSERT INTO `words` VALUES (9499, 'thieves', 'θiːvz', 'n.', '贼;小偷;窃贼;', 'The thieves snatched the camera.', '窃贼偷走了相机。');
INSERT INTO `words` VALUES (9500, 'thin', 'θɪn', 'adj.', '薄的;细的;瘦的;', 'A thin cable carries the signal to a computer...', '一根细电缆将信号传送给一台计算机。');
INSERT INTO `words` VALUES (9501, 'thing', 'θɪŋ', 'n.', '东西;物;物件;', '\'What\'s that thing in the middle of the fountain?\' — \'Some kind of statue, I guess.\'...', '“喷泉中间的那个是什么东西？”——“估计是塑像什么的吧。”');
INSERT INTO `words` VALUES (9502, 'think', 'θɪŋk', 'v.', '认为;以为;想;', 'I certainly think there should be a ban on tobacco advertising...', '我确实认为应该禁止香烟广告。');
INSERT INTO `words` VALUES (9503, 'thinker', 'ˈθɪŋkə(r)', 'n.', '思想家;思想…的人;', '...some of the world\'s greatest thinkers.', '世界上一些最伟大的思想家');
INSERT INTO `words` VALUES (9504, 'thinking', 'ˈθɪŋkɪŋ', 'n.', '思想;思考;思维;', 'There was undeniably a strong theoretical dimension to his thinking.', '不可否认，他的思想很有理论深度。');
INSERT INTO `words` VALUES (9505, 'thinly', 'ˈθɪnli', 'adv.', '细;瘦;薄;', 'A thin cable carries the signal to a computer...', '一根细电缆将信号传送给一台计算机。');
INSERT INTO `words` VALUES (9506, 'third', 'θɜːd', 'ord.', '第三;', 'I sleep on the third floor...', '我睡在三楼。');
INSERT INTO `words` VALUES (9507, 'thirdly', 'ˈθɜːdli', 'adv.', '(用于讲话或文章中列举事项)第三，第三点;', 'First of all, there are not many of them, and secondly, they have little money and, thirdly, they have few big businesses.', '首先，他们人数不多;第二，他们钱很少;第三，他们没有几家大企业。');
INSERT INTO `words` VALUES (9508, 'thirst', 'θɜːst', 'n.', '口渴;干渴感;渴;', 'Coca is well-known for reducing hunger, thirst and fatigue...', '人们都知道可乐能消饿、止渴和解乏。');
INSERT INTO `words` VALUES (9509, 'thirsty', 'ˈθɜːsti', 'adj.', '渴的;口渴的;渴望;', 'If a baby is thirsty, it feeds more often...', '婴儿要是渴了，就会吃得更频繁。');
INSERT INTO `words` VALUES (9510, 'thither', 'ˈðɪðə(r)', 'adv.', '到那里;向那里;', 'They have dragged themselves thither for shelter.', '他们费力地挪动到那边躲避。');
INSERT INTO `words` VALUES (9511, 'thorn', 'θɔːn', 'n.', '(玫瑰之类植物的)刺，棘刺;带刺的树;荆棘;', 'Roses will always have thorns but with care they can be avoided.', '玫瑰花上都有刺，不过只要小心就碰不到。');
INSERT INTO `words` VALUES (9512, 'thorny', 'ˈθɔːni', 'adj.', '棘手的;麻烦的;引起争议的;', '...thorny hawthorn trees.', '长满刺的山楂树');
INSERT INTO `words` VALUES (9513, 'thorough', 'ˈθʌrə', 'adj.', '彻底的;完全的;深入的;', 'We are making a thorough investigation...', '我们正在进行彻底调查。');
INSERT INTO `words` VALUES (9514, 'thoroughfare', 'ˈθʌrəfeə(r)', 'n.', '大街;大道;通衢;', '...a busy thoroughfare.', '繁忙的大街');
INSERT INTO `words` VALUES (9515, 'thoroughly', 'ˈθʌrəli', 'adv.', '非常;极其;彻底;', 'We are making a thorough investigation...', '我们正在进行彻底调查。');
INSERT INTO `words` VALUES (9516, 'thoughtful', 'ˈθɔːtfl', 'adj.', '沉思的;深思的;思考的;', 'Nancy, who had been thoughtful for some time, suddenly spoke...', '南希沉思了一会儿，突然开口说话了。');
INSERT INTO `words` VALUES (9517, 'thoughtfully', '\'θɔːtf(ə)li', 'adv.', '若有所思地;沉思地;意味深长地;', 'Nancy, who had been thoughtful for some time, suddenly spoke...', '南希沉思了一会儿，突然开口说话了。');
INSERT INTO `words` VALUES (9518, 'thoughtfulness', 'ˈθɔːtfʊlnəs', 'n.', '无微不至;体谅;', 'Nancy, who had been thoughtful for some time, suddenly spoke...', '南希沉思了一会儿，突然开口说话了。');
INSERT INTO `words` VALUES (9519, 'thoughtless', 'ˈθɔːtləs', 'adj.', '粗心大意的;欠考虑的;轻率的;', '...a small minority of thoughtless and inconsiderate people...', '一小群只顾自己、不顾别人的人');
INSERT INTO `words` VALUES (9520, 'thrall', 'θrɔːl', 'n.', '控制;奴役;束缚;', 'He is not in thrall to the media...', '他不受制于媒体。');
INSERT INTO `words` VALUES (9521, 'thrash', 'θræʃ', 'v.', '(作为惩罚用棍子等)抽打，连续击打;(使)激烈扭动，翻来覆去;彻底击败，重创(对手);', 'Second-placed Rangers thrashed St Johnstone 5-nil.', '排名第二的流浪者队以 5: 0 大胜圣约翰斯通队。');
INSERT INTO `words` VALUES (9522, 'thrashing', 'ˈθræʃɪŋ', 'n.', '棒打;殴打;痛打;', 'Can the New Zealand bowlers fight back after their thrashing at Christchurch?', '在克赖斯特彻奇遭遇惨败后，新西兰的投球手们还有还手之力吗？');
INSERT INTO `words` VALUES (9523, 'thread', 'θred', 'n.', '(棉、毛、丝等的)线;线索;脉络;', 'This time I\'ll do it properly with a needle and thread.', '这次，我要用针线将它缝好。');
INSERT INTO `words` VALUES (9524, 'threadbare', 'ˈθredbeə(r)', 'adj.', '穿旧的;磨薄的;破旧的;', 'She sat cross-legged on a square of threadbare carpet.', '她盘腿坐在一块破旧的方形地毯上。');
INSERT INTO `words` VALUES (9525, 'threat', 'θret', 'n.', '威胁;恐吓;凶兆;', 'Some couples see single women as a threat to their relationships...', '有些夫妇把单身女人看成是对他们婚姻的威胁。');
INSERT INTO `words` VALUES (9526, 'threaten', 'ˈθretn', 'v.', '扬言要;威胁;恐吓;', 'He said army officers had threatened to destroy the town...', '他说陆军军官们曾威胁要摧毁那座小镇。');
INSERT INTO `words` VALUES (9527, 'threatening', 'ˈθretnɪŋ', 'adj.', '威胁的;恐吓的;阴沉沉的;', 'The police could have charged them with threatening behaviour...', '警方本可指控他们恐吓。');
INSERT INTO `words` VALUES (9528, 'three', 'θriː', 'num.', '三;', 'We waited three months before going back to see the specialist.', '我们等了 3 个月才回去见那位专家。');
INSERT INTO `words` VALUES (9529, 'three-dimensional', 'ˌθriː daɪˈmenʃənl', 'adj.', '三维的;立体的;三度空间的;', '...a three-dimensional model.', '三维模型');
INSERT INTO `words` VALUES (9530, 'thresh', 'θreʃ', 'v.', '(用机器)使脱粒;(尤指旧时手持工具)打(稻、麦等);(使)剧烈扭动，翻滚;', 'The corn was still sown, cut and threshed as it was a hundred years ago.', '玉米的播种、收割、脱粒方法还和一百年前一样。');
INSERT INTO `words` VALUES (9531, 'threshold', 'ˈθreʃhəʊld', 'n.', '门槛;门口;阈;', 'He stopped at the threshold of the bedroom...', '他在卧室门口停住了。');
INSERT INTO `words` VALUES (9532, 'thrice', 'θraɪs', 'adv.', '三次;三倍;', 'They should think not twice, but thrice, before ignoring such advice...', '对于这样的建议，他们应该反复考虑再决定是否拒绝。');
INSERT INTO `words` VALUES (9533, 'thrift', 'θrɪft', 'n.', '节约;节俭;海石竹(海边野生植物，花鲜艳，呈粉红色);', 'They were rightly praised for their thrift and enterprise.', '他们勤俭节约，积极进取，得到了当之无愧的赞扬。');
INSERT INTO `words` VALUES (9534, 'thrifty', 'ˈθrɪfti', 'adj.', '节约的;节俭的;', 'My mother taught me to be thrifty.', '我妈妈教我生活要节俭。');
INSERT INTO `words` VALUES (9535, 'thrill', 'θrɪl', 'n.', '震颤感;兴奋感;兴奋;', 'I can remember the thrill of not knowing what I would get on Christmas morning...', '我还记得自己不知道圣诞节早晨会得到什么礼物时内心有多么激动。');
INSERT INTO `words` VALUES (9536, 'thriller', 'ˈθrɪlə(r)', 'n.', '(尤指关于罪案或间谍的)惊险小说(或戏剧、电影);', '...a tense psychological thriller.', '扣人心弦的心理惊悚片');
INSERT INTO `words` VALUES (9537, 'thrilling', 'ˈθrɪlɪŋ', 'adj.', '惊险的;紧张的;扣人心弦的;', 'Our wildlife trips offer a thrilling encounter with wildlife in its natural state.', '野生动物园之旅使我们得以接触自然状态下的野生动物，让人感觉很刺激。');
INSERT INTO `words` VALUES (9538, 'thrive', 'θraɪv', 'v.', '兴旺发达;繁荣;蓬勃发展;', 'Today his company continues to thrive...', '今天，他的公司继续蓬勃发展。');
INSERT INTO `words` VALUES (9539, 'thriving', 'ˈθraɪvɪŋ', 'v.', '兴旺发达;繁荣;蓬勃发展;', 'Today his company continues to thrive...', '今天，他的公司继续蓬勃发展。');
INSERT INTO `words` VALUES (9540, 'throat', 'θrəʊt', 'n.', '咽喉;喉咙;颈前部;', 'She had a sore throat...', '她嗓子疼。');
INSERT INTO `words` VALUES (9541, 'throb', 'θrɒb', 'v.', '(有规律地)抽动，抽痛;(强烈有节奏地)跳动，搏动，震响;', 'His head throbbed...', '他的头阵阵作痛。');
INSERT INTO `words` VALUES (9542, 'throbbing', 'ˈθrɒbɪŋ', 'v.', '(有规律地)抽动，抽痛;(强烈有节奏地)跳动，搏动，震响;', 'His head throbbed...', '他的头阵阵作痛。');
INSERT INTO `words` VALUES (9543, 'thrombosis', 'θrɒmˈbəʊsɪs', 'n.', '血栓形成;', 'It is generally accepted that thinning of the blood reduces the chances of thrombosis.', '一般认为，稀释血液可降低血栓形成的可能性。');
INSERT INTO `words` VALUES (9544, 'throng', 'θrɒŋ', 'n.', '聚集的人群;一大群人;', 'An official pushed through the throng.', '一名官员费力地挤过人群。');
INSERT INTO `words` VALUES (9545, 'throttle', 'ˈθrɒtl', 'v.', '使窒息;掐死;勒死;', 'The attacker then tried to throttle her with wire...', '然后袭击者企图用电线勒死她。');
INSERT INTO `words` VALUES (9546, 'throughout', 'θruːˈaʊt', 'prep.', '各处;遍及;自始至终;', 'The national tragedy of rival groups killing each other continued throughout 1990...', '敌对派别相互残杀的国家悲剧 1990 年全年都在上演。');
INSERT INTO `words` VALUES (9547, 'throughput', 'ˈθruːpʊt', 'n.', '(某一时期内的)生产量，接待人数;', '...technologies which will allow us to get much higher throughput...', '能让我们大幅提高工作效率的技术');
INSERT INTO `words` VALUES (9548, 'throw', 'θrəʊ', 'v.', '投;掷;抛;', 'He spent hours throwing a tennis ball against a wall...', '他好几个小时都对着一堵墙掷网球。');
INSERT INTO `words` VALUES (9549, 'throwaway', 'ˈθrəʊəweɪ', 'adj.', '脱口而出的;顺嘴说出的;开玩笑的;', 'Now they are producing throwaway razors.', '他们正在生产一次性剃须刀。');
INSERT INTO `words` VALUES (9550, 'thrust', 'θrʌst', 'v.', '猛推;冲;搡;', 'They thrust him into the back of a jeep...', '他们把他硬塞进吉普车后座。');
INSERT INTO `words` VALUES (9551, 'thrusting', 'ˈθrʌstɪŋ', 'v.', '猛推;冲;搡;', 'They thrust him into the back of a jeep...', '他们把他硬塞进吉普车后座。');
INSERT INTO `words` VALUES (9552, 'thud', 'θʌd', 'n.', '砰的一声;扑通一声;', 'She tripped and fell with a sickening thud...', '她绊了一下，重重地摔倒在地。');
INSERT INTO `words` VALUES (9553, 'thug', 'θʌɡ', 'n.', '恶棍;暴徒;罪犯;', '...the cowardly thugs who mug old people.', '抢劫老人的胆小恶棍');
INSERT INTO `words` VALUES (9554, 'thumb', 'θʌm', 'n.', '拇指;(手套的)拇指部分;', 'She bit the tip of her left thumb, not looking at me.', '她咬着左手的拇指尖，眼睛也不看我。');
INSERT INTO `words` VALUES (9555, 'thump', 'θʌmp', 'v.', '重击;狠打;(尤指用拳)捶击;', 'He thumped my shoulder affectionately, nearly knocking me over...', '他亲热地捶了一下我的肩膀，差点把我打倒。');
INSERT INTO `words` VALUES (9556, 'thumping', 'ˈθʌmpɪŋ', 'adj.', '很大的;巨大的;', 'The Right has a thumping majority...', '右派占绝大多数。');
INSERT INTO `words` VALUES (9557, 'thunder', 'ˈθʌndə(r)', 'n.', '雷;雷声;雷鸣般的响声;', 'There was frequent thunder and lightning, and torrential rain.', '雷电交加，大雨倾盆。');
INSERT INTO `words` VALUES (9558, 'thunderous', 'ˈθʌndərəs', 'adj.', '雷鸣般的;声音很大的;怒容满面的;', 'The audience responded with thunderous applause.', '观众报以雷鸣般的掌声。');
INSERT INTO `words` VALUES (9559, 'thursday', 'ˈθɜːzdeɪ', 'n.', '星期四;', 'On Thursday Barrett invited me for a drink...', '星期四，巴雷特约我去喝两杯。');
INSERT INTO `words` VALUES (9560, 'thus', 'ðʌs', 'adv.', '以此方式;如此;这样;', 'Neither of them thought of turning on the lunch-time news. Thus Caroline didn\'t hear of John\'s death until Peter telephoned...', '两个人谁也没有想到打开电视看午间新闻，所以直到彼得打来电话，卡罗琳才得知约翰的死讯。');
INSERT INTO `words` VALUES (9561, 'thwart', 'θwɔːt', 'v.', '阻止;阻挠;对…构成阻力;', 'The accounting firm deliberately destroyed documents to thwart government investigators...', '会计事务所故意毁坏文件，阻挠政府调查工作。');
INSERT INTO `words` VALUES (9562, 'thy', 'ðaɪ', 'det.', '(用作第二人称单数所有格形式)你的;', 'Honor thy father and thy mother.', '尊敬乃父乃母。');
INSERT INTO `words` VALUES (9563, 'thyself', 'ðaɪˈself', 'pron.', '(用作第二人称单数反身代词)你自己;', 'Love thy neighbour as thyself.', '爱邻如爱己。');
INSERT INTO `words` VALUES (9564, 'tick', 'tɪk', 'v.', '发出滴答声;滴答地走时;标记号;', 'His exercise books were full of well deserved red ticks...', '他的练习本上尽是些红钩钩，都是他应得的。');
INSERT INTO `words` VALUES (9565, 'ticket', 'ˈtɪkɪt', 'n.', '票;券;车票;', 'I queued for two hours to get a ticket to see the football game...', '我排了两个小时的队，就是为了买张足球赛的票。');
INSERT INTO `words` VALUES (9566, 'ticking', 'ˈtɪkɪŋ', 'n.', '(尤指做床垫和枕芯套的)结实条纹棉布;', 'His exercise books were full of well deserved red ticks...', '他的练习本上尽是些红钩钩，都是他应得的。');
INSERT INTO `words` VALUES (9567, 'tickle', 'ˈtɪkl', 'v.', '呵痒;胳肢;(使)发痒;', 'I was tickling him, and he was laughing and giggling.', '我在胳肢他，他哈哈哈、咯咯咯地笑个不停。');
INSERT INTO `words` VALUES (9568, 'ticklish', 'ˈtɪklɪʃ', 'adj.', '怕痒的;易痒的;难对付的;', 'So car makers are faced with the ticklish problem of how to project products at new buyers.', '因此，汽车制造商面临着怎样向新顾客树立产品形象的大难题。');
INSERT INTO `words` VALUES (9569, 'tidal', 'ˈtaɪdl', 'adj.', '潮汐的;有潮的;', 'The tidal stream or current gradually decreases in the shallows...', '浅滩上的潮水逐渐退去。');
INSERT INTO `words` VALUES (9570, 'tide', 'taɪd', 'n.', '潮;潮汐;潮水;', 'The tide was at its highest...', '潮水正处于最高位。');
INSERT INTO `words` VALUES (9571, 'tidily', '', 'adv.', '整洁地，整齐地;', 'Having a tidy desk can seem impossible if you have a busy, demanding job...', '如果工作忙、难度大，要想保持桌面整洁似乎不太可能。');
INSERT INTO `words` VALUES (9572, 'tidy', 'ˈtaɪdi', 'adj.', '整洁的;整齐的;井然有序的;', 'Having a tidy desk can seem impossible if you have a busy, demanding job...', '如果工作忙、难度大，要想保持桌面整洁似乎不太可能。');
INSERT INTO `words` VALUES (9573, 'tie', 'taɪ', 'v.', '(用线、绳等)系，拴，绑，捆，束;将…系在…上;束紧;', 'He tied the ends of the plastic bag together...', '他把塑料袋的两头系在了一起。');
INSERT INTO `words` VALUES (9574, 'tight', 'taɪt', 'adj.', '牢固的;紧的;不松动的;', 'She walked off the plane in a miniskirt and tight top...', '她身穿迷你裙和紧身上衣走下了飞机。');
INSERT INTO `words` VALUES (9575, 'tighten', 'ˈtaɪtn', 'v.', '(使)变紧，更加牢固;使更加严格;加强;', 'Luke answered by tightening his grip on her shoulder...', '卢克的回应就是把她的肩头抓得更紧了。');
INSERT INTO `words` VALUES (9576, 'tightly', 'ˈtaɪtli', 'adv.', '紧紧地;牢固地;紧密地;', 'She walked off the plane in a miniskirt and tight top...', '她身穿迷你裙和紧身上衣走下了飞机。');
INSERT INTO `words` VALUES (9577, 'tile', 'taɪl', 'n.', '(贴墙或铺地用的)瓷砖，地砖，小方地毯，片状材料;(铺屋顶的)瓦，瓦片;(棋盘游戏的)棋子;', 'Amy\'s shoes squeaked on the tiles as she walked down the corridor...', '埃米走过走廊时，鞋子踩在地砖上嘎吱作响。');
INSERT INTO `words` VALUES (9578, 'till', 'tɪl', 'conj./prep.', '直到;直到...为止;', 'They had to wait till Monday to ring the bank manager...', '他们必须等到周一才能给银行经理打电话。');
INSERT INTO `words` VALUES (9579, 'tilt', 'tɪlt', 'v.', '(使)倾斜，倾侧;使倾向于;使向…倾斜;', 'She tilted the mirror and began to comb her hair...', '她斜过镜子开始梳理头发。');
INSERT INTO `words` VALUES (9580, 'timber', 'ˈtɪmbə(r)', 'n.', '(用于建筑或制作物品的)树木，林木;用材林;(建筑等用的)木材，木料;', 'These Severn Valley woods have been exploited for timber since Saxon times.', '从撒克逊时代起就开始采伐塞文山谷的这些树林获取木料。');
INSERT INTO `words` VALUES (9581, 'time', 'taɪm', 'n.', '(以分钟、小时、天等计量的)时间;(钟表所显示的)时间，钟点，时刻;(世界某一地区所计量的)时，时间;', '...a two-week period of time...', '两周的时间');
INSERT INTO `words` VALUES (9582, 'timely', 'ˈtaɪmli', 'adj.', '及时的;适时的;', 'The recent outbreaks of cholera are a timely reminder that this disease is still a serious health hazard...', '最近霍乱的爆发及时提醒了人们该疾病仍然会严重危害健康。');
INSERT INTO `words` VALUES (9583, 'timer', 'ˈtaɪmə(r)', 'n.', '时计;计时器;跑表;', '...electronic timers that automatically switch on the lights when it gets dark.', '光线一暗就自动开灯的电子定时器');
INSERT INTO `words` VALUES (9584, 'times', 'taɪmz', '', '乘以;(用于比较)倍;', '...a two-week period of time...', '两周的时间');
INSERT INTO `words` VALUES (9585, 'timetable', 'ˈtaɪmteɪbl', 'n.', '时间表;时刻表;预定计划;', 'The timetable was hopelessly optimistic…', '时间表制定得过于乐观。');
INSERT INTO `words` VALUES (9586, 'timid', 'ˈtɪmɪd', 'adj.', '羞怯的;胆怯的;缺乏勇气的;', 'A timid child, Isabella had learned obedience at an early age.', '伊莎贝拉是一个羞怯的孩子，很小就学会了顺从。');
INSERT INTO `words` VALUES (9587, 'timidity', 'tɪ\'mɪdətɪ', 'n.', '胆小;', 'A timid child, Isabella had learned obedience at an early age.', '伊莎贝拉是一个羞怯的孩子，很小就学会了顺从。');
INSERT INTO `words` VALUES (9588, 'timing', 'ˈtaɪmɪŋ', 'n.', '定时;时间的选择;(事情发生或计划安排的)特定时间;', 'His photo is a wonderful happy moment caught with perfect timing...', '他拍的照片时机掌握完美，记录下了动人的快乐瞬间。');
INSERT INTO `words` VALUES (9589, 'tin', 'tɪn', 'n.', '锡;罐;罐头盒;', '...a factory that turns scrap metal into tin cans.', '把废弃金属制成锡罐的工厂');
INSERT INTO `words` VALUES (9590, 'tinge', 'tɪndʒ', 'v.', '(轻微地)给…着色，给…染色;使略带…感情(或性质);', 'His skin had an unhealthy greyish tinge...', '他的皮肤有种不健康的淡灰色。');
INSERT INTO `words` VALUES (9591, 'tiny', 'ˈtaɪni', 'adj.', '极小的;微小的;微量的;', 'The living room is tiny...', '起居室非常小。');
INSERT INTO `words` VALUES (9592, 'tip', 'tɪp', 'n.', '尖端;尖儿;端;', 'The sleeves covered his hands to the tips of his fingers...', '袖子遮住了他的手，一直盖到指尖。');
INSERT INTO `words` VALUES (9593, 'tiptoe', 'ˈtɪptəʊ', 'n.', '脚尖;趾尖;', 'She slipped out of bed and tiptoed to the window.', '她溜下床，蹑手蹑脚地走到窗户旁边。');
INSERT INTO `words` VALUES (9594, 'tire', 'ˈtaɪə(r)', 'v.', '(使)疲劳，疲倦，困倦;', 'If driving tires you, take the train...', '如果开车觉得累，就乘火车吧。');
INSERT INTO `words` VALUES (9595, 'tired', 'ˈtaɪəd', 'adj.', '疲倦的;疲劳的;困倦的;', 'Michael is tired and he has to rest after his long trip.', '迈克尔累了，他在长途旅行以后必须休息。');
INSERT INTO `words` VALUES (9596, 'tires', 'ˈtaɪəz', 'v.', '(使)疲劳，疲倦，困倦;', 'If driving tires you, take the train...', '如果开车觉得累，就乘火车吧。');
INSERT INTO `words` VALUES (9597, 'tiresome', 'ˈtaɪəsəm', 'adj.', '讨厌的;令人厌烦的;烦人的;', '...the tiresome old lady next door...', '隔壁讨人嫌的老太太');
INSERT INTO `words` VALUES (9598, 'tissue', 'ˈtɪʃuː', 'n.', '(人、动植物细胞的)组织;(尤指用作手帕的)纸巾，手巾纸;(用于包装易碎物品的)薄纸，绵纸;', 'As we age we lose muscle tissue...', '肌肉组织会随着我们日趋衰老而萎缩。');
INSERT INTO `words` VALUES (9599, 'title', 'ˈtaɪtl', 'n.', '(书籍、诗歌、图画、乐曲等的)名称，标题，题目;(书刊的)一种，一本;(人名前表示地位、职业、婚否等的)称号，头衔，职称，称谓;', '\'Patience and Sarah\' was first published in 1969 under the title \'A Place for Us\'.', '《佩兴丝与萨拉》一书最初于1969年出版时以《属于我们的地方》为名。');
INSERT INTO `words` VALUES (9600, 'toast', 'təʊst', 'n.', '烤面包片;吐司;干杯;', '...a piece of toast.', '一片吐司');
INSERT INTO `words` VALUES (9601, 'tobacco', 'təˈbækəʊ', 'n.', '烟叶;烟草;', 'Try to do without tobacco and alcohol...', '尽量不要抽烟喝酒。');
INSERT INTO `words` VALUES (9602, 'today', 'təˈdeɪ', 'adv.', '在今天;在今日;现在;', 'How are you feeling today?...', '你今天感觉怎么样？');
INSERT INTO `words` VALUES (9603, 'toddle', 'ˈtɒdl', 'v.', '(幼儿学步时)摇摇摆摆地走，蹒跚行走;步行;去;', '...once your baby starts toddling...', '一旦你的宝宝开始蹒跚学步');
INSERT INTO `words` VALUES (9604, 'together', 'təˈɡeðə(r)', 'adv.', '在一起;共同;以使接触(或相结合);', 'We went on long bicycle rides together...', '我们一起骑自行车长途旅行。');
INSERT INTO `words` VALUES (9605, 'toil', 'tɔɪl', 'v.', '(长时间)苦干，辛勤劳作;艰难缓慢地移动;跋涉;', 'People who toiled in dim, dank factories were too exhausted to enjoy their family life...', '在昏暗潮湿的工厂辛苦劳作的人们累得无力再去享受家庭生活。');
INSERT INTO `words` VALUES (9606, 'toilet', 'ˈtɔɪlət', 'n.', '坐便器;抽水马桶;厕所;', 'She made Tina flush the pills down the toilet.', '她让蒂娜把药丸扔进马桶里冲走。');
INSERT INTO `words` VALUES (9607, 'token', 'ˈtəʊkən', 'n.', '(用以启动某些机器或用作支付方式的)代币，专用辅币;代价券;赠券;', 'The announcement was welcomed as a step in the right direction, but was widely seen as a token gesture...', '该公告被认为是向正确方向迈进的一步，因而受到欢迎，但也被普遍看作是一种象征性的姿态。');
INSERT INTO `words` VALUES (9608, 'tolerable', 'ˈtɒlərəbl', 'adj.', '尚好的;过得去的;还可以的;', 'The levels of tolerable pain vary greatly from individual to individual…', '对疼痛的耐受程度因人而异，差别很大。');
INSERT INTO `words` VALUES (9609, 'tolerance', 'ˈtɒlərəns', 'n.', '忍受;容忍;宽容;', '...his tolerance and understanding of diverse human nature.', '他对人性多面性的宽容与理解');
INSERT INTO `words` VALUES (9610, 'tolerant', 'ˈtɒlərənt', 'adj.', '忍受的;容忍的;宽容的;', 'They need to be tolerant of different points of view...', '他们需要容忍不同的观点。');
INSERT INTO `words` VALUES (9611, 'tolerate', 'ˈtɒləreɪt', 'v.', '容许，允许(不同意或不喜欢的事物);忍受;容忍;', 'She can no longer tolerate the position that she\'s in...', '她再也受不了自己的处境了。');
INSERT INTO `words` VALUES (9612, 'toll', 'təʊl', 'n.', '(道路、桥梁的)通行费;(战争、灾难等造成的)毁坏;伤亡人数;', 'Church bells tolled and black flags fluttered...', '教堂丧钟敲响，黑色的旗帜飘动。');
INSERT INTO `words` VALUES (9613, 'tomorrow', 'təˈmɒrəʊ', 'adv.', '在明天;在明日;', 'Bye, see you tomorrow...', '再见，明天见。');
INSERT INTO `words` VALUES (9614, 'ton', 'tʌn', 'n.', '吨(英国为2 240磅，即长吨;美国为2 000磅，即短吨);注册吨，吨位(船舶大小的计量单位，1吨等于100立方英尺);', 'Hundreds of tons of oil spilled into the sea...', '数百吨石油溢出流入大海。');
INSERT INTO `words` VALUES (9615, 'tone', 'təʊn', 'n.', '语气;口气;腔调;', 'Cross could hear him speaking in low tones to Sarah.', '克罗斯听得到他对萨拉低声说话。');
INSERT INTO `words` VALUES (9616, 'toneless', 'ˈtəʊnləs', 'adj.', '单调的;呆板的;沉闷的;', '\'What shall we do?\' Milton said again in a toneless voice.', '“我们该怎么办？”米尔顿又用平板而单调的声音问道。');
INSERT INTO `words` VALUES (9617, 'tongue', 'tʌŋ', 'n.', '舌;舌头;口条;', 'I walked over to the mirror and stuck my tongue out...', '我走到镜子前面伸出舌头。');
INSERT INTO `words` VALUES (9618, 'tonic', 'ˈtɒnɪk', 'n.', '奎宁水，汤力水(一种味微苦、常加于烈性酒中的有气饮料);补药;滋补品;', 'Keeler sipped at his gin and tonic.', '基勒小口抿着他加了奎宁水的杜松子酒。');
INSERT INTO `words` VALUES (9619, 'tonight', 'təˈnaɪt', 'adv.', '在今夜;在今晚;', 'I\'m at home tonight...', '我今晚在家。');
INSERT INTO `words` VALUES (9620, 'tonne', 'tʌn', 'n.', '公吨(等于1 000公斤);', '...65.5 million tonnes of coal...', '6,550 万公吨煤');
INSERT INTO `words` VALUES (9621, 'tool', 'tuːl', 'n.', '工具;(有助于做工或完成某事的)用具，器具，手段，方法;受人利用的人;', 'I find the best tool for the purpose is a pair of shears.', '我发现做这件事最得力的工具是一把大剪刀。');
INSERT INTO `words` VALUES (9622, 'tooth', 'tuːθ', 'n.', '牙;齿;齿状部分;', 'She had very pretty straight teeth...', '她有一口漂亮整齐的牙齿。');
INSERT INTO `words` VALUES (9623, 'top', 'tɒp', 'n.', '顶;顶部;顶端;', 'I waited at the top of the stairs.', '我在楼梯顶端等着。');
INSERT INTO `words` VALUES (9624, 'topic', 'ˈtɒpɪk', 'n.', '话题;题目;标题;', 'The weather is a constant topic of conversation in Britain…', '在英国，天气是交谈中永恒的话题。');
INSERT INTO `words` VALUES (9625, 'torrent', 'ˈtɒrənt', 'n.', '急流;激流;湍流;', 'Torrents of water gushed into the reservoir...', '急流涌入了水库。');
INSERT INTO `words` VALUES (9626, 'torture', 'ˈtɔːtʃə(r)', 'n.', '拷打;拷问;酷刑;', 'French police are convinced that she was tortured and killed...', '法国警方确信她是被拷打致死。');
INSERT INTO `words` VALUES (9627, 'toss', 'tɒs', 'v.', '(轻轻或漫不经心地)扔，抛，掷;甩(头，以表示恼怒或不耐烦);(使)摇摆，挥动，颠簸;', 'He screwed the paper into a ball and tossed it into the fire...', '他把纸揉成一团扔进火里。');
INSERT INTO `words` VALUES (9628, 'total', 'ˈtəʊtl', 'adj.', '总的;总计的;全体的;', 'The companies have a total of 1,776 employees.', '这些公司总计有 1,776 名员工。');
INSERT INTO `words` VALUES (9629, 'totally', 'ˈtəʊtəli', 'adv.', '完全;全部地;整个地;', 'The companies have a total of 1,776 employees.', '这些公司总计有 1,776 名员工。');
INSERT INTO `words` VALUES (9630, 'touch', 'tʌtʃ', 'v.', '触摸;碰;接触;', 'Her tiny hands gently touched my face...', '她的小手轻轻触摸我的脸庞。');
INSERT INTO `words` VALUES (9631, 'touching', 'ˈtʌtʃɪŋ', 'adj.', '令人同情的;感人的;动人的;', 'Her story is the touching tale of a wife who stood by the husband she loved...', '她的经历是一个妻子坚定地支持她深爱着的丈夫的感人故事。');
INSERT INTO `words` VALUES (9632, 'touchy', 'ˈtʌtʃi', 'adj.', '易烦恼;易生气;易怒;', 'She is very touchy about her past...', '她对自己的过去非常敏感。');
INSERT INTO `words` VALUES (9633, 'tough', 'tʌf', 'adj.', '艰苦的;艰难的;棘手的;', 'He built up a reputation as a tough businessman...', '他树立了铁面生意人的名声。');
INSERT INTO `words` VALUES (9634, 'tour', 'tʊə(r)', 'n.', '旅行;旅游;游览;', 'The band are currently on a two-month tour of Europe...', '乐队目前正在欧洲进行为期两个月的巡回演出。');
INSERT INTO `words` VALUES (9635, 'tourism', 'ˈtʊərɪzəm', 'n.', '旅游业;观光业;', 'Tourism is vital for the Spanish economy.', '旅游业对西班牙经济至关重要。');
INSERT INTO `words` VALUES (9636, 'tourist', 'ˈtʊərɪst', 'n.', '旅游者;观光者;游客;', '...foreign tourists...', '外国游客');
INSERT INTO `words` VALUES (9637, 'tout', 'taʊt', 'v.', '标榜;吹捧;吹嘘;', 'It has the trappings of an election campaign in the United States, with slick television ads touting the candidates.', '它具有美国竞选活动的典型特征，即通过华而不实的电视广告来吹嘘候选人。');
INSERT INTO `words` VALUES (9638, 'tow', 'təʊ', 'v.', '(用绳索)拖，拉，牵引，拽;', 'He had been using the vehicle to tow his work trailer...', '他一直用这辆车牵引他的活动工作室。');
INSERT INTO `words` VALUES (9639, 'towards', 'təˈwɔːdz', 'prep.', '向;朝;对着;', 'Caroline leant across the table towards him...', '卡罗琳隔着桌子俯身向他靠过来。');
INSERT INTO `words` VALUES (9640, 'tower', 'ˈtaʊə(r)', 'n.', '塔;建筑物的塔形部分;(尤指教堂或城堡的)塔楼;', '...an eleventh century castle with 120-foot high towers.', '带有 120 英尺高塔的 11 世纪城堡');
INSERT INTO `words` VALUES (9641, 'town', 'taʊn', 'n.', '镇;市镇;集镇;', '...Saturday night in the small town of Braintree, Essex...', '埃塞克斯郡布伦特里小镇的周六之夜');
INSERT INTO `words` VALUES (9642, 'toxic', 'ˈtɒksɪk', 'adj.', '有毒的;引起中毒的;', '...the cost of cleaning up toxic waste...', '清除有毒废弃物的成本');
INSERT INTO `words` VALUES (9643, 'toxin', 'ˈtɒksɪn', 'n.', '毒素(尤指生物体自然产生的毒物);', 'Experts have linked this condition to a build-up of toxins in the body…', '专家已把这一病症与体内毒素的积累联系起来。');
INSERT INTO `words` VALUES (9644, 'toy', 'tɔɪ', 'n.', '玩具;玩物;玩意儿;', 'He was really too old for children\'s toys.', '他已经长大了，实在不再适合玩儿童玩具了。');
INSERT INTO `words` VALUES (9645, 'trace', 'treɪs', 'v.', '查出;找到;发现;', 'The exhibition traces the history of graphic design in America from the 19th century to the present...', '展览追溯了美国从19世纪至今的平面设计历史。');
INSERT INTO `words` VALUES (9646, 'track', 'træk', 'n.', '(人踩出的)小道，小径;(人、动物或车辆留下的)足迹，踪迹;车辙;', 'We set off once more, over a rough mountain track.', '我们沿着一条崎岖的山间小路再度出发。');
INSERT INTO `words` VALUES (9647, 'tracking', 'ˈtrækɪŋ', 'v.', '跟踪;追踪;(尤指用特殊电子设备)跟踪;', 'We set off once more, over a rough mountain track.', '我们沿着一条崎岖的山间小路再度出发。');
INSERT INTO `words` VALUES (9648, 'tract', 'trækt', 'n.', '(连通身体组织或器官的)道，束;大片土地;地带;', 'A vast tract of land is ready for development...', '一大片广阔的土地可待开发。');
INSERT INTO `words` VALUES (9649, 'traction', 'ˈtrækʃn', 'n.', '牵引;拖拉;牵引力;', 'Is there an alternative to traction for a broken leg?…', '对于腿部骨折有什么方法替代牵引治疗吗？');
INSERT INTO `words` VALUES (9650, 'trade', 'treɪd', 'n.', '贸易;买卖;商业;', 'The ministry had direct control over every aspect of foreign trade.', '该政府部门直接掌控着外贸的各个方面。');
INSERT INTO `words` VALUES (9651, 'trader', 'ˈtreɪdə(r)', 'n.', '商人;经商者;买卖人;', 'Market traders display an exotic selection of the island\'s produce.', '集市上的商贩摆出了一系列具有异域风味的该岛农产品以供选择。');
INSERT INTO `words` VALUES (9652, 'tradition', 'trəˈdɪʃn', 'n.', '传统;传统的信仰(或风俗);', '...the rich traditions of Afro-Cuban music, and dance...', '非裔古巴人丰富的音乐、舞蹈传统');
INSERT INTO `words` VALUES (9653, 'traditional', 'trəˈdɪʃənl', 'adj.', '传统的;习俗的;惯例的;', '...traditional teaching methods.', '传统教学方法');
INSERT INTO `words` VALUES (9654, 'traffic', 'ˈtræfɪk', 'n.', '路上行驶的车辆;交通;(沿固定路线的)航行，行驶，飞行;', 'There was heavy traffic on the roads...', '道路上车流拥挤。');
INSERT INTO `words` VALUES (9655, 'tragedy', 'ˈtrædʒədi', 'n.', '悲惨的事;不幸;灾难;', 'They have suffered an enormous personal tragedy...', '他们遭逢了巨大的个人不幸。');
INSERT INTO `words` VALUES (9656, 'tragic', 'ˈtrædʒɪk', 'adj.', '悲惨的;悲痛的;可悲的;', 'It was just a tragic accident...', '它只是场不幸的事故。');
INSERT INTO `words` VALUES (9657, 'trail', 'treɪl', 'n.', '(长串的)痕迹，踪迹，足迹;(尤指打猎时跟踪的)踪迹，臭迹;(乡间的)小路，小径;', 'He was following a broad trail through the trees.', '他沿着林间的一条阔路行进。');
INSERT INTO `words` VALUES (9658, 'trainee', 'ˌtreɪˈniː', 'n.', '接受培训者;实习生;见习生;', 'He is a 24-year-old trainee reporter...', '他 24 岁，是一名实习记者。');
INSERT INTO `words` VALUES (9659, 'training', 'ˈtreɪnɪŋ', 'n.', '训练;培训;(为参加体育比赛而进行的)训练，锻炼;', 'He called for much higher spending on education and training...', '他呼吁在教育和培训方面加大投入。');
INSERT INTO `words` VALUES (9660, 'trait', 'treɪt', 'n.', '(人的个性的)特征，特性，特点;', 'The study found that some alcoholics had clear personality traits showing up early in childhood...', '研究发现一些酗酒者在童年早期就显现出鲜明的人格特征。');
INSERT INTO `words` VALUES (9661, 'traitor', 'ˈtreɪtə(r)', 'n.', '背叛者;叛徒;卖国贼;', 'Some say he\'s a traitor to the working class.', '一些人说他是工人阶级的叛徒。');
INSERT INTO `words` VALUES (9662, 'tram', 'træm', 'n.', '有轨电车;', 'You can get to the beach easily from the centre of town by tram.', '你从市中心乘有轨电车到海滩会很方便。');
INSERT INTO `words` VALUES (9663, 'trample', 'ˈtræmpl', 'v.', '踩碎;踩伤;践踏;', 'They say loggers are destroying rain forests and trampling on the rights of natives...', '他们说伐木工人正在毁坏雨林，践踏土著居民的权利。');
INSERT INTO `words` VALUES (9664, 'tranquil', 'ˈtræŋkwɪl', 'adj.', '安静的;平静的;安宁的;', 'The tranquil atmosphere of The Connaught allows guests to feel totally at home...', '康诺特酒店的宁静氛围让客人完全找到了宾至如归的感觉。');
INSERT INTO `words` VALUES (9665, 'transact', 'trænˈzækt', 'v.', '(与人或组织)做业务，做交易;', 'This would free them to transact business across state lines...', '这将使他们能够跨越州界进行交易。');
INSERT INTO `words` VALUES (9666, 'transcend', 'trænˈsend', 'v.', '超出，超越(通常的界限);', '...issues like European union that transcend party loyalty.', '诸如欧洲结盟这样超越党派忠诚的议题');
INSERT INTO `words` VALUES (9667, 'transcribe', 'trænˈskraɪb', 'v.', '记录;抄录;把…转成(另一种书写形式);', 'She is transcribing, from his dictation, the diaries of Simon Forman...', '她正根据他的口述记录西蒙·福曼的日记。');
INSERT INTO `words` VALUES (9668, 'transcript', 'ˈtrænskrɪpt', 'n.', '抄本;誊本;打印本;', 'They wouldn\'t let me have a transcript of the interview.', '他们拒绝给我一份采访的文字整理稿。');
INSERT INTO `words` VALUES (9669, 'transferable', 'trænsˈfɜːrəbl', 'adj.', '可转移的;可调动的;可转让的;', 'Use the transferable skills acquired from your previous working background…', '先前工作中学到的技能能为现在所用的就多加利用。');
INSERT INTO `words` VALUES (9670, 'transform', 'trænsˈfɔːm', 'v.', '使改变形态;使改变外观(或性质);使改观;', 'Your metabolic rate is the speed at which your body transforms food into energy...', '新陈代谢率是身体把食物转换为能量的速度。');
INSERT INTO `words` VALUES (9671, 'transformation', 'ˌtrænsfəˈmeɪʃn', 'n.', '(彻底的)变化，改观，转变，改革;(用于南非)民主改革;', 'Your metabolic rate is the speed at which your body transforms food into energy...', '新陈代谢率是身体把食物转换为能量的速度。');
INSERT INTO `words` VALUES (9672, 'transgress', 'trænzˈɡres', 'v.', '越轨;违背(道德);违犯(法律);', 'If a politician transgresses, that is not the fault of the media.', '如果政客行为失检，那可不是媒体的责任。');
INSERT INTO `words` VALUES (9673, 'transient', 'ˈtrænziənt', 'adj.', '短暂的;转瞬即逝的;倏忽;', '...the transient nature of high fashion...', '顶级时装的瞬息万变性');
INSERT INTO `words` VALUES (9674, 'transit', 'ˈtrænzɪt', 'n.', '运输;运送;搬运;', 'During their talks, the two presidents discussed the transit of goods between the two countries.', '会谈中，两位总统讨论了两国间货物运输的问题。');
INSERT INTO `words` VALUES (9675, 'transition', 'trænˈzɪʃn', 'n.', '过渡;转变;变革;', 'The transition to a multi-party democracy is proving to be difficult.', '事实证明，向多党民主制的转型非常艰难。');
INSERT INTO `words` VALUES (9676, 'transitional', 'trænˈzɪʃənl', 'adj.', '(时期)过渡的;过渡性的;过渡时期的;', '...a transitional period following more than a decade of civil war...', '十多年的内战结束之后的过渡时期');
INSERT INTO `words` VALUES (9677, 'translate', 'trænzˈleɪt', 'v.', '翻译;译;被翻译;', 'Only a small number of Kadare\'s books have been translated into English...', '卡达雷的书仅有一小部分译成了英文。');
INSERT INTO `words` VALUES (9678, 'translation', 'trænzˈleɪʃn', 'n.', '翻译;译;译文;', '...MacNiece\'s excellent English translation of \'Faust\'...', '麦克尼斯精彩的《浮士德》英文译本');
INSERT INTO `words` VALUES (9679, 'translucent', 'trænzˈluːsnt', 'adj.', '半透明的;', 'The building is roofed entirely with translucent corrugated plastic.', '这座建筑完全用半透明瓦楞塑料封顶。');
INSERT INTO `words` VALUES (9680, 'transmission', 'trænzˈmɪʃn', 'n.', '传送;传递;传达;', 'Heterosexual contact is responsible for the bulk of HIV transmission.', '艾滋病病毒大半是经异性性接触传播的。');
INSERT INTO `words` VALUES (9681, 'transmit', 'trænzˈmɪt', 'v.', '传送;输送;发射;', 'The game was transmitted live in Spain and Italy...', '这场比赛在西班牙和意大利进行了现场直播。');
INSERT INTO `words` VALUES (9682, 'transparent', 'trænsˈpærənt', 'adj.', '透明的;清澈的;易识破的;', '...a sheet of transparent coloured plastic...', '透明有色塑料板');
INSERT INTO `words` VALUES (9683, 'transportation', 'ˌtrænspɔːˈteɪʃn', 'n.', '(旧时的)流放;运输;运输系统;', 'The company will provide transportation.', '公司会提供交通工具。');
INSERT INTO `words` VALUES (9684, 'transpose', 'trænˈspəʊz', 'v.', '使掉换顺序;使转移;使换位;', 'Genetic engineers transpose or exchange bits of hereditary material from one organism to the next...', '遗传工程师将某一生物的小段遗传物质调换到另一生物，或与其互换。');
INSERT INTO `words` VALUES (9685, 'trash', 'træʃ', 'n.', '废物;垃圾;劣质品;', 'The yards are overgrown and cluttered with trash…', '院子里杂草丛生，垃圾成堆。');
INSERT INTO `words` VALUES (9686, 'trauma', 'ˈtrɔːmə', 'n.', '精神创伤;痛苦经历;挫折;', 'I\'d been through the trauma of losing a house...', '我经历过失去房子的痛苦。');
INSERT INTO `words` VALUES (9687, 'traverse', 'trəˈvɜːs , ˈtrævɜːs', 'v.', '横过;横越;穿过;', 'I traversed the narrow pedestrian bridge.', '我走过狭窄的人行天桥。');
INSERT INTO `words` VALUES (9688, 'tread', 'tred', 'v.', '踩;踏;践踏;', 'Oh, sorry, I didn\'t mean to tread on your foot...', '哦，对不起，我不是故意踩你脚的。');
INSERT INTO `words` VALUES (9689, 'treasure', 'ˈtreʒə(r)', 'n.', '金银财宝;珠宝;财富;', 'It was here, the buried treasure, she knew it was.', '就在这里，那些埋藏在地下的财宝，她知道就在这里。');
INSERT INTO `words` VALUES (9690, 'treasury', 'ˈtreʒəri', 'n.', '(英国、美国和其他一些国家的)财政部;(城堡等中的)金银财宝库，宝库;', 'The Treasury has long been predicting an upturn in consumer spending.', '财政部早就预测消费者支出会出现上扬。');
INSERT INTO `words` VALUES (9691, 'treat', 'triːt', 'v.', '以…态度对待;以…方式对待;把…看作;', 'Artie treated most women with indifference...', '阿蒂对大多数女人都漠不关心。');
INSERT INTO `words` VALUES (9692, 'treatise', 'ˈtriːtɪs', 'n.', '(专题)论文;', '...Locke\'s Treatise on Civil Government.', '洛克的《政府论》');
INSERT INTO `words` VALUES (9693, 'treatment', 'ˈtriːtmənt', 'n.', '治疗;疗法;诊治;', 'Many patients are not getting the medical treatment they need.', '很多病人没有得到他们需要的医治。');
INSERT INTO `words` VALUES (9694, 'treaty', 'ˈtriːti', 'n.', '(国家之间的)条约，协定;', '…the Treaty of Rome, which established the European Community.', '建立了欧洲共同体的《罗马条约》');
INSERT INTO `words` VALUES (9695, 'treble', 'ˈtrebl', 'n.', '(音乐或音响系统的)高音，高音部;童声高音;唱高音的男童歌手;', 'They will have to pay much more when rents treble in January...', '一月份房租增加两倍时，他们将被迫多付不少钱。');
INSERT INTO `words` VALUES (9696, 'tree', 'triː', 'n.', '树;树木;乔木;', 'I planted those apple trees.', '我栽了那些苹果树。');
INSERT INTO `words` VALUES (9697, 'trek', 'trek', 'n.', '长途跋涉，艰难的旅程(尤指在山区);远距离行走;', '...trekking through the jungles...', '徒步穿过丛林');
INSERT INTO `words` VALUES (9698, 'tremble', 'ˈtrembl', 'v.', '(因紧张、激动、惊慌等)颤抖，哆嗦，抖动，战栗;颤动;轻轻摇晃;', 'His mouth became dry, his eyes widened, and he began to tremble all over...', '他嘴唇发干，眼睛圆睁，全身开始颤抖起来。');
INSERT INTO `words` VALUES (9699, 'tremendous', 'trəˈmendəs', 'adj.', '巨大的;极大的;极好的;', 'I felt a tremendous pressure on my chest...', '我感到胸口堵得难受。');
INSERT INTO `words` VALUES (9700, 'trend', 'trend', 'n.', '趋势;趋向;倾向;', 'This is a growing trend.', '这是日益显著的趋势。');
INSERT INTO `words` VALUES (9701, 'trespass', 'ˈtrespəs', 'v.', '擅自进入，非法侵入(他人的土地或建筑物);做错事;', 'They were trespassing on private property...', '他们擅自闯入私人房屋。');
INSERT INTO `words` VALUES (9702, 'trial', 'ˈtraɪəl', 'n.', '(法院的)审讯，审理，审判;(对能力、质量、性能等的)试验，试用;预赛;', 'New evidence showed the police lied at the trial...', '新的证据表明警方在审讯时撒了谎。');
INSERT INTO `words` VALUES (9703, 'triangle', 'ˈtraɪæŋɡl', 'n.', '三角形;三角形物体;三角铁(打击乐器);', 'This design is in pastel colours with three rectangles and three triangles…', '这一设计由淡色的 3 个长方形和 3 个三角形构成。');
INSERT INTO `words` VALUES (9704, 'triangular', 'traɪˈæŋɡjələ(r)', 'adj.', '三角的;三角形的;涉及三人的;', '...cottages around a triangular green.', '坐落在一块三角形绿地周围的小别墅');
INSERT INTO `words` VALUES (9705, 'tribal', 'ˈtraɪbl', 'adj.', '部落的;部族的;', 'They would go back to their tribal lands.', '他们会回到自己部族的地盘上。');
INSERT INTO `words` VALUES (9706, 'tribe', 'traɪb', 'n.', '部落;(尤指同一职业的)一伙(人)，一帮(人)，一类(人);(动物或植物的)群，族;', '...three-hundred members of the Xhosa tribe.', '300 名科萨族人');
INSERT INTO `words` VALUES (9707, 'tribunal', 'traɪˈbjuːnl', 'n.', '特别法庭;裁判所;', 'His case comes before an industrial tribunal in March.', '他的讼案将于3月在劳资法庭受审。');
INSERT INTO `words` VALUES (9708, 'tributary', 'ˈtrɪbjətri', 'n.', '(流入大河或湖泊的)支流;', '...the Napo river, a tributary of the Amazon.', '纳波河，亚马孙河的一条支流');
INSERT INTO `words` VALUES (9709, 'tribute', 'ˈtrɪbjuːt', 'n.', '(尤指对死者的)致敬，颂词;悼念;致哀;', 'The song is a tribute to Roy Orbison...', '那首歌是对罗伊·奥比森的致敬。');
INSERT INTO `words` VALUES (9710, 'trick', 'trɪk', 'n.', '诡计;花招;骗局;', 'We are playing a trick on a man who keeps bothering me.', '我们正在戏弄那个一直烦我的家伙。');
INSERT INTO `words` VALUES (9711, 'trickle', 'ˈtrɪkl', 'v.', '(使)滴，淌，小股流淌;(使)慢慢走，缓慢移动;', 'A tear trickled down the old man\'s cheek...', '一滴眼泪沿着老人的脸颊淌了下来。');
INSERT INTO `words` VALUES (9712, 'tricky', 'ˈtrɪki', 'adj.', '难办的;难对付的;狡猾的;', 'Parking can be tricky in the town centre...', '在市中心泊车可能很困难。');
INSERT INTO `words` VALUES (9713, 'trifle', 'ˈtraɪfl', 'n.', '稍微;一点儿;小事;', 'As a photographer, he\'d found both locations just a trifle disappointing...', '作为摄影师，他感到两处景点都有点儿不尽如人意。');
INSERT INTO `words` VALUES (9714, 'trifling', 'ˈtraɪflɪŋ', 'adj.', '琐碎的;微不足道的;无足轻重的;', 'The guests had each paid £250, no trifling sum…', '客人每位付了250英镑，可不是一笔小数目。');
INSERT INTO `words` VALUES (9715, 'trigger', 'ˈtrɪɡə(r)', 'n.', '(枪的)扳机;(尤指引发不良反应或发展的)起因，诱因;触发器;', 'A man pointed a gun at them and pulled the trigger.', '一名男子拿枪指着他们，接着扣动了扳机。');
INSERT INTO `words` VALUES (9716, 'trim', 'trɪm', 'v.', '修剪;修整;切去，割掉，剪下，除去(不必要的部分);', 'The neighbours\' gardens were trim and neat.', '邻居们的花园整洁美观。');
INSERT INTO `words` VALUES (9717, 'trimmed', 'trɪmd', 'v.', '修剪;修整;切去，割掉，剪下，除去(不必要的部分);', 'The neighbours\' gardens were trim and neat.', '邻居们的花园整洁美观。');
INSERT INTO `words` VALUES (9718, 'trip', 'trɪp', 'n.', '(尤指短程往返的)旅行，旅游，出行;(服用毒品后所产生的)幻觉，迷幻感受;绊;', 'On the Thursday we went out on a day trip...', '星期四我们出去玩了一天。');
INSERT INTO `words` VALUES (9719, 'triple', 'ˈtrɪpl', 'adj.', '三部分的;三人的;三组的;', '...a triple somersault...', '空翻三周');
INSERT INTO `words` VALUES (9720, 'triumph', 'ˈtraɪʌmf', 'n.', '巨大成功;重大成就;伟大胜利;', 'The championships proved to be a personal triumph for the coach, Dave Donovan...', '事实证明，在这次锦标赛中教练戴夫·多诺万取得了非凡的个人成就。');
INSERT INTO `words` VALUES (9721, 'triumphant', 'traɪˈʌmfənt', 'adj.', '高奏凯歌的;大获全胜的;巨大成功的;', 'Duncan and his triumphant soldiers celebrate their military victory…', '邓肯和他那些欢欣鼓舞的士兵们庆祝他们取得了军事胜利。');
INSERT INTO `words` VALUES (9722, 'trivial', 'ˈtrɪviəl', 'adj.', '不重要的;琐碎的;微不足道的;', 'The director tried to wave aside these issues as trivial details that could be settled later...', '主管试图对这些问题置之不理，认为都是些细枝末节的小事，可以稍后解决。');
INSERT INTO `words` VALUES (9723, 'trolley', 'ˈtrɒli', 'n.', '手推车;手拉车;(运或送食品、饮料的)小推车，台车;', 'A porter relieved her of the three large cases she had been pushing on a trolley.', '搬运工帮她卸下了她一直用手推车推着的3个大箱子。');
INSERT INTO `words` VALUES (9724, 'troop', 'truːp', 'n.', '军队;部队;士兵;', 'The next phase of the operation will involve the deployment of more than 35,000 troops from a dozen countries...', '下一阶段的军事行动将要对来自 12 个国家的 35,000多名士兵进行部署。');
INSERT INTO `words` VALUES (9725, 'tropical', 'ˈtrɒpɪkl', 'adj.', '热带的;来自热带的;产于热带的;', '...tropical diseases.', '热带疾病');
INSERT INTO `words` VALUES (9726, 'trot', 'trɒt', 'v.', '快步;疾走;小跑;', 'I trotted down the steps and out to the shed...', '我小步跑下台阶，奔向外面的小屋。');
INSERT INTO `words` VALUES (9727, 'trouble', 'ˈtrʌbl', 'n.', '问题;忧虑;困难;', 'I had trouble parking...', '我停车有困难。');
INSERT INTO `words` VALUES (9728, 'troublesome', 'ˈtrʌblsəm', 'adj.', '令人烦恼的;讨厌的;令人痛苦的;', 'He needed surgery to cure a troublesome back injury...', '他需要做手术来治好烦人的背伤。');
INSERT INTO `words` VALUES (9729, 'troupe', 'truːp', 'n.', '(演员、歌手等的)班子，表演团;', '...troupes of travelling actors.', '巡回演出演员组成的剧团');
INSERT INTO `words` VALUES (9730, 'truce', 'truːs', 'n.', '停战协定;休战;停战期;', 'The fighting of recent days has given way to an uneasy truce between the two sides...', '最近几天的交战结束了，双方达成了暂时的休战协议。');
INSERT INTO `words` VALUES (9731, 'truck', 'trʌk', 'n.', '(铁路上运送货物或动物的)敞篷车，无盖车皮;(运送货物、士兵、动物等后面敞开的)载重汽车;(运送东西的)手推车，手拉车;', 'Now and then they heard the roar of a heavy truck.', '他们不时地听到大卡车的轰鸣声。');
INSERT INTO `words` VALUES (9732, 'true', 'truː', 'adj.', '符合事实的;确实的;如实的;', 'Everything I had heard about him was true...', '我听说的关于他的一切都是真实的。');
INSERT INTO `words` VALUES (9733, 'truly', 'ˈtruːli', 'adv.', '(用于说法、感觉等)真诚地，诚恳地，衷心地;(指性质)真正，确实;(指描述)确切，准确，精确;', '...a truly democratic system...', '真正民主的制度');
INSERT INTO `words` VALUES (9734, 'trunk', 'trʌŋk', 'n.', '树干;象鼻;大箱子;', '...the gnarled trunk of a birch tree.', '白桦树粗糙多瘤的树干');
INSERT INTO `words` VALUES (9735, 'trust', 'trʌst', 'n.', '相信;信任;信赖;', '\'I trust you completely,\' he said...', '“我完全信任你，”他说。');
INSERT INTO `words` VALUES (9736, 'trustworthy', 'ˈtrʌstwɜːði', 'adj.', '值得信任的;可信赖的;可靠的;', 'He is a trustworthy and level-headed leader.', '他是个头脑冷静、值得信赖的领导。');
INSERT INTO `words` VALUES (9737, 'truth', 'truːθ', 'n.', '真相;实情;事实;', 'Is it possible to separate truth from fiction?…', '有可能把事实与虚构分开吗？');
INSERT INTO `words` VALUES (9738, 'truthful', 'ˈtruːθfl', 'adj.', '诚实;讲真话;坦率;', 'Most religions teach you to be truthful…', '大部分宗教都教导人要诚实。');
INSERT INTO `words` VALUES (9739, 'try', 'traɪ', 'v.', '试图;想要;设法;', 'He secretly tried to block her advancement in the Party...', '他暗地里设法阻挠她在党内的晋升。');
INSERT INTO `words` VALUES (9740, 'trying', 'ˈtraɪɪŋ', 'adj.', '令人厌烦的;难对付的;', 'Support from those closest to you is vital in these trying times...', '在这些困难的时刻，最亲近的人给予你的支持至关重要。');
INSERT INTO `words` VALUES (9741, 'tub', 'tʌb', 'n.', '盆;桶;(塑料或纸的)饭盒，食品盒;', 'He peeled the paper top off a little white tub and poured the cream into his coffee...', '他把小白盒上的纸盖撕去，然后把奶油倒进他的咖啡里。');
INSERT INTO `words` VALUES (9742, 'tube', 'tjuːb', 'n.', '(金属、塑料、橡胶等制成的)管，管子;管状物;(由软金属或塑料制成的带盖的、盛膏状物的)软管;', 'He is fed by a tube that enters his nose.', '他靠鼻饲管进食。');
INSERT INTO `words` VALUES (9743, 'tuck', 'tʌk', 'v.', '(把衣服、纸张等的边缘)塞进，折叠，卷起;把…塞进狭窄的空间;把…藏入;', 'He tried to tuck his flapping shirt inside his trousers...', '他设法把飘起的衬衫塞到裤子里。');
INSERT INTO `words` VALUES (9744, 'tuesday', 'ˈtjuːzdeɪ', 'n.', '星期二;', 'He phoned on Tuesday, just before you came...', '他星期二来电话了，就在你来之前。');
INSERT INTO `words` VALUES (9745, 'tug', 'tʌɡ', 'v.', '(常为几次用力)拉，拖，拽;(朝某一方向用力)拉;', 'A little boy came running up and tugged at his sleeve excitedly...', '一个小男孩跑上来，非常激动地拽着他的袖子。');
INSERT INTO `words` VALUES (9746, 'tuition', 'tjuˈɪʃn', 'n.', '(尤指对个人或小组的)教学，讲授，指导;(尤指大专院校的)学费;', 'The courses will give the beginner personal tuition in all types of outdoor photography.', '该课程将为初学者一对一地讲授各种户外摄影技巧。');
INSERT INTO `words` VALUES (9747, 'tumble', 'ˈtʌmbl', 'v.', '(使)跌倒，摔倒，滚落，翻滚下来;倒塌;坍塌;', 'A small boy tumbled off a third floor fire escape...', '一个小男孩从3楼防火梯上摔了下来。');
INSERT INTO `words` VALUES (9748, 'tumult', 'ˈtjuːmʌlt', 'n.', '骚乱;骚动;混乱;', 'A tumult of feelings inside her fought for supremacy.', '她内心百感交集。');
INSERT INTO `words` VALUES (9749, 'tune', 'tjuːn', 'n.', '曲调;曲子;', 'She was humming a merry little tune.', '她正哼着一支欢快的小调。');
INSERT INTO `words` VALUES (9750, 'tuning', 'ˈtjuːnɪŋ', 'v.', '(为乐器)调音，校音;调整，调节(发动机);(给收音机、电视等)调谐，调频道;', 'She was humming a merry little tune.', '她正哼着一支欢快的小调。');
INSERT INTO `words` VALUES (9751, 'turbulent', 'ˈtɜːbjələnt', 'adj.', '动荡的;动乱的;骚动的;', 'They had been together for five or six turbulent years of rows and reconciliations...', '他们在一起生活了五六年，一直吵吵闹闹，分分合合，没有片刻安宁。');
INSERT INTO `words` VALUES (9752, 'turkish', 'ˈtɜːkɪʃ', 'adj.', '土耳其的;', '…the Turkish capital, Ankara.', '土耳其首都安卡拉');
INSERT INTO `words` VALUES (9753, 'turmoil', 'ˈtɜːmɔɪl', 'n.', '动乱;骚动;混乱;', '...the political turmoil in South Africa.', '南非的政治动荡');
INSERT INTO `words` VALUES (9754, 'turn', 'tɜːn', 'v.', '(使)转动，旋转;转身;扭转(身体部位);', 'He turned abruptly and walked away...', '他突然转身走了。');
INSERT INTO `words` VALUES (9755, 'turnaround', 'ˈtɜːnəraʊnd', 'n.', '(轮船、飞机的)终点装卸时间;(接活到交活之间的)周转期，时限;好转;', 'I have personally never done such a complete turnaround in my opinion of a person...', '我个人在对人的评价上从没有过如此彻底的转变。');
INSERT INTO `words` VALUES (9756, 'turning', 'ˈtɜːnɪŋ', 'n.', '岔路口;拐弯处;转弯处;', 'Take the next turning on the right.', '在下一个路口向右拐。');
INSERT INTO `words` VALUES (9757, 'turnover', 'ˈtɜːnəʊvə(r)', 'n.', '(一定时期内的)营业额，成交量;人事变更率;人员调整率;', 'Her annual turnover is around £45,000…', '她的年营业额大约是 4.5 万英镑。');
INSERT INTO `words` VALUES (9758, 'turns', 'tɜːnz', 'v.', '(使)转动，旋转;转身;扭转(身体部位);', 'He turned abruptly and walked away...', '他突然转身走了。');
INSERT INTO `words` VALUES (9759, 'turquoise', 'ˈtɜːkwɔɪz', 'n.', '绿松石;绿松石色;青绿色;', '...a clear turquoise sea.', '清澈碧蓝的大海');
INSERT INTO `words` VALUES (9760, 'tutor', 'ˈtjuːtə(r)', 'n.', '家庭教师;私人教师;导师;', 'He is course tutor in archaeology at the University of Southampton...', '他是南安普敦大学考古学课程导师。');
INSERT INTO `words` VALUES (9761, 'tv', 'ˌtiː ˈviː', 'n.', '电视;电视机;', 'The TV was on...', '电视开着。');
INSERT INTO `words` VALUES (9762, 'twelfth', 'twelfθ', 'ord.', '第十二;', '...the twelfth anniversary of the April revolution.', '四月革命 12 周年纪念');
INSERT INTO `words` VALUES (9763, 'twentieth', 'ˈtwentiəθ', 'ord.', '第二十;', '...the twentieth century.', '20 世纪');
INSERT INTO `words` VALUES (9764, 'twice', 'twaɪs', 'adv.', '两次;两遍;两倍;', 'He visited me twice that fall and called me on the telephone often...', '那年秋天他来看过我两次，而且经常给我打电话。');
INSERT INTO `words` VALUES (9765, 'twilight', 'ˈtwaɪlaɪt', 'n.', '暮色;薄暮;黄昏;', 'They returned at twilight, and set off for one of the promenade bars.', '他们黄昏时分回来，然后去了一家步行街酒吧。');
INSERT INTO `words` VALUES (9766, 'twin', 'twɪn', 'n.', '孪生儿之一;双胞胎之一;一对相像的事物之一;', 'Sarah was looking after the twins...', '萨拉当时正在照管那对双胞胎。');
INSERT INTO `words` VALUES (9767, 'twinkle', 'ˈtwɪŋkl', 'v.', '闪耀;闪烁;(眼睛因高兴或兴奋)闪光，发亮;', 'At night, lights twinkle in distant villages across the valleys.', '夜间，山谷那头的遥远村落里灯光闪闪。');
INSERT INTO `words` VALUES (9768, 'twirl', 'twɜːl', 'v.', '(使)旋转，转动;使轻快地转动;使旋转;', 'Bonnie twirled her empty glass in her fingers...', '邦妮用手指旋转着她的空杯子。');
INSERT INTO `words` VALUES (9769, 'twist', 'twɪst', 'v.', '使弯曲，使扭曲(成一定形状);(使)弯曲变形，扭曲变形;扭转，转动(身体部位);', 'Her hands began to twist the handles of the bag she carried...', '她双手开始捻弄手提包的拎带。');
INSERT INTO `words` VALUES (9770, 'typical', 'ˈtɪpɪkl', 'adj.', '典型的;有代表性的;一贯的;', 'Cheney is everyone\'s image of a typical cop: a big white guy, six foot, 220 pounds...', '切尼是每个人心目中典型的警察形象：大块头白人，6 英尺高，220磅重。');
INSERT INTO `words` VALUES (9771, 'typically', 'ˈtɪpɪkli', 'adv.', '通常;一般;典型地;', 'Typically, parents apply to several schools and settle, if need be, for their fourth or fifth choice…', '通常父母会申请几个学校，而且如果必要的话会将就接受第四或第五个选择。');
INSERT INTO `words` VALUES (9772, 'tyrannical', 'tɪˈrænɪkl', 'adj.', '暴君的;专横的;残暴的;', 'He killed his tyrannical father with a blow to the head...', '他冲着暴戾的父亲的头部猛击了一下，把他打死了。');
INSERT INTO `words` VALUES (9773, 'tyranny', 'ˈtɪrəni', 'n.', '暴虐;专横;苛政;', 'He described these regimes as tyrannies and dictatorships…', '他把这些政权称作专制与独裁政府。');
INSERT INTO `words` VALUES (9774, 'tyrant', 'ˈtaɪrənt', 'n.', '暴君;专制君主;暴虐的统治者;', 'Since 1804 the country has mostly been ruled by tyrants.', '自 1804 年以来，该国大部分时间都处于暴君的统治之下。');
INSERT INTO `words` VALUES (9775, 'ugly', 'ˈʌɡli', 'adj.', '丑陋的;难看的;令人不快的;', '...an ugly little hat...', '难看的小帽子');
INSERT INTO `words` VALUES (9776, 'ulcer', 'ˈʌlsə(r)', 'n.', '溃疡;', '...stomach ulcers.', '胃溃疡');
INSERT INTO `words` VALUES (9777, 'ultimate', 'ˈʌltɪmət', 'adj.', '最后的;最终的;终极的;', 'He said it is still not possible to predict the ultimate outcome...', '他说现在还无法预料最终的结局。');
INSERT INTO `words` VALUES (9778, 'ultimately', 'ˈʌltɪmətli', 'adv.', '最终;最后;终归;', 'Whatever the scientists ultimately conclude, all of their data will immediately be disputed.', '不管科学家们最后得出什么样的结论，他们的全部数据将会立即引起争议。');
INSERT INTO `words` VALUES (9779, 'ultimatum', 'ˌʌltɪˈmeɪtəm', 'n.', '最后通牒;', 'They issued an ultimatum to the police to rid an area of racist attackers, or they will take the law into their own hands.', '他们向警方发出了最后通牒，要求肃清整个地区的种族主义暴徒，否则他们将自行采取行动。');
INSERT INTO `words` VALUES (9780, 'ultrasound', 'ˈʌltrəsaʊnd', 'n.', '超声;超音;超声波扫描检查;', 'I had an ultrasound scan to see how the pregnancy was progressing.', '我进行了超声波检查，看看妊娠情况如何。');
INSERT INTO `words` VALUES (9781, 'ultraviolet', 'ˌʌltrəˈvaɪələt', 'adj.', '紫外线的;利用紫外线的;', 'The sun\'s ultraviolet rays are responsible for both tanning and burning.', '阳光中的紫外线是皮肤晒黑和灼伤的根源。');
INSERT INTO `words` VALUES (9782, 'umbrella', 'ʌmˈbrelə', 'n.', '伞;雨伞;阳伞;', 'Harry held an umbrella over Dawn.', '哈里为唐打着伞。');
INSERT INTO `words` VALUES (9783, 'un', 'ˌjuː ˈen', 'abbr.', '联合国;', '...a UN peace-keeping mission.', '联合国维和行动');
INSERT INTO `words` VALUES (9784, 'unable', 'ʌnˈeɪbl', 'adj.', '没有所需技能(或力量、时间、知识等);未能;无法;', 'The military may feel unable to hand over power to a civilian President next year...', '军方可能会觉得明年难以将政权移交给一位民选总统。');
INSERT INTO `words` VALUES (9785, 'unacceptable', 'ˌʌnəkˈseptəbl', 'adj.', '不能接受(或允许、同意)的;', 'It is totally unacceptable for children to swear...', '孩子说脏话是完全不能容忍的。');
INSERT INTO `words` VALUES (9786, 'unaffected', 'ˌʌnəˈfektɪd', 'adj.', '未被改变的;未受影响的;无动于衷的;', 'She seemed totally unaffected by what she\'d drunk...', '她所喝的似乎对她没有丝毫影响。');
INSERT INTO `words` VALUES (9787, 'unaided', 'ʌnˈeɪdɪd', 'adj.', '无外援的;独立的;', 'There have been at least thirteen previous attempts to reach the North Pole unaided...', '之前至少已经有过13次想独立到达北极的尝试。');
INSERT INTO `words` VALUES (9788, 'unanimous', 'juˈnænɪməs', 'adj.', '(决定或意见)一致的，一致同意的;(团体)意见一致的，一致同意某事的;', 'Editors were unanimous in their condemnation of the proposals...', '编辑人员一致谴责这些提议。');
INSERT INTO `words` VALUES (9789, 'unattached', 'ˌʌnəˈtætʃt', 'adj.', '未婚的;单身的;未恋爱的;', 'I knew only two or three unattached men.', '我只认识两三个未婚的男士。');
INSERT INTO `words` VALUES (9790, 'unattended', 'ˌʌnəˈtendɪd', 'adj.', '主人不在场的;无人看管(或照料)的;', 'Never leave young children unattended near any pool or water tank...', '千万不能放任小孩呆在水池或水槽附近不管。');
INSERT INTO `words` VALUES (9791, 'unavailable', 'ˌʌnəˈveɪləbl', 'adj.', '无法得到;难以获得;不能(或不愿)见面;', 'Mr Hicks is out of the country and so unavailable for comment...', '希克斯先生目前不在国内，所以无法发表评论。');
INSERT INTO `words` VALUES (9792, 'unavoidable', 'ˌʌnəˈvɔɪdəbl', 'adj.', '无法避免的;难以预防的;', 'Managers said the job losses were unavoidable...', '经理们说裁员是不可避免的。');
INSERT INTO `words` VALUES (9793, 'unaware', 'ˌʌnəˈweə(r)', 'adj.', '不知道;没意识到;未察觉;', 'Many people are unaware of just how much food and drink they consume...', '许多人并不知道他们究竟消耗掉了多少食品和饮料。');
INSERT INTO `words` VALUES (9794, 'unbearable', 'ʌnˈbeərəbl', 'adj.', '难耐的;无法接受的;难以处理的;', 'War has made life almost unbearable for the civilians remaining in the capital...', '对留在首都的平民来说，战争已使生活变得几乎不堪忍受。');
INSERT INTO `words` VALUES (9795, 'unbelievable', 'ˌʌnbɪˈliːvəbl', 'adj.', '非常好(或坏、极端)的;难以置信的;惊人的;', 'His guitar solos are just unbelievable...', '他的吉他独奏棒极了。');
INSERT INTO `words` VALUES (9796, 'unbind', 'ʌnˈbaɪnd', 'v.', '解开;松开;', 'She unbound her hair and let it flow loose in the wind...', '她把头发解开，让它随风飘动。');
INSERT INTO `words` VALUES (9797, 'uncertain', 'ʌnˈsɜːtn', 'adj.', '无把握;犹豫;拿不准;', 'He was uncertain about his brother\'s intentions...', '他拿不准他哥哥的意图是什么。');
INSERT INTO `words` VALUES (9798, 'uncertainty', 'ʌnˈsɜːtnti', 'n.', '犹豫;迟疑;无把握;', '...a period of political uncertainty.', '政治动荡的一段时期');
INSERT INTO `words` VALUES (9799, 'unchanged', 'ʌnˈtʃeɪndʒd', 'adj.', '不变;没有变化;', 'For many years prices have remained virtually unchanged...', '价格多年来几乎没变。');
INSERT INTO `words` VALUES (9800, 'unclassified', 'ʌnˈklæsɪfaɪd', 'adj.', '非机密的;公开的;未分类的;', 'The material consisted only of already published, unclassified information.', '这份材料里只有一些已公开的、非保密性质的信息。');
INSERT INTO `words` VALUES (9801, 'uncle', 'ˈʌŋkl', 'n.', '舅父;叔父;伯父;', 'My uncle was the mayor of Memphis...', '我伯父是孟菲斯市市长。');
INSERT INTO `words` VALUES (9802, 'uncomfortable', 'ʌnˈkʌmftəbl', 'adj.', '使人不舒服的;令人不舒适的;感到难受的;', 'The request for money made them feel uncomfortable...', '索要金钱让他们有些不安。');
INSERT INTO `words` VALUES (9803, 'uncommon', 'ʌnˈkɒmən', 'adj.', '不常有的;罕见的;稀罕的;', 'Cancer of the breast in young women is uncommon...', '乳腺癌在年轻女子当中并不常见。');
INSERT INTO `words` VALUES (9804, 'uncomplicated', 'ʌnˈkɒmplɪkeɪtɪd', 'adj.', '简单的;率真的;容易的;', 'She is a beautiful, uncomplicated girl.', '她是一个美丽而率真的女孩。');
INSERT INTO `words` VALUES (9805, 'unconditional', 'ˌʌnkənˈdɪʃənl', 'adj.', '无条件的;无限制的;绝对的;', 'Children need unconditional love...', '孩子们需要无条件的爱。');
INSERT INTO `words` VALUES (9806, 'unconditionally', 'ˌʌnkənˈdɪʃənli', 'adv.', '无条件地;', 'Children need unconditional love...', '孩子们需要无条件的爱。');
INSERT INTO `words` VALUES (9807, 'unconscious', 'ʌnˈkɒnʃəs', 'adj.', '无知觉的;昏迷的;不省人事的;', 'By the time ambulancemen arrived he was unconscious...', '救护人员到达时他已经不省人事了。');
INSERT INTO `words` VALUES (9808, 'unconsciousness', 'ʌnˈkɒnʃəsnəs', 'n.', '昏迷;无知觉状态;', 'By the time ambulancemen arrived he was unconscious...', '救护人员到达时他已经不省人事了。');
INSERT INTO `words` VALUES (9809, 'uncover', 'ʌnˈkʌvə(r)', 'v.', '揭开盖子;发现;揭露;', 'Auditors said they had uncovered evidence of fraud...', '审计人员说他们已经发现了欺诈的证据。');
INSERT INTO `words` VALUES (9810, 'undated', 'ˌʌnˈdeɪtɪd', 'adj.', '未注日期的;日期不明的;时间不详的;', 'In each packet there are batches of letters, most of which are undated.', '每个包裹里都有一摞摞的信件，其中大部分都没有日期。');
INSERT INTO `words` VALUES (9811, 'under', 'ˈʌndə(r)', 'prep.', '在(或到、通过)…下面;在…表面下;由…覆盖着;', 'They found a labyrinth of tunnels under the ground.', '他们发现了一处迷宫似的地道。');
INSERT INTO `words` VALUES (9812, 'underestimate', 'ˌʌndərˈestɪmeɪt , ˌʌndərˈestɪmət', 'v.', '低估;对…估计不足;对…认识不足(或重视不够);', 'None of us should ever underestimate the degree of difficulty women face in career advancement...', '我们谁都不应该低估女性在事业发展过程中所面临的困难程度。');
INSERT INTO `words` VALUES (9813, 'undergo', 'ˌʌndəˈɡəʊ', 'v.', '经历，经受(变化、不快的事等);', 'New recruits have been undergoing training in recent weeks...', '最近几周，新兵正在接受训练。');
INSERT INTO `words` VALUES (9814, 'undergraduate', 'ˌʌndəˈɡrædʒuət', 'n.', '本科生;', 'Economics undergraduates are probably the brightest in the university.', '经济学专业的本科生可能是大学里最聪明的学生。');
INSERT INTO `words` VALUES (9815, 'underlie', 'ˌʌndəˈlaɪ', 'v.', '构成…的基础;作为…的原因;', 'Try to figure out what feeling underlies your anger.', '努力找出你的愤怒之下潜藏的情感。');
INSERT INTO `words` VALUES (9816, 'underline', 'ˌʌndəˈlaɪn', 'v.', '在(词语等下)画线;画底线标出;强调;', 'The report underlined his concern that standards were at risk...', '报告强调了他对道德水准可能会受影响所表示的关注。');
INSERT INTO `words` VALUES (9817, 'underlying', 'ˌʌndəˈlaɪɪŋ', 'adj.', '根本的;潜在的;隐含的;', 'To stop a problem you have to understand its underlying causes...', '要想杜绝问题的发生，必须弄清它的深层原因。');
INSERT INTO `words` VALUES (9818, 'undermine', 'ˌʌndəˈmaɪn', 'v.', '逐渐削弱(信心、权威等);使逐步减少效力;从根基处破坏;', 'Offering advice on each and every problem will undermine her feeling of being adult...', '对每个问题都给出建议会令她觉得自己不像个成年人。');
INSERT INTO `words` VALUES (9819, 'underneath', 'ˌʌndəˈniːθ', 'prep./adv.', '在…底下;隐藏(或掩盖)在下面;(指真实的感情或性格)在…表象之下;', 'The device exploded underneath a van.', '该装置在一辆货车底下发生了爆炸。');
INSERT INTO `words` VALUES (9820, 'underscore', 'ˌʌndəˈskɔː(r) , ˈʌndəskɔː(r)', 'v.', '在…下面划线;强调;着重说明;', 'The Labor Department figures underscore the shaky state of the economic recovery...', '劳工部的数据进一步印证了经济复苏状况的不稳定。');
INSERT INTO `words` VALUES (9821, 'undersigned', 'ˌʌndə\'saɪnd', 'n.', '(文件的)签字人，具名人，签署人;', 'The undersigned buyers agree to pay a 5,000 pound deposit.', '已签名的买主同意支付5,000英镑的订金。');
INSERT INTO `words` VALUES (9822, 'understand', 'ˌʌndəˈstænd', 'v.', '懂;理解;领会;', 'I think you heard and also understand me...', '我想你听到了我的话，也明白我的意思。');
INSERT INTO `words` VALUES (9823, 'understanding', 'ˌʌndəˈstændɪŋ', 'n.', '理解;领悟;了解;', 'They have to have a basic understanding of computers in order to use the advanced technology.', '要想利用先进技术，他们必须先对计算机有个基本的了解。');
INSERT INTO `words` VALUES (9824, 'undertake', 'ˌʌndəˈteɪk', 'v.', '承担;从事;负责;', 'She undertook the arduous task of monitoring the elections.', '她承担了监督选举的艰巨任务。');
INSERT INTO `words` VALUES (9825, 'undertaking', 'ˌʌndəˈteɪkɪŋ', 'n.', '(重大或艰巨的)任务，项目，事业;企业;承诺;', 'Organizing the show has been a massive undertaking.', '组织那场演出是一项浩大的工程。');
INSERT INTO `words` VALUES (9826, 'underwear', 'ˈʌndəweə(r)', 'n.', '内衣;衬衣;', '...a couple who went for a late-night swim in their underwear.', '深夜穿着内衣去游泳的一对夫妇');
INSERT INTO `words` VALUES (9827, 'underwrite', 'ˌʌndəˈraɪt', 'v.', '承担经济责任(包括支付特别费用或损失);承担保险责任;承保;', '...58,500 dollars to underwrite a new home...', '58,500美元承保一个新居');
INSERT INTO `words` VALUES (9828, 'underwriter', 'ˈʌndəraɪtə(r)', 'n.', '承保人，保险商(尤指船只);核保人(对投保项目进行风险评估并决定保险费率);', 'If the market will not buy the shares, the underwriter buys them...', '如果市场上没有人买这些股票，承销商就会买下来。');
INSERT INTO `words` VALUES (9829, 'undesirable', 'ˌʌndɪˈzaɪərəbl', 'adj.', '不想要的;不得人心的;易惹麻烦的;', 'Inflation is considered to be undesirable because of its adverse effects on income distribution...', '通货膨胀被认为是有害的，因为它会对收入分配产生不利影响。');
INSERT INTO `words` VALUES (9830, 'undo', 'ʌnˈduː', 'v.', '打开;解开;拆开;', 'I managed secretly to undo a corner of the parcel...', '我悄悄地设法解开了包裹的一角。');
INSERT INTO `words` VALUES (9831, 'undone', 'ʌnˈdʌn', 'adj.', '未扣;未系;松开;', 'He left nothing undone that needed attention.', '所有需要注意的事他都注意到了。');
INSERT INTO `words` VALUES (9832, 'undoubtedly', '', 'adv.', '无疑；必定;', 'The event was an undoubted success.', '这件事毫无疑问是成功的。');
INSERT INTO `words` VALUES (9833, 'undue', 'ˌʌnˈdjuː', 'adj.', '不适当的;过分的;过度的;', 'This would help the families to survive the drought without undue suffering...', '这会帮助那些家庭度过干旱期，不至于遭受太多的苦。');
INSERT INTO `words` VALUES (9834, 'unduly', 'ˌʌnˈdjuːli', 'adv.', '过分;过度;不适当地;', '\'But you\'re not unduly worried about doing this report?\' — \'No.\'...', '“但是你没有过于担心如何写这个报告？”——“没有。”');
INSERT INTO `words` VALUES (9835, 'uneasy', 'ʌnˈiːzi', 'adj.', '担心的;忧虑的;不安的;', 'He said nothing but gave me a sly grin that made me feel terribly uneasy...', '他什么也没说，只是朝我狡黠地笑了笑，这让我感到极其不安。');
INSERT INTO `words` VALUES (9836, 'unemployed', 'ˌʌnɪmˈplɔɪd', 'adj.', '失业的;待业的;下岗的;', 'The problem is millions of people are unemployed...', '问题是，大批的人都失业了。');
INSERT INTO `words` VALUES (9837, 'unemployment', 'ˌʌnɪmˈplɔɪmənt', 'n.', '失业;失业人数;无业;', '...an area that had the highest unemployment rate in western Europe...', '西欧失业率最高的地区');
INSERT INTO `words` VALUES (9838, 'unequivocal', 'ˌʌnɪˈkwɪvəkl', 'adj.', '表达明确的;毫不含糊的;斩钉截铁的;', '...Richardson\'s unequivocal commitment to fair play...', '理查森对公平竞争的明确承诺');
INSERT INTO `words` VALUES (9839, 'uneven', 'ʌnˈiːvn', 'adj.', '凹凸不平的;不平坦的;无定型的;', 'He staggered on the uneven surface of the car park...', '他摇摇晃晃地行走在停车场凹凸不平的地面上。');
INSERT INTO `words` VALUES (9840, 'unexpected', 'ˌʌnɪkˈspektɪd', 'adj.', '出乎意料的;始料不及的;', 'His death was totally unexpected...', '他的死亡完全出乎意料。');
INSERT INTO `words` VALUES (9841, 'unexpectedly', 'ˌʌnɪksˈpɛktɪdli', 'adv.', '意外地;出乎意料地;无意;', 'His death was totally unexpected...', '他的死亡完全出乎意料。');
INSERT INTO `words` VALUES (9842, 'unexplained', 'ˌʌnɪkˈspleɪnd', 'adj.', '原因不详的;未解释的;未说明的;', 'The demonstrations were provoked by the unexplained death of an opposition leader...', '一位反对派领袖莫名身亡，引发了示威游行。');
INSERT INTO `words` VALUES (9843, 'unfair', 'ˌʌnˈfeə(r)', 'adj.', '不公正的;不公平的;待人不平等的;', 'She was awarded £5,000 in compensation for unfair dismissal...', '她获判5,000英镑，作为遭到无理解雇的补偿。');
INSERT INTO `words` VALUES (9844, 'unfit', 'ʌnˈfɪt', 'adj.', '不合格的;不适合的;不适宜的;', 'Many children are so unfit they are unable to do even basic exercises.', '许多孩子非常不健康，连基本的健身锻炼都无法完成。');
INSERT INTO `words` VALUES (9845, 'unfold', 'ʌnˈfəʊld', 'v.', '(使)展开;打开;(使)逐渐展现;', 'The outcome depends on conditions as well as how events unfold...', '结果取决于条件，还取决于事件的进展。');
INSERT INTO `words` VALUES (9846, 'unforgettable', 'ˌʌnfəˈɡetəbl', 'adj.', '难以忘怀的;令人难忘的;', 'A visit to the Museum is an unforgettable experience...', '参观博物馆是一段令人难忘的经历。');
INSERT INTO `words` VALUES (9847, 'unfortunate', 'ʌnˈfɔːtʃənət', 'adj.', '不幸的;倒霉的;令人遗憾的;', 'Some unfortunate person passing below could all too easily be seriously injured...', '从下面经过的倒霉蛋儿很容易受重伤。');
INSERT INTO `words` VALUES (9848, 'unfortunately', 'ʌnˈfɔːtʃənətli', 'adv.', '不幸地;遗憾地;可惜地;', 'Unfortunately, my time is limited...', '可惜的是，我的时间有限。');
INSERT INTO `words` VALUES (9849, 'ungainly', 'ʌnˈɡeɪnli', 'adj.', '笨手笨脚的;(举止)不雅观的，难看的;', 'The dog, an ungainly mongrel pup, was loping about the road...', '那只丑陋的杂种幼犬在马路上到处乱跑。');
INSERT INTO `words` VALUES (9850, 'unhappy', 'ʌnˈhæpi', 'adj.', '不快乐的;不幸福的;难过的;', 'Her marriage is in trouble and she is desperately unhappy...', '她的婚姻陷入困境，她为此非常郁闷。');
INSERT INTO `words` VALUES (9851, 'unhealthy', 'ʌnˈhelθi', 'adj.', '不健康的;虚弱的;损害健康的;', 'Avoid unhealthy foods such as hamburger and chips...', '不要吃不健康食品，如汉堡和薯条。');
INSERT INTO `words` VALUES (9852, 'unidentified', 'ˌʌnaɪˈdentɪfaɪd', 'adj.', '未知的;不明的;未确认的;', 'He was shot this morning by unidentified intruders at his house.', '他今天早上在家中被身份不明的闯入者射杀。');
INSERT INTO `words` VALUES (9853, 'unification', 'ˌjuːnɪfɪˈkeɪʃn', 'n.', '合一;联合;统一;', '...the process of general European unification.', '欧洲一体化进程');
INSERT INTO `words` VALUES (9854, 'uniform', 'ˈjuːnɪfɔːm', 'n.', '制服;校服;', 'The town police wear dark blue uniforms and flat caps...', '镇上的警察穿着深蓝色制服，戴着鸭舌帽。');
INSERT INTO `words` VALUES (9855, 'uniformly', 'ˈjuːnɪfɔːmli', 'adv.', '均匀地；一致地；一律；无变化地;', 'The town police wear dark blue uniforms and flat caps...', '镇上的警察穿着深蓝色制服，戴着鸭舌帽。');
INSERT INTO `words` VALUES (9856, 'unify', 'ˈjuːnɪfaɪ', 'v.', '统一;使成一体;使一元化;', 'A flexible retirement age is being considered by Ministers to unify men\'s and women\'s pension rights...', '大臣们正在考虑一个灵活的退休年龄机制，这样可以把男性和女性的养老金权益统一起来。');
INSERT INTO `words` VALUES (9857, 'unilateral', 'ˌjuːnɪˈlætrəl', 'adj.', '单方的;', '...unilateral nuclear disarmament.', '单方面核裁军');
INSERT INTO `words` VALUES (9858, 'unintelligible', 'ˌʌnɪnˈtelɪdʒəbl', 'adj.', '难以理解的;难懂的;', 'He muttered something unintelligible...', '他嘴里不知咕哝了几句什么。');
INSERT INTO `words` VALUES (9859, 'uninterested', 'ʌnˈɪntrəstɪd', 'adj.', '不感兴趣的;冷淡的;漠不关心的;', 'I was so uninterested in the result that I didn\'t even bother to look at it.', '我对结果完全没兴趣，甚至都懒得看一眼。');
INSERT INTO `words` VALUES (9860, 'union', 'ˈjuːniən', 'n.', '协会;联合会;会社;', 'I feel that women in all types of employment can benefit from joining a union.', '我认为女性无论从事什么工作，加入工会都是有好处的。');
INSERT INTO `words` VALUES (9861, 'unique', 'juˈniːk', 'adj.', '唯一的;独一无二的;独特的;', 'Each person\'s signature is unique...', '每个人的签名都是独一无二的。');
INSERT INTO `words` VALUES (9862, 'unit', 'ˈjuːnɪt', 'n.', '单独的事物(或人、群体);单位;单元;', 'Agriculture was based in the past on the family as a unit.', '过去的农业是以家庭为单位的。');
INSERT INTO `words` VALUES (9863, 'unitary', 'ˈjuːnətri', 'adj.', '集中的;统一的;中央集权制的;', '...a call for the creation of a single unitary state.', '建立单一中央集权制国家的呼声');
INSERT INTO `words` VALUES (9864, 'unite', 'juˈnaɪt', 'v.', '(为某事)联合，联手，团结;(与某人或集体)联结，联合;统一;', 'The two parties have been trying to unite since the New Year...', '这两个党从新年开始就试图联合。');
INSERT INTO `words` VALUES (9865, 'united', 'juˈnaɪtɪd', 'adj.', '联合的;统一的;和谐的;', 'Every party is united on the need for parliamentary democracy...', '各党派一致认为有必要实行议会民主。');
INSERT INTO `words` VALUES (9866, 'unity', 'ˈjuːnəti', 'n.', '团结一致;联合;统一;', 'Senior politicians met today to discuss the future of European economic unity.', '高级政要今天会晤，探讨欧洲经济一体化的未来。');
INSERT INTO `words` VALUES (9867, 'universal', 'ˌjuːnɪˈvɜːsl', 'adj.', '普遍的;全体的;全世界的;', 'The insurance industry has produced its own proposals for universal health care...', '保险业提出了自己的全民医疗保健方案。');
INSERT INTO `words` VALUES (9868, 'universally', 'ˌjuːnɪˈvɜːsəli', 'adv.', '全体地;一致地;共同地;', '...a universally accepted point of view...', '普遍接受的观点');
INSERT INTO `words` VALUES (9869, 'universe', 'ˈjuːnɪvɜːs', 'n.', '宇宙;天地万物;万象;', 'Einstein\'s equations showed the Universe to be expanding...', '爱因斯坦的方程式表明宇宙正在膨胀。');
INSERT INTO `words` VALUES (9870, 'university', 'ˌjuːnɪˈvɜːsəti', 'n.', '(综合性)大学;高等学府;', 'Patrick is now at London University...', '帕特里克现在就读于伦敦大学。');
INSERT INTO `words` VALUES (9871, 'unjust', 'ˌʌnˈdʒʌst', 'adj.', '不公平的;不公正的;非正义的;', 'The attack on Charles was deeply unjust...', '对查尔斯的攻击甚为不公。');
INSERT INTO `words` VALUES (9872, 'unkind', 'ˌʌnˈkaɪnd', 'adj.', '不友善的;不亲切的;不客气的;', 'All last summer he\'d been unkind to her...', '去年整个夏天他都对她很恶劣。');
INSERT INTO `words` VALUES (9873, 'unknown', 'ˌʌnˈnəʊn', 'adj.', '未知的;不详的;未被确认的;', 'An unknown number of demonstrators were arrested...', '有数目不详的示威者遭到了逮捕。');
INSERT INTO `words` VALUES (9874, 'unlawful', 'ʌnˈlɔːfl', 'adj.', '不合法的;非法的;违法的;', '...employees who believe their dismissal was unlawful...', '认为将他们解雇是违法行为的雇员');
INSERT INTO `words` VALUES (9875, 'unless', 'ənˈles', 'conj.', '除非;除非在…情况下;若非;', 'Unless you are trying to lose weight to please yourself, it\'s going to be tough to keep your motivation level high...', '除非减肥是为了悦已，否则很难保持积极性。');
INSERT INTO `words` VALUES (9876, 'unlike', 'ˌʌnˈlaɪk', 'prep.', '不像;与…不同;(用于对比)与…不同;', 'This was a foreign country, so unlike San Jose...', '这是外国，与圣何塞很不一样。');
INSERT INTO `words` VALUES (9877, 'unlikely', 'ʌnˈlaɪkli', 'adj.', '不大可能发生的;非心目中的;非想象的;', 'A military coup seems unlikely...', '看起来不太可能会发生军事政变。');
INSERT INTO `words` VALUES (9878, 'unlimited', 'ʌnˈlɪmɪtɪd', 'adj.', '尽量多的;任意多的;无限制的;', 'An unlimited number of copies can still be made from the original...', '仍可以由原件复印出无数份副本来。');
INSERT INTO `words` VALUES (9879, 'unload', 'ˌʌnˈləʊd', 'v.', '(从车、船上)卸，取下;拆掉，退出，取出(子弹或胶卷等);推卸(责任);', 'Unload everything from the boat and clean it thoroughly...', '把所有东西从船上卸下来，然后把船彻底清理一下。');
INSERT INTO `words` VALUES (9880, 'unlock', 'ˌʌnˈlɒk', 'v.', '(用钥匙)开…的锁;发现;揭示;', 'He unlocked the car and threw the coat on to the back seat...', '他用钥匙打开车门，把外套扔到了后座上。');
INSERT INTO `words` VALUES (9881, 'unlucky', 'ʌnˈlʌki', 'adj.', '不幸的;倒霉的;不顺利的;', 'Owen was unlucky not to score on two occasions...', '欧文不幸两次都没有进球。');
INSERT INTO `words` VALUES (9882, 'unmarked', 'ˌʌnˈmɑːkt', 'adj.', '无标志的;无标记的;无记号的;', 'Her shoes are still white and unmarked.', '她的鞋仍然很白，没有任何污渍。');
INSERT INTO `words` VALUES (9883, 'unnatural', 'ʌnˈnætʃrəl', 'adj.', '不自然的;勉强的;反常的;', 'The aircraft rose with unnatural speed on take-off...', '那架飞机以反常的速度起飞升空。');
INSERT INTO `words` VALUES (9884, 'unnecessarily', '', 'adv.', '不必要地，多余地，不需要地;', 'The slaughter of whales is unnecessary and inhuman...', '屠杀鲸鱼是没有必要的也是不人道的。');
INSERT INTO `words` VALUES (9885, 'unnecessary', 'ʌnˈnesəsəri', 'adj.', '不需要的;不必要的;多余的;', 'The slaughter of whales is unnecessary and inhuman...', '屠杀鲸鱼是没有必要的也是不人道的。');
INSERT INTO `words` VALUES (9886, 'unobtainable', 'ˌʌnəbˈteɪnəbl', 'adj.', '得不到;无法得到;', '...an unobtainable married man...', '得不到的已婚男人');
INSERT INTO `words` VALUES (9887, 'unpack', 'ˌʌnˈpæk', 'v.', '打开(箱、包等)取;从(箱、包等中)取出;分析;', 'He unpacked his bag...', '他打开了包。');
INSERT INTO `words` VALUES (9888, 'unpaid', 'ˌʌnˈpeɪd', 'adj.', '未付的;未偿付的;无偿的;', 'Even unpaid work for charity is better than nothing...', '即便是给慈善机构做义工也比什么都不做要好。');
INSERT INTO `words` VALUES (9889, 'unpleasant', 'ʌnˈpleznt', 'adj.', '令人不快的;不舒服的;不和蔼的;', 'The symptoms can be uncomfortable, unpleasant and serious...', '那些症状可能令人不适，很不舒服甚至会比较严重。');
INSERT INTO `words` VALUES (9890, 'unprecedented', 'ʌnˈpresɪdentɪd', 'adj.', '前所未有的;空前的;没有先例的;', 'Such a move is rare, but not unprecedented...', '这种做法很罕见，但也不是没有先例的。');
INSERT INTO `words` VALUES (9891, 'unpredictable', 'ˌʌnprɪˈdɪktəbl', 'adj.', '无法预言的;不可预测的;难以预料的;', 'He is utterly unpredictable.', '他是个高深莫测的人。');
INSERT INTO `words` VALUES (9892, 'unproductive', 'ˌʌnprəˈdʌktɪv', 'adj.', '产量少的;效果不佳的;无益的;', 'Research workers are well aware that much of their time and effort is unproductive.', '研究人员很清楚，他们的很多时间和精力全都是白费。');
INSERT INTO `words` VALUES (9893, 'unquestionable', 'ʌnˈkwestʃənəbl', 'adj.', '无疑的;无可非议的;确实的;', 'He inspires affection and respect as a man of unquestionable integrity...', '他无可争议的忠诚正直赢得了人们的爱戴和尊敬。');
INSERT INTO `words` VALUES (9894, 'unreasonable', 'ʌnˈriːznəbl', 'adj.', '不合理的;不公正的;期望过高的;', 'The strikers were being unreasonable in their demands, having rejected the deal two weeks ago...', '罢工者的要求很不合理，他们在两周前已拒绝接受这份协议。');
INSERT INTO `words` VALUES (9895, 'unrest', 'ʌnˈrest', 'n.', '动荡;动乱;骚动;', 'The real danger is civil unrest in the east of the country...', '真正的危险是该国东部地区的民众动乱。');
INSERT INTO `words` VALUES (9896, 'unsafe', 'ʌnˈseɪf', 'adj.', '不安全的;危险的;身处险境的;', 'Critics claim the trucks are unsafe...', '批评家声称这些卡车很危险。');
INSERT INTO `words` VALUES (9897, 'unsatisfactory', 'ˌʌnˌsætɪsˈfæktəri', 'adj.', '不够好的;不能令人满意的;', 'The inspectors said just under a third of lessons were unsatisfactory...', '督察员说只有不到1/3的课程不够好。');
INSERT INTO `words` VALUES (9898, 'unsigned', 'ʌnˈsaɪnd', 'adj.', '没签名的;未签约的;', 'The envelope contained a typed, unsigned letter demanding £75,000 in cash.', '信封里装着一封打印的、没签名的信，索要75,000英镑。');
INSERT INTO `words` VALUES (9899, 'unsound', 'ˌʌnˈsaʊnd', 'adj.', '不稳妥的;观点不正确的;有错误的;', 'The thinking is good-hearted, but muddled and fundamentally unsound...', '这个想法的出发点是好的，但是条理混乱，而且本质上是错误的。');
INSERT INTO `words` VALUES (9900, 'unstable', 'ʌnˈsteɪbl', 'adj.', '不稳定的;易变的;变化莫测的;', 'After the fall of Pitt in 1801 there was a decade of unstable government...', '1801年皮特倒台后，政府经历了10年的动荡。');
INSERT INTO `words` VALUES (9901, 'unsuccessful', 'ˌʌnsəkˈsesfl', 'adj.', '不成功的;失败的;落空的;', 'His efforts were unsuccessful.', '他的努力失败了。');
INSERT INTO `words` VALUES (9902, 'unsuitable', 'ʌnˈsuːtəbl', 'adj.', '不适当的;不适宜的;不合适的;', 'Amy\'s shoes were unsuitable for walking any distance.', '埃米的鞋子根本不适合走路。');
INSERT INTO `words` VALUES (9903, 'unsuspected', 'ˌʌnsəˈspektɪd', 'adj.', '未预料到的;未知的;未觉察到的;', 'A surprising number of ailments are caused by unsuspected environmental factors...', '有大量疾病是由不明环境因素造成的。');
INSERT INTO `words` VALUES (9904, 'untie', 'ʌnˈtaɪ', 'v.', '解开…的结;打开;', 'Nicholas untied the boat from her mooring...', '尼古拉斯把泊船的绳缆解开。');
INSERT INTO `words` VALUES (9905, 'until', 'ənˈtɪl', 'conj./prep.', '到…时;直到…为止;', 'Until 1982 he was senior lecturer in botany at Durham University.', '1982年以前他一直是达勒姆大学生物系的高级讲师。');
INSERT INTO `words` VALUES (9906, 'untold', 'ˌʌnˈtəʊld', 'adj.', '难以形容的(大、恶劣等);未讲过的;未叙述的;', 'The demise of the industry has caused untold misery to thousands of hard-working tradesmen...', '该行业的没落给数以千计辛苦经营的商贩带来了难言的苦楚。');
INSERT INTO `words` VALUES (9907, 'untoward', 'ˌʌntəˈwɔːd', 'adj.', '异常的;意外的;不幸的;', 'The surveyor\'s report didn\'t highlight anything untoward...', '勘测员的报告没有指明有任何麻烦问题。');
INSERT INTO `words` VALUES (9908, 'unusable', 'ˌʌnˈjuːzəbl', 'adj.', '(破得或差得)不能使用的;破烂不堪的;', 'Bombing had made roads and railways unusable.', '轰炸导致公路和铁路都无法通行。');
INSERT INTO `words` VALUES (9909, 'unusual', 'ʌnˈjuːʒuəl', 'adj.', '特别的;不寻常的;罕见的;', 'They have replanted many areas with rare and unusual plants...', '他们在许多地方重新种上了珍稀植物。');
INSERT INTO `words` VALUES (9910, 'unveil', 'ˌʌnˈveɪl', 'v.', '为…揭幕;揭开…上的覆盖物;拉开…的帷幔;', '...a ceremony to unveil a monument to the victims.', '死难者纪念碑揭幕仪式');
INSERT INTO `words` VALUES (9911, 'unwanted', 'ˌʌnˈwɒntɪd', 'adj.', '不需要的;多余的;不受欢迎的;', '...the misery of unwanted pregnancies...', '意外怀孕的痛苦');
INSERT INTO `words` VALUES (9912, 'unwarranted', 'ʌnˈwɒrəntɪd', 'adj.', '不合理的;不必要的;无正当理由的;', 'Any attempt to discuss the issue of human rights was rejected as an unwarranted interference in the country\'s internal affairs...', '任何讨论人权问题的尝试都会被以无端干预该国内政为由加以拒绝。');
INSERT INTO `words` VALUES (9913, 'unwelcome', 'ʌnˈwelkəm', 'adj.', '不需要的;不受欢迎的;多余的;', 'The media has brought more unwelcome attention to the Royal Family...', '媒体对王室的过度关注令人反感。');
INSERT INTO `words` VALUES (9914, 'unwilling', 'ʌnˈwɪlɪŋ', 'adj.', '不情愿;不愿意;勉强的;', 'Initially the government was unwilling to accept the defeat...', '最初政府不愿意承认失败。');
INSERT INTO `words` VALUES (9915, 'unworkable', 'ʌnˈwɜːkəbl', 'adj.', '不切实际的;难以实行的;行不通的;', 'There is the strong possibility that such cooperation will prove unworkable...', '这种合作很有可能会行不通。');
INSERT INTO `words` VALUES (9916, 'upbringing', 'ˈʌpbrɪŋɪŋ', 'n.', '抚育;养育;教养;', 'Martin\'s upbringing shaped his whole life...', '马丁的家庭教养影响了他的一生。');
INSERT INTO `words` VALUES (9917, 'uphold', 'ʌpˈhəʊld', 'v.', '支持，维护(正义等);维持，确认(原判、裁决等);', 'Our policy has been to uphold the law...', '拥护法律是我们一贯的方针。');
INSERT INTO `words` VALUES (9918, 'upkeep', 'ˈʌpkiːp', 'n.', '保养(费);维修(费);抚养(费);', 'The money will be used for the estate\'s upkeep...', '这笔钱将用于物业维护。');
INSERT INTO `words` VALUES (9919, 'upon', 'əˈpɒn', 'prep.', '强调数目或数量大;在…上;', 'He set the tray upon the table...', '他把盘子放在桌子上。');
INSERT INTO `words` VALUES (9920, 'upper', 'ˈʌpə(r)', 'adj.', '上边的，上面的，上层的(尤指同类或一对中的一个);上部的;靠上部的;', 'There is a smart restaurant on the upper floor...', '楼上有一家时尚的餐馆。');
INSERT INTO `words` VALUES (9921, 'upright', 'ˈʌpraɪt', 'adj.', '直立的;挺直的;竖直的;', 'Helen sat upright in her chair...', '海伦笔直地坐在椅子上。');
INSERT INTO `words` VALUES (9922, 'uprising', 'ˈʌpraɪzɪŋ', 'n.', '起义;暴动;造反;', '...a popular uprising against the authoritarian government...', '反对独裁政府的群众暴动');
INSERT INTO `words` VALUES (9923, 'uproar', 'ˈʌprɔː(r)', 'n.', '吵闹;喧嚣;叫喊;', 'The announcement caused uproar in the crowd...', '公告在人群中引起了骚动。');
INSERT INTO `words` VALUES (9924, 'upstairs', 'ˌʌpˈsteəz', 'adv.', '在(或向)楼上;在(或向)上一层;', 'He went upstairs and changed into fresh clothes...', '他上楼换上干净衣服。');
INSERT INTO `words` VALUES (9925, 'upstream', 'ˌʌpˈstriːm', 'adv.', '向(或在)上游;逆流;', 'The water rose high enough for them to continue upstream.', '水位涨得够高了，他们可以继续向上游航行。');
INSERT INTO `words` VALUES (9926, 'upward', 'ˈʌpwəd', 'adj.', '向上的;朝上的;向高处的;', 'She started once again on the steep upward climb...', '她又开始沿着陡峭的山路往上爬。');
INSERT INTO `words` VALUES (9927, 'upwards', 'ˈʌpwədz', 'adv.', '向上;向高处;(数量、价格)上升，上涨，提高;', '\'There,\' said Jack, pointing upwards...', '“在那里，”杰克向上指着说。');
INSERT INTO `words` VALUES (9928, 'urban', 'ˈɜːbən', 'adj.', '城市的;都市的;城镇的;', 'Most of the population is an urban population...', '绝大多数人口都是城镇居民。');
INSERT INTO `words` VALUES (9929, 'urge', 'ɜːdʒ', 'v.', '敦促;催促;力劝;', 'They urged parliament to approve plans for their reform programme...', '他们敦促议会批准他们的改革方案。');
INSERT INTO `words` VALUES (9930, 'urgency', 'ˈɜːdʒənsi', 'n.', '紧迫;急迫;急事;', 'There is an urgent need for food and water...', '现在亟需食物和水。');
INSERT INTO `words` VALUES (9931, 'urgent', 'ˈɜːdʒənt', 'adj.', '紧急的;紧迫的;迫切的;', 'There is an urgent need for food and water...', '现在亟需食物和水。');
INSERT INTO `words` VALUES (9932, 'urgently', 'ˈɜːdʒəntli', 'adv.', '紧急地，急迫地;', 'There is an urgent need for food and water...', '现在亟需食物和水。');
INSERT INTO `words` VALUES (9933, 'urinary', 'ˈjʊərɪnəri', 'adj.', '尿的;泌尿的;泌尿器官的;', '...urinary tract infections.', '尿道感染');
INSERT INTO `words` VALUES (9934, 'usa', 'ˌjuː es ˈeɪ', 'abbr.', '美国;美利坚合众国;', 'In the USA you can ring Social Security or the Department of Welfare and ask what help is available.', '在美国，你可以致电社会保障部或福利部询问能得到何种帮助。');
INSERT INTO `words` VALUES (9935, 'usage', 'ˈjuːsɪdʒ', 'n.', '(词语的)用法，惯用法;使用;利用;', 'The word \'undertaker\' had long been in common usage...', 'undertaker一词早已广泛使用。');
INSERT INTO `words` VALUES (9936, 'useful', 'ˈjuːsfl', 'adj.', '有用的;有益的;实用的;', 'The slow cooker is very useful for people who go out all day...', '慢炖锅对于整天都不在家的人来说很有用。');
INSERT INTO `words` VALUES (9937, 'usefulness', 'ˈjuːsflnəs', 'n.', '有用;实用;可用性;', 'The slow cooker is very useful for people who go out all day...', '慢炖锅对于整天都不在家的人来说很有用。');
INSERT INTO `words` VALUES (9938, 'useless', 'ˈjuːsləs', 'adj.', '无用的;无效的;无价值的;', 'He realised that their money was useless in this country...', '他意识到他们国家的货币在这个国家一文不值。');
INSERT INTO `words` VALUES (9939, 'user', 'ˈjuːzə(r)', 'n.', '使用者;用户;瘾君子;', 'Beach users have complained about people walking their dogs on the sand.', '海滩上的游客投诉有人在沙滩上遛狗。');
INSERT INTO `words` VALUES (9940, 'usual', 'ˈjuːʒuəl', 'adj.', '通常的;寻常的;惯常的;', 'It is a neighborhood beset by all the usual inner-city problems...', '这是个为各种内城常见问题所困扰的社区。');
INSERT INTO `words` VALUES (9941, 'usually', 'ˈjuːʒuəli', 'adv.', '通常地;正常地;一般地;', 'The best information about hotels usually comes from friends and acquaintances who have been there...', '关于酒店的最可靠信息通常来自曾经去过那里的朋友和熟人。');
INSERT INTO `words` VALUES (9942, 'utensil', 'juːˈtensl', 'n.', '(家庭)用具，器皿;家什;', '...utensils such as bowls, steamers and frying pans...', '碗、蒸锅、煎锅等用具');
INSERT INTO `words` VALUES (9943, 'utility', 'juːˈtɪləti', 'n.', '公用事业;实用;效用;', 'Belief in the utility of higher education is shared by students nationwide...', '全国的学生都相信高等教育是有用的。');
INSERT INTO `words` VALUES (9944, 'utmost', 'ˈʌtməʊst', 'adj.', '最大的;极度的;', 'It is a matter of the utmost urgency to find out what has happened to these people...', '当务之急是要弄清楚这些人出了什么事。');
INSERT INTO `words` VALUES (9945, 'utter', 'ˈʌtə(r)', 'adj.', '完全的;十足的;彻底的;', 'He uttered a snorting laugh...', '他嗤笑了一声。');
INSERT INTO `words` VALUES (9946, 'utterance', 'ˈʌtərəns', 'n.', '用言语的表达;说话;话语;', '...the Queen\'s public utterances.', '女王的公开讲话');
INSERT INTO `words` VALUES (9947, 'utterly', 'ˈʌtəli', 'adv.', '全然;完全地;彻底地;', 'Everything about the country seemed utterly different from what I\'d experienced before...', '这个国家的一切似乎与我以前的经历完全不同。');
INSERT INTO `words` VALUES (9948, 'vacancy', 'ˈveɪkənsi', 'n.', '(职位的)空缺;空职;空额;', 'They had a short-term vacancy for a person on the foreign desk...', '他们在国际新闻组有一个短期的空缺职位。');
INSERT INTO `words` VALUES (9949, 'vacant', 'ˈveɪkənt', 'adj.', '空着的;未被占用的;(职位)空缺的;', 'Half way down the coach was a vacant seat...', '车厢中部有一个空座。');
INSERT INTO `words` VALUES (9950, 'vacation', 'veɪˈkeɪʃn', 'n.', '(英国大学的)假期;(美国学校的)假期;(法庭的)休庭期;', 'During his summer vacation he visited Russia...', '暑假期间他去俄罗斯了。');
INSERT INTO `words` VALUES (9951, 'vaccinate', 'ˈvæksɪneɪt', 'v.', '给…接种疫苗;', 'Dogs must be vaccinated against distemper...', '狗必须接种犬瘟热疫苗。');
INSERT INTO `words` VALUES (9952, 'vaccination', 'ˌvæksɪˈneɪʃən', 'n.', '注射疫苗；接种疫苗；种痘； 牛痘疤;', 'Dogs must be vaccinated against distemper...', '狗必须接种犬瘟热疫苗。');
INSERT INTO `words` VALUES (9953, 'vaccine', 'ˈvæksiːn', 'n.', '疫苗;菌苗;', 'Anti-malarial vaccines are now undergoing trials...', '抗疟疾的疫苗正在试验阶段。');
INSERT INTO `words` VALUES (9954, 'vacuum', 'ˈvækjuːm', 'n.', '真空;真空状态;空白;', 'The collapse of the army left a vacuum in the area...', '这支军队的溃败使这个地区出现了军事真空。');
INSERT INTO `words` VALUES (9955, 'vague', 'veɪɡ', 'adj.', '(思想上)不清楚的，含糊的，不明确的，模糊的;不具体的;不详细的;', 'A lot of the talk was apparently vague and general...', '很多话明显是些含糊的泛泛之论。');
INSERT INTO `words` VALUES (9956, 'vain', 'veɪn', 'adj.', '徒劳的;枉然的;无结果的;', 'The drafting committee worked through the night in a vain attempt to finish on schedule...', '起草委员会为按计划完成草案已通宵加班，但结果仍是徒劳。');
INSERT INTO `words` VALUES (9957, 'vainly', 'ˈveɪnli', 'adv.', '徒劳地;不成功地;白费力地;', 'The drafting committee worked through the night in a vain attempt to finish on schedule...', '起草委员会为按计划完成草案已通宵加班，但结果仍是徒劳。');
INSERT INTO `words` VALUES (9958, 'valid', 'ˈvælɪd', 'adj.', '(法律上)有效的;(正式)认可的;符合逻辑的;', 'They put forward many valid reasons for not exporting...', '他们提出了很多不出口的正当理由。');
INSERT INTO `words` VALUES (9959, 'validate', 'ˈvælɪdeɪt', 'v.', '证实;确认;确证;', 'This discovery seems to validate the claims of popular astrology.', '这个发现似乎能印证流行占星术的一些说法。');
INSERT INTO `words` VALUES (9960, 'validation', 'ˌvælɪˈdeɪʃən', 'n.', '生效；批准；验证；确认；证实；核实;', 'This discovery seems to validate the claims of popular astrology.', '这个发现似乎能印证流行占星术的一些说法。');
INSERT INTO `words` VALUES (9961, 'validity', 'vəˈlɪdəti', 'n.', '(法律上的)有效，合法性;(正式的)认可;符合逻辑;', 'Shocked by the results of the elections, they now want to challenge the validity of the vote...', '选举结果令他们很震惊，他们现在要对选举的可信性提出质疑。');
INSERT INTO `words` VALUES (9962, 'valley', 'ˈvæli', 'n.', '谷;山谷;溪谷;', '...a wooded valley set against the backdrop of Monte Rosa.', '罗莎峰背后树木繁茂的山谷');
INSERT INTO `words` VALUES (9963, 'valuable', 'ˈvæljuəbl', 'adj.', '很有用的;很重要的;宝贵的;', 'Many of our teachers also have valuable academic links with Heidelberg University...', '我们的很多老师也和海德堡大学保持着有益的学术联系。');
INSERT INTO `words` VALUES (9964, 'valuation', 'ˌvæljuˈeɪʃn', 'n.', '(专业)估价;估定的价值;估值;', '...an independent valuation of  the company...', '对这家公司的独立评估');
INSERT INTO `words` VALUES (9965, 'value', 'ˈvæljuː', 'n.', '(商品)价值;(与价格相比的)值，划算程度;用途;', 'The value of this work experience should not be underestimated...', '这种工作经验的重要性不应该被低估。');
INSERT INTO `words` VALUES (9966, 'valued', 'ˈvæljuːd', 'v.', '重视;珍视;给…估价;', 'The value of this work experience should not be underestimated...', '这种工作经验的重要性不应该被低估。');
INSERT INTO `words` VALUES (9967, 'vanguard', 'ˈvænɡɑːd', 'n.', '(政治、艺术、工业等社会活动的)领导者，先锋，先驱者;先头部队;前卫;', 'Such thinking puts Kodak in the vanguard of a movement reshaping the computer industry...', '这种想法使得柯达走在了改造计算机产业运动的最前沿。');
INSERT INTO `words` VALUES (9968, 'vanish', 'ˈvænɪʃ', 'v.', '(莫名其妙地)突然消失;不复存在;消亡;', 'He just vanished and was never seen again...', '他就这么消失了，再也没人见到过他。');
INSERT INTO `words` VALUES (9969, 'vanity', 'ˈvænəti', 'n.', '自负;自大;虚荣;', 'Men who use steroids are motivated by sheer vanity...', '使用类固醇的男人纯粹是受虚荣心的驱使。');
INSERT INTO `words` VALUES (9970, 'variable', 'ˈveəriəbl', 'adj.', '多变的;易变的;变化无常的;', 'The potassium content of foodstuffs is very variable...', '食物中钾的含量差别很大。');
INSERT INTO `words` VALUES (9971, 'variance', 'ˈveəriəns', 'n.', '变化幅度;差额;', 'Many of his statements were at variance with the facts...', '他的说法有很多和事实不符。');
INSERT INTO `words` VALUES (9972, 'variant', 'ˈveəriənt', 'n.', '变种;变体;变形;', 'The quagga was a strikingly beautiful variant of the zebra...', '斑驴是一种极美丽的斑马变种。');
INSERT INTO `words` VALUES (9973, 'varied', 'ˈveərid', 'adj.', '各种各样的;形形色色的;不相同的;', 'It is essential that your diet is varied and balanced...', '关键是你的饮食要多样化，并保持均衡。');
INSERT INTO `words` VALUES (9974, 'variety', 'vəˈraɪəti', 'n.', '(同一事物的)不同种类，多种式样;变化;多样化;', 'Susan\'s idea of freedom was to have variety in her life style...', '苏珊心目中的自由就是拥有丰富多彩的生活。');
INSERT INTO `words` VALUES (9975, 'various', 'ˈveəriəs', 'adj.', '各种不同的;各种各样的;具有多种特征的;', 'His plan is to spread the capital between various building society accounts...', '他打算把资金分散存到不同的购房互助会的账户上。');
INSERT INTO `words` VALUES (9976, 'varnish', 'ˈvɑːnɪʃ', 'n.', '清漆;罩光漆;指甲油;', 'The varnish comes in six natural wood shades.', '有6种深浅不同的天然木质色泽的清漆可选。');
INSERT INTO `words` VALUES (9977, 'vary', 'ˈveəri', 'v.', '(大小、形状等)相异，不同，有别;(根据情况)变化，改变;变更;', 'As they\'re handmade, each one varies slightly...', '由于它们是手工制作的，彼此都会有些微小的差异。');
INSERT INTO `words` VALUES (9978, 'vase', 'vɑːz', 'n.', '花瓶;装饰瓶;', '...a vase of red roses.', '一花瓶的红玫瑰');
INSERT INTO `words` VALUES (9979, 'vast', 'vɑːst', 'adj.', '辽阔的;巨大的;庞大的;', '...Afrikaner farmers who own vast stretches of land...', '拥有广袤土地的南非白人农场主');
INSERT INTO `words` VALUES (9980, 'vault', 'vɔːlt', 'n.', '(尤指银行的)金库，保险库;(教堂的)地下墓室;(坟地的)墓穴;', 'Most of the money was in storage in bank vaults...', '大部分钱都存放在银行的保险库里。');
INSERT INTO `words` VALUES (9981, 'vegetable', 'ˈvedʒtəbl', 'n.', '蔬菜;植物人;生活单调乏味的人;', 'A good general diet should include plenty of fresh vegetables.', '良好的日常饮食应该包含大量的新鲜蔬菜。');
INSERT INTO `words` VALUES (9982, 'vegetarian', 'ˌvedʒəˈteəriən', 'n.', '素食者;吃素的人;', 'Yasmin sticks to a strict vegetarian diet...', '亚斯明坚持不沾一点儿荤腥。');
INSERT INTO `words` VALUES (9983, 'vegetation', 'ˌvedʒəˈteɪʃn', 'n.', '(统称)植物;(尤指某地或环境的)植被，植物群落，草木;', 'The inn has a garden of semi-tropical vegetation.', '这家小旅馆里有一个亚热带植物园。');
INSERT INTO `words` VALUES (9984, 'vehement', 'ˈviːəmənt', 'adj.', '(感情)强烈的，激烈的;(尤指)愤怒的;', 'She suddenly became very vehement and agitated, jumping around and shouting...', '她突然变得激动狂躁，四处乱跳还大喊大叫。');
INSERT INTO `words` VALUES (9985, 'vehicle', 'ˈviːəkl', 'n.', '交通工具;车辆;(赖以表达思想、感情或达到目的的)手段，工具;', 'The vehicle would not have the capacity to make the journey on one tank of fuel.', '这辆车单靠一箱燃料无法跑完整趟旅程。');
INSERT INTO `words` VALUES (9986, 'veil', 'veɪl', 'n.', '(尤指女用的)面纱，面罩;(修女的)头巾;掩饰;', 'She\'s got long fair hair but she\'s got a veil over it...', '她有一头金色的长发，但她用头巾包住了。');
INSERT INTO `words` VALUES (9987, 'vein', 'veɪn', 'n.', '静脉;叶脉;翅脉;', 'Many veins are found just under the skin.', '许多静脉血管就在皮肤下面。');
INSERT INTO `words` VALUES (9988, 'velocity', 'vəˈlɒsəti', 'n.', '(沿某一方向的)速度;高速;快速;', '...the velocity of light.', '光速');
INSERT INTO `words` VALUES (9989, 'velvet', 'ˈvelvɪt', 'n.', '丝绒;立绒;经绒;', '...a charcoal-gray overcoat with a velvet collar...', '带天鹅绒领子的炭灰色外套');
INSERT INTO `words` VALUES (9990, 'vendor', 'ˈvendə(r)', 'n.', '小贩;摊贩;(某种产品的)销售公司;', '...ice-cream vendors.', '卖冰激凌的小贩');
INSERT INTO `words` VALUES (9991, 'venerate', 'ˈvenəreɪt', 'v.', '敬重;崇敬;敬仰;', 'My father venerated General Eisenhower.', '我父亲十分敬仰艾森豪威尔将军。');
INSERT INTO `words` VALUES (9992, 'vengeance', 'ˈvendʒəns', 'n.', '报复;报仇;复仇;', 'He swore vengeance on everyone involved in the murder...', '他发誓要报复每一个参与了这起谋杀的人。');
INSERT INTO `words` VALUES (9993, 'vent', 'vent', 'n.', '(空气、气体、液体的)出口，进口，漏孔;(鸟、鱼等小动物的)肛门;(大衣等的)衩口，开衩，背衩;', 'Quite a lot of steam escaped from the vent at the front of the machine...', '大量蒸汽从机器前部的排风口冒了出来。');
INSERT INTO `words` VALUES (9994, 'ventilate', 'ˈventɪleɪt', 'v.', '使(房间、建筑物等)通风;使通气;公开表达(感情或意见);', 'Ventilate the room properly when paint stripping...', '当油漆开始剥落时，要给房间适当通风。');
INSERT INTO `words` VALUES (9995, 'ventilation', 'ˌvɛntɪˈleɪʃən', 'n.', '通风，通气，通气能力，通气量；通风设备;', 'Ventilate the room properly when paint stripping...', '当油漆开始剥落时，要给房间适当通风。');
INSERT INTO `words` VALUES (9996, 'venture', 'ˈventʃə(r)', 'n.', '(尤指有风险的)企业，商业，投机活动，经营项目;', '...his latest writing venture.', '他最新的写作尝试');
INSERT INTO `words` VALUES (9997, 'verbal', 'ˈvɜːbl', 'adj.', '文字的;言语的;词语的;', 'They were jostled and subjected to a torrent of verbal abuse...', '他们被推来搡去，受到大肆的谩骂攻击。');
INSERT INTO `words` VALUES (9998, 'verdict', 'ˈvɜːdɪkt', 'n.', '(陪审团的)裁定，裁决，裁断;(经过检验或认真考虑后的)决定，结论，意见;', 'The jury returned a unanimous guilty verdict...', '陪审团一致作出了有罪裁决。');
INSERT INTO `words` VALUES (9999, 'verge', 'vɜːdʒ', 'n.', '(路边的)小草地，绿地;', 'The country was on the verge of becoming prosperous and successful...', '这个国家即将走向繁荣富强。');
INSERT INTO `words` VALUES (10000, 'verification', 'ˌvɛrɪfɪˈkeɪʃən', 'n.', '检验，检查，审核；验收；查证，证实，验证，核实；鉴定；查清;宣誓证实;核对（账目的）真实性;', 'I verified the source from  which I had that information...', '我核实了我所获消息的来源。');
INSERT INTO `words` VALUES (10001, 'verify', 'ˈverɪfaɪ', 'v.', '核实;查对;核准;', 'I verified the source from  which I had that information...', '我核实了我所获消息的来源。');
INSERT INTO `words` VALUES (10002, 'versatile', 'ˈvɜːsətaɪl', 'adj.', '多才多艺的;有多种技能的;多面手的;', 'He had been one of the game\'s most versatile athletes.', '他是这项运动中技术最全面的运动员之一。');
INSERT INTO `words` VALUES (10003, 'verse', 'vɜːs', 'n.', '诗;韵文;诗节;', '...a slim volume of verse...', '一本薄薄的诗集');
INSERT INTO `words` VALUES (10004, 'versed', 'vɜːst', 'adj.', '精通的;熟练的;', 'Page is well versed in many styles of jazz.', '佩奇精通多种风格的爵士乐。');
INSERT INTO `words` VALUES (10005, 'version', 'ˈvɜːʃn', 'n.', '变体;变种;型式;', '...an updated version of his book...', '他的书的最新版本');
INSERT INTO `words` VALUES (10006, 'versus', 'ˈvɜːsəs', 'prep.', '(表示两队或双方对阵)对，诉，对抗;(比较两种不同想法、选择等)与…相对，与…相比;', 'Only 18.8% of the class of 1982 had some kind of diploma four years after high school, versus 45% of the class of 1972.', '1982年那一届只有18.8% 的学生在中学毕业4年后获得了某种文凭，而1972年那一届这一比例是45％。');
INSERT INTO `words` VALUES (10007, 'vertical', 'ˈvɜːtɪkl', 'adj.', '竖的;垂直的;直立的;', 'The climber inched up a vertical wall of rock...', '攀登者慢慢爬上了陡直的石壁。');
INSERT INTO `words` VALUES (10008, 'vertically', 'ˈvɜːtɪkəli', 'adv.', '垂直地；直立地;', 'The climber inched up a vertical wall of rock...', '攀登者慢慢爬上了陡直的石壁。');
INSERT INTO `words` VALUES (10009, 'very', 'ˈveri', 'adv.', '(置于形容词、副词和限定词前)很，非常，十分，极;(强调形容词最高级或置于own前)完全，十足;完全同样;', 'The problem and the answer are very simple...', '问题和答案都非常简单。');
INSERT INTO `words` VALUES (10010, 'vessel', 'ˈvesl', 'n.', '大船;轮船;(盛液体的)容器，器皿;', 'A Moroccan fishing vessel and a South Korean cargo ship collided in rough seas.', '一艘摩洛哥渔船与一艘韩国货船在波涛汹涌的大海上相撞了。');
INSERT INTO `words` VALUES (10011, 'veteran', 'ˈvetərən', 'n.', '经验丰富的人;老手;退伍军人;', 'They approved a $1.1 billion package of pay increases for the veterans of the Persian Gulf War.', '他们通过了对参加过波斯湾战争的老兵增发共计11亿美元的一揽子提案。');
INSERT INTO `words` VALUES (10012, 'veto', 'ˈviːtəʊ', 'n.', '否决权;拒绝认可;禁止;', 'De Gaulle vetoed Britain\'s application to join the EEC...', '戴高乐驳回了英国加入欧洲经济共同体的申请。');
INSERT INTO `words` VALUES (10013, 'vex', 'veks', 'v.', '使恼火;使烦恼;使忧虑;', 'It vexed me to think of others gossiping behind my back...', '一想到别人在背后说我闲话，我就很恼火。');
INSERT INTO `words` VALUES (10014, 'vexed', 'vekst', 'adj.', '(指问题等)棘手的，伤脑筋的;(指人)恼火，烦恼，伤脑筋;', 'Ministers have begun work on the vexed issue of economic union...', '部长们开始着手处理经济联盟这个棘手的问题。');
INSERT INTO `words` VALUES (10015, 'via', 'ˈvaɪə', 'prep.', '经由，经过(某一地方);通过，凭借(某人、系统等);', 'We drove via Lovech to the old Danube town of Ruse...', '我们开车经由洛维奇到达了多瑙河畔的古镇鲁塞。');
INSERT INTO `words` VALUES (10016, 'viable', 'ˈvaɪəbl', 'adj.', '可实施的;切实可行的;能独立发展的;', 'Cash alone will not make Eastern Europe\'s banks viable...', '仅靠现金不能使东欧的银行维持下去。');
INSERT INTO `words` VALUES (10017, 'vibrate', 'vaɪˈbreɪt', 'v.', '(使)振动，颤动，摆动;', 'The ground shook and the cliffs seemed to vibrate...', '大地在摇晃，悬崖好像在颤动。');
INSERT INTO `words` VALUES (10018, 'vibration', 'vaɪˈbreɪʃn', 'n.', '震动;颤动;抖动;', 'The ground shook and the cliffs seemed to vibrate...', '大地在摇晃，悬崖好像在颤动。');
INSERT INTO `words` VALUES (10019, 'vice', 'vaɪs', 'n.', '(与性或毒品有关的)罪行;恶行;不道德行为;', 'His only vice is to get drunk on champagne after concluding a successful piece of business...', '他唯一的缺点就是在成功做成一笔生意后会大喝香槟而醉。');
INSERT INTO `words` VALUES (10020, 'vicinity', 'vəˈsɪnəti', 'n.', '周围地区;邻近地区;附近;', 'There were a hundred or so hotels in the vicinity of the railway station...', '在火车站附近大约有100家旅馆。');
INSERT INTO `words` VALUES (10021, 'vicious', 'ˈvɪʃəs', 'adj.', '狂暴的;残酷的;凶猛危险的;', 'He was a cruel and vicious man...', '他是一个残忍凶狠的男人。');
INSERT INTO `words` VALUES (10022, 'victim', 'ˈvɪktɪm', 'n.', '受害者;罹难者;罹病者;', 'Not all the victims survived...', '并非所有受害者都得以幸存。');
INSERT INTO `words` VALUES (10023, 'victims', 'ˈvɪktɪmz', 'n.', '受害者;罹难者;罹病者;', 'Not all the victims survived...', '并非所有受害者都得以幸存。');
INSERT INTO `words` VALUES (10024, 'victorious', 'vɪkˈtɔːriəs', 'adj.', '胜利的;获胜的;战胜的;', 'In 1978 he played for the victorious Argentinian side in the World Cup.', '1978年，他效力于获得世界杯冠军的阿根廷队。');
INSERT INTO `words` VALUES (10025, 'victory', 'ˈvɪktəri', 'n.', '胜利;成功;', 'Union leaders are heading for victory in their battle over workplace rights.', '工会领袖们即将赢得争取工作场所权利斗争的胜利。');
INSERT INTO `words` VALUES (10026, 'video', 'ˈvɪdiəʊ', 'n.', '录像带;盒式录像带;(指制品)录像，录影;', 'We were watching videos with her.', '我们当时在和她一起看录像。');
INSERT INTO `words` VALUES (10027, 'view', 'vjuː', 'n.', '(个人的)看法，意见，见解;态度;(理解或思维的)方法，方式;', 'Washington and Moscow are believed to have similar views on Kashmir...', '据信华盛顿和莫斯科在克什米尔问题上的看法相似。');
INSERT INTO `words` VALUES (10028, 'viewer', 'ˈvjuːə(r)', 'n.', '电视观众;观看者;观察者;', 'These programmes are each watched by around 19 million viewers every week.', '这些电视节目的观众各自都在每周1,900万左右。');
INSERT INTO `words` VALUES (10029, 'viewpoint', 'ˈvjuːpɔɪnt', 'n.', '观点;看法;角度;', 'The novel is shown from the girl\'s viewpoint...', '这部小说是从这个女孩的视角来叙述的。');
INSERT INTO `words` VALUES (10030, 'views', 'vjuːz', 'n.', '(个人的)看法，意见，见解;态度;(理解或思维的)方法，方式;', 'Washington and Moscow are believed to have similar views on Kashmir...', '据信华盛顿和莫斯科在克什米尔问题上的看法相似。');
INSERT INTO `words` VALUES (10031, 'vigilant', 'ˈvɪdʒɪlənt', 'adj.', '警觉的;警惕的;警戒的;', 'He warned the public to be vigilant and report anything suspicious...', '他警告公众要提高警惕，遇到任何可疑情况都要报告。');
INSERT INTO `words` VALUES (10032, 'vigorous', 'ˈvɪɡərəs', 'adj.', '充满活力的;果断的;精力充沛的;', 'Very vigorous exercise can increase the risk of heart attacks...', '运动太过剧烈会增大心脏病发作的风险。');
INSERT INTO `words` VALUES (10033, 'villa', 'ˈvɪlə', 'n.', '度假别墅;(尤指南欧的)乡间庄园;(城内的)豪宅;', 'He lives in a secluded five-bedroom luxury villa.', '他住在一个位置偏远、有5个卧室的豪华乡间别墅里。');
INSERT INTO `words` VALUES (10034, 'village', 'ˈvɪlɪdʒ', 'n.', '村庄;村镇;乡村居民;', 'He lives quietly in the country in a village near Lahti.', '他在拉赫蒂附近的一个乡村里过着平静的日子。');
INSERT INTO `words` VALUES (10035, 'vindicate', 'ˈvɪndɪkeɪt', 'v.', '证实;证明有理;澄清(责难或嫌疑);', 'The director said he had been vindicated by the experts\' report...', '主任说专家们的报告证明了他是正确的。');
INSERT INTO `words` VALUES (10036, 'vine', 'vaɪn', 'n.', '葡萄藤;藤本植物;攀缘植物;', 'Every square metre of soil was used, mainly for olives, vines, and almonds.', '每一寸土地都利用上了，主要用来种植橄榄树、葡萄藤和杏树。');
INSERT INTO `words` VALUES (10037, 'violate', 'ˈvaɪəleɪt', 'v.', '违反，违犯，违背(法律、协议等);侵犯(隐私等);使人不得安宁;', 'They went to prison because they violated the law...', '他们触犯了法律，因此坐了牢。');
INSERT INTO `words` VALUES (10038, 'violation', 'ˌvaɪə\'leɪʃ(ə)n', 'n.', '违反，违法，违章，越轨，触犯法令;侵犯；破坏;违例，犯规;', 'They went to prison because they violated the law...', '他们触犯了法律，因此坐了牢。');
INSERT INTO `words` VALUES (10039, 'violence', 'ˈvaɪələns', 'n.', '暴力;暴行;狂热;', 'Twenty people were killed in the violence...', '有20人在这一暴力事件中丧生。');
INSERT INTO `words` VALUES (10040, 'violent', 'ˈvaɪələnt', 'adj.', '暴力的;强暴的;感情强烈的;', 'A quarter of current inmates have committed violent crimes.', '现在关押的犯人中有1/4是暴力犯罪。');
INSERT INTO `words` VALUES (10041, 'violin', 'ˌvaɪəˈlɪn', 'n.', '小提琴;', 'Lizzie used to play the violin.', '莉齐曾经拉过小提琴。');
INSERT INTO `words` VALUES (10042, 'viral', 'ˈvaɪrəl', 'adj.', '病毒的;病毒性的;病毒引起的;', '...a 65-year-old patient suffering from severe viral pneumonia.', '感染了严重病毒性肺炎的65岁病人');
INSERT INTO `words` VALUES (10043, 'virgin', 'ˈvɜːdʒɪn', 'n.', '处女;童男;童贞马利亚(耶稣之母);', 'I was a virgin until I was thirty years old...', '我30岁前一直是个处女。');
INSERT INTO `words` VALUES (10044, 'virtual', 'ˈvɜːtʃuəl', 'adj.', '很接近的;几乎…的;事实上的;', 'Argentina came to a virtual standstill while the game was being played...', '在比赛进行的时候，阿根廷全国几乎陷入了停顿状态。');
INSERT INTO `words` VALUES (10045, 'virtually', 'ˈvɜːtʃuəli', 'adv.', '几乎;差不多;事实上;', 'Virtually all cooking was done over coal-fired ranges...', '差不多所有的烹饪都是在燃煤灶上进行的。');
INSERT INTO `words` VALUES (10046, 'virtue', 'ˈvɜːtʃuː', 'n.', '高尚的道德;正直的品性;德行;', 'Virtue is not confined to the Christian world...', '善行并不是仅限于基督教世界。');
INSERT INTO `words` VALUES (10047, 'virtuous', 'ˈvɜːtʃuəs', 'adj.', '品行端正的;品德高的;有道德的;', 'Louis was shown as an intelligent, courageous and virtuous family man.', '路易斯在人们眼里是一个智慧、勇敢、品德高尚的顾家男人。');
INSERT INTO `words` VALUES (10048, 'virulent', 'ˈvɪrələnt', 'adj.', '致命的;恶性的;剧毒的;', 'Now he faces virulent attacks from the Italian media...', '现在他面临着意大利媒体的恶毒攻击。');
INSERT INTO `words` VALUES (10049, 'virus', 'ˈvaɪrəs', 'n.', '病毒;滤过性病毒;病毒性疾病;', 'There are many different strains of flu virus.', '流感病毒有很多不同类型。');
INSERT INTO `words` VALUES (10050, 'visa', 'ˈviːzə', 'n.', '(护照的)签证;', 'His visitor\'s visa expired.', '他的访问签证过期了。');
INSERT INTO `words` VALUES (10051, 'visceral', 'ˈvɪsərəl', 'adj.', '(未经过认真思考而)出自内心的，发自肺腑的;内脏的;脏腑的;', 'I never overcame a visceral antipathy for the monarchy.', '我从未能够消除内心对君主政体的反感。');
INSERT INTO `words` VALUES (10052, 'viscous', 'ˈvɪskəs', 'adj.', '黏稠的;黏滞的;', '...dark, viscous blood.', '黑色、黏稠的血液');
INSERT INTO `words` VALUES (10053, 'visibility', 'ˌvɪzəˈbɪləti', 'n.', '可见度;能见度;能见距离;', 'Visibility was poor.', '能见度很差。');
INSERT INTO `words` VALUES (10054, 'visible', 'ˈvɪzəbl', 'adj.', '看得见的;可见的;明显的;', 'The warning lights were clearly visible...', '警示信号灯清晰可见。');
INSERT INTO `words` VALUES (10055, 'vision', 'ˈvɪʒn', 'n.', '视力;视野;想象;', 'I have a vision of a society that is free of exploitation and injustice...', '我希望建立一个没有剥削和不公的社会。');
INSERT INTO `words` VALUES (10056, 'visit', 'ˈvɪzɪt', 'v.', '访问;拜访;看望;', 'He wanted to visit his brother in Worcester...', '他想去看望住在伍斯特的哥哥。');
INSERT INTO `words` VALUES (10057, 'visitor', 'ˈvɪzɪtə(r)', 'n.', '来访者;访问者;参观者;', 'The other day we had some visitors from Switzerland...', '前几天我们接待了几位来自瑞士的参观者。');
INSERT INTO `words` VALUES (10058, 'visitors', 'ˈvɪzɪtəz', 'n.', '来访者;访问者;参观者;', 'The other day we had some visitors from Switzerland...', '前几天我们接待了几位来自瑞士的参观者。');
INSERT INTO `words` VALUES (10059, 'visual', 'ˈvɪʒuəl', 'adj.', '视力的;视觉的;', '...the graphic visual depiction of violence.', '对暴力赤裸裸的影像描绘');
INSERT INTO `words` VALUES (10060, 'vital', 'ˈvaɪtl', 'adj.', '必不可少的;对…极重要的;生命的;', 'The port is vital to supply relief to millions of drought victims...', '这个港口对向数百万旱灾灾民提供救援物资是至关重要的。');
INSERT INTO `words` VALUES (10061, 'vitamin', 'ˈvɪtəmɪn', 'n.', '维生素;维他命;', 'Butter, margarine, and oily fish are all good sources of vitamin D.', '黄油、人造黄油和多脂鱼都含有丰富的维生素D。');
INSERT INTO `words` VALUES (10062, 'vivid', 'ˈvɪvɪd', 'adj.', '清晰的;生动的;逼真的;', 'People of my generation who lived through World War II have vivid memories of confusion and incompetence...', '我们这一代经历过二战的人都真切地记得当时的迷惘和无助。');
INSERT INTO `words` VALUES (10063, 'vividly', '\'vɪvɪdli', 'adv.', '生动地;', 'People of my generation who lived through World War II have vivid memories of confusion and incompetence...', '我们这一代经历过二战的人都真切地记得当时的迷惘和无助。');
INSERT INTO `words` VALUES (10064, 'vocabulary', 'vəˈkæbjələri', 'n.', '(某人掌握或使用的)词汇，词汇量;(某一语言的)词汇;(某学科中所使用的)词汇;', 'His speech is immature, his vocabulary limited...', '他说话很幼稚，使用的词汇有限。');
INSERT INTO `words` VALUES (10065, 'vocal', 'ˈvəʊkl', 'adj.', '嗓音的;发声的;大声表达的;', 'He has been very vocal in his displeasure over the results...', '他直言不讳地表达了他对结果的不满。');
INSERT INTO `words` VALUES (10066, 'vocation', 'vəʊˈkeɪʃn', 'n.', '(认为特别适合自己的)工作，职业，生活方式;(认为某种工作或生活方式特别适合自己的)信心，使命感;圣召;', 'It could well be that he has a real vocation...', '很可能他是找到了自己真正的使命。');
INSERT INTO `words` VALUES (10067, 'vocational', 'vəʊˈkeɪʃənl', 'adj.', '职业的;职业技术的;业务知识的;', '...a course designed to provide vocational training in engineering...', '工程学的职业培训课程');
INSERT INTO `words` VALUES (10068, 'vogue', 'vəʊɡ', 'n.', '流行;时髦;风行;', 'Despite the vogue for so-called health teas, there is no evidence that they are any healthier...', '尽管现在流行所谓的健康茶饮，但没有证据证明这些茶更有益健康。');
INSERT INTO `words` VALUES (10069, 'voice', 'vɔɪs', 'n.', '嗓音;说话声;歌唱声;', 'Miriam\'s voice was strangely calm...', '米里亚姆的声音异乎寻常地平静。');
INSERT INTO `words` VALUES (10070, 'voiceless', 'ˈvɔɪsləs', 'adj.', '清音的;', '...the voiceless \'th\'.', '发清音的th');
INSERT INTO `words` VALUES (10071, 'void', 'vɔɪd', 'n.', '空间;空白;真空;', 'His death has left a void in the cricketing world which can never be filled.', '他的死给板球界留下一个永远无法填补的空白。');
INSERT INTO `words` VALUES (10072, 'volatile', 'ˈvɒlətaɪl', 'adj.', '易变的;无定性的;无常性的;', 'There have been riots before and the situation is volatile...', '以前曾发生过暴乱，现在局势不太稳定。');
INSERT INTO `words` VALUES (10073, 'volatility', 'ˌvɒləˈtɪlɪti', 'n.', '不稳定性，暂时性;挥发性（度）;', 'There have been riots before and the situation is volatile...', '以前曾发生过暴乱，现在局势不太稳定。');
INSERT INTO `words` VALUES (10074, 'volcano', 'vɒlˈkeɪnəʊ', 'n.', '火山;', 'The volcano erupted last year killing about 600 people...', '去年那座火山喷发致使约600人遇难。');
INSERT INTO `words` VALUES (10075, 'volley', 'ˈvɒli', 'n.', '截击空中球;凌空击球(或踢球);(子弹的)群射，齐发;', 'He volleyed the ball spectacularly into the far corner of the net...', '他一脚漂亮的凌空抽射，把球踢进了球门的远角。');
INSERT INTO `words` VALUES (10076, 'voltage', 'ˈvəʊltɪdʒ', 'n.', '电压;伏特数;', 'The systems are getting smaller and using lower voltages.', '这些系统变得更小，使用的电压也更低。');
INSERT INTO `words` VALUES (10077, 'volume', 'ˈvɒljuːm', 'n.', '体积;容积;容量;', 'Senior officials will be discussing how the volume of sales might be reduced.', '高层领导将要讨论销售量如何削减。');
INSERT INTO `words` VALUES (10078, 'voluntary', 'ˈvɒləntri', 'adj.', '自愿的;志愿的;主动的;', 'Attention is drawn to a special voluntary course in Commercial French...', '一门特别的商务法语选修课引起了大家的关注。');
INSERT INTO `words` VALUES (10079, 'volunteer', 'ˌvɒlənˈtɪə(r)', 'n.', '义务工作者;志愿者;自告奋勇者;', 'She now helps in a local school as a volunteer three days a week...', '目前她在当地的一家学校做志愿工作，一周去3天。');
INSERT INTO `words` VALUES (10080, 'vomit', 'ˈvɒmɪt', 'v.', '呕;吐;呕吐;', 'Any product made from cow\'s  milk made him vomit...', '任何乳制品都会让他呕吐。');
INSERT INTO `words` VALUES (10081, 'vote', 'vəʊt', 'n.', '选票;票;投票;', 'He walked to the local polling centre to cast his vote...', '他走到当地的投票中心投了票。');
INSERT INTO `words` VALUES (10082, 'voter', 'ˈvəʊtə(r)', 'n.', '(尤指政治性选举的)投票人，选举人，有选举权的人;', 'The turnout was at least 62 percent of registered voters...', '至少有62％的登记选民参加了投票。');
INSERT INTO `words` VALUES (10083, 'voting', 'ˈvəʊtɪŋ', 'n.', '投票;选举;表决;', 'He walked to the local polling centre to cast his vote...', '他走到当地的投票中心投了票。');
INSERT INTO `words` VALUES (10084, 'voucher', 'ˈvaʊtʃə(r)', 'n.', '代币券;票券;', 'The winners will each receive a voucher for a pair of cinema tickets.', '获胜者每人将会获得两张电影票的代金券。');
INSERT INTO `words` VALUES (10085, 'vow', 'vaʊ', 'n.', '(尤指宗教的)誓，誓言，誓约;', 'While many models vow to go back to college, few do...', '很多模特发誓要重回大学校园，但很少有人做到。');
INSERT INTO `words` VALUES (10086, 'vowel', 'ˈvaʊəl', 'n.', '元音;元音字母;', 'The vowel in words like \'my\' and \'thigh\' is not very difficult.', '单词my和thigh中的元音并不难发。');
INSERT INTO `words` VALUES (10087, 'voyage', 'ˈvɔɪɪdʒ', 'n.', '航行;(尤指)航海，航天;', 'He aims to follow Columbus\'s voyage to the West Indies.', '他打算重走哥伦布到达西印度群岛的航行路线。');
INSERT INTO `words` VALUES (10088, 'vulgar', 'ˈvʌlɡə(r)', 'adj.', '庸俗的;粗俗的;粗野的;', 'I think it\'s a very vulgar house...', '我觉得这所房子很俗气。');
INSERT INTO `words` VALUES (10089, 'vulnerability', 'ˌvʌlnərə\'bɪlətɪ', 'n.', '弱点;脆弱性;易伤性;', 'Old people are particularly vulnerable members of our society.', '老年人是社会中尤为明显的弱势群体。');
INSERT INTO `words` VALUES (10090, 'vulnerable', 'ˈvʌlnərəbl', 'adj.', '(身体上或感情上)脆弱的，易受…伤害的;', 'Old people are particularly vulnerable members of our society.', '老年人是社会中尤为明显的弱势群体。');
INSERT INTO `words` VALUES (10091, 'wade', 'weɪd', 'v.', '跋涉，涉，蹚(水或淤泥等);', 'Rescuers had to wade across a river to reach them...', '救援者必须蹚过一条河才能靠近他们。');
INSERT INTO `words` VALUES (10092, 'wag', 'wæɡ', 'v.', '(狗)摇，摆动(尾巴);(狗尾巴)摇，摆动;摇(头或手指，常表示不赞成);', 'The dog was biting, growling and wagging its tail.', '这条狗撕咬着，咆哮着，摇晃着尾巴。');
INSERT INTO `words` VALUES (10093, 'wage', 'weɪdʒ', 'n.', '(通常指按周领的)工资，工钱;', 'His wages have gone up...', '他涨工资了。');
INSERT INTO `words` VALUES (10094, 'wager', 'ˈweɪdʒə(r)', 'n.', '打赌;', 'Just because people wagered on the Yankees did not mean that they liked them...', '人们将赌注下在扬基队上并不说明就喜欢他们。');
INSERT INTO `words` VALUES (10095, 'wail', 'weɪl', 'v.', '(因悲伤或疼痛)哭号，恸哭;大声呼叫;哀号;', 'The women began to wail in mourning.', '女人们开始哀哭起来。');
INSERT INTO `words` VALUES (10096, 'waist', 'weɪst', 'n.', '腰;腰部;(衣服的)腰部;', 'Ricky kept his arm round her waist...', '里基一直揽着她的腰。');
INSERT INTO `words` VALUES (10097, 'wait', 'weɪt', 'v.', '等;等待;等候;', 'I walk to a street corner and wait for the school bus...', '我走到街角等校车。');
INSERT INTO `words` VALUES (10098, 'waiting', 'ˈweɪtɪŋ', 'n.', '等候;等待;当侍者;', 'I walk to a street corner and wait for the school bus...', '我走到街角等校车。');
INSERT INTO `words` VALUES (10099, 'waive', 'weɪv', 'v.', '放弃(权利、要求等);', 'He pleaded guilty to the murders of three boys and waived his right to appeal.', '他承认谋杀了3个男孩，并放弃上诉。');
INSERT INTO `words` VALUES (10100, 'waken', 'ˈweɪkən', 'v.', '醒来;睡醒;唤醒;', 'The noise of a door slamming wakened her...', '砰的摔门声把她吵醒了。');
INSERT INTO `words` VALUES (10101, 'walk', 'wɔːk', 'v.', '走;行走;步行;', 'Rosanna and Forbes walked in silence for some while...', '罗莎娜和福布斯默不作声地走了一会儿。');
INSERT INTO `words` VALUES (10102, 'wall', 'wɔːl', 'n.', '城墙;围墙;墙;', 'Kathryn leaned against the wall of the church...', '凯瑟琳倚靠在教堂的墙上。');
INSERT INTO `words` VALUES (10103, 'wallpaper', 'ˈwɔːlpeɪpə(r)', 'n.', '壁纸;墙纸;桌面背景;', '...the wallpaper in the bedroom.', '卧室内的壁纸');
INSERT INTO `words` VALUES (10104, 'walnut', 'ˈwɔːlnʌt', 'n.', '核桃;胡桃;核桃树;', '...chopped walnuts.', '碎核桃仁');
INSERT INTO `words` VALUES (10105, 'wan', 'wɒn', 'adj.', '苍白无力的;无血色的;憔悴的;', 'He looked wan and tired...', '他脸色苍白，面带倦容。');
INSERT INTO `words` VALUES (10106, 'wand', 'wɒnd', 'n.', '魔杖;棍;棒;', 'You can\'t simply wave a wand and get rid of nuclear weapons.', '你们不要以为魔杖一挥便能消除核武器。');
INSERT INTO `words` VALUES (10107, 'wander', 'ˈwɒndə(r)', 'v.', '漫游;游荡;闲逛;', 'When he got bored he wandered around the fair...', '他烦了就去集市上逛逛。');
INSERT INTO `words` VALUES (10108, 'wane', 'weɪn', 'v.', '衰落;衰败;败落;', 'While his interest in these sports began to wane, a passion for rugby developed.', '他对这些运动项目的兴趣开始减退，倒是对橄榄球开始有了热情。');
INSERT INTO `words` VALUES (10109, 'want', 'wɒnt', 'v.', '要;想要;希望;', 'I want a drink...', '我想喝点什么。');
INSERT INTO `words` VALUES (10110, 'war', 'wɔː(r)', 'n.', '战争;战争状态;(群体、公司、国家之间的)竞争，斗争，对抗，冲突;', 'He spent part of the war in the National Guard...', '战争期间，他在国民警卫队呆过一段时间。');
INSERT INTO `words` VALUES (10111, 'ward', 'wɔːd', 'n.', '病房;病室;(英国城市中可选出一位地方议员的)区，选区;', 'A toddler was admitted to the emergency ward with a wound in his chest.', '一个蹒跚学步的小孩因胸部受伤被送进急救室。');
INSERT INTO `words` VALUES (10112, 'warden', 'ˈwɔːdn', 'n.', '管理人;看守人;监护人;', 'He was a warden at the local parish church...', '他是当地教堂的管理员。');
INSERT INTO `words` VALUES (10113, 'ware', 'weə(r)', 'n.', '用某材料(或以某方式、在某地)制造的物品;作…用的器皿;…室的物品;', '...boxes of cheap glassware.', '一盒盒便宜的玻璃制品');
INSERT INTO `words` VALUES (10114, 'warehousing', 'ˈweəhaʊzɪŋ', 'n.', '仓储;仓储业;', 'All donations go towards the cost of warehousing.', '所有捐款都用作仓储费用。');
INSERT INTO `words` VALUES (10115, 'warfare', 'ˈwɔːfeə(r)', 'n.', '战;作战;战争;', '...the threat of chemical warfare.', '化学战的威胁');
INSERT INTO `words` VALUES (10116, 'warm', 'wɔːm', 'adj.', '温暖的;暖和的;保暖的;', 'Wheat is grown in places which have cold winters and warm, dry summers...', '小麦适宜在冬季寒冷而夏季温暖干燥的地方生长。');
INSERT INTO `words` VALUES (10117, 'warmly', 'ˈwɔːmli', 'adv.', '热情地；温暖地；亲切地;', 'Wheat is grown in places which have cold winters and warm, dry summers...', '小麦适宜在冬季寒冷而夏季温暖干燥的地方生长。');
INSERT INTO `words` VALUES (10118, 'warmth', 'wɔːmθ', 'n.', '温暖;暖和;热情;', 'She went further into the room, drawn by the warmth of the fire...', '暖暖的炉火吸引着她继续向前走进那间屋子。');
INSERT INTO `words` VALUES (10119, 'warn', 'wɔːn', 'v.', '提醒注意(可能发生的事);使警惕;劝告(使有所防备);', 'When I had my first baby friends warned me that children were expensive...', '当我有了第一个孩子时，朋友们提醒我说养孩子是很花钱的。');
INSERT INTO `words` VALUES (10120, 'warning', 'ˈwɔːnɪŋ', 'n.', '(就可能发生的意外等提出的)警告，警示;先兆;(就将要遭受的处罚等提出的)警告，警戒;', 'The minister gave a warning that if war broke out, it would be catastrophic...', '部长警告说，战争一旦爆发将会是灾难性的。');
INSERT INTO `words` VALUES (10121, 'warrant', 'ˈwɒrənt', 'n.', '执行令;授权令;(接受款项、服务等的)凭单，许可证;', 'The allegations are serious enough to warrant an investigation...', '这些指控很严重，有必要进行一番调查。');
INSERT INTO `words` VALUES (10122, 'warranted', 'ˈwɒrəntɪd', 'v.', '使有必要;使正当;使恰当;', 'The allegations are serious enough to warrant an investigation...', '这些指控很严重，有必要进行一番调查。');
INSERT INTO `words` VALUES (10123, 'warranty', 'ˈwɒrənti', 'n.', '(商品)保用单;', '...a twelve month warranty...', '为期12个月的保修单');
INSERT INTO `words` VALUES (10124, 'wary', 'ˈweəri', 'adj.', '(对待人或事物时)小心的，谨慎的，留神的，小心翼翼的;', 'People did not teach their children to be wary of strangers...', '过去人们不会教育孩子提防陌生人。');
INSERT INTO `words` VALUES (10125, 'wash', 'wɒʃ', 'v.', '洗;洗涤;洗澡;', 'He got a job washing dishes in a pizza parlour...', '他在比萨店里找到一份洗盘子的工作。');
INSERT INTO `words` VALUES (10126, 'wastage', 'ˈweɪstɪdʒ', 'n.', '耗费;损耗;浪费;', '...a series of measures to prevent the wastage of water...', '一系列节水措施');
INSERT INTO `words` VALUES (10127, 'waste', 'weɪst', 'v.', '浪费;滥用;白费;', 'There could be many reasons and he was not going to waste time speculating on them...', '原因可能有很多，他不打算浪费时间对此妄加猜测。');
INSERT INTO `words` VALUES (10128, 'wasteful', 'ˈweɪstfl', 'adj.', '浪费的;挥霍的;', 'This kind of training is ineffective, and wasteful of scarce resources...', '这种训练没有效率，而且是对稀缺资源的浪费。');
INSERT INTO `words` VALUES (10129, 'watchful', 'ˈwɒtʃfl', 'adj.', '注意的;警惕的;提防的;', 'The best thing is to be watchful and see the family doctor for any change in your normal health...', '最佳的做法是保持警惕，一旦你的身体状况有任何变化就立即去看家庭医生。');
INSERT INTO `words` VALUES (10130, 'water', 'ˈwɔːtə(r)', 'n.', '水;大片的水;水域;', 'Get me a glass of water.', '给我杯水。');
INSERT INTO `words` VALUES (10131, 'waterfall', 'ˈwɔːtəfɔːl', 'n.', '瀑布;', '...Angel Falls, the world\'s highest waterfall.', '安赫尔瀑布，世界上最高的瀑布');
INSERT INTO `words` VALUES (10132, 'waterfront', 'ˈwɔːtəfrʌnt', 'n.', '滨水路;滨水区;码头区;', 'They went for a stroll along the waterfront.', '他们沿着滨水区漫步。');
INSERT INTO `words` VALUES (10133, 'waterproof', 'ˈwɔːtəpruːf', 'adj.', '不透水的;防水的;耐水的;', 'Take waterproof clothing — Orkney weather is unpredictable...', '带上防水的衣服吧——奥克尼的天气可是变幻莫测的。');
INSERT INTO `words` VALUES (10134, 'watertight', 'ˈwɔːtətaɪt', 'adj.', '不透水的;防水的;水密的;', 'The flask is completely watertight, even when laid on its side...', '即使平放，这个真空水杯也是滴水不漏。');
INSERT INTO `words` VALUES (10135, 'watery', 'ˈwɔːtəri', 'adj.', '水的;似水的;含水的;', 'A watery light began to show through the branches...', '一缕微弱的光线透过树枝。');
INSERT INTO `words` VALUES (10136, 'watt', 'wɒt', 'n.', '瓦，瓦特(电功率单位);', 'Use a 3 amp fuse for equipment up to 720 watts.', '720 瓦以下的设备使用3安培的保险丝。');
INSERT INTO `words` VALUES (10137, 'wave', 'weɪv', 'n.', '海浪;波浪;波涛;', 'Jessica caught sight of Lois and waved to her...', '杰茜卡看到了洛伊丝，就冲她挥手致意。');
INSERT INTO `words` VALUES (10138, 'wavelength', 'ˈweɪvleŋθ', 'n.', '波长;(广播电台等占用的)频道，波道;', 'Sunlight consists of different wavelengths of radiation...', '阳光由几种不同波长的射线组成。');
INSERT INTO `words` VALUES (10139, 'waver', 'ˈweɪvə(r)', 'v.', '减弱;动摇;颤抖;', 'Some military commanders wavered over whether to support the coup...', '军队的一些指挥官在犹豫是否支持政变。');
INSERT INTO `words` VALUES (10140, 'waving', 'ˈweɪvɪŋ', 'v.', '挥手;招手;摆手;', 'Jessica caught sight of Lois and waved to her...', '杰茜卡看到了洛伊丝，就冲她挥手致意。');
INSERT INTO `words` VALUES (10141, 'wax', 'wæks', 'n.', '蜡;蜂蜡;动物蜡;', 'There were coloured candles which had spread pools of wax on the furniture...', '彩色蜡烛在家具上留下一摊摊蜡油。');
INSERT INTO `words` VALUES (10142, 'way', 'weɪ', 'n.', '方法;手段;途径;', 'Freezing isn\'t a bad way of preserving food...', '冷冻不失为一种保存食物的好方法。');
INSERT INTO `words` VALUES (10143, 'weak', 'wiːk', 'adj.', '虚弱的;无力的;不牢固的;', 'I was too weak to move or think or speak...', '我太虚弱了，无法动弹，无法思考，也无法说话。');
INSERT INTO `words` VALUES (10144, 'weaken', 'ˈwiːkən', 'v.', '(使)虚弱，衰弱;减弱;削弱;', 'The recession has weakened so many firms that many can no longer survive...', '经济衰退使许多公司实力大损，不少难以为继。');
INSERT INTO `words` VALUES (10145, 'weakness', 'ˈwiːknəs', 'n.', '软弱;虚弱;疲软;', 'Stephen himself had a weakness for cats...', '史蒂芬本人偏爱猫。');
INSERT INTO `words` VALUES (10146, 'wealth', 'welθ', 'n.', '钱财;财产;财物;', 'Economic reform has brought relative wealth to peasant farmers...', '经济改革给农民带来了相对的财富。');
INSERT INTO `words` VALUES (10147, 'wealthy', 'ˈwelθi', 'adj.', '富有的;富裕的;富饶的;', '...a wealthy international businessman.', '富有的国际大亨');
INSERT INTO `words` VALUES (10148, 'weapon', 'ˈwepən', 'n.', '武器;兵器;凶器;', '...nuclear weapons.', '核武器');
INSERT INTO `words` VALUES (10149, 'weapons', 'ˈwɛpənz', 'n.', '武器;兵器;凶器;', '...nuclear weapons.', '核武器');
INSERT INTO `words` VALUES (10150, 'wear', 'weə(r)', 'v.', '穿;戴;佩戴;', 'He was wearing a brown uniform...', '他当时穿着褐色的制服。');
INSERT INTO `words` VALUES (10151, 'weary', 'ˈwɪəri', 'adj.', '(尤指长时间努力工作后)疲劳的，疲倦的，疲惫的;使人疲劳的;使人厌烦的;', 'Rachel looked pale and weary.', '雷切尔看起来脸色苍白，疲惫不堪。');
INSERT INTO `words` VALUES (10152, 'weather', 'ˈweðə(r)', 'n.', '天气;气象;气象预报;', 'The weather was bad...', '天气很糟。');
INSERT INTO `words` VALUES (10153, 'weatherproof', 'ˈweðəpruːf', 'adj.', '不受气候影响的;全天候的;防风雨的;', 'Use a weatherproof rucksack to carry your camera and lenses around in.', '用防风雨的帆布背包装照相机和镜头。');
INSERT INTO `words` VALUES (10154, 'web', 'web', 'n.', '网状物;网络;错综复杂的事物;', '...the spider\'s web in the window.', '窗户上的蜘蛛网');
INSERT INTO `words` VALUES (10155, 'wedding', 'ˈwedɪŋ', 'n.', '婚礼;结婚庆典;', 'Most Britons want a traditional wedding.', '大多数英国人都想要传统的婚礼。');
INSERT INTO `words` VALUES (10156, 'wedge', 'wedʒ', 'n.', '楔子;三角木;楔形物;', 'I shut the shed door and wedged it with a log of wood...', '我关上小屋的门，并用一根木棍将门楔住。');
INSERT INTO `words` VALUES (10157, 'wednesday', 'ˈwenzdeɪ', 'n.', '星期三;', 'Come and have supper with us on Wednesday, if you\'re free...', '如果你有空的话，周三晚上来和我们一起吃晚饭吧。');
INSERT INTO `words` VALUES (10158, 'weed', 'wiːd', 'n.', '杂草，野草(尤指庄稼或花园中的);水草;烟草;', '...a garden overgrown with weeds.', '长满杂草的园子');
INSERT INTO `words` VALUES (10159, 'week', 'wiːk', 'n.', '周;星期;礼拜;', 'I had a letter from my mother last week...', '上周我收到了母亲的一封信。');
INSERT INTO `words` VALUES (10160, 'weekday', 'ˈwiːkdeɪ', 'n.', '周工作日(星期一至星期五的任何一天);', 'If you want to avoid the crowds, it\'s best to come on a weekday...', '如果想避免人多，最好在周一到周五来。');
INSERT INTO `words` VALUES (10161, 'weekend', 'ˌwiːkˈend', 'n.', '星期六和星期日;周末;星期六和星期日(或略长一点的)休息时间;', 'She had agreed to have dinner with him in town the following weekend...', '她答应下一个周末和他在城里共进晚餐。');
INSERT INTO `words` VALUES (10162, 'weekly', 'ˈwiːkli', 'adj.', '每周的;', 'Each course comprises 10-12 informal weekly meetings...', '每门课程都包括10至12次每周一次的非正式讨论会。');
INSERT INTO `words` VALUES (10163, 'weep', 'wiːp', 'v.', '(通常因悲伤)哭泣，流泪;流出，渗出(液体);', 'She wanted to laugh and weep all at once...', '她哭笑不得。');
INSERT INTO `words` VALUES (10164, 'weigh', 'weɪ', 'v.', '有…重;重;称重量，量体重(通常用磅秤);', 'It weighs nearly 27 kilos (about 65 pounds)...', '它重近27千克(约65磅)。');
INSERT INTO `words` VALUES (10165, 'weight', 'weɪt', 'n.', '重量;分量;重;', 'What is your height and weight?...', '你的身高和体重是多少？');
INSERT INTO `words` VALUES (10166, 'weird', 'wɪəd', 'adj.', '奇异的;不寻常的;怪诞的;', 'That first day was weird...', '第一天有些怪异。');
INSERT INTO `words` VALUES (10167, 'welcome', 'ˈwelkəm', 'v.', '(打招呼)欢迎(某人的到来);欢迎(新来的人);迎(新);', 'Several people came by to welcome me...', '有几个人过来迎接我。');
INSERT INTO `words` VALUES (10168, 'weld', 'weld', 'v.', '焊接;熔接;锻接;', 'It\'s possible to weld stainless steel to ordinary steel...', '不锈钢是有可能和普通钢焊接在一起的。');
INSERT INTO `words` VALUES (10169, 'welfare', 'ˈwelfeə(r)', 'n.', '(个体或群体的)幸福，安全与健康;(政府给予的)福利;', 'I do not think he is considering Emma\'s welfare...', '我认为他没有考虑到埃玛的幸福。');
INSERT INTO `words` VALUES (10170, 'west', 'west', 'n.', '西;西方;西方(与东方国家相对照的欧洲和北美);', 'I pushed on towards Flagstaff, a hundred miles to the west...', '我继续西行，向100英里外的弗拉格斯塔夫进发。');
INSERT INTO `words` VALUES (10171, 'western', 'ˈwestən', 'adj.', '西方的;向西的;西部的;', '...hand-made rugs from Western and Central Asia.', '来自西亚和中亚的手工小地毯');
INSERT INTO `words` VALUES (10172, 'westerner', 'ˈwestənə(r)', 'n.', '西方人;(尤指)欧美人;(加拿大或美国)西部人;', 'No westerner could fly in without a visa.', '没有签证的西方人不能乘飞机入境。');
INSERT INTO `words` VALUES (10173, 'westward', 'ˈwɛstwəd', 'n.', '西部；西方;', 'He sailed westward from Palos de la Frontera...', '他从帕洛斯-德拉弗龙特拉向西航行。');
INSERT INTO `words` VALUES (10174, 'whaling', 'ˈweɪlɪŋ', 'n.', '捕鲸(业);', '...a ban on commercial whaling.', '禁止商业性捕鲸的禁令');
INSERT INTO `words` VALUES (10175, 'whatever', 'wɒtˈevə(r)', 'det./pron.', '任何;每一;任何事物;', 'Franklin was free to do pretty much whatever he pleased...', '富兰克林几乎可以做自己喜欢做的任何事情。');
INSERT INTO `words` VALUES (10176, 'whatsoever', 'ˌwɒtsəʊ\'evə(r)', 'adv.', '(用于名词词组后，加强否定陈述)丝毫，任何，无论什么;', 'My school did nothing whatsoever in the way of athletics...', '我的学校在体育方面无所作为。');
INSERT INTO `words` VALUES (10177, 'wheat', 'wiːt', 'n.', '小麦(植物);小麦(籽实);', '...farmers growing wheat, corn, or other crops.', '种植小麦，玉米或其他农作物的农民。');
INSERT INTO `words` VALUES (10178, 'wheel', 'wiːl', 'n.', '轮;车轮;轮子;', 'The car wheels spun and slipped on some oil on the road.', '路面上有些油，汽车车轮经过时直打滑。');
INSERT INTO `words` VALUES (10179, 'when', 'wen', 'adv.', '什么时候;何时;什么情况下;', 'When are you going home?...', '你什么时候回家？');
INSERT INTO `words` VALUES (10180, 'whenever', 'wenˈevə(r)', 'conj.', '在任何…的时候;无论何时;在任何…的情况下;', 'She always called at the vicarage whenever she was in the area...', '只要她在那一带，她总会去拜访教区牧师。');
INSERT INTO `words` VALUES (10181, 'whereas', 'ˌweərˈæz', 'conj.', '(用以比较或对比两个事实)然而，但是，尽管;(用于正式文件中句子的开头)鉴于;', 'Pensions are linked to inflation, whereas they should be linked to the cost of living...', '养老金与通货膨胀挂钩，然而它们其实应该和生活费用挂钩。');
INSERT INTO `words` VALUES (10182, 'whereby', 'weəˈbaɪ', 'adv.', '凭此;借以;由于;', '...the system whereby Britons choose their family doctors and the government pays those doctors...', '通过这个系统，英国人选择自己的家庭医生，而由政府给那些医生支付薪水');
INSERT INTO `words` VALUES (10183, 'wherein', 'weərˈɪn', 'adv./conj.', '其中;在那里;在那种情况下;', '...a riding school wherein we could learn the art of horsemanship...', '一所我们可以在那里学习马术的骑术学校');
INSERT INTO `words` VALUES (10184, 'wherever', 'weərˈevə(r)', 'conj.', '在任何地方;在…的各个地方;各处;', 'Some people enjoy themselves wherever they are...', '有些人能够随遇而安。');
INSERT INTO `words` VALUES (10185, 'whether', 'ˈweðə(r)', 'conj.', '(表示迟疑或两个可能性之间的选择)是否;(表示两种情况都真实)是…(还是)，或者…(或者)，不管…(还是);', 'To this day, it\'s unclear whether he shot himself or was murdered...', '至今尚不清楚他究竟是饮弹自杀还是被谋杀。');
INSERT INTO `words` VALUES (10186, 'whichever', 'wɪtʃˈevə(r)', 'det./pron.', '(表示什么特征或品质在作决定时重要)…的那个，…的那些;无论哪个;无论哪些;', 'Whichever way you look at it, nuclear power is the energy of the future...', '不管从哪个角度来看，核能都是未来的能源。');
INSERT INTO `words` VALUES (10187, 'whirl', 'wɜːl', 'v.', '(使)旋转，回旋，打转;(头脑、思想等)混乱不清，激动，恍惚;', 'Not receiving an answer, she whirled round...', '没有得到答案，她飞快地转过身去。');
INSERT INTO `words` VALUES (10188, 'whisky', 'ˈwɪski', 'n.', '威士忌;一杯威士忌;', '...a bottle of whisky.', '一瓶威士忌');
INSERT INTO `words` VALUES (10189, 'whisper', 'ˈwɪspə(r)', 'v.', '耳语;低语;私语;', '\'Keep your voice down,\' I whispered...', '我轻声说：“小声点儿。”');
INSERT INTO `words` VALUES (10190, 'whistle', 'ˈwɪsl', 'n.', '哨子;哨子声;口哨;', 'He whistled and sang snatches of songs...', '他吹口哨并唱了几段小曲。');
INSERT INTO `words` VALUES (10191, 'white', 'waɪt', 'adj.', '白的;白色的;白种人的;', 'He had nice square white teeth...', '他的牙齿洁白而整齐，十分好看。');
INSERT INTO `words` VALUES (10192, 'whoever', 'huːˈevə(r)', 'pron.', '…的那个人(或那些人);…的任何人;无论谁;', 'Whoever did this will sooner or later be caught and will be punished...', '不管是谁干的这事，早晚要被抓住并受到惩罚。');
INSERT INTO `words` VALUES (10193, 'whole', 'həʊl', 'adj.', '全部的;整体的;完全的;', 'He has said he will make an apology to the whole of Asia for his country\'s past behaviour...', '他已表示要为自己国家过去的行径向全亚洲道歉。');
INSERT INTO `words` VALUES (10194, 'wholesale', 'ˈhəʊlseɪl', 'adj.', '批发的;趸售的;大规模的;', 'Warehouse clubs allow members to buy goods at wholesale prices...', '仓储式会员店允许会员以批发价购买商品。');
INSERT INTO `words` VALUES (10195, 'wholesome', 'ˈhəʊlsəm', 'adj.', '有益健康的;有道德的;有良好道德影响的;', '...good, wholesome fun.', '健康有益的娱乐');
INSERT INTO `words` VALUES (10196, 'wholly', 'ˈhəʊlli', 'adv.', '完全地;全面地;整体地;', 'While the two are only days apart in age they seem to belong to wholly different generations...', '尽管这两人年龄上只相差几天，他们却好像是属于完全不同时代的人。');
INSERT INTO `words` VALUES (10197, 'whoop', 'wuːp', 'n.', '(高兴、激动等时的)高喊，大叫;', 'She whoops with delight at a promise of money.', '她因为有可能获得一笔钱而高兴得大叫。');
INSERT INTO `words` VALUES (10198, 'wicked', 'wɪkt , ˈwɪkɪd', 'adj.', '邪恶的;缺德的;淘气的;', 'She described the shooting as a wicked attack...', '她称那次枪击为恶意袭击。');
INSERT INTO `words` VALUES (10199, 'wide', 'waɪd', 'adj.', '宽的;宽阔的;…宽的;', 'All worktops should be wide enough to allow plenty of space for food preparation.', '所有的厨房操作台都应该足够宽大，为制作食物提供足够的空间。');
INSERT INTO `words` VALUES (10200, 'widely', 'ˈwaɪdli', 'adv.', '普遍地;广泛地;范围广地;', 'All worktops should be wide enough to allow plenty of space for food preparation.', '所有的厨房操作台都应该足够宽大，为制作食物提供足够的空间。');
INSERT INTO `words` VALUES (10201, 'widen', 'ˈwaɪdn', 'v.', '(使)变宽;加宽;拓宽;', 'He had an operation last year to widen a heart artery...', '去年他接受了一个扩张心脏动脉的手术。');
INSERT INTO `words` VALUES (10202, 'widening', 'ˈwaɪdnɪŋ', 'v.', '(使)变宽;加宽;拓宽;', 'He had an operation last year to widen a heart artery...', '去年他接受了一个扩张心脏动脉的手术。');
INSERT INTO `words` VALUES (10203, 'widespread', 'ˈwaɪdspred', 'adj.', '分布广的;普遍的;广泛的;', 'There is widespread support for the new proposals...', '新提议获得了广泛的支持。');
INSERT INTO `words` VALUES (10204, 'width', 'wɪdθ', 'n.', '宽度;广度;某一宽度的材料;', 'Measure the full width of the window...', '测量一下窗户的全宽。');
INSERT INTO `words` VALUES (10205, 'wield', 'wiːld', 'v.', '拥有，运用，行使，支配(权力等);挥，操，使用(武器、工具等);', '...a lone assailant wielding a kitchen knife.', '孤身一人、手持菜刀的袭击者');
INSERT INTO `words` VALUES (10206, 'wife', 'waɪf', 'n.', '妻子;太太;夫人;', 'He married his wife Jane 37 years ago...', '他37年前娶了妻子简。');
INSERT INTO `words` VALUES (10207, 'wild', 'waɪld', 'adj.', '自然生长的;野的;野生的;', 'We saw two more wild cats creeping towards us in the darkness...', '我们看见又有两只野猫在黑暗中悄悄向我们靠近。');
INSERT INTO `words` VALUES (10208, 'wilderness', 'ˈwɪldənəs', 'n.', '未开发的地区;荒无人烟的地区;荒野;', '...the icy Canadian wilderness...', '冰封的加拿大旷野');
INSERT INTO `words` VALUES (10209, 'wildlife', 'ˈwaɪldlaɪf', 'n.', '野生动物;野生生物;', 'People were concerned that pets or wildlife could be affected by the pesticides.', '人们担心宠物或野生动物可能受到杀虫剂影响。');
INSERT INTO `words` VALUES (10210, 'willing', 'ˈwɪlɪŋ', 'adj.', '愿意;乐意;自愿的;', 'The military now say they\'re willing to hold talks with the political parties...', '军方现在说，他们愿意与政党进行谈判。');
INSERT INTO `words` VALUES (10211, 'willingly', '\'wɪlɪŋlɪ', 'adj.', '甘心情愿;', 'The military now say they\'re willing to hold talks with the political parties...', '军方现在说，他们愿意与政党进行谈判。');
INSERT INTO `words` VALUES (10212, 'willingness', '', 'n.', '乐意；心甘情愿；自动自发;', 'The military now say they\'re willing to hold talks with the political parties...', '军方现在说，他们愿意与政党进行谈判。');
INSERT INTO `words` VALUES (10213, 'win', 'wɪn', 'v.', '(在比赛、赛跑、战斗等中)获胜，赢;(在比赛、赛跑、选举等中)赢得，夺取，获得，挣得;(尤指通过自己的努力)取得;', 'He does not have any realistic chance of winning the election...', '他没有一点儿赢得选举的现实机会。');
INSERT INTO `words` VALUES (10214, 'window', 'ˈwɪndəʊ', 'n.', '窗;窗户;窗口;', 'He stood at the window, moodily staring out...', '他站在窗前，忧郁地凝视着外面。');
INSERT INTO `words` VALUES (10215, 'windows', 'ˈwɪndəʊz', 'n.', '窗;窗户;窗口;', 'He stood at the window, moodily staring out...', '他站在窗前，忧郁地凝视着外面。');
INSERT INTO `words` VALUES (10216, 'windy', 'ˈwɪndi', 'adj.', '多风的;风大的;当风的;', 'It was windy and Jake felt cold.', '风很大，杰克觉得很冷。');
INSERT INTO `words` VALUES (10217, 'wine', 'waɪn', 'n.', '葡萄酒;(用植物或除葡萄以外的水果酿制的)酒，果酒;紫红色;', '...a bottle of white wine...', '一瓶白葡萄酒');
INSERT INTO `words` VALUES (10218, 'wing', 'wɪŋ', 'n.', '(鸟、昆虫或蝙蝠)翅膀，翼;(飞行器的)翅膀;机翼;', 'The bird flapped its wings furiously...', '鸟儿使劲地拍打翅膀。');
INSERT INTO `words` VALUES (10219, 'wink', 'wɪŋk', 'v.', '眨一只眼，眨眼示意(尤指使眼色或表示开玩笑);闪烁;明灭;', 'Brian winked at his bride-to-be...', '布赖恩向他的准新娘使了个眼色。');
INSERT INTO `words` VALUES (10220, 'winner', 'ˈwɪnə(r)', 'n.', '获胜的人(或队、动物等);优胜者;成功者;', 'She will present the trophies to the award winners...', '她将给获奖者颁奖。');
INSERT INTO `words` VALUES (10221, 'winter', 'ˈwɪntə(r)', 'n.', '冬天;冬季;', 'In winter the nights are long and cold.', '冬夜漫长而寒冷。');
INSERT INTO `words` VALUES (10222, 'wipe', 'waɪp', 'v.', '擦;拭;抹;', 'I\'ll just wipe the table...', '我就擦擦桌子。');
INSERT INTO `words` VALUES (10223, 'wire', 'ˈwaɪə(r)', 'n.', '金属丝;金属线;一段金属丝(或线);', '...fine copper wire.', '细铜丝');
INSERT INTO `words` VALUES (10224, 'wireless', 'ˈwaɪələs', 'n.', '无线电收音机;无线电发射和接收系统;无线电报;', '...the fast-growing wireless communication market.', '迅速扩张的无线通讯市场');
INSERT INTO `words` VALUES (10225, 'wisdom', 'ˈwɪzdəm', 'n.', '智慧;才智;精明;', '...the patience and wisdom that comes from old age.', '历尽岁月沧桑而形成的耐心与智慧');
INSERT INTO `words` VALUES (10226, 'wise', 'waɪz', 'adj.', '充满智慧的;明智的;英明的;', 'She has the air of a wise woman...', '她有才女气质。');
INSERT INTO `words` VALUES (10227, 'wish', 'wɪʃ', 'v.', '希望(不大可能的事)发生;怀着(不可能实现的)愿望;希望(做某事);', 'She was sincere and genuine in her wish to make amends for the past...', '她真诚地希望弥补从前的过失。');
INSERT INTO `words` VALUES (10228, 'wit', 'wɪt', 'n.', '措辞巧妙的能力;风趣;才思;', 'Boulding was known for his biting wit...', '博尔丁爱说尖酸的俏皮话是出了名的。');
INSERT INTO `words` VALUES (10229, 'withdraw', 'wɪðˈdrɔː', 'v.', '(使)撤回，撤离;停止提供;不再给予;', 'He reached into his pocket and withdrew a sheet of notepaper...', '他把手伸进口袋，掏出一张便签。');
INSERT INTO `words` VALUES (10230, 'withdrawal', 'wɪðˈdrɔːəl', 'n.', '撤走;收回;取回;', '...withdrawal of friendship...', '绝交');
INSERT INTO `words` VALUES (10231, 'wither', 'ˈwɪðə(r)', 'v.', '(使)枯萎，凋谢;萎缩;(尤指渐渐)破灭，消失;', 'When he went into retirement, he visibly withered...', '他到退休年龄时已老态毕现。');
INSERT INTO `words` VALUES (10232, 'withhold', 'wɪðˈhəʊld', 'v.', '拒绝给;不给;', 'Police withheld the dead boy\'s name yesterday until relatives could be told...', '警方昨天拒绝在通知亲属前透露死亡男孩的姓名。');
INSERT INTO `words` VALUES (10233, 'within', 'wɪˈðɪn', 'prep.', '不出(某段时间);在(某段时间)之内;不出(某段距离);', 'Clients are entertained within private dining rooms...', '在私人餐室内款待客户。');
INSERT INTO `words` VALUES (10234, 'without', 'wɪˈðaʊt', 'prep.', '没有;缺乏;不和…在一起;', 'I don\'t like myself without a beard...', '我不喜欢自己没胡子的样子。');
INSERT INTO `words` VALUES (10235, 'withstand', 'wɪðˈstænd', 'v.', '承受;抵住;顶住;', '...armoured vehicles designed to withstand chemical attack...', '具有抵御化学武器袭击的设计功能的装甲车');
INSERT INTO `words` VALUES (10236, 'witness', 'ˈwɪtnəs', 'n.', '目击者;见证人;证人;', 'Witnesses to the crash say they saw an explosion just before the disaster...', '空难目击者称，就在灾难发生之前他们看见飞机发生了爆炸。');
INSERT INTO `words` VALUES (10237, 'witty', 'ˈwɪti', 'adj.', '言辞诙谐的;巧妙的;妙趣横生的;', 'His plays were very good, very witty...', '他的剧作非常精彩，妙趣横生。');
INSERT INTO `words` VALUES (10238, 'woe', 'wəʊ', 'n.', '麻烦;问题;困难;', 'He listened to my tale of woe...', '他听我诉说悲伤的故事。');
INSERT INTO `words` VALUES (10239, 'woman', 'ˈwʊmən', 'n.', '成年女子;妇女;女子;', '...a young Lithuanian woman named Dayva.', '名叫戴娃的立陶宛年轻女子');
INSERT INTO `words` VALUES (10240, 'wonder', 'ˈwʌndə(r)', 'v.', '想知道;想弄明白;琢磨;', 'I wondered what that noise was...', '我纳闷儿那是什么声音。');
INSERT INTO `words` VALUES (10241, 'wonderful', 'ˈwʌndəfl', 'adj.', '精彩的;绝妙的;令人高兴的;', 'The cold, misty air felt wonderful on his face...', '雾蒙蒙的寒冷空气使他脸上感觉很舒爽。');
INSERT INTO `words` VALUES (10242, 'wood', 'wʊd', 'n.', '木;木头;木料;', 'Their dishes were made of wood...', '他们的盘子是木制的。');
INSERT INTO `words` VALUES (10243, 'wooden', 'ˈwʊdn', 'adj.', '木制的;木头的;木头似的;', '...the shop\'s bare brick walls and faded wooden floorboards.', '商店裸露的砖墙和褪色的木地板');
INSERT INTO `words` VALUES (10244, 'woods', 'wʊdz', 'n.', '木;木头;木料;', 'Their dishes were made of wood...', '他们的盘子是木制的。');
INSERT INTO `words` VALUES (10245, 'word', 'wɜːd', 'n.', '单词;词;字;', 'The words stood out clearly on the page...', '那些字赫然写在纸上。');
INSERT INTO `words` VALUES (10246, 'wording', 'ˈwɜːdɪŋ', 'n.', '措辞;用词;', 'The two sides failed to agree on the wording of a final report...', '双方未能就最终报告的措辞达成一致。');
INSERT INTO `words` VALUES (10247, 'words', 'wɜːdz', 'n.', '单词;词;字;', 'It was the first show to use the F-word and show nudity on stage...', '这是第一个使用F字头词并且裸体上台的演出。');
INSERT INTO `words` VALUES (10248, 'wordy', 'ˈwɜːdi', 'adj.', '话多的;冗长的;啰唆的;', 'The chapter is mostly wordy rhetoric.', '这一章多为冗长的华丽词藻。');
INSERT INTO `words` VALUES (10249, 'work', 'wɜːk', 'v.', '做体力(或脑力)工作;劳动;干活;', 'Weiner works for the US Department of Transport...', '韦纳就职于美国交通部。');
INSERT INTO `words` VALUES (10250, 'workable', 'ˈwɜːkəbl', 'adj.', '可行的;行得通的;可成形(或延长、挖掘等)的;', 'Investors can simply pay cash, but this isn\'t a workable solution in most cases.', '投资者可以直接付现金，但这个办法在大多数情况下都不可行。');
INSERT INTO `words` VALUES (10251, 'workday', 'ˈwɜːkdeɪ', 'n.', '工作日;', 'His workday starts at 3.30 a.m. and lasts 12 hours.', '他凌晨3点半开始工作，一直持续12小时。');
INSERT INTO `words` VALUES (10252, 'worker', 'ˈwɜːkə(r)', 'n.', '工作者;人员;雇员;', '...office workers...', '办公室职员');
INSERT INTO `words` VALUES (10253, 'workers', 'ˈwɜːkəz', 'n.', '工作者;人员;雇员;', '...office workers...', '办公室职员');
INSERT INTO `words` VALUES (10254, 'working', 'ˈwɜːkɪŋ', 'adj.', '有工作的;有职业的;做工的;', 'Like working women anywhere, Asian women are buying convenience foods.', '像其他地方的职业女性一样，亚洲女性也购买方便食品。');
INSERT INTO `words` VALUES (10255, 'workman', 'ˈwɜːkmən', 'n.', '男工人;工匠;工作…的人;', 'In University Square workmen are building a steel fence...', '在大学广场上，工人们正在修建钢栅栏。');
INSERT INTO `words` VALUES (10256, 'workmanship', 'ˈwɜːkmənʃɪp', 'n.', '手艺;技艺;工艺;', 'The problem may be due to poor workmanship...', '问题可能在于工艺太差。');
INSERT INTO `words` VALUES (10257, 'works', 'wɜːks', 'v.', '做体力(或脑力)工作;劳动;干活;', 'Weiner works for the US Department of Transport...', '韦纳就职于美国交通部。');
INSERT INTO `words` VALUES (10258, 'workshop', 'ˈwɜːkʃɒp', 'n.', '车间;工场;作坊;', 'Trumpeter Marcus Belgrave ran a jazz workshop for young artists...', '小号手马库斯·贝尔格雷夫为年轻艺术家办了一个爵士乐研习班。');
INSERT INTO `words` VALUES (10259, 'world', 'wɜːld', 'n.', '世界;地球;天下;', 'It\'s a beautiful part of the world...', '这是世界上很美的一个地区。');
INSERT INTO `words` VALUES (10260, 'worldwide', 'ˌwɜːldˈwaɪd', 'adj.', '影响全世界的;世界各地的;', 'His books have sold more than 20 million copies worldwide...', '他的书在全世界已经售出两千多万本。');
INSERT INTO `words` VALUES (10261, 'worried', 'ˈwʌrid', 'adj.', '担心的;担忧的;发愁的;', 'He seemed very worried...', '他好像非常担心。');
INSERT INTO `words` VALUES (10262, 'worry', 'ˈwʌri', 'v.', '担心;担忧;发愁;', 'Don\'t worry, your luggage will come on afterwards by taxi...', '别担心，你的行李随后会由出租车送过来。');
INSERT INTO `words` VALUES (10263, 'worth', 'wɜːθ', 'adj.', '有…价值;值…钱;(指行动)值得，有价值;', 'These books might be worth £80 or £', '90 or more to a collector... 这些书对收藏家而言可能值八九十英镑或更多。');
INSERT INTO `words` VALUES (10264, 'worthless', 'ˈwɜːθləs', 'adj.', '没用的;无价值的;品质坏的;', 'The guarantee could be worthless if the firm goes out of business...', '如果厂家倒闭，保修单就会变得毫无用处。');
INSERT INTO `words` VALUES (10265, 'worthwhile', 'ˌwɜːθˈwaɪl', 'adj.', '重要的;令人愉快的;有趣的;', 'The President\'s trip to Washington this week seems to have been worthwhile...', '总统本周的华盛顿之行看来是有价值的。');
INSERT INTO `words` VALUES (10266, 'worthy', 'ˈwɜːði', 'adj.', '值得(或应得)…的;值得尊敬的;值得注意的;', 'The bank might think you\'re worthy of a loan...', '银行也许会认为你具备贷款的条件。');
INSERT INTO `words` VALUES (10267, 'wrap', 'ræp', 'v.', '包，裹(礼物等);用…包裹(或包扎、覆盖等);用…缠绕(或围紧);', 'Harry had carefully bought and wrapped presents for Mark to give them...', '哈里精心购置了礼物又仔细包好，让马克交给他们。');
INSERT INTO `words` VALUES (10268, 'wrapper', 'ˈræpə(r)', 'n.', '包装纸;封皮;封套;', 'I emptied the sweet wrappers from the ashtray.', '我把烟灰缸里的糖纸倒掉。');
INSERT INTO `words` VALUES (10269, 'wrath', 'rɒθ', 'n.', '盛怒;震怒;怒火;', 'He incurred the wrath of the authorities in speaking out against government injustices.', '他直言批评政府的不公正做法，招致当局震怒。');
INSERT INTO `words` VALUES (10270, 'wreath', 'riːθ', 'n.', '花圈(用于祭奠);花环(传统上圣诞节时挂在门上);花冠(旧时用作荣誉的象征);', 'The coffin lying before the altar was bare, except for a single wreath of white roses...', '祭台前的棺木上除了仅有的一个白玫瑰花圈外，没有其他任何装饰。');
INSERT INTO `words` VALUES (10271, 'wreathe', 'riːð', 'v.', '环绕;覆盖;笼罩;', 'The ship was wreathed in smoke...', '船笼罩在烟雾中。');
INSERT INTO `words` VALUES (10272, 'wreck', 'rek', 'n.', '沉船;严重损毁的船;(事故中)遭严重毁坏的汽车(或飞机等);', 'He wrecked the garden...', '他毁掉了花园。');
INSERT INTO `words` VALUES (10273, 'wrench', 'rentʃ', 'v.', '猛拉;猛扭;猛拧;', 'He felt two men wrench the suitcase from his hand...', '他感觉有两个人在使劲拽他手里的手提箱。');
INSERT INTO `words` VALUES (10274, 'wrestle', 'ˈresl', 'v.', '摔跤;奋力对付;努力处理;', 'Delegates wrestled with the problems of violence and sanctions...', '代表们试图解决暴力与惩处的问题。');
INSERT INTO `words` VALUES (10275, 'wretched', 'ˈretʃɪd', 'adj.', '感到不适的;难受的;不愉快的;', 'You have built up a huge property empire by buying from wretched people who had to sell or starve.', '你靠从那些不卖房就得挨饿的可怜人手里买下房子后建立起了庞大的房产帝国。');
INSERT INTO `words` VALUES (10276, 'wring', 'rɪŋ', 'v.', '拧，绞，拧出，绞出(衣服等中的水);扭(鸟的脖子，以将其杀死);', 'Buyers use different ruses to wring free credit out of their suppliers...', '买主们千方百计想从供货商那儿无息赊购。');
INSERT INTO `words` VALUES (10277, 'wrinkle', 'ˈrɪŋkl', 'n.', '(尤指脸上的)皱纹;(布或纸上的)皱褶，皱痕;', 'His face was covered with wrinkles...', '他的脸上布满了皱纹。');
INSERT INTO `words` VALUES (10278, 'write', 'raɪt', 'v.', '书写;写字;写作;', 'If you\'d like one, simply write your name and address on a postcard and send it to us...', '如果你想要一个的话，只要把姓名、住址写在一张明信片上寄给我们就行。');
INSERT INTO `words` VALUES (10279, 'writer', 'ˈraɪtə(r)', 'n.', '作家;作者;著者;', 'Turner is a writer and critic.', '特纳是作家兼批评家。');
INSERT INTO `words` VALUES (10280, 'writing', 'ˈraɪtɪŋ', 'n.', '写;书写;写作;', '\'It\'s from a notebook,\' the sheriff said, \'And there\'s writing on it.\'...', '“这是在一个笔记本里找到的，”法官道，“上面还有文字。”');
INSERT INTO `words` VALUES (10281, 'wrong', 'rɒŋ', 'adj.', '错误的;不对的;不正确的;', 'Pain is the body\'s way of telling us that something is wrong...', '疼痛是身体在告诉我们有地方出问题了。');
INSERT INTO `words` VALUES (10282, 'wrongly', 'ˈrɒŋli', 'adv.', '不公正地;不道德地;错误地;', 'Pain is the body\'s way of telling us that something is wrong...', '疼痛是身体在告诉我们有地方出问题了。');
INSERT INTO `words` VALUES (10283, 'wrought', 'rɔːt', 'v.', '使发生了，造成了(尤指变化);', 'Events in Paris wrought a change in British opinion towards France and Germany.', '巴黎发生的事件改变了英国对法国和德国的看法。');
INSERT INTO `words` VALUES (10284, 'xerox', 'ˈzɪərɒks', 'v.', '复印;复制;', 'The rooms are crammed with humming Xerox machines.', '那些房间里摆满了嗡嗡作响的复印机。');
INSERT INTO `words` VALUES (10285, 'yacht', 'jɒt', 'n.', '帆船;游艇;快艇;', 'His 36ft yacht sank suddenly last summer.', '他那36英尺长的游艇去年夏天突然沉没了。');
INSERT INTO `words` VALUES (10286, 'yard', 'jɑːd', 'n.', '院子;(某种用途的)区域，场地;码(长度单位，等于3英尺或0.9144米);', 'The incident took place about 500 yards from where he was standing...', '事件发生在距他所站位置大约500码远的地方。');
INSERT INTO `words` VALUES (10287, 'yarn', 'jɑːn', 'n.', '纱;纱线;(尤指夸张的或编造的)故事;', 'She still spins the yarn and knits sweaters for her family.', '她仍然纺纱为家人织毛线衫。');
INSERT INTO `words` VALUES (10288, 'yawn', 'jɔːn', 'v.', '打哈欠;非常宽;难以逾越;', 'She yawned, and stretched lazily...', '她打了个哈欠，伸了个懒腰。');
INSERT INTO `words` VALUES (10289, 'yeah', 'jeə', 'int.', '是的;对;', '\'Bring us something to drink.\' — \'Yeah, yeah.\'', '“给我们拿点喝的。” ——“好的，好的。”');
INSERT INTO `words` VALUES (10290, 'year', 'jɪə(r)', 'n.', '年;日历年;一年时间;', 'The year was 1840...', '那是1840年。');
INSERT INTO `words` VALUES (10291, 'yearbook', 'ˈjɪəbʊk', 'n.', '年鉴;年刊;(每年出版的)校刊;', '...an American college yearbook for 1955.', '一所美国大学1955年的年刊');
INSERT INTO `words` VALUES (10292, 'yearly', 'ˈjɪəli', 'adj.', '每年的;一年一次的;年度的;', 'The seven major industrial countries will have their yearly meeting in London.', '7个主要工业国家将在伦敦举行一年一度的会议。');
INSERT INTO `words` VALUES (10293, 'yearn', 'jɜːn', 'v.', '渴望;渴求;', 'He yearned for freedom...', '他渴望自由。');
INSERT INTO `words` VALUES (10294, 'years', 'jɪəz', 'n.', '年;日历年;一年时间;', 'The year was 1840...', '那是1840年。');
INSERT INTO `words` VALUES (10295, 'yell', 'jel', 'v.', '叫喊;大喊;吼叫;', '\'Eva!\' he yelled...', '“伊娃！”他大喊道。');
INSERT INTO `words` VALUES (10296, 'yellow', 'ˈjeləʊ', 'adj.', '黄的;黄色的;黄皮肤的，黄色人种的;', 'The walls have been painted bright yellow...', '墙壁漆成了亮黄色。');
INSERT INTO `words` VALUES (10297, 'yes', 'jes', 'int.', '答话时表示正确或真实;表示同意所说的话;反驳否定的话;', '\'Are you a friend of Nick\'s?\' — \'Yes.\'...', '“你是尼克的朋友吗？”——“是的。”');
INSERT INTO `words` VALUES (10298, 'yesterday', 'ˈjestədeɪ', 'adv.', '在昨天;', 'She left yesterday...', '她昨天走了。');
INSERT INTO `words` VALUES (10299, 'yet', 'jet', 'adv.', '用于否定句和疑问句，谈论尚未发生但可能发生的事;现在;即刻;', 'They haven\'t finished yet...', '他们还没有完成。');
INSERT INTO `words` VALUES (10300, 'yield', 'jiːld', 'v.', '出产(作物);产生(收益、效益等);提供;', 'Will she yield to growing pressure for her to retire?...', '面对要求其隐退的压力越来越大，她会屈服吗？');
INSERT INTO `words` VALUES (10301, 'yoke', 'jəʊk', 'n.', '轭;(尤指)牛轭;奴役;', 'People are still suffering under the yoke of slavery...', '人们仍然生活在奴隶制的枷锁之下。');
INSERT INTO `words` VALUES (10302, 'yolk', 'jəʊk', 'n.', '蛋黄;', 'Only the yolk contains cholesterol.', '只有蛋黄含有胆固醇。');
INSERT INTO `words` VALUES (10303, 'young', 'jʌŋ', 'adj.', '幼小的;未成熟的;年轻的;', 'In Scotland, young people can marry at 16...', '在苏格兰，年轻人16岁就可以结婚。');
INSERT INTO `words` VALUES (10304, 'younger', '\'jʌŋɡə(r)', 'adj.', '幼小的;未成熟的;年轻的;', 'In Scotland, young people can marry at 16...', '在苏格兰，年轻人16岁就可以结婚。');
INSERT INTO `words` VALUES (10305, 'youngster', 'ˈjʌŋstə(r)', 'n.', '年轻人;少年;儿童;', 'Other youngsters are not so lucky...', '其他的孩子则没有这么幸运。');
INSERT INTO `words` VALUES (10306, 'youth', 'juːθ', 'n.', '青年时期(尤指成年以前);年轻;青春;', 'In my youth my ambition had been to be an inventor.', '我年轻时的抱负是成为一个发明家。');
INSERT INTO `words` VALUES (10307, 'youthful', 'ˈjuːθfl', 'adj.', '年轻人的;青年的;青春的;', 'I\'m a very youthful 50.', '我50岁了，但仍富有青春活力。');
INSERT INTO `words` VALUES (10308, 'yuan', 'juˈɑːn', 'n.', '元(中国货币单位);', 'For most events, tickets cost one, two or three yuan.', '大多数活动的票价是一元、两元或三元。');
INSERT INTO `words` VALUES (10309, 'zap', 'zæp', 'v.', '(突然而猛烈地)毁坏，杀死，打击;很快地做;迅速做;', 'A guard zapped him with the stun gun.', '一名警卫用眩晕枪击中了他。');
INSERT INTO `words` VALUES (10310, 'zeal', 'ziːl', 'n.', '热情;激情;', '...his zeal for teaching...', '他对教学工作的热情');
INSERT INTO `words` VALUES (10311, 'zealous', 'ˈzeləs', 'adj.', '热情的;热烈的;充满激情的;', 'She was a zealous worker for charitable bodies...', '她对慈善事业充满热忱。');
INSERT INTO `words` VALUES (10312, 'zero', 'ˈzɪərəʊ', 'num.', '零;(气温、压力等的)零度，零点;最少量;', 'Visibility at the city\'s airport came down to zero, bringing air traffic to a standstill.', '该市机场的能见度降为零，空中交通陷入了停顿。');
INSERT INTO `words` VALUES (10313, 'zest', 'zest', 'n.', '热情;狂热;兴奋;', 'He retired from professional chess because he had lost the zest for winning...', '他退出了职业国际象棋赛场，因为他已经对获胜毫无兴致。');
INSERT INTO `words` VALUES (10314, 'zigzag', 'ˈzɪɡzæɡ', 'n.', '锯齿形线条(或形状);之字形;', 'They staggered in a zigzag across the tarmac.', '他们深一脚浅一脚，跌跌撞撞地穿过停机坪。');
INSERT INTO `words` VALUES (10315, 'zip', 'zɪp', 'n.', '拉链;拉锁;能量;', 'He pulled the zip of his leather jacket down slightly.', '他稍稍往下拉了拉皮夹克的拉链。');
INSERT INTO `words` VALUES (10316, 'zone', 'zəʊn', 'n.', '(有某特色或作用的)地区，地带;(规划的)区域，分区;(尤指有别于周围的)区域，部分;', 'Many people have stayed behind in the potential war zone...', '许多人留在了可能会沦为交战区的地方。');
INSERT INTO `words` VALUES (10317, 'zoo', 'zuː', 'n.', '动物园;', 'He took his son Christopher to the zoo.', '他带着儿子克里斯托弗去了动物园。');
INSERT INTO `words` VALUES (10318, 'zoom', 'zuːm', 'v.', '快速移动;迅速前往;急剧增长;', 'We zoomed through the gallery...', '我们飞快地穿过画廊。');
